INSERT INTO [b26577] VALUES (1,'بسم الله الرحمن الرحيم
INSERT INTO [b26577] VALUES (2,'<span data-type="title" id=toc-2>أبو الزبير محمد بن مسلم بن تدرس عن محمد بن مسلم بن شهاب الزهري</span>
INSERT INTO [b26577] VALUES (3,'<span data-type="title" id=toc-3>الزهري عن عمرو بن شعيب بن محمد بن عبد الله بن عمرو</span>
INSERT INTO [b26577] VALUES (4,'<span data-type="title" id=toc-4>عمرو بن شعيب عن الزهري</span>
INSERT INTO [b26577] VALUES (5,'<span data-type="title" id=toc-5>الأعمش عن أبي صالح</span>
INSERT INTO [b26577] VALUES (6,'٦- حدثنا محمد بن أحمد بن راشد حدثنا الحسن بن السكن الضبعي حدثنا محمد بن جهضم عن إسماعيل بن جعفر عن سهيل عن الأعمش عن أبي صالح عن أبي هريرة قال قال النبي صلى الله عليه وسلم لا تدخل الملائكة بيتا فيه صورة ولا كلب',NULL,'16',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (7,'٧- حدثنا عبدان ومحمد بن يحيى قالا حدثنا أحمد بن محمد المعلى حدثنا أبو همام من كتابه حدثنا إبراهيم بن طهمان عن سهيل عن الأعمش عن أبي صالح عن أبي هريرة قال قال رسول الله صلى الله عليه وسلم من بات وفي يده غمر فعرض له عارض فلا يلومن إلا نفسه',NULL,'17',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (8,'٨- أنبأنا عبدان حدثنا تمتام أخبرنا أبو همام أخبرنا عن سفيان عن سهيل نحوه',NULL,'17',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (9,'٩- حدثنا محمد بن العباس بن أيوب حدثنا عبيد الله بن الحجاج أخبرنا محمد بن جهضم وأخبرنا إسماعيل بن جعفر عن سهيل عن الأعمش عن أبي صالح عن أبي هريرة قال قال النبي صلى الله عليه وسلم من قال أشهد أن لا إله إلا الله وحده لا شريك له وأن محمد عبده ورسوله من جاء بها يوم القيامة غير شاك أدخله الله الجنة',NULL,'17',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (10,'١٠- حدثني وليد بنان الواسطي أخبرنا محمد بن زنبور أخبرنا عبد العزيز بن أبي حازم حدثني سهيل عن الأعمش عن أبي صالح عن أبي هريرة أن رسول الله صلى الله عليه وسلم نزل في غزوة غزاها فأصاب أصحابه جوع وقلت أزوادهم فجاؤوا إلى رسول الله يشكون إليه ما أصابهم وليستأذنوه أن يتخذوا بعض رواحلهم فأذن لهم فمررنا بعمر بن الخطاب فقال من أين جئتم فأخبروه أنهم استأذنوا النبي صلى الله عليه وسلم أن يتخذوا بعض رواحلهم قال فأذن لكم؟ قالوا نعم قال فإني أسألكم وأقسم عليكم ألا رجعتم معي إلى رسول الله صلى الله عليه وسلم فرجعوا معه فذهب عمر إلى رسول الله فقال يا رسول الله أتأذن لهم أن يتخذوا رواحلهم فماذا يركبون؟ قال رسول الله صلى الله عليه وسلم فماذا أصنع ليس معي ما أعطيهم قال عمر بلى يا رسول الله تأمر من كان معه فضل من زاد يأتي به إليك فتجمعه على شيء ثم تدعو فيه بالبركة ثم تقسمه بينهم ففعل فدعا بفضل أزوادهم فمنهم الآتي بالقليل ومنهم الآتي بالكثير فجعله في نطع ثم دعا فيه ما شاء الله أن يدعو ثم قسمه بينهم فما بقي من القوم أحد إلا ملأ ما كان معه من وعاء وفضل فضل فقال عند ذلك أشهد أن لا إله إلا الله وحده لا شريك له ⦗١٨⦘ وأن محمد عبده ورسوله من جاء بها يوم القيامة غير شاك أدخله الله الجنة',NULL,'17',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (11,'<span data-type="title" id=toc-6>الأعمش عن زبيد</span>
INSERT INTO [b26577] VALUES (12,'<span data-type="title" id=toc-7>الأوزاعي عن يونس بن يزيد</span>
INSERT INTO [b26577] VALUES (13,'<span data-type="title" id=toc-8>يونس بن يزيد عن الأوزاعي</span>
INSERT INTO [b26577] VALUES (14,'<span data-type="title" id=toc-9>الأعمش عن سهيل بن أبي صالح</span>
INSERT INTO [b26577] VALUES (15,'١٥- أخبرنا أبو يعلى حدثنا محمد بن عبد الله بن نمير حدثنا خلاد الجعفي عن أبي مسلم عن الأعمش عن سهيل بن أبي صالح عن أبي هريرة قال ذكرنا ليلة القدر فقال رسول الله صلى الله عليه وسلم كم مضى من الشهر قلنا ثنتان وعشرون وبقي ثمان قال مضى ثنتان وعشرون وبقي سبع الشهر تسع وعشرون',NULL,'19',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (16,'١٦- حدثنا عبد الرحمن بن داود بن منصور حدثنا عثمان بن خرزاد حدثنا محمد بن عبد الله بن عبد الملك البغدادي حدثنا الوليد بن أبان الكرابيسي حدثنا أبو معاوية عن الأعمش عن سهيل بن أبي صالح عن أبيه عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم ثلاثة لا ينظر الله إليهم يوم القيامة ولا يزكيهم ولهم عذاب أليم قوم بايعوا إماما على دنيا فإن أعطاهم منها وفوا وإن لم يعطوا نكثوا ورجل بايع بعد العصر فحلف بالله لقد اشتراها بكذا وكذا مغترا ورجل يمنع فضل مائه',NULL,'19',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (17,'<span data-type="title" id=toc-10>سهيل بن أبي صالح عن الأعمش</span>
INSERT INTO [b26577] VALUES (18,'١٨- حدثنا عبدان حدثنا علي بن أبي صالح حدثنا عمر بن عبد الوهاب حدثنا يزيد بن زريع عن نوح بن القاسم عن سهيل نحوه',NULL,'20',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (19,'١٩- حدثنا قاسم بن زكريا المطرز حدثنا بكر بن محمد البزار حدثنا محمد بن جهضم
INSERT INTO [b26577] VALUES (20,'<span data-type="title" id=toc-11>زبيد عن الأعمش</span>
INSERT INTO [b26577] VALUES (21,'<span data-type="title" id=toc-12>الأوزاعي عن الأعمش</span>
INSERT INTO [b26577] VALUES (22,'<span data-type="title" id=toc-13>إسماعيل بن مسلم عن الأعمش</span>
INSERT INTO [b26577] VALUES (23,'٢٤- حدثنا ابن صاعد حدثنا أزهر بن جميل حدثنا عمر بن شقيق عن إسماعيل بن مسلم عن الأعمش عن أبي صالح عن أبي هريرة قال ما جاء النبي صلى الله عليه وسلم طعاما قط إن إشتهاه أكله وإلا تركه',NULL,'21',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (24,'<span data-type="title" id=toc-14>الأعمش عن إسماعيل بن مسلم</span>
INSERT INTO [b26577] VALUES (25,'٢٦- حدثنا محمد بن العباس بن أيوب حدثنا القاسم بن محمد بن الحارث حدثنا عبدان بن عثمان عن أبي حمزة عن الأعمش عن إسماعيل بن مسلم عن الحسن عن عمران بن حصين قال: قال النبي صلى الله عليه وسلم سؤال الغني شيء من وجهه إن أعطى قليلا فقليل وإن أعطى كثيرا فكثير',NULL,'22',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (26,'<span data-type="title" id=toc-15>الأعمش عن شعبة بن الحجاج</span>
INSERT INTO [b26577] VALUES (27,'٢٨- حدثنا الوليد بن أبان قال: سمعت أحمد بن يونس يقول بلغني أن شعبة كان بين يدي الأعمش يسمع منه فقال له الأعمش يا بصري أليس عندكم من الأحاديث التي تغربون علينا فقال له شعبة أنبأنا قتادة عن أنس قال صليت خلف رسول الله صلى الله عليه وسلم وخلف أبي بكر وعمر فكانوا يستفتحون القراءة معنا بالحمد لله فقال له الأعمش: يا بصري أحك عن غير قتادة فقال له شعبة وقد حدثني به ثابت عن أنس فرواه الأعمش عن شعبة عن ثابت عن أنس',NULL,'22',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (28,'٢٩- حدثنا أبو علي أحمد بن محمد بن إبراهيم حدثنا محمد بن خلف ⦗٢٣⦘ المروزي حدثنا يحيى بن هاشم حدثنا الأعمش عن شعبة عن ثابت عن أنس بن مالك قال: قال رسول الله صلى الله عليه وسلم لا يتمنين أحدكم الموت فقال كان لابد فليقل اللهم أحيني ما كانت الحياة خيراً لي وتوفني ما كانت الوفاة خيراً لي',NULL,'22',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (29,'<span data-type="title" id=toc-16>شعبة بن الحجاج عن الأعمش</span>
INSERT INTO [b26577] VALUES (30,'<span data-type="title" id=toc-17>الأعمش عن خالد الحذاء</span>
INSERT INTO [b26577] VALUES (31,'<span data-type="title" id=toc-18>خالد الحذاء عن الأعمش</span>
INSERT INTO [b26577] VALUES (32,'<span data-type="title" id=toc-19>الأعمش عن سفيان بن عيينة</span>
INSERT INTO [b26577] VALUES (33,'<span data-type="title" id=toc-20>سفيان بن عيينة عن الأعمش</span>
INSERT INTO [b26577] VALUES (34,'<span data-type="title" id=toc-21>الأعمش عن إبراهيم بن طهمان وإبراهيم بن طهمان عن الأعمش</span>
INSERT INTO [b26577] VALUES (35,'٣٦- حدثنا جبير بن هارون حدثنا علي الطنافسي حدثنا أبو معاوية عن الأعمش عن إبراهيم بن طهمان عن بشر بن خلال عن الحسن قال لما زوج رسول الله صلى الله عليه وسلم ابنته الثانية عثمان قالوا يا رسول الله أكل هذا لعثمان؟ قال إنا نضعهن حيث أمرنا الله عز وجل',NULL,'25',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (36,'<span data-type="title" id=toc-22>أبو إسحاق السبيعي عن الأعمش</span>
INSERT INTO [b26577] VALUES (37,'٣٨- حدثنا الفضل بن محمد المروزي حدثنا أحمد بن سيار المروزي حدثنا يحيى بن إسحاق حدثنا عبد الكبير بن دينار حدثنا أبو إسحاق الهمداني عن الأعمش عن إبراهيم عن علقمة عن عبد الله قال خرجنا مع رسول الله صلى الله عليه وسلم مخرجا فلم يصب ما يتوضأ به ولا يشربه ومع رسول الله صلى الله عليه وسلم إداوة فيه شيء من ماء فصبه في ⦗٢٦⦘ الإناء ثم وضع كفه عليه , ثم قال: هلم على الوضوء والبركة من الله
INSERT INTO [b26577] VALUES (38,'<span data-type="title" id=toc-23>الأعمش عن أبي إسحاق السبيعي أبو إسحاق عن البراء</span>
INSERT INTO [b26577] VALUES (39,'٤٠- حدثنا عيسى بن محمد حدثنا أحمد بن محمد بن المعلى الدمشقي حدثنا سليمان بن عبد الرحمن حدثنا أبو محمد القميمي عبد الله بن عبد الرحمن عن الأعمش عن أبي إسحاق عن البراء قال: قال رسول الله صلى الله عليه وسلم السرى نهر',NULL,'26',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (40,'٤١- حدثني أحمد بن صالح الذارع الواسطي حدثنا عبد الله بن حبيب المقرئ الجرجرائي حدثنا عبد الله بن الأجلح عن الأعمش عن أبي إسحاق عن البراء قال: قال رسول الله صلى الله عليه وسلم إذا التقى المسلمان فتصافحا لم يتفرقا حتى يغفر لهما',NULL,'26',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (41,'٤٢- حدثنا محمد بن محمد بن سليمان حدثنا عبد الله بن محمد أبو زرعة حدثنا عبد الله بن يحيى الثقفي حدثنا عبد الواحد بن زياد عن الأعمش عن أبي إسحاق عن البراء قال اشترى أبو بكر بن عازب رجلا فذكر ⦗٢٧⦘ الحديث',NULL,'26',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (42,'٤٣- حدثنا أبو بشر محمد بن عمران بن الجنيد حدثنا يعقول الدشتكي حدثنا عبد الحميد الحماني حدثنا الأعمش عن أبي إسحاق عن النعمان بن بشير قال: قال رسول الله صلى الله عليه وسلم إن أدنى أهل النار عذابا رجل عليه نعلان من نار يغلي منهما دماغه ولا يرى أن في النار أحد أشد عذابا منه',NULL,'27',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (43,'٤٤- حدثنا ابن أبي صالح حدثنا أحمد بن سنان حدثنا أبو يحيى الحماني الأعمش مثله',NULL,'27',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (44,'<span data-type="title" id=toc-24>أبو إسحاق عن الأسود</span>
INSERT INTO [b26577] VALUES (45,'٤٦- حدثنا أزهر حدثنا محمد بن بكير حدثنا أبو بكر بن عياش وحدثنا محمد بن يحيى حدثنا أحمد بن منيع حدثنا أبو بكر مثله',NULL,'27',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (46,'٤٧- حدثنا ابن إسحاق المسرحي (١) حدثنا لوين حدثنا حبان عن الأعمش عن أبي إسحاق عن الأسود عن عائشة قالت كان النبي صلى الله عليه وسلم لا يتوضأ بعد الغسل (٢)
INSERT INTO [b26577] VALUES (47,'٤٨- حدثنا عبدان حدثنا أبو بكر بن أبي شيبة وحدثنا عبد الله بن محمد بن زكريا وأبو يحيى قالا حدثنا سهل حدثنا حفص بن غياث عن الأعمش عن أبي إسحاق عن أبي الأحوص عن عبد الله قال النبي صلى الله عليه وسلم الإسلام بدأ غريبا وسيعود كما بدأ فطوبى للغرباء',NULL,'27',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (48,'٤٩- حدثنا عبدان حدثنا أبو بكر حدثنا حفص عن الأعمش عن أبي إسحاق عن أبي الأحوص عن عبد الله قال إذا أراد أن يطلق للسنة',NULL,'28',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (49,'!
INSERT INTO [b26577] VALUES (50,'٥١- وحدثنا محمد بن يحيى بن منده حدثنا الحسين الطحان حدثنا ابن إدريس عن الأعمش عن أبي إسحاق عن أبي الأحوص عن عبد الله عن النبي صلى الله عليه وسلم في التشهد',NULL,'28',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (51,'٥٢- وحدثنا أبو بكر البزار حدثنا إبراهيم بن عبد الله بن محمد العبسي قال حدثنا سعيد بن عمر الأشعثي حدثنا عبثر عن الأعمش عن أبي إسحاق عن أبي الأحوص عن عبد الله قال علمنا رسول الله خطبة التشهد وخطبة الحاجة',NULL,'28',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (52,'٥٣- حدثنا عمر بن سهل حدثنا إبراهيم بن إسحاق السراج حدثنا أبو بكر بن أبي شيبة حدثنا حفص بن غياث عن الأعمش عن أبي إسحاق عن أبي الأحوص عن عبد الله قال: قال رسول الله صلى الله عليه وسلم من أحب لقاء الله أحب الله لقاءه ومن كره لقاء الله كره الله لقاءه',NULL,'28',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (53,'٥٤- حدثنا أبو علي بن الحسن حدثنا جعفر الطيالسي حدثنا مسروق بن المرزبان حدثنا حفص عن الأعمش عن أبي إسحاق عن أبي الأحوص عن عبد الله أن النبي صلى الله عليه وسلم قال مع كل فرحة ترحة',NULL,'29',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (54,'٥٥- حدثنا محمد بن يحيى حدثنا يحيى بن محمد بن أبي عبيدة المسعودي عن أبيه عن جده عن الأعمش عن أبي إسحاق عن أبي الأحوص عن عبد الله أن النبي صلى الله عليه وسلم كان يقول اللهم إني أسألك الهدى والتقى والعفة والعفاف',NULL,'29',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (55,'٥٦- حدثنا أبو بكر البزار ومحمد بن العباس قالا حدثنا أحمد بن يزداد الخياط الكوفي حدثنا عمرو بن عبد الغفار عن الأعمش عن أبي إسحاق عن أبي الأحوص عن عبد الله قال كنا نعد الإمعة هو الرجل يدعى فيأتي ثم لم يدعى. زاد محمد بن العباس وأن الأمعة فيكم اليوم المحقب دينه للناس',NULL,'29',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (56,'٥٧- حدثنا محمد بن أحمد بن معدان حدثنا أبو قلابة حدثنا عبد الرحمن بن المبارك حدثنا عبد العزيز بن مسلم عن الأعمش عن أبي إسحاق عن أبي الأحوص عن عبد الله قال سئل النبي صلى الله عليه وسلم أي العمل أفضل قال الصلاة لوقتها قلت ثم أي؟ قال ثم الجهاد في سبيل الله',NULL,'29',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (57,'٥٨- حدثني أبو بكر محمد بن سعيد الشافعي حدثنا محمد بن غالب بن حرب حدثني عمرو بن حصين حدثنا ابن علاثة حدثنا الأعمش عن أبي إسحاق عن أبي الأحوص عن عبد الله قال: قال رسول الله صلى الله عليه وسلم إذا أراد العبد صلاة من الليل أتاه الملك فقال له قد أصبحت فصل واذكر ربك فيأتيه الشيطان فيقول إن عليك ليل فسوف تقوم فيهم ساعة فإن هو قام فصلى أصبح نشيطا خفيف الجسم قرير العين قد أصاب خيرا وإن هوي الشيطان فنام حتى يصبح فيفح الشيطان فحيحا يقول في أذنيه فيصبح كسلانا حائرا مغبونا ذلك اليوم',NULL,'30',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (58,'٥٩- حدثنا عبد الرحمن بن أبي حاتم حدثنا علي بن عمرو الأنصاري حدثنا يحيى بن سعيد الأموي عن الأعمش عن أبي إسحاق عن أبي جحيفة قال كان رسول الله صلى الله عليه وسلم ذات يوم قاعدا وحوله قوم يصنعون أشياء يكرهه من كلام ولغط فقيل يا رسول الله ولا تنهاهم؟ فقال لو نهيتهم عن المجون لأرسل بعضهم أن يأتيه وإن لم يكن له حاجة',NULL,'30',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (59,'٦٠- أخبرنا إسحاق بن أحمد حدثنا عبيد الله أبو زرعة حدثنا سعيد بن عمرو الأشعثي حدثنا عبثر عن الأعمش عن أبي إسحاق عن أبي الأحوص عن عبد الله قال: قال رسول الله صلى الله عليه وسلم ما صلاة أثقل على المنافقين من صلاة العشاء والفجر ولو يعلمون ما فيهما لأتوهما ولو حبوا',NULL,'30',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (60,'٦١- حدثنا محمد بن أحمد بن عصام الرازي حدثنا خلف بن محمد الواسطي حدثنا معلى وعبد الرحمن حدثنا سليمان عن الأعمش عن أبي إسحاق عن الهجري عن أبي الأحوص عن عبد الله عن النبي صلى الله عليه وسلم قال ليس المسكين بالذي ترده التمرة والتمرتان ولكن المسكين الذي لا يسأل الناس شيئا ولا يعلم بمكانه فيعطى',NULL,'30',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (61,'٦٢- حدثنا محمد بن أحمد بن عصام الرازي حدثنا موسى بن نصر حدثنا مهران حدثنا سفيان عن أبي إسحاق عن أبي عبيدة عن عبد الله أنه قال في خطبة الحاجة في النكاح وغيره إن الحمد لله نستعينه ونستغفره ونعوذ بالله من شرور أنفسنا من يهده الله فلا مضل له ومن يضلل فلا هادي له , فذكر الحديث
INSERT INTO [b26577] VALUES (62,'٦٣- أخبرنا أبو يعلى حدثنا محمد بن أبي بكر المقري حدثنا عثام (١) بن علي عن الأعمش عن أبي إسحاق عن أبي عبيدة عن عبد الله قال: انتهيت إلى أبي جهل وهو صريع وعليه بيضة ومعه سيف حي. ومعي سيف رث فَجَعَلْتُ أَنْقُفُ رَأْسَهُ بِسَيْفِي وَأَذْكُرُ نَقْفًا كَانَ يَنْقُفُ رَأْسِي بِمَكَّةَ، حَتَّى ضَعُفَتْ يَدُهُ وَأَخَذْتُ سَيْفَهُ فَرَفَعَ رَأْسَهُ فَقَالَ: عَلَى مَنْ كَانَتِ الدَّبْرَةُ؟ عَلَيْنَا أَوْ لَنَا؟ أَلَسْتَ رُوَيْعِيَّنَا بِمَكَّةَ؟ قَالَ: فَقَتَلْتُهُ، ثُمَّ أَتَيْتُ النَّبِيَّ صَلَّى الله عَلَيْهِ وسَلَّمَ فَقُلْتُ: قَتَلَ الله أَبَا جَهْلٍ، قَالَ: آللَّهِ الَّذِي لاَ إِلَهَ إِلاَّ هُوَ قَتَلْتَهُ؟ فَاسْتَحْلَفَنِي ثَلاَثَ مَرَّاتٍ، ثُمَّ قَامَ مَعِي إِلَيْهِمْ فَدَعَا عَلَيْهِمْ (٢) .
INSERT INTO [b26577] VALUES (63,'٦٤- حدثني عبد الرحمن بن الحسن حدثنا الصفاري حدثنا موسى بن داود حدثنا حفص عن الأعمش عن أبي إسحاق عن أبي عبيدة عن عبد الله قال: قال رسول الله صلى الله عليه وسلم: ارحم من في الأرض يرحمك من في السماء.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (64,'٦٥- حدثنا محمد بن أحمد بن عصام الرازي حدثنا موسى بن نصر أخبرنا مهران عن سفيان عن أبي إسحاق عن أبي عبيدة عن عبد الله رَضِيَ اللَّهُ عَنْهُ أَنَّهُ قَالَ فِي خُطْبَةِ النِّكَاحِ وغيره: إِنَّ الْحَمْدَ لِلَّهِ [نَحْمَدُهُ وَ] (١) نَسْتَعِينُهُ وَنَسْتَغْفِرُهُ، وَنَعُوذُ بِاللَّهِ مِنْ شُرُورِ أَنْفُسِنَا، مَنْ يَهْدِه اللَّهُ فَلاَ مُضِلَّ لَهُ، وَمَنْ يُضْلِلْ فَلاَ هَادِيَ لَهُ، وَأَشْهَدُ أَنْ لاَ إِلَهَ إِلاَّ اللَّهَ وَاشْهَدُ أَنَّ مُحَمَّدًا عَبْدُهُ وَرَسُولُهُ {وَاتَّقُوا اللَّهَ الَّذِي تَسَاءَلُونَ بِهِ وَالأَرْحَامَ إِنَّ اللَّهَ كَانَ عَلَيْكُمْ رَقِيبًا}
INSERT INTO [b26577] VALUES (65,'٦٦- حدثنا جبير بن هارون حدثنا علي بن محمد الطنافسي حدثنا يحيى بن آدم حدثنا ابن أبي زائدة عن الأعمش عن أبي إسحاق عن أبي عبيدة عن عبد الله قال ما سمعت مناشدا يناشد حقا له أشد مناشدة من محمد صلى الله عليه وسلم ربه يوم بدر جعل يقول: اللهم إني أنشدك عهدك ووعدك اللهم إني أنشدك ما وعدتني اللهم إن تهلك هذه العصابة لا تعبد ثم التفت إلينا كأن شق وجهه القمر فقال كأنما أنظر إلى مصارع القوم عشية.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (66,'٦٧- حدثنا على بن الصباح حدثنا إبراهيم بن عامر حدثنا أبي حدثنا النعمان عن عيسى بن الضحاك عن الأعمش مثله',NULL,'32',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (67,'٦٨- حدثنا عبيد الله بن أحمد بن عقبة حدثنا عبد الله بن عبد الصَّمَد بن أبي خداش حدثنا أبو معاوية عن الأعمش عن أبي إسحاق عن أبي عبيدة قال سئل عبد الله ما الدعاء الذي دعوت به حين قال لك رسول الله صلى الله عليه وسلم سل تعطه؟ قال قلت اللهم إني أسألك إيمانا لا يرتد ونعيما لا ينفد ومرافقة نبيك محمدا في أعلى درج جنة الخلد',NULL,'32',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (68,'٦٩- حدثنا محمد بن يحيى حدثنا عبيد الله بن عمر حدثنا جرير عن الأعمش عن أبي إسحاق عن مسلم بن نذير عن حذيفة عن النبي صلى الله عليه وسلم في الإزار.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (69,'٧٠- حدثنا عبدان حدثنا عثمان بن أبي شبيب والعباس بن عبد العظيم قالا حدثنا ابن أبي عبيدة عن أبيه عن الأعمش عن أبي إسحاق عن مسلم بن نذير عن حذيفة عن النبي صلى الله عليه وسلم قصة الغار.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (70,'٧١- حدثنا عبدان قال قرأت على إبراهيم بن سعيد الجوهري حدثنا أبو أسامة عن الأعمش عن أبي إسحاق عن هبيرة عن عبد الله قال أخذت من في رسول الله صلى الله عليه وسلم سبعين سورة.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (71,'٧٢- حدثنا أبو يحيى الرازي حدثنا سهل بن عثمان حدثني وكيع حدثنا الأعمش عن أبي إسحاق عن عبد الرحمن بن زيد عن ابنة لخباب قالت خرج خباب في سرية قالت فكان رسول الله صلى الله عليه وسلم يتعاهدنا حتى يحلب عنزا لنا في جفنة لنا قالت فتطفح الجفنة حتى تفيض قالت فلما قدم خباب حلبها فعاد حلابها الأول قالت فقلنا له كان رسول الله صلى الله عليه وسلم يحلبها فتمتلئ حتى تفيض فلما حلبتها نقص حلابها',NULL,'33',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (72,'٧٣- حدثنا عبدان حدثنا طالوت بن عباد حدثنا عبد الواحد بن زياد حدثنا الأعمش عن أبي إسحاق عن مصعب بن سعد عن أبيه قال قال رسول الله صلى الله عليه وسلم عجبت للمؤمن يؤجر في كل شيء فإن أصابه خير حمد الله وإن أصابته مصيبة حمد الله وأجر عليها إنه يؤجر في كل شيء حتى اللقمة يرفعها إليه.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (73,'٧٤- حدثنا محمد بن يحيى حدثني ابن أبي عبيدة عن أبيه عن جده عن ⦗٣٤⦘ الأعمش مثله.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (74,'٧٥- حدثني بيان بن أحمد القطان وأبو يعلى وابن مكرم قالوا حدثنا داود بن رشيد حدثنا علي بن هاشم عن الأعمش عن أبي إسحاق عن مصعب بن سعد عن أبيه قال: قال رسول الله صلى الله عليه وسلم على كل خلة يطبع المؤمن إلا الخيانة والكذب',NULL,'34',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (75,'٧٦- حدثنا الوليد بن أبان حدثنا حاتم بن يونس حدثنا داود بن رشيد حدثنا علي بن هاشم قال: سمعت الأعمش يحدث عن سلمة بن كهيل عن مصعب بن سعد عن أبيه عن النبي صلى الله عليه وسلم مثله أخطأ فيه حاتم والصحيح عن أبي إسحاق',NULL,'34',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (76,'٧٧- حدثنا محمد بن محمد بن سليمان حدثنا محمد بن الصباح حدثنا نوح بن دراج عن الأعمش عن أبي إسحاق عن هانئ بن هانئ قال استأذن عمار على علي فقال له ائذنوا له فإنس سمعت رسول الله صلى الله عليه وسلم يقول مرحبا بالطيب المطيب',NULL,'34',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (77,'٧٨- حدثنا محمد بن يحيى بن حدثنا أبو كريب ونصر بن علي قالا حدثنا عثام عن الأعمش عن أبي إسحاق عن هانئ بن هانئ عن علي قال قال النبي صلى الله عليه وسلم ملئ عمار إيمانا إلى مشاشه',NULL,'34',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (78,'٧٩- حدثنا جعفر بن أحمد بن سيار القطان حدثنا عبد الحميد بن سيار حدثنا علي بن هاشم عن الأعمش عن أبي إسحاق عن وهب بن جابر عن عبد الله بن عمرو قال: قال النبي صلى الله عليه وسلم كفى بالمرء إثما أن يضيع من يقوت',NULL,'35',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (79,'٨٠- حدثنا محمد بن العباس حدثنا عمرو الأزدي حدثنا وكيع مثله',NULL,'35',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (80,'٨١- حدثنا عبد الله بن محمد بن ناجية حدثنا ابن أشكيب حدثنا أحمد بن يونس حدثنا أبو شهاب الخياط حدثنا الأعمش عن أبي إسحاق عن حارثة قال دخلنا على خباب وقد أكبوا في بطنه سبع كباب فقال لولا أن النبي صلى الله عليه وسلم قال لا يتمنى أحدكم الموت لتمنيته',NULL,'35',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (81,'٨٢- حدثنا محمد بن حمزة حدثنا صالح بن الهيثم الطحان بواسط حدثنا ثوبان بن سعيد حدثنا أبو معاوية حدثنا الأعمش عن أبي إسحاق عن حارثة بن مضرب قال أتينا خبابا نعوده فبكى قلنا ما يبكيك؟ قال خفت أن يحبط أجرنا مع رسول الله صلى الله عليه وسلم لما أصبنا بعده في الدنيا',NULL,'35',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (82,'٨٣- حدثنا العضل بن محمد بن عقيل النيسابوري حدثنا أبو الأزهر حدثنا ابن نمير عن الأعمش عن أبي إسحاق عن الحارث عن علي قال قال النبي صلى الله عليه وسلم عفوت لكم عن صدقة الخيل والرقيق',NULL,'35',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (83,'٨٤- حدثنا إسحاق بن إبراهيم بن جميل حدثنا أحمد بن منيع حدثنا أبو ⦗٣٦⦘ معاوية عن الأعمش عن أبي إسحاق عن حارثة بن مضرب قال قال عبد الله ابن النواحة سمعت رسول الله صلى الله عليه وسلم يقول لولا أنك رسول لقتلتك',NULL,'35',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (84,'٨٥- حدثنا محمد بن يحيى حدثنا ابن أخي يحيى بن عيسى الرملي حدثنا يحيى بن عيسى عن الأعمش عن أبي إسحاق عن حارثة بن مضرب عن خباب قال شكونا إلي النبي صلى الله عليه وسلم الرمضاء فلم يشكنا',NULL,'36',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (85,'٨٦- حدثني الوليد بن أبان حدثنا محمد بن سعيد بن بلج حدثنا عبد السلام بن عامر حدثنا عبد الرحمن بن عبد الله حدثنا أبو جعفر الرازي عن الأعمش عن أبي إسحاق عن سعيد بن وهب عن خباب قال شكونا إلى النبي صلى الله عليه وسلم الرمضاء فلم يشكنا وقال إذا زالت الشمش فصلوا',NULL,'36',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (86,'٨٧- حدثنا محمد بن محمد بن سليمان حدثنا محمود بن خالد حدثنا الفريابي حدثنا سفيان عن الأعمش عن أبي إسحاق عن مسروق عن عبد الله قال لا تدعوا خصلة مما أمرتم به إلا أبدلكم الله بها ما هو أشد عليكم منها',NULL,'36',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (87,'٨٨- حدثنا علي بن العباس المقانعي حدثنا يحيى بن حسان حدثنا مالك بن سعير عن الأعمش عن أبي صالح عن أبي هريرة أو عن أبي سعيد وعن أبي إسحاق عن أبي مسلم الأغر عن أبي هريرة وأبي سعيد وعن حبيب عن أبي مسلم عن أبي هريرة وأبي سعيد قالا قال رسول الله صلى الله عليه وسلم إن الله يمهل حتى إذا ذهب وسط الليل نزل إلى السماء الدنيا فيقول هل من مستغفر فيغفر له هل من داع يستجاب له هل من مذنب فأتوب عليه حتى ينشق الفجر ثم يرتفع',NULL,'36',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (88,'٨٩- حدثنا عبدان حدثنا محمد بن عبد الله القرشي حدثنا مالك بن سعير عن الأعمش عن أبي إسحاق عن أبي مسلم عن أبي هريرة وأبي سعيد عن النبي صلى الله عليه وسلم نحوه',NULL,'36',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (89,'٩٠- حدثنا محمد بن محمد بن سليمان حدثنا عبد الله بن محمد أبو رفاعة حدثنا عبد الله بن يحيى الثقفي حدثنا عبد الواحد بن زياد عن الأعمش عن أبي إسحاق عن عبد الله بن أبي بصير عن أبي بن كعب قال صلى رسول الله صلى الله عليه وسلم فقال أشاهد فلان. فذكر الحديث',NULL,'37',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (90,'٩١- حدثنا حامد بن شعيب حدثنا عبد الله بن عمر بن أبان حدثنا عبدة بن سليمان عن الأعمش عن أبي إسحاق عن هبيرة بن عامر قال قال عبد الله لقد قرأت على رسول الله صلى الله عليه وسلم بضعا وسبعين سورة وإن زيد بن ثابت له دواتين يلعب مع الغلمان.',NULL,'37',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (91,'٩٢- حدثنا محمد بن جرير الطبري حدثنا يحيى بن إبراهيم بن أبي عبيدة حدثني أبي عن أبيه عن جده عن الأعمش عن أبي إسحاق عن عامر بن سعد قال دخلت على قرظة بن كعب وأبي مسعود الأنصاريّ وقراب بن حبان وناس من أصحاب رسول الله صلى الله عليه وسلم في عرس وإذا عندهم غناء وصوت ولعب فقلت سبحان الله أن لو عند غيركم رأينا هذا لوجدنا عليه فقالوا لا عليك أيها الرجل قد رخص لكم في هذا عند الفرح لو الشيء يكون في.. .. (١) غدا لحزن
INSERT INTO [b26577] VALUES (92,'٩٣- حدثنا محمد بن العباس بن أيوب حدثنا يعقوب الدورقي حدثنا وكيع حدثنا الأعمش عن أبي إسحاق عن عبد خير عن علي قال لو كان الدين بالرأي لكان المسح على باطن الخفين فقد رأيت النبي صلى الله عليه وسلم يمسح على ظاهرهما',NULL,'37',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (93,'٩٤- حدثنا مهران حدثنا أبو زرعة حدثنا عمر بن حفص بن غياث حدثنا أبي حدثنا الأعمش حدثني أبو إسحاق عن عبد خير قال قال علي ما كنت أرى إلا أن بطن القدمين بالمسح أحق من ظهورهما حتى رأيت رسول الله صلى الله عليه وسلم يمسح على ظهر القدمين قال عمر كان أبي يحدث به حفظا الخفين فلما نظر في الكتاب فوجد فيه ⦗٣٨⦘ القدمين',NULL,'37',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (94,'٩٥- حدثنا أبو سعيد الخطابي والوليد بن أبان قالا حدثنا إسحاق بن إبراهيم النهشلي حدثنا جدي سعد بن الصلت عن الأعمش عن أبي إسحاق عن أبي حبيبة الطائي عن أبي الدرداء قال النبي صلى الله عليه وسلم المعتق عند الموت كالمهدي بعد ما شبع',NULL,'38',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (95,'٩٦- حدثنا محمد بن يحيى بن حدثنا ابن أبي عبيدة عن أبيه عن جده عن الأعمش عن أبي إسحاق عن عبيد بن المغيرة عن حذيفة عن النبي صلى الله عليه وسلم قال إني لأستغفر الله في اليوم مائة مرة',NULL,'38',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (96,'٩٧- حدثنا أبو سعيد الخطابي والوليد بن أبان قالا حدثنا إسحاق بن إبراهيم حدثنا سعد حدثنا الأعمش عن أبي إسحاق عن المغيرة بن أبي عبيد عن حذيفة أنه شكا إلى رسول الله صلى الله عليه وسلم ذربا في لسانه على أهله ورقيقه فقال النبي صلى الله عليه وسلم فأين أنت من الاستغفار. إن الاستغفار يذهب ذلك كله وقال رسول الله صلى الله عليه وسلم إني لأستغفر الله في اليوم مائة مرة',NULL,'38',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (97,'٩٨- حدثنا محمد بن عمر بن عبد الله حدثنا أحمد بن منصور حدثنا عمر بن حفص بن غياث حدثنا أبي حدثنا الأعمش عن أبي إسحاق عن أبي مسلم الأغر أنه حدثه عن أبي سعيد وأبي هريرة قالا قال رسول الله صلى الله عليه وسلم قال الله تعالى العزة والكبرياء ردائي فمن نازعني منهما شيئا عذبته',NULL,'38',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (98,'٩٩- أخبرنا إسحاق بن أحمد حدثنا أبو زرعة حدثنا عمر حدثنا محمد بن يحيى حدثني أبو كريب حدثنا وكيع عن الأعمش عن أبي إسحاق عن وهب بن جابر عن عبد الله بن عمرو عن النبي صلى الله عليه وسلم قال كفى بالمرء إثما أن يضيع من يقوت',NULL,'39',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (99,'١٠٠- حدثنا عبد الرحمن بن داود بن منصور حدثنا العباس بن أحمد الأزهر بطرسوس حدثنا مسلم بن سلام بطرسوس حدثنا أبو بكر بن عياش عن الأعمش عن أبي إسحاق عن أبي بكر بن أبي موسى عن البراء قال كان النبي صلى الله عليه وسلم إذا أوى إلى فراشه وضع كفه اليمنى تحت خده الأيمن ثم قال رب قني عذابك يوم تبعث عبادك',NULL,'39',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (100,'١٠١- حدثنا الحسن بن إبراهيم بن بشار حدثنا عبيد الله بن عمر حدثنا عبد الرحمن بن مهدي عن سفيان عن الأعمش عن أبي إسحاق عن رجل قد سماه عبد الله بن مسعود قال أخذت من في رسول الله صلى الله عليه وسلم سبعين سورة بمكة',NULL,'39',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (101,'١٠٢- حدثنا محمد بن عبيدة حدثنا محمد بن أشكيب حدثنا ابن أبي عبيدة حدثنا أبي عن الأعمش عن أبي إسحاق عن عبد الرحمن بن يزيد قال كان عبد الله يمحو المعوذتين من مصاحفه ويقول ليستا من كتاب الله',NULL,'39',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (102,'١٠٣- أخبرنا عبد الرحمن بن أبي حاتم حدثنا محمد بن الحسن بن أشكيب الأصغر حدثنا ابن أبي عبيدة عن أبيه عن الأعمش عن أبي إسحاق عن عبد الرحمن قال كان ابن مسعود يحك المعوذتين من المصحف قال الأعمش فحدثني عاصم عن زر عن أبي قال سألنا رسول الله صلى الله عليه وسلم فقال قيل لي ⦗٤٠⦘ فقلت',NULL,'39',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (103,'١٠٤- أخبرنا أبو محمد بن أبي حاتم حدثنا سليمان بن داود القزاز حدثنا ابن نمير عن الأعمش عن أبي إسحاق عن رجل عن علي أنه أتى النبي صلى الله عليه وسلم فقال إن عمك المشرك أو الكافر قد مات قال اذهب فواره',NULL,'40',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (104,'١٠٥- أخبرنا أبو محمد بن أبي حاتم حدثنا أحمد بن سنان حدثنا أبو أسامة عن الأعمش عن أبي إسحاق عن عبدة السوائي قال لغط قوم قريب من النبي صلى الله عليه وسلم فقال بعض القوم يا رسول الله لو أرسلت إليهم فنهيتهم فقال لو أرسلت إليهم فنهيتهم أن يأتوا المجون لأتاه بعضهم وإن لم يكن له به حاجة',NULL,'40',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (105,'١٠٦- حدثني أبو عمرو بن حكيم حدثنا أحمد بن الحصيب حدثنا محمد بن عمرو بن جبلة حدثنا أبو الجواب عن عمارة بن رزيق عن الأعمش عن أبي إسحاق عن أبي أسماء عن أنس بن مالك قال: سمعت النبي صلى الله عليه وسلم يقول لبيك حجة وعمرة معا',NULL,'40',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (106,'<span data-type="title" id=toc-25>الزهري عن أبي إسحاق السبيعي</span>
INSERT INTO [b26577] VALUES (107,'<span data-type="title" id=toc-26>الحكم بن عتبة عن الأعمش</span>
INSERT INTO [b26577] VALUES (108,'<span data-type="title" id=toc-27>الأعمش عن الحكم بن عتيبة</span>
INSERT INTO [b26577] VALUES (109,'<span data-type="title" id=toc-28>قتادة بن دعامة عن أيوب السختياني</span>
INSERT INTO [b26577] VALUES (110,'١١١- حدثنا أحمد بن روح الشعراني حدثنا الحسن بن الحكم بن مينا الرقي حدثنا مسلم حدثنا همام عن قتادة عن أيوب عن نافع عن ابن عمر أن النبي صلى الله عليه وسلم قطع في مجن قيمته ثلاثة دراهم',NULL,'41',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (111,'١١٢- حدثنا أحمد بن روح قال: حدثنا محمد بن عبد الله السمسار وحدثنا محمد بن العباس قال: حدثنا محمد بن العباس قال: حدثنا مُحَمَّدُ بْنُ يَزِيدَ الإِسْقَاطِيُّ قالا حدثنا يحيى بن كثير حدثنا شعبة عن قتادة عن سعيد بن جبير عن ابن عمر أن النبي صلى الله عليه وسلم نهى عن نبيذ الجر قال شعبة فقلت لقتادة من حدثك؟ قال حدثنيه أيوب فأتيت أيوب فقلت من حدثك؟ قال حدثنيه أبو بشر فأتيت أبا بشر فقلت من حدثك؟ فقال حدثنيه سعيد بن جبير عن ابن عمر',NULL,'42',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (112,'١١٣- كتب إلي محمد بن المنيب الأربعاني حدثنا محمد بن يزيد بن حكيم بطرسوس حدثنا محمد بن المتوكل عن عبد الوهاب عن معمر عن قتادة عن أيوب عن أبي قلابة عن أبي الأشعث عن أبي أسماء عن شداد بن أوس قال: قال رسول الله صلى الله عليه وسلم أخوف ما أخاف على أمتي أئمة يصلون وإذا وضع فيهم السيف لم يسترفع عنهم إلى يوم القيامة',NULL,'42',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (113,'<span data-type="title" id=toc-29>أيوب السختياني عن قتادة</span>
INSERT INTO [b26577] VALUES (114,'١١٥- حدثنا ابن الباغندي حدثنا ابن الستل حدثنا أبي حدثنا حمَّاد بن سَلَمة عن أيوب عن قتادة عن أنس قال: قال النبي صلى الله عليه وسلم من عليه صلاة فليصلها إذا ذكرها.',NULL,'42',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (115,'١١٦- كتب إليَّ عبد الله بن حمدان حدثنا أحمد بن سعيد حدثنا ابن وهب أخبرني الحارث بن نبهان عن أيوب السختياني عن قتادة عن أنس أن النبي صلى الله عليه وسلم لما أراد أن يكتب إلى كسرى وقيصر والملوك قيل إنهم لا يقبلون الكتب والصحف إلا بخواتيم فصنع النبي صلى الله عليه وسلم خاتما من ورق كأني أنظر إلى بصيصه في ظلمة ⦗٤٣⦘ الليل.',NULL,'42',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (116,'١١٧- حدثنا أحمد بن الحسن بن عبد الملك حدثنا أحمد بن المقدام حدثنا الطفاوي حدثنا أيوب عن قتادة عن موسى بن سلمة قال قلت لابن عباس إنا إذا كنا بمكة صلينا أربعا فإذا رجعنا إلى رحلنا صلينا ركعتين فقال تلك سنة أبي القاسم صلى الله عليه وسلم وإن زعمتم',NULL,'43',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (117,'١١٨- حدثنا محمد بن أحمد بن معدان حدثنا جعفر الطيالسي حدثنا سعيد بن أبي الربيع حدثنا يحيى بن عثمان القرشي حدثنا أيوب عن قتادة عن زرارة عن عمران بن حصين قال صلى النبي صلى الله عليه وسلم إحدى صلاتي الظهر أو العصر فقال أيكم قرأ {سبح اسم ربك} فقال رجل أنا يا رسول الله قال قد علمت أن رجلا خالجنيها',NULL,'43',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (118,'١١٩- حدثنا أحمد بن عمرو بن عبد الخالق حدثنا ابن أبي حاتم حدثنا سليمان بن حرب حدثنا حماد قال قلت لأيوب كيف تحفظ من قول الحسن أمرك في يدك؟ فقال لا أحفظ عن غير الحسن ثم قال أستغفر الله حدثنيه قتادة عن كثير بن أبي كثير عن أبي هريرة عن رسول الله صلى الله عليه وسلم مثل قول الحسن',NULL,'43',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (119,'١٢٠- ذكر العباس بن حمدان حدثنا عباد بن زياد حدثنا سفيان بن عيينة عن أيوب عن قتادة عن أبي حسان قال قال ابن عباس أشهد أن السلف المضمون إلى أجل مسمى قد أحل له وذكر في الكتاب قال الله عز وجل {يا أيها الذين آمنوا إذا تداينتم بدين إلى أجل مسمى}',NULL,'43',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (120,'١٢١- حدثني علي بن سعد العسكري حدثنا أحمد بن إسحاق بن صالح حدثنا عاصم بن علي حدثنا يزيد بن إبراهيم حدثنا أيوب عن قتادة عن معاذة العدوية أن امرأة سألت عائشة أتقضي الحائض الصلاة فقالت أحرورية أنت كنا مع رسول الله صلى الله عليه وسلم فلم نكن نقضي ولم نؤمر بذلك حدثنا به المروزي حدثنا عاصم بن علي حدثنا يزيد بن إبراهيم حدثنا أيوب عن معاذة وهو الصحيح',NULL,'44',NULL,NULL,'0');
