INSERT INTO [b26577] VALUES (124,'١٢٥- حدثنا عبدان حدثنا يحيى وعمرو ابنا عثمان قالا حدثنا بقية حدثنا شعبة عن خالد وابن عون عن ابن سيرين عن أبي هريرة أن النبي صلى الله عليه وسلم سجد في مسجد في السهو بعد التسليم',NULL,'45',NULL,NULL,'0');
