INSERT INTO [b26577] VALUES (152,'١٥٣- حدثنا عيسى حدثنا عبد العزيز حدثني أبي عن جدي عن سفيان عن حماد بن سلمة عن ثابت عن أنس قال رأيت رسول الله صلى الله عليه وسلم يصلي على بساط',NULL,'52',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (153,'١٥٤- حدثنا أبو علي بن إبراهيم حدثنا الأبار حدثنا محمد بن إسماعيل الواسطي حدثنا أحمد بن عيسى التنيسي حدثنا عمران بن أبي سلمة عن مصعب بن ماهان عن الثوري عن حماد بن سلمة عن عبيد الله بن أبي بكر عن أنس قال: قال رسول الله صلى الله عليه وسلم هذا ابن آدم ثم وضع يده تحت ذقنه ثم بسط يده فقال هذا هكذا',NULL,'52',NULL,NULL,'0');
