INSERT INTO [b26577] VALUES (160,'<span data-type="title" id=toc-63>مكحول عن عمرو بن شعيب ـ عمرو بن شعيب عن مكحول</span>
INSERT INTO [b26577] VALUES (161,'١٦٢- حدثنا إسماعيل القطان حدثنا حاتم بن الليث الجوهري حدثنا يحيى بن سويد حدثنا الأصمعي عن سليم بن حيان عن قتادة عن حميد بن عبد الرحمن حدثنا ابن عباس عن عمر عن أبي بكر الصديق قال قام فينا خطبنا فقال سمعت رسول الله صلى الله عليه وسلم يقول إنه لم يعط أحد من الناس شيئا أفضل من المعافاة بعد الثلاثين ألا إن الصدق والبر في الجنة ألا وإن الكذب والفجور في النار',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (162,'١٦٣- حدثنا محمد بن أحمد بن عصام الرازي حدثنا محمد بن عبيد بن عتبة حدثنا أبو غسان حدثنا عبد السلام بن حرب عن عبد الله بن بشر عن الزهري عن سعيد بن المسيب عن عثمان بن عفان عن أبي بكر الصديق قال سألت رسول الله صلى الله عليه وسلم عن نجاة هذا الأمر فقال من قبل الكلمة التي عرضتها على عمر فردها علي فهي نجاة',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (163,'١٦٤- حدثنا محمد بن أحمد بن عصام الرازي حدثنا محمد بن عبيد بن عتبة حدثنا سليمان بن عبد الرحمن عن محمد بن عبد الله بن أخي الزهري عن الزهري عن سعيد بن المسيب عن عبد الله بن عمر عن عثمان بن عفان عن أبي بكر الصديق عن النبي صلى الله عليه وسلم مثله',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (164,'١٦٥- حدثنا أبو صالح عبد الرحمن بن أحمد بن يحيى حدثنا إبراهيم بن أحمد ⦗٥٦⦘ البالسي حدثنا علي بن الحسن بن شقيق حدثنا أبو حمزة السكري عن عاصم بن كليب عن عبد الله بن الزبير عن عمر بن الخطاب عن أبي بكر الصديق قال: سمعت رسول الله صلى الله عليه وسلم يقول ما بعث الله نبيا إلا وقد أمَّه أمته',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (165,'١٦٦- حدثنا حاجب حدثنا شعيب بن بكار حدثنا أبو صالح حدثنا ابن المبارك عن معاوية بن يحيى عن علي بن عبد الله عن القاسم عن أسماء بنت أبي بكر عن أم رومان قالت رآني أبو بكر أتميل في الصلاة فزجرني زجرة كدت أن أنصرف من صلاتي ثم قال: قال رسول الله صلى الله عليه وسلم إذا قام أحدكم في صلاته فليسكن أطرافه ولا يتميل تميل اليهود فإن سكون الأطراف في الصلاة من تمامها',NULL,'56',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (166,'١٦٧- حدثنا محمد بن أحمد بن عصام حدثنا محمد بن مسلمة الواسطي حدثنا محمد بن حرب المكي حدثنا الليث عن بكير بن الأشج عن نابل صاحب العباء عن ابن عمر عن صهيب عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم أعوذ بالله من علم لا ينفع وقلب لا يخشع وبطن لا يشبع ودعاء لا يسمع',NULL,'56',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (167,'١٦٨- حدثنا أبو يحيى الرازي حدثنا الهيثم بن يمان حدثنا أيوب بن سيار عن محمد بن المنكدر عن جابر بن عبد الله عن أبي بكر الصديق عن بلال أن النبي صلى الله عليه وسلم قال أسفروا بالفجر فإنه أعظم للأجر',NULL,'56',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (168,'١٦٩- حدثنا البزار حدثنا محمد بن مرزوق حدثنا أبو داود عن شعبة عن سعد بن إبراهيم عن أبيه عن ابن عباس عن عبد الرحمن بن عوف عن عمر بن الخطاب أن النبي صلى الله عليه وسلم قد رجم',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (169,'١٧٠- أخبرنا أبو خليفة حدثنا أبو الوليد عن حماد بن سلمة عن حميد عن أنس عن عبادة بن الصامت قال قال أبي بن كعب قال رسول الله صلى الله عليه وسلم أنزل القرآن على سبعة أحرف',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (170,'١٧١- حدثنا البزار حدثنا بشر بن معاذ حدثنا هشيم حدثنا الزهري عن عبيد الله بن عبد الله بن عتبة عن ابن عباس عن عبد الرحمن بن عوف عن عمر بن الخطاب قال إن رسول الله صلى الله عليه وسلم قد رجم ورجمنا بعده',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (171,'١٧٢- حدثنا البزار حدثنا هارون بن سفيان حدثنا أسيد بن زيد حدثنا عمرو بن أبي المقدام حدثنا عمران بن مسلم عن سويد بن علقمة عن بلال عن أبي بكر أن النبي صلى الله عليه وسلم قال لا يتوضأ من طعام',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (172,'١٧٣- رأيت في كتاب عند إسماعيل بن إسحاق القاضي حدثنا علي بن المديني حدثنا سهل بن عثمان حدثنا ابن أبجر عن أبيه عن واصل بن حيان أن أبا وائل قال خطبنا عمار بن ياسر فأبلغ وأوجز فقلنا يا أبا اليقظان لو.. .. (١) فقال إني سمعت رسول الله صلى الله عليه وسلم يقول طول صلاة الرجل وقصر خطبته.. .. .. سحرا
INSERT INTO [b26577] VALUES (173,'١٧٤- قال وحدثني الحسن بن يمان حدثنا سهل بن عثمان حدثني علي بن عبد الله بن جعفر حدثنا أبي أخبرنا عبد الله بن دينار عن ابن عمر أن النبي صلى الله عليه وسلم كان يقول في خطبته أما بعد وفي كتبه أما بعد',NULL,'58',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (174,'<span data-type="title" id=toc-65>جعفر بن محمد عن سفيان الثوري</span>
INSERT INTO [b26577] VALUES (175,'<span data-type="title" id=toc-66>سفيان الثوري عن جعفر بن محمد</span>
INSERT INTO [b26577] VALUES (176,'١٧٧- حدثنا أبو علي حدثنا أحمد بن علي حدثنا محمد بن إسماعيل بن عامر الواسطي حدثنا أحمد بن عيسى حدثنا عمرو بن أبي سلمة حدثنا مصعب بن ماهان عن سفيان الثوري عن ابن لهيعة عن خالد بن أبي عمران قال سألت سليمان بن يسار عن رجل يشهد عليه رجل انه طلق امرأته بأفريقية ثلاثا وبالمدينة ثلاثا وشهد آخر أنه طلقها بالشام ثلاثا لا يشهد رجل منهم على شهادة صاحبة قال ينتزع منه ⦗٥٩⦘ امرأته',NULL,'58',NULL,NULL,'0');
