INSERT INTO [b26577] VALUES (196,'<span data-type="title" id=toc-80>داود بن أبي هند عن مسعر</span>
INSERT INTO [b26577] VALUES (197,'١٩٨- حدثنا الوليد بن أبان حدثنا إبراهيم بن يوسف حدثنا الفضل بن زياد حدثني هارون بن عبد الله حدثنا محمد بن الحسن بإسناده مثله',NULL,'64',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (198,'<span data-type="title" id=toc-81>مبارك بن فضالة عن عبيد الله بن عمر</span>
INSERT INTO [b26577] VALUES (199,'٢٠٠- حدثنا ابن مكرم حدثنا حجاج بن المساعد حدثنا عمرو بن عاصم عن مبارك بن فضالة عن عبيد الله عن نافع عن ابن عمر قال: قال رسول الله صلى الله عليه وسلم
INSERT INTO [b26577] VALUES (200,'٢٠١- حدثني الوليد بن أبان حدثنا محمد بن الحسن بن يونس حدثنا الحر بن مالك حدثنا مبارك بن فضالة عن عبيد الله بن عمر عن نافع عن ابن عمر أن النبي صلى الله عليه وسلم نهى عن القزع',NULL,'65',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (201,'٢٠٢- حدثني أبو سعيد الخطابي حدثنا يعقوب بن سفيان حدثنا آدم حدثنا مبارك بن فضالة عن عبيد الله بن عمر عن نافع عن ابن عمر قال: قال رسول الله صلى الله عليه وسلم من جر ثوبه من الخيلاء لم ينظر الله إليه يوم القيامة',NULL,'65',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (202,'٢٠٣- حدثنا البرذعي حدثنا الربيع بن سليمان حدثنا أسد بن موسى حدثنا مبارك بن فضالة عن عبيد الله بن عمر عن نافع عن ابن عمر كان النبي صلى الله عليه وسلم إذا أراد أن ينام وهو جنب غسل وجهه ويديه وذراعيه وأخر رجليه للغسل',NULL,'65',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (203,'٢٠٤- حدثني حاجب حدثنا عباد بن الوليد العنزي حدثنا سفيان بن خالد الضبي حدثنا مبارك بن فضالة عن عبيد الله بن عمر عن نافع عن ابن عمر نهى النبي صلى الله عليه وسلم عن تلقي الأجلاب حتى يهبط بها الأسواق',NULL,'65',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (204,'٢٠٥- حدثنا البرذعي حدثنا محمد بن إبراهيم الصوري حدثنا مؤمل حدثنا مبارك عن عبيد الله عن نافع عن ابن عمرقال قال رسول الله صلى الله عليه وسلم صلوا في بيوتكم ⦗٦٦⦘ ولا تتخذوها قبورا',NULL,'65',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (205,'٢٠٦- حدثنا عامر بن إبراهيم بن عامر وحدثنا محمد بن يحيى قالا حدثنا محمد بن عامر حدثنا أبي عن مبارك عن عبيد الله عن نافع عن ابن عمر قال قال النبي صلى الله عليه وسلم الضيافة ثلاثة أيام فما فوق ذلك فهو صدقة',NULL,'66',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (206,'٢٠٧- حدثنا ابن المقري حدثنا حسين بن الحسن المكتب حدثنا إبراهيم بن أيوب عن أبي هانئ عن مبارك بن فضالة عن عبيد الله عن نافع عن ابن عمر أن عمر كان ينهى عن المتعة متعة الحج فقال له أبي بن كعب ليس لك ذاك إنها لفي كتاب الله ما نسخت',NULL,'66',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (207,'٢٠٨- أخبرنا القاسم بن مورك حدثنا علي بن سهل الرملي حدثنا مؤمل حدثنا مبارك عن عبيد الله عن نافع عن ابن عمر قال: قال رسول الله صلى الله عليه وسلم إذا وضع العشاء وأقيمت الصلاة فابدؤوا بالعشاء',NULL,'66',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (208,'٢٠٩- حدثنا عمرو بن أحمد بن عبد الخالق حدثنا أحمد بن محمد بن المعلى حدثنا حفص بن عمار حدثنا عراك عن عبيد الله بن عمر عن نافع عن ابن عمر قال: قال رسول الله صلى الله عليه وسلم إنما أنا عبد آكل كما يأكل العبد',NULL,'66',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (209,'٢١٠- حدثنا ابن المقري حدثنا سعيد بن وهب حدثنا الحوفي حدثنا مبارك عن عبيد الله بن عمر عن نافع عن ابن عمر عن النبي صلى الله عليه وسلم في قوله {يوم يقوم الناس ⦗٦٧⦘ لرب العالمين} قال يقومون في الرشح إلى أنصاف آذاهم',NULL,'66',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (210,'٢١١- حدثنا محمد بن يحيى حدثنا بندار وابن حفص قالا حدثنا يونس بن عبيد الله العميري حدثنا مبارك عن عبيد الله عن نافع عن ابن عمر قال [.. ..] (١) الرأي على الدين فلقد رأيتني يوم أبي جندل والكاتب يكتب بين يدي رسول الله صلى الله عليه وسلم وأهل مكة فرضي رسول الله صلى الله عليه وسلم وأثبت عليهم
INSERT INTO [b26577] VALUES (211,'٢١٢- حدثنا حاجب حدثنا رزق الله بن موسى حدثنا شبابة عن مبارك عن عبيد الله عن نافع عن ابن عمر أنه بلغه أن أبا سعد حرب في الصره',NULL,'67',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (212,'٢١٣- حدثنا ابن المقري حدثنا عقيل حدثنا أبو داود حدثنا مبارك عن عبيد الله عن نافع عن ابن عمر تلقيت التلبية من في رسول الله صلى الله عليه وسلم لبيك اللهم لبيك لبيك لا شريك لك لبيك إن الحمد والنعمة لك لا شريك لك',NULL,'67',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (213,'٢١٤- حدثنا ابن الجنيد حدثنا علي بن الحسن بن أبي عيسى حدثنا محمد بن عبد الملك أبو جابر حدثنا مبارك عن عبيد الله عن نافع عن ابن عمر مقتل عمر بطوله',NULL,'67',NULL,NULL,'0');
