INSERT INTO [b26577] VALUES (415,'٤١٦- حدثنا محمد بن العباس حدثنا نصر بن علي حدثنا أبي حدثنا شعبة عن أيوب عن نافع عن ابن عمر قال قال النبي صلى الله عليه وسلم في النخل إذا أثمرت فثمرتها للبائع إلا أن يشترط المبتاع',NULL,'113',NULL,NULL,'0');
INSERT INTO [b26577] VALUES (416,'٤١٧- حدثنا محمد بن العباس حدثنا نصر حدثنا أبي حدثنا شعبة عن أيوب عن نافع عن ابن عمر قال قال النبي صلى الله عليه وسلم لا تمنعوا نساءكم المساجد',NULL,'113',NULL,NULL,'0');
