-- Insert data for b26658
INSERT INTO [b26658] VALUES (1,'منتقى من السفينة البغدادية

للحافظ أبي طاهر السلفي الأصبهاني
(ت ٥٧٦هـ)

تحقيق
أبي عبد الباري رضا أبوشامة الجزائري

مكتبة ودار ابن حزم
الرياض

الطبعة الأولى
١٤٢٤ هـ - ٢٠٠٣ م
[طبع مع قصيدة من إنشاء السلفي]',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b26658] VALUES (2,'بسم الله الرحمن الرحيم
أنا المسندة الرحلة أم عبد الله عائشة بنت محمد بن عبد الهادي قراءة عليها وأنا أسمع سنة ٨١٤ أنا عبد الله بن الحسين بن أبي التائب الأنصاري أنا مكي بن علان أنا ⦗٥٠⦘ الحافظ أبو طاهر أحمد بن محمد بن أحمد السلفي إجازة
١ - أخبرنا أبو الحسن المبارك بن عبد الجبار بن محمد الصيرفي أنا علي بن أحمد بن علي المؤدب الفالي أنا أبو عبد الله أحمد بن إسحاق بن خربان النهاوندي أنا أبو ⦗٥١⦘ محمد الحسن بن عبد الرحمن بن خلاد الرامهرمزي ثنا عمر بن إسحاق الشيرازي ثنا أبو هارون إسماعيل بن محمد الثقفي ثنا رواد بن الجراح قال قال سفيان الثوري: خذ الحلال والحرام عن المشهورين في العلم وما سوى ذلك فمن المشيخة.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (3,'٢ - أخبرنا أبو محمد الحسن بن عبد الملك بن يوسف المعدل وجعفر بن أحمد بن الحسين اللغوي ببغداد قالا أنا أبو الحسين محمد بن أحمد بن أنيف بن سنيف البلخي أنا أبو الحسين محمد بن عبدان البلخي ثنا علي ⦗٥٣⦘ بن خشرم سمعت سفيان بن عيينة يسأل رجلا ما حرفتك قال طلب الحديث قال بشر أهلك بالإفلاس.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (4,'٣ - قال السلفي فنظمها أبو محمد جعفر بن أحمد السراج:
من كان حرفته كتب الحديث فقد ..... أضحى وأهلوه في الدنيا مفاليسا
لكن عاقبة الإفلاس منزلهم ..... غدا إذا انحشر الناس الفراديسا',NULL,'53',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (5,'٤ - سمعت أبا المعالي ثابت بن بندار بن إبراهيم المقرئ بقراءتي عليه ببغداد قال سمعت أبا القاسم ⦗٥٤⦘ عبد العزيز بن علي بن شكر الوراق الأزجي يقول سمعت أبا بكر محمد بن أحمد بن يعقوب المفيد يقول سمعت أبا بكر عبد الله بن أبي داود السجستاني يقول سمعت أبي أبا داود يقول: الفقه يدور على خمسة أحاديث الحلال بين والحرام بين، وأن رسول الله صلى الله عليه وسلم قال لا ضرر ولا ضرار، وأن رسول الله صلى الله عليه وسلم قال إنما الأعمال بالنيات ⦗٥٥⦘ وإنما لامرئ ما نوى، [وأن رسول الله صلى الله عليه وسلم قال الدين النصيحة] (١)، وأن رسول الله صلى الله عليه وسلم قال ما نهيتكم عنه فاجتنبوه وما أمرتكم به فأتوا منه ما استطعتم.
_________
(١) ما بين المعقوفين ساقط من الأصل، والصواب إثباته.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (6,'٥ - أنشدنا أبو الحسين المبارك بن عبد الجبار بن أحمد الصيرفي ببغداد أنشدنا أبو عبد الله محمد بن علي بن عبد الله الصوري الحافظ وقد كتبت عن جماعة من الحفاظ فما ⦗٥٦⦘ رأيت مثله في الحفظ والإتقان يمدح أصحاب الحديث:
قل لمن عاند الحديث وأضحى ..... عائبا أهله ومن يدعيه
أبعلم تقول هذا أبن لي ..... أم بجهل فالجهل خلق السفيه
أيعاب الذين هم حفظوا الدين ..... من الترهات والتمويه
وإلى قولهم وما قد رووه ..... راجع كل عالم وفقيه
قال السلفي أنشدت هذه الأبيات أولا عن أبي بكر الخطيب الحافظ عن الصوري ثم سمعتها عاليا.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (7,'٦ - أخبرنا أبو محمد جعفر بن أحمد بن الحسين اللغوي ببغداد أنا أبو محمد الحسن بن محمد بن الحسن الحافظ ⦗٥٧⦘ بقراءتي عليه ثنا أبو عمر محمد بن العباس بن حيويه الخزاز ثنا ابن أبي داود ثنا عبد الله بن خبيق قال قال ابن المبارك طلبنا العلم للدنيا فدلنا على ترك الدنيا.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (8,'٧ - فنظمها شيخنا أبو محمد جعفر بن أحمد فقال:
أف لدنيا أقسمت أنها ..... لا تفلت العالم من شركها
لها طلبنا العلم فيما مضى ..... فدلنا العلم على تركها',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (9,'٨ - سمعت أبا محمد جعفر بن أحمد بن الحسين السراج اللغوي ببغداد وحدثنا بحكاية الفضيل بن ⦗٥٨⦘ عياض فنظمها:
إذا كنتم تكتبون الحديث ..... ليلا وفي صبحكم تسمعون
وأفنيتم فيه أعماركم ..... فأي زمان به تعملون.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (10,'٩ - أخبرنا أبو الحسن علي بن محمد بن علي العلاف المقرئ ببغداد أنا أبو الحسن علي بن أحمد بن عمر المقرئ الحمامي ثنا شيخنا أبو طاهر بن أبي هاشم المقرئ ثنا ⦗٥٩⦘ أبو بكر محمد بن علي (١) التوزي ثنا عمر بن شبة ثنا عفان قال قال همام ما حدثت عن قتادة ملحونا فأعربوه فإن قتادة كان لا يلحن.
_________
(١) في الأصل (ابن أبي علي)، والصواب المثبت.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (11,'١٠ - أنشدنا أبو نصر أحمد بن حسنون النرسي ⦗٦٠⦘ أنشدنا أبو حاتم محمد بن عبد الواحد الرازي الحافظ أنشدنا أبو معاذ عبيد الله بن محمد الخطيب:
إن التآلف والأخوة ..... خلقان من خلق النبوة
فاختر لنفسك يا فتى ..... أهل الديانة والمروة.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (12,'١١ - أنشدنا أبو طاهر أحمد بن علي بن سوار المقرئ النحوي وأبو المعالي ثابت بن بندار بن إبراهيم المقرئ ببغداد قالا أنشدنا أبو الحسن علي بن محمد بن قشيش ⦗٦١⦘ المالكي أنشدنا أبو نصر عبد العزيز بن نباتة السعدي لنفسه:
نعلل بالدواء إذا مرضنا ..... وهل يشفي من الموت الدواء
ونختار الطبيب وهل طبيب ..... يؤخر ما يقدمه القضاء ⦗٦٢⦘
وما أنفاسنا إلا حساب ..... ولا حركاتنا إلا فناء.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (13,'١٢ - وأخبرنا أبو الحسين أحمد بن سرور بن سليمان السُمُسْطاوي (١) بمكة أنا عطية الله ⦗٦٣⦘ بن الحسن بن عطية الله القاضي بصور ثنا نصر بن إبراهيم المقدسي الفقيه أنا أبو القاسم هبة الله بن عبد الوارث بن علي الشيرازي أنا أبو طاهر أحمد بن علي بن عبيد الله بن سوار المقرئ أنشدنا أبو الحسن علي بن محمد السمسار أنشدنا أبو نصر بن نباتة لنفسه فذكر الأبيات وكأن الفقيه نصر أخذها عني وأنا فيها بمثابة شيخ شيخ شيخ شيخي، وقد سمعها معي من السمسطاوي هذا ⦗٦٤⦘ بالنزول الإمام أبو بكر محمد بن أبي المظفر السمعاني وذكرت له روايتي بالعلو فاستحسنها واستغرب هذا وأكبر ظني أنه قد كتبها.
_________
(١) في الأصل: الشمسطاوي. والصواب المثبت.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (14,'١٣ - أنشدنا أبو طاهر أحمد بن علي بن سوار المقرئ ببغداد أنشدنا أبو الحسن علي بن محمد بن قشيش المالكي أنشدنا أبو نصر عبد العزيز بن عمر بن نباتة لنفسه:
لا تأمنن نبوة العدو وإن ..... ناصح يوما فغشه لغد
شيمة غدر وإن أخل بها ..... كامنة في طبيعة الأسد',NULL,'64',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (15,'١٤ - أنشدنا أبو طاهر في جمادى الأولى سنة ٥٩٣ أنشدنا أبو علي أحمد بن محمد الحافظ البرداني بقراءتي عليه ببغداد أنشدنا أبو علي محمد بن وشاح الكاتب أنشدنا أبو عمرو عثمان بن حاتم التغلبي النسابة أنشدنا المفجع الشامي (١) لنفسه: ⦗٦٦⦘
رأيت قوما عليهم سمة الخير ..... بحمل الركاء مبتهله
معتزلي الناس في مساجدهم ..... سألت عنهم فقيل متكله
الحال والوقت والحقيقة ..... والبرهان والعكس عندهم مسله
فلم أزل تابعا لهم زمنا ..... حتى تبينت أنهم أكله
_________
(١) كذا في الأصل (الشامي) ولعله تحرف من الشاعر.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (16,'١٥ - أنشدنا أبو الحسين المبارك بن عبد الجبار الصيرفي ببغداد أنشدنا أبو الحسن أحمد بن محمد بن منصور العتيقي أنشدنا أبو عمر محمد بن العباس بن حيويه الخزاز ⦗٦٧⦘ أنشدنا أبو عبد الله بن عرفة نفطويه:
الحسن الظن مستريح ..... يغتم من ظنه قبيح
وليس من باطن صحيح ..... إلا له ظاهر مليح',NULL,'66',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (17,'١٦ - أخبرنا أبو المعالي ثابت بن بندار بن إبراهيم المقرئ ببغداد أنا أبو عبد الله الحسين بن جعفر بن محمد السلماسي أنا أبو العباس الوليد بن بكر بن مخلد الغمري الأندلسي ⦗٦٨⦘[ثنا] (١) أبو علي منصور بن عبد الله الخالدي ثنا أبو إسحاق إبراهيم بن أحمد بن مسدد بن مسرهد بن مسربل بن مغربل بن مرعبل بن أرندل بن سرندل بن عرندل بن ماسك بن مستورد الأسدي البصري حدثني أبي حدثني أبي مسدد ثنا عيسى بن يونس عن هشام بن عروة عن أبيه عن عائشة رضي الله عنها أن النبي صلى الله عليه وسلم كان يقبل الهدية ويثيب عليها.
⦗٧٠⦘
مسدد بن مسرهد الأسدي هذا من ثقات أهل البصرة روى عن حماد بن زيد وأبي عوانة الوضاح والمعتمر بن سليمان ويزيد بن زريع وهشيم بن بشير وسفيان بن عيينة وأقرانهم روى عنه محمد بن يحيى الذهلي وأبو زرعة وأبو حاتم وأبو داود ويعقوب بن شيبة وإسماعيل بن إسحاق القاضي ونظراؤهم
وله مسند سمعناه ببغداد من رواية معاذ بن معاذ العنبري عنه بعلو وبواسط من رواية أبي خليفة الفضل بن الحباب ⦗٧١⦘ الجمحي وهو دون رواية معاذ في الحجم بكثير
وقد روى عنه البخاري في صحيحه واحتج بروايته ولم يزد في نسبه على مسدد بن مسرهد بن مسربل بن مرعبل في تاريخه. وكذلك قال مسلم في كتاب الكنى عن أبيه قال ابن مغربل بدل مرعبل.
⦗٧٢⦘ وذكره أبو نصر الكلاباذي في كتاب الإرشاد فقال مسدد بن مسرهد بن مسربل بن مغربل بن أرمك بن ماهك. وقال أبو العباس المستغفري المسعودي في نسبه مسدد بن مسرهد بن مشرف بن شريك. ذكر هذا عن الأمير أبي نصر ابن ماكولا البغدادي في كتاب الإكمال وقال قال الشريف النسابة مسدد المحدث بالبصرة هو ابن مسرهد بن مسربل بن ماسك بن جرو بن يزيد بن شبيب بن الصلت بن أسد بن شريك بن مالك بن عمرو بن مالك بن فهم والذي ساقه أبو علي الخالدي الهروي فلم يقر الأمير ⦗٧٣⦘ روايته هذه وغيره أوثق منه على أنه من الحفاظ.
وقد روي عن بعض الحفاظ أنه لما وقف على هذه النسبة قال لو كتب أمامه بسم الله الرحمن الرحيم كان رقية للعقرب.
وذكر يحيى بن سعيد القطان قال لو أتيت مسددا في بيته فحدثته لكان يستأهل
وقد توفي في شهر رمضان سنة ٣٣٨ وقيل سنة ٩ ويكنى أبا الحسن ويقال في نسبه الأسَدي والأسْدي بفتح ⦗٧٤⦘ السين وإسكانها وقال أبو الوليد الوقشي الأندلسي في عكس الرتبة مسدد أسدي بإسكان السين منسوب إلى الأسد وهم الأزد وهو من بني شريك بن مالك بن دوس قال وعلى ما قال الدارقطني إنه من بني أسد بن شريك يجوز أن يقال فيه الأسدي بفتح السين والله أعلم.
_________
(١) ليس في الأصل، والصواب إثباته.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b26658] VALUES (18,'١٧ - سمعت أبا الكرم المبارك بن فاخر بن يعقوب النحوي ببغداد يقول سمعت أبا القاسم علي بن المحسِّن ⦗٧٥⦘ التنوخي يقول سمعت أبا الحسن علي بن عيسى الربعي النحوي وسئل كل كتاب له ترجمة فما ترجمة كتاب الله عز وجل قال: {هذا بلاغ للناس ولينذروا به}.
⦗٧٦⦘
آخر الجزء ولله الحمد
فرغ تعليقه أحمد بن اللبودي ليلة الجمعة سابع شهر ربيع الأول الميمون سنة ست وستين وثمان مئة بمنزله بصالحية دمشق والحمد لله رب العالمين.
لله الحمد.
قرأت على الشيخ الصالح بهاء الدين أحمد بن المحدث فخر الدين بن عثمان بن محمد بن الصلف بإجازاته إن لم يكن سماعا من عائشة ابنة محمد بن عبد الهادي مسندته وأجاز فصح وثبت بالقرب من منزل المسمع طاهر بمدينة دمشق في سادس عشري شهر ذي الحجة المكرم سنة إحدى وسبعين وثمان مئة قاله وكتبه خليل بن عبد القادر بن الحصري حامدا مصليا.',NULL,'74',NULL,NULL,'0');
