INSERT INTO [b26754] VALUES (2,'<span data-type="title" id=toc-2>عوالي مالك
برواية هشام بن عمار</span>',NULL,'3',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (3,'بسم الله الرحمن الرحيم
أخبرنا الشيخ الإمام فقيه العلماء من كان فخر الدين مفتي الشام أبو منصور عبد الرحمن بن محمد بن الحسين بن هبة الله الشافعي بقراءتي عليه مرارا سنة ستمئة وخمس قال حدثنا عمي الشيخ الفقيه الإمام العالم الصائن مرتضى الدين أبو الحسين هبة الله بن الحسن بن هبة الله الشافعي رحمه الله في شهر ربيع الأول سنة تسع وخمسين وخمسمئة أخبرنا الشيوخ الأجلاء أبو القاسم عبد المنعم بن علي بن أحمد بن الغمر الكلابي قراءة عليه وأنا أسمع في يوم الثلاثاء الرابع عشر من ذي القعدة سنة اثنين وخمسمئة أخبرنا أبو نصر أحمد بن علي الكفرطابي في رجب من سنة ثمان وأربعين وأربعمئة حدثنا أبو الحسين عبد الوهاب بن الحسن بن الوليد الكلابي (ح) قال الشيخ الصائن رحمه الله وأخبرنا الشريف الأجل السيد أبو القاسم علي بن أبي الحسين إبراهيم بن العباس الحسيني رضي الله عنه أخبرنا أبوالقاسم علي بن محمد بن يحيى السميساطي في جمادى الأولى من سنة سبع وأربعين وأربعمئة وأبو القاسم الحسين بن محمد الحنائي في شهور سنة سبع وأربعين وأربعمئة هجري.
قال الصائن وأخبرنا أيضا الشريف أبو القاسم علي بن أبي الحسين بن إبراهيم الحسيني في سنة ست وخمسمئة أخبرنا أبو نصر أحمد بن علي بن الحسن الكفرطابي حدثنا عبد الوهاب بن الحسين بن موسى بن راشد بن خالد بن محمد بن سعيد بن عبد الله واتفقا بن الوليد بن علي بن محمد الكلابي رحمه الله في مدينة دمشق سنة إحدى وتسعين وثلاثمئة أخبرنا أبو بكر محمد بن خريم بن محمد بن عبد الملك بن مروان العقيلي ⦗٦⦘ قراءة علي وأنا حاضر في مسجد الجامع بسماعه سنة خمس عشر وثلاثمئة ثم اتفقا:
١ - حدثنا هشام بن عمار بن نصير بن ميسرة السلمي حدثنا مالك بن أنس الأصبحي المدني حدثنا محمد بن شهاب الزهري عن أنس بن مالك أن رسول الله صلى الله عليه وسلم دخل مكة يوم الفتح وعلى رأسه المغفر.',NULL,'5',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (4,'٢ - حدثنا أبو الحسين عبد الوهاب بن الحسن حدثنا محمد بن خريم حدثنا هشام حدثنا مالك قال حدثني الزهري عن أنس أن النبي صلى الله عليه وسلم أتي بلبن قد شيب بماء وعن يمينه أعرابي وعن يساره أبو بكر فشرب ثم أعطى الأعرابي وقال الأيمن فالأيمن.',NULL,'7',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (5,'٣ - حدثنا أبو الحسين حدثنا محمد بن علي بن خريم حدثنا هشام بن عمار حدثنا مالك قال حدثني الزهري عن السائب بن يزيد أنه رأى عمر بن الخطاب يضرب المنكدر في الصلاة بعد العصر.',NULL,'8',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (6,'٤ - حدثنا أبو الحسين حدثنا محمد بن خريم قال حدثنا هشام قال حدثنا مالك قال حدثني الزهري عن أبي إدريس الخولاني عن أبي هريرة أن رسول الله صلى الله عليه وسلم قال من توضأ فليستنثر ومن استجمر فليوتر.',NULL,'8',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (7,'٥ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك حدثني نافع عن عبد الله أن رسول الله صلى الله عليه وسلم قطع سارقا في مجن قيمته ثلاثة دراهم.',NULL,'9',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (8,'٦ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك قال حدثني نافع عن ابن عمر أن رسول الله صلى الله عليه وسلم قال ⦗١١⦘ من اشترى نخلا قد أبرت فثمرها للبائع إلا أن يشترط المبتاع.',NULL,'10',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (9,'٧ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك حدثني نافع عن عبد الله بن عمر قال الأضحى يومان بعد الأضحى.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (10,'٨ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك حدثني نافع أن عمر بن الخطاب رضي الله عنه كتب إلى عماله إن أهم أمركم إلي الصلاة فمن حفظها وحافظ عليها حفظ دينه ومن ضيعها فهو لما سواها أضيع ثم كتب أن صلاة الظهر إذا كان الفيء ذراعا إلى أن يكون ظل كل شيء مثله والعصر والشمس بيضاء نقية قدر ما يسير الراكب فرسخين أو ثلاثة والمغرب إذا غربت الشمس والعشاء إذا غاب الشفق إلى ثلث الليل فمن نام فلا نامت عيناه فمن نام فلا نامت عيناه فمن نام فلا نامت عنياه والصبح والنجوم بادية مشتبكة.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (11,'٩ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك حدثنا سمي مولى أبي بكر بن عبد الرحمن بن الحارث بن هشام عن أبي صالح عن أبي هريرة أن رسول الله صلى الله عليه وسلم قال السفر قطعة من العذاب يمنع أحدكم نومه وطعامه وشرابه فإذا قضى ⦗١٣⦘ أحدكم نهمته من سفره فليعجل إلى أهله.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (12,'١٠ - حثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك قال حدثني محمد بن عمرو بن حزم عن محمد بن إبراهيم بن الحارث التيمي عن أم ولد لإبراهيم بن عبد الرحمن ⦗١٤⦘ ابن عوف أنها سألت أم سلمة زوج النبي صلى الله عليه وسلم فقالت إني امرأة أطيل ذيلي وأمشي في المكان القذر فقالت قال رسول الله صلى الله عليه وسلم يطهره ما بعده.',NULL,'14',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (13,'١١ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك حدثنا إسحاق بن عبد الله بن أبي طلحة عن أنس بن مالك أن رسول الله صلى الله عليه وسلم قال ⦗١٥⦘ الرؤيا الحسنة من الرجل الصالح جزء من ستة وأربعين جزءا من النبوة.',NULL,'14',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (14,'١٢ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك عن صفوان بن سليم عن عطاء بن يسار أن رجلا قال لرسول الله صلى الله عليه وسلم أكذب أهلي قال لا خير في الكذوب قال أعدها يا رسول الله قال لا جناح عليك.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (15,'١٣ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك قال حدثني صفوان بن سليم عن سعيد بن سلمة من آل بني الأزرق أن المغيرة بن أبي برده وهو من بني عبد الدار حدثه أنه سمع أبا هريرة يقول جاء رجل إلى رسول الله صلى الله عليه وسلم فقال يا رسول الله إنا نركب البحر ونحمل معنا القليل من الماء فإن توضأنا به عطشنا أفنتوضأ من ماء البحر فقال رسول الله صلى الله عليه وسلم هو الطهور ماؤه الحل ميتته.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (16,'١٤ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك قال حدثني زيد بن أسلم أن عمر بن الخطاب رضي الله عنه قال من نام مضطجعا فليتوضأ.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (17,'١٥ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك بن أنس قال حدثني زيد بن أسلم أنه كان يتأول هذه الآية: (فاغسلوا وجوهكم وأيديكم إلى المرافق) أنها نزلت في القيام من المضاجع.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (18,'١٦ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك قال حدثني أبو الزناد عن الأعرج عن أبي هريرة أن رسول الله صلى الله عليه وسلم قال ⦗١٨⦘ إذا اشتد الحر فأبردوا بالصلاة فإن شدة الحر من فيح جهنم.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (19,'١٧ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك قال حدثني أبو الزناد عن الأعرج عن أبي هريرة قال قال رسول الله صلى الله عليه وسلم إذا توضأ أحدكم فليجعل في فيه (١) ماء ثم ليستنثر.
_________
(١) هكذا ورد عنده وعند الحاكم، كما سيأتي، وهو خطأ، صوابه ما أثبتناه من الموطأت وغيرها: أنفه.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (20,'١٨ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك قال حدثني جعفر بن محمد عن أبيه أن رسول الله صلى الله عليه وسلم قضى باليمين مع الشاهد.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (21,'١٩ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك قال بلغني عن رجل عن عمرو بن شعيب عن أبيه عن جده أن النبي صلى الله عليه وسلم نهى عن بيع العربان.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (22,'٢٠ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك قال كان عبد الله بن عمر ينزل الجحفة فيصلى بها الصلوات الأربع الظهر والعصر والمغرب والعشاء.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (23,'٢١ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام حدثنا مالك قال أتى القاسم أميرا من أمراء المدينة فسأله عن شيء فقال القاسم إن من إكرام المرء نفسه أن لا يقول إلا ما أحاط به علمه.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (24,'٢٢ - حدثنا أبو الحسين حدثنا محمد حدثنا هشام قال وسمعت مالكا يقول قال عمر بن الخطاب من يدلني على رجل بر تقي أوليه قال المغيرة بن شعبة أنا أدلك عليه قال من هو قال عبد الله بن عمر قال قاتلك الله والله ما أردت بها وجه الله.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (25,'٢٣ - حدثنا أبو الحسين حدثنا محمد بن خريم حدثنا هشام قال حدثنا مالك قال ولي أبو ⦗٢٢⦘ بكر رضي الله عنه بها.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b26754] VALUES (26,'٢٤ - حدثنا أبو الحسين سنتين لم يكن فيهما مال إنما كانت جهادا كلها وولي عمر عشر سنين ففتح الله على يديه الفتوح.',NULL,'22',NULL,NULL,'0');
