-- Insert data for b26758
INSERT INTO [b26758] VALUES (1,'لقاء العشر الأواخر بالمسجد الحرام (٨٧)

أخبار الثقلاء

جمع
الإمام أبي محمد الحسن بن محمد بن الحسن الخلال
(٣٥٢ - ٤٣٩ هـ)

اعتنى به
نظام محمد صالح يعقوبي

دار البشائر الإسلامية

الطبعة الأولى
١٤٢٧ هـ - ٢٠٠٦ م',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b26758] VALUES (2,'بسم الله الرحمن الرحيم
قال شيخ الإسلام شهاب الدين أبو الفضل أحمد بن علي بن حجر العسقلاني:
أخبرتنا فاطمة وعائشة بنتا محمد بن عبد الهادي قراءةً عليهما، بسماعهما له على أبي محمد عبد الله بن الحسين بن أبي الثابت، أخبرنا محمد بن أبي بكر البلخي، عن السلفي، أخبرنا جعفر بن أحمد السراج، أخبرنا الشيخ الصالح الحافظ أبو محمد الحسن بن محمد بن الحسن الخلال:
١- أخبرنا عبيد الله بن أحمد بن يعقوب، حدثنا محمد بن محمد يعني الباغندي، حدثنا محمد بن هشام بن أبي حيوة، حدثنا بشر بن المفضل، عن محمد صاحب السباح، عن إبراهيم بن أبي بكر، عن رجل من الأنصار قال: ((كان أبو هريرة إذا استثقل الرجل قال: اللهم اغفر لنا وله وأرحنا منه)) .',NULL,'15',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (3,'٢- أخبرنا عمر بن أحمد الواعظ، حدثنا عبدا لله بن سليمان بن الأشعث، حدثنا محمد بن هشام بن أبي حيوة، فذكر مثله.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (4,'٣- حدثنا عمر بن أحمد، حدثنا أحمد بن نصر، ثنا محمد بن غالب بن حرب، ثنا يعلى بن مهدي، ثنا حماد بن زيد، حدثني رجل من الأعراب قال: ((كان عمي إذا رأى ثقيلاً، غشي عليه!)) .',NULL,'16',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (5,'٤- حدثنا عمر بن أحمد، ثنا أحمد بن نصر بن طالب، ثنا عبد الله بن الليث بن النضر، يعرف بخرولة، حدثنا عبد الله بن عمر، قال: ((قلت لأبي أسامة: أنت والله ثقيلٌ يا أبا أسامة! قال: زد فيها يا بني ووخم)) .',NULL,'16',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (6,'٥- حدثنا عمر بن أحمد، ثنا الحسين بن أحمد بن صدقة، حدثنا أحمد بن أبي خيثمة، ثنا خالد بن خراش، ثنا عمرو بن النضر، عن إسماعيل بن أبي خالد، قال: سمعت الشعبي يقول لداود الطائي: ((سألتك بوجه الله إلا قمت)) .',NULL,'16',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (7,'٦- حدثنا عمر، ثنا الحسن، ثنا أحمد، ثنا عبد الرحمن بن قيلج، ثنا أبو بشر بن بكير، عن إسماعيل بن حماد بن أبي سليمان، ⦗١٧⦘ عن أبيه، قال: ((من خاف أن يكون ثقيلاً فهو خفيفٌ)) .
وقال حماد بن أبي سليمان: ((من أمن أن يثقل، ثقل)) .',NULL,'16',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (8,'٧- حدثنا أبو الحسن محمد بن أحمد بن رزق، ثنا عبد الله بن إسحاق بن إبراهيم الخراساني، ثنا الحسن بن خليل، حدثني معاوية بن سلمة بن خالد بن معاوية بن أبي عمرو بن العلاء، قال: ((كان جدي أبو عمرو بن العلاء يجلس إليه رجلٌ يستثقله؛ فكان إذا طلع، دخل وتركه. قال: وكتب إليه يستعطفه، قال: فكتب إليه أبو عمرو:
أنت يا صاحب الكتاب ثقيل ... وقليلٌ من الثقيل كثير)) .',NULL,'17',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (9,'٨- حدثنا عمر بن أحمد الواعظ إملاءً، ثنا موسى بن عبيد الله بن يحيى بن خاقان الوزير، قال: حدثني عبد الله بن أبي سعد، حدثني عبد الله بن صالح بن مسلم، عن حماد الكاتب، قال: ((كنا نأتي سماك بن حرب، فنسأله عن الشعر، ويأتيه أصحاب الحديث؛ فيدعهم ويقبل علينا، ويقول: إن هؤلاء ثقلاء!)) .',NULL,'17',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (10,'٩- حدثنا محمد بن أحمد، حدثنا عبد الله بن إسحاق، ثنا حسن بن خليل، ثنا البرجمي، ثنا عثمان بن زفر، عن حبان ⦗١٨⦘ بن علي، قال: قال شبرمة:
ومن الناس من يخف ومنهم ... كرحى البزر ركب فوق ظهري.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (11,'١٠- حدثنا عمر بن أحمد، ثنا عبد الله بن محمد بن إسحاق المروزي، ثنا ابن أبي الدنيا، ثنا محمد بن قدامة، ثنا أبو علي الحسن بن علي الرمادي، قال: قال جبريل -متطبب كان بالشام-: ((نجد في كتبنا أن مجالسة الثقيل حمى الروح)) .',NULL,'18',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (12,'١١- حدثنا عمر بن أحمد، ثنا عبد الله بن محمد بن إسحاق، ثنا ابن أبي الدنيا، ثنا مسلمة بن شبيب، ثنا سفيان، قال: قال مساور الوراق: ((إنما تطيب المجالس بخفة الجلساء)) .',NULL,'18',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (13,'١٢- حدثنا أبو محمد عبد الله بن عثمان الصفار، ثنا محمد بن الحسن بن علي الكوفي الدقاق، قال: سمعت أبا عبد الله يونس بن محمد الباهلي الصوفي، قال: بلغني أن مطرف بن مازنٍ ⦗١٩⦘ دخل على المأمون فقال: ((بلغني أن عندكم مصنفاً عالماً يقال له عبد الرزاق، وأنه عمل كتاباً سماه: ((كتاب الثقلاء)) ، وأنه سماك فيمن سمى؟ قال: قلت: نعم يا أمير المؤمنين. قال: فما الذي استثقل منك، وأنت لخفيف المجلس، حسن المحادثة؟ فقال: يا أمير المؤمنين! استثقل طول قلنسوتي ورقة عنق بغلتي!)) .',NULL,'18',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (14,'١٣- وذكر عبيد الله بن عثمان بن يحيى أبو القاسم الدقاق، قال: حدثنا أحمد بن محمد المنادي، حدثنا إبراهيم بن مهدي الأبلي، قال: سمعت بشر بن آدم يقول: ((كنت عند أبي عاصمٍ النبيل، فجاء رجلٌ فنادى على بابه،: يا جارية! فقال لي أبو عاصم: انظر من هو؟ فنظرت؛ ثم قلت: فلانٌ، فوضع رأسه، ثم صبر قليلاً، وقال لي: انظر قد ذهب؟ فنظرت فإذا هو قد ذهب، فأنشأ يقول:
عدمت ثقيل الناس في كل مجلسٍ ... فيا رب لا تغفر لكل ثقيل
إذا ما ثقيلٌ زارنا في رحالنا ... فأفٍ له من زائرٍ وثقيل)) .',NULL,'19',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (15,'١٤- حدثنا علي بن عمرو بن سهل الحريري، ثنا سليمان بن محمد الخزاعي بدمشق، ثنا قاسم بن عثمان الجرعي، ثنا حجاج بن محمد الأعور، ثنا شريك بن عبد الله، قال: ((دخلت على الأعمش أعوده؛ فدخل عليه أبو حنيفة؛ فقال: يا أبا محمد! لولا ما أعرف من تثاقلك بي، لأتيتك في كل وقتٍ، فقال: والله إنك لثقيلٌ علي وأنت في ⦗٢٠⦘ بيتك؛ فكيف إذا جئتني؟!)) .',NULL,'19',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (16,'١٥- حدثنا عمر بن أحمد، ثنا الحسين بن أحمد بن صدقة، ثنا يحيى بن أيوب، قال: سمعت يزيد بن هارون يقول للإنسان إذا استثقله: ((اللهم لا تجعلنا ثقلاء)) .',NULL,'20',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (17,'١٦- حدثنا عمر بن أحمد، ثنا عبد الله بن محمد بن إسحاق المروزي، ثنا محمد بن قدامة، ثنا أبو أسامة، قال: سمعت هشام بن عروة يقول: ((أنت أثقل من الزقاقي! فسألت عنها الفراء فلم يعرفها؛ فقال رجلٌ من جلسائه: كانت العرب تسهر، فإذا زقا الديكة استثقلتها لمجيء الصبح؛ فأعجب الفراء)) .',NULL,'20',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (18,'١٧- أنشدنا أبو عمر بن حيوية، أنشدنا أحمد بن القاسم بن مضر الشاعر، قال: أنشدنا أبو أيوب، قال: أنشدنا دعبل بن علي، قال:
إني أجالس معشراً ... نوكى أخفهم ثقيل
لا يفهموني قولهم ... ويدق عنهم ما أقول
قوم إذا جالستهم ... صدئت لقلوبهم العقول
⦗٢١⦘
فهم كثيرٌ بي وأعلم ... أنني بهم قليل.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (19,'١٨- حدثنا عمر بن أحمد، ثنا الحسين بن أحمد، ثنا ابن أبي خيثمة، قال: عن شعيب بن حرب، قال: قال الثوري: ((إنه ليكون في المجلس عشرةٌ كلهم يخفه علي، فيكون فيهم الرجل الذي أستثقله؛ فيثقلون علي)) .',NULL,'21',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (20,'١٩- قال: وقال ابن المبارك: ((إنه ليكون في المجلس عشرةٌ، كلهم يثقل علي؛ فيكون الرجل منهم أستخفه، فيخفون علي)) .',NULL,'21',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (21,'٢٠- أخبرنا أبو عمرو بن حيوية إجازة، قال: أنشدنا أبو بكر محمد بن القاسم فقال:
لقلع ضرسٍ وضنك حبس ... ورد أمسٍ ويوم نحس
ونفخ نارٍ وحمل عارٍ ... وبيع جارٍ بربع فلس
وضرب ألفٍ وأكل كفٍ ... وضيق خفٍ ونزع نفس
وشرب سمٍ وألف قلس ... أيسر من وقفةٍ ببابٍ
يلقاه حجّابه بعبس ... .',NULL,'21',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (22,'٢١- حدثنا علي بن عمر بن حفص المنقري، حدثنا إبراهيم بن أحمد القرمسيني، ثنا أحمد بن محمد، ثنا سعيد بن بحر القراطيسي، قال: سمعت أبا معاوية الضرير، قال: ((قيل للأعمش: بم عوضك الله من ذهاب بصرك؟ قال: أني لا أرى ثقيلاً!))',NULL,'22',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (23,'٢٢- أخبرنا أبو القاسم الحسن بن الحسن المعدل، ثنا أبو بكر محمد بن عبد الله البزار، قال: حدثني إسماعيل بن الفضل البلخي، قال: حدثني رجاء الوراق، ثنا عروة بن ثابت المديني، عن هشام بن سعد، عن إسماعيل بن عمرو القرشي، عن ابن شهاب، قال: ((إذا ثقل عليك الجليس، فاصبر؛ فإنها ربطةٌ في سبيل الله! فإذا أبرمك وملك بحديثه؛ فجاهد بقيامه عنك أو بقيامك عنه)) .',NULL,'22',NULL,NULL,'0');
INSERT INTO [b26758] VALUES (24,'٢٣- حدثني عبد الواحد بن علي أبو الطيب اللحياني، حدثنا إسماعيل بن محمد النحوي إملاءً، ثنا أحمد بن يحيى بن تغلب، ثنا زبير، أنا محمد بن يحيى بن عبد الحميد، عن ابن أبي يحيى، قال: ⦗٢٣⦘ ((كنا نأتي ابن أبي عتيقٍ نعرض عليه، فربما غمض عينيه؛ فنقف، فيقول: ما لكم؟ فنقول: أنعست؟ فيقول: لا! ولكن مر بي إنسانٌ، فاستثقلته؛ فغمضت عيني)) .',NULL,'22',NULL,NULL,'0');
