-- Insert data for b26865
INSERT INTO [b26865] VALUES (1,'الجامع لابن وهب
في الأحكام

للإمام الحافظ عبد الله بن وهب المصري
١٢٥ - ١٩٧ هـ

حققه وخرج أحاديثه وعلق عليه
الدكتور رفعت فوزي عبد المطلب
الدكتور علي عبد الباسط مزيد

دار الوفاء

الطبعة الأولى
١٤٢٥ هـ - ٢٠٠٥ م
_________
[قوبلت أيضا على طبعة مكتبة العلوم والحكم في مجلدين بتحقيق الدكتور محمد الأمين الحسين الشنقيطي وأثبت ترقيم الأحاديث فيها بين []، وعند الاختلاف بين الطبعتين تم الرجوع للمخطوط، وذلك إلى الحديث (٤٦٧) حيث انتهت المخطوطة الموجودة عندي]',NULL,'4',NULL,NULL,'0');
INSERT INTO [b26865] VALUES (2,'<span data-type="title" id=toc-2>بسم الله الرحمن الرحيم</span>
١ - أخبرنا محمد، أنا ابن وهب قال: أخبرني يحيى بن أيوب، عن أبي صخر المدني، عن أبي معاوية البجلى، عن أبي الصهباء البكري قال: قام ابن الكواء إلى علي بن أبي طالب رضي الله عنه وهو على المنبر، فقال:
إني وطئت على دجاجة ميتة، فخرجت منها بيضة، آكلها؟ قال علي: لا. قال: فإني استحضنتها (¬١) تحت دجاجة، فخرج منها فرخ، آكله؟ قال: نعم. قال: كيف؟ قال: لأنه حي خرج من ميت.
_________
(¬١) [[من طبعة الشنقيطي، وفي المطبوع: أشخصتها]]',NULL,'21','1',NULL,'0');
INSERT INTO [b26865] VALUES (3,'٢- أخبرنا محمد، أنا ابن وهب قال: أخبرني أسامة بن زيد، عن عمرو بن ⦗٢٢⦘ شعيب، عن أبيه، عن جده قال: سمعت رسول الله صلى الله عليه وسلم يقول عام الفتح - وهو بمكة:
((إن الله ورسوله حرم بيع الخمر والميتة والخنزير والأصنام)) .
فقيل له عند ذلك: يا رسول الله، أرأيت شحوم الميتة، فإنه يدهن به السقاء والجلود، ويستصبح به الناس؟
قال: ((لا، هي حرام)) ثم قال عند ذلك: ((قاتل الله يهوداً؛ إن الله لما حرم عليهم شحومها أجملوه، ثم باعوه، فأكلوا ثمنه)) .',NULL,'21','2',NULL,'0');
INSERT INTO [b26865] VALUES (4,'٣- أخبرنا محمد، أنا ابن وهب قال: أخبرني يزيد بن عياض، عن عطاء بن ⦗٢٣⦘ أبي رباح، عن جابر بن عبد الله قال: لما قدم رسول الله صلى الله عليه وسلم مكة أتاه أصحاب الصليب الذين كانوا يجمعون الودك بمنى.
فقالوا: إنا نجمع هذه الأوداك من الميتة وغيرها، فإنما هي للسفن والأديم؟
قال: ((قاتل الله يهوداً، حرم عليهم شحومها، ثم باعوها فأكلوا أثمانها)) . فنهاهم عن ذلك.',NULL,'22','3',NULL,'0');
INSERT INTO [b26865] VALUES (5,'٤- أخبرنا محمد، أنا ابن وهب قال: حدثني زمعة بن صالح المكي عن أبي ⦗٢٤⦘ الزبير المكي، عن جابر بن عبد الله قال:
بينا أنا عند رسول الله صلى الله عليه وسلم إذ جاءه ناس، فقالوا: يا رسول الله، إن سفينة لنا انكسرت، وإنا وجدنا ناقة سمينة ميتة، فأردنا أن ندهن بها سفينتنا، وإنما هي عود هي على الماء.
فقال رسول الله صلى الله عليه وسلم: ((لا تنتفعوا بشيء من الميتة، ولا تنتفعوا بالميتة)) .',NULL,'23','4',NULL,'0');
INSERT INTO [b26865] VALUES (6,'٥- أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، ومالك بن أنس، وسفيان بن سعيد الثوري؛ أن حميداً الطويل حدثهم عن أنس بن مالك قال: ⦗٢٥⦘ حجم أبو طيبة رسول الله صلى الله عليه وسلم، فأعطاه صاعين أو صاعاً من تمر، وأمر أهله أن يخففوا عنه من خراجه.',NULL,'24','5',NULL,'0');
INSERT INTO [b26865] VALUES (7,'٦- أخبرنا محمد أنا ابن وهب قال: أخبرني عبد الرحمن بن أبي الزناد، عن أبيه، أن عمرو بن عامر الأنصاري أخبره؛ أنه مشى مع جار له حجام إلى أنس يسألانه عن كسب الحجام، فقال لهما أنس بن مالك:
قد كان رسول الله صلى الله عليه وسلم يحتجم، فلم يكن يظلم الحجام أجره.',NULL,'25','6',NULL,'0');
INSERT INTO [b26865] VALUES (8,'٧- وقال أبو الزناد: أخبرني الثقة أن قريشاً كانت تتكرم في الجاهلية عن كسب الحجام، ولو كان حراماً لم يقل رسول الله صلى الله عليه وسلم للأنصاري: ((اجعله في علف ناضح اليتيم)) .',NULL,'25','7',NULL,'0');
INSERT INTO [b26865] VALUES (9,'٨-[٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني يحيى بن أيوب، عن المثنى ابن الصباح، عن عمرو بن شعيب، عن أبيه، عن جده؛ أن رسول الله صلى الله عليه وسلم احتجم وأعطى الحجام أجره.',NULL,'26','8',NULL,'0');
INSERT INTO [b26865] VALUES (10,'٩-[٨] أخبرنا محمد، أنا ابن وهب قال: أخبرني مسلم بن خالد، عن العلاء بن عبد الرحمن، عن أبيه، عن أبي هريرة قال:
نهى رسول الله صلى الله عليه وسلم عن كسب الأمة إلا أن يكون لها عمل واصب أو كسب يعرف وجهه.',NULL,'26','9',NULL,'0');
INSERT INTO [b26865] VALUES (11,'١٠-[٩] أخبرنا محمد، أنا ابن وهب قال: حدثني (¬١) عمر بن قيس، عن عطاء بن أبي رباح، عن أبي هريرة قال:
ثمن الكلب غير الصائد سحت.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: أخبرني]]',NULL,'27','10',NULL,'0');
INSERT INTO [b26865] VALUES (12,'١١-[١٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، ويونس بن يزيد، والليث بن سعد؛ أن ابن شهاب حدثهم، عن أبي بكر بن عبد الرحمن، أن أبا مسعود عقبة بن عمرو حدثهم؛ أن رسول الله صلى الله عليه وسلم نهاهم عن ثمن الكلب، ومهر البغي، وحلوان الكاهن.
إلا أن يونس قال في الحديث: ((ثلاثة هن سحت)) .',NULL,'27','11',NULL,'0');
INSERT INTO [b26865] VALUES (13,'١٢-[١١] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الرحمن بن سلمان، عن عقيل بن خالد، عن ابن شهاب، عن أبي بكر عن النبي صلى الله عليه وسلم أنه قال:
((ثلاث هن سحت: حلوان الكاهن، ومهر البغي، وثمن الكلب العقور)) .',NULL,'28','12',NULL,'0');
INSERT INTO [b26865] VALUES (14,'١٣-[١٢] أخبرنا محمد، أنا ابن وهب، ثنا شمر بن نمير، عن ابن ضميرة -وهو حسين بن عبد الله٠ عن أبيه، عن جده، عن علي بن أبي طالب؛ أن النبي صلى الله عليه وسلم نهى عن ثمن الكلب العقور.',NULL,'28','13',NULL,'0');
INSERT INTO [b26865] VALUES (15,'١٤-[١٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني معروف بن سويد الجذامي، أن ⦗٢٩⦘ علي بن رباح اللخمي حدثهم؛ أنه سمع أبا هريرة يقول: قال رسول الله صلى الله عليه وسلم:
((لا يحل ثمن الكلب ولا حلوان الكاهن، ولا مهر البغي)) .',NULL,'28','14',NULL,'0');
INSERT INTO [b26865] VALUES (16,'<span data-type="title" id=toc-3>من كتاب الأشربة</span>
١٥-[١٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الجبار بن عمر؛ أن محمد بن المنكدر حدثه، عن جابر بن عبد الله؛ أن رسول الله صلى الله عليه وسلم نهى عن الخليطين أن يشربا. فقلنا: وما الخليطان يا رسول الله؟ قال: ((التمر والزبيب، وكل مسكر حرام)) .',NULL,'29','15',NULL,'0');
INSERT INTO [b26865] VALUES (17,'١٦-[١٥] أخبرنا محمد، أنا ابن وهب، قال عبد الجبار: وحدثني إسحاق بن عبد الله؛ أن محمد بن يوسف حدثه؛ أن أم مغيث حدثته؛ أنها سمعت رسول الله صلى الله عليه وسلم يقول ذلك.',NULL,'29','16',NULL,'0');
INSERT INTO [b26865] VALUES (18,'١٧-[١٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمرو بن الحارث؛ أن بكير بن عبد الله حدثه؛ أن عبد الرحمن بن الحارث السلمي حدثه، عن أبي قتادة الأنصاري، أنه قال: نهى رسول الله صلى الله عليه وسلم أن ينبذ التمر والزبيب جميعاً.',NULL,'30','17',NULL,'0');
INSERT INTO [b26865] VALUES (19,'١٨-[١٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمرو بن الحارث والليث بن سعد، عن أبي الزبير، عن جابر بن عبد الله؛ أن رسول الله صلى الله عليه وسلم نهى أن ينبذ التمر والزبيب جميعاً، ونهى أن ينبذ التمر والرطب جميعاً.',NULL,'31','18',NULL,'0');
INSERT INTO [b26865] VALUES (20,'١٩-[١٨] أخبرنا محمد، أنا ابن وهب قال: وحدثني الليث بن سعد وجرير بن حازم، أن عطاء بن أبي رباح حدثهما، عن جابر بن عبد الله، عن رسول الله صلى الله عليه وسلم مثله.',NULL,'31','19',NULL,'0');
INSERT INTO [b26865] VALUES (21,'٢٠-[١٩] أخبرنا محمد، أنا ابن وهب قال: وحدثني مالك بن أنس، عن زيد ⦗٣٢⦘ ابن أسلم، عن عطاء بن يسار؛ أن رسول الله صلى الله عليه وسلم نهى عن ذلك.',NULL,'31','20',NULL,'0');
INSERT INTO [b26865] VALUES (22,'٢١-[٢٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الرحمن بن سلمان عن عقيل بن خالد، عن معبد بن كعب بن مالك، عن أخيه عبد الله بن كعب بن مالك، عن امرأة؛ أنها سمعت رسول الله صلى الله عليه وسلم يقول:
((لا تنتبذوا التمر والزبيب جميعاً، انبذوا كل واحد منهما وحده)) .',NULL,'32','21',NULL,'0');
INSERT INTO [b26865] VALUES (23,'٢٢-[٢١] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمرو بن الحارث، أن قتادة ابن دعامة حدثه؛ أنه سمع أنس بن مالك يقول:
⦗٣٣⦘
إن رسول الله صلى الله عليه وسلم نهى أن يخلط التمر والزهو، ثم يشرب وأن ذلك كان عامة خمورهم يوم حرمت الخمر.',NULL,'32','22',NULL,'0');
INSERT INTO [b26865] VALUES (24,'٢٣-[٢٢] أخبرنا محمد، أنا ابن وهب قال: وأخبرني جرير بن حازم، عن أبان ابن أبي عياش؛ أن أنس بن مالك كان يقطع الرطب من البسر لأن رسول الله صلى الله عليه وسلم نهى أن يخلط بين البسر والتمر والزبيب والتمر.',NULL,'33','23',NULL,'0');
INSERT INTO [b26865] VALUES (25,'٢٤-[٢٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني الخليل بن مرة، عن أبان ⦗٣٤⦘ ابن أبي عياش قال:
رأيت جواري أنس بن مالك يضعن البسر في المكاتل، ويأخذن السكاكين، ويتتبعن كل شيء أرطب منه، فيقطعنه حتى يقطعن مثل الشامة ومثل القمع، مما أرطب كراهية أن تكون بسراً وتمراً، فيكون فضيخاً.
قال أبان: وقال أنس بن مالك: هكذا كنا ننبذ على عهد رسول الله صلى الله عليه وسلم.',NULL,'33','24',NULL,'0');
INSERT INTO [b26865] VALUES (26,'٢٥-[٢٤] أخبرنا محمد، أنا ابن وهب قال: حدثني عبد الجبار بن عمر؛ أن محمد بن المنكدر حدثه، عن جابر بن عبد الله؛ أنه قال: قام فينا رسول الله صلى الله عليه وسلم فقال:
((لا تشربوا في المزفت والنقير والحنتم والدباء، واشربوا وكل مسكر حرام)) .',NULL,'34','25',NULL,'0');
INSERT INTO [b26865] VALUES (27,'٢٦-[٢٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن جريج، عن أيوب بن هانئ، عن مسروق الأجدع، عن عبد الله بن مسعود؛ أن رسول الله صلى الله عليه وسلم قال:
((إني كنت نهيتكم عن نبيذ الأوعية، ألا إن وعاء لا يحرم شيئاً، وكل مسكر حرام)) .',NULL,'35','26',NULL,'0');
INSERT INTO [b26865] VALUES (28,'٢٧-[٢٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، أن عبد الرحمن بن ميمون حدثه؛ أنه سمع الحكم بن عتيبة يحدثه بمكة؛ أن رسول الله صلى الله عليه وسلم قام في حجة الوداع فقال:
((ألا إني كنت نهيتكم أن تنبذوا في الحنتم والدباء والنقير فانتبذوا، وكل مسكر حرام)) .
قال عبد الرحمن بن ميمون: فاستحييت أن أسأله عمن يحدثه، فسألت بعض جلسائه فأخبرني أنه يحدث عن عبد الرحمن بن أبي ليلى عن معاذ.',NULL,'35','27',NULL,'0');
INSERT INTO [b26865] VALUES (29,'٢٨-[٢٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني الحارث بن نبهان، عن حنظلة، عن أنس بن مالك قال:
نهى رسول الله صلى الله عليه وسلم عن نبيذ الجر، ثم قال: ((انتبذوا فيما شئتم؛ فإن الآنية لا تحل شيئاً ولا تحرمه)) .',NULL,'36','28',NULL,'0');
INSERT INTO [b26865] VALUES (30,'٢٩-[٢٨] أخبرنا محمد، أنا ابن وهب قال: حدثني أسامة بن زيد الليثي؛ أن محمد بن يحيى بن حبان أخبره؛ أن واسع بن حبان حدثه؛ أن أبا سعيد الخدري حدثه؛ أن رسول الله صلى الله عليه وسلم قال: ((نهيتكم عن النبيذ ألا فانتبذوا، ولا أحل مسكراً)) .',NULL,'36','29',NULL,'0');
INSERT INTO [b26865] VALUES (31,'٣٠-[٢٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني الحارث بن نبهان، عن العلاء بن المسيب، عن إبراهيم، عن الأسود بن يزيد قال:
كانت عائشة تنبذ لرسول الله صلى الله عليه وسلم في جر أخضر، وأن عائشة كانت تشرب النبيذ في جر أخضر.
قال: وأخبرني أن عبد الله بن مسعود كان يشرب في جر أخضر.',NULL,'36','30',NULL,'0');
INSERT INTO [b26865] VALUES (32,'٣١-[٣٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن جريج، عن أبي الزبير، عن جابر بن عبد الله قال:
كان رسول الله صلى الله عليه وسلم إذا لم يجد شيئاً ينبذ له فيه نبذ له في تورٍ من حجارة.',NULL,'37','31',NULL,'0');
INSERT INTO [b26865] VALUES (33,'٣٢-[٣١] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، عن سعيد بن محمد عن أبي النضر، عن سالم بن عبد الله بن عمر، عن أبيه، عن رسول الله صلى الله عليه وسلم أنه قال:
⦗٣٨⦘
((من العنب خمراً، ومن العسل خمراً، ومن الزبيب خمراً، ومن التمر خمراً، ومن الحنطة خمراً، وأنا أنهى عن كل مسكر)) .',NULL,'37','32',NULL,'0');
INSERT INTO [b26865] VALUES (34,'٣٣-[٣٢] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك ويونس بن يزيد، عن ابن شهاب، عن أبي سلمة بن عبد الرحمن بن عوف؛ أنه سمع عائشة زوج النبي صلى الله عليه وسلم تقول:
سئل رسول الله عن البتع؟ فقال: ((كل شراب أسكر فهو حرام)) .',NULL,'38','33',NULL,'0');
INSERT INTO [b26865] VALUES (35,'٣٤-[٣٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمرو بن الحارث؛ أن ⦗٣٩⦘ دراجاً أبا السمح حدثه؛ أن عمر بن الحكم حدثه، عن أم حبيبة زوج النبي صلى الله عليه وسلم؛ أن ناساً من أهل اليمن قدموا على رسول الله صلى الله عليه وسلم فعلمهم الصلاة والسنن والفرائض.
ثم قالوا: يا رسول الله، إن لنا شراباً نصنعه من القمح والشعير فقال: ((الغبيراء؟)) قالوا: نعم. فقال: ((لا تطعموه)) ، ثم لما كان بعد يومين، ذكروه له أيضاً، فقال: ((الغبيراء؟)) قالوا: نعم. قال: ((لا تطعموه)) .
ثم لما أرادوا أن ينطلقوا، سألوه عنه، فقال: ((الغبيراء؟)) ، قالوا: نعم. قال: ((لا تطعموه)) .',NULL,'38','34',NULL,'0');
INSERT INTO [b26865] VALUES (36,'٣٥-[٣٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، عن يزيد بن أبي حبيب، وعياش بن عباس، عن أبي الخير عن ديلم الجيشاني؛ أنه قال:
أتيت رسول الله صلى الله عليه وسلم فقلت: يا رسول الله، إنا بأرض باردة شديدة البرد، نصنع بها شراباً من القمح، أفيحل يا نبي الله؟ قال: ((أليس يسكر؟)) قالوا: بلى. قال: ((فإنه حرام)) .',NULL,'39','35',NULL,'0');
INSERT INTO [b26865] VALUES (37,'٣٦-[٣٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني الليث بن سعد، أن أبا وهب الجيشاني حدثه، عن وفد جيشان الذين قدموا على رسول الله صلى الله عليه وسلم أنهم ذكروا لرسول الله صلى الله عليه وسلم شراباً يصطنعونه من الذرة والحنطة.
فقال رسول الله صلى الله عليه وسلم: ((أيسكر؟)) قالوا: نعم، فنهاهم عنه.',NULL,'40','36',NULL,'0');
INSERT INTO [b26865] VALUES (38,'٣٧-[٣٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، ومالك بن أنس، عن نافع؛ أن عبد الله بن عمر كان يقول:
كل مسكر خمر، وكل مسكر حرام.',NULL,'40','37',NULL,'0');
INSERT INTO [b26865] VALUES (39,'٣٨-[٣٧] أخبرنا محمد، أنا ابن وهب قال: وأخبرني أبو معشر، عن موسى بن ⦗٤١⦘ عقبة، عن سالم بن عبد الله [عن أبيه] (¬١) ؛ أن رسول الله صلى الله عليه وسلم قال:
((كل مسكر خمر، وما أسكر قليله، فكثيره حرام)) .
_________
(¬١) ما بين المعقوفتين ساقط من المخطوط، وأثبتناه من كتب التخريج.',NULL,'40','38',NULL,'0');
INSERT INTO [b26865] VALUES (40,'٣٩-[٣٨] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، عن عمرو بن شعيب، عن أبيه، عن عبد الله بن عمرو بن العاص، عن رسول الله صلى الله عليه وسلم أنه قال:
((ما أسكر كثيره، فقليله حرام)) .',NULL,'41','39',NULL,'0');
INSERT INTO [b26865] VALUES (41,'٤٠-[٣٩] أخبرنا محمد، أنا ابن وهب قال: حدثني شمر بن نمير، عن حسين بن عبد الله، عن أبيه، عن جده، عن علي بن أبي طالب، عن رسول الله صلى الله عليه وسلم مثله.',NULL,'41','40',NULL,'0');
INSERT INTO [b26865] VALUES (42,'٤١-[٤٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني الحارث بن نبهان، عن ليث ابن أبي سليم [عن أبي عثمان الأنصاري] (¬١) عن القاسم بن محمد، عن عائشة زوج النبي صلى الله عليه وسلم، عن رسول الله صلى الله عليه وسلم إلا أنه قال:
((فالحسوة منه حرام)) .
_________
(¬١) ما بين المعقوفتين بيس في الأصل، وأثبتناه من كتب التخريج.',NULL,'42','41',NULL,'0');
INSERT INTO [b26865] VALUES (43,'٤٢-[٤١] أخبرنا محمد، أنا ابن وهب قال: أخبرني إبراهيم بن نشيط الوعلاني، عن عمار بن سعد؛ أنه سمع عبد الرحمن بن حجيرة يحدث؛ أن رسول الله صلى الله عليه وسلم قال:
((كل مسكر حرام، وما أسكر كثيره فقليله حرام)) .
قال إبراهيم: فسألت ابن عبد الرحمن بن حجيرة عن ذلك، فقال: صدوق. قال ذلك أبي لعبد العزيز بن مروان.',NULL,'42','42',NULL,'0');
INSERT INTO [b26865] VALUES (44,'٤٣-[٤٢] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن إسحاق بن عبد الله بن أبي طلحة، عن أنس بن مالك قال:
كنت أسقي أبا عبيدة، وأبا طلحة، وأبي بن كعب شراباً من فضيخ وتمر، فأتى آت فقال: إن الخمر قد حرمت.
فقال أبو طلحة: يا أنس، قم إلى هذه الجرار فاكسرها، فقمت إلى مهراس لنا، فضربتها بأسفله حتى تكسرت.',NULL,'43','43',NULL,'0');
INSERT INTO [b26865] VALUES (45,'٤٤-[٤٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمرو بن الحارث؛ أن حبان بن واسع حدثه، عن أبيه، عن أبي سعيد الخدري؛ أن رسول الله صلى الله عليه وسلم قال:
((كل مسكر حرام)) .',NULL,'43','44',NULL,'0');
INSERT INTO [b26865] VALUES (46,'٤٥-[٤٤] أخبرنا محمد، [أنا ابن وهب] قال: أخبرني سعيد بن أبي أيوب، عن إبراهيم بن أبي عبلة، عن أم الدرداء، عن أبي الدرداء.
أو سعيد بن أبي أيوب، عن شرحبيل بن شريك المعافري، عن أبي عبد الرحمن الحبلي، عن رويفع بن ثابت الأنصاري صاحب رسول الله صلى الله عليه وسلم
قال: وأخبرني معاوية بن صالح، عن بنت أبي أمامة الباهلي، عن أبيها
قال: وأخبرني سليمان بن بلال، عن يحيى بن سعيد، عن سعيد بن المسيب قال: وإسحاق بن طلحة التيمي؛ أن عيسى بن طلحة، وعروة بن الزبير، وسالم ابن عبد الله كانوا يشربون الطلاء، وأن سالم بن عبد الله يشربه بالشام.
فقال [له] (¬١) عمر بن عبد العزيز: أيشرب الطلاء؟ قال له سالم: نعم؛ قد كان أبي يشربه.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]]',NULL,'44','45',NULL,'0');
INSERT INTO [b26865] VALUES (47,'٤٦-[٤٥] أخبرنا محمد، أنا ابن وهب قال: حدثني إبراهيم بن نشيط الوعلاني، ⦗٤٥⦘ وعمرو بن الحارث، عن سعيد بن أبي هلال، عن محمد بن عبد الله؛ أن أبا مسلم الخولاني حج، فدخل على عائشة رضي الله عنها زوج النبي صلى الله عليه وسلم، فجعلت تسأله عن الشام وعن بردها، فجعل يخبرها، فقالت: كيف تصبرون على بردها؟ فقال: يا أم المؤمنين، إنهم يشربون شراباً لهم يقال له: الطلاء.
فقالت: صدق الله وبلَّغ حتى سمعت حبي صلى الله عليه وسلم يقول: ((إن ناساً من أمتي يشربون الخمر، يسمونها بغير اسمها)) .',NULL,'44','46',NULL,'0');
INSERT INTO [b26865] VALUES (48,'٤٧-[٤٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني معاوية بن صالح، عن حاتم ابن حريث (¬١) ، عن مالك بن أبي مريم، عن عبد الرحمن بن غنم الأشعري، ⦗٤٦⦘ عن أبي مالك الأشعري، عن رسول الله صلى الله عليه وسلم أنه قال:
((ليشربن أناس من أمتي الخمر يسمونها بغير اسمها، ويضرب على رؤوسهم المعازف، يخسف الله بهم الأرض، ويجعل منهم قردة وخنازير)) .
_________
(¬١) في المخطوطة: حاتم بن كريب، وهو خطأ، وما أثبتناه من كتب التخريج.',NULL,'45','47',NULL,'0');
INSERT INTO [b26865] VALUES (49,'٤٨-[٤٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني يونس بن يزيد، عن ابن شهاب، عن عروة بن الزبير، عن عائشة؛ أنها كانت تنهى النساء أن يمتشطن بالخمر.',NULL,'46','48',NULL,'0');
INSERT INTO [b26865] VALUES (50,'٤٩-[٤٨] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس وغيره، عن زيد بن أسلم، عن عبد الرحمن بن وعلة السبئي -من أهل مصر- أنه سأل عبد الله ابن عباس عما يعصر من العنب، فقال ابن عباس:
إن رجلاً أهدى إلى رسول الله صلى الله عليه وسلم راوية خمر، فقال له رسول الله صلى الله عليه وسلم: ((هل علمت أن الله عز وجل قد حرمها؟ قال: لا. فسار إنساناً، فقال له رسول الله صلى الله عليه وسلم: ((بم ساررته؟)) فقال: أمرته أن يبيعها. فقال: ((إن الذي حرم شربها، حرم بيعها)) . قال: ففتح المزادتين حتى ذهب ما فيهما.',NULL,'46','49',NULL,'0');
INSERT INTO [b26865] VALUES (51,'٥٠-[٤٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني سليمان بن بلال، عن يحيى بن سعيد، عن عبد الرحمن بن وعلة، عن ابن عباس، عن رسول الله صلى الله عليه وسلم مثله.',NULL,'46','50',NULL,'0');
INSERT INTO [b26865] VALUES (52,'٥١-[٥٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، عن سليمان بن عبد الرحمن؛ أن نافع بن كيسان أخبرني؛ أن أباه كيسان أخبره؛ أنه كان يتجر بالخمر في زمان رسول الله صلى الله عليه وسلم أقبل من الشام ومعه خمر في زقاق يريد به التجارة، فأتى رسول الله صلى الله عليه وسلم فقال: يا رسول الله، إني قد جئت بشراب جيد.
فقال رسول الله صلى الله عليه وسلم: ((يا كيسان، إنها قد حرمت بعدك)) .
قال كيسان: أفأذهب فأبيعها يا رسول الله؟
فقال رسول الله صلى الله عليه وسلم: ((إنها قد حرمت، وحرم ثمنها)) .
فانطلق كيسان إلى الزقاق فأخذ بأرجلها ثم أهرقها جميعاً.',NULL,'47','51',NULL,'0');
INSERT INTO [b26865] VALUES (53,'٥٢-[٥١] أخبرنا محمد، أنا ابن وهب قال: أنا عمرو بن الحارث؛ أن عامر بن يحيى المعافري حدثه؛ أن رجلين من أهل مكة أقبلا يريدان الإسلام، فلما قدما المدينة أتيا كيسان بائع الخمر، فقالا: بعنا خمراً، فإنا نريد أن نشرب منها قبل أن نسلم.
فقال: قد حرمت الخمر، وليس عندي الآن إلا زقاق، ولا أدري هل يحل بيعها؟ انظرا حتى أسأل رسول الله صلى الله عليه وسلم.
فقالا: انطلق، ولا تسمنا له. فذهب إلى رسول الله صلى الله عليه وسلم فأخبره.
فقال: ((من هما؟)) فلم يستطع إلا أن يسميهما له.
⦗٤٨⦘
فقال: ((اذهب فقل لهما: {الحق من ربكم فمن شاء فليؤمن ومن شاء فليكفر إنا أعتدنا للظالمين ناراً أحاط بهم سرادقها وإن يستغيثوا يغاثوا بماءٍ كالمهل يشوي الوجوه بئس الشراب وساءت مرتفقاً} اذهب إليهما فقل لهما: إن الذي حرم شربها، حرم ثمنها)) .',NULL,'47','52',NULL,'0');
INSERT INTO [b26865] VALUES (54,'٥٣-[٥٢] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن الخير الزبادي؛ أن مالك بن سعد التجيبي حدثه؛ أنه سمع عبد الله بن عباس يقول:
إن رسول الله صلى الله عليه وسلم أتاه جبريل فقال: ((يا محمد، إن الله تعالى لعن الخمر، وعاصرها، ومعتصرها، وحاملها، والمحمولة إليه، وشاربها، وبائعها، ومبتاعها، ومسقيها)) .',NULL,'48','53',NULL,'0');
INSERT INTO [b26865] VALUES (55,'٥٤-[٥٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمر بن محمد بن زيد بن ⦗٤٩⦘ عبد الله بن عمر بن الخطاب؛ أن أبا رافع حدثه عن ذويد مولى سعيد بن عبد الله قال: قال رسول الله صلى الله عليه وسلم:
((لعن في الخمر عشرة: العاصر، والمعصورة (¬١) له، والبائع، والمشتري، والحامل، والمحمولة له، والساقي، والشارب، والمكارم بها، والمائدة تدار عليها)) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: والمعصور]]',NULL,'48','54',NULL,'0');
INSERT INTO [b26865] VALUES (56,'٥٥-[٥٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الرحمن بن شريح وابن لهيعة والليث بن سعد، عن خالد بن يزيد، عن ثابت بن يزيد الخولاني أخبره؛ أنه كان له عم يبيع الخمر، وكان يتصدق، فنهيته عنها، فلم ينته، فقدمت المدينة، فلقيت ابن عباس، فسألته عن الخمر وثمنها، فقال: هي حرام وثمنها حرام.
ثم قال: يا معشر أمة محمد، إنه لو كان كتاب بعد كتابكم، ونبي بعد نبيكم، لأنزل فيكم كما أنزل فيمن قبلكم، ولا أخر ذلك من أمركم إلى يوم القيامة، ولعمري لهو أشد عليكم.
قال ثابت: ثم لقيت عبد الله بن عمر، فسألته عن ثمن الخمر، فقال: سأخبرك عن الخمر، إني كنت عند رسول الله صلى الله عليه وسلم في المسجد، فبينا هو محتبي، حل حبوته، ثم قال: ((من كان عنده من هذه الخمر شيء فليؤت بها)) فجعلوا يأتونه بها، فيقول أحدهم: عندي راوية، ويقول الآخر: عندي زق أو ما شاء الله أن يكون عنده.
⦗٥٠⦘
فقال رسول الله صلى الله عليه وسلم: ((اجمعوا ببقيع كذا وكذا، ثم آذنوني)) . ففعلوا: ثم آذنوه. فقام وقمت معه، فمشيت عن يمينه، وهو متكئ علي، فلحقنا أبو بكر، فأخذ رسول الله صلى الله عليه وسلم، فجعلني عن شمال، وجعل أبا بكر مكاني، ثم لحقنا عمر بن الخطاب، فأخرني وجعله عن يساره، فمشى بينهما، حتى إذا وقف على الخمر، فقال للناس: ((أتعرفون هذه؟)) قالوا: نعم يا رسول الله، هذه الخمر. فقال: ((صدقتم)) .
قال: ((فإن الله لعن الخمر، وعاصرها، ومعتصرها، وشاربها، وساقيها، وحاملها، والمحمولة إليه، وبائعها، ومشتريها، وآكل ثمنها)) . ثم دعا بسكين فقال: ((اشحذوها)) . ففعلوا. ثم أخذها رسول الله صلى الله عليه وسلم يخرق بها الزقاق.
فقال الناس: إن في هذه الزقاق منفعة. قال: ((أجل، ولكني إنما أفعل ذلك غضباً لله لما فيها من سخطه)) .
قال عمر: أنا أكفيك يا رسول الله. قال: ((لا)) .
وبعضهم يزيد على بعض في قصة الحديث.',NULL,'49','55',NULL,'0');
INSERT INTO [b26865] VALUES (57,'٥٦-[٥٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة؛ أن أبا طعمة حدثه؛ أنه سمع عبد الله بن عمر بن الخطاب يحدث بهذا عن رسول الله صلى الله عليه وسلم.',NULL,'50','56',NULL,'0');
INSERT INTO [b26865] VALUES (58,'٥٧-[٥٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني عقبة بن صهبان وغيره من ⦗٥١⦘ أهل المدينة، عن أبي النضر، عن أبي هريرة؛ أن رجلاً من الأنصار قدم بخمر، فأنزلها بالمدينة، فذكر ذلك لرسول الله صلى الله عليه وسلم، فجاءه، فقالوا له: ما أقدمك بهذه علينا.
فقال: يا رسول الله، إن لنا جيراناً من يهود، فدعنا نكارمهم بها.
فقال رسول الله صلى الله عليه وسلم: ((إن الله حرم شربها، وبيعها، وابتياعها، والمكارمة بها)) ، فدعا رسول الله صلى الله عليه وسلم بالمدية.
قال أبو هريرة: ما دريت ما المدية قبل ذلك اليوم، فأتى بالشفرة فشقها حتى أهراق ما فيها، وأحدهما يزيد على صاحبه الكلمة ونحوها.',NULL,'50','57',NULL,'0');
INSERT INTO [b26865] VALUES (59,'٥٨-[٥٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، عن جعفر بن ربيعة، عن عطاء بن أبي رباح، عن جابر بن عبد الله قال:
لما كان فتح مكة أهراق رسول الله صلى الله عليه وسلم الخمر وكسر جرارها، ونهى عن بيعها، وعن بيع الأصنام.',NULL,'51','58',NULL,'0');
INSERT INTO [b26865] VALUES (60,'٥٩-[٥٨] أخبرنا محمد، أنا ابن وهب قال: أخبرني شبيب بن سعيد التميمي؛ أن أبان بن أبي عياش حدثهم، عن شهر بن حوشب؛ أن عبد الله بن عمرو بن العاص كان عندهم قال: سمعت رسول الله صلى الله عليه وسلم يقول:
((إن الله لعن الخمر، وعاصرها، ومعتصرها، وشاربها، وساقيها، وحاملها، والمحمولة إليه، وبائعها، ومشتريها، وآكل ثمنها)) .',NULL,'51','59',NULL,'0');
INSERT INTO [b26865] VALUES (61,'٦٠-[٥٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن سمعان، عن ابن شهاب، عن ابن المسيب، عن أبي هريرة أنه قال:
ثمن كل خمر حرام.
وقال رسول الله صلى الله عليه وسلم: ((قاتل الله يهوداً؛ حرمت عليهم الشحوم، فباعوه وأكلوا ثمنه)) .',NULL,'52','60',NULL,'0');
INSERT INTO [b26865] VALUES (62,'٦١-[٦٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني الحارث بن نبهان؛ أن أبان ابن أبي عياش أخبره، عن أنس بن مالك؛ أنه سمع منادي رسول الله صلى الله عليه وسلم ينادي بتحريم الخمر، وأبو طلحة في نفر يشربون وأنا أسقيهم، وهم يشربون فضيخاً -وهي خمر أهل المدينة يومئذ- قال: ففتحنا عزلاء الراوية، ثم انطلقنا إلى رسول الله صلى الله عليه وسلم فقال:
((ألا إن الخمر قد حرمت، فلا تشربوها، ولا تبيعوها، ولا تبتاعوا بها، فمن كان عنده منها شيء فليهريقه)) .
⦗٥٣⦘
فقام إليه رجل فقال: يا رسول الله، إني جعلت فيها مال يتيم؟ فقال رسول الله صلى الله عليه وسلم:
((قاتل الله يهوداً، حرمت عليهم الثروب، فلفوها ثم باعوها فأكلوا أثمانها)) .',NULL,'52','61',NULL,'0');
INSERT INTO [b26865] VALUES (63,'٦٢-[٦١] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمر بن محمد، وعبد الله ابن عمر، ويونس بن يزيد، وابن سمعان، وأسامة بن زيد الليثي؛ أن نافعاً أخبرهم، قال:
أخذ في بيت رجل من ثقيف شراب، فأمر عمر بن الخطاب ببيته فأحرق: وكان الرجل يدعى رويشد، فقال عمر: أنت فويسق.
٦٣- قال: وأخبرني ابن سمعان، وأسامة بن زيد الليثي، عن نافع، عن صفية بنت أبي عبيد، عن عمر بن الخطاب
٦٤- قال: وأخبرنيه ابن سمعان، عن ابن شهاب، عن سالم بن عبد الله، عن أبيه',NULL,'53','62',NULL,'0');
INSERT INTO [b26865] VALUES (64,'٦٥-[٦٢] أخبرنا محمد، أنا ابن وهب قال: حدثني (¬١) عمرو بن الحارث أن عمر ⦗٥٤⦘ ابن السائب حدثه؛ أن القاسم بن أبي القاسم حدثه؛ أنه سمع قاص الأجناد بالقسطنطينية يحدث عن عمر بن الخطاب أنه قال: أيها الناس، إني سمعت رسول الله صلى الله عليه وسلم يقول:
((من كان يؤمن بالله واليوم الآخر فلا يقعد على مائدة تدار عليها الخمر ومن كان يؤمن بالله واليوم الآخر فلا يدخل الحمام إلا بإزار، ومن كانت تؤمن بالله واليوم الآخر فلا تدخل الحمام)) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: أخبرني]]',NULL,'53','65',NULL,'0');
INSERT INTO [b26865] VALUES (65,'٦٦-[٦٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، وابن سمعان؛ أن نافعاً أخبرهم، عن عبد الله بن عمر، عن رسول الله صلى الله عليه وسلم أنه قال:
((من شرب الخمر في الدنيا ولم يتب منها، حرمها في الآخرة، لم يسقها.',NULL,'54','66',NULL,'0');
INSERT INTO [b26865] VALUES (66,'٦٧-[٦٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني حميد بن زياد أبو صخر؛ أن رجلاً حدثه عن عمارة بن حزم؛ أنه سمع عبد الله بن عمرو بن العاص - وهو في ⦗٥٥⦘ الحجر بمكة، وسئل عن الخمر فقال:
والله إن عظيماً عند الله الشيخ مثلي -وأخذ بلحيته- يكذب في هذا المقام على نبي الله صلى الله عليه وسلم، جاءني رجل وأنا في هذا المقام، فسألني عن الخمر، فقلت: ما سمعت فيها شيئاً ولا خبرها. ذاك رسول الله صلى الله عليه وسلم، اذهب وسله وارجع إلي فأخبرني ما قال لك.
قال: فنظرت إليه حتى قعد إلى رسول الله صلى الله عليه وسلم ثم رجع إلي فقال لي: سألته عن الخمر، فقال: ((هي أكبر الكبائر، وأم الفواحش، من شرب الخمر ترك الصلاة، ووقع على أمه وخالته وعمته)) .',NULL,'54','67',NULL,'0');
INSERT INTO [b26865] VALUES (67,'٦٨-[٦٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمر بن محمد، عن عبد الله بن يسار؛ أنه سمع سالم بن عبد الله يقول: قال عبد الله بن عمر: قال رسول الله صلى الله عليه وسلم:
((ثلاثة لا ينظر الله إليهم يوم القيامة، العاق لوالديه، ومدمن خمر، والمنان بما أعطى)) .',NULL,'55','68',NULL,'0');
INSERT INTO [b26865] VALUES (68,'٦٩-[٦٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني أبو محمد عاصم بن ⦗٥٦⦘ حكيم، أن يحيى بن عمرو السيباني حدثه عن عبد الله الديلمي، عن رجل من نجران، أنه سأل عبد الله بن عمرو بن العاص، عن الخمر؟ فقال: سمعت رسول الله صلى الله عليه وسلم:
((من شرب الخمر فسكر، سخط الله تعالى عليه أربعين يوماً، ثم انتظر به التوبة، فإن تاب تاب الله عليه)) ، حتى قال: ((وإن سكر الرابعة لم يرض الله عنه حتى يلقاه، وهو في ردغة الخبال يوم القيامة؛ صديد أهل النار)) .',NULL,'55','69',NULL,'0');
INSERT INTO [b26865] VALUES (69,'٧٠-[٦٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني شبيب بن سعيد التميمي، عن أبان بن أبي عياش، عن شهر بن حوشب، عن عبد الله بن عمرو بن العاص قال: سمعت رسول الله صلى الله عليه وسلم يقول:
((من شرب جرعة من خمر لم يقبل الله له صلاة جمعتين، فإن تاب تاب الله عليه، فإن مات وهو يشربها، مات كافراً)) . ثم قال: أزيدكم؟ قالوا: نعم. قال: سمعت رسول الله صلى الله عليه وسلم يقول: ((لا يدخل الجنة خمسة، لا يدخل الجنة مشرك، ولا كاهن، ولا منان، ولا عاقٌ، ولا مدمن خمر)) .
ثم قال: ((والذي نفسي بيده، إنه لفي الكتاب الأول أن خطيئتها تعلو كل خطيئة كما أن شجرتها تعلو كل الشجر)) .',NULL,'57','70',NULL,'0');
INSERT INTO [b26865] VALUES (70,'٧١-[٦٨] أخبرنا محمد، أنا ابن وهب قال: أخبرني مسلمة، عن هشام بن الغاز، عن مكحول، عن عائشة زوج النبي صلى الله عليه وسلم، عن النبي صلى الله عليه وسلم:
⦗٥٨⦘
((إن أول ما يكفأ هذا الدين على وجهه، كما تكفى الإناء لفي الخمر))',NULL,'57','71',NULL,'0');
INSERT INTO [b26865] VALUES (71,'٧٢-[٦٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمرو بن الحارث؛ أن ابن شهاب حدثه، عن سالم بن عبد الله حدثه؛ أن أول ما حرمت الخمر أن سعد ابن أبي وقاص وأصحاباً له شربوا فاقتتلوا، فكسر أنف سعد، فأنزل الله تعالى: {إنما الخمر والميسر والأنصاب والأزلام رجسٌ من عمل الشيطان فاجتنبوه} الآية.',NULL,'58','72',NULL,'0');
INSERT INTO [b26865] VALUES (72,'٧٣-[٧٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني أسامة بن زيد الليثي، أن ابن شهاب حدثه [إلا] (¬١) أنه قال: فشج سعد.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]]',NULL,'58','73',NULL,'0');
INSERT INTO [b26865] VALUES (73,'٧٤-[٧١] أخبرنا محمد، أنا ابن وهب قال: أخبرني يحيى بن أيوب، عن حميد ⦗٥٩⦘ الطويل، عن أنس بن مالك قال: كنا في بيت أبي طلحة، وأبي عبيدة بن الجراح، وأبي بن كعب، وسهيل بن البيضاء، قال: وكنت أسقيهم وشرابهم يومئذٍ البسر والتمر، فجاء رجل فقال: إن الخمر قد حرمت.
قال: فوالله ما انتظروا حتى يعلموا أصادق الرجل أم كاذب، قالوا: يا أنس، أكفئ ما بقي، فوالله ما عادوا إليه حتى لقوا الله عز وجل.',NULL,'58','74',NULL,'0');
INSERT INTO [b26865] VALUES (74,'٧٥-[٧٢] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، عن عبد الله بن هبيرة، عن أبي هبيرة الكلاعي (¬١) ، عن مولى لعبد الله بن عمرو، عن عبد الله بن عمرو بن العاص؛ أن رسول الله صلى الله عليه وسلم خرج إليهم ذات يوم وهم في المسجد، فقال:
((إن ربي عز وجل حرم علي الخمر والميسر والكوبة والقنين)) . والكوبة: ⦗٦٠⦘ الطبل.
_________
(¬١) في المخطوط الحكلاني، وهو خطأ وما أثبتناه من كتب الرواة والمسند.',NULL,'59','75',NULL,'0');
INSERT INTO [b26865] VALUES (75,'٧٦-[٧٣] أخبرنا محمد، أنا ابن وهب قال: حدثني الليث بن سعد، وابن لهيعة، عن يزيد بن أبي حبيب، عن عمرو بن الوليد بن عبدة، عن قيس بن سعد -وكان صاحب راية النبي صلى الله عليه وسلم - أن رسول الله صلى الله عليه وسلم قال ذلك:
((والغبيرا وكل مسكر حرام)) .
قال عمرو بن الوليد: وبلغني عن عبد الله بن عمرو بن العاص مثله.
ولم يذكر الليث القنين.',NULL,'60','76',NULL,'0');
INSERT INTO [b26865] VALUES (76,'٧٧-[٧٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، عن ابن هبيرة قال: سمعت شيخاً يحدث أبا تميم الجيشاني؛ أنه سمع قيس بن سعد بن عبادة -وهو على مصر- يقول: إن رسول الله صلى الله عليه وسلم قال:
((من كذب علي متعمداً فليتبوأ مضجعاً من جهنم أو بيتاً، ألا ومن شرب الخمر أتى عطشاناً يوم القيامة، وكل مسكر حرام، وإياكم والغبيراء))
⦗٦١⦘
قال: ثم سمعت عبد الله بن عمرو يقول مثل ذلك، فلم يختلفا إلا في مضجع أو بيت.',NULL,'60','77',NULL,'0');
INSERT INTO [b26865] VALUES (77,'٧٨-[٧٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني يونس بن يزيد، عن ابن شهاب، عن سعيد بن المسيب أنه قال:
أتى رسول الله صلى الله عليه وسلم ليلة أسرى به بإيليا بقدحين، خمر ولبن، فنظر إليهما، فأخذ اللبن، فقال له جبريل: الحمد لله الذي هداك الفطرة، لو أخذت الخمر غوت أمتك.',NULL,'61','78',NULL,'0');
INSERT INTO [b26865] VALUES (78,'٧٩-[٧٦] أخبرنا محمد، أنا ابن وهب قال: حدثني عمرو بن الحارث؛ أن عمرو ⦗٦٢⦘ ابن شعيب حدثه، عن أبيه، عن عبد الله بن عمرو بن العاص، عن رسول الله صلى الله عليه وسلم قال:
((من ترك الصلاة سكراً [مرة واحدة فكأنما كانت له الدنيا وما عليها فسلبها، ومن ترك الصلاة سكراً] (¬١) أربع مرات كان حقاً على الله أن يسقيه من طينة الخبال)) .
قيل: وما طينة الخبال؟ قال: ((عصارة أهل جهنم)) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]]',NULL,'61','79',NULL,'0');
INSERT INTO [b26865] VALUES (79,'٨٠-[٧٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني يونس بن يزيد، عن ابن ⦗٦٣⦘ شهاب قال: حدثني أبو بكر بن عبد الرحمن بن الحارث بن هشام؛ أن أباه قال: سمعت عثمان بن عفان يقول:
اجتنبوا الخمر، فإنها أم الخبائث، إنه كان رجل ممن خلا قبلكم يتعبد ويعتزل الناس، فعلقته امرأة غوية فأرسلت إليه جاريتها، فقالت: أنا أدعوك لشهادة. فدخل معها، فطفقت كلما دخل باباً أغلقته دونه حتى أفضى إلى امرأة وضيئة، عندها غلام وباطية خمر، فقالت: إني والله ما دعوتك لشهادة، ولكني دعوتك لتقع علي وتقتل هذا الغلام، أو تشرب هذا الخمر. فسقته كأساً، فقال: زيدوني. فلم يرم حتى وقع عليها، وقتل النفس، فاجتنبوا الخمر؛ فإنها لا تجتمع هي والإيمان أبداً إلا أوشك أحدهما أن يخرج صاحبه.',NULL,'62','80',NULL,'0');
INSERT INTO [b26865] VALUES (80,'٨١-[٧٨] أخبرنا محمد، أنا ابن وهب: حدثني ابن لهيعة، عن عبد الله بن هبيرة؛ أن رجلاً سأل عبد الله بن عباس، وعبد الله بن عمرو بن العاص عن أكبر الكبائر.
فقال: شرب الخمر، ثم قص عليه عبد الله بن عمرو خبر فتى من بني إسرائيل كان من أعبد الناس، ثم ذكر نحو حديث ابن شهاب هذا.',NULL,'63','81',NULL,'0');
INSERT INTO [b26865] VALUES (81,'٨٢-[٧٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، عن معاوية بن أبي الريان، عن أبي فراس مولى عبد الله بن عمرو، عن عبد الله بن عمرو قال:
⦗٦٤⦘
إن في كتاب الله: أنا الله لا إله إلا أنا، خلقت الجنة بيدي، وحظرتها على مسكر أو مدمن في الخمر سكير.',NULL,'63','82',NULL,'0');
INSERT INTO [b26865] VALUES (82,'٨٣-[٨٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني الحارث بن نبهان، عن محمد بن عبيد الله، عن عمرو بن شعيب، عن أبيه، عن عبد الله بن عمرو بن العاص أنه قال: لقد قرأت في ثلاث كتب أن الخمر محرمة، في التوراة والإنجيل وفي الفرقان، وإنها في التوراة أم الخبائث.',NULL,'64','83',NULL,'0');
INSERT INTO [b26865] VALUES (83,'<span data-type="title" id=toc-4>[في الحج]</span>
٨٤-[٨١] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن هشام ابن عروة، عن أبيه؛ أن رسول الله صلى الله عليه وسلم صلى الصلاة بمنى ركعتين، وأن أبا بكر صلاها بمنى ركعتين وأن عمر بن الخطاب صلاها بمنى ركعتين.',NULL,'64','84',NULL,'0');
INSERT INTO [b26865] VALUES (84,'٨٥-[٨٢] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، ويونس بن يزيد، عن نافع؛ أن عبد الله بن عمر كان يكون بمكة، فإذا خرج إلى منى وعرفة قصر الصلاة.',NULL,'64','85',NULL,'0');
INSERT INTO [b26865] VALUES (85,'٨٦-[٨٣] أخبرنا محمد، أنا ابن وهب: أنا ابن جريج، عن عطاء بن أبي رباح قال: ⦗٦٥⦘ لا يفوت الحج حتى ينفجر الفجر من ليلة جمعٍ، قال: قلت لعطاء: أبلغك ذلك عن رسول الله صلى الله عليه وسلم؟ قال عطاء: نعم.',NULL,'64','86',NULL,'0');
INSERT INTO [b26865] VALUES (86,'٨٧-[٨٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن جريج، عن أبي الزبير، عن جابر بن عبد الله أنه قال ذلك.',NULL,'65','87',NULL,'0');
INSERT INTO [b26865] VALUES (87,'٨٨-[٨٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمر بن محمد؛ أن سالم ابن عبد الله بن عمر حدثه؛ أن عمر بن الخطاب قال:
من أدرك ليلة النحر قبل أن يطلع الفجر، فقد أدرك الحج، ومن لم يقف حتى يصبح، فقد فاته الحج.',NULL,'65','88',NULL,'0');
INSERT INTO [b26865] VALUES (88,'٨٩-[٨٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، ويونس بن يزيد، وغيرهما؛ أن نافعاً حدثهم، عن عبد الله بن عمر مثله.',NULL,'65','89',NULL,'0');
INSERT INTO [b26865] VALUES (89,'٩٠-[٨٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني محمد بن أبي حميد، عن ⦗٦٦⦘ محمد بن المنكدر قال: قال رسول الله صلى الله عليه وسلم:
((كل عرفة موقف إلا ما جاوز بطن عرنة، وكل مزدلفة موقف إلا ما خلف بطن محسر)) .',NULL,'65','90',NULL,'0');
INSERT INTO [b26865] VALUES (90,'٩١-[٨٨] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن موسى بن عقبة، عن كريب مولى ابن عباس أنه سمع أسامة بن زيد يقول:
دفع رسول الله صلى الله عليه وسلم من عرفة حتى إذا جاء الشعب نزل، فبال فتوضأ، فلم يسبغ الوضوء فقلت له: الصلاة. فقال: ((الصلاة أمامك)) .
⦗٦٧⦘
فركب حتى إذا جاء المزدلفة نزل فتوضأ، فأسبغ الوضوء، ثم أقيمت الصلاة، فصلى المغرب، ثم أناخ كل واحد بعيره في منزله، ثم أقيمت الصلاة، فصلى العشاء، ولم يصل بينهما شيئاً.',NULL,'66','91',NULL,'0');
INSERT INTO [b26865] VALUES (91,'٩٢-[٨٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، وابن أبي ذئب، عن ابن شهاب، عن سالم بن عبد الله، عن أبيه؛ أن رسول الله صلى الله عليه وسلم صلى المغرب والعشاء بالمزدلفة جميعاً.
قال ابن أبي ذئب في الحديث: لم يناد في كل واحدة منهما إلا بإقامة، ولم يسبح بينهما، ولا على أثر واحدة منهما.',NULL,'67','92',NULL,'0');
INSERT INTO [b26865] VALUES (92,'٩٣-[٩٠] أخبرنا محمد، أنا ابن وهب: أخبرني عبد الله بن عمر، عن نافع، عن عبد الله بن عمر؛ أن النبي صلى الله عليه وسلم كان يجمع بين المغرب والعشاء بالمزدلفة، وأبو بكر الصديق وعمر بن الخطاب، وعبد الله بن عمر.',NULL,'67','93',NULL,'0');
INSERT INTO [b26865] VALUES (93,'٩٤-[٩١] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمرو بن الحارث، أن أبا الزبير المكي أخبره، عن عبد الله بن عباس قال:
كان رسول الله صلى الله عليه وسلم يقدم العيال والضعفة إلى منى من مزدلفة.',NULL,'68','94',NULL,'0');
INSERT INTO [b26865] VALUES (94,'٩٥-[٩٢] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمرو بن الحارث، عن عمرو بن دينار، عن ابن عباس قال:
كنت فيمن يقدم رسول الله صلى الله عليه وسلم إلى منى من المزدلفة من العيال.',NULL,'68','95',NULL,'0');
INSERT INTO [b26865] VALUES (95,'٩٦-[٩٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن أبي ذئب، عن شعبة مولى عبد الله بن عباس، عن ابن عباس؛ أن رسول الله صلى الله عليه وسلم بعث به مع أهله إلى منى يوم النحر، فرموا الجمرة مع الفجر.',NULL,'68','96',NULL,'0');
INSERT INTO [b26865] VALUES (96,'٩٧-[٩٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني يونس بن يزيد، عن ابن شهاب؛ أن سالم بن عبد الله أخبره؛ أن عبد الله بن عمر كان يقدم ضعفة أهله، فيقفون عند المشعر الحرام بالمزدلفة بليل، فيذكرون الله ما بدا لهم، ثم يدفعون قبل أن يقف الإمام، قبل أن يدفع، فمنهم من يقدم ((منى)) لصلاة الفجر)) ، ومنهم من يقدم بعد ذلك، فإذا قدموا رموا الجمرة.
وكان ابن عمر يقول: أرخص في أولئك رسول الله صلى الله عليه وسلم.',NULL,'69','97',NULL,'0');
INSERT INTO [b26865] VALUES (97,'٩٨-[٩٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني يونس بن يزيد، ومالك، وغيرهما؛ أن ابن شهاب أخبرهم، عن عيسى بن طلحة بن عبيد الله أخبره، عن عبد الله بن عمرو بن العاص؛ أن رسول الله صلى الله عليه وسلم وقف للناس (¬١) عام حجة الوداع يسألونه، فجاء رجل فقال: يا رسول الله، لم أشعر فنحرت قبل أن أرمي؟ فقال: ((ارم ولا حرج)) .
قال آخر: يا رسول الله، إني لم أشعر فحلقت رأسي قبل أن أذبح؟
⦗٧٠⦘
فقال: ((اذبح ولا حرج)) .
قال: فما سئل رسول الله صلى الله عليه وسلم يومئذٍ عن شيء قدم أو أخر إلا قال: ((افعل ولا حرج)) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: بالناس]]',NULL,'69','98',NULL,'0');
INSERT INTO [b26865] VALUES (98,'٩٩-[٩٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني أسامة بن زيد الليثي، أن عطاء بن أبي رباح حدثه؛ أنه سمع جابر بن عبد الله يحدث، عن رسول الله صلى الله عليه وسلم مثله. قال: قال رسول الله صلى الله عليه وسلم:
((عرفة موقف، وكل مزدلفة موقف، وكل منى منحر، وكل فجاج مكة طريق ومنحر)) .',NULL,'70','99',NULL,'0');
INSERT INTO [b26865] VALUES (99,'١٠٠-[٩٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن جريج، عن عطاء بن أبي رباح، عن ابن عباس؛ أن النبي صلى الله عليه وسلم لم يرمل في السبع التي أفاض فيها، قال: قال عطاء: لا رمل فيه.',NULL,'70','100',NULL,'0');
INSERT INTO [b26865] VALUES (100,'١٠١-[٩٨] أخبرنا محمد، أنا ابن وهب قال: حدثني عبد الله بن عمر، ومالك بن ⦗٧١⦘ أنس وغيرهما؛ أن نافعاً أخبرهم، عن ابن عمر أن رسول الله صلى الله عليه وسلم قال:
((اللهم ارحم المحلقين)) . قالوا: والمقصرين يا رسول الله. قال: ((اللهم ارحم المحلقين)) . قالوا: والمقصرين يا رسول الله. قال: ((اللهم ارحم المحلقين)) . قالوا: والمقصرين يا رسول الله. قال: ((والمقصرين)) .',NULL,'70','101',NULL,'0');
INSERT INTO [b26865] VALUES (101,'١٠٢-[٩٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، عن نافع، عن ابن عمر؛ أن رسول الله صلى الله عليه وسلم حلق رأسه في حجة الوداع.',NULL,'71','102',NULL,'0');
INSERT INTO [b26865] VALUES (102,'١٠٣-[١٠٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني الليث بن سعد وغيره عن نافع [عن ابن عمر] (¬١) أنه قال:
حلق رسول الله صلى الله عليه وسلم وطائفة من أصحابه، وقصر بعضهم.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]]',NULL,'71','103',NULL,'0');
INSERT INTO [b26865] VALUES (103,'١٠٤-[١٠١] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، وابن جريج، عن أبي الزبير، عن جابر بن عبد الله قال: رأيت رسول الله صلى الله عليه وسلم رمى جمرة العقبة ⦗٧٢⦘ أول يوم ضحى، وهي واحدة، وأما بعد ذلك، فبعد زوال الشمس.',NULL,'71','104',NULL,'0');
INSERT INTO [b26865] VALUES (104,'١٠٥-[١٠٢] أخبرنا محمد، أنا ابن وهب قال: أخبرني سفيان الثوري؛ أنه سمع أبا عمران يحدث؛ أن قدامة بن عمار الكلابي قال: رأيت رسول الله صلى الله عليه وسلم يرمي الجمرة على ناقة صهباء، لا ضرب، ولا طرد، ولا إليك إليك.',NULL,'72','105',NULL,'0');
INSERT INTO [b26865] VALUES (105,'١٠٦-[١٠٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني الحارث بن نبهان، عن محمد بن عبيد الله، عن عمرو بن شعيب، عن أبيه، عن جده؛ أن رسول الله صلى الله عليه وسلم كان يقوم بين الجمرتين، فيطيل القيام، ويقوم عند الجمرة الأخرى أقل من ⦗٧٣⦘ ذلك، ولا يقوم عند جمرة العقبة.',NULL,'72','106',NULL,'0');
INSERT INTO [b26865] VALUES (106,'١٠٧-[١٠٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، عن أبي الزبير، عن جابر بن عبد الله قال:
رأيت رسول الله صلى الله عليه وسلم يأخذ حصى الخذف ويرمي على راحلته، ويقول: ((خذوا منا مناسككم، فإني لا أدري لعلي غير حاج بعد حجتي هذه)) .',NULL,'73','107',NULL,'0');
INSERT INTO [b26865] VALUES (107,'١٠٨-[١٠٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني يحيى بن أيوب، عن عبد الرحمن بن حرملة الأسلمي، عن يحيى بن هنيدة؛ أن حرملة بن عمرو الأسلمي حدثه قال:
رأيت رسول الله صلى الله عليه وسلم بعرفة، وعمي مردفي، وهو واضع أصبعيه أحدهما على الأخرى.
فقلت لعمي: ماذا يقول؟ قال: يقول: ارموا الجمرة بمثل حصى الخذف.',NULL,'73','108',NULL,'0');
INSERT INTO [b26865] VALUES (108,'١٠٩-[١٠٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن عبد الله ⦗٧٤⦘ ابن أبي بكر بن حزم، عن أبيه؛ أن البداح أخبره، عن أبيه عاصم بن عدي أخبره، عن رسول الله صلى الله عليه وسلم؛ أنه رخص لرعاء الإبل في البيتوتة يرمون يوم النحر، ثم يرمون من الغد وبعد الغد ليومين، ثم يرمون يوم النفر.',NULL,'73','109',NULL,'0');
INSERT INTO [b26865] VALUES (109,'١١٠-[١٠٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن جريج، عن عطاء بن أبي رباح؛ أن رسول الله صلى الله عليه وسلم رخص لرعاء الإبل أن يرموا الجمار بالليل.',NULL,'74','110',NULL,'0');
INSERT INTO [b26865] VALUES (110,'١١١-[١٠٨] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمر بن قيس، عن عطاء قال: سمعت ابن عباس يقول: قال رسول الله صلى الله عليه وسلم:
((الراعي يرمي بالليل، ويرعى بالنهار)) .',NULL,'75','111',NULL,'0');
INSERT INTO [b26865] VALUES (111,'١١٢-[١٠٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني يحيى بن أيوب، عن عمارة بن غزية، عن محمد بن إبراهيم، عن أبي سلمة بن عبد الرحمن، عن النبي صلى الله عليه وسلم مثله.',NULL,'75','112',NULL,'0');
INSERT INTO [b26865] VALUES (112,'١١٣-[١١٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، ومالك ابن أنس، وغيرهما؛ أن أيوب بن أبي تميمة أخبرهم، عن سعيد بن جبير، عن عبد الله بن عباس أنه قال:
من نسى من نسكه شيئاً أو تركه، فليهريق دماً.',NULL,'75','113',NULL,'0');
INSERT INTO [b26865] VALUES (113,'١١٤-[١١١] أخبرنا محمد، أنا ابن وهب قال: أخبرني طلحة بن عمرو، أنه سمع عطاء بن أبي رباح يذكر عن عائشة زوج النبي صلى الله عليه وسلم قالت:
طيبت رسول الله صلى الله عليه وسلم يوم النحر بعد رمي الجمرة العقبة، قبل أن يطوف بالبيت.',NULL,'76','114',NULL,'0');
INSERT INTO [b26865] VALUES (114,'١١٥-[١١٢] أخبرنا محمد، أنا ابن وهب قال: أخبرني سفيان الثوري، عن سلمة ابن كهيل، عن الحسن العرني، عن ابن عباس، أنه قال:
إذا رميتم الجمرة فقد حللتم من كل شيء كان عليكم حراماً، إلا النساء حتى تطوفوا بالبيت.
فقال رجل: والطيب يا أبا العباس؟ فقال له: رأيت رسول الله صلى الله عليه وسلم يضمخ رأسه بالسُّك [أفطيب هو] (¬١) أم لا؟.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]] .',NULL,'76','115',NULL,'0');
INSERT INTO [b26865] VALUES (115,'١١٦-[١١٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، وغير واحد؛ ⦗٧٧⦘ أن نافعاً حدثهم، عن عبد الله بن عمر؛ أن عمر بن الخطاب خطب الناس بعرفة يعلمهم أمر الحج، [وكان] (¬١) فيما قال: إذا جئتم منى، فمن رمى الجمرة فقد حل له ما حرم عليه إلا النساء والطيب، لا يمس أحد نساء ولا طيباً حتى يطوف بالبيت.
_________
(¬١) ما بين المعقوفتين ليس في الأصل، وأثبتناه من من السنن الكبرى للبيهقي.',NULL,'76','116',NULL,'0');
INSERT INTO [b26865] VALUES (116,'١١٧- قال مالك: وحدثني عبد الله بن دينار، عن ابن عمر قال: قال عمر ابن الخطاب:
من رمى الجمرة ثم حلق أو قصر أو نحر هدياً إن كان معه، فقد حل له ما حرم عليه إلا النساء والطيب، حتى يطوف بالبيت.',NULL,'77','117',NULL,'0');
INSERT INTO [b26865] VALUES (117,'١١٨-[١١٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، عن أبي الزبير؛ أن عمر بن الخطاب قال على المنبر:
إذا رميتم الجمرة فقد حل لكم كل شيء إلا النساء والطيب. فقالت عائشة: يغفر الله لهذا الشيخ، أنا طيبت رسول الله بيدي هذا اليوم.',NULL,'77','118',NULL,'0');
INSERT INTO [b26865] VALUES (118,'١١٩-[١١٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمرو بن الحارث؛ أن قتادة بن دعامة حدثه، عن أنس بن مالك؛ أن رسول الله صلى الله عليه وسلم صلى الظهر والعصر ⦗٧٨⦘ والمغرب والعشاء ورقد رقدة بالمحصب ثم ركب إلى البيت فطاف.',NULL,'77','119',NULL,'0');
INSERT INTO [b26865] VALUES (119,'١٢٠-[١١٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن عبد الله بن أبي بكر، عن أبيه، عن أبي سلمة بن عبد الرحمن، عن أم سليم قالت:
استفتيت رسول الله صلى الله عليه وسلم، وحاضت أو ولدت بعد ما أفاضت يوم النحر، فأذن لها رسول الله صلى الله عليه وسلم فخرجت.',NULL,'78','120',NULL,'0');
INSERT INTO [b26865] VALUES (120,'١٢١-[١١٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني يونس بن يزيد، وغيره من أهل العلم، عن ابن شهاب، عن أبي سلمة بن عبد الرحمن، وعروة بن الزبير، عن عائشة زوج النبي صلى الله عليه وسلم قالت:
طمثت صفية بنت حيي زوج النبي صلى الله عليه وسلم في حجة الوداع بعد ما أفاضت طاهراً، فطافت بالبيت.
فذكرت ذلك لرسول الله صلى الله عليه وسلم فقال: ((أحابستنا هي؟)) قال: فقلت: يا رسول الله، إنها قد أفاضت، وهي طاهرة ثم طمثت بعد الإفاضة. فقال رسول الله: ((فلتنفر)) .',NULL,'78','121',NULL,'0');
INSERT INTO [b26865] VALUES (121,'١٢٢-[١١٨] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، وغيره؛ ⦗٧٩⦘ أن نافعاً حدثهم، عن عبد الله بن عمر؛ أن رسول الله صلى الله عليه وسلم كان إذا صدر من الحج أو العمرة أناخ بالبطحاء التي بذي الحليفة فصلى بها. قال نافع: فكان ابن عمر يفعل ذلك.',NULL,'78','122',NULL,'0');
INSERT INTO [b26865] VALUES (122,'١٢٣-[١١٩] أخبرنا محمد، أنا ابن وهب قال: حدثني عمر بن محمد بن عبد الله ابن عمر ومالك بن أنس، وغيرهما؛ أن نافعاً حدثهم، عن عبد الله بن عمر؛ أن رسول الله صلى الله عليه وسلم كان إذا قفل من غزو أو حج أو عمرة، يكبر على كل شرف من الأرض ثلاث تكبيرات، ويقول:
((لا إله إلا الله وحده لا شريك له، له الملك وله الحمد، وهو على كل شيء قدير، آيبون تائبون عابدون ساجدون لربنا حامدون، صدق الله وعده، ونصر عبده، وهزم الأحزاب وحده)) .',NULL,'79','123',NULL,'0');
INSERT INTO [b26865] VALUES (123,'١٢٤-[١٢٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، والليث بن ⦗٨٠⦘ سعد، عن أبي الزبير، عن جابر بن عبد الله قال:
أقبلنا مع رسول الله صلى الله عليه وسلم مهلين بحج مفرداً.',NULL,'79','124',NULL,'0');
INSERT INTO [b26865] VALUES (124,'١٢٥-[١٢١] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، ومالك بن أنس، عن محمد بن عبد الرحمن، عن عروة بن الزبير، عن عائشة زوج النبي صلى الله عليه وسلم أنها قالت:
أهل رسول الله صلى الله عليه وسلم بحج.',NULL,'80','125',NULL,'0');
INSERT INTO [b26865] VALUES (125,'١٢٦-[١٢٢] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، عن نافع، أن ابن عمر قال:
من قرن بين الحج والعمرة، لم يطف لهما إلا طوافاً واحداً، ولم يحل منهما إلى يوم النحر.',NULL,'81','126',NULL,'0');
INSERT INTO [b26865] VALUES (126,'١٢٧-[١٢٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني رجال من أهل العلم منهم: مالك بن أنس، عن ابن شهاب، عن عروة بن الزبير، عن عائشة زوج النبي صلى الله عليه وسلم؛ أنها قالت:
أما الذين كانوا جمعوا الحج والعمرة، فإنما كانوا طافوا طوافاً واحداً.',NULL,'81','127',NULL,'0');
INSERT INTO [b26865] VALUES (127,'١٢٨- قال: وسمعت ابن سمعان يقول: حدثني محمد بن علي، عن أبيه، عن جده علي بن أبي طالب؛ أنه أهل بحجة وعمرة معاً، فطاف لهما طوافاً واحداً، وسعى لهما سعياً واحداً.',NULL,'82','128',NULL,'0');
INSERT INTO [b26865] VALUES (128,'١٢٩-[١٢٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن ابن شهاب، عن عروة بن الزبير، عن عائشة زوج النبي صلى الله عليه وسلم:
أنها قالت - في الذين كانوا أهلوا مع النبي صلى الله عليه وسلم بعمرة في حجة الوداع: إنهم طافوا بالبيت وبين الصفا والمروة، ثم طافوا طوافاً آخر بعد أن يرجعوا من منى.',NULL,'82','129',NULL,'0');
INSERT INTO [b26865] VALUES (129,'١٣٠-[١٢٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني يحيى بن أيوب، عن حميد الطويل، عن بكر بن عبد الله المزني، عن سالم بن عبد الله بن عمر، عن أبيه؛ أنه قال:
إذا فرقت بين الحج والعمرة، فطف لكل واحد.',NULL,'82','130',NULL,'0');
INSERT INTO [b26865] VALUES (130,'١٣١-[١٢٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، وغيره؛ أن نافعاً حدثهم؛ أن عبد الله بن عمر خرج في الفتنة معتمراً، وقال:
إن صددت عن البيت صنعنا كما صنع رسول الله صلى الله عليه وسلم، فخرج فأهل بالعمرة، وسار حتى إذا ظهر ظاهر البيداء، التفت إلى أصحابه فقال: ما أمرهما إلا واحد، أشهدكم أني قد أوجبت الحج مع العمرة.
فخرج حتى جاء البيت، فطاف به، وطاف بين الصفا والمروة سبعاً، لم يزد عليه، ورأى أن ذلك مجزياً عنه، وأهدى.',NULL,'83','131',NULL,'0');
INSERT INTO [b26865] VALUES (131,'١٣٢-[١٢٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن سمعان، عن نافع؛ ⦗٨٤⦘ أن سالماً وعبد الله (¬١) بن عبد الله بن عمر كلما أباهما ليالي نزل الحجاج بابن الزبير قبل أن نقتتل، فقالا: لا يضرك ألا تحج العام، فإنا نخاف أن يحال بينك وبين البيت.
فقال: {لقد كان لكم في رسول الله أسوةٌ حسنةٌ} قد خرجنا مع رسول الله صلى الله عليه وسلم معتمرين في عام الحديبية، فحال كفار قريش دون البيت، فنحر رسول الله صلى الله عليه وسلم هديه، وحلق رأسه، وحل من إحرامه، ثم رجع فأهل بعمرة، وقال: إن حيل بيني وبينه، فعلت كما فعل رسول الله صلى الله عليه وسلم وأنا معه.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: وعبيد الله]] .',NULL,'83','132',NULL,'0');
INSERT INTO [b26865] VALUES (132,'١٣٣-[١٢٨] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، وعبد الله بن عمر، وغيرهما؛ أن نافعاً حدثهم، عن سليمان بن يسار؛ أن هبار بن الأسود جاء يوم النحر، وعمر ينحر فقال: يا أمير المؤمنين، أخطأنا، كنا نرى [أن] (¬١) هذا اليوم يوم عرفة؟
فقال عمر بن الخطاب: اذهب إلى مكة فطف بالبيت سبعاً، وبين الصفا والمروة، أنت ومن معك، ثم انحر هدياً إن كان معك، ثم احلقوا أو قصروا وارجعوا، فإذا كان حج قابل، فحجوا واهدوا، فمن لم يجد، فصيام ثلاثة أيام في الحج وسبعة إذا رجعتم.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]] .',NULL,'84','133',NULL,'0');
INSERT INTO [b26865] VALUES (133,'١٣٤-[١٢٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، وابن لهيعة، ⦗٨٥⦘ عن أبي الأسود، عن عروة بن الزبير، عن عائشة زوج النبي صلى الله عليه وسلم، أنها قالت:
خرجنا مع رسول الله صلى الله عليه وسلم فمنا من أهل بحجة، ومنا من أهل بعمرة وحج، ومنا أهل بعمرة.
قالت: فأما من أحرم بالحج، أو جمع الحج والعمرة، فلم يحل، وحل الذين كانوا أهلوا بالعمرة.',NULL,'84','134',NULL,'0');
INSERT INTO [b26865] VALUES (134,'١٣٥-[١٣٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن عبد الله بن دينار قال: سمعت عبد الله بن عمر يقول:
من اعتمر في أشهر الحج، في شوال أو ذي القعدة أو ذي الحجة، فقد استمتع ووجب عليه الهدي، أو الصيام إن لم يجد هدياً.',NULL,'85','135',NULL,'0');
INSERT INTO [b26865] VALUES (135,'١٣٦-[١٣١] أخبرنا محمد، أنا ابن وهب قال: حدثني عبد الله بن عمر، وغير واحد؛ أن نافعاً حدثهم، أن عبد الله بن عمر، مثله.',NULL,'85','136',NULL,'0');
INSERT INTO [b26865] VALUES (136,'١٣٧-[١٣٢] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمر بن محمد، وعبد الله ابن عمر، وغيرهما؛ أن نافعاً حدثهم؛ أن عبد الله بن عمر قال:
من اعتمر في أشهر الحج، فلم يكن معه هدي، ولم يصم الثلاثة الأيام (¬١) قبل أيام التشريق، فليصم أيام منى.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: أيام]] .',NULL,'85','137',NULL,'0');
INSERT INTO [b26865] VALUES (137,'١٣٨-[١٣٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن ابن شهاب، عن عروة بن الزبير، عن عائشة زوج النبي صلى الله عليه وسلم؛ أنها قالت:
الصيام لمن تمتع بالعمرة إلى الحج، فمن لم يجد هدياً ما بين أن يهل بالحج إلى يوم عرفة فإن لم يصم صام أيام منى.',NULL,'85','138',NULL,'0');
INSERT INTO [b26865] VALUES (138,'١٣٩-[١٣٤] أخبرنا محمد، أنا ابن وهب قال: قال مالك: وحدثني ابن شهاب، ⦗٨٦⦘ عن سالم بن عبد الله بن عمر، عن أبيه [عبد الله] (¬١) مثل ذلك.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]] .',NULL,'85','139',NULL,'0');
INSERT INTO [b26865] VALUES (139,'١٤٠-[١٣٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، ومالك بن أنس، وغيرهما، عن نافع، عن عبد الله بن عمر؛ أن عمر بن الخطاب قال:
افصلوا بين حجكم وعمرتكم، فإنه أتم لحج أحدكم، وأتم لعمرته أن يعتمر في غير أشهر الحج.',NULL,'86','140',NULL,'0');
INSERT INTO [b26865] VALUES (140,'١٤١-[١٣٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني حرملة بن عمران التجيبي قال: حدثني عبيد بن وردان التجبيبي؛ أنه رأى عائشة زوج النبي صلى الله عليه وسلم معتمرة في ذي الحجة.',NULL,'86','141',NULL,'0');
INSERT INTO [b26865] VALUES (141,'١٤٢-[١٣٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن علقمة بن أبي علقمة، عن أمه قالت:
كانت عائشة تعتمر بمكة بعد الحج.',NULL,'86','142',NULL,'0');
INSERT INTO [b26865] VALUES (142,'١٤٣-[١٣٨] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، عن عبد الرحمن بن القاسم، عن أبيه، عن عائشة، مثله.',NULL,'86','143',NULL,'0');
INSERT INTO [b26865] VALUES (143,'١٤٤-[١٣٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني الليث بن سعد؛ أن أبا الزبير أخبره، عن جابر بن عبد الله؛ أن رسول الله صلى الله عليه وسلم أعمر عائشة من التنعيم ليلة الحصبة.',NULL,'87','144',NULL,'0');
INSERT INTO [b26865] VALUES (144,'١٤٥-[١٤٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني عيسى بن يونس، عن سعيد ابن أبي عروبة، عن قتادة، عن معاذة العدوية، عن عائشة زوج النبي صلى الله عليه وسلم قالت:
تمت عمرة الدهر كله إلا أربعة أيام: يوم النحر، وثلاثة أيام التشريق.',NULL,'87','145',NULL,'0');
INSERT INTO [b26865] VALUES (145,'١٤٦-[١٤١] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، عن عبد الرحمن بن القاسم، عن أبيه، عن عائشة:
أنها كانت تعتمر في رجب.',NULL,'87','146',NULL,'0');
INSERT INTO [b26865] VALUES (146,'١٤٧-[١٤٢] أخبرنا محمد، أنا ابن وهب قال: أخبرني يحيى بن أيوب، وابن سمعان عن يحيى بن سعيد، عن سعيد بن المسيب، أن عائشة كانت تعتمر في آخر ذي الحجة من الجحفة، وتعتمر في رجب من المدينة، وتهل من ذي الحليفة.',NULL,'87','147',NULL,'0');
INSERT INTO [b26865] VALUES (147,'١٤٨-[١٤٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن علقمة، عن أمه، عن عائشة؛ أنها كانت تأتي الجحفة قبل هلال المحرم، فتقيم بها حتى ترى الهلال، فإذا رأت الهلال أهلت بعمرة.',NULL,'88','148',NULL,'0');
INSERT INTO [b26865] VALUES (148,'١٤٩-[١٤٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، وأسامة ابن زيد، عن نافع، عن عبد الله بن عمر؛ أنه كان يعتمر في رجب ويهدي.
قال نافع: وليس الهدي بواجب، إنما كان منه تطوعاً.',NULL,'88','149',NULL,'0');
INSERT INTO [b26865] VALUES (149,'١٥٠-[١٤٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، وأسامة بن ⦗٨٩⦘ زيد، عن ابن شهاب، عن سالم قال:
كان عبد الله بن عمر ينكر الاشتراط في الحج، ويقول: أليس حسبكم سنة رسول الله صلى الله عليه وسلم إن حصر أحدكم عن الحج، طاف بالبيت وبالصفا والمروة، ثم حل من كل شيء حتى يحج عاماً قابلاً، ويهدي، أو يصوم فإن لم يجد.
قال يونس: قال ربيعة: لا نعلم شرطاً يجوز في إحرام.',NULL,'88','150',NULL,'0');
INSERT INTO [b26865] VALUES (150,'١٥١-[١٤٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمرو بن الحارث، ومالك ابن أنس، عن عبد الرحمن بن القاسم، عن أبيه، عن عائشة زوج النبي صلى الله عليه وسلم؛ أنها قالت:
قدمت مكة وأنا حائض، ولم أطف بالبيت، ولا بين الصفا والمروة.
فشكوت ذلك لرسول الله صلى الله عليه وسلم، فقال: ((افعلي ما يفعل الحاج غير أن لا تطوفي بالبيت حتى تطهري)) .',NULL,'90','151',NULL,'0');
INSERT INTO [b26865] VALUES (151,'١٥٢-[١٤٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن ابن شهاب، عن عروة بن الزبير، عن عائشة، أنها قالت:
خرجنا مع رسول الله صلى الله عليه وسلم، فأهللت بعمرة، فقدمت مكة وأنا حائض، فلم أطف بالبيت، ولا بين الصفا والمروة.
فشكوت ذلك إلى رسول الله صلى الله عليه وسلم، فقال: ((أهلي بالحج ودعي العمرة)) .
فلما قضينا الحج أرسلني رسول الله صلى الله عليه وسلم مع عبد الرحمن بن أبي بكر إلى التنعيم، فاعتمرت، فقال رسول الله صلى الله عليه وسلم: ((هذه مكان عمرتك)) .',NULL,'90','152',NULL,'0');
INSERT INTO [b26865] VALUES (152,'١٥٣-[١٤٨] أخبرنا محمد، أنا ابن وهب قال: أخبرني الليث بن سعد؛ أن أبا الزبير أخبره، عن جابر بن عبد الله؛ أن عائشة أقبلت مهلة بعمرة حتى إذا كانت بسرف عركت، فدخل عليها النبي صلى الله عليه وسلم، فوجدها تبكي، فقال: ((ما يبكيك؟)) قالت: حضت، ولم أحلل ولم أطف بالبيت، والناس يذهبون إلى الحج الآن.
قال: ((فإن هذا أمر كتبه الله على بنات آدم، فاغتلسي ثم أهلي بالحج)) .
ففعلت ووقفت المواقف حتى إذا طهرت طفت بالكعبة وبالصفا والمروة، ثم قال: ((قد حللت من حجك وعمرتك جميعاً)) .
فقالت: يا رسول الله، إني أجد في نفسي أني لم أطف بالبيت حتى حججت.
قال: ((فاذهب بها يا عبد الرحمن، فأعمرها من التنعيم)) ، وذلك ليلة الحصبة.',NULL,'91','153',NULL,'0');
INSERT INTO [b26865] VALUES (153,'١٥٤-[١٤٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، ومالك بن أنس، عن عبد الرحمن بن القاسم، عن أبيه؛ أن أسماء بنت عميس ولدت محمد بن أبي بكر الصديق بالبيداء، فذكر ذلك أبو بكر لرسول الله صلى الله عليه وسلم فقال: ((مرها ⦗٩٢⦘ فلتغتسل، ثم تهل)) .
إلا أن ابن عمر قال: بذي الحليفة.
١٥٥- قال عبد الله بن عمر: وحدثني نافع بمثل ذلك.',NULL,'91','154',NULL,'0');
INSERT INTO [b26865] VALUES (154,'١٥٦-[١٥٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني يونس بن يزيد، وعمرو بن الحارث، والليث بن سعد؛ أن ابن شهاب أخبرهم، عن سعيد بن المسيب، مثله عن النبي صلى الله عليه وسلم.',NULL,'92','156',NULL,'0');
INSERT INTO [b26865] VALUES (155,'١٥٧-[١٥١] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن سعيد ابن أبي سعيد المقبري، عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم [قال] :
((لا يحل لامرأة تؤمن بالله ورسوله تسافر مسيرة يوم وليلة إلا مع ذي محرمٍ منها)) .',NULL,'92','157',NULL,'0');
INSERT INTO [b26865] VALUES (156,'١٥٨-[١٥٢] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، والليث بن سعد؛ أن ابن شهاب أخبرهما أن سليمان بن يسار أخبره؛ أن عبد الله بن عباس أخبره قال: كان الفضل بن عباس رديف رسول الله صلى الله عليه وسلم، قال:
فأتت رسول الله صلى الله عليه وسلم امرأةٌ من خثعم تستفتيه، فجعل الفضل ينظر إليها وتنظر إليه، فجعل رسول الله صلى الله عليه وسلم يصرف وجه الفضل بيده إلى الشق الآخر.
قالت: يا رسول الله، إن فريضة الله في الحج على عباده أدركت أبي شيخاً كبيراً لا يستطيع أن يثبت على الراحلة، أفأحج عنه؟
قال: ((نعم)) وذلك في [عام] (¬١) حجة الوداع.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]] .',NULL,'93','158',NULL,'0');
INSERT INTO [b26865] VALUES (157,'١٥٩-[١٥٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن أيوب ⦗٩٤⦘ ابن أبي تميمة، عن محمد بن سيرين، عن عبد الله بن عباس؛ أن رجلاً أتى النبي ⦗٩٥⦘ صلى الله عليه وسلم فقال: إن أمي امرأة كبيرة، لا تستطيع أن نركبها على البعير، لا تستمسك وإن ربطتها خفت أن تموت، أفأحج عنها؟ قال: ((نعم)) .',NULL,'93','159',NULL,'0');
INSERT INTO [b26865] VALUES (158,'١٦٠-[١٥٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمرو بن الحارث، عن ⦗٩٦⦘ قتادة بن دعامة؛ أن سعيد بن جبير حدثه؛ أن عبد الله بن عباس مر به رجل يهل، يقول:
لبيك بحجة عن شبرمة. فقال: وما شبرمة؟ قال: أوصى أن يحج عنه. قال: ⦗٩٧⦘ أحججت أنت؟ قال: لا. قال: فابدأ أنت فاحجج (¬١) عن نفسك، ثم احجج عن شبرمة.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: فحج]] .',NULL,'95','160',NULL,'0');
INSERT INTO [b26865] VALUES (159,'١٦١-[١٥٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن ⦗٩٨⦘ عبد الكريم الجزري، عن مجاهد بن جبر، عن عبد الرحمن بن أبي ليلى، عن كعب بن عجرة، أنه كان مع رسول الله صلى الله عليه وسلم محرماً، فآذاه القمل.
فأمره رسول الله صلى الله عليه وسلم أن يحلق رأسه، وقال: ((صم ثلاثة أيام، أو أطعم ستة مساكين؛ مدين مدين، أو انسك شاة، أي ذلك فعلت أجزأ عنك)) .',NULL,'97','161',NULL,'0');
INSERT INTO [b26865] VALUES (160,'١٦٢-[١٥٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني حفص بن ميسرة، عن موسى ابن عقبة، عن نافع؛ أن عبد الله بن عمر كان يقطر في عينيه الصبر وهو محرم.',NULL,'98','162',NULL,'0');
INSERT INTO [b26865] VALUES (161,'١٦٣-[١٥٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، والليث بن سعد، وعمر بن قيس، عن عطاء بن أبي رباح، عن عبد الله بن عباس؛ أن رسول الله صلى الله عليه وسلم احتجم وهو محرم.',NULL,'98','163',NULL,'0');
INSERT INTO [b26865] VALUES (162,'١٦٤-[١٥٨] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، عن ⦗٩٩⦘ حميد الطويل، عن أنس بن مالك؛ أن رسول الله صلى الله عليه وسلم احتجم وهو محرم.',NULL,'98','164',NULL,'0');
INSERT INTO [b26865] VALUES (163,'١٦٥-[١٥٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، وغير واحد، عن يحيى بن سعيد، عن سليمان بن يسار؛ أن النبي صلى الله عليه وسلم احتجم وهو محرم بمكان من طريق مكة يقال له: لحى جمل.',NULL,'99','165',NULL,'0');
INSERT INTO [b26865] VALUES (164,'١٦٦-[١٦٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن سمعان؛ أن ربيعة أخبره، أن أبا هند يسار الشامي هو حجم رسول الله صلى الله عليه وسلم بقرن وشفرة من الشكوى الذي كان يعتريه من الأكلة التي أكلها بخيبر.',NULL,'99','166',NULL,'0');
INSERT INTO [b26865] VALUES (165,'١٦٧-[١٦١] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، وعبد الله بن عمر، عن نافع؛ أن عبد الله بن عمر كان يشعر بدنه من الشق الأيسر، إلا أن يكون صعاباً مقرنة، فإذا لم يستطع أن يدخل بينها، أشعر من الشق الأيمن، وإذا ⦗١٠٠⦘ أراد أن يشعرها، وجهها إلى القبلة، وإذا أشعرها قال: ((بسم الله والله أكبر)) . وأنه كان يشعرها بيده، وينحرها بيده قياماً.',NULL,'99','167',NULL,'0');
INSERT INTO [b26865] VALUES (166,'١٦٨-[١٦٢] أخبرنا محمد، أنا ابن وهب قال: أخبرني الحارث بن نبهان، عن منصور عن إبراهيم، عن الأسود، عن عائشة قالت:
كنت أفتل القلائد لهدي رسول الله صلى الله عليه وسلم الغنم، ثم يبعث بها ويقعد حلالاً.',NULL,'100','168',NULL,'0');
INSERT INTO [b26865] VALUES (167,'١٦٩-[١٦٣] أخبرنا محمد، أنا ابن وهب: أنا أفلح بن حميد؛ أن القاسم بن محمد حدثه، عن عائشة زوج النبي صلى الله عليه وسلم، أنها قالت:
فتلت قلائد هدي رسول الله صلى الله عليه وسلم بيدي، ثم قلدها رسول الله بيده، وأشعرها وساقها إلى البيت، وتخلف عن الحج، فما حرم عليه شيء كان له حلال.',NULL,'100','169',NULL,'0');
INSERT INTO [b26865] VALUES (168,'١٧٠-[١٦٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن ⦗١٠١⦘ عبد الله بن أبي بكر، عن عمرة بنت عبد الرحمن، عن عائشة؛ أنها قالت:
[أنا] (¬١) فتلت قلائد هدي رسول الله صلى الله عليه وسلم بيدي، ثم قلدها رسول الله صلى الله عليه وسلم بيديه، ثم بعث بها مع أبي بكر، لم يحرم على رسول الله شيء كان أحله الله له حتى نحر الهدي.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]] .',NULL,'100','170',NULL,'0');
INSERT INTO [b26865] VALUES (169,'١٧١-[١٦٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني رجال من أهل العلم، عن هشام بن عروة، وأبي الأسود، عن عروة بن الزبير، عن عائشة، عن رسول الله صلى الله عليه وسلم بذلك.',NULL,'101','171',NULL,'0');
INSERT INTO [b26865] VALUES (170,'١٧٢-[١٦٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمرو بن الحارث، عن ⦗١٠٢⦘ عبد الرحمن بن القاسم، ومحمد بن عبد الرحمن، عن القاسم بن محمد، عن عائشة أنها قالت:
ما نعلم حراماً يحله إلا الطواف بالبيت.',NULL,'101','172',NULL,'0');
INSERT INTO [b26865] VALUES (171,'١٧٣-[١٦٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمرو بن الحارث، ومالك ابن أنس، وغيرهما؛ أن يحيى بن سعيد حدثهم، عن عمرة، عن عائشة؛ أنها سمعتها تقول: لا يحرم إلا من أهل ولبد (¬١) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: ولبد]] .',NULL,'102','173',NULL,'0');
INSERT INTO [b26865] VALUES (172,'١٧٤-[١٦٨] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، وعبد الله بن عمر، وغير واحد، أن نافعاً حدثهم؛ أن عبد الله بن عمر قال:
الهدي ما قلد وأشعر، ووقف به بعرفة.',NULL,'102','174',NULL,'0');
INSERT INTO [b26865] VALUES (173,'١٧٥-[١٦٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، ومالك ابن أنس، وغير واحد أن نافعاً حدثهم؛ أن عبد الله بن عمر كان يقول:
في البدن الثني فما فوقه.',NULL,'102','175',NULL,'0');
INSERT INTO [b26865] VALUES (174,'١٧٦-[١٧٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني أسامة بن زيد الليثي، عن نافع؛ أن ابن عمر قال: وكل هدي لم يقلد يوم عرفة ويوقف، فهو جزور.',NULL,'103','176',NULL,'0');
INSERT INTO [b26865] VALUES (175,'١٧٧-[١٧١] أخبرنا محمد، أنا ابن وهب قال: أخبرني يحيى بن عبد الله، وسعيد الجمحي، ومالك، وغيرهم؛ أن هشام بن عروة أخبره، عن أبيه؛ أن صاحب هدي رسول الله صلى الله عليه وسلم سأله: كيف نصنع بما عطب من الهدي؟
فقال رسول الله: ((انحرها، وألق قلائدها ونعالها في دمها، وخل بينها وبين الناس يأكلونها)) .',NULL,'103','177',NULL,'0');
INSERT INTO [b26865] VALUES (176,'١٧٨-[١٧٢] أخبرنا محمد، أنا ابن وهب: أنا مالك بن أنس، وعمرو بن الحارث، عن أبي الزبير، عن جابر بن عبد الله قال:
⦗١٠٤⦘
نحرنا مع رسول الله عام الحديبية البدنة عن سبعة، والبقرة عن سبعة.',NULL,'103','178',NULL,'0');
INSERT INTO [b26865] VALUES (177,'١٧٩-[١٧٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني إسماعيل بن عياش، عن عطاء الخراساني، عن ابن عباس قال:
جاء رجل إلى النبي صلى الله عليه وسلم فقال: إني نذرت بدنة، فلم أجدها. فقال النبي: ((اذبح سبعاً من الغنم)) .',NULL,'104','179',NULL,'0');
INSERT INTO [b26865] VALUES (178,'١٨٠-[١٧٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمرو بن الحارث، عن هشام بن عروة، عن أبيه، عن عائشة؛ أنها أضلت لها بدنتان، فأرسل عبد الله ابن الزبير بأخريين، فنحرتهما، ثم وجدت بعد ذلك اللتين ضلتا فنحرتهما.',NULL,'104','180',NULL,'0');
INSERT INTO [b26865] VALUES (179,'١٨١-[١٧٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن أبي ذئب، عن عجلان مولى المشمعل، عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم سئل عن ركوب الهدي، قال: إنها بدنة؟ فقال: ((اركبها ويلك)) .',NULL,'105','181',NULL,'0');
INSERT INTO [b26865] VALUES (180,'<span data-type="title" id=toc-5>من كتاب الزكاة</span>
١٨٢-[١٧٨] حدثنا بحر بن نصر بن سابق الخولاني قال: قرئ على عبد الله بن وهب وأنا أسمع: أخبرك ابن لهيعة، عن عمارة بن غزية، عن عبد الله بن أبي ⦗١٠٦⦘ بكر بن حزم أخبره، أن هذا كتاب رسول الله صلى الله عليه وسلم لعمرو بن حزم:
((صدقة الغنم ليس فيها صدقة حتى يبلغ أربعين شاة، فإذا بلغت أربعين شاة، ⦗١٠٧⦘ ففيها شاة إلى عشرين ومائة، فإذا كانت إحدى وعشرين ومائة، ففيها شاتان إلى مائتي شاة، فإذا كانت مائتي شاة وشاة، ففيها ثلاث شياه إلى ثلاثمائة شاة، فما زاد ففي كل مائة شاة، ولا يجمع بين متفرق، ولا يفرق بين مجتمع خشية الصدقة، ولا يخرج في الصدقة هرمة ولا ذات عوار، ولا تيس إلا أن يشاء المصدق، وما كان من خليطين فإنهما يتراجعان بالسوية.',NULL,'105','182',NULL,'0');
INSERT INTO [b26865] VALUES (181,'١٨٣-[١٧٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس، عن ابن شهاب، عن سالم وعبد الله ابني عبد الله بن عمر، عن رسول الله صلى الله عليه وسلم نحو ذلك.',NULL,'107','183',NULL,'0');
INSERT INTO [b26865] VALUES (182,'١٨٤-[١٨٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن يزيد ابن عمرو المعافري عمن سمع عقبة بن عامر الجهني يقول:
بعثني رسول الله صلى الله عليه وسلم ساعياً، فاستأذنته بأكل الصدقة، فأذن لي.',NULL,'108','184',NULL,'0');
INSERT INTO [b26865] VALUES (183,'١٨٥-[١٨١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، ويحيى ابن عبد الله بن سالم، ومالك، وسفيان الثوري، وسفيان بن عيينة؛ أن عمرو بن ⦗١٠٩⦘ يحيى المازني حدثهم، عن أبيه، عن أبي سعيد الخدري؛ أن رسول الله صلى الله عليه وسلم قال:
((ليس فيما دون خمس أواق من الورق صدقة، وليس فيما دون خمسة أوسق من التمر صدقة، وليس فيما دون خمس ذودٍ من الإبل صدقة)) .',NULL,'108','185',NULL,'0');
INSERT INTO [b26865] VALUES (184,'١٨٦-[١٨٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عياض بن عبد الله القرشي، عن أبي الزبير، عن جابر بن عبد الله، عن رسول الله صلى الله عليه وسلم مثله.',NULL,'109','186',NULL,'0');
INSERT INTO [b26865] VALUES (185,'١٨٧-[١٨٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك جرير بن حازم، والحارث ابن نبهان، عن الحسن بن عمارة، عن أبي إسحاق الهمداني، عن عاصم بن ⦗١١٠⦘ ضمرة، والحارث بن عبد الله، عن علي بن أبي طالب، عن رسول الله صلى الله عليه وسلم أنه قال:
((هاتوا لي ربع العشور، من كل أربعين درهماً درهم، وليس عليك شيء حتى يكون لك مائتا درهم، فإذا كان لك مائتا درهم، وحال عليها الحول، ففيها خمسة دراهم، وليس عليك شيء حتى يكون لك عشرون ديناراً، فإذا كانت لك، وحال عليها الحول، ففيها نصف دينار فما زاد، فبحساب ذلك)) .
قال: فلا أدري أعلي يقول: بحساب ذلك أم رفعه إلى النبي صلى الله عليه وسلم، إلا أن جريراً قال في الحديث عن النبي صلى الله عليه وسلم: ((وليس في مالٍ زكاة حتى يحول عليه الحول)) .',NULL,'109','187',NULL,'0');
INSERT INTO [b26865] VALUES (186,'١٨٨-[١٨٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، ومالك ابن أنس، وأسامة بن زيد، ويونس بن يزيد، وغير واحد؛ أن نافعاً حدثهم، ⦗١١١⦘ عن عبد الله بن عمر، أنه قال:
ليس في الحلى زكاة.',NULL,'110','188',NULL,'0');
INSERT INTO [b26865] VALUES (187,'١٨٩-[١٨٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك، عن عبد الله بن دينار، عن سليمان بن يسار، عن عراك بن مالك، عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم قال: ((ليس على المسلم في عبده ولا في فرسه صدقة)) .',NULL,'111','189',NULL,'0');
INSERT INTO [b26865] VALUES (188,'١٩٠-[١٨٦] حدثنا بحر قال: قرئ على ابن وهب: حدثك أسامة بن زيد الليثي، عن مكحول، عن عراك بن مالك، عن أبي هريرة، عن رسول الله صلى الله عليه وسلم قال:
((لا صدقة على الرجل في خيله ولا في رقيقه)) .',NULL,'112','190',NULL,'0');
INSERT INTO [b26865] VALUES (189,'١٩١-[١٨٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك سفيان بن عيينة، وسفيان الثوري، عن أبي إسحاق الهمداني، عن الحارث، عن علي بن أبي طالب، عن النبي صلى الله عليه وسلم أنه قال:
((عفوت عن الخيل والرقيق - قال: الثوري في الحديث- فأدوا زكاة الأموال)) .',NULL,'112','191',NULL,'0');
INSERT INTO [b26865] VALUES (190,'١٩٢-[١٨٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمرو بن الحارث؛ أن أبا الزبير حدثه؛ أنه سمع جابر بن عبد الله يذكر أن رسول الله صلى الله عليه وسلم قال:
((فيما سقت الأنهار والغيم العشور، وما سقى السانية نصف العشور)) .',NULL,'112','192',NULL,'0');
INSERT INTO [b26865] VALUES (191,'١٩٣-[١٨٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مسلم بن خالد، والقاسم ⦗١١٣⦘ ابن عبد الله، عن حرام بن عثمان، عن أبي عتيق، عن جابر بن عبد الله أن النبي صلى الله عليه وسلم قال:
((احتاطوا لأهل الأموال في الواطئة والعاملة والنوائب وما وجب في الثمر من الحق)) .',NULL,'112','193',NULL,'0');
INSERT INTO [b26865] VALUES (192,'١٩٤-[١٩٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يحيى بن عبد الله بن ⦗١١٤⦘ سالم، عن عبد الله بن الحارث المخزومي، عن عمرو بن شعيب، عن أبيه، عن جده، أن بني شبابة بطن من فهم، كانوا يؤدون إلى النبي صلى الله عليه وسلم من نحل كان عليهم العشر من كل عشرة قرب قربة، كان يحمي لهم واديين لهم، ثم أدوا إلى عمر بن الخطاب ما كانوا يؤدون إلى النبي صلى الله عليه وسلم وحمى لهم وادييهم (¬١) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: واد بينهم',NULL,'113','194',NULL,'0');
INSERT INTO [b26865] VALUES (193,'١٩٥-[١٩١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، ومالك بن أنس، عن نافع، عن عبد الله بن عمر؛ أن رسول الله صلى الله عليه وسلم فرض على ⦗١١٥⦘ الناس زكاة الفطر من رمضان، صاعاً من تمر أو صاعاً من شعير، [على] (¬١) كل حر أو عبد، ذكر أو أنثى، من المسلمين.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]] .',NULL,'114','195',NULL,'0');
INSERT INTO [b26865] VALUES (194,'١٩٦-[١٩٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن أبي ذئب، عن ابن شهاب أن النبي صلى الله عليه وسلم أمر بإخراج زكاة الفطر قبل أن يغدو إلى الصلاة.',NULL,'115','196',NULL,'0');
INSERT INTO [b26865] VALUES (195,'١٩٧-[١٩٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك حفص بن ميسرة، عن موسى بن عقبة، عن نافع، عن ابن عمر، عن رسول الله صلى الله عليه وسلم مثله.',NULL,'115','197',NULL,'0');
INSERT INTO [b26865] VALUES (196,'١٩٨-[١٩٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك محمد بن سعيد، عن أبي معشر، عن نافع، عن عبد الله بن عمر، أنه قال:
إذا انصرف رسول الله صلى الله عليه وسلم من الصلاة قسمه بينهم، فقال: ((اغنوهم عن طواف هذا اليوم)) .',NULL,'115','198',NULL,'0');
INSERT INTO [b26865] VALUES (197,'١٩٩-[١٩٥] حدثنا بحر قال: قرئ على ابن وهب قال: وكتب إلى كثير بن عبد الله بن عمرو المزني يخبر عن ربيح بن عبد الرحمن، عن أبي سعيد الخدري قال:
جاء رجال من أهل البادية إلى النبي صلى الله عليه وسلم فقالوا: يا رسول الله، إنا أولو أموال، فهل يجوز عنا من زكاة الفطر؟ قال: ((لا. فأدوها عن الصغير والكبير والذكر والأنثى والحر والعبد صاعاً من تمر، أو صاعاً من زبيب، أو صاعاً من شعير، أو صاعاً من أقط)) .',NULL,'116','199',NULL,'0');
INSERT INTO [b26865] VALUES (198,'٢٠٠-[١٩٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، والليث بن سعد، عن خالد بن يزيد، عن سعيد بن أبي هلال، عمن حدثه، عن أنس بن مالك أنه قال:
⦗١١٧⦘
أتى رجل من بني تميم إلى رسول الله صلى الله عليه وسلم فقال: يا رسول الله، إذا أديت الزكاة إلى رسولك، فقد برئت منها إلى الله وإلى رسوله؟
فقال رسول الله صلى الله عليه وسلم: ((نعم إذا أديتها إلى رسولي، فقد برئت منها، ولك أجرها، وإثمها على من بدلها)) .',NULL,'116','200',NULL,'0');
INSERT INTO [b26865] VALUES (199,'٢٠١-[١٩٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن عبد الرحمن الأعرج، عن أبي هريرة؛ أن النبي صلى الله عليه وسلم قال:
((يتبع أحدكم كنزه يوم القيامة وهو شجاع أقرع، فلا يزال يفر منه، حتى يلقمه أصبعه، فيجعلها في فيه)) .',NULL,'117','201',NULL,'0');
INSERT INTO [b26865] VALUES (200,'٢٠٢-[١٩٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر قال:
((كل مال يؤدى زكاته فليس بكنز، وإن كانت تحت سبع أرضين، وكل مال لا يؤدى زكاته فهو كنز، وإن كان ظاهراً فوق الأرض)) .',NULL,'117','202',NULL,'0');
INSERT INTO [b26865] VALUES (201,'<span data-type="title" id=toc-6>من كتاب الصلاة</span>
٢٠٣-[١٩٩] حدثنا بحر قال: قرئ على [عبد الله] (¬١) بن وهب: وأنا أسمع: أخبرك يونس ابن يزيد، عن ابن شهاب قال: حدثني عروة بن الزبير، عن عائشة زوج النبي صلى الله عليه وسلم قالت:
فرض الله الصلاة حين فرضها ركعتين، ثم أتمها في الحضر، وأقرت صلاة السفر على الفريضة الأولى.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]] .',NULL,'118','203',NULL,'0');
INSERT INTO [b26865] VALUES (202,'٢٠٤-[٢٠٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمرو بن الحارث، ⦗١١٩⦘ وغيره، عن أبي بكر بن المنكدر، عن علي بن حسين؛ أن رسول الله صلى الله عليه وسلم كان إذا أراد السفر يوماً، جمع بين الظهر والعصر وإذا أراد السفر ليلة، جمع بين المغرب والعشاء.',NULL,'118','204',NULL,'0');
INSERT INTO [b26865] VALUES (203,'٢٠٥-[٢٠١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك جابر بن إسماعيل، عن عقيل، عن ابن شهاب، عن أنس بن مالك، عن رسول الله صلى الله عليه وسلم مثله، إذا عجل به السير.
وقال: ((يؤخر الظهر إلى أول وقت العصر، فيجمع بينهما، ويؤخر المغرب حتى يجمع بينها وبين العشاء حين يغيب الشفق)) .',NULL,'119','205',NULL,'0');
INSERT INTO [b26865] VALUES (204,'٢٠٦-[٢٠٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، والليث بن ⦗١٢٠⦘ سعد، وعمرو بن الحارث، عن جعفر بن ربيعة، عن عراك بن مالك؛ أن ⦗١٢١⦘ رسول الله صلى الله عليه وسلم أقام بمكة عام الفتح [بعد الفتح] (¬١) خمسة عشر ليلة يقصر الصلاة.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]] .',NULL,'119','206',NULL,'0');
INSERT INTO [b26865] VALUES (205,'٢٠٧-[٢٠٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يحيى بن أيوب، عن حميد الطويل، عن رجل، عن عبد الله بن عمر؛ أن رسول الله صلى الله عليه وسلم أقام سبع عشرة يصلي ركعتين محاصراً الطائف.',NULL,'121','207',NULL,'0');
INSERT INTO [b26865] VALUES (206,'٢٠٨-[٢٠٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يحيى بن عبد الله بن سالم ومالك بن أنس، عن عمرو بن يحيى المازني، عن سعيد بن يسار، عن عبد الله بن عمر قال: رأيت رسول الله صلى الله عليه وسلم يصلي على الحمار، وهو متوجه إلى خيبر ويسير.',NULL,'121','208',NULL,'0');
INSERT INTO [b26865] VALUES (207,'٢٠٩-[٢٠٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك الليث بن سعد، وأبو يحيى بن سليمان، عن صفوان بن سليم، عن أبي بسرة، عن البراء بن عازب، أنه قال:
⦗١٢٣⦘
سافرت مع رسول الله صلى الله عليه وسلم ثمانية عشر سفراً، فلم أره ترك ركعتين قبل الظهر.',NULL,'122','209',NULL,'0');
INSERT INTO [b26865] VALUES (208,'٢١٠-[٢٠٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، عن ابن شهاب قال أخبرني عروة بن الزبير، عن عائشة زوج النبي صلى الله عليه وسلم:
خسفت الشمس في حياة رسول الله صلى الله عليه وسلم، فخرج رسول الله إلى المسجد، فقام فكبر، وصف الناس وراءه، فاقترأ رسول الله صلى الله عليه وسلم قراءة طويلة، ثم كبر فركع ركوعاً طويلاً، ثم رفع رأسه فقال: ((سمع الله لمن حمده، ربنا ولك الحمد)) ، ثم قام فاقترأ قراءة طويلة، هي أدنى من القراءة الأولى، ثم كبر فركع ركوعاً طويلاً، هو أدنى من الركوع الأول، ثم قال: ((سمع الله لمن حمده، ربنا ولك الحمد)) .
ثم فعل في الركعة الأخرى مثل ذلك، فاستكمل أربع ركعات، وأربع سجدات، وانجلت الشمس قبل أن ينصرف.
ثم قام فخطب الناس، فأثنى على الله بما هو أهله، ثم قال: ((إن الشمس والقمر آيتان من آيات الله، لا يخسفان لموت أحد، ولا لحياته، فإذا رأيتموها، فافزعوا إلى الصلاة)) .',NULL,'123','210',NULL,'0');
INSERT INTO [b26865] VALUES (209,'٢١١-[٢٠٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن أبي ذئب ويونس ابن يزيد، عن ابن شهاب قال: أخبرني عباد بن تميم المازني؛ أنه سمع عمه، وكان من أصحاب رسول الله صلى الله عليه وسلم يقول:
خرج رسول الله يوماً يستسقي، فحول إلى الناس ظهره يدعو الله، واستقبل القبلة وحول رداءه، ثم صلى ركعتين.
قال ابن أبي ذئب في الحديث: وقرأ فيهما. قال ابن وهب: يريد الجهر.',NULL,'124','211',NULL,'0');
INSERT INTO [b26865] VALUES (210,'٢١٢-[٢٠٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، عن ⦗١٢٥⦘ ابن شهاب قال: أخبرني سعيد بن المسيب، وأبو سلمة بن عبد الرحمن، أنهما سمعا أبا هريرة يقول:
كان رسول الله صلى الله عليه وسلم يقول حين يفرغ من صلاة الفجر من القراءة يكبر ويرفع رأسه: ((سمع الله لمن حمده، ربنا ولك الحمد)) ، ثم يقول وهو قائم: ((اللهم أنج الوليد وسلمة بن هشام وعياش بن أبي ربيعة، والمستضعفين من المؤمنين، اللهم اشدد وطأتك على مضر، واجعلها عليهم سنين كسني يوسف، اللهم العن لحيان ورعلاً وذكوان، وعصية عصت الله ورسوله)) . ثم بلغنا أنه ترك ذلك لما أنزل: {ليس لك من الأمر شيءٌ أو يتوب عليهم أو يعذبهم فإنهم ظالمون} .',NULL,'124','212',NULL,'0');
INSERT INTO [b26865] VALUES (211,'٢١٣-[٢٠٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك داود بن قيس؛ أن عياض بن عبد الله حدثه؛ أنه سمع أبا سعيد الخدري يقول:
⦗١٢٦⦘
كان رسول الله صلى الله عليه وسلم يخرج يوم العيدين (¬١) فيصلي، فيبدأ بالركعتين ثم يسلم، فيقوم قائماً، فيستقبل الناس بوجهه، فيكلمهم ويأمرهم بالصدقة، فإن أراد أن يضرب على الناس بعثاً ذكره وإلا انصرف.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: العيد]] .',NULL,'125','213',NULL,'0');
INSERT INTO [b26865] VALUES (212,'٢١٤-[٢١٠] حدثنا بحر قال: قرئ على ابن وهب قال: وكتب إلي كثير بن عبد الله المزني يحدث، عن أبيه، عن جده أنه قال:
⦗١٢٧⦘
رأيت رسول الله صلى الله عليه وسلم كبر في الأضحى سبعاً وخمساً، وفي الفطر مثل ذلك.',NULL,'126','214',NULL,'0');
INSERT INTO [b26865] VALUES (213,'٢١٥-[٢١١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن خالد ابن يزيد، عن ابن شهاب، عن عروة، عن عائشة؛ أن رسول الله صلى الله عليه وسلم كبر في الفطر والأضحى سبعاً وخمساً سوى تكبير الركوع.',NULL,'127','215',NULL,'0');
INSERT INTO [b26865] VALUES (214,'٢١٦-[٢١٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، وسفيان بن عيينة، عن ضمرة بن سعيد المازني، عن عبيد الله بن عبد الله؛ أن عمر بن الخطاب سأل أبا واقد الليثي:
⦗١٢٨⦘
ماذا كان رسول الله صلى الله عليه وسلم يقرأ في الفطر والأضحى؟
قال: كان يقرأ بـ {ق والقرآن المجيد} ، و {اقتربت الساعة} .',NULL,'127','216',NULL,'0');
INSERT INTO [b26865] VALUES (215,'٢١٧-[٢١٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، عن نافع، عن عبد الله بن عمر؛ أن رسول الله صلى الله عليه وسلم كان يخرج إلى العيدين (¬١) من طريق، ويرجع من طريق أخرى.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: العيد]] .',NULL,'128','217',NULL,'0');
INSERT INTO [b26865] VALUES (216,'٢١٨-[٢١٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك، وغيره، ⦗١٢٩⦘ عن ابن شهاب، عن ابن السباق؛ أن رسول الله صلى الله عليه وسلم قال في جمعة من الجمع:
((يا معشر المسلمين، هذا يوم جعله الله عيداً للمسلمين، فاغتسلوا فيه، ومن كان عنده طيب فلا يضره أن يمس منه، وعليكم بالسواك)) .',NULL,'128','218',NULL,'0');
INSERT INTO [b26865] VALUES (217,'٢١٩-[٢١٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك أن نافعاً حدثهم، ⦗١٣٠⦘ عن عبد الله بن عمر؛ أن رسول الله صلى الله عليه وسلم قال:
((إذا جاء أحدكم الجمعة فليغتسل)) .',NULL,'129','219',NULL,'0');
INSERT INTO [b26865] VALUES (218,'٢٢٠-[٢١٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك حيوة بن شريح، عن بكر بن عمرو، عن بكير بن عبد الله، عن نافع؛ أن عبد الله بن عمر كان يقلم أظفاره، ويقص شاربه في كل جمعة.',NULL,'130','220',NULL,'0');
INSERT INTO [b26865] VALUES (219,'٢٢١-[٢١٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك أبو يحيى بن سليمان المدني، عن عثمان بن عبد الرحمن، عن أنس بن مالك قال:
كان رسول الله صلى الله عليه وسلم يصلي يوم الجمعة حين تميل الشمس.',NULL,'130','221',NULL,'0');
INSERT INTO [b26865] VALUES (220,'٢٢٢-[٢١٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك حنظلة بن أبي سفيان ⦗١٣١⦘ الجمحي؛ أنه سمع سالم بن عبد الله يحدث، عن أبيه؛ أنه سمع عمر بن الخطاب يقرأ:
{إذا نودي للصلاة من يوم الجمعة فامضوا إلى ذكر الله} .',NULL,'130','222',NULL,'0');
INSERT INTO [b26865] VALUES (221,'٢٢٣-[٢١٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، عن ابن شهاب قال: أخبرني أبو عبد الله الأغر؛ أنه سمع أبا هريرة يقول: قال رسول الله صلى الله عليه وسلم:
((إذا كان يوم الجمعة، كان على كل باب من أبواب المسجد ملائكة يكتبون الأول فالأول، فإذا جلس الإمام، طووا الصحف، وجاءوا يستمعون الذكر، ومثل المهجر كالذي يهدي بدنة، ثم كالذي يهدي بقرة، ثم كالذي يهدي بالكبش، ثم كالذي يهدي الدجاجة، [ثم كالذي يهدي البيضة] (¬١)) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]] .',NULL,'131','223',NULL,'0');
INSERT INTO [b26865] VALUES (222,'٢٢٤-[٢٢٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، ⦗١٣٢⦘ ويونس بن يزيد، وابن سمعان؛ أن ابن شهاب أخبرهم قال: أخبرني سعيد بن المسيب؛ أن أبا هريرة قال: قال رسول الله صلى الله عليه وسلم:
((إذا قلت لصاحبك: أنصت، والإمام يخطب، فقد لغوت)) .',NULL,'131','224',NULL,'0');
INSERT INTO [b26865] VALUES (223,'٢٢٥-[٢٢١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن أبي ذئب، عن أسيد بن أبي أسيد، عن عبد الله بن أبي قتادة، عن جابر بن عبد الله؛ أن رسول الله صلى الله عليه وسلم قال:
((من ترك الجمعة ثلاثاً من غير ضرورة، طبع الله على قلبه)) .',NULL,'132','225',NULL,'0');
INSERT INTO [b26865] VALUES (224,'٢٢٦-[٢٢٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، عن ضمرة ابن سعيد المازني، عن عبد الله بن [عبد الله بن] (¬١) عتبة؛ أن الضحاك بن قيس سأل النعمان بن بشير:
ماذا كان يقرأ به رسول الله صلى الله عليه وسلم يوم الجمعة، على أثر سورة الجمعة؟
فقال: كان يقرأ بـ {هل أتاك حديث الغاشية} .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]] .',NULL,'132','226',NULL,'0');
INSERT INTO [b26865] VALUES (225,'٢٢٧-[٢٢٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك سفيان الثوري؛ أنه سمع جعفر بن محمد يحدث عن أبيه، عن أبي رافع، أو ابن أبي رافع، عن أبي هريرة، عن رسول الله؛ أنه كان يقرأ في الجمعة بسورة الجمعة و {إذا جاءك المنافقون} .',NULL,'133','227',NULL,'0');
INSERT INTO [b26865] VALUES (226,'٢٢٨-[٢٢٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن عبد الرحمن الأعرج، أخبره عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم قال:
((في الجمعة ساعة -وقبض أطراف أصابعه، وهو يقللها- لا يسأله فيها مؤمن شيئاً إلا أعطى سؤله)) .',NULL,'133','228',NULL,'0');
INSERT INTO [b26865] VALUES (227,'٢٢٩-[٢٢٥] حدثنا بحر قال: قرئ على ابن وهب: حدثك عمرو بن الحارث، عن الجلاح مولى عبد العزيز؛ أن أبا سلمة بن عبد الرحمن، حدثه عن جابر بن عبد الله، عن رسول الله صلى الله عليه وسلم أنه قال:
⦗١٣٤⦘
((يوم الجمعة، لا يوجد عبد مسلم يسأل الله شيئاً إلا آتاه الله إياه، فالتمسوها آخر ساعة بعد العصر)) .',NULL,'133','229',NULL,'0');
INSERT INTO [b26865] VALUES (228,'٢٣٠-[٢٢٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن جريج، عن ابن أبي مليكة، عن عائشة زوج النبي صلى الله عليه وسلم؛ أن رسول الله صلى الله عليه وسلم قال:
((أبغض الرجال إلى الله الألد الخصم)) .',NULL,'134','230',NULL,'0');
INSERT INTO [b26865] VALUES (229,'٢٣١-[٢٢٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك هشام، عن زيد ابن أسلم، عن عطاء بن يسار، عن رسول الله صلى الله عليه وسلم قال:
((إن الشمس والقمر خلقا من النار، ويعودان فيها)) .',NULL,'134','231',NULL,'0');
INSERT INTO [b26865] VALUES (230,'<span data-type="title" id=toc-7>من كتاب النكاح</span>
٢٣٢-[٢٢٨] أخبرنا محمد بن عبد الله بن عبد الحكم، أنا عبد الله بن وهب؛ أن ⦗١٣٥⦘ مالك بن أنس، وعبد الله بن عمر، أخبراه عن نافع، عن عبد الله بن عمر؛ أن رسول الله صلى الله عليه وسلم نهى عن الشغار؛ والشغار أن يزوج الرجل ابنته على أن يزوجه الرجل الآخر ابنته، وليس بينهما صداق.',NULL,'134','232',NULL,'0');
INSERT INTO [b26865] VALUES (231,'٢٣٣-[٢٢٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، عن نافع، عن عبد الله بن عمر؛ أن رسول الله صلى الله عليه وسلم قال:
((لا شغار في الإسلام)) .',NULL,'135','233',NULL,'0');
INSERT INTO [b26865] VALUES (232,'٢٣٤-[٢٣٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس قال: حدثني عبد الله بن الفضل، عن نافع بن جبير، عن عبد الله بن عباس، أن رسول الله صلى الله عليه وسلم قال:
((الأيم أحق بنفسها من وليها، والبكر تستأذن في نفسها، وإذنها صماتها)) .',NULL,'135','234',NULL,'0');
INSERT INTO [b26865] VALUES (233,'٢٣٥-[٢٣١] أخبرنا محمد، أنا ابن وهب قال: أخبرني الليث بن سعد، عن ⦗١٣٧⦘ عبد الله بن عبد الرحمن القرشي، عن عدي بن عدي الكندي، عن أبيه، عن رسول الله صلى الله عليه وسلم أنه قال:
((شاوروا النساء في أنفسهن)) .
فقيل [له] (¬١) : يا رسول الله، إن البكر تستحي؟ قال: ((الثيب تعرب عن نفسها، والبكر رضاها صمتها)) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]] .',NULL,'136','235',NULL,'0');
INSERT INTO [b26865] VALUES (234,'٢٣٦-[٢٣٢] أخبرنا محمد، أنا ابن وهب قال: حدثني شبيب بن سعيد التيمي، عن ⦗١٣٨⦘ محمد بن عمرو بن علقمة، يحدث عن أبي سلمة بن عبد الرحمن، عن أبي هريرة، أن رسول الله صلى الله عليه وسلم قال:
((اليتيمة تستأمر في نفسها، فإن سكتت فهو إذنها، وإن أبت، فلا جواز عليها)) .',NULL,'137','236',NULL,'0');
INSERT INTO [b26865] VALUES (235,'٢٣٧-[٢٣٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن عبد الرحمن بن القاسم، عن أبيه، عن عبد الرحمن ويزيد ابني مجمع؛ أن خنساء بنت خذام أنكحها أبوها وهي ثيب، فكرهت ذاك، فجاءت إلى رسول الله فرد نكاحها.',NULL,'138','237',NULL,'0');
INSERT INTO [b26865] VALUES (236,'٢٣٨-[٢٣٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني مسلمة بن علي؛ أن هشام ابن حسان، حدثه عن محمد بن سيرين، عن أبي هريرة؛ أنه قال:
((لا تزوج المرأة المرأة، ولا تزوج المرأة نفسها؛ فإن الزانية هي التي تنكح نفسها)) .',NULL,'139','238',NULL,'0');
INSERT INTO [b26865] VALUES (237,'٢٣٩-[٢٣٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني الضحاك بن عثمان، عن ⦗١٤٠⦘ عبد الجبار، عن الحسن؛ أن رسول الله صلى الله عليه وسلم قال:
((لا يحل نكاح إلا بولي وصداق وشاهدي عدل)) .',NULL,'139','239',NULL,'0');
INSERT INTO [b26865] VALUES (238,'٢٤٠-[٢٣٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني سفيان بن سعيد، عن أبي إسحاق الهمداني، عن أبي بردة، عن أبي موسى الأشعري؛ أن رسول الله ⦗١٤١⦘ صلى الله عليه وسلم قال:
((لا نكاح لامرأة بغير إذن ولي)) .',NULL,'140','240',NULL,'0');
INSERT INTO [b26865] VALUES (239,'٢٤١-[٢٣٧] أخبرنا محمد، أنا ابن وهب قال: حدثني عمر بن قيس عن عطاء بن أبي رباح، عن أبي هريرة، عن رسول الله صلى الله عليه وسلم مثله سواء.',NULL,'141','241',NULL,'0');
INSERT INTO [b26865] VALUES (240,'٢٤٢-[٢٣٨] أخبرنا محمد، أنا ابن وهب، أنا ابن جريج، عن سليمان بن موسى، ⦗١٤٢⦘ عن ابن شهاب، عن عروة بن الزبير، عن عائشة زوج النبي صلى الله عليه وسلم، عن رسول الله صلى الله عليه وسلم أنه قال:
((لا تنكح امرأة بغير أمر وليها، فإن نكحت فنكاحها باطل)) ، ثلاث مرات، ((فإن أصابها فلها مهر مثلها بما أصاب منها، فإن اشتجروا فالسلطان ولي من لا ولي له)) .',NULL,'141','242',NULL,'0');
INSERT INTO [b26865] VALUES (241,'٢٤٣-[٢٣٩] أخبرنا محمد، أنا ابن وهب قال: حدثني هشام بن سعد وغيره، عن زيد بن أسلم؛ أن رسول الله صلى الله عليه وسلم قال:
((إذا جاءكم من ترضون دينه ورأيه فأنكحوه)) . قالوا: يا رسول الله، وإن؟ قال: ((إذا جاءكم من ترضون دينه ورأيه فأنكحوه)) . قال: وإن حبشي (¬١) ؟ قال: ((فإن كان أسود)) قال: ((إنكم إلا تفعلوا تكن فتنة في الأرض وفساد عريض)) .
_________
(¬١) في الأصل: حقي، وعليها علامة تمريض، وما أثبتناه الأنسب.',NULL,'143','243',NULL,'0');
INSERT INTO [b26865] VALUES (242,'٢٤٤-[٢٤٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني يزيد بن عياض، عن إسماعيل ابن إبراهيم بن عباد بن شيبان (¬١) ، عن أبيه، عن جده؛ أن رسول الله صلى الله عليه وسلم قال:
⦗١٤٤⦘
((ألا أنكحك أميمة بنت ربيعة بن الحارث؟)) قال: بلى. قال: ((قد أنكحتها)) ، ولم يشهد.
_________
(¬١) [[في المخطوط: إسماعيل بن إبراهيم عن عباد بن سنان، وفي طبعة الشنقيطي: إسماعيل بن إبراهيم عن عباد بن شيبان]]',NULL,'143','244',NULL,'0');
INSERT INTO [b26865] VALUES (243,'٢٤٥-[٢٤١] أخبرنا محمد، أنا ابن وهب قال: حدثني عبد الله بن الأسود القرشي، عن عامر بن عبد الله بن الزبير، عن أبيه، عن رسول الله صلى الله عليه وسلم أنه قال:
⦗١٤٥⦘
((أعلنوا النكاح)) .',NULL,'144','245',NULL,'0');
INSERT INTO [b26865] VALUES (244,'٢٤٦-[٢٤٢] أخبرنا محمد، أنا ابن وهب قال: حدثني شمر بن نمير الأموي، عن حسين بن عبد الله، عن أبيه، عن جده، عن علي بن أبي طالب؛ أن رسول الله صلى الله عليه وسلم مر هو وأصحابه ببني رزيق، فسمعوا غناء ولعباً فقال: ((ما هذا؟)) قالوا: نكح فلان يا رسول الله. قال: ((كمل دينه، هذا النكاح لا السفاح، ولا نكاح السر حتى يسمع دفٌ، ويرى دخان)) .',NULL,'145','246',NULL,'0');
INSERT INTO [b26865] VALUES (245,'٢٤٧- قال حسين: وحدثني عمرو بن يحيى المازني؛ أن رسول الله صلى الله عليه وسلم كان يكره نكاح السر حتى يضرب بالدف.',NULL,'145','247',NULL,'0');
INSERT INTO [b26865] VALUES (246,'٢٤٨-[٢٤٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، والليث بن سعد، وعمرو بن الحارث؛ أن عبد العزيز بن الربيع بن سبرة الجهني، حدثهم عن أبيه؛ أن رسول الله صلى الله عليه وسلم نهى عن متعة النساء عام الفتح.',NULL,'146','248',NULL,'0');
INSERT INTO [b26865] VALUES (247,'٢٤٩-[٢٤٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني سليمان بن بلال، عن ⦗١٤٧⦘ يحيى بن سعيد، عن عبد العزيز بن عمر بن عبد العزيز، عن رجل من السبريين، ⦗١٤٨⦘ عن أبيه قال: سمعت رسول الله صلى الله عليه وسلم يقول في حجة الوداع:
⦗١٤٩⦘
((إن الله حرم المتعة فلا تقربوها، ومن كان على شيء منها فليدعها)) .',NULL,'146','249',NULL,'0');
INSERT INTO [b26865] VALUES (248,'٢٥٠-[٢٤٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني عمر بن محمد، عن زيد بن عبد الله بن عمر بن الخطاب، عن ابن شهاب قال: أخبرني سالم بن عبد الله؛ أن رجلاً سأل عبد الله بن عمر عن المتعة. فقال: حرام. قال: فإن فلاناً يقول فيها. فقال: والله لقد علم أن رسول الله صلى الله عليه وسلم حرمها يوم خيبر، وما كنا مسافحين.
قال أبو العباس: هكذا في كتابي: زيد. والصحيح هو يزيد، والله أعلم.',NULL,'149','250',NULL,'0');
INSERT INTO [b26865] VALUES (249,'٢٥١-[٢٤٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، ويونس ⦗١٥٠⦘ ابن يزيد، وأسامة بن زيد، أن ابن شهاب حدثهم عن عبد الله والحسن ابني محمد، عن أبيهما، عن علي بن أبي طالب؛ أن رسول الله صلى الله عليه وسلم نهى يوم خيبر عن متعة النساء، وعن لحوم الحمر الأهلية.',NULL,'149','251',NULL,'0');
INSERT INTO [b26865] VALUES (250,'٢٥٢-[٢٤٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، عن سالم بن عبد الله بن عمر، عن رسول الله صلى الله عليه وسلم مثله.',NULL,'150','252',NULL,'0');
INSERT INTO [b26865] VALUES (251,'٢٥٣-[٢٤٨] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، عن ⦗١٥١⦘ نافع، عن عبد الله بن عمر؛ أنه سئل عن متعة النساء، فقال: حرام، أما إن عمر بن الخطاب لو أخذ فيها أحداً لرجمه بالحجارة.',NULL,'150','253',NULL,'0');
INSERT INTO [b26865] VALUES (252,'٢٥٤-[٢٤٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، ومالك ابن أنس، وغير واحد؛ أن نافعاً حدثهم عن ابن عمر وزيد بن ثابت أنهما قالا -في الذي يموت ولم يفرض لامرأته-:
إن لها الميراث من زوجها، وليس لها صداق.',NULL,'151','254',NULL,'0');
INSERT INTO [b26865] VALUES (253,'٢٥٥-[٢٥٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني الحارث بن نبهان، عن ⦗١٥٢⦘ أبي هارون العبدي، عن أبي سعيد الخدري قال: قال رسول الله صلى الله عليه وسلم:
((لا يضر أحدكم إذا تزوج أن يتزوج بقليل أو كثير من ماله إذا تراضيتم وأشهدتم)) .',NULL,'151','255',NULL,'0');
INSERT INTO [b26865] VALUES (254,'٢٥٦-[٢٥١] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، عن ابن الهاد، ⦗١٥٣⦘ عن أبي سلمة بن عبد الرحمن بن عوف قال: سألت عائشة زوج النبي صلى الله عليه وسلم: كم كان صداق بنات رسول الله صلى الله عليه وسلم؟
فقالت عائشة: ما أخذ رسول الله صلى الله عليه وسلم لشيء من بناته، ولا أصدق شيئاً من نسائه فوق اثنا عشر أوقية ونش - والنش النصف.',NULL,'152','256',NULL,'0');
INSERT INTO [b26865] VALUES (255,'٢٥٧-[٢٥٢] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، وعبد الله ابن عمر، عن حميد الطويل، عن أنس بن مالك؛ أن عبد الرحمن بن عوف تزوج امرأة، فقال له رسول الله صلى الله عليه وسلم:
((كم سقت إليها؟))
قال: زنة نواة من ذهب.',NULL,'153','257',NULL,'0');
INSERT INTO [b26865] VALUES (256,'٢٥٨-[٢٥٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني يونس، عن نافع؛ أن ⦗١٥٤⦘ عبد الله بن عمر قال:
لا يصلح للرجل أن يقع على المرأة حتى يقدم إليها شيئاً من ماله ما رضيت به من كسوة أو عطاء.',NULL,'153','258',NULL,'0');
INSERT INTO [b26865] VALUES (257,'٢٥٩-[٢٥٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني أسامة بن زيد الليثي، عن نافع؛ أن ابن عمر كان يكره أن يجعل عتق الأمة صداقها.',NULL,'154','259',NULL,'0');
INSERT INTO [b26865] VALUES (258,'٢٦٠-[٢٥٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني سعيد بن عبد الرحمن، وعبد الرحمن بن أبي الزناد، عن هشام بن عروة، عن أبيه، عن عائشة زوج النبي صلى الله عليه وسلم أنها قالت:
⦗١٥٥⦘
تزوجني رسول الله صلى الله عليه وسلم وأنا بنت ست سنين متوفى خديجة، وبنى بي وأنا ابنة تسع سنين، فدخلت على رسول الله صلى الله عليه وسلم وأنا ألعب بالبنات، وكان لي صواحب يلعبن معي، فإذا رأينا رسول الله استحيين وتقمعن، فربما خرج رسول الله صلى الله عليه وسلم فيسربهن إلي.',NULL,'154','260',NULL,'0');
INSERT INTO [b26865] VALUES (259,'٢٦١-[٢٥٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني يونس، عن ابن شهاب قال: أخبرني عروة بن الزبير؛ أنه سأل عائشة زوج النبي صلى الله عليه وسلم عن قول الله: {وإن خفتم ألا تقسطوا في اليتامى فانكحوا ما طاب لكم من النساء مثنى} .
قالت: يا بن أختي هي اليتيمة تكون في حجر وليها تشاركه في ماله، فيعجبه مالها وجمالها، فيريد وليها أن يتزوجها بغير أن يقسط في صداقها، فيعطيها مثل ما يعطيها غيره، فنهوا أن ينكحوهن إلا أن يقسطوا لهن ويبلغوا بهن أعلى سنتهن من الصداق، وأمروا أن ينكحوا ما طاب لهم من النساء سواهن.
قال عروة: فسألت عائشة، ثم إن الناس قد استفتوا رسول الله صلى الله عليه وسلم بعد هذه الآية، فأنزل الله عز وجل هذه الآية: {ويستفتونك في النساء قل الله يفتيكم فيهن وما يتلى عليكم في الكتاب في يتامى النساء اللاتي لا تؤتونهن ما كتب لهن وترغبون أن تنكحوهن} .
قال: والذي ذكر الله أنه يتلى عليهن في الكتاب الآية التي قال فيها: {وإن خفتم ألا تقسطوا في اليتامى فانكحوا ما طاب لكم من النساء} .
قالت عائشة: وقال الله في الآية الأخرى: {وترغبون أن تنكحوهن} رغبة أحدكم عن يتيمته التي تكون في حجره حتى تكون قليلة المال والجمال، فنهوا أن ينكحوا ما رغبوا في مالها وجمالها من يتامى النساء إلا بالقسط من أجل رغبتهم عنهن.',NULL,'155','261',NULL,'0');
INSERT INTO [b26865] VALUES (260,'٢٦٢- (¬١) أخبرنا محمد، أنا ابن وهب قال: أخبرني يحيى بن أيوب، عن المثنى بن الصباح، عن عمرو بن شعيب، عن عروة بن الزبير، عن عائشة زوج النبي صلى الله عليه وسلم، عن رسول الله صلى الله عليه وسلم.
٢٦٣- وعن سعيد بن المسيب عن رسول الله أنه قال: ((أيما امرأة نكحت على صداق أو عدة لأهلها، فهو لها ما كان قبل عصمة النكاح، وما كان بعد ذلك من حباء فلأهلها، وأحق ما أكرم الرجل على ابنته وأخته)) .
_________
(¬١) [[هذا والذي بعده سقطا من طبعة الشنقيطي]]',NULL,'156','262',NULL,'0');
INSERT INTO [b26865] VALUES (261,'٢٦٤-[٢٥٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني يونس بن يزيد، عن ابن ⦗١٥٧⦘ شهاب قال: حدثني عروة بن الزبير؛ أن عائشة زوج النبي صلى الله عليه وسلم أخبرته أن رفاعة القرظي طلق امرأته، فبت طلاقها، فتزوجت بعده عبد الرحمن بن الزبير، فجاءت رسول الله صلى الله عليه وسلم فقالت: إنها كانت تحت رفاعة، فطلقها آخر ثلاث تطليقات، فتزوجت بعده عبد الرحمن بن الزبير، وإنه والله ما معه إلا مثل هذه الهدبة، فأخذت بهدبةٍ من جلبابها.
قال: فتبسم رسول الله صلى الله عليه وسلم ضاحكاً، فقال: ((لعلك تريدين أن ترجعي إلى رفاعة، لا حتى تذوقي عسيلته، ويذوق عسيلتك)) .
قال: وأبو بكر عند رسول الله، وخالد بن سعيد بن العاص جالس بباب الحجرة ليؤذن له، فطفق خالد ينادي أبا بكر، ألا تزجر هذه عما تجهر به عند رسول الله صلى الله عليه وسلم.',NULL,'156','264',NULL,'0');
INSERT INTO [b26865] VALUES (262,'٢٦٥-[٢٥٨] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الرحمن بن أبي الزناد، ⦗١٥٨⦘ عن هشام بن عروة، عن أبيه، عن عائشة، عن رسول الله صلى الله عليه وسلم بهذا.
قالت: فإنه يا رسول الله قد جاءني هبةٌ، قال محمد بن عبد الله: هبة ⦗١٥٩⦘ يعني: مرة.',NULL,'157','265',NULL,'0');
INSERT INTO [b26865] VALUES (263,'٢٦٦-[٢٥٩] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس، عن المسور بن رفاعة القرظي، عن الزبير بن عبد الرحمن بن الزبير، عن أبيه؛ أن رفاعة طلق امرأته تميمة بنت وهب على عهد رسول الله صلى الله عليه وسلم ثلاثاً، فنكحها عبد الرحمن ابن الزبير، فاعترض عنها، فلم يستطع أن يمسها، فطلقها ولم يمسها.
فأراد رفاعة أن ينكحها، وهو زوجها الذي كان طلقها قبل عبد الرحمن.
فذكر ذلك لرسول الله صلى الله عليه وسلم، فنهاه عن تزويجها، وقال: ((لا يحل لك حتى تذوق العسيلة)) .',NULL,'159','266',NULL,'0');
INSERT INTO [b26865] VALUES (264,'٢٦٧-[٢٦٠] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك بن أنس ⦗١٦٠⦘ وعبد الرحمن بن أبي الزناد، عن أبي الزناد، عن عبد الرحمن الأعرج، عن أبي هريرة، عن رسول الله صلى الله عليه وسلم قال:
((لا يخطب أحدكم على خطبة أخيه حتى ينكح أو يترك)) .',NULL,'159','267',NULL,'0');
INSERT INTO [b26865] VALUES (265,'٢٦٨-[٢٦١] أخبرنا محمد، أنا ابن وهب قال: أخبرني مالك، عن محمد بن يحيى بن حبان، عن الأعرج، عن أبي هريرة، عن رسول الله صلى الله عليه وسلم بذلك.',NULL,'160','268',NULL,'0');
INSERT INTO [b26865] VALUES (266,'٢٦٩-[٢٦٢] أخبرنا محمد، أنا ابن وهب قال: أخبرني يونس، عن ابن شهاب، أنه قال: حدثنا سعيد بن المسيب، عن أبي هريرة، عن النبي صلى الله عليه وسلم بذلك.',NULL,'160','269',NULL,'0');
INSERT INTO [b26865] VALUES (267,'٢٧٠-[٢٦٣] أخبرنا محمد، أنا ابن وهب قال: أخبرني رجال من أهل العلم منهم عبد الله بن عمر، ومالك بن أنس، عن حميد الطويل، عن أنس؛ أن عبد الرحمن بن عوف جاء إلى رسول الله صلى الله عليه وسلم وعليه أثر صفرة، فسأله رسول الله صلى الله عليه وسلم، فأخبره أنه تزوج امرأة من الأنصار.
فقال: ((كم سقت إليها؟)) قال: زنة نواة من ذهب. فقال رسول الله صلى الله عليه وسلم: ((أولم ولو بشاة)) .',NULL,'160','270',NULL,'0');
INSERT INTO [b26865] VALUES (268,'٢٧١-[٢٦٤] أخبرنا محمد، أنا ابن وهب قال: أخبرني عبد الله بن عمر، عن ⦗١٦١⦘ إسحاق بن عبد الله بن أبي طلحة، وحميد الطويل، عن أنس بن مالك قال:
⦗١٦٢⦘
شهدنا وليمتين لرسول الله صلى الله عليه وسلم ما فيها خبز ولا لحم.
قلنا: فأي شيء كان طعامكم يا أبا حمزة؟ قال: الحيس.',NULL,'160','271',NULL,'0');
INSERT INTO [b26865] VALUES (269,'٢٧٢-[٢٦٥] أخبرنا محمد، أنا ابن وهب قال: أخبرني يزيد بن عياض، عن عبد الرحمن الأعرج، عن أبي هريرة، عن أبي الدرداء؛ أن رسول الله صلى الله عليه وسلم قال:
⦗١٦٣⦘
((يجوز اللعب في كل شيء غير ثلاث خلال، فمن لعب بشيء منهن جاز وإن كره؛ إن نكح فقد جاز، وإن طلق فقد جاز طلاقه، وإن أعتق فقد جاز عتاقه)) .',NULL,'162','272',NULL,'0');
INSERT INTO [b26865] VALUES (270,'٢٧٣-[٢٦٦] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، وابن سمعان، عن عبد الرحمن الأعرج، عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم نهى عن جمع بين المرأة وعمتها أو بين المرأة وخالتها.',NULL,'163','273',NULL,'0');
INSERT INTO [b26865] VALUES (271,'٢٧٤ -[٢٦٧] أخبرنا محمد، أنا ابن وهب قال: أخبرني ابن لهيعة، عن عبد الله بن هبيرة، عن ابن زرير الغافقي، عن علي بن أبي طالب، عن رسول الله صلى الله عليه وسلم مثله.',NULL,'163','274',NULL,'0');
INSERT INTO [b26865] VALUES (272,'<span data-type="title" id=toc-8>من كتاب الصوم</span>
٢٧٥-[٢٦٩] حدثنا بحر قال: قرئ على ابن وهب أخبرك مالك، وغيره، عن حميد ⦗١٦٤⦘ الطويل، عن أنس بن مالك قال:
سافرنا مع رسول الله صلى الله عليه وسلم في رمضان، فلم يعب الصائم على المفطر، ولا المفطر على الصائم.',NULL,'163','275',NULL,'0');
INSERT INTO [b26865] VALUES (273,'٢٧٦-[٢٧٠] حدثنا بحر قال: قرئ على ابن وهب أخبرك الحارث بن نبهان عن أبان بن أبي عياش، عن أنس بن مالك قال:
وإن كانوا ليرون من صام فهو أفضل. قال أنس: ثم غزونا مع رسول الله صلى الله عليه وسلم حنيناً.
فقال رسول الله صلى الله عليه وسلم: ((من كان له ظهر أو فضل فليصم)) .',NULL,'164','276',NULL,'0');
INSERT INTO [b26865] VALUES (274,'٢٧٧-[٢٧١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمرو بن الحارث، عن ⦗١٦٥⦘ أبي الأسود، عن عروة بن الزبير، عن أبي مراوح، عن حمزة بن عمرو أنه قال: يا رسول الله، إني أجد [بي] (¬١) قوة على الصيام في السفر فهل علي جناح؟
فقال رسول الله صلى الله عليه وسلم: ((هي رخصة من الله، فمن أخذ بها فحسن، ومن أحب أن يصوم فلا جناح عليه)) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]]',NULL,'164','277',NULL,'0');
INSERT INTO [b26865] VALUES (275,'٢٧٨-[٢٧٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن عمرو ابن السائب؛ أن حمزة بن عمرو الأسلمي قال:
بعثني رسول الله صلى الله عليه وسلم في رمضان، فقلت: كيف لي بالصيام؟ فقال: ((اتبع أيسر ذلك عليك)) .',NULL,'165','278',NULL,'0');
INSERT INTO [b26865] VALUES (276,'٢٧٩-[٢٧٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، وأسامة بن ⦗١٦٦⦘ زيد الليثي، وابن سمعان، أن نافعاً حدثهم؛ أن عبد الله بن عمر؛ أن النبي صلى الله عليه وسلم نهى عن الوصال.
فقيل [له] (¬١) : إنك تواصل؟ فقال: ((إني لست كهيئتكم، إني أطعم وأسقى)) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]]',NULL,'165','279',NULL,'0');
INSERT INTO [b26865] VALUES (277,'٢٨٠-[٢٧٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن الأعرج، عن أبي هريرة، عن رسول الله صلى الله عليه وسلم بذلك، إلا أنه قال:
((إني أبيت يطعمني ربي)) .',NULL,'166','280',NULL,'0');
INSERT INTO [b26865] VALUES (278,'٢٨١-[٢٧٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن يزيد بن الهاد، عن عبد الله بن خباب، عن أبي سعيد الخدري، عن رسول الله صلى الله عليه وسلم مثله.
وقال: ((فأيكم واصل، فمن سحر إلى سحر)) .',NULL,'166','281',NULL,'0');
INSERT INTO [b26865] VALUES (279,'٢٨٢-[٢٧٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك سفيان الثوري؛ أن عاصم ⦗١٦٧⦘ ابن عبيد الله بن عمر بن الخطاب حدثه عن عبد الله بن عامر بن ربيعة العدوي، عن أبيه قال:
ما أحصي ولا أعد ما رأيت رسول الله صلى الله عليه وسلم يتسوك وهو صائم.',NULL,'166','282',NULL,'0');
INSERT INTO [b26865] VALUES (280,'٢٨٣-[٢٧٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر؛ أن عبد الرحمن بن القاسم حدثه عن أبيه، عن عائشة زوج النبي صلى الله عليه وسلم؛ أن رسول الله صلى الله عليه وسلم كان يقبل وهو صائم.',NULL,'167','283',NULL,'0');
INSERT INTO [b26865] VALUES (281,'٢٨٤-[٢٧٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك أفلح بن حميد؛ أن القاسم بن محمد حدثه، عن عائشة رضي الله عنها زوج النبي صلى الله عليه وسلم؛ أن رسول الله صلى الله عليه وسلم كان يقبل وهو صائم.',NULL,'167','284',NULL,'0');
INSERT INTO [b26865] VALUES (282,'٢٨٥-[٢٧٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك أفلح بن حميد؛ أن القاسم بن محمد حدثه عن عائشة؛ أن النبي صلى الله عليه وسلم واقع أهله ثم نام، ولم يغتسل حتى أصبح، فاغتسل وتوضأ وصلى، ثم صام يومه ذلك.',NULL,'168','285',NULL,'0');
INSERT INTO [b26865] VALUES (283,'٢٨٦-[٢٨٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن يحيى ابن أيوب، عن عبد الله بن أبي بكر، عن ابن شهاب، عن سالم بن عبد الله بن عمر، عن أبيه، عن حفصة زوج النبي صلى الله عليه وسلم أنها قالت:
⦗١٦٩⦘
من لم يجمع الصيام قبل الفجر فلا صيام له.
⦗١٧٠⦘
٢٨٧- قال: وقال الليث بن سعد مثل ذلك.',NULL,'168','286',NULL,'0');
INSERT INTO [b26865] VALUES (284,'٢٨٨-[٢٨١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، ومالك بن أنس، ويونس، عن ابن شهاب قال:
⦗١٧١⦘
بلغني أن عائشة وحفصة أصبحتا صائمتين متطوعتين، فأهدى لهما طعامٌ، ⦗١٧٢⦘ فأفطرتا عليه.
فدخل عليهما النبي صلى الله عليه وسلم قالت عائشة: وقالت حفصة: وبدرتني بالكلام، وكانت بنت أبيها - يا رسول الله، إني أصبحت أنا وعائشة صائمتين متطوعتين وأهدي لنا طعامٌ فأفطرنا عليه، فقال رسول الله صلى الله عليه وسلم: ((اقضيا مكانه يوماً)) .',NULL,'170','288',NULL,'0');
INSERT INTO [b26865] VALUES (285,'٢٨٩-[٢٨٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك أبو حسين -رجل من أهل مكة- قال: سمعت موسى بن عقبة يحدث عن صالح بن كيسان قال:
قيل: يا رسول الله، رجل كان عليه قضاء من رمضان، فقضى يوماً أو يومين متقطعين، أيجزئ عنه؟
فقال رسول الله صلى الله عليه وسلم: ((أرأيت لو كان عليه دينٌ قضاه درهماً ودرهمين حتى يقضي دينه، أترون ذمته برئت؟)) قال: نعم. قال: ((يقضى عنه)) .',NULL,'172','289',NULL,'0');
INSERT INTO [b26865] VALUES (286,'٢٩٠-[٢٨٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن أبي ذئب، عن ⦗١٧٣⦘ الحسن بن زيد، عن مولى لعبد الله بن عباس، عن ابن عباس؛ أن رسول الله صلى الله عليه وسلم احتجم وهو صائم.',NULL,'172','290',NULL,'0');
INSERT INTO [b26865] VALUES (287,'٢٩١-[٢٨٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك الحارث بن نبهان، عن عطاء ابن عجلان، عن أبي نضرة، عن أبي سعيد الخدري قال: قال رسول الله صلى الله عليه وسلم:
((إذا ذرع الرجل القيء وهو صائم، فإنه يتم صيامه، ولا قضاء عليه، وإن استقاء فقاء، فإنه يعيد صومه)) .
٢٩٢- قال: وقال مالك بن أنس، والليث بن سعد مثله.',NULL,'174','291',NULL,'0');
INSERT INTO [b26865] VALUES (288,'٢٩٣-[٢٨٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، عن ⦗١٧٥⦘ عبد الرحمن بن القاسم بن محمد حدثه عن أبيه؛ أنه كان يقول:
من كان عليه صيام من رمضان، فلم يصمه حتى دخل عليه رمضان من عام قابل، فليصم الذي دخل عليه، وليقض الآخر، فإن كان فرط وترك القضاء فيما بينهما، ولو شاء أن يصوم صام فعليه مع القضاء أن يطعم عن كل يوم مسكيناً.',NULL,'174','293',NULL,'0');
INSERT INTO [b26865] VALUES (289,'٢٩٤-[٢٨٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة؛ أن عمرو بن ⦗١٧٦⦘ دينار حدثه، عن جابر بن عبد الله؛ أن رجلاً قال: يا رسول الله، إن أمي ماتت، وعليها صيام، أفأصوم عنها؟ قال: ((نعم)) .',NULL,'175','294',NULL,'0');
INSERT INTO [b26865] VALUES (290,'٢٩٥-[٢٨٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمرو بن الحارث؛ أن ⦗١٧٧⦘ عبد الرحمن بن القاسم حدثه؛ أن محمد بن جعفر بن الزبير حدثه أنه سمع عائشة تقول:
أتى رجل النبي صلى الله عليه وسلم في المسجد في رمضان، فقال: يا رسول الله، احترقت.
فسأله النبي صلى الله عليه وسلم ما شأنه؟
قال: أصبت أهلي.
⦗١٧٨⦘
قال: ((تصدق)) .
قال: والله ما لي شيء، ولا أقدر عليه.
قال: ((اجلس)) .
فجلس فبينا هو على ذلك، أقبل رجل يسوق حماراً عليه طعام.
فقال رسول الله صلى الله عليه وسلم: ((أين المحترق آنفاً؟)) .
فقام الرجل.
فقال رسول الله صلى الله عليه وسلم: ((تصدق بهذا)) .
فقال: يا رسول الله، أعلى غيرنا، فوالله إنا لجياع، ما لنا شيء.
قال: ((فكله)) .',NULL,'176','295',NULL,'0');
INSERT INTO [b26865] VALUES (291,'٢٩٦-[٢٨٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك الحارث بن نبهان، عن ⦗١٧٩⦘ يزيد بن أبي خالد، عن أبي أيوب، عن أنس بن مالك؛ أن رسول الله صلى الله عليه وسلم لم يكن يكره الكحل للصائم، وكره السعوط أو شيئاً يصبه في أذنيه.',NULL,'178','296',NULL,'0');
INSERT INTO [b26865] VALUES (292,'٢٩٧-[٢٨٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك موسى بن علي، عن أبيه، عن أبي قيس مولى عمرو بن العاص، عن عمرو بن العاص أن رسول الله صلى الله عليه وسلم قال:
(إن فصل ما بين صيامنا وصيام أهل الكتاب أكلة السحر)) .',NULL,'179','297',NULL,'0');
INSERT INTO [b26865] VALUES (293,'٢٩٨-[٢٩٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، عن نافع، عن عبد الله بن عمر، أن النبي صلى الله عليه وسلم قال:
⦗١٨٠⦘
((إن بلالاً يؤذن بليل، فكلوا واشربوا حتى ينادي ابن أم مكتوم)) .
وفي حديث نافع: ((ينادي بليل)) .',NULL,'179','298',NULL,'0');
INSERT INTO [b26865] VALUES (294,'٢٩٩-[٢٩١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك سفيان الثوري، عن ⦗١٨١⦘ منصور بن المعتمر، عن ربعي بن حراش، عن بعض أصحاب النبي صلى الله عليه وسلم قال:
أصبح الناس صياماً لثلاثين (¬١) فجاء أعرابيان، فشهدا أنهما أهلا عشي أمس. فأمر رسول الله صلى الله عليه وسلم الناس أن يفطروا.
_________
(¬١) [[من طبعة الشنقيطي، وفي المطبوع: لليلتين، وفي المخطوط: ليلتين]]',NULL,'180','299',NULL,'0');
INSERT INTO [b26865] VALUES (295,'٣٠٠-[٢٩٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، عن ابن شهاب، عن سالم بن عبد الله؛ أن أناساً رأوا هلال الفطر نهاراً، فأتم عبد الله ابن عمر صيامه إلى الليل وقال: لا، حتى يرى من حيث يرى بالليل.',NULL,'181','300',NULL,'0');
INSERT INTO [b26865] VALUES (296,'٣٠١-[٢٩٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمر بن محمد، وعبد الله ⦗١٨٢⦘ ابن عمر، ومالك بن أنس، وأسامة بن زيد، وابن سمعان؛ أن نافعاً حدثهم، عن عبد الله بن عمر؛ أن رسول الله صلى الله عليه وسلم ذكر رمضان فقال:
((لا تصوموا حتى تروا الهلال، ولا تفطروا حتى تروا الهلال، فإن غم عليكم فاقدروا له)) .',NULL,'181','301',NULL,'0');
INSERT INTO [b26865] VALUES (297,'٣٠٢-[٢٩٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمر بن قيس، عن عطاء بن أبي رباح، عن أبي هريرة قال: سمعت رسول الله صلى الله عليه وسلم يقول:
((شهر ثلاثين وشهر تسع وعشرين)) .',NULL,'182','302',NULL,'0');
INSERT INTO [b26865] VALUES (298,'٣٠٣-[٢٩٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الرحمن بن ⦗١٨٣⦘ سلمان، وبكر بن مضر، عن ابن الهاد؛ أن ثعلبة بن أبي مالك القرظي حدثه قال:
خرج رسول الله صلى الله عليه وسلم ذات ليلة في رمضان فرأى ناساً في ناحية المسجد يصلون، فقال: ((ما يصنع هؤلاء؟)) .
قال قائل: يا رسول الله، هؤلاء ناس ليس معهم قرآن، وأبي بن كعب يقرأ وهم معه، يصلون بصلاته.
قال: ((قد أحسنوا، وقد أصابوا)) . ولم يكره ذلك لهم.',NULL,'182','303',NULL,'0');
INSERT INTO [b26865] VALUES (299,'٣٠٤-[٢٩٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك، ويونس بن يزيد، وابن سمعان، والليث بن سعد؛ أن ابن شهاب أخبرهم، عن عروة بن ⦗١٨٤⦘ الزبير، عن عبد الرحمن بن عبد القاري؛ أن عمر بن الخطاب جمع الناس على أبي بن كعب في قيام رمضان، قال: ثم خرجت مع عمر ليلة والناس يصلون بصلاة قارئهم.
فقال عمر: نعم البدعة هذه، والتي ينامون عنها أفضل من الذي يقومون يعني: يريد آخر الليل، فكان الناس يقومون أوله.',NULL,'183','304',NULL,'0');
INSERT INTO [b26865] VALUES (300,'٣٠٥-[٢٩٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك جرير بن حازم والحارث ابن نبهان، عن أيوب السختياني، عن ابن أبي مليكة، عن عائشة زوج النبي صلى الله عليه وسلم؛ أنها كان يؤمها غلامها ذكوان في المصحف في رمضان.
إلا أن الحارث قال في الحديث: عن أيوب، عن القاسم، عن عائشة.',NULL,'184','305',NULL,'0');
INSERT INTO [b26865] VALUES (301,'٣٠٦-[٢٩٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، عن ابن شهاب، عن أبي سلمة بن عبد الرحمن، عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم قال:
((أريتُ ليلة القدر، ثم أيقظني بعض أهلي فنسيتها، فالتمسوها في العشر الغوابر)) .',NULL,'185','306',NULL,'0');
INSERT INTO [b26865] VALUES (302,'٣٠٧-[٢٩٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك غير واحد منهم مالك ⦗١٨٦⦘ ابن أنس، عن حميد الطويل، عن أنس بن مالك؛ أن رسول الله صلى الله عليه وسلم قال:
((التمسوها في التاسعة والسابعة والخامسة)) .',NULL,'185','307',NULL,'0');
INSERT INTO [b26865] VALUES (303,'٣٠٨-[٣٠٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد؛ أن نافعاً حدثه عن عبد الله بن عمر، أن رسول الله صلى الله عليه وسلم كان يعتكف العشر الأواخر من رمضان.
قال: وقال نافع: وقد أراني عبد الله بن عمر المكان الذي [كان] (¬١) يعتكف رسول الله صلى الله عليه وسلم في المسجد.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]]',NULL,'186','308',NULL,'0');
INSERT INTO [b26865] VALUES (304,'٣٠٩-[٣٠١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمر بن قيس، ويزيد ⦗١٨٧⦘ ابن عياض، عن ابن شهاب، عن سعيد بن المسيب، وعروة بن الزبير؛ أنهما سمعا عائشة تقول:
السنة في المعتكف ألا يمس امرأته ولا يباشرها، ولا يعود مريضاً، ولا يتبع جنازة، ولا يخرج إلا لحاجة الإنسان، ولا اعتكاف إلا في مسجد جماعة، ومن اعتكف فقد وجب عليه الصيام.
٣١٠- قال: وقال مالك بن أنس مثله.',NULL,'186','309',NULL,'0');
INSERT INTO [b26865] VALUES (305,'٣١١-[٣٠٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، ومالك بن أنس، والليث بن سعد، عن ابن شهاب، عن عروة بن الزبير وعمرة ⦗١٨٨⦘ بنت عبد الرحمن؛ أن عائشة زوج النبي صلى الله عليه وسلم كانت إذا اعتكفت في المسجد فدخلت بيتها لحاجة لم تسأل عن المريض إلا وهي مارة.
قالت عائشة: وإن النبي صلى الله عليه وسلم لم يكن يدخل البيت إلا لحاجة الإنسان.
وقالت عائشة: كان يدخل علي رأسه وهو في المسجد فأرجله.',NULL,'187','311',NULL,'0');
INSERT INTO [b26865] VALUES (306,'٣١٢-[٣٠٣/٣٠٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمرو بن الحارث، ⦗١٨٩⦘ والليث بن سعد، وابن سمعان؛ أن يحيى بن سعيد حدثهم، عن عمرة بنت عبد الرحمن؛ أن رسول الله صلى الله عليه وسلم اعتكف عشراً من شوال.',NULL,'188','312',NULL,'0');
INSERT INTO [b26865] VALUES (307,'٣١٣-[٣٠٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك سليمان بن بلال، ⦗١٩٠⦘ والقاسم بن عبد الله، عن موسى بن عبيدة، عن جمهان، عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم قال:
((قال الله عز وجل: كل عمل ابن آدم له، والصيام لي، وأنا أجزي به، يدع زوجته وطعامه وشرابه وشهوته من أجلي، ويدع لذته من أجلي، خلوف فم الصائم أطيب عند الله من ريح المسك، والصيام نصف الصبر، وعلى كل شيء زكاة، وزكاة الجسد الصيام)) .',NULL,'189','313',NULL,'0');
INSERT INTO [b26865] VALUES (308,'٣١٤-[٣٠٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك سليمان بن بلال، عن ⦗١٩١⦘ موسى بن عبيدة عن عمران بن أبي أنس، عن أبي سلمة بن عبد الرحمن أن رجلاً قال:
يا رسول الله، ما أفطرت منذ أربع سنين.
فقال رسول الله صلى الله عليه وسلم: ((ما صمت ولا أفطرت)) .
قال موسى: وذلك فيما نرى لأنه حدث به.',NULL,'190','314',NULL,'0');
INSERT INTO [b26865] VALUES (309,'٣١٥-[٣٠٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك أنس بن عياض الليثي، عن الحارث بن عبد الرحمن، عن عمه، عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم:
⦗١٩٢⦘
((ليس الصائم (¬١) من الأكل والشرب فقط، إنما الصيام من اللغو والرفث، فإن سابك أحد وجهل عليك فقل: إني صائم)) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: الصيام]]',NULL,'191','315',NULL,'0');
INSERT INTO [b26865] VALUES (310,'٣١٦-[٣٠٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك القاسم بن عبد الله، عن العلاء بن عبد الرحمن، عن أبيه، عن أبي هريرة، عن رسول الله صلى الله عليه وسلم قال:
((قال الله عز وجل: كل حسنة عملها ابن آدم أجزي بها عشر حسنات إلى سبعمائة ضعف إلا الصيام فهو لي وأنا أجزي به، يذر الطعام من أجلي، ويذر الشهوة من أجلي، فهو لي وأنا أجزي به، الصيام جنة، فمن كان صائماً فلا يرفث ولا يجهل، فإن امرؤ شاتمه أو آذاه، فليقل: إني صائم)) .',NULL,'192','316',NULL,'0');
INSERT INTO [b26865] VALUES (311,'٣١٧-[٣٠٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك القاسم بن عبد الله، عن سهيل بن أبي صالح، عن أبيه، عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم قال:
((قال الله عز وجل: كل عمل ابن آدم له إلا الصيام فهو لي، وأنا أجزي به، الحسنة بعشر أمثالها إلى سبعمائة ضعف إلا الصيام فهو لي وأنا أجزي به، لا يدع الطعام إلا لي، ولا الشراب، ولا اللذة إلا لي، ولا يدع الشهوة إلا لي، الصيام جنة، فمن أصبح صائماً، فلا يصخب ولا يساب، وإن قاتله أحد أو سابه، فليقل: إني صائم، إني صائم، إني صائم. والذي نفسي بيده، لخلوف فم الصائم أطيب عند الله من ريح المسك، فرحتان يفرح بهما الصائم، إذا أفطر فرح لفطره، وإذا لقي الله يوم القيامة)) .',NULL,'192','317',NULL,'0');
INSERT INTO [b26865] VALUES (312,'٣١٨-[٣١٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله القتباني، عن يزيد ⦗١٩٣⦘ ابن قوذر، عن كعب الأحبار أنه قال:
ينادي يوم القيامة منادي أن كل حارث يعطى بحرثه ويزاد غير أهل القرآن والصيام، يعطون أجورهم بغير حساب.',NULL,'192','318',NULL,'0');
INSERT INTO [b26865] VALUES (313,'٣١٩-[٣١١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك القاسم بن عبد الله، عن سهيل بن أبي صالح، عن النعمان بن أبي عياش، عن أبي سعيد الخدري؛ أن رسول الله صلى الله عليه وسلم قال:
((من صام يوماً في سبيل الله بعد الله وجهه من جهنم سبعين خريفاً)) .',NULL,'193','319',NULL,'0');
INSERT INTO [b26865] VALUES (314,'٣٢٠-[٣١٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك القاسم بن عبد الله، عن سعد بن سعيد الأنصاري، عن عمر بن ثابت؛ أن أبا أيوب الأنصاري حدثه؛ أن النبي صلى الله عليه وسلم قال:
((من صام رمضان وأتبعه بست من شوال كان كصيام الدهر)) .',NULL,'193','320',NULL,'0');
INSERT INTO [b26865] VALUES (315,'٣٢١-[٣١٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الرحمن بن أبي الزناد، عن هشام بن عروة، عن أبيه، عن عائشة زوج النبي صلى الله عليه وسلم قالت:
كان رسول الله صلى الله عليه وسلم يصوم حتى أعرف فيه، ويفطر حتى أقول: ما هو بصائم، وكان صيامه في شعبان.',NULL,'194','321',NULL,'0');
INSERT INTO [b26865] VALUES (316,'٣٢٢-[٣١٤] حدثنا بحر قال: قرئ على ابن وهب: حدثك سلمة بن وردان المدني؛ أنه سمع أنس بن مالك يقول: قال النبي صلى الله عليه وسلم لأصحابه:
((من أصبح منكم صائماً؟)) .
فسكتوا إلا رجل قال: أنا.
قال: ((فمن تصدق اليوم؟)) .
قال: أنا.
قال: ((فمن شهد جنازة اليوم؟)) .
قال: أنا.
قال: ((فمن عاد مريضاً اليوم؟)) .
⦗١٩٥⦘
قال: أنا.
فقال رسول الله صلى الله عليه وسلم: ((وجبت له)) يعني: الجنة.',NULL,'194','322',NULL,'0');
INSERT INTO [b26865] VALUES (317,'٣٢٣-[٣١٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة قال: وكتب إلي عبد الملك بن قدامة الجمحي؛ أن أباه حدثه؛ أن أمه أخبرته؛ أن عثمان بن مظعون استأذن رسول الله صلى الله عليه وسلم في الإخصاء، فقال: إني رجل تشتد علي العزوبة في هذه المغازي.
فنهاه عن ذلك، فقال: ((عليك بالصيام فإنه مجفرة)) .',NULL,'195','323',NULL,'0');
INSERT INTO [b26865] VALUES (318,'<span data-type="title" id=toc-9>[من كتاب الصلاة]</span>
٣٢٤-[٣١٦] حدثنا بحر قال: قرئ على ابن وهب وأنا أسمع حاضر: أخبرك يحيى ⦗١٩٦⦘ ابن عبد الله بن سالم بن عبد الله بن عمر بن الخطاب، عن عبد الرحمن بن الحارث المخزومي، عن نافع بن جبير بن مطعم، عن عبد الله بن عباس؛ أن رسول الله صلى الله عليه وسلم قال:
((أتاني جبريل عند باب الكعبة مرتين، فصلى الظهر حين كان الفيء مثل الشراك، ثم صلى العصر حين كان كل شيء بقدر ظله، ثم صلى المغرب حين أفطر الصائم، ثم صلى العشاء حين غاب الشفق، ثم صلى الصبح حين حرم الطعام والشراب على الصائم، ثم صلى الظهر المرة الآخرة حين كان كل شيء بقدر ظله لوقت العصر بالأمس ثم صلى العصر حين كان ظل كل شيء مثليه، ثم صلى المغرب للوقت الأول لم يؤخرها، ثم العشاء الآخرة حين ذهب ثلث الليل الأول، ثم صلى الصبح حين أسفر.
⦗١٩٧⦘
ثم التفت فقال: يا محمد، هذا وقت الأنبياء من قبلك، والوقت فيما بين هذين الوقتين)) .',NULL,'195','324',NULL,'0');
INSERT INTO [b26865] VALUES (319,'٣٢٥-[٣١٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك أسامة بن زيد الليثي؛ أن ابن شهاب أخبره عن سعيد بن المسيب، وأبي سلمة بن عبد الرحمن بن عوف، عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم قال: ((إذا اشتد الحر فأبردوا عن الصلاة، فإن شدة الحر من فيح جهنم)) .',NULL,'198','325',NULL,'0');
INSERT INTO [b26865] VALUES (320,'٣٢٦-[٣١٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك الليث بن سعد عن عمر بن شبةٍ المدني، عن رجل حدثه، عن أبي الدرداء، عن النبي صلى الله عليه وسلم قال:
((إن تعجيل الصلاة في اليوم الدجن من حقيقة الإيمان)) .',NULL,'198','326',NULL,'0');
INSERT INTO [b26865] VALUES (321,'٣٢٧-[٣١٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك الخليل بن مرة، عن يحيى بن أبي كثير، عن بريدة الأسلمي صاحب النبي صلى الله عليه وسلم، عن رسول الله صلى الله عليه وسلم، أنه قال:
((بكروا بالصلاة في اليوم الغيم، فإن من ترك صلاة العصر حبط عمله)) .',NULL,'199','327',NULL,'0');
INSERT INTO [b26865] VALUES (322,'٣٢٨-[٣٢٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن أبي ذئب، عن الحارث بن عبد الرحمن، عن محمد بن عبد الرحمن بن ثوبان أن رسول الله صلى الله عليه وسلم قال:
⦗٢٠٠⦘
((هما فجران؛ فأما الذي كأنه ذنب السرحان، فإنه لا يحل شيئاً ولا يحرمه، وأما المستطيل الذي يأخذ بالأفق، فإنه يحل الصلاة ويحرم الطعام)) .
٣٢٩- قال: وقال لي مالك بن أنس.',NULL,'199','328',NULL,'0');
INSERT INTO [b26865] VALUES (323,'٣٣٠-[٣٢١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عياض بن عبد الله الفهري قال: لا أعلم إلا أن إبراهيم بن عبيد بن رفاعة حدثه قال: قال رسول الله صلى الله عليه وسلم:
((سيكون بعدي أئمة يضيعون الصلاة، ويتبعون الشهوات، فإن صلوا الصلاة لوقتها، فصلوا معهم، وإن لم يصلوا الصلاة لوقتها، فاجعلوا صلاتكم معهم نافلة)) .',NULL,'200','330',NULL,'0');
INSERT INTO [b26865] VALUES (324,'٣٣١-[٣٢٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عياض بن عبد الله القرشي، عن سعيد بن أبي سعيد المقبري، عن أبي هريرة؛ أن رجلاً أتى النبي صلى الله عليه وسلم فقال:
⦗٢٠١⦘
يا رسول الله، أمن ساعات الليل والنهار ساعة تأمرني ألا صلى فيها؟
فقال رسول الله صلى الله عليه وسلم: ((نعم، إذا صليت الصبح فأقصر عن الصلاة حتى ترتفع الشمس؛ فإنها تطلع بين قرني شيطان، ثم الصلاة مشهودة محضورة متقبلة حتى ينتصف النهار، فإذا انتصف النهار فأقصر عن الصلاة حتى تميل الشمس فإن حينئذٍ تسعر جهنم، وشدة الحر من فيح جهنم، فإذا مالت الشمس، فالصلاة مشهودة محضورة متقبلة حتى يصلى العصر [فإذا صليت العصر فأقصر عن الصلاة حتى تغرب الشمس] (¬١)) .
_________
(¬١) ما بين المعقوفتين مطموس في المخطوط وأثبتناه من رواية ابن وهب في صحيح ابن خزيمة',NULL,'200','331',NULL,'0');
INSERT INTO [b26865] VALUES (325,'٣٣٢-[٣٢٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمرو بن الحارث، ويونس بن يزيد، وابن سمعان، أن ابن شهاب أخبرهم قال: حدثني أنس بن مالك أن رسول الله صلى الله عليه وسلم [قال] :
⦗٢٠٢⦘
((إذا قرب العشاء، وحضرت الصلاة، فابدؤوا به قبل أن تصلوا صلاة المغرب)) .',NULL,'201','332',NULL,'0');
INSERT INTO [b26865] VALUES (326,'٣٣٣-[٣٢٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، عن نافع؛ أن عبد الله بن عمر كان يسمع قراءة الإمام في المغرب وهو على عشائه، فلا يعجل حتى يقضي منه حاجته.',NULL,'202','333',NULL,'0');
INSERT INTO [b26865] VALUES (327,'٣٣٤-[٣٢٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، ومالك بن أنس، والليث بن سعد، وأسامة بن زيد، وابن سمعان، عن نافع، عن عبد الله بن عمر؛ أن رسول الله صلى الله عليه وسلم كان يصلي قبل الظهر ركعتين، وبعدها ركعتين، وبعد صلاة المغرب ركعتين في بيته، وبعد صلاة العشاء ركعتين في بيته، وكان لا يصلي بعد الجمعة في المسجد شيئاً حتى ينصرف، فسجد سجدتين.
قال ابن وهب: يريد ركعتين.',NULL,'202','334',NULL,'0');
INSERT INTO [b26865] VALUES (328,'٣٣٥-[٣٢٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، عن ابن شهاب؛ أن السائب بن يزيد، وعبيد الله بن عبد الله بن عتبة، أخبراه عن عبد الرحمن بن عبدٍ القاري قال: سمعت عمر بن الخطاب يقول: قال رسول الله صلى الله عليه وسلم:
((من نام عن حزبه أو عن شيء منه فقرأه فيما بين صلاة الفجر وصلاة الظهر كتب له كأنما قرأه من الليل)) .',NULL,'203','335',NULL,'0');
INSERT INTO [b26865] VALUES (329,'٣٣٦-[٣٢٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن أبي ذئب، وعمرو بن الحارث، ويونس بن يزيد؛ أن ابن شهاب أخبرهم، عن عروة بن الزبير، عن عائشة زوج النبي صلى الله عليه وسلم قالت:
كان رسول الله صلى الله عليه وسلم يصلي -فيما بين أن يفرغ من صلاة العشاء إلى الفجر- أحدى عشرة ركعة، يسلم من كل ركعتين، ويوتر بواحدة، ويسجد سجدة قدر ما يقرأ أحدكم خمسين آية قبل أن يرفع رأسه، فإذا سكت المؤذن من صلاة الفجر، يتبين له الفجر، قام فركع ركعتين خفيفتين، ثم اضطجع على شقه الأيمن حتى يأتيه المؤذن للإقامة، فيخرج معه.
وبعضهم يزيد على بعض في قصة الحديث.',NULL,'203','336',NULL,'0');
INSERT INTO [b26865] VALUES (330,'٣٣٧-[٣٢٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، عن محمد بن المنكدر، عن سعيد بن جبير، عن رجل عنده رضاً؛ أنه أخبره أن عائشة أم المؤمنين أخبرته؛ أن رسول الله صلى الله عليه وسلم قال:
((ما من امرئ يكون له صلاة بالليل، يغلبه عليه نوم إلا كتب الله عز وجل له أجر صلاته، وكان نومه صدقة عليه)) .',NULL,'204','337',NULL,'0');
INSERT INTO [b26865] VALUES (331,'٣٣٨-[٣٢٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يحيى بن عبد الله بن سالم العمري، وسعيد بن عبد الرحمن الجمحي، ومالك بن أنس، عن هشام بن عروة، عن أبيه، عن عائشة، عن رسول الله صلى الله عليه وسلم قال:
((إذا نعس أحدكم في صلاته فليرقد حتى يذهب عنه النوم؛ فإن أحدكم إذا صلى وهو ناعس لعله يذهب فليستغفر فيسب نفسه)) .',NULL,'204','338',NULL,'0');
INSERT INTO [b26865] VALUES (332,'٣٣٩-[٣٣٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، عن نافع، وعبد الله بن دينار، عن عبد الله بن عمر؛ أن رجلاً سأل رسول الله صلى الله عليه وسلم:
كيف صلاة الليل؟
فقال صلى الله عليه وسلم: ((صلاة الليل مثنى مثنى، فإذا خشي أحدكم أن يصبح فليصل ركعة [واحدة] (¬١) توتر له ما قد صلى)) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]]',NULL,'205','339',NULL,'0');
INSERT INTO [b26865] VALUES (333,'٣٤٠- قال مالك بن أنس: والوتر سنة، أوتر رسول الله صلى الله عليه وسلم وعمل بها المسلمون.',NULL,'205','340',NULL,'0');
INSERT INTO [b26865] VALUES (334,'٣٤١-[٣٣١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، والليث بن سعد، عن يزيد بن أبي حبيب، عن عبد الله بن راشد عن عبد الله بن أبي مرة ⦗٢٠٦⦘ عن خارجة بن حذافة العدوي؛ أنه قال: سمعت رسول الله صلى الله عليه وسلم يقول:
((إن الله عز وجل قد أمدكم بصلاة هي خير لكم من حمر النعم، وهي لكم ما بين صلاة العشاء إلى طلوع الفجر؛ الوتر، الوتر)) .',NULL,'205','341',NULL,'0');
INSERT INTO [b26865] VALUES (335,'٣٤٢-[٣٣٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك حيوة بن شريح، عن ⦗٢٠٧⦘ أبي عيسى الخراساني، عن عبد الكريم الجزري، عن الحسن بن أبي الحسن؛ أن رسول الله صلى الله عليه وسلم كان يقرأ في الركعة الأولى بـ {سبح اسم ربك الأعلى} ، والثانية {قل يا أيها الكافرون} والثالثة بسورة الإخلاص والمعوذتين.',NULL,'206','342',NULL,'0');
INSERT INTO [b26865] VALUES (336,'٣٤٣-[٣٣٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن خالد بن ميمون، عن الحسن؛ أن رجلاً قال:
يا رسول الله، أوتر بعد الفجر؟ فقال له في الثالثة: ((أوتر)) .',NULL,'207','343',NULL,'0');
INSERT INTO [b26865] VALUES (337,'٣٤٤- قال: وقال مالك: وربما أوتر بعد الفجر، وإنما ذلك إذا لم يستيقظ أحدكم، فيقوم من الليل.',NULL,'207','344',NULL,'0');
INSERT INTO [b26865] VALUES (338,'٣٤٥-[٣٣٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، ومالك بن أنس، عن أبي بكر بن عمر بن عبد الرحمن؛ أن سعيد بن يسار أخبره؛ أن عبد الله بن عمر أخبره؛ أن رسول الله صلى الله عليه وسلم كان يوتر على البعير.',NULL,'208','345',NULL,'0');
INSERT INTO [b26865] VALUES (339,'٣٤٦-[٣٣٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، عن ابن شهاب، عن سالم بن عبد الله بن عمر، عن أبيه عبد الله بن عمر قال:
كان رسول الله صلى الله عليه وسلم يسبح على الراحلة قبل أي وجهة توجه، ويوتر عليها، غير أنه لا يصلي عليها المكتوبة.',NULL,'208','346',NULL,'0');
INSERT INTO [b26865] VALUES (340,'٣٤٧-[٣٣٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الرحمن بن زياد ابن أنعم، عن أبي عبد الرحمن الحبلي، عن عبد الله بن عمرو بن العاص أن رسول الله صلى الله عليه وسلم كان يقول:
((لا صلاة بعد طلوع الفجر إلا ركعتي الفجر)) .',NULL,'209','347',NULL,'0');
INSERT INTO [b26865] VALUES (341,'٣٤٨-[٣٣٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مخرمة بن بكير، عن أبيه، عن عائشة قالت:
⦗٢١٠⦘
سئل رسول الله صلى الله عليه وسلم عن الإنسان يرقد عن العشاء قبل أن يصلي. قال: ((لا نامت عينه، لا نامت عينه، لا نامت عينه)) .',NULL,'209','348',NULL,'0');
INSERT INTO [b26865] VALUES (342,'٣٤٩-[٣٣٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يحيى بن أيوب، عن عبيد الله بن زحر، عن الليث بن أبي سليم، عن مجاهد، عن عبد الله بن عمر قال:
كان رسول الله صلى الله عليه وسلم يقرأ في ركعتي الفجر: {قل هو الله أحد} ، و {قل يا أيها الكافرون} .
وقال رسول الله صلى الله عليه وسلم: ((إن قراءة {قل هو الله أحد} تعدل قراءة ثلث القرآن، وإن قراءة {قل يا أيها الكافرون} لتعدل ربع القرآن، وإن هاتين الركعتين فيهما الرغائب والخير كله)) .',NULL,'210','349',NULL,'0');
INSERT INTO [b26865] VALUES (343,'٣٥٠-[٣٣٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمرو بن الحارث، عن بكير بن عبد الله، عن [ابن] أبي سلمة، عن محمد بن عبد الرحمن بن ثوبان حدثه أنه سمع عبد الله بن عمر يقول:
صلاة الليل والنهار مثنى مثنى - يريد به التطوع.',NULL,'211','350',NULL,'0');
INSERT INTO [b26865] VALUES (344,'٣٥١-[٣٤٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن أبي ⦗٢١٢⦘ الزيبر، عن جابر بن عبد الله، عن أبي سعيد الخدري قال: سمعت رسول الله صلى الله عليه وسلم يقول:
((إذا أحدكم قضى صلاته في المسجد ثم رجع إلى بيته، فليصل ركعتين حين يرجع، وليجعل لبيته نصيباً من صلاته، فإن الله عز وجل جاعل من صلاته في بيته خيراً)) .',NULL,'211','351',NULL,'0');
INSERT INTO [b26865] VALUES (345,'٣٥٢-[٣٤١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك أسامة بن زيد الليثي، عن أبي النضر، وموسى بن عقبة؛ أن رسول الله صلى الله عليه وسلم قال:
((أفضل صلاة المرء ما كان منها في بيته إلا الصلاة المكتوبة؛ فإن لها فضلاً)) .',NULL,'212','352',NULL,'0');
INSERT INTO [b26865] VALUES (346,'٣٥٣-[٣٤٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن عبيد الله ابن أبي جعفر، عن صفوان بن سليم، أنه بلغه أن رجلاً صلى قريباً من رسول الله صلى الله عليه وسلم، فجهر بالقراءة، فقال له رسول الله صلى الله عليه وسلم:
⦗٢١٤⦘
((اخفض صلاتك لا تلقن من حولك)) .',NULL,'213','353',NULL,'0');
INSERT INTO [b26865] VALUES (347,'٣٥٤-[٣٤٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، وأسامة بن زيد، عن نافع، عن عبد الله بن عمر:
كان يفتتح أم الكتاب ببسم الله الرحمن الرحيم.',NULL,'214','354',NULL,'0');
INSERT INTO [b26865] VALUES (348,'٣٥٥-[٣٤٤] حدثنا بحر قال: قرئ على ابن وهب: حدثك سفيان بن عيينة، عن أيوب، عن قتادة، عن أنس؛ أن النبي صلى الله عليه وسلم وأبا بكر وعمر وعثمان كانوا يفتتحون الصلاة بـ {الحمد لله رب العالمين} .',NULL,'214','355',NULL,'0');
INSERT INTO [b26865] VALUES (349,'٣٥٦-[٣٤٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك سفيان بن عيينة، عن ⦗٢١٥⦘ حميد الطويل، عن أنس بن مالك في القراءة في الصلاة بذلك.',NULL,'214','356',NULL,'0');
INSERT INTO [b26865] VALUES (350,'٣٥٧-[٣٤٦] حدثنا بحر قال: قرئ على ابن وهب: حدثك عيسى بن يونس، عن حسين المعلم، عن بديل بن ميسرة، عن أبي الجوزاء، عن عائشة قالت: كان رسول الله صلى الله عليه وسلم يستفتح القراءة بالحمد لله.',NULL,'215','357',NULL,'0');
INSERT INTO [b26865] VALUES (351,'٣٥٨-[٣٤٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، عن ابن شهاب قال: أخبرني محمود بن الربيع، عن عبادة بن الصامت قال: قال رسول الله صلى الله عليه وسلم:
⦗٢١٦⦘
((لا صلاة لمن لم يقرأ بأم القرآن)) .',NULL,'215','358',NULL,'0');
INSERT INTO [b26865] VALUES (352,'٣٥٩-[٣٤٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس وغيره عن العلاء بن عبد الرحمن؛ أنه سمع أبا السائب يحدث عن أبي هريرة أنه قال: سمعت رسول الله صلى الله عليه وسلم يقول:
((من صلى صلاة لم يقترئ بأم القرآن فهي خداج، هي خداج، هي خداج غير تمام)) .',NULL,'216','359',NULL,'0');
INSERT INTO [b26865] VALUES (353,'٣٦٠-[٣٤٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يحيى بن أيوب عن المثنى ⦗٢١٧⦘ ابن الصباح عن عمرو بن شعيب، عن أبيه، عن عبد الله بن عمرو بن العاص، عن النبي صلى الله عليه وسلم مثله.',NULL,'216','360',NULL,'0');
INSERT INTO [b26865] VALUES (354,'٣٦١-[٣٥٠] حدثنا بحر قال: قرئ على ابن وهب: حدثك يحيى بن عبد الله بن سالم العمري، ويزيد بن عياض؛ أن رسول الله صلى الله عليه وسلم قال:
((من كان منكم له إمام يأتم به فلا يقرأن معه، فإن قراءته له قراءة)) .',NULL,'217','361',NULL,'0');
INSERT INTO [b26865] VALUES (355,'٣٦٢-[٣٥١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن أبي ذئب، عن الوليد بن أبي الوليد، عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم قال:
((إذا أممتم الناس فخففوا، فإن فيهم الكبير والضعيف والصغير)) .',NULL,'218','362',NULL,'0');
INSERT INTO [b26865] VALUES (356,'٣٦٣-[٣٥٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، عن أبي الزناد، عن الأعرج، عن أبي هريرة، عن النبي صلى الله عليه وسلم [مثله] (¬١) .
⦗٢١٩⦘
وقال رسول الله صلى الله عليه وسلم: ((إذا صلى لنفسه فليطول ما شاء)) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]]',NULL,'218','363',NULL,'0');
INSERT INTO [b26865] VALUES (357,'٣٦٤-[٣٥٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، والليث بن ⦗٢٢٠⦘ سعد، عن أبي الزبير، عن جابر بن عبد الله أخبره، عن معاذ بن جبل، صلى العشاء فطول على أصحابه، فأخبر النبي صلى الله عليه وسلم بذلك.
فقال رسول الله صلى الله عليه وسلم لمعاذ: ((أفتان أنت، خفف على الناس، واقرأ بالشمس وضحاها، وبسبح اسم ربك الأعلى ونحو ذلك، ولا تشق على الناس)) .',NULL,'219','364',NULL,'0');
INSERT INTO [b26865] VALUES (358,'٣٦٥-[٣٥٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس وعبد الله ابن عمر، وابن سمعان، عن نافع؛ ((أن عبد الله بن عمر كان إذا فاته شيء من الصلاة مع الإمام التي يعلن فيها بالقراءة، فإذا سلم الإمام قام عبد الله بن عمر فقرأه لنفسه فيما يقضي)) .',NULL,'220','365',NULL,'0');
INSERT INTO [b26865] VALUES (359,'٣٦٦-[٣٥٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن مشرح ⦗٢٢١⦘ ابن هاعان أبي المصعب حدثه، عن عقبة بن عامر، حدثه قال:
قلت: يا رسول الله، في سورة الحج سجدتان؟
قال: ((نعم، ومن لم يسجدهما فلا يقرأهما)) .',NULL,'220','366',NULL,'0');
INSERT INTO [b26865] VALUES (360,'٣٦٧-[٣٥٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمرو بن الحارث، ⦗٢٢٢⦘ عن سعيد بن أبي هلال، عن عياض بن عبد الله بن سعد بن أبي السرح، عن أبي سعيد الخدري؛ أن رسول الله صلى الله عليه وسلم سجد في {ص والقرآن} .',NULL,'221','367',NULL,'0');
INSERT INTO [b26865] VALUES (361,'٣٦٨-[٣٥٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمرو بن الحارث، عن سعيد بن أبي هلال، عن من أخبره، عن أبي الدرداء، أنه سجد مع رسول الله صلى الله عليه وسلم إحدى عشرة سجدة منهن النجم.',NULL,'222','368',NULL,'0');
INSERT INTO [b26865] VALUES (362,'٣٦٩- قال ابن وهب: وبلغني عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم سجد في النجم.',NULL,'223','369',NULL,'0');
INSERT INTO [b26865] VALUES (363,'٣٧٠-[٣٥٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك قرة بن عبد الرحمن المعافري، عن ابن شهاب، وصفوان بن سليم، عن عبد الرحمن بن سعد، عن أبي هريرة، أنه قال:
سجدت مع رسول الله صلى الله عليه وسلم في {إذا السماء انشقت} و {اقرأ باسم ربك الذي خلق} سجدتين.',NULL,'224','370',NULL,'0');
INSERT INTO [b26865] VALUES (364,'٣٧١-[٣٥٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة عن سعيد ابن الحارث، عن رجل بحصبه (¬١) ، عن عمرو بن العاص، عن النبي صلى الله عليه وسلم مثله.
_________
(¬١) اسم مكان.',NULL,'224','371',NULL,'0');
INSERT INTO [b26865] VALUES (365,'٣٧٢-[٣٦٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك هشام بن سعد، وحفص بن ميسرة، عن زيد بن أسلم، عن عطاء بن يسار قال:
بلغني أن رجلاً قرأ بآية من القرآن فيها سجدة عند النبي صلى الله عليه وسلم، فسجد الرجل وسجد معه النبي صلى الله عليه وسلم، ثم قرأ آخر آية فيها سجدة، وهو عند النبي صلى الله عليه وسلم، فانتظر الرجل أن يسجد النبي صلى الله عليه وسلم فلم يسجد.
⦗٢٢٦⦘
فقال الرجل: يا رسول الله، قرأت السجدة فلم تسجد؟
فقال رسول الله صلى الله عليه وسلم: ((كنت إماماً فلو سجدت سجدت معك)) .',NULL,'225','372',NULL,'0');
INSERT INTO [b26865] VALUES (366,'٣٧٣-[٣٦١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، عن نافع، عن عبد الله بن عمر قال:
كان رسول الله صلى الله عليه وسلم يقرأ علينا القرآن، فيقرأ السجدة فيسجد، ونسجد معه، وذلك في غير صلاة.',NULL,'226','373',NULL,'0');
INSERT INTO [b26865] VALUES (367,'٣٧٤-[٣٦٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، عن ابن شهاب قال: أخبرني الأعرج، عن أبي هريرة قال:
رأيت عمر بن الخطاب رضي الله عنه سجد في النجم في صلاة الفجر ثم استفتح بسورة أخرى.',NULL,'226','374',NULL,'0');
INSERT INTO [b26865] VALUES (368,'٣٧٥-[٣٦٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، ⦗٢٢٧⦘ والليث بن سعد، ويونس بن يزيد [وابن سمعان] (¬١) ؛ أن ابن شهاب أخبرهم قال: أخبرني أنس ابن مالك؛ أن رسول الله صلى الله عليه وسلم ركب فرساً فصرع عنه، فجحش شقه الأيمن، فصلى لنا صلاة من الصلوات وهو جالس، فصلينا معه جلوساً.
فلما انصرف قال: ((إنما جعل الإمام ليؤتم به، فلا تختلفوا عليه، فإن (¬٢) صلى قائماً فصلوا قياماً، وإذا كبر فكبروا، وإذا قال: سمع الله لمن حمده. فقولوا: ربنا ولك الحمد. وإذا سجد فاسجدوا، وإذا صلى قاعداً فصلوا قعوداً أجمعون)) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]]
(¬٢) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: فإذا]]',NULL,'226','375',NULL,'0');
INSERT INTO [b26865] VALUES (369,'٣٧٦-[٣٦٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن جريج؛ أن نافعاً أخبره؛ أن عبد الله بن عمر كان إذا فاتته ركعة أو شيء من الصلاة مع الإمام، فسلم الإمام قام ساعة يسلم، ولم ينتظر قيام الإمام.',NULL,'227','376',NULL,'0');
INSERT INTO [b26865] VALUES (370,'٣٧٧-[٣٦٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك الحارث بن نبهان، عن أبي هارون العبدي؛ أن أبا سعيد الخدري قال: هي السنة.
٣٧٨- وعن ابن المسيب أيضاً.',NULL,'227','377',NULL,'0');
INSERT INTO [b26865] VALUES (371,'٣٧٩-[٣٦٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عثمان بن عطاء، عن أبيه، عن المغيرة بن شعبة؛ أنه سمع رسول الله صلى الله عليه وسلم يقول:
((لا يسبح الإمام في مصلاه حتى يتنحى (¬١) عنه)) .
٣٨٠- قال مالك: ذلك أحب إلي أن يفعل.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: يتجافى]]',NULL,'228','379',NULL,'0');
INSERT INTO [b26865] VALUES (372,'٣٨١-[٣٦٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، ⦗٢٢٩⦘ عن نافع؛ أن عبد الله بن عمر كان يصلي سبحته في مقامه الذي صلى فيه.',NULL,'228','381',NULL,'0');
INSERT INTO [b26865] VALUES (373,'٣٨٢-[٣٦٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يحيى بن أيوب، عن عبد الرحمن بن حرملة، عن أبي علي الهمداني قال: سمعت عقبة بن عامر يقول: سمعت رسول الله صلى الله عليه وسلم يقول:
((من أم الناس فأصاب الوقت وأتم الصلاة فله ولهم، ومن انتقص من ذلك شيئاً فعليه ولا عليهم)) .',NULL,'229','382',NULL,'0');
INSERT INTO [b26865] VALUES (374,'٣٨٣-[٣٦٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يحيى بن أيوب، عن العلاء بن كثير، عن داود بن أيوب، عن سعيد المقبري؛ أن أبا شريح العدوي قال: قال رسول الله صلى الله عليه وسلم:
((الإمام جنة، فإن أتم فلكم، وإن نقص فعليه النقصان، ولكم التمام)) .',NULL,'229','383',NULL,'0');
INSERT INTO [b26865] VALUES (375,'٣٨٤-[٣٧٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، عن سعيد المقبري، عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم رأى رجلاً يصلي يوماً، وهو في المسجد، فلما فرغ الرجل جاء فسلم على رسول الله صلى الله عليه وسلم فقال: ((وعليكم السلام)) .
قال: ((ارجع فصل فإنك لم تصل)) .
فرجع فصلى، ثم جاء، فسلم على النبي صلى الله عليه وسلم.
فقال له مثل [ما] قال.
فرجع فصلى مرتين، أو ثلاثاً، ثم قال: يا رسول الله، ما أحسن غير ما ترى، فعلمني كيف أصلي.
فقال له: ((إذا قمت إلى الصلاة فأسبغ الوضوء، ثم كبر، فإذا استويت قائماً قرأت بأم القرآن، ثم قرأت بما معك من القرآن، ثم ركعت، حتى تطمئن راكعاً، ثم ترفع رأسك حتى تعتدل قائماً، وتقول: سمع الله لمن حمده. ثم تسجد حتى تطمئن ساجداً، ثم ترفع رأسك حتى تطمئن قاعداً، ثم تفعل مثل ذلك في صلاتك كلها)) .',NULL,'230','384',NULL,'0');
INSERT INTO [b26865] VALUES (376,'٣٨٥-[٣٧١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك داود بن قيس المدني، عن علي بن يحيى بن خلاد الزرقي قال: حدثني أبي، عن عم له بدري، أنه كان مع رسول الله صلى الله عليه وسلم جالساً في المسجد، ثم ذكر هذا.
وقال له رسول الله صلى الله عليه وسلم: ((فإذا أتممت صلاتك على نحو هذا، فقد تمت صلاتك، وما نقصت من هذا، فإنما تنقصه من صلاتك)) .',NULL,'231','385',NULL,'0');
INSERT INTO [b26865] VALUES (377,'٣٨٦-[٣٧٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، عن ابن ⦗٢٣٢⦘ شهاب، عن سالم بن عبد الله بن عمر، عن أبيه؛ أن رسول الله صلى الله عليه وسلم كان يرفع يديه حذو منكبيه إذا افتتح التكبير للصلاة، وإذا كبر للركوع، وإذا رفع رأسه من الركوع رفعهما كذلك أيضاً، وقال: ((سمع الله لمن حمده، ربنا ولك الحمد)) ، وكان لا يفعل ذلك في السجود.',NULL,'231','386',NULL,'0');
INSERT INTO [b26865] VALUES (378,'٣٨٧-[٣٧٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، عن يحيى ابن سعيد، عن سليمان بن يسار؛ أن رسول الله صلى الله عليه وسلم كان يرفع يديه في الصلاة.',NULL,'232','387',NULL,'0');
INSERT INTO [b26865] VALUES (379,'٣٨٨-[٣٧٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، عن ابن شهاب، عن سالم، عن أبيه؛ أنه كان يكبر في الصلاة كلما خفض وكلما رفع.',NULL,'232','388',NULL,'0');
INSERT INTO [b26865] VALUES (380,'٣٨٩-[٣٧٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، ويونس بن يزيد، وابن سمعان، عن ابن شهاب، عن علي بن حسين بن علي بن أبي طالب قال:
كان رسول الله صلى الله عليه وسلم يكبر كلما خفض ورفع، فلم تزل تلك صلاته حتى ⦗٢٣٣⦘ لقى الله عز وجل.',NULL,'232','389',NULL,'0');
INSERT INTO [b26865] VALUES (381,'٣٩٠-[٣٧٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمرو بن الحارث، وابن ⦗٢٣٤⦘ لهيعة، عن خالد بن أبي عمران، أن سالم بن عبد الله، ونافعاً حدثاه؛ أن عمر ابن الخطاب كان لا يكبر حتى يلتفت إلى الصفوف ويعتدل، فإذا اعتدل كبر ثم قال:
((سبحانك اللهم وبحمدك تبارك اسمك وتعالى جدك ولا إله غيرك)) . رافعاً بها صوته، وأن أبا بكر الصديق كان يفعل ذلك.',NULL,'233','390',NULL,'0');
INSERT INTO [b26865] VALUES (382,'٣٩١-[٣٧٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة؛ أن أبا ⦗٢٣٥⦘ الزبير المكي أخبره عن جابر بن عبد الله قال:
سمعت رسول الله صلى الله عليه وسلم يأمر أن يعتدل في السجود، ولا يسجد الرجل باسط ذراعيه كالكلب.',NULL,'234','391',NULL,'0');
INSERT INTO [b26865] VALUES (383,'٣٩٢-[٣٧٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن جريج، عن عبد الله ⦗٢٣٦⦘ ابن طاوس، عن أبيه، عن ابن عباس؛ أن رسول الله صلى الله عليه وسلم قال:
((أمرت أن أسجد على سبعة، ولا أكف الشعر ولا الثياب؛ الجبهة، والأنف، واليدين، والركبتين، والقدمين)) .',NULL,'235','392',NULL,'0');
INSERT INTO [b26865] VALUES (384,'٣٩٣-[٣٧٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، وعمرو بن الحارث، عن بكر بن سوادة الجذامي، عن صالح بن خيوان السبائي حدثه؛ أن رسول الله صلى الله عليه وسلم رأى رجلاً يسجد بجنبه وقد اعتم على جبهته، فحسر رسول الله صلى الله عليه وسلم عن جبهته.',NULL,'236','393',NULL,'0');
INSERT INTO [b26865] VALUES (385,'٣٩٤-[٣٨٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن أبي ذئب عن مولى ابن عباس، عن ابن عباس؛ أن رسول الله صلى الله عليه وسلم كان إذا سجد يرى بياض إبطيه.',NULL,'236','394',NULL,'0');
INSERT INTO [b26865] VALUES (386,'٣٩٥-[٣٨١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن أبي ذئب، عن ⦗٢٣٧⦘ إسحاق بن يزيد الهذلي، عن عون بن عبد الله، عن عبد الله بن مسعود؛ أن رسول الله صلى الله عليه وسلم قال:
((إذا ركع أحدكم فقال: سبحان ربي العظيم، ثلاث مرات، فقد تم ركوعه، وذلك أدناه، وإذا سجد فقال: سبحان ربي الأعلى، ثلاث مرات، فقد تم سجوده، وذلك أدناه)) .',NULL,'236','395',NULL,'0');
INSERT INTO [b26865] VALUES (387,'٣٩٦-[٣٨٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، عن ابن شهاب أنه قال: حدثني إبراهيم بن عبد الله بن حنين؛ أن أباه حدثه أنه سمع علي بن أبي طالب قال:
نهاني رسول الله صلى الله عليه وسلم أن أقرأ راكعاً أو ساجداً.',NULL,'237','396',NULL,'0');
INSERT INTO [b26865] VALUES (388,'٣٩٧-[٣٨٣] حدثنا بحر قال: قرئ على ابن وهب: حدثك (¬١) داود بن قيس، عن ⦗٢٣٨⦘ إبراهيم، عن أبيه، عن النبي صلى الله عليه وسلم نحوه.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: أخبرك]]',NULL,'237','397',NULL,'0');
INSERT INTO [b26865] VALUES (389,'٣٩٨-[٣٨٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن يزيد بن أبي حبيب، وعبد الكريم بن الحارث، عن محمد بن عمرو بن حلحلة الدؤلي، عن محمد بن عمرو بن عطاء، عن أبي حميد الساعدي قال:
رأيت رسول الله صلى الله عليه وسلم إذا قعد في الركعتين قعد على بطن قدمه اليسرى ونصب اليمنى، فإذا كانت الرابعة أفضى بوركه اليسرى إلى الأرض، وأخرج قدميه من ناحية واحدة.',NULL,'238','398',NULL,'0');
INSERT INTO [b26865] VALUES (390,'٣٩٩-[٣٨٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك داود بن قيس المدني أن نافع بن جبير بن مطعم القرشي حدثه؛ أن رسول الله صلى الله عليه وسلم قال:
((إذا صلى أحدكم فليصل إلى سترة، وليدنو من سترته؛ فإن الشيطان يمر بينه وبينها)) .',NULL,'239','399',NULL,'0');
INSERT INTO [b26865] VALUES (391,'٤٠٠-[٣٨٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، وسعيد بن ⦗٢٤٠⦘ أبي أيوب، عن محمد بن عبد الرحمن القرشي، عن عروة بن الزبير قال:
سئل رسول الله صلى الله عليه وسلم في غزوة تبوك: ما سترة المصلي؟
فقال: ((مثل مؤخرة الرحل يجعله بين يديه)) .',NULL,'239','400',NULL,'0');
INSERT INTO [b26865] VALUES (392,'٤٠١-[٣٨٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، وغيره، عن زيد بن أسلم، عن عطاء بن يسار، عن أبي سعيد الخدري، أن رسول الله صلى الله عليه وسلم قال: ((إذا كان أحدكم يصلي، فلا يدع أحداً يمر بين يديه)) .',NULL,'240','401',NULL,'0');
INSERT INTO [b26865] VALUES (393,'٤٠٢-[٣٨٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمرو بن الحارث، ⦗٢٤١⦘ عن بكر بن سوادة، عن عبد الله بن أبي مريم، عن قبيصة بن ذؤيب؛ أن قطاً أراد أن يمر (¬١) بين يدي رسول الله صلى الله عليه وسلم فحبسه رسول الله صلى الله عليه وسلم برجله.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: يمشي]]',NULL,'240','402',NULL,'0');
INSERT INTO [b26865] VALUES (394,'٤٠٣-[٣٨٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، ويونس بن يزيد، وابن سمعان، عن ابن شهاب، عن عبيد الله بن عبد الله، عن عبد الله بن عباس قال:
جئت راكباً على أتان، وقد ناهزت الحلم، فإذا النبي صلى الله عليه وسلم يصلي بالناس بمنىً، فسرت على الأتان بين يدي بعض الصف، ثم نزلت فأرسلتها، فدخلت في الصف مع الناس، فلم ينكر ذلك أحد.',NULL,'241','403',NULL,'0');
INSERT INTO [b26865] VALUES (395,'٤٠٤-[٣٩٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك حفص بن ميسرة؛ ⦗٢٤٢⦘ أن زيد بن أسلم حدثه؛ أن رسول الله صلى الله عليه وسلم قال:
((خياركم ألينكم مناكب في الصفوف في الصلاة)) .',NULL,'241','404',NULL,'0');
INSERT INTO [b26865] VALUES (396,'٤٠٥-[٣٩١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن أبي ذئب، عن سعيد المقبري، عن عروة بن الزبير؛ أن رسول الله صلى الله عليه وسلم قال:
((من سد فرجة في الصف رفعه الله بها في الجنة درجة، أو يبني له في الجنة بيتاً)) .',NULL,'242','405',NULL,'0');
INSERT INTO [b26865] VALUES (397,'٤٠٦-[٣٩٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك محمد بن أبي حميد عن أبي حازم؛ أن رسول الله صلى الله عليه وسلم [قال] :
((خير صفوف الرجال أولها، وشرها آخرها، وخير صفوف النساء آخرها، وشرها أولها)) .',NULL,'242','406',NULL,'0');
INSERT INTO [b26865] VALUES (398,'٤٠٧-[٣٩٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، عن وهب بن كيسان، عن جابر بن عبد الله؛ أنه أتى النبي صلى الله عليه وسلم وهو يصلي.
⦗٢٤٣⦘
قال: فقمت على يساره، فجعلني عن يمينه، فصليت معه.',NULL,'242','407',NULL,'0');
INSERT INTO [b26865] VALUES (399,'٤٠٨-[٣٩٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن سمعان؛ أن نافعاً أخبره قال:
كنت إذا صليت مع عبد الله بن عمر، ومعنا امرأة يجعلني عن يمينه، والمرأة خلفه.
٤٠٩- قال ابن سمعان: وأخبرني هشام بن عروة بن الزبير، عن أبيه، مثله.',NULL,'243','408',NULL,'0');
INSERT INTO [b26865] VALUES (400,'٤١٠-[٣٩٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، ويونس بن يزيد، عن ابن شهاب قال: حدثني سعيد بن المسيب، وأبو سلمة بن عبد الرحمن؛ أن أبا هريرة قال: سمعت رسول الله صلى الله عليه وسلم يقول:
((إذا أمن الإمام فأمنوا، فإن الملائكة تؤمن، فمن وافق تأمينه تأمين الملائكة، غفر له ما تقدم من ذنبه)) .
⦗٢٤٤⦘
قال ابن شهاب: وكان رسول الله صلى الله عليه وسلم يقول: ((آمين)) .
قال يونس: وكان ابن شهاب يقول ذلك.',NULL,'243','410',NULL,'0');
INSERT INTO [b26865] VALUES (401,'٤١١-[٣٩٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، ويونس بن يزيد، وعمرو بن الحارث، أن ابن شهاب حدثهم، عن عروة بن الزبير، عن عبد الرحمن بن عبدٍ القاري؛ أنه سمع عمر بن الخطاب يعلم الناس التشهد على المنبر، فيقول:
قولوا: التحيات لله، الزاكيات لله، الطيبات لله، الصلوات لله، السلام عليك أيها النبي ورحمة الله، السلام علينا وعلى عباد الله الصالحين، أشهد أن لا إله إلا الله، وأشهد أن محمداً عبده ورسوله.
٤١٢- قال: وقال مالك بن أنس، مثله.',NULL,'244','411',NULL,'0');
INSERT INTO [b26865] VALUES (402,'٤١٣-[٣٩٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، وغيره، عن مسلم بن أبي مريم، عن علي بن عبد الرحمن المعاوي، عن عبد الله بن عمر قال:
⦗٢٤٥⦘
كان رسول الله صلى الله عليه وسلم إذا جلس في الصلاة، وضع كفه اليسرى على فخذه اليسرى، ووضع كفه اليمنى على فخذه اليمنى، وقبض أصابعه كلها، وأشار بأصبعه التي تلي الإبهام.',NULL,'244','413',NULL,'0');
INSERT INTO [b26865] VALUES (403,'٤١٤-[٣٩٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن قرة ابن عبد الرحمن، عن ابن شهاب؛ أن رسول الله صلى الله عليه وسلم كان يسلم من الصلاة أمام وجهه.
٤١٥- قال: وقال مالك مثله.',NULL,'245','414',NULL,'0');
INSERT INTO [b26865] VALUES (404,'٤١٦-[٣٩٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك إبراهيم بن سعد بن عبد الرحمن بن عوف، عن أبيه، عن أبي عبيدة، عن أبيه عبد الله بن مسعود قال:
كان رسول الله صلى الله عليه وسلم إذا كان في الركعتين الأوليين كأنما يكون على الرضف.
قال: قلت: حتى يقوم.',NULL,'245','416',NULL,'0');
INSERT INTO [b26865] VALUES (405,'٤١٧-[٤٠٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، وابن أبي ذئب، عن ابن شهاب قال: أخبرني أبو أمامة بن سهل بن حنيف؛ أنه رأى زيد بن ثابت دخل المسجد والإمام راكع فمشى حتى إذا أمكنه أن يصل الصف وهو راكع كبر فركع، ثم دب وهو راكع حتى وصل الصف.',NULL,'246','417',NULL,'0');
INSERT INTO [b26865] VALUES (406,'٤١٨-[٤٠١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن عبد الرحمن بن هرمز الأعرج، عن أبي هريرة، عن رسول الله صلى الله عليه وسلم قال:
((لا تأتوا الصلاة وأنتم تسعون، وائتوها تمشون مطمئنين، عليكم السكينة، فما أدركتم فصلوا، وما فاتكم فأتموا.',NULL,'246','418',NULL,'0');
INSERT INTO [b26865] VALUES (407,'٤١٩-[٤٠٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، عن حميد الطويل، عن أنس بن مالك، عن النبي صلى الله عليه وسلم بذلك.',NULL,'246','419',NULL,'0');
INSERT INTO [b26865] VALUES (408,'٤٢٠-[٤٠٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك أسامة بن زيد الليثي وغيره، عن نافع؛ أن أبا سلمة بن عبد الرحمن أخبره؛ أن رسول الله صلى الله عليه وسلم قال:
((إذا خرج ثلاثة نفر في سفر فليؤمهم أحدهم)) .',NULL,'247','420',NULL,'0');
INSERT INTO [b26865] VALUES (409,'٤٢١-[٤٠٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مخرمة بن بكير، عن أبيه [عن] (¬١) نافع، عن أبي سلمة بن عبد الرحمن، عن النبي صلى الله عليه وسلم بهذا.
وقال: ((هو أميرهم)) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]]',NULL,'247','421',NULL,'0');
INSERT INTO [b26865] VALUES (410,'٤٢٢-[٤٠٥] حدثنا بحر قال: قرئ على ابن وهب: سمعت معاوية بن صالح، وذكر يحيى، عن ابن المسيب، أن النبي صلى الله عليه وسلم قال:
((فليؤمهم أفقههم)) ، وذلك أمير أمره رسول الله صلى الله عليه وسلم.',NULL,'247','422',NULL,'0');
INSERT INTO [b26865] VALUES (411,'٤٢٣-[٤٠٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن جريج، أن نافعاً ⦗٢٤٨⦘ أخبرهم، أن عبد الله بن عمر أخبره قال:
كان سالم مولى أبي حذيفة يؤم المهاجرين الأولين وأصحاب رسول الله صلى الله عليه وسلم من الأنصار في مسجد قباء، فيهم أبو بكر، وعمر، وأبو سلمة، وزيد بن حارثة، وعامر بن ربيعة.',NULL,'247','423',NULL,'0');
INSERT INTO [b26865] VALUES (412,'٤٢٤-[٤٠٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك هشام بن سعد قال: حدثني معاذ بن عبد الله الجهني قال: دخلنا عليه فقال لامرأته:
متى يصلي الصبي؟ فقالت: نعم. كان رجل منا يذكر عن رسول الله صلى الله عليه وسلم أنه سئل عن ذلك.
فقال: ((إذا عرف يمينه من يساره فمروه بالصلاة)) .',NULL,'248','424',NULL,'0');
INSERT INTO [b26865] VALUES (413,'٤٢٥-[٤٠٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، أن أبا يونس ⦗٢٤٩⦘ مولى أبي هريرة أخبره، عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم قال:
((فضل صلاة الجمع على صلاة الفذ خمس وعشرون درجة)) .',NULL,'248','425',NULL,'0');
INSERT INTO [b26865] VALUES (414,'٤٢٦-[٤٠٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن عمر، وغيره، عن نافع، عن ابن عمر؛ أن رسول الله صلى الله عليه وسلم، مثله إلا أنه قال:
((سبع وعشرون درجة)) .',NULL,'249','426',NULL,'0');
INSERT INTO [b26865] VALUES (415,'٤٢٧-[٤١٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمرو بن الحارث، ⦗٢٥٠⦘ عن بكير بن عبد الله، عن سمعان مولى خزاعة؛ أن عبد الرحمن بن أبي عمرة، حدثه؛ أنه سمع عثمان بن عفان يقول:
من شهد العشاء مع الإمام فكأنما قام ليلته.
قال سمعان: فحدثت بهذا عامر بن سعد بن أبي وقاص فلقيني بعد فقال: سألت أبان بن عثمان، فقال: صدق، قد قال ذلك عثمان.',NULL,'249','427',NULL,'0');
INSERT INTO [b26865] VALUES (416,'٤٢٨-[٤١١] حدثنا بحر قال: قرئ على ابن وهب: حدثك سعيد بن أبي أيوب، عن خالد بن يزيد، عن أبي رافع؛ أن رسول الله صلى الله عليه وسلم قال:
((سلوا الله حوائجكم [البتة] (¬١) في صلاة الصبح)) .
_________
(¬١) [[من المخطوط وطبعة الشنقيطي]]',NULL,'250','428',NULL,'0');
INSERT INTO [b26865] VALUES (417,'٤٢٩-[٤١٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك الليث بن سعد، عن جعفر بن ربيعة، عن عبد الرحمن الأعرج، عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم قال:
((لينتهين أقوام عن رفعهم أبصارهم عند الدعاء في الصلاة إلى السماء أو لتخطفن أبصارهم)) .',NULL,'250','429',NULL,'0');
INSERT INTO [b26865] VALUES (418,'٤٣٠-[٤١٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، ⦗٢٥١⦘ عن ابن شهاب، أخبرني حميد بن عبد الرحمن، أنه سمع أبا هريرة، وأبا سعيد الخدري يقولان:
رأى رسول الله صلى الله عليه وسلم نخامة في القبلة، فتناول حصاة فحتها، ثم قال:
((لا يتنخم أحدكم في القبلة ولا عن يمينه، وليبصق عن يساره أو تحت رجله اليسرى)) .',NULL,'250','430',NULL,'0');
INSERT INTO [b26865] VALUES (419,'٤٣١-[٤١٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عياض بن عبد الله القرشي، وغيره، عن سعيد المقبري، عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم قال:
((إذا صلى أحدكم فليلبس نعليه، أو ليجعلهما بين رجليه، ولا يؤذي بهما غيره)) .',NULL,'251','431',NULL,'0');
INSERT INTO [b26865] VALUES (420,'٤٣٢-[٤١٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، عن إسماعيل ابن محمد بن سعد بن أبي وقاص، عن مولى لعمرو بن العاص أو لعبد الله بن عمرو بن العاص [عن عبد الله بن عمرو بن العاص] (¬١) أن رسول الله صلى الله عليه وسلم قال:
((صلاة أحدكم وهو قاعد مثل نصف صلاته وهو قائم)) .
_________
(¬١) ما بين المعقوفتين ليس في الأصل، وأثبتناه من الموطأ.',NULL,'252','432',NULL,'0');
INSERT INTO [b26865] VALUES (421,'٤٣٣-[٤١٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك زمعة بن أبي صالح ⦗٢٥٣⦘ المكي، عن عمرو بن دينار قال: صلى عبد الله بن عباس على بساط.
ثم حدث أصحابه أن رسول الله صلى الله عليه وسلم كان يصلي على بساطه.',NULL,'252','433',NULL,'0');
INSERT INTO [b26865] VALUES (422,'٤٣٤-[٤١٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، عن ابن شهاب قال: لم أزل أسمع أن رسول الله صلى الله عليه وسلم صلى على خمرة.',NULL,'253','434',NULL,'0');
INSERT INTO [b26865] VALUES (423,'٤٣٥- وعن أنس بن مالك قال: كان رسول الله صلى الله عليه وسلم يصلي على الخمرة ويسجد عليها.',NULL,'253','435',NULL,'0');
INSERT INTO [b26865] VALUES (424,'٤٣٦-[٤١٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، وعبد الله بن عمر، عن إسحاق بن عبد الله بن أبي طلحة، عن أنس بن مالك؛ أن رسول الله صلى الله عليه وسلم جاءهم فصلى لهم على حصير.',NULL,'254','436',NULL,'0');
INSERT INTO [b26865] VALUES (425,'٤٣٧-[٤١٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك الوليد بن المغيرة، أن وهب بن عبد الله المعافري حدثه قال: قال رسول الله صلى الله عليه وسلم:
((لا يضعن أحدكم ثوبه على أنفه في الصلاة، فإن ذلك خطم الشيطان)) .
٤٣٨- قال مالك بن أنس، مثله.',NULL,'254','437',NULL,'0');
INSERT INTO [b26865] VALUES (426,'٤٣٩-[٤٢٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك أنس بن عياض، عن عيسى بن أبي عيسى الحناط، عن نافع، أن نساء عبد الله بن عمر وجواريه كن ⦗٢٥٥⦘ يخضبن، ويصلين والحناء على أيديهن، وهن على وضوء، فلا ينكره عبد الله بن عمر.
٤٤٠- قال مالك بن أنس: لا بأس بذلك أن يصلين بالحناء إذا كن على طهرٍ.',NULL,'254','439',NULL,'0');
INSERT INTO [b26865] VALUES (427,'٤٤١-[٤٢١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن عبد الرحمن الأعرج، عن أبي هريرة، عن رسول الله صلى الله عليه وسلم.
٤٤٢- وعن أبي الزبير، عن جابر بن عبد الله، عن رسول الله صلى الله عليه وسلم قال:
((التسبيح للرجال، والتصفيق للنساء)) .',NULL,'255','441',NULL,'0');
INSERT INTO [b26865] VALUES (428,'٤٤٣-[٤٢٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك هشام بن سعد ⦗٢٥٦⦘ عن نافع قال: سمعت عبد الله بن عمر قال:
خرج رسول الله صلى الله عليه وسلم إلى قباء، فسمعت به الأنصار، فجاءوا يسلمون على رسول الله صلى الله عليه وسلم، قال: فقلت لبلال أو صهيب: كيف رأيت رسول الله صلى الله عليه وسلم يرد عليهم وهم يسلمون عليه وهو يصلي؟ فقال: يشير بيده.
قال: وبلغني في غير هذا الحديث أن صهيباً الذي سأله ابن عمر.',NULL,'255','443',NULL,'0');
INSERT INTO [b26865] VALUES (429,'٤٤٤-[٤٢٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، عن زيد بن أسلم، عن رجل من بني الديل يقال له: بسر بن محجن، عن أبيه محجن أنه كان جالساً مع رسول الله صلى الله عليه وسلم فأذن بالصلاة فقام رسول الله صلى الله عليه وسلم فصلى ثم رجع، ومحجن في مجلسه كما هو.
فقال له رسول الله صلى الله عليه وسلم: ((ما منعك أن تصلي مع الناس ألست برجل مسلم؟)) .
قال: بلى يا رسول الله، ولكني يا رسول الله كنت قد صليت في أهلي.
قال: ((وإذا جئت فصل مع الناس، وإن كنت قد صليت)) .',NULL,'256','444',NULL,'0');
INSERT INTO [b26865] VALUES (430,'٤٤٥-[٤٢٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك سفيان الثوري، عن ⦗٢٥٧⦘ أبي الزبير، عن جابر قال: لا يعيد الرجل الصلاة من التبسم.',NULL,'256','445',NULL,'0');
INSERT INTO [b26865] VALUES (431,'٤٤٦-[٤٢٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس عن عامر بن عبد الله بن الزبير، عن عمرو بن سليم الزرقي، عن أبي قتادة؛ أن رسول الله صلى الله عليه وسلم قال:
((إذا دخل أحدكم المسجد فليصل ركعتين قبل أن يقعد)) .',NULL,'257','446',NULL,'0');
INSERT INTO [b26865] VALUES (432,'٤٤٧-[٤٢٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك داود بن قيس، عن سعد بن إسحاق بن كعب بن عجرة حدثه عن أبي ثمامة؛ أن كعب بن عجرة حدثه ⦗٢٥٨⦘ عن رسول الله صلى الله عليه وسلم، أنه قال:
((إذا توضأ أحدكم ثم خرج إلى المسجد فلا يشبك بين أصابعه، فإنه في صلاة)) .',NULL,'257','447',NULL,'0');
INSERT INTO [b26865] VALUES (433,'٤٤٨-[٤٢٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك سعيد بن أبي أيوب، ⦗٢٥٩⦘ عمن حدثه، عن عبد الله بن مغفل صاحب رسول الله صلى الله عليه وسلم أنه قال:
نهى رسول الله صلى الله عليه وسلم أن يصلى في معاطن الإبل، وأمر أن يصلى في مراح الغنم والبقر.',NULL,'258','448',NULL,'0');
INSERT INTO [b26865] VALUES (434,'٤٤٩-[٤٢٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يحيى بن أيوب، عن زيد بن جبيرة الأنصاري، عن داود بن الحصين، عن نافع، عن عبد الله بن عمر قال:
⦗٢٦٠⦘
نهى رسول الله صلى الله عليه وسلم عن الصلاة في سبع مواطن: المقبرة، والمجزرة، والمزبلة، والحمام، ومحجة الطريق، وظهر بيت الله، ومعاطن الإبل.',NULL,'259','449',NULL,'0');
INSERT INTO [b26865] VALUES (435,'٤٥٠-[٤٢٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن ⦗٢٦١⦘ عبد الرحمن الأعرج، عن أبي هريرة، عن رسول الله صلى الله عليه وسلم؛ أنه قال:
((لا يصلين أحدكم في الثوب الواحد ليس على عاتقه منه شيء)) .',NULL,'260','450',NULL,'0');
INSERT INTO [b26865] VALUES (436,'٤٥١-[٤٣٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك أسامة بن زيد الليثي، وعمرو بن الحارث، عن أبي الزبير، عن جابر بن عبد الله؛ أنه رأى رسول الله صلى الله عليه وسلم يصلي في ثوب واحد مخالفاً بين طرفيه على عاتقه، وثوبه على المشجب.',NULL,'261','451',NULL,'0');
INSERT INTO [b26865] VALUES (437,'٤٥٢-[٤٣١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، وابن ⦗٢٦٢⦘ أبي ذئب، وهشام بن سعد، وابن لهيعة، وغيرهم؛ أن محمد بن زيد القرشي حدثهم، عن أمه؛ أنها سألت أم سلمة زوج النبي صلى الله عليه وسلم:
ماذا تصلي فيه المرأة من الثياب؟ فقالت: في الخمار والدرع السابغ الذي يغيب ظهر قدميها.',NULL,'261','452',NULL,'0');
INSERT INTO [b26865] VALUES (438,'٤٥٣-[٤٣٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك الحارث بن نبهان، عن ⦗٢٦٣⦘ محمد بن عبيد الله، عن عطاء بن أبي رباح، عن جابر بن عبد الله قال:
صلينا ليلة في غيم وخفيت علينا القبلة، وعلمنا علماً.
فلما أصبحنا نظرنا، فإذا نحن قد صلينا إلى غير القبلة، فذكرنا ذلك لرسول الله صلى الله عليه وسلم فقال: ((قد أحسنتم)) . ولم يأمرنا أن نعيد.',NULL,'262','453',NULL,'0');
INSERT INTO [b26865] VALUES (439,'٤٥٤-[٤٣٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس عن زيد بن أسلم، عن عطاء بن يسار، وعن بسر بن سعيد وعبد الرحمن الأعرج، عن أبي هريرة؛ أن رسول الله صلى الله عليه وسلم قال:
((من أدرك من الصبح ركعة قبل أن تطلع الشمس فقد أدركها، ومن أدرك من العصر ركعة قبل أن تغرب الشمس فقد أدركها)) .',NULL,'263','454',NULL,'0');
INSERT INTO [b26865] VALUES (440,'٤٥٥-[٤٣٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، عن ابن شهاب، عن عروة بن الزبير، عن عائشة زوج النبي صلى الله عليه وسلم عن رسول الله صلى الله عليه وسلم مثله.',NULL,'263','455',NULL,'0');
INSERT INTO [b26865] VALUES (441,'٤٥٦-[٤٣٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرنا عبد الله بن عمر، ومالك بن أنس، وأسامة بن زيد الليثي، وابن سمعان، عن نافع؛ أن عبد الله بن عمر ⦗٢٦٤⦘ أغمي عليه، وذهب عقله، فلم يقض صلاته.',NULL,'263','456',NULL,'0');
INSERT INTO [b26865] VALUES (442,'٤٥٧-[٤٣٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك (¬١) مالك بن أنس، وحفص بن ميسرة، وداود بن قيس، وهشام بن سعد؛ أن زيد بن أسلم حدثهم، عن عطاء بن يسار؛ أن رسول الله صلى الله عليه وسلم قال:
((إذا شك أحدكم في الصلاة فلا يدري كم صلى، ثلاثاً أو أربعاً، فليقم فليصل ركعة، ثم يسجد سجدتين وهو جالس قبل السلام.
وإن كانت الركعة التي صلاها خامسة شفعها بهاتين السجدتين وإن كانت رابعة فالسجدتان ترغيم للشيطان)) .
إلا أن هشاماً بلغ به أبا سعيد الخدري.
_________
(¬١) [[من المخطوط وطبعة الشنقيطي، وفي المطبوع: أخبرنا]]',NULL,'264','457',NULL,'0');
INSERT INTO [b26865] VALUES (443,'٤٥٨-[٤٣٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك جرير بن حازم، عن سليمان الأعمش، عن إبراهيم، عن علقمة، عن عبد الله؛ أن رسول الله صلى الله عليه وسلم صلى خمس ركعات، ثم سجد سجدتين وهو جالس.',NULL,'265','458',NULL,'0');
INSERT INTO [b26865] VALUES (444,'٤٥٩-[٤٣٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، عن ⦗٢٦٦⦘ داود بن الحصين؛ أن أبا سفيان مولى أبي أحمد أخبره؛ أنه سمع أبا هريرة يقول:
صلى رسول الله صلى الله عليه وسلم صلاة العصر، فسلم في ركعتين.
فقال ذو اليدين: أقصرت الصلاة يا رسول الله، أم نسيت؟
فقال رسول الله صلى الله عليه وسلم: ((كل ذلك لم يكن)) . فقال: يا رسول الله، قد كان بعض ذلك.
فأقبل رسول الله صلى الله عليه وسلم على الناس فقال: ((أصدق ذو اليدين؟)) .
فقالوا: نعم.
فأتم رسول الله صلى الله عليه وسلم ما بقي عليه من الصلاة، ثم سلم وسجد سجدتين وهو جالس بعد السلام.',NULL,'265','459',NULL,'0');
INSERT INTO [b26865] VALUES (445,'٤٦٠-[٤٣٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك أشهل بن حاتم، عن ابن عون، عن ابن سيرين، عن عمران بن الحصين صاحب رسول الله صلى الله عليه وسلم مثله، إلا أنه قال: ثم سلم.',NULL,'266','460',NULL,'0');
INSERT INTO [b26865] VALUES (446,'٤٦١-[٤٤٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الرحمن بن سلمان، ⦗٢٦٧⦘ وغيره، عن ابن الهاد، عن عبد الرحمن بن عمار، عن القاسم بن محمد أن رسول الله صلى الله عليه وسلم حين كلمه ذو اليدين قام فكبر وصلى بالناس وسلم وسجد سجدتين.',NULL,'266','461',NULL,'0');
INSERT INTO [b26865] VALUES (447,'٤٦٢-[٤٤١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، والليث ابن سعد، وعمرو بن الحارث، ويونس بن يزيد، وابن سمعان؛ أن ابن شهاب أخبرهم، عن عبد الرحمن الأعرج؛ أن عبد الله بن بحينة حدثه؛ أن رسول الله صلى الله عليه وسلم قام في اثنتين من الظهر فلم يجلس، فلما قضى صلاته سجد سجدتين، فكبر في كل سجدة وهو جالس قبل السلام وسجدهما الناس معه، مكان ما نسي من الجلوس.',NULL,'267','462',NULL,'0');
INSERT INTO [b26865] VALUES (448,'٤٦٣-[٤٤٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، ومالك بن أنس، عن نافع، أن عبد الله بن عمر قال:
⦗٢٦٨⦘
من نسي صلاة من صلواته فلم يذكرها إلا وهو وراء الإمام، فإذا سلم الإمام فليصل الصلاة التي نسيها، ثم ليصل بعد الصلاة الأخرى.
٤٦٤- قال: وقال مالك بن أنس، والليث بن سعد، ويحيى بن عبد الله بن سالم، مثله.',NULL,'267','463',NULL,'0');
INSERT INTO [b26865] VALUES (449,'٤٦٥-[٤٤٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس، ⦗٢٦٩⦘ ويونس بن يزيد، عن ابن شهاب، عن ابن المسيب؛ أن رسول الله صلى الله عليه وسلم قال:
((من نسي صلاة فليصلها)) يعني: إذا ذكرها.',NULL,'268','465',NULL,'0');
INSERT INTO [b26865] VALUES (450,'٤٦٦-[٤٤٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك غير واحد، عن عقيل بن خالد، عن ابن شهاب، عن حميد بن عبد الرحمن؛ أن رسول الله صلى الله عليه وسلم صلى للناس يوماً الصبح، فقرأ {تبارك الذي نزل الفرقان على عبده} فأسقط آية.
فلما فرغ قال: ((أفي المسجد أبي بن كعب؟)) قال: نعم، ها أنا ذا يا رسول الله. [قال] : ((فما منعك أن تفتح علي حين أسقط؟)) .
قال: خشيت أنها نسخت.
قال: ((فإنها لم تنسخ)) .',NULL,'269','466',NULL,'0');
INSERT INTO [b26865] VALUES (451,'٤٦٧-[٤٤٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عثمان بن الحكم ⦗٢٧٠⦘ الجذامي، عن ابن جريج قال: أخبرني غير واحد من آل أبي محذورة؛ أن أبا ⦗٢٧١⦘ محذورة قال: قال رسول الله صلى الله عليه وسلم:
((اذهب فأذن عند المسجد الحرام)) .
قال: فقلت: كيف أؤذن فيه يا رسول الله.
قال: فعلمني الأولى: الله أكبر الله أكبر، أشهد أن لا إله إلا الله، أشهد أن لا إله إلا الله، أشهد أن محمداً رسول الله، أشهد أن محمداً رسول الله.
ثم قال: ((ارجع فامدد من صوتك: أشهد أن لا إله إلا الله، أشهد أن لا إله إلا الله، أشهد أن محمداً رسول الله، أشهد أن محمداً رسول الله، حي على الصلاة، حي على الصلاة، حي على الفلاح، حي على الفلاح، الصلاة خير من النوم -في الأولى من الصبح- الله أكبر، الله أكبر، لا إله إلا الله)) .',NULL,'269','467',NULL,'0');
INSERT INTO [b26865] VALUES (452,'٤٦٨- قال ابن جريج: وحدثني عطاء بن أبي رباح قال: ما علمت تأذين من مضى يخالف تأذينهم اليوم، ما علمت تأذين أبي محذورة يخالف تأذينهم اليوم، وكان أبو محذورة يؤذن على عهد النبي صلى الله عليه وسلم حتى أدركه عطاء وهو يؤذن.
٤٦٩- قال: وقال مالك بن أنس، والليث، مثله.',NULL,'271','468',NULL,'0');
INSERT INTO [b26865] VALUES (453,'٤٧٠-[٤٤٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن سعيد بن محمد الأنصاري، عن عيسى بن جارية، عن ابن المسيب، أنه قال:
أمر رسول الله صلى الله عليه وسلم بلالاً أن يؤذن، فجعل أصبعيه في أذنيه، ورسول الله صلى الله عليه وسلم ينظر إليه، فلم ينكر ذلك، فمضت السنة من يومئذٍ.',NULL,'272','470',NULL,'0');
INSERT INTO [b26865] VALUES (454,'٤٧١-[٤٤٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك أسامة بن زيد الليثي، عن عثمان بن عروة بن الزبير، عن أبيه، عن عائشة زوج النبي صلى الله عليه وسلم، عن رسول الله صلى الله عليه وسلم قال:
((إن الله وملائكته يصلون على الذين يصلون الصفوف)) .',NULL,'272','471',NULL,'0');
INSERT INTO [b26865] VALUES (455,'٤٧٢- (¬١) حدثنا بحر قال: قرئ على ابن وهب: أخبرك داود بن قيس المدني، عن علي بن يحيى بن خلاد الزرقي قال: حدثني أبي، عن عم له بدري، أنه كان مع رسول الله صلى الله عليه وسلم جالساً في المسجد.. .. ثم ذكر هذا، وقال له رسول الله صلى الله عليه وسلم:
((فإذا أتممت صلاتك على نحو هذا فقد أتممت صلاتك، وما نقصت من هذا فإنما تنقص من صلاتك)) .
_________
(¬١) [[هذا الحديث ليس في طبعة الشنقيطي]] .',NULL,'272','472',NULL,'0');
INSERT INTO [b26865] VALUES (456,'٤٧٣-[٤٤٨] حدثنا بحر قال: قرئ على ابن وهب: أخبرك يونس بن يزيد، عن ابن شهاب، أن عطاء بن يزيد الليثي أخبره أن أبا سعيد الخدري قال: سمعت رسول الله صلى الله عليه وسلم يقول:
((إذا سمعتم المؤذن فقولوا مثل ما يقول)) .',NULL,'273','473',NULL,'0');
INSERT INTO [b26865] VALUES (457,'٤٧٤-[٤٤٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرك ابن لهيعة، عن يزيد ابن أبي حبيب، مثله.',NULL,'273','474',NULL,'0');
INSERT INTO [b26865] VALUES (458,'٤٧٥-[٤٥٠] حدثنا بحر قال: قرئ على ابن وهب: أخبرك حيوة بن شريح، عن بكر بن عمرو، عن صفوان بن سليم؛ أن رسول الله صلى الله عليه وسلم قال لبني خطمة من الأنصار:
((يا بني خطمة، اجعلوا مؤذنكم أفضلكم في أنفسكم)) .',NULL,'273','475',NULL,'0');
INSERT INTO [b26865] VALUES (459,'٤٧٦-[٤٥١] حدثنا بحر قال: قرئ على ابن وهب: أخبرك رجال من أهل العلم منهم سفيان الثوري، ويحيى بن عبد الله بن سالم؛ أن عبد الرحمن بن حرملة أخبرهم، عن سعيد بن المسيب؛ أن رسول الله صلى الله عليه وسلم قال:
⦗٢٧٤⦘
((لا يخرج من المسجد بعد النداء إلا منافق، إلا أحد أخرجته حاجة، وهو يريد الرجعة إلى المسجد)) .',NULL,'273','476',NULL,'0');
INSERT INTO [b26865] VALUES (460,'٤٧٧-[٤٥٢] حدثنا بحر قال: قرئ على ابن وهب: أخبرك قرة بن عبد الرحمن المعافري، عن ابن شهاب، أخبرني عروة بن الزبير؛ أن الصلاة كانت تقام ورسول الله صلى الله عليه وسلم يناجي الرجل طويلاً قبل أن يكبر، فإنما جعل العود الذي في القبلة لكي يتوكأ عليه.',NULL,'274','477',NULL,'0');
INSERT INTO [b26865] VALUES (461,'٤٧٨-[٤٥٣] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، عن نافع، عن عبد الله بن عمر؛ أنه ليس على النساء أذان ولا إقامة.',NULL,'274','478',NULL,'0');
INSERT INTO [b26865] VALUES (462,'٤٧٩-[٤٥٤] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عبد الله بن عمر، ⦗٢٧٥⦘ وأسامة بن زيد، عن نافع؛ أن عبد الله بن عمر كان لا يؤذن في السفر بالأولى، ولكنه كان يقيم الصلاة، ويقول:
إن التثويب بالأولى في السفر مع الأمراء الذين معهم الناس ليجتمع الناس إلى الصلاة.',NULL,'274','479',NULL,'0');
INSERT INTO [b26865] VALUES (463,'٤٨٠- قال أسامة: قال نافع: قال ابن عمر:
إذا كانوا ركباناً، وإنما هي الإقامة.',NULL,'275','480',NULL,'0');
INSERT INTO [b26865] VALUES (464,'٤٨١- قال عبد الله وأسامة: قال نافع:
وكان ابن عمر إذا رأى الفجر أذن لصلاة الصبح بالنداء الأول، ويقول في أذانه: الصلاة خير من النوم.',NULL,'275','481',NULL,'0');
INSERT INTO [b26865] VALUES (465,'٤٨٢-[٤٥٥] حدثنا بحر قال: قرئ على ابن وهب: أخبرك مالك بن أنس عن نافع؛ ⦗٢٧٦⦘ أن ابن عمر كان لا يزيد على الإقامة في السفر في الصلاة إلا في الصبح، فإنه كان يؤذن فيها ويقيم، ويقول: إنما الأذان للإمام الذي يجتمع إليه الناس.',NULL,'275','482',NULL,'0');
INSERT INTO [b26865] VALUES (466,'٤٨٣-[٤٥٦] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمر بن محمد العمري؛ أنه رأى سالم بن عبد الله في السفر حين يرى الفجر ينادي بالصلاة على البعير، فإذا نزل أقام ولا ينادي على غيرها من الصلاة إلا الإقامة.
وقال: وكان عبد الله بن عمر يفعل ذلك.
قال: فكان ابن عمر لا يزيد إلا واحدة في الإقامة.
قال: وكان سالم بن عبد الله يفعل ذلك.',NULL,'276','483',NULL,'0');
INSERT INTO [b26865] VALUES (467,'٤٨٤-[٤٥٧] حدثنا بحر قال: قرئ على ابن وهب: أخبرك عمر بن محمد، ⦗٢٧٧⦘ وعبد الله بن عمر، عن نافع؛ أن عبد الله بن عمر نادى بالعشاء وهو بضجنان -وهو من مكة على بريدين- في ليلة باردة، ثم ينادي أن صلوا في رحالكم.
ثم أخبرهم ابن عمر أن رسول الله صلى الله عليه وسلم كان يأمر مناديه فينادي بالصلاة، ثم ينادي في إثرها أن صلوا في رحالكم، في الليلة الباردة والليلة المطيرة.',NULL,'276','484',NULL,'0');
INSERT INTO [b26865] VALUES (468,'<span data-type="title" id=toc-10>كتاب القسامة والعقول والديات</span>
٤٨٥-[٤٥٨] حدثنا أبو عبد الله بحر بن نصر، ثنا عبد الله بن وهب قال: سمعت ⦗٢٧٨⦘ معاوية بن صالح يقول: حدثني العلاء بن الحارث، عن مكحول، أنه قال:
تقسم النساء وغيرهم في دية الخطأ بقدر مواريثهم، وإن جاء واحد وسائرهم غياب يحلف خمسين يميناً، وأخذ حقه ومن جاء بعده يحلف على قدر ميراثه.',NULL,'277','485',NULL,'0');
INSERT INTO [b26865] VALUES (469,'٤٨٦-[٤٥٩] حدثنا بحر قال: قرئ على ابن وهب: أخبرني ابن أبي ذئب، عن ⦗٢٧٩⦘ ابن شهاب، عن ابن المسيب؛ أن رسول الله صلى الله عليه وسلم قال:
((لا يرث قاتل من دية من قتل)) .',NULL,'278','486',NULL,'0');
INSERT INTO [b26865] VALUES (470,'٤٨٧ -[٤٦٠] حدثنا بحر قال: قرئ على ابن وهب قال: أخبرني يزيد بن عياض، وهشام بن سعد، عن زيد بن أسلم؛ أن الناس في الجاهلية إذا قتل الرجل من القوم رجلاً لم يرضوا حتى يقتلوا به رجلاً شريفاً، إذا كان قاتلهم غير شريف لم يقتلوا قاتلهم، وقتلوا غيره فوعظوا من ذلك، يقول الله عز وجل: {ولا تقتلوا النفس التي حرم الله إلا بالحق} إلى {منصوراً} وقال زيد بن أسلم: (السرف أن يقتل غير قاتله) .',NULL,'279','487',NULL,'0');
INSERT INTO [b26865] VALUES (471,'٤٨٨-[٤٦١] حدثنا بحر: ثنا عبد الله بن وهب قال: حدثني جرير بن حازم؛ أن ⦗٢٨٠⦘ المغيرة بن حكيم الصنعاني حدثه، عن أبيه، أن امرأة بصنعاء غاب عنها زوجها، وترك في حجرها ابناً له من غيرها، غلام يقال له: أصيل، فاتخذت المرأة بعد زوجها خليلاً، فقالت لخليلها: إن هذا الغلام يفضحنا فاقتله. فأبى فامتنعت ⦗٢٨١⦘ منه، فطاوعها واجتمع على قتله الرجل ورجل آخر والمرأة وخادمها، فقتلوه ثم قطعوه أعضاء، وجعلوه في عيبة من أدم، فطرحوه في ركية في ناحية القرية، وليس فيها ماء، ثم صاحت المرأة، فاجتمع الناس فخرجوا يطلبون الغلام.
قال: فمر رجل بالركية التي فيها الغلام فخرج منها الذباب الأخضر.
فقلنا: والله إن في هذه لجيفة، ومعنا خليلها، فأخذته رعدة، فذهبنا به فحبسناه، وأرسلنا رجلاً فأخرج الغلام، فأخذنا الرجل فاعترف، فأخبرنا الخبر، فاعترفت المرأة والرجل الآخر وخادمها.
فكتب يعلى -وهو يومئذٍ أمير- بشأنهم.
فكتب إليه عمر بقتلهم جميعاً وقال: والله لو أن أهل صنعاء شركوا في قتله لقتلتهم أجمعين.',NULL,'279','488',NULL,'0');
INSERT INTO [b26865] VALUES (472,'٤٨٩-[٤٦٢] حدثنا بحر: ثنا ابن وهب قال: وحدثني موسى بن علي؛ أن ابن شهاب حدثه؛ أن رسول الله صلى الله عليه وسلم قضى بالدية بين كل وارث.',NULL,'281','489',NULL,'0');
INSERT INTO [b26865] VALUES (473,'٤٩٠-[٤٦٣] حدثنا بحر ثنا ابن وهب قال: حدثني يونس، عن ابن شهاب قال: أتى رسول الله صلى الله عليه وسلم برجل قد قتل رجلاً، فقال له:
خذ الدية. فأبى الرجل، فأمكنه قاتله، فلما مر بالرجل ليقتل قال رسول الله صلى الله عليه وسلم: ((قاتله في النار)) .
⦗٢٨٣⦘
وذهب الناس إلى الذي يريد أن يقتل، وحدثه بما قال النبي صلى الله عليه وسلم، فرجع إلى النبي صلى الله عليه وسلم فذكر له الذي بلغه.
فقال له النبي صلى الله عليه وسلم: ((عمد يد أو خطأ؟)) قلت فأخذ الدية.',NULL,'282','490',NULL,'0');
INSERT INTO [b26865] VALUES (474,'٤٩١-[٤٦٤] حدثنا بحر، ثنا ابن وهب قال: أخبرني مخرمة بن بكير، عن أبيه قال: سمعت عمرة بنت عبد الرحمن تقول: سمعت عائشة زوج النبي صلى الله عليه وسلم تقول:
إن كسر عظم الميت، مثل كسر عظم الحي.',NULL,'283','491',NULL,'0');
INSERT INTO [b26865] VALUES (475,'٤٩٢-[٤٦٥] حدثنا بحر، ثنا ابن وهب قال: أخبرني يونس، عن ابن شهاب قال:
قال الله عز وجل: {يا أيها الذين آمنوا كتب عليكم القصاص في القتلى} الآية كلها.
⦗٢٨٤⦘
ثم قال: {وكتبنا عليهم فيها أن النفس بالنفس} الآية كلها.
قال ابن شهاب: فلما نزلت هذه الآية أقيدت المرأة من الرجل، وفيما يعمد من الجراح.',NULL,'283','492',NULL,'0');
INSERT INTO [b26865] VALUES (476,'٤٩٣-[٤٦٦] حدثنا بحر، ثنا ابن وهب قال: أخبرني يونس، عن أبي الزناد قال:
أما العمد فإنا لا نعلم إلا أن المرأة أن تقاد من الرجل كل شيء أصابه منها: عين بعين، وسن بسن، وأنف بأنف، وأذن بأذن، ويد بيد، ونفس بنفس.',NULL,'284','493',NULL,'0');
INSERT INTO [b26865] VALUES (477,'٤٩٤-[٤٦٧] حدثنا بحر، ثنا ابن وهب قال: أخبرني ابن لهيعة، عن يزيد بن أبي ⦗٢٨٥⦘ حبيب، عن ابن شهاب؛ أن رجلاً من المسلمين ارتد فكفر، ولحق بالمشركين، فلما كان يوم الفتح استجار بعثمان، ونزع من الكفر إلى الإسلام، فأتى به عثمان إلى رسول الله صلى الله عليه وسلم فاستجار له فأجاره رسول الله صلى الله عليه وسلم فقبل منه.',NULL,'284','494',NULL,'0');
INSERT INTO [b26865] VALUES (478,'٤٩٥-[٤٦٨] حدثنا بحر، ثنا ابن وهب قال: أخبرني ابن لهيعة، عن أبي الأسود، عن عروة بن الزبير؛ أن أبا بكر الصديق أمر خالد بن الوليد حين بعثه إلى من ارتد من العرب أن يدعوهم بدعاء الإسلام، وينبئهم بالذي لهم فيه وعليهم، ويحرص على هداهم، فمن أجابه من الناس كلهم أحمرهم وأسودهم، كان يقبل ذلك منه بأنه إنما يقاتل من كفر بالله على الإيمان بالله، فإذا أجاب المُدْعَوْن إلى الإسلام، وصدق إيمانه، لم يكن عليه سبيل، وكان الله عز وجل هو حسيبه، ومن لم يجبه إلى ما دعاه إليه من الإسلام ممن يرجع عنه أن يقتله.',NULL,'285','495',NULL,'0');
INSERT INTO [b26865] VALUES (479,'٤٩٦-[٤٦٩] حدثنا بحر، ثنا ابن وهب قال: أخبرني ابن لهيعة، عن يزيد بن أبي حبيب؛ أن أبا علي الهمداني حدثهم؛ أنهم كانوا مع فضالة بن عبيد صاحب النبي صلى الله عليه وسلم في البحر فأتى البحر فأتي برجل من المسلمين قد فر إلى العدو، فأقاله الإسلام فأسلم، [ثم فر الثانية فأتى به فأقاله الإسلام فأسلم] ، ثم فر الثالثة فأتى به فنزع بهذه الآية: {إن الذين آمنوا ثم كفروا ثم آمنوا ثم كفروا ثم ازدادوا كفراً لم يكن الله يغفر لهم ولا ليهديهم سبيلاً} فضرب عنقه.',NULL,'285','496',NULL,'0');
INSERT INTO [b26865] VALUES (480,'٤٩٧-[٤٧٠] حدثنا بحر، ثنا ابن وهب قال: أخبرني يونس، عن ابن شهاب، عن عبيد الله بن عبد الله بن عتبة، أن عبد الله بن مسعود أخذ بالكوفة رجالاً ينعشون حديث مسيلمة الكذاب، يدعون إليه، فكتب فيهم إلى عثمان بن عفان.
فكتب عثمان: أن أعرض عليهم دين الحق وشهادة أن لا إله إلا الله، وأن محمداً رسول الله، فمن قبلها وبرئ من مسيلمة فلا تقتله، ومن لزم دين مسيلمة فاقتله، فقبلها رجال منهم فتركوا، ولزم دين مسيلمة رجال فقتلوا.',NULL,'286','497',NULL,'0');
INSERT INTO [b26865] VALUES (481,'٤٩٨- حدثنا بحر، ثنا ابن وهب قال: حدثني عبد الله بن عمر، عن نافع، عن عبد الله بن عمر؛ أن جارية لحفصة زوج النبي صلى الله عليه وسلم سحرتها، فأمرت بها فقتلت.',NULL,'286','498',NULL,'0');
INSERT INTO [b26865] VALUES (482,'٤٩٩-[٤٧١] حدثنا بحر، ثنا ابن وهب قال: حدثني مالك، عن أبي الرجال محمد ابن عبد الرحمن؛ أنه بلغه أن جارية كانت لحفصة زوج النبي صلى الله عليه وسلم مدبرة، فسحرتها فأمرت بها حفصة فقتلت.',NULL,'286','499',NULL,'0');
INSERT INTO [b26865] VALUES (483,'٥٠٠-[٤٧٢] حدثنا بحر قال: ثنا ابن وهب قال: حدثني مالك -يعني ابن أنس- وأسامة بن زيد الليثي، وسفيان الثوري؛ عن ربيعة أنه سأل سعيد بن المسيب:
⦗٢٨٧⦘
كم في أصبع المرأة؟ قال: عشرة.
قال: كم في اثنين؟ قال: عشرون.
قال: كم في ثلاث؟ قال: ثلاثون.
قال: كم في أربع؟ قال: عشرون.
قال ربيعة: حين عظم جرحها، واشتدت مصيبتها، نقص عقلها؟
قال: أعراقي أنت؟
قال ربيعة: عالم متثبت، أو جاهل متعلم.
قال: يا ابن أخي، إنها السنة.',NULL,'286','500',NULL,'0');
INSERT INTO [b26865] VALUES (484,'٥٠١-[٤٧٣] حدثنا بحر، ثنا ابن وهب قال: أخبرني يونس، عن ابن شهاب، عن سعيد بن المسيب، وأبي سلمة بن عبد الرحمن؛ أن أبا هريرة قال:
اقتتلت امرأتان من هذيل، فرمت إحداهما الأخرى بحجر فقتلتها وما في بطنها.
فاختصموا إلى رسول الله صلى الله عليه وسلم، فقضى رسول الله صلى الله عليه وسلم أن دية جنينها غرة، عبدٍ أو وليدةٍ، وقضى بدية المرأة على عاقلتها وورثها وولدها ومن معهم.
فقال حمل بن النابغة الهذلي: يا رسول الله، كيف أغرم من لا شرب ولا أكل ولا نطق ولا استهل، فمثل ذلك يطل؟
فقال رسول الله صلى الله عليه وسلم: ((إنما هذا من إخوان الكهان)) من أجل سجعه.',NULL,'287','501',NULL,'0');
INSERT INTO [b26865] VALUES (485,'٥٠٢-[٤٧٤] حدثنا بحر، ثنا ابن وهب قال: وأخبرني الليث؛ أن ابن شهاب حدثه، عن سعيد بن المسيب، عن أبي هريرة؛ أنه قال:
⦗٢٨٨⦘
قضى رسول الله صلى الله عليه وسلم في جنين امرأة من بني لحيان سقط ميتاً بغرة؛ عبدٍ، أو وليدة، ثم إن المرأة التي قضى عليها بالغرة توفيت فقضى رسول الله صلى الله عليه وسلم أن ميراثها لابنها وزوجها، وأن العقل على عصبتها.',NULL,'287','502',NULL,'0');
INSERT INTO [b26865] VALUES (486,'٥٠٣-[٤٧٥] حدثنا بحر، ثنا ابن وهب قال: وحدثني مالك، عن ابن شهاب، عن ⦗٢٨٩⦘ أبي سلمة، عن أبي هريرة؛ أن امرأتين من هذيل في زمان رسول الله صلى الله عليه وسلم رمت إحداهما الأخرى فطرحت جنينها، فقضى فيه رسول الله صلى الله عليه وسلم بغرة: عبدٍ أو وليدةٍ.',NULL,'288','503',NULL,'0');
INSERT INTO [b26865] VALUES (487,'٥٠٤-[٤٧٦] حدثنا بحر، ثنا ابن وهب قال: وأخبرني ابن لهيعة، عن أبي الأسود، عن عروة بن الزبير؛ أنه قال: سأل عمر بن الخطاب عن إملاص المرأة؟ فقال: له المغيرة بن شعبة:
قضى فيه رسول الله صلى الله عليه وسلم بغرة: عبدٍ، أو أمةٍ، أو فرس. فقال عمر: ائتني بمن يعلم ذلك. فأتى محمد بن مسلمة فشهد أن رسول الله صلى الله عليه وسلم قضى بذلك.',NULL,'290','504',NULL,'0');
INSERT INTO [b26865] VALUES (488,'٥٠٥-[٤٧٧] حدثنا بحر، ثنا ابن وهب قال: أخبرني يزيد بن عياض، عن عبد الملك ابن عبيد، عن مجاهد بن جبر، عن ابن عباس؛ أنه كان يقول:
العبد لا يغرم سيده فوق نفسه شيئاً، وإن كان المجروح أكثر من ثمن العبد فلا يزاد له.',NULL,'290','505',NULL,'0');
INSERT INTO [b26865] VALUES (489,'٥٠٦-[٤٧٨] حدثنا بحر، ثنا ابن وهب قال: وأخبرني مخرمة، عن أبيه قال: سمعت سعيد بن عبد الله بن جابر يقول: سمعت سعيد بن المسيب يقول:
⦗٢٩١⦘
إذا شج العبد موضحة، فله فيها نصف عشر ثمنه.
وقال ذلك سليمان بن يسار.',NULL,'290','506',NULL,'0');
INSERT INTO [b26865] VALUES (490,'٥٠٧-[٤٧٩] حدثنا بحر، ثنا ابن وهب قال: حدثني جرير بن حازم الأزدي، عن مجالد، عن عامر الشعبي، عن سويد بن غفلة قال: كنا مع عمر بن الخطاب، وهو أمير المؤمنين بالشام، فأتاه نبطي مضروب مشجج مستعدى.
فغضب غضباً شديداً، فقال لصهيب: انظر من صاحب هذا؟
فانطلق صهيب، فإذا هو عوف بن مالك الأشجعي. فقال له: إن أمير المؤمنين قد غضب غضباً شديداً، فلو أتيت معاذ بن جبل، فمشى معك إلى أمير المؤمنين، فإني أخاف عليكم بادرته، فجاء معه معاذ.
فلما انصرف عمر من الصلاة قال: أين صهيب؟
فقال: أنا هذا يا أمير المؤمنين.
قال: أجئت بالرجل الذي ضربه؟
قال: نعم.
فقام معاذ بن جبل فقال له: يا أمير المؤمنين، إنه عوف بن مالك فاسمع منه ولا ⦗٢٩٢⦘ تعجل عليه.
فقال له عمر: مالك ولهذا؟
قال: يا أمير المؤمنين، رأيته يسوق بامرأة مسلمة، فنخس الحمار ليصرعها، فلم تصرع، ثم دفعها فخرت عن الحمار ثم تغشاها، ففعلت ما ترى.
قال: ائتني بالمرأة لتصدقك. فأتى عوف المرأة فذكر الذي قال له عمر.
قال أبوها وزوجها: ما أردت بصاحبتنا؟ فضحتها.
فقالت المرأة: والله لأذهبن معه إلى أمير المؤمنين؛ فلما أجمعت على ذلك، قال أبوها وزوجها: نحن نبلغ عنك أمير المؤمنين، فأتيا فصدقا عوف بن مالك بما قال.
قال: فقال عمر لليهودي: والله ما على هذا عاهدناكم فأمر به فصلب، ثم قال: يا أيها الناس، فوا بذمة محمد صلى الله عليه وسلم، فمن فعل منهم هذا، فلا ذمة له.
قال سويد بن غفلة: وإنه لأول مصلوب رأيته.',NULL,'291','507',NULL,'0');
INSERT INTO [b26865] VALUES (491,'٥٠٨-[٤٨٠] حدثنا بحر، ثنا ابن وهب قال: أخبرني أسامة بن زيد الليثي، عن عمرو بن شعيب، عن أبيه، عن جده؛ أن رسول الله صلى الله عليه وسلم قال:
⦗٢٩٣⦘
((لا يقتل مؤمن بكافر)) .',NULL,'292','508',NULL,'0');
INSERT INTO [b26865] VALUES (492,'٥٠٩-[٤٨١] حدثنا بحر، ثنا ابن وهب قال: أخبرني عمرو بن الحارث، عن بكر بن سوادة الجذامي؛ أن رسول الله صلى الله عليه وسلم قال:
⦗٢٩٤⦘
((لا يقتل مؤمن بكافر، ولا ذو عهد في عهده)) .',NULL,'293','509',NULL,'0');
INSERT INTO [b26865] VALUES (493,'٥١٠-[٤٨٢] حدثنا بحر، ثنا ابن وهب: وأخبرني يزيد بن عياض، عن عبد الملك ابن عبيد، عن خربنق بنت الحصين، عن أخيها عمران بن الحصين قال: قال رسول الله صلى الله عليه وسلم يوم الفتح:
⦗٢٩٥⦘
((ألم تر إلى ما صنع صاحبكم، هلال بن أمية، لو قتلت مؤمناً بكافر لقتلته، فدوه)) . فوديناه، وبنو مدلج معنا، فجاءوا بغنم عفر لم أر أحسن منها ألواناً، وكانت بنو مدلج حلفاء بني كعب في الجاهلية.',NULL,'294','510',NULL,'0');
INSERT INTO [b26865] VALUES (494,'٥١١-[٤٨٣] حدثنا بحر، ثنا ابن وهب قال: ثنا أسامة بن زيد، عن عمرو بن شعيب، عن أبيه، عن جده؛ أن رسول الله صلى الله عليه وسلم قال:
((عقل الكافر نصف عقل المؤمن)) .',NULL,'295','511',NULL,'0');
INSERT INTO [b26865] VALUES (495,'٥١٢-[٤٨٤] حدثنا بحر، ثنا ابن وهب قال: أخبرني جرير بن حازم؛ أنه سمع الحسن يقول:
⦗٢٩٦⦘
بعث رسول الله صلى الله عليه وسلم سرية فأتاهم رجل فقال: السلام عليكم. فقام إليه رجل ليقتله فقال: إني مؤمن. فقال: كذبت بل أنت متعوذ فقتله، فأنزل الله تعالى: {ولا تقولوا لمن ألقى إليكم السلام لست مؤمناً تبتغون عرض الحياة الدنيا} .',NULL,'295','512',NULL,'0');
INSERT INTO [b26865] VALUES (496,'٥١٣-[٤٨٥] حدثنا بحر، ثنا ابن وهب قال: أخبرني عمرو بن الحارث؛ أن بكر بن سوادة الجذامى حدثه؛ أن زياد بن نعيم الحضرمي حدثه؛ أن وفد كندة قدموا على رسول الله صلى الله عليه وسلم، وفيهم جمدٌ.
فبينا هو عند رسول الله صلى الله عليه وسلم أقبل رجل فقال:
ظلمت يا رسول الله.
فقال رسول الله صلى الله عليه وسلم: ((أفلح المظلومون)) .
⦗٢٩٧⦘
فقالوا: ما رأينا كذا قط إن منا من رجل يحب أن يظلمه أحد، وهذا يقول: أفلح المظلومون، فخرجوا مغضبين.
وقالوا: فأخذت جمداً اللقوة، فأتوا رسول الله صلى الله عليه وسلم فقالوا: سيد الناس يا رسول الله، ادع الله له.
فقال: ((لم أكن لأفعل، ولكن خذوا فسله فاقلبوا مآقي عينيه أو في شفره فاكووه بها، فإنها شفاؤه وهي مصيره، الله أعلم بما قلتم حين أدبرتم)) .
قالوا: أرأيت أكلتنا في الجاهلية يا رسول الله؟ قال: ((هي لكم حتى ينزعها الله منكم)) .
⦗٢٩٨⦘
قالوا: أقد أتيتنا قال: ((ليأتين عليكم زمان ترضون بالكفاف)) .
قالوا: فتحيتنا، قال: ((جاء الله بخير من ذلك، السلام)) .
ثم ارتد جمدٌ، فقتل كافراً بعد وفاة رسول الله صلى الله عليه وسلم.
قال عمرو: وحدثني كعب ابن علقمة أنهم قالوا: أتينا هذا الغلام المضري فما سألنا شيئاً إلا أعطاناه حتى لو أردنا أن نأخذ بأذنه لفعلنا.
وإن رسول الله صلى الله عليه وسلم كان يقول: ((لعن الله جمداً)) وأبضعة وأخته العمردة)) .',NULL,'296','513',NULL,'0');
INSERT INTO [b26865] VALUES (497,'٥١٤-[٤٨٦] حدثنا بحر، ثنا ابن وهب قال: وأخبرني يونس، عن ابن شهاب قال: قرأت كتاب رسول الله صلى الله عليه وسلم الذي كتبه لعمرو بن حزم حين بعثه على نجران.
⦗٢٩٩⦘
وكان الكتاب عند أبي بكر بن حزم، فكتب رسول الله صلى الله عليه وسلم: ((هذا بيان من الله عز وجل ورسوله: {يا أيها الذين آمنوا أوفوا بالعقود} )) فكتب الآيات منها حتى بلغ: {إن الله سريع الحساب} .
ثم كتب: ((هذا كتاب الجراح؛ في النفس مائة من الإبل، وفي الأنف إذا أوعي جدعُهُ مائة من الإبل، وفي العين خمسون من الإبل، وفي الأذن خمسون من الإبل، وفي اليد خمسون من الإبل، وفي الرجل خمسون من الإبل، وفي كل أصبع مما هنالك عشر من الإبل، وفي المأمومة ثلث النفس وفي الجائفة ثلث النفس، وفي المنقلة خمس عشرة، وفي الموضحة خمس من الإبل، وفي السن خمس من الإبل)) .
قال ابن شهاب: فهذا الذي قرأت في الكتاب الذي كتبه رسول الله صلى الله عليه وسلم عند أبي بكر بن حزم.',NULL,'298','514',NULL,'0');
INSERT INTO [b26865] VALUES (498,'٥١٥-[٤٨٧] حدثنا بحر، ثنا ابن وهب قال: حدثني مالك بن أنس؛ أن عبد الله بن ⦗٣٠٠⦘ أبي بكر أخبره؛ أن أباه أخبره عن الكتاب الذي كتبه رسول الله صلى الله عليه وسلم لعمرو بن حزم في العقول مثل حديث ابن شهاب.
إلا أنه لم يذكر الأذنين ولا المنقلة.',NULL,'299','515',NULL,'0');
INSERT INTO [b26865] VALUES (499,'٥١٦-[٤٨٨] حدثنا بحر، ثنا ابن وهب قال: أخبرني يونس بن يزيد، عن ابن شهاب، أن سعيد بن المسيب أخبره؛ أن السنة مضت في العقل بأن في الصلب الدية، وفي الذكر الدية، وفي الأنثيين الدية، وفي اللسان الدية، وفي الشفتين الدية، وإن قطعت الشفة العليا ففيها نصف الدية، وإن قطعت الشفة السفلى ففيها ثلث الدية.
٥١٧- وعن سعيد بن المسيب أنه قال: في السمع إذا ذهب الدية تامة.',NULL,'300','516',NULL,'0');
INSERT INTO [b26865] VALUES (500,'٥١٨-[٤٨٩] حدثنا بحر، ثنا ابن وهب قال: حدثني عياض بن عبد الله الفهري؛ أنه سمع زيد بن أسلم يقول:
مضت السنة أشياء من الإنسان: في نفسه الدية، وفي أنفه الدية، وفي اللسان الدية، وفي العينين الدية، وفي الأذنين الدية، وفي الذكر الدية، وفي الأنثيين الدية، وفي الصوت إذا انقطع الدية، وفي العقل إذا ذهب الدية، وفي الشفتين الدية، ⦗٣٠١⦘ وفي الأصابع الدية، وفي الأسنان الدية.',NULL,'300','518',NULL,'0');
INSERT INTO [b26865] VALUES (501,'٥١٩-[٤٩٠] حدثنا بحر، ثنا ابن وهب قال: أخبرني ابن لهيعة قال: أخبرني ابن جريج، عن عطاء بن أبي رباح؛ إن صفوان بن يعلى بن أمية حدثه، عن يعلى ابن أمية قال:
غزوت مع رسول الله صلى الله عليه وسلم غزوة العسرة، وكانت أوثق أعمالي في نفسي، فكان لي أجير، فقاتل إنساناً، فعض أحدهما صاحبه، فانتزع أصبعه، فسقطت ثنيته، فجاء إلى النبي صلى الله عليه وسلم فأهدر ثنيته.
فقال عطاء: فحسبت أن صفوان بن يعلى قال: قال رسول الله صلى الله عليه وسلم: ((أيدع يده في فيك، فتقضمها كقضم الفحل)) .',NULL,'301','519',NULL,'0');
INSERT INTO [b26865] VALUES (502,'٥٢٠-[٤٩١] حدثنا بحر، ثنا ابن وهب قال: أخبرني جرير بن حازم، عن أيوب السختياني، عن محمد بن سيرين؛ أن رجلاً عض يد رجل فانتزع يده من فيه، فسقطت ثنيتاه، فاستعداه النبي صلى الله عليه وسلم، فقال له رسول الله صلى الله عليه وسلم:
((أفتريد أن يدعك فتقضم يده، كما يقضم الجمل، إن شئت وضعت يدك في فيه فقضمها ثم انتزعتها)) .',NULL,'302','520',NULL,'0');
INSERT INTO [b26865] VALUES (503,'٥٢١-[٤٩٢] حدثنا بحر، ثنا ابن وهب قال: أخبرني ابن لهيعة، عن يزيد بن أبي حبيب، عن ابن شهاب، عن سعيد بن المسيب، أنه قال: أرسل مروان إلى عبد الله بن عباس وأنا عنده.
فسأله الرسول: ماذا ترى في الأصابع؟
فقال ابن عباس: قضى رسول الله صلى الله عليه وسلم في اليد مجتمعين فريضة، وفي الرجل مثل ذلك، وفي الأصابع بعشر عشر.
فرجع الرسول إلى مروان فأخبره، فرده مروان إليه فقال: أخبره أن عمر بن الخطاب قد قضى بالذي سمعته، وقل له: تجعل الإبهام مثل الخنصر؟
فأتاه فأخبره. فقال ابن عباس: يرحم الله عمر، فقد كان مجتهداً موفقاً، ⦗٣٠٣⦘ ولكن قد أخبرته بقول رسول الله صلى الله عليه وسلم، وقل لمروان: أتجعل مثل ضرسك؟ فإنهما عقلهما واحد؟
فقال مران: ما أدري أقوم لهذا أم أقعد.
٥٢٢- قال يزيد: وأخبرني موسى بن سعد، عن زيد بن ثابت، عن أبي غطفان، مثل حديث ابن المسيب.',NULL,'302','521',NULL,'0');
INSERT INTO [b26865] VALUES (504,'٥٢٣-[٤٩٣] حدثنا بحر، ثنا ابن وهب قال: وأخبرني عبد الجبار بن عمر، عن ابن شهاب. وربيعة، وأبي الزناد، وإسحاق بن عبد الله؛ أن رسول الله صلى الله عليه وسلم لم يعقل ما دون الموضحة، وجعل ما دون الموضحة عفواً بين المسلمين.',NULL,'303','523',NULL,'0');
INSERT INTO [b26865] VALUES (505,'٥٢٤-[٤٩٤] حدثنا بحر، ثنا ابن وهب قال: حدثني مالك، عن أبي النضر، ⦗٣٠٤⦘ وغيره أخبروه؛ أن رسول الله صلى الله عليه وسلم رأى رجلاً متخلقاً، فطعنه بقدح كان في يده، ثم قال: ((ألم أنهكم عن مثل هذا؟)) .
فقال الرجل: يا رسول الله، إن الله قد بعثك بالحق، وإنك قد عقرتني.
فألقى إليه رسول الله صلى الله عليه وسلم القدح، وقال له: ((استقد)) .
فقال الرجل: إنك طعنتني وليس علي ثوب، وعليك قميص.
قال: فكشف له رسول الله صلى الله عليه وسلم عن بطنه، فأكب عليه الرجل فقبله.',NULL,'303','524',NULL,'0');
INSERT INTO [b26865] VALUES (506,'٥٢٥-[٤٩٥] حدثنا بحر، ثنا ابن وهب قال: أخبرني عمرو بن الحارث، عن بكير ⦗٣٠٥⦘ ابن الأشج، عن عبيدة بن مسافع، عن أبي سعيد الخدري قال:
بينا رسول الله صلى الله عليه وسلم يقسم شيئاً أقبل رجل فأكب عليه، فطعنه رسول الله صلى الله عليه وسلم بعرجون كان معه، فجرح الرجل، فقال له رسول الله صلى الله عليه وسلم: ((تعال فاستقد)) .
فقال: بل عفوت يا رسول الله.',NULL,'304','525',NULL,'0');
INSERT INTO [b26865] VALUES (507,'٥٢٦-[٤٩٦] حدثنا بحر، ثنا ابن وهب قال: وسمعت حيي بن عبد الله المعافري يقول: حدثني أبو عبد الرحمن الحبلي، عن عبد الله بن عمرو بن العاص؛ أن أبا بكر الصديق قام يوم الجمعة، فقال: إذا كان بالغداة فأحضروا صدقات الإبل تقسم، ولا يدخل عليها أحد إلا بإذن.
فقالت امرأة لزوجها: خذ هذا الخطام لعل الله يرزقنا جملاً، فأتى الرجل فوجد أبا بكر وعمر قد دخلوا إلى الإبل، فدخل معهما، فالتفت أبو بكر فقال: ما أدخلك علينا؟ ثم أخذ منه الخطام فضربه، فلما فرغ أبو بكر من قسم الإبل، دعا بالرجل فأعطاه الخطام وقال: استقد.
فقال له عمر: والله لا يستقيد، لا تجعلها سنة.
قال أبو بكر: فمن لي من الله يوم القيامة؟ فقال عمر: أرضه. فأمر أبو بكر غلامه أن يأتيه براحلة ورحلها وقطيفة وخمسة من الدنانير فأرضاه بها.',NULL,'305','526',NULL,'0');
INSERT INTO [b26865] VALUES (508,'٥٢٧-[٤٩٧] حدثنا بحر، ثنا ابن وهب قال: وأخبرني إبراهيم بن سعد، عن أبيه سعد بن إبراهيم بن عبد الرحمن بن عوف؛ أن رسول الله صلى الله عليه وسلم أقاد من نفسه.',NULL,'305','527',NULL,'0');
INSERT INTO [b26865] VALUES (509,'٥٢٨ - [٤٩٨] حدثنا بحر، ثنا ابن وهب قال: وحدثني عبد الله بن عمر، عن أبي النضر؛ أن رجلاً قام إلى عمر بن الخطاب -وهو على المنبر- فقال: يا أمير المؤمنين، ظلمني عاملك، وضربني.
فقال عمر: والله لأقيدنك منه إذاً.
فقال عمرو بن العاص، يا أمير المؤمنين، تقيد من عاملك؟
قال: نعم، والله لأقيدن، أقاد رسول الله صلى الله عليه وسلم من نفسه، وأقاد أبو بكر من نفسه أفلا أقيد؟
فقال عمرو بن العاص: أو غير ذلك يا أمير المؤمنين!
قال: وما هو؟
قال: أو يرضيه.
قال: أو ذلك.
كتاب القراض.
سمع من أول الكتاب إلى هنا من الشيخ الإمام .. .. أبي منصور ابن الشيخ المجدد .. .. .. الحسين بن محمد العلوي الطبري الشيخ الإمام أبو سعيد ابن .. .. .. الأخ أحمد بن أبي بكر الطرطوسي .. .. .. سماعه سنة ثلاث عشرة.',NULL,'306','528',NULL,'0');
