INSERT INTO [b26871] VALUES (199,'<span data-type="title" id=toc-51>شُعْبَةُ = أَبُو شُعَيْبٍ</span>
INSERT INTO [b26871] VALUES (200,'<span data-type="title" id=toc-52>سُفْيَانُ = أَبُو الْهُذَيْلِ</span>
