INSERT INTO [b26872] VALUES (1,'المحبة لله سبحانه
INSERT INTO [b26872] VALUES (2,'بسم الله الرحمن الرحيم، رب يسر
INSERT INTO [b26872] VALUES (3,'٢ - حدثنا محمد بن حميد الرازي حدثنا سلمة بن الفضل عن محمد بن إسحاق قال: خطب رسول الله صلى الله عليه وسلم فقال: ((أحبوا من ⦗١٨⦘ أحب الله، أحبوا الله من كل قلوبكم، ولا تملوا كلام الله عز وجل، ولا تقسون عليه قلوبكم)).',NULL,'17',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (4,'٣ - حدثني زياد بن أيوب دلويه الطوسي حدثنا أحمد بن أبي الحواري حدثني عبد العزيز بن عمير قال: سمعت أبا سليمان الواسطي يقول: ذكر النعم يورث المحبة.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (5,'٤ - حدثني محمد بن الحسين حدثنا زكريا بن عدي قال: سمعت عابداً باليمن يقول: حب الله تعالى يضني الأبدان، ويورث الطاعة.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (6,'٥ - قال إبراهيم: كان يقال: من علامة المحب لله عز وجل دوام الذكر بالقلب واللسان، وقل ما ولع المرء بذكر الله تعالى إلا أفاد منه حب الله عز وجل.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (7,'٦ - ويقال: تجدد ذكر الموت يحلق من القلب ما سواه، ويحيي القلوب الميتة.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (8,'٧ - حدثني محمد بن الحسين حدثني محمد بن معاوية الأزرق حدثني سعيد العلاف الموصلي قال: قال فتح الموصلي: إيثار محبة الله على محبتك من علامة حبك لله عز وجل.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (9,'٨ - وقال فتح: المحب لله عز وجل لا يجد مع حب الله عز وجل للدنيا لذة، ولا يغفل عن ذكر الله طرفة.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (10,'٩ - حدثنا حرملة بن يحيى الكندي أننا عبد الله بن وهب أننا ابن لهيعة أننا عبد الرحمن بن زياد بن أنعم عن عتبة بن حميد الضبي عن عبادة بن نسي أخبرني أبو موسى الكندي عن أبي هريرة: أن رسول الله صلى الله عليه وسلم قال: ((الإيمان في قلب الرجل أن يحب الله عز وجل)).',NULL,'19',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (11,'١٠ - حدثني محفوظ بن الفضل ثنا غوث بن جابر حدثني عقيل أبو معقل عن وهب بن منبه قال: إن المؤمن الخالص إيمانه لا يحب كحبه الله عز وجل أحداً، ولا يخشى كخشية الله عز وجل أحداً، فإنه يعلم علماً يقيناً أن الخلق كله لله عز وجل وبيد الله، وكان الله تعالى أهل ذلك منهم في القدرة عليهم والنعمة والبلاء الحسنين عندهم.',NULL,'19',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (12,'١١ - حدثنا يحيى بن عبد الله بن بكير حدثني عبد الله بن لهيعة حدثني يزيد بن أبي حبيب عن ابن شهاب: أن النبي صلى الله عليه وسلم قال: ((رأس الإيمان المحبة لله عز وجل وفي الله. وسنام الإيمان البر والعدل. وتحقيق الإيمان إكرام ذي الدين وذي الشيبة. ومن لم يرحم صغيراً ويجل كبيراً فليس منا)).',NULL,'19',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (13,'١٢ - حدثني سعيد بن يعقوب الطالقاني ثنا عبد الله بن المبارك أننا سليمان التيمي عن أبي عثمان النهدي قال: ضرب رسول الله صلى الله عليه وسلم يده في الخندق فقال: ((بسم الله وبه بدينا، ولو عبدنا غيره شقينا، حبذا رباً وحب ديناً (أو حبذا هو وحب دينا))).',NULL,'20',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (14,'١٣ - ثنا موسى بن أيوب النصيبي ثنا مخلد بن حسين عن شيخ من أهل البصرة وكان يهودياً فأسلم، كان يقرأ الكتب قال: أول العبادة حب الله عز وجل، ألا ترى أن الرجل يحب الرجل. فيمر بداره حباً له. قال إبراهيم بن سعيد الجوهري: كأنه يحث على الحج.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (15,'١٤ - حدثنا حفص بن عمر الحوضي ثنا يزيد بن يزيد البكري ثنا حبيب يعني أبا محمد قال: قلت للحسن: يا أبا سعيد! ما الأواب؟ قال: رجل قلبه معلق عند الله عز وجل.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (16,'١٥ - حدثنا يحيى بن عبد الحميد الحماني ثنا جعفر بن سليمان الضبعي ثنا عبد الصمد بن معقل قال: سمعت رجلاً سأل عمي وهب ⦗٢١⦘ بن منبه في مسجد الحرام قال: حدثني رحمك الله عن زبور داود قال: وجدت في أحد ثلاثين سطراً أن: يا داود! اسمع مني والحق أقول: من لقيني وهو يحبني أدخلته جنتي، يا داود! اسمع مني والحق أقول: من لقيني وهو يخاف عذابي لم أعذبه.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (17,'١٦ - حدثني عبد الله بن أبي بكر المقدسي ثنا مؤمل بن إسماعيل قال: [كان] كهمس العابد يقوم في جوف الليل فيقول: أتراك معذبي وأنت قرة عيني يا حبيب قلباه؟!.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (18,'١٧ - حدثني إسماعيل بن محمد ماهان حدثني أحمد بن أبي الحواري قال: لقيني راهب مرة وعلي عباءة فقال لي: لم لبست العباءة؟ قال: قلت خوفاً منه، قال: وتحبه؟ قال: قلت: نعم، قال: كلا، لن يعذب من يحبه.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (19,'١٨ - حدثني إسماعيل بن محمد ثنا زهير البصري قال: لقيت شعوانة العابدة فقالت لي: ما أحسن طريقتك إلا أنك تنكر المحبة، قال: قلت: ما أنكرها، قالت لي: أتحب ربك؟ قال: قلت: نعم، قالت: فكيف تخاف أن لا يحبك وأنت تحبه؟ قال: قلت: أنا أحبه لما أولاني وما بدأني بمعرفته، ولي ذنوب أخاف أن لا يحبني بما كسبت، قال: فغشي عليها، ثم أفاقت فقالت: زه!',NULL,'21',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (20,'١٩ - حدثني حميد بن الربيع اللخمي حدثني زيد بن الحباب العكلي حدثني محمد بن صالح المدني حدثني سليمان بن عبد الرحمن بن خباب قال: كنا مع القاسم بن محمد بن أبي بكر الصديق فمرض بقديد فسمعته يقول: أنت ربي وحبي وسيدي.',NULL,'22',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (21,'٢٠ - حدثني زياد بن أيوب حدثني أحمد بن أبي الحواري قال: سمعت بشر بن السري يقول: ليس من أعلام الحب أن تحب ما يبغضه حبيبك.',NULL,'22',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (22,'٢١ - حدثنا علي بن عيسى المروذي حدثني محمد بن الحسن ثنا عثمان بن زفر التيمي ثنا حسن بن عياش قال: سمعت محمد بن النضر الحارثي يقول: ما يكاد يمل القربة إلى الله تعالى محب لله عز وجل، وما يكاد يسأم من ذلك.',NULL,'22',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (23,'٢٢ - حدثني إسحاق بن إبراهيم قال: بلغني أنه قيل لبعض الحكماء: أي الأعمال أفضل؟ قال: ما زهدك في الدنيا، قال: ثم ماذا؟ قال: ما هون على قلبك بذل المجهود من عملك لله عز ⦗٢٣⦘ وجل، قال: ثم ماذا؟ قال: ما حبب إليك لقاء الله عز وجل، قال: وما الذي يحبب إلي لقاء الله عز وجل؟ قال: شدة حب الله عز وجل.',NULL,'22',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (24,'٢٣ - حدثني محمد بن الحسين حدثني محمد بن مالك بن ضيغم حدثني مولى لنا قد أدرك جدي يكنى أبا أيوب، قال: قال لي جدك ضيغم ذات يوم: يا أبا أيوب! منعني والله حب الله تعالى من الاشتغال بحب غيره، قال: ثم مال فسقط مغشياً عليه.
INSERT INTO [b26872] VALUES (25,'٢٤ - نبأني علي بن مسلم الطوسي ثنا سيار بن حاتم ثنا رياح بن عمرو القيسي ثنا ثور بن يزيد قال: قرأت في التوراة: إن القلب المحب لله عز وجل يحب النصب لله تعالى.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (26,'٢٥ - حدثنا عبد الرحمن بن مسروق ثنا سيار بن حاتم ثنا جعفر بن سليمان قال: سمعت مالك بن دينار يقول: إن القلب المحب لله تعالى يحب النصب لله تعالى.',NULL,'23',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (27,'٢٦ - قال إبراهيم بن الجنيد: قال بعض العباد: وجدت الله غيوراً يمنعني من كل من أرجوه وإذ سمح قلبي في مودته إجراء ذكره على لساني، فواشوقاه واشوقاه! ثم خر مغشياً عليه.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (28,'٢٧ - حدثنا يحيى بن بكير ثنا أنس بن عياض أبو ضمرة نبأني نوفل بن مسعود أنه حدثه عن أنس بن مالك أنه سمعه يقول: سمعت رسول الله صلى الله عليه وسلم يقول: ((ثلاث من لقي الله عز وجل وهن فيه حرم على النار وحرمت النار عليه: إيمان بالله ورسوله، والثانية حب الله، والثالثة لأن توقد نارٌ فيلقى فيها أحب إليه من أن يرجع إلى الكفر)).',NULL,'24',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (29,'٢٨ - حدثني عبيد بن جناد الحلبي ثنا عبيد الله بن عمرو الرقي عن أيوب عن أبي قلابة عن أنس بن مالك قال: قال رسول الله صلى الله عليه وسلم: ((ثلاث من كن فيه وجد بهن حلاوة الإيمان: أن يكون الله ورسوله أحب إليه مما سواهما، وأن يكره أن يرجع في الكفر كما يكره أن يقذف في النار، وأن يحب المرء المسلم لا يحبه إلا لله عز وجل)).',NULL,'24',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (30,'٢٩ - قال إبراهيم بن الجنيد: قد روينا أن القلب المحب لله تعالى يحب التعب والنصب لله عز وجل، وهيهات أن ينال حب الله بالراحة.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (31,'٣٠ - ثنا يحيى بن عبد الله بن بكير ثنا المفضل بن فضالة القتباني عن أبي عروة البصرة عن زياد أبي عمار عن أنس بن مالك قال: قال: رسول الله صلى الله عليه وسلم: ((علامة حب الله حب ذكره، وعلامة بغض الله بغض ذكره)).',NULL,'25',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (32,'٣١ - وحدثنا الحسن بن عثمان بن حماد البصري قال: أخبرني أسد بن راشد أبو يزيد الدقاق ثنا عمار صاحب السقط عن أنس بن مالك قال: إن من علامة حب الله عز وجل حب ذكر الله، ومن علامة بغض الله بغض ذكر الله.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (33,'٣٢ - حدثنا محمد بن يزيد بن كثير العجلي ثنا إسحاق بن سليمان الرازي ثنا أبو جعفر الرازي عن الربيع عن أنس عن بعض أصحابه قال: علامة حب الله تعالى كثرة ذكره فإنك لن تحب شيئاً إلا أكثرت ذكره، ومن علامة الدين الإخلاص لله، وعلامة العلم خشية الله عز وجل، وعلامة الشكر الرضى بقضاء الله والتسليم لقدره.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (34,'٣٣ - نبأني إسحاق بن إبراهيم الصوفي قال: قرأت في بعض الكتب: قال عيسى بن مريم عليه السلام: يا معشر الحواريين! أحيوا قلوبكم بذكر الله عز وجل، وأميتوها بالخشية، ونوروها بحب الله عز وجل، وفرحوها بالشوق إليه.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (35,'٣٤ - قال إبراهيم: قال بعض العلماء: اعلموا أنكم بالمحبة ترتفعون، وبالمعرفة ترهبون، وبالشوق ترغبون، وبحسن النية تقهرون الهوى، وبترك الشهوة تصفو أعمالكم، حتى تريكم ملكوت السماء في عليين. فمن أراد منكم الراحة فليعمل في منازل أهل المحبة، فإن من أخلاق محبة الله تعالى كثرة الذكر في ساعات الليل والنهار بالقلب واللسان، فإن أمسك اللسان فبالقلب، فإن ذكر القلب أبلغ وأنفع.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (36,'٣٥ - نبأني محمد بن الحسين حدثني الصلت بن حكيم قال: سمعت أبا عبد الرحمن المغازلي يقول: ليس يعطى طريق المحبة غافل ولا ساه. المحب لله طائر القلب كثير الذكر، متسبب إلى رضوانه بكل سبيل يقدر عليها من الوسائل والنوافل دوباً دوباً وشوقاً شوقاً.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (37,'٣٦ - نبأني عون عن إبراهيم بن الصلت نبأني أحمد بن أبي الحواري ثنا موسى أبو عمران قال: من شرب بكأس المحبة لله عز وجل فقد ركب رحل المنقطعين إلى الله عز وجل.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (38,'٣٧ - نبأني سلمة بن شبيب أننا سهل بن عاصم أننا عبدة عن أبي خزيمة عن الحسن قال: قال رسول الله صلى الله عليه وسلم، وقال ذلك عن ربه عز وجل، قال: ((علامة الطهر أن يكون قلب العبد عندي معلقاً، وإذا كان كذلك لم ينسني على حال. فإذا كان كذلك مننت عليه بالاشتغال بي كيلا ينساني، فإذا نسيني حركت قلبه، فإذا تكلم تكلم لي وإذا سكت سكت لي. فذلك الذي تأتيه المعونة من عندي نائماً يقظان)).',NULL,'27',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (39,'٣٨ - ثنا يحيى بن بكير حدثني المفضل بن فضالة عن أبي عروة البصري عن أبي عمار عن أنس بن مالك قال: سمعت رسول الله صلى الله عليه وسلم يقول: ((من أصبح همه غير الله تعالى فليس من الله عز وجل)).',NULL,'27',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (40,'٣٩ - ثنا محمد بن مقاتل المروذي أننا عبد الله بن المبارك أننا سفيان قال: كتب إلي الحجاج بن فرافصة قال: قال بديل: من عرف ⦗٢٨⦘ ربه أحبه، ومن عرف الدنيا زهد فيها، والمؤمن لا يلهو حتى يغفل وإن تفكر حزن',NULL,'27',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (41,'٤٠ - أنبأني إسحاق بن إبراهيم: قال: قال بعض طلاب الحكمة: عند معرفة الله يغلي هيجان المحبة، وعند هيجان المحبة اتصال القلوب بذكره ومناجاته.',NULL,'28',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (42,'٤١ - ثنا حرملة بن يحيى أننا عبد الله بن وهب نبأني عبد الرحمن بن زيد بن أسلم قال: إن الله تعالى ليحب العبد فيبلغ من حبه إذ أحبه أن يقول: اذهب فاعمل ما شئت فقد غفرت لك.',NULL,'28',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (43,'٤٢ - نبأني إسحاق بن إبراهيم قال: قال بعض الحكماء: من أيس من الله عز وجل لجأ إلى نفسه، ومن أيس من نفسه لجأ إلى الله تعالى، ومن لجأ إلى الله تعالى تم عزه وغناه.',NULL,'28',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (44,'٤٣ - نبأني محمد بن الحسين الخراساني عن صالح بن عبد الله نبأني عبد العزيز بن عبد الوهاب قال: قال حكيم من الحكماء: إخواني معشر الربانيين، اعلموا أنه لن تصفو القلوب من الدخل والعيوب حتى يكون الهم في الله تعالى هماً واحداً ويكون السبيل إلى محبة الله تعالى قاصداً. ثم قال: عقبةٌ والله من العقاب كؤود، لن يقطعها إلا حازم زاهد قد فارق الدنيا بقلبه وانقطع عن أهلها إلى ربه. ثم قال: جعلنا الله وإياكم من أهل مخالصته والجد والاجتهاد في أمره، حتى نظفر منه بكمال أهل الشوق، وأعلى منازل أهل الصدق إفضالاً من ربنا وامتناناً وإحساناً منه. على أهل العدوان. ثم قال: إخواني؛ المواعظ كثيرة، والصفات عريضة، والعاقل البصير يستدل بالنعت اليسير.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (45,'٤٤ - وحدثني محمد بن الحسين أيضاً عن صالح بن عبد الله ثنا أبو مسكين قال: سألت بعض العابدين قلت: أوصني، قال: صم عن الدنيا واجعل فطرك عنده حتى يكون هو الذي يلي إفطارك عنده، دع عنك المداعبة في جدٍّ أو هزلٍ، وعليك بذكر الله تعالى بقلبك حتى ينتج على محبة الله تعالى.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (46,'٤٥ - حدثني علي بن عيسى عن محمد بن الحسين حدثني سعيد بن خلف بن زيد القسام قال سمعت مضراً يقول: قال لي عبد الواحد بن زيد: ما أحسب شيئاً من الأعمال يتقدم الصبر إلا الرضى، ولا أعلم درجة أرفع ولا أشرف من الرضى وهو رأس المحبة.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (47,'٤٦ - قال إبراهيم: كان يقال: النعيم العاجل نعيم الذكر والتلذذ بالحزن، وسل الله تعالى أن يسقيك شربة من حبه، فإن سقاك فقد سعدت في العاجل والآجل، والتقوى والزهد في حلال الدنيا وحرامها وترك المنزلة عند المخلوقين، وعليك بالتواضع والخشوع والصمت والخلوة لعلك تنجو مما قد استوجبت بإذن الله العزيز الحكيم.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (48,'٤٧ - حدثني أحمد بن إبراهيم حدثني سلمة بن عقار قال: كان الفضيل بن عياض يقول: إلهي لو عذبتني بالنار لم يخرج حبك من قلبي، أنى أنسى أياديك عندي في دار الدنيا؟.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (49,'٤٨ - حدثني محمد بن الحسين حدثني زكريا بن عدي قال: سمعت عابداً باليمن يقول: سرور المؤمن ولذته في الخلوة بمناجاة سيده عز وجل.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (50,'٤٩ - قال إبراهيم: قال بعض الصالحين: إلهي وعزتك وجلالك لقد أحببتك محبة استقرت حلاوتها في قلبي، وما تنعقد ضمائر موحديك على أنك تبغض محبيك.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (51,'٥٠ - حدثني محمد بن الحسين حدثني إبراهيم بن سليمان بن حريش ثنا يزيد بن علي بن جرير الحنفي قال: مررت على كلاب بن جري، وهو يصلي على الساحل في بعض الليل، فسمعته وهو يقول في سجوده: وعزتك لقد خالط قلبي من محبتك أمر يكل لساني عما أجد منه في نفسي، قال: ثم خفي علي ما كان قبل ذلك! فانطلقت.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (52,'٥١ - حدثني محمد ين الحسين ثنا عبيد الله بن محمد التيمي ثنا معاذ بن زياد قال: قال فضل الرقاشي: ما اشتفى محب الله عز وجل من طاعته، ولو حل بعظم الوسائل منازل الأبرار.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (53,'٥٢ - قال إبراهيم: بلغنا أن الله أوحى إلى داود عليه السلام: تزعم أنك تحبني فإن كنت تحبني فأخرج حب الدنيا من قلبك فإن حبي وحبها لا يجتمعان في قلب واحد.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (54,'٥٣ - قال إبراهيم: فنسأل الله تعالى العون والتوفيق والصدق والمحبة له والأنس به والرضى عنه والتسليم لأمره.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (55,'٥٤ - قال إبراهيم: وقد يمن الله تعالى على أهل طاعته بطاعته بلا أمر منهم استوجبوا عليه، وكان الشكر منهم عليها إلهاماً منه، فهو ⦗٣٢⦘ ولي كل نعمة، ذو الطول والنعم المليء بحسن جزاء من أطاعه ذو الفضل العظيم.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (56,'٥٥ - ثنا يحيى بن عبد الله بن بكير حدثني الليث بن سعد عن خالد بن يزيد عن سعيد بن أبي هلال عن زيد بن أسلم أن مخبراً أخبره: أنه دخل على رأس الجالوت وهو يقرأ التوراة فبكى، فقال: ما يبكيك؟ قال: مررت بحرف فأبكاني: يا بني إسرائيل إني كنت أحبكم فلما عصيتموني أبغضتكم.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (57,'٥٦ - قال إبراهيم: وقد قال بعض العلماء: إن محبة الله تعالى ليست كمحبة الآمنين وإنما محبة الله في طاعته واجتناب معصيته.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (58,'٥٧ - حدثني [محمد بن] حميد الرازي ثنا جرير بن عبد الحميد عن الفضيل بن غزوان عن الحسن قال: من قال: إني أحب الله تعالى فهو كاذب لو أحب الله تعالى لعمل بعمل يحبه الله. ومن قال إني أحب الجنة فهو كاذب ولو أحب الجنة لعمل بعمل أهل الجنة. ومن قال: إني أخاف النار فقد كذب لو كان يخاف النار لم يعمل بعمل أهل النار.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (59,'٥٨ - حدثني محمد بن بشر الكوفي المنقري وأنشده في مثل ذلك:
INSERT INTO [b26872] VALUES (60,'٥٩ - حدثني إسماعيل بن إبراهيم بن بسام البلخي قال: سمعت أبا حازم القيساري ببيت المقدس قال: في الإنجيل مكتوب: إن الله تعالى قال لعيسى عليه السلام: إن أسخط عليك لم ينفعك من رضي غيري، وأن أرض عنك لم يضرك المبغضون، أوصيك بالعبد الذليل المسكين. يا عيسى! الحق، والحق أقول، إني أحب إلى عبدي من نفسه التي بين جنبيه.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (61,'٦٠ - قال إبراهيم: يقال: صدق المحبة لله عز وجل بالقلب مداومة ذكر القلب بالفرح بالله وإيثار محبته وشدة الأنس به، وأن يشق عليه أن يحول بينه وبين الله تعالى حائل.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (62,'٦١ - حدثني أحمد بن سعيد الأزدي قال: سمعت عثمان بن صخر العتكي يقول: طوبى لمحبي الرب عز وجل الذين عبدوه بالفرح والسرور والأنس والطمأنينة فصاروا الصفوة من الخلق والخاصة من البرية، يحنون إليه حنين الولهان ويشتاقون إليه شوق من لا صبر لهم عنه، قد كسروا بالخوف وروحوا بالظفر.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (63,'٦٢ - ثنا سعيد بن سليمان ثنا مبارك بن فضالة عن الحسن قال: كان ناس على عهد رسول الله صلى الله عليه وسلم يقولون: يا رسول الله. إنا نحب ربنا عز وجل حباً شديداً. فأحب الله تعالى أن يجعل لحبه علماً فأنزل الله تعالى: {قل إن كنتم تحبون الله فاتبعوني يحببكم الله ويغفر لكم ذنوبكم والله غفورٌ رحيمٌ}.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (64,'٦٣ - ثنا محمد بن حميد الرازي ثنا علي بن مجاهد ثنا محمد بن إسحاق عن يزيد بن أبي زياد عن محمد بن كعب قال: أوحى الله تعالى إلى موسى: إن إبراهيم لم يحبني أحد من خلقي كحبه إياي.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (65,'٦٤ - ثنا أبو بحر فرات بن محبوب السكوني ثنا عبيد الله الأشجعي عن سفيان عن ليث عن مجاهد في قوله تعالى: {لا يشركون بي شيئاً}، قال: لا يحبون غيري.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (66,'٦٥ - قال إبراهيم: حدثني رجل قال: قيل لرابعة: كيف حبك للرسول؟ قالت: إني لأحبه، ولكن شغلني حب الخالق عن حب المخلوقين.',NULL,'34',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (67,'٦٦ - نبأني محمد بن الحسين حدثني أبو النعمان رستم بن أسامة حدثني عمير أبو يحيى قال: سمعت عابداً من أهل الشام ببيت المقدس يقول: محبة الله تعالى ورثت أهلها سرور الأبد في دار المقامة. قال: ثم غشي عليه.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (68,'٦٧ - حدثني يونس بن عبد الأعلى عبد الله بن وهب أخبرني سفيان بن عيينة عن رجل عن يحيى بن أبي كثير اليمامي قال: نظرنا فلم نجد شيئاً يلتذذ به المتلذذون أفضل من حب الله تعالى وطلب مرضاته.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (69,'٦٨ - حدثني إسحاق بن إبراهيم ثنا غوث بن جابر بن غيلان بن منبه الصنعاني عن رجل ثقة من أهل صنعاء عن أبيه عن وهب بن منبه قال: قال الحواريون: يا عيسى، من أولياء الله الذين لا خوف عليهم ولا هم يحزنون؟ قال: الذين رفضوا الدنيا فكانوا برفضها هم الفرحين، وباعوها فكانوا ببيعها هم المربحين، ونظروا إلى أهلها صرعى قد حلت فيهم المثلات، فأحيوا ذكر الموت وأماتوا ذكر الحياة، يحبون الله ويحبون ذكره ويستضيئون بنوره، لهم خبر عجيب، وعندهم الخبر العجيب، بهم قام الكتاب وبه قاموا، وبهم نطق ⦗٣٦⦘ الكتاب وبه نطقوا وبهم علم الكتاب وبه علموا، لا يرون نيلاً مع ما نالوا ولا أمناً دون ما يرجون ولا خوفاً دون ما يحذرون.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (70,'٦٩ - حدثني إسحاق بن إبراهيم الثقفي ثنا جعفر بن سليمان الضبعي قال: سمعت مالك بن دينار قال: قال موسى عليه السلام: إلهي أين أبغيك؟ فأوحى الله تبارك وتعالى إليه: يا موسى، أبغني عند المنكسرة قلوبهم، فإني أدنو منهم كل ليلة باعاً، ولولا ذلك لانهدموا، قال جعفر: فقلت لمالك بن دينار: كيف المنكسرة قلوبهم؟ قال: سألت الذي قرأ الكتاب، فقال: سألت الذي سأل عبد الله بن سلام فقال: سألت عبد الله بن سلام عن المنكسرة قلوبهم ما يعني به؟ قال: المنكسرة قلوبهم بحب الله عن حب غيره.',NULL,'36',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (71,'٧٠ - قال إبراهيم: سئل من أوتي الحكمة عن عمل يقرب على الله تعالى وإلى خلقه، فقال: بذلك لله ولعباده المحبة، وسئل عما امتن الله تعالى به على القلوب؟ فقال: الطهارة من دنس الشرك، وهو قوله: {ويطهركم تطهيراً}.',NULL,'36',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (72,'٧١ - ويقال: المحبة أن تؤثر الله تعالى على جميع الأشياء، وعليك بالاهتمام بما يرضي الله عز وجل والحفظ لكل جارحة مما يسخط الله عز وجل.',NULL,'37',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (73,'٧٢ - ويقال: العزم إياس النفس من خلاف الطاعة، والصدق والعزم على أداء حقوق الله تعالى، والوفاء بها عند مواقع الأعمال، والحياء من الله تعالى، وحسن المراقبة في السر والعلانية.',NULL,'37',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (74,'٧٣ - حدثني يحيى بن سليمان الجعفي نبأني عبد الله بن وهب نبأني حرملة بن عمران أنه سمع كعب بن علقمة يقول: إن موسى عليه السلام لما أن هرب من فرعون قال: رب أوصني، قال أوصيك أن لا تعدل بي شيئاً إلا اخترتني عليه فإني لا أرحم ولا أزكي من لم يكن كذلك.',NULL,'37',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (75,'<span data-type="title" id=toc-3>باب من كان يسأل الله تعالى أن يرزقه حبه</span>
INSERT INTO [b26872] VALUES (76,'٧٥ - نبأني الحسين بن علي العجلي ثنا محمد بن فضيل بن غزوان الضبي عن محمد بن سعد الأنصاري عند عبد الله بن يزيد الدمشقي ثنا عائذ الله أبو إدريس الخولاني عن أبي الدرداء قال: قال رسول الله صلى الله عليه وسلم: ((قال داود: رب أسألك حبك وحب من يحبك والعمل الذي يبلغني حبك. رب اجعل حبك أحب إلي من نفسي وأهلي ومن الماء البارد. قال: وكان رسول الله صلى الله عليه وسلم إذا ذكر داود وحدث عنه قال: كان أعبد البشر)).',NULL,'38',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (77,'٧٦ - نبأني سعيد بن الحكم بن أبي مريم حدثني عبد الله بن وهب حدثني عمرو بن الحارث أن أبا يونس سليم بن جبير مولى أبي هريرة حدثه عن أبي هريرة قال: كان داود النبي عليه السلام كثير الصلاة لا يفتر.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (78,'٧٧ - ثنا عبد السلام بن مطهر ثنا جعفر بن سليمان عن ثابت البناني قال: كان رسول الله صلى الله عليه وسلم لا يشبع من الصلاة.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (79,'٧٨ - ثنا محمد بن عبد الله الخزاعي أننا حماد بن سلمة عن أبي جعفر الخطمي عن محمد بن كعب القرظي عن عبد الله بن يزيد الخطمي أنه كان يقول: اللهم ارزقني حبك وحب من ينفعني حبه عندك، اللهم ما رزقتني مما أحب فأجعله لي قوة فيما تحب، وما زويت عني مما أحب فاجعله لي فراغاً فيما تحب.',NULL,'39',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (80,'٧٩ - نبأني محفوظ بن الفضل نبأني كثير بن هشام ثنا جعفر بن برقان ثنا صالح بن مسمار قال: بلغنا أن الله عز وجل أرسل إلى سليمان بن داود بعد موت أبيه داود ملكاً من الملائكة. فقال له الملك: إن ربي أرسلني إليك لتسأله حاجتك. قال سليمان: فإني أسأل ربي عز وجل أن يجعل قلبي يحبه كما كان أبي داود عليه السلام يحبه، وأسأل الله تعالى أن يجعل قلبي يخشاه كما كان قلب أبي داود يخشاه. فقال الرب عز وجل: أرسلت إلى عبدي ليسألني حاجته، وكانت حاجته إلي أن أجعل قلبه يحبني وأجعل قلبه يخشاني. وعزتي لأكرمنه. ⦗٤٠⦘ فوهب له ملكاً لا ينبغي لأحد من بعده. ثم قال: {هذا عطاؤنا فامنن أو أمسك بغير حساب. وإن له عندنا لزلفى وحسن مئاب}.',NULL,'39',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (81,'٨٠ - ثنا محمد بن حميد ثنا مهران عن سفيان عن أبيه عن عن عكرمة: {هذا عطاؤنا فامنن أو أمسك بغير حساب}: ليس عليه حساب.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (82,'٨١ - ثنا محمد بن حميد الرازي ثنا مهران عن سفيان عن زياد أبي عثمان عن الحسن قال: ما أنعم الله عز وجل على عبد نعمة إلا عليه فيها تبعة غير سليمان، قال عز وجل: {هذا عطاؤنا فامنن أو أمسك بغير حساب}.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (83,'٨٢ - ثنا محمد بن عبد الله الخزاعي أننا موسى بن خلف العمي ثنا يحيى بن أبي كثير عن زيد بن سلام عن جده ممطور عن أبي عبد الرحمن السكسكي عن مالك بن يخامر عن معاذ بن جبل قال: احتبس علينا رسول الله صلى الله عليه وسلم يوماً صلاة الغداة حتى كادت تطلع ⦗٤١⦘ الشمس. فلما خرج صلى بنا الغداة وقال: ((إني صليت الليلة ما قضي لي، فوضعت جنبي في المسجد، فأتاني ربي عز وجل في أحسن صورة فقال: يا محمد هل تدري فيم اختصم الملأ الأعلى؟ قال: قلت: لا أي رب، قال: يا محمد، قالها ثلاث مرار، قال: قلت: لا أي رب. قال: فوضع يده بين كتفي فوجدت بردها بين ثديي فتجلى لي كل شيء وعرفته. فقلت: في الدرجات والكفارات. قال: فما الدرجات؟ قلت: إطعام الطعام وإفشاء السلام والصلاة والناس نيام. قال: صدقت. قال: فما الكفارات؟ قال: قلت: إسباغ الوضوء في السبرات ونقل الأقدام إلى الجماعات. قال: صدقت. فقال: سل يا محمد. قال: قلت: اللهم إني أسألك فعل الخيرات وترك المنكرات وحب المساكين، وأن تغفر لي وترحمني، وإذا أردت بين عبادك فتنة فاقبضني إليك وأنا غير مفتون، اللهم إني أسألك حبك وحب من أحبك وحب عمل يقربني إلى حبك. فقال النبي صلى الله عليه وسلم: تعلموهن وادرسوهن فإنهن حق)).',NULL,'40',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (84,'٨٣ - نبأني زياد بن أيوب نبأني أحمد بن أبي الحواري ثنا جعفر بن محمد عن أبيه قال: كان بعض التابعين يقول: اللهم أمت قلبي بخوفك وخشيتك وأحيه بحبك وذكرك.
INSERT INTO [b26872] VALUES (85,'٨٤ - نبأني محمد بن الحسين نبأني الوليد بن صالح نبأني يونس بن بكير الشيباني عن مرثد أبي عامر عن الحسن بن الحسن بن علي أنه كان يقول في دعائه: اللهم ارزقني محبة لك تقطع عني محبات الدنيا ولذاتها، وارزقني محبة لك تجمع لي بها خير الدنيا ونعيمها، اللهم اجعل محبتك آثر الأشياء عندي وأقرها لعيني، واجعلني أحبك حب الراغبين في محبتك، حباً لا يخالطه حب هو أعلى منه في صدري ولا أكبر منه في نفسي، حتى تشغل قلبي به عن السرور بغيره، حتى يكمل لي به عندك الثواب غداً في أعلى منازل المحبين لك يا كريم.
INSERT INTO [b26872] VALUES (86,'٨٥ - ونبأني محمد بن الحسين ثنا عبيد الله بن محمد التيمي عن عقبة بن فضالة قال: كان أبو عبيدة الخواص يقول في دعائه بعد ما كبر: اللهم ارزقني حباً لك وحباً لطاعتك وحباً لمطيعك وحباً لأوليائك وحباً لآل محبتك خدامك. اللهم ارزقني حباً ترفعني به عندك في أعلى درجات العلى من منازل المحبين لك. قال: وكان يبكي حتى يكاد يهمد. وكان قد كبر جداً.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (87,'٨٦ - نبأني عمر بن شبة النميري ثنا موسى بن إسماعيل المنقري ثنا سلام بن مسكين قال: سمعت الحسن يقول: اللهم املأ قلوبنا إيماناً بك ويقيناً بك ومعرفة لك وتصديقاً لك وحباً لك وشوقاً إلى لقائك.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (88,'٨٧ - نبأني محمد بن الحسين ثنا داود بن محبر [حدثني] عبد الله بن رشيد قال: سمعت عبد الواحد بن زيد يقول في دعائه: أسألك اللهم أركاناً قوية على عبادتك، وأسألك جوارح مسارعة إلى طاعتك وأسألك همماً متعلقة بمحبتك.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (89,'٨٨ - ثنا سعيد بن سليمان ومحمد بن مقاتل قالا: ثنا عبد الله بن المبارك نبأني عمر بن عبد الرحمن بن مهرب قال: سمعت وهب بن منبه يقول: قال حكيم من الحكماء: إني لأستحي من ربي عز وجل أن أعبده رجاء ثواب الجنة، أي قط، فأكون كالأجير إن أعطي الأجر عمل وإن لم يعط لم يعمل، وإني لأستحيي من ربي أن أعبده مخافة النار، أي قط، فأكون كعبد السوء إن رهب عمل وإن لم يرهب لم يعمل، ولكن أبعده بما هو أهله، وإنه ليستخرج مني حبه ما لا يستخرج مني غيره.',NULL,'44',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (90,'٨٩ - قال إبراهيم بن الجنيد: فأهل محبة الله قوامون بأمر الله عز وجل، قطعوا محبتهم بمعرفة ربهم، وتركوا الدنيا لطاعة مليكهم. فهم يلهمون الحق، ويوفقون للتوفيق، وينظرون بنور الله عز وجل، ويدعون ربهم بالاستكانة، ويتلون القرآن بفهم وفكرة، طابت قلوبهم وطهرت من الأدناس والأقذار، لا تشبه قلوب أهل الحرص والطمع والشره والهوى والآمال.',NULL,'44',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (91,'٩٠ - ثنا عبد العزيز بن الخطاب قال: حدثتنا نائلة الأودية مولاة آل أبي العيزار عن أم عاصم عن السوداء قالت: أتيت النبي صلى الله عليه وسلم أبايعه، قال: اختضبي فاختضبت. ثم جئت فبايعته.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (92,'٩١ - قال لنا عبد العزيز: خرجت علينا نائلة وعليها فروٌ كبلٌ وقد غيرت أطرافها، فقالت: أنا أحب ربي وأنا أفرق من النار.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (93,'٩٢ - قال إبراهيم: ومما قرأت من كلام [أبي] سليمان: قال أحمد بن أبي الحواري وقال محمود لأبي سليمان: ما أقرب ما تقرب به إليه؟ فبكى، ثم قال: مثلي أنا يسأل عن هذا؟ أقرب ما تقرب به إليه أن يطلع من قلبك أنك لا تريد من الدنيا والآخرة إلا هو.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (94,'٩٣ - قال أحمد بن أبي الحواري وسمعت النباجي قال: قال رجل للفضيل بن عياض: يا أبا علي، متى يبلغ العبد الحب لله ⦗٤٦⦘ عز وجل؟ قال: إذا كان منعه إياك وعطاؤه عندك سواء فقد بلغت غاية من حبه.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (95,'٩٤ - قال إبراهيم: يقال من علامة الحب لله عز وجل القيام للمحبوب بالطاعة، وإيثاره على النفس فيما أمكنت فيه القدرة.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (96,'٩٥ - قال أحمد بن أبي الحواري: سمعت عواماً قال لأبي سليمان: تحب ابنك إسماعيل؟ قال: ما على ظهر الأرض أحد أجد له في قلبي حباً ولكني أرحمه.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (97,'٩٦ - وحدثني أبو عبيدة بن الفضيل بن عياض عن أبي إسحاق إبراهيم بن الأشعث البخاري قال: سمعت الفضيل بن عياض يقول في مرضه الذي مات فيه: ارحمني بحبي إياك فليس شيء أحب إلي منك.
INSERT INTO [b26872] VALUES (98,'٩٧ - قال إبراهيم: يقال: المتولي لله عز وجل هو المحب الناصر له، الموالي فيه والمعادي فيه، فمن كانت هذه حاله توحش من أكثر الناس واعتزلهم. فإذا أوذي في الله عز وجل شكر ورجا نصرته واعتز به، وسهل على قلبه ما يخوفه به الناس والشيطان.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (99,'٩٨ - نبأني عثمان بن محمد بن أبي شيبة ثنا يحيى بن آدم ثنا قطبة بن عبد العزيز عن الأعمش عن شمر بن عطية عن شهر بن حوشب قال: أخذت معاذ بن جبل قرحة في حلقه فقال: اخنقني خنقك فوعزتك إني لأحبك.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (100,'٩٩ - نبأني إسحاق بن إبراهيم ختن ابن الصباح قال: قرأت في بعض كتب الحكماء: همة المحبين اتصال المحبة ولقاء المحبوب، وهمة أهل الشوق سرعة الموت. والذي يبدي المحبة في القلوب على قدر ما رسخ في القلوب من العلم بكرم الله وبره ولطفه ووده ورأفته ورحمته وكثرة إحسانه إلى خلقه مع إساءة الخلق.',NULL,'47',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (101,'١٠٠ - ثنا داود بن رشيد ثنا أبو حيوة شريح بن يزيد الحضرمي ثنا صفوان بن عمرو عن عبد الله بن بسر اليحصبي عن أبي أمامة الباهلي قال: حببوا الله إلى الناس يحببكم الله.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (102,'١٠١ - ثنا محمد بن سابق ثنا مسعر بن كدام عن إبراهيم السكسكي قال: حدثني أصحابنا عن أبي الدرداء قال: إن أحب عباد الله إلى الله عز وجل الذين يحبون الله ويحببون الله إلى الناس، والذين يراعون الشمس والقمر والأظلة لذكر الله عز وجل.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (103,'١٠٢ - ثنا يحيى بن سليمان الجعفي ثنا عبد الله بن وهب حدثني واقد بن سلامة عن يزيد الرقاشي عن أنس بن مالك عن رسول الله صلى الله عليه وسلم أنه قال: ((ألا أخبركم عن أقوام ليسوا أنبياء ولا شهداء يغبطهم يوم ⦗٤٩⦘ القيامة الأنبياء والشهداء بمنازلهم من الله عز وجل على منابر من نور يعرفون عليها. قالوا: من هم؟ قال: هم الذين يحببون عباد الله إلى الله، ويحببون الله تعالى إلى عباده، ويمشون لله في الأرض نصحاً. فقلنا: هذا حببوا الله تعالى إلى عباده، فكيف يحببون عباد الله إلى الله؟ قال: يأمرونهم بما يحب الله وينهونهم عما يكره الله، فإذا أطاعوهم أحبهم الله بعد)).',NULL,'48',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (104,'١٠٣ - حدثني محمد بن الحسين حدثني أبو الوليد عياش بن عاصم حدثني [سعيد بن] صدقة أبو مهلهل قال: أتاني آتٍ في منامي فقال: أتحب الله عز وجل؟ قلت: إي والذي لا إله غيره إني لأحبه وأحب طاعته، قال: أفلا تناديه نداء أوليائه؟ قلت: وما هو؟ قال: قل: نبهني إلهي للخطر العظيم من محبتك يا بارئ النسم.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (105,'١٠٤ - حدثني علي بن عيسى المروذي حدثني محمد بن عبيدة ثنا الحسين بن الربيع حدثني سعيد بن عبد الغفار قال: كتب محمد بن ⦗٥٠⦘ العلاء بن المسيب من البصرة إلى محمد بن يوسف الأصبهاني: يا أخي من أحب الله بصدق أحب أن لا يعرفه الناس.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (106,'١٠٥ - قال إبراهيم: قال بعض العباد: سبحانك، ولهت قلوب الذاكرين بك فاستنارت بنورك، فسكنت السماوات بأبصارها، وعمرت الملكوت بمناجاتها، فأجسادها منها معطلة، وهي بمحبتك متصلة. فأي لذة تطعم إلا في الأنس بك، يا من أشرقت لنوره السموات، وأنارت لوجهه الظلمات، وحجب جلاله عن العيون، ووصل به معارف العقول، فأنابت إليه أبصار القلوب.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (107,'١٠٦ - ثنا إسحاق بن موسى الخطمي حدثني عباد بن كليب أبو غسان ثنا محمد بن النضر الحارثي قال: قال محمد بن كعب القرظي: وجدت في بعض كتب الحكمة: أيها الصديقون، أفرحوا بي وتنعموا بذكري.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (108,'١٠٧ - قال إبراهيم: قال بعض العباد:
INSERT INTO [b26872] VALUES (109,'١٠٨ - وقال أيضاً:
INSERT INTO [b26872] VALUES (110,'١٠٩ - وقال أيضاً:
INSERT INTO [b26872] VALUES (111,'١١٠ - حدثني أبو سعيد يحيى بن سليمان الجعفي حدثني عبد الله بن وهب قال: سمعت حيي بن عبد الله المعافري يحدث عن أبي عبد الرحمن عبد الله بن يزيد الحبلي عن عبد الله بن عمرو بن العاص أنه قال: من أحب الله تعالى ورسوله صادقاً غير كاذب والمؤمنين غائبهم وحاضرهم فقد ذاق طعم الإيمان.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (112,'١١١ - ثنا أبو طالب عبد الجبار بن عاصم ثنا بقية بن الوليد عن عبد الحميد بن إبراهيم عن عثمان بن سعيد القرشي عن محمد بن عبد العزيز الزهري قال: لما حضرت العباس بن عبد المطلب الوفاة بعث إلى ابنه عبد الله بن عباس، فقال: يا بني إني والله ما مت موتاً. ولكني فنيت فناءً، يا بني أحب الله وطاعته حتى لا يكون شيء أحب إليك منه ومن طاعته، وخف الله ومعصيته حتى لا يكون شيء أخوف عندك منه ومن معصيته، فإنك إذا أحببت الله وطاعته نفعت كل أحد، وإذا خفت الله ومعصيته لم تضر أحداً. أستودعك الله تعالى.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (113,'١١٢ - قال إبراهيم: قرأت في بعض الكتب: إن الله تعالى يقول: معشر المتوجهين إلي بحبي ما ضركم ما فاتكم من الدنيا إذا كنت لكم حظاً، وما ضركم من عاداكم إذا كنت لكم سلماً.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (114,'١١٣ - قال: وأوحى الله تعالى إلى موسى: إياك والتضرع إلى أبناء الدنيا، إذاً أعرض عنك، ولا تجد بدينك لدنياهم، إذاً آمر بأبواب جنتي تغلق دونك.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (115,'١١٤ - ثنا بشر بن آدم ثنا قزعة بن سويد ثنا عبد الله بن أبي نجيح عن مجاهد عن عبد الله بن عباس قال: قال رسول الله صلى الله عليه وسلم: ((لا أسألكم على ما آتيتكم من البينات والهدى أجراً إلا أن توادوا الله عز وجل وتتقربوا إليه بطاعته)).',NULL,'52',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (116,'١١٥ - ثنا محمد بن حميد الرازي ثنا حكام بن سلم عن عنبسة بن سعيد الأسدي عن ابن أبي ليلى عن القاسم بن أبي بزة عن مجاهد في قوله عز وجل: {إن الذين آمنوا وعملوا الصالحات سيجعل لهم الرحمن وداً}، قال: يحبهم ويحببهم إلى خلقه.',NULL,'52',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (117,'١١٦ - قال حكام ثنا أو سنان عن الأعمش قال: محبة في الدنيا.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (118,'١١٧ - ثنا يحيى بن عبد الله بن بكير حدثني عبد الله بن لهيعة عن عطاء بن دينار عن سعيد بن جبير: {إن الذين آمنوا وعملوا الصالحات سيجعل لهم الرحمن وداً} قال: حباً.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (119,'١١٨ - ثنا محمد بن حميد الرازي ثنا مهران بن أبي عمر عن سفيان عن مسلم عن مجاهد عن ابن عباس قال: {سيجعل لهم الرحمن وداً} قال: المحبة.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (120,'١١٩ - ثنا يحيى بن عبد الحميد عن شريك عن عبيد الكاتب عن مجاهد: {سيجعل لهم الرحمن وداً} قال: محبة في صدور المؤمنين.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (121,'١٢٠ - حدثني يحيى بن عبد الحميد ثنا حبان بن علي عن ابن أبي ليلى عن المنهال بن عمرو عن سعيد بن جبير عن ابن عباس {سيجعل لهم الرحمن وداً} قال: محبة في صدور المؤمنين.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (122,'١٢١ - وثنا يحيى ثني وكيع عن ابن أبي ليلى عن المنهال بن عمرو عن سعيد بن جبير قال: يحبهم ويحببهم.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (123,'١٢٢ - قال: وثنا يحيى ثنا أبو معاوية عن جويبر عن الضحاك {سيجعل لهم الرحمن وداً} قال: محبة في صدور المؤمنين.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (124,'١٢٣ - ثنا داود بن رشيد ثنا أبو حيوة شريح بن يزيد الحضرمي ثنا صفوان بن عمرو عن عبد الله بن بسر اليحصبي عن أبي أمامة أنه كان يقول: حببوا الله إلى الناس يحببكم الله عز وجل.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (125,'١٢٤ - ثنا أبو الفضل محرز بن عون ثنا خلف بن خليفة الأشجعي عن ليث عن أبي فزارة قال: بلغني أن داود عليه السلام سأل ربه فقال: رب دلني على عمل يدخلني الجنة، قال: آثر هواي على هواك. قال: رب دلني على عمل يدخلني الجنة، قال: اغضب لي أشد مما تغضب لنفسك. قال: يا داود حبني وأحب من يحبني، وحببني إلى خلقي. قال: يا رب، هذا أحبك وأحب من يحبك، فكيف أحببك إلى خلقك؟ قال: ذكرهم بآلائي فإنهم لا يذكرون مني إلا خيراً.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (126,'١٢٥ - ثنا محمد بن كثير بن يزيد العجلي ثنا محمد بن فضيل بن غزوان عن عطاء بن السائب عن أبي عبد الله الجدلي قال: أوحى الله تعالى إلى داود: أحبني وأحب أحبائي وحببني إلى الناس. قال: يارب، هذا أحبك وأحب أحباءك، فكيف أحببك إلى الناس؟ قال: تذكرني فلا تذكر مني إلا حسناً.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (127,'١٢٦ - حدثني زياد بن أيوب ثنا أحمد بن أبي الحواري حدثني عبد العزيز بن عمير قال: سمعت أبا سليمان الواسطي يقول: ذكر النعم يورث المحبة.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (128,'١٢٧ - قال إبراهيم: يقال: معنى الشكر اعتقاد القلب أنه ليس في السماء والأرض نعمة إلا وهي لله عز وجل.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (129,'١٢٨ - حدثني محمد بن الحسين حدثني سعد بن عمران بن زارة قال: سمعت كلاب بن جري يقول لرجل -من الطفاوة- وهو يوصيه بطرائق البر، فقال له فيما يقول:
INSERT INTO [b26872] VALUES (130,'١٢٩ - حدثني محمد بن الحسين ثنا عبد لله بن محمد بن سعيد الأعور حدثني مطرف بن أبي بكر الهذلي قال: كانت عجوز في عبد القيس متعبدة. فكانت إذا جاء الليل تحزمت وقامت إلى المحراب، وإذا جاء النهار خرجت إلى القبور. قال: وكانت تقول: المحب لا يسأم من خدمة حبيبه، ولا ينزل في جميع أموره إلا عند هواه، ورجاء المحب تحقيق، وقربان المحب الوسائل.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (131,'١٣٠ - ثنا عبد الله بن أبي بكر المقدمي ثنا جعفر بن سليمان حدثني عمر بن نبهان عن قتادة قال: وقف علينا خليد العصري ونحن في حلقة فقال: ما منكم من أحد إلا وهو يحب أن يلقى حبيبه، ألا فأحبوا ربكم وسيروا إليه سيراً جميلاً.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (132,'١٣١ - حدثني محمد بن عبد الملك ثنا الحكم بن نافع أبو اليمان ثنا إسماعيل بن عياش عن شرحبيل بن مسلم الخولاني أن أبا عنبة الخولاني كان يقول: سر سيراً جميلاً لا مصعداً ولا ممهلاً.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (133,'١٣٢ - ثنا أبو الوليد هشام بن عبد الملك ثنا صدقة بن خالد ثنا عبد الرحمن بن يزيد بن جابر حدثني محمد بن أبي عائشة قال: لا ⦗٥٧⦘ تكن ذا وجهين وذا لسانين تظهر للناس أنك تحب الله ليحمدوك وقلبك فاجر',NULL,'56',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (134,'١٣٣ - ثنا سلمة بن شبيب ثنا سهل بن عاصم ثنا أحمد بن عاصم الأنطاكي قال: من عرف الله اكتفى به، ومن لم يعرفه اكتفى بخلقه دونه، فطال غمه وكثرت شكاته. ومن أحب الله تعالى لم يكن في قلبه فضل يحب أحداً، ولو أراد لم يترك.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (135,'١٣٤ - حدثني محمد بن الحسين حدثني حكيم بن جعفر الأعور قال: قال مسلم أبو عبد الله: من أحب الله آثر هوى الله على محبة نفسه، ومن خشي الله خرج من الدنيا بحسرات، والمؤمن من الله بمنزلة كل خير، بين خوف وشفقة وطاعة ومحبة، وما يتلذذ المتقربون بشيء في صدورهم ألذ من حب الله ومحبة أهل ذكره.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (136,'١٣٥ - وحدثني محمد بن الحسين حدثني حكيم بن جعفر عن دويد أبي سليمان عن حيان بن الأسود عن عبد الواحد بن زيد عن فرقد السبخي قال: قرأت في بعض الكتب: من أحب الله لم يكن شيء آثر عنده من هواه، ومن أحب الدنيا لم يكن شيء آثر عنده من هوى نفسه. والمحب لله تعالى أمير مؤمر على الأمراء، زمرته أول الزمر يوم القيامة ومجلسه أقرب المجالس فيما هنالك. والمحبة منتهى القربة ⦗٥٨⦘ والاجتهاد. ولن يسأم المحبون من طول اجتهادهم لله عز وجل: يحبونه ويحبون ذكره ويحببونه إلى خلقه، يمشون بين عباده بالنصائح، ويخافون عليهم من أعمالهم يوم تبدو الفضائح. أولئك أولياء الله وأحباؤه وأهل صفوته أولئك الذين لا راحة لهم دون لقائه.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (137,'١٣٦ - حدثني محمد بن يحيى الأزدي أننا سعيد بن عامر أننا محمد بن ليث عن بعض أصحابه قال: كان حكيم بن حزام عشية عرفة ومعه مائة رقبة ومائة بدنة، فإذا وقف بعرفة أعتق المماليك، وإذا نزل منى نحر البدن، وكان يطوف بالبيت ويقول: لا إله إلا الله، نعم الرب ونعم الإله، أحبه وأخشاه.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (138,'١٣٧ - أخبرني محمد بن الحسين أخبرني عبيد الله بن محمد التيمي قال: سمعتهم يذكرون عن بعض أولئك الضخام أنه قال: إن العمل على المخافة قد يغيره الرجاء، والعمل على المحبة لا يدخله الفتور.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (139,'١٣٨ - ثنا محمد بن حميد الرازي حدثنا [مهران] بن أبي عمر عن سفيان عن عاصم الأحول عن الشعبي في قول الله عز وجل {إن الله يحب التوابين} قال: التائب من الذنب كمن لا ذنب له، وإذا أحب [الله] عبداً لم يضره ذنبه.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (140,'١٣٩ - قال إبراهيم: كان يقال: ليس لمعتب ذنب.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (141,'١٤٠ - حدثنا أحمد بن عبد الله بن يونس وداود بن عمرو بن زهير الضبي قالا: ثنا محمد بن مسلم الطائفي عن إبراهيم بن ميسرة عن عبيد بن سعد قال: خرج أبو أيوب الأنصاري مع غازية، فلما كان عند المدينة، قال: قلت: ما المدينة؟ قال: القسطنطينية، قال: قص قاص فقال: ليس أحد من بني آدم يعمل في الدنيا عملاً أول النهار إلا عرض على أهل معارفه من أهل الآخرة إذا أصبح. فقال أبو أيوب: أيها القائل، انظر ماذا تقول؟ قال: والله إن ذلك لكذلك. قال: فقال أبو أيوب: اللهم لا تفضحني عند سعد بن عبادة ولا عند ⦗٦٠⦘ عبادة بن الصامت بما عملت بعدهما. قال: فقال القاص: والله الذي لا إله إلا هو ما كتب الله ولايته لعبد إلا ستر عورته وأثنى عليه بأحسن عمله.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (142,'١٤١ - حدثني محمد بن الحسين حدثني عبد الله بن أبي نوح قال: سمعت رجلاً من العباد يقول في كلامه: إذا سئم البطالون من بطالتهم لم يسأم محبوك من مناجاتك وذكرك.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (143,'١٤٢ - حدثني أحمد بن خالد بن مهران ثنا إسماعيل بن علية عن غالب القطان عن بكر بن عبد الله المزني قال: ما فاق أبو بكر أصحاب محمد صلى الله عليه وسلم بصوم ولا بصلاة ولكن بشيء كان في قلبه.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (144,'١٤٣ - سمعت بعض الشيوخ من المحدثين يقول: قال عبد الله بن داود الخريبي: إنما سمي أبو بكر الصديق خليفة رسول الله صلى الله عليه وسلم لأنه خلف من رسول الله.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (145,'١٤٤ - قال إبراهيم: بلغني عن ابن علية أنه قال في عقب هذا الحديث: الذي كان في قلبه الحب لله والنصيحة في خلقه.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (146,'١٤٥ - حدثني محمد بن إسماعيل بن عياش حدثني أبي قال: وحدثتني أم عبد الله بنت خالد بن معدان عن أبيها: أن آدم عليه السلام قال لابن له: إني أستحيي من الله ربي أن أسأله الجنة، فانطلق فصم أربعين يوماً، ثم سل ربك: هل يعيدني في الجنة، فإن وعدك ربي أن يدخلني الجنة فجئني بأمارة منها. فانطلق ابن آدم فصام أربعين يوماً، ثم سأل ربه: إن آدم أرسلني إليك: هل تعيده في الجنة؟ فقال الرب: قل لعبدي: تؤمن بي ولا تشرك بي شيئاً ولتحبني ولتحببني، فإذا فعل ذلك فله عندي النعمة والسرور واللذة وقرة العين، وهذه ترنجة ما من الجنة فأبلغها إليه. فلما رآها آدم عرف أنها من الجنة فوضعها على عينيه.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (147,'١٤٦ - ثنا محمد بن بكار ثنا فرج بن فضالة عن لقمان بن عامر عن أبي أمامة الباهلي قال: كان فيما عهد الله عز وجل إلى آدم عليه السلام حين أخرجه من الجنة أن: يا آدم اعبدني ولا تشرك بي شيئاً، وحبني وحببني، واحفظ فرجك الذي بين رجليك، فإنك إذا فعلت ذلك فلك عندي النعمة والسرور واللذة وقرة العين فيما بعد الموت.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (148,'١٤٧ - حدثني محمد بن الحجاج بن جعفر عن إياس بن نذير الضبي ثنا عمرو بن محمد العنقزي أننا أسباط بن نصر الهمداني عن السدي في قوله: {اسكن أنت وزوجك الجنة وكلا منها رغدً حيث شئتما}، قال: الرغد: الهنيء.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (149,'١٤٨ - حدثني محمد بن الحسين حدثني محمد بن معاوية الأزرق قال: قال بعض العباد: ما تداخل القلب شيء أبعث له على سبيل النجاة من سرور مزج بفكرةٍ في حب الله، فعند ذلك يهون عليه كل نصب وتعب.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (150,'١٤٩ - وحدثني محمد بن الحسين حدثني مالك بن ضيغم الراسي حدثني واقد بن يزيد الصفار قال: سمعت عبد العزيز بن سليمان يقول في كلامه: أنت أيها المحب تزعم أن محبتك لله تحقيق، أما والله لو كنت كذلك لضاقت عليك الأرض برحبها حتى تصل إلى رضا حبيبك وإلى النظر إلى وجهه في دار كبريائه وعزه. قال واقد: فكان إذا أخذ في هذا النعت سمعت التصاريخ من نواحي المسجد.',NULL,'62',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (151,'١٥٠ - وحدثني محمد بن الحسين حدثني عبيد الله بن محمد التيمي: أن رجلاً قال لعابد: أوصني أو عظني. فقال: أي الأعمال أغلب على قلبك؟ فقال الرجل: والله ما أجد شيئاً أغلب على قلبي من محبة الله تعالى. فقال له العابد: حسبك ما غلب على قلبك، فوالله ما رأيت شيئاً أنفع للمحب عند حبيبه من المبالغة في محبته. وهل تدري ما ذلك؟ أن لا يعلم شيئاً فيه رضاه إلا أتاه، ولا يعلم شيئاً فيه سخطه إلا اجتنبه، فعند ذلك ينزل المحبون من الله منازل المحبة. قال: وصرخ العابد والسائل وسقطا.
INSERT INTO [b26872] VALUES (152,'١٥١ - كان بعضهم يقول: يا أنيس كل منفرد بذكره وجليس كل متوحد بحبه.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (153,'١٥٢ - وقال آخر: إذا كنت تحبه وهو يبتليك فاعلم أنه إنما يريد أن يصافيك.',NULL,'63',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (154,'<span data-type="title" id=toc-4>الجزء الثاني</span>
INSERT INTO [b26872] VALUES (155,'١٥٤ - ثنا محمد بن سابق ثنا زائدة بن قدامة ثنا منصور عن شقيق عن كردوس بن هانئ قال: كنت أجد في الإنجيل إذ كنت أقرؤه: إن الله تعالى ليصيب العبد بالأمر، وإنه ليحبه، لينظر كيف تضرعه إليه.',NULL,'67',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (156,'١٥٥ - حدثني أبو مسلمة إسحاق بن سعيد الدمشقي ثنا خليد بن دعلج عن الحسن قال: قال رسول الله صلى الله عليه وسلم: ((إذا أحسن عبدٌ فألزق الله تعالى به البلاء فإن الله تعالى يريد أن يصافيه)).',NULL,'67',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (157,'١٥٦ - حدثني هشام بن عمار ثنا صدقة بن خالد ثنا عثمان بن أبي العاتكة عن علي بن يزيد عن القاسم عن أبي أمامة عن النبي صلى الله عليه وسلم أنه كان يقول: ((إن الله عز وجل يقول: ابن آدم اركع لي أربع ركعات أول النهار أكفك آخره. وإن الله تعالى يقول: من أهان لي ولياً فقد بارزني بالعداوة. يا ابن آدم لن تدرك ما عندي إلا بأداء ما افترضت عليك. ولا يزال عبدي يتحبب إلي بالنوافل حتى أحبه، فأكون قلبه الذي يعقل به، ولسانه الذي ينطق به، وبصره الذي يبصر به، وإذا دعاني أجبته، وإذا سألني أعطيته، وإذا استنصرني نصرته، وأحب عبادة عبدي إلي النصيحة)).',NULL,'68',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (158,'١٥٧ - حدثني صالح بن عمران بن صالح حدثني أحمد بن غسان البصري العابد قال: قرأت في التوراة التي لم تبدل: الصديقون لهم منابر من نور وإلى وجه الرحمن تعالى ينظرون. وقرأت في زبور داود: أحبوا الله يا صديقيه، أفرحوا أيها الصديقون بالله وتنعموا بذكره.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (159,'١٥٨ - قال إبراهيم: يقال إن هذا الرضا ينال بالتفويض، والتفويض ينال بالمحبة، والمحبة تنال باشتغال القلب بالذكر في نعم الله عز وجل.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (160,'١٥٩ - حدثني أحمد بن خالد بن مهران ثنا محمد بن مخلد عن سهل بن الخراساني أو غيره قال: دخلنا على عابد بالبصرة وهو يجود بنفسه، وهو يقول: أنا عطشان إني لم أرو من حب ربي، وجائع لم أشبع من حب ربي.',NULL,'69',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (161,'١٦٠ - وقال بعضهم:
INSERT INTO [b26872] VALUES (162,'١٦١ - حدثني يحيى بن معين ثنا نوح بن يزيد أننا إبراهيم بن سعد حدثني محمد بن إسحاق حدثني محمد بن جعفر قال: سمعت رجلاً من بني عذرة يحدث عروة بن الزبير، فقال: يا هذا بحق أنكم أرق الناس قلوباً؟ قال: نعم، والله لقد تركت في الحي ثلاثين شاباً قد خامرهم السل ليس لهم داء إلا الحب.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (163,'١٦٢ - قال إبراهيم: يقال: علامة المحب على صدق الحب ست خصال:
(١) إحداها دوام الذكر بقلبه بالسرور بمولاه.
(٢) والثانية إيثاره محبة سيده على محبة نفسه ومحبة الخلائق، يبدأ بمحبة مولاه قبل محبة نفسه ومحبة الخلائق.
(٣) والثالثة الأنس به والاستثقال لكل قاطع يقطع عنه أو شاغل يشغل عنه.
(٤) والرابعة الشوق إلى لقائه والنظر إلى وجهه.
(٥) والخامسة الرضا عنه في كل شديدة وضر ينزل به.
(٦) والسادسة اتباع رسوله.',NULL,'70',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (164,'١٦٣ - ثنا علي بن عبد الله بن جعفر ثنا إبراهيم بن خالد الصنعاني أخبرني عمر بن عبد الرحمن قال: سمعت وهب بن منبه يقول: القدوس: الطاهر.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (165,'١٦٤ - قال إبراهيم: قال غير وهب بن منبه: القدوس: المبارك، والمهيمن: الشاهد.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (166,'١٦٥ - حدثني محمد بن يعقوب قال: قال أبو عبد الله النباجي: إذا سألت فسل الله تعالى، إما أن يعطيك فيهنئك أو يمنعك فيرضيك، إنك إن أحببت الله أحببت كل ما يرد عليك من الله. ومثل ذلك مثل الرجل يحب الرجل، فإذا رأى ولد حبيبه وصديقه لم يتمالك أن يضمه إليه حباً لصديقه. كذلك من أحب الله عز وجل لم يرد عليه شيءٌ من الله إلا ضمه إليه من شدة حبه لله عز وجل. وتعالى الله علواً كبيراً.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (167,'١٦٦ - حدثني عبد الرحيم بن يحيى الأموي حدثني عثمان بن عمارة قال: كان عتبة الغلام يقول: من سكن حب الله قلبه لم يجد حراً ولا برداً.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (168,'١٦٧ - قال لي عبد الرحيم بن يحيى: يعني من سكن حب الله قلبه شغله حتى لا يعرف الحر من البرد ولا الحلو من الحامض ولا الحار من البارد.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (169,'١٦٨ - حدثني محمد بن الحسين حدثني حكيم بن جعفر قال: قال ضيغم لكلاب: إن حبه شغل قلوب مريديه عن التلذذ بمحبة غيره، فليس لهم في الدنيا مع حبه لذة تداني محبته، ولا يأملون في الآخرة من كرامة الثواب أكثر عندهم من النظر إلى وجهه. قال: فسقط كلابٌ عند ذلك مغشياً عليه.',NULL,'72',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (170,'١٦٩ - قال: وحدثني محمد بن الحسين قال: سمعت عبد الله بن الفرج العابد قال: بلغنا أن رجلاً من العباد كان يقول: ألذ حالت العباد عبادةٌ تهيجها المحبة، وإن الشوق والمخافة يستخرجان من الأبدان خفي التعب والنصب.
INSERT INTO [b26872] VALUES (171,'١٧٠ - وحدثني محمد بن الحسين ثنا الصلت بن حكيم قال: سمعت أبا جعفر المحولي يقول: ولي الله المحب لله لا يخلو قلبه من ذكر ربه ولا يسأم من خدمته. فإذا أعرض أعرض عنه، وإذا أقبل على الله أقبل عليه برأفته ورحمته.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (172,'١٧١ - ثنا أبو صالح عبد الحميد بن صالح البرجمي ثنا أبو شهاب عن ليث عن محمد بن واسع قال: إذا أقبل العبد إلى الله تعالى أقبل الله تعالى إليه بقلوب المؤمنين.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (173,'١٧٢ - حدثني إسحاق بن إبراهيم بن الصباح قال: بلغني عن صالح الناجي أنه كان يقول: الطاعة إمرة والمطيع لله أمير مؤمر على الأمراء، ألا ترى هيبته في قلوبهم، إن قال قبلوا وإن أمر أطاعوا. يحق لمن أحسن خدمتك ومن مننت عليه بمحبتك أن تذل له الجبابرة حتى يهابوه، فهيبته في صدورهم من هيبتك في قلبه، فكل الخير من عندك لأوليائك.',NULL,'73',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (174,'١٧٣ - حدثني عبد الله بن عبيد الكوفي عن محمد بن الحسين ثنا حكيم بن جعفر ثنا عبد الله بن أبي نوح قال: سمعت رجلاً من ⦗٧٤⦘ العباد ذات ليلة يبكي ويعدد على نفسه. ثم ذكر السيد تعالى فجعل يقول في بكائه:
INSERT INTO [b26872] VALUES (175,'١٧٤ - قال محمد بن الحسين: وحدثني أحمد بن سهل الأردني قال: سمعت شيخاً من العباد في بيت المقدس بين المغرب والعشاء يبكي ويقول في دعائه: إليك لجأ المحبون لك في وسائلهم إليك اتكالاً على كرمك في قبولها. قال: ثم خفت فخفي علي ما كان بعد ذلك.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (176,'١٧٥ - حدثني علي بن مسلم بن سعيد الطوسي ثنا عبد الصمد بن عبد الوارث ثنا الربيع بن برة. قال: سمعت الحسن تلا: {يا أيها النفس المطمئنة. ارجعي إلى ربك راضيةً مرضيةً. فادخلي في عبادي. وادخلي جنتي}، قال الحسن: النفس المطمئنة اطمأنت إلى الله عز وجل واطمأن إليها وأدخلها الجنة وجعلها من عباده الصالحين.',NULL,'74',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (177,'١٧٦ - ثنا عبد الله بن أحمد الخزاعي ثنا أبو وهب محمد بن مزاحم أننا عبد الله بن المبارك قال: قال الحسن: إنما عاتب الله أولي الألباب لأنه يحبهم.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (178,'١٧٧ - قال إبراهيم: يقال: إن أولياء الله تعالى وأهل محبته الذين استقرت محبة الله ومعرفته في قلوبهم، منهم المرسلون والنبيون والصديقون والشهداء، فاقوا أهل السماء وأهل الأرض بشدة حبهم لله ومعرفتهم به. سقاهم كأس محبته ولذذهم بنعيمها وأذاقهم حلاوتها. فحجبتهم معرفة ربهم ومحبته عن محبة غيره. واشتغلوا بتلذذ ذكر ربهم، وودوا أنهم أكلوا أكلة تكون آخر زادهم من الدنيا اكتفاء بما قل من الدنيا. فلما أعطوا الله تعالى ذلك من قلوبهم ضيق أمعاءهم وخفف عليهم شهواتهم، فاكتفوا باليسير من المطعم وقصرت شهواتهم عما كانت. فخفت مؤونة الدنيا عليهم، فلا ينافسون فيها أحداً ولا يتنافسون للذة التي قد اكتفوا بها من حب ربهم واستغنوا بها عن كل لذة وكل شهوة مع الشوق إليه. فإذا دخلت في حال الرضا وأهل المحبة ذهبت بصفوة الدنيا والآخرة. فمن عمل في هذا بعزم وإرادة كان ذلك أفضل من كل بر يتقرب به، لأن محبة الله تستغرق أعمال العاملين. فلما اشتغلوا بحب الله أخرجهم حب الله عز وجل إلى الفكرة والعبرة. فهم يتنافسون في حب الله عز وجل كما يتنافس أهل ⦗٧٦⦘ الدنيا في الأموال والنساء والأولاد، وصغر عندهم كل شيء من الأعمال والثواب مع الحب وثواب الحب. فأهل محبة الله في الشرف الأعلى والمنزل من الدرجات العلى.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (179,'١٧٨ - حدثني عون عن إبراهيم بن الصلت حدثني أحمد بن أبي الحواري ثنا عبد العزيز بن عمير قال: قال حيان بن الأسود: الموت! الموت جسر يصل به -إلى الحبيب- المحبون.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (180,'١٧٩ - حدثني محمد بن الحسين حدثني عمار بن عثمان الحلبي حدثني زريق القشيري قال: سمعت ضيغماً يقول، وذكر المتقين فقال: إنما قاموا لأوليائه بحسن الخدمة مع قديم تفضله عليهم.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (181,'١٨٠ - حدثني محمد بن الحسين حدثني حكيم بن جعفر حدثني مسمع بن عاصم قال: سمعت عابداً من أهل البحرين يكنى أبا سليمان يقول في جوف الليل، ونحن على بعض السواحل: قرة عيني وسرور قلبي، ما الذي أسقطني [من عينك] يا مانح العصم؟ ثم صرخ وبكى ثم نادى: طوبى لقلوب ملأتها خشيتك واستولت عليها، محبتك مانعةٌ لها من كل لذة غير مناجاتك والاجتهاد في خدمتك، وخشيتك قاطعةٌ لها عن سبيل كل معصية خوفاً لحلول ⦗٧٧⦘ سخطك. قال: ثم بكى ثم قال: يا إخوتاه ابكوا على خوف فوت خير الآخرة حيث لا رجعة ولا حيلة.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (182,'١٨١ - وحدثني محمد بن الحسين ثنا يوسف بن الحكم ثنا فياض بن محمد بن سنان القرشي قال: قال رجل من العابدين: قليل المحبة تبين على صاحبها كثرة النحول، والشوق خطرات، والخوف مبادرة. ومن طلب خاف أن يدركه الطالب فلم يبق من نفسه باقياً. والمطيع لله من الله على خلال أربع:
(١) إما أن يتقبل طاعته فيفوز لديه بثوابها.
(٢) وإما أن تشغله في الدنيا عن الآثام بها فتقل خطاياه.
(٣) وإما أن يتداركه منه بنظرة فيلحقه بدار المطيعين تفضلاً منه وإن لم يستحق ذلك.
(٤) وإن فاتته هذه الأخلاق. لم يفته ثواب النصب إن شاء الله. قال: وكان يقول: قليل القربة عند الكريم يفك الرقاب من النار.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (183,'١٨٢ - قال إبراهيم: كتب رجل من أهل العلم إلى أخ له: بسم الله الرحمن الرحيم. أسعدنا الله وإياك بطاعته، ومن علينا ⦗٧٨⦘ وعليك بمعرفته، وخصنا وإياك بخدمته، وجعلنا وإياك من أهل محبته، وتفضل علينا وعليك بتصفية معاملته.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (184,'١٨٣ - حدثني أحمد بن همام حدثني محمد بن الحسين حدثني القاسم بن محمد بن سلمة الصوفي قال: قال لي راهب في بيعة في الشام: همة المحبين الوصول بإرادتهم، وهمة الخائفين الوصول من الخوف إلى مأمنهم، وكلٌّ على خير، وأولئك أنصب أبداناً وأعلى في الخير منصباً.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (185,'١٨٤ - حدثني أبو يعقوب الصريفي إسحاق بن إبراهيم قال: قال فرقد السبخي: قرأت في بعض الكتب: الرحمة قسمها الله للمؤمنين لأنفسهم المؤثرين محبة الله على أهوائهم.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (186,'١٨٥ - حدثني محمد بن صالح بن يحيى العدوي قال: قال لي رجل من العباد: هو يحبهم لا يحب أن يشرك [به] شيء، وليس يحب إلا من يحب ما يحب، والعابدون مستريحون والمحبون في شغل وفي هذا القرآن. فإذا مروا به وقفوا عليه.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (187,'١٨٦ - حدثني محرز بن عون ثنا الفضيل بن عياض قال: كان عامر بن عبد قيس يقول: يحبون الدنيا، والله لا أحب ما لا يحب الله.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (188,'١٨٧ - حدثني يحيى بن عبد الله بن بكير ثنا عبد الله بن لهيعة عن عطاء بن دينار الهذلي عن سعيد بن جيبر في قوله: {إن الذين آمنوا وعملوا الصالحات سيجعل لهم الرحمن وداً}، قال: حباً.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (189,'١٨٨ - حدثني أحمد بن محمد بن حنبل رضي الله عنه ثنا عفان بن مسلم ثنا جعفر بن سليمان عن عمر بن نبهان ثنا قتادة قال: سمعت خليد العصري، وهو في المسجد الجامع، قال: يا إخوتاه، هل منكم أحد إلا يحب أن يلقى حبيبه؟ ألا فأحبوا ربكم وسيروا إليه سيراً جميلاً.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (190,'١٨٩ - حدثني محمد بن الحسين حدثني يحيى بن بسطام الأصغر قال: دخلت مع نفر من أصحابنا على غفيرة العبادة، وكانت قد بكت حتى عميت. فقال بعض أصحابنا لرجل إلى جنبه: ما أشد ⦗٨٠⦘ العمى على من كان بصيراً. فسمعت غفيرة قوله، فقالت: يا عبد الله! عمى القلب والله عن الله تعالى أشد من عمى العين عن الدنيا، وبالله لوددت أن الله تعالى وهب لي كنه محبته وأن لم يبق مني جارحة إلا أخذها.',NULL,'79',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (191,'١٩٠ - ثنا سلمة بن شبيب ثنا سهل بن عاصم عن سجف بن منظور العنبري قال: كانت رابعة تقول إذا جنها الليل بصوت لها حزين: جاء الليل واختلط الظلام، وخلا كل محب بحبيبه، وخلوت بك يا محبوب.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (192,'١٩١ - حدثني محمد بن الحسين حدثني صدقة بن سليمان أبو محمد قال: قالت امرأة من العوابد لأولادها: [تعودوا] حب الله وطاعة الله. فإن المتقين ألفوا الطاعة واستوحشت جوارهم من غيرها، فإن عرض لهم الملعون بمعصية مرت المعصية بهم مختشمة، فهم لها منكرون. وكانت تقول لهم: من أحب شيئاً أوفده على مثله.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (193,'١٩٢ - قال إبراهيم بن الجنيد: وقد أوجب الله تعالى لأهل محبته الصنع والتوفيق في جميع أحوالهم، فأورثهم الغنى وسد عنهم طلب الحاجات إلى الخلق، تأتيهم ألطافٌ من الله من حيث لا يحتسبون، وقام لهم بما يكتفون، ونزه أنفسهم عما سوى ذلك، إكراماً لهم عن فضول الدنيا، وطهارة لقلوبهم من كل دنس، وأمشاهم في طرقات الدنيا طيبين، قد رفع أبصار قلوبهم إليه، فهم ينظرون إليه بتلك القلوب غير محجوبة عنه.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (194,'١٩٣ - حدثني صالح بن عبد الله الترمذي قال: قال سفيان بن عامر رجل من العرب عن عمرو عن الحسن أنه كان يقول: إن المؤمن حبيب ربه، أحب ربه فأحبه ربه، وغضب لربه فغضب له ربه. فإياكم وأذى المؤمن، فإن الله تعالى مؤذ من آذاه. وتلا هذه الآية: {والذين يؤذون المؤمنين والمؤمنات بغير ما اكتسبوا فقد احتملوا بهتاناً وإثماً مبيناً}.',NULL,'81',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (195,'١٩٤ - قال إبراهيم: وفي مثله يقول الشاعر:
INSERT INTO [b26872] VALUES (196,'١٩٥ - حدثني محمد بن يحيى الأزدي حدثني إبراهيم بن عبد الوهاب بن إبراهيم عن أبي عثمان الدمشقي قال: قرب رجل قرباناً في بني إسرائيل فلم يتقلب منه. قال: فشكا ذلك إلى أمه، فقالت: يا بني، لعلك رفعت طرفك إلى السماء ثم رددته ولم تعتبر، قال: نعم. فأستغفر الله.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (197,'١٩٦ - قال: وقال رجل: إلهي أعطيتني ما لم أسألك، فأنا أسألك بجلالك أن تسكن قلبي تعظيم شأنك، وأن تسقيني شربة من حبك.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (198,'١٩٧ - وحدثني محمد بن يحيى ثنا جعفر بن النعمان الرازي ثنا أحمد بن أبي الحواري قال: سمعت محمد بن حفص يذكر عن عروة الرقي قال: حب الله تعالى حب القرآن، وحب رسوله صلى الله عليه وسلم العمل بسنته.',NULL,'82',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (199,'١٩٨ - حدثني إسحاق بن موسى الحمصي ثنا سفيان بن عيينة قال: قال عبد الله بن مسعود: من أحب القرآن فهو يحب الله تعالى.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (200,'١٩٩ - حدثني محمد بن سابق ثنا زائدة بن قدامة عن الأعمش عن إبراهيم عن عبد الرحمن بن يزيد عن عبد الله قال: من أحب القرآن فليبشر.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (201,'٢٠٠ - حدثني محمد بن يحيى حدثني جعفر بن النعمان ثنا أحمد بن أبي الحواري عن أبي جعفر الرقي قال: ما فرح أحد بغير الله إلا بالغفلة عن الله.
INSERT INTO [b26872] VALUES (202,'٢٠١ - حدثني ابن الحسين بن عبيد ثنا إسماعيل بن زياد قال: قدم علينا عبد العزيز بن سليمان عبادان في بعض قدماته، فأتيناه نسلم عليه، فقال لنا: صفوا للمنعم قلوبكم يكفكم المؤن عند هممكم. ثم قال: أرأيت لو خدمت مخلوقاً فأطلت خدمته ألم يكن يرعى ⦗٨٤⦘ لخدمتك؟ فكيف من ينعم عليك وأنت تسيء إلى نفسك، تتقلب في نعمه وتتعرض لغضبه؟ هيهات، همتك همة البطالين، ليس لهذا خلقتم ولا بهذا أمرتكم، الكيس الكيس رحمكم الله. وكان يفطر على ماء البحر.',NULL,'83',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (203,'٢٠٢ - قال إبراهيم: قال بعض الحكماء: أشكر لمن أنعم عليك، وأحسن لمن سترك، فإنه لا زوال للنعمة إذا شكرت ولا قوام لها إذا كفرت، والشكر زيادة في النعم وأمان من الغير.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (204,'٢٠٣ - قال إبراهيم: بلغني أيضاً أن حكيماً من الحكماء قال لابنه: يا بني، إياك والغرة لتواتر النعم عليك، وعليك فيما فرطت بكفره الندم. يا بني، لا تجعل لنفسك هماً سوى الله تعالى، فإنك إن تفعل يجعل الله تعالى لك من أمرك مخرجاً ويرزقك من حيث لا تحتسب.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (205,'٢٠٤ - ثنا عثمان بن زفر التيمي ثنا الربيع بن المنذر الثوري عن أبيه عن الربيع بن خثيم في قوله تعالى: {ومن يتق الله يجعل له مخرجاً} قال: من كل أمر ضاق على الناس.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (206,'٢٠٥ - قال إبراهيم: قال حكيم من الحكماء، لو لم يعذب الله عز وجل على معصيته لكان ينبغي أن لا يعصى لشكر نعمته.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (207,'٢٠٦ - ثنا أحمد بن همام حدثني محمد بن الحسين حدثني القاسم بن محمد بن سلمة العابد الصوفي. حدثني أبو صفوان العابد الشامي الذي كان يكون بمكة، قال: مروا براهب قد حدب من الاجتهاد، فنادوه، فأشرف عليهم كأنه قد نزع منه الروح. فقالوا له: علام تعمل وتنصب نفسك؟ قال: على الطمع والرجاء. قالوا: فهل تعتريك فترة؟ قال: إن ذلك [قد كان] قالوا فمم ذلك؟ قال: عند الإياس والقنوط والمخافة، قال: يعني عن العمل. قالوا: فأدوم ما يكون العبد على العبادة وأنشط إذا كان ماذا؟ قال: إذا استولت المحبة على القلب لم يكن له راحة ولا لذة إلا الاتصال بها.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (208,'٢٠٧ - حدثني محمد بن بحير الثمار ثنا عبد الرحمن بن مهدي ثنا عبيد الله بن شميط عن أبيه قال: لقي رجلٌ المسيح عليه السلام فقال: يا معلم الخير علمني كلمات إذا قلتهن كنت تقياً كما ينبغي. قال: أفعل إن قبلتهن في مؤونة يسيرة: تحب الله بقلبك كله. وتجهد هواك له ونفسك، وترحم على ولد جنسك. قال: يا نبي الله من ولد جنسي؟ ⦗٨٦⦘ قال: ولد آدم. وإذا عملت خيراً فاله عنه، فقد حفظه لك من لا ينساه، ولتكن ذنوبك نصب عينيك.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (209,'٢٠٨ - حدثني محمد بن الحسين ثنا عبيد الله بن محمد التيمي ثنا سهم بن عبد الحميد قال: سمعت الفضل بن عيسى الرقاشي يقول في كلامه: إن دون بلوغ الأماني مفاوز تذوب أنفس العابدين وتنصب لله أبدانهم.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (210,'٢٠٩ - قال: وسمعته يقول يوماً: والله لو جمع للعابدين لذاذات الدنيا بحذافيرها لكان امتهانهم أنفسهم لله بطاعته ألذ وأحلى عندهم من ذلك كله.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (211,'٢١٠ - حدثني محمد بن الحسين ثنا معاذ أبو عون حدثني أبو عمران التمار عن الحسن بن أبي جعفر قال: سمعت عتبة الغلام يقول: من عرف الله تعالى أحبه، ومن أحب الله أطاعه، ومن أطاع الله أكرمه، ومن أكرمه الله أسكنه في جواره. ومن أسكنه في جواره فواطوباه واطوباه واطوباه واطوباه. قال: فلم يزل يقول واطوبه واطوباه حتى خر ساقطاً مغشياً عليه.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (212,'٢١١ - حدثني محمد بن الحسين حدثني سجف بن منظور حدثني سليم النحيف قال: رقبت عتبة ذات ليلة بساحل البحر. فما زاد ليلته تلك حتى أصبح على هذه الكلمات: إن تعذبني فإني لك محب وإن ترحمني فإني لك محب: قال: فلم يزل يرددها ويبكي حتى طلع الفجر.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (213,'٢١٢ - حدثني عبد الله بن عون الأصم ثنا أبو حفص البصري قال: كان خليلٌ لي جاراً لعتبة الغلام. قال: فسمع عتبة ذات ليلة وهو يقول: سبحان جبار السماوات والأرض، إن المحب لفي عناء. فقال: [يا] عتبة! صدقت والله. قال: فغشي عليه.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (214,'٢١٣ - حدثني محمد بن الحسين ثنا عصمة بن سليمان ثنا مسلم بن عرفجة العنبري قال: سمعت عنبسة الخواص يقول: كان عتبة يزروني فربما بات عندي، قال: فبات عندي ليلة فبكى من السحر بكاء شديداً. فلما أصبح قلت له: لقد فزعت قلبي الليلة ببكائك، فمم ذلك يا أخي؟ قال: يا عنبسة، إني والله ذكرت يوم العرض على ⦗٨٨⦘ الله عز وجل. ثم مال ليسقط فاحتضنته. فجعلت أنظر إلى عينيه تتقلبان قد اشتدت حمرتهما. ثم أزبد وجعل يخور، فناديته: عتبة عتبة حبيبي قال: فمكث ملياً لا يجيبني. ثم هدأ، فناديته: عتبة عتبة. فأجابني بصوت خفي: قطع ذكر يوم العرض على الله تعالى أوصال المحبين له.
INSERT INTO [b26872] VALUES (215,'٢١٤ - حدثني عبد الله بن عبيد عن محمد بن الحسين بن عبيد حدثني حكيم بن جعفر حدثني عبد الله بن أبي نوح قال: صحبنا فتى من أهل البصرة في بعض المغازي فقلت له: أتعرف عتبة الغلام؟ قال: نعم، كان أخي وصديقي قال: قلت فحدثني ببعض أمره، قال: ما أحدثك:
INSERT INTO [b26872] VALUES (216,'٢١٥ - ثنا إبراهيم بن ناصح ثنا غوث بن جابر بن غيلان بن منبه الصنعاني حدثني عقيل بن معقل عن وهب بن منبه قال: من كان يحب الله حباً صادقاً به فإنه يكرم حبيبه كرامة بينة، ومن لم يتبع رضوان الله فقد هان عليه غضب الله تعالى، ومن هان عليه غضب الله تعالى فلم يرد كرامة الله. فلا تسل عن حب العبد لله إلا كرامة الله، ولا تسل عن كرامة العبد لله إلا اتباعه رضوان الله فيما أحب وكره.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (217,'٢١٦ - حدثني إسحاق بن إبراهيم قال: سمعت سفيان بن عيينة يقول: من أحب الله تعالى أحب من يحب الله.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (218,'٢١٧ - حدثني حميد بن الربيع ثنا زيد بن الحباب حدثني عبد الرحمن بن ثابت بن ثوبان عن أبيه عن مكحول قال: من أحب رجلاً صالحاً فإنما يحب الله تعالى.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (219,'٢١٨ - حدثني محمد بن هارون ثنا عبد القدوس بن الحجاج الحمصي ثنا صفوان بن عمرو عن يزيد بن ميسرة قال: جادل إبليس ربه عز وجل فقال: يا رب! ألا ترى إلى عبادك كيف يحبونك وكيف يبغضوني وكيف يعصونك ويطيعوني؟ وكذلك ابن آدم يحب الحسنة ⦗٩٠⦘ ويفر منها ويكره الخطية ويقع فيها. قال: وضرب لذلك مثلاً، كمثل رجل غرق في حمأة منتنة إلى حلقه وبين عينيه روضة خضراء، يقول للروضة واها ما أشهاها وأطيبها، ولا يحب أن يفارق حمأته التي هو فيها.',NULL,'89',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (220,'٢١٩ - قال إبراهيم: يقال: من آثر محبة الله على محبة نفسه فقد تعلق بعروة الرضا. وإن من صفة الراضي أن لا يكره ما دبر الله في خلقه، وإن علامة الراضي ترك الشغل بذكر الأسباب.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (221,'٢٢٠ - حدثني محمد بن إدريس بن المنذر الرازي حدثني يوسف بن يعقوب الصفار ثنا عبد المجيد بن عبد العزيز بن أبي رواد حدثني أبي عن طاوس قال: إن من الناس من تجري محبة الله على لسانه، فإن عجب من شيء ذكر الله، وإن فزع من شيء، ذكره تعالى، فتحفظ ملائكة الله عليه خيراً. وإن من الناس من تجري اللعنة على لسانه، فإن عجب من شيء لعن، وإن فزع من شيء لعن، فيكون فزعه وعجبه إلى اللعنة، فتحفظ ملائكة الله عليه شراً.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (222,'٢٢١ - حدثني هشام بن عمار ثنا صدقة بن خالد ثنا خالد بن يزيد المري عن هشام بن الغاز قال: شيع أبا الدرداء حين خرج من حمص إلى دمشق ثلاثة نفر من بني سليم. فقال لأحدهم حين أراد أن يفارقهم. ⦗٩١⦘ أما أنت يا جرير فاذكر الله في السراء يذكرك في الضراء، وإذا عددت الموتى فأتممهم بنفسك، وإياك ودعوة المظلوم. وأما أنت يا أبا سلامة فاستحي الله في السر استحياءك الناس في العلانية، وارع نفسك رعية راعٍ لا يغفل، وداو من نفسك ما الله أعلم به. وأما أنا يا أبا نعيم فأحب الله حتى تلقاه فإن الله تعالى جاعلك حيث تحب، واذكر الله بالخير وذر الشر فإن الله تعالى جاعلك في الخير منهما، وإذا عملت عشر حسنات فاعددهن بحسنة.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (223,'٢٢٢ - حدثني محمد بن الحسين حدثني الصلت بن حكيم حدثني قثم العابد قال: اجتمع أربعة من العباد كلهم يسلك سبيل المحبة.
INSERT INTO [b26872] VALUES (224,'٢٢٣ - حدثني إسماعيل بن محمد بن ماهان حدثني أبو علي الجرجاني بعين زربة، وأثنى عليه خيراً، قال: قالت رقية العابدة: قدمت علينا شعوانة وزوجها مكة، فجعلا يطوفان ويصليان، فإن كل أو أعيا جلس وجلست خلفه. فيقول في جلوسه: أنا العطشان في حبك لا أروى. وتقول هي بكلامها بالفارسية: يا سيدي أنبت لكل داء دواء في الجبال، ودواء المحبين في الجبال لم ينبت.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (225,'٢٢٤ - قال إبراهيم: قال بعضهم: إن الناظرين إلى الله لا إلى غيره ذهبوا بصفوة الدنيا والآخرة. فمن أراد سبب الدخول في محبة الله فلا تكن له ثقة إلا الله ولا غناء إلا به ولا يأمل غيره ولا يرجو إلا إياه، وأن يتخذه وكيلاً في جميع أموره، راضياً بقضائه، يتنقل فيما ⦗٩٣⦘ يتنقل فيه من أمر ربه، موقناً باختيار الله له ونظره له، فهو مسلم راض غير مختار ولا متخير. فورث الله قلوبهم محبته وشوقهم إليه، ورضاهم بما يكفيهم من الدنيا وإن قل، وعلق قلوبهم بذكره. ولما أحسنوا بالله الظن ورث قلوبهم محبته، وأخرج مطامع الخلق من قلوبهم، وجعلهم أولي الألباب. ثم ألهمهم علماً من علمه، عرفهم به ما لم يكونوا يعرفون، وعلمهم ما لم يكونوا يعلمون. فعن الله أخذوا علمهم، بالله تأدبوا وطهرت أخلاقهم. لما آثروا الله ولجؤوا إليه تمت عليهم منه نعمة الدنيا ونعمة الآخرة. فهم المحبوبون في السماوات قبل الأرض المعروفون فيها المذكورون فيها بمن الله عليهم.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (226,'٢٢٥ - ثنا حفص بن عمر الحوضي ثنا شعبة بن الحجاج عن سماك بن حرب عن عياض الأشعري قال: لما نزلت هذه الآية: {فسوف يأتي الله بقومٍ يحبهم ويحبونه}، قال رسول الله صلى الله عليه وسلم: ((هم قوم هذا))، يعني أبا موسى الأشعري.',NULL,'93',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (227,'٢٢٦ - حدثني يحيى بن بكير ثنا عبد الله بن لهيعة عن الحارث بن يزيد عن علي بن رباح قال: قال رسول الله صلى الله عليه وسلم: ((أنكحوا الأشعريين فإنهم في الناس كصور المسك)).',NULL,'94',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (228,'٢٢٧ - حدثني يحيى بن سليمان الجعفي ثنا حفص بن غياث ثنا الكلبي عن أبي صالح عن ابن عباس في قوله تعالى: {فسوف يأتي الله بقوم يحبهم ويحبونه}، قال: أهل القادسية: مذحج وكندة وهمدان ومن كان من أصحابهم.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (229,'٢٢٨ - ثنا أحمد بن يونس ثنا السري بن يحيى قال: سمعت الحسن قرأ هذه الآية: {يا أيها الذين آمنوا من يرتد منكم عن دينه فسوف يأتي الله بقومٍ يحبهم ويحبونه}، قال: هو والله أبو بكر رضي الله عنه وأصحابه.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (230,'٢٢٩ - ثنا يونس بن عبد الأعلى أننا عبد الله بن وهب حدثني عبد الله بن عياش عن أبي صخر عن محمد بن كعب القرظي أن عمر بن عبد العزيز أرسل إليه يوماً، وعمر والي المدينة يومئذ، فقال: يا أبا حمزة، آية ⦗٩٥⦘ أسهرتني البارحة. قال محمد: وما هي أيها الأمير؟ قال: قول الله تعالى: {يا أيها الذين آمنوا من يرتد منكم عن دينه فسوف يأتي الله بقومٍ يحبهم ويحبونه} إلى قوله {لومة لائم} قال محمد: إنما عنى الله تعالى: {يا أيها الذين آمنوا} الولاة من قريش {من يرتد منكم عن دينه} عن الحق {فسوف يأتي الله بقومٍ يحبهم ويحبونه}. وهم أهل اليمن. قال عمر: يا ليتني وإياك منهم، قال: آمين.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (231,'٢٣٠ - حدثني يحيى بن عبد الحميد ثنا معتمر بن سليمان حدثني أبو عمرو مولى بني مخزوم عن قياس بن سعد عن مجاهد: {فسوف يأتي الله بقومٍ يحبهم ويحبونه} قال: هم أهل اليمن لما يأتوا بعد.',NULL,'95',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (232,'٢٣١ - حدثني يحيى بن عبد الحميد ثنا عبد الله بن إدريس عن ليث عن مجاهد {يحبهم ويحبونه} قال: قوم من سبأ.',NULL,'95',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (233,'٢٣٢ - حدثني محمد بن الحسين حدثني الصلت بن حكيم ثنا أبو زيد البحراني قال: دخلت على عابد بالبحرين وإذا هو مكبوب لوجهه يبكي وهو يقول: وعزتك يا حبيبي لقد أذاب قلبي الشوق إلى النظر إلى وجهك الكريم. قال: فأبكاني والله. قال: فلم يلبث بعد هذا ⦗٩٦⦘ إلا أياماً حتى مات. فرأت امرأة من أهله كأنها دخلت الجنة وقد زخرفت. فقالت: لمن زخرفت الجنة؟ قالوا: لولي من أولياء الرحمن قدم البارحة من الدنيا. قالت: فخرج علي وفي يده كوب ياقوت. قالت: فلما رأيته بهت. قال: لم تراعي، إنما هي الجنة للمليك يتحف من أحب من عباده. قالت: قلت: بم نلت هذه المنزلة من الله؟ قال: بمحبته وإيثار محبة الله عز وجل.',NULL,'95',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (234,'٢٣٣ - حدثني عبيد الله بن محمد النيسابوري الوراق ثنا الوليد أبو العباس العابد ثنا الحسن بن السكن عن داود بن أبي هند عن عامر قال: أوحى الله تعالى إلى داود في الزبور: يا داود، إني برحمتي أبحت الاستغفار لجميع خلقي إلا لإبليس وحده فإنه تمرد علي وعصاني فلعنته، ومن حرمته الاستغفار فهو ملعون في الدنيا والآخرة. قال داود: إلهي ما أقدرك! تكرم من شئت وتهين من شئت. قال: يا داود. إلي ملك الملوك أفلا أمن عليك؟ قال: بلى يا رب. قال: إذا أردت أن أكرمك كرامة الدنيا والآخرة فلا تنظر إلى عبد من عبيدي تظن أن لك عليه فضلاً، ولا تمقت أهل دينك على الظن، وكن للفقراء محدثاً وجليساً وأنيساً. وإن أردت أن لا أحجب دعاءك وأن أكون أقرب إليك من أم داود في حال الرضا، فقال داود: إلهي أو ⦗٩٧⦘ لست من جميع خلقك قريباً؟ قال: بلى أنا مع جميع خلقي، ولكن إذا أحببت عبداً من عبيدي ملأت قلبه خوفاً مني وشوقاً إلى لقائي وحرصاً على طاعتي حتى كأنه ينظر إلي، فأنا حينئذ أقرب إليه من أم داود في حال الرضا. وإذا كانت لك حاجة أردت نجحها وتيسيرها فابدأ بي أيسرها لك، تأتيك من حيث لا تعلم نائماً ومستيقظاً، وإن بدأت بغيري أتاك الهم والحزن. قال: فقال داود: إلهي فاعطف قلبي على شعب من محابك. قال: يا داود، إني جعلت قوة طاعتي ومحبة ذكري عند من لم ينسني بلسانه وقلبه، ومن هو يستغفرني صباحاً ومساء، يا داود، إياك والإصرار فإني لا أقيل عثرة المصرين في الدنيا والآخرة، ولا شيء أعظم عندي من إصرار نبي أو عالم. فقال داود: إلهي امح اسمي من أسماء الأنبياء والعلماء.',NULL,'96',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (235,'٢٣٤ - حدثني إسحاق بن إبراهيم القاري قال: قال حكيم من حكماء بني تميم: إن همم الأبرار متصلة بمحبة الرحمن، وقلوبهم تنظر إلى مواضع العز من الآخرة بنور أبصارهم، فأهواؤهم بها متعلقة وأنفسهم إليها متطلعة وأعينهم نحوها طامحة. قد جلا رجاؤهم إياها عنهم كل كربة وهون عليهم كل شدة. تخبرهم دواعي الخير من أنفسهم أن لا راحة لهم دون الخروج منها. سكنت مشاوق الجنة قلوبهم ومخاوف النار أجوافهم، فأهملوا لذلك العيون وأغضوا عن الدنيا لذلك الجفون، وسموا بالقربة إلى معالي العز يطلبونه ويدأبون إلى الله فيه.
INSERT INTO [b26872] VALUES (236,'٢٣٥ - حدثني إسحاق بن إبراهيم الثقفي ثنا رياح القيسي قال: بينما أنا أكلم رابعة بضرب من المواعظ إذا جائتني ابنة لي فقبلتها، فصرخت بي رابعة فقالت: ويلك يا رياحٍ، أما تستحي من الله عز وجل أن يراك في المقام وفي قلبك موضع حب لغيره؟',NULL,'98',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (237,'٢٣٦ - حدثني محمد بن الحسين ثنا أبو معمر عبد الله بن عمرو قال: نظرت رابعة إلى رياح القيسي وهو يقبل صبياً، فقالت: أتحبه؟ قال: نعم. قالت: ما كنت أحسبك أن في قلبك موضعاً فارغاً لمحبة غيره تبارك اسمه. قال: فصرخ رياح وسقط مغشياً عليه. ثم أفاق وهو يمسح العرق عن وجهه وهو يقول: رحمة منه تعالى ألقاها في قلوب العباد للأطفال.',NULL,'98',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (238,'٢٣٧ - قال إبراهيم: أنشدني نصر بن جابر القاري من قول بعض البصريين:
INSERT INTO [b26872] VALUES (239,'٢٣٨ - حدثني إسحاق بن إبراهيم قال: أصاب الفضيل بن عياض عسر البول، فقال: يا رب بحبي إياك لما سهلته. قال: فبال على المكان.
INSERT INTO [b26872] VALUES (240,'٢٣٩ - ثنا يحيى بن بكير حدثني عبد الله بن لهيعة عن أبي يونس سليم بن جبير مولى أبي هريرة قال: سمعت أبا هريرة يحدث عن النبي صلى الله عليه وسلم أنه قال: ((قال الله عز وجل: أنا عند ظن عبدي بي، إن ظن خيراً فخيرٌ وإن ظن شراً فله)).',NULL,'100',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (241,'٢٤٠ - ثنا ميمون بن زيد البصري ثنا يونس بن عبيد قال: قال الحسن: والله ما نزلوا إلا على قدر ظنونهم بالله.
INSERT INTO [b26872] VALUES (242,'٢٤١ - حدثني يعقوب بن كعب الحلبي قال: سمعت يوسف بن أسباط يقول: سمعت سفيان يقول: قال: أحسنوا بالله الظن.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (243,'٢٤٢ - حدثني موسى بن إسماعيل ثنا حماد بن سلمة عن حميد عن الحسن قال: {إني ظننت أني ملاق حسابية} قال: إن المؤمن أحسن بربه الظن فأحسن العمل، وإن المنافق أساء بربه الظن فأساء العمل.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (244,'٢٤٣ - ثنا محمد بن حميد الرازي ثنا مهران بن أبي عمر عن سفيان عن جابر عن مجاهد: {إني ظننت أني ملاق حسابية} قال: كل ⦗١٠٢⦘ ظن في القرآن: إني ظننت: إني علمت. قال: ما كان من ظن للآخرة فهو علم.',NULL,'101',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (245,'٢٤٤ - ثنا إسماعيل بن إبراهيم بن بسام البلخي ثنا بقية بن الوليد حدثني أبو بكر بن عبد الله بن أبي مريم حدثني الهيثم بن مالك الطائي قال: كان رسول الله صلى الله عليه وسلم يدعو بهؤلاء الدعوات: ((اللهم اجعل حبك أحب الأشياء إلي، واقطع عنا حاجات الدنيا بالشوق إلى لقائك، وإذا قرت أعين أهل الدنيا بدنياهم فأقر عيني بعبادتك)).',NULL,'102',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (246,'٢٤٥ - حدثني عمر بن محمد بن عبد الحكم النسائي ثنا أحمد بن أبي الحواري عن محمد بن يوسف الفريابي في قوله: {سأصرف عن آياتي}، قال: أمنع قلوبهم من التفكر في أمري.',NULL,'102',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (247,'٢٤٦ - حدثني الحسن بن سعيد الجرجاني قال: سمعت أبا مريم الصلت بن حكيم يقول: كانت امرأة في بني إسرائيل تتعبد، وكانت تفطر كل سبت. فبينما هي ذات يوم قد وضعت إفطارها بين يديها جعلت تقول: محب يحب حبيبه يتشاغل بالأكل عن خدمة حبيبه، يوشك أن يقدم عليه رسول حبه وهو متشاغل بأكله عن خدمته، فلا تقر عينه في لقائه. فمكثت بذلك سبعين سبتاً لا تفطر. ثم وضعت إفطارها بين يديها، وجعلت تقوم مثلما كانت تقول. فإذا بشاب ناحية البيت جميل الوجه طيب الريح يقول لها: السلام عليك يا حبيبة الله أو يا ولية الله. قالت: وعليك السلام، من أنت؟ قال: أنا ملك الموت. قالت: يا ملك الموت، أتأذن لي أن أسجد سجدة أناجي فيها ربي عز وجل، فإذا رأيتني قد فعلت ذلك قبضت روحي؟ قال: لك ذلك. قال: فنحت إفطارها ثم وثبت. فقبض روحها في اجتهادها ذلك.',NULL,'103',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (248,'٢٤٧ - حدثني محمد بن الحسين قال: سألت بعض العلماء فقلت: صف لي شيئاً من أخلاق المحبين لله تعالى وكيف مقامهم في الدنيا. فقال: اعلم يا أخي أن المحب لله فوق الخائف، على أن المحب لله لم يزل على ظهر الخوف لله مقدار ذرة فما دونها. والمحب لا يسقى كأس المحبة إلا بعد أن ينضح قلبه من كأس الخوف، وإنما خوف النار عند خوف ⦗١٠٤⦘ الفوت بمنزلة نقطة نقطت في بحر لجي. ولا أعلم شيئاً أحمد للقلوب من خوف الغرق. فمن أحب الله عز وجل فقد غرق في بحر الحزن حتى وصل إلى قرار الكمد. فالمحب لله لا تعظم عنده جنة ولا نار، لأنه ليس شيء أعظم من الله تعالى.
INSERT INTO [b26872] VALUES (249,'٢٤٨ - حدثني محمد بن منصور بن داود الطوسي ثنا عبد الله بن عيسى البصري عن أبيه عن مسمع بن عاصم عن ثور بن يزيد الشامي قال: نظر الله تعالى إلى داود عليه السلام، فإذا هو وحداني منتبذ. فقال: مالك وحدانياً؟ قال: عاديت الخلق فيك. قال: أو ما علمت أن محبتي أن تعطف على عبادي وتأخذ عليهم بالفضل؟ هنالك أكتبك من أوليائي. يا داود ومن أحبائي فإذا كنت كذلك كتبتك في ديوان أهل المحبة وكنت مني وكنت منك، أجيبك من غير أن تسألني، وأباهي بك حملة عرشي، أرفع الحجب بيني وبينك تنظر إلي ببصر قلبك، لا أحجبك ما دمت متمسكاً بطاعتي وكنت مني وكنت منك. ولا تنظر إلى عبادي نظرة جفاء ولا قسوة فأهلكهم، فإذا أنت قد بطل أجرك. احفظ عني كلمات ثلاث خصال:
(١) خالص حبيبي مخالصة.
INSERT INTO [b26872] VALUES (250,'٢٤٩ - حدثني إسحاق بن داود بن صبيح البلخي ثنا الحسن بن الربيع ثنا عمرو بن أزهر ثنا أبو عبد الرحمن الدمشقي عن عطاء عن عائشة قالت في هذه الآية: {قل إن كنتم تحبون الله فاتبعوني يحببكم الله}، قالت: على البر والتقوى والتواضع وذل النفس.',NULL,'108',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (251,'٢٥٠ - قال إبراهيم: يقال: المنازل التي يعمل فيها أهل الصدق ثلاث منازل:
(١) منزلة الخوف.
(٢) ومنزلة الشوق إلى نعيم الجنة.
(٣) والمنزلة الثالثة شوق العبد إلى ربه تعالى وما يغلب على قلبه وحبه وذكره، فلا يكون لهم همٌّ غيره، والفكرة في أمره، لما ⦗١٠٩⦘ يذوق في ذلك من اللذة والحلاوة، ومع ما يرث من التعظيم لله والعلم به.',NULL,'108',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (252,'٢٥١ - قال إبراهيم: وأنشدني رجل في الحب:
INSERT INTO [b26872] VALUES (253,'٢٥٢ - حدثني علي بن عيسى حدثني محمد بن الحسين ثنا محمد بن عبد العزيز بن سلمان قال: سمعت أبي يقول: كان حسان بن أبي سنان إذا بلغه شيء من المعاصي انتفض حتى يسقط. قال: ثم يقول: أتعصى بفنون من المعاصي وتنعم بفنون من النعم؟ لا يفوتك أحد بطول هربه، ولا يعجزك عبد بقوته، أنت القادر {القاهر فوق عباده}. قال: وكان يقول: بمحبتك التي مننت بها عليهم. فبها نالوا من طاعتك ما يرجون به رضوانك.
INSERT INTO [b26872] VALUES (254,'٢٥٣ - قال إبراهيم: يقال مهر المحبة فطام النفس من حب الشهوات، وإيثار حب الله على محبتك لنفسك.',NULL,'110',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (255,'٢٥٤ - حدثني حرملة بن يحيى أننا عبد الله بن وهب حدثني معاوية بن صالح عن أبي الزاهرية قال: قال داود عليه السلام: اللهم اجعلني من أحبائك، فإنك إذا أحببت عبداً غفرت ذنبه وإن كان عظيماً، وقبلت عمله وإن كان يسيراً.',NULL,'110',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (256,'٢٥٥ - حدثني إبراهيم بن عبد الله بن حاتم أننا حجاج بن محمد عن ابن جريج عن مجاهد: {فسوف يأتي الله بقومٍ يحبهم ويحبونه}: ناس من أهل اليمن.',NULL,'110',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (257,'٢٥٦ - حدثني عمرو بن أيوب أبو حفص النسائي حدثني منصور بن محمد البلخي قال: سمعت أحمد بن مخلد الخراساني يقول: قال الله تبارك وتعالى: ألا قد طال شوق الأبرار إلى لقائي، وإني إليهم لأشد شوقاً، وما شوق المشتاقين إلي لا بفضل شوقي ⦗١١١⦘ إليهم. ألا ومن طلبني وجدني، ومن طلب غيري لم يجدني ومن ذا الذي أقبل إلي فلم أقبل إليه؟ ومن ذا الذي توكل علي فلم أكفه؟ ومن ذا الذي دعاني فلم أجبه؟ ومن ذا الذي سألني فلم أعطه؟',NULL,'110',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (258,'٢٥٧ - ثنا أبو حفص عمرو بن محمد بن الحكم النسائي حدثني أحمد بن أبي الحواري قال: دخلت على أبي سليمان الدارادني يوماً وهو يبكي فقلت له: ما يبكيك؟ فقال لي: يا أحمد، إنه إذا جن الليل على المحبين افترشوا أقدامهم. ودموعهم تجري على خدودهم، وقد أشرف الجليل عليهم فنادى: يا جبريل بعيني من تلذذ بكلامي واستراح إلى مناجاتي، وإني لمطلع عليهم: أسمع حنينهم وأرى بكاءهم، فناد فيهم يا جبريل: ما هذا الجزع الذي أراه فيكم؟ هل أخبركم عني مخبر: أن حبيباً يعذب أحباءه بالنار؟ أم هل يجمل بي أن أبيت أقواماً وعند البيات أجدهم لي وقوفاً، فإذا جنهم الليل تملقوني؟ فبي حلفت لأجعلن هديتي إياهم لو قد وردوا علي القيامة أن أكشف لهم عن وجهي الكريم أنظر إليهم وينظرون إلي.',NULL,'111',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (259,'٢٥٨ - ثنا عمر بن محمد أيضاً ثنا أحمد بن أبي الحواري ثنا زكريا قال: قال أبو عبيدة الخواص: واشوقاه إلى من يراني ولا أراه!',NULL,'112',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (260,'٢٥٩ - ثنا عمر بن محمد بن عبد الحكم ثنا أحمد بن أبي الحواري ثنا إبراهيم بن خال بنان عن أبي بكر المحلمي قال: نمت في سجودي، فرأيته في منامي، فسمعته يقول: ملائكتي انظروا إلي عبدي: بدنه في طاعتي وروحه عندي. قال: فانتبهت فقلت: أنت قرة عيني في نومي وقرة عيني في يقظتي.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (261,'٢٦٠ - نبأني عمر بن محمد ثنا أحمد بن أبي الحواري ثنا محمود عمن أخبره قال: رأيت بالبصرة رجلاً كثير الدؤوب قليل الطعام جيد البدن، فقلت له: أراك كثير الدؤوب قليل الطعام جيد البدن؟ قال: ذلك من فرحي بحب الله تعالى، إذا ذكرت أنه ربي وأني عبده لم يمتنع بدني أن يصلح.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (262,'٢٦١ - حدثني عمر بن محمد حدثني عبد الله بن خبيق قال: سمعت أبا عبد الله اليماني يقول: من أراد أن يشرب كأساً من حب الله تعالى فليدع شهوة لا تضره.',NULL,'112',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (263,'٢٦٢ - وحدثني عمرو بن محمد حدثني عبد الله بن خبيق حدثني عبد الله بن عبد الرحمن قال: قال إبليس: يا رب خلقت خلقاً يحبونك ويبغضوني، وهم في ذلك يعصونك ويطيعوني. فقال تعالى: قد شكرتهم بحبهم إياي وغفرت لهم ببغضهم لك.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (264,'٢٦٣ - حدثني صالح بن عمران ثنا أحمد بن غسان قال: سمعت أحمد بن عطاء يقول: يحنون لله حنين الواله، ويشتاقون إليه شوق من لا صبر لهم عنه، ينادونه بأصوات محزونة من قلوب محترقة قد أنضجها الحزن وقلقلها الحذر. وربما قال: يا رب، بلغنا مبلغ من انقطع قلبه إليك فهو مشغول بك عمن سواك، ليس له طلب في جميع الدارين غيرك، ولا يريد غيرك ولا تطيب النفس إلا بك، والله إنهم يسمعون بذكرك ممن ليس هو أهل لذكرك فتكاد أنفسهم تخرج فرحاً من شدة شوقهم إليك. يا قرة عين العابدين! أسق قلوبنا بكأس ميراث حبك الذي سقيت به أهل مصافاتك وأهل مخالطتك، والطف لنا بكمال الانقطاع إليك، وقرر في قلوبنا كمال معرفتك التي يدرك بها حبك، واجعل مزيدك من فضلك إلينا واصلاً، واجعلنا قابلين لمزيدك، وأخرج من قلوبنا كل ما عدل بنا عنك وباعدنا منك، وصفنا من كل داخلٍ كدر علينا مصافاتك، وأعنا بكل معونة نبلغ بها رضا نفسك.',NULL,'113',NULL,NULL,'0');
INSERT INTO [b26872] VALUES (265,'٢٦٤ - قال إبراهيم: قال وهب بن منبه: إن لله عباداً قالوا: لا نعبده خوفاً ولا رجاءً ولكن نعبده حباً، فإن الحب يخرج من قلوبنا ما لا يخرج الخوف والرجاء.
