INSERT INTO [b26877] VALUES (1,'جزء فيه من حديث
INSERT INTO [b26877] VALUES (2,'جزء فيه من حديث أبي الحسين الكلابي عن شيوخه
INSERT INTO [b26877] VALUES (3,'٢- وبإسناده قال حدثنا مالك بن أنس، حدثني الزهري، عن ⦗٣٦⦘ أنس بن مالك أن النبي صلى الله عليه وسلم أتى بلبن قد شيب بماء، وعن يمينه أعرابي، وعن يساره أبو بكر، فشرب، ثم أعطى الأعرابي وقال: ((الأيمن فالأيمن)) .',NULL,'34',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (4,'٣- وبإسناده حدثنا مالك بن أنس، حدثني الزهري، عن السائب ⦗٣٩⦘ بن يزيد، أنه رأى عمر بن الخطاب رضي الله عنه يضرب المنكدر في الصلاة بعد العصر.',NULL,'37',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (5,'٤- وبإسناده حدثنا مالك بن أنس، حدثني الزهري، عن ⦗٤٣⦘ أبي إدريس الخولاني، عن أبي هريرة أن رسول الله صلى الله عليه وسلم قال: ((من توضأ فليستنثر، ومن استجمر فليوتر)) .',NULL,'40',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (6,'٥- وبإسناده حدثنا مالك بن أنس، حدثني نافع، عن ابن عمر، ⦗٤٤⦘ أن رسول الله صلى الله عليه وسلم قال: ((من اشترى نخلاً قد أبرت فثمرتها للبائع، إلا أن يشترط المبتاع)) .',NULL,'43',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (7,'٦- وبإسناده حدثنا مالك بن أنس، حدثني نافع، أن عمر بن الخطاب رضي الله عنه كتب إلى عماله: إن أهم أمركم إلي الصلاة، فمن ⦗٤٦⦘ حفظها وحافظ عليها حفظ دينه، ومن ضيعها فهو لما سواها أضيع، ثم كتب: إن صلاة الظهر إذا كان الفيء ذراعين إلى أن يكون ظل كل ⦗٤٧⦘ شيء مثله، والعصر والشمس بيضاء نقية قدر ما يسير الراكب فرسخين أو ثلاثة، والمغرب إذا غابت الشمس، والعشاء إذا غاب الشفق إلى ثلث الليل، فمن نام فلا نامتا عيناه، فمن نام فلا نامتا عيناه، فمن نام فلا نامتنا عيناه، والصبح والنجوم بادية مشتبكة.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (8,'٧- وبإسناده حدثنا مالك بن أنس، حدثنا سمي مولى أبي بكر ⦗٥١⦘ بن هشام، عن أبي صالح، عن أبي هريرة، أن رسول الله صلى الله عليه وسلم قال: ((السفر قطعة من العذاب، يمنع أحدكم نومه وطعامه وشرابه، فإذا قضى أحدكم نهمته من سفره؛ فليعجل إلى أهله)) .',NULL,'47',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (9,'٨- وبه حدثنا مالك بن أنس، حدثنا إسحاق بن عبد الله بن أبي ⦗٥٣⦘ طلحة، عن أنس بن مالك، أن رسول الله صلى الله عليه وسلم قال: ((الرؤيا الحسنة من الرجل الصالح جزءٌ من ستةٍ وأربعين جزءاً من النبوة)) .',NULL,'52',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (10,'٩- وبه حدثنا مالك بن أنس، عن صفوان بن سليم، عن عطاء ⦗٥٤⦘ بن يسار، أن رجلاً قال لرسول الله صلى الله عليه وسلم: أكذب أهلي؟ قال: ((لا خير في الكذب)) ، قال: أعِدُها يا رسول الله؟ قال: ((لا جناح عليك)) .',NULL,'53',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (11,'١٠- وبه حدثنا مالك بن أنس، حدثني صفوان بن سليم، عن ⦗٥٧⦘ سعيد بن سلمة من آل بني الأزرق، أن المغيرة بن أبي بردة، وهو من بني عبد الدار، حدثه أنه سمع أبا هريرة يقول: جاء رجل إلى رسول الله صلى الله عليه وسلم فقال: يا رسول الله؛ إنا نركب البحر، ونحمل معنا القليل من الماء، فإن توضأنا به عطشنا، أفنتوضأ من ماء البحر؟ فقال رسول الله صلى الله عليه وسلم: ((هو الطهور ماؤه، الحل ميتته)) .',NULL,'54',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (12,'١١- وبه حدثنا مالك بن أنس، حدثني أبو الزناد، عن الأعرج، ⦗٥٩⦘ عن أبي هريرة أن رسول الله صلى الله عليه وسلم قال: ((إذا اشتد الحر فأبردوا بالصلاة، فإن شدة الحر من فيح جهنم)) .',NULL,'57',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (13,'ومن حديثه عن سعيد بن العزيز، عن أبي نعيم الحلبي وغيره، أخبرنا عبد الوهاب الكلابي:
INSERT INTO [b26877] VALUES (14,'١٣- وبه حدثنا أبو نعيم قال: سألت مالكاً عن امرأةٍ وجب ⦗٦٢⦘ عليها الحج، وليس يأذن لها زوجها؟
INSERT INTO [b26877] VALUES (15,'١٤- وبه حدثنا أبو نعيم قال: سألت مالكاً عن امرأة وجب ⦗٦٤⦘ عليها الحج، وليس لها محرم؟
INSERT INTO [b26877] VALUES (16,'١٥- وأخبرنا سعيد، ثنا محمد بن إبراهيم بن أبي سكينة، ثنا ⦗٦٧⦘ مالك بن أنس، عن الزهري، عن سعيد بن المسيب قال: قال رسول الله ⦗٦٨⦘ صلى الله عليه وسلم: ((لا يغلق الرهن)) . قال محمد: قلت لمالك: ما تفسير لا يغلق الرهن؟ قال: هو الرجل يكون لرجل عليه حق، فيرهنه رهناً، فيقول: إن جئت بحقك إلى كذا وكذا، وإلا فهو لك بمالك علي، قال مالك: فهو الذي نهى عنه رسول الله صلى الله عليه وسلم، وهو الذي لا يجوز.',NULL,'64',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (17,'١٦- وأخبرنا سعيد، ثنا محمد بن إبراهيم، ثنا شريك بن عبد الله، عن أبي إسحاق السبيعي قال: قال كعب: من لبس ثوباً بأربعة دراهم تواضعاً لله؛ أدخله الله الجنة.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (18,'١٧- وأخبرنا سعيد، ثنا محمد بن إبراهيم بن أبي سكينة، ثنا ⦗٧٠⦘ ابن المبارك، عن يحيى بن عبيد الله، عن أبيه، عن أبي هريرة قال: قال رسول الله صلى الله عليه وسلم: ((ما ينتظر أحدكم إلا غنىً مطغياً، أو فقراً منسياً، أو هرماً مفنداً، أو مرضاً مفسداً، أو موتاً مجهزاً، أو الدجال، فشر غائبٍ ينتظر، أو الساعة، والساعة أدهى وأمر)) .',NULL,'68',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (19,'١٨- وأخبرنا سعيد، ثنا أبو نعيم عبيد بن هشام، ثنا إسماعيل ⦗٧١⦘ بن عياش ثنا شرحبيل بن مسلم الخولاني وأسد بن وداعة الكلبي ومحمد بن زياد الألهاني عن أبي أمامة الباهلي قال: قال رسول الله صلى الله عليه وسلم: ((ألا إنه لا نبي بعدي، ولا أمة بعدكم، ألا فصلوا خمسكم، وصوموا شهركم، وصلوا أرحامكم، وأدوا زكاة أموالكم، طيبةً بها أنفسكم؛ تدخلوا جنة ربكم)) .',NULL,'70',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (20,'١٩- وأخبرنا سعيد، ثنا أبو نعيم عبيد بن هشام، حدثنا ابن المبارك، عن مالك بن أنس، عن محمد بن المنكدر، عن أنس بن مالك، ⦗٧٣⦘ أن رسول الله صلى الله عليه وسلم قال: ((من قعد إلى قينةٍ ليستمع منها، صب في أذنيه الآنك يوم القيامة)) .',NULL,'72',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (21,'٢٠- وأخبرنا سعيد، ثنا محمد بن أبي سكينة، حدثنا ⦗٧٦⦘ أبو الأحوص، عن أبي إسحاق، عن عمرو بن ميمون، عن معاذ بن جبل قال: كنت ردف رسول الله صلى الله عليه وسلم على حمار يقال له: عفير، فقال لي: ((يا معاذ، تدري ما حق الله على عباده وما حق العباد على الله؟ قال: قلت: الله ورسوله أعلم. قال: ((حق الله على عباده أن لا يشركوا به شيئاً، وحق العباد على الله أن لا يعذب من لم يشرك به شيئاً)) ، قال: قلت يا رسول الله، أفلا أبشر الناس؟ قال: ((لا تبشرهم؛ فيتكلوا)) .',NULL,'73',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (22,'٢١- وأخبرنا سعيد قال: سمعت البسري يقول: الحلاوة تسكن القلوب.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (23,'٢٢- وأخبرنا سعيد، ثنا أحمد بن شيبان، ثنا سفيان بن عيينة، عن ⦗٧٨⦘ الزهري، عن أنس بن مالك، أن النبي صلى الله عليه وسلم كان يقطع في ربع دينار فصاعداً.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (24,'٢٣- وأخبرنا سعيد قال: سعت قاسماً الجوعي يقول: سمعت ⦗٧٩⦘ مسلم بن زيادٍ يقول: من سالم الناس سلم، ومن سالم الناس نجح، ومن طلب الفضل من غير أهله ندم.',NULL,'78',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (25,'٢٤- وأخبرنا سعيد، ثنا قاسم بن عثمان الجوعي، ثنا عبد الله بن نافع المدني، ثنا مالك بن أنس، عن نافع، عن ابن عمر قال: قال رسول الله صلى الله عليه وسلم: ((ما بين قبري ومنبري روضة من رياض الجنة)) .',NULL,'79',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (26,'٢٥- وأخبرنا سعيد قال: سمعت قاسماً الجوعي يقول: سمعت ⦗٨٨⦘ المضاء بن عيسى يقول: من رجا شيئاً طلبه، ومن خاف شيئاً هرب منه، ومن أحب شيئاً آثره على غيره.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (27,'٢٦- وأخبرنا سعيد، ثنا أبو نعيم عبيد بن هشام، حدثنا سليمان ⦗٩٠⦘ بن حيان، عن أبي همام قال: قال أنس: خدمت النبي صلى الله عليه وسلم وأنا ابن ثمان سنين، وقبض النبي صلى الله عليه وسلم وأنا ابن ثمان عشرة سنة، فما قال لي لشيء صنعته: لم صنعته؟ ولا قال لشيءٍ لم أصنعه: لم لم تصنعه؟ قال: وقال لي في مرضه: ((إني أوصيك بوصية فاحفظها؛ أكثر الوضوء يزد في عمرك، ولا تزل طاهراً، ولا تبيتن إلا على طهرٍ، فإن مت مت شهيداً، وأكثر صلاة الليل والنهار تحبك الحفظة، وصل صلاة الضحى؛ فإنها صلاة الأوابين، وإذا خرجت من بيتك فسلم على من لقيت من المسلمين، تزد في حسناتك، وإذا دخلت على أهلك فسلم عليهم، يزد في بركاتك، ووقر كبير المؤمنين، وارحم صغيرهم؛ تكن معي)) . وضم بين أصابعه.',NULL,'88',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (28,'٢٧- وأخبرنا سعيد قال: سمعت قاسماً الجوعي يقول: ثنا ابن أبي السائب، عن أبيه قال: رأيت رسول الله صلى الله عليه وسلم في المنام، فقلت: يا رسول الله، أبايعك على أن أدخل الجنة؟ قال: ((نعم)) ؛ فمد يده، فبايعته، فما رأيت بناناً قط أشد بياضاً ولا ألين من كف رسول الله صلى الله عليه وسلم.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (29,'٢٨- وأخبرنا سعيد قال: سمعت قاسماً الجوعي يقول: أصل الدين: الورع، وأفضل العبادة: مكابدة الليل، وأفضل طرق الجنة: سلامة الصدر.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b26877] VALUES (30,'٢٩- قال أبو الحسين النرسي: قال لنا أبو الحسين عبد الوهاب بن الحسن بن الوليد الدمشقي: ولدت في ذي القعدة سنة ست وثلاث مائة.
