INSERT INTO [b2700] VALUES (99,'<span data-type="title" id=toc-13>المبحث الرابع: في ذكر تلاميذه الذين رووا عنه المغازي وأبرزهم</span>
<span data-type="title" id=toc-14>المطلب الأول: في ذكر تلاميذه الذين رووا عنه روايات قليلة في المغازي، مرتبين على حروف المعجم</span>.
