-- Insert data for b27086
INSERT INTO [b27086] VALUES (1,'حديث السكن بن جميع

أبو محمد الحسين بن محمد بن أحمد بن جميع
٤٣٧ هـ

دراسة وتحقيق
دكتور عمر عبد السلام تدمري

مؤسسة الرسالة - دار الإيمان

الطبعة الثانية
١٤٠٧ هـ - ١٩٨٧ م

[طبع في آخر معجم الشيوخ لوالده محمد بن أحمد بن جميع الصيداوي]',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b27086] VALUES (2,'بسم الله الرحمن الرحيم
١- أخبرنا الشيخ أبو الحسن علي بن الحسن بن الحسين الموازيني، رضي الله عنه، قراءة عليه وأنا أسمع، في رجب من سنة اثنين وخمسمائة، قال: أخبرنا الشيخ أبو محمد الحسين بن محمد بن أحمد بن جميع، ويعرف بسكن، إجازة، قال: حدثني جدي أبو بكر أحمد بن محمد بن أحمد بن عبد الرحمن بن يحيى بن جميع الغساني، رحمه الله، قال: حدثنا محمد بن عبدان أبو عبيد الله بمكة قال: حدثنا أبو مصعب أحمد بن أبي بكر الزهري قال: حدثنا مالك بن أنس رحمه الله، عن ابن شهاب،
عن أنس بن مالك أنه قال: كنا نصلي العصر ثم يذهب الذاهب إلى قباء، فيأتيهم والشمس مرتفعة.',NULL,'417',NULL,NULL,'0');
INSERT INTO [b27086] VALUES (3,'٢- وحدثني جدي أحمد قال: حدثنا محمد بن المعافى قال: حدثنا ⦗٤١٨⦘ هشام بن عمار، (قال: حدثنا مالك بن أنس) قال: حدثنا إسحاق بن عبد الله بن أبي طلحة،
عن أنس بن مالك، أن رسول الله صلى الله عليه وسلم قال: ((الرؤيا الحسنة من الرجل الصالح جزء من ستة وأربعين جزءاً من النبوة)) .',NULL,'417',NULL,NULL,'0');
INSERT INTO [b27086] VALUES (4,'٣- حدثني والدي أبو الحسين محمد بن أحمد بن جميع، رحمه الله، قال: حدثنا أبو الحسن محمد بن صبح البزاز بصيدا، قال: حدثنا أحمد بن عبد الواحد بن سليمان أبو جعفر القنطري، قال: حدثنا العسقلاني أبو عمران النصيبي موسى بن أيوب عن ابن المبارك، عن أبي بكر بن أبي مريم، عن ضمرة بن حبيب،
عن شداد بن أوس، قال النبي صلى الله عليه وسلم: ((الكيس من دان نفسه وعمل لما بعد الموت، والعاجز من أتبع نفسه (هواها) وتمنى على الله عز وجل)) .',NULL,'418',NULL,NULL,'0');
INSERT INTO [b27086] VALUES (5,'٤- وأخبرني والدي أيضاً قال: حدثنا أبو عبد الله الحسين بن إسماعيل المحاملي ببغداد، قال: حدثنا عبيد بن محمد بن القاسم الوراق قال: حدثنا موسى بن هلال العبدي، عن عبيد الله بن عمر، عن نافع،
عن ابن عمر، قال رسول الله صلى الله عليه وسلم: ((من زار قبري وجبت له شفاعتي)) .',NULL,'419',NULL,NULL,'0');
INSERT INTO [b27086] VALUES (6,'٥- أخبرنا أبو طاهر محمد بن سليمان بن ذكوان البعلبكي بصيدا سنة أربع وخمسين وثلاثمائة قال: حدثنا أبو عبد الله أحمد بن محمد بن يحيى بن حمزة قال: حدثنا عمرو بن هاشم البيروتي قال: سمعت الأوزاعي قال: حدثني إسماعيل بن عبيد الله، عن علي بن عبد الله بن عباس،
عن أبيه عبد الله بن عباس قال: ((عرض على رسول الله صلى الله عليه وسلم ما هو مفتوح على أمته بعده كَفراً كَفراً، فسر بذلك، فأنزل الله عز وجل: {ولسوف يعطيك ربك فترضى} . وأعطاه الله عز وجل في الجنة ألف قصر، في كل قصر ما ينبغي له من الأزواج والخدم)) .',NULL,'419',NULL,NULL,'0');
INSERT INTO [b27086] VALUES (7,'٦- أخبرنا أبو طاهر قال: حدثنا أحمد بن محمد بن يحيى قال: حدثنا أبو اليمان قال: حدثنا سعيد بن سنان، عن أبي الزاهرية، عن كثير بن مرة، عن عبد الله بن عمر،
عن النبي صلى الله عليه وسلم أنه كان يقول: ((من لبس الصوف، وانتعل المخصوف، وركب حماره، وحلب شاته، وأكل مع عياله، فقد نحى الله عنه الكبر. أنا عبد ابن عبد، أجلس كجلسة العبد، وآكل أكلة العبد -وذلك أن النبي صلى الله عليه وسلم لم يطرق طعاماً قط إلا وهو حاب على ركبتيه- إن الله عز وجل قد أوحى إلي أن تواضعوا ولا يبغي أحدكم على أحد. إن يد الله عز وجل مبسوطة على خلقه، فمن رفع نفسه وضعه الله عز وجل، ومن وضع نفسه رفعه الله عز وجل، ولا يمسي امرؤ على الأرض يبغي بها سلطان الله عز وجل إلا أكبه الله عز وجل)) .
وفي غير هذا الحديث:
((وسلطان الله عز وجل في الأرض كتابه وسنة نبيه، صلى الله عليه وسلم تسليماً)) .
وروى ذلك عن ابن عباس رحمه الله.',NULL,'420',NULL,NULL,'0');
INSERT INTO [b27086] VALUES (8,'٧- أخبرنا أبو صادق محمد بن نصر الطبري، بصيدا، سنة تسع وخمسين وثلاثمائة قال: حدثنا أبو عبد الله إبراهيم بن محمد بن عرفة ⦗٤٢١⦘ النحوي، قال: حدثنا شعيب بن أيوب الصريفيني قال: حدثنا مصعب بن المقدام، عن داود الطائي، عن الأعمش، عن يحيى بن وثاب، عن ابن عمر قال:
قال رسول الله صلى الله عليه وسلم: ((المؤمن الذي يخالط الناس ويصبر على أذاهم خير من المؤمن الذي لا يخالط الناس ولا يصبر على أذاهم)) .',NULL,'420',NULL,NULL,'0');
INSERT INTO [b27086] VALUES (9,'٨- أخبرنا أبو بكر أحمد بن محمد الكوفي، بصيدا، في صفر سنة تسع وخمسين وثلاثمائة قال: حدثنا أبو الذهل المورع بن عبد الله بن صفوة قال: حدثنا داود بن معاذ، قال: حدثنا سليمان بن عمرو، عن أبي حازم المدائني: عن سهل بن سعد الساعدي قال:
قال رسول الله صلى الله عليه وسلم: ((من اغتاب أخاه المسلم ولم يستغفر الله له، فإن ذلك كفارة ذلك)) .
يقول الحسين بن محمد بن أحمد بن محمد بن أحمد بن عبد الرحمن بن يحيى بن جميع الغساني: إني اخترت هذه الأحاديث التي كتبتها بخطي وجميع سماعاتي من أبي وجدي، رحمهما الله، ومن جميع شيوخي، رحمهم الله، إلى آخر الجزء، والحمد لله وحده.',NULL,'421',NULL,NULL,'0');
