INSERT INTO [b29741] VALUES (162,'٣٨٠٢٢ - و ٣٨٠٣٣ و ٣٨٠٥٠ و ٣٨٠٨٣ و ٣٨١٦٣ و ٣٨١٧٠ و ٣٨١٧٤ و ٣٨٢٠٤ و ٣٨٢١٩ و ٣٨٢٥٢ و ٣٨٢٥٦ و ٣٨٢٧٩ و ٣٨٢٩٦ و ٣٨٣٣٣ و ٣٨٣٦٠ و ٣٨٣٧٠ و ٣٨٣٨٤ و ٣٨٣٣٦ و ٣٨٣٩٤)، و (تخ ١/ ٢٣ و ٥٣ و ٥٧ و ٦٧ و ٦٨ و ٧٠ و ٨٥ و ١١٧ و ١١٨ و ١٧٠ و ١٧٨ و ١٨٠ و ٢٠٢ و ٢٠٨ و ٢١٣ و ٢١٦ و ٢٢٥ و ٢٤١ و ٢٦٢ و ٢٨٢ و ٣٧٩ و ٣٨٠ و ٢/ ٢٦ و ٢/ ١٢٠) و (تهـ ١٢٣٧)، ومحمد بن عبد الرحمن بن أبي رواد " تاريخ بغداد " (١٠/ ٣٨٧ / ٥٥٦٥) والنضر بن منصور الباهلي " ميزان الاعتدال " (٧/ ٣٠٣ / ٩٨٨٧).
روى عنه: إبراهيم بن عبد الله بن محمد الزبيبي " مسند الشهاب " (١/ ٣١٨ / ٥٣٦) وأحمد بن بشر المرثدي " المستدرك " (٤/ ٤٢٤ / ٨١٥٧) وأبو سيار أحمد بن حمويه التستري " المعجم الكبير " (٣/ ٩٣ / ٢٧٦٢)، والحسين بن أحمد بن بسطام الزعفراني " ناسخ الحديث ومنسوخه " (١/ ٤١٤) وعبدان بن أحمد الأهوازي " المستدرك " (١/ ٥٣٢ / ١٣٩٣) والقاسم بن الليث الرسعني " شعب الإيمان " (٢/ ٤٣ / ١١٢٤)، ومحمد بن الفضل بن جابر " شعب الإيمان " (٢/ ٣٧٣ / ٢٠٨٤).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: صالح الحديث صدوقا.
قلت: روى عنه الطبري جزءا كبيرا، شمل أكثر آيات المصحف، بحيث لو أسمينا هذا الجزء (تفسير قتادة) لكان ذلك حقا وصدقا، حيث زادت عدد رواياته عن (٣٤٢٣) (ثلاث وعشرين وأربعمائة وثلاثة آلاف) رواية، بإسناد واحد، عنه عن يزيد بن زريع، عن سعيد بن أبي عروبة، عن قتادة بن دعامة السدوسي - وهو إسناد حسن، كما قال الشيخ شاكر في (١/ ٢٩٧ / ٣٥٢)، و (٣/ ٣٨٠ / ٢٦١٦) - وقد زادت لفظة (يونس) خطأ في بداية الأثر رقم (١٣٨٩٦).
وانظر: " الجرح والتعديل " (٢/ ٣٦٨ / ١٤١٧) و " الثقات " (٨/ ١٤٤ / ١٢٦٦١) و " تهذيب الكمال " (٤/ ١٤٦ / ٧٠٦) و " الكاشف " (١/ ٢٦٩ / ٥٩٣) و " المقتنى في سرد الكنى " (١/ ٢٩٨ / ٢٩٥٣) و " طبقات المحدثين " (١/ ٨٤ / ٩٠٦) و " " تهذيب التهذيب " (١/ ٤٠١ / ٨٤٣) و " تقريب التهذيب " (١/ ١٢٤ / ٧٠٢) و " رجال تفسير الطبري " (٧١/ ٣١٠).',NULL,'159',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (163,'٦٤ - أبو محمد الصواف (٢٧٥٧٢)
(مسلم، والأربعة)
(طب: ٥ ر / ١ ش)
(تس: ٣ ر / ١ ش)، و (تخ: ٢ ر / ١ ش)
أبو محمد، بشر بن هلال الصواف " النميري " البصري: توفي سنة سبع وأربعين ومائتين، من العاشرة، ثقة.
روى عن: جعفر بن سليمان الضبعي (تس ٢٧٥٧٢ و ٢٧٦٣٩ و ٢٧٦٤٠)، و (تخ ١/ ٢٦٣ و ٢٦٦) (٤)، وبكار بن يحيى، ابن أخي همام " تكملة الإكمال " (٣/ ٦٠٦ / ٣٧٨٦)، وحماد بن زيد " الثقات " (٨/ ١٤٤ / ١٢٦٦٠)، وعلي بن عمر المقدمي " المستدرك " (٤/ ١٥١ / ٧١٩٤).
روى عنه: الحسن بن سفيان " صحيح ابن حبان " (١٤/ ٦٠١ / ٦٦٣٤)، وعبد الله بن أحمد بن حنبل " تكملة الإكمال " (٣/ ٦٠٦ / ٣٧٨٦)، ومحمد بن عبد الله المطين " تكملة الإكمال " (٣/ ٦٠٦ / ٣٧٨٦) وأبو بكر البزار " مسند البزار " (٤/ ٢٩٧ / ١٤٧٢)، وأبو القاسم البغوي " تكملة الإكمال " .. (٣/ ٦٠٦ / ٣٧٨٦) وأبو يعلى الموصلي " معجم أبي يعلى " (١/ ١١٨ / ١١٩)، وعلي بن عبد الله بن الجعد " مسند ابن الجعد " (١/ ٢١٧ / ١٤٣٧).
التعديل والتجريح: قال عبد الرحمن بن أبي حاتم: " محله الصدق وكان أيقظ من [الذي قبله] يعني بشر بن معاذ "
وقال ابن حبان في " الثقات ": " يغرب ".
قلت: وأغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري ".
وانظر: " الجرح والتعديل " (٢/ ٣٦٩ / ١٤٢٦)، و " الثقات " (٨/ ١٤٤ / ١٢٦٦٠) و " تسمية من أخرجهم البخاري ومسلم " (١/ ٨٦ / ٢٢٩) " و " رجال مسلم " (١/ ٨٧ / ١٣٩)، و " تكملة الإكمال " (٣/ ٦٠٦ / ٣٧٨٦)، و " تهذيب الكمال " (٤/ ١٥٩ / ٧١١)، و " الكاشف " (١/ ٢٧٠ / ٥٩٨)، و " المقتنى في سرد الكنى " (٢/ ٥٥ / ٥٤٩١)، و " تهذيب التهذيب " (١/ ٤٠٤ / ٨٤٨)، و " تقريب التهذيب " (١/ ١٢٤ / ٧٠٧) و " نزهة الألباب " (١/ ٤٣٠ / ١٨٠٠)، و " شذرات الذهب " (١/ ١٥٢)، و " رجال تفسير الطبري " (٦٤/ ٢٧٣).',NULL,'160',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (164,'<span data-type="title" id=toc-24>[٣] باب التاء </span>(من اسمه تميم)
٦٥ - أبو عبد الله الواسطي (٨٩٢)
(أبو داود، والنسائي، وابن ماجه)
(طب: ٧٧ ر / ٦ ش)
(تس: ٣٨ ر / ٤ ش)، و (تخ: ٧ ر / ٢ ش)، و (تهـ: ٢٣ ر / ٤ ش)
و (تق: ٨ ر / ٤ ش)، و (صر: ١ ر / ١ ش)
أبو عبد الله، تميم بن المنتصر بن تميم بن الصلت، العباسي، الهاشمي، مولاهم، الواسطي، جد (أسلم بن سهل الحافظ لأمه)، واختلف في مولده كثيرا فقيل: مولده سنة ست وسبعين ومائة!!، وتوفي سنة أربع، أو: خمس وأربعين ومائتين، وله ست وسبعون سنة!! - وهذا لا يستقيم لأنه يكون عند وفاته، ابن (ثمان وستين)، أو: (تسع وستين) سنة فقط - من العاشرة، ثقة، ضابط.
روى عن: إسحاق بن يوسف الأزرق (تس ٣٣٠٠ و ٣٣٤٤ و ٣٧٢٦ و ٣٨٥٠ و ٣٨٧٣ و ٤٢٢٧ و ٤٢٥٨ و ٨٧٥٩ و ٩٥٧٣ و ٩٦١٥ و ١٠٣٤٠ و ١٠٣٦٢ و ١١٤٣٩ و ١١٤٤٠ و ١٢٧٤٦ و ١٥٦٢٠ و ٢٤٦٩٢ و ٢٤٧٠٠ و ٢٥٦٨٦ و ٢٨٦٩٤ و ٣٠٤٣٠ و ٣٠٦٩٤ و ٣٤٥٢٩ و ٣٤٦١٨ و ٣٦٠٥٢)، و (تهـ ٥٠٤ و ٦١٥ و ٦٨٣ و ٩٤٧ و ١٠٧٢ و ١٧٦٥ و ١٨٥١ و ١٨٦٩ و ٢١٣٤ و ٢١٤٢ و ٢٢٥٩ و ٢٧٦٧)، و (تق ٤٢٤ و ٤٣٢ و ٤٦٠ و ٧٥٢) و (تخ ١/ ٢٩ و ١/ ٣٤ و ١/ ٣٩ و ١/ ٤١ و ١/ ٤٢) و (د، س، ق) وأبي أسامة [حماد بن أسامة] (تهـ ١٩٢)، وعبد الله بن عبيد الله (تق ١٠٣٠)، وعبد الله بن نمير (تس ٣٣٢١ و ٤٦٣٩ و ٩٢١٩) و (تهـ ٢٣٨٧ و ٢٦٥٠ و ٢٨٢٥ و ٢٨٣٧)، و (تق ٩٩٣) ومحمد بن يزيد الواسطي (تس ١١٤١١ و ١١٤١٢) ويزيد بن هارون (تس ٨٩٢ و ١٠٤٥ و ٤٢٤٣ و ٤٥٢٠ و ٩١٨٠ و ٩٢٩٨ و ١١٢٦٧ و ١٥١٣٨ و ١٧٨٨٨ و ٢٤٤٤٦ و ٣٦٥٨٧) و (تهـ ١٢٣ و ١٥٤٥ و ١٥٥٠ و ١٨٢٢ و ٢٧٩٣ و ٢٩٣٨) و (تق ١٤) " و (تخ ١/ ١٨)، و (صر ١٨).
روى عنه. أحمد بن عمرو الشيباني " الآحاد والمثاني " (٣/ ١٥١ / ١٤٨٠) وأحمد بن موسى',NULL,'161',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (165,'ابن إسحاق " المستدرك " (١/ ٣٩٧ / ٩٧٧)، وجعفر بن أحمد بن سنان الواسطي " المعجم الكبير " (١٠/ ٢١٩ / ١٠٥٢٧)، وعلي بن عبد الله بن مبشر الواسطي " سنن البيهقي " (٧/ ٢٥٥ / ١٤٢٥٨)، وموسى ابن أبي حصين " المعجم الأوسط " (٨/ ٢١٢ / ٨٤٣١)، وأبو بكر البزار " مسند البزار " (٢/ ٢١٣ / ٦٠٠).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صحيح الكتاب، ضابطا، متوقيا.
وقد تصحف اسم شيخه في الأثر رقم (تس ١٢٧٤٦)، من (إسحاق) إلى (أبي إسحاق) بزيادة لفظة (أبي) قبله، وهو خطأ بلا ارتياب، وما أثبته هو الصواب، وعلى الله حسن الثواب.
وانظر: " الجرح والتعديل " (٢/ ٤٤٤ / ١٧٨٤) و " الثقات " (٨/ ١٥٦ / ١٢٧٢٧) " و " تاريخ واسط " (١/ ٢٠٩) و " تهذيب الكمال " (٤/ ٣٣٤ / ٨٠٧) و " الكاشف " (١/ ٢٧٩ / ٦٧٧) و " تهذيب التهذيب " (١/ ٤٥ / ٩٥٨) و " تقريب التهذيب " (١/ ١٣٠ / ٨٠٥) و " طبقات المحدثين " (١/ ٩٥ / ١٠٧٠) و " المقتنى في سرد الكنى " (١/ ٣٦١ / ٣٧٢٨) و " رجال تفسير الطبري " (٨٣/ ٣٦٢).

<span data-type="title" id=toc-25>[٤] باب الجيم </span>(من اسمه جابر)
٦٦ - أبو العباس الواسطي (١٧٢٤٢)
(النسائي)
(طب: ٩ ر / ٦ ش)
(تس ٤ ر / ٤ ش)، و (تخ ١ ر / ١ ش)، و (تهـ ٢ ر / ١ ش)، و (تق: ٢ ر / ١ ش)
أبو العباس، جابر بن كردي بن جابر، الواسطي، البزار، توفي سنة خمس وخمسين ومائتين، من الحادية عشرة، صدوق.
روى عن: إسماعيل بن أبي أويس (تس ٣١٧٥٢)، وشبابة بن سوار (تهـ ٢١٨١ و ٢٦٠٩) ومحمد بن سابق (تس ١٧٢٤٢) " ويزيد بن هارون (تس ٣٧٨٩١) " وأبي أحمد [الزبيري] (تس ٧٢١٢) وأبي سفيان الحميري (تخ ٢/ ٢٢٣)، (تق ١٦٧ و ٩١١) " وأحمد بن سهل بن علي " تاريخ',NULL,'162',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (166,'واسط " (١/ ١٣٣)، ويحيى بن حماد " صحيح ابن حبان " (١٢/ ٢٨٠ / ٥٤٦٦)، ويعلى بن عبيد " سنن الدارقطني " (٣/ ١٩٩ / ٣٤٥).
روى عنه: الخليل بن أحمد ابن بنت تميم بن المنتصر الواسطي " صحيح ابن حبان " (١٢/ ٢٨٠ / ٥٤٦٦).، ومنصور بن سلمة الخزاعي " صحيح ابن حبان " (١٣/ ٦٣ / ٥٧٥٢).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين قول النسائي: " لا بأس به ما علمت فيه إلا خيرا ".
وقول القطان: " لا يعرف ". ورده الحافظ في " التهذيب ".
وذكره أبو حاتم بن حبان في كتاب " الثقات ".
وانظر: " تاريخ بغداد " (٧/ ٢٣٨ / ٣٧٣١)، و " سؤالات السلفي للحوزي " (١/ ١١٤ / ١٠٥)، و " تهذيب الكمال " (٤/ ٤٥٨ / ٨٧٥)، و " ميزان الاعتدال " (٨/ ٦٨ / ٢٤٢)، و " تهذيب التهذيب " (٢/ ٣٩ / ٧١) و " تقريب التهذيب " (١/ ١٣٦ / ٨٧٥)، و " رجال تفسير الطبري " (٩٢/ ٣٩٨).
٦٧ - جرير غير مكنى ولا منسوب (٢٩٠٤٠)
(طب: (تس: ١ ر / ١ ش)
جرير، شيخ الطبري!!!، غير مكنى، ولا منسوب!! هو: ابن عبد الحميد الضبي (١٠)، والإسناد قبل جرير، فيه سقط أكيد، وهو: محمد بن حميد الرازي (١).
وانظر على سبيل المثال لا الحصر الآثار (٥٨ و ٢٧٤٤ و ٣٠٨٨ و ٣٤٢٩ و ٣٧٥٨ و ٤٥٠٦ و ٤٩٩٩) وهو إسناد كثير الدوران، ثم رأيت الشيخ التركي قد أثبته في " تحقيقه " (١٩/ ٣٩٢) من عدة مخطوطات، والحمد لله الذي بنعمته تتم الصالحات.
روى عن: مغيرة - وهو ابن مقسم الضبي - (تس ٢٩٠٤٠).
روى عنه: أبو جعفر محمد بن جرير الطبري!!! - وهو وهم - وفيه سقط حيث سقط اسم شيخ الطبري فيه، وهو (محمد بن حميد الرازي)، ويروي عن (جرير)، وهو (ابن',NULL,'163',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (167,'عبد الحميد الضبي) وشيخه (المغيرة)، هو: ابن مقسم الضبي. وهو إسناد كثير الدوران في " التفسير " تكرر قبل ذلك في الآثار التي ذكرتها بأعلاه، وغيرها. والله أعلم.
٦٨ - جعفر المحمدي (تخ ٢/ ٦٤٢)
(طب: تخ ١٦ ر / ٢ ش - مقرونين -)
جعفر بن عبد الله المحمدي من الحادية عشرة لم أعرفه ولم أجد له ترجمة ولم أعرف شيخه الثاني وأما شيخه الأول فصدوق رمي بالرفض.
روى عن: عمرو بن حماد بن طلحة، وعلي بن حسين بن عيسى بن زيد بن علي - مقرونين - (تخ ٢/ ٦٤٢ و ٦٤٣ و ٦٤٤ ** و ٦٦٢ ** و ٦٦٣ و ٦٨٧ ** و ٦٨٩ و ٦٩٦ **).
روى عنه: أبو جعفر الطبري.
التعديل والتجريح: لم أقف له على ذكر في كتب التراجم والرجال، وآثاره التي يرويها، أحداث تاريخية.
٦٩ - جعفر البزوري (٦٣٢)
(طب: ١٤ ر / ٥ ش)
(تس ٩ ر / ٤ ش) " و (تخ ١ ر / ١ ش)، و (تهـ ٢ ر / ٢ ش) " و (تق ٢ ر / ١ ش)
جعفر بن محمد، البزوري، الكوفي، بياع البرود - ولعلها مصحفة من (البزور) من العاشرة، سكت عنه الحافظ في " التهذيب ".
روى عن: حسن بن بشر البجلي (تس ٦٣٢)، ويحيى بن مصعب الكلبي (تس ٢٠٣٦٢ و ٣٤٦٠٥)، وعبيد الله بن موسى (تس ٩٨٠٥ و ٣٣١٨٧ و ٣٤٦٨٩)، و (تهـ ١٣٧٢)، و (تخ ٢/ ٢٣) ويعلى ابن عبيد (تس ١٠٨٦١ و ٢٨٦٣٤ و ٣٢٤٩٣ و ٣٦٦٥٩) و (تق ٩٦ و ٣٣٨) وأبي داود الطيالسي (تهـ ٢٩٢١)',NULL,'164',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (168,'وأبي سليمان حمد بن محمد الخطابي " مسند الشهاب " (١/ ٣٥٦ / ٦٠٩)، وموسى بن هلال البصري " ضعفاء العقيلي " (٤/ ١٧٠ / ١٧٤٤)، ويحيى بن موسى الطائفي " حلية الأولياء " (٣/ ٣٥٤).
روى عنه: سعد بن علي الزنجاني " أخبار قزوين " (٣/ ٢٩٩)، ومحمد بن سلامة القضاعي " مسند الشهاب " (١/ ٣٥٦ / ٦٠٩)، ومحمد بن عبد الله الحضرمي " ضعفاء العقيلي " (٤/ ١٧٠ / ١٧٤٤).
التعديل والتجريح: ذكره الحافظ ابن حجر في " لسان الميزان " (٢/ ١٢٦ / ٥٤٤) وقال في نسبه:- " (المروزي) بدل (البزوري)، وسكت عنه، ولم يذكر فيه جرحا ولا تعديلا.
وذكره الشيخ شاكر في تحقيقه " لتفسير الطبري " (١/ ٢٩٧ / ٣٥٢) وسكت عنه ثم ذكره في (٩/ ٤٢٤ / ١٠٨٥٧) وقال: " جعفر بن محمد البزوري = جعفر بن محمد الكوني المروزي ((جعفر بن محمد البزوري)) لم أجده بهذه النسبة، والذي وجدته في تهذيب التهذيب ممن يروي عن يعلى بن عبيد ((جعفر بن محمد الواسطي الوراق)) نزيل بغداد مات سنة ٢٦٥، وهو خليق أن يروي عنه أبو جعفر. ثم راجع تاريخ بغداد ففي ((جعفر بن محمد)) كثرة ولكن لم أجد بينهم ((البزوري)). وعسى أن تكثف الأسانيد الآتية عن الذي يعنيه (أبو جعفر) (١٠٨٥٧).
ثم أكمل ترجمته في (١٦/ ٤٣٤ / ٢٠٣٦٣) حيث قال: ((جعفر بن محمد البزوري من أهل الكوفة)) شيخ الطبري هكذا جاء في المخطوطة، وهو ما لا أعرف. وقد مضى برقم (٩٨٠٠) وذكرت هناك أني لم أجده وكان فيما سلف ((جعفر بن محمد الكوفي المروزي)) " وذكرت أنه روى عنه في " التاريخ " (٥/ ١٨) وصح عندي أنه هو هو في المواضع الثلاثة، لأنه روى عنه في " التاريخ " (٢/ ٥٦٥) قال: ((حدثني جعفر بن محمد الكوفي، وعباس بن أبي طالب قالا، حدثنا أبو زكريا يحيى بن مصعب الكلبي " قال حدثنا عمر بن نافع)). فهذا هو بعض إسنادنا هذا (٢٠٣٦٣) ا. هـ.
قلت: لم أقف له على تعديل أو تجريح، فيما وقفت عليه من كتب التراجم والرجال وإنما هي روايات قليلة له عمن أثبتهم عاليا " في معجم شيوخه وتلاميذه، وقد تصحف نسبه من (البزوري) إلى (المروزي) في الأثر رقم، (تس ٣٢٤٩٣)، وروى الطبري في (تخ: ٥/ ٢١٣)',NULL,'165',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (169,'حكاية بغير إسناد، وقال: حدثني جعفر بن محمد الفراء! فلعله هو المراد، أو هو شيخ آخر للطبري؟!.
ثم وقفت له على روايتين في " الجزء المفقود من تهذيب الآثار " يروي فيهما، عن يعلى بن عبيد الطنافسي.
الأولى: في (٧٨/ ٩٦) وعلق عليها محققها (علي رضا) فقال: " جعفر بن محمد الكوفي الظاهر أنه الثعلبي، وهو صدوق. " التقريب " (٩٥١).
والثانية: في (٢١٤/ ٣٣٨) وعلق عليه محققها (علي رضا) أيضا فقال: " وجعفر بن محمد الكوفي بياع البرود، لم أظفر له على ترجمة! ".
قلت: والظاهر أن الشيخ - حفظه الله - غفل في الموضع الثاني عما استظهره في الموضع الأول، رغم أن الإسناد في الموضعين متشابه في أول ثلاثة رجال منه!!، ورغم أن (الطبري) قد عرف بشيخه في الرواية الثانية أكثر من تعريفه به في أي رواية ساقها عنه فيما مضى من روايات في سائر كتبه، ولا أظن أن (الشيخ علي رضا) أصاب في الموضع الأول! وهو في الموضع الثاني إلى الصواب أقرب! والله تعالى أعلم.
وقد تصحفت نسبته في الأثر رقم (٢٠٣٦٢) من (البزوري) بزاي معجمة بعدها واو فراء مهملة إلى (البروري) براء مهملة بعدها واو فراء مهملة في طبعة دار الكتب العلمية، أما طبعة دار الفكر ١٤٠٥ فسقطت منها النسبة، وكنية ونسبة شيخه أبو زكريا الكلبي.
وقد تصحف اسم شيخه في الأثر رقم (٢٨٦٣٤) بتصحف لفظة (عن) فصارت (بن) ليصير الشيخان، شيخا واحدا، والصحيح ما أثبته، والله تعالى أعلم.
٣٩ - تمييز أبو الخير، جعفر بن محمد، المروزي، الشافعي توفي سنة ٤٤٢.
وانظر: " كشف الظنون " (١/ ٨٢٥).',NULL,'166',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (170,'٧٠ - أبو الفضل الدوري (٢١١٧١)
(طب: (تس: ٢ ر / ٢ ش)
أبو الفضل، جعفر بن مكرم بن يعقوب بن إبراهيم، الدوري، التاجر الرازي، ثم البغدادي، توفي في جمادى الأولى، سنة أربع وستين ومائتين، من العاشرة، صدوق.
روى عن: أبي عاصم الضحاك بن مخلد النبيل (تس ٢١١٧١)، والحفري - غير مكنى ولا مسمى - (تس ٣٦٩٧٥)، وأزهر بن سعد السمان، وأبي علي الحسين بن إدريس الحلواني " المستدرك " (١/ ٦٤٤ / ١٧٣٤)، والحسين بن علي " مسند البزار " (٥/ ٢٥٣ / ١٨٦٩)، وأبي أسامة حماد بن أسامة، وروح بن عبادة، وسعيد بن عامر، وعمر بن يونس اليماني، وقريش بن أنيس كذا في الأصل، من " تاريخ بغداد "، وهو تصحيف والصحيح (أنس) " كما هو في " الجرح والتعديل " وغيره -وأبي بكر الحنفي، وأبي داود الطيالسي، وأبي عامر العقدي.
روى عنه: علي بن سليم الأصبهاني " المستدرك " (١/ ٦٤٤ / ١٧٣٤)، ومحمد بن خلف، ومحمد بن مخلد الدوري، ووكيع [بن الجراح]- وسقط في الأصل، من " تاريخ بغداد " واو العطف قبل وكيع - ويحيى بن محمد بن صاعد، وأبو بكر البزار " مسند البزار " (٥/ ٢٥٣ / ١٨٦٩).
وكل اسم لم يعز إلى كتاب - وميزته بالأسود العريض -، من معجم شيوخه وتلاميذه، فهو من ترجمته في " تاريخ بغداد ".
التعديل والتجريح: قال عبد الرحمن بن أبي حاتم: " كتبنا بعض حديثه فلم يقض السماع منه وهو صدوق ".
قلت: أغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري ".
وقد روى الطبري في الأثر رقم (تس ٣٦٩٧٥) من " التفسير " عن (يعقوب بن مكرم) عن (الحفري)، أثرا، لا أظن إلا أن شيخ الطبري فيه هو صاحب هذه الترجمة، سقط اسمه الأول (جعفر)، وانقلب باقي اسمه ..
وانظر ترجمته في موضعها برقم (٣٧٨) من كتابي هذا.',NULL,'167',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (171,'وانظر: " الجرح والتعديل " (٢/ ٤٩١ / ٢٠١١)، و " تاريخ بغداد " (٧/ ١٧٨ / ٣٦٢٢).
٧١ - جعفر بن هاشم (تق: ٦٧٥)
(طب: تق ١ ر / ١ ش)
جعفر بن هاشم، من الحادية عشرة " لم أعرفه، ولم أعرف شيخه، ولم أجد لهما ترجمة، ولم يتعرض (الشيخ: علي رضا) لهما بشيء.
روى عن: سليمان بن أبي سليمان التميمي (تق ٦٧٥).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: لم أقف له على ترجمة أو ذكر، لا هو، ولا شيخه (سليمان بن أبي سليمان التميمي).
وشيخ شيخه هنا هو: محمد بن عمر الواقدي المتهم بالكذب، وهو من التاسعة، متروك مع سعة علمه، وقد ترجمت له في " المعجمين الصغير والكبير ".
وعلى هذا يكون سليمان بن أبي سليمان من العاشرة، وجعفر بن هاشم؛ شيخ الطبري من الحادية عشرة.
والحديث الذي أورده الطبري من طريقه صحيح كما بين ذلك الشيخ (علي رضا) في تحقيقه " الجزء المفقود من تهذيب الآثار ". ولم يتعرض لجعفر هذا ولا لشيخه بشيء.
وقد وقفت للحديث على ثلاث طرق؛ أخرج العقيلي في " الضعفاء " طريقين منهما، وأخرج الطبراني الثالثة في " الأوسط "، غير الطرق التي أشار إليها الشيخ (علي رضا) في تحقيقه " للجزء المفقود من تهذيب الآثار ".
أما الطريق الأولى: قال العقيلي في " الضعفاء " (٢/ ١٣٥ / ٧٨٦): " حدثنا إبراهيم بن محمد حدثنا عمرو بن مالك الراسبي، حدثنا فضيل بن سليمان النميري، حدثنا عمر بن سعيد التنوخي، أخبرني الزهري، عن سعيد بن المسيب، عن عثمان بن عفان، عن أبي بكر',NULL,'168',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (172,'الصديق قلت: يا رسول الله! ما نجاة هذا الأمر؟ قال: " في الكلمة التي أردت عليها عمي فأباها ".
وأما الطريق الثانية: فقال فيها: " حدثنا يحيى بن عثمان بن صالح، حدثنا حامد بن يحيى البلخي، حدثنا محمد بن عمر بن واقد المديني، عن ابن أخي ابن شهاب الزهري عن سعيد بن المسيب عن عبد الله بن عمر! - كذا في الأصل؛ ولعلها كما هي في " تهذيب الآثار "، و " الأوسط "، (عبد الله بن عمرو) عن. . - عن عثمان بن عفان، عن أبي بكر الصديق قال: أنا سألت رسول الله صلى الله عليه وسلم عن ذلك؛ ما النجاة مما نحن فيه، قال: " الكلمة التي عرضتها على عمي فأبى أن يقبلها، شهادة ألا إله إلا الله هي النجاة ".
وأما الطريق الثالثة: فقال الطبراني في " الأوسط " (٣/ ١٧٤ / ٢٨٣٩): " حدثنا إبراهيم قال: حدثنا عمرو بن خلف قال: حدثنا فضيل بن سليمان النمري قال: حدثنا عمر بن سعيد بن سرحة التنوخي عن الزهري، عن سعيد بن المسيب، عن عبد الله بن عمرو بن العاص، عن عثمان بن عفان عن أبي بكر الصديق قال: قلت يا رسول الله! فيما نجاة هذه الأمة؟ فقال " في الكلمة التي أردت عليها عمي فأباها، شهادة أن لا إله إلا الله وأن محمدا وسول الله ".
قال الطبراني: " لم يرو هذا الحديث عن الزهري إلا عمر بن سعيد ".
قلت: وهناك من طبقة جعفر هذا:
٤٠ - تمييز أبو يحيى " جعفر بن هاشم بن يحيى - وقيل: ابن [أبي سعيد] وقيل: [ابن حلبس القلاس]، العسكري، وقيل [البزاز]. وقيل: [السمسار]- سكن بغداد في دار كعب، وتوفي لثلاث خلون شهر ربيع الأول سنة سبع وسبعين ومائتين، ثقة.
روى عن: سهل بن عثمان العسكري " وعباس بن بكار، ومسلم بن إبراهيم، وأبي الوليد، و [عبد الله بن مسلمة] القعنبي.',NULL,'169',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (173,'روى عنه: حمزة بن محمد الدهقان، وعبد الصمد بن علي الطستي، وابن مخلد، وأبو عمرو بن السماك.
التعديل والتجريح: قال الخطيب البغدادي: " وكان ثقة ".
قلت: لا أدري إن كان هو شيخ الطبري أم غيره؟! وأغلب الظن أنه هو " لأنه من نفس طبقة شيوخ الطبري ومن نفس البلد (بغداد) وقد روى عنه كثيرون ممن روى عنهم الطبري أو عاصروه، والله أعلم.
وانظر: " تاريخ بغداد " (٧/ ١٨٣ / ٣٦٣٣)، و " المقتنى في سرد الكنى " (٢/ ١٤٩ / ٦٧٠٤)، و " مولد العلماء ووفياتهم " (٢/ ٥٩٩)، و " الثقات " (٨/ ١٢٨ / ١٢٥٦٤)، و " إكمال ابن ماكولا " (٧/ ٧٠)، و " الرحلة في طلب الحديث " (١/ ١٢٧ / ٤١)، و (١/ ١٤٥ / ٤٥)، و " المدخل إلى السنن الكبرى " (١/ ٢٣٢ / ٥٠٨)، و " الجامع لأخلاق الراوي " (١/ ٢٣٢ / ٤٤٩)، و " صحيح ابن حبان " (٤/ ١٢ / ١٢١١)، و " المستخرج " (٤/ ١١٠ / ٣٣٥٨)، و " مسند أبي عوانة " (٤/ ١٣ / ٥٨٥٢)، و " سنن البيهقي الكبرى " (١٠/ ٢٨ / ١٩٦٠٨).
٧٢ - جعفر سبط الأزرق (٢٤١٥٠)
(طب: (تس: ٢ ر / ١ ش)
جعفر ابن بنت إسحاق بن يوسف الأزرق، من التاسعة، لم أعرفه، ولم أجد له ترجمة، ولم أقف له في " التفسير " على غير أثرين، ولم يتعرض له الشيخ التركي في " تحقيقه " (١٦/ ٧٥) بشيء.
روى عن: سعيد بن محمد الثقفي الكوفي الوراق (تس ٢٤١٥٠ و ٣١١١٥).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: أغفله الشيخان شاكر " في تحقيقهما " لتفسير الطبري " ولم أقف له على ترجمة في كتب الجرح والتعديل.
وشيخه هنا هو: أبو الحسن سعيد بن محمد الثقفي، الوراق، الكوفي، نزيل بغداد',NULL,'170',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (174,'من صغار الثامنة، ضعيف، روى له الترمذي، وابن ماجه، وقد ترجمت له في " المعجمين الصغير والكبير ".
وانظر: " تقريب التهذيب " (١/ ١٨٠ / ٢٣٧).

<span data-type="title" id=toc-26>[٥] باب الحاء </span>(من اسمه حاتم)
٧٣ - أبو عمرو الضبي (٣٢٢٧)
(ابن ماجه)
(طب: ٩ ر / ٧ ش)
(تس: ٤ ر / ٤ ش) و (تهـ: ٣ ر / ٢ ش)، و (تق: ٢ ر / ٢ ش)
أبو عمرو، حاتم بن بكر بن غيلان، الضبي، البصري، الصيرفي، من الحادية عشرة، مقبول.
روى عن: أشهل بن حاتم الأرطبائي (تس ٣٢٢٧)، وخلاد بن يزيد (تهـ ٦٦٧)، وعبد الأعلى بن حسين المعلم (تس ٩٢٩٥)، و (تق ١٣)، وعثمان بن عمر بن فارس (تس ٢٩١٥٦)، و (تهـ ١٩٠٧)، وعمير بن عبد المجيد (تق ١٠٤)، ومكي [ابن إبراهيم] (تهـ ١٦٧١)، ووهب - غير مكنى، ولا منسوب، (تس ٦١٢٢).
روى عنه: إبراهيم بن مخلد البلخي " شعب الإيمان " (٤/ ٨٣ / ٤٣٦٤)، والعباس بن حمدان الحنفي " طبقات أصبهان " (٣/ ٥٦٥ / ٤٩٦)، وأبو بكر البزار " مسند البزار " (٤/ ٣٠٨ / ١٤٨٩).
التعديل والتجريح: قلت: لم يذكره ممن ترجم له، بتعديل أو تجريح، سوى الحافظ في " التقريب ". حيث قال عنه: " مقبول ".
وقد تصحف اسم أبيه في الأثر رقم (تس ٣٢٢٧)، فصار (بكير) - مصغر - والصحيح (بكر) - مكبر - كما هو في باقي الآثار الأخرى التي في " التفسير "، والتي في " تهذيب الآثار " والأثرين في " المفقود منه " وكما هو عند جميع من ترجم له، أو: أخرج',NULL,'171',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (175,'حديثه، في غير ما كتاب من كتب الحديث، والتراجم.
وانظر: " تهذيب الكمال " (٥/ ١٩١ / ٩٩٣) و " الكاشف " (١/ ٣٠٠ / ٨٣٣) و " تهذيب التهذيب " (٢/ ١١١ / ٢١٠) و " تقريب التهذيب " (١/ ١٤٤ / ٩٩٥) و " رجال تفسير الطبري " (١١١/ ٤٦٠).
٧٤ - أبو محمد بن أبي أسامة (٧٧٣٥)
(طب: ١٦٩٠ ر / ٨ ش)
(تس: ١٥٠٤ ر / ٦ ش) و (تخ: ١٨٨ ر / ٥ ش)
أبو محمد، الحارث بن محمد بن أبي أسامة، التميمي، البغدادي، الحافظ، صاحب المسند المعروف، ولد في شوال، سنة ست وثمانين ومائة، وتوفي ليلة عرفة سنة اثنتين وثمانين ومائتين، وله ست وتسعون سنة، ثقة، صدوق، حافظ، راوية للأخبار، كثير الحديث، تكلم فيه، بلا حجة.
