INSERT INTO [b29741] VALUES (266,'١٦٨٣٦ - و ١٧٠٢٠ و ١٧٠٦٦ و ١٧٢٢٩ و ١٧٣٢٠ و ١٧٣٢٣ و ١٧٣٩٣ و ١٧٩٦٥ و ١٨٠٥٢ و ١٨١٧١ و ١٨٢٣٨ و ١٨٢٤٨ و ١٨٦٦٦ و ١٨٧٩١ و ١٨٨٧١ و ١٨٩٦٥ و ١٩٧١٧ و ٢١٢٤٢ و ٢١٢٦٢ و ٢١٥٢٨ و ٢١٧١٩ و ٢١٧٣٩ و ٢١٩٠٦ و ٢٥٨٤٧ و ٢٥٨٥٧ و ٢٧٣٥٥ و ٢٧٧٤٣ و ٢٧٨٣٠ و ٢٨٣٥٨ و ٢٩٧٣٧)، و (تهـ ١٦٠٩ و ٢٥٢٦ و ٢٧٣٣) و (تخ ١/ ١٧٧ و ١/ ٥٤٤ و ١/ ١٩٩)، و (ت)، وحميد بن عبد الرحمن الرؤاسي (تس ١٢١٢٢ و ١٢١٦٧ و ١٢٦٠٧ و ١٢٦٨١ و ١٢٧٣٢ و ١٣١٢٤ و ١٣٢٩٨ و ١٣٥٠٦ و ١٤٥٧٤ و ١٤٦٦٥ و ١٤٨٦٠ و ١٥٩٦٢ و ١٦١٠١ و ١٦٦١٤ و ١٦٦٦١ و ١٦٨٥٦ و ١٦٩٧٦ و ١٧٦٢٦ و ١٨٩٣٠ و ٢١١٤٦)، و (تهـ ١٢٨٣ و ٢٤٥٧)، و (ت)، وخالد بن مخلد القطواني (تس ٩٩٢٨ و ١٢٥٣٥)، و (ت)، وأبي يزيد الخراز – وهو خالد بن حيان الرقي – (تس ١٤٠١٣ و ١٥١٦٥ و ١٦٩١٢ و ١٨٢٥٦ و ٢٨١١٣)، وروح بن عبادة (تس ١٠٣٥٢ و ١٠٥٣٥ و ١٠٦١٨ و ١٣٦٤٥ و ١٥٨٢٠ و ٣٦٧٣٧)، و (ق)، وزكريا بن عدي (تس ١٧٨٦٢)، وزيد بن الحباب (تس ١١٩٠٣ و ١٢١٥٥ و ١٣٠٤٦ و ١٤٥٢٥ و ١٤٦٣٧ و ١٥١٨٨ و ١٦٩٣٦ و ١٧٠٢٩ و ١٧٤٢٢ و ١٧٤٢٦ و ١٧٥٤٧ و ١٧٨١٩ و ١٧٨٦٣ و ١٨٥٠٣ و ١٨٦٣٦ و ١٨٦٥٥ و ١٨٧٨٠ و ١٩٤٩٨ و ١٩٩٣٦ و ٢٥٩٧٧ و ٢٧٦٤٣ و ٢٧٨٥٢ و ٢٧٩٥٧ و ٢٨٢٧٠)، و (تهـ ١٣٠٥ و ١٦١٥)، و (تق ١٠٣٧)، و (تخ ١/ ١٩٩)، و (ت)، وسفيان بن عيينة (تس ١٤٣ و ٤٣٩٧ و ٦٤٥٤ و ١٠٧٦٣ و ١٠٥٢٥ و ١١٠٥٣ و ١١٠٦٥ و ١١١٤٥ و ١١٥١٢ و ١٢٠١٩ و ١٢٥٤٨ و ١٢٥٩٣ و ١٢٧٣٠ و ١٣٣٠٦ و ١٣٣٦٩ و ١٣٧٢٤ و ١٣٧٣١ و ١٣٨٤١ و ١٤٠٨٥ و ١٤٢٤٢ و ١٤٤٦٠ و ١٤٥١١ و ١٤٥٢٦ و ١٤٦٨١ و ١٤٩٧٤ و ١٦١٢١ و ١٦٤٢١ و ١٦٤٢٤ و ١٧٠٦٩ و ١٧٢٩٩ و ١٧٥١٨ و ١٧٥٢٧ و ١٧٨٠١ و ١٧٨١٤ و ١٨٢٤٣ و ١٨٢٦٢ و ١٨٣٨٩ و ١٩٠١٠ و ١٩٠٢٥ و ١٩٠٢٦ و ١٩٧٤٩ و ١٩٨١١ و ١٩٨٢٤ و ٢١٤٢٣ و ٢١٦٩٥ و ٢١٧٧١ و ٢١٨٤٩ و ٢١٨٩٩ و ٢٢٢٨٥ و ٢٢٤١٦ و ٢٧٥١٦ و ٢٧٥٢٤ و ٢٧٥٧٨ و ٢٨١٤١ و ٢٨٣٠٥ و ٢٨٤١٨ و ٢٨٤٨١)، و (تهـ ٦١٨ و ٨١٣ و ٧٥٥ ٨١٣ و ١٠٣٨ و ١٢٣١ و ١٦٨٨ و ١٨١٥ و ٢٢١٩ و ٢٣٣٧ و ٢٣٩٥ و ٢٤١١ و ٢٤١٥ و ٢٦٨٥ ٢٦٩٧ ٢٨١٤ ٢٩٦٦)، و (تق ٦٥ و ٩٤ و ٥٤٩ و ٦١٧ و ٧٩٩)، و (تخ ١/ ١٦ و ١/ ٢٠٤ و ١/ ٣٤٩)، و (ت)، وأبي خالد سليمان بن حيان الأحمر (تس ٢٤٨٠ و ١٠١٩٤ و ١٠٦٧٤ و ١٠٧٠٨ و ١٠٨٢٧ و ١٠٩٠٣ و ١٠٩٨٩ و ١٠٩٩٠ و ١١٠٠٦ و ١١٠١٥ و ١١٠٢٣ و ١١٠٢٧ و ١١٠٣٣ و ١١٠٦٣ و ١١١٠١ و ١١١٤٤ و ١١٩٦٢ و ١٢٦٤٢ و ١٢٦٤٥ و ١٢٦٩٤ و ١٢٧٠٩ و ١٣٢٩٧ و ١٣٤٥٩ و ١٣٥٧٠ و ١٤٢٢٢ و ١٤٢٩٢ و ١٤٦٦٤ و ١٤٧٠٧ و ١٥٦١١ و ١٥٦٢١ و ١٥٦٤٢ و ١٥٨٠٨ و ١٧٣١٩ و ١٧٨٩٦ و ١٨٣٧٨ و',NULL,'264',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (267,'١٨٥٥٧ - و ١٩٥٥٦ و ٢١٥١٣ و ٢١٦٨٦ و ٢٧٣٨٢ و ٢٧٦٢٦ و ٣٢٥١٦ و ٣٦٥٨٠ و ٣٧٦٨٥) و (تهـ ٢٨٨٦ و ٣٠٣٠) و (تخ ١/ ٢٦٤) و (ت)، وسهل بن يوسف (تس ١٠٥١٧ و ١٠٦٣٦ و ١٠٦٥٣ و ١٠٦٦٧ و ١١٧٢٢ و ١٢٥٥٧ و ١٥٥٣٧ و ١٦٨٧٩ و ٢٨٢٦٠) و (تهـ ٢٤٥١)، و (تخ١/ ٩١)، وسويد بن عمرو الكلبي (تس ١٤٥٢٧ و ١٦٧١٢ و ١٧١٩٠ و ١٧٢٠٨ و ١٧٥٣٨ و ١٧٩٠٥ و ١٩٥٣٥ و ١٩٨٤٢ و ٢٨٣٧٢) و (ت)، وأبي عاصم الضحاك بن مخلد (تس ١٠٦٥٧ و ١٣٠٦١)، وعبد الله بن إدريس (تس ٧٢١ و ١٠٩٩٢ و ١١٠٩٩ و ١١٢٢٩ و ١١٤٦٠ و ١١٨٣٤ و ١٢١٩٦ و ١٢٢٠٢ و ١٢٤٧٧ و ١٢٩٢٢ و ١٣٣٣٨ و ١٣٤٨٨ و ١٤٥٧٣ و ١٥٢٦٣ و ١٥٢٨٩ و ١٥٦٠٣ و ١٥٧٩٧ و ١٥٨٠٢ و ١٦٣٣٥ ١٦٦١٧ و ١٦٦٨٧ و ١٧٤٠٩ و ١٨٠٦٤ و ١٨٠٦٦ و ١٨١٦١ و ١٩٠٣٣ و ١٩١١٦ و ١٩١٨٦ و ١٩٧٦٤ و ١٩٧٦٥ و ٢١٣١٧ و ٢٣٦٩١ و ٢٧٩٢٢)، و (تهـ ٨١٥ و ٨٢٧ و ٢٠٧٨)، و (تق ٦٢٦)، وعبد الله بن بكر (تس ١٥١٩٠) وعبد الله بن رجاء المكي (تس ١٤٦٧٨ و ١٤٩٠٠ و ١٥٠٠٧ و ١٥٥٦٨ و ١٦٥٥٨ و ١٧٥٦٤ و ١٧٨٩٢)، وعبد الله بن نمير (تس ١٦٩٦ و ٩٥٩٩ و ٩٦٤٥ و ١٢٣٩٩ و ١٣٩٥١ و ١٤٢٨٢ و ١٤٢٩٠ و ١٤٥٧٣ و ١٥٣٧٨ و ١٥٣٧٩ و ١٥٦٥١ و ١٦٠٨٧ و ١٦٢٧٨ و ١٦٣٢٤ و ١٦٥٥٢ و ١٦٦٥٧ و ١٦٨٠٩ و ١٦٨١٣ و ١٦٨٢٨ ١٦٩١٨ و ١٧٠٩٨ و ١٧١٥٩ و ١٧٣٢٣ و ١٧٤١٠ و ١٧٤٤٠ و ١٧٥٠٤ و ١٧٥٥٨ و ١٧٧٨١ و ١٧٩٣٦ و ١٧٩٥٧ و ١٨٠٩٥ و ١٨١٨٠ و ١٨٢٠٠ و ١٨٢١٢ و ١٨٢٦١ و ١٨٢٨١ و ١٨٥١١ و ١٨٥٣٥ و ١٨٦٤٤ و ١٨٧١٣ و ١٨٨٧٨ و ١٩٢٤٢ و ١٩٢٦٢ و ١٩٤١٦ و ١٩٥٠٤ و ١٩٥٩٥ و ١٩٦٥٣ و ١٩٦٦٨ و ١٩٦٨٣ و ١٩٦٩٠ و ١٩٩٧٢ و ٢٠٧٥٧ و ٢١٢٥٦ و ٢١٣٠٦ و ٢١٣٠٩ و ٢٨٦٢٠ و ٣٦٦١٥ و ٣٨٢٤٥)، و (تهـ ٢٤٨١)، و (ت)، وعبد الله بن وهب (تس ٣٢٤٤٨)، و (ت)، وعبد الأعلى بن عبد الأعلى (تس ٩٦٠٦ و ١٠٣٧٤ و ١٠٩١٩ و ١١٤٦٢ و ١١٧٩٤ و ١١٨٠١ و ١٢٣٩٧ و ١٢٣٩٨ و ١٢٤٠٧ و ١٢٤٦٣ و ١٢٤٦٩ و ١٢٤٨١ و ١٢٦١٦ و ١٣٧٠٤ و ١٤٠٣٦ و ١٤٥٠١ و ١٥٢١٨ و ١٥٦٦٣ و ١٥٨١٥ و ١٦٣٨٨ و ٢٧٧٩٣ و ٢٨٢٨٠ و ٢٩٢٦٥ و ٣٢٤٧٨ و ٣٨٢٣٣)، و (تهـ ٧ و ٥٩٨ و ٨٤١ و ٩٦٢ و ١٦٦٧ و ١٦٩٠ و ١٧٧٦ و ٢٨٨١)، و (تق ٥٥٠)، و (ت)، وعبد الحميد بن عبد الرحمن الحماني (تس ١٤٤٥٧ و ١٧٩١٤)، و (ق)، وعبد الرحمن بن محمد المحاربي (تس ٤٢٦٨ و ١٠٤٧٦ و ١٠٦٤٥ و ١٠٦٧٤ و ١٢٢١٧ و ١٢٣٥٨ و ١٣٥٨٩ و ١٣٥٩٩ و ١٤٣١٠ و ١٤٥١٥ و ١٤٥٦٦ و ١٤٥٩٠ و ١٤٧٣٤ و ١٤٨٩٩ و ١٥٠٠٠ و ١٥٢٠٦ و ١٥٢٨٧ و ١٥٤٩٩ و ١٥٦٠٤ و ١٥٦٠٧ و ١٥٦١١ و ١٥٦٧٨ و ١٥٧٦١ و ١٥٨٠٣ و ١٥٨٤٤ و ١٥٨٩٨ و ١٥٩٠٥ و ١٥٩٥٩ و ١٦٠٥٧ و ١٦٤٣٧ و ١٧١٦٣ و',NULL,'265',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (268,'١٧٣٩١ - و ١٧٤٢١ و ١٧٥٤٤ و ١٧٨٩٦ و ١٧٩٧٨ و ١٧٠٠٩ و ١٨٣٢٠ و ١٨٣٧٨ و ١٨٥٩٧ و ١٨٩١٩ و ١٩١٤٢ و ١٩١٦٥ و ١٩٢٧٢ و ١٩٦٧٣ و ١٩٧٠٩ و ١٩٧٧٥ و ١٩٨٧١ و ١٩٩٠٧ و ٢٠٨٧٤ و ٢٠٨٩٠ و ٢٠٩٣٠ و ٢١٥٩٨ و ٢١٥٩٩ و ٢٧٨٧٦ و ٢٨٠٤٩)، و (تخ ١/ ٢٠٧)، و (ق)، وعبد الرحمن بن مهدي (تس ١٠٦١٧ و ١٠٦٢٤ و ١٠٦٢٥ و ١١٠٦٢ و ١١٠٩٨ و ١١٩٩٣ و ١٢٣٣٧ و ١٢٤٢٤ و ١٢٤٤٨ و ١٢٨٩٥ و ١٢٩٤٠ و ١٣٣٤٧ و ١٣٤٩٢ و ١٣٩٦٩ و ١٤٣٠٦ و ١٤٦٢٦ و ١٤٦٣٠ و ١٤٦٤٥ و ١٦٧٦٢ و ٢٧٦٤١ و ٢٧٦٦١ و ٢٧٦٦٥ و ٢٨١٠٦ و ٣٤٢٤٠)، و (تهـ ٢٨٨٠)، وعبد الصمد بن الوارث (تس ١٤٤٦٢ و ١٦٨٦٥)، وعبد العزيز بن عبد الصمد العمي (تس ١٣٠٤٥)، وعبد الوهاب بن عبد المجيد الثقفي (تس ١٠٥٩٨ و ١٠٦٦٢ و ١٠٩٤١ و ١١٤٦٢ و ١٢٣٤٤)، و (تهـ ٥٤٢ و ٢٠٨٥ و ٢٦٤٢ و ٢٦٦١ و ٢٨٨٦)، وعبدة بن سليمان (تس ١٠٩٧٥ و ١٠٩٨٣ و ١١٤٦٣ و ١١٥٩٨ و ١٢٩٨١ و ١٣٤٠٨ و ١٣٥٢٥ و ١٤٢٢٨ و ١٤٩٢٥ و ١٥٥٥٢ و ١٥٦٨٩ و ١٥٩٠٨ و ١٦٥٠٣ و ١٦٧٨١ و ١٧٠٣٨ و ١٧٧٦١ و ١٨٦٣٠ و ١٨٦٦٠ و ١٩٧٧٧ و ٢١٦٤٧ و ٢١٦٨٦ و ٢١٧٢٤ و ٢٨٣٦٨ و ٢٨٥٧٣ و (تهـ ٢١٣٠ و ٢٣٩٧) و (تخ ٢/ ٣٣) وعبيد الله بن موسى (تس ٧٣٠ و ٢٢٢٤ و ١٠٢٢٣ و ١٠٥٧٥ و ١٠٧٢٣ و ١٠٥٥١ و ١٠٥٧٥ و ١٠٧٢٣ و ١٠٩٠١ و ١٠٩٦٠ و ١٠٩٦٦ و ١٠٩٣٨ و ١٠٩٨٥ و ١١٦٧٣ و ١١٧١٦ و ١١٧٦٢ و ١٢١٤٥ و ١٢٤١٤ و ١٢٥٣٠ و ١٢٧٨٧ و ١٣٠٠٨ و ١٣٠١٢ و ١٣١٨٠ و ١٣٣٩٩ و ١٣٤٠٤ و ١٣٦٢٨ و ١٣٦٤٤ و ١٣٧١١ و ١٤٠١٥ و ١٤٠٥٢ و ١٤٠٦٢ و ١٤٤٨٨ و ١٤٦١٢ و ١٥١٠٢ و ١٥٢٠٨ و ١٥٦٩٧ و ١٦٢٨١ و ١٦٤٣٠ و ١٧٣١٣ و ١٧٤٥٨ و ١٧٨٢٥ و ١٧٩٨٣ و ١٨٥٦٢ و ١٨٦٧٤ و ١٨٧٥٢ و ١٨٧٥٣ و ١٨٧٦٧ و ١٩٨٥٠ و ١٩٧٥١ و ٢١٨٥٣ و ٢٧٨١٤ و ٢٩٢٦٢) و (تهـ ٢٦٧٢)، و (تق ١٠٤٢)، وعلاء عبد الجبار (تس ١١٦٣٠ و ١٩١٠٩ و ١٩٤٤٤ و ٢٧٣٧٢)، و (تخ ١/ ٢٠٥ و ١/ ٢٣٨)، وعمران بن عيينة (تس ٧١٩ و ١٠٥٨٦ و ١٠٦١٤ و ١٠٨٠٩ و ١٣٨٢٩ و ١٤٤٧٠ و ١٤٦٩٥ و ١٥٢٢٥ و ١٥٢٩٢ و ١٥٣٥٤ و ١٥٣٩٨ و ١٥٤٠٧ و ١٦٧٣٠ و ١٧٨٢٣ و ١٩٩٥٥ و ١٩٩٩٢ و ٢١٣٤٥ و ١٢٧٧٩)، و (تخ ١/ ٨٦)، وأبي داود، عمر بن سعد الحفري (تس ١١٢٥٩ و ١٣٠٣٨ و ١٤٤٩٦ و ١٤٦٧٤ و ١٤٩٦٤ و ١٥٠٤٤ و ١٥٣٧٧ و ١٦٠٧٧ و ١٦٦٠٦ و ١٨٢٦٦ و ٢٠٩٢٧)، و (تهـ ١٢٧٥ و ١٣١٧) و (تخ ١/ ١٠٠ و ١/ ١٧٤)، و (ت)، وعمر بن عبيد الطنافسي (تس) ١٦١٣٧) و (ق)، وعمر بن هارون (تس ١٢٣٩٣ و ١٤٣٥٠)، وعمرو بن حماد (تس ٧٣٢ و ٧٥٥)، و (تخ ١/ ٤٧ و ١/ ٨٧ و ١/ ٢٠٣ و ١/ ٢٠٤)، وعمرو بن طلحة (تس ١٤٤١٨ و ١٥٣٨٢ و ١٥٣٨٣)، وعمرو بن عون',NULL,'266',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (269,'(تس ١٩٧١١)، وعمرو بن محمد العنقزي (تس ١٢٣٧٣ و ١٦٥٥٥ و ١٦٥٦٠ و ١٦٥٦٧ و ١٦٥٦٨ و ١٨٨٠٠ و ١٨٨٠١ و ١٨٨٠٧ و ١٨٨١٠ و ١٨٨٣٣ و ١٨٨٤٢ و ١٨٨٥٢ و ١٨٨٥٣ و ١٨٨٦١ و ١٨٨٩١ و ١٨٨٩٦ و ١٨٩٠٣ و ١٨٩٣٦ و ١٨٩٣٩ و ١٨٩٤٧ و ١٨٩٦٠ و ١٨٩٦٤ و ١٨٩٦٧ و ١٨٩٧٥ و ١٨٩٨٥ و ١٩٠١٢ و ١٩٠٢٠ و ١٩٠٢٣ و ١٩٠٣٩ و ١٩٠٥٣ و ١٩١٠٦ و ١٩١١٧ و ١٩١٣٠ و ١٩١٦٧ و ١٩١٧٤ و ١٩٢٠٦ و ١٩٢١٤ و ١٩٢٣٣ و ١٩٢٥١ و ١٩٢٥٤ و ١٩٢٦٩ و ١٩٢٧١ و ١٩٢٧٥ و ١٩٢٧٦ و ١٩٢٩١ و ١٩٣٢٢ و ١٩٣٣٧ و ١٩٣٤٣ و ١٩٣٥٩ و ١٩٣٧٠ و ١٩٣٧٤ و ١٩٣٨٣ و ١٩٣٩٣ و ١٩٤٠٠ و ١٩٤٠٢ و ١٩٤٢٣ و ١٩٤٢٦ و ١٩٤٥١ و ١٩٤٥٤ و ١٩٤٦٥ و ١٩٤٦٧ و ١٩٤٧١ و ١٩٤٨٠ و ١٩٤٨١ و ١٩٤٩٩ و ١٩٥٠٩ و ١٩٥١٥ و ١٩٥٢٧ و ١٩٥٦٣ و ١٩٥٦٦ و ١٩٥٧٣ و ١٩٥٨١ و ١٩٥٨٤ و ١٩٥٩٠ و ١٩٦١٨ و ١٩٦٢١ و ١٩٦٢٣ و ١٩٦٣٢ و ١٩٦٦٢ و ١٩٦٧٩ و ١٩٦٩٩ و ١٩٧٠٤ و ١٩٧١٤ و ١٩٧٢٢ و ١٩٧٣٥ و ١٩٧٤٠ و ١٩٧٨٢ و ١٩٧٨٨ و ١٩٧٨٩ و ١٩٧٩٦ و ١٩٧٩٩ و ١٩٨٠٤ و ١٩٨٠٦ و ١٩٨٥٦ و ١٩٨٥٨ و ١٩٨٨٢ و ١٩٨٨٧ و ١٩٨٨٩ و ١٩٩٣٣ و ١٩٩٣٩ و ١٩٩٤١ و ١٩٩٥١)، و (تخ ١/ ٢٠١ و ١/ ٢٠٤ و ١/ ٢٠٥ و ١/ ٢٠٦ ** و ١/ ٢٠٧ * و ١/ ٢٠٨ ** و ١/ ٢٠٩ * و ١/ ٢١٠ * و ١/ ٢١١ و ١/ ٢١٢ و ١/ ٢١٣ و ١/ ٢١٤ و ١/ ٢١٦ ** و ١/ ٢١٧*)، وعيسى بن يونس (تس ١٣٩٩٣ و ٢٧٩١٧)، و (تق ٦٠) و (ت، ق) وقبيصة [بن عقبة السوائي] (تس ١٣٣٤٠ و ١٤٦٧٥ و ١٥٠٥٥ و ١٨٦٩٠)، ومالك بن إسماعيل (تس ٤٤٣٦ و ٩٦٠٣ و ٩٦٠٧ و ٩٦٣٣ و ١٢٩٢٦ و ١٢٩٧٧ و ١٦٦٤٧)، و (تهـ ١٧١١)، ومحمد بن بشر العبدي (تس ٩٦٣٠ و ١١٩٢٤ و ١٢٠٩٧ و ١٢٩٩٧ و ١٣١٧٧ و ١٣٥١٣ و ١٧١١٤ و ١٧٣٧٨ و ١٧٣٩٦ و ١٩٠٥٤ و ١٩٤٠٤ و ١٩٤٤٠ و ١٩٤٤٣ و ٢٥٨٥٨ و ٢٥٨٤٨ و ٢٨٢٢٦ و ٢٨٤٦٥ و ٢٨٤٨٨ و ٢٨٥٧٤)، و (تهـ ١٤٦٩)، و (تق ١٠٣١)، و (تخ ٢/ ٩٥ و ٢/ ٩٩ و ٢/ ١٠١ و ٢/ ١٠٣)، ومحمد بن بكر البرساني (تس ١٠٦٥٦ و ١٠٦٧٢ و ١٢٦٤٥ و ١٣٩٧٢ و ١٣٩٩٠ و ١٣٩٩١ و ١٤٠٤٤ و ١٤٠٤٧ و ١٥٥٥٠ و ١٦٤٠٣ و ١٦٤٧٧ و ١٩٥٥٢ و ١٩٧٧١ و ٢١٤٦٩)، و (ت)، ومحمد بن جعفر (غندر) (تس ١٠٥١٣ و ١٠٧٢٢ و ١١٩٥٢ و ١٢٥٥٤ و ١٣٤٣٤ و ١٤٥٠٠ و ١٤٥١٢ و ١٥٤٠٦ و ١٥٤١٧ و ١٦٥٣٧ و ١٦٥٥٤ و ١٨٠٥٣ و ١٨٠٥٥ و ١٨٢٦٣ و ١٩١٨٩ و ٢٠٨٠٣ و ٢١٦٧٨ و ٢١٧٩٨ و ٢٧٦٣١ و ٢٧٨٤٢ و ٢٧٩٤٣ و ٢٧٩٧٦ و ٢٨١٩٧)، وأبي سفيان محمد بن حميد المعمري (تس ١٠٨٧٨ و ١٠٨٨٢ و ١٠٨٩٦ و ١٢٥٠٨ و ١٢٥٠٩ و ١٩٦٥٩ و ٢٧٦١٥)، و (ق)، ومحمد بن عبد الله الأنصاري (تس ١٢٤٦٢)، ومحمد بن عبيد [الطنافسي] (تس 