INSERT INTO [b29741] VALUES (273,'٢٨٢٩٤ - و ٢٨٣٠٢ و ٢٨٣٢٣ و ٢٨٣٩٤ و ٢٨٤٥٠ و ٢٨٤٨٠ و ٢٩٠٧٠ و ٢٩٨٣٩ و ٢٩٩٣٥ و ٢٩٩٧٣ و ٣٠٥١٨ و ٣١٤٦٢ و ٣٥٣٨٩ و ٣٨٣٧٦)، و (تهـ ٩٥ و ٦٥٤ و ٦٦٠ و ٧٥٦ و ١٠٥٢ و ١٣٢٦ و ١٦١٣ و ١٨٣٨ و ٢١٤٠ و ٢١٤٣ و ٢٣٤٢ و ٢٣٥٤ و ٢٣٥٥ و ٢٣٦١ و ٢٤٠٦ و ٢٤٠٧ و ٢٤٣٢ و ٢٦٢٣ و ٢٦٤٠ و ٢٦٩٦ و ٢٨٨٧ و ٢٩٦٥ و ٣٠٢٧)، و (تق ١٢٤ و ٤٥٧ و ٧٠٦ و ٨٢١ و ١٠٣٤)، و (تخ ١/ ٣٢ و ١/ ٤٤ و ١/ ٦٦ و ١/ ٩١ و ١/ ١٧٠ و ١/ ١٧٦ و ١/ ٢٦٢ و ١/ ٢٦٣ و ٢/ ١٠ و ٢/ ٢٦ و ٢/ ٦٢ و ٢/ ١ و ٢/ ١٢٣ و ٢/ ٢٣٠)، و (ت، ق) ووهب بن جرير (تس ١١٩٥٢)، ويحيى بن آدم (تس ٢١٥٦ و ١١٠٦٨ و ١١٧٢١ و ١٢٠٩١ و ١٢١٢٣ و ١٢٩٧٠ و ١٢٩٧٦ و ١٣٠٩٩ و ١٣٠٢١ و ١٣٠٢٣ و ١٣٠٣٧ و ١٣٠٦٠ و ١٣١٩٩ و ١٣٣٤٨ و ١٣٩٣٦ و ١٤٠٢٦ و ١٤٠٩٧ و ١٤١١٩ و ١٤٤١٠ و ١٤٤٥٦ و ١٤٤٦٣ و ١٤٥٦٧ و ١٤٦١٧ و ١٤٦٤٦ و ١٤٩٨٥ و ١٥٤٤١ و ١٦٤٢٣ و ١٧١٢٢ و ١٧٤٠٥ و ١٧٤٠٥ و ١٨٧٥٨ و ١٨٩٢٨ و ٢١١٦٥ و ٢١٢٨٨ و ٢١٤٧٣ و ٢١٦٤٢ و ٢١٦٤٤ و ٢١٧٨١ و ٢٧٦٠٥ و ٢٧٨٢٠ و ٢٨١٤٢)، و (تهـ ٤٠ و ٢٦٨٤)، و (تق ٩٦٠)، و (تخ ٢/ ١٣٠)، و (ت)، ويحيى بن رافع (تس ١٤٢٦٠ و ١٧٢٤٣)، ويحيى بن أبي روق الهمداني (تس ١١٧٥٥) ويحيى بن أبي زائدة (تس ١٤٧٧٢)، ويحيى بن سعيد العطار (تس ٢٧٩٣٨)، ويحيى بن سعيد القطان (تس ١٦٩٥ و ٢٢٣٤ و ١٠٥٣٣ و ١١٥١٢ و ١١٨١١ و ١٣١١٤ و ١٦٤٢٨ و ١٧٠٦٥ و ١٧٢٢٤ و ٢١١٤٧ و ٢١٧٦٧ و ٢٩٧٣٩ و ٣٦٥٨٩)، و (تهـ ٢٠٨٠ و ٢٣٣٦)، و (تخ ١/ ٢٦٢)، و (ت)، ويحيى بن سليم الطائفي (تس ١٦٢١٦ و ٢٨٠١٦)، و (تهـ ٩٤)، ويحيى بن عبد الملك بن أبي غنية (تس ١٠٦٠٢ و ١٨١٩٠ و ٢١٤٨٥)، و (تخ ١/ ١٠٥ و ١/ ١١٧)، ويحيى بن أبي عتبة مصحف من الذي قبله (تس ١١٠٨٩ و ١٥٦٢١)، وأبي تميلة [يحيى بن واضح] (تس ١٤٦٣٦ و ١٤٦٥٠)، ويحيى بن يمان (تس ٧٢٨ و ١٠٤٦٣ و ١٠٦١٥ و ١٠٦١٧ و ١٠٦٢٣ و ١٠٦٦٥ و ١٠٦٦٦ و ١٠٧٤٨ و ١١١٥١ و ١١٢٩٥ و ١١٢٩٧ و ١١٧٩٥ و ١١٧٩٩ و ١١٦٤٠ و ١١٧٨٢ و ١١٧٨٣ و ١٢٠٤٤ و ١٢٨٣٥ و ١٢٨٧٤ و ١٢٤٤٣ و ١٣٥١٥ و ١٣٥١٦ و ١٣٦٢٧ و ١٣٦٤٩ و ١٤١١٨ و ١٤٢٨١ و ١٤٥٢٠ و ١٤٦٩٦ و ١٤٧١١ و ١٤٧٣٥ و ١٥٠٠٩ و ١٥٠٥٣ و ١٥١٦٧ و ١٥٢٢٧ و ١٥٢٤٤ و ١٥٥١٩ و ١٥٥٦٦ و ١٦٦١٩ و ١٦٦٢٠ و ١٦٨٨٨ و ١٧١٤٣ و ١٧١٨٧ و ١٧٧١٨ و ١٧٨٣٦ و ١٨٠٣٢ و ١٨٢٢٤ و ١٨٢٢٧ و ١٨٦١٥ و ١٨٧٢٤ و ١٩٠٢٩ و ١٩٠٥١ و ١٩٠٦٢ و ١٩١٧٧ و ١٩٣١٣ و ١٩٦٧٨ و ٢١٦٠٣ و ٢١٩٠١ و ٢٧٦٠٨ و ٢٧٦٢٨ و ٢٧٦٦٤ و ٢٧٦٧٥ و ٢٧٦٧٨ و ٢٧٨٥٥)، و (تهـ ٢٠٣٠ و ٢٩٧١) و (ت)، ويزيد بن نوح (تس ١٠٤٨٩)، ويزيد بن هارون (تس ٦٦١٢ و ١٠٥١٦ و',NULL,'271',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (274,'١٠٧٢٨ - و ١٠٩٦٨ و ١١٦٠٢ و ١٠٩١٢ و ١١٩٩٤ و ١٢٠٠٣ و ١٢٠٨٧ و ١٢١٤٤ و ١٢٥٠٣ و ١٢٥٧٥ و ١٢٨١٦ و ١٣٤٩٨ و ١٣٨٣٤ و ١٥٤١٩ و ١٦٢٩٤ و ١٦٦٥١ و ١٦٧٥٣ و ١٧١٦٦ و ١٧٣٤٢ و ١٧٩٩٥ و ١٨٠٨١ و ١٩٨٣٢ و ٢٧٣٧٦ و ٢٨٠٠٥)، و (تهـ ٩٢٦ و ١٢٤٢ و ١٣١٦ و ١٤١٨ و ٢٥٢٣)، و (تق ٤٦١)، و (تخ: ١/ ٣١ و ١/ ٢٣٨)، و (ت)، ويعلى بن عبيد (تس ٤٤٠٠ و ١٤٦٠٩ و ١٧٣٢٣ و ١٧٧٧٣ و ١٧٩٨٢ و ١٨٨٧٢ و ١٩٠٧٩ و ١٩٥٩٦ و ٢١٩١٦ و ٢٧٦٨١)، و (هـ ١٢٧٢ و ٢٦٦٥) ويونس بن بكير (تس ٢١٤٠٤ و ٢٦٧٨٧ و ٣٤٢٢٠ و ٣٦٥٨٣) و (تق ٣٠٦)، و (ق)، وابن عبيدة – غير مسمى – (تس ٢٧٠٤)، وأبي أحمد الزبيري (تس ١١٠٢٦ و ١١٠٣٥ و ١٥٨٩١ و ١٦٣٣٣ و ١٧٤٩٦ و ٢٠٩٠٥ و ٢٠٩٠٧ و ٢٨٣٤٧)، وأبي الأحوص (تس ١٢٣٨٤)، وأبي بكر بن عياش (تس ١٠٢٣٩ و ١٣٩٢٩ و ١٦٦٧٢ و ١٧٣٧٦ و ١٩١٢٨ و ١٩٧٥٥)، وأبي الحسن العكلي (تس ١١٤٩٣) وأبي سلمة – غير مسمى، ولا منسوب – (تس ١٣٣٤٩ و ١٧٢٨٨)، وأبي سفيان الحميري (تس ١٢١٩٨) وأبي فزارة – غير مسمى، ولا منسوب – (تس ٤٤٩٢)، وأبي معاوية الضرير (تس ٤٣٩٦ و ٤٤١١ و ٤٨٩٢ و ٤٨٩٤ و ١٠٤٧٥ و ١٠٤٩٧ و ١٠٥٢١ و ١٠٦٤٤ و ١٠٦٧٤ و ١٠٧٥٨ و ١٠٥٣٤ و ١٠٩٧١ و ١١٤٦٣ و ١٢٠٢٧ و ١٢٣٣٢ و ١٢٤٦٠ و ١٣١٩٤ و ١٣٤٥٦ و ١٤٠٢٣ و ١٤٠٧٠ و ١٤٢٨٠ و ١٤٦١٠ و ١٥٥٥١ و ١٥٦٧١ و ١٥٨٩٣ و ١٦٣١٦ و ١٦٥١٨ و ١٦٥٤١ و ١٦٦٢٠ و ١٦٩٠٣ و ١٦٩٨٢ و ١٦٩٩٤ و ١٦٩٩٩ و ١٧٢١٥ و ١٧٤٤٧ و ١٨٢٥٢ و ١٨٧٤٩ و ١٩٩٦٥ و ٢١٤٩٧ و ٢١٥١٣ و ٢١٦٤٧ و ٢١٦٨٦ و ٢١٧٦٣ و ٢١٨١٢ و ٢١٨٤٩ و ٢١٨٩٤ و ٢١٩١٢ و ٢٧٣٧١ و ٢٧٣٨٥ و ٢٧٤٢٦ و ٢٧٩٢٩ و ٢٨١٨٩ و ٢٨٣٤٤)، و (ت ١/ ٩١ و ١/ ٢٦٢)، و (تهـ ٢٧٦٤)، و (تق ٤٩٤ و ٨٢٢)، وأبي نعيم (تس ١٣٧٧٠ و ١٣٧٧٨ و ١٥١٠٨ و ١٥١١٣ و ١٧٢٣٣ و ٢٨٤٩١) وأبي يزيد الرازي (تس ١٧٧٢٥ و ٢٧٨٠٨)
روى عنه: جعفر بن أحمد بن سنان " المعجم الأوسط " (٣/ ٣٥٣ / ٣٣٨٤)، وعبد الله بن الحسن بن النعمان " المعجم الأوسط (٤/ ٣٦٤ / ٤٤٤٧)، والقاسم بن زكريا " مسند أبي حنيفة " (١/ ٢١٣) ومحمد بن القاسم بن زكريا " سنن الدارقطني " (١/ ٣١٥ / ٣)، ومحمد بن هارون الروياني " مسند الروياني " (١/ ٤٠٢ / ٦٠٨) وموسى بن هارون " سنن البيهقي " (٧/ ٦٤ / ١٣١٧٢)، وأبو يعلى الموصلي " معجم أبي يعلى " (١/ ١٧٢ / ١٩٧).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: رجل',NULL,'272',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (275,'صدق، يتكلمون فيه لأشياء لقنوه، وليس بشيء، وكان يتهم بالكذب، وكان يتلقن ما لقن وكان شيخا فاضلا صدوقا، إلا أنه ابتلي بوراق سوء، كان يدخل عليه الحديث، وضعيفاً، وضعف، وكان من أوعية العلم على لين لحقه، وقد حسن له الترمذي، وامتنع أبو داود من التحديث عنه.
وقد تصحفت أسماء بعض شيوخه عدة مرات، مثل (عبد الله – مكبر) بدل (عبيد الله – مصغر) و (جويبر) – غير منسوب -، والصحيح (جرير) كما في الأثر رقم (تس ١٠٩٧٣) في طبعة دار الفكر، والصحيح الذي في طبعة العلمية.
وتصحف اسم شيخه في الأثرين رقم (تس ١٧٤٢٢) و (تس ١٧٥٤٧) من (زيد بن الحباب) إلى (زيد ويزيد بن حيان) وهو خطأ. والصحيح ما أثبته لأن (زيداً، ويزيد بن حيان)، كلاهما في طبقتين متقدمتين عن (زيد بن الحباب) إلا أن يكون بينه وبينهما، أو أحدهما، سقط كما هو روايته عن شبل في الأثر رقم (تس ٢١١٥٧)، وهو مستحيل!! وفي الإسناد سقط أكيد).
وتصحف اسم شيخه (معاوية بن عمرو) في الأثر رقم (٢١٨٤٢) فصارا شيخان بتصحف لفظة (بن) إلى (عن) فصار (معاوية عن عمرو)، وهو خطأ أكيد والصحيح ما أثبته، والله أعلم.
وتصحف اسم شيخه يحيى بن عبد الملك بن أبي غنية إلى ابن أبي عتبة.
وسقطت لفظة (محمد) من اسم شيخه، محمد بن فضيل، في الأثر رقم (١٧٣٥١) فصار كأنه شيخ آخر اسمه (فضيل).
وانظر: " العلل ومعرفة الرجال " (٢/ ٤٧ / ١٥١٠)، و" التاريخ الصغير " (٢/ ٣٨٥ / ٢٩٦٩)، و" سؤالات الآجري لأبي داود " (٩٥/ ٢)، و" سؤالات البرذعي لأبي زرعة " (١/ ٤٠٤)، و" الضعفاء والمتروكين للنسائي " (٥٥/ ٢٨٩)، و" الجرح والتعديل " (٤/ ٢٣١ / ٩٩١)، و" المجروحين والضعفاء والمتروكين " (١/ ٣٥٩ و ٤٧٢)، و" الكامل في الضعفاء " (٣/ ٤١٧ / ٨٤٤) و" تاريخ أسماء الثقات " (١٠٦/ ٤٩٨)، و" كتاب الدعاء " (١/ ٣٣٢)، و" تهذيب الكمال " (١١/ ٢٠٠ / ٢٤١٨)، و" الكاشف " (١/ ٤٤٩ / ٢٠٠٥)، و" المغني في الضعفاء " (١/ ٢٦٩ / ٢٤٨٩)، و" ميزان الاعتدال " (٢/ ١٧٣ / ٣٣٣٤)، و" سير أعلام النبلاء " (١٢/ ١٥٢ / ٥٤)، و" البداية والنهاية " (١٠/ ٣٦٧)، و" الكواكب النيرات " (١٣/ ١)، و" المقتنى في سرد الكنى',NULL,'273',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (276,'" (٢/ ٥٤ / ٥٤٧٩) و" الضعفاء والمتروكين لابن الجوزي " (٢/ ٤ / ١٤٥٢)، و" المقصد الأرشد " (١/ ٤٣١ / ٤٥٧) و" الإرشاد " (٢/ ٥٧١)، و" تهذيب التهذيب " (٤/ ١٠٩ / ٢١٠)، و" تقريب التهذيب " (١/ ٢٤٥ / ٢٤٥٦) و" لسان الميزان " (٧/ ٢٣٤ / ٣١٦١) و" رجال تفسير الطبري " (٢٣٥/ ١٠٣٤).
...
١٣٢ - أبو مالك الخزاعي (٢٥٢)
(طب: تس: ٥ ر / ٣ ش)
أبو مالك، سلام بن سالم، الطويل، الضرير، الخزاعي: من العاشرة، منكر الحديث.
روى عن: خلف بن ياسين الكوفي (تس ٢٥٢ و ١٧٢٦٤)، وأبي حفص عمر بن سعيد التنوخي (تس ٦٥٢٦)، ويحيى بن سليم الطائفي (تس ١٥٢٦٥ و ١٥٢٨٢)، والفضل بن جبير الوراق، وموسى بن إبراهيم المروزي، ويزيد بن هارون.
روى عنه: الحسين بن إسماعيل المحاملي.
وكل اسم لم يعز إلى كتاب - وميزته بالأسود العريض - من معجم شيوخه وتلاميذه، فهو من ترجمته في " تاريخ بغداد ".
التعديل والتجريح: ذكره الخطيب البغدادي في " تاريخ بغداد " (٩/ ١٩٨ / ٤٧٧٦) وساق بعضا من شيوخه وتلاميذه، ولم يذكر فيه جرحا ولا تعديلاً.
وذكره الشيخ شاكر في تحقيقه " لتفسير الطبري " (١/ ٢٢٨ / ٢٥٢) وقال: " شيخ الطبري لم أجد له ترجمة إلا في " تاريخ بغداد "، ولم يذكر فيه جرحاً، ولا تعديلاً أيضا ".
قلت: ووقفت له على ذكر في: " سؤالات ابن أبي شيبة لعلي ابن المديني " (١٦٧/ ٢٤١) في قوله " وسئل علي وأنا أسمع، عن سلام بن سالم الطويل، فقال: كانت له أحاديث منكرة "
وانظر: " رجال تفسير الطبري " (٢٣٧/ ١٠٣٩).
...
٦٠ - تمييز أبو محمد، سلم بن سالم البلخي.',NULL,'274',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (277,'روى عن: زيد بن العمي، وبينه وبين الطبري واسطتان.
وانظر: " تالي تلخيص المتشابه " (١/ ٧٢ / ١٩)، و" الكشف الحثيث " (١٢٢/ ٣٠٠).
...
٦١ - تمييز أبو المسيب، سلام بن سالم الواسطي.
روى عن: مبارك بن فضالة.
روى عنه: محمد بن موسى القطان الواسطي.
وانظر: " مسند البزار " (٩/ ٣٠١ / ٣٨٥٦).
...
١٣٣ - سليمان الخزاز (٩١٨٩)
(طب: ٨ ر / ٤ ش)
(تس: ٢ ر / ١ ش)، و (تهـ: ٥ ر / ٤ ش)، و (تق ١ ر / ١ ش)
سليمان بن ثابت، الواسطي، الخراز: من العاشرة، لم أعرفه، ولم أجد له ترجمة، ولم يعرفه الشيخ شاكر قبلي، (٩١٨٨)، ولم يتعرض الشيخ التركي في " تحقيقه " (٦/ ٦٤٧) لترجمته بشيء.
روى عن: سلم بن سلام الواسطي (تس ٩١٨٩ و ٩٨٩٠)، و (تهـ ٣٣٦ و ٣٣٧)، وسفيان بن عيينة (تهـ ٢٢٣٢)، و (تق ١٨٨)، ومحمد بن أبي فديك (تهـ ١٣١٥)، ومعن بن عيسى (تهـ ١٣٥٨)
روى عنه: أبو جعفر محمد بن جرير الطبري
التعديل والتجريح: ذكره الشيخ شاكر في (٨/ ٢٤١ / ٩١٨٩) وقال: " شيخ الطبري لم أعرف من هو؟ وثبت في ابن كثير " الجحدري " بدل الخراز ". انتهى
قلت: أخرجه ابن الجعد في " مسنده " (١/ ٤٧٧ / ٣٣٠٠)، ومن طريقه أخرجه ابن عبد البر في " التمهيد " (٥/ ٦٩). وذكره ابن كثير في " التفسير " (١/ ٤٨٣). والزيلعي في " نصب الراية " (٢/ ٢٥٢)
وباستقراء معجم شيوخه الذين روى عنهم، وتلاميذهم الذين رووا عنهم لم أجده ممن',NULL,'275',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (278,'روى عمن سبقت الإشارة إليه في " تهذيب الآثار ".
ووقع في نسبه تحريف في الحديث رقم (١٨٨) من " – الجزء المفقود – من تهذيب الآثار (١/ ١٣٦ / ١٨٨) حيث سكت عنه الشيخ علي رضا في تعليقه على الحديث!!.
وانظر: " رجال تفسير الطبري " (٢٤٦/ ١٠٨٤).
...
١٣٤ - القومسي (٢٠٣٩٢)
(طب: ٣ ر / ٣ ش)
(تس: ١ ر / ١ ش)، و (تهـ: ٢ ر / ٢ ش)
سليمان بن داود بن أبي نصر، القومسي، البغدادي: أخو أحمد، ومحمد، من الحادية عشرة لم أعرفه، ولم أجد له ترجمة، ولم يعرفه الشيخ شاكر قبلي (٢٠٣٩٣)، ولم يتعرض الشيخ التركي في تحقيقه " لتفسير الطبري " (١٣/ ٥٢٨) لترجمته بشيء.
روى عن: أبي توبة الربيع بن نافع (تس ٢٠٣٩٢)، وسعيد بن الحكم بن أبي مريم (تهـ ٢٩٠٠) ومحمد بن عباد (تهـ ١٦٧٣).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: ذكره الشيخ شاكر في (١٦/ ٤٤٢ / ٢٠٣٩٣) وقال: " شيخ الطبري لم أجد له ترجمة فيما بين يدي من الكتب ".
قلت وقد أورد الحديث شيخنا الألباني – رحمه الله – في معرض تخريجه لأحاديث كتاب " السنة لابن أبي عاصم " (٢/ ٣١٧ / ٧١٦)، فقال بعد أن ساق الحديث الطبري الذي نحن بصدده: " قلت: والقومسي هذا لم أجد له ترجمة، إلا في " الأنساب للسمعاني "، وقد حكى توثيقه مع أخيه محمد بن داود بن أبي نصر القوسي، عن محمد بن عبد الله الحضرمي.
وبهذا الإسناد يترجح عندي أن ذكر زيد بن سلام في إسناد ابن حبان عن معمر بن',NULL,'276',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (279,'يعمر محفوظ لم يتفرد به ابن يعمر فليس منكرا كما سبق في الحديث الذي قبله فليتنبه لهذا " انتهى كلام شيخنا رحمه الله.
قلت: وقد ساق الخطيب البغدادي ترجمة أخيه، (أحمد) في " تاريخ بغداد " (٤/ ١٤١ / ١٨٢٤) وقال عنه: " صاحب حديث فهم. سمعت محمد بن عبد الله بن سليمان [الحضرمي] يثني عليه وعلى أخيه توفي سنة ٢٥٩ ". انتهى.
ثم ساق ترجمة أخيه (محمد) في " التاريخ " (٥/ ٢٥٣ / ٢٧٤٦)، وقال فيه، نقلا عن الحضرمي أيضا: " كان هو وأخوه عندنا ها هنا من أصحاب الحديث ثقتين ". انتهى
قلت: ولعل هذا وجه توثيق الحافظ الذي ذكره شيخنا – رحمه الله – فيما سبق، وليس لصاحبنا سليمان هذا ذكر هنا، والله تعالى أعلم.
وانظر: " رجال تفسير الطبري " (٢٤٦/ ١٠٨٧).
...
١٣٥ - أبو أيوب الخياط (١١٤)
(الترمذي)
(طب: ٥٠ ر / ١٠ ش)
(تس ٤٠ ر / ٩ ش) و (تخ: ٢ ر / ٢ ش) و (تهـ: ٧ ر / ٣ ش) و (تق ١ ر / ١ ش)
أبو أيوب، سليمان بن عبد الجبار بن زريق، الخياط، البغدادي ثم السامرائي: من الحادية عشرة، صدوق.
روى عن: أحمد بن يونس (تهـ ٨٥٢)، و (تق ٢١٨)، وثابت بن محمد [الليثي] الزاهد (تس ٥٤٣٣ و ٢٧٧٥١ و ٢٩٤٧٩)، و (تهـ ١٨٦٨ و ١٨٧٤ و ١٩١٧)، و (تخ ١/ ١٦)، و [الحسن] بن عطية (تس ٢٤٣٥٩)، وخلف بن الوليد (تس ٨٥٠)، وأبي عاصم الضحاك بن مخلد النبيل (تس ٣٢٥٦٧) وعلي بن حكيم الأودي (تس ١١٤ و ١١٥)، وعلي بن قادم (تس ٢٦٥٠٧)، و (ت)، ومحمد بن الصلت (تس ٥٩٩٤ و ٧٩٦٣ و ٩٧٥٠ و ٩٨٠٤ و ١٥٧٥٨ و ١٩١٨٣ و ١٩٢٠٧ و ١٩٢٢٧ و ٢٢٢١٥ و ٢٣٥٧١ و',NULL,'277',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (280,'٢٣٥٧٦ - و ٢٤٨٩٩ و ٢٥٥٢٧ و ٢٦١٢٠ و ٢٨٧١٩ و ٢٩١٤٥ و ٣٠٢٢١ و ٣١١٥٢ و ٣١٩٢٦ و ٣٢٠٨٣ و ٣٢٣٢٩ و ٣٢٧٩٤ و ٣٢٩١٣ و ٣٢٩١٨ و ٣٣٠٤٩ و ٣٤٦٣٤ و ٣٤٨٣٢ و ٣٤٨٨٣ و ٣٥٧٩١)، و (تهـ ٢٨ و ٦١) و (تخ ١/ ٢٠٨)، وأبي عبد الله محمد بن يزيد بن خنيس المخزومي (تس ٢٣٧٠٩ و ٢٦٤٩٥)، ويوسف بن المنازل (تهـ ١٣٩٣) وعبد الله بن داود الخريبي " الجرح والتعديل " (١/ ١٥١).
روى عنه: محمد بن العباس البغدادي " تاريخ بغداد " (٣/ ١١١ / ١١١٦)، وأبو حاتم الرازي " الجرح والتعديل " (٨/ ٣٦٨ / ١٦٨٥) – ترجمة مسعر-.
التعديل والتجريح: كتب عنه أبو حاتم، وسئل عنه فقال: " صدوق ".
قلت: وقد سقط اسم شيخه (محمد بن الصلت) في الأثر (٩٧٥٠) من طبعة دار الفكر والأثر (٣٢٣٢٩) من الطبعتين حتى صار شيخه فيهما (أبو كدينة)، والصحيح ما أثبته والله أعلم.
وانظر " الجرح والتعديل " (٤/ ١٣٠ / ٥٦٦) و" الثقات " (٨/ ٢٨٠ / ١٣٤٤٧) و" تاريخ بغداد " (٩/ ٥٢ / ٤٦٣٢) و" تهذيب الكمال " (١٢/ ٢٠ / ٢٥٣٩)، و" الكاشف " (١/ ٤٦١ / ٢١٠٧)، و" تهذيب التهذيب " (٤/ ١٧٩ / ٣٤٩)، و" تقريب التهذيب " (١/ ٢٥٢ / ٢٥٨٣)، و" رجال تفسير الطبري " (٢٤٨/ ١٠٩٧)
...
١٣٦ - أبو داود الطلحي (٢٣٧٠٨)
(أبو داود)
(طب: ٢ ر / ٢ ش)
(تس: ١ ر / ١ ش)، و (تق ١ ر / ١ ش)
أبو داود، سليمان بن عبد الرحمن بن حماد بن عمران بن موسى بن طلحة، التيمي الكوفي، التمار، توفي سنة اثنتين وخمسين ومائتين، من العاشرة، صدوق.
روى عن: أبيه عبد الرحمن بن حماد (تق ٦٦٦) والعلاء بن عمرو الحنفي (تس ٢٣٧٠٨)
روى عنه: أبو بكر أحمد بن عمرو البزار " مسند البزار " (٣/ ١٦٣ / ٩٤٩).
التعديل والتجريح: قال محمد بن عبد الله الحضرمي: " ثقة ".',NULL,'278',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (281,'وقال الحافظ العسقلاني في التقريب: " صدوق ".
قلت: أغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري ".
وانظر: " الجرح والتعديل " (٤/ ١٢٩ / ٥٦٠)، و" تهذيب الكمال " (١٢/ ٢٥ / ٢٥٤٣)، و" الكاشف " (١/ ٤٦٢ / ٢١١٠)، و" تهذيب التهذيب " (٤/ ١٨١ / ٣٥٣)، و" التقريب " (١/ ٢٥٣ / ٢٥٨٧).
...
١٣٧ - – سليمان بن عبد الملك (تهـ ٢٤٢١)
(طب: تهـ ١ ر / ١ ش)
سليمان بن عبد الملك: من الحادية عشرة، لم أعرفه، ولم أجد له ترجمة، وقد أغفله الشيخ محمود شاكر في تحقيقه لـ " تهذيب الآثار " (٤/ ٣٨٧ – ٣٨٨/ ٦٥١)، وكذلك فعل الدكتور الرشيد أيضاً.
روى عن: محمد بن الصلت (تهـ ٢٤٢١).
روى عنه: محمد بن جرير الطبري (تهـ ٢٤٢١).
التعديل والتجريح: لم أقف له على ذكر في كتب الحديث، والرجال التي بين يدي وقد أغفله الشيخ شاكر، والدكتور الرشيد في تحقيقهما لـ " تهذيب الآثار ".
...
١٣٨ - أبو أيوب الغيلاني (٢٦٦٩١)
(مسلم، والنسائي)
(طب: ٦ ر / ٣ ش)
(تس: ٢ ر / ٢ ش)، و (تهـ: ٣ ر / ٢ ش)، و (تق ١ ر / ١ ش)
أبو أيوب، - وقيل: أبو عبد الرحمن-، سليمان بن عبيد الله بن عمرو بن جابر، الغيلاني، المازني، البصري، توفي سنة ست وأربعين ومائتين، من الحادية عشرة، صدوق.
روى عن: أيوب – غير مكني، ولا منسوب، وليس بالسختياني تأكيدا – (تس',NULL,'279',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (282,'٣٣٤٠٧ - )، وأبي قتيبة سلم بن قتيبة (تس ٢٦٦٩١)، و (تهـ ١٧٧٤ و ١٨٠٢)، و (تق ٥٤٠)، و (س)، وأبي مسهر (تهـ ١٨٠١)، وعبد الرحمن بن مهدي " الجرح والتعديل " (٤/ ١٢٧ / ٥٥٣).
روى عنه: أبو حاتم الرازي " الجرح والتعديل " (٤/ ١٢٧ / ٥٥٣).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقاً، لا بأس به.
قلت: أغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري " ولا يمكن أن يكون شيخه في الأثر رقم (تس: ٣٣٤٠٧) هو أيوب السختياني، وذلك لقدم وفاته، سنة إحدى وثلاثين ومائة، حيث لم يكن (سليمان الغيلاني) قد ولد بعد.
وقد أخرج الطبري أيضا في " التفسير " (٩/ ١١ / ٢٤٥٠٥)، أثرًا عن: محمد بن سليمان، الغيداني عن أبي قتيبة، لا أظنه إلا محرفا عن صاحبنا؛ صاحب الترجمة، لأنني لم أقف على من هكذا اسمه في كتب الحديث، أو الرجال التي بين يدي، واحتمال التحريف كبير جدا لقرب الحروف من بعضها رسما، ومع هذا فقد أفردت لـ (محمد بن سليمان الغيداني) ترجمة في موضعها، من كتابي هذا؛ أحببت ربطها بهذه، لما أظنه من أن صاحبهما واحد، والله أعلم.
وللغيلاني تراجم في: " الجرح والتعديل " (٤/ ١٢٧ / ٥٥٣)، و" ذكر أسماء التابعين " (٢/ ٩٩ / ٤٤٤)، و" رجال مسلم " (٢/ ٣٨٢ / ١٩٤٤)، و" تسمية من أخرجهم البخاري ومسلم " (١/ ١٢٦ / ٥٩٧) و" تهذيب الكمال " (١٢/ ٣٥ / ٢٥٤٦)، و" الكاشف " (١/ ٤٦٢ / ٢١١٣)، و" ميزان الاعتدال " (٣/ ٣٠٣ / ٣٤٩٢)، و" تهذيب التهذيب " (٤/ ١٨٣ / ٣٥٦)، و" تقريب التهذيب " (١/ ٢٥٣ / ٢٥٩٠)، و" لسان الميزان – ضمنا- " (٧/ ٢٣٧ / ٣٢١٣).
...
١٣٩ - أبو أيوب الأقطع (٦٢٥٢)
(طب: ١٥ ر / ٨ ش)
(تس ١٠ ر / ٦ ش)، و (تخ ١ ر / ١ ش) و (تهـ ٣ ر / ٣ ش)، و (تق ١ ر / ١ ش)',NULL,'280',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (283,'أبو أيوب، سليمان بن عمر بن خالد بن الأقطع، القرشي، العامري، الرقي، توفي سنة تسع وأربعين ومائتين، من العاشرة، وثقه ابن حبان.
روى عن: عبد الله بن المبارك (تس ٦٢٥٢ و ١٧٣٥٠ و ٢٩٠٧٣ و ٣٣٨٥٣)، و (تق ٩٧٧)، وعيسى بن يونس (تس ٢٢٦٢٣)، ومحمد بن سلمة الحراني (تس ١٢٦٧٤ و ١٢٧١١) (تخ ٢/ ١٤)، ومخلد بن حسين (تهـ ١٩٧٧)، ومروان بن معاوية (تهـ ٣٠٦)، ومسكين بن كثير (تس ١٢٧٣٨) ووهب بن راشد الرقي (تس ٢٦٠٨٦)، وأبيه عمر بن خالد (تس ٣٦٥٢٠ و ٣٦٥٢٤)، و (تهـ ١٤١٥)، وإبراهيم بن عبد السلام بن عبد الله بن بأباه القرشي المخزومي المكي " تهذيب الكمال " (٢/ ١٣٨ / ٢٠٦)، وأبي سلمة الحكم بن الحكم بن أبي تحية الحذاء " إكمال ابن ماكولا " (١/ ٤٩٨)، وخالد بن حيان الرقي، وسعيد بن يحيى الأموي، وعتاب ابن بشير الجزري " تهذيب الكمال " (١٩/ ٢٨٧ / ٣٧٦٣)، ومسلمة بن سعيد بن عبد الملك " الجرح والتعديل " (٨/ ٢٦٦ / ١٢١٦)، ومطرف بن زمان " الثقات " (٨/ ٢٨٠ / ١٣٤٤٤)، ويحيى بن سعيد الأموي.
روى عنه: إبراهيم بن عبد الله بن أيوب المخرمي " فضائل الصحابة " (٢/ ٦١٤ / ١٠٥٠)، وأحمد بن محمد بن الهيثم أبو بكر الدوري " تاريخ بغداد " (٥/ ١١٥ / ٢٥٢٦)، والخضر بن أحمد بن قيدهوز " الثقات " (٨/ ٢٨٠ / ١٣٤٤٤)، وأبو الطيب سليمان بن الحسن بن علي بن الجعد بن عبيد الجوهري " تاريخ بغداد " (٩/ ٦٣ / ٤٦٤٧)، وأبو حاتم محمد بن إدريس الرازي، ومحمد بن عبيدة بن يزيد " طبقات أصبهان " (٣/ ٤٧٣ / ٤٥٨)، ومحمد بن علي بن الحسن بن حرب " تاريخ بغداد " (٣/ ٧٢ / ١٠٣٩)، وأبو حامد محمد بن هارون الحضرمي " تاريخ بغداد " (٨/ ٣٩٢ / ٤٤٩٦).
وكل اسم لم يعز إلى كتاب، - وميزته بالأسود العريض-، من معجم شيوخه وتلاميذه، فهو من ترجمته في " الجرح والتعديل ".
التعديل والتجريح: ذكره عبد الرحمن بن أبي حاتم في " الجرح والتعديل " وسكت عنه، ولم يذكر فيه جرحا ولا تعديلاً.
وذكره ابن حبان في " الثقات ".',NULL,'281',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (284,'قلت: وقد تصحفت نسبته، من (الرقي)، إلى (البرقي)، في الآثار ذوات الأرقام (تس ١٢٦٧٤ و ١٢٧١١ و ١٢٧٣٨)، من طبعة دار الفكر، وفي الآثار، ذوات الأرقام، (تس ٣٣٨٥٣ و ٣٦٥٢٤)، من الطبعتين، وهو خطأ بلا ريب، والصحيح ما أثبته، والله تعالى أعلم.
وانظر: " الجرح والتعديل " (٤/ ١٣١ / ٥٧٠)، و" الثقات " (٨/ ٢٨٠ / ١٣٤٤٤) و" مولد العلماء ووفياتهم " (٢/ ٥٥٠) و" المقتنى في سرد الكنى " (١/ ١٠٠ / ٥٧١)، و" رجال تفسير الطبري " (٢٤٩/ ١١٠٣).
...
١٤٠ - سليمان بن مجالد (تخ ٣/ ٣٨٨)
(طب: تخ ١ ر / ١ ش)
سليمان بن مجالد، الضبي: من الحادية عشرة، لم أعرفه، ولم أجد له ترجمة وليست له رواية عن أحد، وإنما هي حادثة تاريخية جهادية يرويها عنه ابن جرير الطبري معلقة بغير إسناد.
...
١٤١ - أبو أيوب الرعيني (٢٧٤٧٥)
(النسائي – وفيه نظر!!)
(طب: تس: ٢ ر / ١ ش)
أبو أيوب، سليمان بن محمد بن سليمان، الرعيني، الحمصي: من الحادية عشرة مقبول.
روى عن: بقية بن الوليد (تس ٢٧٤٧٥ و ٣٨٠٧٩).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: قال النسائي: " صالح ".
قال العسقلاني في " تهذيب التهذيب " (٤/ ١٩٠ / ٣٧٤): " قال المزي " لم أقف على رواية عنه [يعني النسائي]. وقال الذهبي، عن ابن عساكر: أنه روى عنه ". انتهى.',NULL,'282',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (285,'قلت: لم أجد قول المزي في " تهذيب الكمال "!! ولعل قوله في " تحفة الأشراف " وهو ليس بين يدي الآن.
وقد أغفله الشيخان شاكر في تحقيقهما " لتفسير الطبري "
وانظر: " الجرح والتعديل (٤/ ١٤٠ / ٦١٤)، وتهذيب الكمال " (١٢/ ٦٠ / ٢٥٦٠).
...
١٤٢ - أبو حاتم السجستاني (١٦٤٦١)
(أبو داود – قوله في تفسير أسنان الإبل – والنسائي)
(طب: تس: ١ ر / ١ ش)
أبو حاتم، سهل بن محمد بن عثمان، السجستاني، النحوي المقرئ، البصري توفي سنة خمس وخمسين ومائتين، من الحادية عشرة، صدوق، وكانت فيه دعابة.
روى عن: أبي جابر محمد بن عبد الملك المكي [الحرمي] (تس: ١٦٤٦١)
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: قال أبو عبيد [الآجري]: " كان أبو داود لا يحدث عنه بشيء وسألته عن حديث من حديثه فأبى أن يحدثني به ".
وذكره ابن حبان في " الثقات " وقال: " وهو الذي صنف القراءات وكان فيه دعابة غير أني اعتبرت حديثه فرأيته مستقيم الحديث وإن كان فيه ما لا يتعرى عنه أهل الأدب ".
وقال أبو سعيد السيرافي: " كان كثير الرواية عن أبي زيد، وأبي عبيدة، والأصمعي عالما باللغة والشعر ".
وقال مسلمة بن قاسم: " أرجو أن يكون صدوقا ".
وقال أبو بكر البزار: " مشهور لا بأس به ".
وقال أبو عمر الداني في " طبقات القراء ": " أخذ القراءة عرضا عن يعقوب، وهو أكبر أصحابه وله اختيار في القراءة ".',NULL,'283',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (286,'قال المازني: " لو أدركه سلام أستاذ يعقوب، لاحتاج أن يأخذ عنه، ورثاه العباس بن الفرج الرياشي لما مات ".
قلت: وقد تحرفت الأسماء في إسناد هذا الخبر في نسخة دار الفكر المطبوعة سنة: ١٤٠٥ فجاءت هكذا: حدثنا (سهل بن محمد الحساني)، قال: ثنا (أبو جابر الحرثي)، قال: ثنا هشام بن الغازي الجرشي، عن نافع عن ابن عمر قال: وقف رسول الله صلى الله عليه وسلم يوم النحر عند الجمرات في حجة الوداع فقال: " هذا يوم الحج الأكبر ".
ووقع في نسخة دار الكتب العلمية (السجستاني)، يدل (الحساني)، وهي النسبة الصحيحة، وكذلك قال الشيخ شاكر أيضا في نسخته (١٤/ ١٢٤ / ١٦٤٤٧)، وقد اعتمدتها.
وأما بالنسبة لشيخه (أبي جابر الحرمي) – بحاء مهملة مفتوحة، بعدها راء مفتوحة - فهو (أبو جابر محمد بن عبد الملك المكي) نسبة إلى الحرم المكي، وليس إلى البلد (مكة).
وانظر: " الجرح والتعديل " (٤/ ٢٠٤ / ٨٨٢)، و" الثقات " (٨٩/ ٢٩٣ / ١٣٥١٦)، و" تهذيب الكمال " (١٢/ ٢٠١ / ٢٦٢٠) و" الكاشف " (١/ ٤٧٠ / ٢١٧٦)، و" التهذيب " (٤/ ٢٢٦ / ٤٥١)، و" التقريب " (١/ ٢٥٨ / ٢٦٦٦)، و" رجال الطبري " (٢٥٧/ ١١٣٧).
...
١٤٣ - سهل بن موسى الرازي (٦١٨)
(طب: ١٦ ر / ٩ ش)
(تس: ١٣ ر / ٨ ش)، و (تخ: ٢ ر / ٢ ش)، و (تهـ: ١ ر / ١ ش)
سهل بن موسى البزار – آخره راء مهملة – الزاي: من التاسعة، لم أعرفه، ولم أجد له ترجمة ولم يعرفه الشيخان شاكر في " التفسير " (١/ ١٧٤ / ١٨٠) – يقيناً – ولم يتعرض له الشيخ محمود شاكر في تحقيقه لـ " تهذيب الآثار " (٢/ ٢٩٩ / ٥٠٤) ولا الشيخ التركي في تحقيقه لـ " التفسير (١/ ٥٠٣).
روى عن: إسماعيل ابن علية (تس ٦١٨) وسفيان بن عيينة (تس ١٩٠٢٥) وعبد الرحمن بن مغراء (تخ ١/ ٥٣٩)، وعبد المجيد – غير مكني، ولا منسوب – (تس ٢٩٠٠٥)، و [محمد بن',NULL,'284',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (287,'إسماعيل] بن أبي فديك (تس ٤٣٢١ و ٩٤٨٣ و ١٨٥٠٢ و ٣٦٨٥٠)، (تخ ١/ ١٩٩) و [محمد بن عبد الله] بن نمير (تس ٢٢٦٥٣)، ووكيع بن الجراح (تس ٣٢١٤٥ و ٣٢١٤٦ و ٣٤٩٢٥) والوليد بن مسلم (تس ٢٨٢٥٢)، و (تهـ ٩٩٧)، ويحيى بن الضريس (تس ٢٥٤١١)، وسلمة بن شبيب " الحلية " (٧/ ٢٩)، وعمرو بن علي " الحلية " (١/ ٢٣٣)، ومحمد بن بشار (بندار) " مستخرج أبي نعيم " (٢/ ٢٥٢ / ١٤٦٨) ومحمد بن سفيان بن أبي الزرد " الحلية " (٣/ ٥) ومحمد بن عبد الأعلى الصنعاني " الحلية " (١/ ٢٣٤)، وأبي حاتم مسلم بن حاتم الأنصاري " الحلية " (٨/ ١٩٩).
روى عنه: سليمان بن أحمد " الحلية " (١/ ٢٣٣)، ومحمد بن عبد الرحمن بن مخلد " مستخرج أبي نعيم " (٢/ ٢٥٢ / ١٤٦٨)، وأبو الحسن الرامهرمزي " أمثال الحديث " (١/ ٩٢ / ٥٦).
التعديل والتجريح: ذكره الشيخ محمود شاكر في تحقيقه " لتفسير الطبري " (١/ ١٧٤ / ١٨٠) وقال: " موسى بن سهل الرازي " هكذا جاء في المطبوعة، ولكنه في المخطوط " سهل بن موسى الرازي " فرجح أخي السيد أحمد أنه خطأ من الناسخ، وأنه لم يجد له ترجمة ولكن أبا جعفر الطبري قد روى عنه في مواضع من تاريخه " سهل بن موسى الدافري " وهكذا هو في المخطوطة والمطبوعة فالصواب أن يكون في رقم (١٨٠) " سهل بن موسى الرازي " كما في المخطوطة هناك.
و " سهل بن موسى الرازي "، لم يترجم بهذا الاسم في الكتب، ولكني رأيت الطبري يروي عنه في " التاريخ " (١/ ١٦٩) " حدثنا سهل بن موسى الرازي قال: حدثنا ابن أبي فديك .. " فالذي في التاريخ يؤيد ما في التفسير. ثم روى عنه في " التاريخ " (٢/ ٢١٤) " حدثنا سهل بن موسى الرازي " قال حدثنا عبد الرحمن بن مغراء .. " فرأيت في ترجمة " عبد الرحمن بن مغراء " في التهذيب أنه يروي عنه " سهل بن زنجلة " و" سهل بن زنجلة " هو: سهل بن أبي سهل الرازي، روى عن جماعة كثيرة منهم يحيى بن سعيد القطان وسفيان بن عيينة وعبد الرحمن بن مغراء. وروى عنه ابن ماجه فأكثر، وأبو حاتم، وقدم بغداد سنة ٢١٣ وترجم له الخطيب البغدادي في " تاريخ بغداد " (٩/ ١١٦ / ١١٨) ولم يذكروا تاريخ وفاته،',NULL,'285',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (288,'فأخشى أن يكون " سهل بن أبي سهل الرازي " هو " سهل بن موسى الرازي " نفسه – لم يعرفوا اسم أبيه " موسى " وعرفه الطبري لأنه من ناحية بلاده، وأرجو أن يأتي بعد في أسانيد أبي جعفر ما يكشف عن الحق في ذلك. انتهى
قلت: وذكر الطبري (موسى بن سهل الرازي) في " تفسيره " مرة واحدة في الأثر رقم (تس ١٨٠) أما (سهل بن موسى الرازي) فقد ذكره (١٧ مرة).
وأما (سهل بن أبي سهل الرازي)، و (سهل بن زنجلة)، فلم أقف لهما على ذكر عنده لا في شيء من كتبه. وقد ترجمت لموسى بن سهل عند الأثر رقم (تس: ١٨٠)
وانظر: " رجال تفسير الطبري " (٢٥٥/ ١١٢٩)، و (٥٤٧/ ٢٦٣٠).
...
١٤٤ - سوار العنبري (٤١٧)
(أبو داود، والترمذي، والنسائي)
(طب: ٥٣ ر / ١٢ ش)
(تس: ٤١ ر / ٩ ش)، و (تهـ: ١٠ ر / ٦ ش) و (تق ٢ ر / ١ ش)
أبو عبد الله، سوار بن عبد الله بن سوار، التميمي، العنبري، البصري: القاضي، ابن القاضي، ابن القاضي، قاضي الرصافة، وغيرها، مولده بسجستان سنة اثنتين وثمانين ومائة، وتوفي بعدما عمي بأيام، يوم الأربعاء، لأربع ليال بقين من شوال، سنة خمس وأربعين ومائتين، من العاشرة ثقة!! غلط من تكلم فيه.
روى عن: بشر بن المفضل (تس ٧٩٧ و ٤٥٩٤ و ٥٥٦٤ و ١٠١٥٦ و ١١٣١٦ و ١١٩٥٦ و ٢٩٠٢٦) و (تهـ ٥٢٨ و ٢٨٩٠)، وخالد بن الحارث (تس ١٧٤٨ و ١٧٥٧ و ١٠١٧٥)، و (س) وسفيان بن عيينة (تس ٤١٧ و ٨١٣٠ و ١٨٣٨٣)، وأبي داود سليمان بن داود الطيالسي (تهـ ١٨٧٦ و ١٨٨٣) و (س)، وأبيه عبد الله بن سوار (تس ١٢٨٥٢ و ١٧٢٨٧ و ٢٠٦٧٠ و ٢٠٧٣٩ و ٢٨٧٠٢ و ٣٠٨٧١)، وعبد الرحمن بن مهدي (تس ٤٠٠٩)، وعبد الملك بن الصباح (تس ٢٦٤٢ و ٦٣٧٧ و ١٦٠٢٢ و ١٩١٢٤ و)، و (تق ٢٧٧)',NULL,'286',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (289,'ومعاذ بن معاذ العنبري (تهـ ٢٨٩٥)، ومعتمر بن سليمان (تس ٧٣٤٤ و ١٣٨٥٦ و ٢٠٢٢٤ و ٢٠٢٤٥ و ٢٨٤٢٩)، و (تهـ ١٦١)، و (تق ٦٢٨)، و (ت، س)، وموسى بن عبيدة الربذي (تس ٣٣٣٩٧)، ويحيى بن حماد (تهـ ١٧٨٦)، ويحيى بن سعيد القطان (تس ٣٢١٤ و ١١٥١١ و ١٧٠٦٥ و ١٧٠٦٧ و ٢١٧٦٧ و ٢٥٠٦٩ و ٢٥٠٧٩ و ٣٦٤٦٥ و ٣٧٢٢٩ و ٣٧٢٤٢ و ٣٧٩٥٠)، و (تهـ ١٦٨ و ٧٠٥ و ٩٩٦)، و (ت).
روى عنه: أبو بكر أحمد بن محمد بن سلام البغدادي " مسند الشهاب " (٢/ ٣٠ / ٨٢٠) وزكريا بن يحيى الساجي " المعجم الكبير (٧/ ٨٤ / ٦٤٤٤)، وعلي بن الجعد الجوهري " مسند ابن الجعد " (١/ ١٩٩ / ١٣١١).
التعديل والتجريح: قال علي ابن المديني: " هو ثقة عندنا "
وقال أحمد بن حنبل: " ما بلغني عنه إلا خيراً ".
وقال النسائي: " ثقة ".
وقال أحمد بن كامل: " كان فقيها، فصيحا، أديبا، شاعراً، عظيم اللحية ".
وقال الذهبي: " الإمام، العلامة، القاضي ".
وقال العسقلاني: " ثقة، من العاشرة، غلط من تكلم فيه ".
وانظر: " كتاب بحر الدم " (١/ ١٩٣ / ٤١٥) و" سؤالات ابن أبي شيبة لابن المديني " (١/ ٥٨ / ٢٤) " التاريخ الصغير " (٢/ ٣٨٣ / ٢٩٦١)، و" سؤالات الآجري لأبي داود " (١/ ٢٨٧ / ٤١٥)، و" مولد العلماء ووفياتهم " (١/ ٢٠٥)، و" الجرح والتعديل " (٤/ ٢٧١ / ١١٧٤)، و" الثقات " (٨/ ٣٠٢ / ١٣٥٧١)، و" تاريخ بغداد " (٩/ ٢١٠ / ٤٧٨٨)، و" تهذيب الكمال " (١٢/ ٢٣٨ / ٢٦٣٨)، و" الكاشف " (١/ ٤٧٢ / ٢١٩١)، و" البداية والنهاية " (١٠/ ٣٦١)، و" سير أعلام النبلاء " (١١/ ٥٤٣ / ١٦٠)، و" المقصد الأرشد " (٢/ ١١٥ / ٥٩٩)، و" تهذيب التهذيب " (٤/ ٢٣٦ / ٤٧٤)، و" تقريب التهذيب " (١/ ٢٥٩ / ٢٦٨٤)، و" رجال تفسير الطبري " (٢٥٨/ ١١٤٥).
...
٦٢ - تمييز أبو عبد الله، سوار بن عبد الله بن قدامة، العنبري، البصري، القاضي، جد صاحب الترجمة، ولد بسجستان، سنة أربع وثمانين وتوفي سنة ست',NULL,'287',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (290,'وخمس ومائة؛ وقال الطبري: " سنة سبع وخمسين ومائة " انتهى
قلت: أي قبل ولادة حفيده بست وعشرين سنة، من السابعة، وهو أشهر في القضاء من حفيده وحفيده أشهر في الحديث منه، صدوق، محمود السيرة.
وانظر: " الطبقات الكبرى " (٧/ ٢٦٠)، و" طبقات خليفة " (٢٢١)، و" معرفة أسماء الثقات " ١ (٤٤١/ ٦٩٧)، و" الكنى والأسماء " (١/ ٤٨٦ / ١٨٨٣)، و" ضعفاء العقيلي " (٢/ ١٧٠ / ٦٨٦)، و" الجرح والتعديل " (٤/ ٢٧١ / ١١٧١)، و" تاريخ الطبري " (٤/ ٥١١)، و" الثقات " (٦/ ٤٢٣ / ٨٣٩٥) و" مشاهير علماء الأمصار " (١/ ١٥٨ / ١٢٤٦)، و" الكامل في الضعفاء " (٣/ ٤٥١ / ٨٧٠) و" مولد العلماء ووفياتهم " (١/ ٢٠٩ / ٣٦٤) و" المقتنى في سرد الكنى " (١/ ٣٤٥ / ٣٦٣٤)، و" المغني في الضعفاء " (١/ ٢٨٩ / ٢٦٩٨)، و" تهذيب التهذيب " (٤/ ٢٣٦ / ٤٧٥) و" تقريب التهذيب " (١/ ٢٥٩ / ٢٦٨٥)، و" لسان الميزان " (٣/ ١٢٦ / ٤٤٣).
...
٦٣ - تمييز سوار بن عبد الله، شيخ
يروي عن: رفيع أبي العالية.
روى عنه: عكرمة بن عمار
وانظر: " التاريخ الكبير " (٤/ ١٦٨ / ٢٣٥٤)، و" الجرح والتعديل " (٤/ ٢٧١ / ١١٧٢)، و" الثقات " (٦/ ٤٢٣ / ٨٣٩٤)
...
٦٤ - تمييز أبو سنان، سوار بن عبد الله، الهذلي، صاحب البصري، وليس بالقاضي.
روى عنه: أبو عبيدة الحداد.
وانظر: " التاريخ الكبير " (٤/ ١٦٨ / ٢٣٥٥)، و" الجرح والتعديل " (٤/ ٢٧١ / ١١٧٣)، و" الثقات " (٦/ ٤٢٣ / ٨٣٩٦)
***',NULL,'288',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (291,'<span data-type="title" id=toc-32>[١١] باب الصاد المهملة </span>(من اسمه صالح)
١٤٥ - أبو معمر بن حرب (٣٣٦٥٣)
(طب: ٢ ر / ٢ ش)
(تس: ١ ر / ١ ش)، و (تخ: ١ ر / ١ ش)
أبو معمر، صالح بن حرب بن خالد الهاشمي، مولاهم، البغدادي، يعتبر حديثه إذا روى عن الثقات.
روى عن: إسماعيل بن يحيى التميمي (تس ٣٣٦٥٣)، وثمامة بن عبيدة العبدي السلمي (تخ ١/ ٨١)، وخالد بن يزيد الهدادي، وسلام بن أبي خبزة، وعبد الأعلى بن عبد الأعلى السامي، وعيسى بن شعيب " شعب الإيمان " (٥/ ٢٠٥ / ٦٣٧٢).
روى عنه: أحمد بن أبي عوف البزوري، وعبد الله بن محمد بن ناجية، وإسحاق بن إبراهيم بن يونس " شعب الإيمان " (٥/ ٢٠٥ / ٦٣٧٢)، وعبد الله بن إسحاق بن إبراهيم المدائني " المعجم الأوسط " (٤/ ٣٧٣ / ٤٤٦٨)، وعبد الله بن حاتم " معجم الصحابة " (٢/ ١٠٦)، وعبد الله بن العباس الطيالسي " المعجم الكبير " (٧/ ٢٢٢ / ٦٩٢٥)، وعبيد العجل، وعلي بن بيان المطرز البغدادي " المعجم الأوسط (٤/ ١٣٠ / ٣٧٩٠) والفضل بين عبيد الله أبو العباس الحميري الإستراباذي " تاريخ جرجان " (١/ ٣٣٠ / ٦٠١)، ومحمد بن إسحاق الثقفي " الثقات " (٨/ ٣١٨ / ١٣٦٤٨) ومحمد بن عبد الرحيم بن شبيب " طبقات المحدثين بأصبهان " (٣/ ٤٦٩ / ٤٥٧)، وأبو أحمد محمد بن محمد الحافظ النيسابوري " تاريخ بغداد " (٩/ ٣١٦ / ٤٨٥٣)، وأبو حامد محمد بن هارون الحضرمي، وأبو بكر الإسماعيلي " معجم شيوخ الإسماعيلي " (٣/ ٧٦٥)، وأبو بكر بن أبي الدنيا، وأبو يعلى " معجم أبي يعلى " (١/ ١٨١ / ٢٠٧).
وكل اسم لم يعز إلى كتاب، - وميزته بالأسود العريض -، من معجم شيوخه وتلاميذه، فهو من ترجمته في " تاريخ بغداد ".
التعديل والتجريح: ذكره محمد بن حبان في " الثقات " (٨/ ٣١٨ / ١٣٦٤٨) وقال:',NULL,'289',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (292,'" يعتبر حديثه إذا روى عن الثقات ".
وترجم له الخطيب البغدادي في " تاريخ بغداد " (٩/ ٣١٦ / ٤٨٥٣)، ولم يذكر فيه جرحاً ولا تعديلاً.
وذكره الذهبي في " المقتنى في سرد الكنى " (٢/ ٩١ / ٥٩٣٥).
وقال العسقلاني في " لسان الميزان " (٣/ ١٦٨ / ٦٧٩): " كنيته أبو محمد ".
قلت: والظاهر أنه خطأ، أو أن له كنيتان، (أبو محمد)، و (أبو معمر) والله أعلم.
وقد أغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري ".
١٤٦ - صالح بن سمال (تخ ٢/ ٢٢٩)
صالح بن سمال: من الحادية عشرة، لم أعرفه، ولم أجد له ترجمة، وقد روى الطبري عنه، مقروناً بأبي كريب، محمد بن العلاء الهمداني، ولعل في اسمه تحريف ما، والأثر الذي جاء من طريقة مشهور معروف، رواه مسلم برقم (١٦٣٦) من طريق إسحاق بن إبراهيم، به، وأخرجه ابن سعد (٢/ ٢٤٣)، من طريق حجاج بن نصير به أيضاً.
روى عن: وكيع وهو ابن الجراح (تخ ٢/ ٢٢٩).
روي عنه: محمد بن جرير الطبري وقرنه بأبي كريب (تخ ٢/ ٢٢٩).
١٤٧ - أبو الفضل الكشميهني (٢٢٤)
(مسلم، والترمذي)
(طب: ٢١ ر / ١٤ ش)
(تس ٩ ر / ٩ ش)، و (تخ ١ ر / ١ ش)، و (تهـ ٧ ر / ٣ ش)، (وتق ٤ ر / ١ ش)
أبو الفضل – ويقال: أبو العباس – صالح بن مسمار، السُّلَمي، الكُشّميهَني المروزي: توفي سنة ست وأربعين ومائتين، وقيل: سنة خمسين ومائتين، من صغار العاشرة، صدوق.',NULL,'290',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (293,'روى عن: آدم بن أبي إياس (تس ٣٧٨٩٤)، والحارث بن عمران (تس ٣٥٣٩٦)، وخالد بن خداش (تق ٩٩٧)، وزيد بن الحباب (تس ٢٢٤ و ٥٩٨٤)، وسعيد بن أبي مريم (تس ١٨١٤٨) و (تخ ١/ ١١٣)، وسفيان بن عيينة (تهـ ٢٨٤٧ و ٢٨٤٩ و ٣٠٨٠)، و (تق ٧٤٩)، ومحمد بن إسماعيل بن أبي فديك (تس ٢٧١٠٤)، و (ت)، ومعاذ بن هشام الدستوائي (تهـ ٩٤٠ و ١٧١٨ و ٢٨٦٣)، و (م)، ومحمد بن سنان العوفي (تس ١٦٩٩٨)، ومعاذ بن هشام الدستوائي (تق ٥٨٥)، و (م)، ومعلى بن أسد (تق ٩٧٩)، والنضر بن شميل (تس ١٦٧٨٢)، والوليد بن عبد الملك بن مسرح الحراني (تس ٢٣٠٢٢)، ويحيى بن صالح الوحاظي (تهـ ١٧٣٤)، وعبد الله بن الليث المروزي " تاريخ بغداد " (١٠/ ٤٦ / ٥١٧٧).
روى عنه: إبراهيم بن نسر الضبي - سنن البيهقي " (٦/ ١٤٠ / ١١٥٤٥) وعلي بن سعيد الرازي " المعجم الأوسط " (٤/ ١٨٦ / ٣٩٣٢).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً صدوقاً.
وانظر: " الجرح والتعديل " (٤/ ٤١٥ / ١٨٢٤) و" الثقات " (٨/ ٣١٨ / ١٣٦٥١)، و" تهذيب الكمال " (١٣/ ٩١ / ٢٨٣٨)، و" الكاشف " (١/ ٤٩٨ / ٢٣٦٢)، و" تهذيب التهذيب " (٤/ ٣٥٣ / ٦٩٦)، و" تقريب التهذيب " (١/ ٢٧٤ / ٢٨٨٨)، و" رجال تفسير الطبري " (٢٧٥/ ١٢٢١).
٦٥ - تمييز صالح بن مسمار، البصري، سكن الجزيرة، قديم من السابعة، مقبول، يروي المراسيل.
وانظر: " العلل ومعرفة الرجال " (٢/ ٤٦٦ / ٣٠٧٢)، و" التاريخ الكبير " (٤/ ٢٨٩ / ٢٨٥٣)، و" المنفردات والوحدان " (١٧٨/ ٧٣٧)، و" الجرح والتعديل " (٤/ ٤١٤ / ١٨٢٣)، و" الثقات " (٦/ ٤٦٥ / ٨٦٠٤) و" تهذيب الكمال " (١٣/ ٩٢ / ٢٨٣٩)، و" المقتنى في سرد الكنى " (٢/ ٤٦ / ٥٣٧٢)، و" تهذيب التهذيب " (٤/ ٣٥٤ / ٦٩٧)، و" تقريب التهذيب " (١/ ٢٧٤ / ٢٨٨٩).',NULL,'291',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (294,'<span data-type="title" id=toc-33>[١٢] باب الطاء المهملة </span>(من اسمه طليق) *
١٤٨ - أبو سهل البزاز (٣٧٦٨)
(النسائي)
(طب: ٤ ر / ٢ ش)
(تس: ٢ ر / ٢ ش)، و (تهـ: ٢ ر / ١ ش)

أبو سهل، طليق بن محمد بن السكن بن مروان، الواسطي، البزاز: من كبار الحادية عشرة، ثقة.
روى عن: أسباط [ابن محمد] (تس ٣٧٦٨) ويزيد بن هارون (تس ٢١٣١٢) (تهـ ٦٣٥ و ٦٣٦).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: ذكره ابن حبان في " الثقات " وقال: " استقامته في الحديث؛ استقامة الأثبات ".
وانظر: " الثقات " (٨/ ٣٢٨ / ١٣٧٠٥)، و" تهذيب الكمال " (١٣/ ٤٦٤ / ٢٩٩٦)، و" الكاشف " (١/ ٥١٦ / ٢٤٩٢)، و" تهذيب التهذيب " (٥/ ٣١ / ٥٧)، و" تقريب التهذيب " (١/ ٢٨٤ / ٣٠٤٨)، و" رجال تفسير الطبري " (٢٨٩/ ١٢٨٢).

<span data-type="title" id=toc-34>[١٣] باب العين المهملة </span>(من اسمه عباد)
١٤٩ - أبو سعيد الرواجني (٥٤٧٨)
(البخاري والترمذي، وابن ماجه)
(طب: ١٠ ر ٩ ش)
(تس: ٨ ر / ٧ ش)، و (تخ: ١ ر / ١ ش)، و (تهـ: ١ ر / ٢ ش)
أبو سعيد، عباد بن يعقوب الرواجني - بتخفيف الواو، وبالجيم المكسورة، والنون الخفيفة - وقيل الأسدي، الكوفي: توفي سنة خمسين ومائتين، من العاشرة رافضي، صدوق؛',NULL,'292',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (295,'حديثه في البخاري مقروناً، وبالغ ابن حبان فقال: " يستحق الترك ".
روى عن: بشر بن عمارة (تهـ ٤٨١)، والحكم بن ظهير (تس ٣٧٥١٦)، وشريك بن عبد الله النخعي (تس ٥٤٧٨)، وعبد الله بن بكير (تهـ ٤٨١)، و [عبد الرحمن بن محمد] المحاربي (تس ١٨٢٠٢)، و (تخ ١/ ١١٨)، ومحمد بن سليمان (تس ٣٧٠٢٢ و ٣٧٨٧٩)، ومحمد بن فضيل بن غزوان (تس ٢٢٦٣٣)، ونوح بن دراج (تس ٣٧٨٨٨)، ويزيد أبي حازم مولى الضحاك (تس ٣٤١٨٣)، والزبير بن بكار " تاريخ بغداد " (١٤/ ٤١).
روى عنه: ومحمد بن القاسم المحاربي " تاريخ بغداد " (١٢/ ٩ / ٦٣٦٥).
التعديل والتجريح: اضطربت أقوال أئمة الجرح والتعديل فيه بين كونه: شيخاً ثقةً، صدوقاً في روايته، متهماً في دينه، وفيه غلو فيما فيه من التشيع، وروى أحاديث أنكرت عليه في فضائل أهل البيت وفي مثالب غيرهم، وفسق، ونسب إلى أنه يشتم السلف، وأنه كان رافضياً، داعية إلى الرفض ومع ذلك يروي المناكير، عن أقوام مشاهير، فاستحق الترك، وأنه من غلاة الشيعة، ورؤوس البدع.
وانظر: " التاريخ الكبير " (٦/ ٤٤ / ١٦٤٥)، و" الكنى والأسماء " (١/ ٣٦٨ / ١٣٥٤) و" ذكر أسماء التابعين ومن بعدهم " (١/ ٢٥٦ / ٧٤٣) و" رجال البخاري " (٢/ ٨٦٣ / ١٤٥٩)، و" الضعفاء والمتروكين لابن الجوزي " (٢/ ٧٧ / ١٧٨٨)، و" الجرح والتعديل " (٦/ ٨٨ / ٤٤٧)، و" الكامل في الضعفاء " (٤/ ٣٤٨ / ١١٨٠) و" المجروحين " (٢/ ١٧٢ / ٧٩٧)، و" سؤالات الحاكم للدارقطني " (١/ ٢٥٣ / ٤٢٥) و" التعديل والتجريح " (٢/ ٩٢٩ / ١٠١٦)، و" تهذيب الكمال " (١٤/ ١٧٥ / ت ٣١٠٤)، و" الكشف الحثيث " (١٤٦/ ٣٧٠)، و" تهذيب التهذيب " (٥/ ٩٥ / ٨٣)، و" الكاشف " (١/ ٥٣٢ / ١٥٨١)، و" من تُكلم فيه " (١/ ١٠٦ / ١٧٦)، و" السير " (١١/ ٥٣٦ / ١٥٥) و" ميزان الاعتدال " (٤/ ٤٤ / ٤١٥٤) و" المغني " (١/ ٣٢٨ / ٣٠٥٩) و" تقريب التهذيب " (١/ ٢٩١ / ٣١٥٣)، و" لسان الميزان " (٧/ ٢٥٦ / ٣٤٥٤)، و" رجال تفسير الطبري " (٢٩٩/ ١٣٢٣).
١٥٠ - أبو الفضل العنبري (٨٠)
(البخاري - تعليقاً -، ومسلم، والأربعة.',NULL,'293',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (296,'(طب: ٧ ر / ٤ ش)
(تس: ٦ ر / ٤ ش)، و (تهـ: ١ ر / ١ ش)
أبو الفضل، عباس بن عبد العظيم بن إسماعيل بن توبة، العنبري، البصري، الحافظ: توفي سنة ست وأربعين ومائتين، من الحادية عشرة، ثقة، حافظ.
روى عن: جعفر بن عون (تس ٢٤٤٦١ و ٣٤٢٢٦)، وحبان بن هلال (تس ٨٠ و ٧٨٤٥)، و (تهـ ٩٩٥)، ومحمد بن خالد بن عثمة (تس ٩٠)، ويحيى بن كثير العنبري (تس ٢٢٦٤٣)، في (ت)، وأحمد بن يونس " تهذيب الكمال " (٦/ ١٧٧ / ١٢٣٨)، وأبي همام صلت بن محمد بن عبد الرحمن الخاركي " الجرح والتعديل " ت (٤/ ٤٤١ / ١٩٣٣)، وعاصم بن مهجع الأسدي " الثقات " (٨/ ٥٠٦ / ١٤٧٠٤)، وعمرو بن علي " الجرح والتعديل " (٦/ ٢٤٩ / ١٣٧٥)، وعمرو بن هارون المقرئ " الثقات " (٨/ ٤٨٥ / ١٤٥٦٩)، وأبي فاطمة مسكين بن عبد الله " الجرح والتعديل " (٨/ ٣٢٩ / ١٥٢٣).
روى عنه: الحسين بن بيان العسكري " تهذيب الكمال " (٦/ ٣٥٥ / ١٢٩٩)، وعبد الله بن قحطبة " الثقات " (٨/ ٤٨٥ / ١٤٥٦٩).
التعديل والتجريح: لم أقف له على تجريح يذكر، وتراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، مأموناً، صدوقاً، إماماً، ثبتاً، حافظاً، حجةً من عقلاء الناس، وفضلائهم ونبلائهم ومن سادات المسلمين.
وانظر: " التاريخ الكبير " (٧/ ٦ / ٢٣)، و" التاريخ الصغير " (٢/ ٣٨٤ / ٢٩٦٣)، و" الكنى والأسماء (١/ ٦٧٦ / ٢٧٣٦)، و" ذكر أسماء التابعين " (٢/ ١٨٩ / ٩٤٢)، و" رجال صحيح البخاري " (٢/ ٨٧٩ / ١٥٠٥)، و" مولد العلماء ووفياتهم " (٢/ ٥٤٢) و" المقتنى في سرد الكنى " (٢/ ١٦ / ٥٠٦٩)، و" طبقات المحدثين " (١/ ٩٧ / ١٠٩٣)، و" طبقات الحفاظ " (١/ ٢٣٢ / ٥١٩)، و" المقصد الأرشد " (٢/ ٢٧٦ / ٧٧٨)، و" الجرح والتعديل " (٦/ ٢١٦ / ١١٩٠)، و" الثقات " (٨/ ٥١١ / ١٤٧٣٨)، و" تاريخ بغداد " (١٢/ ١٣٧ / ٦٥٩٠)، و" والتعديل التجريح " (٣/ ١٠٠٩ / ١١٥٥)، و" تهذيب الكمال " (١٤/ ٢٢٢ / ٣١٢٨)، و" الكاشف " (١/ ٥٣٥ / ٢٦٠١)، و" التذكرة (ت ٢/ ٥٢٤ / ٥٤١)، و" سير أعلام النبلاء " (١٢/ ٣٠٢ / ١١١) و" تهذيب التهذيب " (٥/ ١٠٧ / ٢١٣)، و" تقريب التهذيب " (٢٣٦/ ٣١٧٦).',NULL,'294',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (297,'١٥١ - – أبو الفضل الدوري (٤٠٦)
(الأربعة)
(طب: ٣٣ ر / ١٠ ش)
(تس ٢٣ ر / ٩ ش) و (تخ ٢ ر / ٢ ش) و (تهـ ٧ ر / ٦ ش) و (تق ١ ر / ١ ش)
أبو الفضل، عباس بن محمد بن حاتم بن واقد، الهاشمي، مولاهم، الدوري، الخوارزمي الأصل البغدادي: مولده سنة خمس وثمانين ومائة، وتوفي سنة إحدى وسبعين ومائتين، من الحادية عشرة ثقة حافظ، وهو صديق يحيى بن معين، وصاحبه، وراويته.
روى عن: حجاج بن محمد المصيصي (تس ٤٠٦ وت ٩٣٦ و ١٥٠٤ و ٢٥١٦ و ٥٣٩٨ و ٦٠٨١ و ١٣٠٨٤ و ١٥٠٠٦ و ٢١٤٣٧ و ٢٣٤٠٤ و ٢٨٠٦٣ و ٣١٣٤٠ و ٣٣٣٢٤ و ٣٦١٠٣ و ٣٦١١٩) والحسين بن علي الجعفي (تس ٢٤١١٧)، وخالد بن مخلد (تس ٢٨٠٨٤) و (تهـ ٣٠٢ و ٢٠٣٤)، و (ت)، وروح بن عبادة (تخ ٣/ ٤٩)، وأبي عاصم الضحاك بن مخلد (تس ٢٣٠٣٦) و (تهـ ١٢٦٤)، وعبد الرحمن بن غزوان المعروف بقراد أبي نوح (تس ٣٣٢٢٢)، و (تهـ ٢٤٦٨)، و (تخ ١/ ٥١٩)، و (س)، وأبي نعيم عبد الرحمن بن هانئ النخعي (تهـ ٣٨٠)، وعبيد الله بن موسى (تهـ ٢٥٠٣)، و (ق، س)، ومسلم – غير مكني، ولا منسوب – [ولعله ابن إبراهيم الفراهيدي] (تس ٧٦٩٩ و ٢٥٩٣٥)، و (تق ١١٢) ت، ومحمد بن عبيد (تس ٥٣٥ و ٢٣٤٠٥)، ويونس بن محمد المؤدب (تهـ ٨٥)، و (س).
روى عنه: أحمد بن يحيى بن زهير التستري " المعجم الأوسط " (٢/ ٣٢٥ / ٢١١٤)، وأبو طاهر محمد بن الحسن المجد أبادي " سنن البيهقي " (١/ ١٨ / ٥٦)، وأبو عوانة الإسفراييني " مسند أبي عوانة " (١/ ٣٣ / ٥٩) – وقد أكثر عنه فيه.
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: إماماً، حافظاً، ثقةً، ثبتاً، صدوقاً، ناقداً، متفق عليه، يعني على عدالته؛ وإلا فالشيخان لم يخرجا له، ولا حتى واحد منهما، ولكنه كان يشرب النبيذ، ثم تاب من شربه، وأقلع عنه. وقد وقع في الأثر رقم (تس ٣٣٣٢٤)، (ابن عباس الدوري)، هو خطأ والصحيح عباس',NULL,'295',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (298,'بدون لفظة (ابن).
ولم أقف له على تجريح يذكر سوى شربه النبيذ وقد ذكرنا توبته من شربه، والله أعلم بحاله، وإليه مرجعه ومآله.
وانظر: " الجرح والتعديل " (٦/ ٢١٦ / ١١٨٩)، و" الثقات " (٨/ ٥١٣ / ١٤٧٤٩)، و" تاريخ بغداد " (١٢/ ١٤٤ / ٦٥٩٩)، و" تهذيب الكمال " (١٤/ ٢٤٥ / ٣١٤١)، و" الكاشف " (١/ ٥٣٦ / ٢٦٠٩)، و" التذكرة " (٢/ ٥٧٩ / ٦٠٣)، و" السير " (١٢/ ٥٢٢ / ١٩٩)، و" المقتنى في سرد الكنى: ٢/ ١٦ / ٥٠٤٤)، و" البداية والنهاية " (١١/ ٥٣)، و" تهذيب التهذيب " (٥/ ١١٣ / ٢٢٦)، و" تقريب التهذيب " (١/ ٢٩٤ / ٣١٨٩)، و" رجال تفسير الطبري " (٣٠٠/ ١٣٣١.
١٥٢ - أبو الفضل البيروتي (١٠٠)
(أبو داود، والنسائي)
(طب: ٦١ ر / ٣ ش)
(تس ١٦ ر / ٣ ش) و (تخ ٦ ر / ١ ش) و (تهـ ٢٦ ر / ٢ ش) و (تق ١٣ ر ١ ش)
أبو الفضل، العباس بن الوليد بن مزيد، العذري، البيروتي: وُلد ليلة الجمعة، لليلة بقيت من رجب، سنة تسع وستين ومائة، وتوفي يوم الثلاثاء، لسبع بقين من ربيع الآخر، سنة سبعين ومائتين، - وقيل: سنة إحدى وسبعين ومائتين – من الحادية عشرة، صدوق، عابد.
روى عن: عقبة بن علقمة البيروتي (تس ١١٠١٨)، و (تهـ ٢٩٠٨)، و (س) ومحمد بن هقل بن زياد (تس ٢٣١٩٠)، وأبيه الوليد بن مزيد (تس ١٠٠ و ٤٩٢٤ و ٥٥٢٢ و ١١٤٤٨ و ١٣٤٦٥ و ١٥٥٩٧ و ١٧٧٣٣ و ١٨٣٧٣ و ٢٣٢١٣ و ٢٤٨٠٦ و ٢٨٠٨٦ و ٣٢٧٨٣ و ٣٣٦٩٦ و ٣٤٩١٥)، و (تهـ: ١٩ و ٥١ و ٣٧٧ و ٥٣٥ و ٦٣٤ و ٦٤٢ و ٦٩٥ و ٦٧٦ و ٧٣٥ و ٧٤٢ و ١٠٣٤ و ١٣٨٠ و ١٤١١ و ١٦٩٢ و ٢٠٥٢ و ٢٢٣٩ و ٢٢٤٥ و ٢٢٦٨ و ٢٣٠١ و ٢٥٦١ و ٢٨٤٥ و ٢٩٠٩ و ٢٩١٠ و ٢٩٤٠ و ٢٩٥٧)، و (تق ٦١ و ٦٢ و ٨٥ و ١٧١ و ٢٢٥ و ٣٧٠ و ٥٦٠ و ٦٠٣ و ٧٠٢ و ٨٠١ و ٨٢٦ و ٩٧٦ و ١٠١٣) و (تخ: ١/ ١٦ و ١٣٧ و ٢٢٢ و ٥٢٧ و ٢/ ٨٣ و ٣٤٩)، و (د،',NULL,'296',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (299,'س)، والحارث بن العباس " الجرح والتعديل " (٢/ ٦٨ / ١٢٦) ودحيم – وهو سالم بن المنذر – الجرح والتعديل (١/ ٢٠٢)، والعباس بن نجيح " الجرح والتعديل " (١/ ١٨٤)، وعبد الله بن سعيد الساحلي " المقتنى " (١/ ٢٦٩ / ٢٥٩٠)، وعبد الغفار بن عفان " الجرح والتعديل " (٦/ ٥٤ / ٢٩٠)، وعبيد بن حبان الجبيلي " الجرح والتعديل " (٥/ ٤٠٥ / ١٨٧٦)، وعتبة بن حماد الحكمي " تهذيب الكمال " (١٩/ ٣٠٣ / ٣٧٧٢)، ومحمد بن سليمان الأسدي " تهذيب الكمال " (٢٥/ ٢٩٧ / ٥٢٥٧)، ومحمد بن هلال " الجرح والتعديل " (٨/ ١١٦ / ٥١٤).
روى عنه: أحمد بن أبي الحواري – وهو من شيوخه – " تهذيب الكمال " (١٤/ ٢٥٥ / ٣١٤٤)، ومحمد بن عون الطائي (تهذيب الكمال " (١٤/ ٢٥٥ / ٣١٤٤).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، حجةً، صدوقاً، إماماً، مقرئاً، حافظاً، مأموناً، فقيهاً، أحد الثقات، ليس به بأس، من شيوخ النبل، وكان صاحب ليل من خيار عباد الله، المتقنين في الروايات، حسن السمت، وكان يفتي برأي الأوزاعي.
وقد تصحف اسم شيخه في الأثر رقم (تس ٢٣١٩٠) من (محمد بن هقل إلى (محمد بن معقل)، وهو خطأ، والصحيح ما أثبته، والله أعلم.
وانظر: " معرفة الثقات " (٢/ ٢٠ / ٨٥٠) و" الجرح والتعديل " (٦/ ٢١٤ / ١١٧٨)، و" الثقات " (٨/ ٥١٢ / ١٤٧٤٥)، و" مولد العلماء ووفياتهم " (٢/ ٥٨٧)، و" تهذيب الكمال " (١٤/ ٢٥٥ / ٣١٤٤)، و" الكاشف " (١/ ٥٣٦ / ٢٦١٢)، و" سير أعلام النبلاء (١٢/ ٤٧١ / ١٧٢)، وأسماء من عاش ثمانين سنة " (٣٥/ ٢٦)، و" المقتنى " (٢/ ١٦ / ٥٠٤٦)، و" طبقات المحدثين " (١/ ٩٧ / ١٠٩٦)، و" تهذيب التهذيب " (٥/ ١١٥ / ٢٣٠)، و" تقريب التهذيب " (١/ ٢٩٤ / ٣١٩٢)، و" رجال تفسير الطبري " (٣٠٠/ ١٣٣٢).
...
١٥٣ - – عباس الآملي (٨٩٢)
(طب: ١٠ ر / ٤ ش)
(تس: ٨ ر / ٢ ش)، و (تخ: ٢ ر / ش ٢)',NULL,'297',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (300,'العباس بن الوليد، الآملي، من العاشرة، لم أعرفه، ولم أجد له ترجمة، وقد أغفله الشيخ قبلي، ولم يتعرف الشيخ التركي في تحقيقه " لتفسير الطبري " (١/ ٦٤٦) لترجمته بشيء.
روى عن: علي بن عاصم (تخ ١/ ٢٨٩)، ويزيد بن هارون (تس ٨٩٢ و ١٤٩٢١ و ٢٧١٨٨ و ٢٤١٣١ و ٢٧٢٢٢ و ٢٧٢٢٦ و ٢٧٢٦٢ و ٢٧٢٧٥)، و (تخ ١/ ٢٣٤).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: أغفله الشيخان شاكر في تحقيقهما " لتفسير الطبري " وقد روى عنه الطبري الأثر رقم (تس ٨٩٢) مقروناً (بتميم بن منتصر الواسطي) بأثر مختصر في فتون موسى عليه السلام، ثم منفرداً في باقي الروايات.
وانظر: " كتاب الدعاء " (١/ ٣٨٦).
١٥٤ - أبو محمد بن أبي طالب (٨٨١)
(النسائي، وابن ماجه، والبخاري في التاريخ مقروناً بغيره)
(طب: ٣٦ ر / ٢٥ ش)
(تس ١٩ ر / ١٥ ش) و (تخ ١ ر / ١ ش)
و (تهـ ١٣ ر / ١١ ش) و (تق ٣ ر / ٣ ش)
أبو محمد، عباس بن أبي طالب – واسم (أبي طالب) – جعفر بن عبد الله بن الزبرقان، البغدادي، البزار: أخو الفضل بن أبي طالب، ويحيى بن أبي طالب، وكان الأصغر، أصله من واسط، توفي يوم الأربعاء، لعشر مضين من جمادى الآخرة، سنة ثمان وخمسين ومائتين، من الحادية عشرة، صدوق.
روى عن: إبراهيم بن مهدي المصيصي (تس ١٨٥٢١)، و (تخ ١/ ١٩٧) وأحمد بن عبد الله بن يونس (تهـ: ٢٧ و ٦٠ و ٣٣٤)، وثابت بن محمد (تهـ ١٧٨٣)، والحجاج بن نصير الفساطيطي (تس ٨٨١) والحسين بن إبراهيم المعروف بـ (أشكاب) (تس ٢٨٢٠١)، وسعد بن عبد الحميد بن',NULL,'298',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (301,'جعفر (تس ٩٢٢٦) وسعيد بن حفص الطلحي (تس ٤٩٠٢)، وعلي بن عبد الله بن جعفر (تس ٣٥٣٣٦)، وعلي بن المعافى بن يعقوب الموصلي (تس ٣٤١٣٣)، وعيسى بن المنذر (تهـ ٦٣٣)، ومالك بن إسماعيل (تهـ ٥٢)، ومحمد بن أسعد التغلبي (تهـ ١٢٩٣)، ومحمد بن رومي (تس ٣٨٣١٨)، ومحمد بن زياد بن رزان (تس ٢٣٧٩٠ و ٢٦٥٢٢)، ومحمد بن الصلت (تس ٣٠٢٢١)، و (تهـ ٢٨٠٨)، ومحمد بن عبد الله (تهـ ٢٥٢٥)، ومسلم بن إبراهيم (تس ١٤٩٣٤)، ومعلى بن أسد (تس ٢٨٢٥٦ و ٢٨٥٩٦)، (تق ٩١٧) وأبي حذيفة موسى بن إسماعيل النهدي (تهـ ٢٣١٣)، و (تق ٩٨٤ و ٩٤٨)، والمنجاب بن الحارث (تس ٥٧٧٩) وهاشم بن عبد الواحد (تهـ ٢٥١٤)، وهشام بن عبد الملك [الطيالسي] (تس ٢٥٢٤)، ويحيى بن أبي بكير الكرماني (تس ٩٣٧٢ و ٢٨١٣٩ و ٣٠٤٨٩)، ويونس بن محمد (تهـ ١٧٤٠)، وأبي عوانة (تس ٢١٧٠٤) والأسود بن عامر " موضح الأوهام " (٢/ ٣٣٦ / ٣٧٥)، والحسن بن عمرو بن يوسف العبدي " الكامل " (٢/ ٣٢٩ / ٤٦٣)، والحسن بن محمد " مسند البزار " (٩/ ١٢٤ / ٣٦٧٣)، وزكريا بن عطية " مسند البزار " (٤/ ٤٧ / ١٢١١)، وسعيد بن الأشعث بن مسكين " مسند البزار " (٥/ ١٤٠ / ١٧٣٠)، وسعيد بن سلام " مسند البزار " (١/ ١٤٤ / ٧٣)، وسلمة بن حيان العتكي " تاريخ بغداد " (٥/ ٢٩١ / ٢٧٩٥)، وأبي عاصم [الضحاك بن مخلد النبيل] " الثقات " (٨/ ٥١٣ / ١٤٧٥٠)، وعبد الكريم بن روح البزاز " التاريخ الكبير " (٣/ ٣٠٧ / ١٠٤٤)، وأبي عمر عبد الواحد بن محمد، " موضح الأوهام " (٢/ ٣٣٦ / ٣٧٥).
روى عنه: أبو بكر البزار " مسند البزار " (١/ ١٤٤ / ٧٣)، والمثنى بن إبراهيم الآملي (تس ٢١٧٠٤) وعنه الطبري، ولا شك أن في الإسناد خطأ ما.
التعديل والتجريح: قال ابن أبي حاتم، وابن حبان، ومسلمة بن القاسم، وعبد الله بن إسحاق المدائني، والهيثمي: " الثقة ".
وقال أبو حاتم: " صدوق ".
وانظر: " الجرح والتعديل " (٦/ ٢١٥ / ٤١١٨)، و" الثقات " (٨/ ٥١٣ / ١٤٥٠)، و" مجمع الزوائد " (٥/ ٧٢)، و" تاريخ بغداد " (١٢/ ١٤١ / ٦٥٩٤)، و" تهذيب الكمال " (١٤/ ٢٠٢ / ٣١١٤)، و" الكاشف " (١/ ٥٣٤ / ٢٥٩٠)، و" سير أعلام النبلاء " (١٢/ ٦٢١ / ٢٤٣)، و" تهذيب التهذيب " (٥ /',NULL,'299',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (302,'١٠١ - / ١٩٧)، و" تهذيب التهذيب " (٥/ ١٠٤ / ٢٠٦)، و" تقريب التهذيب " (١/ ٢٩٢ / ٣١٦٣)، و" رجال تفسير الطبري " (٢٩٩/ ١٣٢٩).
١٥٥ - عبد الأعلى الأسدي (١١١٢٨)
(الترمذي، والنسائي)
(طب: ٣٩ ر / ٢٣ ش)
(تس ٢٩ و ١٦ ش) (تخ ٢ ر / ٢ ش) و (تهـ ٦ ر / ٥ ش) و (تق ٢ ر / ٢ ش)
عبد الأعلى بن واصل بن عبد الأعلى بن هلال، الأسدي، الكوفي، توفي سنة سبع وأربعين ومائتين من كبار العاشرة، ثقة.
روى عن: إسماعيل بن أبان (تهـ ١٠٦٣)، وإسماعيل بن صبيح اليشكري (تس ١٧٢٤٥)، وأسيد بن زيد الجصاص (تس ١٨٥٢٠)، و (تخ ١/ ١٩٧) وثابت بن محمد الكناني (تهـ ٢٥١٣) وجعفر بن عون العمري (تس ٢١٩٣٢)، و (تهـ ٢٨٨٩)، و (ص)، وأبي أسامة حماد بن أسامة (تس ٣٠٠٢١)، و (س)، وخلاد – والصحيح – خالد بن يزيد المقرئ (تهـ ٣٦٦) وسعيد بن محمد الجرمي (تس ٢١٢٥٢)، وعبيد بن إسحاق الضبي العطار (تس ٢٢٥٣١)، وعبيد بن قاسم البزاز (تس ٣٤٥٢٥)، وعبيد الله بن موسى (تس ١٢٥٠٢ و ١٦٤٨٩ و ٢٦٠٨٨ و ٢٦٠٨٩ و ٢٦١٠٣ و ٢٦١٥٥ و ٢٦١٦٤ و ٣٢٢٦٥)، وعمرو بن طلحة القناد (تهـ ٩٧٤)، وعون بن سلام القرشي (تس ٢١٩٠٠ و ٢٦٥٦٢)، وأبي نعيم الفضل بن دكين (تس ٢٨٤٩٢ و ٢٨٤٩٣ و ٣٧٩١٠)، و (ص)، وأبي غسان مالك بن إسماعيل (تس ١٤٦٤٣)، (تهـ ١٤١٧) و (سي)، ومحمد بن عبيد الطنافسي (تس ٣٧٠٨٩ و ٢٤٠٦٢ و ٣٧٠٨٩)، ومحمد بن القاسم الأسدي (تس ١١١٢٨)، و (ت)، ومروان بن معاوية الفزاري (تس ٢٦٩٦٩)، وهدية بن خالد البصري (تق ٨٣٦)، ويعلى بن عبيد (تس ٢١٢٤٠ و ٢٤٤١٤)، و (س)، ويحيى بن آدم (تس ٣٠٨٨٢)، و (تق ٩٣٥)، و (س)، وأبي فقيم (تخ ٣/ ٥٤).
روى عنه: أحمد بن عمرو البزار " مسند البزار " (٥/ ٣١ / ١٥٩٠)، وأبو مسعود عبد الرحمن',NULL,'300',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (303,'بن الحسين الصابوني " المعجم الأوسط " (٥/ ٨٨ / ٤٧٥٥)، ومحمد بن عبد الله الحضرمي، سنن البيهقي " (٥/ ٩٠ / ٩١٠٥).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، صدوقاً.
وقد روي الطبري أيضاً، عن أبيه (واصل بن عبد الأعلى) في " التفسير " و" التاريخ " و" تهذيب الآثار "؛ وقد فصلت ذلك في ترجمته، كما سيأتي في موضعه من كتابي هذا.
ووقع في الأثر رقم (تس ٢٥٥٣١) خطأ في اسمه حيث جاء فيه: حدثني بن عبد الأعلى، وهو خطأ بزيادة لفظة (ابن) قبله، والله أعلم.
وانظر: " الجرح والتعديل " (٦/ ٣٠ / ١٥٧)، و" الثقات " (٨/ ٤٠٩ / ١٤١٣٢)، و" تهذيب الكمال " (١٦/ ٣٧٩ / ٣٦٩٢)، و" الكاشف " (١/ ٦١١ / ٣٠٨٣)، و" تهذيب التهذيب " (٦/ ٩٢ / ٢٠٦)، و" تقريب التهذيب " (١/ ٣٣٢ / ٣٧٣٩)، و" رجال تفسير الطبري " (٣٠١/ ١٣٣٨).
...
١٥٦ - عبد الجبار الرملي (٧٤٢٣)
(طب: تس: ٦ ر / ١ ش)
عبد الجبار بن يحيى، الرملي: من العاشرة، لم أعرفه، ولم أجد له ترجمة، ولم يعرفه الشيخ شاكر قبلي (٧٤٢٥)، ولم يتعرض الشيخ التركي في " تحقيقه " (٥/ ٥٩٠) لترجمته بشيء.
روى عن: ضمرة بن ربيعة (تس ٧٤٢٣ و ٧٤٤٤ و ٢٠٠٨٣ و ٢٠١٢١ و ٢٥٤٢٢ و ٧٢٠٢)، وعقبة بن علقمة " حلية الأولياء " (٥/ ٣١١).
التعديل والتجريح: ذكره الشيخ شاكر في تحقيقه " لتفسير الطبري " (٧/ ٢٠ / ٧٤٢٥) وقال: " شيخ الطبري، لم أجد ترجمته في مكان ".
قلت: وقد أخرج ابن عبد البر في " التمهيد " (٢١/ ٢٤٢)، أثراً من طريق الطبري',NULL,'301',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (304,'عنه عن ضمرة أيضا.
وفي " لسان الميزان " (٣/ ٣٩٠ / ١٥٥١)، ترجمة لـ (عبد الجبار بن يحيى بن الفضل)، وقد أحالها الحافظ إلى ترجمة (يحيى بن قيوم) في " لسان الميزان " أيضا (٦/ ٣٣٥ / ٩١٩٧). وقال في نسبه (الأزدي)، وهو تابعي، ولأبيه صحبة ووفادة، وسماه النبي - عليه السلام- (عبد القيوم). ولا أظن أن (عبد الجبار) هذا هو المراد، والله أعلم.
وانظر: " رجال تفسير الطبري " (٣٣٤/ ١٥١٦).
٦٦ - تمييز: أبو بكر، عبد الجبار بن يحيى بن سعيد بن أحمد بن حرب بن أحمد بن حرب، القاضي، الأزجاهي، المعروف بـ (الحربي)، ولد سنة إحدى وستين وأربعمائة، بأزجاه، وتوفي بها سنة ثلاث وثلاثين وخمسمائة. إمام فاضل عفيف، صالح، سديد السيرة، تفقه على الإمام عبد الملك الأزجاهي، وعلق المذهب عليه، وسمع الحديث الكثير بنفسه، سمع بهراة، أبا عامر محمود بن القاسم الأزدي، وببغشور، أبا سعيد محمد بن علي بن أبي صالح الدباس، وغيرهما.
وانظر: " التحبير في المعجم الكبير " (١/ ٤٢٧ / ٣٨٧)
١٥٧ - أبو الحسن القناد (٣٠)
(مسلم، وأبو داود، وابن ماجه)
(طب: ١٠١ ر / ٧ ش)
أبو الحسن، عبد الحميد بن بيان بن زكريا بن خالد بن أسلم، ويقال: ابن بيان بن أبان – الواسطي، ابن أبي عيسى، العطاردي، السكري، القناد، توفي سنة أربع وأربعين ومائتين، من العاشرة صدوق.',NULL,'302',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (305,'روى عن: إسحاق بن يوسف الأزرق (تس ٣٢٤٤ و ٣٢٤٥ و ٣٢٤٨ و ٣٣١١ و ٣٥٥١ و ٣٥٥٢ و ٣٥٥٣ و ٣٥٩٨ و ٣٥٩٩ و ٣٦٠٠ و ٣٦٠١ و ٣٦٠٢ و ٣٦٠٤ و ٣٦٠٧ و ٣٦٣٧ و ٣٦٣٩ و ٣٦٤٠ و ٣٦٦٠ و ٣٦٧٣ و ٣٦٧٤ و ٣٦٧٩ و ٣٧١١ و ٣٧٤١ و ٣٧٤٥ و ٣٧٧٠ و ٣٨٩٦ و ٧٥٠٤ و ٧٥٠٨ و ٨٩١٥ و ٩٥٩٣ و ١٢٥٩٩ و ١٢٧٤٦ و ١٢٧٧٠ و ١٦٤١٥ و ١٦٤٢٠ و ١٦٤٣٦ و ٢٠١٨٤ و ٢٥٠٦٦ و ٢٥٠٧٠ و ٢٥١٤٥ و ٢٥١٥٠ و ٢٥١٨٤)، و (تهـ ٤٤٧ و ٥٠٠ و ٣٠٢٦)، و (د، ق)، وخالد بن عبد الله الطحان [الواسطي] (تس ١٤٢٠٩ و ٢٦١٦٩ و ٣٢٤٤٦ و ٣٥٩٤٦)، و (تهـ: ٢٩٥٣)، و (تق ٨٠٨)، و (م، ق)، وسفيان – غير مكني، منسوب- (تس ٤٧٠٤ و ٢٥٠٧٢ و ٣٤١٠٤)، و (تهـ ٨٥٧)، ومحمد بن يزيد الواسطي (تس ٣٠ ون ٤٥١٩ و ٤٨٢١ و ٥٥٢٧ و ٧٥٧٨ و ٩٤٢٢ و ٩٦٢٢ و ١٠١٦١ و ١٢٨٢٩ و ٢٤٥٥٩ و ٣٠٣٩٢ و ٣١٥١٨ و ٣٢٤٨٦ و ٣٣٧٥١ و ٣٥٨٩٣ و ٣٥٨٩٠٣ و ٣٥٩١٨ و ٣٦٥٠٤ و ٣٨٠٧٦)، و (تخ ٢/ ٩ و ٢/ ٥٧٨)، و (تهـ ٣٦١ و ١٨١٩ و ٢٢٦٣ و ٢٤٩٨ و ٢٩٣٧) و (تق ١٢١ و ٢٦٥) ويزيد بن هارون (تس ٣٤٣٦ و ٣٤٣٩)، و [محمد بن إبراهيم] بن أبي عدي (تس ٧٣٧٦)، و (تق ٨٩٠)، وأبي بحر البكراوي (تهـ ٤٩٤)، وعلاء بن راشد " تاريخ واسط " (١/ ١٨٥)، ومحمد بن رحمة بن مصعب " تاريخ واسط " (١/ ١٥٣).
روى عنه: الحسين بن إسحاق التستري " المعجم الكبير " (١٠/ ١٤٧ / ١٠٢٧٢) وأبو حاتم محمد بن إدريس الرازي " الجرح والتعديل " ت (٦/ ٩ / ٤٤).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه ثقةً، صدوقاً، قلت: وتحرف اسم شيخه في الأثر (تس ٣٧١١)، من (إسحاق)، وهو خطأ، والصحيح ما أثبته.
وتصحفت نسبته في الآثار (تس ٧٣٧٦ و ٧٥٧٨ و ١٤٢٠٩ و ٣١٥١٨ و ٣٦٥٠٤)، من السكري، إلى اليشكري في طبعة دار الفكر فقط، وتصحف (القناد) إلى (القتات) في الأثر (تس ٤٩٤)، وإلى (القتار) في الأثر (تهـ ٢٢٦٣).
وأظن أن شيخه (إسحاق الأزرق) في الآثار (تس ٤٧٠٤ و ٢٥٠٧٢ و ٣٤١٠٤)، و (تهـ',NULL,'303',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (306,'٨٥٧ - ) قد سقطت اسمه بحيث صار (سفيان) وهو شيخ شيخه في كثير من الآثار التي رواها عن (إسحاق الأزرق)، والله أعلم.

وانظر: " الجرح والتعديل " (٦/ ٩ / ٤٤)، و" الثقات " (٨/ ٤٠١ / ١٤٠٩٢)، و" ذكر أسماء التابعين " (٢/ ١٦٤) و" رجال مسلم " (١/ ٤٤٠ / ٩٨٦) و" تهذيب الكمال " (١٦/ ٤١٣ / ٣٧٠٧)، و" البداية والنهاية " (١٠/ ٣٦٠) و" الكاشف" (١/ ٦١٤ / ٣٠٩٦)، و" المقتنى " (١/ ١٨٢ / ١٥٢٨) و" تهذيب التهذيب " (٦
/ ٢٢٣) و" تقريب التهذيب " (١/ ٣٣٣ / ٣٧٥٤)، و" رجال تفسير الطبري " (٣٣٥/ ١٥٢٠).
١٥٨ - – أبو الفضل الأنصاري (٢٤٠٥٧)
(البخاري – في التعاليق – ومسلم، والأربعة)
(طب: (تس ٥ ر / ٣ ش)
أبو الفضل – ويقال: أبو حفص -، عبد الحميد بن جعفر بن عبد الله بن الحكم بن رافع، الأنصاري المدني، والد سعد بن عبد الحميد بن جعفر، توفي سنة ثلاث وخمسين ومائة من السادسة، صدوق، رمي بالقدر، وربما وهم.
قلت: ولا يمكن أن يكون هذا الشيخ شيخاً للطبري، لقدم وفاته، سنة ثلاث وخمسين ومائة، ولا بد أن يكون بينهما سقط، وقد أخرج له الطبري عدة روايات، ولكن بينهما عدة شيوخ، وقد فصلت القول فيه في " المعجم الكبير " فانظره عند الأثر رقم (١٣٨٩).
١٥٩ - عبد الحميد بن سنان (٢٩٦١)
(أبو داود، والنسائي)
(طب: تف: ١ ر / ١ ش)
عبد الحميد بن سنان، المكي: من السادسة، مقبول، ولا يمكن أن يكون هذا الشيخ شيخاً للطبري؛ لقدم وفاته كالذي قبله أيضاً.
روى عن: إسحاق – غير مكني، ولا منسوب – (تس ٢٩٦١).',NULL,'304',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (307,'روى عنه: أبو جعفر محمد بن جرير الطبري – وهو بعيد جداً.
التعديل والتجريح: قال العقيلي: قال البخاري: " في حديثه نظر ".
وذكره ابن حيان في كتاب " الثقات ".
قلت: ولا يمكن أن يكون شيخاً للطبري، لقدم وفاته وطبقته، فلا بد أن يكون هناك سقط، والله أعلم.
وانظر: " التاريخ الكبير " (٦/ ٥٢ / ١٦٧٨)، و" ضعفاء العقيلي " (٣/ ٤٥ / ١٠٠٢) و" الجرح والتعديل " (٦/ ١٣ / ٦٢) و" الثقات " (٧/ ١٢٢ / ٩٢٧٩)، و" تهذيب الكمال " (١٦/ ٤٣٧ / ٣٧١٨)، و" الكاشف " (١/ ٦١٦ / ٣١٠٧)، و" تهذيب التهذيب " (٦/ ١٠٦ / ٢٣٥)، و" تقريب التهذيب " (١/ ٣٣٣ / ٣٧٦٥)، و" لسان الميزان " (٧/ ٢٧٦ / ٣٧٠٣)، و" رجال تفسير الطبري " (٣٣٥/ ١٥٢٣).
١٦٠ - أبو عمرو الطفاوي (٦٨٥٥)
(الترمذي، والنسائي)
(طب: ١٣ ر / ١ ش)
(تس: ١١ ر / ١ ش)، و (تخ: ١ ر / ١ ش)، و (تق ١ ر / ١ ش)
عبد الرحمن بن الأسود الطفاوي، لعله: أبو عمرو، عبد الرحمن بن الأسود بن المأمول- آخره لام وقيل: المأمون آخره نون – الهاشمي، مولاهم، الوراق، البغدادي، البصري: توفي بعد سنة أربعين ومائتين، من الحادية عشرة، مقبول.
روى عن: محمد بن ربيعة الكلابي (تس ٦٨٥٥ و ٦٨٩٧ و ٦٩٤٦ و ٢١٥٢٦ و ٢٧٧٤٢ و ٢٨١١٤ و ٣٤٨١٣ و ٣٦٩١١ و ٣٧٦٥٨ و ٣٨١٨٤ و ٣٨٣٠٤)، و (تق ٧٥٠)، و (تخ: ١/ ١٧٦)، (ت، س)، ومحمد بن كثير الملائي " الإحكام لابن حزم " (٦/ ٢٤٢).
التعديل والتجريح: لم يذكره أحد بجرح أو تعديل إلا ما سبق.',NULL,'305',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (308,'وذكره الشيخ شاكر في تحقيقه " لتفسير الطبري " (٦/ ٣٣١ / ٦٨٦٠) وترجم له على اعتبار أنه عبد الرحمن بن الأسود بن المأمون الطفاوي.
وانظر: " تاريخ بغداد " (١٠/ ٢٦٨ / ٣٥٨٤)، و" تهذيب الكمال " (١٦/ ٥٢٩ / ٣٧٥٧)، و" الكاشف " (١/ ٦٢١ / ٣١٤٠)، و" تهذيب التهذيب " (٦/ ١٢٧ / ٢٨٧)، و" تقريب التهذيب " (١/ ٣٣٦ / ٣٨٠٢)، و" رجال تفسير الطبري " (٣٣٨/ ١٥٤١).
١٦١ - أبو علي البختري (١٣٨٨٥)
(طب: ٤ ر / ١ ش)
(تس: ١ ر / ١ ش)، و (تهـ: ٣ ر / ١ ش)

أبو علي، عبد الرحمن بن البختري – وقيل: ابن جابر بن البختري، وقيل ابن أبي البختري، وقيل: ابن زبان بن الحكم – وهو: عبد الرحمن بن أبي البحتري الطائي، الحمصي، البغدادي: من الحادية عشرة، سكت عنه الخطيب والدارقطني.
روى عنه: عبد الرحمن بن محمد المحاربي (تس ١٣٨٨٥)، و (تهـ ٩٤١ و ١٨٥٧ و ١٩٥٣) وحنظلة بن يونس، وعبد الصمد بن عبد الوارث، وعبد الله بن إدريس، وعبد المجيد بن عبد العزيز بن أبي رواد، وأبي بكر بن عياش، وأبي القاسم، بشر بن شعيب بن أبي حمزة، واسم أبي حمزة – دينار القرشي مولاهم الحمصي " المعجم الكبير " (٧/ ١٤٩ / ٦٦٥٨)، وأبي روح عبد العزيز بن موسى بن روح اللاحوني البهراني الحمصي، " المعجم الصغير " (٣/ ١٠ / ٦٨٦)، ومصعب بن المقدام " تاريخ دمشق " (٤/ ٢٦).
روى عنه: محمد بن الحسين القنبيطي، ويحيى بن صاعد، وأبو بكر بن أبي الدنيا، وإسحاق بن إبراهيم بن يونس، " الكامل " (٦/ ٣٠٧ / ١٧٩٦) والقاسم بن زكريا " شعب الإيمان " (٣/ ٢١ / ٢٧٥٤)، وأبو بكر محمد بن إبراهيم بن فيروز الأنماطي " تاريخ دمشق " (٤/ ٢٦)، وأبو القاسم الطبراني " المعجم الصغير " (٢/ ١٠ / ٦٨٦).',NULL,'306',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (309,'التعديل والتجريح: ذكره الشيخ شاكر في تحقيقه " لتفسير الطبري " (١٢/ ١١٢ / ١٣٨٨٢)، وقال: " شيخ أبي جعفر، لم أجد له ذكراً فيما بين يدي من الكتب وأخشى أن يكون في اسمه خطأ ".
قلت: وقد ذُكر في بعض كتب الحديث والتراجم، أثبتُّ من خلالها، معجم شيوخه وتلاميذه السابق.
وكل اسم لم يعز إلى كتاب، من معجم شيوخه وتلاميذه، وميزته بالأسود العريض، فهو من ترجمته في " تاريخ بغداد " (١٠/ ٢٦٧ / ٥٣٨٢).
وتصحف اسم جده في الأثرين (تهـ و ١٨٥٧ و ١٩٥٣) من (البختري) إلى (أبي البحتري).
وانظر أيضاً: " تاريخ بغداد " (١٠/ ٢٦٧ / ٥٣٨٢)، و" موضح أوهام الجمع والتفريق " (٢/ ٢٥٠ / ٣٠٧)، و" الفصل للوصل المدرج " (١/ ٢٠٢)، و" شعب الإيمان " (٣/ ٢١ / ٢٧٥٤)، و" علل الدارقطني " (١/ ١٦١) و" تهذيب الكمال " (٢٨/ ٤٤).
١٦٢ - أبو محمد العبدي (١٣٨٠٩)
(البخاري (٤)، ومسلم (٢٣)، وأبو داود، وابن ماجه)
(طب: (تس: ٣ ر / ٢ ش)
أبو محمد، عبد الرحمن بن بشر بن الحكم، العبدي، النيسابوري: توفي سنة ستين ومائتين، أو بعدها، من العاشرة، ثقة.
روى عن: سفيان بن عيينة (تس ١٦٦٣ و ٣٧٦٦٨)، (خ، م)، وموسى بن عبد العزيز القنباري (تس ١٣٨٠٩)، و (د، ق)، وعبد الرحمن بن مهدي " تاريخ بغداد " (١٠/ ٢٧١ / ٥٣٨٧).
روى عنه: أبو بكر محمد بن الحسين القطان " سنن البيهقي " (٢/ ٢٦ / ٢١٤٦).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: صدوقاً ثقة، عالماً ابنَ عالمٍ +ابنَ عالمٍ.',NULL,'307',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (310,'وانظر: " الجرح والتعديل " (٥/ ٢١٥ / ١٠١١)، و" الثقات " (٨/ ٣٨٢ / ١٣٩٩٠)، و" تسمية من أخرجهم البخاري ومسلم " (١/ ١٦٣ / ٩٥٠)، و" تاريخ بغداد " (١٠/ ٢٧١ / ٥٣٨٧)، و" التعديل التجريح " (٢/ ٨٦٠ / ٨٨٢)، و" تهذيب الأسماء " (١/ ٢٧٤ / ٣٤٣)، و" تهذيب الكمال " (١٦/ ٥٤٥ / ٣٧٦٥)، و" المقتنى " (٢/ ٥٥ / ٥٤٩٠)، " وسير أعلام النبلاء " (١٢/ ٣٤٠ / ١٣٨)، و" الكاشف " (١/ ٦٢٢ / ٣١٤٨)، و" تهذيب التهذيب " (٦/ ١٣١ / ٢٩٦)، و" تقريب التهذيب " (١/ ٣٣٧ / ٣٨١٠) و" رجال تفسير الطبري " (٣٣٩/ ١٥٤٦).
١٦٣ - أبو القاسم بن عبد الحكم (١٠٧٧)
(النسائي)
(طب: ٣٣ ر / ٢٠ ش)
(تس ٤ ر / ٤ ش) و (تخ ٩ ر ٧ ش) و (تهـ ١٥ ر / ١٠ ش) و (تق ٥ ر / ٣ ش)
أبو القاسم، عبد الرحمن بن عبد الله بن عبد الحكم بن أعين بن ليث، العثماني، الأموي، مولاهم، المصري: مولده سنة سبع وثمانين ومائة، وتوفي في المحرم سنة سبع وخمسين ومائتين، وهو ابن سبعين سنة، من الحادية عشرة، ثقة.
روى عنه: أحمد بن محمد الأزرقي (تهـ ٢٤١٩)، وأسد بن موسى (تهـ ٢٩١ و ٣٤٥)، وسعيد بن الحكم بن أبي مريم (تهـ ١٤٠٩)، و (س)، وسعيد بن عيسى بن تليد (تهـ ٢٢١٣)، و (س)، وسعيد بن كثير بن عقير (تهـ ١٨٦٦)، و (سي) وشعيب بن الليث بن سعد (تهـ ١٩٠٦) و (س) وأبي زيد بن عبد الرحمن بن أحمد بن أبي الغمر (تس ٤٣٣٢)، وعبد العزيز بن منصور التجيبي (تس ١٠٧٧)، وأبيه عبد الله بن عبد الحكم (تهـ ٩٠٨ و ٩١٢ و ١٩٠٦)، و (تخ ٢/ ٤)، و (س)، وأبي عبد الرحمن عبد الله بن يزيد المقرئ (تهـ ١٢٩٦)، وعبد الملك بن مسلمة المصري (تس ٤٣٣١) (تهـ ٣٦٨)، و (تخ / ٥٤٠)، وعلي بن معبد بن شداد الرقي (تس ٣٦٣٠٢)، و (تخ ١/ ٧٥)، و (س) ومحمد بن عاصم المعافري (تق ٣٠٧)، ومحمد بن المتوكل (تخ ١/ ٢٢٠)، وأبي الأسود النضر بن عبد الجبار (تهـ ١٤٠٩)، و (س)، وأبي زرعة وهب الله بن راشد (تهـ ٨٠١ و',NULL,'308',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (311,'٢٦٤٧ - )، و (تق ٤٢٦ و ٤٣٦ و ٧٧٨)، و (تخ ١/ ٧٥)، ونعيم بن حماد (تخ ٢/ ٥ و ٢/ ٤٦ و ٢/ ٥٦٣)، ويحيى بن عبد الله بن بكير (تخ ٢/ ٣٦)، ويعقوب بن إسحاق بن أبي عباد المكي نزيل القلزم، (تخ ٢/ ٤)، وأبي نباتة يونس بن يحيى بن نباتة (تق ٩٤٥).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، صدوقاً، لا بأس به، فقيهاً، والأغلب عليه الحديث، والأخبار، عالماً بالتواريخ صنف تاريخ مصر، وغيره.
وانظر: " الجرح والتعديل " (٥/ ٢٥٧ / ١٢١٣)، و" مولد العلماء ووفياتهم " (٢/ ٥٦٦)، و" تهذيب الكمال " (١٧/ ٢١٣ / ٣٨٦٨)، و" الكاشف " (١/ ٦٣٢ / ٣٢٣٦)، و" تهذيب التهذيب " (٦/ ١٨٨ / ٤٢٦)، و" تقريب التهذيب " (١/ ٣٤٤ / ٣٩١٥)، و" رجال تفسير الطبري " (٣٤٧/ ١٥٨٣).
١٦٤ - – أبو محمد الجرجاني (١٥٣٦٥)
(طب: ٨ ر / ٦ ش)
(تس: ١ ر / ١ ش)، و (تخ: ١ ر / ١ ش)، و (تهـ: ٦ ر / ٥ ش)
أبو محمد، عبد الرحمن بن الوليد، الجرجاني: من الحادية عشرة، ترجم له أبو القاسم الجرجاني في " تاريخ جرجان "، وروى عنه الإسماعيلي في " معجم شيوخه " وسكتا عنه.
روى عن: أحمد بن أبي طيبة الجرجاني (تس ١٥٣٦٥)، و (تخ ٢/ ٢٤١) وأبي النعمان عارم [ابن محمد بن الفضل السدوسي البصري] (تهـ ١٤٤٢)، وعفان بن مسلم (تهـ ١٤٤٩) و يحيى بن عبد الحميد (١٤٥٠ و ١٤٥١)، ومسلم بن إبراهيم (تهـ ١٤٣٧)، وأبي عاصم (تهـ٣٩)، وسريج بن النعمان " معجم شيوخ أبي بكر الإسماعيلي " (٣/ ٧١٥)، وعبد الله بن يوسف الخوارزمي وعبيد الله بن موسى العبسي، وعون بن عمارة.
روى عنه: أبو جعفر، أحمد بن علي بن محمد – المعروف بـ (ابن أبي طالب',NULL,'309',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (312,'الكاتب) – وعبد الرحمن بن الحسين بن إسحاق الخواتكاني الجوزجاني " معجم البلدان " (٢/ ١٧٥) ومحمد بن الفضل النجار الآملي، وأبو بكر الإسماعيلي " معجم شيوخ أبي بكر الإسماعيلي " (٣/ ٧١٥) وكل اسم لم يعز إلى كتاب، - وميزته بالأسود العريض-، من معجم شيوخه وتلاميذه، فهو من ترجمته في " تاريخ جرجان " (١/ ٢٥٣ / ٤١٢).
التعديل والتجريح: ترجم له أبو القاسم الجرجاني في " تاريخ جرجان "، وروى عنه أبو بكر الإسماعيلي في " معجم شيوخه " وسكتا عنه.
وانظر: " تاريخ جرجان " (١/ ٢٥٣ / ٤١٢)، و" رجال تفسير الطبري " (٣٥٥/ ١٦١٩).
١٦٥ - عبد الرحمن (٩٤٤٦)
(طب: (تس: ١ ر / ١ ش)
عبد الرحمن، غير مكني، ولا منسوب: لم أعرفه، ولم أجد له ترجمة، وقد أغفله الشيخ شاكر قبلي، ولم يتعرض الشيخ التركي في تحقيقه لـ " تفسير الطبري " (٧/ ٧) لترجمته، ولا للأثر الذي رواه بشيء.
روى عن: جرير [ابن عبد الحميد الضبي] (تس: ٩٤٤٦).
التعديل والتجريح: أغفله الشيخ شاكر في تحقيقه " عن خمسة رجال من شيوخه كلهم اسمه (عبد الرحمن)، وليس فيهم من روى عن جرير غير هذا.
١٦٦ - أبو عميرة الرملي (تهـ ١٧٦٧)
(طب: (تهـ: ١ ر / ١ ش)
أبو عميرة، عبد العزيز بن أحمد بن سويد، البلوي، الرملي، مؤذن مسجد الرملة من العاشرة، روى عنه ابن خزيمة وسكت عنه، وأغفله الشيخ شاكر في تحقيقه لـ " تهذيب',NULL,'310',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (313,'الآثار " (٤/ ٧ / ٥) وكذلك فعل الدكتور الرشيد.
روى عن: أيوب بن سويد الرملي (تهـ: ١٧٦٧).
روى عنه: محمد بن إسحاق بن خزيمة " صحيح ابن خزيمة " (٤/ ٢١٩ / ٢٧٣١).
التعديل والتجريح: لم أقف له على ذكر في كتب الرجال والحديث والآثار إلا في الموضعين الذين أثبتهما من " تهذيب الآثار " و" صحيح ابن خزيمة "، وقد روى عنه الطبري أيضاً حديثاً آخر كما هو في " التمهيد " (٢١/ ١٤٠)، وقد ذكره الذهبي في " المقتنى في سرد الكنى " (١/ ٤٤٠ / ٤٧٩٣)، ولكن انقلب اسمه عليه فجعله (أحمد بن عبد العزيز)، والله أعلم.
وشيخه أيوب بن سويد الرملي أبو مسعود الحميري السيباني)، توفي سنة ثلاث وتسعين ومائة، وقيل: سنة اثنتين ومائتين من التاسعة، صدوق، يخطئ، كما هو في " تقريب التهذيب " (١/ ١١٨ / ٦١٥)، وقد ترجمت له في " المعجمين الكبير، والصغير "، فانظره هناك.
١٦٧ - – أبو يحيى الديرعاقولي (٨٩٣)
(طب: ٢٦ ر ش)
(تس: ٢٥ ر / ١ ش)، و (تخ: ١ ر / ١ ش)
أبو يحيى، عبد الكريم بن الهيثم بن زياد بن عمران، القطان، الديرعاقولي البغدادي، مولده سنة ثمان وتسعين ومائة، وتوفي لخمس خلون من شعبان، سنة ثمان وسبعين ومائتين، من الحادية عشرة، ثقة.
روى عن: إبراهيم بن بشار [الرمادي] (تس ٨٩٣ و ٩١٠ و ٩١٩ و ٩٣٧ و ١٠٤٦ و ١١٦٥٣ و ١١٦٦٠ و ١١٦٧١ و ١١٦٩٨ و ١٤٩٢٠ و ١٤٩٣٨ و ١٤٩٤٠ و ١٤٩٤٧ و ١٤٩٥١ و ١٤٩٦٢ و ١٤٩٨٠ و ١٤٩٨٤ و ١٥٠٢٩ و ١٥١٠٤ و ١٥١٢٢ و ١٥١٢٦ و ١٥١٣٩ و ١٥١٥٢ و ١٥١٧٧ و ١٥٢١٤)، و (تخ ١/ ٢٥٧) وإبراهيم بن محمد الشافعي وإبراهيم بن منذر الحزامي، وإبراهيم بن مهدي المصيصي، وأحمد بن صالح، وأحمد بن عبد الله بن يونس، وإسماعيل بن بهرام الخزاز " الثقات " (٨/ ١٠٠ / ١٢٤٢٤)،',NULL,'311',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (314,'وإسماعيل بن أبي مسعود " تاريخ بغداد " (٦/ ٢٥٠ / ٣٢٨٧)، +وحبي بن حاتم " تاريخ بغداد " (١٤/ ٤٢٣ / ٧٧٧٦)، وحجاج بن إبراهيم المصري، والحسن بن قزعة الهاشمي، " تهذيب الكمال " (٦/ ٣٠٤ / ١٢٦٦)، والحسين بن داود (سنيد) " تاريخ بغداد " (٨/ ٤٢ / ٤٠٩٩)، والحسين بن عبد الأول " لسان الميزان " (٢/ ٢٩٤ / ١٢٢٠)، والحكم بن نافع " تذكرة الحفاظ " (٢/ ٦٠٢ / ٦٢٦) وحيوة بن شريح المصري وداود بن منصور النسائي " تهذيب الكمال " (٨/ ٤٥٤ / ١٧٨٨)، والربيع بن روح الحضرمي، " تهذيب الكمال " (٩/ ٧٧ / ١٨٦٠) وأبي توبة الربيع بن نافع، ورجاء بن محمد العذري " تهذيب الكمال " (٩/ ١٦٧ / ١٨٩٦)، وسعيد بن سليمان الضبي " تهذيب الكمال " (١٠/ ٤٨٥ / ٢٢٩١)، وسعيد بن شبيب الحضرمي " تهذيب الكمال " (١٠/ ٤٩٨ / ٢٢٩٦)، وسعيد بن عمرو الحضرمي " تهذيب الكمال " (١١/ ٢٤ / ٢٣٣٦)، وسليمان بن حرب، وسويد – غير مكني، ولا منسوب – " تاريخ بغدد " (١٣/ ٣٠٦ / ٧٢٨٥)، وطاهر بن أبي أحمد الزبيري " علل الدارقطني " (١/ ١٩٣ / ١٧)، وعباس بن الوليد النرسي " تهذيب الكمال " (١٤/ ٢٦٠ / ٣١٤٥)، وعبد الله بن جعفر الرقي " تهذيب الكمال " (١٤/ ٣٧٧ / ٣٢٠٤)، وعبد الله بن محمد العائذي " تهذيب الكمال " (١٦/ ٥٥ / ٣٥٣٣)، وعبد الأعلى بن حماد الباهلي " تهذيب الكمال " (١٦/ ٣٥٠ / ٣٦٨٣)، وعبد العزيز بن موسى اللاحوني البهراني " تهذيب الكمال " (١٨/ ٢١٤ / ٣٤٧٩)، وعبدة بن سليمان المروزي " تهذيب الكمال " (١٨/ ٥٣٥ / ٣٦١٤)، وعبيد الله بن محمد المخزومي " تهذيب الكمال " (١٩/ ١٥٥ / ٣٦٨٢)، وعبيد بن يعيش " تاريخ بغداد " (٦/ ٢٢٩ / ٣٢٧٧) وعتبة بن سعيد بن حيان الحمصي " تهذيب الكمال " (١٩/ ٣٠٦ / ٣٧٧٤)، وعقبة بن مكرم الضبي " تهذيب الكمال " (٢٠/ ٢٢٦ / ٣٩٨٩)، وعلي بن الحسن بن سليمان الحضرمي " تهذيب الكمال " (٢٠/ ٣٧٠ / ٤٠٤١)، وعمرو بن عون، وعمرو بن مرزوق الباهلي " تهذيب الكمال " (٢٢/ ٢٢٦ / ٤٤٤٦)، وأبي نعيم الفضل بن دكين، ومحمد بن حاتم الجرجرائي " تهذيب الكمال " (٢٥/ ٢٦ / ٥١٢٨)، ومحمد بن حرب " مسند أبي عوانة " (٤/ ٢٧٩ /، ٦٧٥٥)، ومحمد بن عبد الأعلى الصنعاني " ت تهذيب الكمال " (٢٥/ ٥٨١ / ٥٣٨٥)، ومحمد بن عمران الأنصاري " تهذيب الكمال " (٢٦/ ٢٣٠ / ٥٥٢٢)، ومحمد بن عيسى بن الطباع، ومحمد بن الفرج القرشي " تهذيب الكمال " (٢٦/ ٢٧٥ / ٥٥٤٢)،',NULL,'312',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (315,'ومحمد بن أبي نعيم الواسطي، ومسدد [ابن مسرهد]، ومسلم بن إبراهيم الأزدي، ومصرف بن عمرو السري " تهذيب الكمال " (٢٨/ ١٦ / ٥٩٧٩)، ومطرف بن عبد الله الهلالي " تهذيب الكمال " (٢٨/ ٧٠ / ٦٠٠٢)، وهارون بن معاوية الأشعري " تهذيب الكمال " (٣٠/ ١٠٦ / ٦٥٢٥)، وهشام بن عبد الملك الباهلي " تهذيب الكمال " (٣٠/ ٢٢٨ / ٦٥٨٤)، ويحيى بن صالح " أخبار قزوين " (٢/ ٣١٧) ويحيى بن طلحة اليربوعي " تهذيب الكمال " (٣١/ ٣٨٨ / ٦٨٥١) ويحيى بن عبد الحميد الحماني، ويحيى بن محمد بن السكن " مسند أبي عوانة " (٣/ ٢١١ / ٤٧٠٩)، وأبي اليمان يحيى بن اليمان " مسند أبي عوانة " (٣/ ٤٢٤ / ٥٥٦١)، ويزيد بن عبد ربه الزبيدي، تهذيب الكامل " (٣٢/ ١٨٣ / ٧٠١٩)، ويعقوب بن كعب الحلبي، " تهذيب الكمال " (٣٢/ ٣٥٩ / ٧١٠٠)، ويوسف بن حماد المعني " تهذيب الكمال " (٣٢/ ٤١٩ / ٧١٣٢)، ويوسف بن سلمان الباهلي " تهذيب الكمال " (٣٢/ ٤٣٢ / ٧١٣٩)، وأبي بكر الحميدي، وأبي سلمة التبوذكي، وأبي عمر الحوضي، وأبي الوليد الطيالسي، وأبي اليمان الحمصي.
روى عنه: أحمد بن خلف السابح " تاريخ بغداد " ت (٤/ ١٣٥ / ١٨١٥)، وأحمد بن كامل القاضي، وأبو سهل [أحمد بن محمد] بن زياد القطان، وأحمد بن محمد بن سعيد " علل الدارقطني " (١/ ١٩٣ / ١٧) وإسماعيل بن محمد الصفار وحمزة بن محمد الدهقان، وصدقة بن زكريا الدهقان " تاريخ بغداد " (٩/ ٣٣٤ / ٤٨٧٨)، وعباس بن عبد الله المزني " تاريخ بغداد " (١٢/ ١٥٥ / ٦٦٢٨)، ووراقه – عبد الله بن الفضل – " تاريخ بغداد " (١٠/ ٤٣ / ٥١٧١)، وعبد الله بن محمد البغوي، وعثمان بن أحمد الدقاق " تاريخ بغداد " (٨/ ٢٦٠ / ٤٣٦٠)، وعثمان بن عبد الله بن مسلم " تاريخ بغداد " (١١/ ٢٩٥ / ٦٠٧٣)، وقاسم بن زكريا المطرز، وابنه محمد بن عبد الكريم " الثقات " (٨/ ٤٢٣ / ١٤٢١١)، ومحمد بن عمرو الرزاز، ومحمد بن يوسف الفراء " تاريخ بغداد " (٣/ ٤٠٦ / ١٥٣٥)، ومنصور بن إبراهيم الهلالي " تاريخ بغداد " (١٣/ ٨٣ / ٧٠٦٠)، وموسى بن عيسى الديرعاقولي " تاريخ بغداد " (١٣/ ٣٠٦ / ٧٢٨٥)، وموسى بن هارون الحافظ، ويحيى بن صاعد،',NULL,'313',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (316,'وأبو إسماعيل الترمذي، وأبو عمرو بن السماك، وأبو الفضل المقرئ القيار " تاريخ بغداد " (١٤/ ٤٢٣ / ت ٧٧٧٦)، والقاضي المحاملي.
وكل اسم لم يعز إلى كتاب - وميزته بالأسود العريض - من معجم شيوخه وتلاميذه، فهو من ترجمته في " تاريخ بغداد ".
التعديل والتجريح: قال الخطيب البغدادي في " تاريخ بغداد " (١١/ ٧٨ / ٥٧٥٣): " كان ثقةً ثبتاً ".
وقال أيضاً بسنده عن أحمد بن كامل القاضي قال: " كان ثقة مأموناً ".
وقال الذهبي في " تذكرة الحفاظ " ت (٢/ ٦٠٢ / ٦٢٦): " الحافظ الصدوق طوف وكتب الكثير ".
وانظر: " الثقات " (٨/ ٤٢٣ / ١٤٢١١)، و" سير أعلام النبلاء " (١٣/ ٣٣٥ / ١٥٤)، و" المقتنى في سرد الكنى " (٢/ ١٤٩ / ٦٧٠٣)، والمقصد الأرشد " (٢/ ١٩٤ / ٦٨٠)، و" طبقات الحفاظ " (١/ ٢٧٢ / ٦١٣)، و" رجال تفسير الطبري " (٣٦٣/ ١٦٦٦).
١٦٨ - عبد الكريم الدهان (١٩٧٠)
(طب: ١٠ ر / ٤ ش)
(تس: ٩ ر / ٣ ش)، و (تهـ: ١ ر / ١ ش)

عبد الكريم بن أبي عمير، الدهان: من العاشرة، فيه جهالة! ولما يرويه نكارة.
روى عن: حماد بن محمد الفزاري (تس ٣٠٣٧٠)، وعمر بن صالح بن أبي الزاهرية (تهـ٣٧٢)، والوليد بن مسلم (تس ١٩٧٠ و ٧٥٧٦ و ١٦٦٠٩ و ١٦٢٧٤ و ٢٣٧٨٠ و ٢٨٤٩٤)، وأبي المطرف المخزومي (تس ١١٣٧١ و ١٢٨٧١).
روى عنه: محمد بن إبراهيم بن زياد الرازي " تاريخ بغداد " (١١/ ٣٠٥ / ٦٠٩٩)، وأبو عبد الله محمد بن موسى النهرتيري " تاريخ بغداد " (١١/ ٣٠٥ / ٦٠٩٩).
التعديل والتجريح: قال الذهبي في " ميزان الاعتدال " (٤/ ٣٨٥ / ٥١٧١) بعد أن ساق',NULL,'314',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (317,'حديثاً من طريقه قال: " عبد الكريم بن أبي عمير الدهان، عن الوليد بن مسلم، فيه جهالة، والخبر منكر ".
وقال في " المغني في الضعفاء " (٢/ ٤٠٢ / ٣٧٨٢): " تفرد بخبر منكر ".
قلت: وتمام القصة والحديث في " تاريخ بغداد " (١١/ ٣٠٥ / ٦٠٩٩).
وانظر: " رجال تفسير الطبري " (٣٦٢/ ١٦٦٢).
١٦٩ - عبد الله بن آدم (٣٤٥٤٨)
(طب: تس ١ ر / ١ ش)
عبد الله بن آدم - والصحيح: عبيد بن آدم - وهو: أبو محمد، عبيد بن آدم بن أبي إياس، العسقلاني، يأتي فيمن اسمه (عبيد) في موضعه من كتابي هذا - إن شاء الله تعالى - وانظر " التاريخ " للمصنف (١/ ٢٨)، فقد أخرجه الطبري فيه بغير هذا التصحيف أيضاً.
روى عن: أبيه، وهو: آدم بن أبي إياس العسقلاني (تس ٣٤٥٤٨).
روى عنه: محمد بن جرير الطبري (تس ٣٤٥٤٨).
١٧٠ - أبو عبد الرحمن بن شبوية (١١٣)
(طب: ٣٦ ر / ٨ ش)
(تس: ١٩ ر / ٨ ش)، و (تخ: ١٧ ر / ١ ش)، و (تهـ: ١ ر / ١ ش)
أبو عبد الرحمن، عبد الله بن أحمد بن شبويه، الخزاعي، مولاهم، المروزي توفي سنة خمس وسبعين ومائتين، من الحادية عشرة، مستقيم الحديث، ومن أئمة أهل الحديث.
روى عن: أبيه أحمد بن ثابت بن شبويه (تس ٤٩٢٦)، و (تهـ ٥٩)، و (تخ ١/ ٣٣٢ و ٢/ ٥٥٢ و ٢/ ٦٤٩ و ٢/ ٦٧٨ و ٢/ ٦٩٨ و ٣/ ٦١ و ٣/ ٦٥ و ٣/ ٧١ و ٣/ ١٤٩ و ٣/ ١٦٤ و ٣/ ١٦٧ و ٣/ ١٦٩ و ٣/ ٢٣٨ و ٣/ ٢٣٩ و ٣/ ٢٦٤ و ٣/ ٤٧٦ و ٤/ ٧٠)، وآدم بن أبي إياس (تس ١٥١٤٨)، وإسحاق بن إبراهيم بن العلاء الزبيدي',NULL,'315',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (318,'المعروف بـ (زبريق) (تس ١٥٣٩٠ و ١٨٦٩٤)، وسعيد بن الحكم بن أبي مريم (تس ٤٦١٦ و ٤٦٤٦ و ١٢٧٧٥)، وشهاب بن عباد العبدي (تس ٣٣٠٥١)، وعلي بن الحسن بن شقيق المروزي (تس ١١٦١٣ و ٢٤٠٧٥ و ٣٢٢٩٥ و ٣٦٧٧١) و (تخ ١/ ١٦٨)، وعلي بن الحسين بن واقد (تس ١١٣ و ١٩١١ و ١٧٥٣٥ و ٣٠٢٦٥ و ٣٠٣١٦ و ٣٤٥٣٦)، ومطهر بن الهيثم بن الحجاج الطائي (تس ٢٨٥٦١)، وأبي إسحاق إبراهيم بن بشار الرمادي البصري " تاريخ بغداد " (٩/ ٣٧١ / ٤٩٤٦)، وأبي إسحاق إبراهيم بن بشار بن محمد الخراساني الصوفي - صاحب إبراهيم الأدهم - تاريخ بغداد " (٦/ ٤٧ / ٣٠٧٠)، وإبراهيم الحارث - من ولد عبادة بن الصامت - " الجرح والتعديل " (١/ ٣٠٩)، وإسحاق بن راهويه " تاريخ بغداد " (٩/ ٣٧١ / ٤٩٤٦)، وإسماعيل بن الخليل الخزاز " تهذيب الكمال " (٣/ ٨٣ / ٤٤١)، وأيوب بن سليمان بن بلال " الثقات " (٨/ ١٢٦ / ١٢٥٥٦)، وحفص بن حميد الإسكافي " الجرح والتعديل " (٣/ ١٧١ / ٧٣٦)، وداود بن سليمان المروزي، تاريخ بغداد (١٠/ ٢٨٣ / ٥٤٠٥) وسليمان بن بلال " الثقات " (٨/ ٣٦٦ / ١٣٩٠٠)، وسليمان بن صالح (سلمويه) " الجرح والتعديل " (٤/ ١٢٣ / ٥٣٧)، وعبدان بن عثمان " تاريخ بغداد " (٩/ ٣٧١ / ٤٩٤٦)، وعلي بن حجر بن إياس المروزي " تاريخ بغداد " (٩/ ٣٧١ / ٤٩٤٦)، وعلي ابن المديني " تاريخ بغداد " (٨/ ٣٤٧ / ٤٤٥)، وعبد العزيز بن الوليد بن سليمان بن أبي السائب الدمشقي " الثقات " (٨/ ٣٩٦ / ١٤٠٦٤)، وعبيد الله بن موسى بن أيوب " الثقات " (٨/ ٣٦٦ / ١٣٩٠٠)، وعمر بن حفص " مسند البزار " (٦/ ٣١٣ / ٢٣٢٥)، وعمرو بن العباس الرازي " الجرح والتعديل " (١/ ٢٧) وعمار بن الحسن الهمداني " تهذيب الكمال " (٢١/ ١٨٥ / ٤١٥٧)، وأبي نعيم الفضل بن دكين " الثقات " (٨/ ٣٦٦ / ١٣٩٠٠)، وقتيبة بن سعيد " الجرح والتعديل " (١/ ٨٦)، وأبي غسان مالك بن إسماعيل " تاريخ بغداد " (٩/ ٣٧١ / ٤٩٤٦)، ومحمد بن أعين المروزي " تهذيب الكمال " (٢٤/ ٤٩٨ / ٥٠٧٥)، وأبي كريب محمد بن العلاء الهمداني " تاريخ بغداد " (٩/ ٣٧١ / ٤٩٤٦)، ومطرف بن عبد الله الهلالي " تهذيب الكمال " (٢٨/ ٧٠ / ٦٠٠٢) وأبي داود الطيالسي " الجرح والتعديل " (١/ ٢٦٢)، وأبي اليمان الحمصي " تاريخ بغداد " (٩/ ٣٧١ / ٤٩٤٦).
روى عنه: أبو العباس أحمد بن عبد الله بن سابور الدقاق " تاريخ بغداد " (٤/ ٢٢٥ / ١٩٢٨)، وأبو بكر أحمد بن عمر البزار " مسند البزار " (٦/ ٣١٣ / ٢٣٢٥)، وأبو بكر أحمد بن القاسم',NULL,'316',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (319,'ابن عطية الرازي «الجرح والتعديل» (١/ ٨٦)، وبكر بن منير البخاري «تاريخ بغداد» (٦/ ٤٧ / ٣٠٧٠)، وزكريا بن داود بن بكر النيسابوري «الجرح والتعديل» (١/ ٣٠٩)، وأبو يحيى زكريا بن يحيى الناقد «تاريخ بغداد» (٩/ ٣٧١ / ٤٩٤٦)، والحسن بن سفيان «الثقات» (٧/ ٦٦٨ / ١٢٠١٨)، وعبد الرحمن بن محمد التميمي «تاريخ بغداد» (١٠/ ٢٨٣ / ٥٤٠٥)، وعلي بن الحسين «الجرح والتعديل» (١/ ٢٧)، وعمر بن إبراهيم بن سليمان البغدادي «تهذيب الكمال» (٢١/ ٢٦٧ / ٤١٩٩)، ومحمد بن عبد الله بن سليمان الحضرمي «تاريخ بغداد» (٨/ ٣٤٧ / ٤٤٥٥)، ومحمد بن فرح الغساني «تاريخ بغداد» (٣/ ١٦٥ / ١٢٠٨)، وأبو حامد محمد بن هارون الحضرمي «تاريخ بغداد» (٩/ ٣٧١ / ٤٩٤٦)، ومحمد بن يحيى «الجرح والتعديل» (١/ ٢٦٢)، ومحمد بن يوسف بن مطر «الثقات» (٨/ ٣٦٦ / ١٣٩٠٠) وأبو بكر بن أبي الدنيا «تاريخ بغداد» (٩/ ٣٧١ / ٤٩٤٦)، ويحيى بن محمد بن صاعد «تاريخ بغداد» (٩/ ٣٧١ / ٤٩٤٦).
التعديل والتجريح: ذكره محمد بن حبان في «الثقات» (٨/ ٣٦٦ / ١٣٩٠٠) وقال: «مستقيم الحديث».
وقال الخطيب البغدادي في «تاريخ بغداد» (٩/ ٣٧١ / ٤٩٤٦): «من أئمة أهل الحديث، وكان من أفاضل الناس، ممن له الرحلة في طلب العلم».
وقد تصحف اسمه واسم شيخه في الأثر رقم (١٥٧٩) من طبعة دار الفكر (١٩١١) من طبعة العلمية فهو: حدثنا عبيد الله بن أحمد بن شبرمة، قال: ثنا علي بن الحسن، وهو خطأ - والصحيح -: حدثنا عبد الله بن أحمد بن شبويه، قال: ثنا علي بن الحسين.
وتصحف اسم جده في الأثر (تس ٣٣٠٥١) من (شبويه) إلى (حبويه) بحاء مهملة بعدها باء، وقد بحثت في أسماء، وألقاب، من يمكن أن يكون (حبويه)، أو قريبًا منه مثل: (حيويه) و (حنويه) و (جبويه) و (جنونه) و (حمويه) كما هو في «الإكمال لابن ماكولا» (٢/ ٣٥٨) و «نزهة الألباب في الألقاب» (١/ ١٩٥ / ٦٩٦، و ٦٩٧ و ٦٩٨ و ٦٩٩ و ٧٠٠) فلم أجد من هكذا اسمه.
وبحثت في مرويات (شهاب بن عباد العبدي)، ومرويات (شهاب بن عباد العصري) والتي تزيد على (١٧٠) رواية، في مختلف كتب الحديث، وخاصة في كتب «التفسير» و',NULL,'317',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (320,'«الصحيحين»، فلم أجد من روى عن (شهاب) ممن اسمه (عبد الله بن أحمد) إلا (عبد الله بن أحمد بن المستور الأشجعي الكوفي)، ولا يمكن أن يكون المراد.
ثم بحثت في مرويات من اسمه (عبد الله بن أحمد بن المستور)، و (غيره)، لعلي أجد من في اسمه (شبويه)، أو (حبويه)، ممن يروي عن (شهاب)، فلم أجد؛ فترجح عندي أنه (ابن شبويه المروزي) وذلك لسببين:
الأول: اتحاد الطبقة.
والثاني: اشتراكهما في كثير من الشيوخ والتلاميذ.
ولذلك أثبتُّ روايته هنا، وأثبتُّ اسم (شهاب بن عباد العبدي) في معجم شيوخه، وأثبتُّه في معجم تلاميذ (شهاب بن عباد العبدي) في ترجمة هذا الأخير من كتابي «المعجم الكبير».
وانظر: «رجال تفسير الطبري» (٣٠٢/ ١٣٤٠).
١٧١ - أبو جعفر الناقد (٢٠٧٥١)
(ابن ماجه)
(طب: ٣ ر / ٣ ش)
(تس ١ ر / ش)، و (تهـ ١ ر / ١ ش) و (تق ١ ر / ش)
أبو جعفر، عبد الله بن إسحاق بن محمد، الناقد: الواسطي، ثم البغدادي، من الحادية عشرة وثقة ابن حبان.
روى عن: روح بن عبادة القيسي (ق ٧٨)، ووهب بن جرير (تس ٢٠٧٥١)، ويحيى بن إسحاق السيلحيني (تهـ ١٤٤٣).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: ذكره ابن حبان في كتاب «الثقات».
وذكره الحافظ في التهذيب: وأرَّخ وفاته بعد سنة (مائتين).',NULL,'318',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (321,'قلت: إن كان يقصد بعد سنة (مائتين) أي حولها – وهو الظاهر من كلامه -!! فبعيد أن يروي عنه الطبري؛ لأن مولد الطبري كان بعد انقضاء ربع المائة الثالثة؛ أي سنة أربع وعشرين، أو: خمس وعشرين ومائتين.
وذكره الشيخ شاكر في تحقيقه «لتفسير الطبري» (١٦/ ٥٩٠ / ٢٠٧٦٠)، وقال: «لم أجد له ترجمة وروى عنه في «تاريخ الصحابة» انظر «ذيل المذيل»، «تاريخ الطبري» (١٣/ ٦١). انتهى.
قلت: بل وهو مترجم في عدة كتب!!
وانظر: «الثقات» (٨/ ٣٦٢ / ١٣٨٧٨)، و «تهذيب الكمال» (١٤/ ٣٠٣ / ٣١٦١)، و «الكاشف» (١/ ٥٣٨ / ٢٦٢٩)، «تهذيب التهذيب» (٥/ ١٢٨ / ٢٥٠)، و «تقريب التهذيب» (١/ ٢٩٥ / ٣٢٠٩).
١٧٢ - عبد الله بن الحجاج (١١٥٣١)
(طب: ٣ ر / ١ ش)
(تس: ٢ ر / ١ ش)، و (تهـ: ١ ر / ١ ش)
عبد الله بن الحجاج، خطأ!! والصواب: عبيد الله بن الحجاج بن المنهال، الأنماطي، السلمي، مولاهم، البصري: من العاشرة، لم أعرفه، ولم أجد له ترجمة.
روى عن: أبيه الحجاج بن المنهال الأنماطي (تس ١١٥٣١ و ١٥١٦٩)، و (تهـ ٢٣١٦)، وبدل بن المحبر «أحاديث أبي عروبة الحراني» (١/ ٥٣ / ٤٦)، والحسين بن حفص «تاريخ دمشق» (٦٠/ ٣١)، والحكم بن مروان «المعجم الأوسط» (٤/ ٣٧٣ / ٤٤٧٠)، وحماد بن سلمة «مسند الروياني» (٢/ ٥٥ / ٨٢٦)، وعباس بن عبد العظيم «مسند الروياني» (٢/ ٥٦ / ٨٢٦)، وعلي بن المديني «مسند الروياني» (٢/ ٥٦ / ٨٢٦)، وعمر بن الخطاب السدوسي البصري «تهذيب التهذيب» (٧/ ٣٨٥ / ٧٢٤)، ويحيى بن حماد «العلل المتناهية» (١/ ٣٧٧).
روى عنه: أبو جعفر محمد بن جرير الطبري، وأحمد بن عبد الله بن محمد وكيل أبي صخرة «تاريخ دمشق» (٦٠/ ٣١)، وسلم وقيل: سالم بن عصام بن سلم الأموي «تاريخ',NULL,'319',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (322,'أصبهان» (١/ ٣٩٦ / ٧٤٣)، وعبد الله بن إسحاق بن إبراهيم المدائني «المعجم الأوسط» (٤/ ٣٧٣ / ٤٤٧٠)، ومحمد بن العباس الأخرم الأصبهاني «المعجم الكبير» (٢٢/ ٢٨٢ / ٧٢٣)، ويعقوب بن إبراهيم البراء «تاريخ دمشق (٣٠/ ٦٦)، وأبو عروبة الحراني «أحاديث أبي عروبة الحراني» (١/ ٥٧ / ٥٦).
التعديل والتجريح: ذكره الشيخ أحمد شاكر في تحقيقه «لتفسير الطبري» (١٣/ ١٤٣ / ١٥١٥٩) وقال: «لم أجد له ترجمة».
قلت: وشيخه هنا هو: أبوه، أبو محمد حجاج بن المنهال، الأنماطي، السلمي مولاهم، البصري، توفي سنة ٢١٧، من التاسعة، ثقة، فاضل، وقد ترجمت له في «المعجمين الكبير والصغير» وإلى الله المرجع والمصير.
وفي الرواة عن أبيه في ترجمة أبيه من «تهذيب الكمال» (٥/ ٤٥٧ / ١١٢١) ابنه عبيد الله بن الحجاج، فإن يكن هو، فقد تصحف اسمه هنا، ولم أقف له على ترجمة في كتب الرجال التي بين يدي، ولكن له رواية في «علل الدارقطني» (٩/ ٦٠ / ١٦٤١) يروي فيها عن: بدل [بن المحبر]، ويروي عنه: إبراهيم بن حماد ووكيل صاحب أبي صخرة، إضافة لرواية الطبري السابقة، بما يخرجه من دائرة الجهالة إلى عدم المعرفة!!
١٧٣ - أبو عبد الرحمن القطواني (٢٢٥٢)
(أبو داود، والترمذي، وابن ماجه)
(طب: ٣٩ ر / ٢٠ ش)
(تس ١٨ ر / ١٤ ش) و (تخ ٣ ر / ٢ ش)
و (تهـ ١٢ ر ش) و (تق ٦ ر / ٥ ش)
أبو عبد الرحمن، عبد الله بن الحكم بن أبي زياد، القطواني، الكوفي، الدهقان، توفي سنة خمس وخمسين ومائتين، من العاشرة، صدوق.
روى عن: حجاج بن محمد (تس ٢٣٢٣٠ و ٢٣٢٣٢)، و (تق ٤٩٦)، وروح بن عبادة (تق ٧٧ و ٧٨ (تخ ١/ ١٢٩)، وزياد بن أيوب (تهـ ٤٥٢)، وزيد بن الحباب (تس ٣٧٠٧٣ و ٣٧١٠٢)، و',NULL,'320',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (323,'(تهـ ٤١٣ و ١٤٨)، و (تخ ٢/ ٢١٠)، (تق ٧٨٨)، و (د، ت)، وأبي زيد سعيد بن أوس الأنصاري النحوي (تس ٢٦٧٩٩)، و (ت)، وسفيان بن عيينة (تس ١٠٥٢٥)، و (ت) وأبي داود سليمان بن داود الطيالسي (تهـ ٢٤٠٥ و ٣٠٠٧)، و (ت)، وسيار بن حاتم العنزي (تهـ ٩٥٣ و ١٩٤٨ و ٤٠٢٠) و (ت، ق)، وعبد الله بن أبي بكر المقدمي (تس ٣١٩٠٥ و ٣١٩٠٦)، وأبي عبد الرحمن عبد الله بن يزيد المقرئ (تس ٣١٨٦ و ٢٣٠٨٨)، و (تهـ١٩٦٣)، و (تق ٤٤٧)، وعبيد الله بن موسى (تس ٥٧٩٧)، و (د، ت)، وعثمان بن عمر (تس ٢٢٥٢)، و (تق ١٠٤١)، ومكي بن إبراهيم البلخي (تس ١٢٧٧٦)، ونصر بن ميمون المضروب (تس ٣٣٧٥٩) ووهب بن جرير بن حازم (تخ ١/ ١٧)، ويحيى بن أبي بكير (تس ٥٧٩٨)، ويزيد بن حيان (تس ١٠٥٢٦)، ويزيد بن هارون [الواسطي] (تس ٢٤٧٠١)، و (تهـ ٢٥٩٢)، ويعقوب بن إبراهيم بن سعد [الزهري] (تس ١١٣٣١) و (تهـ ١٩١٥)، و (ت، ق)، وأبي نباتة يونس بن يحيى المدني (تهـ ١١٩٣٣)، و (ت).
روى عنه: عبيد العجلي «المعجم الكبير» (٢/ ٣٢٢ / ٢٣٤٣)، ويحيى بن مندة «الإيمان» (٢/ ٨٥٨ / ٨٩٥).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً صدوقًا مشهورًا.
وانظر: «الكنى والأسماء» (١/ ٥٣١ / ٢١١٩)، و «الجرح والتعديل» (٥/ ٣٨ / ١٦٩)، و «الثقات» (٨/ ٣٦٤ / ١٣٨٩٥)، و «تهذيب الكمال» (١٤/ ٤٢٧ / ٣٢٣١)، و «الكاشف» (١/ ٥٤٦ / ٢٦٩٠)، و «المقتنى في سرد الكنى» (١/ ٣٧٢ / ٣٨٦٨)، و «تهذيب التهذيب» (٥/ ١٦٦ / ٣٢٧، ٥/ ١٩٥ / ٣٨٤)، و «تقريب التهذيب» (١/ ٣٠٠ / ٣٢٨٠)، و «رجال تفسير الطبري» (٣٠٧/ ١٣٧١).
١٧٤ - عبد الله بن داود (١٠٩٣٦)
(طب: (تس: ١ ر / ١ ش)
عبد الله بن داود، خطأ! والصحيح: علي بن داود، وهو: القنطري (٢٦٥٤)، يأتي.
روى عن: عبد الله بن صالح (تس ١٠٩٣٦).',NULL,'321',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (324,'روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: أغفله الشيخان شاكر في تحقيقهما «لتفسير الطبري».
والظاهر أن اسمه قد تصحف، أو أخطأ النساخ فيه؛ إذ قد يكون هو: علي بن داود القنطري، الذي أخرج له الطبري بنفس الإسناد الذي نحن بصدده نحو (٣٠٠)، (ثلاثمائة) رواية، والله أعلم.
أو أن يكون من شيوخ شيوخ الطبري، الذين روى عنهم بواسطة، كما هو في التراجم رقم (١٠٤، ١٠٧، ١٢٠، ١٦٩، ١٧٦) وغيرها.
وشيخه هو: أبو صالح، عبد الله بن صالح بن محمد بن مسلم، الجهني، المصري كاتب الليث بن سعد، ولد سنة ١٣٧، وتوفي سنة ٢٢٣، من العاشرة صدوق، كثير الغلط، ثبت في كتابه، وكانت فيه غفلة، ولم أقف فيمن روى عنه من هكذا اسمه، وقد ترجمت له في «المعجمين الكبير، والصغير».
ثم رأيت الشيخ التركي في تحقيقه «لتفسير الطبري» (٨/ ١٧)، قد صحح اسمه إلى علي بن داود، فالحمد لله على توفيقه.
١٧٥ - أبو سعيد الكندي (٣٩٥٩)
(الستة: خ: ٨ ر، و م: ٧ ر)
(طب: (تس: ١ ر / ١ ش)
أبو سعيد، عبد الله بن سعيد بن حصين، الكندي، الكوفي، الأشج: مولده نحو سنة سبع وستين ومائة، وتوفي سنة سبع وخمسين ومائتين، وقد زاد على التسعين، من صغار العاشرة، ثقة.
روى عن: أبي خالد الأحمر (تس ٣٩٥٩)، و (م، ٤).
روى عنه: إبراهيم بن أبي طالب «سنن البيهقي» (٧/ ١٣٣ / ١٣٥٣٦)، وبدر بن الهيثم',NULL,'322',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (325,'القاضي «سنن الدارقطني» (٢/ ١٩٥ / ٨٢)، وعبد الله بن عبد الرحمن الدارمي «سنن الدارمي» (١/ ٤٢٣ / ١١٣٤)، ومحمد بن موسى الحلواني «سنن البيهقي» (٧/ ٢١٤ / ١٣٩٩٨)، ويزداد بن عبد الرحمن «سنن الدارقطني» (٢/ ١٩٥ / ٨٢)، وأبو محمد بن صاعد «سنن الدارقطني» (٢/ ١٩٥ / ٨٢).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، صدوقًا، حافظًا، إمام أهل زمانه، شيخ الإسلام، محدث الكوفة، وصاحب التفسير والتصانيف، وليس به بأس، ولكن يروي عن قوم ضعفاء.
وانظر: «الطبقات الكبرى» (٦/ ٤١٥)، و «الكنى والأسماء» (١/ ٣٦٨ / ١٣٥٥)، و «الجرح والتعديل» (٥/ ٧٣ / ٣٤٢)، و «الثقات» (٨/ ٣٦٥ / ١٣٨٩٩)، و «الإرشاد» (٢/ ٥٧٦)، و «من روى عنهم البخاري في الصحيح» (١/ ١٤٨ / ١٣٤)، و «تسمية من أخرجهم البخاري ومسلم» (١/ ١٥٢ / ٨٢٦)، و «رجال البخاري» (١/ ٤٠٩ / ٥٨٣)، و «التعديل والتجريح» (٢/ ٨٤٨ / ٨٥٨)، و «تهذيب الكمال» (١٥/ ٢٧ / ٣٣٠٣)، و «الكاشف» (١/ ٥٥٨ / ٢٧٥١)، و «تذكرة الحفاظ» (٢/ ٥٠١ / ٥١٧) و «المقتنى في سرد الكنى» (١/ ٢٧٥ / ٢٦٦٧)، و «تهذيب التهذيب» (٥/ ٢٠٨ / ٤١١)، و «تقريب التهذيب» (١/ ٣٠٥ / ٣٣٥٤)، و «نزهة الألباب» (١/ ٧٥ / ١١٦)، و «رجال تفسير الطبري» (٣١١/ ١٣٩٥).
١٧٦ - أبو علي العطار (١١٤٥٢)
(البخاري: ٦ ر، ومسلم: ٣ ر، وأبو داود، والنسائي، والترمذي)
(طب: ٣ ر / ٣ ش)
(تس: ١ ر / ١ ش)، و (تهـ: ٢ ر / ٢ ش)
أبو علي، عبد الله بن الصباح، الهاشمي، مولاهم، العطار، المربدي، البصري، توفي سنة خمسين ومائتين – أو بعدها -، من كبار الحادية عشرة، ثقة.
روى عن: حفص بن عمر الحوضي (تس ١١٤٥٢)، وأبي علي عبيد الله الحنفي (تهـ ٦٤٣)، و (خ، م، ت، س)، وعبيد الله بن موسى (تهـ ١٢٤٣)، و (تق) – الترمذي في الشمائل.
روى عنه: أحمد بن يحيى بن زهير التستري «المعجم الأوسط» (٢/ ١٨٥ / ١٦٦٥)،',NULL,'323',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (326,'ومحمد بن أحمد الرقام التستري «المعجم الأوسط» (٧/ ١٦٥ / ٧١٧٠).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، صالحًا.
وانظر: «الجرح والتعديل» (٥/ ٨٨ / ٣٩٩)، و «الثقات» (٨/ ٣٥٩ / ١٣٨٦٣) و «من روى عنهم البخاري في الصحيح» (١/ ١٤٤ / ١٢٩)، و «تسمية من أخرجهم البخاري ومسلم» (١/ ١٥٢ / ٨٢٥)، و «رجال صحيح البخاري» (١/ ٤١١ / ٥٨٩)، و «التعديل والتجريح» (٢/ ٨٣٥ / ٨٣١)، و «تهذيب الكمال» (١٥/ ١٢١ / ٣٣٤٠)، و «الكاشف» (١/ ٥٦٣ / ٢٧٨٤)، و «تهذيب التهذيب» (٥/ ٢٣٢ / ٤٥٣)، و «تقريب التهذيب» (١/ ٣٠٨ / ٣٣٩٢)، و «رجال تفسير الطبري» (٣١٤/ ١٤١٠).
١٧٧ - عبد الله الكلابي (٨٢٨٤)
(طب: ٤ ر / ٤ ش)
(تس: ٢ ر / ٢ ش)، و (تهـ: ٢ ر / ٢ ش)
عبد الله بن عبد الله بن أسيد، الكلابي: من العاشرة، لم أعرفه، ولم أجد له ترجمة، ولم يعرفه الشيخ شاكر قبلي (٨٢٨٤)، وصحح الشيخ التركي في تحقيقه «لتفسير الطبري» (٦/ ٢٧٢) اسمه على الوجه الذي أثبته، ولم يجد له ترجمة أيضًا.
روى عن: عبد الله بن بكر السهمي (تس: ٨٢٨٤)، ومروان بن معاوية الفزاري (تهـ ١٩٤٥) وأبي خالد الأحمر (تس ٢٢٧٥٤) وأبي بكر بن عياش (تهـ ٢٨١٠).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: ذكره الشيخ شاكر في تحقيقه «لتفسير الطبري» (٧/ ٤٣٥ / ٨٢٨٤) وقال: «لم أعرفه ولم أجد له ترجمةً ولا ذكرًا في غير هذا الموضع».
قلت: بل هما في «التفسير» موضعان كما رأيت!!
وقد تحرف اسمه في نسخة (دار الفكر، طبعة سنة ١٤٠٥) في الأثر رقم (تس ٨٢٨٤) بحيث صار (محمد بن عبد الله الكلابي).',NULL,'324',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (327,'وقد أخرج له الطبري في «تهذيب الآثار»، الأثر رقم (تهـ: ١٩٤٥)، عن مروان بن معاوية الفزاري، وهو عين الأثر رقم (تس ٨٢٨٤)، والذي في «التفسير» عن عبد الله بن بكر السهمي.
وقد أخرجه أيضًا الإمام أحمد في «المسند» (٥/ ٣ / ٢٠٠٤٤)، من طريق (يزيد)، والبيهقي في «السنن الكبرى» (٤/ ١٧٩ / ٧٥٥٣)، من طريق (محمد بن الفرج) به.
١٧٨ - عبد الله بن عمير (٩٩١٩)
(طب: ٢ ر / ١ ش)
(تس: ١ ر / ١ ش)، و (تق ١ ر / ١ ش)
عبد الله بن عمير الرازي: من الحادية عشرة، لم أعرفه، ولم أجد له ترجمة، ولم يعرفه الشيخ شاكر قبلي (٩٩١٤)، وأشار الشيخ التركي في تحقيقه «لتفسير الطبري» (٧/ ٢٠٣) إلى اختلاف اسم أبيه في الأصول، ولم يتعرض لترجمته بشيء، وكذلك فعل الشيخ على رضا في «تحقيقه للتهذيب» (٤٣٠/ ٧٧٥)، حيث حكم بصحة الخبر الذي رواه الطبري من طريقه، وهو عين الخبر الذي في «التفسير»، ولم يتعرض لترجمته بشيء أيضًا.
روى عن: عبد الله بن الزبير الحميدي (تس ٩٩١٩)، (تق ٧٧٥).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: ذكره الشيخ شاكر في تحقيقه «لتفسير الطبري» (٨/ ٥٢٢ / ٩٩١٤) وقال: «شيخ الطبري لم أجد له ترجمة ولا ذكرًا في شيء من المراجع».
قلت: وشيخه هنا: أبو بكر، عبد الله بن الزبير بن عيسى، القرشي الأسدي، الحميدي المكي، ثقة، حافظ فقيه، أجلّ أصحاب ابن عيينة من العاشرة، وقد ترجمت له في «المعجمين الكبير والصغير».
وقد تابع الرازي هذا (هارون بن عبدة) عند محمد بن نصر المروزي في «تعظيم قدر',NULL,'325',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (328,'الصلاة» (٢/ ٦٥٦ / ٧٠٨) به.
وتابعهما (يعقوب بن حميد) عند الطبراني في «المعجم الكبير» (٢٣/ ٢٩٤ / ٦٥٢) من طريق أحمد بن عمرو الخلال، نحوه، فقال: «ثنا يعقوب بن حميد، ثنا سفيان، عن عمرو بن دينار، عن سلمة رجل من ولد أم سلمة – عن أم سلمة، قالت: «خاصم الزبير رجلاً إلى رسول الله صلى الله عليه وسلم – فقضى للزبير، فقال الرجل: إنما له لأنه ابن عمته! فنزلت: {فلا وربك لا يؤمنون حتى يحكموك}. الآية».
والأثر عند الحميدي، في «مسنده» (١/ ١٤٣ / ٣٠٠)، سندًا ومتنًا، حيث قال: «ثنا سفيان قال: ثنا عمرو بن دينار، قال: أخبرني سلمة – رجل من ولد أم سلمة – «أن الزبير بن العوام خاصم رجلاً إلى رسول الله صلى الله عليه وسلم، فقضى النبي صلى الله عليه وسلم للزبير، فقال الرجل: إنما قضى له لأنه ابن عمته! فأنزل الله عز وجل: {فلا وربك لا يؤمنون حتى يحكموك فيما شجر بينهم ثم لا يجدوا في أنفسهم حرجًا مما قضيت ويسلموا تسليمًا}.».
وتابعه سعيد بن منصور في «سننه» (٤/ ١٣٠٠ / ٦٦٠) عن سفيان به أيضًا.
ثم رأيت الأثر بعد ذلك في «الجزء المفقود من تهذيب الآثار» (١/ ٤٣٠ / ٧٧٥) وقد سكت عنه الشيخ علي رضا في تعليقه عليه، ولم يتكلم فيه جرحًا ولا تعديلاً!! ولا غير ذلك، وذكر طرقًا أخرى للحديث وقف عليها، فتُنظر هناك.
١٧٩ - أبو صديف الآملي (١٨٤)
(طب: ٢ر / ٢ش)
(تس: ١ر / ١ش)، (تق ١ر /١ش)
أبو صديف، عبد الله بن كثير، الآملي: من العاشرة، لم أعرفه، ولم أجد له ترجمة، ولم يعرفه الشيخ شاكر قبلي، (١٨٤)، ولم يتعرض الشيخ التركي في تحقيقه «لتفسير الطبري» (١/ ١٧٥) لترجمته بشيء.',NULL,'326',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (329,'روى عن: أبي غسان مالك بن إسماعيل (تق ٦٧٤)، وهاشم بن القاسم (١٨٤).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: ذكره الشيخ شاكر في تحقيقه «لتفسير الطبري» (١/ ١٧٥ / ١٤٨).
وقال: «شيخ الطبري: لم أعرف من هو، ولم أجد له ذكرًا، وأخشى أن يكون في تحريف».
قال السقاف في «فهارس رجال الطبري» (١٠٧) أبو صديف الآملي = عبد الله بن كثير.
وقال في (١٢١) عبد الله بن كثير الداري، أبو صديف الآملي.
قلت: لم أقف على ترجمة أو ذكر لمن كنيته (أبو صديف) غيره؛ وقد ترجمت لشيخيه – وكلاهما ثقتان من التاسعة – في «المعجمين الكبير، والصغير» وقد شاركه في اسمه واسم أبيه (عبد الله بن كثير)، عدة منهم:
٦٧ - تمييز: عبد الله بن كثير، المازني، من الصحابة، شهد فتح قبرص مع معاوية بن أبي سفيان، سنة ثلاث وثلاثين.
وانظر: الإصابة (٤/ ٢١٧ / ٤٩١٣).
٦٨ - تمييز: أبو معبد، عبد الله بن كثير، الداري، المكي، القارئ، مولى عمرو بن علقمة الكناني، من أبناء فارس، وكان عطارًا بمكة، توفي سنة عشرين ومائة، من السادسة، أحد الأئمة، ثقة، صدوق، وكان له أحاديث صالحة.
وانظر: «الطبقات الكبرى» (٥/ ٤٨٤)، و «طبقات خليفة» (٢٨٢)، «تهذيب الكمال» (١٥/ ٤٦٨ / ٣٤٩٩)، و «الكاشف» (١/ ٥٨٧ / ٢٩٢٥)، و «تهذيب التهذيب» (٥/ ٣٢٢ / ٦٣٤)، و «تقريب التهذيب» (١/ ٣١٨ / ٣٥٥٠).
٦٩ - تمييز: عبد الله بن كثير بن المطلب القرشي السهمي، من بني عبد الدار المكي القاضي من خيار أهل مكة، توفي بعد سنة اثنتين وعشرين ومائة من السادسة، مقبول.
روى عن: مجاهد.',NULL,'327',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (330,'روى عنه: ابن جريج، وشبل بن عباد.
وانظر: «التاريخ الكبير» (١٥/ ١٨١ / ٥٦٧)، و «الجرح والتعديل» (٥/ ١٤٤ / ٦٧٣)، و «الثقات» (٧/ ٥٣ / ٨٩٧٢)، و «مشاهير علماء الأمصار» (١/ ١٤٧ / ١١٥٩)، و «التعديل والتجريح» (٢/ ٨٢٥ / ٨١١)، و «تهذيب الكمال» (١٥/ ٤٦٤ / ٣٤٩٨)، و «تهذيب التهذيب» (٥/ ٣٢١ / ٦٣٣)، و «تقريب التهذيب» (١/ ٣١٨ / ٣٥٤٩)، و «لسان الميزان» (٧/ ٢٦٨ / ٣٥٩٩).
٧٠ - تمييز: عبد الله بن كثير، الطويل، القارئ، الدمشقي، إمام المسجد الجامع بدمشق، توفي سنة ست وتسعين ومائة، من التاسعة، صدوق مقرئ، لا بأس به.
روى عن: الأوزاعي، وزهير بن محمد.
روى عنه: سليمان بن عبد الرحمن بن بنت شرحبيل.
وانظر: «الجرح والتعديل» (٥/ ١٤٤ / ٦٧٤)، و «الثقات» (٨/ ٣٤٦ / ١٣٨٠٤) و «تهذيب الكمال» (١٥/ ٤٧١ / ٣٥٠٠)، و «تهذيب التهذيب» (٥/ ٣٢٢ / ٦٣٥)، و «تقريب التهذيب» (١/ ٣١٨ / ٣٥٥١).
٧١ - تمييز: أبو عمر، عبد الله بن كثير بن جعفر بن أبي كثير، الأنصاري الزرقي، مولاهم، المدني، من الحادية عشرة، قليل الحديث، كثير التخليط فيما يروي، لا يحتج به إلا فيما وافق الثقات، فهو مقبول صاحب معميات ليس بشيء.
روى عن: سعيد المقبري.
روى عنه: كثير بن عبد الله بن عمرو بن عوف المزني.
وانظر: «المجروحين» (٢/ ١٠ / ٥٣٥)، و «تهذيب الكمال» (١٥/ ٤٦١ / ٣٤٩٧)، و «الكاشف» (١/ ٥٨٧ / ٢٩٢٤)، و «تهذيب التهذيب» (٥/ ٣٢٠ / ٦٣٢)، و «تقريب التهذيب» (١/ ٣١٨ / ٣٥٤٨)، و «لسان الميزان» (٣/ ٣٢٨ / ١٣٦٣ و ١٣٦٤).
٧٢ - تمييز: أبو محمد، عبد الله بن كثير بن وقدان البغدادي.
روى عن: محمد بن سليمان (لوين).',NULL,'328',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (331,'روى عنه: الحسن بن أحمد بن محمد بن إبراهيم الأسترأباذي.
وانظر: تاريخ بغداد (١٠/ ٤٦ / ٥١٧٦).
١٨٠ - – عبد الله بن كثير العبدي (تخ ٢/ ٥٥٧).
عبد الله بن كثير، العبدي: من العاشرة، أو: الحادية عشرة، لم أعرفه، ولم أجد له ترجمة ولعله: الآملي، صاحب الترجمة السابقة.
روى عن: جعفر بن عون (تخ ٢/ ٥٥٧).
روى عنه: أبو جعفر الطبري (تخ ٢/ ٥٥٧).
١٨١ - عبد الله بن محمد الزهري (٩٢٢٩)
(مسلم ١٤ ر، والأربعة)
(طب: ٧ ر / ٢ ش)
(تس: ٥ ر / ٢ ش)، و (تخ: ١ ر / ١ ش)، و (تهـ: ١ ر / ١ ش)
عبد الله بن محمد بن عبد الرحمن بن المسور بن مخرمة، الزهري، البصري، توفي سنة ست وخمسين ومائتين، من صغار العاشرة، صدوق.
روى عن: سفيان [ابن عيينة] (تس ٩٢٢٩ و ٢٢٩٦٠ و ٢٨١٣٨ و ٣٣٣٤٨) و (تهـ ٢٦٦٣)، و (تخ ١/ ٣٧٢)، و (م، د، س، ق)، ومالك بن سعير بن الخمس (تس ٣١٠٤٤ و ٣١٠٧١)، (ت).
روى عنه: أبو حاتم محمد بن إدريس الرازي «الجرح والتعديل» (٥/ ١٦٣ / ٧٥٣)، ومحمد بن عمر بن يوسف «الثقات» (٨/ ٣٦٢ / ١٣٨٨٢).
التعديل والتجريح: قال ابن أبي حاتم: سئل أبي عنه فقال: «صدوق».
وقال النسائي: «ثقة».
وقال الدارقطني: «من الثقات، قليل الخطأ».',NULL,'329',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (332,'وذكره ابن حبان في «الثقات» وقال: «حدثنا عنه محمد بن عمر بن يوسف وغيره».
قلت: تصحف اسمه في الأثر رقم (تس ٢٢٩٦٠) إلى (عبيد الله) – مصغرًا -.
وأخرجه عن الطبري في «التاريخ» (١/ ٣٧٢) باسم (عبد الله) – مكبرًا – عن سفيان.
وانظر: «الجرح والتعديل» (٥/ ١٦٣ / ٧٥٣)، و «الثقات» (٨/ ٣٦٢ / ١٣٨٨٢)، و «تسمية من أخرجهم البخاري ومسلم»، (١/ ١٦٠ / ٩٢٠)، و «رجال مسلم» (١٨٧١/ ٣٤٩ / ٨٥٣) و «تهذيب الكمال» (١٦/ ٦٩ / ٣٥٤٠)، و «تهذيب التهذيب» (٦/ ١١ / ١٦)، و «تقريب التهذيب» (١/ ٣٢١ / ٣٥٨٩)، و «رجال تفسير الطبري» (٣٢٣/ ١٤٦٨).
١٨٢ - أبو العباس الغزي (٣٣٠١٢)
(أبو داود)
(طب: (تس: ر ٣/ ٢ ش)
أبو العباس، عبد الله بن محمد بن عمرو بن الجراح، الأزدي، الشامي، الغزي، الفلسطيني: من الحادية عشرة، ثقة.
روى عن: إبراهيم بن محمد بن يوسف الفريابي (تس ٣٣٠١٢)، ومحمد بن يوسف الفريابي (تس ٣٢٩٩٨ و ٣٤٩٦٥) وعبد الله بن يوسف «منتقي ابن الجارود» (١/ ٣٢ / ٨٤)، وعثمان بن سعيد القرشي «صحيح ابن حبان» (٦/ ٢٠٨ / ٢٤٥٥)، ويحيى بن بكير «صحيح ابن حبان» (٥/ ١٨٥ / ١٨٦٩).
روى عنه: أبو محمد عبد الله بن علي بن الجارود «منتقي ابن الجارود» (١/ ٣٢ / ٨٤).
التعديل والتجريح: قال عبد الرحمن بن أبي حاتم: «كتبت عنه، وهو ثقة».
وأغفله الشيخان أحمد ومحمود شاكر في تحقيقهما «لتفسير الطبري».
وانظر: تهذيب الكمال (١٦/ ٩٥ / ٣٥٤٧)، و «الكاشف» (١/ ٥٩٥ / ٢٩٦٥)، و «تهذيب التهذيب» (٦/ ١٦ / ٢٣)، و «تقريب التهذيب» (١/ ٣٢٢ / ٣٥٩٦).',NULL,'330',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (333,'١٨٣ - أبو سليمان الرازي (١٩٢٣٦)
(طب: ٢ ر / ١ ش)
(تس: ١ ر / ش)، و (تخ: ١ ر / ١ ش)
أبو سليمان، عبد الله بن محمد بن يحيى بن داهر، الرازي، المعروف بـ (الأحمري) - وقد ينسب إلى جده (داهر)، فيقال: عبد الله بن داهر بن يحيى بن داهر، الرازي - رافضي، خبيث، واهٍ ليس بشيء، وما يكتب حديثَه إنسانٌ فيه خير.
روى عن: إسحاق بن منصور السلولي (تهـ ٤٠٦٧)، حجاج بن نَصير (تهـ ٩٦٨)، عفان بن مسلم (تس ١٩٢٣٦)، و (تخ ١/ ٢٠٠)، وعبد الله بن عبد القدوس «المعجم الأوسط» (٤/ ١٩٨ / ٣٩٦٦)، وأبيه محمد بن داخر الرازي «ميزان الاعتدال» (٤/ ٩٢ / ٤٣٠٠) و (٤/ ١٨٥ / ٤٥٦٦).
روى عنه: أحمد بن زهير التستري «مسند الشاشي» (٢/ ١١٠ / ٦٣٥)، وأحمد بن أبي خيثمة «ميزان الاعتدال» (٤/ ٩٢ / ٤٣٠٠)، وأحمد بن يحيى الحلواني «المعجم الكبير» (١٠/ ٨٥ / ١٠٠٣١)، والحسين بن أحمد بن منصور البغدادي (سجادة) «المعجم الأوسط» (٤/ ١٠ / ٣٤٧٨)، والحسين بن عباس الرازي «المعجم الكبير» (١/ ١٤٨ / ٣٣٤)، وعلي بن سعيد الرازي «المعجم الأوسط» (٤/ ١٩٨ / ٣٩٦٦)، وعلي بن طاهر الرازي «الجرح والتعديل» (٦/ ١٩١ / ١٠٣٥) والفضل بن سهل «مسند البزار»: (٤/ ٣١٠ / ١٤٩١)، وموسى بن هارون «المعجم الكبير» (١٠/ ١٣٣ / ١٠٢١٤).
الجرح والتعديل: قال الحافظ الذهبي في «الميزان» (٤/ ٩٢ / ٤٣٠٠): «قال أحمد ويحيى: ليس بشيء. قال: وما يكتب حديثه إنسان فيه خير».
وقال العقيلي: «رافضي خبيث».
وقال ابن عدي: «عامة ما يرويه في فضائل علي، وهو فيه متهم في ذلك».
ووافقه الحافظ في «اللسان» (٣/ ٢٨٢ / ١١٩٠)، و (٣/ ٣٣٧ / ١٣٩١).',NULL,'331',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (334,'وانظر: «الكامل» (٤/ ٢٢٨ / ١٠٤٦) و «الكشف الحثيث» (١/ ١٥١ / ٣٨٤)، و «ضعفاء العقيلي» (٢/ ٢٥٠ / ٨٠٤) و «المغني في الضعفاء» (١/ ٣٣٧ / ٣١٥٦)، «ميزان الاعتدال» (٤/ ٩٢ / ٤٣٠٠)، و (٤/ ١٨٥ / ٤٥٦٦)، «لسان الميزان» (٣/ ٢٨٢ / ١١٩٠) و (٣/ ٣٣٧ / ١٣٩١)، و «رجال تفسير الطبري» (٣٢٣/ ١٤٦٥).
قلت: وقد احتار فيه الشيخ شاكر في تحقيقه «لتفسير الطبري» (١٢/ ١٩٢٢).
وقال: «لم أعرفه .. ولا أدري ما هذا؟».
وقد بحثت عمن يمكن أن يكون هو! فوجدت ثلاثة يمكن أن يكون كل واحد منهم المراد؛ ولكن هناك ما يرجح أحدهم دون الآخَرَيْن، وهو ما أثبتُّه عاليًا لعدة أسباب؛ أهمها: البلد، والطبقة، والتلاميذ والشيوخ. ولذلك لم أثبت من لم يرجح عندي! وأما الآخَرَيْن فأثبتهما تمييزًا، والله أعلم بالصواب.
ثم إنه لم ينفرد بهذا الحديث، لا هو، ولا قرينه الرازي الآخر (أحمد بن ثابت المعروف بـ (فرخويه)؛ بل تابعهما عليه خمسة آخرون عن (عفان) به، هم:
الأول: إبراهيم بن سعيد الجوهري عند ابن عدي في «الكامل» (٥/ ٣٨٥ / ١٥٤٩) به، وذكر ابن عدي الخلاف في رفعه ووقفه، ورجح رفعه، ونقل ذلك الحافظ الذهبي في «الميزان» (٥/ ١٠٣ / ٥٦٨٤).
والثاني: شيبان بن فروخ عند أبي يعلى في «المسند» (٦/ ١٠٧ / ٣٣٧٣) به.
والثالث: محمد بن إسحاق الصاغاني عند الحاكم في «المستدرك» (٢/ ٦٢٢ / ٤٠٨٢)، من طريق أبي العباس، محمد بن يعقوب. به.
والرابع، والخامس: محمد بن غالب بن حرب، وإسحاق بن الحسن بن ميمون، من طريق علي بن حمشاذ العدل عند الحاكم في «المستدرك» (٢/ ٦٢٢ / ٤٠٨٢) أيضًا جميعهم عن عفان بن مسلم به، وقال الحاكم: «صحيح على شرط مسلم ولم يخرجاه».
وأخرجه ابن أبي شيبة في «المصنف» (٤/ ٤٢ / ١٧٥٩٣) به، دون ذكر أم يوسف معه ثم أخرجه فيه (٦/ ٣٤٧ / ٣١٩٢٠)، بتمامه.
وأخرجه أحمد في «المسند» (٣/ ٢٨٦ / ١٤٠٨٢) به أيضًا.',NULL,'332',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (335,'وذكره الديلمي في «مسند الفردوس» (١/ ٤٠٤ / ١٦٢٩) معلقًا من حديث أنس نحوه.
٧٣ - تمييز: أبو سعيد، عبد الله بن محمد، الرازي، الحداد، الصوفي، ابن أخي أبي زرعة، توفي سنة ثلاثمائة وثلاثين.
روى عن: إبراهيم بن زهير الحلواني «شعب الإيمان» (٥/ ٣٦٩ / ٦٩٨٤) وإسحاق بن إبراهيم بن أبي حسان الأنماطي «شعب الإيمان» (١/ ٥٣٩ / ٥٦٨)، ومحمد بن نصر بن منصور الصائغ «شعب الإيمان» (٤/ ١٣١ / ٤٥٥٦)، ومحمد بن الفضل [الفراوي] «شعب الإيمان» (٦/ ١٤٨ / ٧٧٤٤).
روى عنه: أبو عبد الرحمن محمد بن الحسين السلمي (الصوفي) «شعب الإيمان» (١/ ٥٣٩ / ٥٦٨)، وأبو نصر بن قتادة «شعب الإيمان» (٥/ ٣٦٨ / ٦٩٨٤).
٧٤ - تمييز: أبو محمد عبد الله بن محمد الرازي، توفي سنة ثلاثمائة وعشرين.
روى عن: الحسن بن محمد الصباح «المستخرج» (١/ ١٩١ / ٣١٦)، ومحمد بن أيوب [البجلي] الرازي «المختارة» (٦/ ٣٤٧ / ٢٣٧٥)، ومسلم بن إبراهيم «ميزان الاعتدال» (٣/ ٢٠١ / ٣١٨٤)، وأبي حاتم الرازي «المستخرج» (٢/ ١٧٨ / ١٢٧٩)، وأبي زرعة الرازي «المستخرج» (٢/ ١٥٨ / ١٩٨٥٨)، وأبي العباس بن مسروق «حلية الأولياء» (١٠/ ٢١٣)، وأبي عون البزوري «حلية الأولياء» (٥/ ٥٢).
روى عنه: عبد الله بن محمد بن جعفر «المستخرج» (١/ ١٩١ / ٣١٦)، عثمان بن محمد العثماني «حلية الأولياء» (١٠/ ٢٥٣)، وأبو محمد بن حيان «المستخرج» (٢/ ١٧٨ / ١٢٧٩)، وأحمد بن محمد بن أحمد «حلية الأولياء» (٥/ ٥٢)، وأبو يعلى إسحاق بن محمد الصابوني [الواعظ] «سير أعلام النبلاء» (٧/ ٩٦)، وأبو سعد محمد بن أبي بكر الأديب «المختارة» (٦/ ٣٤٧ / ٢٣٧٥)، وأبو عمرو محمد بن يعقوب الخشاني «التدوين في أخبار قزوين» (٢/ ٥٤).
١٨٤ - أبو محمد الحنفي (٥٠٠٣)
(طب: ١٦ ر / ٣ ش)',NULL,'333',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (336,'(تس: ٧ ر / ٢ ش)، و (تهـ: ٩ ر / ٢ ش)
أبو محمد، عبد الله بن محمد بن يزيد، الحنفي، المروزي، ثم البغدادي: توفي سنة خمس وسبعين ومائتين، من الحادية عشرة، ثقة.
روى عن: عبد الله بن عثمان – الملقب بـ (عبدان) – (تس ٥٠٠٣ و ٦١٩٦ و ٦١٩٨ و ٩٦٤٨ و ٩٦٥٣ و ٣٤١٢٩)، و (تهـ ٤٦٦ و ٨٨٥)، وعبد الرزاق بن همام (تس ٩٦٥٢)، ويحيى بن محمد (تهـ ٥٦٤ و ٥٧٣ و ٥٧٤ و ٥٧٥ و ٥٧٨ و ٥٧٩ و ٥٨٠) وإسحاق بن موسى الأنصاري، وعبد الله بن معاوية الجمحي، وأبيه محمد بن يزيد الحنفي.
روى عنه: محمد بن مخلد، ومحمد بن العباس بن نجيح.
وكل اسم لم يعز إلى كتاب - وميزته بالأسود العريض- من معجم شيوخه وتلاميذه، فهو من ترجمته في «تاريخ بغداد».
التعديل والتجريح: قال الخطيب في «تاريخ بغداد» (١٠/ ٨٥ / ٥٢٠١): «كان ثقة».
قلت: وأخرج له الدارقطني في «سننه» (٣/ ٢٠٢ / ٣٥٩)، و (٤/ ٧ / ١٢) من طريق محمد بن مخلد.
وقد ذكره في «العلل أيضًا» (٧/ ٢١١ / ١٢٩٦).
وذكره ابن الجوزي في «العلل المتناهية أيضًا» (١/ ٢١٦ / ٣٤٢).
وانظر: «تاريخ بغداد» (١٠/ ٨٥ / ٥٢٠١)، و «رجال تفسير الطبري» (٣٢٥/ ١٤٧٥).
٧٥ - تمييز: عبد الله بن محمد بن يزيد، الحنفي، الكوفي، العطار متأخر الوفاة بالنسبة لصاحب الترجمة، من الحادية عشرة، وفيه، وفي أبيه جهالة كما قال الذهبي.
روى عن: أبيه أيضًا، ولكنه صغير بالنسبة لصاحب الترجمة، فأبوه من صغار العاشرة، وقد تأخرت وفاة أبيه إلى سنة ٢٩٥ وقيل ٢٧٦.
وانظر: «تهذيب الكمال» (٢٧/ ٣٧ / ٥٧٠٨)، و «ميزان الاعتدال» (٦/ ٣٧٢ / ٨٣٣٥)، و «لسان',NULL,'334',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (337,'الميزان» (٧/ ٣٧٩ / ٤٧٨٣)، و «تهذيب التهذيب» (٩/ ٤٦٧ / ٨٧٠)، و «تقريب التهذيب» (١/ ٥١٤ / ٦٤٠٧) فبين بلده ولقبه فقال: «الكوفي العطار».
١٨٥ - عبد الله الرازي (تق: ٧٤٠)
(طب: تق ١ ر / ١ ش)
عبد الله بن محمد، الهلالي، الرازي: من الحادية عشرة، لم أعرفه، ولم أجد له ترجمة، ولعله مقلوب من: محمد بن عبد الله بن عبيد بن عَقيل، الهلالي، أبي مسعود، البصري (١٢٧٩)، وسيأتي في موضعه من كتابي هذا.
روى عن: يعقوب بن محمد [الزهري] (تق ٧٤٠).
روى عنه: أبو جعفر محمد بن جرير الطبري.
قلت: لم أقف له على ترجمة ولا ذكر في كتب الجرح والتعديل والرجال والحديث والآثار التي بين يدي، وسكت عنه الشيخ علي رضا في تعليقه على «الجزء المفقود من تهذيب الآثار».
والذي روى عن (يعقوب بن محمد الزهري) في ترجمته من «تهذيب الكمال» (٣٢/ ٣٦٧ / ٧١٠٥) هو (أبو أمية عبد الله بن محمد بن خلاد الواسطي) وليس من الواسطيين من نسبته (الهلالي) من هذه الطبقة إلا (أبو محمد، القاسم بن عطاء المقدمي الهلالي الواسطي كما هو في «التاريخ الصغير» (٢/ ٢٨٥ / ٢٦١٩).
وشيخه هنا هو: أبو يوسف، يعقوب بن محمد بن عيسى، الزهري، المدني، نزيل بغداد، صدوق، كثير الوهم، والرواية عن الضعفاء، من كبار العاشرة، مات سنة ثلاث عشرة ومائتين، وقد ترجمت له في «المعجمين الكبير، والصغير».
١٨٦ - عبد الله بن وضاح (تهـ ١٣٩٥)
(طب: (تهـ: ١ ر / ١ ش)
عبد الله بن وضاح بن سعيد الأودي، الكوفي، اللؤلؤي: من التاسعة، ترجم له ابن',NULL,'335',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (338,'أبي حاتم وسكت عنه، ولم يذكر فيه جرحًا ولا تعديلاً.
روى عن: عبد الله بن إدريس (تهـ ١٣٩٥)، وزياد بن عبد الله البكائي «الجرح والتعديل» (٥/ ١٩٢ / ٨٨٨)، وعبادة بن كليب «التدوين في أخبار قزوين» (٣/ ٥٨)، وأبي مالك عمرو بن هاشم الجنبي «الكامل» (٥/ ١٤٢ / ١٣٠٥)، ويحيى بن اليمان «مسند البزار» (٧/ ٢٩٩ / ٢٨٩٥).
روى عنه: أبو بكر أحمد بن عمرو البزار «مسند البزار» (٧/ ٢٩٩ / ٢٨٩٥)، وأبو القاسم جعفر بن أحمد بن عمران الشيباني «التدوين في أخبار قزوين» (٣/ ٥٨)، والقاسم بن زكريا «الكامل» (٧/ ٢٣٦ / ٢١٣٧)، وأبو حاتم محمد بن إدريس الرازي «الجرح والتعديل» (٥/ ١٩٢ / ٨٨٨)، وموسى بن إسحاق الأنصاري «الجرح والتعديل» (٥/ ١٩٢ / ٨٨٨)، ويحيى بن محمد بن صاعد «الكامل» (٥/ ١٤٢ / ١٣٠٥)، وابن مساكن الزنجاني «التدوين في أخبار قزوين» (٣/ ٥٨).
التعديل والتجريح: قال عبد الرحمن بن أبي حاتم في «الجرح والتعديل» (٥/ ١٩٢ / ٨٨٨): «سمع منه أبي بالكوفة في الرحلة الثالثة». وسكت عنه.
قلت: ولم أقف له على ترجمة أو ذكر في كتب الرجال والحديث، إلا ما سبق وأثبتُّ من خلاله معجم شيوخه وتلاميذه السابق.
١٨٧ - أبو قلابة الرقاشي (٣٣٨٠)
(ابن ماجه)
(طب: ٨ ر / ش)
(تس: ٥ ر / ٥ ش)، و (تهـ: ٣ ر / ٣ ش)
أبو محمد؛ ولقبه (أبو قلابة) عبد الملك بن محمد بن عبد الله بن محمد بن عبد الملك الرقاشي، البصري، ثم البغدادي، الضرير، مولده سنة تسعين ومائة، وتوفي سنة ست وسبعين ومائتين، وله ست وثمانون سنة، من الحادية عشرة، صدوق، يخطئ، تغير حفظه لما سكن بغداد.',NULL,'336',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (339,'روى عن: بشر بن عمر الزهراني (تس ٣٣٨٠)، وحجاج بن المنهال (تس ٥٦٢٦)، وعبد الصمد بن عبد الوارث (تس ٤٣٣٤)، و (تهـ ١٢٨٨)، وعمرو بن حكام (تهـ ٣٧٠)، ومسلم بن إبراهيم (تس ١٧٧٣٥)، وأبي ربيعة – غير مسمى ولا منسوب – (تس ٥٦٢٦)، وأبي زيد؛ صاحب الهروي (تهـ ٢٦٧٤).
روى عنه: أبو بكر أحمد بن سلمان بن الحسن الفقيه «سنن البيهقي» (١/ ١٠٢ / ٥٠١)، وأبوسعيد الهيثم بن كليب الشاشي «مسند الشاشي» (١/ ١٧١ / ١١٤)، وأبو بكر البزار «مسند البزار» (٨/ ٢٥٧ / ٣٣٢٢)، وأبو عوانة الإسفراييني «مسند أبي عوانة» (٤/ ٨ / ٥٨٤٠).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: يحفظ أكثر حديثه، وكان مذكورًا بالصلاح والخير، وكان سمح الوجه، ويقال أنه حدث من حفظه ستين ألف حديث، وأنه رجل صدوق أمين مأمون، كثير الخطأ في الأسانيد والمتون، كان يحدِّث من حفظه؛ فكثرت الأوهام منه وكان زاهدًا، قوي الذكاء، متقنًا، ثقةً؛ يحفظ حديث شعبة كما يحفظ السورة! ولا يحتج بما ينفرد به.
وانظر: «الجرح والتعديل» (٥/ ٣٦٩ / ١٧٣٠)، و «الثقات» (٨/ ٣٩١ / ١٤٠٣٧) و «تاريخ بغداد» (١٠/ ٤٢٥ / ٥٥٨٤)، و «مولد العلماء ووفياتهم» (٢/ ٥٩٧)، و «تهذيب الكمال» (١٨/ ٤٠١ / ٣٥٥٦)، و «تذكرة الحفاظ» (٢/ ٥٨٠ / ٦٠٤)، و «المقتنى» (٢/ ٢٦ / ٥١٥٥)، و «من تُكُلِّمَ فيه» (١/ ١٢٧ / ٢٢٥)، و «طبقات المحدثين» (١/ ٩٨ / ١١٠٥)، و «الكاشف» (١/ ٦٦٩ / ٣٤٧٨)، و «ميزان الاعتدال» (٤/ ٤١٠ / ٥٢٥٠)، و «لسان الميزان» (٧/ ٢٩٣ / ٣٨٨٦)، و «نزهة الألباب» (٢/ ٢٧٠ / ٣٠٦٩)، و «تهذيب التهذيب» (٦/ ٣٧١ / ٧٧٨، و ٢/ ٢٢٨ / ٩٦٢)، و «تقريب التهذيب» (١/ ٣٦٥ / ٤٢١٠)، و «من رُمِيَ بالاختلاط» (١/ ٦٢)، و «الكواكب النيرات» (٥٨/ ٣٧)، و «المقصد الأرشد» (٢/ ١٧٨ / ٦٦٤)، و «طبقات الحفاظ» (١/ ٢٦١ / ٥٨٣)، و «رجال تفسير الطبري» (٣٦٦/ ١٦٨٤).
١٨٨ - أبو عبيدة العنبري (٢٣٤٦)
(مسلم: ١٧ ر، والترمذي، والنسائي، وابن ماجه)
(طب: ٢٣ ر / ٢ ش).',NULL,'337',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (340,'(تس: ٢٠ ر / ٢ ش)، و (تهـ: ١ ر / ١ ش)، و (تخ: ٢ ر / ١ ش)
أبو عبيدة، عبد الوارث بن عبد الصمد بن عبد الوارث بن سعيد، العنبري التنوري البصري: توفي سنة اثنتين وخمسين ومائتين، من الحادية عشرة، صدوق.
روى عن: الحسين المعلم (تس ٢٣٤٦)، وأبيه عبد الصمد بن عبد الوارث بن سعيد (تس ٣٨٣٥ و ٨٣٢٩ و ١٣٨٧٠ و ١٣٩١٨ و ١٥٨٣٤ و ١٦٠٩٧ و ١٦١٨٦ و ١٦٥٨٧ و ١٦٧٤٣ و ١٧٠٨٩ و ٢١٦٨٨ و ٢٢٣٧٧ و ٢٥٨٣٧ و ٢٥٨٥١ و ٢٦٣١٩ و ٢٧٠٣٧ و ٢١٢٢٧ و ٣٢٧٩٦ و ٣٣٧٢٣)، و (تهـ ٧٦٦)، و (تخ ١/ ٥٦٤ و ١/ ٥٦٨)، و (م، ت، س، ق).
روى عنه: عبد الله بن محمد بن ناجية «سنن البيهقي» (٤/ ٤٠ / ٦٧٥٤)، ومحمد بن صالح بن الوليد النرسي «المعجم الكبير» (٨/ ٢٨٠ / ٨٠٧٥)، ومحمد بن محمويه الجوهري «المعجم الأوسط» (٧/ ١٧٥ / ٧٢٠٠).
التعديل والتجريح: قال ابن أبي حاتم: «سئل أبي عنه فقال: صدوق».
وقال النسائي: «لا بأس به». وذكره ابن حبان في كتاب «الثقات».
قلت: ولا شك عندي، أن هذا الأثر قد وقع فيه سقط بين عبد الوارث والحسين؛ لأن سائر الآثار التي في «التفسير»، و «التهذيب»، و «التاريخ» من روايته إنما هي عن أبيه، عن غيره، وقد تكررت روايته عن أبيه، عن حسين المعلم في «التفسير» بنحو هذا الإسناد ست مرات كما هي في الآثار ذوات الأرقام التالية: (١٧٠٨٩ و ٢١٦٨٨ و ٢٢٣٧٧ و ٢٦٣١٩ و ٣١٢٢٧ و ٣٢٧٩٦).
وقد وقع في إسنادي الأثرين رقم (١٣٨٧٠ و ١٣٩١٨) زيادات لا يمكن أن تكون صحيحة بحال أيضًا، خاصة وأنه تستحيل رواية عبد الوارث هذا، عن الحسين المعلم؛ لأن ما بين وفاتيهما نحو سبع وتسعين سنة، ولم يذكره الذهبي في رسالته «أسماء من عاش ثمانين سنة بعد شيخه».
وقد تصحف اسم حسين المعلم في بعض الآثار التي أشرنا إليها سابقًا من (الحسين) إلى (الحسن).',NULL,'338',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (341,'وانظر: «الجرح والتعديل» (٦/ ٧٦ / ٣٨٩)، و «الثقات» (٨/ ٤١٦ / ١٤١٧٠)، و «ذكر أسماء التابعين» (٢/ ١٦٣ / ٧٧٩)، و «تسمية من أخرجهم البخاري .. » (١/ ١٧٧ / ١٠٩٠)، و «الكاشف» (١/ ٦٧٣ / ٣٥١١)، و «المقتنى» (١/ ٣٨٤ / ٤٠٠٦)، و «تهذيب الكمال» (١٨/ ٤٨٤ / ٣٥٩٦)، و «تهذيب التهذيب» (٦/ ٣٩٣ / ٨٢٧)، و «تقريب التهذيب» (١/ ٣٦٧ / ٤٢٥٢)، و «رجال تفسير الطبري» (٣٦٩/ ١٧٠٣).
١٨٩ - أبو الحسن الوارق (تهـ ٢٤٥٦)
(أبو داود، والترمذي، والنسائي).
(طب: تهـ: ١ ر / ١ ش)
أبو الحسن، عبد الوهاب بن عبد الحكم – ويقال: ابن الحكم – ابن نافع، الوراق، البغدادي، نسائي الأصل، صاحب أحمد بن حنبل، وخاصته: توفي سنة خمسين ومائتين- وقيل بعدها – من الحادية عشرة، ثقة.
روى عن: يحيى بن سليم الطائفي (تهـ ٢٤٥٦)، (ت).
روى عنه: أبو جعفر الطبري (تهـ ٢٤٥٦).
التعديل والتجريح: اتفقت أقوال أئمة الجرح والتعديل فيه على كونه: ثقةً صالحًا، ورعًا، زاهدًا، وقد أثنى عليه الإمام أحمد كثيرًا.
وانظر: «الجرح والتعديل» (٦/ ٧٤ / ٣٨٣)، و «الثقات» (٨/ ٤١١ / ١٤١٤٣)، و «تاريخ بغداد» (١١/ ٢٥ / ٥٦٩٣) و «تهذيب الكمال» (١٨/ ٤٩٧ / ٣٦٠٢)، و «الكاشف» (١/ ٦٧٤ / ٣٥١٧) و «تهذيب التهذيب» (٦/ ٣٩٦ / ٨٣٥، و ٢/ ٢٢٨ / ٩٦٢) و «تقريب التهذيب» (١/ ٣٦٨ / ٤٢٥٩).
١٩٠ - أبو سهل الصفار (٢٩٧٣)
(البخاري، والأربعة)
(طب: ٧ ر / ٥ ش)
(تس ٢ ر / ٢ ش) و (تخ ١ ر / ١ ش) و (تهـ ٢ ر / ٢ ش) و (تق ٢ ر / ١ ش)',NULL,'339',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (342,'أبو سهل، عبدة بن عبد الله، الصفار، الخزاعي: البصري، الكوفي الأصل: توفي سنة ثمان وخمسين ومائتين، من الحادية عشرة، ثقة.
روى عن: إسماعيل بن زياد الكاتب (تس ٢٩٧٣)، وخالد بن عبد الرحمن المخزومي (تخ ١/ ١٨٩)، وعباد بن ليث (تهـ ٢٠٣٩)، ومحمد بن بشر العبدي (تس ٩٢٩٦)، و (تق ١ و ٣٢٧)، و (ت)، يحيى بن إسحاق (تهـ ١٨٦٤).
روى عنه: إبراهيم بن محمد بن إبراهيم الصيرفي «سنن الدارقطني» (٣/ ١٤٠ / ١٧٦)، ومحمد بن صالح بن جميل «سنن البيهقي» (١/ ١٩٧ / ٩٠١)، ومحمد بن يعقوب «المعجم الأوسط» (٧/ ٣٦٧ / ٧٧٤٧).
التعديل والتجريح: قال أبو حاتم الرازي: «صدوق».
قال النسائي: «ثقة».
وقال الحاكم عن الدارقطني: «ثقة».
وذكره ابن حبان في الثقات وقال: «مستقيم الحديث».
وانظر: «الجرح والتعديل» (٦/ ٩٠ / ٤٦٢)، و «الثقات» (٨/ ٤٣٧ / ١٤٢٩٣)، و «التعديل والتجريح» (٢/ ٩٣٠ / ١٠١٧)، و «الكاشف» (١/ ٦٧٧ / ٣٥٢٨)، و «المقتنى» (١/ ٢٩٨ / ٢٩٥٤) و «تهذيب الكمال» (١٨/ ٥٣٧ / ٣٦١٦)، و «تهذيب التهذيب» (٦/ ٤٠٦ / ٨٥٢)، و «تقريب التهذيب» (١/ ٣٦٩ / ٤٢٧٢)، و «رجال تفسير الطبري» (٣٦٨/ ١٦٩٥).
١٩١ - أبو الفضل الزهري (٤٣١٦)
(البخاري: ٦ ر، وأبو داود، والترمذي، والنسائي)
(طب: ١٦ ر / ٢ ش)
(تس ٢ ر / ١ ش) و (تخ ٦ ر / ١ ش) و (تهـ ٦ و / ٢ ش) و (تق ٢ ر / ١ ش)
أبو الفضل، عبيد الله بن سعد بن إبراهيم بن سعد بن إبراهيم بن عبد الرحمن بن عوف، الزهري، البغدادي، قاضي أصبهان، ونزيل سامراء: مولده سنة خمس وثمانين ومائة',NULL,'340',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (343,'، وتوفي سنة ستين ومائتين، من الحادية عشرة، ثقة.
روى عن: عمه يعقوب بن إبراهيم بن سعد (تس ٤٣١٦ و ٤٣٢٣)، و (تهـ ١١٠١ و ١١٠٢ و ٢٣٩٣ و ٢٤٤٣ و ٢٩٤٩)، و (تق ٧٢ و ٣٦٤)، و (تخ ٢/ ٢٢٤، و ٢/ ٢٤٧، و ٢/ ٣٠٧ و ٢/ ٣١٨)، و (خ، د، ت، س)، ويونس بن محمد المؤدب (تهـ: ٩٦٤)، و (سي).
روى عنه: أبو القاسم البغوي «تاريخ بغداد» (١٠/ ٣٢٣ / ٥٤٦٦).
التعديل والتجريح: قال ابن أبي حاتم: «كتبت عنه مع أبي، وهو صدوق».
وقال النسائي: «لا بأس به».
وقال أبو بكر الخطيب: «كان ثقة».
وقال الحافظ في «التهذيب»: «وثقه الدارقطني».
وانظر: «طبقات المحدثين بأصبهان» (٣/ ٥٤ / ٢٥١)، و «الجرح والتعديل» (٥/ ٣١٧ / ١٥٠٩)، وتاريخ بغداد (١٠/ ٣٢٣ / ٥٤٦٦) و «تسمية من أخرجهم البخاري ومسلم» (١/ ١٧٢ / ١٠٤٣)، و «الإرشاد» (١/ ٣٠٥)، و «التعديل والتجريح» (٢/ ٨٩٣ / ٩٤٣)، و «تهذيب الكمال» (١٩/ ٤٦ / ٣٦٣٧)، و «الكاشف» (١/ ٦٨٠ / ٣٥٤٨)، و «المقتنى» (٢/ ١٦ / ٥٠٤٩)، و «تهذيب التهذيب» (٧/ ١٥ / ٢٩)، و «تقريب التهذيب» (١/ ٣٧١ / ٤٢٩٤)، و «المقصد الأرشد» (٢/ ٦٨ / ٥٥٤) و «رجال تفسير الطبري» (٣٧٨/ ١٧٣٨).
١٩٢ - أبو الحسن المقدسي (١٧)
(طب: ١٣ ر / ٥ ش)
(تس: ٧ ر / ٥ ش)، و (تهـ: ٥ ر / ٢ ش)، و (صر: ١ ر / ١ ش)
أبو الحسن، عبيد الله بن محمد بن هارون، الفريابي، المقدسي، سكت عنه ابن أبي حاتم، وقال ابن حبان: «مستقيم الحديث». ووثقه الشيخ شاكر!!، وكلاهما معروف بالتساهل في التوثيق، كما نبه على ذلك كثيرًا جدًّا شيخنا الألباني - رحمه الله تعالى -.
روى عن: سفيان بن عيينة (تس ٩٢٢٨ و ٢٦٥٠٥)، وضمرة بن ربيعة (تس ١٣٥٤ و ٢٧٩١٦)، و (تهـ ٢٥٣٥)، وعبد الله بن ميمون [القداح] (تس ١٧)، و (تهـ ٦٦٥ و ٩٧١ و ١٣٣٥ و ٢٧٧٤)',NULL,'341',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (344,'و (صر ٢٠)، وعبد المجيد [ابن أبي رواد] (تس ١٥٩٠٩)، وعمرو بن أبي سلمة التنيسي (تس ٣٠٦٩٦)، ومحمد بن عبد الله بن المبارك القرشي (تهذيب التهذيب) (٩/ ٢٤٣ / ٤٥٤).
روى عنه: أحمد بن سيار الرمادي «الجرح والتعديل» (٥/ ٣٣٥ / ١٥٨٥)، وعبد الله بن محمد الدينوري «معجم البلدان» (٢/ ٥٤٥)، وعلي بن سراج المصري «الحلية» (٣/ ٢٠٢)، ومحمد بن إدريس الرازي «الجرح والتعديل» (٥/ ٣٣٥ / ١٥٨٥)، ويعقوب بن إسحاق العسقلاني «لسان الميزان» (٦/ ٣٠٤ / ١٠٩٠).
التعديل والتجريح: ذكره ابن حبان في «الثقات» (٨/ ٤٠٦ / ١٤١١٩) وقال: «مستقيم الحديث».
وذكره الشيخ شاكر في تحقيقه «لتفسير الطبري» (١/ ٢٧ / ١٧) وقال: «شيخ الطبري، فالظاهر أنه ثقة، ولكني لم أجد له ترجمة إلا في الجرح والتعديل».
قلت: وهو في «الجرح والتعديل» (٥/ ٣٣٥ / ١٥٨٥).
١٩٣ - أبو عمرو العنبري (٣٣٢٩)
(البخاري: ٧ ر، ومسلم: ١٦٧ ر، وأبو داود، والنسائي)
(طب: (تس: ١ ر / ١ ش)
أبو عمرو، عبيد الله بن معاذ بن معاذ بن نصر بن حسان، العنبري، البصري: توفي سنة سبع وثلاثين ومائتين، من العاشرة، ثقة، حافظ، رجح ابن معين أخاه المثنى عليه.
روى عن: أبيه معاذ بن معاذ العنبري (تس ٣٣٢٩).
روى عنه: أبو جعفر محمد بن جرير الطبري.
قلت: ومن الممكن - وإن كان الاحتمال ضعيفًا جدًّا - أن يكون (عبيد الله بن معاذ) شيخًا للطبري أيضًا، رغم أنه لم يمض من عمر الطبري يوم مات (عبيد الله بن معاذ) سنة سبع وثلاثين ومائتين إلا نحو ثلاث عشرة سنة، وكانت بداية رحلة (الطبري) من بلده',NULL,'342',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (345,'(آمل طبرستان) في طلب العلم سنة ست وثلاثين ومائتين، كما بينت ذلك في ترجمته، ولكن لم تكن روايته عنه في البصرة على أية حال، لأنه لم يدخل البصرة إلا بعد بغداد، وقد دخل بغداد بعد وفاة الإمام أحمد بن حنبل - رحمه الله تعالى - سنة إحدى وأربعين ومائتين، والذي ما رحل الطبري إلى بغداد إلا للسماع منه في الدرجة الأولى، ولكنه فاته بالموت، ثم انحدر إلى البصرة بعد ذلك - أي بعد وفاة عبيد الله بن معاذ بسنين - فإن كانت له عنه رواية متصلة ففي غير البصرة، وبغداد، وهذا يقودنا للبحث عن رحلة لـ (عبيد الله بن معاذ) إلى (الري)، أو (آمل) قبل ذلك أم لا؟ وإلا فروايته عنه لا بد وأن تكون وجادة لا غير.
والحقيقة أن بينهما واسطة، تمثلت في عدة شيوخ روى عنهم (الطبري) عن (عبيد الله)، عن (أبيه - معاذ -) كما أثبتُّ ذلك في ترجمة (عبيد الله) في «المعجم الكبير في رجال الطبري ابن جرير» أيضًا، والترجمة الخاصة بـ (أحمد بن أبي عمران) من كتابي هذا.
١٩٤ - أبو حفص الجبيري (١٠٩)
(ابن ماجه)
(طب: ١ ر / ٨١)
(تس: ٩ ر / ٣ ش)، و (تهـ: ٤ ر / ٣ ش)، و (تق ٢ ر / ٢ ش)
أبو حفص، عبيد الله بن يوسف، الجبيري، الثقفي، البصري: توفي في حدود سنة خمسين ومائتين، أو بعد ذلك بيسير، من الحادية عشرة، صدوق.
روى عن: حماد بن مسعد [ة]، و (تق (٣٤٦)، وسعيد بن الفضل (تهـ ٧٨٣ و ٨٠٣)، وعبد الله بن داود الخريبي (تهـ ٤٠٤)، وأبي بكر، عبد الكبير بن عبد المجيد بن عبيد الله بن شريك الحنفي (تس ١٠٩)، وعثمان بن عمر، و (تق ٣٤٥)، وعمر بن حفص (تهـ ١٩٩٤)، ومحمد بن كثير السلمي (تس ٢١١٥٣ و ٢٩٢٨ و ٣٢٩٣٣ و ٣٢٩٤٤ و ٣٧٨٢٧)، ومؤمل بن إسماعيل القرشي (تس ٨٣٥٦ و ١٢٨٨٦ و ١٢٩٠١)، ومحمد بن إبراهيم بن أبي عدي «الحلية» (٤/ ٨٢)',NULL,'343',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (346,'ومعدي بن سليمان «ميزان الاعتدال» (٦/ ٤٦٦ / ٨٦٥٨)، وأبي زياد الطحان «الحلية» (٣/ ١٣)، وأبي عاصم «الثقات» (٨/ ٤٢٨ / ١٤٢٤٠).
روى عنه: ابنه أحمد بن عبيد الله بن يوسف الجبيري «الثقات» (٨/ ٤٢٨ / ١٤٢٤٠)، وعبد الرحمن بن أبي حاتم الرازي «المؤتلف والمختلف» (١/ ٤٥).
التعديل والتجريح: ذكره ابن حبان في «الثقات» (٨/ ٤٢٨ / ١٤٢٤٠)
وقال العسقلاني في «التقريب» (١/ ٣٧٥ / ٤٣٥٤): «صدوق».
وانظر: «تهذيب الكمال» (١٩/ ١٧٩ / ٣٦٩٨)، والمقتنى (١/ ١٩٤ / ١٦٩٤)، و «الكاشف» (١/ ٦٨٨ / ٣٦٠٢)، و «تهذيب التهذيب» (٧/ ٥٢ / ١١٠) و «رجال تفسير الطبري» (٣٨١/ ١٧٥٥).
١٩٥ - أبو محمد العسقلاني (٢٠٧٧)
(النسائي في اليوم والليلة)
(طب: ٧ ر / ١ ش)
(تس: ٦ ر / ١ ش)، و (تخ: ١ ر / ١ ش)
أبو محمد، عبيد بن آدم بن أبي إياس العسقلاني: توفي سنة ثمان وخمسين ومائتين من الحادية عشرة، صدوق.
روى عن: أبيه آدم بن أبي إياس (تس ٢٠٧٧ و ٤٢٢٤ و ٤٩٠٣ و ٢٨٠٣٧ و ٣١٠٤٠ و ٣٤٥٦١ و ٣٤٥٤٨)، و (تخ: ١/ ٢٨)، و (سي).
روى عنه: إبراهيم بن أحمد العسكري «ميزان الاعتدال» (٨/ ١١ / ٥)، ومحمد بن أحمد بن هارون الكوفي «أخبار قزوين» (٣/ ٤٩١).
التعديل والتجريح: اتفقت كلمة أئمة الجرح والتعديل على كونه: صدوقًا.
وانظر: «الجرح والتعديل» (٥/ ٤٠٢ / ١٨٦٢)، و «مولد العلماء ووفياتهم» (٢/ ٥٦٩)، و «تهذيب الكمال» (١٩/ ١٨٣ / ٣٧٠١)، و «تهذيب التهذيب» (٧/ ٥٣ / ١١٦)، و «تقريب التهذيب» (١/ ٣٧٦ / ٤٣٥٧)، و «رجال تفسير الطبري» (٣٧٠/ ١٧٠٨).',NULL,'344',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (347,'وقد شاركه في اسمه واسم أبيه تابعي قديم هو:
٧٦ - تمييز: عبيد بن آدم، تابعي، ثقة.
روى عن: عمر، وأبي هريرة.
روى عنه: أبو سنان القسملي.
وانظر: «التاريخ الكبير» (٥/ ٤٤١ / ١٤٣٤)، و «الجرح والتعديل» (٥/ ٤٠١ / ١٨٥٧)، و «الثقات» (٥/ ١٣٤ / ٤٢١٧)، و «الإكمال» (١/ ٢٨٤ / ٥٧٨)، و «تعجيل المنفعة» (١/ ٢٧٦ / ٧٠٠).
١٩٦ - عبيد بن أسباط (٨)
(البخاري في جزء القراءة، والترمذي، وابن ماجه)
(طب: ١٢ ر / ٣ ش)
(تس: ١١ ر / ٢ ش)، و (تهـ: ١ ر / ١ ش)، و (تق ١ ر / ١ ش)
أبو محمد، عبيد بن أسباط بن محمد بن عبد الرحمن بن خالد بن ميسرة، الهمداني القرشي، مولاهم، الكوفي: توفي سنة خمسين ومائتين، من الحادية عشرة، صدوق.
روى عن: أبيه، أسباط بن محمد (تس ٨ و ١٥١٣ و ٧٦١٥ و ٢٢٥٩٤ و ٢٣٧٣٤ و ٢٩١٨٩ و ٣١٦٥١٥ و ٣٤٥٦٣ و ٣٦٣٦٠ و ٣٧٩٣٨)، و (تق ٨٠٦)، (ر، ت، ق)، وعبد الله بن إدريس (تهـ ٢٧٠٠)، (ت)، ويحيى بن يمان (تس ٣٥٢٥٥).
روى عنه: إبراهيم بن عبد الصمد «تذكرة الحفاظ» (٤/ ١٢٧٢ / ١٠٧٢)، وأحمد بن الحسين الصوفي «الكامل» (٣/ ٤١٣ / ٨٤١)، وإسحاق بن إبراهيم بن يونس «الكامل» (٣/ ٣٢١ / ٧٧٨)، وجبارة بن المغلس الحماني «تهذيب الكمال» (٤/ ٤٨٩ / ٨٩١)، وذريح - غير مكني، ولا منسوب - «الكامل» (٣/ ٣٢١ / ٧٧٨)، وزكريا بن يحيى الساجي «تهذيب الكمال» (٨/ ١٢٨ / ١٦٣٣)، ومحمد بن إسماعيل البخاري في «جزء القراءة» و «التاريخ الكبير» (٣/ ١٦٩ / ٥٧٧)، ومحمد بن الحسين بن حفص «الكامل» (٣/ ٣٢١ / ٧٧٨).',NULL,'345',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (348,'التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، صدوقًا.
وأغفله الشيخان أحمد ومحمود شاكر في تحقيقهما «لتفسير الطبري».
وانظر: «التاريخ الكبير» (٥/ ٤٤٢ / ١٤٤٠)، و «التاريخ الصغير» (٢/ ٣٩٠ / ٢٩٧٩)، و «الجرح والتعديل» (٥/ ٤٠٢ / ١٨٦٠)، و «الثقات» (٨/ ٤٣٢ / ١٤٢٦٤)، و «تهذيب الكمال» (١٩/ ١٨٥ / ٣٧٠٢)، و «الكاشف» (١/ ٦٨٨ / ٣٦٠٤)، و «تهذيب التهذيب» (٧/ ٥٤ / ١١٨)، و «تقريب التهذيب» (١/ ٣٧٦ / ٤٣٥٨).
١٩٧ - أبو محمد الهباري (٢٨٩٧)
(البخاري)
(طب: ٣٨ ر / ٧ ش)
(تس: ٢٢ ر / ٧ ش)، و (تهـ: ١٢ ر / ٤ ش)، و (تق ٤ ر / ٣ ش)
أبو محمد، عبيد - ويقال: عبيد الله - ابن إسماعيل القرشي، الهباري، الكوفي، توفي سنة خمسين ومائتين، من العاشرة، ثقة.
روى عن: سفيان بن عيينة (تس ٢٨٥٩٥ و ٣٣٩٣٣ و ٣٥٦١٦ و ٣٥٦١٧)، و (تهـ ٦٦٨)، و تق (٧٩٧)، وعبد الله بن إدريس (تس ٢٨٩٧ و ١٠٣١٥)، و (تهـ ٦٥٥ و ٢٠٧٨)، وعبد الله بن نمير (تس ٣١٩١ و ٣٢٥٩ و ٣٣٣٠ و ٣٣٧٧ و ٣٤٢٠ و ٣٤٢٧ و ٣٤٤٩)، و (تق ٣٨٨)، وعبد الرحمن بن محمد المحاربي (تس ٢٣٣٦٨ و ٢٤٨١٢ و ٣٠٧٧٧ و ٣٦٩٣٠)، و (تهـ ١٧٣ و ٩٥١ و ٩٧٢ و ٢٥٣٣ و ٢٧٨٦ و ٢٧٩٠) و (تق ٢٦٦ و ٢٣٦)، وعثام بن علي (تس ٣٣١٩١ و ٢٨٠٦٠ و ٣٣١٩١)، وأبي معاوية محمد بن خازم الضرير (تس ٤٨٩٢)، ومحمد بن فضيل (تس ٢٤٠٦٧ و ٢٥٧١٥) و (تهـ ٢٧٩ و ٢٨١ و ١٨٠٥).
روى عنه: أبو بكر البزار «مسند البزار» (٤/ ٨٧ / ١٢٥٧).
التعديل والتجريح: اتفقت أقوال أئمة الجرح والتعديل على كونه: ثقةً.
وانظر: «التاريخ الكبير» (٥/ ٤٤٢ / ١٤٤١)، و «الثقات» (٨/ ٤٣٣ / ١٤٢٦٧)، و «ذكر أسماء',NULL,'346',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (349,'التابعين» (١/ ٢٥٩ / ٧٥٤)، و «من روى عنهم البخاري في الصحيح»: (١/ ١٦٧ / ١٧٣)، و «رجال صحيح البخاري» (٢/ ٤٩٩ / ٧٦٨)، و «التعديل والتجريح» (٢/ ٩٢٤ / ١٠٠٣)، و «تهذيب الكمال» (١٩/ ١٨٦ / ٣٧٠٣)، و «الكاشف» (١/ ٦٨٨ / ٣٦٠٥)، و «المقتنى» (٢/ ٥٤ / ٥٤٨٠)، و «تهذيب التهذيب» (٧/ ٥٤ / ١١٩)، و «تقريب التهذيب» (١/ ٣٧٦ / ٤٣٥٩)، و «رجال تفسير الطبري» (٣٧٠/ ١٧٠٩).
١٩٨ - أبو محمد الوراق (١٧٤٤٨)
(طب: ٤ ر / ٣ ش)
(تس: ٢ ر / ٢ ش)، و (تخ: ٢ ر / ١ ش)
أبو محمد، عبيد بن محمد بن القاسم، الوراق، النيسابوري، البغدادي: توفي سنة خمس وخمسين ومائتين، من العاشرة، وثقة ابن حبان، وأبو بكر الخطيب.
روى عن: أبي أسامة حماد بن أسامة (تس ١٧٤٤٨)، وروح بن عبادة (تخ ١/ ٥٢٧ و / ٥٧٣)، ويزيد بن هارون (تس ٢٦٣٧٢)، وبشر بن الحارث والحسن بن موسى الأشيب، وعبد الله بن يونس التنيسي، وعلي بن عاصم «الثقات» (٨/ ٤٣٣ / ١٤٢٦٩)، وأبي سعيد محمد بن يحيى البغدادي – المعروف بـ (حامل كفنه) «تاريخ بغداد «: (٣/ ٤٢٣)، وموسى بن هلال العبدي، وأبي النضر هاشم بن القاسم، ويعقوب بن محمد الزهري.
روى عنه: محمد بن محمد الباغندي، ومحمد بن مخلد، ومحمد بن المسيب «الثقات» (٨/ ٤٣٣ / ١٤٢٦٩)، وأبو بكر بن أبي الدنيا، وأبو عبد الله المحاملي، وأبو عبيد بن حربويه.
وكل اسم لم يعز إلى كتاب - وميزته بالأسود العريض - من معجم شيوخه وتلاميذه، فهو من ترجمته في «تاريخ بغداد».
التعديل والتجريح: ذكره ابن حبان في «الثقات».
وقال الخطيب البغدادي في «تاريخ بغداد»: «كان ثقة».
وانظر: «الثقات» (٨/ ٤٣٣ / ١٤٢٦٩)، و «تاريخ بغداد» (١١/ ٩٧ / ٥٧٨٩)، و «رجال تفسير الطبري» (٣٧٣/ ١٧١٨).',NULL,'347',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (350,'١٩٩ - أبو صالح العسقلاني (٢١٨٨)
(طب:١٧ ر / ١ ش)
(تس: ١٣ ر / ١ ش)، و (تهـ: ٣ ر / ١ ش)، و (تق: ١ ر / ١ ش)
أبو صالح، عصام بن رواد بن الجراح، العسقلاني، من الحادية عشرة، صدوق، وليَّنه الحاكم أبو أحمد.
روى عن: أبيه رواد ابن الجراح (تس ٢١٨٨ و ٥٤٤٢ و ٦١٢٥ و ٦١٣٠ و ٦١٤١ و ٦١٤٩ و ٨٣٧٦ و ٢٢٠٥٧ و ٢٤٧٩٣ و ٢٥٥١٠ و ٢٧١٠٠ و ٢٨٨٩١ و ٣١٠٦١)، و (تهـ: ٢٢٤٠ و ٢٢٤٢ و ٢٦٢٩)، و (تق ٦٨٧)، وآدم بن أبي إياس، وعيسى بن حازم [النيسابوري] «الحلية» (٨/ ٦)، ونعيم بن حماد [الخزاعي] «الكامل» (٧/ ١٧ / ١٩٥٩)، ويعقوب بن أبي عباد «المعجم الكبير» (٢/ ٣٥٢ / ٢٤٨٠).
روى عنه: عبد الرحمن بن أبي حاتم – كتابة – وعبد الله بن محمد بن مسلم «الكامل» (٧/ ٩١ / ٢٠١٤)، وعبد الله بن أبي سفيان الموصلي «الكامل» (٣/ ٤٩ / ٦٠٦)، وعلي بن سعيد الرازي «المعجم الأوسط» (٤/ ١٩٩ / ٣٩٦٩)، ومحمد بن أحمد بن سليمان «الحلية» (٨/ ٦)، ومحمد بن أحمد بن معدان «الحلية» (٧/ ١٢٧)، ومحمد بن إسحاق بن خزيمة «سنن البيهقي» (٥/ ٢٢٢ / ٩٨٩٧)، وأبو حاتم محمد بن إدريس الرازي، ومحمد بن الحسن العسقلاني «المستدرك» (٢/ ٥٧٣ / ٣٩٤٣)، ومحمد بن راشد «المعجم الأوسط» (٧/ ٢٠٠ / ٧٢٦٧)، وأبو الطيب النعمان بن دلهث «الفصل للوصل المدرج» (١/ ٢٨١)، وأبو بشر الدولابي «ميزان الاعتدال» (٢/ ٢٦٦ / ١٩٢١)، وأبو عوانة الإسفراييني «مسند أبي عوانة» (٣/ ٤٨٤ / ٥٧٨٠)، وابن جوصاء «ميزان الاعتدال» (٥/ ٨٥ / ٥٦٢٨).
وكل اسم لم يعز إلى كتاب - وميزته بالأسود العريض - من معجم شيوخه وتلاميذه، فهو من ترجمته في «الجرح والتعديل».
التعديل والتجريح: قال ابن أبي حاتم: «سئل أبي عنه فقال: صدوق».
وذكره محمد بن حبان في «الثقات».
وقال الذهبي في «ميزان الاعتدال»: «ليَّنه الحاكم أبو أحمد».',NULL,'348',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (351,'وانظر: «الجرح والتعديل» (٧/ ٢٦ / ١٤٥)، و «الثقات» (٨/ ٥٢١ / ١٤٨٠٠)، و «ميزان الاعتدال» (٥/ ٨٥ / ٥٦٢٨)، و «المقتنى» (١/ ٣١٤ / ٣١٤٨)، و «لسان الميزان» (٤/ ١٦٧ / ٤٠٨)، و «رجال تفسير الطبري» (٣٩٠/ ١٨١٠).
٢٠٠ - أبو بشر الدارع (٧٩٧)
(طب: ٣ ر / ١ ش)
(تس: ٢ ر / ١ ش)، و (تهـ: ١ ر / ١ ش)
أبو بشر، عقبة بن سنان بن عقبة بن سنان بن سعد بن جابر بن محمد بن محصن، الهدادي، الزهراني، الدارع – وقيل: الذراع – الفزاري، البصري، القزاز: من العاشرة، صدوق.
روى عن: غسان بن مضر (تس ٧٩٧ و ٢٩٠٢٦)، و (تهـ: ٢٥١٥)، وعثمان بن عثمان الغطفاني «الجرح والتعديل» (٦/ ٣١١ / ١٧٣٤)، ومحمد بن مروان العقيلي «المعجم الأوسط» (٤/ ٢٠٠ / ٣٩٧١)، والهيصم بن شداخ «الإكمال لابن ماكولا» (٤/ ٤٥١).
روى عنه: إبراهيم بن أحمد الواسطي «الإكمال لابن ماكولا» (٤/ ٤٥١)، وأحمد بن حماد بن سفيان الكوفي «الإكمال لابن ماكولا» (٤/ ٤٥١)، وزكريا الساجي «المعجم الكبير» (٦/ ٢٨ / ٥٤٠٩)، وسهل بن موسى «المحدث الفاصل» (١/ ٣٧٩)، وعبدان بن أحمد «المعجم الكبير» (٦/ ٢٨ / ٥٤٠٩)، وعبد الرحمن بن الحسين العابوري – وقيل: الصابوني – التستري «المعجم الكبير» (١٧/ ١٧٦ / ٤٦٥) وعلي بن سعيد الرازي «الإكمال لابن ماكولا» (٤/ ٤٥١)، وأبو حاتم محمد بن إدريس الرازي «الجرح والتعديل» (٦/ ٣١١ / ١٧٣٤)، ويحيى بن محمد بن صاعد «الإكمال لابن ماكولا» (٤/ ٤٥١)، وأبو سعيد السوسي «المحدث الفاصل» (١/ ٥٤٥)، والكديمي – غير مسمى – «الإكمال لابن ماكولا» (٤/ ٤٥١).
التعديل والتجريح: سئل أبو حاتم الرازي عنه فقال: «صدوق».
قلت: وقد تحرف اسمه في الأثر رقم (تس ٢٩٠٢٦) بحيث صار (عقبة عن سنان القزاز) بحيث يوهم القارئ بأنهما شيخان، وليسا كذلك، إنما هو شيخ واحد وهو صاحب الترجمة.',NULL,'349',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (352,'ونسبه الطبري في الأثر رقم (تهـ: ٢٥١٥) فصار: (عقبة بن سنان الزهراني).
وانظر: «الجرح والتعديل» (٦/ ٣١١ / ١٧٣٤)، والإكمال لابن ماكولا» (٤/ ٤٥١)، و «رجال تفسير الطبري» (٣٩٣/ ١٨٢٧).
وقد شاركه في اسمه، واسم أبيه أكثر من واحد من الرواة، ذكرتهم تمييزًا وهم.
٧٧ - تمييز: أبو الجلاس، عقبة بن سنان، ويقال: سيار.
(أبو داود، والنسائي في اليوم والليلة)
روى عن: عثمان بن جحاش.
روى عنه: عبد الوارث.
وانظر: «كنى البخاري» (٨٦/ ٨٧١)، و «تهذيب الكمال» (٢٠/ ٤٦٢ / ٤٠٨٢).
٧٨ - تمييز: عقبة بن سنان، الكاتب، البغدادي، مسكوت عنه.
روى عن: أكثم بن صيفي من كتاب «ليس للمختال في حسن الثناء نصيب».
روى عنه: حجاج بن محمد الأعور.
وانظر: «تاريخ بغداد» (١٢/ ٢٦٥ / ٦٧٠٩).
٧٩ - تمييز: عقبة بن سنان، ثقة.
روى عن: أبي خالد الدالاني.
روى عنه: عبد السلام بن حرب، وحكيم بن محمد.
وانظر: «التاريخ الكبير» (٦/ ٤٤٠ / ٢٩٢٤)، و «الجرح والتعديل» (٦/ ٣١١ / ١٧٣٣)، و «الثقات» (٨/ ٤٩٩ / ١٤٦٥٦).
٢٠١ - – على العجلي (تخ ٣/ ١٢)',NULL,'350',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (353,'(طب: تخ ١ ر / ١ ش)
علي بن أحمد بن الحسن، العجلي، من الحادية عشرة، لم أعرفه، ولم أجد له ترجمة، ولم أعرف شيخه، ورواية الطبري عنه كتابة في قصة رجوع عائشة – رضي الله تعالى عنها - إلى مكة من سرف بعد مقتل عثمان، وتولي على - رضي الله عنه - مكانه، ولم أقف له في كتب الطبري على غير هذه الرواية.
روى عن: الحسين بن نصر العطار (تخ ٣/ ١٢).
روى عنه: أبو جعفر الطبري (تخ ٣/ ١٢) – كتابة -.
٢٠٢ - – أبو الحسن الطائي (٦٢٩٣)
(النسائي)
(طب: ١٧ ر / ١٦ ش)
(تس ٤ ر / ٣ ش) و (تخ ٦ ر / ٦ ش) و (تهـ ٥ ر / ٤ ش)، و (تق ٢ ر / ٢ ش)
أبو الحسن، علي بن حرب بن محمد بن علي، الطائي، الموصلي: مولده بأذريبجان، في شعبان سنة خمس وسبعين ومائة، وتوفي في شوال، سنة خمس وستين ومائتين من صغار العاشرة، صدوق، فاضل.
روى عن: إبراهيم بن سليمان الحنفي ابن أخي أبي الأحوص (تخ: ٣/ ٤٩٥) – ومبهم بينه وبين الطبري، (تخ ٣/ ٣٩٥)، وأحمد بن نصر الخرساني (تهـ ١٤٧٣)، وخالد بن اليزيد العدوي (تق ٣٤٩)، وروح بن أسلم (تخ ١/ ١٠٠)، وعبد الرحمن بن محمد المحاربي (تس ٣٣٦٥٦)، وعبد السلام بن صالح (تهـ ١٥٢٥)، وعبد الله بن داود (تهـ ١٥٠٥)، وأبي معن عيسى الكعبي الأنصاري (تخ ١/ ٥٠٢)، ومحمد بن بشر (تق ٣٢٧)، ومحمد بن عمارة القرشي (تخ ١/ ٥٠٠)، و محمد بن فضيل بن غزوان (تس ٦٢٩٣ و ٦٥٣٧)، و (تهـ ٢٧١٥)، و (س)، ومكي بن إبراهيم (تس ٢٢٦٣٥)، ويعلى بن عمران البجلي (تخ ١/ ٤٥٩)، وأبي داود الهمداني (تهـ ١٤٦٢)، وعبد العزيز',NULL,'351',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (354,'بن النعمان الموصلي «تعجيل المنفعة» (١/ ٢٦٣ / ٦٦٥)، والوليد بن مسلم «الجرح والتعديل» (٦/ ١٨٣ / ١٠٠٦).
روى عنه: أبو بكر البزار «مسند البزار» (٢/ ١٥٦ / ٥٢٢)، وأبو بكر الخلال «السنة للخلال» (١/ ٢٨٩ / ٣٥٢)، وأبو عوانة الإسفرائيني «مسند أبي عوانة» (١/ ٢٧ / ٣٠) – وقد أكثر عنه جدًّا – وأبو يعلى الموصلي «الثقات» (٨/ ٤٧١ / ١٤٤٨٥).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، ثبتًا، صدوقًا صالحًا، إخباريًّا، شاعرًا، نسابًا، أديبًا، رحالةً في طلب الحديث.
وقد أبهم الطبري الواسطة بينه وبين علي في الأثر رقم (تخ: ٣/ ٤٩٥) عن إبراهيم بن سليمان الحنفي بن أخي أبي الأحوص.
وانظر: «الجرح والتعديل» (٦/ ١٨٣ / ١٠٠٦)، و «الثقات» (٨/ ٤٧١ / ١٤٤٨٥)، و «الإرشاد» (٢/ ٦١٩)، و «تاريخ بغداد» (١١/ ٤١٨ / ٦٢٩٦)، و «تهذيب الكمال» (٢٠/ ٣٦١ / ٤٠٣٧)، و «الكاشف» (٢/ ٣٧ / ٣٨٩١)، و «المقتنى» (١/ ١٨٢ / ١٥٢٢)، و «السير» (١٢/ ٢٥١ / ٩٣)، و «طبقات المحدثين» (١/ ٩٨ / ١١١١)، و «المقصد الأرشد» (٢/ ٢١٨ / ٧٠٦)، و «تهذيب التهذيب» (٧/ ٢٦٠ / ٥٠٦)، و «تقريب التهذيب» (١/ ٣٩٩ / ٤٧٠)، و «رجال تفسير الطبري» (٣٩٩/ ١٨٥٧).
٢٠٣ - أبو الحسن الخزاز (١٥٤)
(طب: ١٢ ر / ٢ ش)
(تس: ١٠ ر / ١ ش)، و (تخ: ر / ١ ش)، و (تهـ: ١ ر / ١ ش)
أبو الحسن، علي بن الحسن بن عبدويه، البغدادي، الخزاز: توفي سنة سبع وسبعين ومائتين من الحادية عشرة، ثقة.
روى عن: عبد الله بن بكر السهمي (تهـ ١٤٣٥)، ومسلم بن عبد الرحمن الجرمي (تس ١٥٤ و ١٥٨ و ١٩٥ و ٢٠٩ و ٦٤٩ و ٦٥٣ و ٦٦٦ و ٧٧٦ و ٨٤٦ و ١٠٧٦)، و (تخ ١/ ٦٦)، والأسود بن عامر «تاريخ بغداد» (١٣/ ١٠٠ / ٧٠٨٨)، وحبان بن عمار بن واقد، «تاريخ بغداد» (٨/ ٢٥٧ / ٤٣٥٨)، وحجاج بن محمد الأعور، وحفص بن عمر الحبطي الرملي «تاريخ بغداد» (٨/ ٢٠٠ / ٤٣١٤)،',NULL,'352',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (355,'وهاشم بن القاسم.
روى عنه: أحمد بن سلمان النجاد، وأحمد بن الفضل بن خزيمة، وأحمد بن محمد بن عيسى المقرئ «تاريخ بغداد» (٥/ ٦٤ / ٢٤٣٥)، ومكرم بن أحمد القاضي، وأبو بكر الشافعي، وأبو بكر بن مجاهد المقرئ.
وكل اسم لم يعز إلى كتاب - وميزته بالأسود العريض - من معجم شيوخه وتلاميذه، فهو من ترجمته في «تاريخ بغداد».
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، لا بأس به.
وانظر: تاريخ بغداد (١١/ ٣٧٤ / ٦٢٣٠)، و «رجال تفسير الطبري» (٤٠٠/ ١٨٦٠).
٢٠٤ - أبو الحسن اللاني (٦٩٩)
(النسائي)
(طب: ٣٢ ر / ش)
(تس ٢٤ ر / ٥ ش) و (تخ ٣ ر / ٣ ش)، و (تهـ ٤ ر / ٣ ش)، و (تق ١ ر / ١ ش)
أبو الحسن، علي بن الحسن – ويقال: ابن الحسين – ابن سالم، الأزدي – ويقال الأزرقي، ويقال: اللاني، بنون بعدها ياء، وقال: اللائي – بهمز بعده ياء – الكوفي: من صغار العاشرة، صدوق.
روى عن: أبي نصر أحمد بن محمد الخلال (تس ٦٩٩)، و (تخ ١/ ٦٠)، وعبد الرحيم بن سليمان (تخ ٣/ ١٤٥)، (تهـ ٦٠٣)، (س)، وعبيد الله بن عبد الرحمن الأشجعي (تس ١٠٢٦٣ و ٢٢٨٤٥ و ٣٨٣٥٥)، ومسلم الجرمي (تخ ١/ ٦٦)، والمعافى بن عمران الموصلي (تس ٢٨٩٤ و ١٨٠٤٨ و ٢٦٩٢٤ و ٣٦١٤٣ و ٣٦٤٨٥)، و (تهـ ٤٩٩ و ٦٨٢)، ويحيى بن اليمان (تس ١٤٠٩٨ و ١٥٤٥٤ و ٢٦٠٩٢ و ٢٦٠٩٤ و ٢٩٩٧٠ و ٣٠٠٩٢ و ٣٠٢١٤ و ٣١٥٩٥ و ٣٣٤١١ و ٣٤١١١ و ٣٤٤١٧ و ٣٤٦٠٦ و ٣٥٦٩٣ و ٣٦٦٥١)، و',NULL,'353',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (356,'(تق ٦٩٦)، وأبي معاوية الضرير (تس ١٧٦٨٣)، (تهـ ٧٩٠).
روى عنه: أبو الحسن علي بن يحيى النابلسي «التحبير» (١/ ٥٩٦ / ٥٨٤)، ومحمد بن عبد الله الحضرمي «المعجم الأوسط» (٦/ ٣٥ / ٥٧١٧).
التعديل والتجريح: لقد أُشكِلَتْ على أصحاب كتب الجرح والتعديل نسبته، وتراوحت أقوالهم فيه بين كونه: صدوقًا، ولا بأس به.
وذكره الشيخ شاكر في تحقيقه «لتفسير الطبري» في (٣/ ٤٦٩ / ٢٨٨) وسكت عنه!! ثم ذكره في (٩/ ٩٨ / ١٠٢٥٨) وقال: «شيخ الطبري لم أجد له ترجمة، وقد روى عنه في «تاريخه» في مواضع منها (١/ ٤٤ و ٤٩)، (٦/ ٧٣ و ١٤٣).
قلت: وهذه ترجمته بين يديك والحمد لله الذي بنعمته تم الصالحات!! ولقد تردد فيه الحافظ في «تقريب التهذيب» (١/ ٣٩٩ / ٤٧٠٩ و ٤٧٧١)، بين اللاني، والسماك، وكثرت التصحيفات والتحريفات في اسمه، واسم أبيه، ونسبه، وشيوخه في عدة مواضع، من «التفسير»، و «التاريخ» منها على سبيل المثال، لا الحصر:
في «التفسير» الآثار ذوات الأرقام التالية: - (تس: ١٤٠٩٨ و ١٥٤٥٤ و ٢٦٩٢٤ و ٣١٥٩٥ و ٣٤١١١ و ٣٦٦٥١)، سماه: (علي بن الحسين الأزدي).
وفي «التاريخ» (تخ: ٣/ ١٤٥) سماه: (علي بن الحسن الأزدي).
وفي الأثرين رقم (تس: ٦٩٩ و ٣٥٦٩٣)، والأثر رقم (تخ ١/ ٦٦) سماه: (علي بن الحسين) – بدون نسبة -.
وفي الأثر (تس ٢٢٨٤٥) سماه: (علي بن الحسن الأزرقي – ولعله تحريف – من الأزدي).
وتحرف اسم شيخه (عبد الرحيم بن سليمان) إلى (عبد الرحمن) في الأثر (تخ ٣/ ١٤٥).
وانظر: «الثقات» (٨/ ٤٧٥ / ١٤٥١٤)، و «تهذيب الكمال» (٢٠/ ٣٧٧ / ٤٠٤٤)، و «تهذيب التهذيب» (٧/ ٢٦٥ / ٥١٤)، و «تقريب التهذيب» (١/ ٣٩٩ / ٤٧٠٨)، و «رجال تفسير الطبري» (٤٠٠/ ١٨٥٨).',NULL,'354',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (357,'٢٠٥ - – أبو الحسن بن إشكاب (١١٤١٦)
(أبو داود، وابن ماجه)
(طب: ٥ ر / ٤ ش)
(تس: ٤ ر / ٣ ش)، و (تهـ: ١ ر / ١ ش)
أبو الحسن، علي بن الحسين – ويعرف بـ (إشكاب) – ابن إبراهيم بن الحر بن زعلان، العامري النسائي أصلاً، ثم البغدادي: أخو محمد بن الحسين -، وكان أكبر من محمد – ويقال: إنه المراد بقول البخاري: «حدثنا على بن إبراهيم» توفي سنة إحدى وستين ومائتين، من العاشرة، صدوق.
روى عن: علي بن عاصم الواسطي (تهـ ٣٤٦)، وعمر بن يونس اليمامي (تس ٣١٩٣٨) و (د) ومحمد بن ربيعة الكلابي (تس ١١٤١٦ و ٣٣٤٣٣) ومصعب بن المقدام (تس ٣٥٦٦٦).
روى عنه: علي بن جعفر بن محمد بن حبيب التمار «مسند أبي حنيفة» (١/ ٣٢)، وأبو عوانة الإسفراييني «مسند أبي عوانة» (١/ ٤).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقًا، ولا بأس به، واختلفوا في رواية البخاري عنه.
وانظر: «الجرح والتعديل» (٦/ ١٧٩ / ٩٧٩)، و «الثقات» (٨/ ٤٧٢ / ١٤٤٩٠)، و «تاريخ بغداد» (١١/ ٣٩٢ / ٦٢٦٩)، و «تهذيب الكمال» (٢٠/ ٣٧٩ / ٤٠٤٩)، و «الكاشف» (٢/ ٣٧ / ٣٨٩٩)، و «تهذيب التهذيب» (٧/ ٢٦٦ / ٥١٩)، و «تقريب التهذيب» (١/ ٤٠٠ / ٤٧١٣)، و «رجال تفسير الطبري» (٤٠٠/ ١٨٦١).
قلت: قد أغفل الشيخان شاكر ذكر (علي بن الحسين بن أبجر)، و (علي بن الحسين بن الحارث) ولا يوجد لهما ترجمة في كتب الحديث والرجال.
ووقعت بعض التصحيفات والتحريفات في اسمه، واسم أبيه، ونسبه، في عدة مواضع، منها:
تحرف اسمه إلى (علي بن الحسين بن أبجر) كما في الأثرين (تس ٣١٩٩٣٨ و ٣٥٦٦٦).',NULL,'355',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (358,'ثم تحرف إلى (علي بن الحسين بن الحارث) كما في الأثر رقم (تس ٣٣٤٣٣).
تصحف اسم (حسين) إلى (حسن) كما في «مسند أبي عوانة» (١/ ٤٠٢ / ١٤٧٨)، و (٢/ ٦٦) وأما في «مسند أبي عوانة» (٥/ ٢٧١ / ٨٦٩٩)، فقد سماه الحسين.
٢٠٦ - – أبو الحسبن القنطري (٢٦٥٤)
(ابن ماجه)
(طب: ٦٩٤ ر / ٥ ش)
(تس: ٦٨٩ ر / ٤ ش) و (تخ: ١ ر / ١ ش)، و (تهـ ٣ ر / ٣ ش)، و (تق ١ ر / ٢ ش)
أبو الحسن، علي بن داود بن يزيد، التميمي، القَنْطَري، الأَدَمي البغدادي توفي سنة اثنتين – وقيل: ثلاث – وسبعين ومائتين، من الحادية عشرة، صدوق.
روى عن: آدم بن أبي إياس (تس ١٥١٤٨)، وسعيد بن أبي مريم (تس ٣٠٢٢٣)، و (تهـ: ٥٣)، و (تق ٦١٩)، وعاصم بن علي (تس ٣٠٦٨١)، وأبي صالح عبد الله بن صالح المصري – وقد أكثر عنه جدًّا – (تس ٢٦٥٤ و ٣١٠٠ و ٣١٢٧ و ٣٥٩٣ و ٣٦٥٦ و ٣٨٩٥ و ٤١٢٤ و ٤١٣٦ و ٤١٤٠ و ٤١٤٤ و ٤١٦٣ و ٤١٧٧ و ٤١٨١ و ٤١٩٤ و ٤٢٠٧ و ٤٢٤١ و ٤٣١٣ و ٤٨١٦ و ٥٤٩٧ و ٦٧٠٣ و ٦٧٤٣ و ٧٦٢٢ و ٧٦٦٩ و ٨٠٢٠ و ٨٤٢٣ و ٨٥٧٩ و ٨٧٠٩ و ٨٧١٠ و ٩١٢٩ و ٩٣٣٤ و ٩٣٣٥ و ١٣٥٢٣ و ١٣٥٣٨ و ١٣٩٠١ و ١٤٠٩٥ و ١٥٣١٠ و ١٥٩٤٦ و ١٦٩٢٨ و ١٧٠٦٠ و ١٧٠٧٦ و ١٨٢٧٦ و ١٨٣٧٤ و ١٨٣٨٥ و ١٩٢١٨ و ١٩٢٥٣ و ١٩٢٩٤ و ٢٠١٣٧ و ٢٠٣٦٨ و ٢١٤٥٩ و ٢١٥١٠ و ٢١٨٦٢ و ٢١٨٦٣ و ٢١٨٦٤ و ٢١٨٦٥ و ٢١٨٦٦ و ٢١٨٦٧ و ٢١٨٨٦ و ٢١٩٠٤ و ٢١٤٩ و ٢٢٠٥١ و ٢٢٠٦٤ و ٢٢١٠٨ و ٢٢١٥٣ و ٢٢١٧٢ و ٢٢١٨٣ و ٢٢٢٠٩ و ٢٢٢١٧ و ٢٢٢٧٢ و ٢٢٢٨١ و ٢٢٣٠٨ و ٢٢٣١٩ و ٢٢٣٦٥ و ٢٢٣٦٦ و ٢٢٤٦١ و ٢٢٤٦٨ و ٢٢٤٧٣ و ٢٢٤٨١ و ٢٢٤٩٩ و ٢٢٥٠٦ و ٢٢٥٠٨ و ٢٢٥٣٠ و ٢٢٦٦٤ و ٢٢٦٦٨ و ٢٢٦٧٠ و ٢٢٦٨٥ و ٢٢٦٨٦ و ٢٢٧٠٢ و ٢٢٧٢٣ و ٢٢٧٢٥ و ٢٢٧٣٥ و ٢٢٧٥٦ و ٢٢٧٧٣ و ٢٢٧٨٢ و ٢٢٧٩٠ و ٢٢٨٥٥ و ٢٣١٠٦ و ٢٣١٤٢ و ٢٣١٦٢ و ٢٣١٧٦ و ٢٣٣٠٤ و ٢٣٣٤١ و ٢٣٣٤٨ و ٢٣٥٤٩ و ٢٣٧٣٨ و ٢٣٧٥٦ و ٢٣٧٥٨ و ٢٣٧٦٢ و ٢٣٧٩٧ و ٢٣٨٢١ و ٢٣٨٢٩ و ٢٣٨٧٧ و ٢٣٨٨٤ و ٢٣٩١١ و ٢٣٩١٢ و ٢٣٩٢١ و ٢٣٩٢٨ و ٢٣٩٣١ و ٢٣٩٣٦ و ٢٣٩٤٣ و ٢٣٩٤٧ و ٢٣٩٥٣ و ٢٣٩٥٥ و ٢٣٩٥٩ و',NULL,'356',NULL,NULL,'0');
