INSERT INTO [b29741] VALUES (361,'٣٧٩٥٢ - و ٣٨٠٠٦ و ٣٨٠١٩ و ٣٨٠٢٠ و ٣٨٠٤٦ و ٣٨١٢٣ و ٣٨٢٠٨ و ٣٨٢١٣ و ٣٨٣٢٩ و ٣٨٣٣٤ و ٣٨٣٦٣ و ٣٨٣٧١)، و (تهـ: ٢٨٩٩)، و (تخ: ١/ ٣٧ و ١/ ١١٩)، (ق)، ومحمد بن عبد العزيز الأدمي (تهـ: ١٦٩٨)، و [يحيى بن عبد الله] ابن بكير، و (تق ٦١٩).
روى عنه: أحمد بن عبد الله بن محمد النحاس «سنن الدارقطني» (٤/ ٢٩١ / ٧٩)، وأحمد بن محمد بن عثمان القطان «سنن الدارقطني» (٣/ ٢٨٧ / ١٥٧)، وعلي بن سعيد الرازي «المعجم الأوسط» (٤/ ٢٠٧ / ٣٩٩٣)، وأبو طالب، علي بن محمد بن أحمد بن الجهم «سنن الدارقطني» (١/ ٢٤٠ / ٢٤)، ومحمد ابن سهل بن الفضيل الكاتب «سنن الدارقطني» (٢/ ١١ / ٨)، وأبو جعفر الرزاز «سنن البيهقي» (٢/ ٣٥٧ / ٣٧٢٢)، وأبو عمرو البزار «مسند البزار» (٤/ ٣٤١ / ١٥٣٥).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه على كونه: ثقةً، صدوقًا، صالح الحديث؛ ولكنه روى خبرًا منكرًا عن (سعيد بن أبي مريم) فَتُكُلِّمَ فيه لذلك.
وانظر: «الجرح والتعديل» (٦/ ١٨٥ / ١٠١٥)، و «الثقات» (٨/ ٤٧٣ / ١٤٤٩٧) و «تاريخ بغداد» (١١/ ٤٢٤ / ٦٣٠٨)، و «تهذيب الكمال» (٢٠/ ٤٢٣ / ٤٠٦٥)، و «الكاشف» (٢/ ٣٩ / ٣٩١٢)، و «ميزان الاعتدال» (٥/ ١٥٤ / ٥٨٤٣)، و «المقتنى» (١/ ١٨٤ / ١٥٥٤) و «تهذيب التهذيب» (٧/ ٢٧٩ / ٥٣٩)، و «تقريب التهذيب» (١/ ٤٠١ / ٤٧٣٠)، و «لسان الميزان» (٧/ ٣١١ / ٤١٠٧) و «رجال تفسير الطبري» (٤٠١/ ١٨٦٤).
٢٠٧ - أبو الحسن الكندي (١١٨٦)
(الترمذي، و النسائي)
(طب: ٣٥ ر / ١ ش)
(تس ٢٥ ر / ١ ش) و (تخ ١ ر / ١ ش) و (تهـ ٣ ر / ٣ ش)، و (تق ٦ ر / ٣ ش)
أبو الحسن: علي بن سعيد بن مسروق، الكندي، الكوفي: توفي سنة تسع وأربعين ومائتين، من العاشرة، صدوق، وهو ابن أخي محمد بن مسروق، قاضي مصر، وابن عم مسروق بن المرزبان.
روى عن: أحمد بن بشير (تهـ: ١١٨٦)، وحفص بن عاصم (تس: ٣٢١٠٧)،',NULL,'359',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (362,'وحفص بن غياث (تس ٢٧٩١ و ٢٧٥٧٣ و ٢٨٢٢٤)، و (تخ: ١/ ٢٦٣)، (ت)، والحكم بن ظهير (تس: ١٨٧٩٢)، وخالد بن نافع الأشعري (تس ٢١٠٠٥)، وعبد الرحيم بن سليمان (تس ٨٩٧٠ و ٨٩٩٤) و (تهـ ١٧٩٢) و (تق ٧٥٦ و ٧٦٥)، و (ت، س) وعبد السلام بن حرب (تس ١١٨٦ و ١١٩٩ و ١٢٠٩)، وعبد الله بن إدريس (تس ١١١٣٤)، وعبد الله بن المبارك (تس ٥٥٠١ و ٢١٩٧٦ و ٢٨٩٩٦)، و (ت، س)، وعبد الرحمن بن محمد المحاربي (تس ١٢١٨٩)، وعبدة أبي غسان (تس ١٦٩٦٨)، وعلي بن عابس (تس ١١٢٣٦)، و (تهـ ٣٨٥)، وعلي بن غراب (تق ٣٧٦ و ٧٢٢)، وعلي بن مسهر (تس ٤١١٤ و ٤٤٥٦)، وأبي المحياة يحيى بن يعلى التيمي (تس ١٤٤٦٧ و ٢٠٥٣٥ و ٣١٢٥١)، (ت)، وعيسى بن يونس (تس ٢٣٧٧٨ و ٣٦٥٧٩)، و (تق ٤٨٦ و ٥٥٥)، ومحمد بن مروان (تس ٣٧٨٨١).
روى عنه: الحسن بن حباش الحماني «المعجم الكبير» (١٠/ ٧٥ / ٩٩٩٧)، ومحمد بن الحسين الخثعمي «شعب الإيمان» (٣/ ٤٣١ / ٣٩٨١)، وأبو بكر البزار «مسند البزار» (٤/ ٢٠٦ / ١٣٦٩).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقًا، لا بأس به.
قلت: وقد تحرفت نسبته في الأثر رقم (٨٩٧٠) من الكندي إلى الكناني. وتصحف اسم أبيه في الأثر رقم (٣١٢٥١) من (سعيد) فصار (سعد). وتحرف اسم شيخه، (أبو المحياة، يحيى بن يعلى) فصار (أبو محمد بن يعلى). والصحيح ما أثبته، والله تعالى أعلم.
وانظر: «الجرح والتعديل» (٦/ ١٨٩ / ١٠٤٢)، و «الثقات» (٨/ ٤٧٥ / ١٤٥١٥)، و «تهذيب الكمال» (٢٠/ ٤٥٠ / ٤٠٧٤)، و «الكاشف» (٢/ ٤٠ / ٣٩٢٠)، و «تهذيب التهذيب» (٧/ ٢٨٧ / ٥٥٠)، و «تقريب التهذيب» (١/ ٤٠١ / ٤٧٣٨) و «رجال تفسير الطبري» (٤٠٢/ ١٨٧٠).
٢٠٨ - أبو الحسن الرملي (١٣٨٧)
(أبو داود، والنسائي – في اليوم والليلة -)
(طب: ١٩٤ ر / ١٣ ش)',NULL,'360',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (363,'(تس ١٣١ ر / ١٢ ش)، و (تخ: ٧ ر / ٥ ش)، و (تهـ: ٤٤ ر / ٩ ش)، و (تق ١٠ ر / ٤ ش)، و (صر: ٢ ر / ١ ش)
أبو الحسن، علي بن سهل بن قادم – ويقال -: ابن موسى، الحرشي الرملي، أخو موسى بن سهل الرملي، نسائي الأصل، توفي سنة إحدى وستين ومائتين، من كبار الحادية عشرة، صدوق.
روى عن: أحمد بن محمد النسائي (تس ٢٤٤٠٣)، و (تهـ: ١٠٨٨ و ١٨٧٣)، وأيوب بن بشر (تس ٦٦٥٢)، وأبي مسعود أيوب بن سويد (تهـ: ١٣٢٨ و ١٧٩٦)، وجعفر بن برقان (تس ٢٥٨٨٤)، وحجاج بن محمد المصيصي (تس ٣٧٢٢ و ١٤٨٦١ و ٢٠٩٦١ و ٢٢٠٢١ و ٢٢٣٠٥ و ٢٣٤٠٣ و ٢٣٧٦٢ و ٢٤٨٧٨ و ٢٥٥٧١ و ٢٥٩٤٨ و ٢٦٧٠٠ و ٢٨٧٣٨ و ٢٨٨١٤ و ٣٠٣٦٠ و ٣١١٢١ و ٣٢٤٩٥ و ٣٢٥٠٣ و ٣٢٥٢٤ و ٣٢٦٩٢ و ٣٥٨٦٣)، و (تهـ ١٢٢١ و ٢٤٠٩)، (د، سي)، والحسن بن بلال (تس: ٣٦٢٤٧ و ٣٧٨٩٠) و (تهـ ١٥٩ و ٨٩٠ و ٩٣٣ و ١٤٣٣ و ١٨٩٣)، و (تق ١٠٦ و ٣٨٩)، و (تخ ١/ ٤٥، وزيد بن أبي الزرقاء الموصلي (تس ١٣٨٧ و ٢٦٢٢ و ٢٦٧٠ و ٣٣٩٠ و ٣٥٥٩ و ٤٩٥٨ و ٤٩٧٤ و ٤٩٧٦ و ٥٢٧٦ و ٦٣١٦ و ١٠٦٤٦ و ١٤٠١٤ و ١٤٥٨٥ و ١٥٨١٧ و ١٧٦٨٧ و ٢٠٤٦٢ و ٢٥٠٨٣ و ٢٥٥٧٥ و ٢٥٨٨٤ و ٢٦٠٢٢ و ٢٦٠٣٤ و ٢٦٧٨٠ و ٢٧٨١٦ و ٢٨١١٢ و ٢٩٦٦٤ و ٣٠٣٩٨ و ٣٣٧٣٩)، و (تهـ ١٣٨ و ٢٦٠ و ٤٢٥ و ٥٤١ و ٨٨٢ و ١٣٥٧ و ١٥١٧ و ١٦١٩ و ٢٨٥٨ و ٣٠٧٦)، و (تق ١٢٧ و ٤٢٧ و ٤٧٦ و ٤٧٧ و ٩٨٨)، و (تخ ١/ ٢٦) و (د)، وضمرة بن ربيعة (تس ٧١٣٠ و ١٢٨٧٢ و ١٥٣٦٣ و ١٦٢٤٦ و ١٨٤٦٩ و ٢٢٩٩٨ و ٣٢٧٧٠ و ٣٦٩٤٣)، و (تخ ٢/ ٥٧٩) و (تهـ ٨٤ و ٣٧٣ و ١٠٠٦) و (تق ٨٩٣)، ومحمد بن كثير (تس ٤٨٢٠)، و (تهـ ١٤١٢)، ومؤمل بن إسماعيل (تس ٢٣٤٥ و ٢٣٦٥ و ٢٩٧٦ و ٣٤٦٧ و ٤٥١٣ و ٥٢٠٧ و ٩٩٦٤ و ١٠٣٢٤ و ١٢٣١١ و ١٦٥٩٣ و ٢٤٩٧٩ و ٢٨٧٣٨ و ٣٠٣٨٦ و ٣١٦٧٣ و ٣١٩٧٨ و ٣٢٦٣٥ و ٣٣٠٨٩ و ٣٣٧٠١ و ٣٥٢٨٤ و ٣٥٧٩٠ و ٣٦٩٤٥)، و (تهـ ١٨٣ و ٣٣٠ و ٨٠٠ و ١٠٥٥ و ١٣٥٥ و ١٤٣٢ و ١٤٤٤ و ١٦٢٧ و ١٧٩٠ و ٢٠٦٢ و ٢١١٥ و ٢١٢٢ و ٢٣٨٩ و ٢٤٢٢ و ٣٠٣٦ و ٤٠٣٤)، و (تق ٦٠٠ و ٩٩٤)، و (تخ ٢/ ١٦٩)، و (سي)، والوليد بن مسلم (تس ٢١٨٩ و ٤٥٦٦ و ٤٦١٤ و ٤٦١٥ و ٤٦٥٣ و ٤٨٥١ و ٦٦٠٨ و ٨١٩٨ و ٨٦٣١ و ٩٠٧٢ و ٩٢١٦ و ٩٦٥٨ و ١١٣٩٥ و ١١٨١٨ و ١١٨٢٢ و',NULL,'361',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (364,'١١٨٢٦ - و ١١٨٥٨ و ١١٨٦٢ و ١١٨٦٣ و ١١٨٧٠ و ١١٨٨٣ و ١١٨٨٧ و ١١٨٩٠ و ١١٨٩٢ و ١١٨٩٦ و ١١٨٩٨ و ١٢٨٦٧ و ١٦٢٣٧ و ١٦٧٥٧ و ١٦٧٦٦ و ١٦٧٧٠ و ١٧٤٧٧ و ١٧٥٠٠ و ١٧٧٤٦ و ٢٣٧٩٩ و ٢٥٩٦٥ و ٢٧٠٦١ و ٢٩٨٥٤ و ٢٦٨٦٠ و ٣٠٤٣٧ و ٣١٩٩٨ و ٣٢٠٠٢ و ٣٤٧٧١ و ٣٤٩٨٢ و ٣٦٢٤٥ و ٣٦٦٢٧ و ٣٦٦٤٤) و (تهـ ١٤٥٦ و ١٥١٦ و ٢٩٣٤)، و (تخ ١/ ٢٨٤ و ٢/ ٥١٦ و ٢/ ٦٠٢)، و (صر ٢٩ و ٤٠)، (د)، ويحيى بن عيسى (تس: ٢٥٧٢٦ و ٣٥٧٨٥)، وأبي صالح - كاتب الليث - (تس: ٣١١٥٣) ولعله خطأ!!
روى عنه: أبو الحسن علي بن محمد بن عبيد الحافظ «تاريخ بغداد» (١١/ ٤٢٩ / ٦٣١٩).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، صدوقًا، وكان محدث أهل الرملة، وحافظهم، له أحاديث عن مؤمل بن إسماعيل وغيره يتفرد بها.
وتصحف اسم شيخه (زيد بن أبي الزرقاء) إلى (يزيد) بزيادة ياء في أوله في عدة آثار منها (٢٦٧٠).
وتحرف اسمه في الأثر رقم (تس ٢٥٧٢٦) بحيث صار (اسم قرينه) ضمن اسمه فصار حدثنا (علي ابن سهل بن موسى بن إسحاق الكناني ابن القواس) قالا! ثنا يحيى بن عيسى.
والصحيح حدثنا (علي بن سهل) و (موسى بن إسحاق الكناني ابن القواس) قالا: ثنا يحيى بن عيسى.
ووقع تحريف أكيد في الأثر رقم (تس ٢١١٥٣)، بحيث صار حدثني (علي بن سهل) قال: ثنا أبو صالح، وهو خطأ والصحيح (علي بن داود)، وهو إسناد كثير الدوران جدًّا، في «التفسير»، و «التاريخ»، و «التهذيب».
وانظر: «الجرح والتعديل» (٦/ ١٨٩ / ١٠٣٩)، و «الثقات» (٨/ ٤٧٥ / ١٤٥٠٧)، و «تهذيب الكمال» (٢٠/ ٤٥٤ / ٤٠٧٧)، و «الكاشف» (٢/ ٤٠ / ٣٩٢٣)، و «ميزان الاعتدال» (٥/ ١٦٠ / ٥٨٥٨)، و «سير أعلام النبلاء» (١٢/ ٢٤١ / ٨٥)، و «تهذيب التهذيب» (٧/ ٢٨٩ / ٥٥٣)، و «تقريب التهذيب» (١/ ٤٧٤١)، و «رجال تفسير الطبري» (٤٠٢/ ١٨٧١).',NULL,'362',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (365,'وقد شاركه في اسمه، واسم أبيه، وكنيته، ونسبه، وطبقته، وكثير من شيوخه ورواية الطبري عنه.
٨٠ - تمييز: أبو الحسن، علي بن سهل بن المغيرة، البزار، النسائي الأصل ثم البغدادي المعروف بـ (العفاني)، لملازمته (عفان بن مسلم)، وتوفي سنة إحدى وسبعين ومائتين من الحادية عشرة، ثقة.
وقال الحافظ في «التهذيب» (٧/ ٢٩٠ / ٥٥٤): وقد جمعهما مسلمة بن قاسم في كتاب «الصلة» فقال: «علي بن سهل بن المغيرة النسوي كان وراق عفان بن مسلم، أصله من خراسان، نزل الرملة فمات بها، سنة (٦١)، وكان ثقة صدوقًا، وإنما ذكرت هذا، وإن كان الصواب خلافه، لزيادة التوثيق، ولمتابعة أبي القاسم على تاريخ وفاة الرملي». انتهى كلام الحافظ - رحمه الله-.
روى عن: شبابة، ويزيد بن هارون.
روى عنه: محمد بن المنذر بن سعيد، وغيره.
وانظر: «الجرح والتعديل» (٦/ ١٨٩ / ١٠٣٨)، و «الثقات» (٨/ ٤٧٣ / ١٤٤٩٨) و «تاريخ بغداد» (١١/ ٤٢٩ / ٦٣١٩) و «تهذيب الكمال» (٢٠/ ٤٥٦ / ٤٠٧٨)، و «سير أعلام النبلاء» (١٣/ ١٥٩ / ٩٢)، و «تهذيب التهذيب» (٧/ ٢٩٠ / ٥٥٤)، و «تقريب التهذيب» (١/ ٤٠٢ / ٤٧٤٢)، و «المقصد الأرشد» (٢/ ٢٢٦ / ٧١٩).
٨١ - تمييز: علي بن سهل، المدائني، من الحادية عشرة، صدوق.
روى عن: شبابة بن سوار.
روى عنه: محمد بن جرير الطبري في غير كتبه.
وانظر: «تاريخ بغداد» (١١/ ٤٢٩ / ٦٣١٨)، و «تهذيب الكمال» (٢٠/ ٤٥٨ / ٤٠٧٩)، و «تهذيب التهذيب» (٧/ ٢٩٠ / ٥٥٥)، و «تقريب التهذيب» (١/ ٤٠٢ / ٤٧٤٣).
٢٠٩ - أبو الحسن البزاز (٣٠٥٩)',NULL,'363',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (366,'(النسائي)
(طب: ٤ ر / ٢ ش)
(تس: ٣ ر / ٢ ش)، و (تهـ: ١ ر / ١ ش)
أبو الحسن، علي بن شعيب بن عدي بن همام، السمسار، البزاز – بزاي معجمة في آخره – البغدادي، فارسي الأصل: توفي سنة ثلاث وخمسين ومائتين، من الحادية عشرة، ثقة.
روى عن: محمد بن إسماعيل بن أبي كديك – والصحيح فديك - (تس ٢٩٠٣١)، ومعن بن عيسى القزاز (تس ٣٠٥٩ و ٣٠٤٠٧)، و (تهـ ١٨٠٣)، و (س)، وأحمد بن إسحاق «صحيح ابن خزيمة» (٤/ ٢٨٥ / ٢٨٩٤)، وعبد الرحمن بن قيس «ميزان الاعتدال» (٤/ ٣٠٩ / ٤٩٤٩).
روى عنه: أحمد بن يحيى بن زهير «صحيح ابن حبان» (٨/ ١١٣ / ٣٣١٩)، وأبو بكر محمد بن إسحاق بن خزيمة «صحيح أبو خزيمة» (٤/ ٢٨٥ / ٢٨٩٤).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، جليلاً، كثير الحديث.
وقد أغفله الشيخان أحمد ومحمود شاكر في تحقيقهما «لتفسير الطبري».
وتصحف اسم شيخه في الأثر رقم (تس ٢٩٠٣١) (محمد بن إسماعيل بن أبي كديك) والصحيح (محمد بن إسماعيل بن أبي فديك).
وانظر: «الثقات» (٨/ ٤٧٥ / ١٤٥١٢)، و «تاريخ بغداد» (١١/ ٤٣٥ / ٦٣٣١) و «تهذيب الكمال» (٢٠/ ٤٦٠ / ٤٠٨١)، «الكاشف» (٢/ ٤١ / ٣٩٢٥)، و «المقتنى» (١/ ١٨٢ / ١٥٢٣)، و «تهذيب التهذيب» (٧/ ٢٩١ / ٥٥٨)، و «تقريب التهذيب» (١/ ٤٠٢ / ٤٧٤٥)، و «نزهة الألباب» (١/ ٣٧٣ / ١٥٣٨).
٢١٠ - – علي المحاربي (٤٤٨٩)
(طب: ١٦ ر / ٣ ش)
(تس: ١٥ ر / ش)، و (تهـ: ١ ر / ١ ش)',NULL,'364',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (367,'علي بن عبد الأعلى، المحاربي: من العاشرة، وثقه ابن حبان.
روى عن: عبد الرحمن بن محمد المحاربي (تس ٤٤٨٩ و ٤٦٧٣ و ٤٨٠١ و ٤٨٠٣ و ٤٨٠٨ و ١١٥٢٨ و ٢٦٥٣٨ و ٢٩٩٩٨ و ٣٤٢٣١ و ٣٤٢٤٨ و ٣٤٢٦٢ و ٣٤٢٦٩ و ٣٤٢٧٤ و ٣٤٢٨٦)، ومحمد بن مروان (تس ٢٦٥٣٨)، والمطلب بن زياد (تهـ ٨٠٧)، وسفيان بن عيينة «شرح معاني الآثار» (٤/ ٣٧٩).
روى عنه: [محمد بن عبد الله] الحضرمي «الثقات» (٨/ ٤٧٢ / ١٤٤٨٧)، وأبو جعفر الطحاوي «شرح معاني الآثار» (٤/ ٣٧٩).
التعديل والتجريح: ذكره ابن حبان في «الثقات» (٨/ ٤٧٢ / ١٤٤٨٧).
وذكره الشيخ شاكر في تحقيقه «لتفسير الطبري» (٤/ ٤٥٩ / ٤٤٨٥)، وقال: «شيخ الطبري ولم أجد في شيوخه: (علي بن عبد الأعلى) .. ».
٢١١ - أبو الحسن المخزومي (تهـ: ١٢٩٥)
(النسائي في اليوم والليلة)
(طب: تهـ: ١ ر / ١ ش)
أبو الحسن، علي بن عبد الرحمن بن محمد بن المغيرة بن نشيط، المخزومي، مولاهم، الكوفي، ثم المصري لقبه (علان) – بفتح المهملة وتشديد اللام – ابن أخي عبد الله بن محمد بن المغيرة مولى جعدة بن هبيرة بن أبي وهب المخزومي: توفي يوم الخميس لعشر خلون – وقيل: لثمان خلون من شعبان – سنة اثنتين وسبعين ومائتين، من الحادية عشرة، صدوق.
روى عن: أبي سعيد محمد بن سعيد التغلبي (تهـ: ١٢٩٥).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: قال عبد الرحمن بن أبي حاتم: «صدوق».
قال الحافظ في «التهذيب» مستدركًا على المزي: «قال المزي: لم يذكره ابن يونس في «تاريخ مصر» ولا الغرباء، قلت: كأنه سقط من نسخة الشيخ!! وإلا فقد ذكره ابن يونس في',NULL,'365',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (368,'«تاريخ مصر» بما نصه علي بن عبد الرحمن بن محمد بن المغيرة بن نشيط، يكنى أبا الحسن، ولد بمصر، وكتب الحديث، وحدث، وكان ثقة حسن الحديث، توفي بمصر»، ثم قال: «وذكره ابن حبان في الثقات».
قلت: ولم أقف له على ذكر في نسختي من «الثقات».
وانظر: «الجرح والتعديل» (٦/ ١٩٥ / ١٠٧١)، و «تهذيب الكمال» (٢١/ ٥١ / ٤١٠١)، و «تهذيب التهذيب» (٧/ ٣١٥ / ٥٨١)، و «تقريب التهذيب» (١/ ٤٠٣ / ٤٧٦٥).
٢١٢ - علي الدَّهان (٣٧٥٠٣)
(طب: (تس: ١ ر / ١ ش)
علي بن عبد الله بن صالح، الدهان، الكوفي، من العاشرة، لم أعرفه، ولم أجد له ترجمة، ولم يتعرض الشيخ التركي في تحقيقه «لتفسير الطبري» (٢٤/ ٤٨٥)، لترجمته بشيء.
روى عن: أبي جميلة المفضل بن صالح الأسدي (تس ٣٧٥٠٣)، و (تق ٩١٣).
روى عنه: حاجب بن مالك «الكامل» (٦/ ٤١١ / ١٨٩٣)، والحسين بن إسحاق «المعجم الكبير» (١/ ١٨٧ / ٤٩٣).
التعديل والتجريح: لم أقف له على تعديل أو تجريح، وقد أغفله الشيخان أحمد ومحمود شاكر في تحقيقهما «لتفسير الطبري» ولم يذكراه.
٢١٣ - على الكراجِكي (٢١٧٣)
(الترمذي، وابن خزيمة)
(طب: ر / ٤ ش)
(تس: ٤ ر / ٣ ش)، و (تهـ: ٣ ر / ٢ ش)، و (تق ١ ر / ١ ش)
علي بن عيسى بن يزيد، الكراجكي – ويقال: الكراشكي – البغدادي، البزار: توفي سنة سبع وأربعين ومائتين، من الحادية عشرة، مقبول.',NULL,'366',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (369,'روى عن: سعيد بن سليمان (تس ٢١٧٣ و ٣٧٨٩٥)، و (تهـ: ٢٤٧٤)، وشبابة بن سوار (تس ١٧٦٣١)، و (ت)، وعبيد الله بن محمد العيشي (تس ١٧٥٨٥)، و (تق ٦٦٧)، و (ت)، ومحمد بن الصباح (تهـ: ١٨٩٥ و ٢٧٩٥)، وأبيه عيسى بن يزيد «لسان الميزان» (٤/ ٢٤٧ / ٦٧١)، ومحمد بن الحسين «شعب الإيمان» (٤/ ٢٧٢ / ٥٠٧٠).
روى عنه: أبو عثمان سعيد بن عثمان الحناط «شعب الإيمان» (٤/ ٢٧٢ / ٥٠٧٠)، ومحمد بن أحمد القاضي «ميزان الاعتدال» (٧/ ١٧٧ / ٩٥١٤).
التعديل والتجريح: قال العقيلي في الضعفاء: «لا يُتابع على حديثه».
وقال أبو بكر الخطيب: «ما علمت من حاله إلا خيرًا».
وتصحف اسم شيخه في الأثر (تق ٦٦٧) من (عبيد الله) مصغر، إلى (عبد الله) مكبر.
وانظر: «ضعفاء العقيلي» (٣/ ٢٤٣ / ١٢٤١)، و «تاريخ بغداد» (١٢/ ١٢ / ٦٣٧٣)، و «تهذيب الكمال» (٢١/ ٨٧ / ٤١١٧)، و «الكاشف» (٢/ ٤٥ / ٣٩٥٢)، و «ميزان الاعتدال» (٥/ ١٧٩ / ٥٩٠٧)، و «تهذيب التهذيب» (٧/ ٣٢٣ / ٥٩٩)، و «تقريب التهذيب» (١/ ٤٠٤ / ٤٧٨٠)، و «لسان الميزان» (٤/ ٢٤٧ / ٦٧١)، و «رجال تفسير الطبري» (٤٠٥/ ١٨٨٣).
٢١٤ - أبو الحسن الطوسي (٤١٧٣)
البخاري: ٧ ر، وأبو داود، والنسائي)
(طب: ٢٩ ر / ١١ ش)
(تس ٩ ر ٤ ش) و (تخ ٤ ر / ٣ ش)، و (تهـ ١٣ ر / ٩ ش)، و (تق ٣ ر / ٣ ش)
أبو الحسن، علي بن مسلم بن سعيد، الطوسي، نزيل بغداد، مولده سنة ستين ومائة، وتوفي سنة ثلاث وخمسين ومائتين، وله ثلاث وتسعون سنة، من العاشرة، ثقة.
روى عن: حبان بن هلال (تخ ٢/ ٧٠٠ و ٣/ ١٠٣)، و (خ)، وسيار بن حاتم (تهـ ٢٨١١)، و (س)، وأبي عاصم الضحاك بن مخلد النبيل (تس ٤١٧٣ و ٧٤٧٠)، و (تهـ ٦٣ و ٥٦٠).',NULL,'367',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (370,'و (تق ٨٩٥)، وعباد بن عباد (تهـ ٢٣٩٨)، و (تخ ٢/ ٢٣٤)، و (س)، وعباد بن العوام (تس ٥٤٣٦ و ٢٨٦٧٦ و ٣٦٤٢٧)، و (تهـ ٢٨١٥)، و (د) وعبد الصمد بن عبد الوارث (تس ٥٧٩٠ و ١١٥٢١ و ١٥٨١٣) و (تهـ ٨٠٥ و ١٨٦٠)، و (، د، س)، وعلي بن الأزهر – وقيل: ابن أبي الأزهر – الأهوازي، (تهـ ٢٨١٢)، ومحمد بن إسماعيل بن أبي فديك (تس ٩٨٨١)، و (تق ٧٨٠)، و (تخ ٢/ ٣٤٩)، ومحمد بن أبي عبيدة بن معن المسعودي (تهـ ١٨٧١)، ويوسف بن يعقوب بن الماجشون (تهـ ٢٥٠٢) و (س)، وأبي داود الطيالسي (تهـ ٢٥٠ و ٧٨٠ و ٢٧٥٧)، و (تق ٨٩٥)، و (د)، وأبي النعمان – غير مسمى، ولا منسوب – (تهـ ١٩٨١)، وروح بن عبادة «تاريخ بغداد» (١٢/ ١٠٨ / ٦٥٤٨)، وخالد بن مخلد «المستدرك» (١/ ٦٣٦ / ١٧٠٦)، وعبد المجيد بن أبي رواد «مسند البزار» (٤/ ٣٣١ / ١٥٢٥)، وأبي يوسف القاضي «سنن البيهقي» (٣/ ٣١٤ / ٦٠٦٨).
روى عنه: أحمد بن يحيى بن زهير «صحيح ابن حبان» (٨/ ١٦٨ / ٣٣٧٥)، وعبد الله بن علي ابن الجارود «منتقى ابن الجارود» (٢/ ٥٥٩ / ٨٣٤)، وأبو الحسن علي بن الجعد الجوهري «مسند ابن الجعد» (١/ ٢١٧ / ١٤٤٠)، وعلي بن سعيد الرازي «المعجم الأوسط» (٤/ ١٥٦ / ٣٨٥٦)، وأبو طالب الكاتب علي بن محمد بن الجهم «سنن الدارقطني» (٢/ ١٧٦ / ٢٠)، وأبو بكر محمد بن إسحاق بن خزيمة «صحيح ابن حبان» (٦/ ٣١١ / ٢٥٧٣)، ومحمد بن الحسن بن مكرم البزاز «صحيح ابن حبان» (١١/ ٥١٧ / ٥١١٥)، وموسى بن هارون «المعجم الكبير» (٥/ ١٥٢ / ٤٩١٦)، وأبو بكر الإسماعيلي «معجم شيوخ أبي بكر الإسماعيلي» (٢/ ٦٨٠)، وأبو بكر البزار «مسند البزار» (٤/ ٣٣١ / ١٥٢٥).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، ليس به بأس.
وقد وقع في الأثر (تس ٤١٧٣) من طبعة دار الكتب العلمية تصحيف، هو (حدثنا أبو عاصم وابن عجلان) وهو خطأ. والصحيح: (حدثنا أبو عاصم) عن (ابن عجلان) كما هو في غيرها من الطبعات.
وذكر الأثر بعينه ابن كثير في «تفسيره» (١/ ٢٥٧) من طريق الطبري عن ابن عجلان.',NULL,'368',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (371,'وانظر: «الكني والأسماء» (١/ ٢٣٠ / ٧٦٦)، و «الجرح التعديل» (٦/ ٢٠٣ / ١١١٥)، و «الثقات» (٨/ ٤٧٣ / ١٤٤٩٦)، و «من روى عنهم البخاري في الصحيح» (١/ ١٥٧ / ١٤٩)، و «تسمية من أخرجهم البخاري ومسلم» (١/ ١٨٦ / ١١٧٣)، و «رجال صحيح البخاري» (٢/ ٥٣٣ / ٨٣٢)، و «الإرشاد» (٢/ ٦٠٣ و ٣/ ٨٦٤)، و «كتاب الدعاء» (١/ ٥٠٧)، و «تاريخ بغداد» (١٢/ ١٠٨ / ٦٥٤٨)، و «التعديل والتجريح» (٣/ ٩٦١ / ١٠٧٥)، و «تهذيب الكمال» (٢١/ ١٣٢ / ٤١٣٦)، و «الكاشف» (٢/ ٤٧ / ٣٩٦٦)، و «المقتنى» (١/ ١٨٢ / ١٥١٩)، و «السير» (١١/ ٥٢٥ / ١٤٨)، و «طبقات المحدثين» (١/ ٨٧ / ٩٦٢)، و «تهذيب التهذيب» (٧/ ٣٣٤ / ٦٢٣)، و «تقريب التهذيب» (١/ ٤٠٥ / ٤٧٩٩)، و «رجال تفسير الطبري» (٤٠٥/ ١٨٨٦).
٢١٥ - أبو الحسن الجهضمي (١٥٧٣١)
(مسلم، وأبو داود، والترمذي، والنسائي)
(طب: ٣ ر / ١ ش)
(تس: ١ ر / ١ ش)، و (تخ: ٢ ر / ١ ش)
أبو الحسن، علي بن نصر بن علي بن نصر بن علي، الجهضمي، البصري، الصغير، ابن الحفيد، توفي لأيام بقين من شعبان، سنة خمسين ومائتين، بعد أبيه بنحو خمسة أشهر، من الحادية عشرة، ثقة، حافظ.
روى عن: عبد الصمد بن عبد الوارث (تس ١٥٧٣٧) و (تخ ١/ ٥٦٨ و ٢/ ١٦٥) و (د، ت) وملازم بن عمرو «صحيح ابن حبان» (٣/ ٤٠٢ / ١١١٩)، ونوح بن قيس «صحيح ابن حبان» (٤/ ٣٦ / ١٢٣٥)، ويزيد بن زريع «صحيح ابن حبان» (٥/ ٥٥٤ / ٢١٨٠).
روى عنه: بكر بن أحمد بن سعيد الطاحي العابد «صحيح ابن حبان» (٢/ ٢٧٩ / ٥٢١)، وعلي بن أحمد بن عمران الجرجاني «صحيح ابن حبان» (٤/ ٢٩٥ / ١٤٤٧)، وأبو بكر محمد بن إسحاق بن خزيمة «صحيح ابن خزيمة» (٢/ ١٦٥ / ١١١٧)، ومحمد بن زهير «صحيح ابن حبان» (٥/ ١٥٥٤ / ٢١٨٠).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً،',NULL,'369',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (372,'صدوقًا، حافظًا صاحب حديث.
وأطنب أبو حاتم في ذكره والثناء عليه.
وقال أبو زرعة: «أرجو أن يكون خلفًا».
وانظر: «التاريخ الكبير» (٦/ ٢٩٩ / ٢٤٦٤)، و «التاريخ الصغير» (٢/ ٣٩١ / ٢٩٨٠)، و «الجرح والتعديل» (٦/ ٢٠٧ / ١١٣٤)، و «الثقات» (٨/ ٤٧١ / ١٤٤٨٣)، و «رجال مسلم» (٢/ ٦٠ / ١١٤٩) و «تهذيب الكمال» (٢١/ ١٥٩ / ٤١٤٥) و «التذكرة» (٢/ ٥٤١ / ٥٦٠)، و «السير» (١٢/ ١٣٨ / ٥٠)، و «الكاشف» (٢/ ٤٨ / ٣٩٧٥)، و «تهذيب التهذيب» (٧/ ٣٤١ - ٦٣٢)، و «تقريب التهذيب» (١/ ٤٠٦ / ٤٨٠٨)، و «طبقات الحفاظ» (١/ ٢٤١ / ٥٣٧)، و «تسمية من أخرجهم البخاري ومسلم» (١/ ١٨٤ / ١١٦٠)، و «رجال تفسير الطبري» (٤٠٦/ ١٨٨٩).
٨٢ - تمييز: أبو الحسن علي بن نصر بن علي، الأزدي، الجهضمي، البصري، الكبير، توفي سنة سبع وثمانين ومائة، من التاسعة، ثقة صدوق، أخرج له الستة، وروى عن شعبة، وعنه ابنه نصر.
وانظر: «التاريخ الكبير» (٦/ ٢٩٩ / ٢٤٦٣)، و «التاريخ الصغير» (٢/ ٢٤٢ / ٢٤٥٤)، و «الكنى والأسماء» (١/ ٢٣٠ / ٧٦٣)، و «الجرح التعديل» (٦/ ٢٠٧ / ١١٣٣)، و «الثقات» (٨/ ٤٦٠ / ١٤٤٢٨)، و «ذكر أسماء التابعين» (٢/ ١٧٣ / ٨٤٦)، و «مولد العلماء ووفياتهم» (١/ ٤٢٢)، و «رجال مسلم» (٢/ ٥٩ / ١١٤٨)، و «تسمية من أخرجهم البخاري ومسلم» (١/ ١٨٤ / ١١٦١)، و «التعديل والتجريح» (٣/ ٩٦٢ / ١٠٧٦)، و «رجال البخاري» (٢/ ٥٣٤ / ٨٣٣)، و «تهذيب الكمال» (٢١/ ١٥٩ / ٤١٤٤)، و «السير» (١٢/ ١٣٨ / ٤٩)، و «الكاشف» (٢/ ٤٨ / ٣٩٧٤)، و «المقتنى» (١/ ١٨١ / ١٥٠٨)، و «تهذيب التهذيب» (٧/ ٣٤١ / ٦٣١)، و «تقريب التهذيب» (١/ ٤٠٦ / ٤٨٠٧).
٢١٦ - أبو الفضل التمار (١٠١٦٧)
(النسائي، وابن ماجه – وقيل: أبو داود – أيضًا)
(طب: تس: ١ ر ١ ش)',NULL,'370',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (373,'أبو الفضل، - أو: أبو إسماعيل – عمار بن خالد بن يزيد بن دينار، الواسطي، التمار: توفي سنة ستين ومائتين، من صغار العاشرة، ثقة.
روى عن: يحيى بن سعيد القطان (تس ١٠١٦٧)، و (ق)، وعلي بن هشام «المستدرك» (٢/ ٢٢٢ / ٢٨١٨)، وابن أبي عدي «الجرح والتعديل» (٦/ ٣٩٥ / ٢٢٠١).
روى عنه: محمد بن إسحاق بن خزيمة «صحيح ابن خزيمة» (٤/ ١٤١ / ٢٥٤١).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً صدوقًا.
وقال الحافظ في «التهذيب»: «وذكره أبو علي الغساني في شيوخ أبي داود».
قلت: أغفله الشيخان أحمد ومحمود شاكر في تحقيقهما «لتفسير الطبري».
وانظر: «الجرح والتعديل» (٦/ ٣٩٥ / ٢٢٠١)، و «الثقات» (١٨/ ٥١٨ / ١٤٧٨٣)، و «تهذيب الكمال» (٢١/ ١٨٧ / ٤١٥٨)، و «الكاشف» (٢/ ٥٠ / ٣٩٨٧)، و «المقتنى» (٢/ ١٧ / ٥٠٦٠)، و «تهذيب التهذيب» (٧/ ٣٤٩ / ٦٤٧)، و «تقريب التهذيب» (١/ ٤٠٧ / ٤٨٢٠).
٢١٧ - – عمر الهمداني (١١/ ٢٨٦)
(الترمذي)
(طب: ٧ ر / ٥ ش)
(تس: ٥ ر / ٤ ش)، و (تخ: ١ ر / ١ ش)، و (تق ١ ر / ١ ش)
عمر بن إسماعيل بن مجالد، الهمداني، الكوفي، ثم البغدادي: من صغار العاشرة متروك.
روى عن: أبيه إسماعيل بن مجالد بن سعيد (تس ٢٨٦١١)، و (ت)، وعبد الله بن إدريس (تس ٣٧٣٠٩)، ومحمد بن ربيعة الكلابي (تس ٣٣٣٩٦)، و (تق ٩٤١)، ومطرف بن مازن قاضي اليمن (تس ٣٦٦٠٨ و ٣٦٦٥٥)، وأبي معاوية الضرير (تخ ٢/ ٥٦٦).',NULL,'371',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (374,'روى عنه: أبو بكر البزار «مسند البزار» (٤/ ٢٤٣ / ١٤١١)، وعبد الله بن أحمد «المعجم الكبير» (١٩/ ٣٥٨ / ٨٤٠)، وأبو العيناء «شعب الإيمان» (١/ ٢٣١ / ٢٤٢).
التعديل والتجريح: لم أقف له على تعديل يذكر، ولم يذكره أحد من أهل العلم بخير، وأغفله الشيخان أحمد ومحمود شاكر في تحقيقهما «لتفسير الطبري».
وانظر: «العلل ومعرفة الرجال» (٣/ ٩ / ٣٩٠٦)، و «الضعفاء والمتروكين للنسائي» (١/ ٨٢ / ٤٦٦)، و «الجرح والتعديل» (٦/ ٩٩ / ٥١٤)، و «سؤالات البرذعي» (١/ ٥٢٠) و «المجروحين» (٢/ ٩٢ / ٦٥٧)، و «ضعفاء العقيلي» (٣/ ١٤٩ / ١١٣٤)، و «الكامل» (٥/ ٦٧ / ١٢٤٤)، و «الضعفاء والمتروكين لابن الجوزي» (٢/ ٢٠٥ / ٢٤٣٩)، و «تاريخ بغداد» (١١/ ٢٠٣ / ٥٩٠٨)، و «تهذيب الكمال» (٢١/ ٢٧٤ / ٤٢٠٣)، و «ميزان الاعتدال» (٥/ ٢١٩ / ٦٠٦١)، و «الكاشف» (٢/ ٥٥ / ٤٠٢٥)، و «المغني في الضعفاء» (٢/ ٤٦٢ / ٤٢٣)، و «تهذيب التهذيب» (٧/ ٣٧٤ / ٦٩٨)، و «تقريب التهذيب» (١/ ٤١٠ / ٤٨٦٦)، و «لسان الميزان» (٧/ ٣١٦ / ٤١٦٥)، و «الكشف الحثيث» (١/ ١٩٣ / ٥٣٩)، و «مجمع الزوائد» (٩/ ٢٧٥).
٢١٨ - أبو زيد النحوي (٦٤٥).
(ابن ماجه)
(طب: ١٠٢ ر / ٣٧ ش)
(تس ٧ ر / ٧ ش) و (تخ ٩١ ر / ٢٩ ش)، و (تهـ ٣ ر / ٣ ش)، و (تق ١ ر / ١ ش)
أبو زيد، عمر بن شَبَّة بن عبيدة بن زيد، النميري، النحوي، الأخباري، البصري، نزيل بغداد: مولده سنة ثلاث وسبعين ومائة، وتوفي سنة اثنتين وستين ومائتين، وعاش تسعًا وثمانين سنة، من كبار الحادية عشرة، صدوق له تصانيف.
روى عن: أحمد بن معاوية (تخ ٤/ ٢٦٢)، وأيوب بن عمر بن أبي عمرو (تخ ٤/ ١٩٦)، وحاتم بن قبيصة (تخ ٣/ ٢١٦)، وخلاد بن يزيد الأرقط (تخ ٣/ ٥٩١)، وزهير بن حرب (تخ ٣/ ٣٦٤ و ٣٦٦ و ٣٤٢ و ٤٢٥)، وسعيد بن إبراهيم (تخ ٤/ ٥٦١)، وسعيد بن نوح الضبعي (تخ ٤/ ٤٦٢)، وسعيد بن هريم (تخ ٤/ ٥٨٨)، وسهاد بن عباد (تس ٩٦٣٧)، وأبي عاصم، الضحاك بن مخلد',NULL,'372',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (375,'(تس ٢٨٥٩٧)، (تهـ ٢٠٨٤)، وعبد الله بن المغيرة بن عطية (تخ ٣/ ٥٨٦)، وأبي الحسن علي بن محمد بن عبد الله المدائني (تخ ٢/ ٢٥٢ و ٢٦٣ و ٣٠٧ و ٣٢٩ و ٣٣١ و ٤٣٩ و ٤٤٠ و ٥٦٣ و ٥٧٥ و ٥٧٦ و ٥٨٠ و ٦٠٧ و ٦٠٨ و ٦٨٣ و ٦٩٧ و ٧٠٠، و ٣/ ٧ و٨ و٩ و ١٨ و ٢١ و ٢٢ و ٣٥ و ٣٦ و ٣٩ و ٤٢ و ٤٦ و ٤٧ و ٦١ و ٧٠ و ٧١ و ١٣٦ و ١٤٢ و ١٦٩ و ١٧١ و ١٧٧ و ١٩٩ و ٢٠٠ و ٢٠٧ و ٢١٠ و ٢٤٠ و ٢٧٧ و ٣٨٦ و ٤٨٣ و ٤٩٦ و ٥١٧ و ١٨ و ٥٢١ و ٥٢٤ و ٦٢٠، و ٤/ ٣٤ و ٤٤ و ٤٩ و ١١٠ و ١١١)، وعيسى بن عبد الله بن محمد بن عمر بن أبي طالب (تخ ٤/ ٣٤٤ و ٤٣٧)، وقبيصة بن عقبة السوائي (تس ٦٣٠٧)، وأبي غسان مالك بن إسماعيل النهدي (تس ٤٩٢٩)، ومحمد بن أحمد الهاشمي (تخ ٥/ ١٠٦)، ومحمد بن إسماعيل [بن أبي فديك] (تخت ٤)، ومحمد بن عبد الوهاب المهلبي (تخ ٤/ ٥٣٣)، ومحمد بن عمران العباسي مولاهم (تخ ٤/ ٥١٥)، ومحمد بن معروف بن سويد (تخ ٤/ ٤٥٧)، ومحمد بن يحيى (تخ ٣/ ٣٦٢، و ٤/ ٤١٠ و ٤/ ٤٣٣ و ٤/ ٤٥٤)، ومخلد بن يحيى بن حاضر (تخ ٣/ ٥٢١)، ومسعود بن واصل (تهـ ٩٣١)، و (ق)، وموسى بن إسماعيل (تس ٢٢٦٣٨)، و (تخ ٣/ ٣٦٤)، وهارون بن مسلم (تخ ٣/ ٢٨١)، والوليد بن مسلم (تس ٣٤٦٨٨)، ووهب بن جرير (تهـ ٢٠٨٨)، ويحيى بن سعيد القطان (تس ٦٤٥)، و (تخ ١/ ٦٣)، ويزيد الباهلي (تخ ٣/ ١٩٤)، ويعقوب بن إسحاق الحضرمي (تق ٩٤٦)، وأبي سلمة الغفاري (تخ ٤/ ٥٨٨)، وأبي عبيدة [معمر بن المثنى] (تخ ٣/ ٢١٠، ٥٧٢)، وأبي الهذيل العلاف (تخ ٤/ ٥٣٧)، وابن عائشة (تخ ٣/ ٦٥٣)، ومعلقات عن علي بن أبي طالب – رضي الله عنه – (تخ ٣/ ١٥١)، ومعلقات عن معاوية بن أبي سفيان – ري الله عنه – (تخ ٣/ ١٩٥)، وجماعة من المبهمين (تخ٣/ ١٥٤)، وأزهر [السمان] «تاريخ بغداد» (١٢/ ٥٢ / ٦٤٣٣)، وحرمي بن عمارة «تهذيب الكمال» (٢٣/ ٢٣٨ / ٤٧٤١)، والحسين بن حسن بن عطية «تاريخ بغداد» (٨/ ٢٩ / ٤٠٧٩)، وسعيد بن أوس الأنصاري «تاريخ بغداد» (٩/ ٧٧ / ٤٦٦٠)، وعبد الله بن محمد التوزي «تهذيب الكمال» (١٤/ ٣٠٥ / ٣١٦٣)، وعبيد بن واقد القيسي «تهذيب الكمال» (١٩/ ٢٤٥ / ٣٧٤٣)، وعلي بن محمد المدائني «تهذيب الكمال» (٢/ ٤٣٨ / ٣٦٢)، ومخشي بن معاوية «تاريخ بغداد» (١٢/ ١١٣ / ٦٥٥٢)، ومروان بن الحكم الأسدي «الثقات» (٩/ ٢٩٣ / ١٦٥٠٧)، وأبي عبيدة معمر بن المثنى «الجرح والتعديل» (٦/ ١١٦ / ٦٢٤).',NULL,'373',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (376,'روى عنه: حاجب بن أركين الفرغاني «الثقات» (٨/ ٤٤٦ / ١٤٣٤٧)، ومحمد بن سهل الكاتب «تاريخ بغداد» (١١/ ٢٠٨ / ٥٩١٤)، وأبو حاتم الرازي «الجرح والتعديل» (٦/ ١١٦ / ٦٢٤)، وأبو قاسم البغوي «تاريخ بغداد» (١١/ ٢٠٨ / ٥٩١٤).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، صدوقًا، حافظًا، حجةً، علاَّمةً، مستقيم الحديث، فقيهًا، ذكيًا، عالمًا بالسير، وأيام الناس، وله تصانيف كثيرة، وصاحبَ عربية، وشعرٍ، وأدبٍ، وأخبارٍ، ومعرفة بتاريخ الناس.
وانظر: «الجرح والتعديل» (٦/ ١١٦ / ٦٢٤)، و «الثقات» (٨/ ٤٤٦ / ١٤٣٤٧)، و «تاريخ بغداد» (١١/ ٢٠٨ / ٥٩١٢٤)، و «تهذيب الكمال» (٢١/ ٣٨٦ / ٤٢٥٥)، و «الكاشف» (٢/ ٦٣ / ٤٠٧١)، و «التذكرة» (٢/ ٥١٦ / ٥٣٣)، و «السير» (١٢/ ٣٦٩ / ١٥٨)، و «البداية والنهاية» (١١/ ٣٩)، و «تهذيب التهذيب» (٧/ ٤٠٤ / ٧٦٨)، و «تقريب التهذيب» (١/ ٤١٣ / ٤٩١٨)، و «رجال تفسير الطبري» (٢٠٩/ ٩٢١).
٢١٩ - أبو حفص الطائي (٦٦٥٣)
(النسائي)
(طب: (تس: ٢ ر / ١ ش)
أبو حفص عمر – وقيل: عمرو – ابن عبد الملك بن حكيم، الطائي، الحمصي: من الثانية عشرة مقبول.
روى عن: محمد بن عبيدة المددي اليماني (تس ٦٦٥٣ و ٣٧٤٧٦).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: تراوحت أقول أئمة الجرح والتعديل فيه بين كونه: صالحًا، مقبولاً، وفي ثبوت رواية النسائي عنه نظر.
ووجدت أن الطبراني قد أخرج في «المعجم الكبير» (١/ ٣٥٤ / ١٠٧٧، و ٢/ ٣١٨ / ١٣٢٣١)',NULL,'374',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (377,'و «المعجم الأوسط» (٣/ ٢١٠ / ٢٩٤١)، وغيرهما من المواضع، بهذين الإسنادين اللذين عند الطبري، ولكن من طريق (عمر بن عبد الملك الوصابي الحمصي)، ولعله تصحف لفظ (الوصابي) على بعض النساخ، أو الرواة، فصار (الطائي)، والله أعلم.
وانظر: «تهذيب الكمال» (٢١/ ٤٤٧ / ٤٢٧٩)، و «تهذيب التهذيب» (٧/ ٤٢١ / ٧٩٤)، و «تقريب التهذيب» (١/ ٤١٥ / ٤٩٤٢)، و «رجال تفسير الطبري» (٤١٤/ ١٩٣٣).
٢٢٠ - عمر بن المثني (٢٧٤٧)
(طب: تس: ١ ر / ١ ش)
عمر بن المثنى، وهو محرف من: محمد بن المثنى، بلا ريب، وستأتيك ترجمته بعد صفحات.
روى عن: عبد الوهاب [الثقفي] (تس ٢٧٤٧).
التعديل والتجريح: أغفله الشيخان شاكر في تحقيقهما «لتفسير الطبري».
وقد تحرف اسم شيخ الطبري في هذا الأثر من (محمد بن المثنى) إلى (عمر بن المثنى) الذي هو موضوع الترجمة.
وتصحف اسم الشيخ الذي روى عنه (عمر بن المثني) من (عبيد الله – مصغر-)، وهو (ابن عمر العمري)، إلى (عبد الله - مكبر-)، وهو دون أخيه صاحب الرواية.
وقد أخرج هذا الأثر البيهقي في «السنن الكبرى» (٤/ ٢٠٠ / ٧٦٨٧)، فقال: أخبرنا أبو عمرو، محمد بن عبد الله الأديب، أنبأ أبو بكر الإسماعيلي، أخبرني محمد بن محمد بن سليمان، ثنا وهب بن بقية، وأبو بكر بن أبي شيبة، قالا: ثنا عبد الوهاب الثقفي، عن عبيد الله بن عمر، عن نافع، عن ابن عمر، قال: نَسَخَت هذه الآية يعني: {فدية طعام مسكين}، هذه الآية التي بعدها {فمن شهد منكم الشهر فليصمه}. أخرجه البخاري في «الصحيح» انتهى.',NULL,'375',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (378,'قلت: وهو عند البخاري في «الصحيح» (٤/ ١٦٤٦ / ٤٢٣٦)، من طريق عياش بن الوليد، حدثنا عبد الأعلى، حدثنا عبيد الله، عن نافع، عن ابن عمر، رضي الله عنهما، أنه قرأ: {فدية طعام مسكين} قال: «هي منسوخة».
وقد صرح بما يؤكد هذا التصحيف وذاك التحريف الحافظ ابن حجر في «تغليق التعليق» (٣/ ١٨٤)، فقال ما نصه: «وقال ابن جرير: حدثنا محمد بن المثنى، ثنا عبد الوهاب، ثنا عبيد الله، عن نافع عن ابن عمر قال: نسخت هذه الآية، يعني: {وعلى الذين يطيقونه فدية طعام مسكين}، وانتهى.
ويوجد في الرواة من اسمه:
٨٣ - تمييز: عمر بن المثنى، الأشجعي، الرقي، من الثامنة، مستور وهو من طبقة شيوخ شيوخ الطبري، أخرج له ابن ماجه في «السنن».
روى عن: عطاء الخراساني، وأبي إسحاق السبيعي.
روى عنه: سلام بن سليمان المدائني، وعمر بن عبيد الطنافسي، والعلاء بن هلال الباهلي.
وانظر: «ضعفاء العقيلي» (٣/ ١٩٠ / ١١٨٥)، و «تهذيب الكمال» (٢١/ ٤٩٤ / ٤٣٠٠)، و «الكاشف» (٢/ ٦٨ / ٤١٠٥)، و «تهذيب التهذيب» (٧/ ٤٣٤ / ٨٢٠)، و «تقريب التهذيب» (١/ ٤١٦ / ٤٩٦٢)، و «لسان الميزان» (٤/ ٣٢٤ / ٩١٦ و ٧/ ٣٢٠ / ٤٣١٠).
٢٢١ - أبو موسى الكلاعي (١٤٩).
(النسائي)
(طب: ٣١ ر / ١٣ ش)
(تس ١٨ ر / ١٠ ش) و (تخ ١ ر / ١ ش) و (تهـ ١٠ ر / ٦ ش) و (تق ٢ ر / ٢ ش)
أبو موسى، عمران بن بكار بن راشد، الكلاعي، الحمصي، المؤذن، البراد، توفي',NULL,'376',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (379,'سنة إحدى وسبعين ومائتين، من الحادية عشرة، ثقة.
روى عن: أحمد بن خالد الوهبي (تهـ ٤٦١)، (س)، وبشر بن شعيب بن أبي حمزة (تهـ ١٠٣٧)، (س)، وأبي اليمان الحكم بن نافع (تس ٢٠٧٦)، و (تخ ١/ ١٢٩)، (س) وخطاب بن عثمان الفوزي (تس ٥٥١٤ و ٣٧٩١٦ و ٣٧٩١٨)، و (س)، وأبي روح الربيع بن روح اللاحوني (تس ١٠٧٣٥ و ٣١٢٠٨)، و (تهـ ١٨٣٢)، (س)، وعبد السلام بن محمد الحضرمي (تس ١٥١٣٤)، وعبد العزيز بن موسى اللاحوني (تس ١١٢١٤)، وأبي المغيرة عبد القدوس بن الحجاج الخولاني (تس ١١٥٥٢)، و (تهـ ١٠٣٦)، (س)، ومحمد بن المبارك الصوري (تس ٢٨٢٩٨)، و (س)، ويحيى بن صالح الوحاظي (تس ١٤٩ و ١٠٣٧٢ و ١٠٣٨٠ و ٢٣٤١٠ و ٢٤٧٧٩ و ٢٥٣٨٨)، و (تهـ ٢٩٩ و ٣٧٨ و ١٤٦٤ و ١٧٤١ و ٢١٦٢) و (تق ١٠٣)، ويحيى بن واضح (تس ٣٤١٧٣)، وهاشم بن عمرو «الثقات» (٩/ ٢٤٢ / ١٦٢٢٦).
روى عنه: صالح بن أبي الجن «الكامل» (٦/ ٢٢ / ١٥٧٠)، وعلي بن إبراهيم بن الهيثم «الكامل» (٧/ ١٧١ / ٢٠٧٩)، وأبو عبد الله، محمد بن أحمد بن عمرويه الصفار «تاريخ بغداد» (١/ ٣٢٦ / ٢٣١)، ومعاوية بن العباس الحمصي (الكامل) (٢/ ١٧٠ / ٣٥٧)، وموسى بن العباس «الكامل «٢/ ٢٣٥ / ٤١٣)، وأبو محمد، يحيى بن محمد بن صاعد «علل الدارقطني» (٢/ ٥٨ / ١٠٩).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: شيخًا، ثقةً، صدوقًا، محدثًا، حافظًا، مؤذنًا، لا بأس به.
قلت: روي الطبري في الأثرين رقم (تس ١٠٧٣٥ و ٣١٢٠٨) عن عمران بن بكار بن راشد الكلاعي عن (أبي روح) غير مسمى! وبعد التدقيق في أسماء شيوخه تبين لي أن (عمران) يروي عن شيخين لهما نفس الكنية، ونفس النسب (أبي روح، اللاحوني)!! أحدهما: ثقة، والآخر: صدوق.
أولهما: وهو الأرجح أبو روح، الربيع بن روح اللاحوني).',NULL,'377',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (380,'والثاني: هو (أبو روح، عبد العزيز بن موسى اللاحوني)، وقد أخرج عنه الأثر رقم (تس ١١٢١٤)، وقد سماه فيه، وبذلك لا ينزل حديثهما عن الصحة، والله أعلم.
وانظر: «الجرح والتعديل» (٦/ ٢٩٤ / ١٦٣٣)، و «تهذيب الكمال» (٢٢/ ٣١١ / ٤٤٨٢)، و «الكاشف» (٢/ ٩١ / ٤٢٥٧)، و «السير» (١٢/ ١٤٢ / ٧٣)، و «تهذيب التهذيب» (٨/ ١١٠ / ٢١٦)، و «تقريب التهذيب» (١/ ٤٢٩ / ٥١٤٦)، و «رجال تفسير الطبري» (٤١٨/ ١٩٥٢).
٢٢٢ - أبو عمرو الصفار (٢١٥٩)
(الترمذي، والنسائي، وابن ماجه)
(طب: ٤٨ ر / ١ ش)
(تس ٤٣ ر / ١ ش) و (تخ ١ ر / ١ ش) و (تهـ ١ ر / ١ ش) و (تق ٣ ر / ١ ش)
أبو عمرو، عمران بن موسى، الليثي، القزاز، الصفار، البصري: توفي بعد سنة أربعين ومائتين، من العاشرة، صدوق.
روى عن: عبد الوارث بن سعيد (تس: ٢١٥٩ و ٣٤٢٤ و ٤٢٧ و ٤٥٣٥ و ٤٦٨٤ و ٥٢٠٣ و ٥٦٦٨ و ٦٥٨٦ و ٦٥٨٨ و ٦٦٩٩ و ٦٨١٤ و ٧٣٩٤ و ٨٦٨٥ و ٩٦٦٣ و ١٠٣٦٧ و ١١٣٩٠ و ١٢٧٤٧ و ١٢٨٨٨ و ١٢٩١٣ و ١٢٩٤٥ و ١٢٩٦٩ و ١٣٦٩٥ و ١٣٧٣٣ و ١٣٩٢٠ و ١٥٣٥٠ و ١٦١١٣ و ٢٠٤١٣ و ٢١٣٢٤ و ٢١٥٢٥ و ٢٢٢٤٠ و ٢٢٢٦٠ و ٢٣٩٨٩ و ٢٥٧٨٣ و ٢٨٦٠٥ و ٢٩٠٦٤ و ٢٩١٤٦ و ٢٩٥٧١ و ٢٩٨٢٤ و ٣٣١٠٤ و ٣٦٠٥٠ و ٣٦٢٣٥ و ٣٧٣٠٠ و ٣٧٥٧٨)، و (تهـ: ١٩٧)، و (تق ٥١٢ و ٥٣٩ و ٥٨٣)، و (تخ: ١/ ٨٦) و (ت، س، ق).
روى عنه: إبراهيم بن أبي طالب «المستدرك» (٣/ ٥٧٣ / ٦١٢٦)، وأحمد بن سلمة بن عبد الله البزار «شعب الإيمان» (١/ ٣٣٨ / ٣٧٢)، وأبو بكر، أحمد بن عمرو البزار «مسند البزار» (١/ ٢٤٨ / ١٣٨)، والحسين بن محمد القباني «المستدرك» (٣/ ٥٧٣ / ٦١٢٦)، وزكريا بن يحيى الساجي «الكامل» (٦/ ٤٦٠ / ١٩٤٠)، وعبدان بن أحمد «المعجم الأوسط» (٥/ ٣٣ / ٤٥٩٦).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، صدوقًا، ولا بأس به.',NULL,'378',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (381,'قلت: وتصحف اسمه في الأثر (تس ٢١٥٢٥) إلى (عمرو)، وفي الأثر (تس ٣٦٢٣٥)، إلى (عمارة).
وانظر: «الجرح والتعديل» (٦/ ٣٠٥ / ١٦٩٧)، و «الثقات» (٨/ ٤٩٩ / ١٤٦٥٣)، و «تهذيب الكمال» (٢٢/ ٣٦٠ / ٤٥٠٦)، و «الكاشف» (٢/ ٩٥ / ٤٢٧٦)، و «تهذيب التهذيب» (٨/ ١٢٥ / ٢٤٥)، و «تقريب التهذيب» (١/ ٤٣٠ / ٥١٧٢)، و «رجال تفسير الطبري» (٤٢٠/ ١٩٦٣).
٢٢٣ - عمرو بن بيذق (٩٤٨٠)
(طب: (تس: ٣ ر / ١ ش)
عمرو بن بيذق، من التاسعة، فما دونها، لم أعرفه، ولم أجد له ترجمة، ولم يعرفه الشيخ شاكر قبلي (٧٤٧٩)، ولم يتعرض الشيخ التركي في تحقيقه «لتفسير الطبري» (٧/ ١٥) لترجمته بشيء، ولم أقف له في «التفسير» على غير هذا الأثر، ولعله محرف من: عمر بن بندق، وهو غير معروف أيضًا.
روى عن: مروان بن معاوية (تس ٩٤٨٠ و ٢٥٣٩١ و ٢٥٩٧٤).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: سقط هذا الأثر، وسقط معه خمسة وخمسون أثرًا آخر، من طبعة الشيخ شاكر، ولم يترجم له، ولم أقف له على ترجمة في كتب الجرح والتعديل، وتصحف اسم أبيه في الأثرين رقم (تس ٢٥٣٩١ و ٢٥٩٧٤)، من (بيذق) إلى، (بنقد)، ولم يذكر الشيخان شاكر (عمرو بن بندق)، ولعل (عمرو بن بيذق) وهذا هو نفسه (عمرو بن عبد الحميد، الآملي)، صاحب الترجمة رقم (٢٢٦) الآتية بعد حين، والله أعلم.
وشيخه هنا هو: أبو عبد الله، مروان بن معاوية بن الحارث بن أسماء الفزاري، الكوفي، نزيل مكة، ودمشق، توفي سنة ثلاث وتسعين ومائة، من الثامنة، ثقة حافظ، وكان يدلس أسماء الشيوخ، وقد ترجمت له في «المعجمين الكبير، والصغير».',NULL,'379',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (382,'٢٢٤ - عمرو بن سعيد (٢٥١١٩)
(طب: (تس: ٤ ر / ٢ ش)
عمرو بن سعيد بن يسار، القرشي، من العاشرة، لم أعرفه، ولم أجد له ترجمة، ولم يتعرض الشيخ التركي في تحقيقه «لتفسير الطبري» (١٦/ ٥٣٢)، لترجمته ولا للأثر الذي رواه، بشيء.
روى عن: [محمد بن عبد الله] الأنصاري (تس ٢٥١١٩ و ٣٧١٦٨)، وأبو قتيبة [سلم بن قتيبة الشعيري] (تس ٣١٠٠٢ و ٣٢٩٩٠).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: لم أقف له على ذكر، أو ترجمة في كتب الحديث والتراجم التي وقفت عليها.
وقد أغفله الشيخان أحمد ومحمود شاكر في تحقيقهما «لتفسير الطبري».
وتصحف اسم جده (يسار) في الأثر رقم (تف: ٣٢٩٩٠)، إلى (بشار).
وقد تتبعت أسماء (الأنصاريين)، والذين يمكن أن يكون روى عنهم، من خلال تتبعي لمن اسمه (أشعث)، ممن يروي عن (الحسن) فوجدتهم (أربعة)؛ جميعهم مترجمون في «المعجم الكبير لرواة ابن جرير»، وهم:
١ - أشعث بن إسحاق بن سعد، الأشعري، الكوفي، يروي عن الحسن؛ ولكن ليس فيمن يروي عنه (أنصاري)!! فهو بعيد، ولذلك استبعدته.
٢ - أشعث بن سوار، الكندي، النجار، الكوفي، يروي عن الحسن، ويروي عنه القاضي أبو يوسف يعقوب بن إبراهيم الأنصاري الكوفي - صاحب أبي حنيفة - وهو بعيد أيضًا، ولكنه أقرب من الذي قبله؛ وقد استبعدته أيضًا.
٣ - أشعث بن عبد الله بن جابر الحداني، أبو عبد الله البصري، يروي عن الحسن، ويروي عنه محمد بن عبد الله الأنصاري.',NULL,'380',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (383,'٤ - أشعث بن عبد الملك الحمراني، أبو هانئ البصري، يروي عن الحسن، ويروي عنه محمد بن عبد الله الأنصاري أيضاً.
ومن هذا البحث، والتتبع، والاستقراء، ترجح لديَّ أن (الأنصاري) هو محمد بن عبد الله بن المثنى، القاضي، البصري: وهو مترجم في " المعجمين الصغير والكبير ".
وهناك من شابهه في الاسم، أخرج عنه الطبري في عدة مواضع من كتبه في " التفسير " و " التاريخ " ولكنه متقدم في الطبقة عن هذا، وترجمت له في " المعجم الكبير " أيضاً. والله أعلم.
٢٢٥ - عمرو الآملي (٣٧٦٢)
(طب: ٤٩ ر / ٩ ش)
(تس ٤٢ ر / ٨ ش) و (تخ ١ ر / ١ ش) و (تهـ ٢ ر / ٢ ش) و (تق ٥ ر / ٢ ش)
عمرو بن عبد الحميد، الآملي، من العاشرة، لم أعرفه، ولم أجد له ترجمة، ولم يعرفه الشيخ شاكر قبلي (٣٧٥٩)، ولم يتعرض الشيخ التركي في تحقيقه لـ " تفسير الطبري " (٣/ ٥٠٠) لترجمته بشيء.
روى عن: جرير بن عبد الحميد الضبي (تس ١٩٩٩٤ و ٣٤١٤٤)، وأبي أسامة (حماد بن أسامة) (تس ١٩٧٣٩ و ٢٧٠٩٩ و ٣٦٠٥٩)، و (تخ ١/ ٢١٥)، و سفيان [الثوري] (تس ٣٧٦٢ و ٧٩٦٩ و ١٠٣٨٣ و ١٢٢٨٠ و ١٢٧٢٩ و ١٧٧٤٠ و ٢٤٦١٤)، وسفيان بن عيينة (تس ١٢٦٨٨)، و (تق ١٦٦ و ٦١٥ و ٧٨٩) وعبد الرحمن بن عثمان (تهـ ٦٥٢)، وعبد العزيز بن عبد الصمد العمي (تق ٩٠٣)، ومروان بن معاوية (تس ١٤٥٧٨ و ١٤٥٩٣ و ٢٥٩٦١ و ٢٥٩٦٤ و ٢٦٦٧١ و ٢٦٩٧١ و ٢٧٩٩٠ و ٢٨٧٢٨ و ٢٨٧٥٣ و ٢٨٧٥٦ و ٢٨٧٦٣ و ٢٨٨٩٧ و ٢٨٩٠٧ و ٢٨٩٦٥ و ٢٨٩٨٥ و ٢٩١٩٣ و ٢٩٢٩٨ و ٢٩٥٤٧ و ٢٩٦٣٠ و ٣١٦٤٠ و ٣١٦٥٤ و ٣٢٨٨٧ و ٣٢٨٨٨ و ٣٣١٢٠ و ٣٦٩٩٢ و ٣٨٠١٨)، و (تهـ ١٢١)، والمطلب بن زياد (تس ٣٥١١٧) وهارون - غير مكني، ولا منسوب - (تس ٢٩١٥١)، ويحيى بن زكريا (تس ٢٥٧٢).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: ذكره الشيخ شاكر في تحقيقه " لتفسير الطبري " (٤/ ١٦١ / ٣٧٥٩).',NULL,'381',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (384,'وقال: " شيخ الطبري: لم أعرف من هو؟ ولم أجد له ترجمة ".
وقد ذكره الشيخ علي رضا في تعليقه على " الجزء المفقود من تهذيب الآثار " (٠١/ ١٢٤ / ١٦٦)، وقال: " لم أقف له على ترجمة فيما بين يدي من كتب الرجال وقد ضبطه هكذا الإملي السمعاني في الأنساب (١/ ٣٤) " ثم ذكر كلام الشيخ شاكر وضبط نسبته بما لا يزيد هذا الشيخ إلا غموضاً.
قلت: أبهمه الطبري في الأثر رقم (تس ٢٢٧٣١)، فقال: حُدِّثتُ عن مروان، عن جويبر، عن الضحاك ... وساق الأثر.
وروى عنه عن (مروان بن معاوية) ولم ينسبه في الآثار (تس ٢٣٤٥٧ و ٢٥٥٧٨ و ٢٨٧٦٣)، ولقد تتبعت مرويات الطبري في " التفسير "، والتي أخرجها من طريق (جويبر)، وهي أكثر من (٤٠٠) رواية، يروي عنه (مروان بن معاوية)، منها نحو (١٨) رواية، كلها عن (عمرو بن عبد الحميد)، فتأكد لي من خلالها أن المبهم هو (عمرو بن عبد الحميد)، هذا.
وأخرج الطبري في " التاريخ " (تخ ١/ ٢١٥)، عنه، عن أبي أسامة، الأثر الذي في " التفسير " برقم (تس ١٩٧٣٩).
وأخرج له في " تهذيب الآثار " (تهـ: ١٢١)، عنه، عن مروان بن معاوية عن الضحاك، مرسلاً، وأخرجه أيضاً:
الديلمي في " الفردوس بمأثور الخطاب " (٢/ ١٢ / ٢٠٩٦)، عن ابن عباس به.
وأبو نعيم في " الحلية " (٤/ ٧٢)، عن ابن عباس أيضاً، من طريق وهب بن منبه، ولكن من غير طريق (عمرو بن عبد الحميد).
ولقد تتبعت أسماء من روى عن شيوخه التسعة السابقين، ممن اسمه (عمرو)، أو (عمر)، أو من نسبته (الآملي)، أو من اسم أبيه (عبد الحميد)، أو (بيذق)، أو (بندق)، لورود أثر برقم (تس ٩٤٨٠)، يروي فيه الطبري عن (عمرو بن بيذق)، عن مروان، وأثرين برقم (تس: ٢٥٣٩١ و ٢٥٩٧٤)، يروي فيهما الطبري عن (عمرو بن بندق)، عن مروان.',NULL,'382',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (385,'والظاهر أن (عمرو بن بيذق)، و (عمرو بن بندق)، اسمان (لشيخ واحد)، تصحف أحد الاسمين عن الآخر، ولا أظنهما إلا: (عمرو بن عبد الحميد) صاحب الترجمة، لتطابق الأسانيد، وعدم معرفة الشيخ، والله أعلم.
٢٢٦ - عمرو الزهري (تهـ: ٢٧٣٩)
(طب: تهـ: ٢ ر / ٢ ش)
عمرو بن عثمان بن عبد الرحمن بن محمد بن بكر بن عمر بن عبد الرحمن بن عوف الزهري – والصحيح: عمر بن عثمان بن عبد الرحمن الزهري – وفي " التهذيب ": عمر بن عثمان بن عبد الرحمن بن محمد بن بكر بن عمر بن عبد الرحمن بن عوف، الزهري، وهو اسم مشكل – وكذلك أشكل على الشيخ شاكر، في تحقيقه لـ " تهذيب الآثار " (٢/ ٦٦٤ / ٩٧٩ و ٢/ ٦٧٢ - ٦٧٣/ ٩٨٨)، وكذلك سائر الإسناد، ولعله أبو عثمان، عمرو بن محمد بن يحيى بن عثمان، القاضي، العثماني، المكي (٤٥)، وقد ترجمناه في " معجم الشيوخ " برقم (٢٣٠)، وسائر الإسناد، بينه وبين عبد الله مشكل، وباستقراء طرقه؛ ورواياته تبين لي أن الإسناد كتالي: حدثنا عمرو بن عثمان، عن عبد الرحمن، عن الزهري، أن أخاه عبد الله أخبره أن أنس بن مالك صاحب النبي صلى الله عليه وسلم أخبره .. إلخ. وعلى كل حال فالحديث صحيح من غير هذه الطريق، والحمد لله رب العالمين.
روى عن: إبراهيم بن المنذر الحزامي (تهـ ٢٧٣٩) وإسماعيل بن أبي أويس (تهـ ٢٧٤٨).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: لم أقف له على ذكر، ولا على ترجمة في كتب الرجال والحديث التي بين يدي، وقد بحثت عن أسماء آبائه وأجداده الذين ذكرهم الطبري في نسبه فوجدت أنه قد يشتبه بـ (أبي عثمان، عمرو بن محمد بن يحيى بن عثمان، القاضي، العثماني، المكي، والتي ستأتي ترجمته بعد حين والذي روى عن شيخي صاحب الترجمة',NULL,'383',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (386,'السابقين كما ستراها في ترجمة الأخير، في موضعها من كتابي هذا.
ولم أجد من آبائه من يمكن أن يشتبه به أنه هو، من قبيل اتصال كنيته باسمه أو وجود سقط، أو إضافات في أسمائهم بحيث يتوهم أنه هم.
وبهذا يكون هذا الشيخ من شيوخ الطبري مجهولاً كغيره كثيرين من شيوخه الذين سبق ذكرهم، والذين سيأتي ذكرهم في التراجم الآتية.
وأما شيخاه هنا فهما: من العاشرة، صدوقان، أولهما واقفي تكلم فيه أحمد، والثاني يخطئ، وقد ترجمت لهما في " المعجمين الكبير والصغير ".
وقد تصحف اسمه في الأثر رقم (تهـ: ٢٧٤٨) من (عمرو) إلى (عمر) ولم أجد لأحد من آبائه ترجمة إلا لجده التابعي:
٨٤ - تمييز أبو حفص، عمر بن عبد الرحمن بن عوف، الزهري، القرشي الحجازي، المدني، من الثالثة، مقبول.
روى عن: أبيه، وجماعة من أصحاب النبي صلى الله عليه وسلم.
روى عنه: ابناه، حفص، وعبد العزيز.
وانظر: " التاريخ الكبير " (٦/ ١٧١ / ٢٠٦٦)، و " الجرح والتعديل " (٦/ ١٢٠ / ٦٤٩)، و " الثقات " (٥/ ١٤٦ / ٤٢٩٢)، و " تهذيب الكمال " (٢١/ ٤٢٥ / ٤٢٧٣)، و " الكاشف " (٢/ ٦٥ / ٤٠٨٥)، و " تهذيب التهذيب " (٧/ ٤١٦ / ٧٨٧)، و " تقريب التهذيب " (١/ ٤١٥ / ٤٩٣٦).
٢٢٧ - أبو حفص الصيرفي الفلاس (٤١٢)
(خ: ٤٧ ر، وم: ٢ ر، والأربعة)
(طب: ١٨٤ ر / ٥٤ ش)
(تس ١٤١ ر / ٤٣ ش) و (تخ ٨ ر / ٨ ش) و (تهـ ٣١ ر / ٢٠ ش) و (تق ٦ ر / ٦ ش)
أبو حفص، عمرو بن علي بن بحر بن كنيز، الباهلي، البصري، الصيرفي الفلاس.',NULL,'384',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (387,'توفي سنة تسع وأربعين ومائتين، من العاشرة، ثقة، حافظ.
روى عن: أزهر بن سعد السمان (تهـ ٢١٦٥)، و (خ، س)، وأبي اليسع إسماعيل بن حماد بن أبي المغيرة الأشعري مولاهم (تس ١٨٣٣٦)، والحشرج بن عبد الله بن الحشرج المزني (تهـ ٢٤٤٦)، وحفص بن غياث (تس: ١٧٣٧٣)، وخالد بن الحارث (تس ٢٧١٤)، و (خ، س)، وخالد بن يزيد [العتكي] اللؤلؤي (تس ٥٥٢ و ٢٧٢٥ و ٢٧٢٠)، و (د)، وأبي سليمان رباح الرقاء (تس ٢٢٢٢١)، وزيد بن أخزم (تس ١٠٦١٣)، وسعيد بن عامر (تهـ ١٠٩٣)، وسفيان بن حبيب (تهـ ١٣٩٧)، وسفيان بن عيينة (تس ٣٥٨٤ و ٣٥٨٩ و ٣٧٣٤ و ١٢٦٤٩ و ١٩٠٣٧)، و (س)، وأبي قتيبة سلم بن قتيبة (تس ٥١٩٠ و ٨٩٢٧ و ١٢٩٠٨ و ٢٣٢٤٧ و ٣٠٥١٦)، و (تخ ٣/ ٢٧٥)، و (خ، ت، س) وأبي داود سليمان بن داود الطيالسي (تس ١٩٠٥ و ١٢٩٢٤ و ١٢٩٣١ و ١٢٩٤١ و ٢٦٦٣٩ و ٢٨٦١٢ و ٣٨١٣٠)، و (تهـ ٣٠٠٧)، و (تق ٩٦٩)، و (م، س)، وسهل بن يوسف (تس ٢٠٤٧٣)، وصفوان بن عيسى (تس ٢٨٠٤١)، و (د، س)، وأبي عاصم الضحاك بن مخلد (تس ٤١٢ و ٤١٨ و ٤٥٧ و ١٩٩٧ و ٢١٦٠ و ٣٥٨٨ و ٤٩٨٧ و ٤٩٩٤ و ٨٤٤٩ و ١٠٤٧٧ و ١٢٣٥٤ و ١٢٥٦٤ و ١٢٦١٢ و ١٢٦٢٩ و ١٤٠٢١ و ٢٠٤٦٦ و ٢٦٩٥٦ و ٢٩٥٠٠)، و (تهـ ٩٨٠)، و (تخ ١/ ١٦٠)، و (خ، ت، س)، وعامر بن إبراهيم الأصبهاني (تس ٣٨٠٠٤ و ٣٨٠١٥ و ٣٨٠١٧ و ٣٨٠٢٨)، و (س)، وعبد الله بن إدريس (تس ٤٩٩٤ و ٤٩٩٧ و ٥٠٢٥)، وعبد الله بن داود الخريبي (تس ٣٠٣٨١)، و (تهـ ١٣٩٩)، و (خ، ت، س)، وعبد الأعلى بن عبد الأعلى (تس ٥١٩١ و ٨٩٢٦ و ٢٩٨٠٥)، و (خ، م)، وعبد الرحمن بن مهدي (تس ٤٩٩٦ و ٥٠١٤ و ٥٠١٥ و ٥٠١٦ و ٥٠١٨ و ٥٠٢٣ و ٥٠٢٩ و ٥٠٣٦ و ٥١٨٣ و ٨٠٧٤ و ١٠٤٨٨ و ١٣٩٦٩ و ١٣٩٩٨ و ١٤٠٢٨ و ١٧٦٤٠ و ٢٨٤٢٧) و (تهـ ٦٠٩ و ١٧١٧ و ١٧٧٩ و ٢٥٢٨ و ٢٧٢٩ و ٢٩٣٣ و ٢٩٤٢) و (تخ ٢/ ٢٣)، و (خ، م، س)، وعبد الرحمن بن عثمان (تس ١٢٩٢٥)، وأبي عبد الصمد [عبد العزيز بن عبد الصمد العمي] (تس ٢٨٦٥٤)، و (تهـ ١٠٤٢)، و (تق ٩٠٣)، و (س)، وعبد الوهاب بن عبد المجيد الثقفي (تس ١٣٩٨٨ و ١٩٧٢٩ و ١٩٩٢٣) و (تخ ١/ ٢١٨) و (خ، س)، وعثمان بن الهيثم بن الجهم (تس ١٢٩٣٦)، وعفان بن مسلم (تس ١٢٥٥٩)، و (مق) وعمر بن علي المقدمي (تس ١٢٣٣٠) و',NULL,'385',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (388,'(عخ، س)، وعمران بن تمام البناني (تس ٣٨٠٤٠)، وعمران بن عيينة (تس ٤١٩٢ و ١٠٥٨٥ و ٢٦٠٤٦ و ٢٨٠٤٣)، و (تخ ١/ ٧٩)، و (س)، وعون بن الحكم (تق ٦٦١)، وقرة بن سليمان (تس ٣٦٨٩٥)، و (تهـ ١٥٥٨) وكثير بن هشام (تس ١٢٦٥٢ و ١٢٦٦٧ و ١٤٠١٢) ومحمد بن جعفر (غندر) (تس ١٢٩٥٣ و ١٤٥١٠ و ٣٤٣٧١)، و (م، س)، ومحمد بن سواء (تهـ ٨٩ و ١٣٨٩)، و (س)، ومحمد بن الزبرقان (تس ١٢٩٦٤ و ١٤٠٤٨)، ومحمد بن عبد الله الأسدي الزبيري (تس ١٢٩١٧)، و (تهـ ٢٦٧٨)، ومحمد بن أبي عدي (تس ١٨٨٢ و ٥٦١٢ و ١٢٥٥٨ و ١٢٦٦٥ و ١٨٣٣٧ و ٣٦٧٤١)، و (تهـ ١٦٧ و ١٠٩٤ و ٢٢٥٤)، و (خ، م، ت)، ومحمد بن مروان العقيلي (تس ٦٣٣٤)، ومعاذ بن سقيط (تق ٨٦٣)، ومعاذ بن معاذ العنبري (تس ١٠٤٨٧)، و (خ، مق)، ومعاذ بن هشام (تهـ ١٩٩٢)، و (خ، س)، ومعاوية – غير مكني، ولا منسوب – (تس ٤١٨) - ولعله (أبو معاوية) سقطت منه لفظة (أبو) ومعتمر بن سليمان (تس ١٢٦٧٠ و ١٤٠٤٠)، و (تهـ ١٤٦٠)، و (س)، ومؤمل بن إسماعيل (تس ١٨٨٧ و ١٨٩٣ و ١٩٠٠ و ١٩٧٢٧) وميمون بن زيد (تق ٨٦٩)، وهشيم [ابن بشير] (تس ١٥٢٤٣) – ولكن بينه وبين الطبري واسطة هو المثنى بن إبراهيم – ووكيع بن الجراح (تس ٤٣٠٢ و ٤٣٠٣ و ١٠٤٦٤ و ١٢٥٦٨ و ١٢٩١٥ و ١٩٤٣٩ و ٢٠٤٦٤ و ٣٤٣٧٢) ويحيى بن سعيد القطان (تس ١٩٠٢ و ٣٥٨٤ و ٣٥٨٥ و ٤١٦٨ و ٨٤٥٦ و ٨٩٣٤ و ١٠٤٨٦ و ١٢٦٦٥ و ١٢٩٥٧ و ١٤٠٠١ و ١٤٠٠٩ و ٢٠٤٩٥)، و (تخ ٢/ ٢٢٩)، و (خ، م، ت، س)، وأبي زكير يحيى بن محمد بن قيس المدني (تهـ ٢٤٠١)، و (تخ١/ ٥٢٦)، و (ت) ويزيد بن زريع (تس ١٨٨٤ و ١٩٨٩ و ١٩٩٨ و ٤١٦٢ و ١٢٣٥٥ و ١٢٩٦٥)، و (تهـ ٣١٩ و ٢٤٥٠)، و (تخ ٣/ ٣٧)، و (ت، س)، ويزيد بن هارون (تس ٢٧١٤ و ٤١٧٥ و ١٢٦١٣ و ١٤٠٤٥)، و (تهـ ١٤١٨ و ١٤١٩ و ١٦١٦)، و (تق ٧٠) و (خ)، وأبي معاوية الضرير (تس ٤٦٤ و ٣٥٩٠ و ١٠٤٧٥ و ١٢٥٦٨ و ١٢٦٥٧ و ١٢٩١٥ و ٣٦١٥٢)، وابن سوار – غير مسمى، ولا منسوب – (تس ٢٧٠٣٧)، وأبي عامر – غير مسمى، ولا منسوب – (تهـ ٢٥٢٩)، وسلمة بن رفيع " الجرح والتعديل " (٦/ ٢٤٩ / ١٣٧٥).
روى عنه: أحمد بن أبي خيثمة " تاريخ بغداد " (١٢/ ٢٠٧ / ٦٦٦٨)، وأحمد بن منصور الرمادي " تاريخ بغداد " (١٢/ ٢٠٧ / ٦٦٦٨)، وبشر بن موسى " تاريخ بغداد " (١٢/ ٢٠٧ / ٦٦٦٨)،',NULL,'386',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (389,'والحسين بن إدريس الأنصاري " الثقات " (٨/ ٤٨٧ / ١٤٥٨٤) والحسين بن إسماعيل المحاملي " تاريخ بغداد " (١٢/ ٢٠٧ / ٦٦٦٨)، وأبو بكر عبد الله بن سليمان بن الأشعث " تاريخ بغداد " (١٢/ ٢٠٧ / ٦٦٦٨)، وعبد الله بن محمد بن ناجية " تاريخ بغداد " (١٢/ ٢٠٧ / ٦٦٦٨)، وعفان بن مسلم – وهو من شيوخه أيضاً – " طبقات أصبهان " (٢/ ١٩٢ / ١٤٨)، والفتح بن إدريس " طبقات أصبهان " (٢/ ١٩٢ / ١٤٨).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، ثبتاً، مكثراً متقناً، مجوداً، صدوقاً، حافظاً، محسناً، إماماً، ناقداً، مصنفاً جامعاً، من فرسان الحديث، ومن نبلاء المحدثين، أحد الأعلام، وبعض أصحاب الحديث يفضلونه على (علي ابن المديني)، ويتعصبون له، وقد صنف " المسند "، و " العلل "، و " التاريخ "، وقد تكلم فيه (علي ابن المديني)، وطعن في روايته عن (يزيد بن زريع)، وإنما طعن في روايته عن (يزيد)، لأنه استصغره فيه.
وانظر: " التاريخ الكبير " (٦/ ٣٥٥ / ٢٦١٧)، و " الجرح والتعديل " (٦/ ٢٤٩ / ١٣٧٥)، و " الثقات " (٨/ ٤٨٧ / ١٤٥٨٤)، و " طبقات المحدثين بأصبهان " (٢/ ١٩٢ / ١٤٨)، و " تاريخ بغداد " (١٢/ ٢٠٧ / ٦٦٦٨)، و " الكنى والأسماء " (١/ ٢١١)، و " المقتنى " (١/ ١٩٣)، و " طبقات المحدثين " (١/ ٩٢ / ١٠٤٠)، و " طبقات الحفاظ " (١/ ٢١٤ / ٤٧٦)، و " ذكر أسماء التابعين " (٢/ ١٧٩ / ٨٨١)، و " من روى عنهم البخاري ومسلم " (١/ ١٦٥ / ١٦٤)، و " رجال مسلم " (٢/ ٧٣ / ١١٨٦)، و " تسمية من أخرجهم البخاري ومسلم " (١/ ١٨٩ / ١٢٠٣)، و " التعديل والتجريح " (٣/ ٩٨٠ / ١١٠٩)، و " تهذيب الكمال " (٢٢/ ١٦٢ / ٤٤١٦)، و " الكاشف " (٢/ ٨٤ / ٤٢٠٠)، و " التذكرة " (٢/ ٤٨٧ / ٥٠٢)، و " السير " (١١/ ٤٧٠ / ١٢١) و " البداية والنهاية " (١١/ ٥)، و " تهذيب التهذيب " (٨/ ٧٠ / ١٢٠)، و " تقريب التهذيب " (١/ ٤٢٤ / ٥٠٨١)، و " لسان الميزان " (٧/ ٥٠٩ / ٥٨٣٦)، و " نزهة الألباب " (٢/ ٧٣ / ٢١٧١)، و " مشتبه أسامي المحدثين " (١/ ١٨٢ / ٣٢٠)، و " رجال تفسير الطبري " (٤٢٧/ ٢٠٠٨).
٨٥ - تمييز أبو حفص، عمرو بن علي، البغدادي يعرف بـ (نقيب الفقهاء) حدث بدمشق.
روى عن: أبي سعيد بن علي العدوي.',NULL,'387',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (390,'روى عنه: تمام الرازي أيضاً. وقد وافقه في الاسم والكنية والبلد.
وانظر: " تاريخ بغداد " (١٢/ ٢٢٧ / ٦٦٧٩).
٢٢٨ - أبو عثمان الراسبي (١٤٣٦٠)
(الترمذي)
(طب: ٦ر / ٤ش)
(تس: ٥ر / ٣ش)، و (تق ١ر / ١ش)
أبو عثمان، عمرو بن مالك، الراسبي – وقيل: الغبري، وقيل: النكري، البصري الصغير: توفي بعد الأربعين ومائتين، من العاشرة، ضعيف، يغرب، ويخطئ.
روى عن: سفيان بن عيينة (تس ٣٠٨٧٨ و ٣٧٥٢٩)، وعبد الله بن وهب (تق ٧٥٧)، ووكيع بن الجراح (تس ٢٨٤٥٠)، ويحيى بن سليم الطائفي (تس ١٤٣٦٠ و ٣٢٩٢٨)، وإسحاق بن يوسف " مسند البزار " (٩/ ١٩٩ / ٣٧٤٧)، وحماد بن خالد " مسند البزار " (٩/ ١١٦ / ٣٦٦٤)، وعبيدة بن حميد " مسند البزار " (٩/ ٦٣ / ٣٥٥٣)، وعمر بن علي المقدمي " المعجم الأوسط " (٣/ ١٧٤ / ٢٨٤٠)، وعون بن كهمس " مسند البزار " (٩/ ١٢٠ / ٣٦٦٩)، ومحمد بن الحسن الواسطي " مسند البزار " (٨/ ٢٥٢ / ٣٣١٦)، ومحمد بن حمران " مسند البزار " (٩/ ١١٩ / ٣٦٦٨)، ومحمد بن عبد الرحمن الطفاوي " مسند البزار " (٩/ ٣١ / ٣٥٤٥) ومعاذ بن محمد بن حيان " مسند البزار " (٩/ ٧٧ / ٣٦٠٧)، ومعاذ بن هشام " مسند البزار " (٩/ ٢٢ / ٣٥٢٥) ويزيد بن هارون " مسند البزار " (٩/ ٧٦ / ٣٦٠٦)، ويوسف بن خالد " مسند البزار " (٤/ ١٠٠ / ١٢٧٦)، وأبي قتيبة " مسند البزار " (٦/ ٣٢٤ / ٢٣٣٥).
روى عنه: إبراهيم بن محمد " ضعفاء العقيلي " (٢/ ٢٣٥ / ٧٨٦)، وعلي بن سعيد بن بشير الرازي " الكامل " (٦/ ٢٠٧ / ١٦٨١).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ضعيفاً، يغرب، ويخطئ، منكر الحديث عن الثقات، ويسرق الحديث، ولم يكن بصدوق.',NULL,'388',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (391,'قلت: وأغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري ".
وقد تصحفت نسبته في " التفسير "، أكثر من مرة:
فهو في الأثر رقم (تس ٢٨٤٥٠)، (البكري)، بدل (النكري).
وفي الأثر رقم (تس ٣٢٩٢٨)، (النضري)، بدل البصري.
واختلف علماء الجرح والتعديل في نسبته: راسبيا، وغبريا، ونكريا، وأنكر الذهبي في " الميزان " أن يكون نكريا.
ومنهم من جمعها كالحافظ في " لسان الميزان " في أكثر من موضع.
وانظر: " الثقات " (٨/ ٤٨٧ / ١٤٥٨٥)، و " الدعاء " (١/ ٥٢٩) و " الضعفاء والمتروكين لابن الجوزي " (٢/ ٢٣١ / ٢٥٨٧)، " تهذيب الكمال " (٢٢/ ٢٠٧ / ٤٤٣٩)، و " تهذيب التهذيب " (٨/ ٨٣ / ١٥٢) و " تقريب التهذيب " (١/ ٤٢٦ / ٥١٠٣)، و " الكاشف " (٢/ ٨٧ / ٤٢٢١) و " الجرح والتعديل " (٦/ ٢٥٩ / ١٤٢٨)، و " ميزان الاعتدال " (٥/ ٣٤٢ / ٦٤٤١)، و " المغني في الضعفاء " (٢/ ٤٨٨ / ٤٦٩٩)، و " لسان الميزان " (٧/ ٣٢٧ / ٤٢٨٣) و " الكامل " (٥/ ١٥٠ / ١٣١٥).
٨٦ - تمييز أبو مالك، عمرو بن مالك، النكري، توفي سنة ثمان، أو تسع وعشرين ومائة، من السابعة، صدوق، ووقع في روايته مناكير بسبب ابنه يحيى.
روى عن: أبي الجوزاء.
روى عنه: ابنه يحيى، وحماد بن زيد.
وانظر: " تهذيب الكمال " (٢٢/ ٢١١ / ٤٤٤١)، و " رجال تفسير الطبري " (٤٢٨/ ٢٠١٦).
٢٢٩ - أبو عثمان العثماني (٤٥)
(طب: ٧ر / ١ش)
(تس: ٣ر / ١ش)، و (تهـ: ٢ر / ١ش)، و (تق ١ر / ١ش)
أبو عثمان، عمرو بن محمد بن يحيى بن عثمان، القاضي، العثماني، المكي: -',NULL,'389',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (392,'وقد نسبه الطبري إلى جده – من الحادية عشرة، صدوق.
روى عن: إسماعيل بن أبي أويس (تس ٤٥ و ٣٨١٧٩)، و (تهـ ٣٢١ و ٣٢٢)، و (تق ٦٨٢) وعبد الرحمن عن الزهري وهو مشكل (تس ٣١٥٧٨)، وإبراهيم بن حمزة، وإبراهيم بن المنذر [الحزامي] وأبي مصعب [أحمد بن أبي بكر الزهري] " أخبار مكة " (٤/ ٢١)، وعبد الله بن مسلمة " أخبار مكة " (٤/ ٤ / ٢٢٩٤)، وعبد الله بن نافع الزبيري، وعثمان بن يعقوب " أخبار مكة " (٣/ ١٥٧ / ١٩١٣)، وعمرو بن خالد بن عاصم بن عمرو " المستدرك " (٣/ ٢٩٥ / ٥١٤٨)، ومطرف بن عبد الله " أخبار مكة " (٢/ ٢٣١ / ١٤١٤).
روى عنه: زكريا بن يحيى الساجي " تهذيب التهذيب " (٦/ ٣٦١ / ٧٦٠)، وأبو جعفر محمد بن إبراهيم الراسبي " لسان الميزان " (٤/ ٣٧٦ / ١١١١)، وأبو عبد الله محمد بن إسحاق الفاكهي – صاحب كتاب " أخبار مكة – وقد أكثر عنه – " (٢/ ١١٠ / ١٢٣٥)، والمؤمل بن الحسن بن عيسى " المستدرك " (٣/ ٢٩٥ / ٥١٤٨) وأبو عوانة الإسفراييني " مسند أبي عوانة " (٣/ ٢٣٢ / ٤٧٨٣).
وكل اسم لم يعز إلى كتاب، - وميزته بالأسود العريض -، من معجم شيوخه، فهو من ترجمته في " الجرح والتعديل ".
التعديل والتجريح: قال عبد الرحمن بن أبي حاتم: " كتبت عنه، وهو صدوق ".
وقال مسلمة بن القاسم: " ضعيف ".
قلت: وأغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري "
وقول (عبد الرحمن بن أبي حاتم) فيه، مقدم – عندي – على قول (مسلمة بن القاسم)، والذي عزاه إليه الحافظ (ابن حجر) في " لسان الميزان "، وذلك لمعرفة (عبد الرحمن بن أبي حاتم) الوثيقة به؛ حيث إنه كتب عنه.
وقد سقطت (الواو) من آخر اسمه في الأثر رقم (تس: ٣٨١٧٩) فصار اسمه (عمر) واشتبه باسم من قبله في الأثر رقم (تس: ٣٨١٧٨)، (عمر بن عثمان الزهري). والله أعلم.
وقد تصحف اسمه في الأثرين رقم (تس: ٣٨١٧٨ و ٣٨١٧٩ من (عمرو بن عثمان)',NULL,'390',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (393,'نسبة إلى جده إلى (عمر بن عثمان).
وانظر: " الجرح والتعديل " (٦/ ٢٦٣ / ١٤٥٤)، و " لسان الميزان " (٤/ ٣٧٦ / ١١١١).
٢٣٠ - عمرو البجلي (٢٩٦٥٥)
(طب: ٢ر / ٢ش)
(تس: ١ر / ١ش، وتهـ: ١ر / ١ش)
عمرو بن يحيى بن عفرة، البجلي: من العاشرة، أو الحادية عشرة، روى عنه البزار في " المسند " (٥/ ٢٨٥ / ١٩٠١) مقروناً بـ (أحمد بن عبدة – وهو: الضبي)، وسكت عنه، وقال الهيثمي في " المجمع " (٤/ ٢٠٥): " لم أجد من ترجمه "
روى عن: عمرو بن سعيد الأبح (تس ٢٩٦٥٥)، وعبد الوارث بن سعيد (تهـ ١١٣)، وحماد بن زيد " المعجم الكبير " (١١/ ١١ / ١٠٨٦٩)، ويزيد بن زريع " مسند البزار " (٩/ ٣١ / ٣٥٤٤).
روى عنه: أبو بكر أحمد بن عمرو البزار " المعجم الصغير " (١/ ٩٨ / ١٣٤).
التعديل والتجريح: قال الهيثمي في " مجمع الزوائد " (٤/ ٢٠٥) معلقاً على حديث رواه الطبراني في الكبير، عن ابن عباس قال: قال رسول الله صلى الله عليه وسلم: «كل شرط ليس في كتاب الله فهو باطل وإن كان مائة شرط». قال: " رواه الطبراني في " الكبير "، وفيه عمرو بن يحيى بن عفرة، ولم أجد من ترجمه ". انتهى.
قلت: هو عند الطبراني في " الكبير " (١١/ ١١ / ١٠٨٦٩)، من طريق البزار ولكن تصحفت (عفرة) إلى (غفرة).
وقد روى عنه من طريقه في " الأوسط " (٢/ ٢٨٨ / ٢٠٠٥) أيضاً.
وروى عنه البزار في " المسند " (٥/ ٢٨٥ / ١٩٠١) مقروناً بأحمد بن عبدة – وهو: الضبي وقد أغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري ".',NULL,'391',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (394,'٢٣١ - عيسى النهشلي (٣٠٠)
(الترمذي)
(طب: ٢٧ ر / ١ش)
(تس: ٢١ ر / ١ش) و (تخ: ١ر / ١ش)، و (تهـ: ٥ر / ١ش)
عيسى بن عثمان بن عيسى بن عبد الرحمن، التميمي، النهشلي، الكوفي، ثم الرملي، الكسائي ابن أخي يحيى بن عيسى الرملي، توفي سنة إحدى وخمسين ومائتين، من الحادية عشرة، صدوق.
روى عن: عمه يحيى بن عيسى الرملي (تس ٣٠٠ و ٢٨٢٠ و ١١٥٣٨ و ١٣٤٨٢ و ١٤٢٠٦ و ١٦٤٢٥ و ٢١٣٦٧ و ٢٤٨٦٥ و ٢٤٨٧٩ و ٢٤٩٠٩ و ٢٦٥٠٨ و ٢٧٤٦٥ و ٢٨٠٨٣ و ٣١٠٤٣ و ٣٢٦٩٧ و ٣٣٧٢٦ و ٣٤١٩٥ و ٣٦٥٢٩ و ٣٦٦٣٠ و ٣٧٧٧٩)، و (تهـ ٢٠٢ و ٩٥٧ و ١٠٧١ و ١٢٠٦ و ١٩٨٧) و (تخ ٢/ ٢٣١)، و (ت)، عن الأعمش!! ولعل فيه سقط ما!!
روى عنه: الحسن بن محمد الأشناني " المعجم الكبير " (٢٤/ ١٤٦ / ٣٨٦).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: صدوقًا صالحاً.
وقد وقفت له عند الترمذي في " السنن "، على روايتين، وعند الطبراني في " المعجم الكبير " على رواية، وجميع ما رواه عندهما، أو عند الطبري، كان عن عمه " يحيى بن عيسى الرملي ".
إلا أنه وقع تصحيف في اسم شيخه، في الرواية الثانية من روايات " الترمذي " عنه، عن عمه؛ حيث قال في نسخة الشيخ شاكر – طبعة دار التراث – (٥/ ٦٤٣ / ٣٧٣٦): " حدثنا عيسى بن عثمان ابن أخي يحيى بن عيسى، حدثنا أبو عيسى الرملي، عن الأعمش، عن عدي بن ثابت، عن زر بن حبيش، عن علي قال: لقد عهد إلي النبي الأمي صلى الله عليه وسلم: «أنه لا يحبك إلا مؤمن ولا يبغضك إلا منافق».',NULL,'392',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (395,'وفي نسخة دار الفكر من " تحفة الأحوذي بشرح جامع الترمذي " ١٠/ ١٨١ / ٣٧٤٥)
قال: " حدثنا عيسى بن عثمان ابن أخي يحيى بن عيسى، حدثنا عيسى الرملي، عن الأعمش، عن عدي بن ثابت، عن زر بن حبيش، عن علي، به ". حيث سقطت لفظة (أبو) من اسم عمه (أبو عيسى)؛ فصار عيسى!!.
وقد وقع في الأثر رقم (تس: ١١٥٣٨) من " التفسير " تحريف، وتصحيف، في اسم شيخه الوحيد الفرد (عمه، يحيى بن عيسى الرملي) فصار: " عمرو بن يحيى بن سعيد "، وهو خطأ بلا ريب!! والصحيح ما أثبته.
وقد أخرج الطبري عنه في " التاريخ " (٢/ ٢٣١) عن الأعمش بلا واسطة!! والظاهر أنه سقط اسم شيخه وهو عمه لا غير!! والله أعلم.
وانظر: " التاريخ الكبير " (٢/ ١١٢ / ١٨٧٥)، و " التاريخ الصغير " (٢/ ٢٩٤ / ٢٦٥٧)، و " تهذيب الكمال " (٢٢/ ٦٣٥ / ٤٦٤١)، و " الكاشف " (٢/ ١١١ / ٤٣٨٦)، و " تهذيب التهذيب " (٨/ ١٩٧ / ٤١٠)، و " تقريب التهذيب " (١/ ٤٣٩ / ٥٣١٠)، و " رجال تفسير الطبري " (٤٣٥/ ٢٠٥٤).
٨٧ - تمييز عيسى بن عثمان، شيخ قديم.
رأى فاطمة بن علي رضي الله تعالى عنهما.
روى عنه: زيد بن أبي أنيسة، وعبد الكريم الجزري، وأهل الجزيرة.
وانظر: " التاريخ الكبير " (٦/ ٣٩٥ / ٢٧٥٨)، و (٦/ ٤٠٣ / ٢٧٨٧)، و " الجرح والتعديل " (٦/ ٢٨٢ / ١٥٦٤)، و " الثقات " (٧/ ٢٣٣ / ٩٨٢٩).
٨٨ - تمييز عيسى بن عثمان، الآجري، الكوفي.
روى عن: أبي الحسن محمد بن عبد الواحد بن جعفر.
روى عنه: إسماعيل بن مسلم.
وانظر: " موضع أوهام الجمع والتفريق " (١/ ٤١٨).',NULL,'393',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (396,'٢٣٢ - – عيسى بن عمرو (٣١٣٧٥)
(طب: تف: ٢ر / ١ش)
عيسى بن عمرو، من العاشرة، أو الحادية عشرة، لم أعرفه، ولم أجد له ترجمة ولم أقف له في " التفسير " على غير هذا الأثر، والذي يليه، ولم يتعرض الشيخ التركي في تحقيقه " لتفسير الطبري " (٢١/ ٢٠٠) لترجمته بشيء.
روى عن: إبراهيم بن محمد (تس ٣١٣٧٥ و ٣١٣٧٦).
روى عنه: أبو جعفر الطبري (تس ٣١٣٧٥ و ٣١٣٧٦).
٢٣٣ - أبو يحيى الطباع (٢٤٨٦٠)
(طب: (تس: ١ر / ١ش)
أبو يحيى، عيسى بن يوسف بن عيسى بن الطباع، البغدادي، توفي سنة أربع وأربعين ومائتين من التاسعة، ترجم له، وسكت عنه الخطيب البغدادي في " تاريخ بغداد " (١١/ ١٦٢ / ٥٨٥٧).
روى عن: سفيان [ابن عيينة] (تس ٢٤٨٦٠)، وعمه إسحاق بن عيسى، وبشر بن عمر الزهراني، وحلبس بن محمد الكلبي – وقيل الكلابي- وأبي بكر بن عياش، وابن أبي فديك.
روى عنه: أحمد بن سليمان بن أيوب " الحلية " (٦/ ٣٧٤)، وأحمد بن محمد بن الحسين العباسي " الحلية " (٦/ ٣٧٤)، وعبد الله بن محمد الكرخي، وعبد الله بن محمد بن ناجية، وعبد الوهاب بن أبي حية – وراق الجاحظ -، وقاسم بن زكريا المطرز، وأخوه أبو جعفر، محمد بن عيسى بن يوسف بن الطباع – وهو أشهر منه -، ومحمد بن غالب بن حرب " الحلية " (٦/ ٣٧٤)، ومحمد بن موسى الحلواني " الحلية " (٦/ ٣٧٤)، ويحيى بن صاعد، ويعقوب بن يوسف الطحان " تاريخ بغداد " (١٤/ ٢٩٣ / ٧٥٩٦)، وأبو بكر بن أبي الدنيا.',NULL,'394',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (397,'وكل اسم لم يعز إلى كتاب، - وميزته بالأسود العريض-، من معجم شيوخه وتلاميذه، فهو من ترجمته في " تاريخ بغداد ".
التعديل والتجريح: ترجم له وسكت عنه الخطيب في " تاريخ بغداد " (١١/ ١٦٢ / ٥٨٥٧) ولم يذكر فيه جرحاً، ولا تعديلاً، وإنما ساق له أثراً، سبق وأن أخرجه الأصبهاني في " الحلية " (٦/ ٣٧٤) من ثلاثة طرق، عنه، عن حلبس، وقد تكلم في حلبس في هذا بعض أهل العلم.
وقد أغفله الشيخان (شاكر) في تحقيقهما " لتفسير الطبري ".
وأما الخبر موضوع الترجمة فقد أخرجه: الحميدي في " مسنده " (١/ ٢٢٦ / ٤٨٣)، وأبو يعلى في " مسنده " (٤/ ٢٨٥ / ٢٣٩٦) من طريق أبي خيثمة عن سفيان بن عيينة، به، إلا أنهما زادا لفظتي (حفاة، عراة).
وللطباع رواية في: " سنن الدارقطني " (٢/ ٨١ / ٥)، و " الحلية " (٦/ ٣٧٤).
وذكر في: " معجم الصحابة " (٢/ ١٥٧ / ٦٣١)، و " الكامل " (٢/ ٤٥٧ / ٥٦٧)، و " الإكمال لابن ماكولا " (٢/ ٤٩٨)، و " المجروحين " (١/ ٢٧٧ / ٢٨٩)، و " لسان الميزان " (١/ ٢٧٧ / ١٤٠٠)، و " فيض القدير " (٤/ ١٠٥)، ونقل فيه عن الحاكم في كتاب " الكنى " في ترجمة حلبس.

<span data-type="title" id=toc-35>[١٤] باب الفاء الموحدة </span>(من اسمه فضالة)
٢٣٤ - أبو الفضل الطهوي (١٨٢٥٠)
(الترمذي)
(طب: (تس: ٣ر / ١ش)
أبو الفضل، فضالة بن الفضل بن فضالة، التميمي، الطهوي، الكوفي، العطار: توفي سنة خمسين ومائتين، من صغار العاشرة، صدوق، ربما أخطأ.
روى عن: أبي حازم بزيع بن عبد الله اللحام، مولى يحيى بن عبد الرحمن السعيدي (تس ١٨٢٥٠ و ٢٣٣٨٨ و ٣٤١٨٣).',NULL,'395',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (398,'روى عنه: محمد بن إسحاق بن خزيمة " صحيح ابن خزيمة " (٣/ ٣٤٤ / ٢٢٢١) ومحمد بن عثمان بن أبي شيبة " المعجم الكبير " (٩/ ١٥٢ / ٨٧٦١)
التعديل والتجريح: قال أبو حاتم: " صدوق ".
وقال النسائي: " ثقة ".
وذكره ابن حبان في " الثقات " وقال: " ربما أخطأ ".
وانظر: " الجرح والتعديل " (٧/ ٧٨ / ٤٤٦)، و " الثقات " (٩/ ١٠ / ١٤٨٩٩)، و " تهذيب الكمال " (٢٣/ ١٨٩ / ٤٧٢٧)، و " الكاشف " (٢/ ١٢١ / ٤٤٥٩)، و " المقتنى " (٢/ ١٧ / ٥٠٦٢)، و " تهذيب التهذيب " (٨/ ٢٤١ / ٥٠٠)، و " تقريب التهذيب " (١/ ٤٤٥ / ٥٣٩٦)، و " رجال تفسير الطبري " (٤٤٥/ ٢٠٩٣).
٢٣٥ - أبو العباس البزار (٧٨٥٣)
(طب: ٦ر / ٤ش)
(تس: ٤ر / ٢ش)، و (تهـ: ٢ر / ٢ش)
أبو العباس، الفضل بن إسحاق بن حيان، البزاز، الدوري، البغدادي: توفي سنة اثنتين وأربعين ومائتين، من الحادية عشرة، ثقة، مأمون.
روى عن: أشعث بن عبد الرحمن بن زبيد اليامي (تس ١٤٢١٢)، وأبي قتيبة سلم بن قتيبة (تس ٢٤٤٦٦ و ٢٦٥٥٥ و ٣٣٠٩٦ و ٣٦١٧٤)، وسعيد بن محمد الوراق (تهـ ١٩٢٥)، ووكيع [ابن الجراح] (تس ٧٨٥٣) وأبي بكر بن عياش (تهـ ١٣٦٦) وأبي عبد الرحمن بن عبيد الله بن عبد الرحمن الأشجعي، وعمر بن أيوب الموصلي، والقاسم بن مالك المزني، ومروان بن معاوية " حلية الأولياء " (٦/ ٤٤)، ومعاذ بن معاذ " الثقات " (٩/ ٦ / ١٤٨٧٥)، وابن أبي عدي " الثقات " (٩/ ٦ / ١٤٨٧٥) وابن مهدي " حلية الأولياء " (٩/ ٧).
روى عنه: إبراهيم بن موسى الرواس، وعبد الله بن أحمد بن حنبل، وعبد الله بن إسحاق المدائني، ومحمد بن إبراهيم الفاسي " الثقات " (٩/ ٦ / ١٤٨٧٩) ومحمد بن إسحاق بن',NULL,'396',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (399,'إبراهيم الثقفي " المستدرك " (٣/ ٥٣٥ / ٥٩٨٩)، ومحمد بن محمد بن سليمان الباغندي، والهيثم بن خلف " المعجم الأوسط " (٩/ ١٥٨ / ٩٤١٣)، وأبو أحمد بن عبدوس السراج، وأبو بكر بن أبي الدنيا " مكارم الأخلاق " (١/ ١١٥ / ٣٨١)، وأبو بكر بن عبيد " حلية الأولياء " (٦/ ٤٤).
وكل اسم لم يعز إلى كتاب، من معجم شيوخه وتلاميذه، وميزته بالأسود العريض فهو من ترجمته في " تاريخ بغداد ".
التعديل والتجريح: ذكره محمد بن حبان في " الثقات ".
قال الخطيب البغدادي في " تاريخ بغداد " بسنده عن السراج قال: " ثقة مأمون " وذكره الشيخ شاكر في تحقيقه " لتفسير الطبري " (١٢/ ٢٠ / ١٤٢٠٧) وقال " شيخ الطبري لم أعرفه من هو؟ ولم أجد له ترجمة ولعله محرف عن اسم آخر ".
قلت: نعم قد تحرف اسمه من (الفضل) إلى (المفضل)، في الأثر رقم (تس ١٤٢١٣)، والصحيح (الفضل)، كما هو في الآثار ذوات الأرقام (تس ٢٤٤٦٦ و ٢٦٥٥٥ و ٣٣٠٩٦ و ٣٦١٧٤)، وكما في " تهذيب الآثار "، وغيره من كتب الحديث والتراجم، التي اتفقت على ذكر شيوخه وتلاميذه بهذا الاسم.
وتصحف اسم شيخه في الأثر (تس ٢٦٥٥٥) من مسلم بن قتيبة إلى سالم بن قتيبة والصحيح ما أثبته.
وانظر: " الكنى والأسماء " (١/ ٦١٣ / ٢٥٠٤)، و " الثقات " (٩/ ٦ / ١٤٨٧٥)، و " تاريخ بغداد " (١٢/ ٣٦٠ / ٦٧٩٢)، و " المقتنى في سرد الكنى " (١/ ٣٤٤ / ٣٤٩٧).
وله مرويات في: " المستدرك " (٣/ ٥٣٥ / ٥٩٨٩)، و " المعجم الأوسط " (٩/ ١٥٨ / ٩٤١٣)، و " مكارم الأخلاق " (١/ ١١٥ / ٣٨١)، و " حلية الأولياء " (٦/ ٤٤)، و (٩/ ٧).
٢٣٦ - أبو الحسن الواسطي (٢٣٣٠٨)
(طب: ٢ر / ٢ش)',NULL,'397',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (400,'(تس: ١ر / ١ش)، و (تهـ: ١ر / ١ش)
أبو الحسن، الفضل بن داود، الواسطي، من العاشرة، ترجم له، وسكت عنه ابن أبي حاتم.
روى عن: أبي داود سليمان بن داود الطيالسي (تس ٢٣٣٠٨)، وعمر بن يونس (تهـ ١٨٤٠)، وأبي قتيبة سلم بن قتيبة " الجرح والتعديل " (٧/ ٦٢ / ٣٥٤)، وسهل بن عبد الله بن أبي سهل الواسطي " صحيح ابن حبان " (١٥/ ١٣٠ / ٦٧٣٦)، وصالح بن عبد الله العجلي " العظمة " (٥/ ١٧٥٧ / ١٢٥٢٧٨)، وعبد الصمد بن عبد الوارث " المعجم الكبير " (٢٣٣/ ٣٦٤ / ٨٥٨)، وقريش بن أسد " دلائل النبوة للأصبهاني " (١/ ٢١٥ / ٢٩٦)، وموسى بن داود الضبي الطرسوسي الخلقاني " تهذيب الكمال " (٢٩/ ٥٧ / ٦٢٥١)، وأبي أحمد الزبيري " الآحاد والمثاني " (٢/ ١٤٨ / ٨٦٨).
التعديل والتجريح: ذكره ابن أبي حاتم في " الجرح والتعديل " (٧/ ٦٢ / ٣٥٤) وسكت عنه، ولم يذكر فيه جرحا ولا تعديلا.
وأغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري ".
٢٣٧ - الفضل بن زياد (١٠٠٥٥)
(طب: تف: ١ر / ١ش)
الفضل بن زياد، الواسطي: من العاشرة، لم أعرفه، ولم أجد له ترجمة، ولم يعرفه الشيخ شاكر قبلي (١٠٠٤٩) واضطرب فيه وتردد، وسماه الشيخ التركي في تحقيقه " لتفسير الطبري " (٧/ ٢٨١) (الفضل بن داود الواسطي) – وقد سبقت ترجمته – ولم يتعرض لترجمته بشيء.
روى عن: أبي داود الطيالسي (تس: ١٠٠٥٥)
روى عنه: أبو جعفر، محمد بن جرير الطبري.
التعديل والتجريح: ذكره الشيخ شاكر في تحقيقه " لتفسير الطبري " (٩/ ١٠٠٤٩)',NULL,'398',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (401,'وقال: " لا أدري من هو؟ والترجمة الوحيدة التي وجدتها بهذا الاسم هي " الفضل بن زياد الطساس البغدادي وهو من هذه الطبقة فلعله هو المترجم في " الجرح والتعديل " (٣/ ٢ / ٦٢) و " تاريخ بغداد " (١٢/ ٣٦٠)، وله ترجمة غير محررة في " لسان الميزان " (٤/ ٤٤١)، انتهى.
قلت: وشيخه هنا هو: أبو داود، سليمان بن داود بن الجارود الطيالسي البصري، ولد سنة ١٣٣ وتوفي سنة ٢٠٤، من التاسعة، ثقة، حافظ، غلط في أحاديث، وقد ترجمت له في " المعجمين الكبير، والصغير ". ولم أستطع تحديده هل هو الطساس؟! أم غيره؟! فقد أخرج الطبري في " تهذيب الآثار " (٣/ ٣١ / ١٨٤٠)، عن الفضل بن داود الواسطي، عن عمر بن يونس ولذلك ترجمت (للطساس) تمييزاً.
٨٩ - تمييز الفضل بن زياد – غير مكني، ولا منسوب.
يروي عن: شيبان.
وانظر: " الضعفاء للعقيلي " (٣/ ٤٥٤ / ١٥٠٨)
٩٠ - تمييز أبو العباس، الفضل بن زياد، بياع الطساس، البغدادي.
روى عن: إسماعيل بن عياش، وخلف بن خليفة، وعاصم بن علي " المستخرج " (٤/ ١٥٦ / ٣٤٨٠) وعباد بن عباد المهلبي، وعباد بن العوام، وعلي بن هاشم بن البريد.
روى عنه: أحمد بن علي بن سعيد القاضي " المعجم الأوسط " (١/ ٣١ / ٧٦)، وإسحاق بن الحسن الحربي، وجعفر بن أحمد بن محمد بن الصباح الجرجرائي، والحسن بن سفيان، وموسى بن هارون بن إبراهيم بن هاشم البغوي، وأبو بكر بن أبي الدنيا، وأبو زرعة الرازي.
التعديل والتجريح: قال عبد الرحمن بن أبي حاتم: " سئل أبو زرعة عنه فقال: كتبت عنه كان يبيع الطساس، شيخ ثقة ".
وقال الخطيب البغدادي في تاريخ بغداد: " وكان ثقة ".
وذكره ابن حبان في الثقات وقال: " حدثنا عنه الحسن بن سفيان ".',NULL,'399',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (402,'وانظر: " لسان الميزان " (٤/ ٤٤١ / ١٣٤٦) – حيث جعله الحافظ هو عينه المترجم في " الجرح والتعديل " (٧/ ٦٢ / ٣٥٥)، والثقات " (٩/ ٦ / ١٤٨٧٢)، و " تاريخ بغداد " (١٢/ ٣٦٠ / ٦٧٩١).
٩١ - تمييز الفضل بن زياد القطان، أحد أصحاب أحمد بن حنبل وممن أكثر الرواية عنه.
حدث عنه: أحمد بن محمد بن إسماعيل الآدمي، وجعفر بن محمد الصندلي، والحسن بن عبد الوهاب بن أبي العنبر، ويعقوب بن سفيان الفسوي.
التعديل والتجريح: قال الخطيب: " حدثت عن عبد العزيز بن جعفر الحنبلي قال: أخبرنا أبو بكر الخلال، قال: والفضل بن زياد من المقدمين عند أبي عبد الله، وكان أبو عبد الله يعرفه قدره، ويكرمه ويصلي بأبي عبد الله ". انتهى
وانظر: " تاريخ بغداد " (١٢/ ٣٦٣ / ٦٧٩٧).
قلت: ولعله:
٩٢ - تمييز الفضيل بن زياد، الذي في " تهذيب الأسماء " (١/ ٨٠) – وتصحف بزيادة الياء؛ مصغراً! وفيه: وقال الفضيل بن زياد: " قال أحمد بن حنبل: هذا الذي ترون!! كله – أو عامته – من الشافعي، ما بت مدة أربعين سنة – أو قال -: ثلاثين سنة – إلا وأدعو الله للشافعي، وأستغفره له ".
وفي " تهذيب التهذيب " (١٠/ ٧) قال الفضيل بن زياد: " سألت أحمد بن حنبل عن ضرب مالك فقال: " ضربه بعض الولاة في طلاق المكره وكان لا يجيزه ". وأما:
٩٣ - تمييز الفضيل بن زياد الذي في " حلية الأولياء " (٨/ ٢٨٠).
روى عن: الأوزاعي، عن عبدة بن أبي لبابة، عن أبي سلمة، عن أبي هريرة، «أن رجلاً كان له على رسول الله صلى الله عليه وسلم بكر من الإبل، فجاء يتقاضاه، فقال له: " نعم لنقرضك " قال: إني محتاج إليه! وألح على رسول الله صلى الله عليه وسلم فأراد أصحاب رسول الله صلى الله عليه وسلم أن ينهروه',NULL,'400',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (403,'فقال: " دعوه! فإن طالب الحق أعذر من النبي صلى الله عليه وسلم اقضوه واشتروا له " قالوا: لا نجد إلا أفضل من بكره! فقال: " اشتروه».
وأما الحديث الذي ترجمنا للفضل بسببه، فقد أخرجه الإمام مسلم – رحمه الله – في " صحيحه " (٢/ ١٠٠٦ / ١٣٨٤) بنحو إسناد الطبري، وبمثل لفظه فقال الإمام مسلم: " وحدثنا عبيد الله بن معاذ – وهو العنبري -، حدثنا أبي، حدثنا شعبة عن عدي، - وهو ابن ثابت – سمع عبد الله بن يزيد، عن زيد بن ثابت، عن النبي – صلى الله عليه وسلم – قال: «إنها طيبة يعني المدينة وإنها تنفي الخبث كما تنفي النار خبث الفضة».
٢٣٨ - – أبو العباس الأعرج (٣٣١٢)
(البخاري، ومسلم، وأبو داود، والترمذي، والنسائي)
(طب: ٢ر / ٢ش)
(تس: ١ر / ١ش)، و (تهـ: ١ر / ١ش)
أبو العباس، الفضل بن سهل بن إبراهيم، الأعرج، الخرساني أصلاً، البغدادي وفاة: مولدة نحو سنة خمس وثمانين ومائة، وتوفي سنة خمس وخمسين ومائتين – وقد جاوز السبعين – من الحادية عشرة صدوق.
روى عن: مخول بن إبراهيم النهدي الكوفي الرافضي الخبيث (تس ٣٣١٢)، ويحيى بن إسحاق (تهـ ١٤٤٥).
روى عنه: أبو بكر أحمد بن محمد بن إسماعيل الآدمي " سنن الدارقطني " (١/ ٣٤ / ٣)، وأحمد بن يحيى بن زهير " المعجم الكبير " (٩/ ١٢٤ / ٨٦٢٣)، ومحمد بن أحمد بن أبي خيثمة " المعجم الأوسط " (٥/ ٣٠١ / ٥٣٧٦)، ومحمد بن إسماعيل بن مهران " سنن البيهقي " (٩/ ٧٠ / ١٧٨٣٣).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقاً، حافظاً، متقناً، من كبار محدثي بغداد، كان لا يفوته حديثاً، فرداً، جيداً!!،',NULL,'401',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (404,'وكان أحد الدواهي – قال الحافظ أبو بكر الخطيب معلقا: يعني في الذكاء والمعرفة، وجودة الأحاديث ". والله أعلم.
وانظر: " الكنى والأسماء (١/ ٦١٣ / ٢٤٠٥)، و " الجرح والتعديل " (٧/ ٦٣ / ٣٥٩)، و " الثقات " (٩/ ٧ / ١٤٨٨١)، و " ذكر أسماء التابعين " (١/ ٢٩٤ / ٨٧٣)، و " تاريخ بغداد " (١٢/ ٣٦٤ / ٦٨٠٠)، و " من روى عنهم البخاري في الصحيح " (١/ ١٧١ / ١٨٤)، و " رجال مسلم " (٢/ ١٣٢ / ١٣٣٢)، و " رجال البخاري " (٢/ ٦٠٨ / ٩٦٤)، و " التعديل والتجريح " (٣/ ١٠٥٠ / ١٢٢٧)، و " تهذيب الكمال " (٢٣/ ٢٢٣ / ٤٧٣٤)، و " تذكرة الحفاظ " (٢/ ٥٥٢ / ٥٧٤)، و " سير أعلام النبلاء " (١٢/ ٢٠٩ / ٧٢)، و " طبقات المحدثين " (١/ ٩٨ / ١١١٧)، و " الكاشف " (٢/ ١٢٢ / ٤٤٦٥)، و " المقتنى في سرد الكنى " (١/ ٣٤٤ / ٣٤٩٩) و " ميزان الاعتدال " (٥/ ٤٢٧ / ٦٧٣٤)، و " لسان الميزان " (٧/ ٣٣٥ / ٤٣٦٨)، و " تهذيب التهذيب " (٨/ ٢٤٩ / ٥٠٨)، و " تقريب التهذيب " (١/ ٤٤٦ / ٥٤٠٣)، و " تحفة التحصيل " (١/ ٢٥٨)، و " طبقات الحفاظ " (١/ ٢٥٠ / ٥٥٧)، و " رجال تفسير الطبري " (٤٤٦/ ٢١٠٢).
٩٤ - تمييز الفضل بن سهل، الإسفراييني، ثم الدمشقي، وكان يلقب الأشتر، توفي سنة ثمان وأربعين وخمسمائة، سماعه صحيح لكنه متهم بالكذب.
وانظر " لسان الميزان " (٤/ ٤٤٢ / ١٣٥٢).
٩٥ - – تمييز أبو العباس، الفضل بن سهل بن عبد الله، الملقب (ذا الرياستين) – يعني السيف والقلم – وزير المأمون، ولد سنة إحدى وستين ومائة، وقتل سنة اثنتين ومائتين، عن إحدى وأربعين سنة، وخمسة أشهر.
وانظر: " تاريخ بغداد " (١٢/ ٣٣٩ / ٦٧٨٤)، و " نزهة الألباب " (١/ ٢٩٢ / ١١٦٤).
٢٣٩ - أبو العباس النهاوندي (٢٢٥٧)
(الترمذي، وابن ماجه)
(طب: ٢٣ر / ١ ش)',NULL,'402',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (405,'(تس: ١٧ر / ٨ش)، و (تهـ: ٤ر / ٤ش) و (تق ٢ر / ٢ ش)
أبو العباس، الفضل بن الصباح، النهاوندي، ثم البغدادي، السمسار: توفي سنة خمس وأربعين ومائتين، من العاشرة، ثقة، عابد.
روى عن: إسماعيل بن عبد الكريم الصنعاني (تس ٢٠٣٨٩)، وإسماعيل ابن علية (تس ٢٠٥٣٢) وأيوب بن النجار (تق ٨٩٨)، وجعفر بن عون (تس ٢١٨٥٥)، والحسن بن إسماعيل البجلي (تس ٣١٠٣٩) وسفيان بن عيينة (تس ٣٩٦١ و ٣٣٩٣٣)، و (تهـ ٢٩٠٢)، و (ت)، ومحمد بن فضيل بن غزوان (تس ٢٤٠١٣ و ٢٥٠٢٤ و ٢٧٣٥٤ و ٢٨٠٤٤ و ٢٩١٥٠ و ٢٩١٥٠ و ٢٩١٥٢ و ٣١٠٣٤ و ٣٣١٤٦)، و (تهـ ١١٢٧) و (ت)، وهشيم بن بشير (تس ٢٢٥٧ و ٣٧٣١)، والوليد بن مسلم (تهـ ١٤٣٦) ويزيد بن هارون (تس ٩٥١٢)، وأبي سفيان المعمري (تق ٢٤٣)، وأبي عبد الرحمن المقرئ (تهـ ١٢٩٧) وإسحاق بن سليمان الرازي " الكامل " (٢/ ٤٢٠ / ٥٣٧)، وبكار بن عبد الله الربذي " الكامل " (٢/ ٤٤ / ٢٨١)، وعبد الواحد بن واصل " الكامل " (٥/ ٢٩٧ / ١٤٨٣).
روى عنه: الحسن بن سفيان " الكامل " (٦/ ٢٠٤ / ١٦٧٨)، وعبد الله بن أحمد بن حنبل " السنة " (١/ ١١٣ / ٢٧) – أثراً واحداً؛ عن عبد الله بن إدريس في تكفير الجهمية – وعبد الله بن إسحاق المدائني " الكامل " (٥/ ٢٩٧ / ١٤٣٨).
التعديل والتجريح: اتفقت أقوال أئمة الجرح والتعديل فيه على كونه: ثقة، عابداً، من خيار عباد الله.
وانظر: " الجرح والتعديل " (٧/ ٦٣ / ٣٦٢) و " الثقات " (٩/ ٦ / ١٤٨٧٣) و " تاريخ بغداد " (١٢/ ٣٦١ / ٦٧٩٣) و " تهذيب الكمال " (٢٣/ ٢٢٧ / ٤٧٣٦)، و " الكاشف " (٢/ ١٢٢ / ٤٤٦٦)، و " تهذيب التهذيب " (٨/ ٢٥٠ / ٥١١)، " وتقريب التهذيب " (١/ ٤٤٦ / ٥٤٠٥)، " رجال تفسير الطبري " (٤٤٦/ ٢١٠٣)
٢٤٠ - أبو سهل بن أبي طالب (تهـ: ٢٧٥١)
(الترمذي – وقال الحافظ في الموضع الثاني من ترجمته في التهذيب -: النسائي).',NULL,'403',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (406,'(طب: (تهـ ١ر / ١ش)
أبو سهل، الفضل بن أبي طالب – واسم أبي طالب جعفر – ابن عبد الله بن الزبرقان الهاشمي، العباسي، مولاهم – مولى آل العباس بن عبد المطلب – الواسطي الأصل، ثم البغدادي أخو يحيى بن أبي طالب، والعباس بن أبي طالب، مولده سنة ست وثمانين ومائة، وتوفي ببغداد سنة اثنتين وخمسين ومائتين وله ست وستون سنة، من الحادية عشرة، ثقة.
روى عن: داود بن المحبر (تهـ ٢٧٥١).
روى عنه: أبو جعفر الطبري (تهـ ٢٧٥١).
التعديل والتجريح: ذكره الخطيب في " تاريخ بغداد "، وقال: " كان ثقة " وذكره ابن حبان في " الثقات ".
وانظر: تاريخ بغداد " (١٢/ ٣٦٤ / ٦٧٩٩)، و " الثقات " (٩/ ٧ / ١٤٨٨٠)، و " تهذيب الكمال " (٢٣/ ١٩٢ / ٤٧٢٩)، و " الكاشف " (٢/ ١٢١ / ٤٤٦١)، " تهذيب التهذيب " (٨/ ٢٤٢ / ٥٠٢)، و " تهذيب التهذيب " (٨/ ٢٥١ / ٥١٢) و " تقريب التهذيب " (١/ ٤٤٥ / ٥٣٩٨).
٩٦ - تمييز الفضل بن جعفر، البغدادي.
روى عن: خشيش بن القاسم.
روى عنه: صالح بن بشر بن سلمة الطبراني.
التعديل والتجريح: قال ابن أبي حاتم: " سألت أبي عنه فقال: لا أعرفه ".
وانظر: " الجرح والتعديل " (٧/ ٦٠ / ٣٤٥)، و " تاريخ بغداد " (١٢/ ٣٦٣ / ٦٧٩٨).
٩٧ - تمييز أبو العباس، الفضل بن جعفر، المخرمي، الخواص.
روى عن: أبي نصر التمار، وبشر بن الحارث.
روى عنه: محمد بن مخلد.
وانظر: " تاريخ بغداد " (١٢/ ٣٦٨ / ٦٨٠٥).',NULL,'404',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (407,'٩٨ - تمييز أبو علي، الفضل بن جعفر بن الفضل بن يونس، النخعي البصير الشاعر.
قال المرزباني: " كان أديباً ظريفاً بليغاً، يتشيع وفيه بعض الغلو، وكان أعمى فلقب البصير ".
وانظر: " لسان الميزان " (٤/ ٤٣٨ / ١٣٣٩).
٩٩ - تمييز أبو القاسم، الفضل بن جعفر بن محمد بن عبيد الله بن يزيد بن المنادى، وهو أخو أبي الحسين أحمد بن المنادى، توفي ليلة الأربعاء وقت السحر الأعلى، لتسع خلون من ذي القعدة، سنة ثمان وتسعين، ودفن من الغد يوم الأربعاء، وكان عمره سبعاً وأربعين سنة وشهراً واحداً.
روى عن: جده أبي جعفر بن المنادى، وأبي قلابة الرقاشي، وإسماعيل بن إسحاق القاضي.
روى عنه: أبو جعفر محمد بن عمرو العقيلي.
وانظر: " تاريخ بغداد " (١٢/ ٣٧٤ / ٦٨١٩).
١٠٠ - تمييز الفضل بن جعفر، المدائني، وكيل بن داهر.
روى عن: محمد بن عبد الله بن سليمان الحضرمي.
روى عنه: أحمد بن محمد بن عمران بن الجندي، وذكر أنه سمع منه بالمدائن.
وانظر: " تاريخ بغداد " (١٢/ ٣٧٨ / ٦٨٣٣).

<span data-type="title" id=toc-36>[١٥] باب القاف المثناة </span>(من اسمه قاسم)
٢٤١ - أبو محمد البغدادي (١٠٥١٤)
(أبو داوود)
(طب: ٢٣ ر / ٧ش)',NULL,'405',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (408,'(تس ٨ر / ٤ش) و (تخ ٧ر / ٣ش) و (تهـ ٥ر / ٣ش) و (تق ٣ر / ٢ش)
أبو محمد، القاسم بن بشر بن أحمد – وقيل: القاسم بن أحمد بن بشر - ابن معروف - ويقال: القاسم بن بشر بن معروف، البغدادي: من العاشرة، صدوق.
روى عن: حجاج بن محمد المصيصي (تس ٣٠٤٣٢)، و (تخ ١/ ٢١ و ١/ ٣٥ و ١/ ٤١ و ١/ ٤٢ و ١/ ٤٦)، وروح بن عبادة (تخ ١/ ١٢٩)، وسليمان بن حرب (تس ١٠٥١٤ و ١٠٥٣٦ و ٢٨٢٦١ و ٢٨٦١٠)، و (تهـ ١٦٠ و ١٩٨٣) و (تخ ٢/ ٢٥١) وشبابة بن سوار (تس ١٥٩٣٦ و ١٧٠١٤)، و (تهـ ٢٢٢٩) – وفي نسخة (ابن أسامة) وهو خطأ – وعثمان بن خالد العثماني (تق ٩٢٣) – والد أبي مروان العثماني، وقيل: خالد بن عثمان، العثماني – كما في " لسان الميزان " (٢/ ٣٨١) – وعثمان بن عمر بن فارس (تهـ ٢١٣٩) و (تق ٤٥٥)، وأبي داود الطيالسي (تس ٣٢٠٥٩)، (وتهـ ٥٢٩)، و (تق ١٠٥)، وبشر بن السري، وخالد بن عمرو القرشي، وسفيان بن عيينة، وعبد الله بن نافع الصائغ، وعبد الصمد بن عبد الوارث، وقبيصة بن عقبة " مسند البزار " (٧/ ٢٦٧ / ٢٨٤٩)، ومحمد بن إسماعيل بن أبي فديك " تاريخ بغداد " (٥/ ٤٧٦)، ومصعب بن المقدام، والوليد بن مسلم، ووهب بن جرير بن حازم، ويحيى بن سليم الطائفي، ويزيد بن هارون، وأبي عامر العقدي " مسند البزار " (٦/ ٣٤٦ / ٢٣٥٥).
روى عنه: أحمد بن محمد بن دالان الخيشي، وأبو عبد الله أحمد المعلى بن الشونيزي المقرئ، وعبد الله بن أبي سعد الوراق، وعمر بن محمد بن بجير البجيري، ومحمد بن إبراهيم بن عيسى بن فروخ – نزيل الرقة -، ومحمد بن أحمد بن هلال الشطوي، ومحمد بن إسحاق بن خزيمة، ومحمد بن إسحاق الثقفي وأبو جعفر محمد بن الحسين الدقاق، وهارون بن العباس الهاشمي، والهيثم بن خلف الدوري، ويحيى بن محمد بن صاعد، وأبو بكر البزار " مسند البزار " (٦/ ٣٤٦ / ٢٣٥٥).
وكل اسم لم يعز إلى كتاب – وميزته بالأسود العريض -، من معجم شيوخه وتلاميذه، فهو',NULL,'406',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (409,'من ترجمته في " تاريخ بغداد "
التعديل والتجريح: قال الخطيب البغدادي في " تاريخ بغداد ": " كان ثقة ".
وقال محمد بن إسحاق الثقفي: " صدوق، ثقة ".
وذكره ابن حبان في كتاب " الثقات ".
وانظر: " تاريخ بغداد " (١٢/ ٤٢٧ / ٦٨٧٥)، و " الثقات " (٩/ ١٩ / ١٤٩٥٠)، و " الكامل " (٥/ ١٧٥)، و " المجروحين " (١/ ٢٨٣)، و " تهذيب الكمال " (٢٣/ ٣٣٥ / ٤٧٨٠)، و " تهذيب التهذيب " (٨/ ٢٧٦ / ٥٥٩)، و " تقريب التهذيب " (١/ ٣٨٥ / ٥٤٤٩)، و " رجال تفسير الطبري " (٤٥١/ ٢١٢٤).
٢٤٢ - – القاسم بن الحسن (١٤٤)
(طب: ٢٤٣٣ ر / ١ش)
(تس ٢٣٩٤ ر / ١ش)، و (تخ: ٣٨ ر / ١ش)، و (تهـ: ١ر / ١ش)
القاسم بن الحسن، من الحادية عشرة، لم أعرفه، ولم أجد له ترجمة، ولم يعرفه الشيخ شاكر قبلي (١٤٤ و ٨٣٩٨ و ٨٤٥٩ و ٨٤٦٠) وتردد فيه واضطرب وتمنى أن يجد له من الروايات ما يدله على ترجمته، ولم يتعرض الشيخ التركي في تحقيقه " لتفسير الطبري " (١/ ١٢٢) لترجمته بشيء، وقد صحح اسم أبيه في (١/ ١٤٧ و ٢٠٤) من الحسين إلى الحسن.
