INSERT INTO [b29741] VALUES (521,'١٦٧٦٨ - و ١٧٨٨٢ و ١٩٩٠٩ و ٢١١١٦ و ٢١١٣٤ و ٢١٩٩٦ و ٢٢٥٤٩ و ٢٢٩٩٢ و ٢٢٩٩٥ و ٢٢٣٧١ و ٢٣٦٦٨ و ٢٤٣٤٩ و ٢٥١٩٩ و ٢٥٣٢٩ و ٢٥٤١٤ و ٢٥٧١٦ و ٢٥٧٤٢ و ٢٥٩٧٥ و ٢٦٠١٠ و ٢٦٤١٣ و ٢٦٥٤٤ و ٢٦٥٥٦ و ٢٦٦٤٢ و ٢٦٧٩٣ و ٢٦٧٩٥ و ٢٦٩٠٤ و ٢٧٥٢٣ و ٢٧٨٥٤ و ٢٧٩٥٠ و ٢٨٥٥٢ و ٢٨٦٠٣ و ٢٨٨٤٢ و ٢٩٠٧٢ ٢٩١٨٨ و ٢٩٢٠٣ و ٢٩٥٥٨ و ٢٩٨٢١ و ٣١٣١٧ و ٣١٣٧٢ و ٣١٦١٩ و ٣١٦٢٩ و ٣١٦٩٩ و ٣١٧٤٨ و ٣٢٣٤٦ و ٣٢٦٣٢ و ٣٣٠٩٠ و ٣٣٣٤ و ٣٣٥٣ و ٣٣٥٢٦ و ٣٣٥٨١ و ٣٤٦٠٣ و ٣٤٣٩٤ و ٣٥٠٥١ و ٣٥٠٥٦ و ٣٥١١٨ و ٣٥١٤٥ و ٣٥٢٥٤ و ٣٥٦٨٣ و ٣٥٧٩٨ و ٣٦٠٤ و ٣٦١٤٩ و ٣٦٤٥٨ و ٣٦٥٥٥ و ٣٧٢١٨ و ٣٧٥٣٣ و ٣٧٧٥٥ و ٣٨٢٧٤) و (تهـ ٤٢ و ١٤٥ و ١٤٧ و ٢١٩ و ٢٥٣ و ٦٥١ و ٤١٦ و ٧٢٧ و ٨٢٩ و ٩٨٢ و ١٠٦٢ و ١١٤٨ و ١١٦٧ و ١١٨٣ و ١١٩٢ و ١٣٣٣ و ١٥٦٤ و ١٧٣٦ و ١٩٠٢ و ٢٣٢٦ و ٢٣٧٨ و ٢٥٦٠ و ٢٦١٤ و ٢٨٢١ و ٢٨٣٦ و ٣٠٤٧)، و (تق ٤٤ و ٢٧٨ و ٣١٠ و ٤٠١ و ٤١٨ و ٤٣٤ و ٤٦٥ و ٤٦٦ و ٥١٩ و ٥٦٨ و ٥٨٨ و ٥٩٨ و ٧٧٩ و ٩٢٠)، و (تخ ١/ ١٧) و (م، قد، ت، س، ق) ويزيد بن زريع (تس ١٩٠٨٠) و (ت، س)، ووكيع بن الجراح (تس ٣٨٣٢٧)، وأبي بكر بن عياش (تهـ ١٧٦٣) و (س).
روى عنه: عمر بن محمد الهمداني " الثقات " (٩/ ١٠٤ / ١٥٤٢٨)، وعمران بن موسى السختياني، " الثقات " (٤/ ٣٠٠ / ٣٠١١)، ومحمد بن محمد الهمداني " مقدمة الثقات " (١/ ١).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، لا بأس به.
وقد تصحف اسم شيخه معتمر في الأثر رقم (١٢٧٠٣) إلى معمر.
وانظر: " التاريخ الكبير " (١/ ١٧٤ / ٥٢٤)، و" التاريخ الصغير " (٢/ ٣٨٣ / ٢٩٦٠) و" الجرح والتعديل " (٨/ ١٦ / ٧٠)، و" الثقات " (٩/ ١٠٤ / ١٥٢٨)، و" ذكر أسماء التابعين " (٢/ ٢٢٦ / ١١٠٩)، و" تسمية من أخرجه البخاري ومسلم " (١/ ٢٢٦ / ١٥٨٤)، و" تهذيب الكمال " (٢٥/ ٥٨١ / ٥٣٨٥)، و": الكاشف " (٢/ ١٩١ / ٤٩٨٤)، و" تهذيب التهذيب " (٩/ ٢٥٧ / ٤٨١)، و" تقريب التهذيب " (١/ ٤٩١ / ٦٠٦٠)، و" رجال تفسير الطبري " (٤٩٤/ ٢٣٣٩).
١٢٧ - تمييز محمد بن عبد الأعلى بن كناسة، الأسدي، وهو ابن أخت (إبراهيم بن أدهم الزاهد) توفي بالكوفة سنة (٢٠٩)، وكان عالمًا بالعربية، وأيام الناس، والشعر.',NULL,'519',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (522,'روى عن: الأعمش، وهشام بن عروة وغيرهما.
وانظر: " الطبقات الكبرى " (٦/ ٤٠١).
٢٧٩ - أبو يحيى الصاعقة (تق: ٩٦٥)
(البخاري (٣٦ حديثًا)، وأبو داود، والترمذي، والنسائي)
(طب: تق: ١ر / ١ش)
أبو يحيى، محمد بن عبد الرحيم بن أبي زهير، العدوي، العمري، مولاهم الفارسي، ثم البغدادي، البزاز، صاحب السابري، المعروف بـ (صاعقة)، مولده سنة خمس وثمانين ومائة، وتوفي في شعبان سنة خمس وخمسين ومائتين، وله سبعون سنة، من الحادية عشرة، ثقة، حافظ.
روى عن: أبي المنذر [إسماعيل بن عمر الواسطي] (تق ٩٦٥).
روى عنه: علي بن سعيد الرازي " المعجم الأوسط " (٤/ ١٥٤ / ٣٨٥٣)، ومحمود بن علي الأصبهاني " المعجم الأوسط " (٨/ ٤٦ / ٧٩١٤)، وابن خزيمة " صحيح ابن حبان " (٧/ ١٤٠ / ٢٨٨٥).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، ثبتًا، متقنًا، ضابطًا، عالمًا، حافظًا، وكان من أصحاب الحديث المأمونين، وسُمِّيَ (صاعقة) لجودة حفظه.
وانظر: " الجرح والتعديل " (٨/ ٩ / ٣٣)، و" " تاريخ بغداد " (٢/ ٣٦٣ / ٨٧٣)، و" الثقات " (٩/ ١٣٢ / ١٥٥٩٣)، و" التعديل والتجريح " (٢/ ٦٦٢ / ٥٣٥)، و" تهذيب الكمال " (٢٦/ ٥ / ٥٤١٧)، و" تذكرة الحفاظ " (٢/ ٥٥٣ / ٥٧٥)، و" الكاشف " (٢/ ١٩٥ / ٥٠٠٩)، و" تهذيب التهذيب " (٩/ ٢٧٧ / ٥١٥)، و" تقريب التهذيب " (١/ ٤٩٣ / ٦٠٩١).
١٢٨ - تمييز أبو بكر، محمد بن عبد الرحيم بن أحمد بن إسحاق بن إبراهيم بن سعيد بن مازن بن عمرو، الأزدي، المازني، الكاتب، توفي في ربيع الآخر سنة ٣٨٢.',NULL,'520',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (523,'روى عن: أبي حامد محمد بن هارون الحضرمي، ويحيى بن صاعد.
روى عنه: ابنه علي، والحسن بن محمد الخلال، وعمر بن إبراهيم الفقيه.
وانظر: " تاريخ بغداد " (٢/ ٣٦٥ / ٨٧٦).
١٢٩ - تمييز أبو بكر، محمد بن عبد الرحيم بن إبراهيم بن شبيب بن يزيد بن خالد بن عبد الله بن زاذان، المقرئ، الأصبهاني، نزل بغداد، وحدث بها.
روى عن: أبي عبد الله محمد بن عيسى المقرئ، وسليمان بن داود بن أبي طيبة.
روى عنه: القاضيان أبو بكر أحمد بن كامل، وأبو الحسن الجراحي.
وانظر: " تاريخ بغداد " (٢/ ٣٦٤ / ٨٧٤).
١٣٠ - تمييز أبو الحسين، محمد بن عبد الرحيم بن سعيد بن بشر بن حماد بن ماهان، الدينوري، قدم بغداد.
روى عن: عبد الله بن محمد بن سنان الروحي.
روى عنه: محمد بن أحمد بن جميع الغساني.
وانظر: " تاريخ بغداد " (٢/ ٣٦٤ / ٨٧٥).
وقد شاركه في اسمه عدة أشياخ أعرضت عن ترجمتهم لشهرته، وعدم الخوف من الالتباس بهم.
٢٨٠ - أبو عبد الله بن بزيع (٢٤٥٩)
(مسلم: ٩ أحاديث، والترمذي، والنسائي)
(طب: ٦٢ر / ١٧ش)
(تس ٤١ر / ١١ش) و (تخ ٢ر / ١ش) و (تهـ ١٤ر / ٥ش) و (تق ٥ر / ٣ش)
أبو عبد الله، محمد بن عبد الله بن بزيع، البصري، ثم البغدادي، توفي سنة سبع',NULL,'521',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (524,'وأربعين ومائتين - وقيل: في شهر رمضان، سنة خمسين ومائتين - من العاشرة ثقة.
روى عن: بشر بن المفضل (تس ٤١٦٥ و ٥٦٨٨ و ٩٠٨٩ و ١٠٢٤٤ و ١٢٧٤٨ و ١٢٧٥٢ و ١٢٧٥٨ و ١٦٣١٠ و ٢٠٩٦٥ و ٢٢٦٤٥ و ٢٢٦٥٥ و ٢٣١٤٠ و ٢٤٤١٨ و ٢٦١٢٢ و ٢٩٨٩١ و ٢٩٩٠٥ و ٣١٠٥٨ و ٣١٧٤٠ و ٣٢٠٤٤ و ٣٢٦٩٣)، و (تهـ ١٦٦٠ و ١٨٤٨ و ٢٤٧٠ و ٢٥٨٦ و ٢٨٢٤ و ٣٠٧٢ و ٤٠٣٦)، و (تق ٢٢٦ و ٥٥١ و ٩٣٠)، و (م، ت، س)، وجعفر بن سليمان الضبعي (تس ٤٠٠١)، وأبي محصن حصين بن نمير (تس ١٣٣٠٧ و ٣٦٣٩٤ و ٣٠٤٤٢ و ٣٦٢٣٢)، وحكيم بن حزام (تس ١٧٣٠١)، وحماد بن زيد (تس ١١٣٨٢)، وشجاع [ابن الوليد] (تهـ ٩) وأبي بحر عبد الرحمن بن عثمان البكراوي (تس ٣١٤٥١)، وفضيل بن سليمان النميري (تهـ ١٨٣٤ و ١٩٥٢ و ٢٠٢٥ و ٢٣٩٠)، و (تخ ١/ ١٧ و ٢٢)، (ت، س)، ومحمد بن أبي عدي (تهـ ٣٠١٨)، و (م، ت)، ومعاذ بن معاذ (تس ٣٠٧٢٩)، وأبي المغيرة النضر بن إسماعيل (تس ٣٢١٨٠ و ٣٢١٨٩ و ٣٣٤٦٣)، ووكيع بن الجراح (تق ٧٦٠)، ويزيد بن زريع (تس ٧٣٥٨ و ٢٠٩٦٤) و (م، س)، ويوسف بن خالد السمتي (تس ٢٤٥٩ و ٣١٣٦ و ٥٨٠٤ و ٦١٠٤ و ٩٩٨٧ و ١٣٠١٧)، ويونس - غير مكنى، ولا منسوب - (تس: ٣٢٥٦٩)، وأبي أحمد الزبيري (تق)، وأبي بكر الحنفي (تهـ ٢٥٠٩).
روى عنه: أبو حاتم محمد بن إدريس الرازي " الجرح والتعديل " (٧/ ٢٩٤ / ١٥٩٧) ومحمد بن سهل، " الجرح والتعديل " (٧/ ٢٩٤ / ١٥٩٧).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، لا بأس به.
وقد تصحف اسم جده إلى (زريع) في الأثر رقم (تس: ٢٤٥٩) في طبعتي دار الفكر، وطبعة دار الكتب العلمية.
وإلى (بزيغ) في الأثر (تهـ ٩)، وفي الأثر (تس ٦١٠٤) في طبعتي دار الفكر فقط.
وانظر: " الجرح والتعديل " (٧/ ٢٩٤ / ١٥٩٧)، و" الثقات " (٩/ ١٠٨ / ١٥٤٥٥) و ٩/ ١٠٩ / ١٥٤٥٧) و" تسمية من أخرجهم البخاري ومسلم " (١/ ٢٢٥ / ١٥٧٨)، و" تهذيب الكمال " (٢٥/ ٤٥٣ / ٥٣٢٠)، و" الكاشف " (٢/ ١٨٥ / ٤٩٣٩) و" تهذيب التهذيب " (٩/ ٢٢١ / ٤٠٦)، و" تقريب التهذيب " (١/ ٤٨٦ / ٦٠٠٢)',NULL,'522',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (525,'و" رجال تفسير الطبري " (٤٩٤/ ٢٣٤٠).
٢٨١ - محمد الواسطي (٢٨٧٤)
(طب: ١٠ر / ٢ش)
(تس: ٥ر / ٢ش)، و (تهـ: ٥ر / ٢ش)
محمد بن عبد الله بن سعيد، الواسطي، من العاشرة، لم أعرفه، ولم أجد له ترجمة، وقد أغفله الشيخان شاكر في تحقيق " التفسير " وتحقيق " التهذيب " (٢/ ١٣٢ / ١٧٣ - ١٧٤) وكذلك أغفله الشيخ التركي أيضًا في: تحقيق " التفسير " (٣/ ٢٠٧ - ٢٠٨).
روى عن: يعقوب بن محمد بن عيسى الزهري المدني (تس ٢٨٧٤ و ٢٨٩٥ و ٨٠١١ و ٢٣٩٧٠)، و (تهـ ٢٠٩ و ٢٢٦ و ٦٦٣ و ٦٨٤)، ويزيد بن عياض، وهو خطأ أكيد، والصحيح أنه يزيد بن هارون الواسطي (تس ٢٨٧٥)، (تهـ ٦٦٤).
روى عنه: الحسن بن علي المعمري " المعجم الكبير " (١٠/ ١٦٤ / ١٠٣٣٦)، وأبو جعفر الطبري " المستدرك " (٣/ ٢٠٨ / ٤٨٦٤).
التعديل والتجريح: ذكره الشيخ شاكر في تحقيقه، " لتفسير الطبري " (٣/ ٤٦٣ / ٢٨٦٧) وقال: " محمد بن عبيد الله بن سعيد الواسطي = محمد بن عبد الله، محمد بن عبد الله بن سعيد الواسطي - شيخ الطبري: لم أجد له ترجمة، وسيأتي بهذا الاسم أيضًا في (٢٨٨٨) ولكن سيأتي في الإسناد الذي عقب هذا باسم (محمد بن عبيد الله بن سعيد) بجعل أبيه (عبيد الله) بدل (عبد الله)، وأنا أرجح الذي في إسنادين على الذي في إسناد واحد، ترجيحًا بدائيًا غير محقق ". انتهى.
قلت: وأخرج الطبري في " تهذيب الآثار "، الأثر رقم (تهـ ٦٦٤) وهو نفس الأثر (تس ٢٨٧٥) الذي في " التفسير " عن (يزيد بن عياض)، ولكنه قال فيه زيادة عما في " التفسير " (تس ٢٨٧٥): " أخبرنا يزيد، قال: أخبرنا يزيد بن عياض. . به ".
ولا ريب عندي أن يزيد الأول - الغير منسوب - هو (يزيد بن هارون الواسطي)؛ لأنه',NULL,'523',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (526,'هو الذي يروي عن (يزيد بن عياض) كما ذكر ذلك (المزي) في " تهذيب الكمال " (٣٢/ ٢٢١ / ٧٠٣٥) في ترجمة الأخير (يزيد بن عياض)، وفي (٣٢/ ٢٦١ / ٧٠٦١) في ترجمة الأول (يزيد بن هارون الواسطي).
وهو: أبو خالد، يزيد بن هارون بن زاذان، السلمي، مولاهم، الواسطي مولده سنة ١١٨، وتوفي سنة ٢٠٦، من التاسعة، ثقة، متقن، عابد.
ولا يمكن أن يكون: أبو الحكم، يزيد بن عياض بن جعدبة - بضم الجيم والمهملة بينهما مهملة ساكنة - وقد ينسب لجده - الليثي، المدني، نزيل البصرة، من السادسة، وكذب مالك وغيره، وذلك لقدمه واستحالة رواية الطبري عنه، إذ توفي في البصرة زمن المهدي قبل مولد الطبري بكثير، وقد ترجمت لهما في " المعجمين: الكبير، والصغير ".
وأظن إن إسناده في " تهذيب الآثار " أصح منه في " التفسير " من ثلاثة وجوه:
أولها: أنه سمي أباه (عبد الله)، في حين تصحف في " التفسير " إلى (عبيد الله).
ثانيهما: أنه نسبه فيه، فقال: (الواسطي) في حين لم ينسبه في " التفسير ".
ثالثها: أنه أثبت بينه وبين (يزيد بن عياض) واسطة، هو (يزيد بن هارون الواسطي) المتوفى سنة ست ومائتين؛ وهو حري أن يكون سمع - وقد سمع - (يزيد بن عياض) المتوفى في خلافة المهدي الممتدة بين سنة سبع وعشرين ومائة، وسنة تسع وستين ومائة.
وكذلك الخبر الذي قبله من " تهذيب الآثار " (تهـ ٦٦٣) أخرجه في " التفسير " (تس ٢٨٧٤) وقد أخرج الخبر أيضًا ابن أبي شيبة في " المصنف " (٢/ ٢٧٩ / ٨٩٦٢)، والنسائي في " الكبرى " (٢/ ١٠٦ / ٢٥٩٣ و ٢٥٩٤ و ٢٥٩٥)، و" المجتبى " (٤/ ١٨٣ / ٢٢٨٥ و ٢٢٨٦)، والضياء المقدسي في " المختارة " (٣/ ١١٠ / ٩١١)، جميعهم، موقوفًا على عبد الرحمن بن عوف من طريق ابن أبي ذئب، عن ابن شهاب، عن أبي سلمة بن عبد الرحمن، عن أبيه به.
وأخرجه ابن ماجه في " السنن " (١/ ٥٣٢ / ١٦٦٦)، وأبو بكر الخطيب في " تاريخ بغداد " (١١/ ٣٨٢ / ٦٢٥٢)، من نفس الطريق، مرفوعًا.
وقد تصحف اسم أبيه في الأثر رقم (٢٨٧٥) من التفسير من (عبد الله) مكبر، إلى',NULL,'524',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (527,'(عبيد الله) مصغر، وليس في الرواة عن يزيد بن هارون من اسمه محمد بن عبيد الله، إلا ابن المنادي، واحتمال أن يكون هو صاحب الترجمة - كما أشار إلى ذلك الشيخ شاكر - احتمال ممكن؛ إن سَلَمْنا أن شيخ الطبري هو: يزيد بن هارون، وليس يزيد بن عياض، وليس لنا إلا أن نسلم بذلك لما سبق وبيناه، والله تعالى أعلم.
٢٨٢ - مطين الحضرمي (تخ: ١/ ٣٣)
(طب: تخ ١ر / ١ش)
أبو جعفر، محمد بن عبد الله بن سليمان، الحضرمي، الكوفي، المعروف بـ (المطين): مولده سنة اثنتين ومائتين، وتوفي في ربيع الآخر سنة سبع وسبعين ومائتين، ثقة.
روى عن: مصرف بن عمرو اليامي (تخ ١/ ٣٣)، وأحمد بن يونس، وعبد الحميد بن صالح، وعبيد بن يعيش، وعلي بن حكيم الأودي، والهيثم بن عبيد الله القرشي، ويحيى بن بشر الحريري.
روى عنه: علي بن حسان الدمي، وعلي بن عبد الرحمن البكائي، وأبو بكر الإسماعيلي، وأبو بكر النجاد، وأبو القاسم الطبراني.
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، جبلًا، صدوقًا، وكان من أوعية العلم.
وانظر: الجرح والتعديل " (٧/ ٢٩٨ / ١٦١٨)، " تذكرة الحفاظ (٢/ ٦٦٢ / ٦٨٢)، والمقتنى في سرد الكنى " (١/ ١٤٩ / ١١٢٠)، ومولد العلماء ووفياتهم (٢/ ٦٢٥)، و" سير أعلام النبلاء " (١٤/ ٤١)، و" طبقات المحدث " (١/ ١٠٦ / ١٢٠٣)، و" لسان الميزان " (٥/ ٢٣٣ / ٨١٥).
٢٨٣ - محمد بن عبد الله بن صفوان الثقفي (تهـ: ١٨٥٥)
(طب: ٣ر / ٢ش)
(تهـ: ١ر / ١ش)، و (تخ: ٢ر / ١ش)',NULL,'525',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (528,'محمد بن عبد الله بن صفوان، الثقفي، من العاشرة، ولعله: محمد بن عثمان بن أبي صفوان، الثقفي، أبو عبد الله، وقيل: أبو صفوان، البصري. وستأتي ترجمته في موضعها من كتابي هذا.
روى عن: أمية بن خالد (تخ ٢/ ٣٨٩ و ٢/ ٥٢٤)، وعبد الرحمن بن مهدي (تهـ ١٨٥٥).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: لم أقف له على رواية في كتب التراجم، والرجال والحديث التي بين يدي، وأخشى أن يكون محرفًا من (محمد بن عثمان بن أبي صفوان الثقفي) وقد ترجمت له في موضعه من كتابي هذا.
وشيخاه هنا هما: الأول: أبو عبد الله، أمية بن خالد بن الأسود بن هدية القيسي البصري، أخو هدبة، وهو الكبير، توفي سنة مائتين، أو سنة إحدى ومائتين، من التاسعة، صدوق.
والثاني: أبو سعيد، عبد الرحمن بن مهدي بن حسان بن عبد الرحمن العنبري، مولاهم، البصري، اللؤلؤي، ولد سنة ١٣٥، وتوفي سنة ١٩٨، من التاسعة، ثقة، ثبت، حافظ، عارف بالرجال والحديث، وقد ترجمت لهما في " المعجمين: الكبير، والصغير " أيضًا، وكلاهما روى عنه محمد بن عثمان بن أبي صفوان، الثقفي، المترجم في موضعه من كتابي هذا، كما هو في تراجم الثلاثة من " تهذيب الكمال " وغيره.
وهذا يؤكد لي أن صاحب الترجمة، هو: عينه محمد بن عثمان بن أبي صفوان، الثقفي، المترجم في موضعه من كتابي هذا، والله أعلم.
وقد يشتبه ببعض الرواة، الذين شابههم في أسمائهم، وأنسابهم، ذكرتهم تمييزًا كما يلي:
١٣١ - تمييز محمد بن عبد الله بن إنسان الثقفي الطائفي، من السادسة، لَيِّن.',NULL,'526',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (529,'روى عن: أبيه عبد الله، وعبد الله بن عبد ربه بن الحكم الثقفي.
روى عنه: عبد الله بن الحارث المخزومي.
قال يحيى بن معين: " ليس به بأس ".
وانظر: " تهذيب الكمال " (٢٥/ ٤٥٢ / ٥٣٢٩)، و" لسان الميزان " (٧/ ٣٦٣ / ٤٦٣٥)، و" تهذيب التهذيب " (٩/ ٢٢١ / ٤٠٥)، و" تقريب التهذيب " (١/ ٤٨٦ / ٦٠٠١).
١٣٢ - تمييز أبو العنبس، محمد بن عبد الله بن قارب، الثقفي.
روى عن: عبد الله بن عمرو بن العاص (بخ)، وأبيه عبد الله بن قارب.
روى عنه: داود بن أبي عاصم الثقفي، وعبد الملك بن عمير.
روى له البخاري في الأدب حديثًا واحدًا.
وانظر: " تهذيب الكمال " (٣٤/ ١٤٤ / ٧٥٤٦)، و" تهذيب التهذيب " (١٢/ ٢٠٧ / ٨٧٠).
٢٨٤ - محمد بن عبد الله الطوسي (٣٤٥٤٤)
(طب: (تس: ١ر / ١ش)
محمد بن عبد الله الطوسي، من الحادية عشرة، لم أعرفه، ولم أجد له ترجمة، ولم يتعرض الشيخ التركي في تحقيقه " لتفسير الطبري " (٢٣/ ١٤٦) لترجمته بشيء، وقد أخرج المصنف نفس الأثر، سندًا ومتنًا في " التاريخ " (١/ ٢٨) مثله، إلا أن شيخه فيه هو: أحمد بن محمد بن حبيب، الطوسي، وهو صدوق، سيء الحفظ.
روى عن: علي بن الحسن بن شقيق المروزي (تس (٣٤٥٤٤).
روى عنه: محمد بن جرير الطبري.
التعديل والتجريح: لم أقف له على ترجمة، ولا ذكر في كتب الرجال ولا الحديث التي بين يدي، ولم أعرف من هو؟!.',NULL,'527',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (530,'وشيخه هنا: أبو عبد الرحمن علي بن الحسن بن شقيق المروزي، توفي سنة خمس عشرة ومائتين، وقيل قبل ذلك، من كبار العاشرة، ثقة حافظ.
وليس في من روى عنه في ترجمته من " تهذيب الكمال " (٢٠/ ٣٧١ / ٤٠٤٠) من اسمه هكذا إلا (محمد بن عبد الله بن قهزاد المروزي)، فإن كان هو فهو من رجال مسلم، توفي سنة اثنتين وستين ومائتين، الحادية عشرة، صدوق، ثقة وقد ترجمت لهما في " المعجمين الكبير والصغير ".
٢٨٥ - أبو عبد الله العثماني (٧٦٣)
(النسائي)
(طب: ١١٧ر / ١٤ش)
(تس: ٦٥ر / ١٤ش) و (تخ ٣ر / ٢ش) و (تهـ ٣٦ر / ٨ش) و (تق ١٣ر / ٨ش)
أبو عبد الله، محمد بن عبد الله بن عبد الحكم بن أعين، العثماني، الأموي، مولاهم المصري، الفقيه، مولى عثمان بن عفان، أخو سعد، وعبد الرحمن، من الحادية عشرة، مولده سنة اثنتين وثمانين ومائة، وتوفي في ذي القعدة، سنة ثمان وستين ومائتين، ثقة.
روى عن: إسحاق بن الفرات - قاضي مصر - (تس ٣٣٧٠٠)، و (تهـ ٢٦٥٤)، و (س)، وإسماعيل ابن مرزوق (تس ٤٤٦٢ و ٤٤٦٣ و ٤٤٦٤ و ٤٤٨١)، وأيوب بن سويد الرملي (تس ٢٩٢٨ و ٢٩٦١ و ٥٤٩٨ و ٩٥٩٤ و ١١٣٧٤ و ١٦٢٧٧ و ٢٢٢٠١ و ٢٥١٨٧)، و (تهـ ٧٣٤ و ٨٨١ و ١٥٨٨ و ٢١٢٧)، و (تق ١٠٣٥)، و (تخ ١/ ١٦)، وبشر بن بكر التنيسي (تس ٣٤٥٩ و ٣٤٨٢ و ٤٨٥٠ و ٦٦٥٢ و ٩٦٥٩ و ٣٤٣٣٧)، و (تهـ ٣٦٣ و ١٨١٢)، و (تق ١٧٠ و ٩١٨)، وحجاج بن رشدين بن سعد (تس ٧٦٣ و ٤٦١٢ و ٥٥٩٢ و ١١٣٥٨)، و (تق ٣٩ و ٨٢ و ٦٩١ و ٦٩٣)، و (تهـ ١٣٥٩)، وأبي سلامة، زياد بن يونس (تس ٣٣٨٨٤) وخالد بن نزار الأيلي (تس ٦٦١١ و ٦٦٢٣ و ٢٥٨٦٥)، و (تق ٥٢٦)، و (سي)، وشعيب بن الليث بن سعد (تس ٣٠٤١ و ٤٠٨٤ و ٤٥٠٨ و ٤٥٨٧ و ٤٨٣٤ و ٥٣٩٢ و ٥٤٦٨ و ٥٤٦٩ و ٩٥٠٩ و ١٢٨٢٣ و ٢٢٦١٠ و ١٢٦٣٧ و ٢٣٨٦٢ و ٢٤٤٢٠ و ٢٥٢٤٢ و ٣٤٠١٩ و ٣٤٦٨٦ و ٣٥٤٣٢ و ٣٨١٧٦)، و (تهـ ٤٢٧ و ٤٣١ و ١٦٩٥ و ٢٢٩٧ و ٢٢٩٨ و ٢٤٠٣ و ٢٥٦٧ و ٢٦٧٥ و ٢٦٨٩ و ٢٨٩٣)، و (تق ١٠١ و ٣٤١)، و (تح ٢/ ٢١٥ و ٢٣١)، و',NULL,'528',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (531,'(س)، وأبيه عبد الله بن عبد الحاكم (تس ٤٥٠٨ و ٤٥٨٧ و ٤٨٣٤ و ٥٣٩٢ و ٥٤٦٨ و ٥٤٦٩ و ٩٥٠٩ و ١٢٨٢٣ و ٢٢٦١٠ و ١٢٦٣٧ و ٢٣٨٦٢ و ٢٤٤٢٠ و ٢٥٢٤٢ و ٣٤٠١٩ و ٣٤٦٨٦ و ٣٥٤٣٢ و ٣٨١٧٦)، و (تهـ ٤٢٧ و ١٦٩٥ و ٢٤٠٣ و ٢٥٦٧ و ٢٦٧٥ و ٢٦٨٩ و ٢٨٩٣)، و (تق ١٠١ و ٣٤١)، و (س)، وأبي عبد الرحمن عبد الله بن يزيد المقرئ (تس ٧٤٩٥ و ١٠٢٦٧)، و (س)، وأبي بكر عبد الحميد بن أبي أويس [الأعشى] (تس ٤٣٣٦) (س)، ومحمد بن إسماعيل بن أبي فديك (تس ٨٧٣٤ و ١٠٣٢٣ و ١٤٦٢٢ و ٢٨٤٤٠ و ٣٠٨٢٥)، و (تق ١٦٤) و (تهـ ٥١٩ و ١٣٨٤ و ٢٧٨٥)، (س)، وأبي زرعة وهب الله بن راشد الحجري المؤذن (تس ٢٣٨٤ و ٢٨٩٨ و ٥٠٠٨ و ٥٣٨٩ و ١١٥١٣ و ١٦٣٩٦ و ٢٩٨٨٤ و ٣١٣١٨ و ٣١٩٨٥)، و (تق ٢٥ و ٥٩٩)، و (تهـ ٦٠٠ و ٦٤٥ و ٧٢٨ و ٧٣٢ و ١٦٧٧ و ٢٢٩٧ و ٢٧٠٧ و ٢٨٤٢)، ويحيى بن سلام البصري (تس ٣٤٧٥)، وإسحاق بن إبراهيم البارودي " الكامل " (٧/ ٥٩ / ١٩٨٨)، وسعيد بن أبي مريم " الكامل " (٤/ ١٩٩ / ١٠١٠)، وعبد الغفار بن الحسن البصري " الكامل " (٣/ ٢٣٧ / ٧٢٩)، ومحمد بن عاصم المصري " ضعفاء العقيلي " (١/ ١٠٢ / ١١٩)، ومحمد بن غالب (تمتام) " المجروحين " (٣/ ١٢٤ / ١٢١٨)، ومحمد بن موسى (المجروحين " (٢/ ٩٧ / ٦٦٥).
روى عنه: إبراهيم بن أحمد بن مروان " علل الدارقطني " (٥/ ٢٢٩ / ٨٤٠)، و [أبو عمر] شكر " الثقات " (٩/ ٢٧٢ / ١٥٥٩٤).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، صدوقًا، إمامًا حافظًا، عالمًا، فاضلًا، متواضعًا، شيخ الإسلام، لا بأس به، عارفًا بأقاويل الصحابة والتابعين، وهو أظرف من أن يكذب، وأحد فقهاء مصر، من أصحاب مالك، كان المفتي بمصر في أيامه، وكان مقدمًا في العلم والديانة، وكان أهل مصر لا يعدلون به أحدًا.
قلت: قد وقع تصحيف في اسم جده في الأثر رقم (تس: ١٦٢٧٧) من (عبد الحكم) إلى (عبد الكريم) وذلك في نسخة دار الكتب العلمية، بسبب كلام الطبري على (عبد الكريم) في حديث سابق، وسائر النسخ (عبد الحكم).
وانظر: " تسمية فقهاء الأمصار " (١٢٩/ ١٠٥)، و" تسمية من لم يرو عنه غير واحد " (١٢٩)،',NULL,'529',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (532,'و" الجرح والتعديل " (٧/ ٣٠٠ / ١٦٣٠)، و" الثقات " (٩/ ١٣٢ / ١٥٥٩٤)، و" مولد العلماء ووفياتهم " (٢/ ٥٨٥)، والتقييد " (١/ ٧٤)، و" تهذيب الكمال " (٢٥/ ٤٩٧ / ٥٣٥٤)، و" الكاشف " (٢/ ١٨٧ / ٤٩٥٩، و" تذكرة الحفاظ " (٢/ ٥٤٦ / ٥٦٦)، و" ميزان الاعتدال " (٣/ ٦١١ / ٧٨١٥)، و" سير أعلام النبلاء " (١٢/ ٤٩٧ / ١٨١)، و" طبقات المحدثين " (١/ ٩٩ / ١١٣٢)، و" تهذيب التهذيب " (٩/ ٢٣٢ / ٤٣٥) و" تقريب التهذيب " (١/ ٤٨٨ / ٦٠٢٨)، و" لسان الميزان " (٧/ ٣٦٦ / ٤٦٥٧)، و" طبقات الحفاظ " (١/ ٢٤٥ / ٥٤٤)، و" رجال تفسير الطبري " (٤٩٦/ ٢٣٤٥).
١٣٣ - – تمييز محمد بن عبد الله بن عبد الحكم البالسي، من الثانية عشرة، لَيِّن.
روى عن: أحمد بن مسعود.
روى عنه: أبو حامد بن محمد بن الحسين.
وانظر: " تهذيب الكمال " (٢٥/ ٥٠٠ / ٢٣٥٥)، و" تهذيب التهذيب " (٩/ ٢٣٣ / ٤٣٦)، و" تقريب التهذيب " (١/ ٤٨٨ / ٦٠٢٩).
٢٨٦ - أبو مسعود الهلالي (١٢٧٩)
(أبو داود، والنسائي، وابن ماجه)
(طب: ١١ر / ٩ش)
(تس: ١٠ر / ٨ش)، و (تهـ: ١ر / ١ش)
أبو مسعود، محمد بن عبد الله بن عبيد بن عقيل، الهلالي، البصري: من الحادية عشرة، صدوق.
روى عن: أبي عاصم الضحاك بن مخلد (تس ٣٤٠٦٧)، وعبد الرحمن بن المبارك (تهـ ١٧٨٩)، وعبد العزيز بن الخطاب (تس ١٢٧٩)، وعبيد الله بن موسى (تس ٢٤٨٧٥)، وعمران بن محمد الحداد (تس ٩٦٤٢)، وفروة بن أبي المغيرة الكندي (تس ٣٨٠٠٣)، ومحمد بن خالد بن عثمة (تس ٣٢٠٠٩ و ٣٢٠٢٣)، و (ق)، ومسلم بن إبراهيم (تس ٦١٧٩)، وأبي علي الحنفي (تس ٣٢٢٢٦ و ٣٧١٢٥).',NULL,'530',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (533,'روى عنه: عمر بن حفص البزار " الثقات " (٩/ ١١٩ / ١٥٥١٣).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، يغرب، لا بأس به.
وانظر: الثقات (٩/ ١١٩ / ١٥٥١٣)، و" تهذيب الكمال " (٢٥/ ٥٠٦ / ٥٣٦٠)، و" الكاشف " (٢/ ١٨٨ / ٤٩٦٢) و" تهذيب التهذيب " (٩/ ٢٣٥ / ٤٤٢)، و" تقريب التهذيب " (١/ ٤٨٩ / ٦٠٣٤)، و" رجال تفسير الطبري " (٤٩٧/ ٢٣٤٧).
٢٨٧ - أبو جعفر المخرِّمي (٣٧٣٣)
(البخاري، وأبو داود، والنسائي)
(طب: ٢٥ر / ١٥ش)
(تس ١٠ر / ٧ش) و (تخ ١ر / ١ش) و (تهـ ١٠ر / ٨ش) و (تق ٤ر / ٣ش)
أبو جعفر، محمد بن عبد الله بن المبارك، المخرِّمي، البغدادي، المدائني، قاضي حلوان، توفي سنة أربع وخمسين ومائتين، من الحادية عشرة، ثقة، حافظ.
روى عن: جعفر بن عون (تس ١٨٠٦٧ و ١٨٠٨٨)، و (تق ٨٢٥)، وشبابة بن سوار (تس ٣٧٣٣ و ٢٧٦٠٠)، و (د، س)، وعبد الرحمن بن غزوان - المعروف بقراد أبي نوح - (تس ٨٩٣٩)، (تهـ ٢٤٠ و ٢٠٤٠)، و (خ، س)، وعبد الرحمن بن مهدي (تس ٢٢١٧٧)، و (د)، وعبد الصمد بن عبد الوارث (تهـ ٩٩٤)، ومحمد بن بشر (تهـ ٤٠١٨)، ومسهر بن عبد الملك بن سلع الهمداني (تهـ ٢٥٤١)، (عس)، وأبي كامل مظفر بن مدرك (تهـ ٢٠٤١)، و (س)، ومنصور بن سلمة (تهـ ١٩٨٤)، وموسى بن داود (تق ١٠٢٩)، وأبي الفتح نصر بن المغيرة (تخ ٢/ ٣٥١)، ووكيع بن الجراح (تهـ ٢٢٤٩)، و (تق ٧٥٩ و ٩١١)، و (د، س)، وأبي عامر العقدي (تس ٥٤٥٠ و ٢٤٣٢٥) و (س)، وأبي هشام المخزومي (تهـ ٨٤٧ و ١٧٧٧)، و (د، س)، ويحيى بن الصامت (تس ٦٣١٥)، وأبي الجواب (تس ١٨٩٧٧) - وهو الأحوص بن جواب الضبي - كما في " الكنى والأسماء " (١/ ١٩٧ / ٦٠٩).',NULL,'531',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (534,'روى عنه: أحمد بن حنبل " المسند " (٤/ ١٦٣ / ١٧٥٣٢)، والحسن بن محمد بن شعبة " المستخرج " (٢/ ٣٢١ / ١٦٤٤)، وعبد الله بن علي بن الجاود النيسابوري " المنتقى " (١/ ٢١ / ٣٤)، وعبد الرحمن بن أبي حاتم الرازي " الجرح والتعديل "، (٧/ ٣٠٥ / ١٦٥٨)، ومحمد بن إبراهيم " معجم شيوخ الإسماعيلي " (١/ ٤٦٣ / ١١٧)، وأبو بكر البزار " مسند البزار " (٢/ ١٢٥ / ٤٨٢)، وأبو عوانة الأسفرائيني، مسند أبي عوانة، (١/ ٤٨٢ / ١٧٩٤)، وأبو يعلى الموصلي، " معجم أبي يعلى " (١/ ٥٩ / ٣١).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، ثبتًا، عالمًا، صدوقًا، حافظًا، متقنًا، حجةً، جليل القدر، وكان من أحفظ الناس للأثر، وأعلمهم بالحديث، ومن المتقنين المأمونين، وما رُئيَ بالعراق مثله.
قلت: وقد تصحفت نسبته في الأثر (تهـ ١٧٧٧) من نسخة (الرشيد) من (المخرمي)، إلى (المخزومي).
وتصحف اسم شيخه في الأثر رقم (تهـ ٨٤٧) من (أبي هشام المخزومي) إلى (ابن هشام المخزومي).
وانظر: " الكنى والأسماء " (١/ ١٨١ / ٥٤٢)، و" الجرح والتعديل " (٧/ ٣٠٥ / ١٦٥٨)، و" الثقات " (٩/ ١٢١ / ١٥٥٣٠) و" من روى عنهم البخاري ومسلم " (١/ ٢٠٤ / ٢٤٢)، وذكر أسماء التابعين ومن بعدهم " (١/ ٣٣٩ / ١٠٢٨)، و" رجال صحيح البخاري " (٢/ ٦٥٩ / ١٠٦٠)، و" تسمية من أخرجهم البخاري ومسلم " (١/ ٢٢٠ / ١٥٢٢)، و" تاريخ بغداد " (٥/ ٤٢٣ / ٢٩٣٥)، و" التعديل والتجريح " (٢/ ٦٥٤ / ٥٢٤)، و" تهذيب الكمال " (٢٥/ ٥٣٤ / ٥٣٧١)، و" تذكرة الحفاظ " (٢/ ٥١٩ / ٥٣٧)، و" السير " (١٢/ ٢٦٥ / ١٠١)، و" طبقات المحدثين " (١/ ١٠٠ / ١١٣٣)، و" الكاشف " (٢/ ١٨٩ / ٤٩٧٢)، و" المقتني " (١/ ١٤٨ / ١١٠٧)، و" الميزان " (٧/ ٤٦٢ / ١٠٩٠٥) و" تهذيب التهذيب " (٩/ ٢٤٢ / ٤٥٤)، و" تقريب التهذيب " (١/ ٤٩٠ / ٦٠٤٥)، و" لسان الميزان " (٧/ ٥١٨ / ٥٨٥٩)، و" طبقات الحفاظ " (١/ ٢٣١ / ٥١٥)، ورجال تفسير الطبري " (٤٩٧/ ٢٣٥٠).
١٣٤ - تمييز أبو جعفر، محمد بن عبد الله بن عمار بن سوادة المخرمي، الأزدي، البغدادي، نزيل الموصل، ولد سنة اثنتين وستين ومائة، وتوفي سنة اثنتين وأربعين ومائتين، وله ثمانون سنة، من العاشرة، ثقة، حافظ، أخرج له النسائي.',NULL,'532',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (535,'وانظر: " الجرح والتعديل " (٧/ ٣٠٢ / ١٦٤١)، و" الثقات " (٩/ ١١٣ / ١٥٤٧٩)، و" الكامل " (٦/ ٢٧٩ / ١٧٦٤) و" تاريخ بغداد " (٥/ ٤١٦ / ٢٩٣١)، و" تهذيب الكمال " (٢٥/ ٥٠٩ / ٥٣٦٢)، و" تذكرة الحفاظ " (٢/ ٤٩٤ / ٥١٠)، و" الكاشف " (٢/ ١٨٨ / ٤٩٦٤)، و" تهذيب التهذيب " (٩/ ٢٣٦ / ٤٤٤)، و" تقريب التهذيب " (١/ ٤٨٩ / ٦٠٣٦).
١٣٥ - تمييز محمد بن عبد الله المخرمي: قال ابن ماكولا: لعله من ولد مخرمة بن نوفل ".
روى عن: الشافعي.
روى عنه: عبد العزيز بن محمد بن الحسن بن زبالة.
قال ابن الصلاح في الخمسين من " علوم الحديث "، " غير مشهور ".
وانظر: " ميزان الاعتدال " (٨/ ١٨١ / ٦٤٤)، و" لسان الميزان " (٥/ ٢٣٥ / ٨١٩).
٢٨٨ - محمد بن عبد الله المخزومي (تهـ ١٧٧٧)
(طب: تهـ ١ر / ١ش)
محمد بن عبد الله، المخزومي، خطأ، والصحيح: محمد بن عبد الله، المخرمي، صاحب الترجمة السابقة رقم (٢٨٧).
٢٨٩ - محمد بن عبد الله الواسطي (٢٠)
(طب: ٢ر / ١ش)
(تس: ر١/ ١ش)، و (تق: ١ر / ١ش)
محمد بن عبد الله بن أبي مخلد، الواسطي، من التاسعة، لم أعرفه، ولم أجد له ترجمة، ولم يعرفه الشيخ علي رضا قبلي، ولم أقف له في " التفسير " على غير هذا الأثر، وقد أغفله الشيخ شاكر قبلي مع أنه خرج الحديث، ونقل عن ابن كثير تصحيح إسناده، ولم',NULL,'533',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (536,'يتعرض الشيخ التركي في تحقيقه " لتفسير الطبري " (١/ ٢٧ - ٢٨) لترجمته بشيء.
روى عن: سفيان بن عيينة (تس ٢٠)، و (تق ٢٠١).
روى عنه: محمد بن جرير الطبري.
التعديل والتجريح: أغفله الشيخان أحمد، ومحمود شاكر، وكذلك الدكتور التركي في تحقيقهم " لتفسير الطبري "، ولم يعرفه الشيخ على رضا في تحقيقه لـ " الجزء المفقود من تهذيب الآثار ".
وأخشى أن يكون في اسمه تحريف ما؛ لأنني وجدت من طبقته وبلده ثلاثة يشاركونه في بعض اسمه، وانظر الترجمة رقم (٣٥٤) من هذا الكتاب أيضًا.
وشيخه هنا هو: أبو محمد، سفيان بن عيينة، الهلالي، المكي، من كبار الثامنة إمام، ثقة، وعليه فيكون صاحب الترجمة من التاسعة، فما دونها، وقد ترجمت لسفيان في " المعجمين الكبير، والصغير "، كما ذكرت ذلك فيما سبق، وعلى كل الأحوال فجهالة صاحب الترجمة لا تضر؛ لأنه لم ينفرد بالحديث فقد تابعه عليه، يونس بن عبد الأعلى الصدفي، وهو ثقة، وكذلك سائر رجال الأثر الذي في " التفسير "، ثقات، وكذلك الخبر الذي " الجزء المفقود من تهذيب الآثار " يقال فيه ما يقال في الأثر.
١٣٦ - تمييز أبو الحسن، شباب بن صالح بن عبد الله بن أبي مخلد، الواسطي البزاز.
وانظر: " تهذيب الكمال " (٣٥/ ١٣٩ / ٥١٧٨) ترجمة محمد بن خالد الواسطي.
١٣٧ - تمييز خالد بن محمد بن أبي مخلد، الواسطي.
وانظر: السنة لابن أبي عاصم " (١/ ٥٣ / ١١٥).
١٣٨ - تمييز حميد بن أبي مخلد، الواسطي.',NULL,'534',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (537,'وانظر: المعجم الكبير (١١/ ١٤٢ / ١١٢٩٨).
٢٩٠ - أبو عبد الله بن أبي الشوارب (٢٣٤١)
(مسلم: ١٠ح، والترمذي، والنسائي، وابن ماجه)
(طب: ٥٦ر / ٢ش).
(تس ٣٨ر / ٢ش) و (تخ ٢ر / ٢ش)، و (تهـ ٩ر / ٢ش) و (تق: ٧ر / ٢ش)
أبو عبد الله، محمد بن عبد الملك بن أبي الشوارب، الأموي، القرشي البصري: توفي سنة أربع وأربعين ومائتين، من كبار العاشرة، صدوق.
روى عن: عبد الواحد بن زياد (تس ٤٢٣٦ و ٤٢٦٥ و ٤٢٨٥ و ٧٤٢٧ و ٧٤٥٧ و ٨١٣٥ و ٨١٣٦ و ٨٧٨٥ و ١٠٣٦٠ و ١١٢٢٣ و ١١٢٦٢ و ١٢٣١٨ و ١٢٤٩٣ و ١٦٤١١ و ١٦٤٢٩ و ٢٢٠٢٩ و ٢٥٢٢٣ و ٢٥٨٨١ و ٣١٧٤٩ و ٣٢٤٤٥ و ٣٢٨٣٧ و ٣٣٤٤٧ و ٣٧٥٠٧ و ٣٧٦٦٦)، و (تق ٤٥ و ١٣٦ و ١٥١ و ١٥٩ و ٨٦٠)، و (تهـ ٣٨٩ و ٧٩٦ و ١٦٣٠ و ٢٠٠٠ و ٢٤٢٣ و ٣٠٤٢ و ٣٠٤٦ و ٣٠٥٦) و (تخ ١/ ٥٣٢)، و (ق)، ويزيد بن زريع (تس ٢٣٤١ و ٢٣٤٣ و ٣٣٣٩ و ٤٤٤٠ و ٤٥٦٤ و ٦١٦٢ و ١١١٩٠ و ١١٣٩١ و ١١٣٩٨ و ٢٠٩٦٤ و ٢٢١٢١ و ٢٥٠٨٩ و ٢٥٧٨٢ و ٢٥٨٠٥ و ٢٦١٩١)، و (تهـ ١٨٤٧)، و (تق ٢٤٩ و ٤٨٤)، و (تخ ١/ ٥٢)، و (ت، س).
روى عنه: أحمد بن سلمة النيسابوري " الجرح والتعديل " (٨/ ٥ / ١٨)، والحسين بن إدريس الأنصاري " الثقات " (٩/ ١٠٢ / ١٥٤١٧)، وعلي بن الحسين بن الجنيد " الجرح والتعديل " (٨/ ٥ / ١٨)، ومحمد بن المثنى " الجرح والتعديل " (٨/ ٥ / ١٨).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: شيخًا جليلًا، ثقةً، صدوقًا، لا بأس به، وقال أحمد بن حنبل: " ما بلغني عنه إلا خيرًا .. ".
وانظر: " الجرح والتعديل " (٨/ ٥ / ١٨)، و" الثقات " (٩/ ١٠٢ / ١٥٤١٧)، وتاريخ أسماء الثقات " (١/ ٢١١ / ١٢٧٢)، و" ذكر أسماء التابعين "، (٢/ ٢٢٧ / ١١١١)، و" مولد العلماء " (٢/ ٥٣٦)، و" تسمية من أخرجهم البخاري ومسلم " (١/ ٢٢٦ / ١٥٨٦)، و" رجال مسلم " (٢/ ١٨٨ / ١٤٦٣) و" تاريخ بغداد " (٢/ ٣٤٤ / ٨٤٧)، و" تهذيب الكمال " (٢٦/ ١٩ / ٥٤٢٤)، و" السير " (١١/ ١٠٣ / ٣٢)، و" طبقات المحدثين ".',NULL,'535',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (538,'(١/ ٨٩ / ٩٩٤)، و" المقتنى " (١/ ٣٦٠ / ٣٧١٢)، و" الكاشف " (٢/ ١٩٦ / ٥٠١٦)، و" تهذيب التهذيب " (٩/ ٢٨١ / ٥٢٣)، و" تقريب التهذيب " (١/ ٤٩٤ / ٦٠٩٨)، و" رجال تفسير الطبري (٥٠١/ ٢٣٦٦).
٢٩١ - أبو بكر الغزّال (٥٨٩٩)
الأربعة
(طب: ٥ر / ٤ش)
(تس: ٤ر / ٣ش)، و (تق: ١ر / ١ش)
أبو بكر، محمد بن عبد الملك بن زنجويه، الغزال، البغدادي، جار أحمد بن حنبل، وجليسه وصاحبه، توفي سنة ثمان وخمسين ومائتين، من الحادية عشرة، ثقة.
روى عن: إسماعيل بن عبد الكريم (تس ٥٨٩٩ و ٢٢٠٨٨)، وأبي اليمان الحكم بن نافع (تس ٢٦٧٩٠)، وعبد الرزاق بن همام (تس ٦٢٥٤)، و (د، ت، س، ق)، وأبي صالح عبد الغفار ابن داود، الحراني (تق ١٣٣)، وعصام بن خالد " الجرح والتعديل " (٨/ ٥ / ٢٠)، ومحمد بن موسى الأشيب " تاريخ بغداد " (٢/ ٣٤٥ / ٨٤٨).
روى عنه: أبو حاتم محمد بن إدريس الرازي " الجرح والتعديل " (٨/ ٥ / ٢٠).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، صدوقًا، كثيرًا الخطأ.
وانظر: " الجرح والتعديل " (٨/ ٥ / ٢٠)، و" الثقات " (٩/ ١٣٠ / ١٥٥٨٦)، و" مولد العلماء ووفياتهم " (٢/ ٥٦٨)، و" تاريخ بغداد " (٢/ ٣٤٥ / ٨٤٨)، و" تهذيب الكمال " (٢٦/ ١٧ / ٥٤٢٣)، و" تذكرة الحفاظ " (٢/ ٥٥٤ / ٥٧٦)، و" المقتنى " (١/ ١٢٠ / ٧٩٩)، " وسير أعلام النبلاء " (١٢/ ٣٤٦ / ١٤٢)، و" الكاشف " (٢/ ١٩٦ / ٥٠١٥)، و" ميزان الاعتدال " (٨/ ١٨٣ / ٦٥٢)، و" تهذيب التهذيب " (٩/ ٢٨٠ / ٥٢٢)، و" تقريب التهذيب " (١/ ٤٩٤ / ٦٠٩٧)، و" نزهة الألباب " (٢
/ ٢٠٧١)، و" المقصد الأرشد " (٢/ ٤٣٨ / ٩٨٣)، و" طبقات الحفاظ " (١/ ٢٥١ / ٥٥٩)، و" رجال تفسير الطبري " (٥٠٠/ ٢٣٦٥).',NULL,'536',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (539,'٢٩٢ - محمد بن عبيد الله بن المنادي (٢٢٠٢٢)
(البخاري)
(طب: تس: ١ر / ١ش)
محمد بن عبيد الله، هو: أبو جعفر، محمد بن عبيد الله بن يزيد، ويقال له ابن أبي داود، ويقال: ابن المنادي، البغدادي، جد أبي الحسين، أحمد بن جعفر بن المنادي، مولده سنة إحدى وسبعين ومائة، وتوفي سنة اثنتين وسبعين ومائتين، وقد جاز المائة، بسنة واحدة، ومن صغار العاشرة، صدوق.
روى عن: أبي النضر هاشم بن القاسم (تس ٢٢٠٢٢)
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، صدوقًا
وانظر: " الجرح والتعديل " (٨/ ٣ / ١٢)، " الثقات " (٩/ ١٣٢ / ١٥٥٩٧)، " تهذيب الكمال " (٢٦
/ ٥٤٣٩)، " تهذيب التهذيب " (٩/ ٢٩٠ / ٥٤٠)، " تقريب التهذيب " (١/ ٤٩٥ / ٦١١٣).
٢٩٣ - محمد بن عبيد الآدمي (تهـ: ١٩٨٥)
(البخاري، وابن ماجه)
(طب: تهـ: ١ر / ١ش)
محمد بن عبيد الأدمي، هو: أبو عبيد بن أبي عباد، محمد بن عبيد بن ميمون، القرشي، التيمي، مولاهم، المديني، مولى ابن جدعان - ويقال: مولى هارون بن زيد بن المهاجر بن قنفذ: - التبان - بفتح المثناة، وتشديد الموحدة، من العاشرة، صدوق، يخطئ.
روى عن: أبي ضمرة أنس بن عياض (تهـ ١٩٨٥)، (بخ).
روى عنه: محمد بن جرير الطبري.
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه شيخًا، صدوقًا، وربما أخطأ.',NULL,'537',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (540,'وانظر: " التاريخ الكبير " (١/ ١٧٣ / ٥١٩)، " الثقات " (٩/ ٨٢ / ١٥٣٠٤)، و (٩/ ١٠٠ / ١٥٤٠)، " تهذيب الكمال " (٢٦/ ٧٢ / ٥٤٤٧)، " الكاشف " (٢/ ١٩٩ / ٥٠٣٥)، وتهذيب التهذيب (٩/ ٢٩٦ / ٥٤٨)، وتقريب التهذيب (١/ ٤٩٥ / ٦١٢١).
٢٩٤ - محمد بن عبيد الهمذاني (تهـ: ١١٥٣)
(الترمذي)
(طب: تهـ: ١ر / ١ش)
محمد بن عبيد الهمذاني، هو: أبو عبد الله، محمد بن عبيد بن عبد الملك الأسدي الهمذاني - بالتحريك - الجلاب - بالجيم -، كوفي الأصل: توفي آخر سنة ثلاث، أو: أول سنة أربع وأربعين ومائتين، وقيل: سنة تسع وأربعين ومائتين - من العاشرة، ثقة.
روى عن: أبي بدر شجاع بن الوليد (تهـ ١١٥٣)، ونوح بن ميمون، " تهذيب الكمال "، (٣٠/ ٦٢ / ٦٤٩٦).
روى عنه: محمد بن الحسين بن مرداس " الكامل " (٥/ ١٨٢ / ١٣٤١).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة صالحًا.
وانظر: " الثقات " (٩/ ٩٩ / ١٥٤٠٥)، " تهذيب الكمال " (٢٦/ ٦٣ / ٥٤٤٣) و" الكاشف " (٢/ ١٩٨ / ٥٠٣١)، " وتهذيب التهذيب " (٩/ ٢٩٣ / ٥٤٤)، و" تقريب التهذيب " (١/ ٤٩٥ / ٦١١٧).
٢٩٥ - أبو جعفر المحاربي (١٩٥٤)
(أبو داود، والترمذي، والنسائي)
(طب: ٦٢ر / ٢٨ش)
(تس ٤٣ر / ١٨ش) و (نخ ٥ر / ٥ش) و (تهـ ١٢ر / ٩ش) و (تق ٢ر / ٢ش)
محمد بن عبيد المحاربي، هو: أبو جعفر - وقيل: أبو يعلى - محمد بن عبيد بن',NULL,'538',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (541,'محمد بن واقد المحاربي، الكوفي، النحاس: توفي سنة خمس وأربعين ومائتين - وقيل: سنة إحدى وخمسين ومائتين - من العاشرة، صدوق، لا بأس به.
روى عن: أسباط بن محمد القرشي (تس ٢٣٨٥٤ و ٣٤٤٧٢ و ٣٦٨٥٨)، و (تهـ ٤٠٥)، و (ت س)، وأسد بن عمرو (تس ٣٣٤١)، وأبي يحيى إسماعيل بن إبراهيم (تخ ٢/ ٢٦)، وإسماعيل ابن عياش (تهـ ٣٤٧) و (مد)، وأيوب بن جابر السحيمي (تس ١٩١١٣ و ٢١٦٩٤ و ٣٦٦٧٩)، وحاتم بن إسماعيل (تس ٢٤٧٨٠)، و (س) والحكم بن ظهير (تس ٥٥٢٦ و ١١٣٨)، وسعيد بن خثيم الهلالي (تخ ١/ ٥٣٧) و (س)، وسفيان بن عيينة (تهـ ٤٠٢٨)، وأبي الأحوص سلام بن سليم (تس ٣١٧٣ و ٥٣٨٤ و ٨٧٥٨ و ٩١٨١ و ١٢١١٤ و ١٦٥٤٨ و ٢٢٢٤٤ و ٢٣٧٩٤ و ٢٨٢٤٣)، و (تهـ ٣٣٨)، و (س)، وعباد بن يعقوب (تس ٣٧٠٢٢)، وعبد الله بن المبارك (تس ١٠٢٣ و ٤٣٠٩ و ١٥٧٤٨ و ٣٤٦٦٩ و ٣٤٦٧٤)، و (تهـ ٢٣١)، و (تخ ٢/ ٣٣)، و (س)، وعبد العزيز بن أبي حازم (تخ ٢/ ١٥)، و (ت)، وأبيه عبيد بن محمد بن واقد المحاربي (تس ٧١٢٠ و ٣٣١١٢)، وعبيدة بن حميد (تهـ ٢٠٠٨)، وعلي بن عبد الله الدهان (تهـ ٥٩٥)، وعلي بن هاشم بن البريد (تس ٤٢٩٦ و ١٣٦٢٢)، و (تهـ ٢٨٩ و ١١٢٤ و ١٧٢٥ و ٢٨٨٥) و (ت، س)، وعمر بن عبيد الطنافسي (تق ٤٤٤)، وعمرو بن سعد (تس ٣٣٣٢٨)، وعمرو بن صفوان (تق ٨٨٦) وقبيصة بن ليث الأسدي (تس ٣٣٤٤٨)، ومحمد بن فضيل بن غزوان (تس ١٣٦٢٢)، و (د، س)، ومسلم بن خالد الزنجي (تس ١٩٥٤)، ومطلب ابن زياد (تس ٣٣١٢٨ و ٣٣٧٨٩)، وموسى بن عمير العنبري (تس ٩٨٤٨ و ٢٩٠٢٢ و ٣٣٠٣٩ و ٣٤٠١٠ و ٣٦١٠٧)، و (تهـ ١٩٧٥)، وأبي مالك الجنبي (تس ١٠٥٣١ و ١١٥٣٣ و ٢٢٣٥٠ و ٣٢٦١٩) و (تح ٢/ ٢٥)، و (د، ص)، وأبي بكر بن عياش (تس ٣٠٢٧٠)، و (س).
روى عنه: بقي بن مخلد " تهذيب التهذيب " (٩/ ٢٩٥ / ٥٤٧).
التعديل والتجريح: اتفقت أقوال أئمة الجرح والتعديل فيه على كونه: صدوقًا، لا بأس به.',NULL,'539',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (542,'قلت: وقد وقع اسمه في الأثر رقم (١٠٢٣) محمد بن عبد الله المحاربي، وهو خطأ وسقط من نسخة الشيخ التركي (١/ ٧٢٥).
وانظر: " الجرح والتعديل " (٨/ ١١ / ٤٤)، و" الثقات " (٩/ ١٠٨ / ١٥٤٥٣)، و" تهذيب الكمال " (٢٦/ ٧٠ / ٥٤٤٦)، و" الكاشف " (٢/ ١٩٨ / ٥٠٣٤)، و" تهذيب التهذيب " (٩/ ٢٩٥ / ٥٤٧)، و" تقريب التهذيب " (١/ ٤٩٥ / ٦١٢٠)، و" رجال تفسير الطبري " (١٠٦/ ٤٤٧).
٢٩٦ - محمد الواسطي (٢٥١٧٨)
(طب: ٥ر / ٤ش)
(تس: ٣ر / ٢ش)، و (تهـ ٢ر / ٢ش)
محمد بن عثمان بن مخلد، الواسطي، من العاشرة، فما دونها، لم أعرفه، ولم أجد له ترجمة مع كثرة روايته - وليس مجهولًا، كما سيأتي - وقد أغفله الشيخ شاكر في تحقيقه لـ " تهذيب الآثار " (٤/ ١١٥ / ١٨٩)، ولم يتعرض الشيخ التركي في تحقيقه لـ " التفسير " (١٦/ ٥٥٢) لترجمته بشيء.
روى عن: جعفر بن عون (تس ٢٥٨٤٦ و ٢٨٥٢٥)، وحفص بن عمر (تس ٢٥١٧٨)، ويزيد - وهو ابن هارون السلمي الواسطي - (تهـ ٢٦٤١)، وأبي بلال بن محمد بن الحارث، الأشعري (تهـ ١٩٥١)، و" مسند البزار " (٧/ ٢٠٦ / ٢٧٧٨)، وأبيه عثمان بن مخلد الواسطي " المعجم الأوسط " (٥٧/ ٢٣٦ / ٧٣٧٥)، وأبي سفيان عبيد الله بن سفيان الغذائي " تاريخ بغداد " (١/ ٣٢) والقاسم بن الحكم العرني " تهذيب الكمال " (٢٣/ ٣٤٤ / ٤٧٨٥)، ومحمد بن عمر " العظمة " (٥/ ١٧٣٥ / ١٢٠٩٣٥)، وأبي عامر العقدي " المعجم الأوسط " (٥/ ٩٩ / ٤٧٨٦).
روى عنه: أبو بكر أحمد بن عمرو البزار " مسند البزار " (٢/ ٢٤٦ / ٥٠٨)، وإسحاق بن عبد الله الكوفي البزاز " الكامل " (٣/ ٣٠٥ / ٧٦٨)، والحسن بن الصباح " شعب الإيمان (٣/ ١٥٤ / ٣١٨٣)، وعبدان بن أحمد (المروزي) العظمة (٥/ ١٧٣٥ / ١٢٠٩٣٥)، وعبيد بن غنام " المعجم الأوسط " (٥/ ٩٩ / ٤٧٨٦)، وأبو بكر عمر بن إبراهيم " تاريخ بغداد " (١/ ٣٢)، ومحمد بن أبان',NULL,'540',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (543,'" المعجم الأوسط " (٧/ ٢٣٦ / ٧٣٧٥)، ومحمد بن عبد الله الحضرمي " المعجم الكبير " (١٢/ ٢٠٠ / ١٢٨٨٨).
التعديل والتجريح: أغفله الشيخان شاكر في تحقيقهما " لتفسير الطبري ".
وقد تأكد لي أن اسمه على ما أثبته سباقا، من خلال ذكر (الحافظ المزي) لاسمه ضمن أسماء من روى عن (جعفر بن عون) في " تهذيب الكمال " (٥/ ٧٢).
وكل ما وقفت له عليه من تراجم في كتب الجرح والتعديل، فهي لرجل قديم الرواية مجهول، كما قال الدارقطني مجيبًا البرقاني في " سؤالاته " له عنه في (١/ ٦١ / ٤٤٨)، وبعيد جدًا، أن يكون المجهول هو صاحب هذه الترجمة، فقد روي عدة شيوخ، يخرجونه عن دائرة الجهالة، حسب ما تقتضيه الصنعة الحديثية، كما بينًا ذلك في معجم تلاميذه، في الصفحة السابقة، إلا أن يكون (المجهول) هو صاحب الترجمة التميزية التالية، وهو شيخ قديم:
١٣٩ - تمييز محمد بن عثمان، الواسطي، مجهول.
روى عن: ثابت البناني.
روى عنه: أبو عباد يحيى بن عباد الضبعي، وأبو عوانة.
التعديل والتجريح: قال البخاري في " تاريخه الكبير " (١/ ١٨٠ / ٥٥١): " محمد بن عثمان الواسطي، سمع ثابتًا البناني، عن أنس بن مالك، قال: عبد الملك الجدي، عن سعيد بن خالد، عن محمد، وقال أبو عباد: حدثنا محمد بن عثمان الواسطي، قال: ثنا ثابت، عن أنس: " كان النبي - صلى الله عليه وسلم - إذا أعجبه نحو الرجل أمره الصلاة ". انتهى.
وذكره محمد بن حبان في " الثقات " (٧/ ٤٣٨ / ١٠٨١٢)، وقال: " محمد بن عثمان الواسطي، يروي عن ثابت البناني روى عنه أبو عوانة ".
قلت: لم أقف على روايته عند أبي عوانة، إلا أن كان هو محمد بن عثمان بن خالد، فروايته عند أبي عوانة في " المسند " (١/ ٣٤٨)، وكنيته أبو مروان.
ونقل شيخنا الألباني في تحقيقه لكتاب " السنة لابن أبي عاصم " (١/ ٩٤ / ٢١٣) أقوال',NULL,'541',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (544,'أئمة الجرح والتعديل السابقة، وحدد أن المجهول المراد هو: (محمد بن عثمان الواسطي)، وقد رد تحسين العلامة (أحمد شاكر) في تعليقه على " المسند " (لمحمد بن عثمان الواسطي) هذا، وأيد نكارة خبر علي في أن " أولاد المشركين في النار "، بسبب مخالفته كثيرًا من نصوص الكتاب والسنة أولًا، ثم مخالفته العقل ثانيًا، ثم بسبب جهالة (محمد بن عثمان) هذا، الذي هو أحد رجال السند، ثالثًا.
وانظر: " التاريخ الكبير " (١/ ١٨٠ / ٥٥١، و ٨/ ٢٩٢ / ٣٠٤٤)، و" الثقات " (٧/ ٤٣٨ / ١٠٨١٢)، و" الضعفاء والمتروكين لابن الجوزي، (٣/ ٨٤ / ٣١١٦)، و" ميزان الاعتدال " (٦/ ٢٥٢ / ٧٩٣٣)، و" المغني في الضعفاء " (٢/ ٦١٢ / ٥٨٠٧)، و" لسان الميزان " (٥/ ٢٧٨ / ٩٥٨).
٢٩٧ - محمد بن عثمان الثقفي (تهـ: ١٨٠٨)
(أبو داود، والنسائي)
(طب: ٣ر / ٢ش)
(تخ: ٢ر / ٢ش)، و (تهـ: ١ر / ١ش)
محمد بن عثمان بن أبي صفوان، الثقفي، توفي سنة اثنتين وخمسين ومائتين من الحادية عشرة، ثقة، وراجع الترجمة رقم (٢٨٣)، من كتابي هذا أيضًا.
روى عن: أمية بن خالد الأزدي (تهـ ١٨٠٨)، و (تخ ٢/ ٢٣٧)، (س)، وأبي قتيبة، سلم بن قتيبة (تخ ٢/ ٢٣٧).
روى عنه: عبدان الأهوازي " المستدرك " (١/ ٥٩٩ / ١٥٨٤).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، لا بأس به.
قلت: لم أطنب وأطل في ترجمته، كونه ثقة من جهة، وكوني ترجمت لن يشتبه بأنه هو في الترجمة رقم (٢٨٣)، من جهة أخرى.',NULL,'542',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (545,'وانظر: " تهذيب الكمال " (٢٦/ ٨٥ / ٥٤٥٧)، " الكاشف " (٢/ ٢٠٠ / ٥٠٤٢)، " تهذيب التهذيب، (٩/ ٣٠٠ / ٥٦٠)، و" تقريب التهذيب " (١/ ٤٩٦ / ٦١٣١).
٢٩٨ - محمد بن عثمان الرازي (٢٢٥٨٤)
(طب: (تس: ١ر / ١ش)
محمد بن عثمان، الرازي، من الحادية عشرة، لم أعرفه، ولم أجد له ترجمة ولم أقف له على غير هذا الأثر، ولعله المذكور في " كامل " ابن عدي، وسكت عنه.
روى عن: سهل بن بكار (تس: ٢٢٥٨٤).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: أغفله الشيخان شاكر في تحقيقهما " لتفسير الطبري "
وقد وقفت له على رواية عند ابن عدي في " الكامل " (٦/ ٣١٠ / ١٧٩٧) في ترجمة (مسلم بن خالد الزنجي) من طريق عباس بن محمد، قال: " ثنا عثمان بن محمد بن عثمان الرازي، ثنا مسلم بن خالد الزنجي المكي ".
وأكدها كل من: الحافظ المزي في " تهذيب الكمال " (٢٧/ ٥٠٨ / ٥٩٢٥) والذهبي في " الميزان " (٦/ ٤١٣ / ٨٤٩١)، وفي ترجمة (الزنجي) أيضًا.
وهذا ليس ابنه، تأكيدًا وتحقيقًا، أو أنه سقط من الإسناد اسم (محمد) ولكنه بعيد جدًا أيضًا؛ لأن (الزنجي) هذا من الثامنة، وتوفي سنة ١٧٩ أو بعدها، والعباس بن محمد، وهو الدوري - راوية تاريخ ابن معين - من شيوخ الطبري، فبعيد أن يكون هذا شيخه، والله أعلم.
وشيخه هنا هو: أبو بشر، سهل بن بكار بن بشر، الدارمي، البصري، المكفوف، توفي سنة سبع أو ثمان وعشرين ومائتين، من العاشرة، ثقة، ربما وهم، وعليه فيكون صاحب الترجمة من الحادية عشرة، وترجمت لـ (سهل) في " المعجمين الكبير والصغير.',NULL,'543',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (546,'٢٩٩ - أبو عبد الله المروزي (٨١)
(الترمذي، والنسائي)
(طب: ٢٦ر / ٦ش)
(تس: ١٤ر / ١ش)، و (تخ: ٣ر / ٣ش)، و (تهـ: ٧ر / ٣ش)
و (تق: ١ر / ١ش)، و (صر: ١ر / ١ش)
أبو عبد الله، محمد بن علي بن الحسن بن شقيق بن دينار - وقيل: ابن محمد بن دينار، العبدي مولاهم، المروزي: توفي سنة خمسين ومائتين - وقيل: سنة إحدى وخمسين ومائتين، سقط من السطح فمات - من الحادية عشرة، ثقة، صاحب حديث.
روى عن: إبراهيم بن الأشعث البخاري (تهـ ١٤٧٦ و ١٥١٢)، وأحمد بن حنبل (ص ٢٨) وأبيه علي بن الحسن بن شقيق (تس ٨١ و ١٥٩٤ و ٢٥٨٢ و ٢٦٠١ و ٣٠٢٥ و ٩٩٥٧ و ١٠٩٢ و ١١٨١٤ و ١٢٨٠٩ و ٢٢٨٢٩ و ٢٩٦٧٨ و ٣٠٣٩١ و ٣١٥٥٤ و ٣٥٦٥٣)، و (تهـ ١٥٧ و ٢١٢١ و ٢٨٨٤) و (تق: ٦٤٦)، و (تخ ١/ ٧٦)، و (ت، س)، ومحمد بن سعد (تخ ٣/ ٢١٠)، والنضر بن شميل النحوي (تهـ ١٤١ و ٢٣٧٢)، و (س)، وأبي سعدان (تخ ٣/ ٢٦٩).
روى عنه: أبو بكر أحمد بن عمرو بن الضحاك الشيباني " الآحاد والمثاني " (٢/ ٤١٢ / ١٢٠٧)، وحماد بن أحمد القاضي " سنن البيهقي " (٧/ ٤٨٠ / ١٥٥٢٣)، وأبو محمد عبد الله بن علي بن الجارود (المنتقى) (١/ ٢٤١ / ٩٦٠)، ومحمد بن أحمد بن النضر الخلقاني " صحيح ابن حبان " (٤/ ١٨٨ / ١٣٥٤)، ومحمد بن سليمان بن فارس " صحيح ابن حبان " (٣/ ١١٧ / ٨٣٦)، وأبو بكر البزار " مسند البزار " (١/ ٤٢١ / ٢٩٧)، وأبو يعلى الموصلي " معجم أبي يعلى " (١/ ٦٤ / ٣٨).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقةً، صدوقًا، صاحب حديث، وكان محدث (مرو)، وابن محدثها.
وذكر الحاكم: " أن البخاري ومسلمًا رويًا عنه ".
قال الحافظ في " التهذيب " معلقًا على قول الحاكم: " وكأنه في غير الجامعين ".',NULL,'544',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (547,'وانظر: " الجرح والتعديل " (٨/ ٢٨ / ١٢٦)، و" الثقات " (٩/ ١١٠ / ١٥٤٦٤) و" تاريخ بغداد " (٣/ ٥٥ / ٩٩٨)، و" تهذيب الكمال " (٢٦/ ١٣٤ / ٥٤٧٧)، و" الكاشف " (٢/ ٢٠٢ / ٥٠٥٩)، و" المقتنى في سرد الكنى " (١/ ٣٦٠ / ٣٧٠٥)، و" تهذيب التهذيب " (٩/ ٣١١ / ٥٨١)، و" تقريب التهذيب " (٤٣١/ ٦١٥٠)، و" المقصد الأرشد " (٢/ ٤٦٦ / ١٠١٣)، و" رجال تفسير الطبري " (٥٠٣/ ٢٣٧٩).
٣٠٠ - أبو العباس الرقي (تهـ: ١٤٢٢)
(النسائي)
(طب: (تهـ: ٢ر / ٢ش)
أبو العباس، محمد بن علي بن ميمون، الرقي، العطار: مولده سنة ثلاث وتسعين ومائة، وتوفي آخر سنة ثمان وستين ومائتين، من الحادية عشرة، ثقة.
روى عن: أحمد بن عبد الله بن يونس (تهـ ١٤٢٤)، والحسن بن بشر الكوفي (تهـ ١٤٢٢) و (عس)، وسعيد بن عبد الملك بن واقد الحراني " المستدرك " (٢/ ١٩٣ / ٢٧٢٧)، وعبيد بن جناد " مسند أبي عوانة " (١/ ٨٤ / ٢٥١).
روى عنه: أبو عوانة الإسفرايني " مسند أبي عوانة " (١/ ٨٤ / ٢٥١)، ومحمد بن عبد الله بن عبد السلام (مكحول) " حلية الأولياء " (٧/ ٢٥).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: إمامًا، حافظًا، ثقةً مأمونًا، إمام أهل الجزيرة في عصره.
وانظر: " الجرح والتعديل " (٨/ ٢٨ / ١٢٧)، و" الثقات " (٩/ ١٣٧ / ١٥٦٢٧)، " الثقات " (٩/ ١٤٤ / ١٥٦٧٠)، و" تهذيب الكمال " (٢٦/ ١٥٦ / ٥٤٨٦)، و" الكاشف " (٢/ ٢٠٤ / ٥٠٦٥)، و" المقتنى " (١/ ١٨٢ / ١٥٢٧)، و" تهذيب التهذيب " (٩/ ٣١٦ / ٥٩٠)، و" تقريب التهذيب " (١/ ٤٩٧ / ٦١٥٩).
٣٠١ - أبو جعفر الوازعي (١٣٨١٣)
(طب: ٩ر / ٧ش)',NULL,'545',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (548,'(تس: ٦ر / ٥ش)، و (تخ: ١ر / ١ش)، و (تهـ: ٢ر / ٢ش)
أبو جعفر، محمد بن عمار بن الحارث، الوازعي، الرازي: من الحادية عشرة مستقيم الحديث، صدوق، ثقة.
روى عن: إسحاق بن سليمان الرازي (تهـ ٨١٧)، وإسماعيل بن عبيد بن أبي كريمة (تس ٢٩٥٣٠)، و (تخ ١/ ٢٥٨)، وسعيد بن سليمان الواسطي (سعدويه) (تس: ١٢٠٠١ و ١٣٨١٣)، وسهل بن بكار (تهـ ١٤٠١)، ومحمد بن الصباح (تس ٣٤١٤٣)، وأبي معمر المنقري (تس ٣٦٦٤٦)، وأبي نعيم (تس ٣٦٩٨٩)، والحجاج بن محمد الأعور، والسندي بن عبدويه، وعبد الرحمن الدشتكي، وعبيد الله بن موسى، " تعظيم قدر الصلاة " (١/ ٤٢٢ / ٤١٨) ومؤمل بن إسماعيل، ومحاضر بن المورع، ومكي بن إبراهيم، ويحيى بن أبي بكير.
روى عنه: أحمد بن إبراهيم بن سموية " التدوين في أخبار قزوين " (٢/ ٢٢٢)، وأحمد بن محمد بن يحيى " الثقات " (٩/ ١٣٨ / ١٥٦٢٩)، وعلي بن الحسين الآملي " تاريخ جرجان " (١/ ٣١٤ / ٥٥١)، ومحمد بن قازن بن العباس " الإرشاد " (٢/ ٦٧٢)، ومحمد بن نصر المروزي " تعظيم قدر الصلاة " (١/ ٤٢٢ / ٤١٨)، والوليد بن أبان " المعجم الأوسط " (٩/ ١١٤ / ٩٢٨٣).
وكل اسم لم يعز إلى كتاب، من معجم شيوخه وتلاميذه، وميزته بالأسود العريض فهو من ترجمته في " الجرح والتعديل ".
التعديل والتجريح: قال عبد الرحمن بن أبي حاتم الرازي: " كتبت عنه وهو صدوق، ثقة.
ذكره محمد بن حبان في كتاب " الثقات "، وقال: " مستقيم الحديث ".
قلت: وتصحف اسم أبيه في الأثرين (تس ٣٤١٤٣ و ٣٦٩٨٩) من (عمار)، إلى عمارة).
وانظر: " الجرح والتعديل " (٨/ ٤٣ / ١٩٨)، و" الثقات " (٩/ ١٣٨ / ١٥٦٢٩)، و" الإرشاد " (٢/ ٦٧٢)، و" رجال تفسير الطبري " (٥٠٤/ ٢٣٨٣).',NULL,'546',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (549,'١٤٠ - تمييز محمد بن عمار بن عطية، السكري، الرازي.
روى عن: أبي هارون البكاء، وسهل بن عثمان العسكري.
وانظر: " الجرح والتعديل " (٨/ ٤٣ / ١٩٩).
٣٠٢ - محمد بن عمارة (٦٤٥)
(طب: ١٤٣ر / ٢٥ش)
(تس ١٠١ر / ١٦ش)، و (تخ ١٠ر / ٣ش)، و (تهـ ٢٣ر / ٨ش)، و (تق ٨ر / ٥ش) و (صر ١ر / ١ش)
محمد بن عمارة بن صبيح، الأسدي، الكوفي: من الحادية عشرة، وثقه ابن حبان.
روى عن: إسماعيل بن أبان (الوراق) (تس ٦٤٥ و ١٦١٢٧ و ٢٢٢٤٣ و ٢٨٥٠٠ و ٣٠٦٧٧). وثابت بن محمد (تهـ ١٧٨٣ و ١٧٨٤)، و (تخ ١/ ٦٣)، وحسن بن عطية (تس ١٠٦٩٨)، وحسن بن مالك (تس ٢١٢٤٩)، وخالد بن مخلد (تهـ ٣٤٨ و ٢٥٣٠)، و (تق ٢٤ و ٣٤٢)، وخالد بن يزيد (تس ١٥٦٩٢ و ٣٥٠٥٢)، وزريق بن مرزوق (تس ١٨٠٦٢ و ٣٧٩٩٥)، وسعد بن حفص الطلحي (تهـ ٦٠٧) و (تق ١٦٩)، وسهل بن عامر البجلي (تس ٥٤٣٤ و ٦٣١٠ (١) و ٧٧٧١ و ١٤٢١٣ و ٢٠٨٩٦ و ٢٨٩٤٦ و ٣٢٤٩٢ و ٣٢٥١٣ و ٣٣١٢١ و ٣٦٣١٢)، و (تهـ ٩٥٥ و ١٩٧٣ و ٢٧٦٩ (٢) وشهاب بن عباد (تق ٦١٠)، وعاصم بن يوسف اليربوعي (تس ٢٥٦٨٧)، وأبي عبد الرحمن بن عبد الله بن يزيد المقرئ (تس ٣١٨٦ و ١٨٦٧٥ و ٢٣٠٨٨ و ٢٧١٩٨ و ٢٧٢٠٠)، وعبيد الله بن موسى (تس ١٥١٤ و ٢٨٤٧ و ٣٢٩٦ و ٣٧٦٧ و ٥٥١٩ و ٧٤٢٦ و ٧٧٥٧ و ٩٤٤٧ و ٩٤٥٧ و ٩٧٢٠ و ١٣٢٢٢ و ١٦٠٣٩ و ١٦١٨٩ و ١٧٣٠٥ و ١٧٧١٧ و ١٩١٠٨ و ٢١٨٧١ و ٢٣٢٩٠ و ٢٣٣٤٤ و ٢٤٧٩٨ و ٢٤٨٦٢ و ٢٥٢١٢ و ٣٥٣٧٦ و ٢٢٥٢٤ و ٢٥٥٤٦ و ٢٦٣٨٠ و ٢٦٨٢٩ و ٢٦٩٤٢ و ٢٧١٩٨ و ٢٧٢٠٠ و ٢٧٤٠٣ و ٢٧٩٧١ و ٢٨٢٠٤ و ٢٨٢٨٥ و ٢٨٢٩٠ و ٢٨٤٢٣ و ٢٨٧٧٣ و ٢٨٨٠٣ و ٢٩١٢٨ و ٢٩٢٤٠ و ٣٠٣٩٤ و ٣٠٦٨٠ و ٣٠٧٦٧ و ٣١٥١٧ و ٣١٥٦٣ و ٣١٥٦٤ و ٣١٦١٦ و ٣١٩٧٢ و ٣٢٣١٨ و ٣٢٤٦٤ و ٣٢٦٢٢ و ٣٣١٤٤ و ٣٣١٤٨ و ٣٥٤٢٩ و ٣٦٤٠١ و ٣٦٤١٣ و ٣٦٤١٨ و ٣٦٤٢٢ و ٣٧٠٩٠ و ٣٧١٠٥ و',NULL,'547',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (550,'٣٧١١٨ - و ٣٧١٢٦ و ٣٧١٣٤ و ٣٧١٤٤ و ٣٧٣٧٠ و ٣٨٠٦٨)، و (تهـ ١١٥ و ٤٠٦ و ٤١٧ و ٧٢٠ و ٨٥٤ و ٩٥٤ و ١٠٤١ و ١٨٤١ و ٢١٠٥ و ٢١٣٢ و ٢٣٨٥ و ٢٦٠٨)، و (تق: ٢١٢ و ٦٨٩)، و (تخ ١/ ٧٦ / و ١/ ٢٠٥ و ٢/ ١٩ و ٢/ ١٢٠ و ٢/ ١٢١ و ٣/ ٤١ و ٣/ ٤٢ و ٣/ ١٢٤)، و (صر ٢٦)، وعثمان بن سعيد بن مرة (تس ٣٦٩٩٠)، وعثمان بن عبد الرحمن الأصبهاني (تخ ٣/ ١٦٤)، وعلي بن عبد الحميد (تق ٧٢٥)، وعلي بن قادم (تس ٢٨٥٢٩)، و (تهـ ٢١١٦)، وعمرو بن حماد (تس ٢٥٩٤٤ و ٣٢٦٩٨)، و (تق ٤٤٥)، والفيض بن الفضل (تهـ ٢٧٤٢)، وقبيصة بن عقبة السوائي (تس ٣٤٨٨٩)، ومالك بن إسماعيل (تهـ ٢٣٩١ و ٢٩٣٩)، ومحمد بن سعيد بن زائدة (تس ١٧٢٤٦ و ٣٥٤١٢)، ويزيد بن مهران (تس ١٣١٨٩)، وأبي عاصم (تس ٢٥٥٢٥) وإسماعيل بن صبيح " المعجم الكبير " (١٢/ ٤٢٩ / ١٣٥٧٤)، وحسن بن حسين العرني " علل الدارقطني " (٥/ ١٦٣ / ٧٩٥)، وعون بن سلام " ضعفاء العقيلي " (٤/ ٤٣٧ / ٢٠٦٩)، ومالك بن إسماعيل " الإخوان " (١٤٣/ ٩٣)، ومحمد بن الطفيل " حلية الأولياء " (٦/ ٢٧٣)، ونصر بن مزاحم " الكامل " (٧/ ٣٧ / ١٩٧٢)، ووكيع بن الجراح " الثقات " (٩/ ١١٢ / ١٥٤٧٤)، وأبي نعيم " سنن الدارقطني " (٤/ ٨٦ / ٦٢).
روى عنه: أحمد بن عمرو البزار " المعجم الكبير " (٨/ ٣٠١ / ٨١٤٥)، وأحمد بن محمد بن عبد الكريم الوزان " الثقات " (٩/ ١١٢ / ١٥٤٧٤)، وزكريا بن يحيى الحلواني " ضعفاء العقيلي " (٤/ ٤٣٧ / ٢٠٦٩)، والعباس بن حمدان الحنفي الأصبهاني " المعجم الكبير " (١٠/ ٢١٣ / ١٠٥٠٦)، وعبد الله بن محمد بن ناجية " ضعفاء العقيلي " (٣/ ٩٢ / ١٠٦٥)، وعلي بن العباس البجلي " المعجم الكبير " (٩/ ٣٢٠ / ٩٦٠٢)، ومحمد بن سعيد بن دحيم " المعجم الكبير " (١٢/ ٤٢٩ / ١٣٥٧٤)، ويحيى بن محمد بن صاعد " الثقات " (٩/ ١١٢ / ١٥٤٧٤)، وأبو بكر بن أبي الدنيا " سنن الدارقطني " (٤/ ٨٦ / ٦٢).
التعديل والتجريح: ذكره ابن حبان في " الثقات " (٩/ ١١٢ / ١٥٤٧٤).
وذكره الشيخ شاكر في تحقيقه " لتفسير الطبري " (١/ ٣٤ / ٢٨) في ترجمة محمد بن عبادة البختري، وقال: " شيخ الطبري: هذا الشيخ مضى مرارًا في المطبوعة على أوجه منها (٦٤٥) (١٥١١) باسم (محمد بن عمارة الأسدي)، وذكرنا في ثانيهما أننا لم نجد له ترجمة ولا ذكرًا إلا في رواية الطبري عنه مرارًا في التاريخ، ولم نجده في فهارس التاريخ إلا كذلك.',NULL,'548',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (551,'ومنها (١٩٧١) باسم (محمد بن عمار) وصححناه فيه على ما رأيناه من قبل (محمد بن عمارة) ولكنه جاء هنا - كما ترى - باسم (محمد بن عبادة)، والراجح عندي الآن أنه هو الصواب، فإن يكن ذلك تكن نسخ الطبري في التفسير وفي التاريخ محرفة في كل موضع ذكر فيه على غير هذا النحو.
وهذا الشيخ " محمد بن عبادة بن البختري الأسدي الواسطي ": ثقة صدوق، كان صاحب نحو وأدب، شيئًا من ترجمته ".
قلت: وستجد ترجمته برقم (٢٧٧) مفصلة إن شاء الله في كتابي هذا في موضعها الأوحد (٢٠٩٧).
ثم قال: " وإنما رجحت - هنا - أنه (محمد بن عبادة): لأن (محمد بن عمارة الأسدي) مفقود ذكره في كتب التراجم والرواية، فيما وصل إليه علمي ".
ثم قال: " نعم: يمكن أن يكون هناك شيخ آخر - لم يصل إلينا علمه - باسم (محمد بن عمارة) يتفق مع هذا في شيوخه وفي الرواية عنه، ولكني أرى أن ما ذكرت هو الأرجح (٢٠٩٢) [(١٣٩٢٧)] " انتهى ببعض الاختصار.
وقال السقاف في " فهارس رجال الطبري " (١٨٥): " محمد بن عباد الأسدي = محمد بن عمارة الأسدي).
قلت: رحم الله الشيخين (شاكر)، وجميع سلفنا الصالحين، على ما بذلوه من جهد واجتهاد، هم فيه بين الأجر والأجرين، ولكل مجتهد في الحق من الأجر نصيب، فهل يترك (المائة وواحد) من أجل (الواحد).
وقد تبين لي، بما لا يدع مجالًا للشك أبدًا، أن العكس هو الصحيح، وأن (محمد بن عمارة)، تحرف إلى (محمد بن عبادة)، وليس العكس، وأن (عمارة) يجوز فيها أن تكون مثلثة العين، بالفتح (عمارة)، وبالضم (عُمارة)، وبالكسر (عمارة)، أو أنه روى عن الاثنين (محمد بن عبادة)، و (محمد بن عمارة)، وأن (محمد بن عمارة) هو (ابن صبيح).',NULL,'549',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (552,'وأنه كوفي، ثقة، كما ذكره ابن حبان - وأثبته عاليا - ولقد أكثر الرواة عنه، وأكثر هو أيضا الرواية عنهم، وخاصة (عبيد الله بن موسى) والذي تصحف اسمه في الآثار رقم (٢٧٩٧١ و ٢٨٧٧٣ و ٢٨٢٠٤) إلى (عبد الله)، والذي حدث عنه، أكثر مما حدث عن جميع شيوخه مجتمعين، حيث أخرج عنه:
في " التفسير ": نحو (ست وستين رواية) من أصل (مائة رواية ورواية واحدة) عن (ستة عشر شيخا).
وفي " التاريخ ": (ثماني روايات) من أصل (عشر روايات) عن (ثلاثة شيوخ).
وفي " تهذيب الآثار ": (اثنتي عشرة رواية) من أصل (ثلاث وعشرين رواية) عن (ثمانية شيوخ).
وفي " المفقود منه ": (روايتين) من أصل (ثماني روايات) عن (خمسة شيوخ).
وفي " صريح السنة ": (رواية واحدة) عن (شيخ واحد).
بحيث يكون مجموع ما رواه الطبري عن (عبيد الله بن موسى) (٨٩) من أصل (١٤٣) رواية أي نحو (ثلثي رواياته) والثلث كثير، فكيف بالثلثين؟!.
ولقد أثبت، في معجم شيوخه وتلاميذه، نحو (عشرين موضعا)، لرواياته وشيوخه والرواة عنه خارج " التفسير "، و" التاريخ "، و" تهذيب الآثار " وهو بعض ما وقفت عليه وليس كله، خشية الإطالة، وحيث ورد اسمه صريحا (محمد بن عمارة الأسدي)، في كثير من كتب الحديث والتراجم، غير " تفسير الطبري "، و" تهذيب الآثار "، و" المفقود منه "، و" تاريخ الطبري "، و" صريح السنة ".
وكذلك (محمد بن عمارة بن صبيح)، و (محمد بن عمارة بن صبيح الأسدي) و (محمد بن عمارة بن صبيح الكوفي).
وبهذا الاسم، ورد ذكره في ترجمة شيخه (إسماعيل بن أبان الوراق)، في " تهذيب الكمال " (٣/ ٥ / ٤١١)، كما ورد اسم (عنبسة بن عبد الرحمن القرشي) وهو شيخ شيخه في الأثر موضوع الترجمة -، ضمن معجم شيوخ (إسماعيل) المذكور.',NULL,'550',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (553,'فهل يترك هذا الكم الكبير، من روايات (محمد بن عمارة) عن (عبيد الله بن موسى) من أجل أثر، واحد، يتيم، فرد، ربما يكون قد وقع فيه التحريف، أو التصحيف، أو أن الطبري قد روى عن (محمد بن عبادة) أيضا - وهذا هو الأرجح - كما روى عن (محمد بن عمارة)، والله أعلم.
وقد تصحف اسم شيخه، (عبيد الله - مصغرا - ابن موسى) في الآثار (تس ١٧٧١٧ و ٢١٨٧١ و ٢٧١٩٨ و ٢٧٢٠٠ و ٢٨٧٧٣ و ٢٨٨٠٣) إلى (عبد الله - مكبرا).
وأكثر شيوخه الذين روى عنهم عند الطبري روي عنهم أيضا من طريقه في. " المعجم الكبير " (٩/ ٣٢٠ / ٩٦٠٢، و ١٠/ ١٣٦ / ١٠٢٢٨) "، و" الكامل " (٥/ ١٢٩ / ١٢٩٢)، و" الإخوان " (٤١/ ٢٤)، و" حلية الأولياء " (٦/ ٢٧٣).
وقد شاركه في أسماء بعض شيوخه، وطبقته (محمد بن عبادة بن البختري، الأسدي، الواسطي (٢٠٩٧)، وقد مضت ترجمتة برقم (٢٧٧)).
وتردد فيه أيضا شيخنا الألباني - رحمه الله تعالى - كما هو في " الضعيفة " (١٢/ ٨٦٤ / ٥٧٧٧ - مخطوطة الشيخ)، (١٢/ ٥٩٦ / ٥٧٧٨). - مطبوعة المكتبة الإسلامية التجريبية).
وقد شاركه في اسمه، واسم أبيه، أكثر من راو اسمه (محمد بن عمارة) وهم:
١٤١ - تمييز محمد بن عمارة الرازي، مصحف من (محمد بن عمار الرازي)، وقد مضت ترجمته، في الترجمة السابقة برقم (٣٠١).
١٤١ - تمييز محمد بن عمارة بن حزم.
روى عن: أبي هريرة.
روى عنه: أبو الزناد.
وانظر: " التاريخ الكبير " (١/ ١٨٦ / ٥٧٤)، و" الجرح والتعديل " (٨/ ٤٥ / ٢٠٥)، و" الثقات " (٥/ ٣٨٠ / ٥٢٩٦) و" الإصابة " (٦/ ٢٥٤ / ٨٣١٥)، و" تهذيب التهذيب " (٩/ ٣٢٠ / ٥٩٩).',NULL,'551',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (554,'١٤٣ - تمييز محمد بن عمارة بن عمرو بن حزم، الأنصاري، المدني من السابعة، صدوق يخطئ. أخرج له (الأربعة).
روى عن: أبي بكر عمرو بن حازم.
روى عنه: مالك بن أنس.
وانظر: " التاريخ الكبير " (١/ ١٨٧ / ٥٧٥)، و" الجرح والتعديل " (٨/ ٤٤ / ٢٠٤)، و" الثقات " (٧/ ٢٦٨ / ١٠٤٧٤)، و" تهذيب الكمال " (٢٦/ ١٦٧ / ٥٤٩٤)، و" الكاشف " (٢/ ٢٠٤ / ٥٠٧١)، و" تهذيب التهذيب " (٩/ ٣٢٠ / ٥٩٨)، و" تقريب التهذيب " (١/ ٤٩٨ / ٦١٦٧)، و" تعجيل المنفعة " (١/ ٣٧٤ / ٩٦٤)، و" إسعاف المبطأ " (٢٦).
١٤٤ - تمييز محمد بن عمارة بن خزيمة بن ثابت، الأنصاري، المديني، لا يكاد يعرف، أخرج له أحمد في " المسند ".
روى عن: أبيه، عن جده خزيمة.
روى عنه: ابنه خزيمة، وابن جريج.
وانظر: التاريخ الكبير (١/ ١٨٦ / ٥٧٣)، و" الجرح والتعديل " (٨/ ٤٤ / ٢٠٣)، و" الثقات " (٧/ ٤٣٦ / ١٠٧٩٩)، و" الإكمال " (٣٨٢/ ٧٨٧)، و" تعجيل المنفعة " (١/ ٣٧٣ / ٩٦٣).
١٤٥ - تمييز محمد بن عمارة - وقيل: عبادة -.
روى عن: المدنيين، وقد أدرك محمود بن الربيع.
روى عنه: السكن بن بأبي كريمة.
وانظر: " الجرح والتعديل " (٨/ ٤٥ / ٢٠٦)، و" الثقات " (٧/ ٤٣٧ / ١٠٨٠٧)
١٤٦ - تمييز محمد بن عمارة، الليثي، شيخ، حدث بدمشق، بعد الثلاثمائة، مجهول.',NULL,'552',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (555,'روى عنه: ابنه أحمد.
وانظر: لسان الميزان (٥/ ٣١٨ / ١٠٤٨).
٣٠٣ - محمد بن عمر بن الصباح الهمداني (تخ ٢/ ٢٢٨)
(طب: تخ ١ر / ١ش)
محمد بن عمر بن الصباح، الهمداني، خطأ، والصحيح: أبو عبد الله، محمد بن عمر بن الهياج، الهمداني، الصائدي، الكوفي، (تخ ١/ ١٧). يأتي (٣٠٥).
٣٠٤ - أبو عبد الله المقدمي (٢٦٥٣)
(الأربعة)
(طب: ٥٥ر / ١٣ش)
(تس ١٢ر / ٧ش)، و (تخ ٢ر / ٢ش)، و (تهـ ١٥ر / ٤ش)، و (تق ٢٦ر / ٣ش)
أبو عبد الله، محمد بن عمر بن علي بن عطاء بن مقدم، المقدمي، البصري، توفي قريب من سنة خمسين ومائتين، من صغار العاشرة، صدوق.
روى عن: إسماعيل بن حكيم (تس ٢٧٦٣٢) وأشعث بن عبد الله السجستاني (تس ٦٨٠٦ و ٢٥٥٤٤ و ٢٧٠٩٣ و ٢٩٧٩٠ و ٣٤٢٠٩ و ٣٧٤٤٦)، و (د)، والحجاج [بن محمد] (تهـ ٧٩٥ و ٨١٨ و ٨٣٧ و ٨٣٩ و ١٠٣١ و ١٠٤٦ و ١٠٤٨ و ١٠٥٦ و ١٠٦٤ و ١١٦٤ و ١٥٤١) والحجاج [ابن المنهال] (تق ٢٦ و ٤٢ و ٥١ و ٥٤ و ٥٦ و ٢٢٢ و ٢٣٠ و ٢٨١ و ٢٩٢ و ٢٩٣ و ٣٠٠ و ٣٠٣ و ٣٠٤ و ٣٨٦ و ٣٨٧ و ٤٤٢ و ٤٩١ و ٥٦٣ و ٥٦٥ و ٥٧٢ و ٥٧٣ و ٥٨١ و ٦٠١)، وريحان بن سعيد (تس ٦٢٥٣)، وسعيد بن عامر الضبعي (تس ١١٧٢٨)، و (ت، س)، وأبي عاصم [الضحاك بن مخلد النبيل] (تس ٥٩١٢٩)، و (تخ ١/ ٥٣) وأبي الحسن عبد الله بن هاشم بن أبي عبد الله الدستوائي (تهـ ١٣٤٠)، وقريش بن أنس (تس ٣٢١٥٣ و ٣٤٩٣٠)، وأبي بكر محمد بن واسع (تق ٨٦١ و ٨٦٢)، ويحيى بن سعيد القطان (تس ٢٤٤٠٩)، وأبي ذكير يحيى بن محمد بن قيس المدني (تخ ٢/ ٢٢١)، و (س)، وأبي بحر',NULL,'553',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (556,'البكراوي (تهـ ٤٩٣) وأبي زيد الأنصاري [سعيد بن أوس] (تهـ ٢٧٦٠)، و (تق ٧٩٢) وأبي عبد الصمد - غير مسمى، ولا منسوب - (تس ٢٨٦٥٤)، ومحمد بن كثير " ضعفاء العقيلي " (٣/ ٢١٩ / ١٢٢٠).
روى عنه: إبراهيم بن مثوية الأصبهاني " المعجم الكبير " (١٨/ ١٤٨ / ٣١٧)، والحسين بن إسحاق التستري " المعجم الكبير " (٩/ ٦٢ / ٨٣٩٧)، وعبدان الأهوازي " المستدرك " (٤/ ٢٣٥ / ٧٤٨٠) وعلي بن إسحاق الوزير الأصبهاني " المعجم الكبير " (٦/ ١٠٤ / ٥٦٤٠)، وعمرو بن أبي عاصم الشيباني " السنة " (١/ ٢٣٥ / ٥٣٢)، والقاسم بن زكريا " المعجم الأوسط " (٥/ ١٧٠ / ٤٩٧٦)، وأبو بكر بن رجاء " الزهد الكبير " (٢/ ٢٢٣ / ٥٧٦).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوق، لا بأس به.
قلت: تصحف اسم أبيه ونسبه على النساخ في الأثر (تس ٢٧٠٩٣) فصار (محمد بن عمرو المقدسي).
ووقع خطأ في الأثر (تق ٨٦١) من " التهذيب المفقود " في اسم شيخه (أبي بكر محمد واسع) بحيث صار (أبو بكر بن محمد بن واسع) وهو خطأ، والصواب ما أثبته، والله أعلم.
وانظر: التاريخ الكبير (١/ ١٧٩ / ٥٤٦)، و" الجرح والتعديل " (٨/ ٢١ / ٩٣)، و" تهذيب التهذيب " (٩/ ٣٢١ / ٦٠٢)، و" تقريب التهذيب " (١/ ٤٩٨ / ٦١٧١) و" رجال تفسير الطبري " (٥٠٤/ ٢٣٨٤).
٣٠٥ - محمد بن عمر بن هياج الهمداني (تخ ١/ ١١٧)
(الترمذي، والنسائي، وابن ماجه)
(طب: ٣ر / ١ش)
(تخ: ٢ر / ١ش)، و (تهـ: ١ر / ١ش)
أبو عبد الله، وقيل: أبو عبيد الله، محمد بن عمر بن هياج، الصائدي، ويقال: الأسدي، الهمداني، الكوفي: توفي في شوال سنة خمس وخمسين ومائتين، من الحادية عشرة',NULL,'554',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (557,'ثقة، صدوق.
روى عن: يحيى بن عبد الرحمن الأرحبي (تهـ ٦١٢)، و (تخ ١/ ١٧ و ٢/ ٢٢٨)، و (ت، س، ق)، وأبي نعيم الفضل بن دكين " مسند البراز " (٨/ ١٣١ / ٣١٣٩)، وقدامة بن زائد بن قدامة " مسند البراز " (٧/ ٣١٤ / ٢٩١٤)، ومنصور بن يعقوب بن أبي نويرة الكامل (١/ ٣٩٧ / ٢١٣).
روى عنه: عبيد الله بن جنادة الأزدي " الجرح والتعديل " (٢/ ٥١٥ / ٢١٣٠).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقا، لا بأس.
وانظر: الجرح والتعديل (٨/ ٢٢ / ٩٧)، والثقات (٩/ ١١٩ / ١٥٥١٨)، و" تهذيب الكمال " (٢٦/ ١٧٨ / ٥٥٠٠)، " تهذيب التهذيب " (٩/ ٣٢٣ / ٦٠٥)، و" تقريب التهذيب " (١/ ٤٩٨ / ٦١٧٤)، و" الكاشف " (٢/ ٢٠٥ / ٥٠٧٧)
٣٠٦ - أبو الكروس الكلبي (١٧٥٨٦)
(طب: ٧ر / ٣ش)
(تس: ٢ر / ١ش)، و (تهـ: ٢ر / ٢ش)، و (تق: ٣ر / ١ش)
أبو عبد الله، محمد بن عمرو بن تمام، الكلبي، المصري، ويقال له: (أبو الكروس)، توفي في جمادى الأولى سنة إحدى وستين ومائتين، من الحادي عشرة، صدوق.
روى عن: [سعيد] ابن أبي مريم (تهـ ٤٠٣١)، وسليمان بن أيوب بن عيسى بن موسى بن طلحة بن عبيد الله (تس ١٧٥٨٦ و ٢٨٤٣٢)، و (تق ٦٢٥ و ٦٦٨ و ٦٧) ويحيى بن عبد الله بن بكير (تهـ ٤٥٦) وأسد بن موسى، وأصبغ بن الفرج " صحيح ابن خزيمة " (١/ ٣١٨ / ٦٢٧)، وحجاج بن إبراهيم الأزرق البغدادي " تهذيب الكمال " (٥/ ٤١٨ / ١١١١)، وسعيد بن هاشم المخزومي " إكمال ابن ماكولا " (٧/ ١٣٢)، وعبد الله بن عبد الحكم بن أعين بن ليث المصري " تهذيب الكمال " (١٥/ ١٩١ / ٣٣٧١)، وعبد الله بن يوسف التنيسي، وعلي بن معبد " صحيح ابن خزيمة " (١/ ١٤٣ / ٢٨٢)، وأبيه عمرو بن تمام الكلبي " الجرح والتعديل " (٦/ ٢٢٣ / ١٢٣٧)، وعمرو بن خالد " صحيح',NULL,'555',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (558,'ابن خزيمة " (٤/ ٢٠ / ٢٢٧٠)، ومعاوية بن زايد المؤذن، والمعلى بن الوليد " مسند أبي عوانة " (١/ ٩٧ / ٣٠٤)، والنضر بن عبد الجبار " صحيح ابن خزيمة " (٤/ ٦٣ / ٢٣٥٨)، ونعيم بن حماد " صحيح ابن خزيمة " (٢/ ٣٥٨ / ١٤٦٢)، ويحيى بن عبد الله بن بكير، ويوسف بن عدي بن زريق، ويقال: ابن الصلت التميمي " صحيح ابن خزيمة " (١/ ٢٤٤ / ٤٨٤).
روى عنه: (مكحول) محمد بن عبد الله بن عبد السلام " الإكمال لابن مأكولا " (٧/ ١٣٢)، وابن جوصاء [الدمشقي] " المقتنى " (٢/ ٣١ / ٥٢٠٥).
التعديل والتجريح: قال عبد الرحمن بن أبي حاتم: (كتبت عنه، صدوق). وكل اسم لم يعز إلى كتاب، من معجم شيوخه وتلاميذه، وميزته بالأسود العريض، فهو من ترجمته في " الجرح والتعديل ".
وانظر: " الجرح والتعديل " (٨/ ٣٤ / ١٥٧)، و" مولد العلماء " (٢/ ٥٧٤)، و" المقتنى " (٢/ ٣١ / ٥٢٠٥)، و" نزهة الألباب في الألقاب " (٢/ ٢٧١ / ٣٠٧٢)، و" رجال تفسير الطبري " (٥٠٥/ ٢٣٨٨).
٣٠٧ - أبو بكر الباهلي (١٢٥)
(طب: ٢١٢٩ر / ١٠ش)
(تس: ٢١١٨ر / ١٠ش)، و (تخ: ١٠ر / ١ش)، و (تق: ١ر / ١ش)
أبو بكر، محمد بن عمرو بن العباس، الباهلي، البصري: توفي سنة تسع وأربعين ومائتين، من العاشرة، ثقة.
روى عن: جعفر بن عون (تس ٢١٥٥٥)، والحسين - غير مكنى، ولا منسوب - (تس ٢٧٤٣٩)، وسالم بن سعيد (تس ٢٦٤٩٩)، وسفيان بن عيينة (تس ١٨٢٣٥)، وعبد الصمد بن عبد الوارث (تس ١٨٤٨٣)، وعبيد الله [ابن موسى] (تس ٤٣٥٦)، وعبيدة بن بكار الأزدي (تس ٣٣١٣٥)، وعطاء المقدمي (تس ٦٨٠٦)، ومحمد بن إبراهيم بن أبي عدي (تس ٣٦١١٢)، وأبي عاصم الضحاك بن مخلد النبيل (تس ١٢٥ و ٢٧٨ و ٣١٤ و ٣٥٤ و ٣٧٥ و ٣٨٣ و ٣٩٣ و ٤١٢ و ٤٥٥ و ٤٦٦ و ٤٩٣ و ٤٩٧ و ٥١٤ و ٥٢٥ و ٥٤٢ و ٥٥٩ و ٥٦٦ و ٦٢٣ و ٦٣٣ و ٦٤٧ و ٦٦٩ و ٧١٣ و ٧٥٦ و ٨١٤ و ٨٣٠ و ٨٣٤ و ٨٧٢ و',NULL,'556',NULL,NULL,'0');
