INSERT INTO [b29741] VALUES (566,'روى عنه: الحسن بن عبد الله القطان " الثقات " (٩/ ١٠٧ / ١٥٤٤٩)، والحسين بن إسماعيل المحاملي، والحسين بن محمد المطبقي " تاريخ بغداد " (٨/ ٩٧ / ٤١٩٩) وعبد الله بن أحمد بن حنبل، وأبو قريش، محمد بن جمعة بن خلف، وعبد الله بن محمد البغوي، ويحيى بن محمد بن صاعد.
وكل اسم لم يعز إلى كتاب - وميزته بالأسود العريض - من معجم شيوخه وتلاميذه، فهو من ترجمته في " تاريخ بغداد ".
التعديل والتجريح: ذكره محمد بن حيان في " الثقات "
وقال الخطيب البغدادي في " تاريخ بغداد " بسنده عن عبد الرحمن بن يوسف [بن خراش] قال: " كان ثقة ".
وقال الشيخ أحمد شاكر في تحقيقه " لتفسير الطبري " (٢/ ١٦ / ٥٨): " وهن من شيوخ الطبري الثقات، أكثر من الرواية عنه ".
وانظر: " الثقات " (٩/ ١٠٧ / ١٥٤٤٩)، و" تاريخ بغداد " (٣/ ١٢٧ / ١١٤٥) و" رجال تفسير الطبري " (٥٠٥/ ٢٣٨٧).
١٤٧ - تمييز أبو العباس، محمد - وقيل: أحمد - بن عمرو بن العباس بن عبيد، العصفوري، القلوري، البصري، توفي سنة ثلاث وستين ومائتين، من الحادية عشر، ثقة.
وانظر: (تقريب التهذيب) (١/ ٥٧٥ / ٨٢٠٤).
١٤٨ - تمييز محمد بن عمرو بن عباد بن جبلة بن أبي رواد، العتكي، من أهل البصرة، توفي سنة ٢٣٤، يغرب، ويخطئ.
روى عن: حماد بن زيد، وعمرو بن عباس الباهلي، وبن عاصم النبيل.
روى عنه: الحسن بن سفيان.
قلت: والعتكي من أقران والد (محمد بن عمرو) صاحب الترجمة، وممن روى عنه.',NULL,'564',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (567,'وانظر: (الثقات) (٩/ ٨٣ / ١٥٣١٠)، و" تهذيب الكمال " (٢٦/ ٢٠٨ / ٥٥١١).
١٤٩ - تمييز محمد بن عمرو بن عباد بن جبلة بن أبي رواد، الباهلي، من أهل البصرة، يغرب، ويخالف.
يروي عن: محمد بن مروان العقيلي.
روى عنه: الحسن بن سفيان.
وانظر: (الثقات) (٩/ ٩٠ / ١٥٣٥٢).
٣٠٨ - أبو جعفر الطائي (٥٤٤٨)
(أبو داود، والنسائي في مسند علي)
(طب: ٥ر / ٢٦ش)
(تس: ٢ر / ١ش)، و (تخ: ٢ر / ٢ش)، و (تهـ: ٢١ر / ١٨ش)
و (تق: ٥ر / ٣ش)، و (صر: ١/ ١ش)
أبو جعفر - ويقال: أبو عبد الله - محمد بن عوف بن سفيان، الطائي الحمصي:
توفي سنة اثنتين - أو: ثلاث - وسبعين ومائتين، من الحادية عشرة، ثقة حافظ.
روى عن: آدم بن أبي إياس (٣٨١٧١)، و (تهـ ٢٦٤٥ و ٢٦٥٦)، وأحمد بن خالد (تق ٣٤٣)، وأحمد بن يونس الحمصي (تهـ ١٢٤٨) وإسماعيل بن عبد الكريم الصنعاني (تهـ ٢١٠) وحيوة بن شريح الحمصي (تس ١٥٣٨٩ و ٢٢٦٤٢) و (تق ٦٤١) و (تهـ ١٤٣٨) والربيع بن روح (تس ٢٢٦٤٢)، و (د)، وشريح (تس ٣٨٣٠١)، وأبي صالح [عبد الله بن صالح الجهني المصري كاتب الليث] (تس ١٥٣٩١)، و (تهـ ٦٥٣ و ١٣١٠)، وعبد الله بن عبد الجبار الخبائري (تهـ ٩٨) و (د) وأبي عبد الرحمن المقرئ عبد الله بن يزيد (تهـ ١٢٩٨، و (د) وأبي المغيرة عبد',NULL,'565',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (568,'القدوس بن الحجاج الخولاني (تس ١٣١١١ و ١٧٧٧١ و ١٨٣٦٢ و ٢٠٩٧٦ و ٢٤٨٨٣ و ٢٩٩٥٠ و ٢٩٩٥٨ و ٢٩٩٩٦ و ٣٠٢٢٧ و ٣١٤٤٦ و ٣١٥٠٩ و ٣٣٦٢٦)، و (تهـ ٢٢٤)، و (تق ٦٦٥ و ٥٨٤)، و (تخ ٢/ ٥٦٥)، (صر ٣٩) و (د)، وعثمان بن سعيد (تق ٩٠٤)، وعلي بن الحسن العسقلاني (تس ٢٦٥٦١)، وعمرو ـ غير مكنى، ولا منسوب - (تهـ ٥٦٣) ومحمد بن إسماعيل بن عياش (تس ٥٤٤٨ و ٢٦٥٦٠ و ٣١٠٦٢ و ٣٦٨٤٠ و ٣٦٨٥٢) و (تهـ ٧٤٠ و ٢٥٤٨)، و (د)، ومحمد بن عمران بن محمد بن أبي ليلى (تهـ ٢٤٩٣)، ومحمد بن المبارك الصوري (تس ٢٤٨٨٣)، و (تهـ ٤٦٩)، و (د)، ومحمد بن مسلم الرازي (صر ٣٩) ومحمد بن يوسف الفريابي (تهـ ١٢٥٠)، و (د، عس)، والمعتمر [بن سليمان] (تس ٣٢٢١٣) وموسى بن أيوب النصيبي (تهـ ٥٥٤)، والهيثم بن جميل الأنطاكي (تهـ ٢٥) والوليد بن عتبة (تهـ ٣٥٧)، ويزيد بن عبد ربه الحمصي (تس ١٥٣٨٩)، و (عس)، وأبي أيوب الدمشقي (تهـ ٢١٢)، وأبي نعيم (تخ ١/ ١٥)، وأبي اليمان (تهـ ٣٠٢٠)، وأحمد بن حنبل - مسائل - " السنة للخلال " (١/ ٨١ / ١١)، وعبد السلام بن عبد الحميد السكوني " تذكرة الحفاظ " (٢/ ٥٨١ / ٦٠٦)، وعبد العزيز بن موسى اللاحوني " تاريخ أسماء الثقات " (١/ ١٦٢ / ٩٣٣) ومحمد بن المتوكل " السنة للخلال " (١/ ١٨٩ / ٢٠١).
روى عنه: أبو بكر أحمد بن عمرو الشيباني " الآحاد والمثاني " (١/ ٣٣١ / ٤٥٥)، وأحمد بن هارون بن روح البرذعي " المعجم الأوسط " (٢/ ٣٧٦ / ٢٢٧٥)، وإسحاق بن إبراهيم " سنن الدارقطني " (١/ ١٠١ / ٢٩)، وأبو محمد عبد الله بن علي بن الجارود " المنتقى " (١/ ١٩ / ٢٤)، ويحيى بن عبد الباقي المصيصي " المعجم الكبير " (٤/ ٢٣٧ / ٤٢٣٤)، وأبو بكر بن داسة " سنن البيهقي " (١/ ٣٧ / ١٥٧).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه ثقة، صدوقًا، صاحب حديث، يحفظ، أعرف بحديث بلده من غيره، وما كان بالشام منذ أربعين سنة مثله، عالما بحديث الشام، صحيحًا، وضعيفا.
وقال الخلال: " هو إمام، حافظ في زمانه، معروف بالتقدم في العلم والمعرفة، كان أحمد يعرف له ذلك، ويقبل منه، وله عن أبي عبد الله - يعني - أحمد بن حنبل، مسائل',NULL,'566',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (569,'صالحة يغربه فيها بأشياء، والله تعالى أعلم ".
وقال الذهبي في " التذكرة ": " قد وثقه غير واحد، وأثنوا على معروفه ونبله، وقد سمع منه أحمد بن حنبل حدثه به عن والده "
قلت: وتصحف اسم أبيه (عوف) إلى (عون) في الآثار (تس: ٢٠٩٧٦ و ٢٦٥٦٠ و ٢٦٥٦١ و ٣٠٢٢٧).
وتصحف اسم شيخه، (سريج بن يونس) في الأثر رقم، (تس: ٣٨٣٠١) فصار (شريج) بالمثلثة المعجمة، بدل السين المهملة، ولم ينسبه أيضا.
وقد روى (أبو يعلى الموصلي)، الأثر نفسه أيضا، عن (سريج بن يونس) في " مسنده " (٤/ ٣٨ / ٢٠٤٤) وكما هو في " تفسير بن كثير " (٤/ ٥٦٦)، عن أبي يعلى أيضا.
وانظر: " الجرح والتعديل " (٨/ ٢٥ / ٢٤١)، و" الثقات " (٩/ ١٤٣ / ١٥٦٥٩)، و" تهذيب الكمال " (٢٦/ ٢٣٦ / ٥٥٢٧)، و" الكاشف " (٢/ ٢٠٨ / ٥٠٩٨)، و" تذكرة الحفاظ " (٢/ ٥٨١ / ٦٠٦)، و" المقتنى " (١/ ١٤٨ / ١١١٦)، و" سير أعلام النبلاء " (١٢/ ٦١٣ / ٢٣٨)، و" طبقات المحدثين " (١/ ١٠٠ / ١١٤٤)، و" تهذيب التهذيب " (٩/ ٣٤٠ / ٦٣٤)، و" تقريب التهذيب " (١/ ٥٠٠ / ٦٢٠٢)، و" المقصد الأرشد " (٢/ ٤٨٢ / ١٠٣٧)، و" طبقات الحفاظ " (١/ ٢٦٢ / ٥٨٥)، و" رجال تفسير الطبري " (٥٠٥/ ٢٣٩٢).
٣٠٩ - أبو الحسين الدامغاني (٢٥٣١)
(النسائي)
(طب: ١٦ر / ٣ش)
(تس: ١٣ر / ٢ش)، و (تهـ: ٢ر / ٢ش)، و (تق: ١ر / ١ش)
أبو الحسين، محمد بن عيسى بن زياد، الدامغاني، الرازي: من العاشرة، مقبول.
روى عن: سفيان بن عيينة (تس ١١٤١٨ و ١٧٢٩٩ و ٣٧٥٠٤)، (تهـ ١٦٨٩)، وعبد الله بن عمرو (تهـ ٨٦٣)، وعبد الله بن المبارك (تس ٢٨٣١ و ٣٢٣٠ و ٥٤٨٣ و ١٣٣٦٤ و ٢٠٩٧٩ و ٣١٥٨٢ و',NULL,'567',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (570,'٢٢٨٠٦ - و ٣١٥٩٧ و ٣٥٢٦٠)، و (تق: ٤٠٥)، و (س).
روى عنه: محمد بن الحسين الطبركي " العظمة " (٢/ ٤٦٨ / ٣٠).
التعديل والتجريح: قال أبو حاتم: " رازي يكتب حديثه ".
قلت: وقد تحرف اسمه في الأثر رقم (تس: ٢٢٨٠٦) من " التفسير " إلى (يحيى) بدلا من (محمد)، وهو خطأ من النساخ أو المطابع، والله أعلم.
وانظر: " الجرح والتعديل " (٨/ ٣٩ / ١٧٦)، و" الكنى والأسماء " (١/ ٢٥٣ / ٨٧٠)، و" تهذيب الكمال " (٢٦/ ٢٤٨ / ٥٥٣٠)، و" الكاشف " (٢/ ٢٠٨ / ٥١٠١)، والمقتنى (١/ ١٨٧ / ١٥٩٨)، و" تهذيب التهذيب " (٩/ ٣٤٣ / ٦٣٧)، و" تقريب التهذيب " (١/ ٥٠٠ / ٦٢٠٥)، و" رجال تفسير الطبري " (٥٠٦/ ٢٣٩٣).
٣١٠ - أبو موسى الزمن (٣٥)
(الستة)
(طب: ١٤٤٨ر / ١٠٣ش)
(تس ١٠١٩ر / ٩٠ش) (تخ ٦ر / ٥ش) (تهـ ٣٠٨ر / ٣٨ش) (تق ١١٦ر / ٢٨ش)
أبو موسى، محمد بن المثني بن عبيد بن قيس بن دينار العنزي، البصري، المعروف بـ (الزمن)، مشهور بكنيته، وباسمه، مولده سنة سبع وستين ومائة، وتوفي في ذي القعدة، سنة اثنتين وخمسين ومائتين - ويقال: سنة إحدى وخمسين، ويقال: سنة خمسين - من العاشرة، ثقة، ثبت.
روى عن: أبي إسحاق إبراهيم بن إسحاق الطلقاني (تس ٢٠٦٣٢ و ٢٣٠٤٩ و ٢٤٩٩٣) و (د) وإبراهيم بن عمر بن أبي الوزير (تس ٩٥٢١) و (د، س، ق)، وإبراهيم بن يزيد بن مردانبة (تس ١١٥٤٨) وأحمد [بن الفضل الحفري] (تس ٣٠٦٢٤)، وأسباط بن محمد القرشي (تس ٣٣٩١)، و (سي)، وإسحاق بن يوسف الأزرق (تس ٢٠٥٩٣ و ٢١٨٨٨)، (خ)، وإسماعيل بن حكيم الخزاعي (تس ٢١٦٦٩)، وأبي اليسع إسماعيل بن حماد بن أبي المغيرة الأشعري، مولاهم',NULL,'568',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (571,'(تس ١٨٣٣٦) وبدل من المحبر (تس ١٠٤٣١ و ٢٢٧٧٦ و ٢٣٠٢٦ و ٢٣٢٣١ و ٢٧٥٤٧ و ٣٦٤٠٨)، و (ت) وبكر بن بكار العباسي (تهـ ١٧٢٠)، وبكر بن يحيى بن زبان العنزي (تس ٢٨٤٨٧)، وحبان بن موسى (تس ٤٨٧٥)، وحجاج بن منهال (تس ١١٠٤٤ و ٢٨٦٢٩ و ٣٧٧٥٤)، و (د، س، ق) وحرمي بن عمارة بن أبي حفصة (تس ٨٥١٥ و ٨٦٢٦ و ١٢٠٨٣ و ١٥٢٩٩ و ٢٢٢٦٧ و ٢٦١١٨ و ٢٦١٧٠ و ٢٩٥٧٠ و ٣٠٦٦٩ و ٣١١٠٨ و ٣٣٢١٨ و ٣٤٩٧٢ و ٣٧٢٧٠ و ٣٨١٥٥) و (تهـ ٩٤٥ و ١٤٠٢ و ٢٣٥٣ و ٢٤٦٣ و ٢٧٨٤) والحسن بن خالد القريعي (تس ٢٨٦٣)، والحسن بن عنبسة الوراق (تس ٢٠٩٦٧)، وحسين [ابن داود الملقب (سنيد)] (تس ٧٣٥)، وحفص بن عمر الضرير (تس ١٨٠٣٧ و ١٨٠٣٨)، وأبي النعمان الحكم بن عبد الله العجلي (تس ٢٣٤ و ٨٤٩٢ و ١٠١٩٠ و ١٧٠٢٨ و ١٨٠٤٧ و ١٨٦٨٥ و ٢٢٣٧٦ و ٢٢٣٨٦ و ٢٧٥٤٥ و ٢٩١٣٧ و ٢٩٩١٤ و ٢٩٩٢٠ و ٣٥٣٧٦ و ٣٦٩٢٩)، و (تهـ ٤٤٥)، (م، ت، س)، والحكم بن مروان (تق ٣٢٨)، وأبي أسامة حماد بن أسامة (تهـ ٢٤٦٢)، و (د) وحماد بن مسعدة (تس ١٨٨٦٧)، و (م)، وحماد بن واقد (تهـ ١٩١١)، و (د)، وخالد بن الحارث الهجيمي (تس ٩٨٨٣ و ٢٢٠١٥ و ٣١١٥٨ و ٣٢٢٨٨ و ٣٢٤٩٨)، و (تق ٨٢٠ و ٩٧٣)، و (تهـ ١٢١٨)، و (ع)، وخلف بن موسى العمي (تخ ١/ ١٦)، وربعي بن إبراهيم الأسدي (تس ١٦١١ و ٣٥٦٢١) وروح بن أسلم الباهلي (تهـ ٩٠١)، وسالم بن نوح (تس ١١٢٠٤)، و (تهـ ١٩٩٧ و ٢١٩٣)، و (بخ، م، د، س)، وسعيد بن ربيع الحرشي (تس ١١٢٠٠ و ٣١٧٤٢)، و (تق ٦٧١) وسعيد بن عامر الضبعي (تس ١٠٢)، و (تهـ ١٠٨٦ و ٢٥١٦)، وسكن بن نافع الباهلي (تس ٣٨٠٣٧) وسلم بن قتيبة الشعيري (تس ١٠٢٠٤ و ٣٦٩١٤)، و (تهـ ٢٤٣٦)، وأبي عتاب سهل بن حماد الدلال (تس ١٨٤٦٢ و ٢٤٧٩٧ و ٣١٣٤٨)، و (د)، وسهل بن يوسف (تس ٨٧٦٧ و ٢٢٧٤٨ و ٢٩٤٧٣ و ٣١٧٩٢)، و (د، س) وشعبة - غير مكنى، ولا منسوب - (تس ١٧٦٠)، وأبي محمد، شيبان بن فروخ أبي شيبة الحبطي الأبلي (تس ٦٩٢ و ١٨٥٨٧ و ٢٣١١٧)، وصفوان بن عيسى (تس ٢٥٥٠٩)، و (تهـ ٢٥٩٣) و (بخ، د، س)، وأبي عاصم الضحاك بن مخلد (تس ١١٠٤٣ و ١١٢٢٨ و ١١٢٢٩ و ١٢٧٠٠ و ١٢٧٠١ و ١٢٧٠٧ و ١٢٨١٩ و ١٣٧٩٤ و ١٣٨٣٠ و ١٦٦٠٨ و ١٧١٠١ و ٣٢٨٣٥ و ٣٦٠٧٠)، و (تهـ ٣٩٧ و ٣٩٨ و ١٦٧٠)،',NULL,'569',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (572,'و (خ، م، د، ت، س)، وعباس بن الوليد (تس ٢٣٤٠٠)، وعبد الله بن إدريس (تس ٤٢٩١ و ٢٣٦٩١ و ٢٦٤٤٢ و ٣٣٦١٦ و ٣٦٠٦٣ و ٣٦٠٧٥)، (م، س)، وعبد الله بن حمران الحمراني (تق ١٠٠٠)، وعبد الله بن داود (تق ٨٧٤)، وعبد الله بن رجاء الغداني (تس ٢٦٢٦٣)، و (سي)، وعبد الله بن نمير (تهـ ٧٠٦)، و (تق ٤٥٩ و ٤٦٧)، و (م)، وعبد الأعلى بن عبد الأعلى (تس ١٩٠٩ و ١٩٤٢ و ٢٨٢٥ و ٣٤٧٢ و ٤٤٤٢ و ٤٤٨٧ و ٤٥٧٣ و ٤٦٣٠ و ٤٦٥٠ و ٤٩٥٤ و ٥٢٩٣ و ٥٨١٧ و ٥٨٤٢ و ٦٤٤٢ و ٧٢٨١ و ٧٣٥٩ و ٧٣٧٧ و ٧٧٤٣ و ٧٧٧٠ و ٨٢٨٢ و ٨٦٣٦ و ٨٦٩٧ و ٨٧٠١ و ٨٩٧٩ و ٩٠٣٨ و ٩٥٨٧ و ٩٦٢٥ و ٩٧٧٩ و ٩٧٨٠ و ٩٧٩١ و ٩٨٩٧ و ١٠١٦٤ و ١٠٥٦١ و ١١٠٩٥ و ١١١٦٧ و ١١٢٠١ و ١١٢٦٦ و ١٢٩١٣ و ١٢٩٤٧ و ١٢٩٧٨ و ١٥٣٤٥ و ١٥٨١٠ و ١٦٢٠٨ و ٢٠٦٤٢ و ٢٠٩٦٩ و ٢٢٥٢٩ و ٢٢٦٧٧ و ٢٣٢٩٦ و ٢٣٥٧٥ و ٢٤٧٩٠ و ٢٤٩٢٨ و ٢٥٢٧٤ و ٢٥٢٨٥ و ٢٥٦٣٧ و ٢٥٧٤٧ و ٢٦٥٧٣ و ٢٦٧١٨ و ٢٦٧٧٥ و ٢٧٧٩٣ و ٢٧٨٧٠ و ٢٨٠٩٨ و ٢٨١٤٩ و ٢٨٣٥٩ و ٢٨٥٨٤ و ٢٨٦٢٥ و ٢٨٨٤٣ و ٢٩٢٦٥ و ٢٩٣١٦ و ٣٠٥٧٧ و ٣٠٥٧٨ و ٣١٠٤٨ و ٣١٠٧٠ و ٣١٢٤٥ و ٣١٤٥٧ و ٣١٧١٩ و ٣١٧٤١ و ٣٢٧١٠ و ٣٣٦٠٩ و ٣٣٧١٤ و ٣٣٧٣٦ و ٣٣٧٤٤ و ٣٣٨٣٧ و ٣٤٦١٤ و ٣٤٦٢٢ و ٣٥١٦٨ و ٣٥٣١٣ و ٣٧٤٢٤ و ٣٧٤٣٧ و ٣٧٦٩٧ و ٣٧٩٥٤ و ٣٨٠٠٨ و ٣٨٢٣١) و (تهـ ١٠١ و ٢٢٠ و ٦٨٦ و ٦٨٨ و ٨٧١ و ٢٢٠١ و ٢٩٧٥ و ٣٠٩٠)، و (تق ١١٩ و ٥١١ و ٧٧١)، و (تخ ١/ ١٦٨)، و (ع)، وعبد الرحمن بن مهدي (تس ٥٠ و ٢٣٣ و ٣٠١٣ و ٣٢٤٧ و ٤٣٤٧ و ٥٢٢٤ و ٥٢٣٠ و ٨٧٧٥ و ١١٣١٠ و ١١٥١٧ و ١٥٤٠٥ و ١٥٤١٣ و ١٥٤١٥ و ١٥٥٩٩ و ١٨٧٥٥ و ١٨٧٥٩ و ١٨٧٧٠ و ١٨٧٧٢٢ و ٢٠٠٤٥ و ٢٠٥٩٤ و ٢٠٧٨٥ و ٢٠٨٠٥ و ٢١٧١٢ و ٢٣٨٥٣ و ٢٣٩٣٠ و ٢٣٩٣٧ و ٢٤٣٨٠ و ٢٤٩٢٩ و ٢٥٤٦١ و ٢٦٠٣٨ و ٢٦٥٧١ و ٢٨٠٥١ و ٣٠٣٨٤ و ٣١٥٤١ و ٣٢٨٦٣ و ٣٤٩١٩ و ٣٦٠٦٥ و ٣٧٤٣٣ و ٣٧٤٣٨ و ٣٧٤٥٩ و ٣٧٤٦٣ و ٣٨١٠٠)، و (تهـ ٥٨٤ و ٦٣٩ و ٧٦٥ و ١٠١٨ و ١٠٢٨ و ١٠٨٠ و ١٠٨٢ و ١١٣٨ و ١٨٥٥ و ١٩١٢ و ٢٠٤٣ و ٢٠٥٧ و ٢١٠٤ و ٢٢٧٣ و ٢٣٦٣ و ٢٣٧٤ و ٢٥٢٨ و ٢٩٩٧ و ٢٩٩٨ و ٤٠١٦ و ٤٠٤٦)، و (تق ١١٧ و ١٨٤ و ٤٠٩ و ٥١٣ و ٥٦٢ و ٩١٤ و ٩٨٢ و ٩٨٧ و ٩٩٢)، و (تخ ٢/ ٥٦٦)، و (ح، م، ت، س، ق) وعبد الصمد بن عبد الوارث (تس ٢٨٧٢ و ٤٦٢٧ و ٧٤٣٢ و ١٠١٦٨ و ١٠٢٠٥ و ١٠٨٢٢ و ١١١٩٩ و ١٢٠٨٢ و ١٢٠٤٧ و ١٥٨١٣ و ١٧٥٢٩ و ٢٠١٦٧ و ٢٠٨٠٤ و ٢٢٢٣١ و ٢٣٤٠٨ و ٢٥٣٣٢ و ٢٥٣٤٩ و ٢٥٦٠٩ و ٢٥٦٢٣ و ٢٩٠٧١ و ٢٩١٣٨ و ٣١١٥٦ و ٣١٩٢٢ و ٣٣٦٩٩ و ٣٤٢٢٢ و ٣٤٥٤٧ و ٣٥٥٠٨ و ٣٥٥٠٩ و ٣٨١٥٣ و ٣٨١٧٣)، و (تهـ ١٥٠ و ٧١٤ و ٧٢٢ و ٧٦٤ و ١١٨٤ و ٢١٦١ و ٢١٧٤ و ٢٢٣٨ و ٢٧٩١ و ٣٠٧٠ و ٤٠٤٧)، و (تق ١٨٥ و ٢٤٠ و ٢٦٩ و ٣٩٤ و ٥٠٢ و ٨٤٥ و ٩٨٠)، و (',NULL,'570',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (573,'م، ت، س، ق)، وعبد العزيز بن عبد الصمد العمي (تهـ ١٠٤٢)، و (خ، د، س)، وعبد الوارث [بن سعيد] (تس ٢٥٧٨٤)، وعبد الوهاب بن عبد المجيد الثقفي (تس ٢٣٤٢ و ٢٧٤٧ و ٢٨٢٣ و ٣٠٣٢ و ٣٣١٧ و ٤٤٤٣ و ٤٤٨٦ و ٤٥٧٢ و ٤٦٤٧ و ٤٦٥٧ و ٤٧١٩ و ٤٩٥٣ و ٥٢٠١ و ٥٢١٨ و ٥٢٦٣ و ٥٣٣٠ و ٥٣٥٥ و ٥٤٦٥ و ٥٤٨٢ و ٥٨٤٥ و ٦٣٣١ و ٧٢٨٠ و ٨٦٨٧ و ٨٧٠٥ و ٨٧٨٧ و ٩١٤٢ و ٩١٩٧ و ٩٥٩٦ و ٩٦٦٨ و ٩٧٩٢ و ٩٨٩٦ و ١٠٧٣٣ و ٧٠٧٣٤ و ١١١٠٧ و ١١٢٠٦ و ١١٢٦٥ و ١١٤٠٣ و ١١٤٨٦ و ١٢٣٧٦ و ١٣١٠٠ و ١٥٦٦٥ و ١٥٧١٢ و ٢٠١٧٨ و ٢١٩٩٧ و ٢٤٧٩١ و ٢٤٨٧٣ و ٢٥٢٦٧ و ٢٥٢٨٩ و ٢٨٥٨٣ و ٢٨٦٢٤ و ٣١٢٤٤ و ٣١٧١٨ و ٣٢٤٧٥ و ٣٣٦٠٨ و ٣٥٧٠٩ و ٣٧٦٩٨ و ٣٨٢٢٢ و ٣٨٢٣٣)، و (تهـ ٦٨٧ و ١١٦٨ و ١٣٥١ و ١٥٦٦ و ١٨٤٩ و ٢٩٧٤ و ٣٠٨٩)، و (تق ٢٥٠ و ٤٦٩ و ٥١٠ و ٧٧٠)، و (ع)، وعبيد الله بن معاذ (تس ١٠٣٥ و ٣١١١١ و ٣١١١٢ و ٣٥٨٨٤ و ٣٥٩٠٤ و ٣٥٩١٦)، و (تهـ ١٨٠٧)، وعبيد الله بن موسى (تس ٣٧٠٦٧ و ٣٣٥٥٥)، و (تق ٦٤٢ و ٧٦١)، و (تهـ ١٧٢٤ و ١٨٨٠ و ٣٠٣٥)، و (س)، وعثمان بن عمر بن فارس، (تس ١٥٢٣٦ و ١٦٣٨٤ و ٢٦١٩٧ و ٢٧٨٢٣ و ٢٨٣٣٧ و ٣١٠٩٦)، و (تهـ ٥٦ و ١٢٨)، و (خ م، د، س)، وعفان بن مسلم الصفار (تس ٢٤٧٤٢)، و (تهـ ٩٤٦)، و (م)، وعلي بن يحيى (تس ١٧٧٣٦)، وأبي الهذيل العلاء بن الفضل بن عبد الملك بن أبي السوية المنقري (تق ٦٥٨)، وعمر بن يونس اليمامي (تس ١١٥٠٩ و ٣٤٤١٤)، و (بخ، م، د)، وعمر بن شقيق (تق ٩٠٠)، وعمرو بن عاصم الكلابي (تس ٢٥٧٤٦) و (د)، وعمرو بن عون (تس ٧٢١١)، وأبي قطن عمرو بن الهيثم البغدادي (تس ١٨٦٨٧ و ٣٤٣٩٩)، وأبي نعيم الفضل بن دكين (تس ٧٢١٣)، وقريش بن أنس (تس ٣٤٩٣١)، و (قد، ت)، ومحمد بن إبراهيم بن أبي عدي (تس ٣٦ و ٤٨ و ٧٩ و ٤٢٠ و ١٨٨٢ و ٢٨٢٤ و ٢٧٦٩ و ٣١٥١ و ٣١٥٣ و ٤٢٦٤ و ٤٤٤١ و ٤٤٥٢ و ٤٥١٨ و ٤٦٤٨ و ٤٦٨٨ و ٤٧٠٢ و ٤٧١٣ و ٥٢٠٢ و ٥٢٢١ و ٥٣٤١ و ٥٤٤٣ و ٥٨٣٦ و ٧٣٧٥ و ٧٤٣٢ و ٨٢٠٨ و ٨٢٨٧ و ٨٦٧٨ و ٨٧٨٨ و ٩٠٤١ و ٩٥٥٩ و ٩٦٦٩ و ٩٧٧١ و ١٠٤٢٧ و ١٠٨٧٢ و ١١١٩٤ و ١١٣٤٥ و ١١٣٧٨ و ١١٤٠١ و ١١٤٢٨٩ و ١١٤٨٧ و ١١٥٠٢ و ١٢٤٨٠ و ١٢٥٥٥ و ١٢٦٥٨ و ١٢٦٧٠ و ١٢٦٧٥ و ١٢٧٦٤ و ١٢٨٨٩ و ١٢٩٤٦ و ١٣١٠١ و ١٥٤٠٥ و ١٥٤١٤ و ١٥٧٨١ و ١٥٩١٧ و ١٦٩٦٥ و ١٧٢٢٦ و ١٧٧٣٢ و ١٨٧٥٢ و ١٨٣٣٧ و ١٨٧٠٥ و ١٩٩٩٧ و ٢٠٦٩٦ و ٢١١٢٥ و ٢١٣٧٠ و ٢١٨٥١ و ٢٢٠١٦ و ٢٢٥٥٩ و ٢٣٢٩٥ و ٢٣٨٧٥ و ٢٣٨٨٣ و ٢٤٨٧٤ و ٢٥١٤٦ و ٢٥١٨٨ و ١٥٧٨٥ و ٢٥٨٣٢ و ٢٥٨٤٥ 