INSERT INTO [b29741] VALUES (574,'٢٦١٨١ - و ٢٨١٣١ و ٢٩٣١٥ و ٢٩٤٧٢ و ٢٩٤٩٣ و ٣٠٥٧٦ و ٣٠٩٢٩ و ٣٠٩٥٠ و ٣١٥٢٤ و ٣١٧٤٣ و ٣٢١٠١ و ٣٢٢٨٧ و ٣٢٣٤٧ و ٣٢٤٧٦ و ٣٢٤٩٧ و ٣٢٥٨٣ و ٣٣٧٤٢ و ٣٤١٠٣ و ٣٤١١٠ و ٣٤١٥٨ و ٣٤٥٢٨ و ٣٤٩٠٦ و ٣٥٧١٠ و ٣٦١١٤ و ٣٦١٤١ و ٣٦٥٨٩ و ٣٧٣٢٨ و ٣٧٦١٦ و ٣٧٦١٧ و ٣٧٦٦٩ و ٣٧٧٥٢ و ٣٧٧٥٣)، و (تهـ ١١٩ و ٣٢٨ و ٣٤١ و ٤٤٦ و ٦٥٠ و ٦٨٥ و ٧٠٣ و ٧٩٩ و ١٠٢٩ و ١٢١٧ و ١٣٦٨ و ١٣٨١ و ١٤٣٠ و ١٤٩١ و ١٦٣٧ و ١٨٥٠ و ١٩٨٩ و ٢١٢٤ و ٢١٩٨ و ٢٢٥٠ و ٢٢٧١ و ٢٢٨٧ و ٢٣٠٤ و ٢٤٥٨ و ٢٥٨١ و ٢٦٢٧ و ٢٦٣٦ و ٢٦٥٩ و ٢٨٣٣ و ٢٨٦٤ و ٢٩٧٧ و ٢٩٨٢ و ٢٩٨٦ و ٣٠٥٩ و ٣٠٦٧ و ٣٠٩٠)، و (تق ٣٣ و ٥٢ و ٩٥ و ١٠٩ و ٢٢٧ و ٢٥١ و ٢٥٣ و ٢٧٤ و ٢٧٥ و ٣٠١ و ٣٩٠ و ٤٦٣ و ٥٠٤ و ٥٠٦ و ٥٤٢ و ٦٥٤ و ٧١٤ و ٨١٩ و ٩٧٠)، و (تخ ١/ ٢٩ و ٢/ ٢٢١)، و (ع)، وأبي سعيد بن أسعد التغلبي (تس ٢٧٨٦٥)، و (عخ)، ومحمد بن جعفر غندر (تس ٣٥ و ١٠١ و ١٩٤ و ٢٠٨ و ٤١٩ و ٩٣٥ و ٢٠٦١ و ٢١١٤ و ٢٥٣١ و ٢٧٣٨ و ٢٧٤١ و ٢٨٤١ و ٢٨٥٢ و ٢٨٩١ و ٢٨٩٢ و ٢٨٩٣ و ٢٩٠٠ و ٢٩١٢ و ٢٩٤٣ و ٣٠١٤ و ٣٠١٦ و ٣٠٨٢ و ٣١٥٢ و ٣١٩٨ و ٣٢١١ و ٣٢٤٦ و ٣٣٠٢ و ٣٣٤٣ و ٣٤٤٨ و ٣٤٦٤ و ٣٤٧١ و ٣٦٤٩ و ٣٦٨٩ و ٣٧٢٤ و ٣٨٦٢ و ٣٨٩٣ و ٣٩٢٧ و ٣٩٤١ و ٤١١٢ و ٤١٧٦ و ٤٤٨٤ و ٤٥١٠ و ٤٥١٥ و ٤٥٣٩ و ٤٥٤٠ و ٤٥٤١ و ٤٥٤٩ و ٤٥٧٩ و ٤٥٨٩ و ٤٦١٧ و ٤٦٥٤ و ٤٦٨٧ و ٤٨٥٧ و ٤٨٧٢ و ٤٩٦٤ و ٥٠٧٦ و ٥١٠٤ و ٥١٦٠ و ٥٢٠٨ و ٥٢٢٣ و ٥٢٢٩ و ٥٢٣٤ و ٥٤٢٥ و ٥٤٢٨ و ٥٤٥١ و ٥٤٦٢ و ٥٤٩٥ و ٥٥٦٠ و ٥٦٧٣ و ٥٩٧١ و ٥٩٩٥ و ٦١٢٠ و ٦٤٤٩ و ٧٤٢١ و ٧٤٥٥ و ٧٥٣٦ و ٧٨٣٢ و ٧٨٣٣ و ٧٨٥٢ و ٨٢٨٦ و ٨٢٩٤ و ٨٣٤٦ و ٨٤٦٣ و ٨٥٤٦ و ٨٦١٢ و ٨٦١٥ و ٨٦٤٨ و ٨٦٩٥ و ٨٧٦٣ و ٨٨١١ و ٨٨٦٤ و ٨٩٨٣ و ٩٠٣٩ و ٩٠١٤ و ٩٠٤٣ و ٩٠٩٢ و ٩١٠٥ و ٩١٥٢ و ٩١٥٣ و ٩١٩٩ و ٩٢٢٣ و ٩٢٨٠ و ٩٣٦٣ و ٩٤٢١ و ٩٤٧٥ و ٩٥٣٧ و ٩٥٨٥ و ٩٦٠٩ و ٩٦١٠ و ٩٦٧٨ و ٩٨٤٣ و ١٠١٥٢ و ١٠١٩٨ و ١٠٢٠٢ و ١٠٢٤٢ و ١٠٣٤٥ و ١٠٤٩٤ و ١٠٤٩٥ و ١٠٥٨١ و ١٠٨١٣ و ١٠٨٢١ و ١٠٩٨٦ و ١١١٤٩ و ١١١٨٤ و ١١١٨٦ و ١١١٩٢ و ١١٢٣٧ و ١١٢٦٣ و ١١٢٨١ و ١١٣٠٤ و ١١٣٠٩ و ١١٣٢٦ و ١١٣٥٠ و ١١٤٠٠ و ١١٤٢٨ و ١١٤٣٠ و ١١٤٣١ و ١١٥٢٤ و ١١٩٥٤ و ١١٩٥٥ و ١٢٠٨٠ و ١٢١٩٣ و ١٢٥٢٢ و ١٢٥٣٣ و ١٢٥٥٤ و ١٢٥٨٨ و ١٢٥٩٨ و ١٢٧٤٩ و ١٢٧٦١ و ١٢٧٦٩ و ١٢٨٣٠ و ١٢٩٠٠ و ١٣٠٠٦ و ١٣٠٢٥ و ١٣١٤١ و ١٣٦٢٦ و ١٣٩٨٥ و ١٤٠٠٣ و ١٤٠٥٧ و ١٤٢٣٣ و ١٤٥٠٨ و ١٤٥٦٧٢ و ١٥١٦٨ و ١٥٣٩٦ و ١٥٦٠١ و ١٥٦٢٢ و ١٥٦٧٤ و ١٦٢٧١ و ١٦٤١٤ و ١٦٤٦٢ و ١٦٥٥٤ و ١٦٥٩٤ و ١٧٠١١ و ١٧٠٥٥ و ١٧١٧٨ و ١٧٣٦٢ و ١٧٣٨٥ و ١٧٤٢٤ و ١٤٦٢٨ و ١٧٨٧٢ و ١٨٠٦٨ و ١٨٦٩٢ و ١٨٧٧٦ و ١٩٢٣٣ و ٢٠٠٩٠ و ٢٠٢١٠ و ٢٠٢٢٨ و ٢٠٣٥٠ و ٢٠٥٤٦ و ٢٠٥٥٠ و ٢٠٥٩٩ و ٢٠٦٦٥ و ٢٠٧١٨ و',NULL,'572',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (575,'و٢٠٧٢٨ و٢٠٧٨٤ و٢٠٧٨٦ و٢٠٨٥٤ و٢٠٩٤١ و٢٠٩٥٤ و٢١٠٩١ و٢١١٨٤ و٢١٣٧٨ و٢١٦٩٩ و٢١٩٧٣ و٢٢١١٦ و٢٢٢٣٤ و٢٢٢٤٦ و٢٢٥٩٩ و٢٢٦١١ و٢٢٦٢٢ و٢٣٢٧٨ و٢٢٧١٥ و٢٢٧٤٧ و٢٢٨١٥ و٢٣٠٩٧ و٢٣٣٩١ و٢٣٥٥٠ و٢٣٧٩٣ و٢٤١٣٧ و٢٤٣٧٦ و٢٤٧٥٩ و٢٤٧٨٩ و٢٤٧٩٥ و٢٤٨٠٠ و٢٥٠٢٨ و٢٥٠٩٥ و٢٥٣٢٠ و٢٤٨٥٨ و٢٥١٥١ و٢٥٢٣٢ و٢٥٧٤٨ و٢٥٧٦٥ و٢٥٧٩٩ و٢٥٨١٩ و٢٥٩٥٢ و٢٥٩٨٠ و٢٦٠٣١ و٢٦٠٣٩ و٢٦٠٤٣ و٢٦٠٥١ و٢٦٠٦٩ و٢٦٢٠٩ و٢٦٢٥٢ و٢٦٣٢٦ و٢٦٥١٤ و٢٦٥٧٠ و٢٧٠٦٩ و٢٧٤٧٦ و٢٧٦٢٩ و٢٧٨٤١ و٢٧٨٨٨ و٢٧٩٤٢ و٢٨٠٥٢ و٢٨٠٧٣ و٢٨٠٨١ و٢٨٠٩٧ و٢٨٠٩٩ و٢٨١٩٨ و٢٨٢٣٧ و٢٨٢٤٦ و٢٨٢٦٩ و٢٨٣٩٣ و٢٨٥٤٢ و٢٨٥٥٥ و٢٨٥٥٧ و٢٨٩٨٩ و٢٩٠١٢ و٢٩٠٤٩ و٢٩٠٥٤ و٢٩١٥٥ و٢٩٢٦٦ و٢٩٤٩٦ و٢٩٥١٤ و٢٩٨٢٣ و٢٩٩٦٢ و٣٠٠٢٩ و٣٠٣٨٣ و٣٠٦٢٦ و٣٠٦٥١ و٦٠٦٨٢ و٣١٣٠٠ و٣١٤٥٨ و٣١٥٣٠ و٣١٥٨٥ و٣١٦٢٦ و٣١٧٥١ و٣١٨٧٠ و٣١٩٨٢ و٣٢٠٠٨ و٣٢٠١٣ و٣٢٠٢٢ و٣٢٠٢٥ و٣٢١١٤ و٣٢١٢٦ و٣٢١٤٨ و٣٢١٥٤ و٣٢١٦٧ و٣٢٢٤٨ و٣٢٢٩٠ و٣٢٣٠٥ و٣٢٣٤٢ و٣٢٣٦٠ و٣٢٥٦٨ و٣٢٥٨٢ و٣٢٦٨٩ و٣٢٧٠٤ و٣٢٧٣٨ و٣٣٠٨٤ و٣٣٣٧٤ و٣٣٢٩٤ و٣٣٤٠٨ و٣٣٤٥٣ و٣٣٦٥١ و٣٣٨٠٥ و٣٤٠٣٢ و٣٤٠٣٦ و٣٤٠٣٨ و٣٤١٥٧ و٣٤٢٢١ و٣٤٢٢٤ و٣٤٣٣٦ و٣٤٣٧١ و٣٤٤٤٥ و٣٤٤٦٥ و٣٤٦٢١ و٣٤٧٣٨ و٣٤٨٠٦ و٣٤٨٣٣ و٣٤٨٦١ و٣٤٩٢٨ و٣٤٩٣٦ و٣٤٩٣٩ و٣٥٠٤٨ و٣٥٣٤٩ و٣٥٥٠٧ و٣٥٧٠٢ و٣٥٧١٧ و٣٥٧٥١ و٣٥٩٧٧ و٣٦٣٦٩ و٣٦٣٧٠ و٣٦٤٠٢ و٣٦٤٤٩ و٣٦٤٧٨ و٣٦٥٤٠ و٣٦٨١٣ و٣٦٨٤٢ و٣٧٢٥٩ و٣٧٠٤٤ و٣٧٤٢٠ و٣٧٤٧٢ و٣٧٤٧٧ و٣٧٥٠٥ و٣٧٥٣٥ و٣٧٥٣٨ و٣٨٠٦٨ و٣٨٠٩٣ و٣٨٠٩٩ و٣٨١٢٦)، و (تهـ ٣٤ و٧٦ و٨١ و١١٧ و١٢٧ و١٧٥ و١٨٥ و٢٠٣ و٢٣٤ و٢٤٢ و٢٦٤ و٣٢٣ و٣٢٧ و٣٣٢ و٣٧٤ و٣٨٦ و٤٧٢ و٤٧٦ و٤٧٩ و٥١١ و٥٨٣ و٦٠٨ و٦٩٠ و٧٠٤ و٧١٥ و٧٢١ و٧٢٣ و٧٢٤ و٧٣٩ و٨٧٣ و١٠١٩ و١٠٥٠ و١٠٧٩ و١٠٨٣ و١١٠٥ و١١٠٦ و١١٠٧ و١١١٥ و١١٣٦ و١١٣٧ و١٣٢٤ و١٣٧٧ و١٤٢٠ و١٤٤٨ و١٤٩٥ و١٥٨٣ و١٦٤٥ و١٦٥٣ و١٧٣٧ و١٧٤٣ و١٧٧ و١٨٣٣ و١٨٥٢ و١٨٩١ و١٨٩٢ و١٨٩٧ و١٩٥٦ و١٩٨٨ و٢٠١٧ و٢٠٧١ و٢٠٩٩ و٢١٤٦ و٢١٥٤ و٢١٦٣ و٢١٨٠ و٢١٨٦ و٢١٨٩ و٢١٩٥ و٢٢٠٤ و٢٢٢٨ و٢٢٥٥ و٢٢٦١ و٢٢٨٠ و٢٢٨٩ و٢٣١٢ و٢٣١٥ و٢٣٥٢ و٢٣٧١ و٢٤٠٨ و٢٤٢٤ و٢٤٤١ و٢٥٢٧ و٢٦٠٦ و٢٦١١ و٢٦٦٩ و٢٧٠٣ و٢٧٠٩ و٢٧٢٣ و٢٧٣٠ و٢٧٦٢ و٢٧٦٨ و٢٨٠٠ و٢٨٢٣ و٢٨٦٦ و٢٨٧٢ و٢٨٧٣ و٢٩٨٤ و٢٩٨٧ و٢٩٩٥ و٣٠٠٥ و٣٠١٠ و٣٠١٧ و٣٠٢٥ و٣٠٣٨ و٣٠٧٨ و٣٠٩٢ و٤٠١٥)، و (تق ٤٨ و١١٣ و١١٥ و١١٦ و١١٨ و١٤٥ و١٥٢ و١٥٥ و٢٣٩ و٢٥٤ و٢٦٤ و٢٧٣ و٣١٧ و٣٣٣ و٣٦٥ و٣٨١ و٣٩٨ و٤١١ و٤٣٠ و٤٦٢ و٥٠٨ و٥١٤ و٥٣٣ و٥٤١ و٥٦٧ و٥٨٧ و٦٠٢ و٦٠٥ و٦٥٥ و٧١٦ و٧١٧ و٧٥٥ و٨١٣ و٨٣٩ و',NULL,'573',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (576,'١٠٠٥ - و١٠٤٠)، وتخ ٢/ ١٨٧)، و (ع)، ومحمد بن خالد بن عثمة تس ٩٥٨٩ و٢٥٧٩٢ و٢٥٧٩٣)، و (تق ٢٢) (د، ت)، ومحمد بن عبد الله الأنصاري (تس ٣٦٩٨٥) وتهـ ٢٥٩ و٢٤٧٦ و٢٥٠٥)، و (خ، م، د، ت، س)، ومحمد بن فضل بن غزوان (تس ١٠٣٦١ و٢٥٧١٤ و٣٨١٨٠)، و (م)، ومحمد بن المفضل (تس ٢٦١١٦)، ومحمد بن نافع (تس ٤١١٣)، ومرحوم بن عبد العزيز العطار (تس ٢٣٨٣٧)، و (س)، ومسلم بن إبراهيم (تهـ ١٠٨)، و (تق ١١١)، ومسلمة بن الصلت الشيباني (تهـ ٢١٦٨ و٢١٦٩)، ومعاذ بن معاذ (تس ١٦٣٦٨)، و (تق ٩٦٤ و٩٧٠ و٩٧٤)، و (خ، م، د، س)، ومعاذ بن هانئ (تس ١٥٢٨٠)، و (س)، ومعاذ بن هاشم (تس ٢٦٩٦ و٤٦٢٩ و١٤٨٢٨ و١٥٢٣١ و٣٢٤٩٩)، و (تهـ ٤٨ و٩٩ و١٠٣٩ و١٠٦٩ و٢٥٦٥ و٢٥٨٠ و٢٦١٨ و٢٨٦٣)، و (تق ٥٩ و١٠٨ ٢٥٢ و٩٨٦)، و (خ، م، د، س، ق)، ومعتمر بن سليمان (تس ٤٨٦٨ و٢٠٦٣٣)، وأبي حذيفة موسى بن مسعود النهدي (تس ٨٢٩٩ و١٠٤٣٣)، ومؤمل بن إسماعيل (تس ١٦٨٧ و٢٠٥٩ و٢٤٣٨٠ و٣٠٣٩٧)، و (ت)، وهارون ـ غير مكني ولا منسوب، ولعله ابن المغيرة ـ (تس ٩٥٤٣)، وهاشم بن الوليد (تس ١٢٧٢٦)، والهيثم بن عبيد الصيد (تهـ ١٩٧٨)، ووكيع بن الجراح (تس ٣٢٨٣٤)، والوليد بن مسلم (تس ٣٥٣٠٨)، و (تق ٢٢٤ و٤٨٥)، و (خ، م، د، س)، ووهب بن جرير بن حازم (تس ٢٨٦٥ و٣٤٥٥ و٢٥٤٤ و٣٧٢١ و٤٤٥٠ و٤٤٥١ و٤٥١١ و٥٤٧١ و٤٧٢٥ و٨٣٤٧ و٨٧٣٢ و٨٧٧٦ و١٠٧٤٧ و١١٣٢٩ و١١٣٣٩ و١١٣٧٥ و١٢٦٠٢ و١٤١٦٢ و١٥٤١٥ و١٦٢١٨ و١٩١٩٠ و٢١٣٦١ و٢٥٧٣١ و٢٥٩١٠ و٣٠٢٣٥ و٣٠٥١٣ و٣١٥٨٣ و٣٢١٥٥ و٣٤٩٤٧ و٣٥٣٩٨ و٣٥٨١١ و٣٨٠٧٥)، (تق ٨٣١)، و (تهـ ٦٧٠ و٦٩٩ و٨٩٦ و١٠٨٥ و١٣٢٣ و٢٠٤٤ و٢١٥٥ و٢١٨٧ و٢٥٩١ و٢٦٨٢)، و (م، د، س)، ويحيى بن أبي بكير الكرماني (تس ٢٥٧٢٢ و٣٣٨٤٦ و٣٤٥١٧)، و (د)، ويحيى بن حماد الشيباني (تس ٢٧٨٨١ و٢٨٢٣٨ و٣١٤٥٩ و٣١٥١٩ و٣١٥٢٠) و (م، قد، ت، س)، ويحيى بن سعيد القطان (تس ٣٠٤٠ و٣٢٠٨ و٣٣١٨ و٣٤١٤ و٤٦٣٨ و٤٩٠٠ و٦٧١٠ و٧٥٤٤ و٨٦٩٥ و٩١٦١ و٩٨٨٢ و١١٥١٤ و١١٥٣٠ و١٢٧٥٤ و١٦٤٦٣ و١٦٩٦٢ و١٧٠٦٥ و٢٢٦١٣ و٢٣٨٥٢ و٢٤٣٨٠ و٢٧٤٧٧ و٢٨٢٢٢ و٢٨٢٦٧ و٢٨٨٤٣ و٣٠٠٨٧ و٣٢١٠١ و٣٣١٩٥ و٣٥٣٠٦ و٣٥٣٢١ و٣٦٥٨٩)، و (تهـ ٤١٥ و٧٠٥ و١٣٥٠',NULL,'574',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (577,'و ٢١٣١ و٢١٦٧ و٢١٨٢ و٢٣٨٦ و٢٦٤٩ و٢٦٦٩ و٢٨٢٢ و٣٠٤٤ و٣٠٤٩)، و (خ، م، س، ق)، ويحيى بن سليم (تس ٢٨٠١٦)، ويحيى بن عبيد الله (تس ٩٦٦٥)، ويحيى بن كثير العنبري (تس ٩٢٢٢ و٢٢٢٥١ و٣٥٨٢٤ و٣٧٣٠٨) و (تهـ: ٧٠٥ و٢٥٩٩)، و (خ، م، د، تم، س)، ويزيد بن هارون (تس ٤٤٤٢ ٩٥٩٧ و١١١٢٨ و١١٢٠٢ و٢٢٧٧٨ و٢٣٣٠٧ و٢٥١٤٧ و٢٥١٦٤ و٢٥١٦٩)، و (تق ٣٥١ و٤٥٩ و٣٦٦)، و (تهـ ١٧٠٠ و١٩٩١ و٢٩٧٦ و٢٩٧٨)، و (م، س)، ويعلى بن عبيد (تس ٢٦٤٤٦ و٣٠٩٣٨)، ويعمر بن بشر المنقري (تس ٢٣٠٣٧ و٢٤٩٩٤ و٣١١٦٤ و٣١١٦٧ و٣٤٨٢٢ و٣٦٠٨١)، ويوسف بن يعقوب [السدوسي] (تس ٣٦١٦٨ و٣٦١٨٢ و٣٧٩٧٦)، وأبي أحمد الزبيري (تس ٧٢١٢)، وتهـ ١٥٢٩)، و (خ، س، ق)، وأبي بكر الحنفي (تس ٢٨٥٠١)، و (م، س)، وأبي داود الطيالسي (تس ٤٩ و١١٠ و١١١ و٦٤٣ و٢١٦١ و٢٧٣٩ و٢٧٤٢ و٢٨٧١ و٢٩٧٤ و٤٤٠٨ و٤٤٨٥ و٤٦٢٨ و٥٤٥٢ و٥٦٧٤ و٨٠٧٥ و٩٠٤٠ و٩٥٠٧ و١٠٥٨٢ و١١٢٦٤ و١١٣١٣ و١١٣٤٦ و١٢٧٩٩ و١٣٧٢٠ و١٦٤١٩ و١٧٠١٢ و١٨٦٨٦ و١٨٦٩٣ و٢٠٣٥١ و٢٠٤١٧ و٢١١٦٩ و٢٢٢٢٣ و٢٢٣٥١ و٢٣٢٥٩ و٢٣٢٦٩ و٢٣٨٤٧ و٢٤٩٦٧ و٢٩٤٦٥ و٣١٤٥٤ و٣١٥٢٨ و٣١٧٥٠ و٢٣١٠٣ و٣٢٢٠٨ و٣٢٢٢٤ و٣٢٦٩٠ و٣٣٣٧٥ و٣٣٨٧٥ و٣٤٣٩٨ و٣٧٧٥٨)، و (تق ٣٩٥ و٦٥١ و٩٥٩ و٩٦٨ و٩٦٩ و٩٨٣ و١٠١٠)، و (تهـ ٧٧ و١٠٧ و٦٩١ و٧٨٦ و٨٠٦ و٨٩٥ و٩٨٤ و١١١٤ و١١٣٩ و١١٤٠ و١١٥٢ و١١٨٠ و١١٨٥ و١٤٢١ و١٥٤٠ و١٩٣٧ و٢٢٣٧ و٢٢٥٧ و٢٢٦٦ و٢٢٧٤ و٢٦٧٠ و٢٧٠٤ و٤٠٢٥)، و (م، ت، س، ق)، وأبي الربيع ـ خطأ ـ (تس ٢٣٥٠٥)، وأبي عامر العقدي (تس ١١٥١٠ و١٢٧٩٩ و١٨٥٨٣ و٢٣٤٠٩ و٢٨٣٦٠)، و (تق ٨١ و٩٨٧)، وتهـ ١٠٢ و٨٣١ و٩١٨ و٩١٩ و١٠٤٠ و٢١٣٣ و٢٨٢٦ و٢٨٦٤ و٢٠٣٢)، و (خ، م، د، س)، وأبي عثمان ـ صاحب الأكفان ـ (تق ٨٣٥)، وأبي معاوية الضرير (تس ٣٠٣١ و٨٢٨٣ و١٥٦٧١ و٢٦٤٤٣ و٣٢٣٢١)، و (تهـ ٧٩٠ و٢٦٨٠)، و (خ، م، د)، وأبي الوليد الطيالسي (تس ٢٥٦٦ و٢٥٨٨ و٢٨٥٣ و٣٥٣٥ و٣٧٢٥ و٥٦٨٩ و١٠١٥٣ و١٢١٩٤ و٢٠٧٥٢ و٢٤٤٦٧ و٢٥٣٣٠ و٢٥٨١٣ و٢٥٨١٥ و٢٥٨١٧ و٢٨٦٣٠ و٣٧٢٧١ و٣٧٣٠١ و٣٧٤٢١ و٣٨٠٩٤)، و (تق ٢٠٣ و٢٩٥ و٤٥٣ و٩١٢)، و (تهـ ١٧ و٥٣٢ و١٩٣٧ و٢٢٦٢ و٢٢٧٧ و٢٣٦٤ و٢٦٢٧ و٣٠١٤)، و (م، د، ت، س)، وأحمد بن بشير القرشي " تهذيب الكمال " (١/ ٢٧٣ / ١٤)، وأحمد بن سنان القطان " تهذيب الكمال " (١/ ٣٢٢ / ٤٥)، وأحمد بن صالح المصري " تهذيب الكمال "',NULL,'575',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (578,'(١/ ٣٤٠ / ٤٩)، وإسحاق بن إدريس الإسواري " الجرح والتعديل " (٢/ ٢١٣ / ٧٢٩)، وإسماعيل بن سعيد الثقفي " الجرح والتعديل " (٢/ ١٧٣ / ٥٨٦)، وبشر بن وضاع البصري " الجرح والتعديل " (٢/ ٣٦٩ / ١٤٢٢)، وبهلول بن مورق الشامي " تهذيب الكمال " (٤/ ٢٦٣ / ٧٧٦)، وجندب بن حفص السمان " الجرح والتعديل " (٢/ ٥١٢ / ٢١١١)، وحبان بن هلال الباهلي " تهذيب الكمال " (٥/ ٣٢٨ / ١٠٦٤)، وحبوة بن أبي السمح القصاب " الجرح والتعديل " (٣/ ٣١٨ / ١٤٢٦)، وحجين بن المثنى البغدادي " التاريخ الكبير " (٣/ ١٣٤)، وحرمي بن حفص العتكي " تهذيب الكمال " (٥/ ٥٥٣ / ١١٦٨)، والحسن الحارثي " التاريخ الكبير " (٥/ ٩٢ / ٢٥٥)، وحسين بن حسين الأشقر الفزاري " الثقات " (٨/ ١٨٤ / ١٢٨٨٧)، وحنظلة بن ثهلان بن قبيصة " التاريخ الكبير " (٣/ ٤٤ / ١٦٩)، ورباح بن خالد الكوفي " الثقات " (٨/ ٢٤٢ / ١٣٢٣١)، وزكريا بن يحيى بن عمارة " تهذيب الكمال " (٩/ ٣٨١ / ٢٠٠١)، وزياد بن الربيع اليحمدي " تهذيب الكمال " (٩/ ٤٥٨ / ٢٠٤٠)، وسهل بن محمد بن الزبير " تهذيب الكمال " (١٢/ ٢٠٠ / ٢٦١٩)، وشبيب بن سليم الأسيدي " الجرح والتعديل " (٤/ ٣٥٩ / ١٥٧٠)، وشريح بن سراج الجرمي " الجرح والتعديل " (٤/ ٣٣٥ / ١٤٦٨) وعاصم بن عبد العزيز الأشجعي " تهذيب الكمال " (١٣/ ٤٩٩ / ٣٠١٣)، وعامر بن صالح المزني " تهذيب الكمال " (١٤/ ٤٣ / ٣٠٤٥)، وعباس بن عبد العظيم العنبري " تهذيب الكمال " (١٤/ ٢٢٢ / ٣١٢٨)، وعبد الله بن سنان الهروي ثم البصري " تاريخ بغداد " (٩/ ٤٦٩ / ٥٠٩٨)، وعبد الرحمن بن عثمان بن أبي بكرة الثقفي " تهذيب الكمال " (١٧/ ٢٧١ / ٣٨٩٧)، وعبد الملك بن الصباح " التاريخ الكبير " (٨/ ١٠٥ / ٢٣٥٣)، وعبد الملك بن عمرو القيسي " تهذيب الكمال " (١٨/ ٣٦٤ / ٣٥٤٥)، وعبيد الله بن عبد المجيد الحنفي " تهذيب الكمال " (١٩/ ١٠٤ / ٣٦٦١)، وعبيد بن واقد القيسي " تهذيب الكمال " (١٩/ ٢٤٥ / ٣٧٤٣)، وعبيدة بن رداد التميمي " الثقات " (٥/ ١٤٠ / ٤٢٥٧)، وعتاب بن المثنى القشيري " تهذيب الكمال " (١٩/ ٢٩٤ / ٣٧٦٧)، وعثمان بن الفرقد العطار " تهذيب الكمال " (١٩/ ٤٧٥ / ٣٨٥٤)، وعرعرة بن البرند السامي " تهذيب الكمال " (١٩/ ٥٥٢ / ٣٨٩٧)، وعمرو بن شقيق السدوسي " التاريخ الكبير " (٦/ ٣٤٣ / ٢٥٨٢)، وعمرو بن صالح بن المختار " الجرح والتعديل " (٦/ ٢٤٠ / ١٣٣٠)، وعمرو بن محمد بن أبي رزين الخزاعي " تهذيب الكمال " (٢٢/ ٢١٨ / ٤٤٤٣)، وعون بن كهمس بن الحسن " الجرح والتعديل " (٦/ ٣٨٨ / ٢١٥٩)، وعيسى بن',NULL,'576',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (579,'شعيب النحوي " تهذيب الكمال " (٢٢/ ٦١٢ / ٤٦٢٩)، غسان بن مضر " التاريخ الكبير " (٧/ ٣٥٥ / ١٥٣٥)، القاسم بن الحكم البصري " الثقات " (٧/ ٣٣٨ / ١٠٣٤٧)، ومحمد بن الزبرقان الأهوازي " تهذيب الكمال " (٢٥/ ٢٠٨ / ٥٢١٨)، ومحمد بن سعيد التغلبي " التاريخ الكبير " (١/ ٩٥ / ٢٦٢)، ومحمد بن أبي الشمال العطاردي " الجرح والتعديل " (٧/ ٢٨٦ / ١٥٥٠)، ومحمد بن الملك بن أبي الشوارب " الجرح والتعديل " (٨/ ٥ / ١٨)، ومحمد بن عمر الباهلي " تهذيب الكمال " (٢٦/ ١٧٠ / ٥٤٩٥)، ومحمد بن عمر بن مطرف القرشي " تهذيب الكمال " (٢٦/ ١٧٧ / ٥٤٩٩)، ومحمد بن فضل السدوسي " تهذيب الكمال " (٢٦/ ٢٨٧ / ٥٥٤٧)، ومحمد بن محبب القرشي " تهذيب الكمال " (٢٦/ ٣٦٥ / ٥٥٨٠)، ومحمد بن مروان بن قدامة " تهذيب الكمال " (٢٦/ ٣٨٧ / ٥٥٩٥)، ومحمد بن النعمان البصري " الجرح والتعديل " (٨/ ١٠٨ / ٤٦٦)، ومحمد بن يحيى الذهلي " تهذيب الكمال " (٢٦/ ٦١٧ / ٥٦٨٦)، ومحرز بن وزر بن عمران " التاريخ الكبير " (٣/ ٢ / ٥)، ومطهر بن الهيثم الطائي " تهذيب الكمال " (٢٨/ ٨٨ / ٦٠٠٩)، ومعدي بن سليمان أبو سليمان – صاحب الطعام – " تهذيب الكمال " (٢٨/ ١٥٨ / ٦٠٨٣)، ومعقل بن مالك الباهلي " تهذيب الكمال " (٢٨/ ٢٧٧ / ٦٠٩٣)، ومغيرة بن سلمة " التاريخ الكبير " (١/ ١٤٢ / ٤٢٧)، وموسى بن زياد بن بحر " الجرح والتعديل " (٨/ ١٤٣ / ٦٤٤)، والنضر بن كثير السعدي " تهذيب الكمال " (٢٩/ ٦٤٣٣)، ونوح بن يزيد البغدادي " الجرح والتعديل " (٨/ ٤٨٥ / ٢٢١٦)، والهذيل بن الحكم الأزدي " تهذيب الكمال " (٣٠/ ١٥٩ / ٦٥٥٥)، ووهب بن إسماعيل الأسدي " تهذيب الكمال " (٣١/ ١١٣ / ٦٧٤٩)، ويحيى بن أبي الحجاج الأهتمي " تهذيب الكمال " (٣١/ ٢٦٣ / ٦٨٠٧)، ويونس بن عبيد الله العميري " الجرح والتعديل " (٩/ ٢٤١ / ١٠١٦).
روى عنه: إبراهيم بن إسحاق الصيدلاني " تاريخ بغداد " (٦/ ٤١ / ٣٠٦١)، وإبراهيم بن محمد الكندي " تاريخ بغداد " (٦/ ١٥٧ / ٣٢٠١)، وأبو ذر أحمد بن عبد الله الترمذي " تاريخ بغداد " (١٤/ ٣٢٤ / ٧٦٤٨)، وأحمد بن محمد بن إبراهيم الأبلي " تهذيب الكمال " (١/ ٤٢٧ / ٩٠)، وأحمد بن محمد بن نصر " تاريخ بغداد " (٥/ ٢٥١٥) وأحمد بن محمد بن أبي النماش " تاريخ بغداد " (٥/ ١٠٩ / ٢٥١٨)، إسحاق بن إبراهيم بن حاتم المديني " تاريخ بغداد " (٦/ ٣٩٠ / ٣٤٣١)، وإسحاق بن إبراهيم الكتاني " تاريخ بغداد " (٦/ ٣٩١ / ٣٤٣٤)، وابنه ـ إسحاق بن محمد بن المثنى',NULL,'577',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (580,'" تاريخ بغداد " (١١/ ٢٥٢ / ٦٠٠٢)، وإسماعيل بن سعدان البزاز " تاريخ بغداد " (٦/ ٢٩٨ / ٣٣٣٣)، وجعفر بن محمد بن أبي الصعو " تاريخ بغداد " (٧/ ٢١٠ / ٣٦٨١)، والحسن بن علي العباسي مولاهم " تاريخ بغداد " (٧/ ٣٨٤ / ٣٩١١)، والحسين بن علي القطان " تاريخ بغداد " (٨/ ٧٠ / ٤١٤٥)، وعبد الله بن محمد القطان " تاريخ بغداد " (١٠/ ١٠٥ / ٥٢٢٤)، وأبو الحسين عبد الله بن محمد بن يونس السمناني، وعبد الله بن يزيد الدقيقي " تاريخ بغداد " (١٠/ ١٩٧ / ٥٣٤٠)، وعبد الرحمن بن روح السمسار " تاريخ بغداد " (١٠/ ٢٧٩ / ٥٣٩٧)، وعلي بن إبراهيم البلدي " تاريخ بغداد " (١١/ ٣٣٧ / ٦١٧١)، وعلي بن إسماعيل البزاز " تاريخ بغداد " (١١/ ٣٤٦ / ٦١٨٨)، وعلي بن الحسن بن الجنيد " الجرح والتعديل " (٢/ ١٢٥ / ٣٩٠)، وعلي بن حماد العسكري " تاريخ بغداد " (١١/ ٤٢٠ / ٦٢٩٨)، وعلي بن الفتح بن محمد " تاريخ بغداد " (١٢/ ٤٩ / ٦٤٢٦)، وعلي بن محمد الكاتب " تاريخ بغداد " (١٢/ ٧١ / ٦٤٧٠)، وعمر بن إبراهيم بن سليمان " تهذيب الكمال " (٢١/ ٢٦٧ / ٤١٩٩)، والقاسم بن زكريا المطرز ومحمد بن أحمد المقدمي " تاريخ بغداد " (١/ ٣٣٦ / ٢٤٦)، ومحمد بن بنان الخلال " تاريخ بغداد " (٢/ ١٠٧ / ٥٠٠)، ومحمد بن جعفر الخوارزمي " تاريخ بغداد " (٢/ ١٣٤ / ٥٣٣)، وأبو بكر محمد بن عيسى السمسار " تاريخ بغداد " (٢/ ٤٠٢ / ٩٢٧)، ومحمد بن الفضل البغدادي " تاريخ بغداد " (٣/ ١٥٤ / ١١٨٦)، ونصر بن أحمد الكندي " تاريخ بغداد " (١٣/ ٢٩٣ / ٧٢٦٥)، وهارون بن محمد العكبري " تاريخ بغداد " (١٤/ ٣١ / ٧٣٧٠)، وهارون أبو محمد الطرسوسي " تاريخ بغداد " (١٤/ ٣١ / ٧٣٧١)، ويزداد بن عبد الرحمن الكاتب " تاريخ بغداد " (٣/ ٢٢٨ / ١٢٩١)، ويزيد بن محمد بن عبد الصمد القرشي " تهذيب الكمال " (٣٢/ ٢٣٤ / ٧٠٤٤).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقا، حجة، ثبتا، ورعا، فاضلا، عاقلا، حافظا، صالح الحديث، لا بأس به، صدوق اللهجة، صاحب كتاب، لا يحدث إلا من كتابه، واحتج سائر الأئمة بحديثه، وكان يُقَدَّمُ على (البندار)، وكان في عقله شيء، وكان يغير في كتابه.
قلت: ولم أقف له على تجريح يذكر، سوى غمز صالح جزرة في عقله، وقد رد الخطيب البغدادي ذلك عليه وأثنى على محمد بن المثنى. والله أعلم بحال الجميع.',NULL,'578',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (581,'وقد وقعت إشكالات، وتصحيفات، وتحريفات، وإسقاطات، لأسماء بعض شيوخه، من النساخ، حتى لا يتوهم الناظر أنه يروي عن شيوخه مثل:
الأثر رقم (١٧٦٠)، حيث سقط اسم (محمد بن جعفر)، بينه وبين (شعبة).
والأثر رقم (٢٥٧٨٤)، سقط اسم (عبد الصمد)، بينه وبين (عبد الوارث).
وروى عن أسماء مبهمة أو أنها سقطت منها أسماء شيوخه وبقيت أسماء آبائهم مثل:
الأثر رقم (تس ٩٥٤٣)، حيث روى عن (هارون)، غير منسوب!! ولعله (يزيد بن هارون الواسطي). أو هارون بن المغيرة.
والأثر رقم (تس ٧٣٥)، حيث روى عن (حسين) غير منسوب!! ولعله (ابن داود المصيصي) (الملقب سنيد).
وقع تحريف في الأثر (تس ٢٧٤٧)، بحيث صار اسم (محمد بن المثنى) (عمر بن المثنى).
وتصحف اسم (عبيد الله)، إلى (عبد الله). في الأثر رقم (تس ٢٧٠٦٧)، وقد فصلت القول في ذلك في ترجمة (عمر بن المثنى) رقم (٢٢٠)، من هذا الكتاب. فانظرها هناك.
وانظر: " الكنى والأسماء " (١/ ٧٦٧ / ٣١٢٨)، و" تاريخ أسماء الثقات " (١/ ٢١٢ / ١٢٧٨)، و" الجرح والتعديل " (٨/ ٩٥ / ٤٠٩)، و" الثقات " (٩/ ١١١ / ١٥٤٧١)، و" ذكر أسماء التابعين " (١/ ٣٣٣ / ١٠٠٧ و٢/ ٢٣٢ / ١١٥١)، و" من روى عنهم البخاري في الصحيح " (١/ ١٧٧ / ١٩٣)، و" رجال مسلم " (٢/ ٣٩٩ / ٢١١٩)، و" تاريخ بغداد " (٣/ ٢٨٣ / ١٣٧١)، و" التعديل والتجريح " (٢/ ٦٤٧ / ٥٠٨)، و" تهذيب الكمال " (٢٦/ ٣٥٩ / ٥٥٧٩ و٣٥/ ٤٤)، و" الكاشف " (٢/ ٢١٤ / ٥١٣٤)، و" تذكرة الحفاظ " (٢/ ٥١٢ / ٥٢٧)، و" المقتنى " (٢/ ١٠٥ / ٦١٢٣)، و" طبقات المحدثين " (١/ ٩٠ / ٩٩٩)، و" ميزان الاعتدال " (٤/ ٤٢٤ / ٨١١٥)، و" الإرشاد " (٢/ ٥١٦)، و" البداية والنهاية " (١١/ ١٣)، و" نزهة الألباب " (١/ ٣٤٥)، و" لسان الميزان " (٧/ ٣٧٣ / ٤٧٣٠)، و" تهذيب التهذيب " (٩/ ٣٣٧ / ٦٩٨)، و" تقريب التهذيب " (١/ ٥٠٥ / ٦٢٦٤)، و" طبقات الحفاظ " (١/ ٢٢٦ / ٥٠٥)، و" رجال تفسير الطبري " (٥٠٨/ ٢٤٠٧).
..
١٥٠ - ـ تمييز أبو جعفر محمد بن المثنى بن زياد السمسار، كان أحد الصالحين، توفي سنة ٢٦٠، صحب بشر بن الحارث، صدوق.',NULL,'579',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (582,'روى عن: بشر بن الحارث، وعفان بن مسلم، ونوح بن يزيد المؤدب.
روى عنه: جعفر بن محمد الصندلي، وعبد الرحمن بن أبي حاتم الرازي، وأبيه محمد بن إدريس الرازي، ومحمد مخلد الدوري.
وانظر: " الجرح والتعديل " (٨/ ٩٥ / ٤١٠) و" تاريخ بغداد " (٣/ ٢٨٦ / ١٣٧٢) ..
٣١١ - ـ أبو عبد الله الباهلي (٢٨)
(مسلم سبعة أحاديث، والترمذي، وابن ماجه)
(طب: ٢٧ ر / ١٩ ش)
(تس ١٠ ر / ٧ ش) و (تخ ٢ ر / ٢ ش) و (تهـ ٩ ر / ٨ ش) و (تق ٦ ر / ٥ ش)
أبو عبد الله، محمد بن محمد بن مرزوق، الباهلي، البصري ـ ابن بنت مهدي بن ميمون: وأكثر ما ينسب إلى جده لأبيه (مرزوق) ـ توفي سنة ثمان وأربعين ومائتين، من الحادية عشرة، صدوق، له أوهام.
روى عن: إبراهيم بن الحجاج (تق ٦٥٣)، وأشهل بن حاتم (تس ٢٨٦١٣)، وبشر بن عمر الزهراني (تهـ ٦٩)، و (م)، وحجاج بن منهال (تس ٢٣٢١٤ و٢٦٢٩٤ و٣٢٣٠٠) و (تخ ١/ ٢٢٢)، وروح بن عبادة (تس ٢٣٤١٧)، و (تهـ ٦٢٨)، و (م)، والضحاك بن مخلد النبيل (تهـ ٢٦ و٦٢)، أبي معمر المقعد عبد الله بن عمرو بن أبي الحجاج (تس ٤٦)، وعبد الله بن عبد الوهاب (تس ٢٠٢٧٠)، وعبد الله بن مسلمة (تهـ ١٢٧٩)، وعبد الملك بن عمرو (تق ٦٨٤)، وعمران بن ميسرة (تهـ ١٠٢٣)، و (أبي يحيى)، عمر بن سنان الملقب ب (صغدي) (تق ٨٢٨ و٩٣١)، وعمر بن يونس بن القاسم اليمامي (تس ٨٢٢٤)، و (تق ٨١١)، و (تخ ٢/ ٨٢)، ومحمد بن جعفر الجرمي (تهـ ١٤٧٤)، ومحمد بن كثير (تق ٦٢٢)، وأبي الوليد هشام بن عبد الملك الطيالسي (تس ٢٨)، ووهب بن جويرية السلمي (تهـ ٣٥٤)، وأبي عامر العقدي (تهـ ١٢٨٩). وأشعث بن شبيب " تذكرة الحفاظ " (٣/ ٩٦٧ / ٩٠٨)، وأبي سعيد الأصمعي " تاريخ بغداد " (٣/ ١٩٩ / ١٢٤٤).',NULL,'580',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (583,'روى عنه: عبد الله بن عبد الحميد الواسطي " الكامل " (٦/ ٢٩١ / ١٧٧٨)، وعبد الله بن محمد بن ياسين " تاريخ بغداد " (٣/ ١٩٩ / ١٢٤٤)، وعلي بن الحسن القافلائي " تاريخ بغداد " (٣/ ١٩٩ / ١٢٤٤)، وأبو عبيد القاسم بن إسماعيل " تاريخ بغداد " (٣/ ١٩٩ / ١٢٤٤)، وأخوه أبو عبيد [بن محمد بن مرزوق] " تاريخ بغداد " (٣/ ١٩٩ / ١٢٤٤).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقا، لينا ربما أخطأ، وله أوهام.
وقد يشتبه ب (محمد بن مرزوق بن النعمان البصري)، وسيأتي في الأثر رقم (تس: ١٧٢٦٣)، وفي الترجمة رقم (٣١٤).
وانظر: " الجرح والتعديل " (٨/ ٨٩ / ٣٨٤)، و" الثقات " (٩/ ١٢٥ / ١٥٥٥٣)، و" الكامل " (٦/ ٢٩١ / ١٧٧٨)، و" تاريخ بغداد " (٣/ ١٩٩ / ١٢٤٤)، و" ذكر أسماء التابعين " (٢/ ٢٣٢ / ١١٤٨)، و" رجال مسلم " (٢/ ٢١٠ / ١٥١٥)، و" تهذيب الكمال " (٢٦/ ٣٧٧ / ٥٥٨٦)، و" ميزان الاعتدال " (٤/ ٢٦ / ٨١٢٣)، و" الكاشف " (٢/ ٢١٥ / ٥١٣٩)، و" المغني في الضعفاء " (٢/ ٦٢٩ / ٥٩٥٠)، و" لسان الميزان " (٧/ ٣٧٤ / ٤٧٣٦)، و" تهذيب التهذيب " (٩/ ٣٨٢ / ٧٠٦)، و" تقريب التهذيب " (١/ ٥٠٥ / ٦٢٧١)، و" رجال تفسير الطبري " (٥٠٩/ ٢٤١١).
..
٣١٢ - ـ محمد بن محمد الصوري (تهـ: ١٧٣٩).
(أبو داود، والنسائي)
(طب: تهـ: ٢ ر / ٢ ش)
أبو عبد الله، محمد بن محمد بن مصعب، الصوري، ولقبه (وحشي) ـ بمهملة ساكنة، ثم معجمة ـ توفي سنة ستين ومائتين، من الحادية عشرة، صدوق.
روى عن: محمد بن المبارك الصوري (تهـ ٢٥٦٢)، و (س)، ويحيى بن المبارك (تهـ ١٧٣٩)
روى عنه: عبد الرحمن بن أبي حاتم، وأبو جعفر الطبري.',NULL,'581',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (584,'التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقا.
وانظر: " الجرح والتعديل " (٨/ ٨٧ / ٣٧٣)، و" الثقات " (٩/ ١٤٠ / ١٥٦٤٢)، و" تهذيب الكمال " (٢٦/ ٣٨٠ / ٥٥٨٧) و" تكملة الإكمال " (٣/ ٦١١)، و" الكاشف " (٢/ ٢١٥ / ٥١٤٠)، و" تهذيب التهذيب " (٩/ ٣٨٣ / ٧٠٧)، و" تقريب التهذيب " (١/ ٥٠٥ / ٦٢٧٢)، و" نزهة الألباب " (٢/ ٢٢٩ / ٢٨٧٣).
٣١٣ - ـ محمد بن محمد العطار (تس: ٥٩٢٠)
(طب: تس: ١ ر / ١ ش)
محمد بن محمد العطار، لعله: أبو الحسن محمد بن محمد بن عمر بن الحكم، البغدادي، ويعرف ب (ابن العطار): من العاشرة، أو الحادية عشرة، توفي فجأة، يوم الأحد لأربع خلون من صفر سنة ثمان وستين ومائتين، وكان ثقة، أمينا، وكان عنده " التفسير "، عن: سنيد بن داود، وكتاب أحمد بن شبويه، عن: ابن المبارك " في الأخبار "، وبنحو هذا قال الشيخ شاكر (٥٩١٩). ولم يتعرض الشيخ التركي في تحقيقه لتفسير الطبري (٤/ ٦٠٢)، لترجمته بشيء.
روى عن: (أبي عبيد)، القاسم بن سلام (تس ٥٩٢٠)، والحسن بن أحمد البزار " سير أعلام النبلاء " (١٣/ ٢٦)، ومسلم بن إبراهيم، وأبي الوليد الطيالسي، وعبد الله بن مسلم القعنبي، وعلي بن بكار " السنن الواردة في الفتن " (٣/ ٥٥١ / ٢٤٢)، ومعمر بن محمد بن عبيد الله بن أبي رافع، وسنيد بن داود، وأحمد بن شبويه المروزي.
روى عنه: إسحاق بن بنان " السنن الواردة في الفتن " (١/ ٢٣٥ / ٣٤)، وعبد الله بن أحمد بن حنبل، ومسعود بن محمد بن شنيف " سير أعلام النبلاء " (١٣/ ٢٦)، وموسى بن هارون ومحمد بن عبد الملك التاريخي، ومحمد بن مخلد العطار.
التعديل والتجريح: ترجم له الخطيب في " تاريخ بغداد " وقال: كان ثقة أمينا.',NULL,'582',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (585,'وكل اسم من معجم شيوخه وتلاميذه ميزته " بالأسود العريض "، ولم أعزه إلى كتاب فهو من ترجمته في " تاريخ بغداد ".
وانظر: " تاريخ بغداد " (٣/ ٢٠٣ / ١٢٤٨)، و" تاريخ دمشق " (٢٦/ ١٩٩)، و" السنن الواردة في الفتن " (١/ ٢٣٥ / ٣٤) ..
٣١٤ - ـ محمد بن مرزوق بن النعمان (١٧٢٦٣)
(طب: ٢ ر / ١ ش)
(تس: ر١/ ١ ش)، و (تق: ١ ر / ١ ش)
محمد بن مرزوق بن النعمان، البصري: من الحادية عشرة، مقبول، وثقه ابن حبان.
روى عن: أبي سلمة موسى بن إسماعيل التبوذكي (تس ١٧٢٦٣) و (تق ٨٢٩)، وأبي عاصم الضحاك بن مخلد (النبيل)، " الثقات " (٩/ ١٢٦ / ١٥٥٥٥).
روى عنه: أهل العراق " الثقات " (٩/ ١٢٦ / ١٥٥٥٥).
التعديل والتجريح: ذكره ابن حبان في " الثقات " وقال: ليس هذا بالباهلي.
قال الحافظ في " التهذيب " ـ معلقا ـ: قلت: وما أظنه إلا هو، فقد تقدم التنبيه على أنه ربما نسب إلى جده، ووقع ذلك عند الطبراني في (الأوسط)، في الأول من الحديثين الذين ذكرهما له ابن عدي. انتهى كلام الحافظ.
قلت: ولقد أشكل على (الشيخين شاكر) أيضا، في تحقيقهما فجعلاهما واحدا، وتردد فيهما الشيخ علي رضا في تعليقه على " الجزء المفقود من تهذيب الآثار "، وفي بعض الرواة عنهما مثل (صغدي).
وقد أشكل علي أنا أيضا ابتداء، كما أشكل عليهم!! وعلى الحافظ من قبلهما!! ولقد استقرأت الطرق التي رواها الطبري عنهما في " التفسير " وأرقامها (تس ٢٨ و٤٦ و٨٢٢٤ و',NULL,'583',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (586,'١٧٢٦٣ - و٢٠٢٧٠ و٢٣٢١٤ و٢٣٤١٧ و٢٦٢٩٤ و٢٨٦١٣ و٣٢٣٠٠)، وفي " تهذيب الآثار " وأرقامها (تهـ ٢٦ و٦٢ و٦٩ و٣٥٤ و٦٢٨ و١٠٢٣ و١٢٧٩ و١٢٨٩ و١٤٧٤)، وفي " التاريخ " (تخ: ١/ ٢٢٢ و٢/ ٢٨ و٢/ ٢٦٧)، وأخيرا في " الجزء المفقود من تهذيب الآثار "، وأرقامها هي (٦٢٢ و٦٥٣ و٦٨٤ و٨١١ و٨٢٨ و٨٢٩ و٩٣١).
وأغلب الظن أنهما اثنان، كما قال ابن حبان في " الثقات " لسببين:
أولهما: أنه أقرب إلى عصرهما من الحافظ، إذ أنه إما أن يكون معاصرا لهما، أو في الطبقة التي بعدهما مباشرة، على أبعد تقدير، ولقد ميز بينهما بعلم مثبت، ومعلوم أن من علم حجة على من لم يعلم، وإن في هذا التمييز، زيادة علم من عالم ثقة ـ على تساهل معروف فيه ـ وهو بالتالي مقبول، لأنها زيادة ثقة، وهذه القاعدة، وإن اشتهرت في الحديث، فليست مختصة به ـ والله تعالى أعلم.
والثاني: أن الطبري روى في " التفسير " عن (محمد بن مرزوق)، في (عشرة مواضع)، سماه في تسعة منها (محمد بن مرزوق) فقط، ولم ينسبه. وفي هذا الموضع أثبت نسبه وسماه: (محمد بن مرزوق البصري). ومعلوم أن زيادة المبنى تدل على زيادة في المعنى. وعلى كل حال فكونهما واحدا أو اثنين، لا يضر الأثر، لأنهما ثقتان، والله تعالى أعلم.
وأما (محمد بن مرزوق الباهلي)، فقط سبقت ترجمته عند الأثر رقم (٢٨)، من كتابي هذا، برقم (٣١١). والحمد لله الذي بنعمته تتم الصالحات.
وانظر: " الثقات " (٩/ ١٢٦ / ١٥٥٥٥)، و" تهذيب الكمال " (٢٦/ ٣٨٦ / ٥٥٩٣)، و" تهذيب التهذيب " (٩/ ٣٨٥ / ٧١٧)، و" تقريب التهذيب " (١/ ٥٠٦ / ٦٢٨٠) ـ ذكره الحافظ فيه تمييز.
..
١٥١ - ـ تمييز أبو عبد الله، محمد بن مرزوق بن إبراهيم بن إسحاق.
روى عن: روح بن عبادة.
وانظر: " الجرح والتعديل " (٨/ ٩٠ / ٣٨٥).
..
١٥٢ - ـ تمييز أبو عبد الله محمد بن مرزوق بن راشد المصري.',NULL,'584',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (587,'روى عن: بشر بن بكر التنيسي، وخالد بن عبد الرحمن الخراساني.
وانظر: " الجرح والتعديل " (٨/ ٩٠ / ٣٨٦).
٣١٥ - ـ محمد بن مروان البصري (تهـ: ١٧٣٣)
(طب: تهـ: ١ ر / ١ ش)
أبو عمر، محمد بن مروان بن عمرو بن مروان بن عنبسة بن سعيد بن العاص الأموي، البصري: توفي يوم الأحد، لإحدى عشرة ليلة خلت من المحرم سنة أربع وتسعين ومائتين، من الحادية عشرة، ترجم له الخطيب، وسكت عنه.
روى عن: الضحاك بن مخلد: (تهـ ١٧٣٣)، وأبي حاتم سهل بن محمد السجستاني.
روى عنه: محمد بن مخلد الدوري.
التعديل والتجريح: لم أقف له على جمة ولا ذكر إلا في " تاريخ بغداد " ولم يذكر الخطيب فيه جرحا ولا تعديل، وغلب على ظني أنه شيخ الطبري المراد لموافقته طبقة شيوخه، ولسنة وفاته، وهناك من وافقه في الاسم والبلد، ولكنه متقدم عليه في الطبقة، أثبتُّه تمييز.
وكل اسم من معجم شيوخه وتلاميذه ميزته " بالأسود العريض "، ولم أعزه إلى كتاب فهو من ترجمته في " تاريخ بغداد ".
وانظر: " تاريخ بغداد " (٣/ ٢٩٣ / ١٣٧٨).
..
١٥٣ - ـ تمييز أبو بكر محمد بن مروان بن قدامة، العقيلي ـ ويقال: العجلي ـ البصري، من الثامنة، صدوق، ثقة، ليس به بأس، له أوهام لا يتابع عليها. أخرج له أبو داود في " الناسخ والمنسوخ "، وابن ماجه.
روى عن: يونس بن عبيد، وعبد الملك بن نضرة، وعمارة بن أبي حفصة.',NULL,'585',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (588,'روى عنه: عبد الرحمن بن مهدي، ومحمد بن سلام.
وانظر: " التاريخ الكبير " (١١/ ٢٣٢ / ٧٢٧)، " ضعفاء العقيلي " (٤/ ١٣٣ / ١٦٩١)، و" الجرح والتعديل " (٨/ ٨٥ / ٣٦١)، و" الثقات " (٧/ ٤٢٧ / ١٠٧٤٦) و (٩/ ٤١ / ١٥٠٧٧)، و" تهذيب الكمال " (٢٦/ ٣٨٧ / ٥٥٩٥)، و" الكاشف " (٢/ ٢١٥ / ٥١٤٤)، و" لسان الميزان " (٧/ ٣٥٧ / ٤٧٤٠)، و" تهذيب التهذيب " (٩/ ٣٨٦ / ٧١٩)، و" تقريب التهذيب " (١/ ٥٠٦ / ٦٢٨٢) ..
٣١٦ - ـ أبو عبد الله بن وارة (٩٢٥٤)
(النسائي، والبخاري خارج صحيحه)
(طب: ٤ ر / ٣ ش)
(تس: ٣ ر / ٢ ش)، و (صر: ١ ر / ١ ش)
أبو عبد الله، محمد بن مسلم بن عثمان بن عبد الله، الرازي: المعروف ب (ابن وارة) ـ بفتح الراء المخففة ـ توفي في شهر رمضان، سنة خمس وستين ومائتين، وقيل: سنة سبعين ومائتين، من الحادية عشرة، ثقة، حافظ.
روى عن: أبي المغيرة عبد القدوس بن الحجاج الخولاني (صر ٣٩)، وأبي جعفر (عبد الله بن محمد)، النفيلي (تس ٩٢٥٤ و٩٢٥٥)، وأبي جعفر الرازي ـ عيسى بن ماهان ـ (تس ٩٥٠١)
روى عنه: أحمد بن حنبل " الزهد " (١/ ٧٨ / ١٦٠)، وأحمد بن محمد بن يحيى الشحام " صحيح ابن حبان " (٥/ ٢٤١ / ١٩١٣)، وأبو الحسين علي بن إبراهيم بن معاوية النيسابوري " سنن البيهقي " (٤/ ١٢٣ / ٧٢٣٥)، وعلي بن سعيد الرازي " المعجم الكبير " (٢/ ٦٩ / ١٣١٨). التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقا، متقنا، عالما، حافظا، فهما، صاحب سنة وحديث، يحفظ على صلف فيه، وكان من المتقنين الأمناء، وكان أبو زرعة يبجله، ويكرمه، وكان لا يقوم لأحد، ولا يجلس أحدا في مكانه إلا ابن وارة.
قلت: أغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري ".',NULL,'586',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (589,'ولا ريب عندي أن هذه الأسانيد، فيها عدة أخطاء، حيث جاء الإسناد رقم (تس ٩٥٠١)، أتم من الإسناد رقم (٩٢٥٤)، من حيث رجاله.
وجاء في الإسناد (تس ٩٢٥٤)، زيادات في النسب، أتم من الإسناد (تس ٩٥٠١)، وأظن أن الإسنادين إسناد واحد، أخطأ فيه بعض الرواة، أو النساخ، ولعل متنهما أيضا واحد لتقارب ألفاظهما. والله أعلم.
ولذلك ترددت كثيرا في الترجمة ل (محمد بن مسلم بن وارة الرازي)، خشية أن يكون غير المراد.
وأثبتُّ الآثار الثلاثة، بأسانيدها، ومتونها، ليسهل على الباحث النظر فيها والاستدراك والتدقيق! ومن ثم التأييد، أو التسديد، بالتي هي أحسن للتي هي أقوم. وهي:
(٩٢٥٤) ـ حدثنا محمد بن مسلم الرازي، قال: ثنا: أبو جعفر النفيلي، قال: ثنا: يحيى بن يمان، عن، أشعث، عن، سعيد: {واسألوا الله من فضله}، قال: " العبادة: ليست من أمر الدنيا ".
٩٢٥٥ - ـ حدثنا محمد بن مسلم، قال: ثني: أبو جعفر، قال ثنا: موسى عن ليث، قال (فضله): " العبادة، ليس من أمر الدنيا ".
٩٥٠١ - ـ حدثنا محمد بن مسلم الرازي، قال: ثني: أبو جعفر الرازي، قال: ثنا يحيى، عن عارم، عن أشعث، عن جعفر، عن سعيد بن جبير، في قوله: {الذين يبخلون ويأمرون الناس بالبخل}، قال: " هذا للعلم: ليس للدنيا منه شيء ".
وانظر: " الجرح والتعديل " (٨/ ٧٩ / ٣٣٢)، و" الثقات " (٩/ ١٥٠ / ١٥٧١٣)، و" تاريخ بغداد " (٣/ ٢٥٦ / ١٣٤٩) و" تهذيب الكمال " (٢٦/ ٤٤٤ / ٥٦٠٧) و" الكاشف " (٢/ ٢٢١ / ٥١٥٣) و" تذكرة الحفاظ " (٢/ ٥٧٥ / ٦٠٠) و" تهذيب التهذيب " (٩/ ٣٩٩ / ٧٣٥) و" تقريب التهذيب " (١/ ٥٠٧ / ٦٢٩٧) ..
٣١٧ - – أبو جعفر الأنماطي (٥٦٢٣)
(النسائي)',NULL,'587',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (590,'(طب: ٣ ر / ٢ ش)
(تس: ١ ر / ١ ش)، و (تخ: ١ ر / ١ ش)، و (تهـ: ١ ر / ١ ش)
أبو جعفر، محمد بن معاوية بن يزيد، الأنماطي، الواسطي، ثم البغدادي، المعروف ب (ابن مالج) ـ: من العاشرة، صدوق، ربما وهم.
روى عن: خلف بن خليفة (تس ٥٦٢٣)، و (س)، وعباد بن العوام (تهـ ١١٩٦)، و (تخ ١/ ٢٨)، و (س)، وعمر بن يزيد المدائني " الكامل " (٥/ ٢٩ / ١١٩٩)، ومحمد بن زياد الطحان " الكامل " (٦/ ١٣٠ / ٦١٣١)، وهشيم بن بشير الواسطي " صحيح ابن خزيمة " (١/ ٣٤ / ٥٩)
روى عنه: محمد بن إسحاق بن خزيمة " صحيح ابن خزيمة " (١/ ٣٤ / ٥٩)، ومحمد بن موسى بن سهل البربهاري " سنن الدارقطني " (١/ ١٣٧ / ١٥)، وأبو بكر البزار " مسند البزار " (٣/ ٢٨٨ / ١٠٨٠).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقا، لا بأس به، ربما وهم، وكان واقفيا.
قلت: أغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري ".
وقد أشكل عليّ نسبته، (النيسابوري)، ابتداء، ثم تأكد لي أنه من تصرف وتلفيق، بعض النساخ، وليس من صنيع (أبي جعفر الطبري)، لسببين:
الأول: كون النيسابوري توفي بعد مولد الطبري بنحو خمس سنين، ولا يمكن أن يكون روى عنه.
والثاني: وقد ذكر (الخطيب البغدادي)، ثم (الحافظ المزي)، ثم (الحافظ ابن حجر)، وغيرهم، ـ ذكروا ـ (خلف بن خليفة)، في شيوخه، وذكروا (أبا جعفر، محمد بن جرير الطبري)، في تلاميذه. في حين خلت ترجمة النيسابوري من مثل ذلك.
وقد أخرج (سعيد بن منصور)، الأثر المذكور في " سننه " (٣/ ٩٣٤ / ٤١٧)، من طريق (خلف بن خليفة)، به.',NULL,'588',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (591,'وأخرجه (ابن أبي حاتم)، من طريق (الحسن بن عرفة)، مثله، ذكر ذلك عنه (ابن كثير)، في " تفسيره " (١/ ٣٠٠)، و (٤/ ٣٠٨)
وأخرجه (القرطبي)، في " جامع الأحكام " (٣/ ٢٣٧)، بسنده عن (محمد بن معاوية بن صالح)، به. وهو من الأسانيد القليلة عند القرطبي.
وانظر: " الثقات " (٩/ ١١٦ / ١٥٤٩٦)، و" تاريخ بغداد " (٣/ ٢٧٤ / ١٣٦٢) و" الضعفاء والمتروكين لابن الجوزي " (٣/ ١٠٠ / ٣٢٠٤) و" تهذيب الكمال " (٢٦/ ٤٧٦ / ٥٦١٧) و" ميزان الاعتدال " (٦/ ٣٤٢ / ٨١٩٥) و" الكاشف " (٢/ ٢٢٢ / ٥١٦٠) و" المغني " (٢/ ٦٣٤ / ٥٩٩١) و" تهذيب التهذيب " (٩/ ٤٠٩ / ٧٥٠) و" تقريب التهذيب " (١/ ٥٠٧ / ٦٣٠٩) و" لسان الميزان " (٧/ ٣٧٦ / ٤٧٤٨) ..
١٥٤ - ـ تمييز أبو علي، محمد بن معاوية بن أعين، النيسابوري الخراساني البغدادي، ثم المكي، توفي سنه ٢٢٩، من العاشرة. ولا يمكن أن يكون هو شيخ الطبري، لأنه توفي قبل أن يبلغ الطبري خمس سنين، متروك مع معرفته، لأنه كان يتلقن، وقد أطلق عليه (ابن معين)، الكذب، روى أحاديث لا يتابع عليها.
روى عن: الليث، ومحمد بن سلمة.
وانظر: " التاريخ الكبير " (١/ ٢٤٥ / ٧٧٩)، و" الضعفاء والمتروكين " (٩٤/ ٥٣٩)، و" الجرح والتعديل " (٨/ ١٠٣ / ٤٤٣)، و" المجروحين " (٢/ ٢٩٨ / ١٠٠٣)، و" ضعفاء العقيلي " (٤/ ١٤٤ / ١٧٠٩)، و" الكامل " (/ ٢٧٧/ ١٧٦٢)، و" الضعفاء والمتروكين لابن الجوزي " (٣/ ١٠٠ / ٣٢٠٣)، و" تهذيب الكمال " (٢٦/ ٤٧٨ / ٥٦١٨) و" المغني في الضعفاء " (٢/ ٦٣٤ / ٥٩٨٩)، و" تهذيب التهذيب " (٩/ ٤٠٩ / ٧٥١)، و" تقريب التهذيب " (١/ ٥٠٧ / ٦٣١٠)
..
١٥٥ - ـ تمييز محمد بن معاوية البصري، فيه نظر.
روى عن: جويرية بن أسماء.
وانظر: " التاريخ الكبير " (١/ ٢٤٦ / ٧٨٠).',NULL,'589',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (592,'٣١٨ - ـ أبو عبد الله البحراني (٢٤١)
(خ: ٤ ح، م: ٨ ح، والأربعة)
(طب: ٦١ ر / ١٣ ش)
(تس ٢٩ ر / ١٠ ش) و (تخ ٢ ر / ٢ ش) و (تهـ ٢٣ ر / ٧ ش) و (تق ٧ ر / ٤ ش)
أبو عبد الله، محمد بن معمر بن ربعي، القيسي، البحراني، البصري، توفي بعد سنة خمسين ومائتين، من كبار الحادية عشرة، صدوق.
روى عن: حرمي بن عمارة بن أبي حفصة (تس ٣٦٨٧٤)، و (س)، وحماد بن مسعدة (تس ٣٠٦٣ و١٠٣٨١)، و (تق ١٠١٦)، و (س)، وروح بن عبادة (تس ٤١٧٤ و١٢٨٠٠ و١٦٧٠٠)، و (تهـ ٢٢١٢ و٢٩٤٧ و٢٩٧٢)، و (تخ ١/ ٥٧٢)، و (خ، م، ت، س، ق)، وأبي عاصم الضحاك بن مخلد (تهـ ٣٥٨)، و (خ)، وعباس بن زياد الباهلي (تس ٢٤١ و٣٤٥٣٧)، وعبد الصمد بن عبد الوارث (تس ١٩٥٤٢)، وموسى بن داود الضبي (تهـ ٢٠٦٩)، (ق)، وهشام (بن سعد)، المخزومي يتيم زيد بن الأسلم (تس ٣٥٥١٣)، و (تق ٣٣٠)، ووهب بن جرير (تهـ ١٦٥٦)، ويعقوب بن إسحاق الحضرمي (تس ٤٣٤٨ و٤٣٤٨ و٣٢٥٦٦)، و (تهـ ١٧٧٧)، و (تق ٦٠٩)، و (ق)، وأبي عامر العقدي (تس ٤١٤٦ و٤٨١٢ و٥٠٩١ و٥٣٩٦ و٥٤٢٣ و١٨٠٨٣ و٢٠٧٥٣ و٢٣٨٠٦ و٣٤٠٨٦ و٣٤١٦٩)، و (تهـ ٣١٥ و٤٥٤ و٧٨٨ و٩١٤ و٩١٥ و١٢٨٩ و١٨٨٢ و١٩٢١ و٢٢٧٦ و٢٥٩٠ و٢٧٧٦ و٢٨٧٨ و٣٠٨٥)، و (تق ٥٥٤ و٦٢١ و٦٧٨ و٨٩١)، و (تخ ١/ ٧٥)، و (س)، وأبي مسلم ـ غير مسمى، ولا منسوب!! ولعله مسلم بن إبراهيم ـ (تس ٣٥١٣٣)، وأبي هشام المخزومي ـ واسمه المغيرة بن سلمة ـ (تس ١٧٠١٣ و٢٨٥١٢ و٣١٠٤١ و٣٣٣٣١ و٣٥٠٤٣)، و (تهـ ٣٠٧ و١١٦٥ و٢٣٠٧)، و (م، س).
روى عنه: أحمد بن زيد الحريش الأهوازي " مسند الشاميين " (١/ ٢٨٤ / ٤٩٦)، وعبد الله بن أحمد بن موسى بن عبدان " صحيح ابن حبان " (٨/ ١٣٤ / ٣٣٤٥)، ومحمد بن أحمد الرقام " المعجم الصغير " (٢/ ١٢٣ / ٨٩٤)، ومحمد بن المسيب بن إسحاق الأرغياني " صحيح ابن حبان " (١٣/ ٢٣٧ / ٥٩١٦)',NULL,'590',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (593,'التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقا، حافظا لا بأس به، وليس به بأس، وكان من خيار عباد الله.
وانظر: " الجرح والتعديل " (٨/ ١٠٥ / ٤٥٣)، و" الثقات " (٩/ ١٢٢ / ١٥٥٣٥)، و" ذكر أسماء التابعين " (١/ ٣٣٦ / ١٠١٨ و٢/ ٢٣٢ / ١١٤٧) و" رجال البخاري " (٢/ ٦٨٣ / ١١٠٩) و" رجال مسلم " (٢/ ٢١٤ / ١٥٢٨) و" معجم البلدان " (١/ ٣٤٧) و" التعديل والتجريح " (٢/ ٦٤٨ / ٥١٠) و" تهذيب الكمال " (٢٦/ ٤٨٥ / ٥٦٢١) و" تذكرة الحفاظ " (٢/ ٥٦٣ / ٥٨٧) و" المقتنى في سرد الكنى " (١/ ٣٦١ / ٣٧٢٤) و" طبقات المحدثين " (١/ ١٠٠ / ١١٤٦) و" الكاشف " (٢/ ٢٢٣ / ٥١٦٣) و" تهذيب التهذيب " (٩/ ٤١٢ / ٧٥٥) و" تقريب التهذيب " (١/ ٥٠٨ / ٦٣١٣) و" طبقات الحفاظ " (١/ ٢٢٣ / ٤٩٦ و١/ ٢٥٥ / ٥٦٧) و" رجال تفسير الطبري " (٥١٠/ ٢٤٢٠) ..
٣١٩ - ـ محمد بن مقاتل الرازي (تهـ: ٦٣١)
(طب: ٧ ر / ٤ ش)
(تهـ: ٢ ر / ١ ش)، و (تق: ٥ ر / ٤ ش)
محمد بن مقاتل، الرازي: توفي سنة ثمان وأربعين ومائتين، ـ وقيل: في التي بعدها ـ من الحادية عشرة، ضعيف.
روى عن: الحارث بن مسلم (تق ٣٨٠)، وأبي زهير عبد الرحمن بن مغراء الدوسي (تهـ ٦٣١ و٧١٠)، و (تق ٣٦٢ و٣٧٩)، ومحمد بن الحسن (الشيباني) ـ صاحب أبي حنيفة ـ (تق ٤٤١)، ويعلى بن عبيد (تق ٣٦١)، وجرير (بن عبد الحميد الضبي) " تهذيب التهذيب " (٩/ ٤١٤ / ٧٦٢)، وحكام بن سلم " لسان الميزان " (٥/ ٣٨٨ / ١٢٦١)، وسفيان بن عيينة " لسان الميزان " (٥/ ٣٨٨ / ١٢٦١) وسلم بن الفضل " لسان الميزان " (٥/ ٣٨٨ / ١٢٦١)، و (عبد الرحمن بن محمد)، المحاربي " لسان الميزان " (٥/ ٣٨٨ / ١٢٦١)، وقبيصة (بن عقبة السوائي) " لسان الميزان " (٥/ ٣٨٨ / ١٢٦١)، وأبي معاوية (محمد بن خازم الضرير) " تهذيب التهذيب " (٩/ ٤١٤ / ٧٦٢)، و (محمد) بن فضل " لسان الميزان (٥/ ٣٨٨ / ١٢٦١)، ووكيع (بن الجراح) " تهذيب التهذيب " (٩/ ٤١٤ / ٧٦٢).',NULL,'591',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (594,'روى عنه: أحمد بن خالد بن جعفر " لسان الميزان " (٥/ ٣٨٨ / ١٢٦١)، وأحمد بن علي الأسعدي " تهذيب التهذيب " (٩/ ٤١٤ / ٧٦٢)، والحسين بن حمدان " لسان الميزان " (٥/ ٣٨٨ / ١٢٦١)، وعيسى بن محمد المروزي " تهذيب التهذيب " (٩/ ٤١٤ / ٧٦٢)، ومحمد بن علي الحكيم الترمذي " تهذيب التهذيب " (٩/ ٤١٤ / ٧٦٢)، وسمع منه البخاري ولم يحدث عنه " تهذيب التهذيب " (٩/ ٤١٤ / ٧٦٢). محمد بن أيوب " لسان الميزان " (٥/ ٣٨٨ / ١٢٦١)، والحمامي " لسان الميزان " (٥/ ٣٨٨ / ١٢٦١).
التعديل والتجريح: ذكره الحافظ في " تهذيب التهذيب " ـ تمييزا ـ وقال: " محمد بن مقاتل: رازي لا مروزي، ذكره الخطيب في " المتفق " .. إلى أن قال: فروى الخليلي في " الإرشاد " من طريق صهيب بن سليم سمعت البخاري يقول: ثنا محمد بن مقاتل. فقيل له: الرازي؟ فقال: لأن أخر من السماء أحب إلي من أحدث عن محمد بن مقاتل الرازي ". انتهى كلام الحافظ ببعض اختصار.
وقال في " اللسان ": " وقد ذكره ابن القيم في " إغاثة اللهفان " وذكر له ترجمة فغلط فيه فإنه ذكر أن البخاري روى عنه، وليس كذلك، وإنما روى عن محمد بن مقاتل المروزي ".
وقال أيضا: (ذكره أبو الحسن بن بأبويه في " تاريخ الري " فقال: كان إمام أصحاب الرأي بالري وكان مقدما في الفقه ".
وانظر: " تهذيب التهذيب " (٩/ ٤١٤ / ٧٦٢)، و" لسان الميزان " (٥/ ٣٨٨ / ١٢٦١)، " تقريب التهذيب " (١/ ٥٠٨ / ٦٣١٩).
٣٢٠ - ـ أبو جعفر الطوسي (٦٦٥٠)
(أبو داود، والنسائي)
(طب: ١٧ ر / ١٣ ش)
(تس: ١٣ ر ١١ ش)، و (تخ ١ ر / ١ ش)، و (تهـ: ٣ ر / ٣ ش)، و (تق ١ ر / ١ ش)
أبو جعفر، محمد بن منصور بن داود، الطوسي، ثم البغدادي، العابد: مولده سنة ثمان وسبعين ومائتين، وتوفي سنة أربع وخمسين ومائتين، ـ وقيل: سنة ست وخمسين.',NULL,'592',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (595,'ومائتين ـ، من صغار العاشرة، ثقة، صاحب أحوال.
روى عن: إسحاق بن سليمان (تس ١٧٩٩٧ و٢٨٢٥٠ و٣٣١٣٨)، وأبي المنذر إسماعيل بن عمر الواسطي (تس ٣٤٤٦٢)، (س)، والحسن بن موسى الأشيب (تس ٣٤١٨٧)، و (س)، وحسين بن محمد (تس ٣٠٦١٣)، و (تهـ ١٧١٢)، وروح بن عبادة (تهـ ١٨٠٩)، وسفيان ـ وهو ابن عيينة ـ (تق ٦١٦)، (د)، وصالح بن إسحاق الجهبذ (تس ٢٤٠٤٨)، وعفان بن مسلم (تس ١٧٦٥٨)، وعلي بن الحسن بن شقيق (تس ٣٥٦٥٢)، وعمر بن شبيب (تس ٣٦٥١٩)، وأبي أحمد محمد بن عبد الله الزبيري (تس ٦٦٥٠)، ويحيى بن أبي بكر (تس ٢٣٧٦١)، وأبي قطن (تس ٣٤٨٩٩)، و (تهـ ١٩٧٢)، وأبي عاصم (النبيل) " الثقات " (٩/ ١٣٠ / ١٥٥٨٢).
روى عنه: أحمد بن الحسين بن الجنيد " سنن الدارقطني " (٣/ ٢٩٠ / ١٧١)، وأبو بكر أحمد بن عمرو بن الضحاك الشيباني " الآحاد والمثاني " (١/ ١١٤ / ١١١)، والحسين بن أحمد بن عبيد العجلي " حلية الأولياء " (٧/ ٢٤٠)، وشبابة بن سوار " مسند ابن الجعد " (١/ ١٨٢ / ١١٨٠)، وعبد الله بن أحمد بن حنبل " السنة " (١/ ٢٧٤ / ٥١٢)، ومحمد بن عبدوس السراج " المعجم الكبير " (٦/ ٢٣٥ / ٦٠٨٥)، ومحمد بن عمرو بن يوسف " الثقات " (٩/ ١٣٠ / ١٥٥٨٢)، وأبو يعلى الموصلي " معجم أبي يعلى " (١/ ٦٥ / ٢٠٩).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، لا بأس به وكان من الأخيار، وكان يُشَبَّهُ في صلاحه بمعروف الكرخي.
وكان أحمد بن حنبل يقول عنه: " لا أعلم إلا خيرا، صاحب صلاة ".
وانظر: " الجرح والتعديل " (٨/ ٩٤ / ٤٠٧)، و" الثقات " (٩/ ١٥٥٨٢) و" تاريخ بغداد " (٣/ ٢٤٧ / ١٣٣٨)، و" الإرشاد " (٣/ ٨٦٤ / ٧٧٩)، و" تهذيب الكمال " (٢٦/ ٤٩٩ / ٥٦٣١)، و" الكاشف " (٢/ ٢٢٤ / ٥١٦٩)، و" المقتنى في سرد الكنى " (١/ ١٤٨ / ١١٠٨)، و" سير أعلام النبلاء " (١٢/ ٢١٢ / ٧٣)، و" المقصد الأشد " (٢/ ٤٩٣ / ١٠٤٨)، و" تهذيب التهذيب " (٩/ ٤١٧ / ٧٦٨)، و" تقريب التهذيب " (١/ ٥٠٨ / ٦٣٢٦)، و" رجال تفسير الطبري " (٥١١/ ٢٤٢٢). ....
....
....
......',NULL,'593',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (596,'٣٢١ - ـ أبو عبد الله الحرشي (٤٣٨٥)
(الترمذي، والنسائي)
(طب: ١٦ ر / ١١ ش)
(تس: ٩ ر / ٨ ش)، و (تخ: ٣ ر / ٢ ش)، و (تهـ: ٤ ر / ٣ ش)
أبو عبد الله، محمد بن موسى بن نفيع، الحرشي، البصري: توفي سنة ثمان وأربعين ومائتين، من العاشرة، ليِّن.
روى عن: جعفر بن سليمان الضبعي (تس ٣٣٤٩١ و٣٧٢٥٦)، و (ت، س)، وحسان بن إبراهيم الكرماني (تس ٤٣٨٥)، وحماد بن زيد (تس ٢٨٥٢٠)، و (تهـ ٢٨٢٠ و٤٠٤٣)، و (ت)، وحماد بن عيسى الجهني (تهـ ١٩٨)، وعامر بن يساف (تس ٢٧٩١٥)، وأبي خلف (عبد الله بن عيسى) (تس ٣٨٢٢٥) و (تخ ١/ ٥٥٠٨، و٢/ ٦١٤)، وعبد الله بن الحارث القرشي (تس ٣٣٠٨٧)، وعبيد الله بن ميمون المرادي (تس ٤٤٦١)، ونوح بن قيس (تس ٢١١٣٦)، وقحذم بن النضر الجرمي (تهـ ٢٣٥٦)، ومعاذ بن هشام (تخ ٢/ ٩٦١).
روى عنه: أبو بكر محمد إسحاق بن خزيمة " صحيح ابن خزيمة " (١/ ١٧٤ / ٣٣٩)، والحسين بن محمد القباني " صحيح ابن حبان " (٤/ ٧٧ / ٦٩٣٤)، وزكريا بن يحيى الساجي " الثقات " (٩/ ١٠٨ / ١٥٤٥٤)، والقاسم بن محمد الجهني الحذاء الموصلي " المعجم الصغير " (٢/ ٤٤ / ٧٥١)، وأبو بكر الإسماعيلي " معجم شيوخ الإسماعيلي " (٢/ ٥٩٠).
التعديل والتجريح: اضطربت أقوال أئمة الجرح والتعديل فيه:
فقال عبد الرحمن بن أبي حاتم: " سئل أبي عنه فقال: شيخ. "
وقال أبو عبيد الآجري: " سألت أبا داود عنه فوهاه وضعفه ".
وقال النسائي: " صالح ".
وقال الحافظ في " التهذيب " ـ معلقا ـ: " وبقية كلام النسائي في " مشيخته ": أرجو أن يكون صدوقا ".',NULL,'594',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (597,'وقال مسلمة بن القاسم: " بصري، صالح ".
وذكره ابن حبان في كتاب " الثقات "
قلت: وقد تصحف نسبه من الحرشي إلى " الحرسي " ـ بخاء، بعدها راء ثم سين مهملات ـ في الأثرين رقم (تس: ٢١١٣٦ و٢٧٩١٥)، وإلى (الجرشي) ـ بجيم معجمة من أسفل ـ في الآثار ذوات الأرقام (تس: ٢٨٥٢٠ و٣٣٤٩١ و٣٧٢٥٦ و٣٨٢٢٥).
وقد أخرج له الطبري في " التاريخ " عن: أبي خلف، عبد الله بن عيسى (تخ: ١/ ٥٥٠)، نفس الأثر رقم (تس: ٣٨٢٢٥ الذي أخرجه في " التفسير ".
وانظر: " الجرح والتعديل " (٨/ ٨٤ / ٣٥٤)، و" الثقات " (٩/ ١٠٨ / ١٥٤٥٤)، و" تهذيب الكمال " (٢٦/ ٥٢٨ / ٥٦٤٢)، و" الكاشف " (٢/ ٢٢٥ / ٥١٧٧)، و" تهذيب التهذيب " (٩/ ٤٢٥ / ٧٨٠)، و" تقريب التهذيب " (١/ ٥٠٩ / ٦٣٣٨)، و" رجال تفسير الطبري " (١٢/ ٢٤٢٩).
١٥٦ - ـ تمييز أبو جعفر، محمد بن موسى (بن نفيع)، الحرشي، ولقبه: (شاباص)، من الثانية عشرة، ثقة، حافظ.
روى عن: خليفة بن خياط، وكثير بن يحيى، ويزيد بن عمر بن خبزة.
روى عنه: إسماعيل بن محمد الصفار، والحسين بن إسماعيل المحاملي.
وانظر: " تاريخ بغداد " (٣/ ٢٤٠ / ١٣٢٣)، و" تهذيب الكمال " (٢٦/ ٥٣٢ / ٥٦٤٤)، و" تهذيب التهذيب " (٩/ ٤٣٥ / ٧٨٢)، و" تقريب التهذيب " (١/ ٥٠٩ / ٦٣٤٠) و" لسان الميزان " (٧/ ٣٧٧ / ٤٧٦٠).
٣٢٢ - ـ أبو بكر البصري (٣٨٩٠)
(مسلم (٥٤ ح)، والترمذي، والنسائي)
طب: (تس: ٣ ر / ٣ ش)
محمد بن نافع، هو: أبو بكر، محمد بن أحمد بن نافع، العبدي، القيسي، البصري: ـ مشهور بكنيته، وقد نسب هنا إلى جده ـ، توفي بعد الأربعين ومائتين، من صغار',NULL,'595',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (598,'العاشرة، صدوق.
روى عن: أمية بن خالد (تس ٢٣٢٢٩)، (ت)، وعبد الرحمن بن مهدي (تس ٣٤١٨٩)، (م)، ومحمد بن جعفر غندر (تس ٣٨٩٠)، (م)، وزيد العمي " سنن البيهقي " (١/ ٣٧٥ / ١٦٣٢)، وأبي عبيدة معمر بن المثنى " الجرح والتعديل " (٨/ ١٠٨ / ٤٧٠).
روى عنه: أحمد بن سلمة " سنن البيهقي " (١/ ٣٧٥ / ١٦٣٢)، وسهل بن محمد " الجرح والتعديل " (٨/ ١٠٠٨ / ٤٧).
التعديل والتجريح: ترواحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقا.
وقد التبس أمره على المباركفوري في " تحفة الأحوذي " (٢/ ٤٣٤)، فقال: " لم أقف على ترجمته ". وقال في (٢/ ٤٣٥): " لم أقف على حاله ". ثم قال في (٨/ ٢٠٣ / ٣): " أبو بكر بن نافع البصري، اسمه: محمد بن أحمد بن نافع العبدي ".
وقال في (١٠/ ٢٤٩ / ١٢٢): " اسمه: محمد بن أحمد البصري العبدي ".
وانظر: " الجرح والتعديل " (٨/ ١٠٨ / ٤٧٠) " تهذيب الكمال " (٢٤/ ٣٥١ / ٥٠٤٥)، " الكاشف " (٢/ ١٥٥ / ٤٠٧٨)، " تهذيب التهذيب " (٩/ ٢٢) و" تقريب التهذيب " (١/ ٤٦٧ / ٥٧١٦)، " رجال تفسير الطبري " (٤٧٨/ ٢٢٥٣).
..
٣٢٣ - ـ أبو عبد الملك السندي (٣٩٦٧)
(الترمذي)
طب: (تس: ٢ ر / ١ ش)
أبو عبد الملك، محمد بن نجيح بن عبد الرحمن، السندي، الهاشمي، مولاهم، المدني: مولده سنة ست وأربعين ومائة، وتوفي سنة سبع وأربعين ومائتين، عن تسع وتسعين سنة، من العاشرة، صدوق. وهو ابن أبي معشر المديني، ووالد الحسين بن محمد بن أبي معشر، وداود بن محمد بن أبي معشر، رأى ابن أبي ذئب، وأبا بكر الهزلي، ولم يسمع منهما.',NULL,'596',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (599,'روى عن: أبيه أبي معشر نجيح بن عبد الرحمن المدني (تس ٣٩٦٧ و٢١١١٥) و (ت)، وأبي العباس عبد الله بن أحمد بن إبراهيم الدورقي " السير " (١٣/ ١٥٤).
روى عنه: إبراهيم بن عبد الرحيم المكي " أخبار مكة " (٢/ ٨٧ / ١١٧٩).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، محله الصدق، ويعتبر حديثه إذا روى عن غير أبيه، لضعف أبيه، وثقه أبو يعلى وأشار ابن معين إلى لين فيه.
وأغفله الشيخان أحمد ومحمود شاكر في تحقيقهما (لتفسير الطبري).
وانظر: الكنى والأسماء (١/ ٦٠٠ / ٢٤٤٩)، و" الجرح والتعديل " (٨/ ١١٠ / ٤٨٧)، و" الثقات " (٩/ ١٠٦ / ١٥٤٤٢)، و" تاريخ بغداد " (٣/ ٣٢٦ / ١٤٣٣)، و" تهذيب الكمال " (٢٦/ ٥٤٩ / ٥٦٥٣)، و" الكاشف " (٢/ ٢٢٧ / ٥١٨٥)، و" ميزان الاعتدال " (٦/ ٣٥٤ / ٨٢٦١)، و" تهذيب التهذيب " (٩/ ٤٣٠ / ٧٩٦)، و" تقريب التهذيب " (١/ ٥١٠ / ٦٣٤٩)، و" لسان الميزان " (٧/ ٣٤٨ / ٤٧٦٦).
..................... ..
١٥٧ - ـ تمييز محمد بن نجيح
روى عن: سهيل بن أبي صالح، ومحمد بن زياد الجمحي.
روى عنه: يزيد بن زريع، وخلف بن خليفة.
التعديل والتجريح: ذكره ابن عدي في " الكامل " وقال: ليس بالمعروف، ولا أدري من أي بلد هو؟! وإنما ذكرته لأنه مجهول غير معروف. انتهى. وأورد له ثلاثة أحاديث محفوظة.
قلت: وقد أنكر الذهبي في " الميزان " على ابن عدي ذكره. وهو أقدم من محمد بن أبي معشر.
وانظر: " الكامل " (٦/ ٢٣٣ / ١٧٠٦)، و" تهذيب التهذيب " (٩/ ٤٣١ / ٧٩٧)، و" ميزان الاعتدال " (٦/ ٣٥٤ / ٨٢٦٠)، و" لسان الميزان " (٥/ ٤٠٤ / ١٣٢٦)
............................',NULL,'597',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (600,'٣٢٤ - ـ أبو جعفر البغدادي (٩٥١٣)
(النسائي، وابن ماجه في التفسير)
(طب: (تس: ٤ ر / ٣ ش)
أبو جعفر، محمد بن هارون بن إبراهيم، الربعي، الحربي، البغدادي، البزاز الملقب بأبي نشيط: توفي سنة ثمان وخمسين ومائتين، من الحادية عشرة صدوق.
روى عن: أبي المغيرة، عبد القدوس بن الحجاج الخولاني (تس ١٠٣٧٦) و (فق)، ونعيم بن حماد الخزاعي (تس ٢٢٩٩٠)، ويحيى بن أبي بكير الكرماني (تس ٩٥١٣ و١٤٢٩٩). وعبيد الله بن موسى " الثقات " (٩/ ١٢٢ / ١٥٥٣).
روى عنه: أبو حاتم محمد بن إدريس الرازي " الجرح والتعديل " (٨/ ١١٧ / ٥٢٥)، ومحمد بن النسيج " الثقات " (٩/ ١٢٢ / ١٥٥٣٧).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقا، وربما أخطأ.
وانظر:" الجرح والتعديل " (٨/ ١١٧ / ٥٢٥)، والثقات (٩/ ١٢٢ / ١٥٥٣٧)، و" تاريخ بغداد " (٣/ ٣٥٢ / ١٤٥٤)، و" تهذيب الكمال " (٢٦/ ٥٦٠ / ٥٦٦١)، و" تهذيب التهذيب " (٩/ ٤٣٦ / ٨١٠)، و" تقريب التهذيب " (١/ ٥١٠ / ٦٣٥٩)، و" رجال تفسير الطبري " (٥١٢/ ٢٤٣٣).
..
١٥٨ - ـ تمييز أبو نشيط، محمد بن هارون، المقرئ، صاحب قالون، من الحادية عشرة صدوق.
وانظر " تقريب التهذيب " (١/ ٥١٠ / ٦٣٦٠)
١٥٩ - ـ تمييز محمد بن هارون المقرئ، يعرف ب (السواق).
روى عن: يحيى بن أيوب العابد، والحسن بن حماد (سجادة).
روى عنه: أبو القاسم النخاس المقرئ.',NULL,'598',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (601,'وانظر: " تاريخ بغداد " (٣/ ٣٥٥ / ١٤٥٩) ..
١٦٠ - ـ تمييز أبو جعفر، محمد بن هارون، الفلاس المخرمي يلقب (شيطا)، توفي سنة ٢٦٥، ثقة، حافظ.
روى عن: الحسن بن بشر الكوفي، وسعد بن حفص، وسليمان بن حرب، وعباد بن موسى، وعبيد الله بن عمر القواريري، وعمرو بن حماد بن طلحة، وأبي نعيم الفضل بن دكين، ويحيى بن معين.
روى عنه: القاضي حسين المحاملي، ومحمد بن مخلد.
وانظر: " تاريخ بغداد " (٣/ ٣٥٣ / ١٤٥٥)
١٦١ - ـ تمييز محمد بن هارون، البغدادي، معروف الحديث.
روى عن: جرير، وهشيم، وابن علية.
روى عنه: إبراهيم بن إسحاق الصواف.
وانظر: " تاريخ بغداد " (٣/ ٣٤٧ / ١٤٥٢)
..
١٦٢ - ـ تمييز أبو يكر، محمد بن هارون بن عيسى، الأزدي، الرزاز، البصري. أحاديثه مستقيمة. وقال الدارقطني: " ليس بالقوي ".
روى عن: أحمد بن عبد الله بن يونس، وجبارة بن مغلس.
روى عنه: حمزة بن القاسم، وأبو بكر الشافعي، وأبو العباس بن عقدة.
وانظر: " تاريخ بغداد ": (٣/ ٣٥٤ / ١٤٥٦).
..
٣٢٥ - ـ أبو جعفر القطان الرازي (تس: ١٧٩٩٥)
(طب: ٦ ر / ٣ ش)',NULL,'599',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (602,'(تس: ٢ ر / ٢ ش)، (تخ: ١ ر / ١ ش)، (تهـ: ٢ ر / ١ ش)، (تق: ١ ر / ١ ش).
أبو جعفر، محمد بن هارون، القطان، الرازقي ـ مصحف؛ والصحيح: الرازي: ثقة، نقل الأصبهاني عن أبي مسعود توثيقه.
روى عن: سفيان بن عيينة (تس: ٣٧٥٠٤)، و (تهـ ١٧٠٢ و٢٣٤٩)، ومحمد بن بشر (تق ٣٢٧)، ويزيد بن هارون (تس ١٧٩٩٥)، و (تخ ١/ ٣١)، وإسحاق بن منصور، وسعد بن عبد الحميد الأنصاري، وعثمان بن عمر.
روى عنه: عامر بن عقبة.
التعديل والتجريح: قال أبو الشيخ الأصبهاني في " طبقات المحدثين بأصبهان " (٢/ ٢٩٠ / ١٧٧): سمعت عامر بن عقبة يقول: سمعت أبا مسعود يقول: اكتبوا عنه، فإنه ثقة.
وقال الشيخ علي رضا في تعليقه على " الجزء المفقود من تهذيب الآثار " (١/ ٢٠٧ / ٣٢٧): " لم أقف له على ترجمة فيما بين يدي من كتب الرجال.
قلت: وهذا ما وقفت له عليه من ترجمة أو ذكر، أو رواية، وقد يشتبه بغيره من وافقه في الاسم، والشيوخ، والتلاميذ، والنسبة، وقد أثبتهم تمييزا.
وكل اسم لم أعزه إلى كتاب من معجم شيوخه وتلاميذه، وميزته بالأسود العريض فهو من ترجمته في " طبقات المحدثين بأصبهان ".
وانظر: " طبقات المحدثين بأصبهان " (٢/ ٢٩٠ / ١٧٧).
..
١٦٣ - ـ تمييز أبو عبد الله، محمد بن هارون، الرازي، اللؤلؤي: مستور.
روى عن: عمرو بن صفوان.
روى عنه: أبو زرعة الرازي.
التعديل والتجريح: قال أبو زرعة: كتبت عنه حديثا واحدا وكان ينزل',NULL,'600',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (603,'المدينة ب (دستك)، شيخ مستور.
قال عبد الرحمن بن أبي حاتم: سئل أبي عنه فقال: شيخ مستور.
وانظر: " الجرح والتعديل " (٨/ ١٧٧ / ٥٢٤).
١٦٤ - ـ تمييز أبو عبد الله، محمد بن هارون، الجوزداني، كان يختلف إلى مجلس البزار، شيخ، دين، فاضل.
روى عن: الحسن بن عرفة.
روى عنه: أبو الشيخ الأصبهاني.
وانظر " طبقات المحدثين بأصبهان (٣/ ٤١٧ / ٤٣٤) ..
١٦٥ - ـ تمييز أبو جعفر، محمد بن هارون، توفي سنة ٣٢٤، صاحب أصول.
روى عن: الربيع بن سليمان، والمصريين. ومحمد بن العباس السمسار.
روى عنه: أبو الشيخ الأصبهاني.
وانظر: " طبقات المحدثين بأصبهان " (٣/ ٥٩٢ / ٥١٠).
١٦٦ - ـ تمييز أبو بكر، محمد بن هارون، الروياني، الحافظ الإمام، صاحب (المسند)، توفي قبل الطبري بنحو ثلاث سنين، سنة سبع وثلاثمائة.
روى عن: إسحاق بن شاهين، ومحمد بن حميد، ويحيى القوام، والزهراني.
روى عنه: إبراهيم بن أحمد القرميسيني، وجعفر بن عبد الله بن فناكي.
التعديل والتجريح: وثقة أبو يعلى الخليلي وذكر أن له تصانيف في الفقه. وانظر: " تذكرة الحفاظ " (٢/ ٧٥٢ / ٧٥٥).
..
١٦٧ - ـ تمييز أبو جعفر، محمد بن هارون، الربعي، البغدادي، توفي سنة ثمان وخمسين ومائتين، من الحادية عشرة، ثقة.',NULL,'601',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (604,'وانظر: " التقريب " (١/ ٤٤ / ٦٣٩).
..
٣٢٦ - ـ محمد بن الهيثم (تق: ٣٧٣).
(ابن ماجه حديثا واحدا في الاستسقاء)
(طب: تم: ١ ر / ١ ش)
أبو عبد الله، ـ وقيل: أبو محمد ـ، المعروف ب (أبي الأحوص)، محمد بن الهيثم بن حماد بن واقد، الثقفي، مولاهم، البغدادي، القنطري، ثم العكبري ـ بفتح الموحدة ـ قاضيها، توفي في جمادى الأول، سنة تسع وتسعين ومائتين، ـ وقيل: سنة ثمان! والأول أصح، أي قبل الثلاثمائة بسنة ـ وقيل قبل ذلك بعشرين سنة ـ من الحادية عشرة، ثقة، حافظ.
روى عن: سعيد بن حفص النفيلي (تق ٣٧٣)، وخلائق غيره.
روى عنه: أبو جعفر الطبري، وخلائق.
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: من الأثبات المتقنين، ومن " الثقات " الحفاظ، وثقة، مأمونا، حافظا، ومستقيم الحديث، ومن أهل الفضل، ورحل في الحديث إلى الكوفة، والبصرة، والشام، ومصر.
قلت: ووقع إشكال في تاريخ وفاته، وأظنه خطأ نساخ أو طابعين؛ حيث قال المزي في " تهذيب الكمال "، تبعا للخطيب البغدادي في (تاريخ بغداد)، وتابعهما الذهبي في (الكاشف)، قالوا: مات سنة تسع وسبعين ومائتين ـ زاد ابن المنادي – لخمس بقين من جمادى الأولى – وقيل: في آخر جمادى الأولى، سنة تسع وسبعين ومائتين - وكان قاضي أهل عكبرا.
وقال ابن السماك: في آخر جمادى الأول. وقال محمد بن يوسف الهروي: مات سنة ثمان وسبعين ومائتين. والصحيح الأول، والله أعلم. انتهى.
وانظر: " الثقات " (٩/ ١٤٤ / ١٥٦٦٨)، و (٩/ ١٥١ / ١٥٧١٧)، و" تاريخ بغداد " (٣/ ٣٦٢ / ١٤٧٤) و" تهذيب الكمال " (٢٦/ ٥٧١ / ٥٦٦٨) و" تذكرة الحفاظ " (٢/ ٦٠٥ / ٦٣٠) و" الكاشف " (٢ /',NULL,'602',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (605,'٢٢٧ - / ٥١٩٤) و" تهذيب التهذيب " (٩/ ٤٤٠ / ٨٢١)، و (١٢/ ٦ / ١٤)، و (١٢/ ٣٧٥ / ٢٣٩٨)، و" تقريب التهذيب " (١/ ٥١١ / ٦٣٦٧).
..
١٦٨ - ـ تمييز أبو عيسى، محمد بن الهيثم بن خالد، المخرمي الوراق.
روى عن: أبي همام الوليد بن شجاع، وحماد بن المؤمل الكلبي، وسعدان الثقفي.
روى عنه: عمرو بن محمد بن سبنك البجلي، وأبو بكر محمد بن أحمد المفيد.
وانظر: " تاريخ بغداد " (٣/ ٣٦٤ / ١٤٧٥) ..
١٦٩ - – تمييز أبو بكر محمد بن الهيثم الأنماطي المقرئ.
روى عن: أبي محمد بن أبي العنبر عبد الله بن ثابت المقرئ.
روى عنه: محمد بن عبيد الله بن قفرجل الكيال.
وانظر " تاريخ بغداد " (٣/ ٣٦٤ / ١٤٧٦) ..
١٧٠ - ـ تمييز أبو الحسين، محمد بن الهيثم بن السري، الكلوذاني.
روى عن: أحمد بن علي الخراز.
روى عنه: أبو القاسم بن الثلاج.
وانظر: " تاريخ بغداد " (٣/ ٣٦٤ / ١٤٧٧).
١٧١ - ـ تمييز محمد بن الهيثم بن كعب، الذراع من أهل واسط.
روى عن: زيد بن هارون، وأبي عاصم.
روى عنه: ابنه.
وانظر: " الثقات " (٩/ ١٤١ / ١٥٦٥١)
..',NULL,'603',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (606,'٣٢٧ - ـ ابن الوزير الواسطي (١١٦٦٥)
(الترمذي، وقيل: أبو داود أيضا)
(طب: ٢ ر / ٢ ش)
(تس: ١ ر / ١ ش)، (تق: ١ ر / ١ ش)
أبو عبد الله، محمد بن الوزير بن قيس، العبدي، الواسطي: مولده سنة تسع وسبعين ومائة، وتوفي سنة سبع ـ أو: ثمان ـ وخمسين ومائتين، منصرفه من الحج، من العاشرة، ثقة، عابد.
روى عن: نوح بن قيس (تق ٣٥٢)، وأبيه وزير بن قيس (تس ١١٦٦٥)، والمعتمر بن سليمان التيمي " اعتقاد أهل السنة " (٣/ ٥٢٠).
روى عنه: أحمد بن عمير بن جوصاء " الثقات " (٩/ ١٢٢ / ١٥٥٣١)، وأحمد بن يحيى بن زهير التستري " المعجم الأوسط " (٢/ ٣١٢ / ٢٠٧٦)، وعلي بن عبد الله بن مبشر " اعتقاد أهل السنة " (١/ ١٢٤ / ٢٠٥)، وعمر بن سنان " شعب الإيمان " (٦/ ١١٩ / ٧٦٦)، وأبو بكر محمد بن إسحاق بن خزيمة " صحيح ابن خزيمة " (٤/ ١٩ / ٢٢٦٨).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقا، وكان من العباد الخشن.
وقال مسلمة بن قاسم: " روى عنه أبو داود، وتوفي سنة سبع وخمسين ".
قلت: روى أبو داود في " السنن " (٢/ ٢٦٧ / ٢٢١٨)، قال: " قرأت على محمد بن وزير المصري قلت له: حدثكم بشر بكر، ثنا الأوزاعي، ثنا عطاء عن أوس أخي عبادة بن الصامت: «إن النبي صلى الله عليه وسلم: أعطاه خمسة عشر صاعا من شعير إطعام ستين مسكينا».
قال أبو داود: " وإعطاء لم يدرك أوسا، وهو من أهل بدر، قديم الموت؛ والحديث مرسل، وإنما رووه عن الأوزاعي، عن عطاء، أن أوسًا ". انتهى.
قلت: فلعل مسلمة بن القاسم أراد هذه الرواية، أو أنه روى عنه في غير " السنن ".',NULL,'604',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (607,'وانظر: " الجرح والتعديل " (٨/ ١١٥ / ٥١٠)، و" الثقات " (٩/ ١٢٢ / ١٥٥٣١)، و" تهذيب الكمال " (٢٦/ ٥٨٣ / ٥٦٧١)، و" الكاشف " (٢/ ٢٢٨ / ٥١٩٧)، و" لسان الميزان " (٧/ ٣٧٨ / ٤٧٦٩)، و" تهذيب التهذيب " (٩/ ٤٤٢ / ٨٢٤)، " تقريب التهذيب " (١/ ٥١١ / ٦٣٧٠)، و" رجال تفسير الطبري " (٥١٣/ ٢٤٣٦).
٣٢٨ - ـ محمد بن يحيى الأزدي (٢٦٣٧٠)
(أبو داود في القدر، والترمذي، وابن ماجه)
طب: ٦ ر / ٦ ش)
(تس: ٢ ر / ٢ ش)، و (تهـ ٣ ر / ٣ ش)، و (تق ١ ر / ١ ش)
محمد بن يحيى بن عبد الكريم بن نافع، الأزدي، البصري، البغدادي، توفي سنة اثنتين وخمسين ومائتين، من كبار الحادية عشرة، ثقة.
روى عن: حجاج بن محمد المصيصي (تس ٣٦١١٩)، و (ت، ق)، وحسين بن محمد المروذي (تس ٢٦٣٧٠)، والضحاك بن مخلد (تهـ ١٢٥٦)، وعمر بن سعيد الدمشقي (تق ٩٥٧)، ومحمد بن القاسم (تهـ ١٢٥٦)، ويزيد ـ غير مكنى، ولا منسوب ـ (تهـ ٢٥٨٧)، وإسماعيل بن أبان الوراق " سنن الدارقطني " (٢/ ٨٨ / ٢)، والأسود بن عامر " مستخرج أبي نعيم " (٢/ ٢٧٢ / ٢٥٢٤)، وأصرم بن حوشب " الكامل " (١/ ٤٠٥ / ٢١٩)، وبشر بن مصلح الحلية (٧/ ٣٥٧)، وجعفر بن محمد الخراساني " شعب الإيمان " (٥/ ٣٢٧ / ٦٨٠٩)، وداود بن سليمان " مسند الشهاب " (١/ ٤٣٩ / ٧٥١)، وزيد بن عوف " تعظيم قدر الصلاة " (٢/ ٨٤٣ / ٨٥٥)، وسعيد بن سليمان " الحلية " (٥/ ٢٨٤)، وعاصم بن مهجع " المعجم الأوسط " (٦/ ١٢٥ / ٥٩٩٣)، وعبد الملك بن سيف " شعب الإيمان " (٢/ ٣٦١ / ٢٠٤٦)، وعبد الوهاب بن عطاء الخفاف " صحيح ابن حبان " (١/ ٤٣٤ / ٢٠٤)، وعبيد الله بن حفص " المعجم الأوسط " (٦/ ١٢٠ / ٥٩٨١)، وعبيد الله بن عبد المجيد الحنفي " المعجم الأوسط " (٧/ ٨٩ / ٦٩٣٨)، وعبيد بن نوح " الحلية " (٥/ ٢٦٣)، ومحمد بن الحسين " الزهد الكبير " (٢/ ٢٤١ / ٦٣٥)، ومحمد بن عمرو الواقدي " المعجم الأوسط " (٧/ ١٦٧ / ٧١٧٥)، ومسلم بن إبراهيم " المعجم الأوسط " (٨/ ١٨٨ / ٨٣٥٧)',NULL,'605',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (608,'والنضر بن حماد " المعجم الوسط " (٨/ ١٩٠ / ٨٣٦٦)، ويحيى بن راشد " ضعفاء العقيلي " (٣/ ٣١٤ / ١٣٢٨)، ويعقوب بن إبراهيم الدورقي " المعجم الأوسط " (٢/ ١٤٤ / ١٥١٩)، ويعلى بن عباد " المعجم الكبير " (٧/ ٢١٨ / ٦٩٠٩).
روى عنه: إسحاق بن إبراهيم بن يونس " مسند الشهاب " (١/ ٤٣٩ / ٧٥١)، وعبدان بن أحمد الأهوازي " المعجم الكبير " (١٨/ ٨٩ / ١٦٥)، وسعيد بن عثمان الخياط " شعب الإيمان " (٢/ ٣٦١ / ٢٠٤٦)، ومحمد بن إبراهيم بن نيروز " سنن الدارقطني " (٢/ ٢٦٥ / ١٣٤)، ومحمد بن أحمد الرقام " المعجم الأوسط " (٧/ ١٦٧ / ٧١٧٥)، ومحمد بن صالح بن الوليد النرسي " المعجم الكبير " (١٩/ ٢٠٠ / ٤٥٠)، أبو أحمد محمد بن نصر المروزي " تعظيم قدر الصلاة " (٢/ ٨٣٥ / ٨٤١).
التعديل والتجريح: اتفقت أقوال أئمة الجرح والتعديل على كونه: ثقة.
وذكر له الخطيب في " المؤتلف والمختلف " حديثا وقال: " أخطأ فيه الأزدي "، انتهى.
قلت: وأغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري ".
وروى الطبري في الأثر (تهـ ١٢٩٢)، عن (أحمد بن يحيى الأزدي، عن عون بن سلام .. )، فلعل اسمه تصحف من اسم صاحب الترجمة. والله أعلم. وانظر: " الثقات " (٩/ ١٢١ / ١٥٥٢٨)، و" تاريخ بغداد " (٣/ ٤١٤ / ١٥٤٧)، و" تهذيب الكمال " (٢٦/ ٦٣٣ / ٥٦٨٩) و" الكاشف " (٢/ ٢٢٩ / ٥٢١٣). و" التهذيب " (٩/ ٤٤٨ / ٨٣٧)، و (٩/ ٤٥٦ / ٨٤٦) و" التقريب " (١/ ٥١٣ / ٦٣٨٩).
٣٢٩ - ـ أبو عبد الله القطعي (٤٥٠١)
(مسلم: عشرة أحاديث، وأبو داود، والترمذي، والنسائي)
(طب: ٢٥ ر / ٨ ش)
(تس: ١٧ ر / ٢ ش)، و (تخ: ٢ ر / ٢ ش) و (تهـ: ٥ ر / ٤ ش)، و (تق: ١ ر / ١ ش)
أبو عبد الله، محمد بن يحيى بن أبي حزم، القطعي، البصري، توفي سنة ثلاث وخمسين ومائتين، من العاشرة، صدوق.',NULL,'606',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (609,'روى عن: بشر بن عمر الزهراني (تهـ ٨٩٤)، و (م، ت)، والحارث بن إسحاق (تخ ٤/ ٤٢٦)، والحجاج (بن محمد) (تس ٨٨٩ و١٤٣٤)، وحماد ـ غير مكنى، ولا منسوب ـ (تهـ ١٤٣٦)، وعبد الأعلى بن عبد الأعلى السامي (تس ٤٥٠١ و٤٥١٦ و٤٥٢٤ و٤٥٢٩ و٤٥٣٠ و٤٦٨٠ و١٦٨١ و٤٦٨٣ و٤٦٩٦ و٤٧٠٣ و٤٧١٧ و٤٧١٨ و٤٧٢٩ و٤٧٥٥ و٤٧٨٥ و٤٨٦٦) و (تق ٣٦٣)، و (ت، س)، وعبد العزيز بن عمران (تخ ٤/ ٤٢٣)، وعمر بن علي بن مقدم المقدمي (تهـ ١٤٤٧) و (قد)، وأبي داود (تس ٣٠٤٩٦).
روى عنه: أحمد بن محمد بن عبد الكريم " سنن الدارقطني " (٢/ ١١ / ٥)، وصالح بن أحمد بن أبي مقاتل " المعجم الأوسط " (٤/ ٨٥ / ٣٦٧٦)، وعبد الله بن أحمد بن موسى " صحيح ابن حبان " (٦/ ٤٤ / ٢٢٦٦)، وعبد الله بن موسى بن قتيبة " تأويل مختلف الحديث " (١/ ١٠٥)، ومحمد بن أحمد بن الصباح البصري " المعجم الأوسط " (٦/ ١٢١ / ٥٩٨٤)، محمد بن مكرم " المعجم الأوسط " (٦/ ١٣٠ / ٦٠٠٠).
التعديل والتجريح: " ترواحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، وصالح الحديث، صدوقا.
وانظر: " الجرح والتعديل " (٨/ ١٢٤ / ٥٥٩)، و" الثقات " (٩/ ١٠٦ / ١٥٤٣٨)، و" تهذيب الكمال " (٢٦/ ٦٠٨ / ٥٦٨٢)، و" الكاشف " (٢/ ٢٢٩ / ٥٢٠٨) والمقتنى في سرد الكنى (١/ ٣٦٠ / ٣٧١٧)، طبقات المحدثين (١/ ٩٠ / ١٠٠٧)، و" تهذيب التهذيب " (٩/ ٤٤٩ / ٨٣٩) و" تقريب التهذيب " (١/ ٥١٢ / ٦٣٨٢)، و" رجال تفسير الطبري " (٥١٤/ ٢٤٣٩).
٣٣٠ - ـ أبو جعفر الآدمي (٤٨٩٤)
(النسائي)
(طب: ٣ ر / ٣ ش)
(تس: ٢ ر / ٢ ش)، و (تخ: ١ ر / ١ ش)، و (تق: ١ ر / ١ ش)
أبو جعفر، محمد بن يزيد، الآدمي، الخراز، البغدادي، المقابري، العابد،',NULL,'607',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (610,'ويعرف ب (الأحمر) ـ وقيل: أنهما اثنان! وليس بشيء ـ توفي سنة خمس وأربعين ومائتين، من صغار العاشرة، ثقة، عابد.
روى عن: أبي ضمرة ـ وهو: أنس بن عياض ـ (تخ ١/ ١٧)، ومعن بن عيسى (تس ١٦٥٩٩)، و (تق ٩٠) و (س)، ويحيى بن سليم الطائفي (تس ٤٨٩٨)، ومحمد بن كثير " شعب الإيمان " (٧/ ٣٧٢ / ١٠٦٢٥)، ويحيى بن يعلى الأسلامي " المعجم الكبير " (٢٣/ ٣٢٣ / ٧٣٩).
روى عنه: أبو عثمان، سعيد بن عثمان الخياط " شعب الإيمان " (٥/ ٤٢٤ / ٧١٤٠)، وعمر بن محمد الهمداني " الثقات " (٩/ ١٢٠ / ١٥٥١٩)، ومحمد بن الحسين بن مكرم ـ " المعجم الكبير " (٢٣/ ٣٢٣ / ٧٣٩)
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، زاهدا، عابدا، من خيار المسلمين.
قلت: تحرف اسم أبيه في الأثر رقم (تس: ٤٨٩٨)، من (يزيد)، إلى (زيد)، وأغلب ظني أيضا، أنه هو (محمد بن عبيد الآدمي)، الذي روى عنه الطبري في: " تهذيب الآثار " الأثر رقم (١٩٨٥)، عن أنس بن عياض أيضا وقد سبق وأن أفردت له ترجمة برقم (٢٩٣)، فلتراجع.
وانظر " الجرح والتعديل " (٨/ ١٢٩ / ٥٨١) و" الثقات " (٩/ ١٢٠ / ١٥٥١٩)، و" تاريخ بغداد " (٣/ ٣٧٤ / ١٤٨٨) و" تهذيب الكمال " (٢٧/ ٣٨ / ٥٧٠٩)، و" الكاشف " (٢/ ٢٣٢ / ٥٢٢٧) و" تهذيب التهذيب " (٩/ ٤٦٧ / ٨٧١)، و" تقريب التهذيب " (١/ ٥١٤ / ٦٤٠٨)، و" رجال تفسير الطبري " (٥١٤/ ٢٤٤٠)
..
٣٣١ - ـأبو بكر الطرسوسي (٩١)
(طب: ٣ ر / ٣ ش)
(تس: ر١/ ١ ش)، و (تق ٢ ر / ٢ ش)
أبو بكر، محمد بن يزيد الطرسوسي، هو: محمد بن يزيد بن عبد الله، السلمي، الأشلي المستملي، الطرسوسي، لا النيسابوري: سراق حديث، ووضاع و
روى عن: معن ـ وهو بن عيسى القزاز ـ (تس ٩١)، وعبد الوهاب بن عطاء (الخفاف)',NULL,'608',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (611,'(تق ٨١٢)، ويحيى بن آدم (تق ٩٨١)، وإسحاق الجنبي، وأنس بن عياض، وخالد بن سليمان بن خالد بن سلمة، وزيد بن الحبان، وسليمان بن قيس " تاريخ بغداد " (٣/ ٢٨٨ / ٧٦٨)، ويزيد بن هارون، ومبشر بن إسماعيل، وأبي أسامة ـ هو حماد بن أسامة ـ " الثقات " (٩/ ١١٥ / ١٥٤٨٨)
روى عنه: أحمد بن محمد بن عنبسة، وعلي بن محمد بن سليم الحلبي، وابن أبي قتيبة، وأبو أحمد محمد بن حامد بن محمد بن إبراهيم بن إسماعيل السلمي " تاريخ بغداد " (٣/ ٢٨٨ / ٧٦٨)، ومحمد بن عمر الديماسي، وعبد العزيز العسقلاني.
وكل اسم لم يعز إلى كتاب، من معجم شيوخه وتلاميذة، وميزاته بالأسود العريض، فهو من ترجمنه في " الكامل في الضعفاء ".
التعديل والتجريح: قال ابن عدي في " الكامل ": " يسرق الحديث ويزيد في ويضع ".
ثم قال بعد أن ساق له عدة أحاديث مسروقة: " ولمحمد بن يزيد المستملي غير ما ذكرت، مما سرق من حديث الثقات.
وذكره ابن حبان في " الثقات " وقال: ربما أخطأت ".
قال الخطيب البغدادي بعد أن ساق له حديثا مرفوعا من طريقة عن أنس قال: قال رسول الله ـ صلى الله عليه وسلم ـ «سيأتي من بعدي رجل يقال له النعمان بن ثابت ويكنى أبا حنيفة ليحيين دين الله وسنتي على يديه ".
قال الخطيب: " لم أكتب هذا الحديث إلا من هذا الوجه، وهو باطل موضوع.
ومحمد بن يزيد متروك الحديث، وسليمان بن قيس، وأبو المعلى: مجهولان، وأبان بن عياش: رمي بالكذب ".
قلت: والظاهر من سياق كلام الخطيب البغدادي أن: (محمد بن يزيد السلمي)، (نيسابوري)، لا (طرسوسي)، في حين جعلهما الحافظ في " اللسان " واحدا هو (محمد بن يزيد السلمي النيسابوري الطرسوسي)',NULL,'609',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (612,'وأغفله الشيخان شاكر في تحقيقهما " لتفسير الطبري ".
وجزم الشيخ علي رضا في تعليقه على " الجزء المفقود من تهذيب الآثار " (١/ ٥١٤ / ٩٨١)، بأنه " يضع الحديث ويسرقه ".
وتصحف اسم الراوي عنه (ابن أبي قتيبة)، في " الكامل " إلى (ابن عيينة)، في " اللسان "
وانظر: " الكامل " (٦/ ٢٨٢ / ١٧٦٨)، و" الثقات " (٩/ ١١٥ / ١٥٤٨٨)، و" تاريخ بغدادي " (٣/ ٢٨٨ / ٧٦٨)، و" الكاشف " (٢٥٣/ ٧٥١)، و" اللسان " (٥/ ٤٢٩ / ١٤٠٣)
١٧٢ - ـ تمييز أبو بكر، محمد بن عيسى بن يزيد، التميمي، الطرسوسي، توفي سنة ٢٧٦ الحافظ، البارع، يخطئ كثيرا.
روى عن: أبي نعيم، وأبي اليمان، وعفان.
روى عنه: أبو عوانة، وابن خزيمة، وأبو العباس الدغولي.
قال ابن عدي: " عامة ما يرويه لا يتابعونه عليه وهو في عداد من يسرق الحديث ".
وقال الحاكم: " هو من المشهورين بالرحلة والفهم، والتثبت. . ".
وانظر: " الثقات " (٩/ ١٥ / ١٥٧٢٢)، و" الكامل " (٦/ ٢٨٣ / ١٧٦٩)، " تذكرة الحفاظ " (٢/ ٦٠١ / ٦٢٥)، و" لسان الميزان " (٥/ ٣٣٥ / ١١٠٨).
٣٣٢ - ـ أبو هاشم الموصلي (٢١٣٣٥)
(النسائي، وابن ماجه)
(طب: (تس: ١ ر / ١ ش)
أبو هاشم، محمد بن أبي خداش ـ واسم (أبي خداش)، (علي) ـ الأسدي، الموصلي: قتل في سبيل الله تعالى، في وقعة (شمشاط)، مقبلا غير مدبر، في رد الروم، سنة اثنتين وعشرين ومائتين، من العاشرة، ثقة، عابد.
ولا يمكن أن يكون شيخا مباشرا للطبري، المولود سنة ٢٢٥ أو ٢٢٦!! إلا أن',NULL,'610',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (613,'يكون بينهما واسطة، أو تكون روايته عنه وجادة، ولم يصرح بها، أو أن يكون هناك سقط ما في الإسناد، والله أعلم. وقد صححه الشيخ التركي في تحقيقه (١٤/ ١١٧)، إلى (محمود بن خداش)، تبعا لترجمة الأخير في " تهذيب الكمال " (٢٧/ ٢٩٨)، وأشار إلى كونه (محمد بن حداس)، كما في بعض المخطوطات التي اعتمد في تحقيقه عليها، وإن كنت أرجح وأميل إلى تصحيحه كما صححه الشيخ التركي فقد ترجمت ل (محمود بن خداش)، كما ترجمت ل (محمد بن أبي خداش)، في هذا الموضع أيضا، خاصة وأنه من أهل الحديث المجاهدين الأشداء، والصالحين الشهداء ـ نحسبه، والله حسيبه، ولا نزكيه على الله تعالى ـ.
روى عن: محمد بن عبيد (تس ٢١٣٣٥)، ورجاء بن الجارود " الكامل " (١/ ٤١٧)، ومروان نب معاوية " المحلى " (١٠/ ٧٦ / ١٩٢٠).
روى عنه: إسماعيل بن إسحاق " المحلى " (١٠/ ٧٦ / ١٩٢٠).
التعديل والتجريح: قال العجلي: " ثقة، رجل صالح ".
وقال صاحب تاريخ الموصل: " حدثت عن محمد بن غالب التمتام، قال: " قلت ليحيى بن معين، كتبت " جامع الثوري " عن أبو هاشم عن المعافى (ابن عمران الموصلي). فقال يحيى: بلغني أن هذا الرجل نظير المعافى أو أفضل منه ".
وقال أيضا: " أخبرني علي بن مكي قال: سمعت يعلى الزراد يقول: سمعت بشر بن الحارث يقول: " وددت أني ألقى الله عز وجل، بمثل عمل أبي هاشم أو بمثل صحيفته " وروى بإسناده عن أحمد بن عباس الأزدي قال: " كنا عند المعافى، فأقبل أبو هاشم. فقال المعافى: أراه من القوم، يعني الأبدال ـ "
وذكره في الطبقة الخامسة من أهل الموصل وقال: " رحل إلى الكوفة والبصرة وأكثر عن ابن عيينة، وعيسى بن يونس من أهل الصلاح والفضل والجهاد، قتل في سبيل الله عز وجل، بشمشاط، لما جاءت الروم إليها، مقبل غير مدبر، سنة اثنين وعشرين ومائتين ".
وقال أيضا: " حدثني العلاء بن أيوب قال: حدثني من حضر أبا هاشم، لما',NULL,'611',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (614,'التقى المسلمون والروم في وقعت شمشاط، فقال أبو هاشم لرفقائه: هذا يوم كنت أتمناه، عليكم السلام ثم سدد رمحه، وجعله على قربوس فرسه وحمل على القوم فكان آخر العهد به ".
وقال إدريس بن سليم: " كنا عند غسان بن الربيع، أو يعلى بن مهدي، فجاء نعي أبي هاشم، وقال قائل: مات شيخ الموصل. فقال: نعم! وشيخ الجزيرة ومصر الشام ".
قلت: أغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري ".
وانظر: معرفة " الثقات " (٢/ ٢٣٦ / ١٥٩٠)، و" المحلى " (١٠/ ٧٦ / ١٩٢٠)، و" تكملة الإكمال " (٢/ ٤٠٦ / ١٨٧٦) و" تهذيب الكمال " (٢٦/ ١٦٠ / ٥٤٨٨)، و" الكاشف " (٢/ ٢٠٤ / ٥٠٦٧)، و" تهذيب التهذيب " (٩/ ٣١٧ / ٥٩٢)، و" تقريب التهذيب " (١/ ٤٩٨ / ٦١٦١)
٣٣٣ - ـ محمد بن أبي منصور الآملي (تخ: ١/ ٣٥)
(طب: ٥ ر / ٣ ش)
(تخ: ٤ ر / ٢ ش)، و (صر: ١ ر / ١ ش)
محمد بن منصور، وقيل: ابن أبي منصور الآملي، الطبري: من الثانية عشرة، مجهول.
روى عن: أبي مروان، الحكم بن محمد الآملي (صر ١٦)، وخلف بن واصل (تخ ١/ ٤٦ و١/ ٤٧)، وعلي بن الهيثم (تخ ١/ ٣٥ و١/ ١١٣).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: لم أقف له على ذكر، أو ترجمة، أو كتب الرجال والتراجم، ولم أقف له على رواية في كتب الحديث التي بين يدي، ولم أجد له ذكرا إلا في المواضع الخمس عن شيوخه الثلاثة والمشار إليها أعلاه، ولعله محرف، أو مصحف، أو منسوب إلى غير أبيه، وأقرب من يمكن أن يكون هو (محمد بن منصور الطوسي)، وقد سبقت ترجمته في الترجمة رقم (٣٢٠)، والظاهر أنه من طبقة الطبري وأقرانه، أو أصغر منه. وأما شيوخه الثلاثة هنا:',NULL,'612',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (615,'فأولهم، (عخ)، أبو مروان، الحكم بن محمد، الآملي، الطبري، نزيل مكة، توفي سنة بضع عشرة ومائتين، من العاشرة، صدوق.
وأما الثاني، فهو: خلف بن واصل، لم أعرفه، وأفردت له الترجمة رقم (١٠٩)، من كتابي هذا، وترجمته أيضا في " المعجمين الصغير، والكبير " لأن الطبري روى عنه بواسطة وبغير واسطة.
وأما الثالث: علي بن الهيثم، فهو البغدادي، صاحب الطعام، من الحادية عشرة، مقبول، وهو شيخ البخاري، وقيل إنهما أكثر من واحد، وقد ترجمت له في موضعه من الشيوخ المبهمين من هذا الكتاب، وفي " المعجمين الكبير والصغير " أيضا، لأن الطبري روى عنه بوسائط صرح بها كما هو هنا، وفي غير هذا الموضع أيضا، وأبهمها في مواضع أخرى.
..
٣٣٤ - ـ أبو محمد الطالقاني (١٧٨)
(الترمذي، والنسائي ـ في مسند علي ـ، وابن ماجه)
(طب: ١١ ر / ٨ ش)
(تس: ١٠ ر / ٨ ش)، و (تخ: ١ ر / ١ ش)
أبو محمد، محمود بن خداش، الطالقاني، نزيل بغداد: مولده سنة ستين ومائة، وتوفي يوم الأربعاء، لأربع عشرة ليلة خلت من شعبان، سنة خمسين ومائتين، وهو ابن تسعين سنة، من العاشرة، صدوق.
روى عن: حماد بن خالد الخياط (تس ١٨٥٠١)، و (تخ ١/ ١٩٩)، وحميد بن عبد الرحمن الرؤاسي (تس ١٧٨)، ومحمد بن ربيعة الكلابي (تس ١٨١)، وسيف بن محمد (بن أحمد)، الثوري (تس ٢٠١٢٦)، و (ت)، وعباد بن العوام (تس ٢٣٥٣٩)، و (ت)، ومحمد بن يزيد الواسطي (تس ٢٦٢٨٧ و٣٧٧٤٦)، و (ت)، وهشيم بن بشير (تس ١٢٠٨٦)، وأبي سعيد الصنعاني، والصحيح: محمد بن ميسر أبي سعد الصاغاني (تس ٣٨٢٩٨ و٣٨٣٢٤)، ويوسف بن الغرق',NULL,'613',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (616,'الكامل، (٧/ ١٦٧ / ٢٠٧٣)، والخفاف ـ غير مسمى، ولا مكنى، ولا منسوب؛ ولعله: عبد الوهاب بن عطاء " تاريخ بغداد " (١٣/ ٩٠ / ٧٠٧٤).
روى عنه: أحمد بن السندي بن فروخ " الكامل " (٦/ ٢٦٢ / ١٧٤١)، وإسحاق بن أحمد بن جعفر " الكامل " (٦/ ٢٦٢ / ١٧٤١)، وجعفر بن محمد بن سعيد البغدادي " السير " (١٤/ ١١٠ / ٥٤)، وعلي بن الحسن المرواني " الكامل " (٣/ ٣٤٢ / ٧٩١)، وعمر بن محمد بن عيسى السزابي " الكامل " (٥/ ١٩٢ / ١٣٤٨)، محمد بن مسلمة بن قرناء " المجروحين " (١/ ٣٦٠ / ٤٧٤).
التعدين والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة. صدوقا، من أهل الصدق، لا بأس به.
وانظر: " التاريخ الصغير " (٢/ ٣٩٢ / ٢٩٨١)، و" الجرح والتعديل " (٨/ ٢٩١ / ١٣٣٩)، و" الثقات " (٩/ ٢٠٢ / ١٦٠١٥)، و" تاريخ بغداد " (١٣/ ٩٠ / ٧٠٧٤)، " تهذيب الكمال " (٢٧/ ٢٩٨ / ٥٨١٤)، و" الكاشف " (٢/ ٢٤٥ / ٥٣١٩)، و" المقتنى " (٢/ ٥٦)، و" سير أعلام النبلاء " (١٢/ ١٧٩ / ٦٢)، و" المقصد الأرشد " (٢/ ٥٤٥ / ١١١٤)، و" تهذيب التهذيب " (١٠/ ٥٦ / ١٠٢)، و" تقريب التهذيب " (١/ ٥٢٢ / ٦٥١١)، و" رجال تفسير الطبري " (٥١٥).
٣٣٥ - ـ مخلد بن الحسن (تهـ: ٢٥٥)
(النسائي)
(طب: (تهـ ٣ ر / ٤ ش)
أبو محمد ـ ويقال: أبو أحمد ـ، مخلد ـ بفتح أوله وثالثه، وسكون ثانيه ـ ابن الحسن بن أبي زميل ـ بالزاي المضمومة، مصغر ـ المروزي، الحراني، نزيل بغداد: من التاسعة، صدوق، لا بأس به.
روى عن: أبي وهب، عبيد الله بن عمرو، الرقي (تهـ ٢٥٥ و٣٦٢ و١٤٠٧)، و (س)
روى عنه: عبد الله بن أحمد بن حنبل " مسند أحمد " (٥/ ٩٧ / ٢٠٩٥٣)، وعبد الله بن صالح البخاري " الكامل " (٥/ ٢٢٣ / ١٣٧٩)، ومحمد بن إبراهيم بن شبيب الأصبهاني العسال " تالي',NULL,'614',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (617,'تلخيص المتشابه " (٢/ ٥٨١ / ٣٥٢).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقا، مستقيم الحديث، لا بأس به.
وانظر: " الجرح والتعديل " (٨/ ٣٤٩ / ٤٦٠٢)، و" تاريخ بغداد " (١٣/ ١٧٥ / ٧١٥٤)، و" تالي تلخيص المتشابه " (٢/ ٥٨٠ / ٣٥١)، و" الكاشف " (٢/ ٢٤٨ / ٥٣٣٥)، و" تهذيب الكمال " (٢٧/ ٣٣٠ / ٥٨٣١)، و" تهذيب التهذيب " (١٠/ ٦٥ / ١٢٢)، و" تقريب التهذيب " (١/ ٥٢٣ / ٦٥٢٨) ..
١٧٣ - ـ تمييز مخلد بن الحسن، البصري، من العاشرة، مقبول.
روى عن: حماد بن زيد، ومحمد بن ثابت العبدي.
روى عنه: أبو حاتم الرازي.
التعديل والتجريح: ذكره عبد الرحمن بن أبي حاتم في كتابه، وقال: سمع منه أبي بالبصرة في الرحلة الثالثة.
وانظر: " الجرح والتعديل " (٨/ ٣٤٩ / ١٦٠٣)، و" تهذيب الكمال " (٢٧/ ٣٣١ / ٥٨٣٢)، و" تهذيب التهذيب " (١٠/ ٦٥ / ١٢٣)، و" تقريب التهذيب " (١/ ٥٢٣ / ٦٥٢٩).
..
٣٣٦ - ـ مخلد بن محمد المهلبي (تق: ٥٣)
(طب: تق: ١ ر / ١ ش)
مخلد بن محمد المهلبي، البصري، من الحادية عشرة، ترجمة ابن أبي حاتم الرازي، ونقل عن أبيه قوله: " صالح الحديث ".
روى عن: ( ... )، بياض في أصل الكتاب بمقدار كلمة (تق ٥٣)، وجعفر الفريابي " المستخرج " (٢/ ٨٩ / ١٠٤٧)، ومعاوية بن عبد الكريم " الجرح والتعديل " (٨/ ٣٤٩ / ١٦٠٤).
روى عنه: أبو حاتم الرازي " الجرح والتعديل " (٨/ ٣٤٩ / ١٦٠٤)، أبو نعيم الأصبهاني " المستخرج " (٢/ ٨٩ / ١٠٤٧).',NULL,'615',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (618,'التعديل والتجريح: قال عبد الرحمن بن أبي حاتم: " سمع منه أبي بالبصرة وسألته عنه فقال: صالح الحديث ".
وانظر: " الجرح والتعديل " (٨/ ٣٤٩ / ١٦٠٤)
..
٣٣٧ - ـ مروان بن الحكم الحراني (تهـ: ٨٣)
(طب: تهـ: ٤ ر / ٣ ش)
مروان بن الحكم الحراني، من الحادية عشرة، ترجمة الحافظ في " التهذيب " ـ تمييزا ـ، وسكت عنه.
روى عن: الخضر بن محمد الحراني (تهـ ٨٣) وأبي جعفر النفيلي (تهـ ١٢٦٥)، والبابلتي (تهـ ١٩٤٧ و٢٠٥٦).
روى عنه: أبو جعفر محمد بن جري الطبري.
التعديل والتجريح: لم أقف له على ترجمة، ولا على ذكر في كتب الرجال والحديث التي بين يدي إلا في " تهذيب التهذيب " (١٠/ ٨٣ / ١٦٨)، تمييزا، ولم يذكر الحافظ في ترجمته جرحا ولا تعديلا. وانظر: " تهذيب التهذيب " (١٠/ ٨٣ / ١٦٨).
٣٣٨ - ـ أبو حاتم الأنصاري (٣٨٤٧)
(أبو داود، والترمذي)
(طب: تس: ١ ر / ١ ش)
أبو حاتم، مسلم بن حاتم، الأنصاري، البصري، إمام المسجد الجامع في البصرة، من العاشرة، صدوق ربما وهم.
روى عن: بشار بن بكير الحنفي (تس ٣٨٤٧).',NULL,'616',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (619,'روى عنه: الحسين بن إسحاق التستري " المعجم الكبير " (١٢/ ٧٠ / ١٢٥٠٤)، وسهل بن موسى " الحلية " (٨/ ١٩٩)، ومحمد بن عمران الناقط البصري " المعجم الأوسط " (٦/ ١٢٣ / ٥٩٩١)، ومحمد بن يعقوب الأهوازي " الثقات " (٩/ ١٥٨ / ١٥٧٥٨).
التعديل والتجريح: قال الترمذي وأبو القاسم الطبراني: " كان ثقة ".
وذكره ابن حبان في كتاب " الثقات " وقال: " ربما أخطأ ".
قلت: وقع خطأ مطبعي أو نسخي، في المطبوعتين بعد ذكر (الحنفي)، حيث وقعت لفظة (قالا)، بما يشعر أن للطبري في هذا الإسناد شيخين هما: مسلم بن حاتم الأنصاري و (بشار بن بكير الحنفي). وهو خطأ بلا ريب.
وأما شيخ: بشار بن بكير الحنفي، فله رواية في (التفسير، و" الحلية "، و" القول المسدد " (١/ ٣٧).
وأخرج أبو نعيم هذا الأثر سندا، ومتنا في " الحلية " (٨/ ١٩٩)، من طريق سهل بن موسى به.
وانظر: " الثقات " (٩/ ١٥٨ / ١٥٧٥٨)، و" تهذيب الكمال " (٢٧/ ٤٩٦ / ٥٩٢١)، و" الكاشف " (٢/ ٢٥٨ / ٥٤١٠)، و" المقتنى " (١/ ١٥٩ / ١٢٣٣)، و" ميزان الاعتدال " (٧/ ١٠٠٨٨)، و" تهذيب التهذيب " (١٠/ ١١٢ / ٢٢٥)، و" تقريب التهذيب " (١/ ٥٢٩ / ٦٦٢١)، و" رجال تفسير الطبري " (٥٢٢/ ٢٤٩٢).
..
٣٣٩ - ـ أبو ثابت الخطاب (١٣٨٦)
(طب: ٧ ر / ٢ ش)
(تس: ٥ ر / ١ ش)، و (تهـ: ٢ ر / ٢ ش)
أبو ثابت، مشرف بن أبان الخطاب، البغدادي، من العاشرة، ذكره ابن حبان في " الثقات " وسكت عنه الخطيب.
روى عن: إسحاق بن عيسى بن ثابت ـ ابن بنت داود بن هند (تهـ ٢٧٠٦)، ووكيع بن الجراح (تس ١٣٨٦ و١٩٥٣ و٢٣٨٩ و٣٧٩٢٠ و٣٧٩٢٤)، و (تهـ ٨٩٣)، وسفيان بن عيينة،',NULL,'617',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (620,'وصالح بن عبد الكريم العابد، وعبد العزيز بن أبان الدوري، " حلية الأولياء " (٨/ ٣٣٥)، وعمر بن السكن " تهذيب الكمال " (١١/ ١٩٣ / ٢٤١٣ ـ ترجمة سفيان بن عيينة)، وعمرو بن جرير البجلي، ومحمد الحسن بن أبي يزيد الهمداني، وأبو بكر الموصلي " شعب الإيمان " (١/ ٥١٨ / ٩٠٢)، وأبي عاصم " الثقات " (٩/ ٢٠٣ / ١٦٠١٩)، وأبي نعيم " الثقات " (٩/ ٢٠٣ / ١٦٠١٩).
روى عنه: الحسن بن سفيان " حلية الأولياء " (٨/ ٣٣٥)، وسهيل بن إبراهيم المروزي البغدادي " تاريخ بغداد " (٩/ ٢٢٢ / ٤٧٩٩)، ويحيى بن محمد بن صاعد، وأبو بكر بن أبي الدنيا.
وكل اسم لم يعز إلى كتاب، ـ وميزته بالأسود العريض ـ، من معجم شيوخه وتلاميذه، فهو من ترجمته في " تاريخ بغداد ".
التعديل والتجريح: ذكره محمد بن حبان في " الثقات " (٩/ ٢٠٣ / ١٦٠١٩)، وسماه: (مشرف بن أبان الخطاب)، وقال: " يروي عن أبي عاصم وأبي نعيم. روى عنه أهل بلده ". وترجم له الخطيب البغدادي في " تاريخ بغداد " (١٣/ ٢٢٤ / ٧١٩٤)، وسماه: (مشرف بن أبان الخطاب)، أيضا، وساق له بسنده حديثا من طريق أنس بن مالك، في مناقب أبي طلحة الأنصاري، ولم يذكر فيه جرحا ولا تعديلا.
وذكره الشيخ شاكر في تحقيقه " لتفسير الطبري " (٣/ ٢١ / ١٩٥١)، وقال: " بشر بن أبان الحطاب = مشرف بن أبان. شيخ الطبري: لم أجد له ترجمة ولا ذكرا فيما بين يدي من المراجع (١٣٨٣)، انتهى.
وأغفله الشيخ شاكر في الموضوعين من " تهذيب الآثار " (٢/ ٢٤٣ / ٤٠٠)، و (٥/ ٦٣٩ / ٩٤٦).
قلت: وقد أشرت إلى ترجمته برقم (٦١)، باسم: بشر بن أبان الحطاب ـ بالمهملة، وهو الخطأ والصواب: " بالمعجمة "، الخطاب ـ وقد تصحف اسمه في طبعات " تفسير الطبري ".
فهو في طبعة دار الفكر (سنة ١٤١٥) (١/ ٥٣٤ / ١١٤١)، (بشر بن أبان الخطاب) " بالمعجمة.',NULL,'618',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (621,'وفي طبعة دار الفكر سنة (١٤٠٥) (١/ ٣٧٨). وطبعة دار الكتب العلمية سنة (١٤١٨) (١٤٢٠)، في الأثر رقم (تس ١٣٨٦)؛ (بشر بن أبان الحطاب، بالمهملة).
وفي طبعة دار الفكر (سنة ١٤١٥) (١/ ٧٣٨ / ١٦٠٥)، (مسروق، على وزن: مسبوق).
وفي طبعة دار الكتب العلمية (١٢/ ٦٨٧ / ٣٧٩٢٠ و٣٧٩٢٤)، من نفس الطبعة: (مسروق)، مثل طبعة دار الفكر.
وفي طبعة دار الكتب العلمية (١/ ٥٧٨ / ١٩٥٣) و (٢/ ٥٨ / ٢٣٨٩): (مشرف)، بتشديد الراء وفتحها .. وفي طبعة دار المعارف الأثر رقم (١٣٨٣): بشر، وقد ساوها الشيخان شاكر فيها (بمشرف)، ولم يجدا له ترجمة! كما نقل عنهما الحلاق في " رجال تفسير الطبري " (٦٨/ ٢٩٦).
وبالاستقراء والتتبع، ترجح عندي أن صاحب الترجمة المراد هو: (مشرف بن أبان الخطاب)، وذلك أربعة أسباب هي:
الأول: وقفت له على ترجمة في " الثقات "، " وتاريخ بغداد " ووقفت له على رواية في غيرها من الكتب أيضا.
الثاني: روايته عن " وكيع، عن سفيان "، في أربعة من المواضع الخمسة، التي روي له فيها، في " التفسير "، مع تعدد الأسماء التي تصحفت في مختلف الطبعات وكما فصلته سابقا.
الثالث: بقاء اسمه من غير تصحيف ولا تحريف، في روايته من الخمسة وبقاء نسبته في الثالثة.
الرابع: مساواة الشيخ شاكر بين الاسمين، المصحف والصحيح.
كل ذلك يرجح عندي أن المراد هو (مشرف بن أبان الخطاب)، وأنه لا وجود (لبشر بن أبان)، ولا (لمسروق بن أبان)، لأنهما مصحفان، والله تعالى أعلى وأعلم ..',NULL,'619',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (622,'٣٤٠ - ـ أبو النضر الضبي (٤٣٩)
(طب: ٢٠ ر / ٩ ش)
(تس: ١٨ ر / ٨ ش) و (تق: ٢ ر / ٢ ش)
أبو النضر، مطر بن محمد الضبي، ـ والظاهر أنه ـ أبو النضر، مطر بن محمد بن جناح بن الضحاك، السكري، الواسطي، البصري: من الحادية عشرة، ذكره ابن حبان في " الثقات " وقال: " يخطئ ويخالف ".
روى عن: أبي قتيبة سلم بن قتيبة (تس ١٧٢٦٨ و١٧٣٦١ و٢٢٨٤٨ و٢٩٠٢٥)، وأبي عاصم الضحاك بن مخلد النبيل (تس ٤٣٩ و١٧٣٦١ و٢١٣٧١ و٣٧٣٢٩)، وعبد الرحمن بن مهدي (تس ٢٣٤٧٥ و٢٥٣٠١ و٣٦٤٦٣)، عبد الله بن داود الواسطي (تس ١٤٦١٦ و٢٢٨١١ و٢٩٦٢٣)، ووهب بن جرير (تس ٢٢٨٤٨)، وكثير بن هشام الكلابي (تس ٢٥٣٠٥)، ويزيد [بن هارون] (تس ٢٩٦١٨)، وأبي داود [الطيالسي] (تس ١٢٢٠٣)، و (تق ٧١٩)، وأبي عامر [العقدي] (تق ٧٠٣).
روى عنه: محمد بن جرير الطبري.
التعديل والتجريح: ذكره الشيخ شاكر في تحقيقه " لتفسير الطبري " (١٠/ ٤١٧ / ١٢١٩٨)، وقال: " شيخ الطبري، لم أجد له ترجمة ولا ذكرا ".
قلت لم أجد له ترجمة، ولا رواية في كتب الرجال والحديث التي وقفت عليها بهذا الاسم والنسبة (مطر بن محمد الضبي).
وقد تصحف اسمه من (مطر بن محمد الضبي)، إلى (مطرف بن محمد الضبي)، في الأثر رقم (تس ٣٧٣٢٩)، وإلى: (مطرف بن عبد الله الضبي)، في الأثر رقم (تس ٢٩٠٢٥).
وقد وقفت على ذكر لأسماء عدة رجال، يشاركونه في اسمه واسم أبيه أقربهم أن يكون المراد:
١٧٤ - ـ تمييز أبو النضر، مطر بن محمد بن جناح بن الضحاك، السكري، الواسطي، البصري، من الحادية عشرة، يخطئ ويخالف.',NULL,'620',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (623,'روى عن: عبد المؤمن بن سالم بن ميمون المسمعي " ضعفاء العقيلي " (٣/ ٩٣ / ١٠٦٦)، و" مسند البزار " (٩/ ٨٠ / ٣٦١٢)، و" مسند الروياني " (١/ ١٣٨ / ١٤٦)، و" المعجم الكبير " (١٨/ ١٣٦ / ٤٤٢) و" طبقات أصبهان " (٣/ ٤٤٥)، و" الحلية " (٣/ ٣٥)، و" إكمال بن ماكولا " (٧/ ٢٦٦)، و" ميزان الاعتدال " (٤/ ٤٢١ / ٥٢٧٩)، و" لسان الميزان " (٤/ ٧٥ / ١٢١)، وعبد الله بن داود الواسطي " ميزان الاعتدال " (٤/ ٩١ / ٤٢٩٩)، ترجمة عبد الله بن داود ويزيد بن هارون الواسطي " الثقات " (٩/ ١٨٩ / ١٥٩٢٨)، و" لسان الميزان " (٦/ ٤٩ / ١٨٨)، وأبي داود الطيالسي " تفسير القرطبي " (١٠/ ٧٣)
روى عنه: أحمد بن محمد بن نصر الضبعي " الحلية " (٣/ ٣٥)، ومحمد بن إسحاق بن خزيمة " الثقات " (٩/ ١٨٩ / ١٥٩٢٨)، ومحمد بن صالح بن الوليد النرسي " المعجم الكبير " (١٨١٣٦/ ٤٤٢)، وأبو بكر البزار " مسند البزار " (٩/ ٨٠ / ٣٦١٢)، وأبو بكر الروياني " مسند الروياني " (١/ ١٣٨ / ١٤٦).
ومن خلال ما تقدم يظهر جليا أن " الضبي " شارك " السكري " في اسمه واسم أبيه (وثلاثة من شيوخه الأربعة، وبلدهم وطبقاته، وطبقة الرواة عنه، ويغلب على الظن أن يكون هو المراد، وأن يكون الاثنين واحدا، والله تعالى أعلم.
وقد جمعت كنيته واسمه ونسبه من خلال كتب الحديث والرجال التي أخرجت روايته للحديث المتواتر:» من كذب علي متعمدا فليتبوأ مقعده من النار «وغيره من الآثار المتفرقة، والتي أشرت إليها في معجم شيوخه وتلاميذه ..
١٧٥ - ـ تمييز مطر بن محمد، الأسدي.
روى عن: عمرو بن الحصين.
روى عنه: هارون بن عيسى بن مسكين.
وانظر: " المجروحين " (٢/ ٢٨٠ / ٩٧٣) ـ ترجمة القاضي محمد بن علاثة ..
١٧٦ - ـ تمييز أبو طاهر، مطر بن محمد بن نصر، التميمي، الهروي.',NULL,'621',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (624,'روى عن: محمد بن عبدة القيسي.
روى عنه: أحمد بن الحسن بن أحمد الوكيل.
وانظر: " تاريخ بغداد " (١٣/ ٢٧٥ / ٧٢٣٦).
٣٤١ - ـ أبو سهل الصقري (٢٨٦٠)
(طب: ٢ ر / ١ ش)
(تس: ١ ر / ١ ش)، و (تهـ: ١ ر / ١ ش)
أبو سهل معاذ بن شعبة بن ميمون بن صقر، الصقري البصري: من العاشرة سكت عنه ابن أبي حاتم!! ووثقه وابن حبان.
روى عن: شريك (بن عبد الله النخعي) (تس ٢٨٦٠)، و (تهـ ٢٨٦٨)، وداود بن الزبرقان " معجم بن يعلى " (١/ ٢٤٥ / ٣٠٢)، وعباد بن العوام " الجرح والتعديل " (٨/ ٢٥١ / ١١٤١)، وعثمان بن مطر " الجرح والتعديل " (٨/ ٢٥١ / ١١٤١)، وعبيدة بن حميد " شعب الإيمان " (٣/ ١٢٣ / ٣٠٧٥)، ومالك بن غسان " الكامل " (٦/ ٣٨٢ / ١٨٦٦)، ومعتمر [بن سليمان] " الكفاية " (١/ ٣٩٢)، وأبي داود [الطيالسي] " الثقات " (٩/ ١٧٨ / ١٥٨٦٩)، وأبي عاصم " الثقات " (٩/ ١٧٨ / ١٥٨٦٩).
روى عنه: الحسن بن سفيان " الثقات " (٩/ ١٧٨ / ١٥٨٦٩)، وعبد الله بن أحمد بن حنبل " الكفاية " (١/ ٣٩٢)، وموسى بن إسحاق الأنصاري " الجرح والتعديل " (٨/ ٢٥١ / ١١٤١)، وهشام بن علي السيرافي " شعب الإيمان " (٣/ ١٢٣ / ٣٠٧٥)، وأبو يعلى الموصلي " المعجم " (١/ ٢٤٥ / ٣٠٢).
التعديل والتجريح: ذكره ابن أبي حاتم في الجرح والتعديل وسكت عنه وذكره ابن حبان في " الثقات ".
وانظر: " الجرح والتعديل " (٨/ ٢٥١ / ١١٤١)، و" الثقات " (٩/ ١٧٨ / ١٥٨٦٩)، و" رجال تفسير الطبري " (٥٣٠/ ٢٥٣٣).
..
٣٤٢ - ـ أبو عباس البزاز (١٤٢١٢)',NULL,'622',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (625,'(طب: ٦ ر / ٤ ش)
(تس: ٤ ر / ٢ ش)، تهـ: ٢ ر / ٢ ش)
أبو العباس، المفضل بن إسحاق وهو خطأ ـ والصحيح ـ الفضل بن إسحاق بن حيان، البزاز الدوري، البغدادي: توفي سنة اثنتين وأربعين ومائتين، من الحادية عشرة، ثقة، مأمون. وقد مضت ترجمته برقم (٢٣٥)، من كتابي هذا.
..
٣٤٣ - ـ مهنأ الشامي (تهـ: ١٩٨٢)
(طب: تهـ: ١ ر / ١ ش)
أبو عبد الله، مهنأ بن يحيى، الشامي، ثم البغدادي: صاحب بشر الحافي والإمام أحمد، من الحادية عشرة، ذكره ابن حبان في " الثقات " وقال: " كان من خيار الناس، من جلساء أحمد بن حنبل، وبشر الحافي مستقيم الحديث ". وقال الدارقطني: " ثقة نبيل ". وقال الأزدي " منقر الحديث ".
روى عن: ضمرة بن ربيعة (تهـ ١٩٨٢)، وبقية بن الوليد " الثقات " (٩/ ٢٠٤ / ١٦٠٢٧)، وزيد بن أبي الزرقاء " ميزان الاعتدال " (٦/ ٥٣٣ / ٨٨٤٢)، يزيد بن هارون " الثقات " (٩/ ٢٠٤ / ١٦٠٢٧)، وأبو العباس عبد الله بن أحمد بن إبراهيم بن مالك المارستاني " لسان الميزان " (٣/ ٢٥٢ / ١٠٩٩).
روى عنه: أحمد بن منيع " العلل المتناهية " (١/ ٦٥ / ٥٤)، وعلي بن الحسين بن جرثومة " لسان الميزان " (٦/ ١٠٨ / ٣٧٩)، ويحيى بن صاعد " لسان الميزان " (٦/ ١٠٨ / ٣٧٩).
التعديل والتجريح: قال ابن حبان في " الثقات " كان من خيار الناس من جلساء أحمد بن حنبل، وبشر الحافي، مستقيم الحديث ".
وقال الأزدي: " منكر الحديث ". وقال الدارقطني: " ثقة نبيل. "
وانظر: " الثقات " (٩/ ٢٠٤ / ١٦٠٢٧)، و" ميزان الاعتدال " (٦/ ٥٣٣ / ٨٨٤٢)، و" لسان الميزان " (٣/ ٢٥٢ / ١٠٩٩ و٦/ ١٠٨ / ٣٧٩) ..',NULL,'623',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (626,'٣٤٤ - ـ أبو بكر القواس (٢٥٧٢٦)
طب: (تس: ٣ ر / ٣ ش)
أبو بكر ـ وقيل: أبو محمد، ـ موسى بن إسحاق، الجبائي، ـ وقيل: الحبئي، وقيل: الكناني، ـ المعروف ب (ابن القواس)، الكوفي، الضرير، توفي بعد سنة تسع وخمسين ومائتين، من الحادية عشرة، محله الصدق.
روى عن: يحيى بن عيسى الرملي (تس ٢٥٧٢٦ و٢٩٦٧٦ و٢٩٦٧٧)، وحفص بن غياث، وزيد بن الحباب " المعجم الكبير " (٦/ ٢٢٨ / ٦٠٧٠)، و" المقتنى في سرد الكنى " (٢/ ٥٥ / ٦٠٧٠)، وعبد الحميد الحماني، وعبد الله بن نمير، وعصام بن علي، وعثمان بن عبد الرحمن " الكامل " (٢/ ١٣٦)، ووكيع بن الجراح.
روى عنه: أبو أسيد أحمد بن محمد بن أسيد " تكملة الإكمال " (١/ ١٣٢ / ٤٨)، وأبو حامد أحمد بن محمد بن يحيى بن بلال النيسابوري الخشاب " السير " (١٥/ ٢٨٤ / ١٢٧)، وزكريا بن يحيى الساجي " المعجم الكبير " (٦/ ٢٢٨ / ٦٠٧٠)، و" المقتنى في سرد الكنى " (٢/ ٥٥ / ٦٠٧٠)، وعبد الرحمن بن أبي حاتم الرازي، وأبو عوانة " مسند أبي عوانة " (١/ ٦١ / ١٦٠)، وقد أكثر عنه فيه.
التعديل والتجريح: قال ابن أبي حاتم في " الجرح والتعديل " (٨/ ١٣٥ / ٦١٢): " كتبت عنه، ومحله الصدق ".
وذكره الذهبي في " المقتنى في سرد الكنى " (٢/ ٥٥ / ٥٤٩٥)، فيمن كنيته (أبو محمد)، وأكثر عنه أبو عوانة في " المسند "، فأخرج عنه نحو أربعين رواية.
وأغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري ".
ووقفت له على أكثر من موضع، ذكر فيه اسمه في كتب التراجم، أثبت من خلالها كنيته، ووفاته، ولقبه، ونسبه، وبلده، وبعض شيوخه وتلاميذه. وقد تحرف اسمه وتصرف فيه النساخ كثيرا.
ففي الأثر رقم (تس: ٢٥٧٢٦)، قال الطبري: " حدثنا علي بن سهل بن موسى بن',NULL,'624',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (627,'إسحاق الكناني، وابن القواس قالا: ثنا يحيى بن عيسى، عن سفيان، عن ابن أبي نجيح عن مجاهد في قول الله: {وليشهد عذابهما طائفة من المؤمنين}. قال: (الطائفة: رجل). قال علي: (فما فوق ذلك). وقال ابن القواس: (فأكثر من ذلك) ". انتهى.
وفي الأثر رقم (تس: ٢٩٦٧٦)، قال الطبري: " حدثني موسى بن إسحاق الجبائي ـ وفي نسخة دار الكتب العلمية (الحبئي)، بدل (الجبائي) ـ المعروف ب (ابن القواس)، قال: ثنا يحيى بن عيسى الرملي، عن الأعمش، عن أبي يحيى، عن مجاهد عن ابن عباس قال: " لو أن قطرة من زقوم جهنم أنزلت إلى الدنيا لأفسدت على الناس معايشهم، وإن ناركم هذه لتعوذ من نار جهنم ".
وفي الأثر رقم (تس: ٢٩٦٧٧)، قال الطبري: " حدثنا موسى بن إسحاق قال: ثنا يحيى بن عيسى، عن الأعمش، عن زيد بن وهب، قال: قال عبد الله بن مسعود: " إن ناركم هذه لما أنزلت، ضربت في البحر مرتين ففترت، فلولا ذلك لم تنتفعوا بها ".
قلت: فيصير الطبري بذلك راويا عن شيخين في الأثر الأول هما: (علي بن سهل بن موسى بن إسحاق الكناني)، و (ابن القواس)، وهو خطأ لا ريب فيه. يظهر جليا من استقراء المواضع التي ذكر فيها في كتب الرجال والتراجم والحديث، وأما الأثر الثاني؛ فلا شك أنه تحرف في الطبعتين من الكناني. والصواب أن الطبري، روى هذا الأثر عن شيخين هما:
الأول: (علي بن سهل، وهو ابن قادم الرملي)، وقد مضت ترجمته برقم (١٩٦).
والثاني: (موسى بن إسحاق الكناني بن القواس)، صاحب الترجمة وذلك لما يلي:
١ - ـ لا أجد في الرواية من اسمه (علي بن سهل بن موسى بن إسحاق الكناني)، مع كثرة البحث والتنقيب، وأقرب من يمكن أن يكون بهذا الاسم هو (علي بن سهل بن قادم)، وقيل: (ابن موسى الجرشي الرملي أبو الحسن)، أخو (موسى بن سهل)، ولعله المراد، لأن شيخه فيه وهو (يحيى بن عيسى)، رملي هو الآخر، وقد ذكره ابن حبان في " الثقات " (٨/ ٤٧٥ / ١٤٥٠٧)',NULL,'625',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (628,'ممن يروي عنه علي هذا. وقد ذكرت أسماء من اسمه (علي بن سهل)، وهم ثلاثة في ترجمة (علي)، برقم (٢٠٨)، من كتابي هذا، وتمييز بالرقمين (٨٠ و٨١).
٢ - ـ ذكر المزي في " تهذيب الكمال " (٣١/ ٤٨٨ / ٦٨٩٦)، في ترجمة يحيى بن عيسى بن عبد الرحمن التميمي النهشلي أبو زكريا الكوفي الجرار الفاخوري)، الذي سكن الرملة فنسب إليها وكان يختلف إلى العراق ـ ذكر ـ (موسى بن إسحاق الكناني القواس الكناني)، ضمن من روى عن (يحيى بن عيسى).
٣ - ـ وكذلك ذكره الذهبي في " السير " (١٥/ ٢٨٤ / ١٢٧)، في ترجمة (ابن بلال الخشاب)، أنه سمع بالكوفة من (موسى بن إسحاق القواس الكناني)، وسماعه منه في سنة تسع وخمسين ـ يعني: ومائتين ـ فاتفق المزي، مع الذهبي في الاسم، والنسبة، واللقب.
٤ - ـ وقد ذكر البيهقي قبلهما في " شعب الإيمان " (٦/ ٤٩٧ / ٩٠٤١)، رواية (ابن بلال)، هذا فقال: " نا أبو محمد، موسى بن إسحاق الكناني، بالكوفة، سنة تسع وخمسين ومائتين ". فكناه ولكنه لم يذكر (القواس)، فاتفقا في الاسم والبلد، والنسبة، وسنة السماع، وانفرد كل من (المزي)، و (الذهبي)، بذكر اللقب، و (البيهقي)، بذكر الكنية.
٥ - ـ التوافق بين أسماء من روى عنهم، أو رووا عنه، وإن اختلفت كنيته أو نسبته في بعض المواضع. فقد كناه أبو عوانة في " مسنده " (٣/ ٥٩ / ٤١٨٣) (أبا محمد)، وكناه محمد بن عبد الغني في " تكملة الإكمال " (١/ ١٣٢ / ٤٨) (أبا بكر).
وانظر: " الكامل " (١/ ٢٧٩ / ١١٨)، و (٢/ ١٣٦)، و (٥/ ١٦٧)، و (٧/ ٢١٣)، و" المعجم الكبير " (٦/ ٢٢٨ / ٦٠٧٠) و" شعب الإيمان " (٦/ ٤٩٧ / ٩٠٤١)، و" تكملة الإكمال " (١/ ١٣٢ / ٤٨)، و" تهذيب الكمال " (١٠/ ٤٠ / ٢٠٩٥)، (٣١/ ٤٩٠ / ٦٨٩٦)، و" ميزان الاعتدال " (٧/ ١٧١).
٣٤٥ - ـ أبو عمران الرملي (٨٧٩)
(أبو داود، والنسائي في اليوم والليلة)
(طب: ٣٠ ر / ١٧ ش)',NULL,'626',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (629,'(تس: ١٧ ر / ١١ ش)، و (تهـ: ٨ ر / ٧ ش)، و (تق: ٢ ر / ٢ ش)
و (تخ: ١ ر / ١ ش)، و (صر: ٢ ر / ٢ ش)
أبو عمران، موسى بن سهل بن قادم، الجوني، الرملي، الصغير، نسائي الأصل: أخو علي بن سهل الرملي، توفي في جمادى الأولى، سنة اثنتين وستين ومائتين، من الحادية عشرة، ثقة.
روى عن: آدم بن أبي إياس العسقلاني (تس ١٥١٤٨ و١٦٩٥٩ و٢٠٥٠٣)، و (تق ١٨٦) و (سي)، وأبي النضر إسحاق بن إبراهيم الفراديسي (تس ٨٧٨٩). وإسحاق بن عبد الواحد الموصلي (تس ٥٤٣٧)، وإسماعيل بن مسلمة القعنبي (تهـ ٢٣٢٢)، وعبد الله بن السري الأنطاكي (تس ١٠٠٥٠)، وعبد الله بن صالح (صر ٢٣)، وعبد العزيز بن عبد الله الأوسي (تهـ ٢٥٣١)، وعمرو بن هاشم البيروتي (تس ٢٢٢٨٦ و٣٧٥١٣)، ومجمع بن يعقوب الأنصاري (تق ١٠٠٣)، ومحمد بن بكار البصري (تس ٢٢٨٠٥)، ومحمد بن عبد الله (تس ٦٦٣٤)، ومحمد بن عبد العزيز (تهـ ١٢٧٧)، ومحمد بن عيسى بن الطباع (تس ٣١٤٦٣)، و (تهـ ١٧١٣)، وموسى بن داود الضبي (تس ٢٣٤٠٧)، و (تهـ ٢٢٢١) و (صر ١٥)، ونعيم بن حماد المروزي (تس ٨٧٩ و٩٦٧٣ و٢٤٢٢٠ و٣٤٥٤٥) و (تهـ ٣١٢ و٣٥٢)، و (تخ ١/ ٢٨)، ويحيى بن واضح (تس ٨٣٩٦)، وابن أبي فديك (تهـ ١٣٧١).
روى عنه: أحمد بن عمير بن يوسف " صحيح ابن حبان " (١٣/ ١١٣ / ٥٧٩٦).
التعديل والتجريح: " تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقا.
قلت: وقد أخرج الطبري عن (موسى بن سهل الرازي)، الأثر رقم (تس ١٨٠)، ولا أدري إن كان هو المراد، أو أنه مقلوب من (سهل بن موسى الرازي)؟! فكلاهما ترجمت لهما في موضعيهما.
وانظر: " الجرح والتعديل " (٨/ ١٤٦ / ٦٦٠)، و" تهذيب الكمال " (٢٩/ ٧٥ / ٦٢٦٤)، و" الكاشف " (٢/ ٣٠٤ / ٥٧٠٢) و" سير أعلام النبلاء " (١٢/ ٢٤٢ / ٨٦)، و" تاريخ دمشق " (٦٠/ ٤١٤ / ٧٧٢٣)، و" تهذيب التهذيب " (١٠/ ٣٠٩ / ٦١٨)، و" تقريب التهذيب " (١/ ٥٥١ / ٦٩٧٢)، و" رجال تفسير الطبري " (٥٤٨/ ٢٦٣١)',NULL,'627',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (630,'٣٤٦ - ـ أبو هارون الراسبي (١٨٠)
(طب: (تس: ١ ر / ١ ش)
أبو هارون، موسى بن سهل بن هارون، الراسبي، الرازي، توفي سنة ثمان وسبعين ومائتين، من الحادية عشرة، منكر الحديث.
روى عن: يحيى بن عوف المرادي (تس ١٨٠)، وإسحاق بن يوسف الأزرق.
روى عنه: دعبل بن علي الخزاعي الشاعر الرافضي، ومحمد بن عبد الرحيم المعروف ب (بنان)، المصري، وأبو بكر الشافعي " الإرشاد " (٢/ ٥٠٣ / ٢١٨).
وكل اسم لم يعز إلى كتاب، من معجم شيوخه وتلاميذه، وميزته بالأسود العريض فهو من ترجمته في " تاريخ بغداد ".
التعديل والتجريح: قال الخطيب البغدادي في " تاريخ بغداد " (١٣/ ٣٢ / ٦٩٨٨): " أحمد المجهولين. روى عن دعبل بن علي الشاعر، عنه، عن أبي إسحاق حديثا. أخبرناه أبو الحسين، زيد بن جعفر بن الحسين العلوي المحمدي، حدثنا أبو عبد الله، محمد بن وهبان الهنائي البصري، حدثنا إسماعيل بن علي بن علي بن رزين الخزاعي بواسط، حدثنا أبي، حدثنا أخي دعبل، قال: حدثني موسى بن سهل الراسبي، في دهليز محمد بن زبيدة، حدثنا أبو إسحاق، عن أبي الأحوص، عن عبد الله بن مسعود قال: قال رسول الله صلى الله عليه وسلم:» من أحبني، فليحب عليا، ومن أبغض عليا، فقد أبغضني، ومن أبغضني فقد أبغض الله عز وجل، ومن أبغض الله، أدخله النار «. قلت: هذا الحديث موضوع الإسناد، والحمل فيه عندي على إسماعيل بن علي، والله أعلم. انتهى.
وقال في الموضع الآخر (١٣/ ٤٠ / ٦٩٩٨): " موسى بن سهل، أبو هارون الفزاري، حدث عن إسحاق بن يوسف الأزرق، روى عنه محمد بن عبد الرحيم المعروف ببنان المصري " وقال أيضا بسنده عنه، عن ابن مسعود، قال: قال رسول الله صلى الله عليه وسلم:» ما مولود يولد إلا وفي سرته من تربته التي ولد منها، فإذا رد إلى أرذل العمر، رد إلى تربته التي خلق',NULL,'628',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (631,'منها، حتى يدفن فيها وأنا، أبو بكر، خلقنا من تربة واحدة، وفيها ندفن ". «
قال الدارقطني في " الضعفاء والمتروكين " (١٦٣/ ٥٢٢)، عن إسماعيل ابن علية: " حدثونا عنه هو غير ابن عبد الحميد الجوني. الجوني صالح الحديث.
وقال الخليلي في " الإرشاد " (٢/ ٥٠٣ / ٢١٨): " ليس بذاك المشهور، تأخر موته، سمع منه المتأخرون، مثل أبي بكر الشافعي وأقرانه ".
قال ابن الجوزي في " الضعفاء والمتروكين " (١/ ١٤٦ / ٣٤٥١): قال الدارقطني: " ضعيف " ذكره الذهبي في " المغني " في موضعين؛ الأول: (٢/ ٦٨٣ / ٦٤٩٤)، وقال فيه: عن إسحاق الأزرق بخبر باطل، عن الثوري، عن أبي إسحاق، عن أبي الأحوص، عن عبد الله، رضي الله عنه، مرفوعا:» خلقت أنا، وأبو بكر، وعمر، من تربة واحدة، فيها ندفن «. رواه عنه نكرة مثله. انتهى.
وقال في الموضع الأخر (٢/ ٦٨٤ / ٦٤٩٥): " مشهور ضعفه الدارقطني ".
وقال في " الميزان " (٤/ ٢٠٦ / ٨٨٧٢)، و (٤/ ٢٠٦ / ٨٨٧٣): " بخبر باطل، لا يعرف، والراوي عنه دعبل الخزاعي ".
وقال العسقلاني في " لسان الميزان " (٦/ ١٢٠ / ٤١٤): " موسى بن سهل بن هارون الرازي، عن إسحاق الأزرق ـ بخبر باطل عن الثوري، عن أبي إسحاق، عن أبي الأحوص عن عبد الله، رضي الله عنه، مرفوعا:» خلقت أنا وأبو بكر وعمر من تربة واحدة فيها ندفن. «رواه عنه نكرة مثله. انتهى.
وقد أخرج الخطيب في " تاريخه ". وذكره إلا أنه أخر قوله: موسى بن سهل أحد المجهولين. إلى آخر الترجمة.
ثم قال في الموضع الآخر معلقا على ذلك (٦/ ١٢٠ / ٤١٤): " وهذا هو الذي قبله، فالسند واحد ولا منافاة من كونه راسبيا، أو رازيا، لأن الأولى إلى قبيلة والثانية إلى بلد ولو سمى الراوي غير هذا، لازداد الأمر وضوحا ". ا. هـ "',NULL,'629',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (632,'قلت: والذي قبله هو موسى بن سهل الراسبي وهو نفسه الذي نسبه الخطيب رازيا كما تقدم.
وقال العسقلاني في " التهذيب " (١٠/ ٣١٠ / ٦١٩) ـ في ترجمة موسى بن سهل الحرقي الوشاء البغدادي ـ: " وممن يسمى موسى بن سهل، اثنان، أحدهما: الراسبي، شيخ لدعبل الشاعر لا يعرف، والآخر: رازي، اسم جده: هارون، روى عن إسحاق الأزرق. ذكره في الميزان. وقال البرقاني: ضعيف جدا ". انتهى.
قلت: وقد سبق ترجيح الحافظ أنهما واحد، ولعل ما في " التهذيب " سابق لما في " لسان الميزان ". والله أعلم.
وقال فيه التقريب (١/ ٥٥١ / ٦٩٧٣): " ضعيف من صغار العاشرة، مات سنة ثمان وسبعين. تمييز ". ا. هـ.
وذكره الشيخ الحلاق في نقله عن الشيخين أحمد ومحمود شاكر في " رجال تفسير الطبري " (٥٤٧/ ٢٦٣٠)، وقال " موسى بن سهل الرازي = سهل بن موسى الرازي. تقدم "
ثم وجدته عنده في (ص: ٢٥٥/ ١١٢٩)، حيث قال " سهل بن زنجلة = سهل بن أبي سهل = سهل بن موسى الرازي " (موسى بن سهل الرازي)، هكذا جاء في المطبوعة ولكنه في المخطوطة (سهل بن موسى الرازي)، فرجح أخي السيد أحمد أنه خطأ من الناسخ وأنه لم يجد له ترجمة. ولكن أبا جعفر الطبري قد روى عنه في مواضع من تاريخه: (سهل بن موسى الدافري)، وهكذا هو في المخطوطة والمطبوعة فالصواب أن يكون في رقم (١٨٠) (سهل بن موسى الرازي)، كما في المخطوطة هناك. و (سهل بن موسى الرازي)، لم يترجم بهذا الاسم في الكتب ولكني رأيت الطبري يروي عنه في التاريخ (١/ ١٦٩) (حدثنا سهل بن موسى الرازي قال حدثنا بن أبي فديك ... )، فالذي في التاريخ يؤيد ما في التفسير. ثم روى عنه في التاريخ (٢/ ٢١٤) (حدثنا سهل بن موسى الرازي)، قال حدثنا عبد الرحمن بن مغراء
)، فرأيت في ترجمة (عبد الرحمن بن مغراء)، في التهذيب أنه يروي عنه (سهل بن زنجلة) و (سهل بن',NULL,'630',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (633,'زنجلة)، هو: سهل بن أبي سهل الرازي، روى عن جماعة كثيرة، منهم: يحيى بن سعيد القطان، وسفيان بن عيينة، وعبد الرحمن مغراء. وروى عنه ابن ماجه فأكثر، وأبو حاتم، وقدم بغداد سنة ٢١٣. وترجم له الخطيب البغدادي في " تاريخ بغداد " (٩/ ١١٦ ـ ١١٨) [٤٧٢٧]، ولم يذكروا تاريخ وفاته، فأخشى أن يكون (سهل بن أبي سهل الرازي)، هو (سهل بن موسى الرازي)، نفسه ـ لم يعرفوا اسم أبيه (موسى)، وعرفه الطبري لأنه من ناحية بلاده، وأرجو أن يأتي بعد في أسانيد أبي جعفر ما يكشف عن الحق في ذلك. انتهى.
قلت: أما سهل بن موسى الرازي فقد مضت ترجمته برقم (١٤٣)
وانظر: " تفسير الطبري تحقيق الشيخ شاكر " (١/ ١٧٤ / ١٨٠)
٣٤٧ - ـ أبو عيسى الكندي (١٧٤)
(الترمذي، والنسائي، وابن ماجه)
(طب: ٦٨ ر / ١١ ش)
(تس: ٥٥ ر / ١٠ ش) (تخ: ٦ ر / ٦ ش) (تهـ: ٤ ر / ٣ ش) (تق: ٣ ر / ٣ ش)
أبو عيسى موسى بن عبد الرحمن بن سعيد بن مسروق الكندي المسروقي الكوفي، توفي سنة ثمان وخمسين ومائتين، من كبار الحادية عشرة، ثقة.
روى عن: إسحاق بن الربيع العصفري (تهـ ٦٤٠)، وجعفر بن عون (تس ٩٥٠٨ و١٤٣٤١ و٢١٤٤٦ و٣٤٦٨٥) و (تخ ٢/ ٥٧) و (س)، وحسين بن علي الجعفي (تس ١٧٤ و٤٤١٨ و٤٤٧٠ و٧٢٨٤ و١٣٢٧٣ و١٣٣٧٢ و١٦٠٢٨ و٣٧٢٣٦)، و (تق ٤٥٤) و (س، ق)، وزيد بن الحباب (تس ٣٣٥٠ و٦١٢١ و٨٩٠٧ و١٤٣٦٨ و١٦٦٩٩ و١٨١٩٦ و٢١٦٧٩ و٢٤٧٦٠ و٣٠٩٨٧ و٣١٥٤٣ و٣٢٧٣٩ و٣٣٥٠١)، و (تخ ١/ ١١٧)، و (ت، س، ق)، وأبي يحيى عبد الحميد بن عبد الرحمن الحماني (تس ٢٧١٣١ و٣٥٢٢٦ و٣٦٣٣٥ و٣٧٧٢١)، و (تق ٨٢٣)، و (تخ ٣/ ١٦٥)، و (ت)، وأبي عبد الرحمن عبد الرحمن الخزاعي الحراني (تخ ٣/ ١٥٥)، وعبيد بن الصباح الخزاز (تس ٤٧٨١)، و (تخ ٣/ ٩٩)',NULL,'631',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (634,'وعمرو بن محمد العنقزي (تس ٦٩٢٦)، ومحمد بن بشر العبدي (تس ٦٢٩ و٩٩٨٤ و٧٨٤٠ و٤٢٢٥ و٣٠٩٣٩ و٣٢٣٥٤ و٣٣١٤٣ و٣٦٤١٤ و٣٧١٨٧ و٣٧٧٤٥) و (تهـ ١٧٧٣) و (تق ٨٥٠) و (س)، ويحيى بن زكريا بن إبراهيم بن سويد النخعي (تس ٢٨٤٩٠)، وأبي أسامة (تس ٩٨٤٤ و٢٢٥٦٩ و٢٣٠٠٣ و٢٣٣٧١ و٢٤٣٦٥ و٢٦٣١٨ و٢٩٦٨٢ و٣٠٣٣٥ و٣٢٤٨٧ و٣٣٠١٧ و٣٣٧٩٠ و٣٣٨٠٠ و٣٤٧٧٦ و٣٤٧٨١)، و (تهـ ١٦٨٢ و١٩٤٩)، و (تخ ٢/ ١٧٠)، و (س).
روى عنه: أبو عروبة (الحسين بن محمد الحراني) " الكاشف " (٢/ ٣٠٥ / ٥٧١٣)، وعبد الله بن علي بن الجارود " المنتقى " (١
/ ١٦٣)، وابن منده " المستخرج " (٣/ ٨٨ / ٢٢٦٣).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة صدوقا، لا بأس به.
قلت: وقع تحريف في الأثر رقم (تس ٣٢٣٥٤)، من " التفسير " في اسم شيخه (محمد بن بشر)، فصار (موسى بن بشر)، والصحيح ما أثبته.
وقد تردد الطبري في الأثر رقم (تخ: ٣/ ١٦٥)، من " التاريخ " في اسم شيخه فقال: " حدثني موسى بن عبد الرحمن المسروقي قال: حدثنا عثمان بن عبد الحميد ـ أو ابن - عبد الرحمن الحراني الخزاعي، أبو عبد الرحمن .. "
وقد تحرف اسمه في الأثرين رقم (١٣٢٧٣ و٣٦٤١٤)، من طبعة العلمية فقط، فصار (محمد بن موسى بن عبد الرحمن الكندي). وفي الأثر رقم (٣٦٤٠٧)، صار (محمد بن عبد الرحمن)، والصحيح ما أثبته، والله تعالى أعلم.
وانظر: " التاريخ الكبير " (١/ ٥٧٩ / ٢٣٦٣)، و" الجرح والتعديل " (٨/ ١٥٠ / ٦٨٢)، و" الثقات " (٩/ ١٦٤ / ١٥٧٩١)، و" تهذيب الكمال " (٢٩/ ٩٨ / ٦٢٧٨)، و" الكاشف " (٢/ ٣٠٥)، و" المقتنى في سرد الكنى " (١/ ٤٤٦ / ٤٨٧٢)، وتهذيب التهذيب (١٠/ ٣١٧ / ٦٣٤)، و" تقريب التهذيب " (١/ ٥٥٢ / ٦٩٨٧)، و" رجال تفسير الطبري " (٥٤٩/ ٢٦٣٢).
..
٣٤٨ - ـ موسى بن هارون (١٦٨)',NULL,'632',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (635,'(طب: ٤٤٧ ر / ١ ش)
(تس: ٤٠٠ ر / ١ ش)، و (تخ: ٤٧ ر / ١ ش)
موسى بن هارون بن إسحاق، الهمداني، الكوفي، من الحادية عشرة، لم أعرفه، ولم أجد له ترجمة، ولم يعرفه الشيخ شاكر قبلي (١٦٨)، ولم ير حاجة للبحث فيه من حيث " الجرح والتعديل "، كونه يروي كتابا معروفا عند أهل الحديث، وإنما تكلم فيمن فوقه من رجال السند، ولم يتعرض الشيخ التركي في تحقيقه " لتفسير الطبري " (١/ ١٥٨)، لترجمته بشيء، ونقل عن الحاكم (٢/ ٢٨٤ / ٣٠٢٢)، تصحيح هذا الإسناد على شرط مسلم، وقد روى هذا الطريق في " التفسير " أكثر من (٤٠٠)، مرة، وفي التاريخ نحو (٥٠)، مرة، من الطريق السدي.
روى عن: عمرو بن حماد القناد (تس ١٦٨ و١٨٢ و٢٠١ و٢١٧ و٢٤٠ و٢٥٤ و٢٠٦ و٢٦٣ و٢٧٣ و٢٧٧ و٢٨٨ و٢٩٠ و٢٩٢ و٣٠٨ و٣١٧ و٣٢٤ و٣٣٠ و٣٣٩ و٣٤٤ و٣٥١ و٣٦٤ و٣٦٧ و٣٧١ و٣٨١ و٣٨٨ و٤٠٠ و٤٠٣ و٤٥٢ و٤٧٣ و٤٧٥ و٤٧٨ و٤٨٢ و٥٠٠ و٥١٢ و٥٢٨ و٥٥٤ و٥٦٧ و٥٦٨ و٥٧٦ و٥٩١ و٦٠٥ و٦٠٧ و٦١٩ و٦٤٤ و٦٧٢ و٦٧٩ و٦٨٨ و٧٠٤ و٧٠٨ و٧١٠ و٧١١ و٧٣١ و٧٤٣ و٧٥٥ و٧٦٧ و٧٧٠ و٧٨٠ و٨٠٧ و٨١٣ و٨٢١ و٨٣٦ و٨٤٢ و٨٦٥ و٨٧٥ و٨٨٣ و٨٩١ و٨٩٦ و٩٠١ و٩٠٥ و٩١١ و٩١٣ و٩١٨ و٩٢٠ و٩٣٨ و٩٥٤ و٩٥٦ و٩٥٩ و٩٧٤ و٩٨٠ و٩٨١ و٩٩٢ و١٠٠١ و١٠٠٦ و١٠٣٠ و١٠٥٠ و١٠٦١ و١٠٦٩ و١٠٨٣ و١١١٣ و١١٣١ و١١٦٤ و١١٩٧ و١٢٠٧ و١٢٨٢ و١٤٢٢ و١٤٤٧ و١٤٧٥ و١٤٨٩ و١٥٣٧ و١٥٦٧ و١٦١٦ و١٦٤٦ و١٦٤٩ و١٦٨٩ و١٧١٣ و١٧٤٩ و١٧٨٢ و١٨٢١ و١٩٣٩ و٢٠٠٨ و٢٠١١ و٢٠٥٣ و٢٠٥٥ و٢٠٦٤ و٢١٢٧ و٢٢٠٦ و٢٢٢٩ و٢٢٣٨ و٢٢٦١ و٢٢٦٢ و٢٢٦٨ و٢٢٨٤ و٢٣٠١ و٢٣١١ و٢٣١٤ و٢٣٤٠ و٢٤٠٣ و٢٤٢٤ و٢٤٣٨ و٢٤٤٢ و٢٤٥١ و٢٤٥٣ و٢٤٧٣ و٢٥٠١ و٢٥٠٤ و٢٥٠٦ و٢٥١٥ و٢٥٣٧ و٢٥٧١ و٢٥٩٨ و٢٦١٦ و٢٦٣٣ و٢٦٦٧ و٢٦٧٦ و٢٦٩٢ و٢٧٠٩ و٢٧٢٨ و٢٧٦٤ و٢٨١١ و٢٤١٥ و٢٨٣٠ و٢٩١٧ و٢٩٣٦ و٢٩٤٠ و٢٩٥٧ و٢٩٦٨ و٢٩٧٧ و٢٩٩١ و٣٠٥١ و٣٠٦٤ و٠٧١ و٣٠٧٨ و٣٠٩١ و٣١٢٣ و٣١٣٥ و٣١٤١ و٣١٦٣ و٣٢٠١٦ و٣٢٥٨ و٣٤٣٢ و٣٤٥٩ و٣٦١٦ و٣٧٨٥ و٣٧٩٥ و٣٨٦٩ و٣٨٨٦ و٣٩٠٨ و٣٩٢٤ و٣٩٥٣ و٣٩٦٤ و٣٩٧٤ و٣٩٨١ و٣٩٨٧ و٤٠١٥ و٤٠٣٠ و٤٠٣١ و٤٠٤٧ و٤٠٥٩ و٤٠٦٠ و٤٠٦٦ و٤٠٦٧ و٤٠٧١ و٤٠٨١ و٤٠٨٦ و٤١٢٥ و٤١٣٤ و٤١٣٨ و٤١٥٠ و٤١٦٠ و٤١٧٩ و٤١٩٨ و٤٢٢٨ و٤٢٣٧ و٤٢٣٨ و٤٢٩٩ و٤٣١٩ و٤٤٧٩ و٤٦٥٩ و٤٨٨٩ و٤٩٤٢ و٥٠٤٢ و٥٥٢٤ و٥٦٠٥ و٥٦٢٤ و٥٦٣٨ و٥٦٤١ و٥٦٨١ و٥٧١٠ و٥٧١٨ و٥٧٢٢ و٥٧٣٤ و٥٧٣٦ و٥٧٤١ و٥٧٤٦ و٥٧٥٠ و٥٧٦٩ و٦٧٧٧ و٥٧٨٦ و٥٧٨٧ و٥٧٩١ و٥٨٠٩ و٥٨٢٠ و٥٨٤١ و',NULL,'633',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (636,'٥٨٥٠ - و٥٨٥٦ و٥٨٦٩ و٥٨٧٩ و٥٨٨٩ و٥٩١٠ و٥٩١٤ و٥٩٢٤ و٥٩٣٥ و٥٩٤٧ و٥٩٤٩ و٥٩٦٠ و٥٩٦٨ و٥٩٨٧ و٦٠٠٧ و٦٠٢٠ و٦٠٢٨ و٦٠٤٢ و٦٠٥٠ و٦٠٥٣ و٦٠٥٧ و٦٠٧٧ و٦٠٨٢ و٦٠٨٦ و٦٠٩٠ و٦١١٣ و٦١٢١ و٦١٢٩ و٦١٣٤ و٦١٣٥ و٦١٣٩ و٦١٥٩ و٦١٩٠ و٦٢٠٩ و٦٢١٢ و٦٢١٥ و٦٢١٧ و٦٢٢٣ و٦٢٢٧ و٦٢٤٥ و٦٢٤٨ و٦٢٥٦ و٦٢٦٩ و٦٢٨١ و٦٢٩٩ و٦٣٤٢ و٦٣٤٦ و٦٣٥١ و٦٣٨٧ و٦٣٩٥ و٦٣٩٦ و٦٣٩٧ و٦٤٤٢ و٦٤٤٣ و٦٤٧٦ و٦٥٠١ و٦٥٠٣ و٦٥٠٨ و٦٥١٣ و٦٥٢٧ و٦٥٣٣ و٦٥٦٦ و٦٥٧٣ و٦٥٩٣ و٦٥٩٨ و٦٦١٣ و٦٦٢١ و٦٦٣٧ و٦٦٦٢ و٦٦٧٨ و٦٧١٥ و٦٧٢٤ و٦٧٤٢ و٦٧٤٧ و٦٧٥٧ و٦٧٩٢ و٦٨٠٥ و٦٨٢٣ و٦٨٣٤ و٦٨٣٦ و٦٨٦٧ و٦٨٩٩ و٦٩٣٥ و٦٩٤١ و٦٩٥٤ و٦٩٥٩ و٩٦٩٤ و٦٩٩٦ و٦٩٩٨ و٦٩٩٩ و٧٠١٥ و٧٠٤٤ و٧٠٨٨ و٧٠٩٥ و٧١٠٢ و٧١١٧ و٧١٩١ و٧٣٦٢ و٨٤٠٦ و٩٧٠٧ و١١٥٧٥ و١١٦٣٩ و١١٦٥٧ و١١٦٥٩ و١١٦٧٢ و١١٦٩١ و١١٦٩٧ و١١٧١٨ و١١٧٣٣ و١١٧٤٩ و١١٧٥٧ و١١٨٢١ و١٣٥٤١ و١٤٣٦٥ و١٤٩٢٠ و١٤٩٣١ و١٤٩٤٢ و١٤٩٤٧ و١٤٩٥٣ و١٤٩٦٤ و١٤٩٦٦ و١٤٩٧١ و١٥٠١٤ و١٥٠٢٥ و١٥٠٥٢ و١٥٠٦٩ و١٥٠٨٣ و١٥١١١ و١٥١٣٥ و١٥١٦٢ و١٥١٧٨ و١٥٢٦٨ و١٥٣٨٤ و١٥٥٣٦ و١٨٣١٧ و١٨٣٢٨ و١٨٣٤٢ و١٨٣٥٩ و١٨٣٦٨ و١٨٣٩٥ و١٨٤٢٨ و١٨٤٤٦ و١٨٤٦١ و١٨٤٧٠ و٢١٥٦٦ و٢٣٤٧٩ و٢٣٥١٢ و٢٣٦٥٥ و٢٣٦٦٢ و٢٤٠١٩ و٢٤٠٨٩ و٢٤١٢٣ و٢٤٢١٤ و٢٤٢١٨ و٢٤٣٠٤ و٢٦٦٠٤ و٢٦٦١٣ و٢٦٦١٦ و٢٧١٦٠ و٢٧١٧٦ و٢٨١٠٣ و٢٨٦٩٧ و٢٨٧٧٥ و٢٨٧٧٨ و٢٩٤٧٦ و٣٠٤٥٠ و٣٢٢٠٤)، و (تخ: ١/ ٣٢ و٣٧ و٣٩ و٤٠ و٤١ و٥٦ و٥٩ و٦٢ و٦٤ و٦٧ و٦٩ و٧١ و٨٨ و٩٤ و١٤٤ و١٤٧ و١٤٨ و١٥١ و١٥٣ و١٦٠ و١٦٤ و١٧٠ و١٧٣ و١٧٦ و١٧٧ و١٧٩ و١٨٢ و١٨٤ و١٨٧ و٢٥٠ و٢٥٥ و٢٥٧ و٢٧١ و٢٧٦ و٢٩٦ و٣٢٠ و٣٤٦ و٣٥٢ و٢/ ١٦ و١٨ و٢٦)، وأزهر بن مروان " تفسير ابن كثير " (١/ ٢٧٥)، ومنجاب بن الحارث بن عبد الرحمن التميمي الكوفي " الكامل " (٥/ ١٢٠ / ١٢٨٦)، وأبيه أبي القاسم هارون بن اسحق بن محمد الهمداني الكوفي " تهذيب الكمال " (٣٠/ ٧٥ / ٦٥٠٦)، ويحيى الحماني " تفسير ابن كثير " (٢/ ١٤)
روى عنه: أبو العباس، أحمد بن محمد بن سعيد " الكامل " (٥/ ١٢٠ / ١٢٨٦)، ومحمد بن عبد الله بن نمير " تاريخ بغداد " (١/ ٢١٤ / ٥١)
التعديل والتجريح: ذكره الشيخ شاكر في تحقيقه " لتفسير الطبري " (١/ ١٥٦ / ١٦٨)، وقال: " أما شيخ الطبري، وهو" موسى بن هارون الهمداني " فما وجدت له ترجمة، ولا ذكرا، في شيء مما بين يدي من المراجع، إلا ما يرويه عنه الطبري أيضا في " تاريخه " وهو أكثر من خمسين موضعا في الجزئين الأول والثاني منه. وما بنا حاجة إلى ترجمته من حيث',NULL,'634',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (637,'" الجرح والتعديل "، فإن هذا التفسير الذي يرويه عن عمرو بن حماد، معروف عند أهل العلم بالحديث. وما هو إلا رواية كتاب لا رواية حديث بعينه ". ا. هـ
قلت: وهناك آلاف الروايات عن (موسى بن هارون)، عند ابن خزيمة، وابن حبان ومن بعدهما، الطبراني، وأبي نعيم الأصبهاني، والدارقطني، والبيهقي، وغيرهم، ولكن من غير أن ينسبوه وأغلب الظن أنه هو.
وقد وقفت على تراجم كاملة لأبيه هارون، وهو: ثقة، صدوق، متعبد، من صغار العاشرة، ويظهر من ترجمته أن الإمام الطبري قد روى عنه أيضا، فيكون (موسى)، وأبوه (هارون)، من شيوخ الطبري.
ولكون هذا الإسناد الذي بسببه ترجمت له كثير الدوران في " تفسير الطبري " وكذلك في تاريخه " ولأن من (اسمه موسى بن هارون)، أكثر من واحد في نفس الطبقة، وقد اشتركوا في بعض شيوخهم، وتلاميذهم، سأسردهم وبعضا من تراجمهم للتمييز ..
١٧٧ - – تمييز أبو عمر، موسى بن هارون بن بشير، القيسي، البني، البردي، المدني الكوفي: توفي سنة ٢٢٤، من العاشرة، ثقة صدوق، لا بأس به، ربما أخطأ أخرج له البخاري مقرونا بغيره، وأبو داود، والنسائي.
وانظر: " الجرح والتعديل " (٨/ ١٦٧ / ٧٣٨)، و" الثقات " (٩/ ١٦٠ / ١٥٧٧٢) و" التعديل والتجريح " (٢/ ٧١٠ / ٦١٦)، و" تهذيب الكمال " (٢٩/ ١٦٢ / ٦٣١١)، و" الكاشف " (٢/ ٣٠٩ / ٥٧٤٠)، و" تهذيب التهذيب " (١٠/ ٣٣٥ / ٦٦٨) و" تقريب التهذيب " (١/ ٥٥٤ / ٧٠٢١) و" لسان الميزان " (٦/ ١٣٤ / ٤٦٦).
١٧٨ - – تمييز أبو عيسى، موسى بن هارون بن عمرو، الطوسي، البغدادي: توفي سنة ٢٨١، وكان ثقة.
وانظر: " تاريخ بغداد " (١٣/ ٤٨ / ٧٠١٥)، و" الجرح والتعديل " (٨/ ١٦٧ / ٧٣٩)',NULL,'635',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (638,'١٧٩ - تمييز موسى بن هارون بن عبد الله، الحمال، البغدادي، البزاز، محدث العراق، مولده سنة ٢١٤، وتوفي سنة ٢٩٤، من صغار الحادية عشرة، الإمام، الحجة، الثقة، الحافظ الكبير، أحد المشهورين بالحفظ، ومعرفة الرجال.
وانظر: " تاريخ بغداد " (١٣
/ ٧٠١٩)، و" تذكرة الحفاظ " (٢/ ٦٦٩ / ٦٨٩)، و" تقريب التهذيب " (١/ ٥٥٤ / ٧٠٢٢)
١٨٠ - تمييز أبو عمران، موسى بن هارون بن برطق، المكاري، البغدادي، توفي سنة تسع وتسعين ومائتين.
وانظر: " تاريخ بغداد " (١٣/ ٥٤ / ٧٠٢٥).
١٨١ - تمييز موسى بن هارون بن سعيد، التوزي، كان يسكن (سر من رأى)، توفي سنة (٣٠٥)، خمس وثلاثمائة. وانظر: " تاريخ بغداد " (١٣/ ٥٦ / ٧٠٢٨)
١٨٢ - تمييز أبو عمران موسى بن هارون بن سعيد الأصبهاني.
وانظر: " طبقات المحدثين بأصبهان " (٤/ ١٨٦ / ٦٠٩) ..
١٨٣ - تمييز موسى بن هارون، الخراساني، مجهول.
وانظر: " الجرح والتعديل " (٨/ ١٦٦ / ٧٣٧)، و" ميزان الاعتدال " (٤/ ٢٢٥ / ٨٩٣٦)، و" لسان الميزان " (٦/ ١٣٤ / ٤٦٥).
٣٤٩ - أبو هشام اليشكري (١٠٣٨٢)
(البخاري، وأبو داود، والنسائي)
(طب: (تس: ٣ ر / ١ ش)',NULL,'636',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (639,'أبو هشام، مؤمل بن هشام، اليشكري، البصري: - ختن إسماعيل ابن علية وقد أكثر عنه، وكان راويته - توفي سنة ثلاث وخمسين ومائتين، من العاشرة، ثقة.
روى عن: إسماعيل ابن علية (تس ١٠٣٨٢ و ١٠٨٧١ و ١٤٢١٠)، و (خ، د، س)
روى عنه: أبو بكر أحمد بن عمرو بن عبد الخالق البزاز " مسند البزاز " (٢/ ٢٤ / ٣٦٤)، وأبو طلحة، أحمد بن محمد بن عبد الكريم الفزاري " سنن الدارقطني " (٣/ ٤ / ٦)، وأحمد بن يوسف بن الضحاك " المستخرج " (٢/ ١٩ / ٨٩٧)، وعبدان بن أحمد " المعجم الكبير " (٧/ ٣١٥ / ٧٢٣٩)، ومحمد بن صالح بن الوليد النرسي " المعجم الكبير " (٢٢/ ٤٠٥ / ١٠١٣)، والهيثم بن خلف الدوري " المعجم الكبير " (١٢/ ٢٦٩ / ١٣٠٨٠)، وأبو عيسى الختلي " المستخرج " (١/ ٢٢١ / ٣٩٧)
التعديل والتجريح: قال ابن أبي حاتم: " سئل أبي عنه، فقال: صدوق ".
وذكره ابن حبان في كتاب " الثقات ".
قال أبو داود، والنسائي، ومسلمة بن القاسم: " ثقة ".
وانظر: " الجرح والتعديل " (٨/ ٣٧٥ / ١٧١٤)، و " الثقات " (٩/ ١٨٨ / ١٥٩٢٠)، و " من روى عنهم البخاري في الصحيح " (١/ ٢١٣/ ٢٦٦)، و " تسمية من أخرجهم البخاري ومسلم " (١/ ٢٣٨/ ١٧١٤)، و " رجال صحيح البخاري " (٢/ ٧٤٣)، و " التعديل والتجريح " (٢/ ٧٦٦ / ٧٢٠)، و " تهذيب الكمال " (٢٩/ ١٨٦/ ٦٣٢٣)، و " الكاشف " (٢/ ٣١٠/ ٥٧٥٠)، و " المقتنى " (٢/ ١٢٧/ ٦٣٩٥)، و " تهذيب التهذيب " (١٠/ / ٣٤٢/ ٦٨٧)، و " تقريب التهذيب " (١/ ٥٥٥ / ٧٠٣٣)، و " رجال تفسير الطبري " (٤٦٩/ ٢٢٠٦)
************

<span data-type="title" id=toc-38>[١٧] باب النون </span>(من اسمه نجيح)
٣٥٠ - أبو إبراهيم الزهري (٨٨٧)
(طب: ٢ر / ١ش)
(تس: ١ر / ١ش)، و (تهـ: ١ر / ١ش)
أبو إبراهيم، نجيح بن إبراهيم بن محمد بن الحسين، الزهري، القرشي مولاهم الكوفي، الكرماني، - وقيل: الرماني. وقيل: الزماني. وقيل: الجماني -: من الحادية',NULL,'637',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (640,'عشرة، ثقة، كان يتفقه! ويُغرِبُ. وضعفه مسلمة بن القاسم.
روى عن: علي بن حكيم (تس ٨٨٧)، (تهـ ١٦٠١)، وسعيد بن عمر " علل أحمد " (٤/ ١٠٧ / ٤٥٨)، وشريح بن مسلمة التنوخي " مسند ابن أبي أوفى " (١/ ١٤٣ / ٤٥)، ومعمر بن بكار السعدي " الجرح والتعديل " (٨/ ٢٥٩ / ١١٧٤)، وأبي بكر بن أبي شيبة " تاريخ بغداد " (٥/ ٣٣٨ / ٢٨٥٨)، وضرار بن صرد " تهذيب الكمال " (٢٤/ ٢١٨ / ٤٩٩٦)، وأبي نعيم.
روى عنه: إبراهيم بن محمد بن علي بن بطة " أحاديث الخلاف " (٢/ ٣٣٧ / ١٨٥٤)، وابنه أبو القاسم إبراهيم بن نجيح - وهو أشهر من أبيه وأوثق - " تاريخ بغداد " (٦/ ١٩٨ / ٣٢٥٥)، وبلبل بن هارون الديرعاقولي " تاريخ بغداد " (٧/ ١٣٣ / ٣٥٧٥)، وخيثمة بن سليمان الأطرابلسي " تهذيب الكمال " (٢٤/ ٢١٨ / ٤٩٩٦)، وعبد الله بن عمر بن البازيار " تاريخ بغداد " (١٠/ ٢٣ / ٥١٣٩)، وأبو العباس بن سعيد " تاريخ بغداد " (٥/ ٣٣٨ / ٢٨٥٨)، وعلي بن عبد الله البازيار " علل أحمد " (٤/ ١٠٧ / ٤٥٨)، وعلي بن أحمد بن مروان " الكامل " (٢/ ٦٦ / ٢٩٩)، ومحمد بن جعفر المطيري " الكامل " (٢/ ١٨٥ / ٣٧٠)، و [محمد بن عبد الرحمن] الدغولي.
وكل اسم لم يعز إلى كتاب، من معجم شيوخه وتلاميذه، وميزته بالأسود العريض فهو من ترجمته في " ثقات ابن حبان "
التعديل والتجريح: ذكره ابن حبان في " الثقات " (٩/ ٢٢٠ / ١٦١٠٧)، وقال: (" كوفي كان يتفقه .. يغرب ".
وقال الحافظ في " لسان الميزان " (٦/ ١٤٩ / ٥٢٥): " قال مسلمة بن القاسم: أخبرنا عنه ابن الأعرابي، وكان بالكوفة قاضيا، وهو ضعيف ".
وذكره الشيخ شاكر في تحقيقه " لتفسير الطبري " (٢/ ٣٤ / ٨٨٦)، وقال: " لم أجد في كل المراجع التي بين يدي، غير ترجمة (نجيح بن إبراهيم بن محمد الكرماني) " في " لسان الميزان " (٦/ ١٤٩)، وأنه كوفي ثقة .. ؟؟!! "
قلت: والأثر الذي ترجمت له من أجله ساقه الحافظ " ابن كثير في " تفسيره "',NULL,'638',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (641,'من طريق الطبري سندا، ومتنا به.
*********
٣٥١ - أبو سليمان الوشاء (٤٢٣)
(الترمذي، وابن ماجة)
(طب: ٤٩ ر / ١١ ش)
(تس: ٤٠ ر / ١٠ ش)، و (تخ: ١ر / ١ش)، و (تهـ: ٦ر / ٤ش)، و (تق: ٢ر / ٢ش)
أبو سليمان، نصر بن عبد الرحمن بن بكار، الناجي، الأودي – وقيل: الأزدي - الكوفي، الوشاء: توفي سنة ثمان وأربعين ومائتين، من العاشرة، ثقة.
روى عنه: أحمد بن بشير الكوفي (تس ١٢١٨٨ و ٢٣١٢٩ و ٣٤١٩٤)، و (تهـ ١١٥٤)، (ت)، وحفص بن عمر (تس ٢١٣١٤)، وحكام بن سلم الرازي (تس ١١٧٤٥ و ١٨٧٨٦ و ٢١٦٣٤ و ٢٢٩٣ و ٢٤٤٠١ و ٢٦١٢٨ و ٢٦١٣٨ و ٢٧٣٤٣ و ٢٧٣٤٤ و ٢٨٠٨٢ و ٣٠١٢٥ و ٣٢٠٠٥)، و (تهـ ١٣٨٥ و ٢٥٢١) (ت)، وأبو أسامة حماد بن أسامة (تس ١٧٥٨٤)، وعبد الرحمن بن محمد المحاربي (تس ٨٧٦ و ٢٨٦٦ و ٣٣٨٦ و ٣٧٤٣ و ٣٧٥٩ و ٣٧٦٥ و ١٠٣٤٣ و ٢١٧٥٠ و ٢٥٠٢٢ و ٢٥٠٥٧ و ٢٥٠٩٩ و ٢٥٢٢٨ و ٢٥٢٣٣ و ٣٤٤٥٣)، و (تهـ ٧١٣ و ١٩٦٢)، (ت، ق)، وعبيدة بن حميد (تس ٨٧٨٤)، وعمر بن زرعة (تس ٢١٢١٠)، وأبي قطن عمرو بن الهيثم البصري (تس ٣٦٩٣١)، و (تق ٣٥٣)، (ت)، ومحمد بن يعلى السلمي (تس ٤٢٣ و ٢٢٣٢٥)، و (تخ ١/ ٤٥٦)، وهشيم بن أبي ساسان (تهـ ١٠٩٨)، و (تق ٩٢٩)، ويحيى بن إبراهيم السلمي (تس ١٦٥٩ و ١٨٩٣ و ٢١٤٠٨ و ٣٤٣٣٤).
روى عنه: أحمد بن يوسف الضحاك " المستخرج " (١/ ١٩٧ / ٣٣٢)، وعبد الرحمن بن الحسين الصابوني " المعجم الأوسط " (٥/ ٨٩ / ٤٧٥٧).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: شيخا، ثقة، يحفظ ما يحدث به.
قلت: وتصحفت نسبته في الأثر (تخ ١/ ٤٥٦) من (الأودي) بالواو إلى (الأزدي) بالزاي.',NULL,'639',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (642,'وانظر: " المنفردات والوحدان " (١٢٥/ ٢٩٢)، و " الجرح والتعديل " (٨/ ٤٧٢ / ٢١٦٣)، و " الثقات " (٩/ ٢١٧ / ١٦٠٨٧)، وتهذيب الكمال " (٢٩/ ٣٥٠ / ٦٤٠١)، و " الكاشف " (٢/ ٣١٩ / ٥٨١٤)، و " المقتنى في سرد الكنى " (١/ ٢٩٢ / ٢٨٨٠)، و " تهذيب التهذيب " (١٠/ ٣٨٢ / ٧٧٦)، و " تقريب التهذيب " (١/ ٥٦٠ / ٧١١٥)، و " لسان الميزان (٧/ ٤١٠ / ٥٠٢٤)، و " رجال تفسير الطبري " (٥٥٦/ ٢٦٧٤)
********
٣٥٢ - – أبو عمرو الجهضمي (٢٣٨٣)
(الستة)
(طب: ٣٦ر / ١٥ش)
(تس ١٨ر / ٩ش)، و (تخ ٢ر / ١ش)، و (تهـ ١٥ ر / ١٠ ش)، و (تق ١ر / ١ش)
أبو عمرو، نصر بن علي بن نصر بن علي، الأزدي، الجهضمي، البصري، الصغير، الحفيد: مولده سنة نيف وستين ومائة، وتوفي في أحد الربيعين سنة خمسين ومائتين أو: بعدها – وهي نفس السنة التي توفي فيها ابنه علي – من العاشرة، ثقة، ثبت، طلب للقضاء فامتنع.
روى عن: الحارث بن وجيه (تهـ ٤٦٧)، و (د، ت، ق)، وحاتم بن وردان (تس ٢٣٨٣)، وحماد ابن مسعدة (تس ١١٤٣٦)، وحسن بن دعامة (تق ٩٠٨)، وزياد بن الربيع اليحمدي (تهـ ١٢٦٧)، و (ق)، وسليمان بن سليمان (تس ١٥١٣٧)، وسفيان بن عيينة (تهـ ٦١٧ و ٢٢٣٢)، و (م)، وعبد الأعلى بن عبد الأعلى (تهـ ٢٥٥٤)، و (خ، م، س، ق)، وعبد الملك بن قريب الأصمعي (تهـ ٢٧٠٥)، و (مق)، وأبيه علي بن نصر الجهضمي الكبير (تس ٣٥٤٩ و ١٣٤٩٧ و ٢٩١٩٤ و ٣٥٥٧٤ و ٣٥٦١١)، و (تهـ ٩٤٢ و ١١١٦ و ٢٧٥٩)، (ع)، ومسلم بن إبراهيم (تس ٢٨٦٨ و ٣٦٧٣٥)، (د، ت، ق)، والمعتمر بن سليمان (تس ٨١٤١ و ١٠٢٣٨)، و (تهـ ٧٢٥ و ٢٢١٤)، و (ت، س)، ونوح بن قيس الحداني (تس ٢٤٨٤٨ و ٢٤٨٤٩ و ٢٧٧١٢)، و (تهـ ١٠٣٠)، و (تخ ١/ ١١٢ و ١/ ١١٨)، و (م، قد، ت، س، ق)، ووهب بن جرير (تهـ ٢٤٧٩)، و (م)، ويحيى بن سليم',NULL,'640',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (643,'[الطائفي] (تس ١٤٦٣٩)، ويزيد بن زريع (تس ٣٣٤٥)، و (تهـ ٢١٠٨)، و (م، د، ت، ق)، وأبي أحمد الزبيري (تس ٢٩٠٦٩)، و (تهـ ١٤٣٩)، و (خ م، د، ق).
روى عنه: أحمد بن سلمة بن عبد الله " المستدرك " (١/ ٣١٧ / ٧٢٠)، وأحمد بن يحيى بن زهير " صحيح ابن حبان " (١١/ ١٥١ / ٤٨٢٢)، وبكر بن أحمد سعيد الطاحي " صحيح ابن حبان " (٢/ ٤٨٠ / ٧٠٥)، والحسن بن سفيان " صحيح ابن حبان " (٥/ ٥٨٥ / ٢٢٠٨)، والحسين بن إدريس الأنصاري " صحيح ابن حبان " (٦/ ١٨٠ / ٣٤٣٦)، والحسين بن محمد القتباني " المستدرك " (١/ ٦٣٥ / ١٧٠٤)، وخالد بن النضر بن عمرو القرشي " صحيح ابن حبان " (١٤/ ٢٠٠ / ٦٢٩٠)، وعلي بن أحمد الجرجاني " صحيح ابن حبان " (٢/ ٣٠٥ / ٥٤٦)، ومحمد بن أحمد الرقام " صحيح ابن حبان " (٩/ ١٣٩ / ٣٨٣٠)، ومحمد بن زهير الأبلي " صحيح ابن حبان " (٢/ ١٢٦ / ٤٠١)، ومحمد بن عمر بن يوسف بن حمزة " صحيح ابن حبان " (١/ ٢٢٩ / ٤١)، ومحمد بن النضر الجارودي " المستدرك " (١/ ٤٣١ / ١٠٨٥)
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، حجة، صدوقا ما به بأس، من نبلاء الناس.
وانظر: " التاريخ الكبير " (٨/ ١٠٦ / ٢٣٦٢)، و " التاريخ الصغير " (٢/ ٣٩١ / ٢٩٨٠)، و " الجرح والتعديل " (٨/ ٤٧١ / ٢١٥٩)، و " الثقات " (٩/ ٢١٧ / ١٦٠٩١)، و " ذكر أسماء التابعين " (٢/ ٢٦٠ / ١٣٠٤)، و " مولد العلماء ووفياتهم " (٢/ ٥٥١)، و " من روى عنهم البخاري في الصحيح " (١/ ٢١٥ / ٢٦٩)، و " رجال مسلم " (٢/ ٢٨٦ / ١٧٠٧)، و " تسمية من أخرجهم البخاري ومسلم " (١/ ٢٤٦ / ١٨٠٢)، و " رجال البخاري " (٢/ ٧٥٠ / ١٢٥٦)، و " تاريخ بغداد " (١٣/ ٢٨٧ / ٧٢٥٥)، و " التعديل والتجريح " (٢/ ٧٧٤ / ٧٣١)، و " تهذيب الكمال " (٢٩/ ٣٥٥ / ٦٤٠٦)، و " تذكرة الحفاظ " (٢/ ٥١٩ / ٥٣٦)، و " السير " (١٢/ ١٣٣ / ٤٧)، و " الكاشف " (٢/ ٣١٩ / ٥٨١٩)، و " المقتنى في سرد الكنى " (١/ ٤٣٤ / ٤٧٠٢)، و " تهذيب التهذيب " (١٠/ ٣٨٤ / ٧٨١)، و " تقريب التهذيب " (١/ ٥٦١ / ٧١٢٠)، و " طبقات الحفاظ " (١/ ٢٣١ / ٥١٤)، " رجال تفسير الطبري " (٥٥٧/ ٢٦٧٦).
*********
١٨٤ - تمييز (٤) أبو عمرو، نصر بن علي، الجهضمي، البصري، الأزدي، الجد الكبير توفي، قبل سنة خمسين ومائة، من السابعة، ثقة، صدوق.',NULL,'641',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (644,'روى عن: جده لأمه أشعث الحداني، والنضر بن شيبان.
روى عنه: مسلم بن عبدة، وأبو نعيم، وابنه علي.
وانظر: " التاريخ الكبير " (٨/ ١٠٣ / ٢٣٤٥)، و " الجرح والتعديل " (٨/ ٤٦٦ / ٢١٣٦)، و " الثقات " (٩/ ٢١٤ / ١٦٠٧٤)، و " ذكر أسماء التابعين " (١/ ٣٧٦ / ١١٥٨)، و " تهذيب الكمال " (٢٩/ ٣٥٤ / ٦٤٠٥)، و " السير " (١٢/ ١٣٦ / ٤٨)، و " الكاشف " (٢/ ٣١٩ / ٥٨١٨)، و " تهذيب التهذيب " (١٠/ ٣٨٣ / ٧٨٠)، و " تقريب التهذيب " (١/ ٥٦١ / ٧١١٩)
*********
٣٥٣ - أبو الفتح المصري (تهـ: ٢٣٥١)
(ب: تهـ ١ر / ١ش)
أبو الفتح، نصر بن مرزوق، المصري، الإسكندراني، توفي في ربيع الآخر، سنة إحدى وستين ومائتين، من الحادية عشرة، صدوق.
روى عن: خالد بن نزار (تهـ ٢٣٥١)، وإدريس بن يحيى الخولاني " مسند أبي عوانة " (٤/ ٤٠٠ / ٧٠٩٤)، وأسد بن موسى " صحيح ابن خزيمة " (١/ ١١١ / ٢٢٣)، والخصيب بن ناصح، وأبي صالح عبد الله كاتب الليث بن سعد (شرح معاني الآثار) (٣/ ٦٦) وعلي بن محمد بن المغيرة " المعجم الأوسط " (٤/ ١٨٠ / ٣٩١٥) وعمرو بن أبي سلمة المصري " صحيح ابن خزيمة " (٢/ ٢٠٦ / ١١٩١)، والقاسم بن كثير " مسند أبي عوانة " (٣/ ٢٤٢ / ٤٨٢٧)، ومحمد بن أسد، ووهب الله بن راشد.
روى عنه: إبراهيم بن أحمد بن محمد الحارث " الكامل " (٣/ ٢٦١)، وأحمد بن عبد الواحد الكتاني " إكمال ابن ماكولا " (/ ١٤٥)، وجامع بن القاسم البلخي " حلية الأولياء " (٨/ ٤٤)، وعبد الرحمن بن أبي حاتم، وعلي بن سعيد الرازي " المعجم الأوسط " (٤/ ١٨٠ / ٣٩١٥)، وعمير بن يوسف " حلية الأولياء " (٣/ ١١٣).
التعديل والتجريح: قال عبد الرحمن بن أبي حاتم في " الجرح والتعديل ": " كتبنا عنه وهو صدوق ".',NULL,'642',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (645,'قلت: وقد تصحفت نسبته في " تهذيب الآثار " من (المصري) – بالميم، إلى (البصري) بالباء. ووقفت له على رواية كثيرة في كتب الحديث انتقيت منها ما أثبت من خلاله بعض معجم شيوخه وتلاميذه. ووقفت للطبري على رواية عنه في " التدوين في أخبار قزوين " (٣/ ٣٨٢) عن خالد بن نزار.
وما لم أعزه إلى كتاب منهم، وميزته بالأسود العريض، فهو من ترجمته في " الجرح والتعديل ".
وانظر: " الجرح والتعديل " (٨/ ٤٧٢ / ٢١٦٧)، " المقتنى " (٢/ ١٠ / ٤٩٥٨)، و " مولد العلماء " (٢/ ٥٧٤).
***********
٣٥٤ - أبو محمد البذشي (تخ ٣/ ٣٤٧)
(أبو داود، والنسائي)
(طب: تخ ٢ر / ٢ش)
أبو محمد، نوح بن حبيب، القومسي _ بضم القاف، وسكون الواو، آخره مهملة – البذشي _ بفتح الموحدة، وسكون المعجمة، بعدها معجمة – من قرية من قرى بسطام: توفي سنة اثنتين وأربعين ومائتين، من العاشرة، ثقة، سني.
روى عن: هشام بن محمد (تخ ٣/ ٣٨١)، وهشام بن يوسف (تخ ٣/ ٣٤٧).
روى عنه: أبو جعفر الطبري (تخ ٣/ ٣٤٧ و ٣٨١).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقا، لا بأس به، صاحب سنة وجماعة.
قلت: وأثنى عليه الإمام أحمد بن حنبل – رحمه الله تعالى - كثيرا.
وانظر: " التاريخ الكبير " (٨/ ١١٢ / ٢٣٨٧)، " الجرح والتعديل " (٨/ ٤٨٦ / ٢٢١٩) " الثقات " (٩/ ٢١١ / ١٦٠٦١)، " تاريخ بغداد " (١٣/ ٣١٩ / ٧٢٩٠)، " تهذيب الكمال " (٣٠/ ٣٩ / ٦٤٨٨)، " الكاشف " (٢/ ٣٢٧ / ٥٨٨٨)، تهذيب التهذيب " (١٠/ ٤٢٩ / ٨٧١)، " تقريب التهذيب " (١/ ٥٦٦ / ٧٢٠٣)',NULL,'643',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (646,'<span data-type="title" id=toc-39>[١٨] باب الهاء </span>(من اسمه هارون)
٣٥٥ - هارون الأصم (٢٢٨)
(طب: ١٤ ر / ١ ش)
(تس: ١٢ ر / ١ش)، و (تخ: ٢ ر / ١ش)
هارون بن إدريس، الكوفي، الأصم، من العاشرة، لم أعرفه، ولم أجد له ترجمة ولم يعرفه الشيخ شاكر قبلي (١٤٥٥)، ولم يتعرض الشيخ التركي في تحقيقه " لتفسير الطبري " (١/ ٢٠٥) لترجمته بشيء.
روى عن: عبد الرحمن بن محمد المحاربي (تس ٢٢٨ و ٢٤٧ و ٢٥١ و ١٤٥٨ و ٢٣٣٢ و ١٠٢١٨ و ٢٤٦٩٣ و ٢٥٠٢٧ و ٣٠٢٣٤ و ٣٠٣٠١ و ٣٧٤٥٧ و ٣٨٠٧٧)، و (تخ ١/ ٢٨٧ و ١/ ٤٥٤).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: ذكره الشيخ شاكر في تحقيقه " لتفسير الطبري " (١/ ٢٠٥ / ٢٢٨)، ثم ذكره مرة أخرى في (٢/ ٢٦٩ / ١٤٥٥)، وقال: " شيخ الطبري: لم أجد له ترجمة، ولا وجدته في مكان، إلا في روية الطبري عنه في " التاريخ " أيضا (١/ ٢٥٣)، (٢/ ١٢٦) روى عنه عن المحاربي ".أ. هـ.
قلت: وأخرج الطبراني في " الكبير " (١١/ ٢٥٥ / ١١٦٥٨)، عن هارون بن إدريس الخشكي، عن عبد الحميد الحماني، من طريق محمد بن عبد الله الحضرمي ولا أدري إن كان هو المراد أم غيره؟!.
وأخرج الحافظ ابن كثير في " تفسيره " (٣/ ١٨٧)، و (٤/ ٥٢١) بعض ما أخرجه الطبري عنه، وعزاه إليه أيضا.
وشيخه هنا هو: أبو محمد، عبد الرحمن بن محمد بن زياد، المحاربي، الكوفي مولده سنة + وتوفي سنة (١٩٥)، من التاسعة، لا بأس به وكان يدلس، وعلى هذا يكون صاحب الترجمة من العاشرة، وقد ترجمت له في " المعجمين الكبير والصغير ".',NULL,'644',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (647,'٣٥٦ - – أبو القاسم الهمداني (٣٠٠٩)
(البخاري في جزء القراءة، والترمذي، والنسائي، وابن ماجة)
(طب: ١٧ ر / ٤ش)
(تس: ٩ر / ١ش)، و (تهـ: ٣ر / ٢ش)، (تق: ٥ ر / ٣ ش)
أبو القاسم، هارون بن إسحاق بن محمد بن مالك، الهمداني، الكوفي: توفي سنة ثمان وخمسين ومائتين، من صغار العاشرة، صدوق.
روى عن: عنبسة – غير منسوب – (تهـ ٤٩٨)، ومحمد بن عبد الوهاب القناد (تق ٦٧٠)، (ت، س، ق)، و [محمد] بن فضيل (تق ٣٣٤ و ٣٧٥ و ٤٢٠)، (ق)، ومصعب بن المقدام العجلي (تس ٣٠٠٩ و ٥٧٢٦ و ٦٦٨٠ و ٨٠٠٤ و ١٠٨٧٧ و ١١٣٤٨ و ١٥٧٧٧ و ٢١١٨٧ و ٢٣٦٧٤)، و (تخ ٢/ ٢٢ و ٥٥ و ٦٢ و ١٢٣ و ١٦٨)، و (تهـ ٤٧٨ و ٢٦٨٣)، و (تق ٩٣٤)
روى عنه: أبو بكر، أحمد بن عمرو البزار " مسند البزار " (٢/ ١١٠ / ٤٦٢)، والحسن بن سفيان " المستخرج " (٢/ ١٩٢ / ١٣١٧)، والحسين بن محمد بن أبي زياد " المستدرك " (١/ ١٣٦ / ٢٢٢)، وعبد الله بن أحمد ابن حنبل " المستدرك " (١/ ١٥١ / ٢٦٤)، وعبد الله بن علي بن الجارود " المنتقى " (١/ ٧٠ / ٢٤٠)، وعلي بن الجعد " مسند ابن الجعد " (١/ ٢٨٣ / ١٩٠٤)، ومحمد بن إسماعيل بن مهران " المستدرك " (٣/ ١١٤ / ٤٥٦٧)، وأبو يعلى الموصلي " مسند أبي يعلى " (٢/ ١٤ / ٦٤٢).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقا، نعم الشيخ، كان قليل الحديث، وكان من خيار عباد الله.
وانظر: " الطبقات الكبرى " (٦/ ٤١٤)، و " الكنى والأسماء " (١/ ٦٩٠ / ٢٧٨٤)، و " الجرح والتعديل " (٩/ ٨٧ / ٣٦٠)، و " الثقات " (٩/ ٢٤١ / ١٦٢١٥)، وتهذيب الكمال " (٣٠/ ٧٥ / ٦٥٠٦)، و " السير " (١٢/ ١٢٦ / ٤٣)، و " الكاشف " (٢/ ٣٢٩ / ٥٩٠٢)، " المقتنى في سرد الكنى " (١/ ٥٣ / ٤٠)، و " تهذيب التهذيب " (١١/ ٣ / ٢)، و " تقريب التهذيب " (١/ ٥٦٨ / ٧٢٢١)، و " رجال تفسير الطبري " (٥٦٣/ ٢٧٠٧).',NULL,'645',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (648,'٣٥٧ - – أبو بشر البزاز (١٩١٨٢)
(طب): (تس: ٢ر / ٢ش)
أبو بشر، هارون بن حاتم، التميمي، البزاز، المقرئ، الكوفي – صاحب " التاريخ " – توفي سنة تسع وأربعين ومائتين، من الحادية عشر، ضعيف، ليس بشيء.
روى عن: سفيان بن عيينة (تس ٣٢٣٢٣)، وهشيم بن الزبرقان (تس ١٩١٨٢)، وعبد الله بن نمير " المستدرك " (٢/ ٢٦١ / ٢٩٣٩)، وعبد الرحمن بن أبي حماد " الجرح والتعديل " (٩/ ٨٨ / ٣٦٤)
وعبد السلام بن حرب " الجرح والتعديل " (٩/ ٨٨ / ٣٦٤)، وعبيده بن حميد " الكامل " (٢/ ١٠٥ / ٣٢١)، والفضل بن عمرو " موضح أوهام الجمع والتفريق " (٢/ ٣٦٥)، ومحمد بن إسماعيل بن أبي فديك " مسند البزار " (٢/ ٢٥٥ / ٦٦٣)، ومحمد بن عبد الرحمن بن أبي مليكة " المعجم الكبير " (٦/ ١٧٩ / ٥٩٢٢)، ومحمد بن فضيل بن غزوان " المستدرك " (٢/ ٢٦١ / ٢٩٣٩)، ووكيع بن الجراح " الثقات " (٩/ ٢٤١ / ١٦٢١٧)، ويحيى بن عيسى الرملي " الكامل " (٧/ ٢١٨ / ٢١١٤)، وأبي بكر بن عياش " الجرح والتعديل " (٩/ ٨٨ / ٣٦٤)، وأبي معاوية الضرير " المستدرك " (٢/ ٢٦١ / ٢٩٣٩).
روى عنه: أحمد بن حسين الصوفي " الكامل " (٧/ ٢١٨ / ٢١١٤)، وأحمد بن محمد بن الأزهر السجستاني " الثقات " (٩/ ٢٤١ / ١٦٢١٧)، وبقي بن مخلد " لسان الميزان " (٦/ ١٧٧ / ٦٢٥)، والحسن بن حباش " الحلية " (٥/ ٥٨)، وعلي بن حسين الباقلاني " الكامل " (٢/ ١٠٥ / ٣٢١)، ومحمد بن محمد بن عقبة " لسان الميزان " (٦/ ١٧٧ / ٦٢٥)، وأبو بكر البزار " مسند البزار " (٢/ ٢٥٥ / ٦٦٣) - روى عنه القراءات - وموسى بن إسحاق، وأحمد بن يزيد الحلواني، والحسن بن العباس الرازي – روى عنه قراءة أبي بكر.
التعديل والتجريح: قال النسائي: " ليس بثقة ".
قال عبد الرحمن بن أبي حاتم: " كتب عنه أبو زرعة ثم أمسك عن الرواية عنه ".
وقال أيضا: " سمعت أبا زرعة يقول: كتبت عن هارون بن حاتم ولا أحدث عنه "
وقال أيضا: " سمعت أبي – وسئل عن هارون بن حاتم – فقال: أسال الله السلامة،',NULL,'646',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (649,'كان أبو زرعة كتب عنه، فأخبرته بسببه فكان لا يحدث عنه، وترك حديثه ".
وذكره ابن حبان في " الثقات ".
وأورد له الدارقطني خبرا تفرد بوصله ويقال: " هو ضعيف ".
وذكر الذهبي كلام الناس فيه، وذكر من منا كيره حديثا، وهو: عن عبد الله مرفوعا: " النظر إلى وجه علي عبادة " ثم قال: " وهذا باطل ". انتهى.
وذكر الذهبي هذا الحديث في ترجمة (يحيى بن عيسى) ثم قال: " لعله من وضع هارون " – يعني – ابن حاتم هذا.
قلت: وأغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري ".
وانظر ": " الضعفاء والمتروكين، للنسائي " (١٠٥/ ٦١٤)، و " الجرح والتعديل " (٩/ ٨٨ / ٣٦٤)، و " الثقات " (٩/ ٢٤١ / ١٦٢١٧)، و " الضعفاء والمتروكين، لابن الجوزي " (٣/ ١٦٩ / ٣٥٦٢)، و " ميزان الاعتدال " (٧/ ٥٩ / ٩١٥٩)، و " المغني في الضعفاء " (٢/ ٧٠٤ / ٦٦٩٠)، و " الكشف الحثيث " (٢٧٠/ ٨١٢)، و " لسان الميزان " (٦/ ١٧٧ / ٦٢٥).
***********
٣٥٨ - أبو القاسم النهشلي (١٢٢٤)
(الترمذي)
(طب: (تس: ١ر / ١ ش)
أبو القاسم، هشام بن يونس بن وابل، - وقيل: وائل – ابن الوضاح بن سليمان، التميمي، النهشلي، الكوفي، اللؤلؤي: توفي في ذي القعدة من سنة اثنتين وخمسين ومائتين، من العاشرة، ثقة.
روى عن: حفص بن غياث (تس ١٢٢٤)، وعبد الله بن يحيى القاص " مسند أحمد " (١/ ٦٣ / ٤٥٤).
روى عنه: الحسن بن عليل العنبري " شعب الإيمان " (٣/ ٣١٤ / ٣٦٣٥)، وعبد الرحمن بن سلم " المعجم الكبير " (٢٠/ ٢٤٥ / ٥٨١)، وأبو بكر البزار " مسند البزار " (٦/ ٣٩ / ٢١٠٨)، وابن',NULL,'647',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (650,'خزيمة " الثقات " (٩/ ٢٣٤ / ١٦١٧٩).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه ثقة، صدوقا، يغرب.
وانظر: " الجرح والتعديل " (٩/ ٧٢ / ٢٧٣) " و " الثقات " (٩/ ٢٣٤ / ١٦١٧٩)، و " تهذيب الكمال " (٣٠/ ٢٧٠ / ٦٥٩٤)، و " الكاشف " (٢/ ٣٣٨ / ٥٩٧٨)، و " تهذيب التهذيب (١١/ ٥٢ / ٩٩)، و " تقريب التهذيب " (١/ ٥٧٤ / ٧٣١١)، و " رجال تفسير الطبري " (٥٦٨/ ٢٧٣٦).
..
٣٥٩ - أبو عمر الرقي (٤٩٦٧)
(النسائي)
(طب: ١٢ ر / ٦ ش)
(تس: ٢ر / ٢ش)، و (تهـ: ٧ ر / ٤ ش)، و (تق: ٣ر / ٣ش)
أبو عمر، هلال بن العلاء بن هلال بن عمر، الباهلي، مولاهم، الرقي، مولده نحو سنة أربع وثمانين ومائة، وتوفي في ثالث المحرم سنة ثمانين ومائتين، - وقيل: بعدها – وقد قارب المائة، من الحادية عشرة، صدوق.
روى عن: الحسين بن عياش (تق ٩٦٧)، وحفص بن عمر (تق ٢٢٩)، وسعيد بن عبد الملك بن واقد الحراني (تس ١٣٨٥٩)، و (تهـ ١٧٢)، و (تق ٦٧٣)، وعبد الله بن جعفر الرقي (تهـ ١٣٠٣)، وعفان بن مسلم (تهـ ٨٥٠)، و (س)، وأبيه العلاء بن هلال الباهلي (تس ٤٩٦٧)، و (تهـ ٣٠٨ و ٨٦٩ و ١٣٠٣ و ٢٧١٤)، و (س).
روى عنه: أبو بكر البزار " مسند البزار " (٣/ ١٢٩ / ٩١٥)، وأبو عوانة الإسفرائيني – وقد أكثر عنه في مسنده " مسند أبي عوانة " (١/ ٨٠ / ٢٣٨).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: صدوقا صالحا، حافظا؛ رحل إليه الحفاظ، ليس به بأس، روى أحاديث منكرة عن أبيه فلا يدرى الريب منه! أو من أبيه؟!.',NULL,'648',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (651,'وانظر: الجرح والتعديل " (٩/ ٧٩ / ٣١٨)، و " الثقات " (٩/ ٢٤٨ / ١٦٢٥٧)، و " مولد العلماء ووفياتهم " (٢/ ٦٠٦)، و " الإرشاد " ((٢/ ٤٧٤ / ١٣١)، و " تهذيب الكمال " (٣٠/ ٣٤٦ / ٦٦٢٩)، و " تذكرة الحفاظ " (٢/ ٦١٢ / ٦٣٧)، و " المقتنى في سرد الكنى " (١/ ٤٢٥ / ٤٥٧١)، و " السير " (١٣/ ٣٠٩ / ١٤٣)، و " ميزان الاعتدال " (٤/ ٣١٥ / ٩٢٧٦)، و " الكاشف " (٢/ ٣٤٢ / ٦٠٠٥)، و " طبقات المحدثين " (١/ ١٠١ / ١١٥٨)، و " تهذيب التهذيب " (١١/ ٧٣ / ١٣٥)، و " تقريب التهذيب " (١/ ٥٧٦ / ٧٣٤٦)، و " لسان الميزان " (٧/ ٤٢١ / ٥١٢٣)، و " المقصد الأرشد " (٣/ ٨٠ / ١٢٠٢)، و " طبقات الحفاظ " (١/ ٢٦٨ / ٥٩٩)، و " رجال تفسير الطبري " (٥٧٠/ ٢٧٤٤).
٣٦٠ - – أبو السري التميمي (٢٠٦٠)
(البخاري في خلق أفعال العباد، والباقون)
(طب: ٤١٥ ر ش)
(تس ٣٧٤ ر / ٣٥ ش)، و (تخ ١١ ر / ٦ش)، و (تهـ ٢٦ ر / ٧ش)، و (تق ٤ر / ١ش)
أبو السري، هناد بن السري بن مصعب، التميمي، الدرامي الكوفي، الحافظ، الزاهد، العابد، راهب الكوفة: مولده سنة اثنتين وخمسين ومائة، وتوفي سنة ثلاث وأربعين ومائتين، عن نحو إحدى وتسعين سنة، من العاشرة، ثقة.
روى عن: إسحاق بن عيسى (تس ٢٨٤٣)، وجرير [ابن عبد الحميد الضبي] (تس ٢٨٤٥ و ١٢٥٧٢ و ١٢٦٠٦ و ١٢٦٤٣)، وحاتم بن إسماعيل المدني (تس ٢٧٧١)، و (ت، س)، وحفص بن غياث (تس ٢٨٦٢ و ١١٨٥٧ و ١٢١٨٤ و ١٢٣٧٥ و ١٢٤٠٤ و ١٢٦٢١ و ١٣٣٣٢ و ١٣٣٣٣)، وأبي أسامة، حماد بن أسامة (تس ٢٨٨٩ و ١١٨٤٥ و ١١٨٥٥ و ١١٨٥٦ و ١٢٣٩٦ و ١٢٣٩٨ و ١٢٤٠٨ و ١٢٤١١ و ١٢٤٦٨)، و (تهـ ٢٠٤٩ و ٢٠٥١ و ٢٩٩٢ و ٢٩٩٣)، (ت)، وأبي الأحوص سلام بن سليم (تس ٢٠٦٠ و ٢٤٥٨ و ٢٨٥٤ و ٤٤٠١ و ٤٤١٦ و ٤٤٢٩ و ٤٤٨٣ و ٤٤٨٩ و ٥٦٧٢ و ٦١٦٩ و ٧٤٢٠ و ٨٤٦٤ و ٩٦٥٦ و ١٠٥٨٠ و ١٢٣٨٤ و ١٢٤٠٣ و ١٢٤٢٣ و ١٢٤٢٥ و ١٢٤٧٤ و ١٢٥٢٣ و ١٢٥٨١ و ١٢٧١٠ و ١٣٦٣١ و ١٦٦١٣ و ١٨٦٨١ و ١٨٧٢٦ و ١٩٩٥٦ و ٢١٧٧٧ و ٢٣٤٣٢ و ٢٣٤٤٢ و ٢٣٤٥٠ و ٢٣٤٦١ و ٢٣٤٦٧ و ٢٤٠١١ و ٢٢١٥٩ و ٢٤٥٦١ و ٢٥٧٦٤ و ٢٦١١٧ و ٢٦٧٢٤ و ٢٨٠٩٦ و ٢٨١٩١ و ٢٩٢٠٠ و ٢٩٢٩٤ و ٣٠٠٦٥ و ٣٠٠٦٩ و ٣١٧٢١ و ٣١٨٢٩ و ٣٢٠٠٧ و',NULL,'649',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (652,'و ٣٢٠٢١ و ٣٢٢٨٩ و ٣٢٣٠٢ و ٣٢٩٣٨ و ٣٢٩٤٨و ٣٣١٥٨ و ٣٣٢٨٤ و ٣٣٥٩٤ و ٣٤١٩٩ و ٣٤٤٤٣ و ٣٤٦٣١ و ٣٤٦٤٨ و ٣٥٢٠٠ و ٣٥٤٩٩ و ٣٥٨٩٥ و ٣٦٤٥٠ و ٣٦٤٧٧ و ٣٦٧٧٠ و ٣٦٧٧٧ و ٣٦٧٩٥ و ٣٧٢٩٤ و ٣٧٣٤٦ و ٣٧٧٦٥ و ٣٧٨١٠ و ٣٧٨١٨)، و (تهـ ١٦٢ و ١٥٨٠ و ١٦٧٨ و ٢٠٥٥ و ٢١٥٩ و ٢٨٦٠)، و (تق ٣٢٥ و ٧١٢ و ٨١٤ و ١٠٣٩)، و (تخ ١/ ١٥٢)، وعخ، م، ٤) وسفيان بن عيينة (تس ٣٨٣٢) و (ت، س)، وسقط بينه وبين المغيرة (تس ١٢٤٨٦) وشريك بن عبد الله النخعي تس ٢٤٨٩ و ٢٨٤٤ و ١٢٣٨٢ و (تخ ٢/ ٥٦٢ و (د، ت، س)، وأبي زبيد عبثر بن القاسم (تس ١١١٨٠ و ١٢٤٠٦ و ١٣٢٥٨ و (م، د، ت، س، ق)، وعبد الرحمن بن عتبة (تس ٢٨٤٩)، وعبد الرحمن بن محمد المحاربي (تس ٢٨٣٧)، و (د)، وعبد الرحيم بن سليمان (تس ٢٨٤٠ و ٢٨٨٦ و ٢٨٩٦ و ٣٩٦٣)، و (ت)، وعبد السلام بن حرب (تس ١٢٤٨٢)، (د، ت، س)، وعبد الله بن المبارك (تس ١١١٣٥ و ١٢٤٦٦ و ١٢٤٩٦ و ١٢٥٠٤)، و (م، د)، وعبدة بن سليمان (تس ٢٧٦٥ و ٢٧٦٦ و ٢٧٦٧ و ٢٧٧٠ و ٢٨٣٩ و ٢٨٥٨ و ٢٨٩٦ و ٣٠٣٠ و ٤٣٨٠ و ٤٤١٢ و ١١١٩٤ و ١١١٩٥ و ١١٢٠٥ و ١٢١٨٦ و ١٢٢١٦ و ١٢٣٣١ و ١٢٣٧٢ و ١٢٤٥٣ و ١٢٧٣٣ و ١٣١٧٦)، و (تهـ ٢٠١٨ و ٢٠٤٩ و ٢٩٨٠)، و (م، ٤)، وعبيده بن حميد (تس ٢٧٨٨ و ٢٨٥٦ و ٣٠٠٦ و ١١٩٢٧ و ١١٩٦٨ و ١٢٠٩٦ و ١٢٤٤٩ و ١٢٤٦٠ و ١٢٥٧٦ و ١٢٨٢٦ و ١٢٦٤٢)، و (س)، وعلي بن مسهر (تس ٢٤٩٤ و ٢٧٥٦ و ٢٧٧٣)، (م، ت)، وعمر بن هارون البلخي (تس ١٢٣٩٣ و ١٢٤٢٦ و ١٢٤٣٢ و ١٢٤٥٢)، و (ت)، وعيسى بن يونس (تس ١١١٣٦ و ١١١٥٨)، و (ت)، وقبيصة بن عقبة (تس ٢٧٧٥ و ٢٧٩٩ و ١١٢١٠ و ١٣١٧٣ و ١٣٢٩٥ و ١٣٣٣٤ و ١٣٤٥٧ و ١٣٤٨٩ و ١٣٦٤٣ و ١٣٨٥٨)، و (د، ت)، وليث بن هارون (تس ١٢٨٦٣ و ١٢٨٧٠)، ومحمد بن فضيل بن غزوان (تس ٤٣٧٢ و ١١٨٠٦ و ١١٩٦٩ و ١٢٤٧٥ و ١٣٢٧١ و ١٣٥٠٠)، و (د، ت)، ومروان بن معاود الفزاري (تس ٢٧٨٩ و ١٣٥٨٨)، وهيثم بن بشير (تس ١١١٧٧ و ١١٨٦٥ و ١٢٠٠٦ و ١٢٤٨٨)، و (ت، س)، ووكيع بن الجراح (تس ٢٧٥١ و ٢٧٥٧ و ٢٧٧٤ و ٢٧٧٨ و ٢٨٠٤ و ٢٨٣٨ و ٢٨٤٦ و ٢٨٤٨ و ٢٨٥٠ و ٢٨٨٠ و ٢٨٨٤ و ٢٨٨٥ و ٢٨٨٧ و ٢٨٩٦ و ٣٠٣٠ و ٣٨٠٨ و ٣٨١١ و ٣٨١٣ و ٤٣٨٠ و ٤٣٩٢ و ٤٣٩٤ و ٤٤١٠ و ٤٤١٣ و ٤٤٢٤ و ١١١٨١ و ١١١٨٧ و ١١١٨٨ و ١١١٩٣ و ١١٢٠٥ و ١١٦٣٢ و ١١٦٦٧ و ١١٦٨٥ و ١١٧١٤ و ١١٩٠٤ و ١١٩٥٠ و ١١٩٥٣ و',NULL,'650',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (653,'١١٩٦١ - و ١١٩٨٤ و ١٢٠٣٤ و ١٢٠٥٥ و ١٢٠٥٧ و ١٢٠٥٨ و ١٢١١٣ و ١٢١١٦ و ١٢١٢٥ و ١٢١٢٧ و ١٢٢٤٤ و ١٢٢٧٨ و ١٢٢٨٣ و ١٢٣١٤ و ١٢٣٣٤ و ١٢٣٨٣ و ١٢٣٨٩ و ١٢٣٩١ و ١٢٣٩٢ و ١٢٤٠٠ و ١٢٤٠٢ و ١٢٤٠٩ و ١٢٤١٢ و ١٢٤١٨ و ١٢٤٢٠ و ١٢٤٢٢ و ١٢٤٢٨ و ١٢٤٣٣ و ١٢٤٣٧ و ١٢٤٣٩ و ١٢٤٤٦ و ١٢٤٤٧ و ١٢٤٥١ و ١٢٤٦١ و ١٢٤٦٥ و ١٢٤٦٧ و ١٢٤٨٣ و ١٢٤٨٩ و ١٢٤٩٠ و ١٢٥٠١ و ١٢٥٠٧ و ١٢٥١٦ و ١٢٥٢٩ و ١٢٥٤٤ و ١٢٥٦٣ و ١٢٥٧٩ و ١٢٥٩٢ و ١٢٦٢٧ و ١٢٦٢٨ و ١٢٦٥٦ و ١٢٧٧٧ و ١٢٨٧٥ و ١٢٩١١ و ١٢٩٨١ و ١٣٠٥٧ و ١٣٠٦٤ و ١٣١٢٣ و ١١٣١٤٩ و ١٣١٧٣ و ١٣١٧٩ و ١٣٢٨٧ و ١٣٣٨٤ و ١٣٤٥٠ و ١٣٤٨٣ و ١٣٤٩٠ و ١٣٦٦٩ و ١٣٧٠٢ و ١٣٧٠٦ و ١٣٧١٢)، و (تهـ ٤٦٣ و ٢٩٨٣)، و (تخ ١/ ٩١ و ٢/ ٥٦٢)، و (د، ت، س، ق)، ويحيى بن زكريا بن أبي زائدة (تس ٢٤٩٣ و ٣٨٠٢ و ٣٨٠٤ و ٣٨٠٥ و ٣٨٠٩ و ٣٨١٢ و ٣٨١٣ و ٣٩٢٨ و ١١١٣٣ و ١١١٥٨ و ١١١٨٥ و ١٢٥١٧ و ١٢٥٢٤ و ١٢٥٣٢ و ١٢٥٣٤ و ١٢٥٤١ و ١٢٥٤٢ و ١٢٥٥٦ و ١٢٥٦٤ و ١٢٥٧٢ و ١٢٥٨٠ و ١٢٥٨٩ و ١٢٦١٠ و ١٢٦١٤ و ١٢٦٢٢ و ١٢٦٢٤ و ١٢٦٢٦ و ١٢٦٣٠ و ١٢٦٣١ و ١٢٦٣٢ و ١٢٦٣٣ و ١٢٦٤٠ و ١٢٦٤٧ و ١٢٧٢٢ و ١٢٧٢٣ و ١٢٧٢٤ و ١٢٧٣٤ و ١٢٧٨٦ و ١٢٧٨٨ و ١٢٧٨٩ و ١٢٨١٨)، و (تهـ ٢٠٢١ و ٢٠٣١ و ٢٠٣٢ و ٢٠٤٦ و ٢٠٥٨ و ٢٠٦١ و ٢٠٧٣ و ٢٩٧٩ و ٢٩٨٩)، و (م، ت، س)، ويعلى بن عبيد الطنافسي (تس ٤٤٠٠ و ١٢٠٥١)، و (تهـ ٢٠٣٨ و ٢٠٥٩ و ٢٠٦٦ و ٢٩٩٠)، ويونس بن بكير الشيباني (تس ٢٨٥٧ و ١١٥٦٣ و ١١٩٢٦ و ١١٩٧٩ و ١٢١٦٣ و ١٢١٦٤ و ١٢١٧٣ و ١٢٢١٢ و ١٢٢٢١ و ١٢٢٢٤ و ١٢٢٨٧ و ١٢٣٢٩ و ١٢٣٣٣ و ١٢٥٢٠ و ١٢٥٢١ و ١٢٨٢٤ و ١٢٨٢٥ و ١٣١٣٢ و ١٣١٧٨ و ١٣٥٤٢ و ١٣٧٥٠)، و (تخ ١/ ٤٥٤) (ت)، وابن مطيع (تس ١١٩٧٠)، وأبي بكر بن عياش (تس ٣٠٣١ و ٣٠٤٢٩)، و (تخ ١/ ١٦ و ١/ ٢١ و ١/ ٣٥ و ١/ ٤١ و ١/ ٤٢ و ١/ ٤٦)، و (د، س)، وأبي خالد الأحمر (تس ١٢٣٩٥ و ١٢٤٣١)، وأبي عبيدة – غير مكني، ولا منسوب - (تس ٣٠٣١)، وأبي معاوية الضرير (تس ٢٧٩٠ و ٢٤٩٤ و ٢٧٩٠ و ٢٨٨١ و ٢٨٨٨ و ٣٠٠٧ و ٣٠٣٠ و ٣٠٣١ و ٤٣٨٠ و ٤٣٩٥ و ١١٢٠٣ و ١١٧٤١ و ١١٧٩٠ و ١١٧٩٧ و ١١٨٤٦ و ١١٩٢٧ و ١١٩٤٤ و ١٢٠٤١ و ١٢٣٣٢ و ١٢٤٠١ و ١٢٤١٩ و ١٢٨١٧ و ١٣١٥٠ و ١٣١٩٣ و ١٣٤٥٦ و ١٣٤٨٤)، و (تهـ ٤٥٩ و ٢٠٣٣ و ٢٠٥٩ و ٢٠٦٦)، و (تخ ١/ ٩١)، و (د، ت، س)، وأبي يحيى الرازي – لعله إسحاق بن سليمان – (تس ١٢١٣٩).
روى عنه: إبراهيم بن أبي طالب " المستدرك " (١/ ٥٧٥ / ١٥١٣)، وإبراهيم بن يوسف',NULL,'651',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (654,'" المستخرج " (١/ ٤٩ / ٣٦)، وأبو يعلى، أحمد بن علي بن المثنى " معجم أبي يعلى " (١/ ٢٥٥ / ٣١٨)، والحسن بن سفيان " المستخرج " (٤/ ١٢٨ / ٣٤٠٨)، والسري بن يحيى بن السري التيمي " سنن الدارقطني " (١/ ١١٥ / ٢)، وعبد الله بن أحمد بن حنبل " مسند أحمد " (١/ ١٣٣ / ١١١٨)، وعمران بن موسى بن مجاشع " صحيح ابن حبان " (٧/ ١٧٨ / ٢٩١٦)، ومحمد بن عبدوس بن كامل " المستدرك " (٣/ ٧٩ / ٤٤٥٣)، ومحمد بن نصر " المستدرك " (١/ ٢٧٨ / ٦١٠)، ومحمد بن يحيى " المستخرج) (١/ ٢٠٤ / ٣٥٥)، وأبو داوود السجزي " مسند أبي عوانة " (٤/ ٤٨ / ٥٩٨١).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة صدوقا. وقال أحمد بن حنبل: عليكم بهناد.
وقال قتيبة بن سعيد: " ما رأيت وكيعًا يعظم أحدًا تعظيمه لهناد ".
وتصحف اسم شيخه (عبيدة بن حميد) في الأثر (١٢٠٩٦) فصار (عبد الحميد)، والصحيح ما أثبته، والله تعالى أعلم.
وانظر: كتاب بحر الدم " (١/ ٤٤٣ / ١١٠٤)، و " التاريخ الكبير " (٨/ ٢٤٨ / ٢٨٨٩)، و " التاريخ الصغير " (٢/ ٣٨٠ / ٢٩٥٥)، و " الكنى والأسماء " (١/ ٤١١ / ١٥٤٦)، و " الأسماء المفردة " (١/ ١٧٥ / ٤١٨)، و " الجرح والتعديل " (٩/ ١١٩ / ٥٠١)، و " الثقات " (٩/ ٢٤٦ / ١٦٢٤٦)، " تسمية من أخرجهم البخاري ومسلم " (١/ ٢٥٥ / ١٨٨٨)، " ومشتبه أسماء المحدثين " (١/ ٢٤٩ / ٤٤٢)، و " رجال مسلم " (٢/ ٣٢٨ / ١٨٠٦)، و " الإرشاد " (٢/ ٥٧٨)، و " تهذيب الكمال " (٣٠/ ٣١١ / ٦٦٠٣)، و " تذكرة الحفاظ " (٢/ ٥٠٧ / ٥٢٢)، و " السير " (١١/ ٤٦٥ / ١١٨)، و " طبقات المحدثين " (١/ ٩١ / ١٠٢٤)، و " تقريب التهذيب " (١/ ٥٧٤ / ٧٣٢٠)، وطبقات الحفاظ " (١/ ٢٢٤ / ٥٠٠)، و " رجال تفسير الطبري " (٥٧١/ ٢٧٥١).
**********
وقد وافقه في كنيته، واسمه، ولقبه، وبلده، ابن ابن أخيه:
١٨٥ - تمييز أبو السري، هناد بن السري بن يحيى بن السري التميمي، الكوفي: توفي سنة إحدى وثلاثين وثلاثمائة، من الثانية عشرة، وكان ثقة، عسرًا في الحديث.
روى عن: أبيه السري بن يحي بن السري التميمي، وأبي سعيد عبد الله بن',NULL,'652',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (655,'سعيد الأشج.
روى عنه: ابن أخيه، أبو بكر أحمد بن محمد بن السري المعروف بـ (ابن أبي دارم)، وغيره.
وله ترجمة تمييزية في: " تهذيب الكمال " (٣٠/ ٣١٣ / ٦٦٠٤)، و " تهذيب " (١١/ ٦٣ / ١١٠)، " وتقريب التهذيب " (١/ ٥٧٤ / ٧٣٢١).
**********
٣٦١ - الهيثم بن بشر (تهـ: ١٧٦١)
(طب: تهـ ر / ش)
الهيثم بن بشر بن معروف – والصحيح -: القاسم بن بشر بن معروف، أبو محمد البغدادي (١٠٥١٤)، وقد مضت ترجمته برقم (٢٤١).
روى عن: الأسود بن عنبر (تهـ ٧٦١).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: لم أقف له على ترجمة، ولا على ذكر في كتب الرجال والحديث التي بين يدي، واحتمال أن يكون اسمه مصحفًا من اسم شيخ آخر للطبري هو (القاسم بن بشر بن معروف) ليس ببعيد لتقارب الحروف، وقد ترجمت للقاسم بن بشر في الترجمة رقم (٢٤١) من كتابي هذا.
وقد وقفت على اسم رجل أصبهاني يوافقه في اسمه، واسم أبيه: ويخالفه في اسم جده، ولا يمكن أن يكون هو بحال لتأخره وهو:
١٨٦ - تمييز أبو نصر، الهيثم بن بشر بن خالد.
روى عن: عبد الرحمن بن المبارك، وقيس بن حفص، والبصريين.
روى عنه: محمد بن علي بن الجارود.
وانظر: " طبقات المحدثين بأصبهان " (٣/ ٣٤٤)، و (٣/ ٢٦٠ / ٣٤٧).
**********',NULL,'653',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (656,'<span data-type="title" id=toc-40>[١٩] باب الواو </span>(من اسمه واصل)
٣٦٢ - أبو القاسم الأسدي (٦٢٧٤)
(مسلم، والأربعة)
(طب: ١٩ ر / ٣ش)
(تس ١٦ ر / ٣ش)، و (تخ ٢ر / ١ش)، و (تهـ ١ر / ١ش)، و (تق ١ر / ١ش)
أبو القاسم - وقيل: أبو محمد -، واصل بن عبد الأعلى بن هلال، الأسدي، الكوفي، والد عبد الأعلى بن واصل: توفي سنة أربع وأربعين ومائتين، من العاشرة، ثقة.
روى عن: عبيد بن القاسم البزاز (تس ٣٤٥٢٥)، ومحمد بن فضيل (تس ٦٢٧٤ و ١٠١٤٠ و ١٠١٤٢ و ١٠١٦٦ و ١١٩٥١ و ١٢٠٤٥ و ١٤٥٦٩ و ٢٢١٣٧ و ٢٢٥٥٧ و ٢٤١٢٦ و ٣١٠٥٦ و ٣٤٥٣١ و ٣٥٤٧٨)، (تهـ ١٤٦٧)، (تق ٩٦٢)، و (تخ ١/ ٢٨ و ١/ ٣٩)، و (م، ٤)، ووكيع بن الجراح (تس ٣٤٥٣٢ و ٣٧٤٦٩)، و (س).
روى عنه: الحسن بن سفيان " الثقات " (٩/ ٢٣١ / ١٦١٦٥)، وعبدان بن أحمد الأهوازي " المعجم الكبير " (٢/ ٢٠٠ / ١٨١٦)، وموسى بن إسحاق الخطمي " المستدرك " (٤/ ٤٣٨ / ٨١٩٥).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقًا.
قلت: أغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري "، ولعلهما اعتبراه وابنه (عبد الأعلى) شيخًا واحدًا حيث ترجمًا لابنه كما بينت ذلك في ترجمة ابنه رقم (١٤٧) من هذا الكتاب.
وانظر: " الجرح والتعديل " (٩/ ٣٢ / ١٤٤)، و " الثقات " (٩/ ٢٣١ / ١٦١٦٥)، و " تهذيب الكمال " (٣٠/ ٤٠٤ / ٦٦٦٤)، و " الكاشف " (٢/ ٣٤٦ / ٦٠٢٩)، و " تهذيب التهذيب " (١١/ ٩٢ / ١٧٩)، و " تقريب التهذيب " (١/ ٥٧٩ / ٧٣٨٤).
**********',NULL,'654',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (657,'٣٦٣ - أبو همام السكوني (٢٧٤٩)
(مسلم: ٣ أحاديث، والبخاري في التاريخ، وأبو داود، والترمذي، وابن ماجة)
(طب: ٢١ ر / ١٠ ش)
(تس: ٥ر / ٣ش)، و (تق: ١٦ ر / ٧ش)
أبو همام، الوليد بن شجاع بن الوليد بن قيس، السكوني، الكندي، الكوفي، نزيل بغداد: توفي سنة ثلاث وأربعين ومائتين، من العاشرة، ثقة.
روى عن: أبي نعيم إسحاق بن الفرات (تق ١٣٤)، وحفص [بن غياث] (تق ١٣٧)، أبيه أبي بدر شجاع بن الوليد السكوني (تس ٢٨٢٤١)، و [عبد الله] ابن إدريس (تق ١٣٨ و ١٦١) [وعبد الله] ابن المبارك (تق ١٤٨ و ١٥٦ و ١٥٧ و ١٦٢)، وعبد الله بن وهب المصري (تس ١٤٦٩٩)، (م، د)، و [عبد الرحمن] المحاربي (تق ١٤٠، ١٥٤) وعبد الوهاب بن عطاء [الخفاف] (تق ١٥٠)، وعلي بن مسهر – قاضي الموصل – (تس ٢٧٤٩ و ٤١١٦ و ٨٧٤٧)، و (ق)، وهيثم [ابن بشير الواسطي] (تق ١٣٩ و ١٤١ و ١٤٢ و ١٦٠ و ١٦٣).
روى عنه: أحمد بن أحمد بن الحسن بن عبد الجبار – " سنن البيهقي " (١٠/ ١٤٥ / ٢٠٣٠٠)، وإسحاق بن بنان " سنن البيهقي " (٥/ ٢٦٤ / ١٠١٨٤)، والحسن بن سفيان " صحيح ابن حبان "، (٢/ ٥٠٨ / ٧٢٩)، والحسن بن علي المعمري " المستدرك " (٣/ ٩٩ / ٤٥١٩)، والحسين بن محمد بن حاتم " المستخرج " (١/ ٣٧٨ / ٧٤٢)، وعبد الله بن أحمد بن حنبل " الحلية " (١/ ٥١). وعلي بن عبد الحميد الغضائري " صحيح ابن حبان " (٦/ ٣٤٤ / ٢٦١٠)، وعلي بن عبد الصمد " سنن البيهقي " (٧/ ٢٥٧ / ١٤٢٧٠)، ومحمد بن يعقوب الصفار " سنن البيهقي " (٦/ ٧٧ / ١١١٩٩).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: صدوقًا يكتب حديثه، ولا يحتج به، وليس به بأس، يأخذ الحديث أخذا رديئا، وتكلم فيه أحمد بن حنبل، وما قال يحيى بن معين فيه سوءا قط، وكان يقول: لا بأس به، ليس هو ممن يكذب، ليس له بخت. وكانوا يضعفونه في الجراح أبي وكيع.',NULL,'655',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (658,'قلت: وروى البخاري في " التاريخ الكبير " (١/ ٨٣ / ٢٢٧) عنه، عن أبيه.
وانظر: " الطبقات الكبرى " (٧/ ٣٣٤، و ٧/ ٣٦٢)، و " بحر الدم " (١/ ٤٥١ / ١١٢١)، و " التاريخ الصغير " (٢/ ٣٧٨ / ٢٩٤٩)، و " الكنى والأسماء " (١/ ٨٨٨ / ٣٦٠٢)، و " معرفة الثقات " (٢/ ٣٤٢ / ١٩٤١)، و " الجرح والتعديل " (٩/ ٧ / ٢٨)، و " الثقات " (٩/ ٢٢٧ / ١٦١٤٣)، و " مولد العلماء ووفياتهم " (٢/ ٥٣٤)، و " تاريخ أسماء الثقات " (١/ ٢٤٦ / ١٥٠٢)، و " أسماء التابعين " (٢/ ٢٦٩ / ١٣٥٧)، و " رجال مسلم " (٢/ ٣٠٠ / ١٧٤٣) و " تاريخ بغداد " (١٣/ ٤٧٣ / ٧٣٢٠)، و " الضعفاء والمتروكين لابن الجوزي " (٣/ ١٨٤ / ٣٦٥٢)، و " تهذيب الكمال " (٣١/ ٢٢ / ٦٧٠٩)، و " الكاشف " (٢/ ٣٥٢ / ٦٠٦٨)، و " المقتنى " (٢/ ١٢٩ / ٦٤١٧)، و " من تكلم فيه " (١/ ١٩٠ / ٣٦٢)، و " طبقات المحدثين " (١/ ٩١ / ١٠٢٥)، و " الميزان " (٧/ ١٣٢ / ٩٣٨٢)، و " المغني " (٢/ ٧٧٢ / ٦٨٥٨)، و " تهذيب التهذيب " (١١/ ١١٩ / ٢٢٦، و ١٢/ ٢٩٢ / ١٢٢٢)، و " تقريب التهذيب " (١/ ٥٨٢ / ٧٤٢٨)، و " لسان الميزان " (٧/ ٤٢٦ / ٥١٦٤)، و " رجال تفسير الطبري " (٥٧٧/ ٢٧٧٨).

<span data-type="title" id=toc-41>[٢٠] باب الياء </span>(من اسمه يحيى)
٣٦٤ - يحيى المسعودي (٨٤)
(النسائي)
(طب: ٣٩ ر / ١ش)
(تس: ٢٩ ر / ١ش)، و (تهـ: ٩ر / ٢ش)، و (تق: ١ر / ١ش)
يحيى بن إبراهيم بن محمد بن أبي عبيدة بن معن بن عبد الرحمن بن عبد الله بن مسعود، المسعودي، الكوفي: من الحادية عشرة، صدوق.
روى عن: أبيه إبراهيم بن محمد بن أبي عبيدة بن معن (تس ٨٤ و ٥٣٨٢ و ٨٣٢٥ و ٨٨١٢ و ٩٧٤٩ و ١٢٨٣٣ و ٢١١٢٤ و ٢٢٦٧٦ و ٢٣٠٨٤ و ٢٣٧١٨ و ٢٣٨٢٣ و ٢٤٧٩٩ و ٢٤٩٠٧ و ٢٦٠٧٣ و ٢٦٢٠٨ و ٢٧٨٨٢ و ٢٩٨٤٠ و ٣١٣٠١ و ٣١٤٦١ و ٣١٥٢١ و ٣١٦٤٢ و ٣٣٥٢٧ و ٣٣٧٢٧ و ٣٣٨٨١ و ٣٣٩٠٣ و ٣٥٦٦٠ و ٣٦١٤٢ و ٣٦٨١٦ و ٣٨٢٤٤)، و (تق ٣٨٤)، و (تهـ ١١٠ و ٩٥٨ و ١٢٠٤ و ١٨٧٢ و ١٩٠٤ و ١٩٦٠ و ٢٩١٧ و ٢٩٩٩)، وجده محمد بن أبي عبيدة بن معن (تهـ ٢٧٥).
روى عنه: أبو جعفر محمد بن جرير الطبري.',NULL,'656',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (659,'التعديل والتجريح: قال النسائي، وابن حجر: " صدوق ".
وانظر: " الجرح والتعديل " (٩/ ١٢٧ / ٥٤٠)، و " الثقات " (٩/ ٢٦٥ / ١٦٣٤٦)، و " تهذيب الكمال " (٣١/ ١٨٧ / ٦٧٧٧)، و " تهذيب التهذيب " (١١/ ١٥٤ / ٢٩٩)، و " تقريب التهذيب " (٥١٧/ ٧٤٩٥)، و " رجال تفسير الطبري " (٥٨٠/ ٢٧٩٢).
**********
٣٦٥ - يحيى القرقساني (٣٣٧٣٠)
(طب: ٢ر / ٢ش)
(تس: ١ر / ١ش)، و (تهـ: ١ر / ١ش)
يحيى بن بشر – وقيل: بشير، القرقساني، من الحادية عشرة، لم أعرفه، ولم أجد له ترجمة، ولم أقف له في " التفسير " على غير هذا الأثر، وآخر في " تهذيب الآثار " وله ترجمة مقتضبة في: " الإكمال " لابن ماكولا (١/ ٢٤٩)، وسكت عنه، والباقي مرويات في الكتب، استخرجت منها معجم شيوخه وتلاميذه، ولم يتعرض الشيخ التركي في تحقيقه " لتفسير الطبري " ٢٢/ ٤٥٥)، ومن قبله الشيخ في تحقيقه لـ " تهذيب الآثار " (٢/ ٥٧٠ / ٨٩٧)، لترجمته بشيء.
روى عن: عبد العزيز بن عبد الرحمن الأموي (تس ٣٣٧٣٠)، ويوسف بن منازل (تهـ ١٣٩٦)، وحبان بن الأغلب بن تميم المسعودي البصري " الثقات " (٨/ ٢١٤ / ١٣٠٦٢)، وسعيد بن مسلمة بن هشام بن عبد الملك " تهذيب الكمال " (١١/ ٦٥ / ٢٣٥٧)، ومحمد مصعب القرقساني " الكامل " (٢/ ٥٢)، ومروان بن معاوية الفزاري " معجم الصحابة " (٢/ ١٧٨)، والوليد بن سلمة الشامي – قاضي الأردن – " تهذيب الكمال " (١١/ ٦٥).
روى عنه: أحمد بن الحسين بن عبد الصمد [الجدادمي] " الكامل " (٥/ ١٣)، و " المجروحين " (٣/ ٨٠)، وإسحاق بن بشير الرازي " الإكمال " (١/ ٢٩٤)، وعلي بن أحمد بن علي بن عمران الجرجاني – نزيل حلب – " الكامل " (٢/ ٥٢)، وعلي بن يعقوب أبو الحسن الربيعي " تاريخ دمشق " (٤٣/ ٢٩٠ / ٥١٢٠)، ومحمد بن أحمد حمدان بن عيسى أبو الطيب الرسعني الوراق " تاريخ',NULL,'657',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (660,'دمشق " (٥١/ ٣٢ - ٣٣/ ٥٨٩٠)، وشاذان البصري " معجم الصحابة " (٢/ ١٧٨).
التعديل والتجريح: لم أقف له على ترجمة إلا في " الإكمال " لابن ماكولا (١/ ٢٤٩)، وسكت عنه، والباقي مرويات استخرجت منها معجم شيوخه وتلاميذه.
**********
٣٦٦ - أبو زكريا الشيباني (٧٨١٧)
(مسلم، والأربعة)
(طب: ٨ر / ٣ش)
(تس: ٦ر / ٢ش)، و (تهـ: ١ر / ١ش)، و (تق: ١ر / ١ش)
أبو زكريا، يحيى بن حبيب بن عربي، الحارثي، وقيل: الشيباني – البصري: توفي سنة ثمان وأربعين ومائتين، أو بعدها، من العاشرة، ثقة.
روى عن: خالد بن الحارث (تس ٧٨١٧ و ٩٢٢١ و ١٤٥٠٩ و ٣٤٩٠٥ و ٣٧٨٣٩)، و (تهـ ٣١٧)، و (م، د، ت، س)، وروح بن عبادة (تس ٢٨٦٧٣)، و (م، د)، وعبد الوهاب [الثقفي] (تق ٥٦٤).
روى عنه: إبراهيم بن أبي طالب " المستدرك " (١/ ٦٨١ / ١٨٥٢)، وبكر بن محمد الفزاز " المعجم الكبير " (٥/ ٤٠ / ٤٥٢٩)، وجعفر بن أحمد بن نصر " سنن البيهقي " (٣/ ٩٦ / ٤٩٤١)، والحسين بن الليث المروزي " مسند أبي عوانة " (٥/ ١٩٣ / ٨٣٦٠)، وسهل بن أحمد بن عثمان الواسطي " المستدرك " (١/ ٢٠١ / ٣٩٦)، وعبد الله بن قحطبة " صحيح ابن حبان " (٢/ ٤٩ / ٣٣٧)، وعبد الله بن محمد " سنن البيهقي " (٣/ ٩٦ / ٤٩٤١)، ومحمد بن محمد بن رجاء " مسند أبي عوانة " (٥/ ١٩٣ / ٨٣٦٠)، وأبو عمران التستري " مسند أبي عوانة " (٣/ ٤٢٩ / ٥٥٨٦).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقًا، مأمونًا.
وقد تصحف اسمه في الأثر رقم (تس ٢٨٦٧٣) من يحيى إلى (بحر).',NULL,'658',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (661,'وانظر: " التاريخ الصغير " (٢/ ٣٨٦ / ٢٩٧٣)، و " الجرح والتعديل " (٩/ ١٣٧ / ٥٨١)، و " الثقات " (٩/ ٢٦٥ / ١٦٣٤٨)، و " رجال مسلم " (٢/ ٣٣٥ / ١٨١٨)، و " تسمية من أخرجهم البخاري ومسلم " (١/ ٢٦١ / ١٩٥٧)، و " تهذيب الكمال " (٣١/ ٢٦٢ / ٦٨٠٦)، و " الكاشف " (٢/ ٣٦٣ / ٦١٤٩)، و " سير أعلام النبلاء " (١١/ ١٥٦ / ٦١)، و " تهذيب التهذيب " (١١/ ١٧٣ / ٣٣٠)، و " تقريب التهذيب " (١/ ٥٨٩ / ٧٥٢٦)، و " طبقات الحفاظ " (١/ ٢٠٠ / ٤٤٠)، و " رجال تفسير الطبري " (٥٨٢/ ٢٨٠١).
**********
٣٦٧ - يحيى البزار (٥١)
(ابن ماجة)
(طب: ٢١ ر / ٦ش)
(تس: ١٤ ر / ٤ش)، و (تهـ: ٧ر / ٥ش)
يحيى بن داود بن ميمون، البزار – براء مهملة – الواسطي: توفي سنة أربع وأربعين ومائتين من صغار العاشرة، ثقة.
روى عن: إبراهيم بن يزيد بن مردانبة (تس: ١١٥٤٨)، و (تهـ ٩١٦)، وإسحاق بن يوسف الأزرق (تس ١٠٥ و ١٣٨١٥ و ٢٥٢٥٤ و ٢٨٦٦٩ و)، و (تهـ ٧٦١)، وأبي أسامة حماد بن أسامة (تس ٥١ و ٤١٨٨ و ٤٤٥٤ و ١٧٥٣٣ و ٢١٧٣٥ و ٢١٧٣٦ و ٢٣٤٨٤ و ٢٦٩٩٤ و ٣٥٢٢٥)، و (تهـ ٥٤٠ و ٥٩٢ و ٧٦٧)، وأبي معاوية محمد بن خازم الضرير (تهـ ٧٧٧)، ويحيى بن سعيد القطان (تس ٣٧٢٧٤)، وأبي أحمد الزبيري (تهـ ١٤٣٩).
روى عنه: أيوب بن محمد الوزان " المعجم الكبير " (١١/ ٢٥٠ / ١١٦٤٣)، وحسين بن إسحاق الواسطي " المعجم الكبير " (٢٣/ ٣٢٧ / ٧٥٢)، وعبد الله بن أحمد بن حنبل " الإكمال " (٤٦٤/ ٩٧١)، أبو بكر البزار " مسند البزار " (٧/ ١٩٧ / ٢٧٦٦).
التعديل والتجريح: ذكره ابن حبان في " الثقات " وقال: " مستقيم الحديث ".
وقال المزي في " تهذيب الكمال " (٣١/ ٢٩٥): " يحيى بن داود بن ميمون، الواسطي أبو السقر، العسكري، روى عنه: ابن ماجة، مات بواسط، سنة أربع وأربعين ومائتين ".',NULL,'659',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (662,'وهكذا ذكره أبو القاسم في " المشايخ النبل "، ولم يذكر فيهم (يحيى بن يزداد)، وذلك من أوهامه، فإن ابن ماجة، إنما روى عن: يحيى بن يزداد، وهو أبو السقر العسكري روى عنه حديثًا واحدًا، عن حسين بن محمد، عن جرير بن حازم، عن أيوب، عن عكرمة عن ابن عباس: " أن جارية بكرًا أتت النبي صلى الله عليه وسلم ذكرت أن أباها زوجها وهي كارهة ".
هكذا هو في عامة الأصول القديمة، ووقع في بعض النسخ المتأخرة (يحيى بن داود) أبو السقر العسكري، وهو خطأ. فإن (يحيى بن داود)، واسطي، وليس بعسكري، ولا تعرف له كنية، وهو: (يحيى بن داود بن ميمون) ". انتهى كلام المزي.
قلت: ولقبه صاحب " تاريخ واسط " (١/ ٢٠٨)، وابن أبي عاصم في " الآحاد والمثاني " (٤/ ٣٣١ / ٢٣٦٦) بـ (البزار).
ولعله سقط من الإسناد رقم (تس: ٢١٧٣٥)، اسم (يحيى)، فبقي هكذا (حدثني داود الواسطي قال ... ) يعززه ويؤيده الأثر الذي بعده رقم (٢١٧٣٦).
وقد فصلت القول فيه في الترجمة رقم (١١١)، ترجمة (داود – لعله – الواسطي).
وانظر: " الثقات " (٩/ ٢٦٦ / ١٦٣٥٥)، و " تهذيب الكمال " (٣١/ ٢٩٥)، " الإكمال " (٤٦٤/ ٩٧١)، و " تعجيل المنفعة " (١/ ٤٤٢ / ١١٦٤)، وتهذيب التهذيب " (١١/ ١٨٠ / ٣٤٤)، و " تقريب التهذيب " (١/ ٥٩٠ / ٧٥٤١)، و " رجال تفسير الطبري " (٥٨٣/ ٢٨٠٤).
**********
١٨٧ - تمييز أبو السقر، يحيى بن داود – وقيل: بن يزداد، العسكري وقيل: البغدادي – الوراق، وكنى ابن عدي (أبا السقر) – بالسين المهملة – في " الكامل " (٣/ ٢٢٣ / ٧١٦، و ٥/ ١٢٤، و ٧/ ٤٧) (أبا الصقر) – بالصاد المهملة – ولقبه (الوراق)، ونسبه (بغدادي).
**********
٣٦٨ - أبو زكريا البكراوي (تهـ ٢٢٩٩)
(الترمذي، والنسائي، وابن ماجة)
(طب: تهـ: ٣ر / ١ش)',NULL,'660',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (663,'أبو زكريا، يحيى بن درست – بضمتين، وسكون المهملة – ابن زياد، القرشي، الهاشمي، البكراوي، البصري: من العاشرة، ثقة.
روى عن: إبراهيم بن عبد الملك القناد (تهـ ٢٢٩٩ و ٢٥٦٣ و ٢٦٢٦)، و (ت، س).
روى عنه: الترمذي، والنسائي، وابن ماجة، وأبو جعفر الطبري.
التعديل والتجريح: اتفقت أقوال أئمة الجرح والتعديل فيه على كونه: ثقة.
وانظر: " الثقات " (٩/ ٢٦٩ / ١٦٣٧٣)، " تهذيب الكمال " (٣١/ ٢٩٦ / ٦٨٢١)، و " الكاشف " (٢/ ٣٦٥ / ٦١٦٤)، و " تهذيب التهذيب " (١١/ ١٨١ / ٣٤٥)، " تقريب التهذيب " (١/ ٥٩٠ / ٧٥٤٢).
**********
٣٦٩ - أبو زكريا اليربوعي (٧٣)
(الترمذي)
(طب: ٦٨ ر / ٨ش)
(تس ٥٣ ر / ٥ش)، و (تخ ١ر / ١ش)، و (تهـ ١٢ ر / ٥ش)، و (تق ٢ر / ٢ش)
أبو زكريا، يحيى بن طلحة بن أبي كثير، اليربوعي، الكوفي: من العاشرة، لين الحديث.
روى عن: أبي الأحوص سلام بن سليم (تهـ ١٨١٨)، وشريك بن عبد الله (تس ٧٣ و ٣٢٣١ و ١٧٦٣٠ و ٢٣٤٣٦ و ٢٣٤٤٥ و ٢٣٤٦٢ و ٢٣٤٧٠ و ٢٣٩٥٨ و ٢٣٩٦١ و ٢٦٤٦٥ و ٣١١٥٥ و ٣١١٦٣ و ٣٤٦٨١ و ٣٦٥٩١)، و (تهـ ٥٨٢)، وعباد بن العوام (تهـ ١٠٦٧)، وعيسى بن يونس (تس ٣١١٢٩)، وفضيل بن عياض (تس ٤٢١ و ٢٤٢٥ و ٣٣٩٣ و ٣٣٩٤ و ٧٣١٦ و ١٠١٠٦ و ١٢٥٥٢ و ١٢٦٤٨ و ١٢٦٥٥ و ١٣٣٩٥ و ١٣٤٨٦ و ١٤٦٢٣ و ١٤٦٤٢ و ١٧٦٤٩ و ١٧٦٨٤ و ١٩١٨٤ و ٢٠٥٦٧ و ٢٢٥١٥ و ٢٤٧٢٤ و ٢٨٧٢٣ و ٢٩٦٢٦ و ٢٨١٢٢ و ٣١١٢٥ و ٣٢٠٨٤ و ٣٣١٩٧ و ٣٤٠٨٠ و ٣٤٣٣٥ و ٣٥٣٣ و ٣٥٣٥٠ و ٣٥٧٧٧ و ٣٦٦٢٩ و ٣٦٦٩٧ و ٣٧٩٧٠)، و (تق ٢٨)، و (تهـ ٨٧٤ و ٨٨٠ و ٩٤٩ و ١٠٦٨ و ١١٤١ و ٢١٩٤ و ٢٧٦٦)، و (تخ ١/ ١٩٥)، وهشيم بن بشير (تس ٤٨٦٩)، و (تق ٨٤٠)، ويحيى بن اليمان (تهـ ٩٥٠ و ٢٧٧٢)، وأبي بكر بن عياش (تس ٧٨٠٧ و ١٩١١٤ و ٣١٧٦٩ و ٣٢٦١٠)، و (ت). وتصحف في الأثر الأخير إلى (أبي بكير) مصغر.',NULL,'661',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (664,'روى عنه: الحسن بن سفيان " صحيح ابن حبان " (٣/ ٢٨٦ / ١٠٠٦)، والحسين بن محمد بن حاتم – عبيد العجل – " حلية الأولياء " (٨/ ١٢٥)، وعبد الرحمن بن سلم الرازي " المعجم الكبير " (١٧/ ٢٢٧ / ٦٢٧)، وعلي بن سعيد الرازي " المعجم الأوسط " (٤/ ١٦٢ / ٣٨٧٢)، وأبو جعفر محمد بن عبد الله الحضرمي " المعجم الأوسط " (٦/ ٣٨ / ٥٧٢٧).
التعديل والتجريح: اتفقت أقوال أئمة الجرح والتعديل على توهينه فقالوا " ليس بشيء، يغرب، صويلح الحديث، وقد وثق، وكذبه علي بن الحسين بن الجنيد، خطأه الصاغاني ".
وانظر: " الضعفاء والمتروكين للنسائي " (١١٠/ ٦٤١)، و " الجرح والتعديل " (٩/ ١٦٠ / ٦٦٣)، و " الثقات " (٩/ ٢٦٤ / ١٦٣٤٢)، و " الضعفاء والمتروكين لابن الجوزي " (٣/ ١٩٧ / ٣٧٢٨)، و " تهذيب الكمال " (٣١/ ٣٨٨ / ٦٨٥١)، و " الكاشف " (٢/ ٣٦٨ / ٦١٨٨)، و " المغني في الضعفاء " (٢/ ٧٣٨ / ٦٩٩٥)، و " ميزان الاعتدال " (٤/ ٣٨٧ / ٩٥٤٩)، و " لسان الميزان " (٧/ ٤٣٣ / ٥٢١٦)، و " تهذيب التهذيب " (١١/ ٢٠٤ / ٣٧٩)، و " رجال تفسير الطبري " (٥٨٦/ ٢٨٢٠)
**********
١٨٨ - تمييز أبو طلحة، يحيى بن طلحة بن عبيد الله، القرشي، التيمي المدني، تابعي من الثالثة، ثقة.
روى عن: أبيه، وأمه، سعدى المرية، وهو والد إسحاق بن يحيى.
روى عنه: ابناه طلحة، وبلال.
وانظر: " الطبقات الكبرى " (٥/ ١٦٤)، و " التاريخ الكبير " (٨/ ٢٨٣ / ٣٠١٢)، و " معرفة الثقات " (٢/ ٢٥٤ / ١٩٨٢)، و " الجرح والتعديل " (٩/ ١٦٠ / ٦٦١)، و " الثقات (٥/ ٥١٨ / ٦٠٢٢)، و " مشاهير علماء الأمصار " (١/ ٧٤ / ٥٢٦)، و " تهذيب الكمال " (٣١/ ٣٨٧ / ٦٨٥٠)، والكاشف (٢/ ٣٦٨ / ٦١٨٧)، و " تهذيب التهذيب " (١١/ ٢٠٤ / ٣٧٨)، و " تقريب التهذيب " (١/ ٥٩٢ / ٧٥٧٢).
**********
١٨٩ - تمييز أبو طلحة، يحيى بن طلحة، الأسلمي، المرادي، ابن بنت سعيد بن جهمان – البصري.',NULL,'662',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (665,'روى عن: النبي صلى الله عليه وسلم – المراسيل -.
روى عنه: البصريون.
وانظر: " التاريخ الكبير " (٨/ ٢٨٣ / ٣٠١٣)، و " الجرح والتعديل " (٩/ ١٦٠ / ٦٦٢)، و " الثقات " (٧/ ٥٩٥ / ١١٦٣١)، و " لسان الميزان " (٦/ ٢٦٣ / ٩٢٣).
**********
٣٧٠ - أبو زكريا السهمي (١١٧)
(ابن ماجة)
(طب: ١١ ر / ٣ش)
(تس: ٩ر / ٣ش)، و (تهـ: ٢ر / ١ش)
أبو زكريا، يحيى بن عثمان بن صالح بن صفوان السهمي، القرشي، مولاهم، المصري: توفي في ذي القعدة سنة اثنتين وثمانين ومائتين، من الحادية عشرة، صدوق، رمي بالتشيع، ولينه بعضهم، لكونه حدث من غير أصله.
روى عن: أبي صالح عبد الله بن صالح كاتب الليث (تس ١١٧ و ١٢٣ و ٢٣٦ و ٢٦٨ و ١٠٧)، وأبيه عثمان بن صالح السهمي (تس ١٧٤٣٢ و ٢٩٩٩٤ و ٢٩٩٩٥)، و (تهـ ٦٦٢ و ٢٧١٦)، و (ق)، ويحيى بن عبد الله بن بكير (تس ٢٥٥١٦)، وأبي علي أحمد بن الحكم " المغني في الضعفاء " (١/ ٣٥٠ / ٣٢٩٤)، وحرملة بن يحيى التجيبي الطلحي " تاريخ بغداد " (٣/ ١٩١ / ١٢٣٤)، وعبد الله بن عيسى بن أبي المكدم المصري " تهذيب الكمال " (١٣/ ٣٠١ / ٢٩٣١)، ومحمد بن الحارث المؤذن المصري " تهذيب الكمال " (٣١/ ٢٩٩ / ٦٨٢٣).
روى عنه: القاسم بن علي الجوهري " سنن البيهقي " (٧/ ٣٩٧ / ١٥٠٨١)، ومحمد بن إسحاق بن إبراهيم الآملي " أمثال الحديث " (١
/ ١٩)، وأبو عبد الله محمد بن إسماعيل الفارسي " سنن الدارقطني " (٣/ ١٦٩ / ٢٥٣)، وأبو عمرو، محمد بن عبد الرحمن الجرجاني " تاريخ جرجان " (١/ ٣٩٨ / ٦٧١)، ومحمد بن عمرو العقيلي " تاريخ بغداد " (٣/ ١٩١ / ١٢٣٤)، ومحمد بن نصر المروزي " تعظيم قدر الصلاة " (١/ ٤٤٢ / ٤٥٤)، وأبو عوانة الإسفراييني " مسند أبي عوانة " (٥/ ٣٦٣ / ٨٦٦٣)،',NULL,'663',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (666,'وأبو القاسم الطبراني " مسند الشاميين " (١/ ١٨٥ / ٣٢٢) – وقد أكثر عنه فيه، وفي المعاجم الثلاثة.
التعديل والتجريح: اختلفت أقوال أئمة الجرح والتعديل فيه بين كونه: صدوقًا علامة، حافظًا إخباريًا، له ما ينكر، رمي بالتشيع، ولينه بعضهم لكونه حدث من غير أصله، فطعن فيه لأجل ذلك، وتكلموا فيه، وكان عالمًا بأخبار البلد، [يعني مصر]، وبموت العلماء، وكان حافظًا للحديث: وحدث بما لم يكن يوجد عند غيره.
وسقط اسمه (يحيى) في الأثر رقم (تهـ ٢٧١٦)، وبقى اسم أبيه (عثمان).
وانظر: " الجرح والتعديل " (٩/ ١٧٥ / ٧٢١)، و " مولد العلماء ووفياتهم " (٢/ ٦٠٧)، و " تهذيب الكمال " (٣١/ ٤٦٢ / ٦٨٨٣)، و " الكاشف " (٢/ ٣٧١ / ٦٢١٣)، و " ميزان الاعتدال " (٤/ ٣٩٦ / ٩٥٨٦)، و " سير أعلام النبلاء " (١٣/ ٣٥٤ / ١٧١)، و (من تكلم فيه " (١/ ١٩٧ / ٣٧٥)، و " المغني في الضعفاء " (٢/ ٧٤٠ / ٧٠١٨) و " تهذيب التهذيب " (١١/ ٢٢٥ / ٤١٥)، و " تقريب التهذيب " (١/ ٥٩٤ / ٧٦٠٥)، و " رجال تفسير الطبري " (٥٨٨/ ٢٨٣١).
**********
٣٧١ - يحيى بن نصر (٢٦٥٠)
(طب: تس: ١ر / ١ش)
يحيى بن نصر. والصحيح: بحر بن نصر – وهو: أبو عبد الله، بحر بن نصر بن سابق، الخولاني، مولاهم، المصري: مولده سنة أربع وسبعين ومائة – وقيل: سنة ثمانين ومائة؛ أو: إحدى وثمانين ومائة، وتوفي سنة سبع وستين ومائتين، وله سبع وثمانون سنة، من الحادية عشرة، ثقة، صدوق، فاضل، مشهور، ومضى برقم (٥٩).
روى عن: يحيى بن حسان التنيسي (تس ٢٦٥٠).
روى عنه: أبو جعفر محمد بن جرير الطبري.
**********
١٩٠ - تمييز أبو عبد الله، يحيى بن نصر بن حاجب بن عمرو بن سلمة، القرشي المروزي، البغدادي: المتوفى سنة خمس عشرة ومائتين، لا بأس به.
روى عن: مالك بن أنس، ويونس بن يزيد الأيلي، وابن شبرمة.',NULL,'664',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (667,'روى عنه: أحمد بن سيار
ولم يخرج له الطبري في تفسيره وكتبه الأخرى أي أثر، لا مباشرة ولا بواسطة.
وانظر: " ضعفاء العقيلي " (٤/ ٤٣٣ / ٢٠٦٤)، و " الجرح والتعديل (٩/ ١٩٣ / ٨٠٥)، و " الثقات " (٩/ ٢٥٤ / ١٦٢٩١)، و " الكامل " (٧/ ٢٤٦ / ٢١٤٦)، و " تاريخ بغداد " (١٤/ ١٥٩ / ٧٤٧٢)، و " لسان الميزان " (٦/ ٢٧٨ / ٩٨٣).
**********
٣٧٢ - أبو بكر البزاز (٢٨٤)
(طب: ٧٨ ر / ٥ش)
(تس: ٧٧ ر / ٥ش)، و (تخ: ١ر / ١ش)
أبو بكر، يحيى بن أبي طالب، واسم أبي طالب (جعفر) – ابن عبد الله بن الزبرقان، العباسي، مولاهم، الواسطي، بغدادي، البزاز – بالزاي المعجمة: - مولده سنة اثنتين وثمانين ومائة، وتوفي سنة خمس وسبعين ومائتين، من الحادية عشرة، صدوق، لا بأس به.
روى عن: أحمد بن بكر (تس ١٥٦٧٣)، وداود بن المحبر (تس ٣٣٦٧٧)، وعلي بن عاصم (تس ٦٤٨١)، و (تخ ١/ ٤٧٢)، يحيى بن السكن (تس ٢٢٣٨٥)، ويزيد بن هارون (تس ٢٨٤ و ٢٨٧ و ٨٥٦ و ١٠٩٤ و ١٩٦٣ و ٢٤٥٠ و ٣٩٩٩ و ٤٤٦٧ و ٤٦٧٧ و ٤٧٥٠ و ٤٧٦٩ و ٤٨٠٢ و ٤٨٢٥ و ٤٨٨٨ و ٥١٥٢ و ٥١٨٢ و ٥٣٦١ و ٥٣٧٤ و ٥٣٧٩ و ٥٤١٨ و ٥٥٠٨ و ٥٥٤٦ و ٥٧٧٦ و ٥٨٠٥ و ٦١٥٥ و ٦٢٥٨ و ٦٢٩٢ و ٦٦٣٠٤ و ٦٣٤٧ و ٦٣٩١ و ٦٤٠٤ و ٦٤٣٤ و ٦٤٤٠ و ٦٤٥٢ و ٦٤٦٦ و ٦٤٨٠ و ٦٤٨٩ و ٦٥١٥ و ٦٥٢٣ و ٦٥٣٢ و ٦٥٧٩ و ٦٦٤٤ و ٦٦٥٨ و ٧٤٤٥ و ٧٤٩١ و ٧٥١٣ و ٧٥٩٥ و ٧٦١١ و ٧٦٤٠ و ٧٦٧٦ و ٨١٤٦ و ٨٢٢٥ و ٨٣٤٠ و ٨٣٩٠ و ٨٤٨٧ و ٨٥٣٣ و ٨٧١٦ و ٨٨١٠ و ٩٢١٨ و ٩٤٠٦ و ٩٤٢٩ و ٩٤٣٠ و ٩٤٣٧ و ٩٤٥٦ و ٩٤٧١ و ٩٤٩١ و ٩٤٦٥ و ٩٧٨٩ و ٩٨٠٩ و ٩٩١٦ و ٩٩٩٥ و ١٠٤١٣ و ١٠٤٤٢ و ١٠٥٠٤ و ١٠٥٢٢ و ١٠٥٤٣ و ١٢٣٨٠)، وأحمد بن هشام بن حميد " تاريخ بغداد " (٥/ ١٩٨ / ٢٢٦٨)، وأزهر بن سعد السمان " الجرح والتعديل " (٩/ ١٣٤ / ٥٦٧)، وأبي بدر شجاع بن الوليد، وبكر بن بكار " الكامل " (١/ ٢٣٩ / ٧١)، والحارث بن منصور " تاريخ جرجان " (١/ ٤٧٢ / ٩٤٢)، والحسن بن شاذان الواسطي " التذكرة (١/ ٣١٧ / ٢٩٨)، وزيد بن',NULL,'665',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (668,'الحباب، وعبد الملك بن إبراهيم الجدي، وعبد الوهاب بن عطاء الخفاف، ومحمد بن إبراهيم الشامي " الكامل " (٦/ ٢٧١ / ١٧٥٥)، ووهب بن جرير، وأبي بكر الحنفي، وأبي داود الطيالسي، وأبي عاصم النبيل وأبي عامر العقدي، وأبي النضر.
روى عنه: إبراهيم بن أحمد العجلي " الكشف الحثيث " (٣٤/ ٤)، وإبراهيم بن محمد الهمذاني (مموس) " تذكرة الحفاظ " (٣/ ٨٣٨ / ٨١٩)، وأحمد بن سلمان النجاد، وأحمد بن يحيى بن زهير " الكامل " (١/ ٢٣٩ / ٧١)، وإسماعيل بن محمد الصفار، وجعفر بن أبي عثمان الطيالسي، وعبد الله بن إسحاق بن الخرساني، وعبد الرحمن بن أبي حاتم " الجرح والتعديل " (٩/ ١٣٤ / ٥٦٧)، وعلي بن محمد عبيد الحافظ، ومحمد الحافظ ومحمد بن أحمد بن إبراهيم الحكيمي، ومحمد بن إدريس الرازي " الجرح والتعديل " (٩/ ١٣٤ / ٥٦٧)، ومحمد بن الحسين بن بخيت " الكامل " (٥/ ١٥ / ١١٨٩)، ومحمد بن عمر الرزاز، ومحمد بن عمرو بن البختري " تاريخ الثقات " (٢٦٩/ ١٦٤٩)، ومحمد بن المنذر بن سعيد " الثقات " (٩/ ٢٧٠ / ١٦٣٧٦)، ومحمد بن منير " الكامل " (٥/ ١٩١ / ١٣٤٨)، ومحمد بن موسى الأنباري " التذكرة " (١/ ١٩ / ٨)، ومحمد بن يعقوب الأصم " الإملاء والاستملاء " (١)، ومعروف بن أبي بكر الرازي " تاريخ جرجان " (١/ ٤٧٢ / ٩٤٢)، ويحيى بن صاعد، وأبو بكر الإسماعيلي " معجم شيوخ الإسماعيلي " (٣/ ٧٨٧)، وأبو بكر بن أبي الدنيا، وأبو الحسين بن المنادي، وأبو سهل بن زياد القطان، وأبو عمرو بن السماك، وأبو عوانة الإسفراييني " مسند أبي عوانة " (١/ ٧٠ / ١٩٩).
وكل اسم لم يعز إلى كتاب – وميزته بالأسود العريض – من معجم شيوخه وتلاميذه، فهو من ترجمته في " تاريخ بغداد ".
التعديل والتجريح: اضطربت أقوال أئمة الجرح والتعديل فيه بين كونه: محدثًا، مشهورًا عالمًا، إمامًا، محله الصدق، وثقه الدارقطني وغيره، وتكلم الناس فيه، ولم يطعن فيه أحد بحجة، وليس بالمتين، وليس به بأس، ولا بأس به.',NULL,'666',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (669,'وقال موسى بن هارون: " أشهد أنه يكذب ".
وتصحف اسم شيخه (يزيد بن هارون) في الأثر رقم (تس ٨٥٦) إلى (ابن يزيد)، وهو خطأ! والصواب (يزيد) بدون ابن. وقد سمى الطبري أبا (يحيى). (جعفرًا) في خمس روايات في " التفسير " حيث يقول: حدثني (يحيى بن جعفر)، وهذه الروايات هي:
(تس: ١٩٦٣ و ٤٤٦٧ و ١٢٣٨٠) عن يزيد بن هارون.
و (تس: ١٥٦٧٣) عن أحمد بن أبي بكر.
و (٢٢٣٨٥) عن يحيى بن السكن.
ورواية واحدة في التاريخ: (تخ: ١/ ٤٧٢)، عن علي بن عاصم.
وأما باقي الروايات فقد كنى فيها أباه (بأبي طالب) حيث يقول: حدثني يحيى بن أبي طالب.
وانظر: " الجرح والتعديل " (٩/ ١٣٤ / ٥٦٧)، و " الثقات " (٩/ ٢٧٠ / ١٦٣٧٦)، و " تاريخ بغداد " (١٤/ ٢٢٠ / ٧٥١٢)، و " ميزان الاعتدال " (٤/ ٣٦٧ / ٩٤٧٤)، و " السير " (١٢/ ٦١٩ / ٢٤٢)، و " المغني في الضعفاء " (٢/ ٧٣٨ / ٦٩٩٣)، و " ولسان الميزان " (٦/ ٢٦٢ / ٩٢١)، و " رجال تفسير الطبري " (٥٨٢/ ٢٨٠٠).
**********
٣٧٣ - أبو خالد الواسطي (١١٣٧٠)
(طب: ٥ر / ٢ش)
(تس: ٤ر / ٢ش)، و (تهـ: ١ر / ١ش)
أبو خالد – وقيل: أبو خداش، يزيد بن مخلد بن خداش، الواسطي: من العاشرة، سكت عنه ابن أبي حاتم، والذهبي.
روى عن: خالد بن عبد الله الطحان (تس ٢١٣٥١ و ٢٤٤١٧ و ٣١٧٣٩)، و (تهـ ٢٤٦٩)، وهشيم بن بشير (تس ١١٣٧٠)، وإسحاق الأزرق " حلية الأولياء " (٧/ ٨٨)، وبشر بن مبشر " الجرح والتعديل " (٩/ ٢٩١ / ١٢٤٥)، وأبي أسامة " العظمة " (٢/ ٧٢٧ / ٣).
روى عنه: إبراهيم بن يوسف الهسنجاني " الجرح والتعديل " (٩/ ٢٩١ / ١٢٤٥)، وعلي',NULL,'667',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (670,'بن الحسين بن الجنيد " الجرح والتعديل (٩/ ٢٩١ / ١٢٤٥)، ومهران الرازي " حلية الأولياء (٧/ ٨٨).
التعديل والتجريح: ذكره غير واحد من أئمة الجرح والتعديل في كتبهم، وسكنوا عنه، ولم يذكروا فيه جرحًا ولا تعديلًا.
وتصحف اسمه في الأثر (تهـ ٢٤٦٩) فصار (زيد بن خالد بن خداش)
وقد سبق ونبهت على ذلك في الترجمة رقم (١٢٢) من كتابي هذا.
وانظر: " الجرح والتعديل " (٩/ ٢٩١ / ١٢٤٥)، و " المقتنى في سرد الكنى " (١/ ٢١٣ / ١٩٢٣)، و " رجال تفسير الطبري " (٥٩٨/ ٢٨٨٤).
**********
٣٧٤ - يعقوب الواسطي (١٦٧٤٤)
(طب: ٥ر / ٦ش)
(تس: ١ر / ٢ش)، و (تهـ: ١ر / ١ش)، و (تق: ٣ر / ٣ش)
يعقوب بن إبراهيم بن جبير، المادراني، الواسطي: من العاشرة، سكت عنه ابن أبي حاتم.
روى عن: حبان [بن هلال] (تس ١٦٧٤٤)، وعفان [بن مسلم] (تس ١٦٧٤٤)، وأبي داود [الطيالسي] (تهـ ٢٦٧٠)، وأبي قتيبة سلم بن قتيبة، وصفوان [بن عيسى القسام] و (تهـ ١٨٣٧) , (تق ٦٩٢)، وأبي الوليد هشام [الطيالسي] (تق ٧٤٢)، ويزيد [بن هارون] (٧٣٧)، ويحيى بن أبي الحجاج.
روى عنه: أحمد بن عمرو بن الضحاك الشيباني " الآحاد والمثاني " (٣/ ٢٠٧ / ١٥٦٢)، وأحمد بن علي " ضعفاء العقيلي " (٣/ ٢١٩)، وأسلم بن سهل الرزاز (بحشل)، " تاريخ واسط " (١/ ٢٢٥)، وأبو زرعة الرازي.
وكل اسم لم يعز إلى كتاب من معجم شيوخه وتلاميذه، وميزته بالأسود العريض، فهو من ترجمته في " الجرح والتعديل ".',NULL,'668',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (671,'التعديل والتجريح: ذكره ابن أبي حاتم في " الجرح والتعديل " (٩/ ٢٠٢ / ٨٤٥)، وسكت عنه، ولم يذكر فيه جرحًا ولا تعديلا.
وعلق عليه الشيخ علي رضا في تعليقه على " الجزء المفقود من تهذيب الآثار " (١/ ٣٨٤ / ٦٩٢) بقوله: " وقد روى عنه بحشل في تاريخ واسط " ((ص ٢٢٥)، ولم يذكر فيه جرحًا ولا تعديلًا، وعليه فإسناد المؤلف حسن لغيره " ا. هـ.
قلت: وأما الخبر الذي في " التفسير "، والذي نحن بصدد الترجمة له من أجله، فهو في الصحيحين، وغيرهما، ورجال إسناد الطبري فيه، رجال البخاري إلا يعقوب هذا!! وقد تصحف اسم جده في " تاريخ واسط " (١/ ٢٥٥)، من (جبير) إلى (حير)، ونسبه (المادراني).
وانظر: " الجرح والتعديل " (٩/ ٢٠٢ / ٨٤٥)، و " تاريخ واسط " (١/ ٢٥٥)، و " رجال تفسير الطبري " (٦٠٠/ ٢٨٩٦).
**********
٣٧٥ - أبو يوسف الدورقي (٥٥)
(الستة)
(طب: ١٣٨٢ ر / ٤١ ش)
(تس: ١١٥٩ ر / ٢٧ ش)، و (تخ ٢٩ ر / ٥ش)، و (تهـ ١٤٦ ر / ١٥ ش)، و (تق ٤٨ ر / ١٠ ش)
أبو يوسف، يعقوب بن إبراهيم بن كثير بن زيد بن أفلح بن منصور بن مزاحم، العبدي، القيسي، مولاهم الدورقي، البغدادي: - أخو أحمد بن إبراهيم، وكان الأكبر: وتأخر بالوفاة عنه – مولده سنة (١٦٦) ست وستين ومائة، وتوفي سنة (٢٥٢) اثنتين وخمسين ومائتين من العاشرة، ثقة، وكان من الحفاظ – رأي الليث بن سعد ولم يرو عنه -.
روى عن: إسماعيل ابن علية (تس ٥٥ و ٥٦ و ٦١ و ٩٧ و ٩٨ و ٩٩ و ١٠٣ و ١٢٧ و ١٣٥ و ١٩٧ و ٢١١ و ٢٣٧ و ٢٤٥ و ٢٤٨ و ٥٢٠ و ٦١٨ و ٦٤٥ و ٧٩٧ و ٨٥٢ و ٨٧٤ و ١٥٩٦ و ١٦١٢ و ١٦٣٢ و ١٩٣٥ و ١٩٨٨ و ٢٠٤٢ و ٢٢٥٥ و ٢٣٤٣ و ٢٣٤٩ و ٢٣٨٥ و ٢٥٣٦ و ٢٦٤٠ و ٢٦٥٩ و ٢٦٦٨ و ٢٨٢٧ و ٢٨٣٣ و ٢٨٣٤ و ٢٨٤٢ و',NULL,'669',NULL,NULL,'0');
