INSERT INTO [b29741] VALUES (675,'(د، س)، وعبد العزيز بن محمد (تق ٩٢١)، وعبيد الله الأشجعي (تس ٩٣١٣٣)، و (ت، س)، وأبي عبيدة عبد الواحد بن واصل الحداد (تس ٨٢٨٤ و ١٤١٤ و ٢١٦٧٠)، وعثمان بن عمر بن فارس (تس ١٦٣٨٤)، و (تهـ ٥٦)، وعيسى بن يونس (تهـ ٢٣٩٩)، والقاسم بن مالك المزني (تس ٦٦٩٦)، وكثير بن هشام (تس ٢٩٦٠٦)، ومحمد بن إبراهيم بن أبي عدي (تس ٣٢٥٧١)، وأبي الحسن محمد بن الحسن (تس: ٢٧٠٩١)، ومحمد بن عبد الرحمن الطفاوي (تس ٣٧٤٠٤)، و (تق ١٠٧)، (تهـ ٢٠ و ٥٠ و ٢٤٥٥)، و (د، س)، ومحمد بن كناسة (تق ٧٩٥)، ومروان بن معاوية الفزاري (تس ١٢٢٦ و ٣٣٢٧ و ١٤٥٦١ و ٢١٧٤٠ و ٢٦٧٢٧ و ٢٨٢٩٦ و ٣٠٦٧٩ و ٣٣١٤٥ و ٣٣٥٤٦ و ٣٥٨٢٥)، و (تق ٤٤٠)، (م)، ومعاذ بن سقيط (تق ٨٦٣)، ومعتمر بن سليمان (تس ٤١١٩ و ٤٨٤٤ و ٦٢٣٠ و ١٤٧٨٨ و ٢٣٠٥٢ و ٢٨٢٥٥ و ٢٩٢٣٥ و ٣١٢٧١ و ٣٦١٤٣ و ٣٧٥٥٦ و ٣٧٥٨١ و ٣٧٥٩٧ و ٣٧٦١٤ و ٣٧٦٢٦ و ٣٧٦٤٤)، و (تهـ ١٧٥٩)، و (تق ٧٠٨)، و (تخ ٢/ ٦٥٥ و ٦٧١ و ٣/ ٣٥)، و (د)، وأبي النضر هاشم بن القاسم (تس ٦٢٢ و ٤٢٥٤ و ٨٢٣٩ و ٢٣٢٤٨ و ٢٤٣٨٥ و ٣٠٣٨٨)، و (تهـ ٢٧٦١)، وهشام بن المفضل الفزاري (تهـ ١٩٦٩)، وهشيم بن بشير (تس ١٣٠ و ٣٣٥ و ٤٢٢ و ٥٧٩ و ٧١٩ و ٧٣٣ و ٧٩٣ و ١٠٦٨ و ١٢٢٥ و ١٢٥٨ و ١٤٦٠ و ١٦١٨ و ١٦١٩ و ١٧٣٦ و ١٧٥٨ و ١٧٦٦ و ١٨٩٥ و ١٩٧١ و ١٩٨٧ و ٢٢٥٧ و ٢٣٦٤ و ٢٥٣٣ و ٢٦١٩ و ٢٦٢٠ و ٢٦٣٥ و ٢٦٤٥ و ٢٧١٣ و ٢٧١٥ و ٢٨٢٦ و ٢٨٣٢ و ٢٨٦١ و ٢٩٦٧ و ٣١٥٦ و ٣١٨٠ و ٣٢٠٥ و ٣٢٢٣ و ٣٢٤٩ و ٣٢٦٤ و ٣٢٧٩ و ٣٢٩٩ و ٣٣٠٤ و ٣٣٢٢ و ٣٣٥٣ و ٣٣٥٥ و ٣٣٦٨ و ٣٣٦٩ و ٣٣٩٧ و ٣٤٠٠ و ٣٤٠٤ و ٣٤٠٥ و ٣٤٠٦ و ٣٤١٢ و ٣٤١٧ و ٣٤٤٠ و ٣٤٧٦ و ٣٥٠٢ و ٣٥٥٠ و ٣٧٤٤ و ٣٧٦٦ و ٣٧٧٤ و ٣٧٧٥ و ٣٨٠٣ و ٣٨٢٧ و ٣٨٢٨ و ٣٨٥٤ و ٣٨٥٥ و ٣٨٨٩ و ٣٩١٣ و ٣٩١٦ و ٣٩١٧ و ٣٩١٨ و ٤٠٩٧ و ٤١٢١ و ٤١٥٩ و ٤٢٧٧ و ٤٢٧٨ و ٤٣١٧ و ٤٣٢٨ و ٤٣٦٥ و ٤٣٨٣ و ٤٣٨٤ و ٤٣٨٨ و ٤٤١٧ و ٤٤٣٠ و ٤٤٤٦ و ٤٤٧٨ و ٤٥٦٩ و ٤٥٧٠ و ٤٦٩٠ و ٤٧٢٦ و ٤٨٥٥ و ٤٨٧٣ و ٤٩٠٥ و ٥٠١٣ و ٥١٢٠ و ٥١٦٢ و ٥٢٨٧ و ٥٢٩٢ و ٥٣٤٩ و ٥٤٠٧ و ٥٤٠٨ و ٥٤٥٥ و ٥٥٣٨ و ٥٦٧٠ و ٥٧٨٩ و ٥٨٣٧ و ٥٨٣٨ و ٥٩٩٧ و ٦٢٧٥ و ٦٢٧٦ و ٦٢٧٧ و ٦٣٣٣ و ٦٣٨١ و ٦٣٨٢ و ٦٣٨٣ و ٦٤٥١ و ٦٤٦٤ و ٦٥٧٠ و ٦٨٥٨ و ٦٩١٤ و ٦٩١٥ و ٦٩١٩ و ٧٠٩٦ و ٧٣٠٣ و ٧٤٠٣ و ٧٤٣٣ و ٧٤٣٤ و ٧٤٥٩ و ٧٤٦١ و ٧٤٩٩ و ٧٥٦٠ و ٧٦٨٣ و ٧٧٣٤ و ٧٧٨٧ و ٧٨٠٦ و ٧٩٦٠ و ٧٩٦٤ و ٨١٤٣ و ٨١٥٠ و ٨٤١٥ و ٨٤٩٤ و ٨٥٠٤ و ٨٥٢٤ و ٨٥٨٨ و ٨٦٠٤ و ٨٦٠٩ و 