INSERT INTO [b29741] VALUES (677,'و ٣٨٣ و ٤٤٢ و ٤٥٠ و ٤٩٦ و ٥٠٣ و ٥٠٧ و ٥٠٩ و ٥٣٨ و ٥٥٠ و ٥٦٦ و ٥٧٧ و ٥٨٩ و ٥٩٠ و ٨٥٦ و ٨٧٦ و ٨٧٧ و ١٠٠٢ و ٢٢٦٤ و ٢٢٥٧ و ٢٢٥٩ و ٢٢٧٣ و ٢٣٨٨ و ٢٤٤٨ و ٢٥٠٧ و ٢٦٩٩ و ٢٨٠٤ و ٢٩٢٠ و ٤٠١٢ و ٤٠٥١)، و (تق ٦ و ٣١١ و ٣٢٣ و ٣٢٦ و ٦٦٣ و ٧٠٤ و ٨٤٤ و ٨٤٦)، و (تخ ١/ ١١٦ و ١٦١ و ١٦٢)، و (خ، م، ت، س، ق)، ويحيى بن أبي بكير (تس ٣٢٧٢٩ و ٣٧٣٠ و ٣٣٥٥٦)، و (خ، م، د)، ويحيى بن زكريا بن أبي زائدة (تس ٢٣٤٤ و ٣٣٦٩٨)، و (م، س)، ويحيى بن سعيد القطان (تس ٣٢٩٧ و ١١٥١١ و ١٢٧٧٦ و ٣١٥٦٧ و ٣١٥٧٧ و ٣٣٩٧٩)، و (تخ: ٢/ ١٦ و ١١٨ و ١٢٤)، و (م، س)، وأبي تميلة يحيى بن واضح (تس ٣٣٤٧٠)، و (ت، س)، وأبي سفيان المعمري (تهـ١٥١٠)، وأبي عامر (تهـ ١٩٣٢)، وأبي عتاب الدلال (تهـ ١٣٤٣)، وإبراهيم بن سعد الزهري " تاريخ بغداد " (١٤/ ٢٧٧ / ٧٥٧٢)، وإبراهيم بن مهدي المصيصي " تاريخ بغداد " (٦/ ١٧٨ / ٣٢٣٢)، وبشر بن الحارث، " الثقات " (٨/ ١٤٣ / ١٢٦٥١)، والحارث ابن مرة الحنفي " تهذيب الكمال " (٥/ ٢٨٠ / ١٠٤٣)، وسعيد بن يحيى الحميري " تاريخ بغداد " (٩/ ٧٥ / ٤٦٥٩)، وسهل بن حماد العنقزي " تهذيب الكمال " (١٢/ ١٧٩ / ٢٦٠٨)، وعامر بن صالح الزبيري " تهذيب الكمال " (١٤/ ٤٥ / ٣٠٤٦)، وعبد الله بن بكر السهمي " تاريخ بغداد "، (٩/ ٤٢١ / ٥٠٣٠)، وعبد الله بن المبارك، " تاريخ بغداد " (١٠/ ١٥٢ / ٥٣٠٦)، وعبد العزيز بن يعقوب بن الماجشون، " الثقات "، (٧/ ١١٥ / ٩٢٥١)، وعبد الملك بن الصباح المسمعي " تهذيب الكمال ": (١٨/ ٣٣١ / ٣٥٣٤)، وعبد الملك بن عبد العزيز الماجشون " الثقات " (٨/ ٣٨٩ / ١٤٠٢٨)، وعلي بن إسحاق السلمي " تهذيب الكمال " (٢٠/ ٣١٨ / ٤٠٢٣)، وعلي بن ثابت الجزري " تهذيب الكمال " (٢٠/ ٣٣٥ / ٤٠٣٢)، وعمر بن شبيب المذحجي، " تهذيب الكمال " (٢١/ ٣٩٠ / ٤٢٥٦)، وعمر بن هاشم الجنبي " الجرح والتعديل " (٦/ ٢٦٧ / ١٤٧٨)، ومحمد بن سابق البغدادي، " الجرح والتعديل " (٧/ ٢٨٣ / ١٥٢٨)، ومحمد بن مصعب القرقساني " تاريخ بغداد " (٣/ ٢٧٦ / ١٣٦٥)، ومحمد بن ميمون الزعفراني " تاريخ بغداد " (٣/ ٢٦٩ / ١٣٦٠)، والوليد بن القاسم الهمداني " تهذيب الكمال " (٣١/ ٦٥ / ٦٧٢٨)، ويحيى بن عبد الملك الخزاعي " تهذيب الكمال " (٣١/ ٤٤٦ / ٦٨٧٥).
روي عنه: إبراهيم بن رجاء المقرئ " تاريخ بغداد " (٦/ ٧٥ / ٣١١١)، وإبراهيم بن محمد',NULL,'675',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (678,'ابن بقيرة " تاريخ بغداد " (٦/ ١٥٨ / ٣٢٠٤)، وأحمد بن جعفر البزاز " تاريخ بغداد " (٤/ ٦٢ / ١٦٨٠)، وأحمد بن سندي المطرز " تاريخ بغداد " (٤/ ١٨٧ / ١/ ١٨٧٣)، وأحمد بن عبد الله بن نصر الذهلي " تاريخ بغداد " (٤/ ٢٢٩ / ١٩٣٥)، وأحمد بن محمد الخيشي " تاريخ بغداد " (٥/ ٥ / ٢٣٥٢)، وأحمد بن محمد بن الليث " تاريخ بغداد " (٥/ ٨٤ / ٢٤٧٧)، وأحمد بن محمد بن مخلد التوزي " تاريخ بغداد " (٥/ ١٠٥ / ٢٥٠٨)، وأحمد بن محمد يوسف الشيباني " تاريخ بغداد " (٥/ ١٢٢ / ٢٥٤١)، وأحمد بن نصر الدمشقي " تاريخ بغداد " (١/ ٥٠٣ / ١١٨)، وأحمد بن يحيى الخزاعي " تاريخ بغداد " (٥/ ٢١٣ / ٢٦٨٦)، وإسحاق بن إبراهيم المنجنيقي " تاريخ بغداد " (٦/ ٣٨٥ / ٣٤٢٤)، وإسحاق بن أحمد الكاغدي " تاريخ بغداد " (٦/ ٣٩٣ / ٣٤٣٧)، وإسحاق بن محمد الزيات " تاريخ بغداد " (٦/ ٣٩٦ / ٣٤٤٣)، وإسماعيل بن إبراهيم الصيرفي " تاريخ بغداد " (٦/ ٢٩٧ / ٣٣٣٠)، وإسماعيل بن إبراهيم بن أبي عطاء " تاريخ بغداد " (٦/ ٢٩٧ / ٣٣٣١)، وبنان بن أحمد القطان " تاريخ بغداد " (٧/ ١٠٠ / ٣٥٤٢)، وجعفر بن محمد البزاز " تاريخ بغداد " (٧/ ٢٠٨ / ٣٦٧٧)، وجعفر بن محمد بن أبي الصعو " تاريخ بغداد " (٧/ ٢١٠ / ٣٦٨١)، وجعفر بن موسى القصري " تاريخ بغدد " (٧/ ٢٠٨ / ٣٦٧٦)، والحسن بن سفيان " الثقات " (٩/ ٢٨٦ / ١٦٤٦٨)، والحسن بن محمد الأزرق " تاريخ بغداد " (٧/ ٤١٤ / ٣٩٦٦)، والحسن بن محمد الأنصاري " تاريخ بغداد " (٧/ ٤١٥ / ٣٩٦٨)، وشعيب بن محمد الذراع " تاريخ بغداد " (٩/ ٢٤٥ / ٤٨٢٢)، وصالح بن محمد الجلاب " تاريخ بغداد " (٩/ ٣٢٨ / ٤٨٦٤)، وعبد الله بن إبراهيم المؤذن " تاريخ بغداد " (٩/ ٤٠٥ / ٥٠١١)، وعبد الله بن أحمد البزاز " تاريخ بغداد " (٩/ ٣٨٧ / ٤٩٧٥)، وعبد الله بن جعفر الصيرفي " تاريخ بغداد " (٩/ ٤٢٨ / ٥٠٤٤)، وعبد الله بن محمد البخاري " تاريخ بغداد " (١٠/ ١٢٥ / ٥٢٥٥)، وعبد الله بن محمد الجمال " تاريخ بغداد " (١٠/ ١٢٠ / ٥٢٤٧)، وعبد الله بن محمد القطان " تاريخ بغداد " (١٠/ ١٠٥ / ٥٢٢٤)، وعبد الله بن محمد مخول " تاريخ بغداد " (١٠/ ٩١ / ٥٢١٠)، وعبد الملك بن أحمد الدقاق " تاريخ بغداد " (١٠/ ٤٢٧ / ٥٥٨٥)، وعبد الوهاب بن عيسى " تاريخ بغداد " (١١/ ٢٨ / ٥٦٩٥)، ومحمد بن أحمد المؤدب المعروف بميمون السامري " تاريخ بغداد " (١/ ٢٨٨ / ١٣٨)، ومحمد بن أحمد المقدمي " تاريخ بغداد " (١/ ٣٣٦ / ٢٤٦)، ومحمد بن جعفر الديباجي " تاريخ بغداد " (٢/ ١٣٥ / ٥٣٥)، ومحمد بن الحسين القنبيطي " تاريخ بغداد " (٢/ ٢٣١ / ٦٨٥)، ومحمد بن سليمان',NULL,'676',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (679,'المخرمي " تاريخ بغداد " (٥/ ٣٠٠ / ٢٨٠٥)، ومحمد بن عبد الله بن واقد " تاريخ بغداد " (٥/ ٤٣٥ / ٢٩٥٥)، ومحمد بن المعلى الشونيزي " تاريخ بغداد " (٣/ ٣٠٩ / ١٤٠٥)، ومحمد بن موسى النهرتيري " تاريخ بغداد " (٣/ ٢٤١ / ١٣٢٥)، ومحمد بن يوسف التركي " تاريخ بغداد " (٣/ ٣٩٥ / ١٥١٩).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقًا، حافظًا، متقنًا، إمامًا، صنف المسند.
وقد تصحف اسم شيخه في الأثرين (تس ٣٧٢٩ و ٣٧٣٠) من (يحيى بن أبي بكير) إلى (يحيى ابن أبي كثير) في طبعتي دار الفكر.
ووقع سقط لكل الشيوخ الذين بينه وبين (سعيد بن جبير) في الأثر رقم (تس ٣٣٢٢٣).
ووقع سقط بينه وبين (ابن أبي نجيح) في الأثر رقم (تس ٣٨٠٦٤) في جميع الطبعات.
وانظر: " الطبقات الكبرى " (٧/ ٣٦٠)، و " الجرح والتعديل " (٩/ ٢٠٢ / ٨٤٤)، و " الثقات " (٩/ ٢٨٦ / ١٦٤٦٨)، و " الكنى والأسماء " (١/ ٩٢٢ / ٣٧٦٢)، و " ذكر أسماء التابعين " (٢/ ٢٨١ / ١٤٢٦)، و " من روى عنهم البخاري في الصحيح " (١/ ٢٣١ / ٢٩٤)، " تسمية من أخرجهم البخاري ومسلم " (١/ ٢٦٥ / ١٩٩٠)، و " رجال صحيح البخاري " (٢/ ٨٢٣ / ١٣٩٣)، و " رجال مسلم " (٢/ ٣٦٦ / ١٩٠٤)، و " تاريخ بغداد " (١٤/ ٢٧٧ / ٧٥٧٢)، " والتعديل والتجريح " (٣/ ١٢٤٨ / ١٥٣٢)، و " تهذيب الكمال " (٣٢/ ٣١١ / ٧٠٨٣)، والكاشف " (٢/ ٣٩٣ / ٦٣٨٥)، " تذكرة الحفاظ " (٢/ ٥٠٥ / ٥٢١)، و " المقتنى " (٢/ ١٦٤ / ٦٩١٤)، و " تذكرة الحفاظ " (٢/ ٥٠٥ / ٥٢١)، " وسير أعلام النبلاء " (١٢/ ١٤١ / ٥١)، و " طبقات المحدثين " (١/ ٩٢ / ١٠٣٦)، و " نزهة الألباب " (٢/ ١٩٢ / ٣١٧١)، و " تهذيب التهذيب " (١١/ ٣٣٤ / ٦٤٣)، و (١٢/ ٣٧٣ / ٢٤٤١)، و " تقريب التهذيب " (١/ ٥٣٦ / ٧٨١٢)، و " رجال تفسير الطبري " (٦٠١/ ٢٨٩٧).
******
٣٧٦ - يعقوب بن برهان (١٩٩١٠)
(طب: ٣ر / ٢ش)
(تس: ٢ر / ٢ش، تهـ: ١ر / ١ش)
يعقوب بن برهان – وقيل: ابن ماهان، وقيل: ابن شاهان: - من العاشرة، صدوق، وانظر الترجمة التالية.',NULL,'677',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (680,'روى عن: إسماعيل ابن علية (تس ١٩٩١٠)، وهشيم (بن بشير] (تس ٤٩٠٥)، و (تهـ ١٩٥٤).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: ذكره الشيخ شاكر في تحقيقه " لتفسير الطبري " (١٦/ ٢٧١ / ١٩٩٠)، وقال: " يعقوب بن ماهان البغدادي = يعقوب بن برهان.
قلت: لعله تحرف من (ماهان) كما جزم الشيخ شاكر، فقد أخرج الطبري عنه في " التفسير " مقرونًا بـ (ويعقوب بن إبراهيم) عن (هشيم) في الأثر رقم (تس ٤٩٠٥)، وباسم ويعقوب بن ماهان) كما قرنه بـ (يعقوب بن إبراهيم) في هذا الأثر أيضًا، ولكن سماه هنا (يعقوب بن برهان)، وفي (تهذيب الآثار) (تهـ ١٩٥٤) سماه (يعقوب بن شاهان).
وشيخاه هنا هما: الأول: أبو بشر، إسماعيل بن إبراهيم بن مقسم الأسدي، مولاهم، البصري المعروف بـ (ابن) علية: ولد سنة (١١٠)، وتوفي سنة (١٩٣)، من الثامنة ثقة، حافظ.
والثاني: أبو معاوية: هشيم بن بشير بن القاسم بن دينار، السلمي، الواسطي: ولد سنة ١٠٥، وتوفي سنة ١٨٣، من السابعة، ثقة، ثبت، كثير التدليس والإرسال الخفي؛ وعلى هذا فهو من كبار التاسعة، أو العاشرة وقد ترجمت لشيخيه في " المعجمين الكبير " والصغير "، وهو مما يؤكد لي أنه صاحب الترجمة التالية.
***********
٣٧٧ - أبو يوسف البناء (٤٩٠٥)
(النسائي)
(طب: ٥ ر / ٣ ش)
(تس: ٤ ر / ٣ ش)، و (تق: ١ ر / ١ ش)
أبو يوسف: يعقوب بن ماهان، الهاشمي، مولاهم، البناء، البغدادي: توفي',NULL,'678',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (681,'سنة (٢٤٤) أربع وأربعين ومائتين، من العاشرة صدوق.
روى عن: إسماعيل ابن علية (تس ١٩٩١٠)، والقاسم بن مالك المزني (تس ١٠٣٤٤ و ٢٠١٦٣)، (تق ٨٥١)، و (س)، وهشيم بن بشير (تس ٤٩٠٥)، و (س)، وسعيد بن محمد " الحلية " (٣/ ٢٢٠).
روى عنه: علي بن حسن بن سليمان " الحلية " (٣/ ٢٠)، وعلي بن سعيد الرازي " المعجم الكبير " (١٢/ ٥٤ / ١٢٤٥٢).
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، صدوقًا، ليس ببغداد مثله، لا بأس به، ربما أغرب.
وانظر: " الجرح والتعديل " (٩/ ٢١٦ / ٩٠٠)، و " القات " (٩/ ٢٨٥ / ١٦٤٦٥)، و " تاريخ بغداد " (١٤/ ٢٧٤ / ٧٥٦٧)، و " تهذيب الكمال " (٣٢/ ٣٦٠ / ٧١٠١)، و " الكاشف " (٢/ ٣٩٥ / ٦٤٠١)، و " تهذيب التهذيب " (١١/ ٣٤٦ / ٦٦١)، و " تقريب التهذيب " (١/ ٦٠٨ / ٧٨٣٠)، و " رجال تفسري الطبري " (٦٠٢/ ٢٩٠٥).
***********
(فائدة): روى كل من يعقوب الدورقي، ويعقوب بن برهان، ويعقوب بن ماهان - إن كانا اثنين - عن إسماعيل ابن علية؛ وهو: ابن إبراهيم الحنظلي، البصري، قاضي الرشيد، وصاحب ابن المبارك، نُسب إلى أمه، واشتهر بذلك، وكان – يتناكد و - يغضب من هذه النسبة - رحمه الله تعالى -.
وكذلك رووا عن هشيم بن بشير الواسطي، وهو مما أكد لي أن يعقوب بن برهان، ويعقوب بن ماهان هما واحد لا اثنان.
*********
٣٧٨ - – يعقوب بن مكرم (٣٦٩٧٥)
(طب: (تس: ١ ر / ١ ش)
يعقوب بن مكرم، من العاشرة، لم أعرفه، ولم أجد له ترجمة، ولم أقف له في " التفسير " على غير هذا الأثر، ولم يتعرض الشيخ التركي في تحقيقه " لتفسير الطبري " (٢٤/ ٣١٢)',NULL,'679',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (682,'لترجمته، ولا للأثر الذي رواه، بشيء، ولعل تصحفيًا أو سقطا، وقع في اسم: جعفر بن مكرم بن يعقوب بن إبراهيم، الدوري (٢١١٧١) جعله على هذا الوجه من التسمية، والله أعلم.
روى عن: الحفري ـ غير مسمى، ولا مكني _ (تس: ٣٦٩٧٥).
روى عنه: أبو جعفر بن جرير الطبري.
التعديل والتجريح: أغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري ".
وقد أخرج الطبري في " التفسير " الأثر رقم " تس: ٢١١٧١)، عن جعفر بن مكرم بن يعقوب، وأظنه أنه هو بعينه، ولكنه نسب هنا إلى جده.
وانظر: ترجمة رقم (٧٠) من كتابي هذا.
وشيخه هنا هو أحد رجلين؛ وكلاهما ثقتان من التاسعة، أخرج لهما (مسلم) في صحيحه، ويرويان عن سفيان؛ وهو الثوري، وجهالتهما لا تضر إن كانا أحد المرادين، وقد ترجمت لكليهما في " المعجمين الكبير، والصغير "
الأول منهما: أبو علي، أحمد بن المفضل الحفري الأموي، القرشي، مولاهم، الكوفي: – ابن عم عمرو بن محمد العنقزي – توفي سنة (٢١٥)، من التاسعة، شيعي، صدوق، في حفظه شيء.
وأما الثاني فهو: أبو داود، عمر بن سعد بن عبيد، الحفري، توفي سنة (٢٠٣) من التاسعة، ثقة، عابد.
*******
٣٧٩ - – أبو عمر المازني (٢٠٠٥)
(الترمذي، والنسائي في مسند علي)
(طب: ٧ ر / ١ ش)
(تس: ٣ ر / ١ ش)، و (تهـ: ٤ ر / ١ ش)
أبو عمر، يوسف بن سلمان، الباهلي ـ أو المازني ـ البصري: من العاشرة، صدوق.',NULL,'680',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (683,'روى عن: حاتم بن إسماعيل (تس: ٢٠٠٥ و ٢٣٧١ و ٨٩٠٦)، و (تهـ ١٤٠٣ و ١٤٠٤ و ١٤٠٥ و ٣٠٣٤)، و (ت).
روى عنه: عمر بن محمد بن يحيى الهمداني " الثقات " (٩/ ٢٨٢ / ١٦٤٤٧)، وموسى بن زكريا " العجم الأوسط " (٨/ ١٧٢ / ٨٣٠٨)
التعديل والتجريح: تراوحت أقوال أئمة الجرح والتعديل فيه بين كونه: ثقة، شيخا، مشهورا، لا بأس به.
وروى له الحاكم في " المستدرك " حديثا وقال: " إنه مجهول ". وقد تصحف اسمه في الأثر رقم (تس ٨٩٠٦) إلى (يونس بن سليمان البصري)، وليس هو إلا (يوسف بن سلمان الباهلي، أو المازني البصري) لأن الحديث هو نفسه؛ ولكن الطبري – رحمه الله – جزأه في هذين الموضعين من " التفسير " وكذلك في " تهذيب الآثار "، وهو الحديث المعروف بحديث " حجة النبي – صلى الله عليه وسلم – كما رواها جابر ".
وأخرجه مسلم في " صحيحه " (٢/ ٨٨٦ / ١٢١٨)، وابن الجارود في " المنتقى " (١/ ١٢٣ / ٤٦٩)، وابن حبان في " صحيحه " (٤/ ٣١٠ / ١٤٥٧، و ٩/ ٢٥٣ / ٣٩٤٤)، وأبو نعيم في " المستخرج " (٣/ ٣١٦ / ٢٨٢٧)، والدارمي في " السنن " (٢/ ٦٧ / ١٨٥٠)، أبو داود في " السنن " (٢٢/ ١٨٢ / ١٩٠٥)، وابن ماجة في " السنن " (٢٢/ ١٠٢٢ / ٣٠٧٤)، والنسائي في " الكبرى " (٥/ ٣٧٥)، والبيهقي في " الكبرى " (٧/ ٢٩٥ / ١٤٥٠، و ٧/ ٣٠٤ / ١٤٥٥١)، وابن أبي شيبة في " المصنف " (٣/ ٣٣٤ / ١٤٧٠٥)، وعبد بن حميد في " المسند " (١/ ٣٤١ / ١١١٣٥)، وابن حزم في " حجة الوداع " (١/ ١٦٨)، جميعهم من طريق حاتم بن إسماعيل.
وأخرجه الترمذي في موضعين من " السنن " (٣/ ٤٦٧ / ١١٦٣)، و (٥/ ٢٧٣ / ٣٠٨٧) من طريق الحسن بن علي الخلال.
وأما الطبري في " التاريخ " (٢/ ٢٠٦) فقد أخرج نفس الحديث من طريق محمد بن حميد الرازي عن سلمة عن محمد بن إسحاق.
وانظر: " الجرح والتعديل " (٩/ ٢٢٣ / ٩٣٦)، و " الثقات " (٩/ ٢٨٢ / ١٦٤٤٧)، و " تهذيب',NULL,'681',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (684,'الكمال " (٣٢/ ٤٣٢ / ٧١٣٩)، و " الكاشف " (٢/ ٣٩٩ / ٦٤٣٦)، و " المقتنى في سرد الكنى " (١/ ٤٢٤ / ٤٥٦٥)، و " ميزان الاعتدال " (٨/ ٢١٠ / ٧٤٩)، و " تهذيب التهذيب " (١١/ ٣٦٥ / ٧٠٩)، و " تقريب التهذيب " (١/ ٦١١ / ٧٨٦٧)، و " رجال تفسير الطبري " (٦٠٤/ ٢٩١٧).
*****
٣٨٠ - – أبو يعقوب القطان (٣١٥٢٣)
(البخاري، وأبو داود، والترمذي، والنسائي _ في مسند علي، وابن ماجة)
(طب: ٦ ر / ٥ ش)
(تس: ٣ ر / ٣ ش)، و (تق: ٣ ر / ٣ ش)
أبو يعقوب، يوسف بن موسى بن راشد بن بلال، القطان، الكوفي: ثم الرازي ثم البغدادي: توفي سنة ثلاث وخمسين ومائتين، من العاشرة، صدوق.
روى عن: سعيد بن شرحبيل المصري (تس ٣١٥٢٣)، ومسلم بن إبراهيم الأزدي (تق ٦٩٩)، وهشام بن عبد الملك (تس ٣١٥٢٣)، ووكيع بن الجراح (تس ٣٧٠٥٣)، و (تق ٦٣٤). و (خ)، وأبي أسامة (تق ٦٣٦).
روى عنه: أبو بكر أحمد بن عمرو البزار " مسند البزار " (١/ ٢٥٠ / ١٤٠)، وأحمد بن عمرو بن أبي عاصم " الآحاد والثماني " (١/ ١٠٠ / ٧٧)، وأحمد بن محمد بن يحيى بن زهير التستري " المعجم الكبير " (٢/ ٣٦ / ١٢٠٩)، وإسحاق بن محمد الزيات " سنن الدارقطني " (١/ ٢٩ / ١٠)، وحجاج بن عمران بن الفضل البصري " شرح معاني الآثار " (١/ ٣٠٢)، وعبد الله بن جعفر بن خشيش " سنن البيهقي " (١/ ٦٦ / ٣١٩)، وعبد الله بن عبد الرحمن الدارمي " سنن الدارمي " (١/ ٦٦ / ١٤١)، وأبو محمد عبد الله بن علي بن الجارود " المنتقى " (١/ ٤٠ / ٢٩) وعلي بن الجعد الجوهري " مسند ابن الجعد " (١/ ١٩٨ / ١٣٠٦)، ومحمد بن إسحاق بن إبراهيم الثقفي مولاهم، صحيح ابن حبان " (٥/ ٢٩٠ / ١٩٦٠). ومحمد بن إسحاق بن خزيمة " صحيح ابن خزيمة " (١/ ١١ / ١٦)، ومحمد بن أبي الثلج " معجم الشيوخ " (١/ ١٣٦)، ومحمد بن النضر الجارودي " المستدرك " (١/ ٢٠ / ٤٢٠)، ومحمد بن يحيى " المستخرج " (٣/ ٣٠٢ / ٢٧٩)، وأبو إسحاق الفزاري " مسند ابن أبي أوفى " (١/ ١٢٠ / ٢٩).',NULL,'682',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (685,'وأبو يعلى الموصلي " معجم أبي يعلى " (١/ ٢٦٨ / ٣٣٣)، وغيرهم كثير، ومن تركته أضعاف من أثبته، وأكثر هؤلاء أكثروا عنه جدا كالبزار وغيره.
التعديل والتجريح: لم يذكره أحد بتجريح؛ إلا أن الإمام أحمد أنكر عليه رواية حديث عن عبيد الله بن موسى، ولكنه لم ينفرد به، فقد تابعه عليه (أحمد بن حازم بن أبي غرزة الغفاري)، فرواه عن (عبيد الله بن موسى) أيضا، فسقطت العهدة عن (يوسف) كما قال الخطيب البغدادي في " تاريخ بغداد " (١٤/ ٣٠٤ / ٧٦١٥).
وانظر: " الطبقات الكبرى " (٧/ ٣٦٢)، و " الكنى والأسماء " (١/ ٩١٩ / ٣٧٤٩)، " والجرح والتعديل " (٩/ ٢٣١ / ٩٦٩)، و " الثقات " (٩/ ٢٨٢ / ١٦٤٤٨)، و " من روى عنه البخاري في الصحيح " (١/ ٢٣٠ / ٢٨٨)، و " ذكر أسماء التابعين، ومن بعدهم " (١/ ٣٤٠ / ١٠٣٢)، و " مولد العلماء، ووفياتهم " (٢/ ٥٥٨)، و " تاريخ بغداد " (١٤/ ٣٠٤ / ٧٦١٥)، و " تسمية من أخرجه البخاري ومسلم " (١/ ٢٦٦ / ٢٠٠٢)، و " التعديل والتجريح " (٣/ ١٢٣٩ / ١٥١٤)، و " تهذيب الكمال " (٣٢/ ٤٦٥ / ٧١٥٩)، و " الكاشف " (٢/ ٤٠١ / ٦٤٥٤)، والمقتنى (١/ ٤٤٢ / ٤٨٢١)، و " طبقات المحدثين " (١/ ٩٢ / ١٠٣٨)، و " سير أعلام النبلاء " (١٢/ ٢٢١ / ٧٦)، و " تهذيب التهذيب " (١١/ ٣٧٤ / ٧٣١)، و" تقريب التهذيب " (١/ ٦١٢ / ٧٨٨٧).
*****
١٩١ - – تمييز أبو يعقوب، يوسف بن موسى بن عبد الله بن خالد بن القطان المروزي، ولكنه متأخر الوفاة إلى سنة (٢٩٦)
وانظر: ـ " تاريخ بغداد " (١٤/ ٣٠٨ / ٧٦٢٧).
*****
٣٨١ - أبو أحمد القطيعي (١١٦٥٢)
(طب: تس ١ ر / ١ ش)
أبو أحمد، يوسف بن هارون بن زياد، القطيعي، - والد هارون بن يوسف المعروف بـ (ابن مقراض) – البغدادي: توفي في رجب سنة سبعين ومائتين من الحادية عشرة، ترجم له الخطيب البغدادي في " تاريخ بغداد "، وسكت عنه وأخشى أن يكون محرفا من موسى بن هارون (١٦٨)، والذي يروي عن عمرو بن حماد القناد في كثير من المواضع من " التفسير ".',NULL,'683',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (686,'روى عن: عمرو بن حماد (تس ١١٦٥٢)، وعبد الله بن الزبير الحميدي، وعصمة بن بشر " لسان الميزان " (٤/ ١٦٨ / ٤١٤)، وابن أبي عمر [العدني] " المحدث الفاضل " (١/ ٤٧٠).
روى عنه: الحسن بن عبد الرحمن الرامهرمزي " المحدث الفاضل " (١/ ٤٧٠)، ومحمد بن مخلد.
التعديل والتجريح: أغفله الشيخان أحمد ومحمود شاكر في تحقيقهما " لتفسير الطبري " وأخشى أن أيكون محرفا من (موسى بن هارون) الذي يروي عن عمرو بن حماد النقاد في كثير من المواضع من " التفسير ".
وكل اسم لم يعز إلى كتاب من معجم شيوخه وتلاميذه، وميزته بالأسود العريض فهو من ترجمته في " تاريخ بغداد " (١٤/ ٣٠٧ / ٧٦٢٢).
وورد ذكر لابنه (هارون) عند أبي بكر الإسماعيلي في " معجم شيوخه " (٣/ ٧٨٩)، وعند محمد بن يحيى بن أبي عمر العدني المكي في كتاب " الإيمان " (١/ ٦٧)، وعند الرامهرمزي في " المحدث الفاضل " كما أشرت إليه في معجم شيوخه وتلاميذه، ولكنه عنه نفسه، وقد وافقه على ذلك الحافظ ابن كثير في " التفسير " (٢/ ٤٠٤)، وساق الحديث الذي من طريق كما هو عنه به.
وشيخه هنا هو: أبو محمد، عمرو بن حماد بن طلحة القناد، الكوفي، توفي سنة (٢٢٢) اثنتين وعشرين ومائتين، من العاشرة، صدوق رمي بالرفض.
وعليه فيكون صاحب الترجمة من الحادية عشرة، وقد ترجمت لشيخه أيضا في: " المعجمين الكبير والصغير ".
*****
١٩٢ - – تمييز أبو عمر، يوسف بن هارون، الرمادي، شاعر الأندلس
وانظر: " المؤتلف والمختلف " (١/ ١٧٤)، و " سير أعلام النبلاء " (١٧/ ١٦٢).
*****
٣٨٢ - – يونس بن سليمان (٨٩٠٦)',NULL,'684',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (687,'(طب: (تس: ١ ر / ١ ش)
يونس بن سليمان البصري، ـ خطأ، والصواب: يوسف بن سلمان البصري (٢٠٠٥)، وقد ترجمته في " معجم الشيوخ " برقم (٣٧٩).
روى عن: حاتم بن إسماعيل (تس ٨٩٠٦).
روى عنه: أبو جعفر محمد بن جرير الطبري.
التعديل والتجريح: لم أقف له على ترجمة أو ذكر فيما بين يدي من كتب الرجال والتراجم والحديث.
وشيخه هنا هو: أبو إسماعيل حاتم بن إسماعيل، المدني، الحارثي مولاهم، أصله من الكوفة، توفي سنة ست أو سبع ومائتين، من الثامنة، صدوق، صحيح الكتاب يهم، وعلى هذا يكون صاحب الترجمة من التاسعة أو العاشرة، وقد ترجمت لشيخه في " المعجمين الكبير، والصغير ".
ولعله: أبو عمر، يوسف بن سلمان، الباهلي، أو المازني، البصري، من العاشرة، صدوق؛ وهو صاحب الترجمة رقم (٣٧٩) من هذا الكتاب فشيخهما واحد، واحتمال التصحيف وارد!!.
*****
٣٨٣ - – أبو موسى الصدفي (١٥)
(مسلم، والنسائي، وابن ماجة)
(طب: ٢٧٨٦ ر / ١٠ ش)
(تس ٢٥٦٩ ر / ١٧ ش) (تخ ٣٣ ر / ٤ ش) (تهـ ١٣٦ ر / ١٣ ش) (تق ٤٨ ر / ٥ ش)
أبو موسى، يونس بن عبد الأعلى بن ميسرة بن حفص بن حيان، الصدفي المصري: مولده في ذي الحجة، سنة سبعين ومائة ـ وقيل: سنة ثمان وستين ومائة ـ وتوفي غداة يوم الإثنين، ليومين مضيا من ربيع الآخر. سنة أربع ومائتين، عن ست وتسعين',NULL,'685',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (688,'سنة، من صغار العاشرة، ثقة أخذ القرآن عن عثمان بن سعيد – الملقب بورش – المقرئ، المشهور الراوي عن نافع بن أبي نعيم.
