INSERT INTO [b29741] VALUES (775,'وحوثرة بن محمد المنقري، وزكريا بن أبي كنانة «المستدرك» (٣/ ٤٩٩/٥٨٦٢)، وأبو شعيب صالح بن يحيى الطالقاني «مسند الشاميين» (٢/ ٤٢٨/١٦٢٨)، وعبد الجبار بن العلاء المكي، وعبد الله بن محمد الزهري، وعبد الله بن مسلم «المستدرك» (٣/ ٢٧٨/٥٠٨٦)، وعبيد الله بن عبد الله بن المنكدر «المعجم الأوسط» (٥/ ٨/٤٥٢١)، وعثمان بن يحيى القرقساني «المعجم الأوسط» (٥/ ٩/٤٥٢٥)، وعلي بن حجر، والفضل بن سهل البغدادي «المستدرك» (٣/ ٢٦٠/٥٠٣٤)، وقتيبة بن سعيد، ومحمد بن بشار، وأبي كريب محمد بن العلاء، ومحمد بن غالب الأنطاكي «المعجم الكبير» (١/ ١٨٩/٥٠١)، ومحمد بن المثنى، وهشام بن عمار «المعجم الأوسط» (٥/ ٧/٤٥١٧)، والهيثم بن خلف «تاريخ بغداد» (١٤/ ٦٠/٧٣٩٩)، وأبي سعيد الضرير «تهذيب الكمال» (٢٣/ ٣٥٤/٤٧٩٢).
روى عنه: عبد الله بن محمد الحنفي (تهـ ٤٦٦ و ٨٨٥ و ١٥٥٢ و ١٥٥٤ و ١٥٩٢ و ١٥٩٣ و ١٥٩٤ و ١٥٩٥ و ١٥٩٨ و ١٥٩٩ و ١٦٠٠ و ١٦٠٦)، والقاضي أحمد بن كامل، وأبو بكر أحمد بن محمد المنكدري «تاريخ بغداد» (١٤/ ٦٠/٧٣٩٩)، والقاضي عبد الباقي بن قانع، وعمر بن علك «تذكرة الحفاظ» (٢/ ٦٨٧/٧٠٨)، وأبو نعيم عبد الرحمن بن محمد الغفاري «المستدرك» (١/ ٣٨٥/٩٣٥)، ومحمد بن إسحاق الفسوي «تهذيب الكمال» (٣٢/ ٣٢٤/٧٣٩٩)، وأبو بكر محمد بن عيسى العطار «المستدرك» (٣/ ٢٦٠/٥٠٣٤)، ويحيى بن محمد العنبري «التذكرة» (٢/ ٦٨٧/٧٠٨)، وأبو أحمد العسال «التذكرة» (٢/ ٦٨٧/٧٠٨)، وأبو العباس الدغولي، وأبو القاسم الطبراني «التذكرة» (٢/ ٦٨٧/٧٠٨)، وابن الشرقى «التذكرة» (٢/ ٦٨٧/٧٠٨).
وكل اسم لم يعز إلى كتاب - وميزته بالأسود العريض - من معجم شيوخه وتلاميذه، فهو من ترجمته في «تاريخ بغداد».
التعديل والتجريح: قال الخطيب في «تاريخ بغداد» (١١/ ١٣٥/٥٨٢٨): «كان ثقة حافظًا، صالحًا، زاهدًا».
وقال أيضًا بسنده: «ولد سنة عشرين ومائتين في ذي الحجة ليلة عرفة، وتوفي عبدان في ذي الحجة ليلة عرفة سنة ثلاث وتسعين ومائتين».',NULL,'773',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (776,'قال الذهبي في «التذكرة» (٢/ ٦٨٧/٧٠٨): «الفقيه، الحافظ، كان مفتي مرو، وعالمها وزاهدها. وكان المرجوع إليه في الفتاوى، والمعضلات».
قلت: لم أقف له على تجريح يُذْكَر، وقد أغفله الشيخان شاكر - رحمهما الله - رغم أن الطبري قد أكثر عنه جدًّا في رواياته: عن الحسين بن الفرج، وعمار بن الحسن، إلا أنه أبهمه في كل ما رواه عنهما في «التفسير». ولا أدري ما الذي حمل الطبري على إبهامه رغم كثرة روايته عنه، سواء عن الحسين بن الفرج، أو عمار بن الحسن؟! إلا أن يكون قرينه، أو أصغر منه، أو أن هناك شيئًا آخر من تدليس أو عداوة، أو غيره!! وقد غلب على ظني أنه المراد، من خلال رواية الطبري عنه:
١ - في «تهذيب الآثار» (٢/ ٩٠ /١٢٤٠) حيث قال: «حدثنا عبدان بن محمد المروزي قال: حدثنا الحسين بن الفرج، قال: سمعت أبا معاذ يقول: ثنا عبيد بن سليمان قال: سمعت الضحاك يقول في قوله: {وما جعلنا الرؤيا التي أريناك إلا فتنة للناس} يعني ليلة أسري به إلى بيت المقدس، ثم رجع من ليلته؛ فكانت فتنة لهم». ثم أخرج الأثر نفسه في «التفسير» سندًا ومتنًا برقم (تس: ٢٢٤٢٩).
٢ - وأخرج في «التاريخ» أيضًا (١/ ٥٦) مصرحًا باسم (عبدان) حيث قال: «حدثني عبدان المروزي، حدثني الحسين بن الفرج قال: سمعت أبا معاذ الفضل بن خالد قال: أخبرنا عبيد الله بن سليمان قال: سمعت الضحاك بن مزاحم يقول - في قوله عز وجل -: {فسجدوا إلا إبليس كان من الجن} قال: كان ابن عباس يقول: «إن إبليس كان من أشرف الملائكة، وأكرمهم قبيلة، وكان خازنًا على الجنان، وكان له سلطان سماء الدنيا، وكان له سلطان الأرض». ثم أخرج الأثر نفسه في «التفسير»، سندًا، ومتنًا أيضًا، برقم (٢٣١٢٥).
٣ - وبيَّن المبهم بينه وبين عمار بن الحسن في «التاريخ» (١/ ٧٨) حيث قال: «قرأت على عبدان بن محمد المروزي قال: حدثنا عمار بن الحسن قال: حدثنا عبد الله بن أبي جعفر، عن أبيه، عن الربيع عن أنس، عن أبي العالية قال: «أُخرج آدم من الجنة للساعة',NULL,'774',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (777,'التاسعة أو العاشرة» فقال لي: «نعم لخمسة أيام مضين من نيسان».
٤ - وقال في «التاريخ» أيضًا (١/ ١٦٩): «حدثني عبدان المروزي قال: حدثنا عمار بن الحسن قال: حدثنا عبد الله بن أبي جعفر، عن أبيه، عن مطر، عن أبي الجلد قال: «ابتُلِيَ إبراهيم عليه السلام بعشرة أشياء، هن في الإنسان سنة: المضمضة، والاستنشاق، وقص الشارب، والسواك، ونتف الإبط، وتقليم الأظفار، وغسل البراجم، والختان، وحلق العانة، وغسل الدبر، والفرج».
وقد أورد الطبري هذا الأثر بعينه إسنادًا، ومتنًا في «التفسير» (١/ ٥٧٢/١٩١٥) إلا أنه أبهم (عبدان) في الإسناد، ولم يذكر (المضمضة) في المتن؛ وكأنها سقطت نَسْخًا أو طباعة.
فهذه آثار أربعة، توافق منها ثلاثة، في «التفسير» و «تهذيب الآثار» و «التاريخ»، إسنادًا ومتنًا. وتوافق أربعتها على التصريح باسم شيخ الطبري (عبدان بن محمد المروزي).
وأما الرواية التي في «التاريخ» (١/ ٦٩)، والتي صرح فيها بالتحديث عن (عمار بن الحسن) مباشرة! فأغلب الظن أن فيها إسقاطًا للواسطة بينهما! وهو والحالة هذه (عبدان بن محمد المروزي)، أو سقطت لفظة (حُدِّثتُ) قبل اسم (عمار).
وأخيرًا: - فإن كان المبهم هو - (أبو محمد، عبدان بن محمد بن عيسى المروزي) فالحمد لله على توفيقه. وها نحن قد وُفِّقْنا لمعرفة شيخ من شيوخ الطبري المبهمين، ممن أكثر الطبري في تفسيره عنهم، والذين روى عنهم جزأين كاملين كما يظهر من أسانيده.
وإن لم يكن هو المبهم، فها هي ترجمة عبدان بن محمد بن عيسى المروزي شبه كاملة بين يديك، ولن تظفر له بمثلها في غير هذا الموضع - فيما أعلم - والله تعالى أعلى وأعلم.
وانظر: «تاريخ بغداد» (١١/ ١٣٥/٥٨٢٨)، و «تذكرة الحفاظ» (٢/ ٦٨٧/٧٠٨)، و «المقتنى في سرد الكنى» (٢/ ٥٦/٥٥١٤)، و «سير أعلام النبلاء» (١٤/ ١٣/٥)، و «طبقات الحفاظ» (١/ ٣٠٢/٦٨٤).
٢١٩ - تمييز: عبدان بن محمد العسكري.
روى عن: يحيى بن زكريا بن أبي زائدة.',NULL,'775',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (778,'روى عنه: شعيب بن عمران العسكري.
وانظر: «المعجم الكبير» (٢٤/ ٩٦/٢٥٨).
٢٢٠ - تمييز: عبدان بن عثمان المروزي، وهو من شيوخ شيخ الطبري (عبد الله بن محمد الحنفي المروزي).
وانظر: «المعجمين الصغير والكبير في تراجم رواة ابن جرير».
٤٣٥ - عن داود بن الزبرقان (٣٥٨٩٤)
(طب: تس: ١ ر/١ ش)
روى عن: أبي عمرو داود بن الزبرقان الرقاشي البصري (تس ٣٥٨٩٤).
روى عنه: أبو جعفر محمد بن جرير الطبري.
ولعلهما اثنان وليسا واحدًا؛ هما:
القاسم بن الحسن، عن الحسين بن داود (سنيد). كما هو في الأثرين رقم (تس ٢٣٨٤١ و ٢٣٨٦٨).
أو واحد فقط؛ وهو:
إسماعيل بن موسى الفزاري، كما هو في الأثرين رقم (تس ٣١٤٩٦ و ٣١٤٩٧)، وهو بعيد، والأول أقرب، وكلاهما مترجم، والله أعلم.
٤٣٦ - عن داود بن المحبر (١٨٠٠٣)
(طب: تس: ١ ر/١ ش)
روى عن: داود بن المحبر (تس ١٨٠٠٣).
روى عنه: أبو جعفر محمد بن جرير الطبري.
ولا شك عندي أن المبهم هنا هو: الحارث بن محمد بن أبي أسامة، التميمي،',NULL,'776',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (779,'الذي أخرج هذا الخبر في «مسنده» (٣/ ٨٠٩/٨٣١)، سندًا ومتنًا فقال: «حدثنا داود بن المحبر، ثنا عبد الواحد بن زياد، عن كليب بن وائل، عن ابن عمر رضي الله عنهما، عن النبي صلى الله عليه وسلم، «أنه تلا: {تبارك الذي بيده الملك}. . حتى بلغ {أيكم أحسن عملاً}. قال: أيكم أحسن عقلاً، وأورع عن محارم الله عز وجل، وأسرعهم في طاعة الله عز وجل».
قلت: وهذا الخبر (لا يصح)، وإن كان متصلاً!! لأن داود بن المحبر – هذا - رُمِيَ بالكذب ووضع الحديث، ورواية المناكير، كما قال كثير من علماء الجرح والتعديل، ولم يوثقه أحَدٌ منهم.
وانظر: «الجرح والتعديل» (٣/ ٤٢٤/١٩٣١)، و «التاريخ الصغير» (٢/ ٢٩١/٢٦٤٨)، و «ضعفاء ابن الجوزي» (١/ ٢٦٧/١١٦٨)، و «ميزان الاعتدال» (٣/ ٣٣/٢٦٤٩)، و «لسان الميزان» (٧/ ٢١٢/٢٨٨٥) و «الضعفاء» (١/ ٧٨/٦١)، و «الكشف الحثيث» (١/ ١١٣/٢٨٧)، و «الضعفاء الصغير» (١/ ٤٢/١١٠)، و «المغني في الضعفاء» (١/ ٢٢٠/٢٠٢٤)، و «المجروحين» (١/ ٢٩١/٣٢٦) و «الكاشف» (١/ ٣٨٢/١٤٦٠)، و «التقريب» (١/ ٢٠٠/١٨١١).
٤٣٧ - عن سفيان بن عيينة (٨٢٨٩)
(طب: تس: ١ ر/١ ش)
روى عن: سفيان بن عيينة (تس ٨٢٨٩).
روى عنه: أبو جعفر محمد بن جرير الطبري.
لعلهما اثنان: أبو عوانة الإسفراييني، عن الحميدي [عبد الله بن الزبير] كما في: «مسند أبي عوانة» (٤/ ٤٥ /٥٩٧٣).
أو: ابن ماجه، عن محمد بن [يحيى] بن أبي عمر العدني كما في: «سنن ابن ماجه» (١/ ٥٦٨/١٧٨٤).
أو: لعلهما ثلاثة: الطحاوي، عن المزني، عن الشافعي، كما في: «السنن المأثورة» (١/ ٣٣٣/٣٨٦).',NULL,'777',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (780,'٤٣٨ - عن صالح بن نصر الخزاعي (٦٥)
(طب: تس: ٢ ر/١ ش)
روى عن: صالح بن نصر الخزاعي (تس ٦٥ و ٦٦).
روى عنه: أبو جعفر محمد بن جرير الطبري.
أغلب ظني أنه: أحمد بن أبي خيثمة النسائي.
وقد أخرج الأثر الأول رقم (تس ٦٥) - موضوع الترجمة - عبد الله بن أحمد في «العلل ومعرفة الرجال» (٣/ ٣٧٦/٥٦٥٤) حيث قال: «حدثني أبي، قال: أخبرنا إسحاق أبو عمرو الشيباني، قال: سمعت شعبة يقول: «نزل القرآن بلسان الكعبين: كعب بن لؤي، وكعب خزاعة».
وقال الحافظ في «الفتح» (٩/ ٢٧) - شرح الحديث رقم (٤٩٩٢) -: «وأخرج أبو عبيد من وجه آخر عن ابن عباس قال: «نزل القرآن بلغة الكعبين: كعب قريش، وكعب خزاعة. قيل: وكيف ذاك؟ قال: «لأن الدار واحدة».
وأما الأثر الثاني رقم (تس ٦٦) موضوع الترجمة، فقد أخرجه بلفظه كاملاً الخطيب البغدادي في «تاريخ بغداد» (٩/ ٣١٣ /٤٨٤٩)؛ إلا أنه قدم (كعب بن لؤي)، على (كعب بن عمرو). وأخرجه من طريق (أحمد بن أبي خيثمة) في ترجمة (صالح بن نصر بن مالك بن الهيثم أبو الفضل الخزاعي) حيث قال: «أخبرنا عبد الملك بن محمد بن عبد الله الواعظ، أخبرنا أبو سهل أحمد بن محمد بن عبد الله بن زياد القطان، حدثنا أحمد بن أبي خيثمة، حدثنا صالح بن نصر، حدثنا شعبة، عن قتادة، عن أبي الأسود الدؤلي قال: «نزل القرآن بلسان الكعبين، كعب بن لؤي، وكعب بن عمرو» قال: فقال خالد بن سلمة لسعد بن إبراهيم: ألا تسمع ما يقول هذا الأعمى؟! نزل القرآن بلسان الكعبين؛ وإنما نزل بلسان قريش، تفرد به صالح بن نصر عن شعبة. اهـ.
وقال أيضًا: «أنبأنا محمد بن جعفر بن علان، وأحمد بن محمد بن عبد الله الكاتب',NULL,'778',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (781,'قالا: أخبرنا مخلد بن جعفر، حدثنا محمد بن جرير الطبري قال: صالح بن نصر بن مالك بن الهيثم الخزاعي كان ثقة، وكان من ساكني بغداد، وبها كانت وفاته في سنة تسع عشرة ومائتين».
٤٣٩ - عن عبد الرحمن المحاربى (١٦٩٧٢)
(طب: تس: ٢ ر/١ ش)
روى عن: عبد الرحمن المحاربي (تس: ١٦٩٧٢ و ١٦٩٧٣).
روى عنه: أبو جعفر محمد بن جرير الطبري.
المبهم في الأثر الأول ربما يكون واحدًا؛ كما في الأثرين رقم (تس ٤٨٠٣ و ٤٨٠٨)، وهو (علي بن عبد الأعلى)، عن عبد الرحمن، عن جويبر.
وربما يكون (سفيان بن وكيع) عن عبد الرحمن عن جويبر، كما في الأثرين (تس ١٠٦٤٥ و ١٠٦٧٤).
وربما يكونا اثنين، كما في الأثر رقم (تس ١٧٤٦٧)، وهما: المثنى، عن، إسحاق بن إسماعيل عن عبد الرحمن المحاربي، عن جويبر عن الضحاك.
وقد أكثر الطبري في «التفسير» عن المحاربي هذا من عدة طرق، وعن عدة شيوخ، فصلناها في ترجمته في «المعجم الكبير».
٤٤٠ - عن عبد العزيز بن محمد [الدراوردي] (١٠٦٨١)
(طب: تس: ١ ر ١ ش)
ر وى عن: عبد العزيز بن محمد [الدراوردي] (تس ١٠٦٨١).
روى عنه: أبو جعفر محمد بن جرير الطبري.
لعل المبهمَ واحدٌ: كما في (٥٩)، وهو: أحمد بن عبدة الضبي، أو: إسحاق بن إسرائيل كما في: (٣٤٤٣٤).',NULL,'779',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (782,'أو اثنان: أحمد بن الحسن الترمذي، عن ضرار بن صرد، عنه كما في (٨٠٧٨).
أو: سفيان بن وكيع، عن القاسم بن عمرو، عنه، كما في (١٧٢١٦).
أو: عبد الرحمن بن عبد الله بن عبد الحكم عن عبد الملك بن مسلمة عنه كما في (٤٣٣١).
أو: عمار بن بكار الكلاعي، عن يحيى بن صالح، عنه، كما في (٢٣٤١٠).
أو: موسى بن سهل، عن نعيم بن حماد، عنه، كما في (٨٧٩).
أو: يونس بن عبد الأعلى، عن نعيم بن حماد الخزاعي، عنه، كما في (٦٠).
أو ثلاثة: المثنى، عن إسحاق، عن يعقوب بن محمد الزهري، عنه كما في (١٥٧٢٧).
وقد أَكْثَرَ المحدثون من إخراج نحو هذا الحديث وعبارته، ولكن عن أبي موسى الأشعري، وقومه الأشعريين، وليس كما هو هنا، عن سلمان، وقومه الفرس!!
فقد أخرج ابن حبان في «صحيحه» (١٦/ ٢٩٨ /٧٣٠٨) نحو هذا الحديث من طريق الدراوردي، ولكنه عن ثور بن زيد - كذا في الأصل! والصحيح: ثور بن يزيد -.
وقد أخرجه مسلم أيضًا قبله في «صحيحه» (٤/ ١٩٧٢/٢٥٤٦)، ولكن من غير طريق الدراوردي بنحو رواية ابن حبان.
٤٤١ - عن عبد الله بن أبي جعفر (٢٣٦٣٥)
(طب: تس: ٢ ر/١ ش)
روى عن: عبد الله بن أبي جعفر (تس: ٢٣٦٣٥ و ٣٠١٤١).
روى عنه: أبو جعفر محمد بن جرير الطبري.
لعلهما اثنان: المثنى بن إبراهيم، عن علي بن الهيثم؛ فإن الإسناد قد جاء على هذا النحو في الأثر رقم (تس: ٧٦٠٢) فحسب!!
وذلك أنني استقرأت أكثر من مائتي أثر، أوردها الطبري في «تفسيره» من رواية عبد الله بن أبي جعفر، عن أبيه، عن الربيع بن أنس، عن أبي العالية الرياحي، فوجدتها كثيرة الدوران، وأن مدارها على أربعة أوجه هي:',NULL,'780',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (783,'* حدثني المثنى، قال: ثنا إسحاق، عن ابن أبي جعفر، عن أبيه، عن الربيع، عن أبي العالية.
* حدثني القاسم، قال: ثنا الحسين، قال: حدثني حجاج، عن ابن أبي جعفر عن الربيع عن أبي العالية.
* حدثت عن عمار، قال: ثنا ابن أبي جعفر، عن أبيه، عن الربيع، عن أبي العالية.
* حدثني أحمد بن يوسف، عن أبي عبيد القاسم بن سلام، قال: ثنا عبد الله بن أبي جعفر الرازي، عن أبيه، عن الربيع بن أنس، عن أبي العالية.
ولم يأت الأثر متصلاً من (أبي العالية الرياحي) إلى (أبي بن كعب) إلا الأثر رقم (تس ٧٦٠٢) الذي أشرت إليه.
والأثر الذي نحن بصدده رقم (تس ٢٣٦٣٥)؛ فترجح عندي أنهما المبهمان، سيما وأن الطبري لم يخرج لابن أبي جعفر، عن أبيه، إلا عن شيخين:
أحدهما: - مبهم دائمًا - وهو (عبدان بن محمد المروزي) الذي بينه وبين (عمار بن الحسن)، وما كان على شاكلته.
وثانيهما: - يُبهمه حينًا، ويصرح به أحيانًا - وهو (أحمد بن يوسف) عن أبي عبيد القاسم بن سلام كما في الأثر رقم (تس ٤٠٣٥)، وما كان على شاكلته أيضًا.
وآخران يصرح بهما دائمًا، هما:
١ - (المثنى) قال: ثنا إسحاق بن الحجاج، عن ابن أبي جعفر، عن أبيه، عن الربيع، عن أبي العالية.
٢ - (القاسم)، قال: ثنا الحسين، قال: حدثني حجاج، عن ابن أبي جعفر، عن الربيع عن أبي العالية.
وعلى كل حال؛ فإن المبهمين، لا بد وأن يكونا مترجَمَين في ثنايا كتابي هذا، والظاهر أنهما من حيث الجرح والتعديل ليسا بذاك! والله أعلم.',NULL,'781',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (784,'٤٤٢ - عن عبد الله بن صالح (٢٢٦١٦)
(طب: تس: ٢ ر ١/ش)
روى عن: عبد الله بن صالح (تس ٢٢٦١٦ و ٣٦٩٣٨).
روى عنه: أبو جعفر محمد بن جرير الطبري.
أغلب ظني أن المبهم هنا هو: المثنى، وهو: ابن إبراهيم الآملي؛ لأنني تتبعت مرويات عبد الله بن صالح، عن الليث بن سعد، فوجدت جلها من طريق المثنى، إلا ما ندر كرواية الطبري عن محمد بن سهل البخاري، عن عبد الله بن صالح، عن الليث بن سعد كما في (٢٥١١٧) أو: روايته عن علي بن داود، عن عبد الله بن صالح، عن الليث بن سعد كما في (٥٤٩٧).
ووجدت نحو الأثر رقم (تس: ٣٦٩٣٨) عند البيهقي في «شعب الإيمان» (٣/ ٢٠/٢٧٥١)، ولكن من طريق غير طريق (أبي صالح عبد الله بن صالح) كاتب الليث بن سعد، ونصه سندًا، ومتنًا هو: «أخبرنا أبو بكر أحمد بن الحسن القاضي، وعبد الملك بن عثمان الزاهد، وأبو نصر بن قتادة، قالوا: ثنا أبو علي حامد بن محمد الهروي، ثنا محمد بن يونس، ثنا أبو علي الحنفي، ثنا عمران القطان، عن قتادة، عن خليد العصري، عن أبي الدرداء، قال: قال رسول الله - صلى الله عليه وسلم -: «ضمن الله عز وجل خلقه أربعًا الصلاة، والزكاة، وصوم رمضان، والغسل من الجنابة، وهو السرائر! قال الله عز وجل: {يوم تبلى السرائر}».
٤٤٣ - عن عبد الله بن يزيد المقري (٥٢١١)
(طب: تس: ١ ر/١ ش)
روى عن: أبي عبد الرحمن، عبد الله بن يزيد المقري (تس ٥٢١١).
روى عنه: أبو جعفر محمد بن جرير الطبري.',NULL,'782',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (785,'أغلب الظن أنهما اثنان.
أولهما: لم أعرفه! وأما الثاني: فهو: أبو بكر بن أبي شيبة، الذي أخرج نفس الأثر، سندًا، ومتنًا في «مصنفه» (٤/ ١٤٢/١٨٧٢٠) فقال: «نا أبو عبد الرحمن المقري عن سعيد بن أبي أيوب، قال: حدثني عقيل، عن ابن شهاب قال: ... » وذكره.
ولا يمكن أن يكون وحده؛ بالنسبة لسماع الطبري المولود سنة (٢٢٤)؛ لأن أبا بكر بن أبي شيبة قديم الوفاة، سنة (٢٣٥) فتحتمت الواسطة بينهما، وقد أشرت إلى هذا في غير هذا الموضع من الكتاب أيضًا.
ولم يُبهم الطبري في «التفسير» الواسطة بينه وبين عبد الله بن يزيد إلا في هذا الموضع؛ إذ إنه أخرج عنه عدة آثار عن: إبراهيم بن سعيد الجوهري (تس: ٢٩٢٠٨)، وعبد الله بن أبى زياد (تس ٣١٨٦ و ١٨٦٧٥)، والمثنى بن إبراهيم (تس ٧٤٩٥)، ومحمد بن حميد الرازي (تس ٦٧٤٠ و ٧٤٩٥)، ومحمد بن سنان القزاز (تس ٣٠٦٩٨)، ومحمد بن عبد الله بن عبد الحكم (تس ٧٤٩٥)، ومحمد بن عمارة الأسدي (تس ٣١٨٦ و ١٨٦٧٥).
فإن يكن أحد هؤلاء، فقد ترجمت لجميعهم، وإن كان آخر!! فالله المستعان، وقد فاتنا البيان، ونعوذ بالله من الخذلان والخسران، ونزغات ووساوس الشيطان.
٤٤٤ - عن عبد الوهاب الخفاف (١٧٧٠١)
(طب: تس: ٣ ر / ١ ش)
ر وى عن: عبد الوهاب بن عطاء الخفاف العجلي مولاهم (تس: ١٧٧٠١ و ١٧٧٧٥ و ٢٩٧٠٧).
روى عنه: أبو جعفر محمد بن جرير الطبري.
لعل المبهم هنا هو: الحسن بن محمد الزعفراني، كما في: (٢٠٠٠٠ و ٢٠٥٤٥).
٤٤٥ - عن عبيد الله بن موسى (١٣٦٢١)
(طب: تس: ٣ ر/١ ش)',NULL,'783',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (786,'روى عن: عبيد الله بن موسى (تس ١٣٦٢١ و ١٤٤٢١ و ١٤٤٢٢).
روى عنه: أبو جعفر محمد بن جرير الطبري.
لعلهما اثنان، وهما:
المثنى بن إبراهيم الآملي، عن إسحاق بن الحجاج، عن عبيد الله بن موسى عن إسرائيل، كما في الآثار رقم (تس ٧٦٠ و ٢٨٢٩ و ١٠٢٥٥).
٤٤٦ - عن علي بن المسيب (١٨٩٧١)
(طب: تس: ١ ر/١ ش)
روى عن: علي بن المسيب - كذا في الأصل، وهو خطأ - (تس ١٨٩٧١).
روى عنه: أبو جعفر محمد بن جرير الطبري.
وفي اسمه خطأ بلا ريب، والصحيح هو: علي، عن المسيب - وهو: ابن شريك - عن أبي روق. . وانظر: «تاريخ الطبري» (١/ ٣٥).
ولعل المبهمَ، هو: محمد بن أبي منصور الآملي، وقد ترجمت له مختصرًا برقم (٣٣٣) - عن على - وهو: ابن الهيثم - عن المسيب - وهو: ابن شريك.
٤٤٧ - عن علي بن المغيرة (١٩٠٠٦)
(طب: تس: ١ ر/١ ش)
روى عن: علي بن المغيرة (تس: ١٩٠٠٦)، وشاهد شعري رواه في «التفسير» (٧/ ٣٦٢) عن معمر بن المثنى هو:
فرع نبع يهتز في غصن المجـ ... ـ د كثير الندى عظيم المحال.
روى عنه: أبو جعفر محمد بن جرير الطبري.
لعله: الزبير بن بكار، وقد مضى برقم (١١٣).',NULL,'784',NULL,NULL,'0');
INSERT INTO [b29741] VALUES (787,'٤٤٨ - عن علي بن الهيتم (١٨٩٧٠)
(طب: تس: ١ ر/١ ش)
ر وى عن: علي بن الهيثم (تس: ١٨٩٧٠ و ٢٩٧٦٧ و ٢٩٧٧٠).
روى عنه: أبو جعفر محمد بن جرير الطبري.
لعله: المثنى بن إبراهيم الآملي الطبري، وذلك أنه أبهم الشيخ الذي بينه وبين علي بن الهيثم، ثلاث مرات في الآثار رقم (تس ١٨٩٧٠)، عن بشر بن المفضل، ورقم (تس ٢٩٧٦٧ و ٢٩٧٧٠) عن عبد الله بن أبي جعفر. وقد صرح به في الأثر رقم (تس ٧٦٠٢) بباقي الإسناد؛ فاحتمال أن يكون هو المراد؛ احتمال قوي، والله تعالى أعلم.
