INSERT INTO [b29864] VALUES (323,'<span data-type="title" id=toc-127>الفصل الخامس في الياء التي بعدها ساكن </span>(١)
اعلم أنّ جملة المختلف فيه من الياءات التي بعدها ساكن باتّفاق الجماعة إلّا ابن عامر إحدى وعشرون ياء، منها ما الساكن بعده لام تعريف ومنها ما الساكن بعده فاء فعل.
فأمّا ما الساكن بعده لام تعريف فجملته أربع عشرة ياء أوّلهنّ في البقرة ثنتان لا ينال عهدي الظّالمين (١٢٤) وربّي الّذي يحيي ويميت (٢٥٨) وفي الأعراف ثنتان حرّم ربّي الفواحش (٣٣) وسأصرف عن آياتي الّذين يتكبّرون (١٤٦) وفي إبراهيم وقل لعبادي (٢) (٣١) وفي مريم آتاني الكتاب (٣٠) وفي الأنبياء/ ١٢ ظ/ ثنتان عبادي الصّالحون (١٠٥) ومسّني الضّرّ (٨٣) وفي العنكبوت يا عبادي الّذين آمنوا (٥٦) وفي سبأ عبادي الشّكور (١٣) وفي صاد مسّني الشّيطان (٤١) وفي الزمر ثنتان إن أرادني الله (٣٨) يا عبادي الّذين أسرفوا (٥٣) وفي الملك إن أهلكني الله (٢٨).
فسكّنهنّ كلهن حمزة (٣).
وافقه حفص في عهدي وابن عامر في عن آياتي وابن عامر والكسائيّ وروح في قل لعبادي في إبراهيم والبصريّان والكسائيّ وخلف في يا عبادي التي في العنكبوت (٤).
_________
(١) ينظر: التيسير/ ٦٦، والإقناع ١/ ٥٤٠، والنشر ٢/ ١٧٠.
(٢) بعدها في س: الذين.
(٣) ينظر: الإرشاد/ ٢٥٥، ٣٤٣، ٣٤٤، ٣٩٤، ٤٣١، ٤٤٦، ٤٩١، ٥٠٩، ٥٢٩، ٥٣٣، ٦٠٠.
(٤) ينظر: الإرشاد/ ٢٥٥، ٣٤٤، ٣٩٤، ٤٩١.','1','373',NULL,NULL,'0');
INSERT INTO [b29864] VALUES (324,'وأمّا ما كان الساكن بعده فاء فعل (١) فجملته سبع ياءات (٢) أولهنّ في الأعراف إنّي اصطفيتك (١٤٤) وفي طه ثلاث ياءات أخي اشدد (٣٠ - ٣١) ولنفسي اذهب أنت (٤١ - ٤٢) وفي ذكري اذهبا (٤٢ - ٤٣) وفي الفرقان ثنتان يا ليتني اتّخذت (٢٧) وإنّ قومي اتّخذوا (٣٠) وفي الصف من بعدي اسمه أحمد (٦).
وهي عند ابن عامر عشرون ياء لقراءته أخي اشدد بقطع الهمزة.
فحرّك إنّي اصطفيتك وأخي اشدد ابن كثير/ ١١٣ و/ وأبو عمرو (٣).
وحرّك يا ليتني أبو عمرو (٤).
وحرّك إنّ قومي المدنيان وأبو عمرو والبزيّ وروح (٥).
وحرّك لنفسي وذكري الحجازيون وأبو عمرو (٦).
وحرّك أهل الحجاز والبصرة وأبو بكر من بعدي اسمه أحمد (٧).
...
_________
(١) مكان (فاء فعل) في س: فاعل.
(٢) ينظر: التيسير/ ٦٧، والإقناع ١/ ٥٤٢، والنشر ٢/ ١٧١.
(٣) ينظر: الإرشاد/ ٣٤٤، ٤٤٠.
