INSERT INTO [b30067] VALUES (209,'<span data-type="title" id=toc-225>فهرس الأحاديث والآثار</span>
الحديث أو الأثر رقم الصفحة
أشراف أمتي حملة القرآن ٢٤
أعربوا القرآن والتمسوا غرائبه ٢٢
اقرأ القرآن على حرف ١٩
أقرأني جبريل عليه السلام على حرف ١٩
اقرءوا البقرة فإنها تجيء يوم القيامة شفيعا ٢٣
اقرءوا القرآن بألحان العرب ٢٧، ٣٢
اقرأ يا ابن حضير ٤٠
أما بعد، فإني تارك فيكم ثقلين ٤٢
إذا كان يوم القيامة وضعت منابر ٣١
إذا قام أحدكم من الليل فليجهر (عبادة بن الصامت) ٣٠
إن في التوراة أن الغلام إذا تعلم القرآن (كعب الأحبار) ٣٠
إن القرآن مأدبة الله ٢٠، ٤١
إنكم لن ترجعوا إلى الله بشيء أفضل مما خرج منه ٢٠
إن الله تعالى يرفع بهذا الكتاب أقواما ٢٠
إن لله أهلين ٢٠، ٤٢
إن هذه القلوب تصدأ ٢١
أن عثمان بن عفان دخل على ابن مسعود في مرضه ٩٥
أن النبي صلى الله عليه وسلم كان له خرقة يتنشف بها عند الوضوء ٨١',NULL,'217',NULL,NULL,'0');
INSERT INTO [b30067] VALUES (210,'أيكم يحب أن يغدو إلى بطحان ٤٠
تضمن الله سبحانه لمن اتبع القرآن لا يضل (ابن عباس) ٢٣
تعلموا القرآن فإنه نعم الشفيع ٢٣
الحال المرتحل ٢٩
حسبك ٣٨
خيركم من تعلم القرآن وعلّمه ٢١، ٣٨
سمعت عثمان بن عفان يقرأ: إِلَّا مَنِ اغْتَرَفَ غُرْفَةً بِيَدِهِ (يحيى الذماري) ٧٥
عرضت عليّ الذنوب ٤١
القرآن غنى لا فقر بعده ٢٥
كان الكتاب الأول ينزل من باب واحد ١٨
كانت قراءة النبي صلى الله عليه وسلم المدّ (عبد الرحمن بن أبي بكرة عن أبيه) ٢٧
لا حسد إلا في اثنتين ٢١، ٣٨
لحامل كتاب الله في بيت مال المسلمين مائتا دينار ٣٢
لقد أوتيت مزمارا ٣٨
الذي يقرأ القرآن وهو ماهر به ٣٩
لله أشد أذنا ٤٢
ليس منا من لم يتغن بالقرآن ٢٥، ٢٦
لو كانت الدنيا تزن عند الله جناح بعوضة ٢٤
ما اجتمع قوم في بيت من بيوت الله ٢٧، ٤٠
الماهر بالقرآن مع السفرة ٢٢',NULL,'218',NULL,NULL,'0');
INSERT INTO [b30067] VALUES (211,'مثل المؤمن الذي يقرأ القرآن مثل الأترجة ٢٤، ٣٩
المسلم من سلم المسلمون من لسانه ٣١
من حفظ القرآن فكأنما أدرج النبوة ٢٢
من جمع القرآن فظن أن أحدا أغنى منه ٢٦
من جمع القرآن فقد حمل أمرا عظيما (عبد الله بن عمرو بن العاص) ٤٣
من سمع آية من كتاب الله كانت له نورا (ابن عباس) ٢٨
من شفع له القرآن يوم القيامة نجا ٢٣
من استظهر القرآن كانت له دعوة (معاذ بن جبل) ٤٤
من قرأ سورة آل عمران (ابن مسعود) ٢٦
من قرأ سورة الواقعة لم تصبه فاقة ٩٥
من قرأ القرآن ألبس والداه التيجان ٢٤
من قرأ القرآن ظاهرا فظن أن الله لا يغفر له ٣٢
من قرأ القرآن لم يرد إلى أرذل العمر (عكرمة) ٢٨
من قرأ القرآن نظرا خفف الله عن أبويه ٣٢
من قرأ القرآن واتبع ما فيه هداه الله (ابن عباس) ٢٢، ٤٣
من قرأ القرآن وعمل بما فيه ألبس ٢٤، ٣٩
من قرأ القرآن يبتغي به وجه الله (ابن مسعود) ٢٩
نعتت أم سلمة رضي الله عنها قراءة رسول الله صلى الله عليه وسلم ٢٧
نعم كنز الصعلوك آل عمران (ابن مسعود) ٢٦
يا أبا هريرة تعلم القرآن وعلمه الناس ٣٣',NULL,'219',NULL,NULL,'0');
INSERT INTO [b30067] VALUES (212,'يقال لقارئ القرآن يوم القيامة ٢٠
يقول الله عز وجل من شغله قراءة القرآن ١٨
ينبغي لحامل القرآن أن يعرف بليله (ابن مسعود) ٣١',NULL,'220',NULL,NULL,'0');
