-- Insert data for b309
INSERT INTO [b309] VALUES (1,'بسم الله الرحمن الرحيم
<span data-type="title" id=toc-1>مقدمة المصنف</span>
قال الفقير أبو البركات محمد بن أحمد بن محمد الخطيب الشافعي الشهير بابن الكيال الذهبي لطف الله تعالى به وبمحبيه وجميع المسلمين الحمد لله رب العالمين والعاقبة للمتقين وصلى الله على سيدنا محمد خير الخلائق أجمعين وعلى آله وصحبه وسلم تسليما كثيرا إلى يوم الدين أما بعد فهذا كتاب مشتمل على معرفة من صح أنه خلط في عمره من الرواة الثقات في الكتب الستة وغيرها وهو مؤلف وجيز وعلم غزير ينبغي أن يعتني به من له اعتناء بحديث سيد المرسلين وسند المتقدمين والمتأخرين',NULL,'57',NULL,NULL,'0');
INSERT INTO [b309] VALUES (2,'أفرده بالتصنيف الحافظ صلاح الدين العلائي في جزء اختصره جدا.
وذكر الحافظ تقى الدين أبو عمرو بن الصلاح في علومه ستة عشرة رجلا.
وأفرده بالتصنيف الحافظ برهان الدين سبط بن العجمي ورتبهم على حروف المعجم لكنه ذكر الثقات وغيرهم.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b309] VALUES (3,'ومن قيل إنه اختلط ولم يثبت ذلك حتى ذكر رحمه الله من تغير في مرض الموت وليس المقصود ذلك لأن عامة من يموت يختلط قبل موته ولا يضره ذلك وإنما المضعف للشيخ أن يروى شيئا حين اختلاطه فجمعت في هذا المصنف سبعين راويا من رواة الأصول المشهورين الثقات مبسوطة تراجمهم فيما صح واشتهر ومن رووا له وروى عنهم من أهل الأثر رتبتهم على حروف المعجم وجمعت ذلك من مختصر التهذيب للأندرشي وكتاب ابن ماكولا الحافظ.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b309] VALUES (4,'والكاشف للذهبي وعلوم ابن الصلاح الحافظ وعلوم الحافظ العراقي ومن الشذا الفياح',NULL,'60',NULL,NULL,'0');
INSERT INTO [b309] VALUES (5,'للأبناسي الحافظ ومن الأغتباط للحافظ الحلبي ومن التمهيد لابن عبد البر الحافظ ومن الأنتصار للحافظ المقدسي',NULL,'61',NULL,NULL,'0');
INSERT INTO [b309] VALUES (6,'وغيرهم وغير ذلك مما وقعت عليه واستندت إليه ثم الحكم في حديث من اختلط من الثقات التفصيل فما حدثه قبل الاختلاط فإنه يقبل وإن حدث به فيه أو أشكل أمره فلم يدر أأخذ عنه قبل الاختلاط أو بعده فإنه لا يقبل وقد ذكرت في بعض التراجم من أخذ عنه قبل الاختلاط أو بعده ثم هم منقسمون فمنهم من خلط لاختلاطه وخرفه ومنهم من خلط لذهاب بصره أو لغير ذلك قال بن الصلاح واعلم أن من كان بهذا القبيل محتجا بروايته في الصحيحين أو أحدهما فإنا نعرف على الجملة أن ذلك مما تميز وكان مأخوذا عنه قبل الاختلاط قلت وهذا من باب حسن الظن بهم رضى الله تعالى عنهم وسميته الكواكب النيرات في معرفة من اختلط من الرواة الثقات وعلى الله الكريم اعتمادي وإليه تفويضي واستنادي وأسأله النفع به لي ولسائر أحبابي',NULL,'62',NULL,NULL,'0');
INSERT INTO [b309] VALUES (7,'<span data-type="title" id=toc-2>باب الألف</span>
١ -<span data-type="title" id=toc-3> أحمد بن عبد الرحمن بن وهب بن مسلم القرشي أبو عبيد الله المصري </span>بحشل
عن عمه عبد الله بن وهب',NULL,'63',NULL,NULL,'0');
INSERT INTO [b309] VALUES (8,'والإمام الشافعي وغيرهما وعنه مسلم',NULL,'64',NULL,NULL,'0');
INSERT INTO [b309] VALUES (9,'وإبراهيم بن عبد الله الأصبهاني وابن خزيمة وأبو بكر بن زياد',NULL,'65',NULL,NULL,'0');
INSERT INTO [b309] VALUES (10,'أطلق محمد بن عبد الله بن عبد الحكم وعبد الملك بن شعيب بن الليث القول بتوثيقه عن عبد الملك بن شعيب بن الليث قال كتبنا عنه وأمره مستقيم ثم خلط بعد ثم جاءنا الخبر إنه رجع عن التخليط',NULL,'66',NULL,NULL,'0');
INSERT INTO [b309] VALUES (11,'قال وسئل أبي عنه بعد ذلك فقال كان صدوقا وقال أبو حاتم خلط ثم رجع وقيل لأبي زرعة أنه رجع عن تلك الأحاديث فقال إن رجوعه مما يحسن حاله ولا يبلغ المنزلة التي كان قبل وذكر الحاكم أنه اختلط بعد الخمسين ومئتين بعد خروج',NULL,'67',NULL,NULL,'0');
INSERT INTO [b309] VALUES (12,'مسلم من مصر كذا ذكره الشيخ محي الدين النووي في مقدمة شرح مسلم له عن أبي عمرو بن الصلاح ولم يذكره في علومه وقيل لابن خزيمة لم رويت عنه وتركت سفيان بن وكيع فقال لأن أحمد لما أنكروا عليه تلك الأحاديث رجع',NULL,'68',NULL,NULL,'0');
INSERT INTO [b309] VALUES (13,'عنها عن آخرها إلا حديث مالك عن الزهرى عن أنس إذا حضر العشاء فإنه ذكر أنه وجده في درج من كتب عمه في قرطاس وأما سفيان بن وكيع فإن وراقه أدخل عليه أحاديث وكلم في شأنها فلم يرجع عنها فتركت الرواية عنه وقال عبدان مستقيم الأمر في أيامنا وقال بن عدي من ضعفه أنكر عليه أحاديث',NULL,'69',NULL,NULL,'0');
INSERT INTO [b309] VALUES (14,'وأكثر روايته عن عمه وكل ذلك محتمل وإن لم يروه عن عمه غيره ولعله خصه به انتهى وكان أبو الطاهر بن السرح يحسن القول فيه ومر عليه هارون بن سعيد الأيلي فسلم عليه وقال إن أصحاب الحديث سألوني عنك فقلت لهم إنما يسأل أبو عبيد الله عنا هو الذي كان يستملي لنا عند عمه وهو الذي',NULL,'70',NULL,NULL,'0');
INSERT INTO [b309] VALUES (15,'كان يقرأ لنا عند عمه أو كما قال روى له مسلم وتوفى سنة أربع وستين ومئتين وقيل أربع وعشرين ولم يصح والله أعلم',NULL,'71',NULL,NULL,'0');
INSERT INTO [b309] VALUES (16,'٢ -<span data-type="title" id=toc-4> أبان بن صمعة بالصاد المهملة الأنصاري</span>
معدود',NULL,'71',NULL,NULL,'0');
INSERT INTO [b309] VALUES (17,'في البصريين قيل هو والد عتبة الغلام عن بن سيرين وشهر بن حوشب',NULL,'72',NULL,NULL,'0');
INSERT INTO [b309] VALUES (18,'وجابر بن عمرو الراسبي وعن أمه عائشة',NULL,'73',NULL,NULL,'0');
INSERT INTO [b309] VALUES (19,'وعنه يحيى القطان ووكيع',NULL,'74',NULL,NULL,'0');
INSERT INTO [b309] VALUES (20,'وخالد بن الحارث وأبو عاصم الضحاك',NULL,'75',NULL,NULL,'0');
INSERT INTO [b309] VALUES (21,'أطلق يحيى بن معين القول بتوثيقه وقال أحمد بن حنبل صالح لكنه تغير قال يحيى بن سعيد تغير بأخرة وكذا قال الذهبي في الكاشف وقال عبد الرحمن بن مهدي اختلط قبل موته برمان وقال أبو أحمد بن عدي أنما عيب عليه اختلاطه لما كبر ولم ينسب إلى الضعف وقال بن مهدي اختلط البتة',NULL,'76',NULL,NULL,'0');
INSERT INTO [b309] VALUES (22,'روى له مسلم عن أبي الوازع عن أبي برزة في فضل عمار مستشهدا به لأبي بكر بن شعيب وروى له النسائي وابن ماجة وتوفي سنة ثلاث وخمسين ومئة',NULL,'77',NULL,NULL,'0');
INSERT INTO [b309] VALUES (23,'٣ -<span data-type="title" id=toc-5> إبراهيم بن أبي العباس </span>ويقال بن العباس السامري بكسر الميم وتخفيف الراء
قاله بن ماكولا في كتابه الإكمال ويقال بفتح اليم المخففة قاله الذهبي في الكاشف أبو إسحاق معدود في الكوفيين وفيمن نزل بغداد عن إسماعيل بن عياش وشريك بن عبد الله النخعي وأبي معشر وغيرهم',NULL,'78',NULL,NULL,'0');
INSERT INTO [b309] VALUES (24,'وعنه أحمد بن حنبل والعباس بن محمد الدوري وعدة',NULL,'79',NULL,NULL,'0');
INSERT INTO [b309] VALUES (25,'أطلق الإمام أحمد وأبو عوانة والدارقطني القول بتوثيقه وعن أحمد لا بأس به وقال أبو حاتم شيخ قال محمد بن سعد اختلط في آخر عمره فحجبه أهله',NULL,'80',NULL,NULL,'0');
INSERT INTO [b309] VALUES (26,'في منزله حتى مات قاله المزي في تهذيبه وتابعه الذهبي عليه في تذهيبه وميزانه زاد في الميزان قلت فما ضره الاختلاط وعامة من يموت يختلط قبل موته وإنما المضعف للشيخ أن يروي شيئا من اختلاطه روى له النسائي',NULL,'81',NULL,NULL,'0');
INSERT INTO [b309] VALUES (27,'٤ -<span data-type="title" id=toc-6> إسحاق بن إبراهيم بن مخلد بن مطر الحنظلي أبو يعقوب المعروف بابن راهويه </span>مروزي إمام من أعلام الأئمة',NULL,'81',NULL,NULL,'0');
INSERT INTO [b309] VALUES (28,'المبرزين عن إسماعيل بن عليه وسفيان بن عيينة وعبد الله بن وهب وعفان بن سالم',NULL,'82',NULL,NULL,'0');
INSERT INTO [b309] VALUES (29,'والفضل بن دكين وغيرهم وعنه الجماعة سوى النسائي',NULL,'83',NULL,NULL,'0');
INSERT INTO [b309] VALUES (30,'وبقية شيخه',NULL,'85',NULL,NULL,'0');
INSERT INTO [b309] VALUES (31,'وأبو العباس السراج أملى المسند بن حفظه قال وهب بن جرير',NULL,'86',NULL,NULL,'0');
INSERT INTO [b309] VALUES (32,'جزى الله إسحاق بن راهويه وصدقة ويعمر عن الإسلام خيرا أحيوا السنة بأرض المشرق وقال محمد بن أسلم الطوسي وقت موته ما أعلم أحدا كان أخشى لله منه يقول الله إنما يخشى الله من عباده',NULL,'87',NULL,NULL,'0');
INSERT INTO [b309] VALUES (33,'العلماء وكان أعلم الناس وقال أحمد بن سعيد الرباطي لو كان الثوري وابن عيينة والحمادان في الحياة لاحتاجوا إليه وقال بن خزيمة لو كان في التابعين لأقروا بحفظه وعلمه وفقهه وقال الخفاف أملى علينا أحد عشر ألف حديث من حفظه ثم قرأها فلم يزد حرفا ولا نقصه قال إسحاق ما سمعت شيئا قط إلا حفظته ولا حفظته',NULL,'88',NULL,NULL,'0');
INSERT INTO [b309] VALUES (34,'العلماء وكان أعلم الناس وقال أحمد بن سعيد الرباطي لو كان الثوري وابن عيينة والحمادان في الحياة لاحتاجوا إليه وقال بن خزيمة لو كان في التابعين لأقروا بحفظه وعلمه وفقهه وقال الخفاف أملى علينا أحد عشر ألف حديث من حفظه ثم قرأها فلم يزد حرفا ولا نقصه قال إسحاق ما سمعت شيئا قط إلا حفظته ولا حفظته فنسيته وعنه ما كنت لأسمع شيئا قط إلا حفظته وكأني أنظر إلى سبعين ألف حديث أو قال أكثر من سبعين ألف حديث في كتبي وعنه كأني أنظر إلى مائة ألف حديث في كتبي وثلاثين ألفا أسردها وعنه أعرف مكان مائة ألف حديث كأني أنظر إليها وأحفظ سبعين ألف حديث وأحفظ أربعة آلاف مزورة فقيل له لم حفظت هذا فقال لأعرفه فإذا مر بي في الأحاديث الصحيحة فليته منها فليا وقيل لأبي حاتم أملى التفسير من حفظه فقال هذا أعجب فإن ضبط الأحاديث المسندة بمتونها أسهل من ضبط أسانيد التفسير قال أبو داود تغير قبل أن يموت بستة أشهر فرميت بما سمعت منه في تلك الأيام',NULL,'89',NULL,NULL,'0');
INSERT INTO [b309] VALUES (35,'وقال الذهبي في ميزانه في ترجمة بن راهويه أحد الأعلام وذكر لشيخنا أبي الحجاج يعني المزي حديث فقال قيل إن إسحاق اختلط في آخر عمره روى له البخاري ومسلم',NULL,'90',NULL,NULL,'0');
INSERT INTO [b309] VALUES (36,'وأبو داود والترمذي وابن ماجة عاش سبعا وسبعين سنة وتوفى سنة سبع أو ثمان وثلاثين ومئتين',NULL,'91',NULL,NULL,'0');
INSERT INTO [b309] VALUES (37,'٥ -<span data-type="title" id=toc-7> أحمد بن مالك أبو بكر القطيعي</span>
راوي مسند الإمام أحمد قال بن الصلاح اختلط في آخر عمره وخرف حتى كان لا يعرف شيئا مما يقرأ عليه ذكر الخطيب في التاريخ فقال حدثت عن أبي الحسن بن الفرات قال كان القطيعي مستورا صاحب سنة كثير',NULL,'92',NULL,NULL,'0');
INSERT INTO [b309] VALUES (38,'السماع من عبد الله بن أحمد وغيره إلا أنه اختلط في آخر عمره وكف بصره وخرف حتى كان لا يعرف شيئا مما يقرأ عليه قال الأبناسى وقد أنكر صاحب الميزان هذا على بن الفرات وقال هذا غلو وإسراف',NULL,'93',NULL,NULL,'0');
INSERT INTO [b309] VALUES (39,'وقال أبو عبد الرحمن السلمي أنه سأل الدارقطني عنه فقال ثقة زاهد سمعت أنه مجاب الدعوة وقال الحاكم ثقة مأمون وسئل عنه البرقاني فقال كان شيخا صالحا غرقت بعض كتبه فنسخها من كتاب ذكروا أنه لم يكن سماعه فغمزوه',NULL,'94',NULL,NULL,'0');
INSERT INTO [b309] VALUES (40,'لأجل ذلك وإلا فهو ثقة قال البرقاني وكنت شديد التنفير عن حاله حتى ثبت عندي أنه صدوق لا شك في سماعه وإنما كان فيه بله فلما غرقت القطيعة بالماء الأسود غرق شيء من كتبه فنسخ بدل ما غرق من كتاب لم يكن فيه سماعه قال ولما اجتمعت مع الحاكم أبي عبد الله ذكرت بن مالك ولينته فأنكر على وقال الخطيب لم أجد أحدا امتنع من الرواية عنه ولا ترك الاحتجاج به وقال أبو بكر بن نقطة كان ثقة',NULL,'95',NULL,NULL,'0');
INSERT INTO [b309] VALUES (41,'وعلى تقدير ما ذكره أبو الحسن بن الفرات من التغيير وتبعه بن الصلاح فممن سمع منه في الصحة أبو الحسن الدارقطني وأبو حفص بن شاهين وأبو عبد الله الحاكم وأبو بكر البرقاني وأبو نعيم الأصبهاني وأبو',NULL,'96',NULL,NULL,'0');
INSERT INTO [b309] VALUES (42,'علي بن المذهب (١) راوي المسند عنه فإنه سمعه عليه في سنة ست وستين وثلاث مائة انتهى',NULL,'97',NULL,NULL,'0');
INSERT INTO [b309] VALUES (43,'٦ -<span data-type="title" id=toc-8> أحمد بن أبي القاسم بن سنبلة البغدادي</span>',NULL,'97',NULL,NULL,'0');
INSERT INTO [b309] VALUES (44,'شيخ متأخر اختلط قبل موته بأربع سنين قال الذهبي في ميزانه توفى سنة ثنتي عشرة ومئتين',NULL,'98',NULL,NULL,'0');
INSERT INTO [b309] VALUES (45,'٧ -<span data-type="title" id=toc-9> إسماعيل بن عياش بن سليم بالضم معدود في الحمصيين</span>
عن إسحاق بن عبد الله',NULL,'98',NULL,NULL,'0');
INSERT INTO [b309] VALUES (46,'وسليمان الأعمش وهشام بن عروة وغيرهم',NULL,'99',NULL,NULL,'0');
INSERT INTO [b309] VALUES (47,'وعنه حيوة بن شريح',NULL,'100',NULL,NULL,'0');
INSERT INTO [b309] VALUES (48,'وعلي بن حجر وغيرهما أثبته بن سميع في السادسة وقال يعقوب تكلم فيه قوم وهو ثقة عدل أعلم',NULL,'101',NULL,NULL,'0');
INSERT INTO [b309] VALUES (49,'الناس بحديث الشام ولا يدفعه دافع وكلامهم فيه أكثره إنما هو ذكره بأنه يغرب عن ثقات المدنيين والمكيين وقال دحيم هو عن الشاميين غاية وخلط عن المدنيين وقال يحيى بن معين خلط في حديث عن أهل العراق وليس أحد أعلم منه بحديث الشام وقال البخاري في حديثه عن غير بلده نظر',NULL,'102',NULL,NULL,'0');
INSERT INTO [b309] VALUES (50,'وقال وكيع قدم علينا فأخذ منى أطرافا لإسماعيل بن أبي خالد فرأيته يخلط في أخذه وقال البخاري إذا حدث عن أهل حمص صحيح وذكره بن الجوزي في الموضوعات في باب النهى عن التسمية بالوليد وإسماعيل بن عياش لما كبر تغير حفظه وكثر الخطأ في حديثه وهو لا يعلم فلعل هذا الحديث أدخل عليه في كبره أو قد رواه وهو مختلط انتهى وذكره صاحب الأغتباط روى له أبو داود',NULL,'103',NULL,NULL,'0');
INSERT INTO [b309] VALUES (51,'والترمذي والنسائي وابن ماجة وتوفى سنة إحدى وثمانين ومئة',NULL,'104',NULL,NULL,'0');
INSERT INTO [b309] VALUES (52,'٨ -<span data-type="title" id=toc-10> إبراهيم بن خثيم بن عراك بن مالك</span>',NULL,'104',NULL,NULL,'0');
INSERT INTO [b309] VALUES (53,'قال الجوزجاني اختلط بأخرة قاله برهان الدين الحلبي في كتاب الأغتباط',NULL,'105',NULL,NULL,'0');
INSERT INTO [b309] VALUES (54,'<span data-type="title" id=toc-11>باب الباء</span>
٩ -<span data-type="title" id=toc-12> بحر بن مرار بن عبد الرحمن بن أبي بكرة الثقفي أبو معاذ </span>معدود في البصريين
عن الحكم بن الأعرج وجده عبد الرحمن وجد أبيه',NULL,'106',NULL,NULL,'0');
INSERT INTO [b309] VALUES (55,'أبي بكرة بطريق الإرسال وعنه الأسود بن شيبان',NULL,'107',NULL,NULL,'0');
INSERT INTO [b309] VALUES (56,'وشعبة بن الحجاج وغيرهما أطلق يحيى بن معين وابن ماكولا القول بتوثيقه وقال بن المديني سمعت يحيى يثني عليه خيرا وقال النسائي ليس به بأس قال يحيى بن سعيد رأيته قد خلط',NULL,'108',NULL,NULL,'0');
INSERT INTO [b309] VALUES (57,'وقال النسائي تغير وقال الكوسج ثقة روى له بن ماجة',NULL,'109',NULL,NULL,'0');
INSERT INTO [b309] VALUES (58,'١٠ -<span data-type="title" id=toc-13> بشر بن الوليد الكندي الفقيه</span>',NULL,'109',NULL,NULL,'0');
