INSERT INTO [b309] VALUES (61,'والحسن البصري وحميد الطويل',NULL,'112',NULL,NULL,'0');
INSERT INTO [b309] VALUES (62,'وأبي رجاء العطاردي وعنه ولده وهب',NULL,'113',NULL,NULL,'0');
INSERT INTO [b309] VALUES (63,'وعبد الرحمن بن مهدى ووكيع بن الجراح ويزيد بن هارون',NULL,'114',NULL,NULL,'0');
INSERT INTO [b309] VALUES (64,'وهدبه بن خالد وشيبان وغيرهم',NULL,'115',NULL,NULL,'0');
INSERT INTO [b309] VALUES (65,'أطلق يحيى بن معين والعجلي وأبو حاتم بصلاحه وصدقه وهو أحسن حديثا من السرى بن يحيى',NULL,'116',NULL,NULL,'0');
INSERT INTO [b309] VALUES (66,'وعن يحيى بن معين ليس به بأس فقيل له هو يحدث عن قتادة عن أنس بمناكير فقال هو عن قتادة ضعيف وقيل له من أحب إليك هو أو أبو الأشهب فقال ما أقربهما وجرير أكثرهما وهما وقال بن مهدي هو أثبت عندي من قرة بن خالد',NULL,'117',NULL,NULL,'0');
INSERT INTO [b309] VALUES (67,'وقال بن عدي هو من أجلة أهل البصرة ورفعائهم حدث عنه الكبار وعنه هو مستقيم الحديث صالح إلا روايته عن قتادة فإنه يروى عنه ما لا يرويه غيره وهو من ثقات المسلمين قال بن مهدي اختلط فحجبه أولاده فلم يسمع أحد عليه زمان اختلاطه شيئا وقال أبو حاتم إن اختلاطه كان قبل موته بسنة وكان حماد بن سلمة يعظمه كثيرا حدث عنه شيبان بن فروخ ويزيد بن حبيب وبين وفاتهما مائة وثمان سنين وأيوب السختياني وبين',NULL,'118',NULL,NULL,'0');
INSERT INTO [b309] VALUES (68,'وفاته ووفاة شيبان مائة وخمس سنين روى له البخاري ومسلم وأبو داود والترمذي والنسائي وابن ماجة وتوفى سنة سبعين ومئة',NULL,'119',NULL,NULL,'0');
INSERT INTO [b309] VALUES (69,'١٢ -<span data-type="title" id=toc-16> جرير بن عبد الحميد الضبي</span>',NULL,'120',NULL,NULL,'0');
INSERT INTO [b309] VALUES (70,'اختلط عليه حديث أشعث وعاصم الأحول حتى قدم عليه بهز فعرفه وقال أبو حاتم صدوق تغير قبل موته كذا نقل هذا الكلام النباتي في ترجمة بن عبد',NULL,'121',NULL,NULL,'0');
INSERT INTO [b309] VALUES (71,'الحميد وقال البيهقي نسب في آخر عمره إلى سوء الحفظ',NULL,'122',NULL,NULL,'0');
INSERT INTO [b309] VALUES (72,'<span data-type="title" id=toc-17>باب الحاء </span>المهملة
١٣ -<span data-type="title" id=toc-18> حبان بالكسر بن يسار الكلابي أبو روح ويقال أبو رويحة البصري</span>
عن عبيد الله بن طلحة بن عبيد الله ويزيد بن أبي',NULL,'123',NULL,NULL,'0');
INSERT INTO [b309] VALUES (73,'مريم وثابت البناني وعنه حبان بالفتح بن هلال',NULL,'124',NULL,NULL,'0');
INSERT INTO [b309] VALUES (74,'والتبوذكي وعمرو بن عامر الكلابي',NULL,'125',NULL,NULL,'0');
INSERT INTO [b309] VALUES (75,'أثبته بن حبان في الثقات وعن غيره صويلح تغير حفظه واختلط وقال الصلت بن محمد اختلط آخر عمره وقال الذهبي تغير حفظه روى له أبو داود',NULL,'126',NULL,NULL,'0');
INSERT INTO [b309] VALUES (76,'١٤ -<span data-type="title" id=toc-19> حصين بضم الحاء المهملة بن عبد الرحمن السلمي</span>',NULL,'126',NULL,NULL,'0');
INSERT INTO [b309] VALUES (77,'بضم السين المهملة أبو الهذيل معدود في الكوفيين بن عم منصور عن جابر بن سمرة وحبيب بن أبي ثابت',NULL,'127',NULL,NULL,'0');
INSERT INTO [b309] VALUES (78,'وعامر الشعبي وعطاء بن أبي رباح وغيرهم',NULL,'129',NULL,NULL,'0');
INSERT INTO [b309] VALUES (79,'وعنه سفيان الثوري وشعبة بن الحجاج وأبو عوانة الوضاح بن عبد الله',NULL,'130',NULL,NULL,'0');
INSERT INTO [b309] VALUES (80,'وهشيم وعلى بن عاصم وغيرهم',NULL,'131',NULL,NULL,'0');
INSERT INTO [b309] VALUES (81,'أحد الثقات الأثبات احتج به الشيخان ووثقه أحمد وأبو زرعة ويحيى بن معين والعجلي وأبو حاتم وزاد أحمد من كبار أصحاب الحديث والعجلي سكن المبارك بأخرة وأبو حاتم صدوق وفى آخر عمره ساء حفظه وقيل لأبي زرعة تحتج بحديثه قال إي والله وقال مالك بن مغول',NULL,'132',NULL,NULL,'0');
INSERT INTO [b309] VALUES (82,'للقاسم بن الوليد هل رأيت بعينيك مثل طلحة بن مصرف قال نعم حصين بن عبد الرحمن قال هشيم أتى عليه ثلاث وتسعون سنة وكان أكبر من الأعمش وقريبا من إبراهيم',NULL,'133',NULL,NULL,'0');
INSERT INTO [b309] VALUES (83,'قال يزيد بن هارون إنه اختلط وقال النسائي تغير وقال علي بن عاصم إنه لم يختلط حكاه صاحب الميزان قال بن الصلاح في علومه حصين بن عبد الرحمن اختلط وتغير ذكره النسائي وغيره اعترض عليه الحافظ الأبناسي من وجهين أحدهما أن من تسمى بهذا الاسم أربعة كل منهم اسمه حصين بن عبد الرحمن الكوفي ويتميز كل واحد منهم بنسبه أو كنيته وقد ذكر الأربعة الخطيب في المتفق والمفترق والمزى في التهذيب والذهبي في الميزان وميزوا بينهم فكان ينبغي أن يميز بينهم يعنى بن الصلاح فأما هذا المتكلم فيه اختلط فهو حصين بن عبد الرحمن الكوفي كنيته أبو الهذيل',NULL,'134',NULL,NULL,'0');
INSERT INTO [b309] VALUES (84,'وهو سملى وروايته في الكتب الستة وليس لغيره من بقية الأربعة المذكورين رواية في شيء من الكتب الستة وإنما ذكرهم المزي في التهذيب للتمييز وهذا ثقة حافظ وثقه أحمد وأبو زرعة وابن معين والعجلي والنسائي في الكنى وابن حبان وغيرهم وقال أبو حاتم الرازي ثقة ساء حفظه في الآخر وقال يزيد بن هارون طلبت الحديث وحصين حي كان يقرأ عليه وكان قد نسي واختلط وذكره البخاري في الضعفاء وكذلك العقيلي وابن عدي ولم',NULL,'135',NULL,NULL,'0');
INSERT INTO [b309] VALUES (85,'يذكروا فيه تضعيفا غير أنه كبر ونسي الثاني أنه لم يذكر يعني بن الصلاح في ترجمة هذا من سمع عنه قبل الاختلاط أو بعده وقد سمع منه قديما قبل أن يتغير سليمان التيمي وسليمان الأعمشي وشعبة وسفيان والمشهور أنه توفى سنة ست وثلاثين ومئة قاله محمد بن عبد الله الحضرمي الملقب ب مطين وقال بن حبان سنة ثلاث وستين كذا ذكره في طبقات التابعين ثم ذكره في طبقة أتباع التابعين وقال سنة',NULL,'136',NULL,NULL,'0');
INSERT INTO [b309] VALUES (86,'ست وهذا هو المشهور والذي حزم به الذهبي في العبر وأما حصين الثاني فهو حصين بن عبد الرحمن الكوفي أيضا نسبه الحارثي حدث عن الشعبي روى عنه إسماعيل بن أبي خالد والحجاج بن أرطاة ذكره البخاري في التاريخ وابن أبي حاتم في الجرح والتعديل وحكى عن أحمد أنه قال فيه ليس يعرف ما روى عنه غير الحجاج وإسماعيل بن أبي خالد وذكره بن حبان في الثقات وقال ليس هذا بالأول مات سنة تسع وثلاثين ومئة والثالث حصين بن عبد الرحمن الكوفي النخعي أخو',NULL,'137',NULL,NULL,'0');
INSERT INTO [b309] VALUES (87,'مسلم بن عبد الرحمن النخعي روى عن الشعبي أيضا روى عنه حفص بن غياث ذكره البخاري في التاريخ وابن أبي حاتم في الجرح والتعديل والخطيب روى عن أحمد أنه قال هذا رجل لا يعرف وقال الخطيب لم يرو عنه غير حفص بن غياث وذكره بن حبان في الثقات قال وليس هذا بالأولين والثلاثة من أهل الكوفة وقد رووا ثلاثتهم عن الشعبي روى عنهم أهل الكوفة قال وربما يتوهم المتوهم أنهم واحد وليس كذلك أحدهم سلمى والآخر حارثي والثالث نخعي والرابع حصين بن عبد الرحمن الكوفي الجعفي أخو',NULL,'138',NULL,NULL,'0');
INSERT INTO [b309] VALUES (88,'إسماعيل بن عبد الرحمن روى عن عبد الله بن علي بن الحسين بن علي بن أبي طالب روى عنه طعمة بن غيلان الكوفي ذكره الخطيب في المتفق والمفترق وتبعه المزي في التهذيب والذهبي في الميزان وقال مجهول انتهى قال حصين الأول جاءنا قتل الحسين بن علي فمكثنا ثلاثا كأن وجوهنا طليت رمادا روى له البخاري ومسلم',NULL,'139',NULL,NULL,'0');
INSERT INTO [b309] VALUES (89,'وأبو داود والترمذي والنسائي وابن ماجة وتوفي سنة ست وثلاثين ومئة',NULL,'140',NULL,NULL,'0');
INSERT INTO [b309] VALUES (90,'١٥ -<span data-type="title" id=toc-20> حنظلة بن عبد الله</span>
وقيل بن عبيد الله وقيل بن عبد الرحمن وقيل بن أبي صفية السدوسي البصري أبو عبد الرحيم إمام مسجد بنى سدوس بفتح السين المهملة',NULL,'141',NULL,NULL,'0');
INSERT INTO [b309] VALUES (91,'عن أنس بن مالك وشهر بن حوشب وعبد الله بن الحارث بن نوفل وغيرهم',NULL,'142',NULL,NULL,'0');
INSERT INTO [b309] VALUES (92,'وعنه جرير بن حازم وعبد الله بن المبارك وشعبة',NULL,'143',NULL,NULL,'0');
INSERT INTO [b309] VALUES (93,'وعبد الوارث أثبته بن حبان في الثقات قال بن معين تغير في آخر عمره',NULL,'144',NULL,NULL,'0');
INSERT INTO [b309] VALUES (94,'وقال يحيى القطان اختلط بأخرة وضعفه الإمام أحمد روى له الترمذي وابن ماجة وتوفى سنة',NULL,'145',NULL,NULL,'0');
INSERT INTO [b309] VALUES (95,'١٦ -<span data-type="title" id=toc-21> الحسين بن الحسين</span>',NULL,'145',NULL,NULL,'0');
INSERT INTO [b309] VALUES (96,'الفانيد الراوي عن أبي علي بن شاذان قال شجاع الذهلي وغيره تغير بآخره',NULL,'146',NULL,NULL,'0');
INSERT INTO [b309] VALUES (97,'١٧ -<span data-type="title" id=toc-22> الحسين بن علي النخعي</span>
شيخ كتب عنه الإسماعيلي عمر وتغير ذكره برهان الدين الحلبي في كتابه الاغتباط',NULL,'147',NULL,NULL,'0');
INSERT INTO [b309] VALUES (98,'<span data-type="title" id=toc-23>باب الخاء المعجمة</span>
١٨ -<span data-type="title" id=toc-24> خالد بن طهمان أبو العلاء الخفاف</span>
معدود في الكوفيين عن حبيب بن أبي حبيب البجلي وعطية',NULL,'148',NULL,NULL,'0');
INSERT INTO [b309] VALUES (99,'العوفي وأنس وغيرهم وعنه عبد الله بن المبارك وأحمد بن يونس وغيرهم',NULL,'149',NULL,NULL,'0');
INSERT INTO [b309] VALUES (100,'أثبته بن حبان في الثقات وقال أبو عبيد الأجري لم يذكره أبو داود إلا بخير ضعفه بن معين وقال خلط قبل موته بعشر سنين وكان قبل ذلك ثقة قلت وما ضعفه بن معين إلا من أجل أنه اختلط والله أعلم روى له الترمذي',NULL,'150',NULL,NULL,'0');
INSERT INTO [b309] VALUES (101,'١٩ -<span data-type="title" id=toc-25> خطاب بفتح الخاء المعجمة وتشديد الطاء المهملة بن القاسم الحراني أبو عمر قاضي حران</span>
عن زيد بن أسلم وخصيف بالمعجمة بن عبد الرحمن وغيرهما',NULL,'151',NULL,NULL,'0');
INSERT INTO [b309] VALUES (102,'وعنه المعافى بن سليمان',NULL,'152',NULL,NULL,'0');
INSERT INTO [b309] VALUES (103,'والنفيلي وغيرهما',NULL,'153',NULL,NULL,'0');
INSERT INTO [b309] VALUES (104,'أطلق يحيى بن معين القول بتوثيقه وأثبته بن حبان في الثقات وقال بن أبي حاتم عن أبي زرعة ثقة وعنه عن أبيه يكتب حديثه يقال أنه تغير قبل موته قاله الذهبي وقال صاحب التهذيب اختلط قبل موته وذكره برهان الدين الحلبي في الأغتباط روى له أبو داود والنسائي',NULL,'154',NULL,NULL,'0');
INSERT INTO [b309] VALUES (105,'٢٠ -<span data-type="title" id=toc-26> خلف بن خليفة بن صاعد الأشجعي التابعي مولاهم أبو أحمد</span>
كان بالكوفة ثم انتقل إلى واسط ثم تحول إلى بغداد سكنها وبها توفى رأى عمرو بن حريث الصحابي وهو بن ست سنين وروى عن جعفر بن أبي وحشية',NULL,'155',NULL,NULL,'0');
INSERT INTO [b309] VALUES (106,'وحميد بن عطاء الأعرج ومحارب بن دثار وغيرهم',NULL,'156',NULL,NULL,'0');
INSERT INTO [b309] VALUES (107,'وعنه علي بن حجر وقتيبة بن سعيد وسعيد بن منصور',NULL,'157',NULL,NULL,'0');
INSERT INTO [b309] VALUES (108,'وابن عرفة صدوق قاله الذهبي وقال يحيى بن معين والنسائي ليس به بأس وقال محمد بن عمار',NULL,'158',NULL,NULL,'0');
INSERT INTO [b309] VALUES (109,'لا بأس به وقال أبو حاتم صدوق وقال بن عدي أرجو أنه لا بأس به ولا أبرئه من أن يخطىء في بعض الأحايين وقال بن سعد ثقة أنكر سفيان بن عيينة وأحمد بن حنبل رؤيته لعمرو بن حريث وكذبه في ذلك سفيان قال أحمد شبه عليه وقال أحمد أيضا رأيته مفلوجا سنة سبع وسبعين ومئة وكان لا يفهم فمن كتب عنه قديما فسماعه',NULL,'159',NULL,NULL,'0');
INSERT INTO [b309] VALUES (110,'صحيح وعنه قد أتيته فلم أفهم عنه قيل له في أي سنة مات قال أظنه في سنة ثمانين أو آخر سنة تسع وسبعين وقال بن سعد تغير قبل موته واختلط وفي مسند أحمد دخلت عليه فرأيته قد اختلط فلم أسمع منه وقال عبد الله عن أبيه يعنى الإمام أحمد رأيت خلفا وهو كبير فوضعه إنسان فصاح يعنى من الكبر فقال له إنسان يا أبا أحمد حدثكم محارب بن دثار وقص الحديث فتكلم بكلام خفي فجعلت لا أفهم فتركته',NULL,'160',NULL,NULL,'0');
INSERT INTO [b309] VALUES (111,'روى له مسلم وأبو داود والترمذي والنسائي وابن ماجة عاش تسعين سنة وتوفي سنة إحدى وثمانين ومئة',NULL,'161',NULL,NULL,'0');
INSERT INTO [b309] VALUES (112,'<span data-type="title" id=toc-27>باب الدال </span>المهملة
٢١ -<span data-type="title" id=toc-28> داود بن فراهيج</span>
قال أبو حاتم تغير حين كبر وهو ثقة صدوق نقلته من الاغتباط للحلبي الحافظ',NULL,'162',NULL,NULL,'0');
INSERT INTO [b309] VALUES (113,'<span data-type="title" id=toc-29>باب الراء </span>المهملة
٢٢ -<span data-type="title" id=toc-30> ربيعة بن أبي عبد الرحمن واسم أبي عبد الرحمن فروخ القرشي التيمي التابعي أبو عثمان المعروف بربيعة الرأي</span>
فقيه أهل المدينة أحد الأئمة الثقات وعنه أخذ مالك الفقه يروي عن أنس والسائب بن يزيد وابن المسيب',NULL,'163',NULL,NULL,'0');
INSERT INTO [b309] VALUES (114,'والحارث بن بلال والقاسم بن محمد بن أبي بكر',NULL,'164',NULL,NULL,'0');
INSERT INTO [b309] VALUES (115,'الصديق وغيرهم وعنه مالك والليث',NULL,'165',NULL,NULL,'0');
INSERT INTO [b309] VALUES (116,'والداروردي',NULL,'166',NULL,NULL,'0');
INSERT INTO [b309] VALUES (117,'وأبو ضمرة وإسماعيل بن جعفر وسفيان الثوري وسليمان بن بلال وغيرهم احتج به الشيخان',NULL,'167',NULL,NULL,'0');
INSERT INTO [b309] VALUES (118,'وأطلق أحمد بن حنبل والعجلي وأبو حاتم والنسائي ويعقوب بن شيبة القول بتوثيقه زاد أحمد بن حنبل أبو الزناد أعلم منه وزاد يعقوب أحد مفتي المدينة وذكر أن والده فروخ خرج في البعوث إلى خراسان أيام بنى أمية غازيا وربيعة حمل في بطن أمه وخلف عند أمه ثلاثين ألف دينار فقدم المدينة بعد سبع وعشرين سنة وقد أنفقت المال عليه ولما خرج إلى المسجد وأبصر حلقته فيها أشراف أهل',NULL,'168',NULL,NULL,'0');
INSERT INTO [b309] VALUES (119,'المدينة سر بذلك وقال إنك لم تضيعي المال وقال يحيى بن سعيد ما رأيت أفطن من ربيعة وقال فيه عبيد الله بن عمر هو صاحب معضلاتنا وعالمنا وأفضلنا وقال عبد الرحمن بن زيد بن أسلم مكث دهرا طويلا يصلى الليل والنهار ثم جالس القوم فنطق بلب وعقل وكان القاسم إذا سئل عن شيء فإن كان في كتاب الله تعالى أو سنة',NULL,'169',NULL,NULL,'0');
INSERT INTO [b309] VALUES (120,'نبيه صلى الله عليه وسلم أخبرهم وإلا قال سلوا عن هذا ربيعة أو سالما وكان يحيى بن سعيد كثير الحديث فإذا حضر ربيعة كف إجلالا له ولم يكن ربيعة بأسن منه وقال سوار بن عبد الله العنبري ما رأيت أعلم منه قيل له ولا الحسن وابن سيرين قال ولا الحسن وابن سيرين وقال عبد العزيز بن أبي سلمة لما جئت العراق قالوا لي حدثنا عن ربيعة الرأي فقلت لهم تقولون هذا والله ما رأيت أحدا أحفظ لسنة منه',NULL,'170',NULL,NULL,'0');
INSERT INTO [b309] VALUES (121,'وقال عبد الرحمن بن زيد بن أسلم صار ربيعة إلى فقه وفضل وما كان بالمدينة رجل أسخى منه وكان يستصحب القوم فيأبى صحبة أحد وإلا رجلا لا زاد معه ولم يكن في يده ما يحمل ذلك أمر له العباس بجائزة فأبى أن يقبلها وكان يذكر مع جلة التابعين في الفتوى بالمدينة وكان مالك يفضله ويثني عليه في الفقه والفضل على أنه ممن اعتزل حلقته لإغراقه في الرأي وكان يقول ذهبت حلاوة الفقه مذ مات ربيعة وعن بن أبي أويس قال سمعت خالي مالك بن أنس',NULL,'171',NULL,NULL,'0');
INSERT INTO [b309] VALUES (122,'يقول كانت أمى تلبسني الثياب وتعممنى وأنا صبي وتوجهنى ألى ربيعة بن أبي عبد الرحمن وتقول يا بنى إيت مجلس ربيعة فتعلم من سمته وأدبه قبل أن تتعلم من حديثه وفقهه وقال مالك وحدث ربيعة يوما يبكى فقيل له ما الذي أباكاك أمصيبة نزلت بك فقال لا ولكن أبكاني أنه استفتى من لا علم له وكان عبد العزيز بن أبي سلمة يجلس إلى ربيعة فلما حضرت ربيعة الوفاة قال له عبد العزيز يا أبا عثمان إنا قد تعلمنا منك وربما جاءنا من يستفتينا في الشيء لم نسمع فيه شيئا فنرى أن رأينا له خير من رأيه لنفسه فنفيته فقال ربيعة أجلسوني فجلس ثم قال ويحك يا عبد العزيز لأن تموت جاهلا خير لك من أن تقول في شيء بغير علم لا لا ثلاث مرات',NULL,'172',NULL,NULL,'0');
INSERT INTO [b309] VALUES (123,'وعن الدراوردي قال إذا قال مالك وعليه أدركت أهل بلدنا وأهل العلم ببلدنا والأمر المجتمع عليه عندنا فإنه يريد ربيعة وابن هرمز وقال مالك لما خرج ربيعة إلى العراق قال لي إن سمعت أنى حدثتهم شيئا أو أفتيتهم فلا تعدني شيئا قال فكان كما قال وقال لبعض من يفتى ههنا أحق بالسجن من السراق قال بن الصلاح قيل أنه تغير في آخر عمره وترك الاعتماد عليه لذلك انتهى قال الأبناسي وما تعرض أحد لاختلاطه ووثقه الجماعة إلا أن النباتي أورده في ذيل الكامل وقال إن البستي وهو بن حبان ذكر في الزيادات متقصرا على قول ربيعة لابن شهاب إن',NULL,'173',NULL,NULL,'0');
INSERT INTO [b309] VALUES (124,'حالى ليست تشبه حالك أنا أقول برأى من شاء أخذه وذكر البخاري قول ربيعة هذا في التاريخ الكبير وقال بن سعد بعد توثيقه كانوا يتقونه لوضع الرأي انتهى وقال بن عبد البر وكان سفيان بن عيينة والشافعي وأحمد بن حنبل لا يرضون عن رأيه لأن كثيرا منه يوجد له بخلاف المسند لأنه لم يتسع فيه فضحه فيه بن شهاب وكان أبو الزناد معاديا له وكان أعلم منه وكان ربيعة أورع وقد ذمه جماعة من أهل الحديث لإغراقه في الرأي انتهى وروى بن عبد البر أيضا في كتاب جامع بيان العلم بإسناده إلى مالك قال قال لي بن هرمز لا تمسك على شيء مما سمعت منى من هذا الرأي فإنما أفتخر به وأنا وربيعة فلا تتمسك به قال والذين ابتدعوا الرأي ثلاثة وكلهم من أبناء سبايا الأمم',NULL,'174',NULL,NULL,'0');
INSERT INTO [b309] VALUES (125,'وهم ربيعة بالمدينة وعثمان البتي بالبصرة وفلان بالكوفة قال وذكر العقيلي في التاريخ الكبير بإسناده إلى الليث قال رأيت ربيعة في المنام فقلت له ما حالك فقال صرت إلى خير إلا أنى لم أحمد على كثير مما خرج منى من الرأي انتهى قال الأنباسى لم يتكلم فيه أحد إلا من جهة الرأي لا من جهة الأختلاط مع أنه قد برأه غير واحد من الرأي انتهى روى له البخاري ومسلم وأبو داود والترمذي',NULL,'175',NULL,NULL,'0');
INSERT INTO [b309] VALUES (126,'والنسائي وابن ماجة وتوفى بالمدينة سنة ست وثلاثين ومئة',NULL,'176',NULL,NULL,'0');
INSERT INTO [b309] VALUES (127,'٢٣ -<span data-type="title" id=toc-31> رواد بن الجراح العسقلاني أبو عصام</span>',NULL,'176',NULL,NULL,'0');
INSERT INTO [b309] VALUES (128,'قال أبو حاتم محله الصدق تغير حفظه وقال البخاري رواد عن سفيان كان قد اختلط لا يكاد يقوم له حديث قائم ذكره بن برهان الدين الحلبي في كتابه الاغتباط',NULL,'177',NULL,NULL,'0');
INSERT INTO [b309] VALUES (129,'<span data-type="title" id=toc-32>باب السين </span>المهملة
٢٤ -<span data-type="title" id=toc-33> سعيد بن إياس أبو مسعود الجريري </span>بضم الجيم وفتح الراء المهملة معدود في البصريين
عن ثمامة بن حزن القشيري وحبان بن عمير',NULL,'178',NULL,NULL,'0');
INSERT INTO [b309] VALUES (130,'القيسي وعبد الله بن بريدة',NULL,'179',NULL,NULL,'0');
INSERT INTO [b309] VALUES (131,'وأبي الطفيل ويزيد بن الشخير وغيرهم وعنه إسماعيل بن علية وسفيان الثوري وعبد الله بن المبارك وشعبة ويزيد بن هارون وغيرهم وهو ثقة احتج به الشيخان',NULL,'180',NULL,NULL,'0');
INSERT INTO [b309] VALUES (132,'وأطلق يحيى بن معين والنسائي القول بتوثيقه وقال أحمد بن حنبل محدث أهل البصرة قال أبو حاتم تغير حفظه قبل موته فمن كتب عنه قديما فهو صالح وهو حسن الحديث وقال كهمس أنكرنا الجريري أيام الطاعون وقال النسائي ثقة أنكر أيام الطاعون وقال يزيد بن هارون سمعت منه سنة اثنتين وأربعين ومئة وهى أول سنة دخلت البصرة ولم ننكر منه شيئا وكان قيل لنا',NULL,'181',NULL,NULL,'0');
INSERT INTO [b309] VALUES (133,'أنه اختلط وعنه ربما ابتدأنا الجريري وكان قد أنكر وقال بن عدى لا نكذب والله سمعنا منه وكان قد اختلط وقال بن حبان كان قد اختلط قبل أن يموت بثلاث سنين قال وقد رآه يحيى القطان وهو مختلط ولم يكن اختلاطه فاحشا',NULL,'182',NULL,NULL,'0');
INSERT INTO [b309] VALUES (134,'قال الأبناسى وممن سمع منه قبل التغيير شعبة وسفيان الثوري والحمادان وإسماعيل بن علية ومعمر وعبد الوارث بن سعيد ويزيد بن زريع ووهيب بن خالد وعبد الوهاب بن عبد المجيد الثقفي وذلك لأن هؤلاء كلهم سمعوا من أيوب السختياني وقد قال أبو داود فيما رواه عنه أبو عبيد الآجري كل من أدرك أيوب فسماعه من الجريري جيد وممن سمع منه بعد التغيير محمد بن أبي عدى وإسحاق',NULL,'183',NULL,NULL,'0');
INSERT INTO [b309] VALUES (135,'الأزرق ويحيى بن سعيد القطان ولذلك لم يحدث عنه شيئا وقد روى الشيخان للجريري من رواية بشر بن المفضل وخالد بن عبد الله وعبد الأعلى بن عبد الأعلى',NULL,'184',NULL,NULL,'0');
INSERT INTO [b309] VALUES (136,'وعبد الوارث بن سعيد عنه وروى له مسلم فقط من رواية محمد بن عبد الله الأنصاري عنه وروى له مسلم فقط من رواية جعفر بن سليمان الضبعي',NULL,'185',NULL,NULL,'0');
INSERT INTO [b309] VALUES (137,'وحماد بن أسامة وحماد بن سلمة وشعبة وسفيان الثوري وسالم بن نوح وابن المبارك وعبد الواحد بن زياد',NULL,'186',NULL,NULL,'0');
INSERT INTO [b309] VALUES (138,'ويزيد بن هارون وقد قيل أن يزيد بن هارون إنما سمع منه بعد التغيير فقد روى بن سعد عنه قال سمعت منه سنة اثنتين وأربعين ومئة انتهى',NULL,'187',NULL,NULL,'0');
INSERT INTO [b309] VALUES (139,'روى له البخاري ومسلم وأبو داود والترمذي والنسائي وابن ماجة وتوفى سنة أربع وأربعين ومئة',NULL,'188',NULL,NULL,'0');
INSERT INTO [b309] VALUES (140,'.......................................................................
ــ',NULL,'189',NULL,NULL,'0');
INSERT INTO [b309] VALUES (141,'٢٥ -<span data-type="title" id=toc-34> سعيد بن أبي عروبة </span>بفتح العين المهملة وتخفيف الراء المضمومة واسم أبى عروبة مهران بكسر الميم وإسكان الهاء أبو النضر معدود في البصريين اليشكري مولاهم
أحد الأعلام الثقات عن الحسن ومحمد وأبي رجاء العطاردي وقتادة وأيوب',NULL,'190',NULL,NULL,'0');
INSERT INTO [b309] VALUES (142,'السختياني وأبي معشر زياد بن كليب ويحيى بن سعيد الأنصاري وغيرهم',NULL,'191',NULL,NULL,'0');
INSERT INTO [b309] VALUES (143,'وعنه شعبة والقطان وغندر وإسماعيل بن علية وبشر بن المفضل وعبد الله بن المبارك وغيرهم احتج به الشيخان وأطلق يحيى بن معين وأبو زرعة والنسائي القول بتوثيقه وعن يحيى أثبت الناس في قتادة سعيد بن أبي عروبة وهشام الدشتوائي وشعبة فمن حدثك من هؤلاء الثلاثة',NULL,'192',NULL,NULL,'0');
INSERT INTO [b309] VALUES (144,'بحديث يعنى عن قتادة فلا تبالي أن لا تسمعه من غيره وقال أبو عوانة لم يكن عندنا في ذلك الزمان أحفظ منه قال يحيى بن معين خلط سعيد بن أبي عروبة بعد هزيمة إبراهيم بن عبد الله بن حسن سنة ثنتين وأربعين يعنى ومئة ومن سمع منه بعد ذلك فليس بشيء ويزيد بن هارون صحيح السماع منه سمع منه بواسط وهو يريد الكوفة وأثبت الناس سماعا منه عبدة بن سليمان انتهى وقال بن الصلاح وممن عرف أنه سمع بعد اختلاطه وكيع والمعافى بن عمران الموصلي بلغنا عن بن',NULL,'193',NULL,NULL,'0');
INSERT INTO [b309] VALUES (145,'عمران الموصلي أحد الحفاظ أنه قال ليست روايتهما عنه بشيء إنما سماعهما بعدما اختلط وقد روينا عن يحيى بن معين أنه قال لوكيع تحدث عن سعيد بن أبي عروبة وإنما سمعت منه في الأختلاط فقال رأيتني حدثت عنه إلا بحديث مستو انتهى وقال أبو حاتم هو قبل أن يختلط ثقة وكان أعلم الناس بحديث قتادة انتهى وقال الأبناسى ثقة احتج به الشيخان لكنه اختلط وطالت مدة اختلاطه فوق العشر سنين قال وقد اختلف في مدة اختلاطه فقال بعضهم اختلط مخرج إبراهيم سنة خمس وأربعين ومئة وكذا قال بن حبان وزاد وبقى خمس',NULL,'194',NULL,NULL,'0');
INSERT INTO [b309] VALUES (146,'سنين في اختلاطه واعترض على بن الصلاح في اقتصاره على أن هزيمة إبراهيم سنة اثنتين وأربعين مع أن المشهور في التواريخ أن خروجه وقتله في سنة خمس وأربعين قتل فيها يوم الإثنين لخمس بقين من ذي القعدة واحتز رأسه وممن سمع منه قبل اختلاطه عبد الله بن المبارك ويزيد بن زريع قاله بن حبان وغيره وكذلك شعيب بن إسحاق سمع منه أربع وأربعين قبل أن يختلط بسنة وكذلك يزيد بن هارون صحيح السماع منه قاله بن معين وكذلك عبدة بن سليمان قال بن معين إنه أثبت الناس سماعا منه',NULL,'195',NULL,NULL,'0');
INSERT INTO [b309] VALUES (147,'وقال بن عدى أرواهم عنه عبد الأعلى السامي قلت السامي بالسين المهملة ليس إلا ثم شعيب بن إسحاق وعبدة بن سليمان وعبد الوهاب الخفاف وأثبتهم فيه يزيد بن زريع وخالد بن الحارث ويحيى القطان وقال عبدة بن سليمان عن نفسه إنه سمع منه في الاختلاط إلا أنه يريد بذلك بيان اختلاطه وأنه لم يحدث بما سمع منه في الاختلاط وممن سمع منه في الاختلاط أبو نعيم الفضل بن دكين',NULL,'196',NULL,NULL,'0');
INSERT INTO [b309] VALUES (148,'ووكيع والمعافى بن سليمان الموصلي روى له الشيخان من رواية خالد بن الحارث وروح بن عبادة وعبد الأعلى السامي وعبد',NULL,'197',NULL,NULL,'0');
INSERT INTO [b309] VALUES (149,'الرحمن بن عثمان البكراوي ومحمد بن سوار السدوسي',NULL,'198',NULL,NULL,'0');
INSERT INTO [b309] VALUES (150,'ومحمد بن أبى عدى ويزيد بن زريع ويحيى بن سعيد القطان عنه وروى البخاري فقط من رواية بشر بن المفضل وسهل بن يوسف',NULL,'199',NULL,NULL,'0');
INSERT INTO [b309] VALUES (151,'وابن المبارك وعبد الوارث بن سعيد ومحمد بن عبد الله الأنصاري وكهمس بن المنهال عنه',NULL,'200',NULL,NULL,'0');
INSERT INTO [b309] VALUES (152,'وروى له مسلم فقط من رواية بن علية وأبي أسامة وسعيد بن عامر الضبعي وسالم بن نوح وأبي خالد الأحمر',NULL,'201',NULL,NULL,'0');
INSERT INTO [b309] VALUES (153,'وعبد الوهاب بن عطاء وعبدة بن سليمان وعلي بن مسهر',NULL,'202',NULL,NULL,'0');
INSERT INTO [b309] VALUES (154,'وعيسى بن يونس ومحمد بن بكر البرساني وغندر عنه وقال بن مهدى سمع غندر منه في الاختلاط انتهى وقال أبو نعيم كتبت عنه بعدما اختلط حديثين',NULL,'203',NULL,NULL,'0');
INSERT INTO [b309] VALUES (155,'وقال النسائي من حدث عن سعيد بن أبي عروبة ولم يسمع منه لم يسمع من عمرو بن دينار ولا من هشام بن عروة ولا من زيد بن أسلم ولا من عبيد الله بن عمر ولا من أبي الزناد ولا من الحكم',NULL,'204',NULL,NULL,'0');
INSERT INTO [b309] VALUES (156,'ولا من حماد ولا من إسماعيل بن أبي خالد وسئل أبو زرعة عن يحيى بن سلام بالتشديد المغربي',NULL,'205',NULL,NULL,'0');
INSERT INTO [b309] VALUES (157,'فقال لا بأس به وربما وهم ثم قال ثنا أبو سعيد الجعفي قال ثنا يحيى بن سلام عن سعيد بن أبي عروبة عن قتادة في قوله تعالى سأوريكم دار الفاسقين قال مصر فاستعظم أبو زرعة ذلك واستقبحه فقيل له فأيش أراد بهذا قال هو في تفسير سعيد عن قتادة مصيرهم وقال أحمد بن حنبل لم يكن لسعيد كتاب إنما كان يحفظ ذلك كله وزعموا أنه قال لم أكتب إلا تفسير قتادة لأن أبا معشر كتب إلى أن أكتبه قال الأبناسي وأما مدة اختلاطه فقيل خمس سنين وقال صاحب الميزان ثلاث عشرة سنة وقال في العبر عشر سنين مع قوله فيهما أنه توفي سنة ست وخمسين',NULL,'206',NULL,NULL,'0');
INSERT INTO [b309] VALUES (158,'يعنى ومئة وكذا قال الغلاسى وأبو موسى وغير واحد في وفاته وقيل سنة سبع وخمسين روى له البخاري ومسلم وأبو داود والترمذي والنسائي وابن ماجة',NULL,'207',NULL,NULL,'0');
