INSERT INTO [b309] VALUES (160,'.....................................................................
ــ',NULL,'209',NULL,NULL,'0');
INSERT INTO [b309] VALUES (161,'.....................................................................
ــ',NULL,'210',NULL,NULL,'0');
INSERT INTO [b309] VALUES (162,'.....................................................................
ــ',NULL,'211',NULL,NULL,'0');
INSERT INTO [b309] VALUES (163,'.....................................................................
ــ',NULL,'212',NULL,NULL,'0');
INSERT INTO [b309] VALUES (164,'٢٦ - (١)<span data-type="title" id=toc-35> سعيد بن عبد العزيز بن أبي يحيى التنوخي أبو محمد </span>ويقال أبو عبد العزيز معدود في أهل دمشق وهو كان فقيها مفتي دمشق وعالمها بعد الأوزاعي قرأ القرآن على عبد الله بن عامر',NULL,'213',NULL,NULL,'0');
INSERT INTO [b309] VALUES (165,'روى عن إسماعيل بن عبيد الله بن أبي المهاجر ومحمد بن مسلم بن شهاب الزهري ومكحول الشامي وغيرهم',NULL,'214',NULL,NULL,'0');
INSERT INTO [b309] VALUES (166,'وعنه عبد الله بن المبارك وأبو مسهر عبد الأعلى بن مسهر الغساني ووكيع بن الجراح والوليد بن مسلم وغيرهم',NULL,'215',NULL,NULL,'0');
INSERT INTO [b309] VALUES (167,'أطلق يحيى بن معين وأبو حاتم والعجلي القول بتوثيقه وقال أحمد بن حنبل ليس بالشام رجل أصح حديثا منه وقال عمرو بن على حديث الشاميين كله ضعيف إلا نفر منهم الأوزاعي وسعيد بن عبد العزيز وعبد الرحمن بن ثابت بن ثوبان وعبد الله بن العلاء بن [زبر (٦) ]',NULL,'216',NULL,NULL,'0');
INSERT INTO [b309] VALUES (168,'وقيل لدحيم من بعد عبد الرحمن بن يزيد بن جابر من أصحاب مكحول قال الأوزاعي وسعيد بن عبد العزيز وكان سعيد أكثر مجالسة لمكحول من الأوزاعي وقيل ليحيى بن معين محمد بن إسحاق ممن يحتج به فقال إنما الحجة عبيد الله بن عمر ومالك بن أنس وسعيد بن عبد العزيز',NULL,'217',NULL,NULL,'0');
INSERT INTO [b309] VALUES (169,'قيل كان كثير البكاء في الصلاة فقال مروان بن محمد ما هذا البكاء الذي يعرض لك في الصلاة قال يا بن أخي وما سؤالك عن ذلك فقال لعل الله ينفعني به فقال ما قمت إلى الصلاة إلا مثلت لي جهنم وقال أبو حاتم كان أبو مسهر يقدمه على الأوزاعي ولا أقدم بالشام بعد الأوزاعي على سعيد بن عبد العزيز أحدا وقال مروان بن محمد لم يكن له كتاب وإنما كان علمه في صدره وقال الحاكم أبو عبد الله هو لأهل الشام كمالك بن أنس لأهل المدينة في التقدم والفقه والأمانة',NULL,'218',NULL,NULL,'0');
INSERT INTO [b309] VALUES (170,'وقال النسائي ثقة ثبت وقال أبو مسهر كان قد اختلط قبل موته كذا قال صاحب التهذيب وقال حمزة الكناني إنه تغير وذكره صاحب الاغتباط في جملة من رمى بالاختلاط روى له مسلم وأبو داود',NULL,'219',NULL,NULL,'0');
INSERT INTO [b309] VALUES (171,'والترمذي والنسائي وابن ماجة توفي سنة سبع وستين ومئة رحمه الله',NULL,'220',NULL,NULL,'0');
INSERT INTO [b309] VALUES (172,'٢٧ - (٤)<span data-type="title" id=toc-36> سفيان بن عيينة بن أبي عمران واسمه ميمون</span>',NULL,'220',NULL,NULL,'0');
INSERT INTO [b309] VALUES (173,'الهلالي أبو محمد معدود في الكوفيين وفي الموالي وولاؤه لمحمد بن مزاحم أخى الضحاك بن مزاحم وكان أعور وقيل إن أبا عيينة كان يكنى أبا عمران أحد الأعلام ثقة حافظ إمام يروى عن إبراهيم بن عقبة',NULL,'221',NULL,NULL,'0');
INSERT INTO [b309] VALUES (174,'وإسحاق بن عبد الله بن أبي طلحة وإسماعيل بن أبي خالد وأيوب السختياني',NULL,'222',NULL,NULL,'0');
INSERT INTO [b309] VALUES (175,'وزكريا بن أبي زائدة وسلمة بن دينار',NULL,'223',NULL,NULL,'0');
INSERT INTO [b309] VALUES (176,'وعبد الله بن طاوس وأبي إسحاق السبيعي بفتح السين وتقدمت ترجمته والزهري',NULL,'224',NULL,NULL,'0');
INSERT INTO [b309] VALUES (177,'وعمرو بن دينار ويروى عنه أحمد بن صالح المصري وإسحاق بن',NULL,'225',NULL,NULL,'0');
INSERT INTO [b309] VALUES (178,'راهويه وعلى بن المديني وأبي كريب محمد بن',NULL,'226',NULL,NULL,'0');
INSERT INTO [b309] VALUES (179,'العلاء ويعقوب بن إبراهيم ومن شيوخه الأعمش وابن جريج',NULL,'227',NULL,NULL,'0');
INSERT INTO [b309] VALUES (180,'قال بن المديني لم يكن في أصحاب الزهري أتقن منه وقال العجلي كوفي ثقة ثبت في الحديث وقال بعضهم هو أثبت الناس في حديث الزهري وقال مجاهد بن موسى سمعته يقول ما كتبت شيئا قط إلا شيئا حفظته قبل أن أكتبه وقال الشافعي لولا مالك وسفيان لذهب علم الحجاز وسئل عنه بن المبارك فقال ذاك أحد الأحدين وقيل لابن المديني هو إمام في الحديث فقال هو إمام منذ',NULL,'228',NULL,NULL,'0');
INSERT INTO [b309] VALUES (181,'أربعين سنة وقيل ليحيى بن معين بن عيينة أحب إليك في عمرو بن دينار أو الثوري فقال بن عيينة أعلم به فقيل له فابن عيينة أحب إلي فيه أو حماد بن زيد قال بن عيينة أعلم به قيل له فشعبة قال وأيش روى عنه شعبة إنما روى عنه نحوا من مائة حديث قال بن وهب ما رأيت أحدا أعلم بكتاب الله من بن عيينة وقال الشافعي ما رأيت أحدا أكف عن الفتيا منه وقال أحمد بن حنبل كان إذا سئل عن المناسك سهل عليه وإذا سئل عن الطلاق اشتد عليه قال بن الصلاح وجدت عن محمد بن عبد الله بن عمار الموصلي أنه سمع يحيى بن [سعيد] (٦) القطان يقول أشهد أن سفيان بن عيينة اختلط سنة سبع وتسعين فمن سمع منه في هذه',NULL,'229',NULL,NULL,'0');
INSERT INTO [b309] VALUES (182,'السنة و [بعدها] (١) فسماعه لا شيء قلت توفي بعد ذلك [بنحو] (٢) بسنتين سنة تسع وتسعين ومئة انتهى قال الأبناسي قوله يعني بن الصلاح سفيان بن عيينة إلى آخره فيه أمور منها أن
صاحب الميزان استبعد مقالة بن عمار وعدها غلطا منه لأن القطان مات في صفر سنة ثمان وتسعين وقت قدوم الحاج ووقت تحدثهم عن أخبار الحجاز فمتى تمكن يحيى بن سعيد من أن يسمع اختلاط سفيان ثم يشهد عليه بذلك والموت قد نزل به ثم قال فلعله بلغه ذلك في أثناء سنة سبع وتسعين',NULL,'230',NULL,NULL,'0');
INSERT INTO [b309] VALUES (183,'وقد سمع منه في هذه السنة، محمد بن عاصم صاحب ذلك الجزء العالي كما هو مؤرخ في الجزء المذكور وهكذا ذكره صاحب الميزان قال فلما كان سنة ثمان وتسعين فإنه مات فيها ولم يلقه أحد يحدث فإنه توفي قبل قدوم الحاج بأربعة أشهر قال ويغلب على الظن أن سائر شيوخ الأئمة الستة سمعوا منه قبل سنة سبع ومنها قوله أنه توفي سنة تسع والمشهور سنة ثمان ومنها قوله أنه بقي بعد اختلاطه سنتين وهذا ينافي',NULL,'231',NULL,NULL,'0');
INSERT INTO [b309] VALUES (184,'ما صححه في وفاته أنه سنة تسع وإلا فالمشهور أنها سنة ثمان فتكون مدة اختلاطه نحو سنة لأن وفاته كانت بمكة يوم السبت أول شهر رجب سنة ثمان وتسعين ومئة قال محمد بن سعد وابن حبان إلا أنه قال آخر يوم من جمادي الآخرة انتهى روى له البخاري ومسلم وأبو داود',NULL,'232',NULL,NULL,'0');
INSERT INTO [b309] VALUES (185,'والترمذي والنسائي وابن ماجة',NULL,'233',NULL,NULL,'0');
INSERT INTO [b309] VALUES (186,'....................................................................
ــ',NULL,'234',NULL,NULL,'0');
INSERT INTO [b309] VALUES (187,'٢٨ -<span data-type="title" id=toc-37> سلمة بن نبيط بن شريط الأشجعي أبو فراس</span>
معدود في الكوفيين عن الضحاك بن مزاحم',NULL,'235',NULL,NULL,'0');
INSERT INTO [b309] VALUES (188,'وأبيه وقيل عن رجل من الحي عن أبيه وعنه سفيان الثوري ووكيع بن الجراح وغيرهما أطلق يحيى بن معين وأحمد بن حنبل',NULL,'236',NULL,NULL,'0');
INSERT INTO [b309] VALUES (189,'والعجلي والنسائي ومحمد بن عبد الله بن نمير القول بتوثيقه وكان وكيع يفتخر به ويقول حدثنا سلمة بن نبيط وكان ثقة وقال أبو حاتم صالح ما به بأس وأثبته بن حبان في الثقات وقال البخاري يقال اختلط بأخرة (٧) ذكره صاحب الاغتباط',NULL,'237',NULL,NULL,'0');
INSERT INTO [b309] VALUES (190,'٢٩ -<span data-type="title" id=toc-38> سماك بن حرب بن أوس بن خالد بن ثعلبة الذهلي</span>',NULL,'237',NULL,NULL,'0');
INSERT INTO [b309] VALUES (191,'البكري أبو المغيرة معدود في الكوفيين عن إبراهيم بن يزيد النخعي وأنس بن مالك وجابر بن سمرة وسعيد بن جبير وغيرهم',NULL,'238',NULL,NULL,'0');
INSERT INTO [b309] VALUES (192,'وعنه سفيان الثوري وشعبة بن الحجاج وأبو عوانة الوضاح وغيرهم قال يحيى بن معين ثقة وقال أبو حاتم صدوق ثقة وقال علي بن المديني له نحو مائتي حديث وقال سماك بن حرب أدركت ثمانين من أصحاب النبي صلى الله عليه وسلم وكان قد ذهب بصري فدعوت الله فرد علي بصري',NULL,'239',NULL,NULL,'0');
INSERT INTO [b309] VALUES (193,'قال الذهبي ساء حفظه وقال محمد بن عبد الله بن عمار يقولون أنه كان يغلط ويختلفون في حديثه وقال يعقوب روايته عن عكرمة مضطربة وهو في غير عكرمة صالح ومن سمع منه قديما مثل سفيان وشعبة فحديثه عنه صحيح مستقيم [قال يعقوب: وقول ابن المبارك فيه إنما نراه فيمن سمع منه بأخرة (٤) .]',NULL,'240',NULL,NULL,'0');
INSERT INTO [b309] VALUES (194,'روى له مسلم وأبو داود والترمذي والنسائي وابن ماجة وتوفي سنة ثلاث وعشرين ومئة',NULL,'241',NULL,NULL,'0');
INSERT INTO [b309] VALUES (195,'٣٠ - سهيل بن أبي صالح واسمه ذكوان السمان أبو',NULL,'241',NULL,NULL,'0');
INSERT INTO [b309] VALUES (196,'يزيد معدود في المدنيين كان مولى لجويرية بنت الأحمس وهو أخو صالح وعبد الله ومحمد أبناء أبي صالح عن سليمان الأعمش وسمي مولى أبي بكر بن عبد',NULL,'242',NULL,NULL,'0');
INSERT INTO [b309] VALUES (197,'الرحمن بن الحارث بن هشام وعبد الله بن دينار والنعمان بن أبي عياش وغيرهم',NULL,'243',NULL,NULL,'0');
INSERT INTO [b309] VALUES (198,'وعنه إبراهيم بن محمد الفزاري وإسماعيل بن زكريا وحماد بن سلمة وسفيان الثوري وغيرهم',NULL,'244',NULL,NULL,'0');
INSERT INTO [b309] VALUES (199,'أطلق أحمد بن عبد الله العجلي القول بتوثيقه وقال النسائي ليس به بأس وقال بن عدي ثبت مقبول حدث عن أبيه وعن جماعة عن أبيه وقال سفيان بن عيينة كنا نعده ثبتا في الحديث وقال أحمد بن حنبل ما أصح حديثه وقال هو أثبت من محمد',NULL,'245',NULL,NULL,'0');
INSERT INTO [b309] VALUES (200,'بن عمرو قال الذهبي عن بن القطان أنه هو وهشام بن عروة اختلطا وتغيرا ذكر ذلك في الميزان وقال عبد العزيز الدراوردي أصاب سهيلا علة أذهبت بعض عقله ونسي بعض حديثه',NULL,'246',NULL,NULL,'0');
INSERT INTO [b309] VALUES (201,'روى له البخاري مقرونا ومسلم وأبو داود والترمذي والنسائي وابن ماجة وتوفي سنة [أربعين (٧) ومائة] .',NULL,'247',NULL,NULL,'0');
INSERT INTO [b309] VALUES (202,'٣١ -<span data-type="title" id=toc-40> سعيد بن سفيان الأندلسي</span>
رحل وأدرك إسحاق الدبري قال بن الفرضي',NULL,'248',NULL,NULL,'0');
INSERT INTO [b309] VALUES (203,'خلط في آخر عمره الظاهر أنه أراد الاختلاط ذكره صاحب الاغتباط',NULL,'249',NULL,NULL,'0');
INSERT INTO [b309] VALUES (204,'<span data-type="title" id=toc-41>باب الشين </span>المعجمة
٣٢ -<span data-type="title" id=toc-42> شريك بن عبد الله النخعي أبو عبد الله القاضي</span>
معدود في الكوفيين عن إبراهيم بن جرير بن عبد الله البجلي',NULL,'250',NULL,NULL,'0');
INSERT INTO [b309] VALUES (205,'والحجاج بن أرطاة وعبد الله بن شبرمة وعبد',NULL,'251',NULL,NULL,'0');
INSERT INTO [b309] VALUES (206,'الملك بن عمير وليث بن أبي سليم وغيرهم وعنه الأسود بن عامر شاذان وأبو أسامة حماد بن أسامة وعلي بن حجر ووكيع بن الجراح ويزيد بن هارون وغيرهم قال يحيى بن معين ثقة وهو أحب إلي من أبي الأحوص',NULL,'252',NULL,NULL,'0');
INSERT INTO [b309] VALUES (207,'وجرير روى عن قوم لم يروى عنهم سفيان وقال العجلي كوفي ثقة وقال وكيع لم يكن في الكوفيين أروى من شريك وقال أحمد بن حنبل هو أثبت في أبي إسحاق من زهير وأبي إسرائيل وزكريا',NULL,'253',NULL,NULL,'0');
INSERT INTO [b309] VALUES (208,'وقال عيسى بن يونس ما رأيت أحدا أورع في علمه من شريك وأثبته بن حبان في الثقات وقال كان في آخر عمره يخطىء فيما يروي تغير عليه حفظه فسماع المتقديمن عنه الذين سمعوا بواسط ليس فيهم تخليط مثل يزيد بن هارون وإسحاق الأزرق وسماع المتأخرين عنه بالكوفة فيه أوهام وقال الذهبي في ميزانه في ترجمته قال عبد الجبار بن محمد قلت ليحيى بن سعيد زعموا أن شريكا إنما خلط بأخرة قال ما زال مختلطا',NULL,'254',NULL,NULL,'0');
INSERT INTO [b309] VALUES (209,'وقال أبو زرعة كان كثير الخطأ صاحب وهم وهو يغلط أحيانا فقيل له إنه حدث بواسطة بأحاديث بواطيل فقال أبو زرعة لا تقل بواطيل وقال بن عدي له حديث كثير من المقطوع والمسند وبعض ذلك فيه إنكار والغالب على حديثه الصحة والذي يقع فيه النكرة من حديثه أوتي فيه من سوء حفظه وليس يتعمد شيئا من ذلك فينسب بسببه إلى الضعف وقيل له من أدبك فقال أدبتني نفسي لقد كنت بالكوفة أضرب اللبن وأبيعه وأشتري به دفاتر وطروسا فأكتب فيها',NULL,'255',NULL,NULL,'0');
INSERT INTO [b309] VALUES (210,'العلم والحديث ثم طلبت الفقه فبلغت ما ترى روى له مسلم وأبو داود والترمذي والنسائي وابن ماجة وتوفي سنة سبع وسبعين ومئة وله ٨٢ سنة يعني اثنتين وثمانين',NULL,'256',NULL,NULL,'0');
INSERT INTO [b309] VALUES (211,'.............................................................
ــ',NULL,'257',NULL,NULL,'0');
INSERT INTO [b309] VALUES (212,'<span data-type="title" id=toc-43>باب الصاد </span>المهملة
٣٣ -<span data-type="title" id=toc-44> صالح بن نبهان مولى التوأمة بنت أمية بن خلف الجمحي أبو محمد</span>
معدود في المدنيين وقال أبو زرعة هو صالح بن صالح بن نبهان وقيل صالح بن أبي صالح عن عبد الله بن عباس',NULL,'258',NULL,NULL,'0');
INSERT INTO [b309] VALUES (213,'وأبي هريرة وعائشة وغيرهم وعنه السفيانان ومحمد بن عبد الرحمن بن أبي ذئب',NULL,'259',NULL,NULL,'0');
INSERT INTO [b309] VALUES (214,'وموسى بن عقبة وغيرهم أطلق يحيى بن معين القول بتوثيقه والحكم بأنه حجة وقال [أحمد بن سعد] (٤) بن أبي مريم قلت ليحيى لم يسمع منه مالك بل تركه فقال إنما ترك السماع منه لأنه أدركه بعدما كبر',NULL,'260',NULL,NULL,'0');
INSERT INTO [b309] VALUES (215,'وقال يحيى بن معين ثقة خرف قبل أن يموت فمن سمع منه [قبل (١) ] فهو ثبت قال بن الصلاح قال أبو حاتم بن حبان تغير في سنة خمس وعشرين ومئة واختلط حديثه الأخير بحديثه القديم ولم يتميز فاستحق الترك قال الأبناسي وقال بن حبان تغير في سنة خمس وعشرين ومائة وجعل يأتي بما يشبه الموضوعات عن الثقات فاختلط حديثه الأخير بالمتقدم ولم يميز فاستحق [الترك (٣) ] كذا اقتصر يعين بن الصلاح على كلام بن حبان فيه وليس كذلك فقد ميز غير واحد من الأئمة بعض من سمع منه في صحته ممن سمع منه بعد اختلاطه فممن سمع منه قديما محمد بن عبد الرحمن بن أبي ذئب قال علي بن المديني ويحيى بن معين والجوزجاني وابن عدي وسمع منه قديما أيضا عبد الملك بن جريج وزياد بن سعد قاله بن عدي',NULL,'261',NULL,NULL,'0');
INSERT INTO [b309] VALUES (216,'وكذلك سمع منه قديما أسيد بن أبي أسيد وسعيد بن أبي أيوب وعبد الله بن علي الأفريقي',NULL,'262',NULL,NULL,'0');
INSERT INTO [b309] VALUES (217,'وعمارة بن غزية وموسى بن عقبة وممن سمع منه بعد الاختلاط مالك بن أنس والسفيانان انتهى وقال سفيان بن عيينة لقيته سنة خمس أو ست وعشرين ومئة وقد تغير ولقيه الثوري بعدي وجعلت أقول له أسمعت من بن عباس أسمعت من أبي هريرة فجعل لا يجيبني فقال شيخ عنده أنه قد كبر قلت فهذا مما يدل على أن السفيانين سماعهما منه بعد [الاختلاط (٤) ] وكذا قال يحيى أن سفيان لم يدركه إلا بعد الاختلاط فسمع منه أحاديث منكرات',NULL,'263',NULL,NULL,'0');
INSERT INTO [b309] VALUES (218,'وقال الأصمعي كان شعبة لا يحدث عنه وقال بن عدي لا بأس به إذا سمعوا منه قديما مثل بن أبي ذئب وزياد بن سعد وابن جريج وغيرهم ومن سمع منه بأخرة وهو مختلط مالك والثوري وغيرهما وحديثه الذي حدث به قبل الإختلاط لا أعرف فيه منكرا إذا روى عنه ثقة وإنما البلاء من دون بن أبي ذئب فيكون الرازي ومحمد بن حمزة بن عمارة الأصبهاني ضعيفا فيروى',NULL,'264',NULL,NULL,'0');
INSERT INTO [b309] VALUES (219,'عنه ولا يكون البلاء من قبله وصالح لا بأس به برواياته وقال يحيى لم يدركه بن أبي ذئب إلا قبل الاختلاط روى له أبو داود والترمذي وابن ماجة وتوفي سنة خمس وعشرين ومئة',NULL,'265',NULL,NULL,'0');
INSERT INTO [b309] VALUES (220,'<span data-type="title" id=toc-45>باب العين </span>المهملة
٣٤ -<span data-type="title" id=toc-46> عبد الرزاق بن همام بن نافع الحميدي الصنعاني أبو بكر</span>
معدود في أهل صنعاء وفي الموالي عن إبراهيم بن يزيد والسفيانين وغيرهم',NULL,'266',NULL,NULL,'0');
INSERT INTO [b309] VALUES (221,'وعنه أبو الأزهر أحمد بن الأزهر النيسابوري وأبو مسعود أحمد بن الفرات وأحمد بن حنبل',NULL,'267',NULL,NULL,'0');
INSERT INTO [b309] VALUES (222,'وإسحاق بن راهويه ويحيى بن معين وغيرهم صنف التصانيف واحتج به الشيخان قال أحمد بن حنبل ما رأيت أحسن حديثا منه وقال أبو زرعة هو أحد من ثبت حديثه',NULL,'268',NULL,NULL,'0');
INSERT INTO [b309] VALUES (223,'وسئل أحمد بن حنبل عن حديث من حديثه فقال هو باطل من يحدث به عن عبد الرزاق فقال الأثرم حدثني به أحمد بن شبويه فقال هؤلاء سمعوا بعدما عمي وكان يلقن فيتلقن وليس هو في كتبه وقد أسندوا عنه أحاديث ليست في كتبه كان يلقنها بعدما عمى وعن أحمد أيضا من سمع منه بعدما ذهب بصره فهو ضعيف السماع وقال يحيى بن معين هو أثبت في حديث معمر من هشام بن يوسف وكان هشام في حديث بن جريج أثبت',NULL,'269',NULL,NULL,'0');
INSERT INTO [b309] VALUES (224,'منه وقال هشام كان سن عبد الرزاق وقت قدوم بن جريج اليمن ثمان عشرة سنة وقال علي بن المديني قال لي هشام بن يوسف كان عبد الرزاق أعلمنا وأحفظنا وقال علي أيضا قال لي عبد الرزاق كتب عني ثلاثة',NULL,'270',NULL,NULL,'0');
INSERT INTO [b309] VALUES (225,'لا أبالي أن لا يكتب عني غيرهم بن الشاذكوني وهو من أحفظ الناس ويحيى بن معين وهو من أعرف الناس بالرجال وأحمد بن حنبل وهو من أزهد الناس وقيل ليحيى بن معين أن عبيد الله بن موسى يرد حديثه للتشيع فقال يحيى كان والله الذي لا إله إلا هو عبد الرزاق أعلى في ذلك منه مائة ضعف',NULL,'271',NULL,NULL,'0');
INSERT INTO [b309] VALUES (226,'وقال سلمة بن شبيب سمعت عبد الرزاق يقول والله ما انشرح صدري قط أن أفضل عليا على أبي بكر وعمر رحمهم الله جميعهم وقال بن عدي له حديث كثير وقد رحل اليه ثقات المسلمين وأثبتهم وكتبوا عنه فلم يروا بحديثه بأسا الا انهم نسبوه للتشيع وقد روى الفضائل مالا يوافقه عليه [أحد (٣) من الثقات] وهذا أعظم [ما ذموه (٤) ] وأما في باب الصدق فإني أرجو أنه لا بأس به إلا أنه قد سبق منه أحاديث في فضائل أهل البيت مناكير قال بن الصلاح ذكر أحمد بن حنبل أنه عمي في أخر عمره فكان يلقن فيتلقن فسماع من سمع منه بعدما عمي لا شيء',NULL,'272',NULL,NULL,'0');
INSERT INTO [b309] VALUES (227,'وقال النسائي فيه نظر لمن كتب عنه بأخرة قلت و [على (١) ] هذا يحمل قول عباس بن عبد العظيم لما رجع من صنعاء والله لقد تجشمت إلى عبد الرزاق وإنه لكذاب والواقدي أصدق منه قلت قد وجدت فيما روى الطبراني عن إسحاق بن إبراهيم الدبري عن عبد الرزاق أحاديث [استنكرتها (٣) ] جدا [فأحلت (٤) ] أمرها على ذلك فإن سماع الدبري [منه] (٥) متأخر جدا قال إبراهيم مات عبد الرزاق وللدبري ست أو سبع سنين [ويحصل أيضا نظر في كثير من العوالي الواقعة عمن تأخر سماعه من سفيان بن عيينة وأشباهه.',NULL,'273',NULL,NULL,'0');
INSERT INTO [b309] VALUES (228,'انتهى (١) ] قال الأبناسي اقتصر يعني بن الصلاح على من سمع منه بعد تغيره على إسحاق مع أنه سمع منه بعد عماه جماعة منهم أحمد بن محمد قاله أحمد بن حنبل ومنهم محمد بن حماد الطهراني وإبراهيم بن منصور الرمادي',NULL,'274',NULL,NULL,'0');
INSERT INTO [b309] VALUES (229,'ومنهم الجماعة الذين سمع منهم الطبراني في رحلته إلى صنعاء من أصحاب عبد الرزاق منهم الدبري الذي تقدم وكان سماعه من عبد الرزاق سنة عشر ومئتين ومنهم إبراهيم بن محمد بن برة الصنعاني',NULL,'275',NULL,NULL,'0');
INSERT INTO [b309] VALUES (230,'ومنهم إبراهيم بن محمد بن عبد الله بن سويد ومنهم الحسن بن عبد الأعلى الصنعاني فهؤلاء الأربعة سمع منهم الطبراني سنة اثنين وثمانين وسماعهم من عبد الرزاق بأخرة وممن سمع منه قبل الاختلاط أحمد وإسحاق بن راهويه وعلي بن المديني ويحيى بن معين ووكيع بن الجراح في آخرين آخرج لهم الشيخان من رواياتهم عن عبد الرزاق فممن اتفق الشيخان على الإخراج له عن عبد الرزاق مع',NULL,'276',NULL,NULL,'0');
INSERT INTO [b309] VALUES (231,'إسحاق بن راهويه إسحاق بن منصور ومحمود بن غيلان وممن أخرج له البخاري فقط عن عبد الرزاق مع على بن',NULL,'277',NULL,NULL,'0');
INSERT INTO [b309] VALUES (232,'المديني إسحاق بن إبراهيم السعدي وعبد الله بن محمد المسندي ومحمد بن يحيى بن أبي عمر العدني ويحيى بن جعفر البيكندي ويحيى بن موسى البلخي الملقب خت',NULL,'278',NULL,NULL,'0');
INSERT INTO [b309] VALUES (233,'وممن أخرج له مسلم عن عبد الرزاق مع أحمد بن حنبل أحمد بن يوسف السلمي وحجاج بن يوسف الشاعر والحسن بن علي الخلان وسلمة بن شبيب وعبد الرحمن بن بشر بن الحكم',NULL,'279',NULL,NULL,'0');
INSERT INTO [b309] VALUES (234,'وعبد بن حميد وعمرو بن محمد الناقد ومحمد بن رافع ومحمد بن مهران الحمال واستصغر الدبري في عبد الرزاق لأنه مات وللدبري ست سنين أو سبع قال الذهبي اعتنى به أبوه فأسمعه تصانيفه وعمره سبع سنين أو نحوها',NULL,'280',NULL,NULL,'0');
INSERT INTO [b309] VALUES (235,'واحتج به أبو عوانة في صحيحه وغيره ومن احتج به لا يبالي بتغيره لأنه إنما حدث من كتبه لا من حفظه انتهى روى له البخاري ومسلم وأبو داود والترمذي والنسائي وابن ماجة عاش خمسا وتسعين سنة وتوفي سنة إحدى عشرة ومئتين رحمه الله',NULL,'281',NULL,NULL,'0');
INSERT INTO [b309] VALUES (236,'٣٥ -<span data-type="title" id=toc-47> عبد الرحمن بن عبد الله بن عتبة بن عبد الله بن مسعود الهذلي المسعودي أخو أبي العميس </span>من كبار العلماء عن أبي بكر بن حزم',NULL,'282',NULL,NULL,'0');
INSERT INTO [b309] VALUES (237,'وعبد الرحمن بن الأسود النخعي وعمرو بن مرة',NULL,'283',NULL,NULL,'0');
INSERT INTO [b309] VALUES (238,'وجامع بن شداد وسعيد بن أبي بردة وغيرهم',NULL,'284',NULL,NULL,'0');
INSERT INTO [b309] VALUES (239,'وعنه علي بن الجعد وعاصم بن علي والسفيانان',NULL,'285',NULL,NULL,'0');
INSERT INTO [b309] VALUES (240,'وأبو داود سليمان بن داود الطيالسي وشعبة بن الحجاج وغيرهم وحكم يحيى بن معين وغيره بتوثيقه إلا أن الإمام أحمد ذكر أنه اختلط ببغداد وأن سماع من سمع منه هناك ليس بشيء قال ومن سمع منه بالكوفة فسماعه جيد وذكر الحاكم أبو عبد الله في كتاب المزكين للرواة عن يحيى بن معين أنه قال من سمع من المسعودي في زمان أبي جعفر فهو',NULL,'286',NULL,NULL,'0');
INSERT INTO [b309] VALUES (241,'صحيح السماع ومن سمع منه في أيام المهدي فليس سماعه بشيء وذكر حنبل بن إسحاق عن أحمد بن حنبل أنه قال سماع عاصم بن علي وأبي النضر هاشم من المسعودي بعدما اختلط قال الأبناسي في كتابه الشذا الفياح وقد سمع',NULL,'287',NULL,NULL,'0');
INSERT INTO [b309] VALUES (242,'من المسعودي بعد الاختلاط عاصم بن علي وأبو النضر هاشم بن القاسم وعبد الرحمن بن مهدي ويزيد بن هارون وحجاج بن محمد بن الأعور وأبو داود الطيالسي وعلي بن الجعد قال محمد بن عبد الله بن نمير كان المسعودي ثقة فلما كان بأخرة اختلط سمع منه عبد الرحمن بن مهدي ويزيد بن هارون أحاديث مختلطة وما روى عنه الشيوخ فهو مستقيم وقال عمرو بن علي الفلاس سمعت يحيى بن سعيد يقول رأيت المسعودي سنة رآه عبد الرحمن بن مهدي فلم أكلمه قال الطيالسي سمع بن مهدي من المسعودي بمكة',NULL,'288',NULL,NULL,'0');
INSERT INTO [b309] VALUES (243,'شيئا يسيرا وقال الفلاس سمعت أبا قتيبة هو سالم بن قتيبة يقول رأيت المسعودي سنة ثلاث وخمسين وكتبت عنه وهو صحيح ثم رأيته سنة سبع وخمسين والذر يدخل في أذنيه وأبو داود يكتب عنه فقلت له أتطمع أن تحدث عنه وأنا حي وقال عثمان بن عمر بن فارس كتبنا عن المسعودي وأبو',NULL,'289',NULL,NULL,'0');
INSERT INTO [b309] VALUES (244,'داود جرو يلعب بالقرب وأما علي بن الجعد فإن سماعه منه أيضا ببغداد فإن علي بن الجعد إنما قدم البصرة سنة ست وخمسين ومائة والمسعودي يومئذ ببغداد وقال معاذ بن معاذ رأيت المسعودي سنة أربع وخمسين يطالع الكتاب يعني أنه قد تغير حفظه وهذا موافق لما حكي عن أحمد أنه اختلط ببغداد ومن سمع منه بالكوفة وبالبصرة فسماعه جيد وقدومه بغداد سنة أربع وخمسين ولكن لم يختلط في أول قدومه فقد سمع منه شعبة بها وعلى هذا',NULL,'290',NULL,NULL,'0');
INSERT INTO [b309] VALUES (245,'فقد طالت مدة اختلاطه لا سيما على قول من قال أنه مات سنة خمس وستين وهو قول يعقوب بن أبي شيبة رواه الخطيب في التاريخ عنه وقال معاذ بن معاذ قدم علينا المسعودي البصرة قدمتين يملي علينا إملاء ثم لقيت المسعودي ببغداد سنة أربع وخمسين وما أنكر منه قليلا ولا كثيرا فجعل يملي على ثم أذن لي في بيته ومعي عبد الله بن عثمان ما ننكر منه قليلا ولا كثيرا ثم قدمت عليه قدمة أخرى مع عبد الله بن حسن فقلت لمعاذ سنة كم',NULL,'291',NULL,NULL,'0');
INSERT INTO [b309] VALUES (246,'قال سنة إحدى وستين قال ثم لقيته يوما فسألته عن حديث القاسم فأنكره وقال ليس من حديثي قال ثم رأيت رجلا جاءه بكتاب عمرو بن مرة عن إبراهيم فقال كيف هو في كتابك قال عن علقمة وجعل يلاحظ كتابه قال معاذ فقلت له إنك إنما حدثتناه عن عمرو بن مرة عن إبراهيم عن عبد الله قال هو عين علقمة فهذا يدل على أنه تأخر إلى سنة إحدى وستين',NULL,'292',NULL,NULL,'0');
INSERT INTO [b309] VALUES (247,'ومنها في بيان من سمع منه قبل اختلاطه قال أحمد يعني بن حنبل سماع وكيع من المسعودي بالكوفة قديم وأبو نعيم أيضا قال أنه اختلط ببغداد وعلى هذا تقبل رواية كل من سمع منه بالكوفة والبصرة قبل أن يقدم بغداد كأميه بن خالد وبشر بن المفضل وجعفر بن عون وخالد بن الحارث وسفيان بن حبيب وسفيان',NULL,'293',NULL,NULL,'0');
INSERT INTO [b309] VALUES (248,'الثوري وأبو قتيبة سلم بن قتيبة وطلق بن غنام وعبد الله بن رجاء وعثمان بن عمر بن فارس وعمرو بن مرزوق وعمرو بن الهيثم والقاسم بن معن بن',NULL,'294',NULL,NULL,'0');
INSERT INTO [b309] VALUES (249,'عبد الرحمن ومعاذ بن معاذ العنبري والنضر بن شميل ويزيد بن زريع وقد شدد بعضهم في أمر المسعودي ورد حديثه كله لأنه لا يتميز حديثه القديم من حديثه الأخير قال بن حبان في تاريخ الضعفاء كان المسعودي صدوقا إلا أنه اختلط في آخر عمره اختلاطا شديدا حتى ذهب عقله وكان يحدث بما يحب فحمل عنه ولم يتميز فاستحق الترك والصحيح ما تقدم من التفصيل قبل الاختلاط فيقبل وبعده فلا انتهى وقال يحيى بن معين كان يغلط ويخطىء فيما يروي عن',NULL,'295',NULL,NULL,'0');
INSERT INTO [b309] VALUES (250,'شيوخه الصغار كعاصم وسلمة والأعمش بخلاف ما يروي عن الكبار وعن يحيى أيضا أحاديثه عن الأعمش مقلوبة وأحاديثه عن القاسم وعن عون صحيحة وقال علي بن المديني كان ثقة إلا أنه كان يغلط فيما روى عن عاصم بن بهدلة وسلمة وماروى عن القاسم ومعن',NULL,'296',NULL,NULL,'0');
INSERT INTO [b309] VALUES (251,'صحيح وقال محمد بن عبد الله بن نمير كان ثقة إلا أنه اختلط بأخرة سمع منه عبد الرحمن بن مهدي ويزيد بن هارون أحاديثه مختلطة وما روى عنه الشيوخ مستقيم وقال محمد بن سعد كان ثقة كثير الحديث إلا أنه اختلط في آخر عمره ورواية المتقدمين عنه صحيحة وقال النسائي ليس به بأس وقال مسعر ما أعلم أحد أعلم بعلم بن مسعود منه',NULL,'297',NULL,NULL,'0');
INSERT INTO [b309] VALUES (252,'روى له البخاري وأبو داود والنسائي وابن ماجة وتوفي سنة ستين ومائة',NULL,'298',NULL,NULL,'0');
INSERT INTO [b309] VALUES (253,'٣٦ -<span data-type="title" id=toc-48> عبد الله بن جعفر بن غيلان بالغين المفتوحة الحافظ الرقي أبو عبد الرحمن القرشي</span>
معدود في آل عقبة بن أبي معيط عن الحسن بن عمر الرقي عبيد الله بن عمرو',NULL,'299',NULL,NULL,'0');
INSERT INTO [b309] VALUES (254,'الرقي ومعتمر بن سليمان وأبي المليح وابن المبارك وغيرهم',NULL,'300',NULL,NULL,'0');
INSERT INTO [b309] VALUES (255,'وعنه إسماعيل بن عبد الله الرقي وعبد الله بن عبد الرحمن الدارمي والفضل بن يعقوب الرخامي',NULL,'301',NULL,NULL,'0');
INSERT INTO [b309] VALUES (256,'ومحمد بن يحيى الذهلي وناس غيرهم أثبته بن حابن في الثقات وأطلق يحيى بن معين وأبو حاتم القول بتوثيقه',NULL,'302',NULL,NULL,'0');
INSERT INTO [b309] VALUES (257,'وقال النسائي ليس به بأس قبل أن يتغير ذهب بصره فيما قيل سنة ست عشرة ومئتين وتغير سنة ثمان عشرة ومئتين كذا قال المزي في تهذيبه وذكره صاحب الاغتباط في جملة من رمي بالاختلاط وذكر قول النسائي والمزي وزاد وقال بن حبان اختلط سنة ثمان عشرة ولم يكن اختلاطه اختلاطا فاحشا وقال هلال بن العلاء عمي سنة ست عشرة ومئتين وتغير سنة ثمان عشرة ومات سنة عشرين انتهى روى له البخاري',NULL,'303',NULL,NULL,'0');
INSERT INTO [b309] VALUES (258,'ومسلم وأبو داود والترمذي والنسائي وابن ماجة',NULL,'304',NULL,NULL,'0');
INSERT INTO [b309] VALUES (259,'٣٧ -<span data-type="title" id=toc-49> عبد الملك بن محمد بن عبد الله بن محمد بن عبد</span>',NULL,'304',NULL,NULL,'0');
INSERT INTO [b309] VALUES (260,'الملك بن مسلم الرقاشي الحافظ الضرير أبو قلابة وكان فيما قيل يكنى أبا محمد إلا أنه غلب عليه أبو قلابة عن أشهل بن حاتم وعبد العزيز بن الخطاب',NULL,'305',NULL,NULL,'0');
INSERT INTO [b309] VALUES (261,'ومعمر بن محمد بن عبد الله بن أبي رافع ويزيد بن هارون وروح',NULL,'306',NULL,NULL,'0');
INSERT INTO [b309] VALUES (262,'وأبو داود وغيرهم وعنه بن ماجة',NULL,'307',NULL,NULL,'0');
INSERT INTO [b309] VALUES (263,'ومحمد بن جرير وأبو بكر محمد بن إسحاق بن خزيمة وغيرهم قال أبو داود صدوق مأمون وقال الدارقطني صدوق وقال الخطيب أبو بكر كان مذكورا بالصلاح والخير ذكر أن أمه لما حملت به قالت أريت كأني ولدت هدهدا فقيل لي إن صدقت رؤياك ولدت ولدا يكثر الصلاة فيقال',NULL,'308',NULL,NULL,'0');
INSERT INTO [b309] VALUES (264,'إنه كان يصلي كل يوم أربع مائة ركعة ويقال أنه حدث من حفظه ستين ألف حديث أثبته بن حبان في الثقات كذا قال المزي قال بن الصلاح روينا عن الإمام بن خزيمة أنه قال حدثنا أبو قلابة بالبصرة قبل أن يختلط ويخرج إلى بغداد انتهى قال الأبناسي هو أحد شيوخ بن خزيمة فمن سمع منه بالبصرة قبل أن يخرج إلى بغداد فهو بعد الاختلاط أو مشكوك فيه فممن سمع منه بالبصرة أبو داود السجستاني وابن ماجة وأبو مسلم وأبو بكر بن أبي داود',NULL,'309',NULL,NULL,'0');
INSERT INTO [b309] VALUES (265,'ومحمد بن إسحاق الصغاني وأحمد بن يحيى البلاذري وأبو عروبة الحسين بن محمد',NULL,'310',NULL,NULL,'0');
INSERT INTO [b309] VALUES (266,'وممن سمع منه آخر ببغداد أبو عمرو وعثمان بن أحمد بن السماك وأحمد بن سليمان النجاد وأحمد بن كامل بن شجرة القاضي وأحمد بن عثمان بن يحيى الأدمي وأبو سهل أحمد بن محمد بن عبد الله بن زياد القطان',NULL,'311',NULL,NULL,'0');
INSERT INTO [b309] VALUES (267,'وإسماعيل بن محمد الصفار وعبد الله بن إسحاق بن إبراهيم بن الخرساني وأبو بكر محمد بن يعقوب بن شيبة السدوسي وأبو بكر محمد بن عبد الله بن إبراهيم الشافعي وأبو عيسى محمد بن علي بن الحسين بن التخاري بالتاء المثناة',NULL,'312',NULL,NULL,'0');
INSERT INTO [b309] VALUES (268,'من فوق المضمومة وأبو جعفر محمد بن عمرو بن البختري ومحمد بن مخلد الدوري وأبو العباس محمد بن يعقوب الأصم قال الحاكم إن الأصم لم يسمع بالبصرة حديثا واحدا وإن أباه رحل به سنة خمس وستين وتوفي أبو قلابة سنة ست وسبعين ومائتين ببغداد انتهى قال الخطيب أبو بكر أحمد بن علي بن ثابت البغدادي الحافظ حدث عنه محمد بن إسحاق الصاغاني وأبو بكر الشافعي وبين وفاتيهما أربع وثمانون سنة روى له بن ماجة',NULL,'313',NULL,NULL,'0');
INSERT INTO [b309] VALUES (269,'٣٨ -<span data-type="title" id=toc-50> عبد الوهاب بن عبد المجيد بن الصلت بن عبيد الله بن الحكم بن أبي العاص الثقفي أبو محمد</span>
معدود في البصريين الحافظ أحد الأشراف ولجده الحكم صحبة عن أيوب السختياني وحميد الطويل وخالد الحذاء ويونس وغيرهم',NULL,'314',NULL,NULL,'0');
INSERT INTO [b309] VALUES (270,'وعنه أحمد بن حنبل وإسحاق بن راهويه وقتيبة بن سعيد ومحمد بن بشار بندار وابن عرفة وغيرهم ثقة احتج به الشيخان وأطلق يحيى بن معين القول بتوثيقه إلا أنه قال اختلط بأخرة',NULL,'315',NULL,NULL,'0');
INSERT INTO [b309] VALUES (271,'وقال عقبة كان اختلط قبل موته بثلاث سنين أو أربع وقال علي بن المديني كتابه عن يحيى بن سعيد أصح كتاب وقال أحمد بن حنبل هو أحب إلي من عبد الوهاب الخفاف وقال عبد الرحمن بن مهدي عبد الوهاب الثقفي وجرير بن عبد الحميد ومعتمر بن سليمان وعبد الأعلى السامي أمرهم في الحديث واحد يحدثون من كتب الناس ولا يحفظون ذلك الحفظ',NULL,'316',NULL,NULL,'0');
INSERT INTO [b309] VALUES (272,'وقال محمد بن سعد كان ثقة وفيه ضعف قال الأبناسي قال صاحب الميزان لكنه ما ضر تغيره حديثه فإنه ما حدث بحديث في زمن التغيير ثم استدل بقول أبي داود تغير جرير بن حازم وعبد الوهاب الثقفي فحجب الناس عنهم انتهى وذكره العقيلي فقال تغير في آخر عمره وذكر أن غلته كانت ما بين أربيعن ألفا إلى خمسين ألفا في كل سنة وكان ينفق جميع ذلك على أصحاب الحديث وقال إبراهيم بن النظام هو أحلى من أمن بعد خوف',NULL,'317',NULL,NULL,'0');
INSERT INTO [b309] VALUES (273,'وبرء بعد سقم وخصب بعد جدب وغنى بعد فقر ومن طاعة المحبوب وفرج المكروب ومن الوصال الدائم مع الشباب الناعم روى له البخاري ومسلم وأبو داود والترمذي والنسائي',NULL,'318',NULL,NULL,'0');
INSERT INTO [b309] VALUES (274,'وابن ماجة وتوفي سنة أربع وتسعين ومئة وقيل سنة أربع وثمانين',NULL,'319',NULL,NULL,'0');
INSERT INTO [b309] VALUES (275,'٣٩ -<span data-type="title" id=toc-51> عطاء بن السائب بن مالك الثقفي أبو السائب</span>',NULL,'319',NULL,NULL,'0');
INSERT INTO [b309] VALUES (276,'وعلي رأى أبي زيد معدود في الكوفيين أحد الأعلام على لين فيه عن أبيه وابن أبي أوفى وأبي عبد الرحمن السلمي',NULL,'320',NULL,NULL,'0');
INSERT INTO [b309] VALUES (277,'وإبراهيم النخعي وأنس بن مالك وأدخل بعض الرواة بينهما يزيد الرقاشي وعن الحسن البصري وسعيد بن جبير وعامر الشعبي وأبي سلمة بن عبد الرحمن بن عوف',NULL,'321',NULL,NULL,'0');
INSERT INTO [b309] VALUES (278,'وعنه إسماعيل بن علية وجريرة بن عبد الحميد وشعبة بن الحجاج والحمادان والسفيانان وأبو جعفر الرازي وعلى بن عاصم وغيرهم حكموا بتوثيقه وصلاحه وباختلاطه اختلط في آخر عمره قال أحمد بن حنبل ثقة رجل صالح من سمع منه قديما فسماعه صحيح ومن سمع منه حديثا فسماعه ليس بشيء وشعبة وسفيان ممن سمع منه قديما وجرير وخالد بن عبد الله وإسماعيل بن علية ممن سمع منه حديثا كان يرفع عن سعيد بن جبير أشياء لم يكن يرفعها وقال وهيب لما قدم البصرة قال كتبت عن عبيدة ثلاثين حديثا قال أحمد بن حنبل ولم يسمع من عبيدة شيئا',NULL,'322',NULL,NULL,'0');
INSERT INTO [b309] VALUES (279,'انتهى وقال يحيى بن معين لم يسمع عطاء من يعلى بن مرة واختلط وما سمع منه جرير ليس من صحيح حديثه وسمع منه أبو عوانة في الصحة والاختلاط فلا يحتج بحديثه وروى عن يحيى أيضا الحكم بضعفه وبأن كل من روى عنه إنما روى في الاختلاط إلا شعبة وسفيان انتهى وقال الحافظ بن الصلاح عطاء بن السائب اختلط في آخر عمره فاحتج أهل العلم برواية الأكابر عنه مثل سفيان الثوري وشعبة لأن سماعهم منه كان في الصحة وتركوا الاحتجاج برواية من سمع منه آخر انتهى قلت فيفهم من قول بن الصلاح مثل أن غيرهما روى عنه قبل أن يختلط وقال أبو حاتم الرازي قديم السماع من عطاء',NULL,'323',NULL,NULL,'0');
INSERT INTO [b309] VALUES (280,'سفيان وشعبة وقد استثنى غير واحد من الأئمة معهما حماد بن زيد قال يحيى بن سعيد القطان سمع حماد بن زيد من عطاء قبل اختلاطه وقال النسائي رواية حماد بن زيد وشعبة وسفيان عنه جيدة وصحح أيضا حديثه عنه أبو داود والطحاوي كما سيأتي وقال أبو حاتم سمع منه حماد بن زيد قبل أن يتغير ونقل أبو عبد الله بن المواق الاتفاق على أنه سمع منه',NULL,'324',NULL,NULL,'0');
INSERT INTO [b309] VALUES (281,'قديما وقد استثنى الجمهور رواية حماد بن سلمة عنه أيضا قاله بن معين وأبو داود والطحاوي وحمزة الكناني وذكر ذلك عن بن معين بن عدي في الكامل وعباس الدوري وأبي بكر بن أبي خيثمة وقال الطحاوي وإنما حديث عطاء الذي كان منه قبل تغيره يؤخذ من أربعة لا من سواهم وهم شعبة وسفيان الثوري وحماد بن سلمة وحماد بن زيد',NULL,'325',NULL,NULL,'0');
INSERT INTO [b309] VALUES (282,'وقال حمزة بن محمد الكناني في أماليه حماد بن سلمة قديم السماع من عطاء وقال عبد الحق في الإحكام أن حماد بن سلمة سمع منه بعد الاختلاط كما قاله العقيلي قال الأبناسي وقد تعقب الحافظ أبو عبد الله محمد بن أبي بكر بن المواق كلام عبد الحق يعني الذي ذكرناه وقال لا نعلم من قاله غير العقيلي وقد غلط من قال أنه قدم في آخر عمره إلى البصرة وإنما قدم عليهم مرتين فمن سمع منه في القدمة الأولى صح حديثه منه واستثنى أبو داود أيضا هشاما الدستوائي فقال وقال',NULL,'326',NULL,NULL,'0');
INSERT INTO [b309] VALUES (283,'غير أحمد قدم عطاء البصرة قدمتين سمع في القدمة الأولى منه الحمادان وهشام والقدمة والثانية كان تغير فيها سمع منه وهيب وإسماعيل بن عليه وعبد الوارث فسماعهم منه ضعيف وينبغي أن يستثنى أيضا سفيان بن عيينة فقد روى الحميدي عنه قال كنت سمعت من عطاء بن السائب قديما ثم قدم علينا قدمة فسمعته يحدث ببعض ما كنت سمعت فخلط فيه فاتقيته واعتزلته فينبغي أن يكون روايته عنه صحيحة وقال العقيلي إنما يقبل من حديث عطاء ما روى عنه مثل شعبة وسفيان فأما جرير وخالد بن عبد الله وابن علية وعلى بن عاصم وحماد بن سلمة وأهل البصرة',NULL,'327',NULL,NULL,'0');
INSERT INTO [b309] VALUES (284,'فأحاديثهم عنه مما سمع منه بعد الاختلاط لأنه إنما قدم عليهم في آخر عمره فهؤلاء وأمثالهم ممن روى عنه بعد الاختلاط فلا يقبل حديثهم وكذلك من روى عنه قبله أو بعده كأبي عوانة كما رواه عباس الدوري عن يحيى بن معين وممن سمع منه بأخرة هشيم وليس له عند البخاري غير حديث واحد عن عمرو الناقد عن هشيم عن أبي بشر وعطاء بن السائب عن سعيد بن جبير عن بن عباس قال الكوثر الخير الكثير أعطاه الله إياه إلا أنه قرنه بأبي بشر',NULL,'328',NULL,NULL,'0');
INSERT INTO [b309] VALUES (285,'قال وممن سمع منه أيضا بأخرة من البصرين جعفر بن سليمان الضبعي وروح بن القاسم وعبد العزيز بن عبد الصمد العمي وعبد الوارث بن سعيد انتهى وقال أبو حاتم الرازي وفي حديث البصريين الذين يحدثون عنه تخاليط كثيرة انتهى وقال حماد بن زيد قال لنا أيوب إن عطاء قدم من الكوفة فاذهبوا فاسمعوا منه حديث أبيه في التسبيح فإنه ثقة وقال يحيى القطان لم أسمع أحدا يقول في حديثه القديم شيئا',NULL,'329',NULL,NULL,'0');
INSERT INTO [b309] VALUES (286,'قط وحديث سفيان وشعبة عنه صحيح يعني القديم إلا حديثين من حديث شعبة سمعهما بأخرة عن زاذان انتهى قلت والعجب منه أنه لم يذكرهما وقال العجلي اختلط عطاء بأخرة فمن سمع منه حال اختلاطه فهو مضطرب الحديث وهشيم وخالد بن عبد الله الواسطي ممن سمع منه بأخرة',NULL,'330',NULL,NULL,'0');
INSERT INTO [b309] VALUES (287,'وقال أبو حاتم صالح مستقيم الحديث قبل الاختلاط وحديث البصريين عنه بلغني فيه تخاليط لأنهم سمعوا منه حال الاختلاط وما روى منه بن فضيل بلغني فيه غلط واضطراب رفع أشياء عن الصحابة كان يرويها عن التابعين وقال النسائي ثقة إلا أنه تغير ورواية حماد بن زيد وشعبة وسفيان عنه جيدة وقال إسماعيل بن علية قال لي شعبة ما حدثك عطاء عن رجاله زاذان وميسرة',NULL,'331',NULL,NULL,'0');
INSERT INTO [b309] VALUES (288,'وأبي البختري فلا تكتبه وما حدثك عن رجل بعينه فاكتبه وقال أبو بكر بن عياش كنت إذا رأيت عطاء وضرار بن مرة رأيت أثر البكاء على خدودهما وقال الإمام أحمد بن حنبل كان عطاء يختم القرآن كل ليلة',NULL,'332',NULL,NULL,'0');
INSERT INTO [b309] VALUES (289,'روى له البخاري وأبو داود والترمذي والنسائي وابن ماجة توفي سنة ست وثلاثين ومئة',NULL,'333',NULL,NULL,'0');
INSERT INTO [b309] VALUES (290,'.....................................................................
ــ',NULL,'334',NULL,NULL,'0');
INSERT INTO [b309] VALUES (291,'٤٠ -<span data-type="title" id=toc-52> العلاء بن عبد الوارث الحضرمي أبو وهب ويقال أبو محمد</span>
معدود في أهل دمشق صاحب مكحول عن حرام بن حكيم الدمشقي',NULL,'335',NULL,NULL,'0');
INSERT INTO [b309] VALUES (292,'وأبي الأشعث الصنعاني وزيد بن أرطاة',NULL,'336',NULL,NULL,'0');
INSERT INTO [b309] VALUES (293,'وعمرو بن شعيب وغيرهم وعنه الأوزاعي',NULL,'337',NULL,NULL,'0');
INSERT INTO [b309] VALUES (294,'والهيثم وعيسى بن موسى القرشي ومعاوية بن صالح الحضرمي وغيرهم',NULL,'338',NULL,NULL,'0');
INSERT INTO [b309] VALUES (295,'أطلق يحيى بن معين وعلى بن المديني ويعقوب بن سفيان ودحيم وأبو داود القول بتوثيقه لكنه خلط قال أبو داود كان يرى القدر وتغير عقله وقال محمد بن سعد كان أعلم أصحاب مكحول وكان يفتي حتى خولط وقال أبو حاتم لا أعلم أحدا من أصحاب مكحول أوثق منه وعنه صدوق من خيار أصحاب مكحول وكان يرى القدر',NULL,'339',NULL,NULL,'0');
INSERT INTO [b309] VALUES (296,'وأدخل بعضهم في ترجمته العلاء بن الحصين وزعم أن النسائي روى له وهو وهم وقال البخاري منكر الحديث وذكره بن حبان في الثقات وقال يعتبر حديثه من رواية الثقات عنه روى له مسلم وأبو داود والترمذي',NULL,'340',NULL,NULL,'0');
INSERT INTO [b309] VALUES (297,'والنسائي وابن ماجة وتوفي سنة ست وثلاثين ومئة',NULL,'341',NULL,NULL,'0');
INSERT INTO [b309] VALUES (298,'٤١ -<span data-type="title" id=toc-53> عمرو بن عبد الله بن عبيد ويقال عمرو بن عبد الله بن علي الهمداني أبو إسحاق السبيعي</span>
بفتح السين المهملة وكسر الباء الموحدة نسبة إلى السبيع بن سبع وهو بن صعب بن معاوية معدود في الكوفيين أحد الأعلام من أئمة التابعين عن جرير',NULL,'341',NULL,NULL,'0');
INSERT INTO [b309] VALUES (299,'وعدي بن حاتم وزيد بن أرقم',NULL,'342',NULL,NULL,'0');
INSERT INTO [b309] VALUES (300,'والأسود بن يزيد النخعي والبراء بن عازب وحارثة بن وهب الخزاعي',NULL,'343',NULL,NULL,'0');
INSERT INTO [b309] VALUES (301,'والأغر أبي مسلم وابن عباس وأصم وعنه ابنه يونس وشعبة والسفيانان',NULL,'344',NULL,NULL,'0');
INSERT INTO [b309] VALUES (302,'والأجلح بن عبد الله الكوفي ومطرف بن طريف',NULL,'345',NULL,NULL,'0');
INSERT INTO [b309] VALUES (303,'وحفيده إسرائيل وأبو بكر بن عياش وغيرهم أطلق يحيى بن معين والنسائي والعجلي وأبو حاتم القول بتوثيقه واحتج به الشيخان قال علي بن المديني لم يرو عن هبيرة بن يريم',NULL,'346',NULL,NULL,'0');
INSERT INTO [b309] VALUES (304,'وهاني بن هاني إلا أبو إسحاق وقد روى عن سبعين أو ثمانين لم يرو عنهم غيره ومشايخه تبلغ نحوا من ثلاثمائة شيخ وعنه أربعمائة شيخ قلت فمن مشايخه سبيع بن خالد بضم السين المهملة وفتح البار الموحدة وسكون الياء المثناة من تحت السلولي بفتح السين المهملة وإنما ذكرته لئلا يقع الاشتباه على من لا يعرف هذا الفن قال العجلي سمع ثمانية وثلاثين من أصحاب النبي صلى الله عليه وسلم والشعبي أكبر منه بسنتين ولم يسمع من',NULL,'347',NULL,NULL,'0');
INSERT INTO [b309] VALUES (305,'علقمة شيئا وسمع من الحارث الأعور أربعة أحاديث فقط وسائر ما يلقى له عنه إنما هو كتاب وقال أبو حاتم هو شبيه بالزهري في كثرة الرواية والاتساع في الرجال وهو أحفظ من أبي إسحاق الشيباني وقال له رجل زعم شعبة أنك رأيت علقمة ولم ترو عنه فقال صدق وقال عيسى بن يونس بن أبي إسحاق قال لي شعبة لم يسمع حدك من الحارث الأعور إلا أربعة أحاديث قال',NULL,'348',NULL,NULL,'0');
INSERT INTO [b309] VALUES (306,'فقلت له من أين علمته قال هو قال لي وقال أحمد بن حنبل ثقة إلا أن الذين حملة عنه إنما كان حملهم عنه بأخرة قال بن الصلاح اختلط أبو إسحاق اختلط أبو إسحاق ويقال إن سماع سفيان بن عيينة منه بعدما اختلط وتغير حفظه قبل موته قال الأبناسي قال بعض أهل العلم كان قد اختلط وإنما تركوه مع بن عيينة لاختلاطه ولم يخرج له الشيخان من رواية بن عبدة شيئا إنما أخرج له من طريقه الترمذي وكذلك النسائي في عمل اليوم والليلة وأنكر صاحب الميزان اختلاطه فقال شاخ ونسي ولم',NULL,'349',NULL,NULL,'0');
INSERT INTO [b309] VALUES (307,'يختلط وقد سمع منه سفيان بن عيينة وقد تغير قليلا واقتصر بن الصلاح على من روى عنه بعد الاختلاط على بن عيينة وقد ذكر ذلك عن إسرائيل بن يونس وزكريا بن أبي زائدة وزهير بن معاوية وفي رواته زائدة بن قدامة وقال أبو زرعة زهير بن معاوية ثقة إلا أنه سمع من أبي إسحاق بعد الاختلاط وروى عن أحمد أنه قال إذا سمعت الحديث عن زائدة وزهير فلا تبال أن لا تسمعه من غيرهما إلا حديث أبي إسحاق وروايته عنه في سنن أبي داود فقط',NULL,'350',NULL,NULL,'0');
INSERT INTO [b309] VALUES (308,'وقد أخرج الشيخان في الصحيحين لجماعة من روايتهم عن أبي إسحاق وهم إسرائيل بن يونس بن أبي إسحاق وزكريا بن أبي زائدة وزهير بن معاوية وسفيان الثوري',NULL,'351',NULL,NULL,'0');
INSERT INTO [b309] VALUES (309,'وأبو الأحوص سلام بن سليم وشعبة وعمر',NULL,'352',NULL,NULL,'0');
INSERT INTO [b309] VALUES (310,'بن أبي زائدة ويوسف بن إسحاق بن أبي إسحاق أخرج البخاري من رواية جرير بن حازم عنه',NULL,'353',NULL,NULL,'0');
INSERT INTO [b309] VALUES (311,'وأخرج مسلم من رواية إسماعيل بن أبي خالد ورقبة بن مصقلة العبدي وسليمان بن مهران الأعمش وسليمان بن معاذ وعمار بن رزيق بتقديم الراء المهملة يعني الضبي',NULL,'354',NULL,NULL,'0');
INSERT INTO [b309] VALUES (312,'ومالك بن مغول بكسر الميم ومسعر بن كدام بكسر الميم في مسعر وكسر الكاف في كدام عنه انتهى وقال الحافظ الذهبي في الكاشف تي ترجمة أبي إسحاق وهو كالزهري في الكثرة غزا مرات وكان صواما قواما رحمه الله روى له البخاري ومسلم وأبو داود',NULL,'355',NULL,NULL,'0');
INSERT INTO [b309] VALUES (313,'والترمذي والنسائي وابن ماجة وعاش خمسا وتسعين سنة وتوفي سنة ست وعشرين ومئة وقيل سبع وقيل ثمان وقيل تسع والله أعلم',NULL,'356',NULL,NULL,'0');
INSERT INTO [b309] VALUES (314,'٤٢ -<span data-type="title" id=toc-54> عمرو بن عيسى بن سويد بن هبيرة أبو نعامة العدوي</span>
معدود في البصريين عن خالد بن عمير',NULL,'357',NULL,NULL,'0');
INSERT INTO [b309] VALUES (315,'وأبي السوار وبنت سيرين وغيرهم',NULL,'358',NULL,NULL,'0');
INSERT INTO [b309] VALUES (316,'وعنه زهير بن هنيد العدوي والنضر بن شميل ووكيع بن الجراح وغيرهم أطلق يحيى بن معين والنسائي القول بتوثيقه وقال أبو حاتم لا بأس به',NULL,'359',NULL,NULL,'0');
INSERT INTO [b309] VALUES (317,'وأثبته بن حبان في الثقات وقال الإمام أحمد ثقة إلا أنه اختلط قبل موته رواه الأثرم عن الإمام أحمد وقال الذهبي ثقة قيل تغير بأخرة روى له مسلم وابن ماجة',NULL,'360',NULL,NULL,'0');
INSERT INTO [b309] VALUES (318,'٤٣ -<span data-type="title" id=toc-55> عبد الله بن محمد بن محمد بن سليمان النشاوري المكي</span>',NULL,'360',NULL,NULL,'0');
INSERT INTO [b309] VALUES (319,'ذكره برهان الدين الحلبي في الاغتباط له وقال اختلط قبل وفاته بنحو سنتين اختلاطا خفيفا وتوفي سنة تسعين وسبعمائة ودفن بالمعلى من مكة شرفها الله تعالى ورحمه',NULL,'361',NULL,NULL,'0');
INSERT INTO [b309] VALUES (320,'٤٤ -<span data-type="title" id=toc-56> عبد الله بن واقد أبو قتادة الحراني</span>',NULL,'361',NULL,NULL,'0');
INSERT INTO [b309] VALUES (321,'قال الإمام المحدث الشريف الحسيني في رجال مسند الإمام أحمد كلاما آخره ولعله كبر كاختلط انتهى وفي الجرح والتعديل لابن أبي حاتم عن أحمد لعله اختلط وفي كلام آخر لأحمد ولعله كبر فاختلط وذكره صاحب الاغتباط',NULL,'362',NULL,NULL,'0');
INSERT INTO [b309] VALUES (322,'٤٥ -<span data-type="title" id=toc-57> عبد الباقي بن قانع أبو الحسين الحافظ مصنف المعجم في الصحابة</span>
وقال أبو الحسن بن الفرات حدث به الاختلاط قبل موته بسنتين وقال الخطيب في جملة كلامه وقد تغير في آخر عمره',NULL,'363',NULL,NULL,'0');
INSERT INTO [b309] VALUES (323,'٤٦ -<span data-type="title" id=toc-58> عبد السلام بن سهل أبو علي السكري بغدادي</span>
حدث بمصر عن الحماني والقواريري',NULL,'364',NULL,NULL,'0');
INSERT INTO [b309] VALUES (324,'وعنه بن شنبوذ والطبراني',NULL,'365',NULL,NULL,'0');
INSERT INTO [b309] VALUES (325,'قال بن يونس من نبلاء الناس وأهل الصدق تغير في آخر أيامه وذكره صاحب الاغتباط',NULL,'366',NULL,NULL,'0');
INSERT INTO [b309] VALUES (326,'٤٧ -<span data-type="title" id=toc-59> عبيدة بن معتب الضبي</span>',NULL,'366',NULL,NULL,'0');
INSERT INTO [b309] VALUES (327,'قال شعبة أخبرني عبيدة قبل أن يتغير ذكره صاحب الاغتباط وقال والظاهر أنه أراد بتغيره الاختلاط وقد يريد أنه ساء حفظه والله أعلم',NULL,'367',NULL,NULL,'0');
INSERT INTO [b309] VALUES (328,'٤٨ -<span data-type="title" id=toc-60> على بن الحسين أبو الفرج الأصبهاني صاحب الأغاني</span>',NULL,'367',NULL,NULL,'0');
INSERT INTO [b309] VALUES (329,'ذكر صاحب الميزان عن أبي الفتح بن أبي الفوارس أنه خلط قبل موته ذكره صاحب الاغتباط',NULL,'368',NULL,NULL,'0');
INSERT INTO [b309] VALUES (330,'<span data-type="title" id=toc-61>باب الفاء</span>
٤٩ -<span data-type="title" id=toc-62> فطر بن حماد بن واقد بصري</span>
قال أبو داود تغير تغيرا شديدا وذكره صاحب الاغتباط',NULL,'369',NULL,NULL,'0');
INSERT INTO [b309] VALUES (331,'<span data-type="title" id=toc-63>باب القاف</span>
٥٠ -<span data-type="title" id=toc-64> قريش بن أنس الأنصاري وقيل الأموي مولاهم أبو أنس</span>
معدود في البصريين عن حبيب بن الشهيد وعبد الله بن عون',NULL,'370',NULL,NULL,'0');
INSERT INTO [b309] VALUES (332,'وغيرهما وعنه أحمد بن عثمان النوفلي وعلي بن المديني وأبو موسى محمد بن المثنى وغيرهم',NULL,'371',NULL,NULL,'0');
INSERT INTO [b309] VALUES (333,'أطلق على بن المديني والنسائي القول بتوثيقه وقال أبو حاتم لا بأس به إلا أنه تغير وقال أبو داود تغير وقال إسحاق بن إبراهيم بن حبيب مات سنة تسع ومائتين وكان قد اختلط ست سنين وقال الذهبي ثقة تغير قبيل موته وذكره صاحب الاغتباط وقال قال النسائي تغير قبل موته بست سنين',NULL,'372',NULL,NULL,'0');
INSERT INTO [b309] VALUES (334,'وقال البخاري في الضعفاء اختلط ست سنين في البيت وقال بن حبان كان شيخا صدوقا إلا أنه اختلط في آخر عمره حتى كان لا يدري ما يحدث به بقي ست سنين في اختلاطه إلى آخر كلامه انتهى روى له البخاري ومسلم وأبو داود والترمذي والنسائي. انتهت ترجمته',NULL,'373',NULL,NULL,'0');
INSERT INTO [b309] VALUES (335,'٥١ -<span data-type="title" id=toc-65> قيس بن أبي حازم واسمه حصين بن عوف ويقال عبد بن عوف بن عبد الحارث بن عوف البجلي الأحمسي</span>
معدود في الكوفيين وفيمن أدرك الجاهلية فأتته الصحبة بليال هاجر إلى النبي صلى الله عليه وسلم ليبايعه فقبض النبي صلى الله عليه وسلم وهو في الطريق وقول من قال أنه رآه يخطب لا يصح',NULL,'374',NULL,NULL,'0');
INSERT INTO [b309] VALUES (336,'وأبوه أبو حازم له صحبة عن جرير بن عبد الله البجلي وحذيفة بن اليمان وخالد بن الوليد وغيرهم وعنه إسماعيل بن أبي خالد',NULL,'375',NULL,NULL,'0');
INSERT INTO [b309] VALUES (337,'وأبو بشر بيان بن بشر الأحمسي والمغيرة بن شبيل وغيرهم قال علي بن المديني روى عن بلال ولم يلقه وعن',NULL,'376',NULL,NULL,'0');
INSERT INTO [b309] VALUES (338,'عقبة بن عامر ولا أدري هل سمع منه أولا ولم يسمع من أبي الدرداء ولا من سلمان وقال سفيان بن عيينة لم يكن بالكوفة أحد أروى عن',NULL,'377',NULL,NULL,'0');
INSERT INTO [b309] VALUES (339,'أصحاب رسول الله صلى الله عليه وسلم منه وقال أبو داود أجود التابعين إسنادا قيس بن أبي حازم روى عن تسعة من العشرة ولم يرو عن عبد الرحمن بن عوف وقال يعقوب بن شيبة السدوسي ليس أحد من التابعين حصلت له الرواية عن العشرة غير عبد الرحمن بن عوف غيره ولم يرو عن عبد الرحمن بن عوف من العشرة شيئا كما ذكرنا وقد روى بعد العشرة عن جماعة من أصحاب النبي صلى الله عليه وسلم وكبرائهم وهو متقن الرواية قال وقد روى عنه جماعة من الثقات كإسماعيل بن أبي خالد وبيان بن بشر وغيرهما',NULL,'378',NULL,NULL,'0');
INSERT INTO [b309] VALUES (340,'وقد كاد أن يكون صحابيا وقد أذى نفسه من تكلم فيه وقال عبد الرحمن بن يوسف بن خراش تحصلت له الرواية عن العشرة وأطلق يحيى بن معين القول بتوثيقه وقال هو أوثق من الزهري ومن السائب بن يزيد وكان إسماعيل بن أبي خالد يقول حدثنا قيس بن أبي حازم',NULL,'379',NULL,NULL,'0');
INSERT INTO [b309] VALUES (341,'هذه الإسطوانة على جملة المبالغة في تثبته وقال الذهبي تابعي كبير وثقوه وأجمعوا على الاحتجاج به وذكره صاحب الاغتباط وقال حجة كاد أن يكون صحابيا وثقه بن معين والناس إلى أن قال قال الذهبي أجمعوا على الاحتجاج به ومن تكلم فيه فقد أذى نفسه نسأل الله العافية وترك الهوى قال إسماعيل بن أبي خالد كان ثبتا قال وقد كبر حتى جاوز المائة وخرف وقال صاحب التهذيب قال إسماعيل بن أبي خالد جاوز المائة بسنين كثيرة حتى خرف وذهب عقله روى له البخاري',NULL,'380',NULL,NULL,'0');
INSERT INTO [b309] VALUES (342,'ومسلم وأبو داود والترمذي والنسائي وابن ماجة توفي سنة أربع وثمانين وقيل سنة سبع وتسعين أو ثمان وتسعين والله أعلم',NULL,'381',NULL,NULL,'0');
INSERT INTO [b309] VALUES (343,'<span data-type="title" id=toc-66>باب الميم</span>
٥٢ -<span data-type="title" id=toc-67> محمد بن الفضل أبو النعمان السدوسي الحافظ عارم</span>
بالعين المهملة وهو لقبه معدود في البصريين عن جرير بن حازم وحماد بن زيد وعبد الله بن',NULL,'382',NULL,NULL,'0');
INSERT INTO [b309] VALUES (344,'المبارك والوضاح بن عبد الله ووهيب بن خالد وغيرهم وعنه البخاري وأحمد بن نصر النيسابوري وحجاج بن الشاعر',NULL,'383',NULL,NULL,'0');
INSERT INTO [b309] VALUES (345,'وعبد بن حميد ومحمد بن عبد الملك الدقيقي',NULL,'384',NULL,NULL,'0');
INSERT INTO [b309] VALUES (346,'ومحمد بن يحيى الذهلي وهارون بن عبد الله الحمال وغيرهم أحد الثقات الأثبات قال البخاري تغير في آخر عمره وقال أبو حاتم لا يتأخر عن عفان فإذا حدثك بشيء فاختم عليه وكان سليمان بن حرب يرجع إلى قوله إذا خالفه في شيء',NULL,'385',NULL,NULL,'0');
INSERT INTO [b309] VALUES (347,'ويقدمه على نفسه ويقول هو أثبت أصحاب حماد بن زيد بعد عبد الرحمن بن مهدي وقال أبو حاتم أيضا هو أحب إلى من أبي سلمة وقال أيضا اختلط في آخر عمره وزال عقله فمن سمع منه قبل الاختلاط فسماعه صحيح وكتبت عنه قبل الاختلاط سنة أربع عشرة ولم أسمع منه بعد الاختلاط وبالجملة من سمع منه قبل سنة عشرين ومائتين فسماعه جيد وأبو زرعة إنما لقيه سنة اثنتين وعشرين وعنه إطلاق القول بتوثيقه وقال أبو داود كنت عنده فحدث عن حماد بن زيد عن هشام بن عروة عن أبيه أن ماعزا الأسلمي سأل النبي',NULL,'386',NULL,NULL,'0');
INSERT INTO [b309] VALUES (348,'صلى الله عليه وسلم عن الصوم في السفر فقلت له حمزة الأسلمي فقال يا بني ماعز لا يشقي به جليسه وكان هذا منه وقت اختلاطه وذهاب عقله وقال أبو داود أيضا بلغنا أنه أنكر سنة ثلاث عشرة ثم راجعه عقله واستحكم به الاختلاط سنة ست عشرة وقال محمد بن مسلم',NULL,'387',NULL,NULL,'0');
INSERT INTO [b309] VALUES (349,'صدوق مأمون وقال محمد بن يحيى الذهلي كان بعيدا من العرامة قال بن الصلاح اختلط بأخرة فما رواه عنه البخاري ومحمد بن يحيى الذهلي وغيرهما من الحفاظ ينبغي أن يكون مأخوذا عنه قبل اختلاطه انتهى قال الأبناسي العلامة عارم بن الفضل روى عنه البخاري في صحيحه ومسلم بواسطة قال البخاري تغير في آخر عمره وقال أبو حاتم اختلط في أخر عمره وزال عقله وقال بن حبان اختلط في آخر عمره وتغير حتى كان لا يدري ما يحدث به فوقع في حديثه المناكير الكثيرة فيجب التنكير عن حديثه فيما رواه المتأخرون فإذا لم يعلم هذا من هذا ترك',NULL,'388',NULL,NULL,'0');
INSERT INTO [b309] VALUES (350,'الكل وأنكر صاحب الميزان هذا القول من بن حبان وحكى قول الدارقطني تغيره بأخرة وما ظهر له بعد اختلاطه حديث منكر وهو ثقة ومات عارم سنة أربع وعشرين ومائتين فيكون اختلاطه ثمان سنين على قول أبي داود وأربع سنين على قول أبي حاتم وممن سمع منه قبل الاختلاط أحمد وعبد الله بن محمد المسندي وأبو حاتم الرازي وأبو علي محمد بن أحمد بن خالد الذربقي وكذلك ينبغي أن يكون من حدث عنه من شيوخ البخاري أو',NULL,'389',NULL,NULL,'0');
INSERT INTO [b309] VALUES (351,'مسلم وروى عنه في الصحيح شيئا من حديثه ومع كون البخاري روى عنه في الصحيح أيضا عن عبد الله بن محمد المسندي عنه وروى مسلم في الصحيح عن جماعة عنه وهم أحمد بن سعيد الدارمي وحجاج بن الشاعر',NULL,'390',NULL,NULL,'0');
INSERT INTO [b309] VALUES (352,'وأبو داود سليمان بن معبد السنجي وعبد بن حميد وهارون بن عبد الله الحمال وممن سمع منه بعد الاختلاط أبو زرعة الرازي كما قال أبو حاتم وعلي بن عبد العزيز البغوي على قول أبي داود أنه',NULL,'391',NULL,NULL,'0');
INSERT INTO [b309] VALUES (353,'استحكم به الاختلاط سنة ست عشرة لأن سماع على كان في سنة سبع عشرة كما قاله العقيلي وعلى قول أبي حاتم يكون سماعه منه قبل اختلاطه وجاء إليه أبو داود فلم يسمع منه لما رأى من اختلاطه وكذلك إبراهيم الحربي انتهى روى له البخاري ومسلم وأبو داود والترمذي والنسائي',NULL,'392',NULL,NULL,'0');
INSERT INTO [b309] VALUES (354,'وابن ماجة توفي سنة أربع وعشرين ومائتين',NULL,'393',NULL,NULL,'0');
INSERT INTO [b309] VALUES (355,'٥٣ -<span data-type="title" id=toc-68> محمد بن عبد الله بن المثنى بن عبد الله بن أنس بن</span>',NULL,'394',NULL,NULL,'0');
INSERT INTO [b309] VALUES (356,'مالك الأنصاري معدود في البصريين وكان قضى بالبصرة بعد معاذ بن معاذ العنبري وببغداد بعد العوفي عن أشعث بن عبد الملك الحمراني وحميد الطويل',NULL,'395',NULL,NULL,'0');
INSERT INTO [b309] VALUES (357,'وسعيد بن أبي عروبة وعبد الله بن عون وعبد الملك بن جريج وغيرهم وعنه أحمد بن إسحاق البخاري وأحمد بن حنبل وخليفة بن خياط وعلى بن المديني',NULL,'396',NULL,NULL,'0');
INSERT INTO [b309] VALUES (358,'وقتيبة بن سعيد وأبو حاتم الرازي وغيرهم أطلق يحيى بن معين القول بتوثيقه وقال أبو حاتم صدوق وعنه لم أر من الأئمة إلا ثلاثة أحمد بن حنبل وسليمان بن داود الهاشمي ومحمد بن عبد الله الأنصاري وقال النسائي ليس به بأس',NULL,'397',NULL,NULL,'0');
INSERT INTO [b309] VALUES (359,'وأثبته بن حبان في الثقات وقال زكريا بن يحيى الساجي جليل عالم لم يكن عندهم من فرسان الحديث مثل يحيى القطان ونظرائه غلبه عليه الرأي وقال يحيى بن معين كان يليق به القضاء وقيل له فالحديث فقال
للحرب أقوام لها خلقوا وللدواب حساب وكتاب قال أبو داود تغير تغيرا شديدا وقال أحمد بن حنبل وأبو خيثمة أنكر معاذ بن معاذ ويحيى بن سعيد حديث الأنصاري عن حبيب بن الشهيد',NULL,'398',NULL,NULL,'0');
INSERT INTO [b309] VALUES (360,'عن ميمون بن مهران عن بن عباس احتجم النبي صلى الله عليه وسلم وهو محرم صائم قال الخطيب أبو بكر الصواب حبيب الشهيد عن ميمون بن مهران عن يزيد بن الأصم أن النبي صلى الله عليه وسلم تزوج ميمونة وهو محرم ويقال إن غلاما له',NULL,'399',NULL,NULL,'0');
INSERT INTO [b309] VALUES (361,'أدخل عليه حديث بن عباس وقال أحمد بن حنبل ما كان يضعه عند أصحاب الحديث إلا النظر في الرأي وإلا فقد سمع وذكر هذا الحديث فقال ذهبت له كتب فكان بعد يحدث من كتب غلامه وأرى هذا الحديث من ذلك وقال علي بن المديني حديث الأنصاري عن حبيب بن الشهيد عن ميمون بن مهران عن بن عباس أن النبي صلى الله عليه وسلم احتجم وهو صائم ليس منه شيء قال سليمان بن داود المنقري وجه المأمون إلى',NULL,'400',NULL,NULL,'0');
INSERT INTO [b309] VALUES (362,'الأنصاري خمسين ألف درهم وأمره أن يقسمها بين الفقهاء فقال الهلال بن مسلم هي لي ولأصحابي فقال الأنصاري لهلال كيف تتشهد يعنى في الصلاة فتشهد هلال على حديث بن مسعود فقال له الأنصاري من حدثك به ومن أين ثبت عندك فانقطع هلال فقال له الأنصاري تصلي كل يوم خمس صلوات وتردد فيها هذا الكلام وأنت لا تدري من رواه عن نبيك صلى الله',NULL,'401',NULL,NULL,'0');
INSERT INTO [b309] VALUES (363,'عليه وسلم قد باعد الله بينك وبين الفقه فقسمها الأنصاري في أصحابه وذكره صاحب الإغتباط روى له البخاري ومسلم وأبو داود والترمذي والنسائي وابن ماجة وتوفي سنة خمس عشرة ومائتين',NULL,'402',NULL,NULL,'0');
INSERT INTO [b309] VALUES (364,'٥٤ -<span data-type="title" id=toc-69> محمد بن أحمد بن الحسين بن القاسم بن السرى بن الغطريف الغطريفي الجرجاني الرباطي</span>
قال بن الصلاح وممن بلغنا عنه ذلك يعنى الاختلاط من المتأخرين أبو أحمد الغطريفي الجرجاني الرباطي ذكر الحافظ أبو عبد الله البردعي ثم السمرقندي في',NULL,'403',NULL,NULL,'0');
INSERT INTO [b309] VALUES (365,'معجمه أنه بلغه أنه اختلط في آخر عمره قال الأنباسي ولم يعرف له اختلاط إلا ما رواه يعنى بن الصلاح عن أبي على البردعي وقد ترجمه الحافظ حمزة السهمي في تاريخ جرجان فلم يذكر عنه شيئا من ذلك وهو أعرف به فإنه أحد شيوخه وقد حدث عنه الحافظ أبو بكر الإسماعيلي في صحيحه',NULL,'404',NULL,NULL,'0');
INSERT INTO [b309] VALUES (366,'إلا أنه دلس اسمه فقال مرة حدثنا محمد بن أبي حامد النيسابوري وقال مرة حدثنا محمد بن أحمد البغوي وقال مرة ثنا محمد بن أحمد بن الوردي وقال مرة ثنا محمد بن أبي حامد وقال مرة ثنا محمد بن أحمد العبقي وقال مرة محمد بن أحمد بن الحسين ولم ينسبه ونسبة الغطريفي إلى أحد أجداده ولم يدلسه الإسماعيلي لضعفه ولكن لكونه ليس في مرتبة شيوخه وإنما هو من أقرانه وكان نازلا في منزل الإسماعيلي وتوفي الإسماعيلي قبله في سنة إحدى وسبعين وثلاثمائة في غرة رجب وتأخر الغطريفي ست سنين فتوفى سنة سبع وسبعين في شهر رجب أيضا فلذلك أبهم نسبه فإن كان قد حصل للغطريفي تغير فهو بعد موت الإسماعيلي وآخر من بقى من أصحاب الغطريفي القاضي أبو الطيب طاهر بن عبد الله الطبري وهو أيضا سمع منه قبل إن كان حصل للغطريفي',NULL,'405',NULL,NULL,'0');
INSERT INTO [b309] VALUES (367,'القاضي أبو الطيب رحل إلى جرجان سنة إحدى وسبعين في حياة الإسماعيلي فقدمها فاشتغل بدخول الحمام ثم أصبح فأراد الاجتماع بالإسماعيلي والسماع عليه فقال له ابنه أبو سعيد أنه شرب دواء لمرض حصل له فتعال غدا للسماع عليه من الغد يوم السبت فوجده قد مات فلم يحصل للقاضي أبو الطيب لقى الإسماعيلي وسمع في تلك السنة من الغطريفي فإنه كان نازلا منزل الإسماعيلي ولم يذكر الذهبي في الميزان الغطريفي فيمن تغير ولكن ذكر السمعاني في الأنساب أنهم أنكروا على الغطريفي حديثا رواه من طريق مالك عن الزهري عن أنس أن النبي صلى الله عليه وسلم',NULL,'406',NULL,NULL,'0');
INSERT INTO [b309] VALUES (368,'أهدى جملا لأبي جهل قال السمعاني فكان يذكر أن بن صاعد وابن مظفر أفادا عن الصوفي هذا الحديث قال ولا يبعد أن يكون قد سمع إلا',NULL,'407',NULL,NULL,'0');
INSERT INTO [b309] VALUES (369,'أنه لم يخرج أصله قال وقد حدث غير واحد من المتقدمين والمتأخرين بهذا الحديث عن الصوفي قال السمعاني وأنكروا عليه أيضا أنه حدث بمسند إسحاق بن إبراهيم الحنظلي عن بن شيرويه من غير الأصل الذي سمع فيه وقال حمزة السهمي سمعت أبا عمرو فيقول رأيت سماع الغطريفي في جميع كتاب بن شيرويه',NULL,'408',NULL,NULL,'0');
INSERT INTO [b309] VALUES (370,'وإذا لم يثبت له اختلاط فيحتمل أنه اشتبه بشخص آخر معاصر له وافقه في اسمه واسم أبيه وبلده وهو محمد بن أحمد بن الحسن الجرجاني وهذا بين الحاكم اختلاطه في تاريخ نيسابور فقال سافر معي وسبرته في الحضر والسفر نيفا وأربعين سنة ما اتهمته في الحديث قط ثم تغير بأخرة وخلط والله يغفر لنا وله وينتقم ممن أفسد علمه وهذا توفي عشية الإثنين الرابع من جمادي الأولى سنة ثلاث وثمانين وثلاثمائة انتهى',NULL,'409',NULL,NULL,'0');
INSERT INTO [b309] VALUES (371,'٥٥ -<span data-type="title" id=toc-70> محمد بن أحمد بن الحسن الجرجاني</span>',NULL,'409',NULL,NULL,'0');
INSERT INTO [b309] VALUES (372,'سمع أبا العباس الأصم تغير واختلط بأخرة قاله الحاكم وذكره صاحب الاغتباط وهذا هو المذكور في ترجمة الغطريفي توفي سنة ثلاث وثمانين وثلاثمائة',NULL,'410',NULL,NULL,'0');
INSERT INTO [b309] VALUES (373,'٥٦ -<span data-type="title" id=toc-71> محمد بن الفضل بن محمد بن إسحاق بن خزيمة</span>
اختلط قل موته بثلاث سنين وتجنب الناس حديثه والرواية',NULL,'410',NULL,NULL,'0');
INSERT INTO [b309] VALUES (374,'عنه قاله صاحب الميزان وقال الحاكم مرض في الآخر وتغير بزوال عقله وذكره بن الصلاح فيهم وذكر عن البرذعي أنه قال اختلط في آخر عمره قال الذهبي في ميزانه ما عرفت أحدا سمع منه أيام عدم عقله وقال الحافظ العراقي فيما ذكره الأبناسي عنه أن الحاكم قد بين في تاريخ نيسابور مدة الاختلاط فقال أنه مرض وتغير',NULL,'411',NULL,NULL,'0');
INSERT INTO [b309] VALUES (375,'بزوال العقل في ذي الحجة من سنة أربع وثمانين وثلاثمائة فإني قصدته بعد ذلك غير مرة فوجدته لا يعقل وكل من أخذ عنه بعد ذلك فلقلة مبالاته بالدين فيكون مدة اختلاطه سنتين وخمسة أشهر أو مع زيادة بعض أشهر وأما نقل صاحب الميزان عن الحاكم أنه عاش بعد تغيره ثلاث سنين فهو نقل غير محرر قال وما عرفت أحدا سمع منه أيام عدم عقله توفي ليلة الجمعة الثامن عشر من جمادى الأولى سنة سبع وثمانين وثلاثمائة',NULL,'412',NULL,NULL,'0');
INSERT INTO [b309] VALUES (376,'٥٧ -<span data-type="title" id=toc-72> محمد بن مبارك بن مشق البغدادي</span>',NULL,'412',NULL,NULL,'0');
INSERT INTO [b309] VALUES (377,'من طلبة الحديث اختلط قبل موته بثلاثة أعوام فما حدث فيها بشيء قاله الذهبي في ميزانه وذكره صاحب الاغتباط',NULL,'413',NULL,NULL,'0');
INSERT INTO [b309] VALUES (378,'٥٨ -<span data-type="title" id=toc-73> محمد بن علي بن محمود بن الصابوني المحمودي الحافظ روى عنه الدمياطي</span>',NULL,'413',NULL,NULL,'0');
INSERT INTO [b309] VALUES (379,'والمزي والبرزالي',NULL,'414',NULL,NULL,'0');
INSERT INTO [b309] VALUES (380,'وأبو الحسن بن العطار ذكره بن عبد الهادي في الطبقات التي اختصرها من طبقات الحفاظ للذهبي فقال تغير قبل موته وقال بن أبي الفتح',NULL,'415',NULL,NULL,'0');
INSERT INTO [b309] VALUES (381,'اختلط قبل أن يموت بسنة وكذا ذكر أنه تغير واختلط البرزالي الحافظ علم الدين في معجمه وكذا الذهبي في معجمه أيضا وذكره صاحب الاغتباط وتوفي في ذي القعدة سنة ثمان وستين ودفن بسفح قاسيون',NULL,'416',NULL,NULL,'0');
INSERT INTO [b309] VALUES (382,'٥٩ -<span data-type="title" id=toc-74> محمد بن إسحاق بن يحيى بن منده الحافظ الجوال صاحب التصانيف</span>
قال أبو نعيم في تاريخه هو حافظ من أولاد المحدثين مات في سلخ ذي القعدة سنة خمس وتسعين وثلاثمائة اختلط في آخر عمره إلى آخر كلامه وذكره صاحب الاغتباط',NULL,'417',NULL,NULL,'0');
INSERT INTO [b309] VALUES (383,'٦٠ -<span data-type="title" id=toc-75> محمد بن زهير أبو يعلى الأبلى</span>',NULL,'417',NULL,NULL,'0');
INSERT INTO [b309] VALUES (384,'قال بن غلام الزهري اختلط قبل موته بسنتين ذكره صاحب الاغتباط',NULL,'418',NULL,NULL,'0');
INSERT INTO [b309] VALUES (385,'٦١ -<span data-type="title" id=toc-76> محمد بن سعيد بن نبهان الكاتب</span>
عاش مائة سنة وسماعه صحيح لكنه يتشيع ثم أنه قد اختلط قبل موته بعامين فيعتبر تاريخ السامع منه قاله',NULL,'418',NULL,NULL,'0');
INSERT INTO [b309] VALUES (386,'الذهبي وذكره صاحب الاغتباط',NULL,'419',NULL,NULL,'0');
INSERT INTO [b309] VALUES (387,'٦٢ -<span data-type="title" id=toc-77> محمد بن عبد القادر بن عثمان الجعفري النابلسي الحنبلي</span>
قال العلامة صاحب الاغتباط بلغني أنه اختلط قبل موته',NULL,'419',NULL,NULL,'0');
INSERT INTO [b309] VALUES (388,'بسبب موت ابنه الإمام شرف الدين عبد القادر الحنبلي قاضي دمشق انتهى',NULL,'420',NULL,NULL,'0');
INSERT INTO [b309] VALUES (389,'٦٣ -<span data-type="title" id=toc-78> محمد بن موسى بن محمد اللخمي الشافعي بن سند الحافظ شمس الدين</span>
قال صاحب الاغتباط بلغني اختلاطه قبل موته بمدة تزيد على سنة اختلاطا فاحشا انتهى وحذفت من هذا الباب مسلم بن كيسان وقد صح اختلاطه وهو ضعيف وقيل متروك',NULL,'420',NULL,NULL,'0');
INSERT INTO [b309] VALUES (390,'<span data-type="title" id=toc-79>باب الهاء</span>
٦٤ -<span data-type="title" id=toc-80> هاشم بن القاسم بن شيبة القرشي أبو محمد</span>
معدود في أهل حران وفي موالي قريش عن عبد الله بن وهب وعتاب بن بشير ومحمد بن عجلان وغيرهم',NULL,'421',NULL,NULL,'0');
INSERT INTO [b309] VALUES (391,'وعنه بن ماجة وعبد الله بن محمد بن أبي الدنيا وعبد الله بن محمد بن ناجية وغيرهم',NULL,'422',NULL,NULL,'0');
INSERT INTO [b309] VALUES (392,'قال عبد الرحمن بن أبي حاتم محله الصدق وأثبته بن حبان في الثقات قال أبو عروبة كبر وتغير قاله الحافظ الحلبي صاحب الاغتباط فأما هاشم بن القاسم محدث بغداد فثقة مشهور روى له بن ماجة وتوفي سنة ستين ومائتين',NULL,'423',NULL,NULL,'0');
INSERT INTO [b309] VALUES (393,'٦٥ -<span data-type="title" id=toc-81> هشام بن عمار بن نصير بن ميسرة السلمي أبو الوليد</span>
معدود في أهل دمشق الخطيب بالمسجد الجامع بها يعنى الأموي عن حاتم بن إسماعيل وسفيان بن عيينة وصدقة بن خالد',NULL,'424',NULL,NULL,'0');
INSERT INTO [b309] VALUES (394,'وعبد الرحمن بن أبي الرجال ومالك بن أنس والوليد بن مسلم وغيرهم وعنه البخاري وأبو داود والنسائي وابن ماجة وأبو عبيد القاسم بن سلام',NULL,'425',NULL,NULL,'0');
INSERT INTO [b309] VALUES (395,'ويحيى بن معين ويعقوب بن سفيان الفارسي وغيرهم قال النسائي لا بأس به وقال الدارقطني صدوق كيس',NULL,'426',NULL,NULL,'0');
INSERT INTO [b309] VALUES (396,'وقال يحيى بن معين كيس وقال أحمد بن حنبل طياش خفيف قال أبو حاتم كبر وتغير فكان يتلقن كلما لقن وعنه صدوق قال الحافظ أبو عبد الله محمد بن أبي نصر الحميدي أخبرني بعض أهل الحديث أنه سمعه يقول سألت الله سبع حاجات فقضى لي منها ستة والواحدة ما أدرى ما صنع فيها سألته أن يغفر لي ولوالدي وهى التي لا أدرى ما صنع فيها وسألته أن يرزقنى الحج ففعل وسألته أن يجعل الناس يغدون إلى في طلب العلم ففعل وسألته أن يجعلني مصدقا على حديث رسوله صلى الله عليه وسلم ففعل وسألته أن أخطب على منبر دمشق ففعل وسألته أن يعمرني مائة سنة ففعل وسألته أن يرزقني ألف دينار حلالا ففعل فقيل له كل شيء قد عرفناه فألف دينار حلال من أين لك',NULL,'427',NULL,NULL,'0');
INSERT INTO [b309] VALUES (397,'فقال وجه المتوكل ببعض ولده ليكتب عنى لما خرج إلينا ونحن نلبس الأزر ولا نلبس السراويلات فجلست فانكشف ذكرى فرآه الغلام فقال استر يا عم فقلت رأيته قال نعم فقلت له أما إنك لا ترمد عينك أبدا إن شاء الله تعالى فلما دخل على المتوكل ضحك فسأله فأخبره بما قلت فقال احملوا له ألف دينار فأتتني من غير مسألة ولا استشراف نفس ذكره الأندرشى الحافظ في عمدته وذكر أيضا عن محمد بن الفيض قال سمعت هشام بن عمار يقول باع أبي بيتا بعشرين دينارا وجهزني للحج فلما صرت إلى المدينة أتيت مجلس مالك بن أنس ومعي مسائل أريد أن أسأله عنها فأتيته وهو جالس في هيأة الملوك وغلمان قيام والناس يسألونه وهو يجيبهم فلما انقضى المجلس قال لي بعض أصحاب الحديث سل عما معك فقلت يا أبا عبد الله ما',NULL,'428',NULL,NULL,'0');
INSERT INTO [b309] VALUES (398,'تقول في كذا وكذا فقال حصلنا على الصبيان يا غلام احمله فحملني كما يحمل الصبي وأنا يومئذ غلام مدرك فضربني بدرة مثل درة المعلمين سبع عشرة درة فوقفت أبكى فقال مالك بن أنس ما يبكيك أوجعتك هذه يعني الدرة فقلت إن أبي باع منزله ووجه بي أتشرف بك وبالسماع منك فضربتني فقال اكتب فحدثني سبعة عشرة حديثا وسألته عما كان معي بن المسائل فأجابني وقال صالح بن محمد الحافظ سبب ضربه إياه أن مالكا قال اقرأ فقال هشام لا بل حدثني وأكثر عليه من ذلك فأمر حينئذ بضربه فلما ضربه قال له ظلمتني إذ ضربتني بغير سبب لا أجعلك في حل فقال له مالك ما كفارة ذلك فقال له هشام تحدثني في مقابلة كل درة حديثا فلما حدثه قال له هشام زد من الضرب وزد في الحديث وقال محمد بن ضريم الخريمي سمعته يقول في خطبته',NULL,'429',NULL,NULL,'0');
INSERT INTO [b309] VALUES (399,'قولوا الحق ينزلكم الحق منازل أهل الحق يوم لا يقضى إلا بالحق وقال أبو المستضىء معاوية بن أوس كان إذا مشى يطرق رأسه إلى الأرض ولا يرفعه حياء من الله تعالى وذكره صاحب الاغتباط روى له البخاري وأبو داود والترمذي والنسائي',NULL,'430',NULL,NULL,'0');
INSERT INTO [b309] VALUES (400,'وابن ماجة وتوفي سنة ست وأربعين ومائتين وقيل سنة خمس وأربعين وقيل أربع وأربعين',NULL,'431',NULL,NULL,'0');
INSERT INTO [b309] VALUES (401,'٦٦ -<span data-type="title" id=toc-82> هلال بن خباب العبدي أبو العلاء</span>
معدود في البصريين وفي الموالي وولاؤه لزيد بن صوحان',NULL,'431',NULL,NULL,'0');
INSERT INTO [b309] VALUES (402,'عن سعيد بن جبير وميسرة أبي صالح ويحيى بن جعد وغيرهم',NULL,'432',NULL,NULL,'0');
INSERT INTO [b309] VALUES (403,'وعنه عباد بن العوام ومسعر بن كدام وهشيم بن بشير وغيرهم',NULL,'433',NULL,NULL,'0');
INSERT INTO [b309] VALUES (404,'أطلق يحيى بن معين وأحمد بن حنبل ومحمد بن عبد الله بن عمار وغيرهم القول بتوثيقه وأثبته بن حبان في الثقات وقال يخطىء ويخالف وقال أبو أحمد بن عدي أرجو أنه لا بأس به ووهم من اعتقد أن يونس بن خباب وصالح بن خباب أخوان له قال يحيى القطان تغير قبل موته واختلط وقال مرة أتيته وكان قد تغير',NULL,'434',NULL,NULL,'0');
INSERT INTO [b309] VALUES (405,'وقال العقيلي في حديثه وهم وتغير بأخرة وذكره فيهم صاحب الاغتباط روى له أبو داود والترمذي والنسائي وابن ماجة',NULL,'435',NULL,NULL,'0');
INSERT INTO [b309] VALUES (406,'<span data-type="title" id=toc-83>باب الياء </span>المثناة من تحت
٦٧ -<span data-type="title" id=toc-84> يحيى بن يمان العجلي الكوفي أبو زكريا</span>
عن إسماعيل بن أبي خالد وسفيان الثوري وغيرهما وعنه إسحاق بن إبراهيم وأبو بكر بن أبي شيبة ومحمد',NULL,'436',NULL,NULL,'0');
INSERT INTO [b309] VALUES (407,'بن العلاء وغيرهم أثبته بن حبان في الثقات وقال وكيع لم يكن أحد من أصحابنا أحفظ للحديث منه كان يحفظ في المجلس خمسمائة حديث ثم نسي ولا أعلم بالكوفة أحدا أحفظ من ابنه داود وقال علي بن المديني صدوق إلا أنه تغير حفظه',NULL,'437',NULL,NULL,'0');
INSERT INTO [b309] VALUES (408,'وقال الذهبي في كاشفه فلج فساء حفظه وقال أحمد بن حنبل حدث عن الثوري بعجائب لا أدري هل ترك لهذا أو تغير لقيناه لم يزل الخطأ في كتبه روى له البخاري في كتاب الأدب ومسلم في صحيحه وأبو داود والنسائي وابن ماجة وتوفي سنة تسع وثمانين ومائة',NULL,'438',NULL,NULL,'0');
INSERT INTO [b309] VALUES (409,'<span data-type="title" id=toc-85>الكنى</span>
٦٨ -<span data-type="title" id=toc-86> أبو بكر بن عياش بن سالم الأسدي الإمام المقرىء</span>
معدود في الكوفيين وفي موالي واصل بن حيان الأحدب الأسدي وهو الخياط المقري أخو الحسن بن عياش واسمه محمد وقيل عبد الله وقيل سالم وقيل شعبة',NULL,'439',NULL,NULL,'0');
INSERT INTO [b309] VALUES (410,'والصحيح أن اسمه كنيته قال الأندرشي يروي عن حصين بن عبد الرحمن السلمي وحميد الطويل وسليمان الأعمش ومطرف بن طريف وأبي إسحاق السبيعي وغيرهم وعنه أحمد بن عبد الله بن يونس وإسماعيل بن أبان الوراق وأبو بكر إسماعيل بن حفص الإبلي وأبو بكر عبد الله بن محمد بن أبي شيبة',NULL,'440',NULL,NULL,'0');
INSERT INTO [b309] VALUES (411,'ويحيى بن آدم وغيرهم أثبته بن حبان في الثقات وقال الإمام أحمد بن حنبل صدوق ووصفه مرة بالثقة وقال ربما غلط وقال أبو أحمد بن عدي روى عن أجلة الناس وحديثه فيه',NULL,'441',NULL,NULL,'0');
INSERT INTO [b309] VALUES (412,'كثرة وأثنى عليه بن المبارك ووثقه يحيى بن معين وقال أبو حاتم هو أحفظ من عبد الله بن بشر الرقي وذكره صاحب الاغتباط وقال الكلام فيه معروف ذكره الذهبي في الميزان وذكر كلام الناس فيه وقد ذكر الإمام جمال الدين الزيلعي في تخريج أحاديث',NULL,'442',NULL,NULL,'0');
INSERT INTO [b309] VALUES (413,'الهداية عنه عن حصين عن مجاهد قال صليت خلف بن عمر فلم يكن يرفع يديه إلا في التكبيرة الأولى من الصلاة ثم بعد ذلك ذكر عن البيهقي أنه أسند عن البخاري أنه قال أبو بكر بن عياش اختلط بأخرة روى له البخاري في صحيحه ومسلم في مقدمة كتابه وأبو داود والنسائي',NULL,'443',NULL,NULL,'0');
INSERT INTO [b309] VALUES (414,'والترمذي وابن ماجة وتوفي سنة اثنتين وتسعين ومائة وقيل سنة ثلاث وقيل سنة أربع',NULL,'444',NULL,NULL,'0');
INSERT INTO [b309] VALUES (415,'٦٩ -<span data-type="title" id=toc-87> أبو جعفر الرازي مولى بني تميم</span>
اسمه عيسى بن أبي عيسى وأبو عيسى اسمه ماهان قاله يحيى بن معين وغيره وقيل فيه عيسى بن عبد الله بن ماهان ذكر ذلك أبو حاتم الرازي والأكثر على أنه مروزي الأصل',NULL,'444',NULL,NULL,'0');
INSERT INTO [b309] VALUES (416,'وذكر بعض الناس أنه كان يتجر إلى الري وأن ذلك هو سبب نسبته إليها قاله الأندرشي يروى عن حصين بن عبد الرحمن السلمي والربيع بن أنس الخراساني وسليمان الأعمش وغيرهم وعنه إسحاق بن سليمان الرازي',NULL,'445',NULL,NULL,'0');
INSERT INTO [b309] VALUES (417,'وخالد بن يزيد العتكي وأبو النضر هاشم ب القاسم ويحيى بن أبي بكير وغيرهم',NULL,'446',NULL,NULL,'0');
INSERT INTO [b309] VALUES (418,'قال يحيى بن معين صالح وعنه يكتب حديثه لكنه يخلط وعنه الحكم بتوثيقه وأطلق بن المديني ومحمد بن عبد الله بن عمار الموصلي القول بتوثيقه وأطلق أبو حاتم القول بتوثيقه وصدقه وصلاح حديثه ووثقه محمد بن سعد وقال بن عدي له أحاديث صالحة وقد روى عنه الناس وأحاديثه عامتها مستقيمة وأرجو أنه لا بأس به قال بن المديني ثقة كان يخلط وقال مرة هو نحو',NULL,'447',NULL,NULL,'0');
INSERT INTO [b309] VALUES (419,'موسى بن عبيدة وهو يخلط وقال عبد الرحمن بن يوسف بن خراش سيء الحفظ صدوق وقال عمرو بن علي هو من أهل الصدق وهو سيء الحفظ وقال أبو زرعة شيخ يهم كثيرا وذكره صاحب الاغتباط روى له البخاري في كتاب الأدب وأبو داود في سننه والترمذي في جامعه والنسائي وابن ماجة في سننهما',NULL,'448',NULL,NULL,'0');
INSERT INTO [b309] VALUES (420,'<span data-type="title" id=toc-88>النساء</span>
٧٠-<span data-type="title" id=toc-89> سكرة بنت عبد الله الملقبة قطر النبات عتيقة جمال الدين محمد بن علي بن عبد النور</span>',NULL,'449',NULL,NULL,'0');
INSERT INTO [b309] VALUES (421,'قال الحافظ برهان الدين الحلبي في كتابه الاغتباط سمعت علي أبي الطاهر إسماعيل بن إبراهيم بن قريش المخزومي وعلى يونس بن عبد القوي الدبوسي توفيت في رمضان سنة خمس وثمانين وسبعمائة بالقاهرة أخبرت أنها اختلطت قبل وفاتها قرأت عليها ما قرب سنده لابن شاهين وجزءا من حديث بن رزقويه الأول بسماعها على بن قريش والثاني بسماعها على بن الدبوسي في المحرم سنة اثنتين وثمانين وسبعمائة بسكنها بالقاهرة رحمها الله تعالى. انتهى',NULL,'450',NULL,NULL,'0');
INSERT INTO [b309] VALUES (422,'قلت وهذا آخر ما قصدته من هذا الكتاب جعله الله خالصا لوجهه الكريم ولله الحمد أولا وآخرا وظاهرا وباطنا حمدا كثيرا طيبا مباركا فيه كما يحب ربنا ويرضى سبحانه لا نحصي ثناء عليه هو كما أثنى على نفسه وصلى الله على سيدنا محمد سيد الأولين والآخرين وأكرم السابقين واللاحقين وعلى جميع إخوانه النبيين والمرسلين وآل كل وسائر الصالحين ورضي الله عن سادتنا وقادتنا أصحاب سيدنا رسول الله أجمعين وعن العلماء العاملين وعن علمائنا ومشائخنا وأئمتنا أئمة الهدى والدين خصوصا سيدنا وقدوتنا وشيخنا شيخ الإسلام والمسلمين حافظ العصر وأمير المؤمنين في حديث سيد المرسلين برهان الدين الناجي الشافعي أمتع',NULL,'451',NULL,NULL,'0');
INSERT INTO [b309] VALUES (423,'الله الوجود بوجوده وعامله بكرمه وجوده إنه على ما يشاء قدير وبالإجابة جدير وهو حسبي ونعم الوكيل وفرغت من هذا المؤلف المبارك مع كثرة الهموم والأشغال نهار السبت رابع جمادي الأول سنة تسع وثمانين وثمانمائة أحسن الله عاقبتها في خير انتهى',NULL,'452',NULL,NULL,'0');
INSERT INTO [b309] VALUES (424,'<span data-type="title" id=toc-90>الملحق الأول</span>
وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف.
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'453',NULL,NULL,'0');
INSERT INTO [b309] VALUES (425,'١ -<span data-type="title" id=toc-91> إسحاق بن محمد بن إسماعيل بن عبد الله بن أبي فروة المدني - أبو يعقوب</span>.
روى عن مالك، وسليمان بن بلال، ومحمد بن جعفر بن أبي كثير، وغيرهم.
وعنه البخاري والأثرم وعلي بن عبد العزيز البغوي وجعفر بن محمد الطيالسي، ويحيى بن معلى بن منصور الرازي وأبو إسماعيل الترمذي وغيرهم.
قال أبو حاتم: كان صدوقا ولكنه ذهب بصره فربما لقن الحديث زكتبه صحيحه.
وقال الآجري: سألت أبا داود عنه فوهاه جداً
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'453',NULL,NULL,'0');
INSERT INTO [b309] VALUES (426,'وقال النسائي: ليس بثقة.
قال الحافظ ابن حجر في هدي الساري: والمعتمد فيه ما قاله أبو حاتم وقال الدارقطني والحاكم عيب على البخاري إخراج حديثه. قلت روى عنه البخاري في "كتاب الجهاد" حديثا في الصلح مقرونا بالأويسي وكأنها مما أخذه عنه من كتابه قبل ذهاب بصره.
وقال في التقريب: صدوق كف فساء حفظه من العاشرة توفي سنة٢٢٣/ خ ق ت.',NULL,'454',NULL,NULL,'0');
INSERT INTO [b309] VALUES (427,'٢ -<span data-type="title" id=toc-92> أصبغ مولى عمرو بن حُرَيث - بضم ففتح - المخزومي</span>.
روى عن مولاه عمرو بن حريث.
وعنه إسماعيل بن أبي خالد.
قاله يحيى بن معين: ثقة ووثقه النسائي أيضا.
وقال أبو حاتم: شيخ
قال البخاري: قال ابن المبارك: حدثنا إسماعيل بن أبي خالد عن أصبغ وأصبغ حي في وثاق قد تغير.
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'454',NULL,NULL,'0');
INSERT INTO [b309] VALUES (428,'وقال النسائي: قيل إنه كان تغير.
وقال ابن عدي: له عن غير مولاه اليسير من الحديث وليس هو بالمعروف.
وقال ابن حبان: تغير بآخره حتى كبل بالحديد ولا يجوز الاحتجاج بخبره إلا بعد التخليص وعلم الوقت حيث حدث فيه، والسبب الذي يؤدي إلى هذا العلم معدوم فيه.
وقا الذهبي في الميزان: فيه جهالة ويقال إنه تغير.
وقال الحافظ ابن حجر في التقريب: ثقة تغير من الرابعة / د ق
وقال في التهذيب: رويا له يعني د ق حديثا واحداً في القراءة في الصبح.',NULL,'455',NULL,NULL,'0');
INSERT INTO [b309] VALUES (429,'٣ -<span data-type="title" id=toc-93> الحارث بن عمير - أبو عمير - البصري نزيل مكة</span>.
روى عن أيوب السختياني وحميد الطويل وسليمان بن المغيرة وأبي طوالة وغيرهم.
وعنه ابن عيينة وابن مهدي وابنه حمزة بن الحارث وجماعة.
وثقه يحيى بن معين وأبو حاتم.
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'455',NULL,NULL,'0');
INSERT INTO [b309] VALUES (430,'وقال أبو زرعة: رجل صالح
وقال ابن حبان: يروي عن الأثبات الأشياء الموضوعات
وقال الأزدي: ضعيف منكر الحديث.
وقال الذهبي في الميزان بعد أن ذكر توثيق من وثقه: وما أراه إلا بين الضعف ثم ذكر ما قاله ابن حبان وقال: قال الحاكم: روى عن حميد وجعفر الصادق أحاديث موضوعة.
وقال الحافظ ابن حجر: من الثامنة وثقه الجمهور وفي أحاديثه مناكير ضعفه بسببها الأزدي وابن حبان وغيرهما فلعله تغير حفظه في الآخر.',NULL,'456',NULL,NULL,'0');
INSERT INTO [b309] VALUES (431,'٤ -<span data-type="title" id=toc-94> حجاج بن محمد المصيصي الأعور - أبو محمد - الترمذي الأصل نزل بغداد ثم المصيصة</span>.
روى عن إسرائيل بن يونس وحمزة بن حبيب الزيات وشعبة وابن جريج والليث وجماعة.
وعنه الإمام أحمد وحجاج بن يوسف الشاعر وشريح بن يونس ويحيى بن معين وغيرهم.
قال ابن سعد: كان ثقة صدوقا إن شاء الله وكان قد تغير
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'456',NULL,NULL,'0');
INSERT INTO [b309] VALUES (432,'في آخر عمره.
وقال الإمام أحمد: ما كان أضبط حجاج يعني ابن محمد وأصح حديثه وأشد تعاهده للحروف ورفع أمره.
وفي رواية عنه: كان قد اختلط في آخر عمره.
ووثقه علي بن المديني والنسائي.
وقال أبو حاتم صدوق
ويحكى أن يحيى بن معين منع ابنه أن يدخل عليه بعد اختلاطه أحداً.
ونقل الخطيب في تاريخه أن حجاجا الأعور خرج من بغداد إلى الثغر سنة تسعين يعني ومائة وقد اختلط حجاج في آخر قدمته إلى بغداد وآخر قدمة كانت بعد هذا.
وقال أبو داود: خرج أحمد ويحيى إلى حجاج الأعور إلى المصيصة وبلغني أن يحيى كتب عنه نحواً من خمسين ألف حديث.
ونقل الاثرم عن أحمد أنه قال: كان سنيد لزم حجاجا قديما قد رأيت حجاجا يملي عليه وأرجو أن لا يكون حدث إلا بالصدق
وقال عبد الله بن أحمد عن أبيه رأيت سنيدا عند حجاج ابن محمد وهو يسمع منه كتاب الجامع لابن جريج أخبرت عن الزهري وأخبرت عن صفوان ابن سليم وغير ذلك قال فجعل سنيد يقول لحجاج يا أبا محمد قل ابن جريج عن الزهري وابن جريج عن صفوان بن سليم قال فكان',NULL,'457',NULL,NULL,'0');
INSERT INTO [b309] VALUES (433,'يقول له هكذا قال ولم يحمده أبي فيما رآه يصنع بحجاج وذمه على ذلك قال أبي وبعض تلك الاحاديث التي كان يرسلها ابن جريج أحاديث موضوعة كان ابن جريج لا يبالي عن من أخذها
وقال الخلال عن الاثرم نحو ذلك ثم قال الخلال وروى أن حجاجا كان هذا منه في وقت تغيره ويرى أن أحاديث الناس عن حجاج صحاح إلا ما روى سنيد كما في التهذيب في ترجمة سنيد
وقال ابن حجر: ثقة ثبت، لكنه اختلط في آخر عمره لما قدم بغداد قبل موته، مات سنة ست ومائتين.',NULL,'458',NULL,NULL,'0');
INSERT INTO [b309] VALUES (434,'٥ -<span data-type="title" id=toc-95> حفص بن غياث - بمعجمة مكسورة وياء ومثلثة - ابن طلق بن معاوية النخعي أبو عمر الكوفي القاضي</span>.
روى عن أبي مالك الاشجعي وعاصم الاحول وهشام بن عروة والاعمش والثوري وابن جريج وخلق.
وعنه أحمد وإسحاق وأبو كريب وابن معين وأبو داود
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'458',NULL,NULL,'0');
INSERT INTO [b309] VALUES (435,'الجفري وآخرون.
قال ابن سعد: كان ثقة مأموناً، ثبتاً، إلا أنه كان يدلس وأطلق يحيى بن معين القول بتوثيقه.
وقال أبو حاتم حفص اتقن واحفظ من أبي خالد الاحمر
وقال أبو زرعة ساء حفظه بعدما استقضي فمن كتب عنه من كتابه فهو صالح وإلا فهو كذاب
وقال الحافظ في " هدي الساري": حفص من الأئمة الأثبات أجمعوا على توثيقه والاحتجاج به إلا أنه في الآخر ساء حفظه فمن سمع من كتابه أصح ممن سمع من حفظه
قال أبو زرعة: وقال ابن المديني: كان يحيى بن سعيد القطان يقول: حفص أوثق أصحاب الأعمش. قال فكنت أنكر ذلك فلم اقدمت الكوفة بأخرة أخرج إلي ابنه عمر كتاب أبيه عن الأعمش فجعلت أترحم على القطان.
قال الحافظ بعد هذا: قلت: اعتمد البخاري على حفص هذا في حديث الأعمش، لأنه كان يميز ما صرح به الأعمش بالسماع وبين ما دلسه.
نبه على ذلك أبو الفضل بن طاهر وهو كما قال.
وقال في التقريب: ثقة فقيه تغير حفظه قليل في الآخر من الثامنة مات سنة أربع أو خمس وتسعين وقد قارب الثمانين /ع',NULL,'459',NULL,NULL,'0');
INSERT INTO [b309] VALUES (436,'٦ -<span data-type="title" id=toc-96> حماد بن سلمة بن دينار - أبو سلمة - البصري</span>.
روى عن حميد الطويل وثابت البناني وقتادة وخاله وعبد الملك بن عمير ومحمد بن زياد الجمحي وغيرهم.
وعنه ابن المبارك والقطان وابن مهدي وابن جريج والثوري وشعبة وآخرون.
قال ابن سعد: قالوا: ثقة كثير الحديث وربما حدث بالحديث المنكر
وقال وهيب: هو سيدنا وأعلمنا
وقال الإمام أحمد: صالح، وجعله أثبت في معمر وحميد الطويل، ووثقه يحيى بن معين.
ونقل ابن حجر في " التهذيب" عن البيهقي أنه قال: احد ائمة المسلمين إلا أنه لما كبر ساء حفظه فلدا تركه البخاري وأما مسلم فاجتهد وأخرج من حديثه عن ثابت ما سمع منه قبل تغيره وما سوى حديثه عن ثابت لا يبلغ اثني عشر حديثا أخرجها في الشواهد
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'460',NULL,NULL,'0');
INSERT INTO [b309] VALUES (437,'وقال ابن رجب: قال عبد الله بن أحمد: سمعت يحيى بن معين يقول: من أراد أن يكتب حديث حماد بن سلمة فعليه بعفان بن مسلم.
وقال أيضا: قال النسائي: أثبت أصحاب حماد بن سلمة بن مهدي وابن المبارك وعبد الوهاب الثقفي.
وقال ابن حجر: ثقة عابد أثبت الناس في ثابت وتغير حفظه بآخره من كبار الثامنة.
مات سنة سبع وستين ومائة / خت م٤',NULL,'461',NULL,NULL,'0');
INSERT INTO [b309] VALUES (438,'٧ -<span data-type="title" id=toc-97> خالد بن مهران - أبو المنازل - بفتح الميم وقيل بضمها وكسر الزاي البصري الحذاء</span>
والحذاء لقب له ولم يكن حذاء، وإنما كان يجلس إليهم كما ذكر ذلك ابن سعد.
روى عن أبي قلابة الخراساني ومعاوية بن قرة ويزيد بن شخير وأنس ومحمد وحفصة أولاد سيرين وغيرهم عنه شيخه ابن سيرين وشعبة وبشر بن المفضل والحمادان والثوري وغيرهم.
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'461',NULL,NULL,'0');
INSERT INTO [b309] VALUES (439,'قال الامام أحمد: ثبت
وأطلق يحيى بن معين القول بتوثيقه، وكذلك النسائي وغيره وقال ابو حاتم يكتب حديثه ولا يحتج به
وقال الحافظ في هدي الساري تكلم فيه شعبة وابن علية إما لكونه دخل في شيء من عمل السلطان أو لما قال حماد بن زيد قدم علينا خالد قدمه من الشام فكأنما أنكرنا حفظه.
وقال الحافظ في التقريب: ثقة يرسل وقد أشار حماد بن زيد إلى أن حفظه تغير لما قدم من الشام.
توفي سنة إحدى أو اثنتين وأربعين ومائة / ع.',NULL,'462',NULL,NULL,'0');
INSERT INTO [b309] VALUES (440,'٨ -<span data-type="title" id=toc-98> خصيف بالصاد المهملة مصغرا ابن عبد الرحمن الجزري أبو عون</span>
روى عن عطاء وعكرمة وأبي الزبير وعبد العزيز بن جريج وآخرين
وعنه السفيانان وعبد الملك بن جريج وحجاج بن أرطاة وابن اسحاق وغيرهم
وثقه ابن سعد وأبو زرعة
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'462',NULL,NULL,'0');
INSERT INTO [b309] VALUES (441,'وقال يحيى بن معين: صالح
وقال الامام أحمد ضعيف الحديث
وقال النسائي ليس بالقوي
ونقل ابن أبي حاتم عن يحيى بن سعيد أنه قال: كنا تلك الأيام نتجنب حديث خصيف وما كتبت عن خصيف بالكوفة شيئا إنما كتبت عن خصيف بأخرة وكان يحيى يضعف خصيفا.
وقال ابو حاتم صالح يخطل وتكلم في سوء حفظه
وقال ابن حجر في التقريب صدوق سيء الحفظ خلط بأخرة ورمي بالإرجاء
مات سنة سبع وثلاثين ومائة / ٤',NULL,'463',NULL,NULL,'0');
INSERT INTO [b309] VALUES (442,'٩ -<span data-type="title" id=toc-99> زيد بن حبان - بكسر المهملة وبالموحدة - الرقي كوفي الاصل مولى ربيعة</span>.
روى عن جريج وأيوب السختياني وعطاء بن السائب وأبي اسحاق السبيعي ومحمد بن المنكدر وغيرهم
وعنه معمر بن سليمان الرقي وأبو أحمد الزبيري ومسكين بن بكير وعلي بن ثابت الجزري فياض بن محمد الرقي وآخرون
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'463',NULL,NULL,'0');
INSERT INTO [b309] VALUES (443,'قال الامام أحمد حينما سئل عنه: حدثنا عنه معمر الرقي وتركنا حديثه
وقال أيضا في علله: قال معمر الرقي: أنا سمعت من زيد بن حبان قبل أن يفسد أو يتغير.
وقال يحيى بن معين: لا شيء
ووثقه يحيى في رواية أخرى، كما نقله الذهبي
وذكره ابن حبان في الثقات
وقال ابن عدي: لا أرى به بأسا
وضعفه الدارقطني وقال لا يثبت حديثه
وقال الحافظ ابن حجر: صدوق كثير الخطأ وتغير بأخره
مات سنة ثمان وخمسين ومائة / س ق.
قلت وبما تقدم تبين أن معمر الرقي سمع منه قبل تغيره',NULL,'464',NULL,NULL,'0');
INSERT INTO [b309] VALUES (444,'١٠ -<span data-type="title" id=toc-100> سعد بن سنان بن سعد الكندي المصري</span>
روى عن انس
وعنه يزيد بن أبي حبيب وحده
والرواة عن يزيد يقول بعضهم ك عن يزيد عن سعد بن سنان وبعضهم يقول عن يزيد عن سنان بن سعد وذكر البخاري الاختلاف في اسمه وذكره في باب سنان وصوب ابن يونس سنان بن سعد
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'464',NULL,NULL,'0');
INSERT INTO [b309] VALUES (445,'وقال الامام أحمد لم أكتب أحاديث سنان بن سعد لأنهم اضطربوا فيها فقال بعضهم سعد بن سنان وبعضهم سنان بن سعد وقال أيضا تركت حديثه لأنه مضطرب غير محفوظ.
وقال أيضا يشبه حديثه حديث الحسن لا يشبه حديث أنس
وقال النسائي منكر الحديث
ونقل الحافظ ابن حجر عن ابن معين توثيقه كما نقل عنه أنه قال: سمع عبد الله بن يزيد من سنان بن سعد بعد ما اختلط
وقال الحافظ في التقريب: سعد بن سنان ويقال سنان بن سعد الكندي المصري وصوب الثاني البخاري وابن يونس صدوق له أفراد من الخامسة / بخ د ت ق.',NULL,'465',NULL,NULL,'0');
INSERT INTO [b309] VALUES (446,'١١ -<span data-type="title" id=toc-101> سعيد بن حفص بن عمرو بن نفيل - بالنون والفاء مصغرا - النفيلي أبوعمرو الحراني</span>
روى عن موسى بن أعين وأبو المليح الرقي وزهير بن معاوية ومعقل بن عبيد الله وشريك بن عبد الله وغيرهم
وعنه إبراهيم بن عبد السلام الجزري وبقي بن مخلد وهلال بن العلاء الرقي وأحمد بن سليمان الرهاوي ومحمد بن يحيى بن كثير الحراني والاحوص القاضي وغيرهم
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'465',NULL,NULL,'0');
INSERT INTO [b309] VALUES (447,'ذكره ابن حبان في الثقات
ووثقه مسلم بن قاسم والذهبي
وقال الحافظ في التهذيب قال أبو عروبة الحراني كان قد كبر ولزم البيت وتغير في آخر عمره
وقال في التقريب: صدوق تغير في آخر عمره
مات سنة سبع وثلاثين ومائتين / س',NULL,'466',NULL,NULL,'0');
INSERT INTO [b309] VALUES (448,'١٢ -<span data-type="title" id=toc-102> سعيد بن أبي سعيد كيسان المقبري أبو سعيد المدني</span>
والمقبري نسبة إلى مقبرة بالمدينة
روى عن أبيه وجبير بن مطعم وجابر وعائشة وأم سلمة ومعاوية ويزيد بن هرمز وعبد الله بن رافع وآخرين
وروايته عن عائشة وأم سلمة مرسلة
وعنه اسماعيل بن أمية وأيوب بن موسى ويحيى بن سعيد الانصاري وابن عجلان والليث وغيرهم
وثقه ابن المديني وابن سعد وأبو زرعة والعجلي والنسائي
وقال الامام أحمد ليس به بأس
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'466',NULL,NULL,'0');
INSERT INTO [b309] VALUES (449,'وقال ابو حاتم صدوق
وقال يعقوب بن شيبة قد كان تغير واختلط قبل موته بأربع سنين
وذكره ابن حبان في الثقات وقال اختلط قبل موته بأربع سنين
وأنكر الذهبي اختلاطه فقال: شاخ ووقع في الهرم ولم يختلط
قلت: والعجب من الذهبي انكار اختلاطه وقد أقر باختلاطه الواقدي وابن سعد ويعقوب بن شيبة وابن حبان
قال الحافظ في هدي الساري كان شعبة يقول: حدثنا سعيد المقبري بعد أن كبر وقال الساجي عن يحيى بن معين أثبت الناس فيه ابن أبي ذئب وقال قال ابن خراش أثبت الناس فيه الليث بن سعد ثم قال الحافظ:
قلت: أكثر ما أخرج له البخاري من هذين عنه وأخرج أيضا من حديث مالك وإسماعيل بن أمية وعبيد الله ابن عمر العمري وغيرهم من الكبار وروى له الباقون لكن لم يخرجوا من حديث شعبة عنه شيئا
قال المحدث التهانوي بعد أن ذكر كلام الحافظ المذكور:
قلت: فرواية الكبار من أصحاب المختلط محمولة على الصحة
وقال السخاوي: وثقه ابن سعد وقال: اختلط قبل موته بأربع سنين، وقال زاد غيره: وكأنه لم يرو فيها شيئاً أو تميز وإلا فقد احتج به الأئمة الستة.',NULL,'467',NULL,NULL,'0');
INSERT INTO [b309] VALUES (450,'مات في حدود العشرين ومائة أو قبلها وقيل بعدها.
١٣ -<span data-type="title" id=toc-103> سعيد بن أبي هلال الليثي مولاهم - أبو العلاء - المصري قيل مدني الأصل</span>.
روى عن ربيعة وأبي الزناد وعمرو بن مسلم وزيد بن أسلم وعون بن عبد الله وغيرهم
وعنه سعيد المقبري وهو أكبر منه وعمرو بن الحارث وهشام بن سعد وخالد بن يزيد المصري والليث وآخرون
وثقه ابن سعد والعجلي وأبو حاتم وابن خزيمة والدارقطني وابن حبان وآخرون كما نقل الحافظ في هدي الساري وقال ابن حزم: ليس بالقوي
وقال الساجي صدوق كان أحمد يقول: ما أدري أي شيء يخلط في الأحاديث
وقال الحافظ في التقريب صدوق لم أر لابن حزم في تضعيفه سلفا إلا أن الساجي حكى عن أحمد أنه اختلط
وقال الشيخ حماد الأنصاري، بعد أن نقل كلام الساجي وابن حزم: وقد تبع ابن حزم في تضعيفه الألباني ولم يصب في ذلك.
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'468',NULL,NULL,'0');
INSERT INTO [b309] VALUES (451,'قال الذهبي: مات سنة خمس وثلاثين ومائة / ع.
١٤ -<span data-type="title" id=toc-104> سليمان بن موسى الأموى الدمشقي الأشدق</span>
روى عن واثلة بن الأسقع وطاوس والزهري ونافع وأبي الأشعث الصنعاني ومكحول وآخرين
وعنه ابن جريج والأوزاعي ومعاوية بن يحيى الصدفي وثور بن يزيد وسعيد بن عبد العزيز وغيرهم
قال يحيى بن معين حينما سئل ما حاله في الزهري؟ فقال: ثقة
وقال أيضا ثقة وحديثه صحيح عندنا
وقال النسائي أحد الفقهاء ليس بالقوي في الحديث وقال في موضع آخر في حديثه شيء
وذكر العقيلي عن ابن المديني قوله: كان من كبار أصحاب مكحول وكان خولط قبل موته بيسير
وقال ابن عدي حدث عنه الثقات وهو أحد علماء أهل الشام وقد روى أحاديث ينفرد بها لا يرويها غيره وهو عندي ثبت صدوق
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'469',NULL,NULL,'0');
INSERT INTO [b309] VALUES (452,'وقال الدارقطني من الثقات اثنى عليه عطاء والزهري
وقال ابن حجر صدوق فقيه في حديثه بعض لين وخلط قبل موته بقليل
مات سنة تسع عشرة ومائة / م ٤
١٥ -<span data-type="title" id=toc-105> سويد بن سعيد الهروي الاصل - أبو محمد - سكن الحديثة تحت غابة وفوق الأنبار</span>
روى عن مالك وحفص بن ميسرة وحماد بن زيد ومسلم بن خالد الزنجي ويزيد بن زريع ومعتمر بن سليمان وغيرهم
وعنه مسلم وابن ماجة وأبو زرعة وأبو حاتم ويعقوب بن شيبة والباغندي وأحمد بن الأزهر وغيرهم
قال البخاري: فيه نظر كان عمي فلقن ما ليس من حديثه
وقال أبو حاتم صدوق كان يدلس يكثر ذاك يعني التدليس
ونقل الميموني عن أحمد قوله: ما علمت إلا خيرا وفي رواية أخرى قال: أرجو أن يكون صدوقا لا بأس به
وقال النسائي: ليس بثقه
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'470',NULL,NULL,'0');
INSERT INTO [b309] VALUES (453,'وقال يعقوب بن شيبة: صدوق مضطرب الحفظ ولا سيما بعد ما عمي
وقال البرذعي: رأيت أبا زرعة يسيء القول فيه فقلت له: فأيس حاله قال: أما كتبه فصحاح وكنت أتتبع أصوله فأكتب منها فأما إذا حدث عن حفظه فلا
وقال الحاكم أبو أحمد: عمي في آخر عمره فربما لقن ما ليس من حديثه فمن سمع منه وهو بصير فحديثه عنه أحسن
وقال الذهبي في الكاشف: كان يحفظ لكنه تغير
وقال ابن حجر صدوق في نفسه إلا أنه عمي فصار يتلقن ما ليس من حديثه
مات سنة ست وأربعين ومائتين / م ق...
وقال إبراهيم بن أبي طالب: قلت لمسلم كيف استجزت الرواية عن سويد في الصحيح؟ فقال: ومن أين كنت آتي بنسخة حفص بن ميسرة؟ قلت: هذا يدل على أن مسلماً روى عنه من كتابه وقد تقدم عن أبي زرعة أن كتبه صحاح والله أعلم.',NULL,'471',NULL,NULL,'0');
INSERT INTO [b309] VALUES (454,'١٦ -<span data-type="title" id=toc-106> شرحبيل بن سعد أبو سعد المدني مولى الأنصار</span>
روى عن زيد بن ثابت وأبي هريرة والحسن بن علي وابن عباس وجابر وغيرهم
وعنه عكرمة ومات قبله ويحيى بن سعيد الأنصاري وابن اسحاق وفطر بن خليفة وغيرهم
قال البخاري: روى عنه مالك ولم يسمه
قال ابن المديني: لسفيان بن عيينة: كان شرحبيل بن سعد يغني؟ قال: نعم ولم يكن بالمدينة أحد أعلم بالمغازي والبدريين منه فاحتاج فكأنهم اتهموه، وكانوا يخافون إذا جاء إلى الرجل يطلب منه شيئا فلم يعطه أن يقول: لم يشهد أبوك بدرا
وثقه يحيى بن معين في رواية وضعفه في أخرى
وقال أبو حاتم ضعيف الحديث
وقال أبو زرعة مديني فيه لين
ونقل ابن حجر عن ابن سعد قوله كان شيخا قديما روى عن زيد بن ثابت وعامة الصحابة وبقي حتى اختلط واحتاج وله أحاديث وليس يحتج به
وقال الدارقطني ضعيف يعتبر به
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'472',NULL,NULL,'0');
INSERT INTO [b309] VALUES (455,'وذكره ابن حبان في الثقات
خرج ابن خزيمة وابن حبان في صحيحهما
وقال الحافظ ابن حجر صدوق اختلط بآخره من الثالثة
مات سنة ثلاث وعشرين ومائة / بخ د ق',NULL,'473',NULL,NULL,'0');
INSERT INTO [b309] VALUES (456,'١٧ -<span data-type="title" id=toc-107> عاصم بن بهدلة وهو ابن أبي النجود - أبو بكر - المقري</span>.
روى عن زر بن حبيش وأبي عبد الرحمن السلمي ومعبد بن خالد وغيرهم وعنه الأعمش وشعبة والسفيانان والحمادان وآخرون.
قال ابن سعد: كان ثقة إلا أنه كان كثير الخطأ في حديثه
وقال الإمام أحمد ثقة رجل صالح خير ثقة والأعمش أحفظ منه وكان شعبة يختار الأعمش عليه في تثبيت الحديث.
وقال أبو حاتم: محله عندي محل الصدق صالح الحديث ولم يكن بذاك القوي
ونقل الذهبي عن الدارقطني قال في حفظ عاصم شيء
وقال الحافظ في التهذيب: قال ابن قانع قال حماد بن سلمة خلط عاصم في آخر عمره وذكره ابن حبان وابن شاهين في الثقات
وقال الحافظ في التقريب صدوق له أوهام حجة في القراءة وحديثه في الصحيحين مقرون من السادسة مات سنة ثمان وعشرين / ع
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'473',NULL,NULL,'0');
INSERT INTO [b309] VALUES (457,'١٨ -<span data-type="title" id=toc-108> عباد بن منصور الناجي - بالنون والجيم - أبو سلمة البصري القاضي بها</span>.
روى عن القاسم بن محمد وعطاء بن أبي رباح وأيوب السختياني وعكرمة وأبي رجاء العطاردي وغيرهم
وعنه الثوري وريحان بن سعيد ومسلم بن إبراهيم وحماد بن سلمة وشعبة وآخرون
قال يحيى بن سعيد القطان: عباد بن منصور ثقة ليس ينبغي أن يترك حديثه لرأي اخطأ فيه - يعني القدر -.
وقال يحيى بن معين: ليس بشيء ضعيف.
وقال أبو حاتم: ضعيف الحديث يكتب حديثه.
وقال أبو زرعة: بصري لين
وقال النسائي: ضعيف وقد كان أيضا تغير
وقال أبو داود: ولي قضاء البصرية خمس مرات وليس بذاك وعنده أحاديث فيها نكارة وقالوا تغير
وقال ابن عدي: في جملة من يكتب حديثه
وقال العجلي: لا بأس به يكتب حديثه وقال مرة: جائز الحديث
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'474',NULL,NULL,'0');
INSERT INTO [b309] VALUES (458,'وقال الحافظ ابن حجر: صدوق رمي بالقدر وكان يدلس وتغير بأخرة.
من السادسة مات سنة اثنين وخمسين ومائة / خت ع.
١٩ -<span data-type="title" id=toc-109> عبد الحميد بن إبراهيم الحضرمي - أبو تقي بفتح المثناة ثم قاف مكسورة - الحمصي</span>.
روى عن عبد الله بن سالم الأشعري وسلمة بن كلثوم وعمرو بن واقد وإسماعيل بن عياش وعقبة بن معدان وغيرهم.
وعنه صفوان بن عمرو الصغير وعمران بن بكار وعلي بن الحسين الحمصي وأيوب بن سليمان وسليمان بن عبد الحميد البهراني ومحمد بن عوف الطائي وغيرهم.
قال ابن أبي حاتم: سألت محمد بن عوف عنه فقال: كان شيخاً ضريراً لا يحفظ وكنا نكتب من نسخة الذي كان عند إسحاق بن زبريق لابن سالم فنحمله إليه ونلقنه فكان لا يحفظ الإسناد ويحفظ بعض المتن فيحدثنا وإنما حملنا الكتاب عنه شهرة الحديث وكان إذا حدث عنه محمد بن عوف قال: وجدت في كتاب ابن سالم حدثنا به أبو تقي.
وقال أبو حاتم: ليس هذا عندي بشيء، رجل لا يحفظ وليس عنده كتب
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'475',NULL,NULL,'0');
INSERT INTO [b309] VALUES (459,'وقال الذهبي في الميزان: قال النسائي ليس بشيء وقواه غيره وضعفه في الكاشف
وقال الحافظ في التقريب: صدوق إلا أنه ذهبت كتبه فسائ حفظه من التاسعة',NULL,'476',NULL,NULL,'0');
INSERT INTO [b309] VALUES (460,'٢٠ -<span data-type="title" id=toc-110> عبد الرحمن بن ثابت بن ثوبان العنسي - بالنون - الدمشقي الزاهد</span>.
روى عن حسان بن عطية وأبي الزبير وعطاء بن أبي رباح وعمرو بن دينار والزهري والعلاء بن عبد الرحمن وغيرهم.
وعنه الوليد بن مسلم وبقية وعلي بن ثابت الجزري زيد بن حباب وأبو عامر العقدي ومحمد بن يوسف الفريابي وآخرون.
قال يحيى بن معين: صالح الحديث، وفي رواية ثانية عنه قال: ضعيف وفي رواية ثالثة قال: لين
وقال أحمد: أحاديثه مناكير
وقال أبو زرعة: شامي لا بأس به
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'476',NULL,NULL,'0');
INSERT INTO [b309] VALUES (461,'وقال أبو حاتم: ثقة يشوبه شيء من القدر وتغير عقله في آخر حياته وهو مستقيم الحديث كما نقله عنه الحافط في التهذيب.
وقال يعقوب بن شيبة: ابن ثوبان رجل صدق لا بأس به
وقال ابن عدي: له أحاديث صالحة وكان رجلا صالحا يكتب حديثه على ضعفه
وقال الحافظ في التقريب صدوق يخطئ ورمي بالقدر وتغير بأخره من السابعة مات سنة سنة خمس وستين مائة / بخ ٤.',NULL,'477',NULL,NULL,'0');
INSERT INTO [b309] VALUES (462,'٢١ -<span data-type="title" id=toc-111> عبد الرحمن بن أبي الزناد عبد الله بن ذكوان المدني مولى قريش</span>
روى عن أبيه والأوزاعي وسهيل بن ابي صالح ومعاذ العنبري وهشام بن عروة وموسى بن عقبة وغيرهم.
وعنه عبد الله بن وهب وحجاج بن محمد الأعور وأبو الوليد وأبو داود الطيالسي وعلي بن حجر وسويد بن سعيد وآخرون.
قال علي بن المديني: ما حدث بالمدينة فهو صحيح وما حدث ببغداد أفسدة البغداديون
وعنه أيضا حديثه بالمدينة مقارب، وما حدث به بالعراق فهو مضطرب
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'477',NULL,NULL,'0');
INSERT INTO [b309] VALUES (463,'وقال يحيى بن معين: لا يحتج بحديثه
وقال أبو حاتم: يكتب حديثه ولا يحتج به
وقال الساجي في ضعف وما حدث بالمدينة أصح مما حدث ببغداد
وذكر الحافظ في التهذيب عن ابن سعد: كثير الحديث كان يضعف لروايته عن أبيه
وقال النسائي: لا يحتج بحديثه
وقال ابن عدي: هو ممن يكتب حديثه
قال الحافظ ابن حجر: صدوق تغير حفظه لما قدم بغداد وكان فقيها من السابعة مات سنة أربع وسبعين ومائة / خت م ٤',NULL,'478',NULL,NULL,'0');
INSERT INTO [b309] VALUES (464,'٢٢ -<span data-type="title" id=toc-112> عبد الله بن رجاء المكي - أبو عمران - البصري نزيل مكة</span>.
روى عن ابن خثيم وعبيد الله بن عمر وعبد الرحمن بن إسحاق وموسى بن عقبة وابن جريج والثوري وغيرهم.
وعنه الحميدي وأبو يعلى وأحمد بن حنبل ويحبى بن معين وصدقة بن الفضل وآخرون
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'478',NULL,NULL,'0');
INSERT INTO [b309] VALUES (465,'قال ابن سعد: كان ثقة كثير الحديث وكان أعرج وكان من أهل البصرة فانتقل فنزل مكة إلى أن مات بها
وسئل الامام أحمد عنه فحسن أمره
وقال يحيى بن معين: ثقة صدوق
وقال أبو حاتم: صدوق
وقال أبو زرعة شيخ صالح
وروى عن الإمام أحمد أنه قال: زعموا أن كتبه ذهبت فكان يحدث من حفظه وعنده مناكير وقال ما سمعت منه إلا حديثين
وقال الحافظ في التقريب: ثقة تغير حفظه قليلاً من صغار الثامنة مات في حدود التسعين - أي بعد المائة - / م د س ق.',NULL,'479',NULL,NULL,'0');
INSERT INTO [b309] VALUES (466,'٢٣ -<span data-type="title" id=toc-113> عبد الله بن سلمة - بكسر اللام - المرادي الكوفي أبوم العالية </span>روى عن معاذ وابن مسعود وسلمان الفارسي وغيرهم من الصحابة
وعنه أبو اسحاق السبيعي وعمرو بن مرة
قال البخاري قال أبو داود عن شعبة عن عمرو بن مرة: كان عبد الله يحدثنا فنعرف وننكر وكان قد كبر لا يتابع في حديثه وقال النسائي يروى عنه عمرو بن مرة يعرف وينكر
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'479',NULL,NULL,'0');
INSERT INTO [b309] VALUES (467,'كنيته أبو العالية
وثقه العجلي ويعقوب بن ابي شيبة
وقال ابن عدي أرجو أنه لا بأس به
وقال الذهبي في المغني صدوق
وقال ابن حجر صدوق تغير حفظه من الثانية / ع ٤',NULL,'480',NULL,NULL,'0');
INSERT INTO [b309] VALUES (468,'٢٤ -<span data-type="title" id=toc-114> عبد الله بن صالح بن محمد بن مسلم الجهني المصري أبو صالح كاتب الليث بن سعد</span>
روى عن معاوية بن صالح والليث وسعيد بن عبد العزيز التنوخي وغيرهم
وعنه أبو داود والترمذي وأبو حاتم الرازي وابن معين وآخرون
قال عبد الملك بن شعيب بن الليث: ثقة مأمون سمع من جدي حديثه
وقال أبو حاتم: الأحاديث التي أخرجها أبو صالح في آخر عمره التي أنكروا عليه نرى أن هذه مما افتعل خالد بن نجيح وكان أبو صالح يصحبه وكان سليم الناحية وكان خالد بن نجيح يفتعل الحديث ويضعه في كتب الناس ولم يكن وزن أبي صالح وزن الكذب كان رجلا صالحا
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'480',NULL,NULL,'0');
INSERT INTO [b309] VALUES (469,'وقال أبو زرعة: لم يكن عندي ممن يتعمد الكذب وكان حسن الحديث
وقال ابن حبان: منكر الحديث جدا يروي عن الأثبات ما لا يشبه حديث الثقات وعنده المناكير الكثيرة عن أقوام مشاهير أئمة وكان في نفسه صدوقا يكتب لليث بن سعد الحساب وكان كاتبه على الغلات وإنما وقع المناكير في حديثه من قبل جار له رجل سوء ...
وقال الإمام أحمد كان أول أمره متماسكا ثم فسد بأخرة وليس هو بشيء
وقال الحافظ في التقريب صدوق كثير الغلط ثبت في كتابه وكانت فيه غفلة
من العاشرة مات سنة اثنتين وعشرين / خت د ت ق',NULL,'481',NULL,NULL,'0');
INSERT INTO [b309] VALUES (470,'٢٥ -<span data-type="title" id=toc-115> عبد الله بن لهيعة - بفتح اللام وكسر الهاء - ابن عقبة الحضرمي أبو عبد الرحمن المصري القاضي</span>
روى عن جعفر بن ربيعة وعطاء بن أبي رباح وعبد الله بن هبيرة وعطاء بن دينار وابن المنكدر وغيرهم
وعنه عبد الله بن المبارك وابن وهب والثوري
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'481',NULL,NULL,'0');
INSERT INTO [b309] VALUES (471,'والأوزاعي والوليد بن مسلم وسعيد بن أبي مريم وآخرون
قال ابن سعد: كان ضعيفا وعنده حديث كثير ومن سمع منه في أول أمره أحسن حالاً في روايته ممن سمع منه بأخرة وأما أهل مصر فيذكرون أنه لم يختلط ولم يزل أول أمره وآخره واحدا
وقال الإمام أحمد: من كان مثل ابن لهيعة بمصر في كثرة حديثه واتقانه وضبطه كما ذكر الذهبي في الكاشف
ونقل ابن أبي حاتم تضعيفه عن الإمام أحمد ويحيى بن معين وأبي حاتم وأبي زرعة
ولما سئل أبو زرعة عن رواية القدماء عنه فقال: آخره وأوله سواء إلا أن ابن المبارك وابن وهب يتتبعان أصوله فيكتبان منه
وقال ابن مهدي: ما اعتد بشيء سمعته من حديث ابن لهيعة إلا سماع ابن المبارك ونحوه
وقال خالد بن خراش: رآني ابن وهب لا أكتب حديث ابن لهيعة، فقال: اني لست كغيري فاكتبها
وقال الفلاس: من كتب عنه قبل احتراق كتبه مثل ابن المبارك والمقري فسماعه صحيح
وقال أبو الطاهر بن السرح: سمعت ابن وهب يقول حدثني والله الصادق البار عبد الله بن لهيعة وكان أحمد بن صالح',NULL,'482',NULL,NULL,'0');
INSERT INTO [b309] VALUES (472,'يثني عليه
وذكر الحافظ ابن حجر عن عبد الغني بن سعيد أنه قال: إذا روى العبادلة عن ابن لهيعة فهو صحيح: ابن المبارك وابن وهب والمقري وقال: وذكر الساجي وغيره مثله
وقال ابو جعفر الطبري: اختلط عقله في آخر عمره
وقال ابن حبان: كان أصحابنا يقولون سماع من سمع منه قبل احتراق كتبه مثل العبادلة عبد الله بن وهب وابن المبارك وعبد الله بن المقرئ وعبد الله بن مسلمة القعنبي فسماعهم صحيح وكان ابن لهيعة من الكاتبين للحديث والجماعين للعلم والرحالين فيه
وقال الحافظ في التقريب: صدوق خلط بعد احتراق كتبه ورواية ابن المبارك وابن وهب عنه أعدل من غيرهما.
قلت: إذا قلنا إن رواية من روى عنه قبل الاحتراق كتبه صحيحه كما هو رأي كثير من الأئمة فرواية سفيان الثوري وشعبة والأوزاعي وعمرو بن الحارث المصري عنه صحيحة لأن هؤلاء الأربعة رووا عنه وماتوا قبل احتراق كتبه لأن كتبه احترقت سنة ١٦٩ والله أعلم (١) توفي رحمه الله سنة ١٧٤
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 (١) لقد ذكرت ما ذكرته هنا قبل الوقوف على ما قاله الإمام أبو زكريا في حق ابن لهيعة فقد ورد في ص٩٧ من كتاب من كلام أبي زكريا يحيى بن معين في الرجال الذي حققه أحمد محمد نور سيف ما نصه: ابن لهيعة ليس بشيء قيل ليحيى: فهذا الذي يحكي الناس أنه احترقت كتبه؟ قال: ليس لهذا أصل، سألت ==',NULL,'483',NULL,NULL,'0');
INSERT INTO [b309] VALUES (473,'٢٦ -<span data-type="title" id=toc-116> عبد الله بن محمد بن عقيل بن أبي طالب الهاشمي - أبو محمد - المدني</span>
روى عن جابر وابن عمر وأنس بن مالك وسعيد بن المسيب وعبد الله بن جعفر وحمزة بن صهيب وآخرين.
وعنه الثوري وابن عيينة وشريك وحماد بن سلمة وابن جريج وفليح بن سليمان ومعمر وجماعة
قال يحيى بن معين: ليس بذاك وقال مرة ضعيف في كل أمره
وقال أبو حاتم: لين الحديث ليس بالقوي ولا ممن يحتج بحديثه وهو أحب إلى من تمام بن نجيح
وقال النسائي ضعيف
وقال العجلي مدني تابعي جائز الحديث
وقال ابن خزيمة: لا أحتج به لسوء حفظه
وقال الترمذي: صدوق وقد تكلم فيه بعض أهل العلم من قبل حفظه وسمعت محمد بن إسماعيل يقول: كان أحمد وإسحاق والحميدي يحتجون بحديث ابن عقيل قال محمد بن إسماعيل: وهو مقارب الحديث.
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف
== عنها بمصر وجاء في ص١٠٨ منه: ابن لهيعة ليس بشيء تغير أو لم يتغير',NULL,'484',NULL,NULL,'0');
INSERT INTO [b309] VALUES (474,'وقال أبو احمد الحاكم: كان أحمد بن حنبل وابن راهوية يحتجان بحديثه وليس بذاك المتين المعتمد
وقال العقيلي: كان فاضلا خيرا موصوفا بالعبادة وكان في حفظه شيء
وقال ابن عدي: روى عنه جماعة من المعروفين الثقات وهو خير من ابن سمعان ويكتب حديثه
وقال الذهبي في المغني حسن الحديث
وقال الحافظ ابن حجر صدوق وفي حديثه لين ويقال تغير بأخرة من الرابعة مات بعد الأربعين / بخ د ت ق',NULL,'485',NULL,NULL,'0');
INSERT INTO [b309] VALUES (475,'٢٧ -<span data-type="title" id=toc-117> عبد الله بن مطر - أبو ريحانة البصري مشهور بكنيته</span>.
روى عن سفينة وابن عباس وصحب ابن عمر
وعنه عوف الأعرابي ووهيب بن خالد وسليمان بن كثير وبشر بن المفضل وإسماعيل بن علية وعلي بن عاصم وغيرهم.
قال يحيى بن معين: صالح وقال مرة ليس به بأس
وقال النسائي: لا بأس به وفي رواية عنه: ليس بالقوي
وقال ابن عدي: لا أعرف له حديثا منكرا فأذكره
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'485',NULL,NULL,'0');
INSERT INTO [b309] VALUES (476,'وذكره ابن حبان في الثقات وقال ربما أخطأ
وقال الحافظ في التهذيب: ذكره ابن خلفون في الثقات وقال: إنه تغير وإن من سمع منه قديما فحديثه صالح
وقال في التقريب: أبو ريحانة البصري مشهور بكنيته صدوق تغير بأخرة من الثالثة / م د ت ق',NULL,'486',NULL,NULL,'0');
INSERT INTO [b309] VALUES (477,'٢٨ -<span data-type="title" id=toc-118> عبد الملك بن عمير أبو عمرو وقيل أبو عمر اللخمي الكوفي المعروف بالقبطي رأى عليا رضي الله عنه</span>
وروى عن جابر بن سمرة وجندب البجلي وعدي بن حاتم وعبد الله بن الزبير والمغيرة بن شعبة وأم عطية الأنصارية وعبد الرحمن بن أبي ليلى وغيرهم.
وعنه زائدة والسفيانان وشهر بن حوشب وشعبة وأبو عوانة وآخرون
قال يحيى بن معين عبد الملك بن عمير مخلط
وقال الإمام أحمد: مضطرب الحديث جدا مع قله حديثه ما أرى له خمسائة حديث وقد غلط في كثير منها
وقال أبو حاتم: ليس بخافظ هو صالح تغير حفظه قبل موته
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'486',NULL,NULL,'0');
INSERT INTO [b309] VALUES (478,'ووثقه العجلي
وقال النسائي ليس به بأس
وقال الحافظ في هدي الساري احتج به الجماعة وأخرج له الشيخان من رواية القدماء عنه في الاحتجاج ومن رواية بعض المتأخرين عنه في المتابعات وإنما عيب عليه أنه تغير حفظه لكبر سنه لأنه عاش مائة وثلاث سنين ولم يذكره ابن عدي في الكامل ولا ابن حبان
وقال في التقريب: ثقة فقيه تغير حفظه وربما دلس من الثالثة مات سنة ست وثلاثين ومائة / ع',NULL,'487',NULL,NULL,'0');
INSERT INTO [b309] VALUES (479,'٢٩ -<span data-type="title" id=toc-119> عبيد بن هشام الحلبي أبو نعيم جرجاني الأصل</span>
روى عن مالك بن أنس وأبي المليح الرقي وعيسى بن يونس وعتاب بن بشير وبكر بن خنيس وغيرهم
وعنه الحسن بن سفيان وأبو عروبة الحراني وأبو داود وأبو زرعة وأبو حاتم وآخرون
قال أبو حاتم صدوق
وقال أبو داود ثقة إلا أنه تغير في آخر عمره لقن أحاديث ليس لها أصل
وقال النسائي ليس بالقوي
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'487',NULL,NULL,'0');
INSERT INTO [b309] VALUES (480,'وقال حمزة السهمي: قال لنا أبو احمد بن عدي سألت عبدان عن أبي نعيم الحلبي فقال: هو عندهم ثقة
وقال أبو أحمد الحاكم روى ما لا يتابع عليه
وقال صالح جزرة صدوق ولكن ربما غلط
وقال الخليلي في الإرشاد ثقة وقال آخر من روى عنه بالعراق الباغندي وبالمشرق الحسن بن سفيان النسوي وقال أيضا مرضي عندهم
وقال الحافظ ابن حجر صدوق تغير في آخر عمره فتلقن / د',NULL,'488',NULL,NULL,'0');
INSERT INTO [b309] VALUES (481,'٣٠ -<span data-type="title" id=toc-120> عثمان بن الهيثم بن جهم بن عيسى العبدي أبو عمرو البصري المؤذن</span>
روى عن ابن جريج وهشام بن حسان وعمران بن حدير ومبارك بن فضالة وهشام بن زياد وغيرهم
وعنه البخاري والنسائي ويعقوب بن سفيان وأبو حاتم والذهلي ومحمد بن عبد الرحيم البزار وغيرهم
قال أبو حاتم: صدوق غير انه بأخرة كان يتلقن ما يلقن
وذكره ابن حبان في الثقات
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'488',NULL,NULL,'0');
INSERT INTO [b309] VALUES (482,'وقال الدارقطني صدوق كثير الخطأ
وقال الحافظ في هدي الساري قال الساجي ذكر عند أحمد فأومأ إليه أنه ليس بثبت ولم يحدث عنه
وقال أيضاً: له في البخاري حديث أبي هريرة في فضل آية الكرسي ذكره في مواضع عنه مطولا ومختصرا وروى له عنه حديثا آخر عن محمد وهو الذهلي عنه عن ابن جريج وآخر في العلم صرح بسماعه منه وهو متابعة
وقال في التقريب ثقة تغير فصار يتلقن من كبار العاشرة
مات في رجب سنة عشرين ومائة.',NULL,'489',NULL,NULL,'0');
INSERT INTO [b309] VALUES (483,'٣١ -<span data-type="title" id=toc-121> عفان بن مسلم بن عبد الله الصفار أبوعثمان البصري</span>
روى عن عبد الله بن بكر المزني وشعبة ووهيب بن خالد والحمادين وغيرهم
وعنه البخاري وحجاج الشاعر وعبد الله الدارمي وأحمد بن حنبل وآخرون
قال الإمام أحمد عفان أثبت من عبد الرحمن بن مهدي لزمنا عفان عشر سنين ببغداد
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'489',NULL,NULL,'0');
INSERT INTO [b309] VALUES (484,'وقال أبو حاتم ثقة متقن متين
وسئل ابن معين عن عفان وبهز أيضا أيهما أوثق فقال: كلاهما ثقة
فقيل له: إن ابن المديني يزعم أن عفان أصح الرجلين فقال كانا جميعا ثقتين صدوقين
وقال ابن معين أيضا: ما أخطأ عفان قط إلا مرة أنا لقنته فاستغفر الله ونقل الذهبي عن أبي خيثمة قال: أنكرنا عفان قبل موته بأيام ثم قال الذهبي:
قلت: هذا التغير هو تغير مرض الموت وما ضره لأنه ما حدث فيه بخطأ مات سنة تسع عشرة ومائتين وقيل سنة عشرين ومائتين
وقال الحافظ في التقريب ثقة ثبت قال ابن المديني كان إذا شك في حرف من الحديث تركه وربما وهم وقال ابن معين: أنكرناه في صفر سنة تسع عشرة، ومات بعدها بيسير من كبار العاشرة / ع',NULL,'490',NULL,NULL,'0');
INSERT INTO [b309] VALUES (485,'٣٢ -<span data-type="title" id=toc-122> عكرمة بن عمار</span>
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'490',NULL,NULL,'0');
INSERT INTO [b309] VALUES (486,'عن أبي كثير السحيمي وعنه عبد الله بن يزيد المقري',NULL,'491',NULL,NULL,'0');
INSERT INTO [b309] VALUES (487,'قال البيهقي: اختلط في آخر عمره وساء حفظه فروى ما لم يتابع عليه
٣٣ -<span data-type="title" id=toc-123> قيس بن الربيع الأسدي أبو محمد الكوفي</span>
روى عن أبي إسحاق السبيعي والمقدام بن شريح وابن أبي ليلى والأعمش والسدي وغيرهم
وعنه أبان بن تغلب وشعبة والثوري وعبد الرزاق ووكيع وأبو داود الطيالسي وغيرهم
قال ابن سعد: كان يقال لقيس: الجوال لكثرة سماعه وعلمه
وذكره البخاري في الضعفاء وقال: كان وكيع يضعفه
وقال ابن معين: ليس حديثه بشيء وقال في رواية أخرى: ضعيف الحديث لا يساوي شيئا
وقال أبو حاتم ليس بقوي ومحله الصدق يكتب حديثه
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'492',NULL,NULL,'0');
INSERT INTO [b309] VALUES (488,'ولا يحتج به
وقال الذهبي في الكاشف كان شعبة يثني عليه
وقال الحافظ في التقريب صدوق تغير لما كبر أدخل ابنه ما ليس من حديثه فحدث به من السابعة
مات سنة بضع وستين ومائة / د ت ق',NULL,'493',NULL,NULL,'0');
INSERT INTO [b309] VALUES (489,'٣٤ -<span data-type="title" id=toc-124> ليث بن أبي سليم بن زنيم - بالزاي والنون مصغرا - أبو بكر الكوفي</span>
روى عن طاوس ومجاهد وعطاء والشعبي وشهر بن حوشب وأبي إسحاق وغيرهم
وعنه عبد السلام بن حرب والثوري والحسن بن صالح وشعبة بن الحجاج وأبو بدر شجاع بن الوليد وآخرون
قال ابن سعد: كان ليث رجلا صالحا عابدا وكان ضعيفا في الحديث
وقال يحيى بن معين: ليس حديثه بذاك ضعيف
وقال أبو حاتم وأبو زرعة لا يشتغل به هو مضطرب الحديث
وقال الإمام أحمد مضطرب الحديث ولكن حدث الناس عنه
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'493',NULL,NULL,'0');
INSERT INTO [b309] VALUES (490,'وذكر ابن أبي حاتم عن ابن مهدي أنه قال: ليس أحسن حالا عندي من عطاء ابن السائب ويزيد بن أبي زياد
وذكره البخاري في الكبير ولم يذكر فيه جرحا ولا تعديلا
وقال ابن عدي: له أحاديث صالحة وقد روى عنه شعبة والثوري ومع الضعف الذي فيه يكتب حديثه
وقال الحافظ ابن حجر: صدوق اختلط أخيراً ولم يتميز حديثه فترك مات سنة إحدى أو اثنين وأربعين ومائة / خت م ٤',NULL,'494',NULL,NULL,'0');
INSERT INTO [b309] VALUES (491,'٣٥ -<span data-type="title" id=toc-125> محمد بن جابر بن سيار بن طارق الحنفي اليمامي أبو عبد الله أصله من الكوفة</span>
روى عن عمير بن سعيد النخعي وعطية العوفي وسماك بن حرب وغيرهم
وعنه الثوري وشعبة وابن عيينة وآخرون
قال البخاري في الضعفاء ليس بالقوي
وقال في التاريخ الصغير يتكلمون فيه
وقال يحيى بن معين: كان أعمى واختلط عيه حديثه كان
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'494',NULL,NULL,'0');
INSERT INTO [b309] VALUES (492,'كوفيا انتقل إلى اليمامة وهو ضعيف
وقال النسائي ضعيف
وتكلم فيه أبو حاتم وأبو زرعة وضعفاء
ونقل الحافظ عن ابن عدي أنه قال: روى عنه الكبار: أيوب وابن عون وسرد جماعة ثم قال: ولولا أنه في ذلك المحل لم يرو عنه هؤلاء وقد خالف في أحاديث ومع ما تكلم فيه من تكلم يكتب حديثه
وقال الحافظ في التقريب صدوق ذهبت كتبه فساء حفظه وخلط كثيرا وعمي فصار يلقن ورجحه أبو حاتم على ابن لهيعة من السابعة مات بعد السبعين / د ق',NULL,'495',NULL,NULL,'0');
INSERT INTO [b309] VALUES (493,'٣٦ -<span data-type="title" id=toc-126> محمد بن دينار الازدي ثم الطاحي بمهملتين أبو بكر ابن أبي الفرات البصري</span>
روى عن هشام بن عروة ويونس بن عبيد وسعيد بن إياس الجريري وإبراهيم الهجري وقرة بن خالد وغيرهم
وعنه عبد الصمد بن عبد الوارث وقتيبة بن سعيد وأبو داود الطيالسي ومعلى بن منصور ومسلم بن إبراهيم وآخرون
قال يحيى بن معين: ليس به بأس وكان على مسائل سوار العنبري ولم يكن له كتاب وقال مرة: ضعيف
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'495',NULL,NULL,'0');
INSERT INTO [b309] VALUES (494,'وقال أبو حاتم: لا بأس به وكذلك قال العجلي والنسائي
وقال أبو زرعة صدوق
وقال أبو داود تغير قبل أن يموت وقال في موضع آخر: كان ضعيف القول في القدر
وقال ابن عدي: ولمحمد بن دينار غير ما ذكرت وهو مع هذا كله حسن الحديث وعامة حديثه يتفرد به
وقال الذهبي في الكاشف حسنوا أمره
وقال الحافظ في التقريب: صدوق سيء الحفظ رمي بالقدر وتغير قبل موته من الثامنة / د ت',NULL,'496',NULL,NULL,'0');
INSERT INTO [b309] VALUES (495,'٣٧ -<span data-type="title" id=toc-127> الهيثم بن جميل - بفتح الجيم البغدادي أبو سهل نزيل أنطاكية</span>
روى عن مالك بن أنس وزهير بن معاوية والمبارك بن فضالة وجرير بن حازم وحماد بن سلمة والليث وغيرهم.
وعنه الإمام أحمد وعمرو الناقد وحسين بن الحسن
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'496',NULL,NULL,'0');
INSERT INTO [b309] VALUES (496,'المروزي والفضل بن يعقوب الرخامي والعباس بن عبد الله السندي ومحمد بن يحيى الذهلي وآخرون
قال ابن سعد: كان ثقة وذكر عن موسى بن داود أن الهيثم أفلس مرتين في طلب الحديث
ووثقه الإمام أحمد والعجلي والدارقطني أيضا
وقال ابن عدي: ليس بالحافظ يغلط على الثقات وأرجو أنه لا يتعمد الكذب
وقال الذهبي في الكاشف حجة صالح
وقال في المغني حافظ له مناكير وغرائب
وقال الحافظ في التقريب ثقة من أصحاب الحديث وكأنه ترك فتغير من صغار التاسعة
مات سنة ثلاث عشرة ومائتين / بخ قد عس ق',NULL,'497',NULL,NULL,'0');
INSERT INTO [b309] VALUES (497,'٣٨ -<span data-type="title" id=toc-128> وهيب - بالتصغير - ابن خالد بن عجلان الباهلي أبو بكر البصري</span>
روى عن ايوب السختياني ويونس بن عبيد وهشام بن عروة وابن جريج وسهيل بن أبي صالح وأبي حازم سلمة بن دينار وغيرهم
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الأول وفيه تراجم ثمان وثلاثين من المختلطين الثقات الذين لم يذكرهم المؤلف',NULL,'497',NULL,NULL,'0');
INSERT INTO [b309] VALUES (498,'وعنه إسماعيل بن علية وابن المبارك وابن مهدي ويحيى القطان وسليمان بن حرب وسهل بن بكار وآخرون
قال ابن سعد: كان وهيب قد سجن فذهب بصره وكان ثقة كثير الحديث حجة وكان أحفظ من أبي عوانة وكان يملي حفظا
وقال عبد الرحمن بن مهدي: كان من أبصر أصحابه بالحديث وبالرجال
وقال الإمام أحمد ليس به بأس
وقال أبو حاتم: ما أنقى حديثه وهيب لا تكاد تجده يحدث عن الضعفاء وهو الرابع من حفاظ البصرة وهو ثقة ويقال: إنه لم يكن بعد شعبة أعلم بالرجال منه ذهب بصره قبل أن يموت
وقال أبو داود: تغير وهيب بن خالد وكان ثقة
وقال الحافظ ابن حجر: ثقة ثبت لكنه تغير قليلا بآخرة من السابعة مات سنة خمس وستين ومائة وقيل بعدها / ع.',NULL,'498',NULL,NULL,'0');
INSERT INTO [b309] VALUES (499,'<span data-type="title" id=toc-129>الملحق الثاني</span>
وفيه تراجم ثلاثة عشر مختلطاً من الضعفاء',NULL,'499',NULL,NULL,'0');
INSERT INTO [b309] VALUES (500,'١ -<span data-type="title" id=toc-130> إسماعيل بن مسلم المكي أبو إسحاق البصري</span>
سكن مكة روى عن عامر بن وائلة والحسن البصري والشعبي وقتادة وغيرهم وعنه الأعمش وابن المبارك وعلي بن مسهر ومحمد بن عبد الله الأنصاري وعدة
قال البخاري تركه ابن المبارك وربما روى عنه وتركه يحيى وابن مهدي ونقل ابن أبي حاتم عن يحيى القطان قال لم يزل مختلطا كان يحدثنا بالحديث الواحد على ثلاثة ضروب
وقال الإمام أحمد منكر الحديث
وقال أبو حاتم ضعيف الحديث مخلط وقال ابن أبي حاتم قلت لأبي هو أحب إليك أو عمرو بن عبيد قال جميعا ضعيفين وإسماعيل هو ضعيف الحديث ليس بمتروك يكتب حديثه
وقال ابن عدي: أحاديثه غير محفوظة إلا أنه ممن يكتب حديثه
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الثاني وفيه تراجم ثلاثة عشر مختلطاً من الضعفاء',NULL,'499',NULL,NULL,'0');
INSERT INTO [b309] VALUES (501,'وقال الحافظ في التقريب كان من البصرة ثم سكن مكة كان فقيها ضعيف الحديث من الخامسة / ت ق
٢ -<span data-type="title" id=toc-131> عبد العزيز بن أبان بن محمد الأموي السعيدي أبو خالد الكوفي</span>
نزيل بغداد روى عن فطر بن خليفة وإبراهيم بن طهمان وجرير بن حازم والسفيانين وغيرهم. وعنه إبراهيم بن الحارث البغدادي وعلي بن محمد الطنافسي ويعقوب بن شيبة وغيرهم.
قال البخاري: تركه أحمد وزاد أبو حاتم عنه: وأسقطوا حديثه. وكذبه يحيى بن معين وضعفه أبو زرعة وغيره
وقال ابن حبانك كان ممن يأخذ كتب الناس فيرويها من غير سماع ويسرق الحديث ويأتي عن الثقات بالأشياء المعضلات تركه أحمد بن حنبل وكان شديد الحمل عليه
قال ابن سعد: كان قد ولي قضاء واسط ثم عزل فقدم بغداد فنزلها وتوفي في رجب سنة سبع ومائتين
وكان كثير الرواية عن سفيان ثم خلط بعد ذلك فأمسكوا عن حديثه
وقال الحافظ في التقريب متروك وكذبه ابن معين وغيره ... / ت.
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الثاني وفيه تراجم ثلاثة عشر مختلطاً من الضعفاء',NULL,'500',NULL,NULL,'0');
INSERT INTO [b309] VALUES (502,'٣ -<span data-type="title" id=toc-132> عبد الله بن جعفر بن نجيح السعدي مولاهم ابو جعفر المديني والد علي بن المديني</span>
روى عن عبد الله بن دينار والعلاء بن عبد الرحمن وزيد بن أسلم وغيرهم وعنه ابنه علي وزكريا بن يحيى وعلي بن الجعد وقتيبة بن سعيد وآخرون
قال ابن معين: ليس بشيء
وقال أبو حاتم: منكر الحديث جداً ضعيف الحديث يحدث عن الثقات بالمناكير يكتب حديثه ولا يحتج به كان علي لا يحدثنا عن أبيه وكان قوم يقولون: علي يعق أباه لا يحدث عنه فلما كان بأخرة حدث عنه.
وقال الساجي: قال ابن معينك كان من أهل الحديث ولكنه بلي في آخر عمره وقال ابن حبان كان ممن يهم في الأخبار حتى يأتي بها مقلوبة ويخطئ في الآثار حتى كأنها معمولة
ونقل ابن حبان عن ابن المديني تضعيف أبيه
وذكر الحافظ ابن حجر عن صالح بن محمد قال: سمعت ابن المديني يقول: أبي صدوق وهو أحب إلي من الدراوردي
قال البخاري: مات سنة ثمان وسبعين ومائة
وقال الحافظ في التقريب ضعيف من الثامنة يقال تغير حفظه بأخرة مات سنة ثمان وسبعين / ت ق
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الثاني وفيه تراجم ثلاثة عشر مختلطاً من الضعفاء',NULL,'501',NULL,NULL,'0');
INSERT INTO [b309] VALUES (503,'٤ -<span data-type="title" id=toc-133> عبد الله بن عبد العزيز بن عبد الله الليثي أبو عبد العزيز المدني</span>
روى عن الزهري وسعيد المقبري وعمرو بن مرداس وربيعة وغيرهم
وعنه إسماعيل بن عياش وسعيد بن منصور ويحيى بن عبد الله بن بكير وغيرهم
قال البخاري منكر الحديث وقال: قال إبراهيم بن منذر: حدثني أبو ضمرة
قال: كان عبد الله بن عبد العزيز قد خلط
وقال أبو حاتم: منكر الحديث ضعيف الحديث لا يشتغل بحديثه ليس في وزن من يشتغل بخطئه عامة حديثه خطأ لا أعلم له حديثا مستقيما يكتب حديثه
وقال أبو زرعة ليس بالقوي
وقال ابن حبان: كان ممن اختلط بأخرة حتى كان يقلب الأسانيد وهو لا يعلم ويرفع المراسيل من حيث لا يفهم فاستحق الترك وربما أدخل بينه وبين الزهري محمد بن عبد العزيز.
ونقل الحافظ ابن حجر عن محمد بن يحيى قال: في حديثه - يعني عن الزهري - نكارة وسألت سعيد بن منصور عنه فقال: كان مالك يرضاه وكان ثقة
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الثاني وفيه تراجم ثلاثة عشر مختلطاً من الضعفاء',NULL,'502',NULL,NULL,'0');
INSERT INTO [b309] VALUES (504,'وقال الساجي: يقال أنه خلط
وقال الحافظ في التقريب: ضعيف اختلط بأخرة من السابعة / ق
٥ -<span data-type="title" id=toc-134> عثمان بن عمير البجلي أبو اليقظان الكوفي ويقال ابن قبس ويقال ابن أبي حمير</span>
روى عن زيد بن وهب وأبي الطفيل وعدي بن ثابت وغيرهم
وعنه حصين بن عبد الرحمن وشعبة والثوري وآخرون
قال البخاري: كان يحيى وعبد الرحمن لا يحدثان عنه
وقال ابن معين: كوفي ليس حديثه بشيء
وكان أحمد يضعف حديثه كما نقل ذلك ابن أبي حاتم عن أبيه عنه
وقال أبو حاتم: ضعيف الحديث منكر الحديث كان شعبة لا يرضاه وذكر أنه حضره فروى عن شيخ فقال له شعبة: كم سنك؟ قال: كذا فإذا قد مات الشيخ وهو ابن سنتين
وقال ابن عدي: رديء المذهب يؤمن بالرجعة على أن الثقات قد رووا عنه مع ضعفه
وقال ابن حبان: كان ممن اختلط حتى لا يدري ما يحدث به فلا
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الثاني وفيه تراجم ثلاثة عشر مختلطاً من الضعفاء',NULL,'503',NULL,NULL,'0');
INSERT INTO [b309] VALUES (505,'يجوز الاحتجاج بخبره الذي وافق الثقات ولا الذي انفرد به عن الأثبات لاختلاط البعض بالبعض
وقال الحافظ في التقريب: ضعيف واختلط وكان يدلس ويغلو في التشيع من السابعة مات في حدود الخمسين ومائة / د ت ق',NULL,'504',NULL,NULL,'0');
INSERT INTO [b309] VALUES (506,'٦ -<span data-type="title" id=toc-135> المثنى بن الصباح اليماني الأبناوي أبو عبيد الله</span>
روى عن طاوس وعطاء بن ابي رباح وعمرو بن دينار وعمرو بن شعيب وعيرهم وعنه ابن المبارك وعيسى بن يونس وعبد الله بن رجاء المكي وعدة
قال البخاري: قال يحيى القطان: لم يترك المثنى من أجل عمرو بن شعيب
ونقل ابن أبي حاتم عن يحيى القطان قال: لم نتركه من أجل حديث عمرو بن شعيب ولكن كان اختلاطا منه في عطاء
وقال أحمد لا يساوي حديثه شيئا مضطرب الحديث
وقال ابن معين: ضعيف
وقال ابن حبان كان ممن اختلط في آخر عمره حتى كان لا يدري ما يحدث به فاختلط حديثه الذي فيه الأوهام والمناكير بحديثه العظيم (الصواب القديم) الذي فيه الأشياء المستقيمة عن أقوام مشاهير فبطل الاحتجاج به ونقل الحافظ
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الثاني وفيه تراجم ثلاثة عشر مختلطاً من الضعفاء',NULL,'504',NULL,NULL,'0');
INSERT INTO [b309] VALUES (507,'عن عبد الرزاق قال أدركته شيخا كبيرا بين اثنين يطوف الليل أجمع
وقال الحافظ في التقريب ضعيف اختلط بأخرة وكان عابداً من كبار السابعة مات سنة تسع وأربعين / د ت ق',NULL,'505',NULL,NULL,'0');
INSERT INTO [b309] VALUES (508,'٧ -<span data-type="title" id=toc-136> مجالد بن سعيد بن عمير الهمداني أبو عمرو ويقال أبو سعيد الكوفي</span>
روى عن الشعبي وزياد بن علاقة ومحمد بن بشر الهمداني وغيرهم
وعنه ابنه إسماعيل وجرير بن حازم والسفيانان وآخرون
قال ابن سعد كان ضعيفا في الحديث
ونقل ابن أبي حاتم عن ابن مهدي قال: حديث مجالد عند الأحداث يحيى بن سعيد وأبي أسامة ليس بشيء ولكن حديث شعبة وحماد بن زيد وهشيم هؤلاء القدماء قال أبو محمد يعني أنه تغير حفظه في آخر عمره
وقال أحمد: ليس بشيء يرفع حديثاً كثيراً لا يرفعه الناس وقد احتمله الناس
وقال ابن حبان: كان رديء الحفظ يقلب الأسانيد ويرفع
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الثاني وفيه تراجم ثلاثة عشر مختلطاً من الضعفاء',NULL,'505',NULL,NULL,'0');
INSERT INTO [b309] VALUES (509,'المراسيل لا يجوز الاحتجاج به
وقال العجلي جائز الحديث إلا أن ابن مهدي كان يقول: أشعث بن سوار كان أقرأ منه قال العجلي: بل مجالد أرفع من أشعث وكان يحيى بن سعيد يقول: كان مجالد يلقن في الحديث إذا لقن ذكر ذلك الحافظ في التهذيب.
وقال في التقريب ليس بالقوي وقد تغير في آخر عمره من صغار السادسة مات سنة أربع وأربعين.',NULL,'506',NULL,NULL,'0');
INSERT INTO [b309] VALUES (510,'٨ -<span data-type="title" id=toc-137> محمد بن معاوية بن أعين النيسابوري أبو علي</span>
سكن بغداد ثم مكة روى عن سليمان بن بلال وشريك القاضي ومحمد بن سلمة الحراني وغيرهم.
وعنه يحيى الحماني وموسى بن سهل الرملي وخلف بن عمرو العكبري وآخرون
قال أحمد: رأيت أحاديثه أحاديث موضوعة
وقال أبو زرعة: كان شيخا صالحا إلا أنه كلما لقن يلقن وكلما قيل إن هذ من حديثك حدث به يجيئه الرجل فيقول: هذا من حديث معلى الرازي وكنت أنت معه فيحدث بها على التوهم.
وقال ابن حبان: كان ممن ينفرد بالمناكير عن المشاهير ويأتي عن الثقات بما لا يتابع عليه فاستحق الترك إلا عند الاعتبار فيما
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الثاني وفيه تراجم ثلاثة عشر مختلطاً من الضعفاء',NULL,'506',NULL,NULL,'0');
INSERT INTO [b309] VALUES (511,'وافق الثقات لأنه كان صاحب حفظ وإتقان قبل أن يظهر منه ما ظهر..
وقال أبو حاتم: روى أحاديث لم يتابع عليها أحاديث منكرة فتغير حاله عند أهل الحديث.
وقال الحافظ في التقريب: متروك مع معرفته لأنه كان يتلقن وقد أطلق عليه ابن معين الكذب من العاشرة مات سنة تسع وعشرين ومائتين / تمييز',NULL,'507',NULL,NULL,'0');
INSERT INTO [b309] VALUES (512,'٩ -<span data-type="title" id=toc-138> مسلم بن كيسان الضبي الملائي البراد أبو عبد الله الكوفي الأعور</span>
روى عن أنس بن مالك ومجاهد وسعيد بن جبير وغيرهم
وعنه الأعمش ومحمد بن جحادة وشعبة وشريك وآخرون
قال البخاري: يتكلمون فيه
وقال أبو زرعة: كوفي ضعيف الحديث
وقال أبو حاتم: يتكلمون فيه وهو ضعيف الحديث
وقال ابن معين قال جرير: اختلط
وقال ابن حبان: اختلط في آخر عمره حتى كان لا يدري ما يحدث به فجعل يأتي بما لا أصل له عن الثقات فاختلط
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الثاني وفيه تراجم ثلاثة عشر مختلطاً من الضعفاء',NULL,'507',NULL,NULL,'0');
INSERT INTO [b309] VALUES (513,'حديثه ولم يتميز تركه أحمد بن حنبل ويحيى بن معين
وقال الحافظ في التقريب ضعيف من الخامسة / ت ق
١٠ -<span data-type="title" id=toc-139> نجيح بن عبد الرحمن السندي أبو معشر</span>
روى عن سعيد بن المسيب ومحمد بن كعب القرظي وهشام بن عروة وغيرهم
وعنه الثوري والليث بن سعد وابن مهدي وآخرون
قال البخاري: منكر الحديث ونقل عن ابن مهدي قال أبو معشر تعرف وتنكر
وقال أحمد كان صدوقا لكنه لا يقيم الإسناد ليس بذاك
وقال أيضا كان بصيرا بالمغازي
وقال أبو حاتم: ليس بالقوي في الحديث وقال أيضا كنت أهاب حديث أبي معشر حتى رأيت أحمد بن حنبل يحدث عن رجل عنه أحاديث فتوسعت بعد في كتابة حديثه ونقل عنه أيضا أنه قال صدوق
وقال أبو زرعة: صدوق في الحديث وليس بالقوي
وقال ابن حبان: كان ممن اختلط في آخر عمره وبقي قبل أن يموت سنتين في تغير شديد لا يدري ما يحدث به فكثر المناكير في روايته من قبل اختلاطه فبطل الاحتجاج به
وقال ابن حجر ضعيف من السادسة أسن واختلط
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الثاني وفيه تراجم ثلاثة عشر مختلطاً من الضعفاء',NULL,'508',NULL,NULL,'0');
INSERT INTO [b309] VALUES (514,'مات سنة سبعين ومائة / ٤
١١ -<span data-type="title" id=toc-140> يحبى بن محمد بن عباد بن هانئ المدني الشجري</span>
روى عن مالك وابن اسحاق وموسى بن عقبة وغيرهم
وعنه ابنه إبراهيم وعبد الجبار بن سعيد ومحمد بن المنذر وغيرهم
لم يذكر في البخاري جرحا ولا تعديلا
وقال ابو حاتم ضعيف الحديث
ونقل الذهبي في الميزان عن العقيلي قال: في حديثه مناكير وأغاليط وكان ضريرا فيما بلغني يلقن
وقال الحافظ ابن حجر ضعيف وكان ضريراً يتلقن من التاسعة
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الثاني وفيه تراجم ثلاثة عشر مختلطاً من الضعفاء',NULL,'509',NULL,NULL,'0');
INSERT INTO [b309] VALUES (515,'١٢ -<span data-type="title" id=toc-141> يزيد بن أبي زياد القرشي الهاشمي أبو عبد الله الكوفي</span>
روى عن إبراهيم النخعي وعبد الرحمن بن أبي ليلى ومجاهد وعكرمة وغيرهم
وعنه زائدة وشعبة وزهير بن معاوية وآخرون
قال ابن سعد كان ثقة في نفسه إلا أنه اختلط في آخر عمره
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الثاني وفيه تراجم ثلاثة عشر مختلطاً من الضعفاء',NULL,'509',NULL,NULL,'0');
INSERT INTO [b309] VALUES (516,'فجاء بالعجائب
ونقل البخاري عن جرير قال كان يزيد بن أبي زياد أحسن حفظا من عطاء بن السائب
وقال أبو حاتم ليس بالقوي
وقال أبو زرعة كوفي لين يكتب حديثه ولا يحتج به
وقال العجلي جائز الحديث وكان بأخرة يلقن وأخوه برد بن أبي زياد ثقة وهو أرفع من أخيه يزيد
وقال ابن حبان: كان صدوقا إلا أنه لما كبر ساء حفظه وتغير، فكان يتلقن ما لقن فوقع المناكير في حديثه من تلقن غيره إياه وإجابته فيما ليس من حديثه لسوء حفظه فسماع من سمع منه قبل دخوله الكوفة في أول عمره سماع صحيح وسماع من سمع منه في آخر قدومه الكوفة بعد تغير حفظه وتلقنه ما يلقن سماع ليس يشيء
وقال ابن حجر: ضعيف كبر فتغير صار يتلقن وكان شيعيا من الخامسة مات سنة ست وثلاثين / خت م',NULL,'510',NULL,NULL,'0');
INSERT INTO [b309] VALUES (517,'١٣ -<span data-type="title" id=toc-142> أبو بكر بن عبد الله بن أبي مريم الغساني الشامي</span>
وقد ينسب إلى جده قيل اسمه بكير وقيل عبد السلام
روى عن أبيه وراشد بن سعد وضمرة بن حبيب
وعنه عبد الله بن المبارك وعيسى بن يونس وأبو اليمان وغيرهم
_________
<span data-type="title">[تعليق مُعِدّ الكتاب للشاملة]</span>
 قال المحقق: الملحق الثاني وفيه تراجم ثلاثة عشر مختلطاً من الضعفاء',NULL,'510',NULL,NULL,'0');
INSERT INTO [b309] VALUES (518,'قال أحمد ضعيف كان عيسى لا يرضاه وضعفه أبو زرعة وابن معين
وقال أبو حاتم ضعيف الحديث طرقته لصوص فأخذوا متاعه فاختلط
وقال ابن حيان: كان من خير أهل الشام ولكنه كانه رديء الحفظ يحدث بالشيء ويهم فيه لم يفحش ذلك منه حى استحق الترك ولا سلك سنن الثقات حتى صار يحتج به فهو عندي ساقط الاحتجاج إذا انفرد
وقال ابن عدي: الغالب على حديثه الغرائب وقلما يوافقه الثقات
وقال الدارقطني متروك
قال الحافظ في التقريب: ضعيف وكان قد سرق بيته فاختلط من السابعة مات سنة ست وخمسين',NULL,'511',NULL,NULL,'0');
