INSERT INTO [b31061] VALUES (1089,'١٥٤ - من صفات الداعية: تذكر النعم والاعتراف بالتقصير ٧٤١ ١٥٥ من صفات الداعية: الصوت الجهوري عند الحاجة ٧٤٨ ١٥٦ أهمية حكم العالم برضى الخصمين ٧٥٤ ١٥٧ أهمية اخذ الداعية بالشدة والقوة عند الحاجة أو المصلحة ٧٦٥ ١٥٨ استجابة دعاء الداعية وإكرامه حيا وميتا ٧٦٩ ١٥٩ من صفات الداعية: طاعة ولي أمر المسلمين بالمعروف ٧٧٨ ١٦٠ من وظائف الإمام المسلم: قتل الجاسوس الحربي الكافر ٧٧٨ ١٦١ من آداب الداعية: الثناء على الله بما هو أهله ٧٨١، ٨٥٣ ١٦٢ من صفات الداعية: إكرام العلماء والدعاة ٨١٥ ١٦٣ من صفات الداعية: القوة وجودة النظر ٧٩٢ ١٦٤ من صفات الداعية: تعجيل المعروف وتحقيره ٨٢١ ١٦٥ من آداب الداعية: تطييب الطعام وتعظيمه ٨٢٢ ١٦٦ أهمية زيارة العلماء للاستفادة من علمهم ٨٣٧ ١٦٧ من صفات الداعية: الاعتزاز بما يقع من إكرام الشرع ٨٤٣ ١٦٨ من صفات الداعية: الحذر من مخالفة أمر رسول الله صلي الله عليه وسلم ٨٥٤ ١٦٩ من صفات الداعية: الرجوع إلى الحق بدليله ٨٥٥ ١٧٠ من صفات الداعية: الخشوع لله عز وجل ٨٥٦ ١٧١ أهمية الاعتراف بالفضل لأهله ٨٥٦ ١٧٢ من صفات الداعية: سلامة القلب وحفظ اللسان ٨٧٣ ١٧٣ أهمية تربية الأبناء على الأمور المهمة ٨٧٧ ١٧٤ أهمية تفريغ قلب الداعية من المشاغل الدنيوية ٨٩٣ ١٧٥ من صفات الداعية: الثقة بالله عز وجل ٨٩٨، ١٠٦٤ ١٧٦ من صفات الداعية العفة وقوة النفس ٩٠١ ١٧٧ أهمية الكف عما جرى بين الصحابة رضي الله عنهم ٩٠٧ ١٧٨ من وظائف الداعية الدفاع عن أئمة الهدى والتماس العذر لهم ٩٠٦ ١٧٩ أهمية الحنث في اليمين والتكفير عنها للمصلحة الراجحة ٩١٤ ١٨٠ أهمية الاستثناء في اليمين ٩١٦ ١٨١ من آداب الداعية: إكرام الضيف ٩١٧ ١٨٢ من صفات الداعية: الفرح بنعمة الله عز وجل والتحدث بها ٩٢٦ ١٨٣ من صفات الداعية: المكافأة على المعروف ٩٣٤، ٩٣٩','2','1093',NULL,NULL,'0');
INSERT INTO [b31061] VALUES (1090,'١٨٤ - أهمية الغضب لله ولرسوله في حدود الحكمة ٩٤٦ ١٨٥ أهمية عدم احتقار الصغير في الأمور المهمة ٩٤٨ ١٨٦ حسن أدب الأنصار مع رسول الله صلي الله عليه وسلم ٩٥٧ ١٨٧ من صفات الداعية: دفع السيئة بالحسنة ٩٦٤ ١٨٨ من صفات الداعية: الإعراض عن الجاهلين ٩٦٥، ٩٧١ ١٨٩ مكانة الصحابيات وصبرهم على خدمة الأزواج ٩٧٨ ١٩٠ أهمية الحياء وعظم منزلته ٩٧٨، ٩٨٩ ١٩١ من صفات الداعية: الغيرة ٩٨٠ ١٩٢ من صفات الداعية: الحرص على صلة الأرحام ٩٨١ ١٩٣ من صفات الداعية: توقير النبي صلي الله عليه وسلم وإجلاله ٩٨٨ ١٩٤ أهمية تفقد الإمام أحوال رعيته ومراقبتهم ٩٩٢ ١٩٥ من صفات الداعية: الانقياد للدليل الشرعي والعمل به ٩٩٩ ١٩٦ من صفات الداعية: الإلحاح في الدعاء ١٠٣٦ ١٩٧ من أسباب تحصيل العلم: زيارة العلماء ١٠٤٧ ١٩٨ منزلة أبي بكر العظيمة رضي الله عنه ومؤازرته لرسول الله صلي الله عليه وسلم ١٠٦٤ ١٩٩ أهمية الانقياد والتسليم لأمر رسول الله صلي الله عليه وسلم ١٠٦٥ ٢٠٠ من صفات الداعية: الحرص على الإصلاح بين لناس ١٠٧٠ ٢٠١ أهمية الوفاء ببيعة الإمام المسلم ١٠٧٩
وعلى ضوء ما تقدم من الفوائد الدعوية في الجدول السابق يظهر أن المنهج الدعوي المتعلق بالداعية على النحو الآتي:
أولا: كل مسلم دل على خير، أو حذر عن شر، فهو داعية؛ لأن الدعوة تجب على كل أحد بحسبه، فيدخل في لفظ الداعي كل مسلم دل على خير أو حث عليه، أو حذر من شر، أو نفر عنه.
ثانيا: الداعية ينجح في دعوته بإذن الله عز وجل بصفات، منها:
١ - العلم (١).
_________
(١) انظر من أمثلة ذلك: ص ٧١، ١٦٩، ٢٢٠، ٣٤٧، ٣٤٨ من هذا البحث.','2','1094',NULL,NULL,'0');
INSERT INTO [b31061] VALUES (1091,'٢ - الرفق (١).
٣ - الصبر (٢).
قال شيخ الإسلام ابن تيمية - رحمه الله -: ((فلا بد من هذه الثلاثة: العلم، والرفق، والصبر، العلم قبل الأمر والنهي، والرفق معه، والصبر بعده، فإن كان كل من هذه الثلاثة مستصحبا في هذه الأحوال)) (٣).
٤ - الإخلاص (٤).
٥ - موافقة القول للعمل (٥).
٦ - مراعاة أحوال المدعوين (٦).
ثالثا: يزيد نشاط الداعية بصفات، منها:
١ - قوة الإيمان (٧).
٢ - محبة الله ورسوله صلى الله عليه وسلم (٨).
٣ - الرغبة فيما عند الله عز وجل (٩).
٤ - الغيرة لله عز وجل (١٠).
٥ - قوة اليقين والثقة بالله عز وجل (١١).
٦ - الحذر من مخالفة أمر رسول الله صلى الله عليه وسلم (١٢).
_________
(١) انظر من أمثلة ذلك: ص ٤٦١، ٥١٢ من هذا البحث.
(٢) انظر من أمثلة ذلك: ص ٥٦١، ٥٦٤، ٥٦٧.
(٣) الحسبة في الإسلام: ص ٤٨، ومجموع الفتاوى، ٢٨/ ١٦٧، ٣٢٢، ٣٤٦، ٤٩٥، ٥٣٠.
(٤) انظر من أمثلة ذلك: ص ٣٠٣، ٣٣٦، ٤١٢.
(٥) انظر من أمثلة ذلك ٦٣، ٩٣، ١١٦، ١٤٠.
(٦) انظر من أمثلة ذلك: ٣٢٣.
(٧) انظر من أمثلة ذلك: ١٠٨، ٤٨٠.
(٨) انظر من أمثلة ذلك: ١٠٨، ٥٣٦، ٥٩١.
(٩) انظر من أمثلة ذلك ١٣٥، ١٤٨، ١٧٦.
