INSERT INTO [b31061] VALUES (1101,'<span data-type="title" id=toc-347>[الفصل الثالث المنهج الدعوي المتعلق بموضوع الدعوة]</span>','2','1105',NULL,NULL,'0');
INSERT INTO [b31061] VALUES (1102,'موضوع الدعوة: لا شك أن الدعوة إلى الله عز وجل هي: الدعوة إلى لإيمان به، وبما جاءت به رسله - عليهم الصلاة والسلام - بتصديقهم بما أخبروا به، وطاعتهم فيما أمروا به (١) وذكر شيخ الإسلام ابن تيمية "رحمه الله -: أن الدعوة إلى الله تتضمن الأمر بكل ما أمر الله به، والنهي عن كل ما نهى الله عنه، وهذا هو الأمر بكل معروف، والنهي عن كل منكر (٢) فظهر من هذا التعريف أن موضوع الدعوة: الدعوة إلى الخير، والحث عليه، والتحذير من الشر، والتنفير منه. قال الله عز وجل: {وَلْتَكُنْ مِنْكُمْ أُمَّةٌ يَدْعُونَ إِلَى الْخَيْرِ وَيَأْمُرُونَ بِالْمَعْرُوفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَأُولَئِكَ هُمُ الْمُفْلِحُونَ} [آل عمران: ١٠٤] (٣).
وعلى ضوء هذا التعريف استخلصت الفوائد الدعوية المتعلقة بموضوع الدعوة على النحو الآتي:
م الدرس الصفحة ١ الحديث عن حقوق العباد ٥٥ ٢ رفع الحرج عن الأمة ٥٣، ٢٢٩، ٢٨٤، ٣٠١، ٩٨٨ ٣ أهمية الوقف في العمل الدعوي ٦٠ ٤ الحث على الجهاد والإعداد له وبيان أهميته ٥٩، ١٦٠، ١٨٣، ١٩٢، ٢٣٤، ٣١٥، ٣٢٢، ٣٢٩، ٣٣٥، ٣٣٩، ٣٦٣، ٤٥٣، ٤٥٧، ٤٥٩، ٤٦٨، ٤٧٧، ٤٨١، ٥٠٩، ٥٤٥، ٥٧٢، ٧٢٩، ٧٩٦، ٨٣٨، ١٠١٨ ٥ الحث على العلم والعمل بالكتاب والسنة ٦٢ ٦ الرد على الفرق الضالة ٦٦ ٧ تبليغ العلم النافع ٧٤ ٨ بيان الناسخ والمنسوخ ٧٦، ١٢٢
_________
(١) انظر: مجموع فتاوى شيخ الإسلام ابن تيمية: ١٥/ ١٥٧.
(٢) المرجع السابق: ١٥/ ١٦١.
(٣) سورة آل عمران: الآية ١٠٤.','2','1106',NULL,NULL,'0');
INSERT INTO [b31061] VALUES (1103,'٩ - عناية الإسلام بحقوق الإنسان ٧٥، ١٥٤ ١٠ التحذير من المعاصي وبيان خطرها ١١٧ ١١ التحذير من السبع المهلكات ١٣١ ١٢ إنكار الغيبة وردها ١١٨ ١٣ الحث على الإحسان إلى الأقرباء واليتامى والمساكين ١٢١ ١٤ الحث على الإحسان إلى الوالدين بعد موتهما ٩٢، ١٢٦ ١٥ الحث على أداء الزكاة ١٢٨ ١٦ الحث على الإنفاق والصدقات في وجوه الخير ١٤٧ ١٧ الحث على الوصية عند الموت ١٥٣ ١٨ من أعلام النبوة الإخبار بالمغيبات ١٧٥، ١٩٥، ٣٣١، ٤٢٩، ٤٤١، ٤٩٩، ٥٠٢، ٥٣٥، ٥٩١، ٦٠٥، ٦٤٣، ٦٨١، ٧٣٤، ٧٥١، ٧٦٩، ٧٩٩، ٨٠٢، ٨٦٤، ٩٥٦، ١٠٠٨، ١٠١٣، ١٠٣٢، ١٠٤٩، ١٠٥٤، ١٠٧٠ ١٩ الحث على العمل بأصول الإيمان ١٨١، ٥٧١ ٢٠ الحث على العمل بأصول الإسلام ١٨٢، ٥٧١ ٢١ الحث على الدعاء ١٨٥، ٢٣٥، ٥٠٩ ٢٢ تاريخ الدعوة في تحريم الخمر ٢٣٠ ٢٣ تعليم المدعوين: الدعاء والأذكار ٢٦٠ ,٨٨٠ ٢٤ الحث على سلوك الأدب وتعلي المدعوين ما يحتاجون إليه ٢٣٣، ٥٠٩، ٧٢٩ ٢٥ الحث على مكارم الأخلاق ٢٥١، ٦١٨ ٢٦ إثبات صفات الكمال لله عز وجل ٢٦٥، ٦٤١، ٦٨٨، ٧٨٣ ٢٧ الحث على التوبة النصوح ٢٦٨ ٢٨ من خصائص الإسلام: شهداء غير المعركة ٢٧٧ ٢٩ الحث على اخذ الحذر والأهبة لصد أعداء الإسلام ٢٩٠ ٣٠ الحث على صيام التطوع ٣٠٢ ٣١ الحث على تجهيز وإعداد الدعاة والغزاة في سبيل الله عز وجل ٣٠٧ ٣٢ من خصائص الإسلام: البقاء إلى يوم القيامة ٣٣١، ٤٩٨ ٣٣ الدعوة إلى التوحيد والتحذير من الشرك ٣٥٢ ٣٤ الحض على الطاعات واجتناب المعاصي ٣٤٩ ٣٥ التحذير من الاتكال ٣٥٤ ٣٦ الحث على الثبات في الجهاد في سبيل الله عز وجل ٣٦٧ ٣٧ الحث على التواضع والتحذير من الكبر ٣٧٩','2','1107',NULL,NULL,'0');
INSERT INTO [b31061] VALUES (1104,'٣٨ - تاريخ الدعوة في الأمر بالحجاب ٣٨٨ ٣٩ التحذير من إرادة الدنيا دون الآخرة ٤١٠ ٤٠ الحث على العناية بالفقراء والضعفاء ٤٢٣ ٤١ بيان فضل السلف الصالح للتأسي بهم ٤٣٠ ٤٢ الإيمان بالقدر والعمل بأسباب النجاة ٤٣٤، ٧٩٨ ٤٣ التحذير من الاغترار بالأعمال ٤٣٨، ٧٩٨ ٤٤ الحث على النية الصالحة ٤٤٢، ٦٥١، ٧٩٩، ٨٣٨ ٤٥ الحث على طلب حسن الخاتمة بالقول والفعل ٤٤٣، ٧٩٩ ٤٦ الحث على الرمي والترغيب فيه ٤٧٧ ٤٧ من معجزات النبي صلي الله عليه وسلم: ثبات القلب وعدم الخوف والجزع ٤٨٧ ٤٨ الحث على الالتجاء إلى الله عز وجل والإلحاح في الدعاء ٤٩٠ ٤٩ من خصائص الإسلام: يسر الدين وسماحة الشريعة ٣٠١، ٤٩٤، ٩٨٨ ٥٠ من تاريخ الدعوة: ذكر غزوة الأحزاب ٥٠٧ ٥١ الحث على أداء الصلاة في وقتها ٥٠٥ ٥٢ الحض على لين الجانب بالقول والفعل ٥١٢ ٥٣ الدعوة إلى كلمة التوحيد ٥٢١، ٥٣٨، ٥٤٢ ٥٤ من معجزات النبي صلي الله عليه وسلم: إجابة دعوته ٥٣٢، ٧١٦، ٧٣٥، ٨٣٠ ٥٥ الحث على نشر العلم، وتعليم الناس الخير ٥٣٨ ٥٦ من معجزات النبي صلي الله عليه وسلم شفاء المرضى على يديه ٥٣٥، ٧٢٧ ٥٧ التحذير من التعذيب بعذاب الله عز وجل ٥٤٧، ٧٠١ ٥٨ الحض على طاعة ولاة الأمر بالمعروف ٥٥١، ٥٥٦ ٥٩ الحث على طاعة الله ورسوله صلي الله عليه وسلم ٥٥٥ ٦٠ البيان ببقاء الهجرة من بلاد الشرك إلى بلاد الإسلام ٥٧٣، ٨٣٩ ٦١ من معجزات النبي صلي الله عليه وسلم: الإخبار ببقاء مكة دار إسلام ٥٧٣، ٨٣٩ ٦٢ أهمية الحراسة في الأمور المهمة ٥٩٦ ٦٣ تعظيم الكعبة وبيت الله الحرام ٥٩٩ ٦٤ بيان خصائص النبي صلي الله عليه وسلم ٦٠٢ ٦٥ الحث على حسن التفهم لمعاني جوامع الكلم ٦٠٥ ٦٦ الحض على إعانة العلماء والمجاهدين وشد أزرهم ٦١٠ ٦٧ من تاريخ الدعوة: ذكر الهجرة ٦١٣ ٦٨ الحض على تعظيم القرآن الكريم ٦٣٥','2','1108',NULL,NULL,'0');
INSERT INTO [b31061] VALUES (1105,'٦٤٤ - ٦٩ الحث على ذكر الله عز وجل ٦٤٤ ٧٠ الحض على خفض الصوت بالذكر إلا ما شرع الجهر به ٦٤٤ ٧١ بيان آداب السفر ٦٥٤ ٧٢ الحض على بر الوالدين ٦٦١ ٧٣ التحذير من الشرك ووسائله ٦٧٠ ٧٤ التحذير من الخيانة لله ولرسوله صلي الله عليه وسلم ٦٧٩ ٧٥ الولاء والبراء ٦٨٥ ٧٦ من خصائص الإسلام الخيرية ٦٩١ ٧٧ الحض على إبطال عادات الجاهلية ٦٩٥ ٧٨ تحذير المجاهدين عن قتل النساء والصبيان ٦٩٨ ٧٩ التحذير من مؤاخذة أحد بذنب غيره ٧٠٨ ٨٠ لحض على إزالة الشركيات ٧١٢ ٨١ التحريض على خداع الكفار في الحرب ٧٣١ ٨٢ الحث على اخذ الحذر والحيطة في الحروب ٧٣٣ ٨٣ التحذير من معصية النبي صلي الله عليه وسلم وبيان خطرها ٧٣٩ ٨٤ من تاريخ الدعوة: ذكر عزوة أحد ٧٤٤ ٨٥ الحث على العفو والرفق وتسهيل الأمور ٧٤٩ ٨٦ من معجزات النبي صلي الله عليه وسلم ظهور الكرامات لأتباعه ٧٦٨ ٨٧ الحث على تخلص أسرى المسلمين من أعداء الإسلام ٧٧٢ ٨٨ الحض على إطعام الطعام ٧٧٢ ٨٩ الحث على عيادة المرضى ٧٧٤ ٩٠ الحض على إجابة الدعوة ٧٧٤ ٩١ التحذير من فتنة الدجال ٧٨٢ ٩٢ التحذير من الظلم ٧٨٧، ١٠٥٤ ٩٣ الحث على الشفقة والرحمة بالمسلمين ٧٩٢ ٩٤ التحذير من الإعجاب بالكثرة ٧٩٦ ٩٥ بيان عذاب القبر ونعيمه ٨٠٢ ٩٦ الحث على الإيثار ٨١٦ ٩٧ من معجزات النبي صلي الله عليه وسلم تكثير الطعام ٨٢٢ ٩٨ التحذير من الغلول ٨٣٢ ٩٩ الحث على الإحسان إلى الأيتام ١٣٦، ٨٤١','2','1109',NULL,NULL,'0');
INSERT INTO [b31061] VALUES (1106,'١٠٠ - التحذير من الفتن ٦٨٥، ٩٣٥ ١٠١ الحض على النصيحة بالحكم ٨٧٤ ١٠٢ الحض على احترام النبي صلي الله عليه وسلم وتوقيره وتعزيزه ٨٨٤ ١٠٣ الحث على اختيار التسمية بالأسماء الحسنة ٨٨٦ ١٠٤ التحذير من صرف الأموال في الباطل ٨٨٨ ١٠٥ من خصائص النبي صلي الله عليه وسلم وأمته: حل الغنائم ٦٠٢، ٨٩٤ ١٠٦ من معجزات النبوة: حبس الشمس واستجابة الدعاء ٨٩٤ ١٠٧ من تاريخ الدعوة: ذكر غزوة تبوك ٩١٨، ١٠٤٧ ١٠٨ من تاريخ الدعوة: ذكر موت البدريين وأهل الحديبية ٩٣٦ ١٠٩ من تاريخ الدعوة: معرفة زمن وقعة بدر ٩٤٤ ١١٠ من تاريخ الدعوة: ذكر غزوة الفتح وحنين ٥٧٧، ٩٥٤، ٩٧٤ ١١١ الحض على الصبر ٩٥٧ ١١٢ من تاريخ الدعوة: ذكر غزوة خيبر ٩٩١، ١٠٢٩ ١١٣ بيان تحريم الحمر الأهلية ٩٩٤ ١١٤ إنكار المنكر إذا ظهر فعله ٩٩٨ ١١٥ التحذير من التنافس في الدنيا والانشغال بها ١٠٠٤ ١١٦ التحذير من قتل أهل الذمة بغير حق ١٠٢٠ ١١٧ من سماحة الإسلام: حفظه لحرمة العهد والميثاق ١٠٢١ ١١٨ الحض على الدخول في الإسلام ١٠٢٣ ١١٩ الحض على إخراج المشركين من جزيرة العرب ١٠٢٤ ١٢٠ من معجزات الرسول صلي الله عليه وسلم عصمته فيما يبلغه وإخباره بمكان السحر ١٠٣٨ ١٢١ أهمية الأخذ بالأسباب وأنها لا تنافي التوكل ١٠٤٠ ١٢٢ التحذير من السحر وبيان خطره ١٠٤٢ ١٢٣ بيان علامات الساعة ١٠٤٨ ١٢٤ الحض على الوفاء بالعهد ١٠٥٣ ١٢٥ الحض على الأخذ بالكتاب والسنة ١٠٥٩ ١٢٦ من تاريخ الدعوة: ذكر يوم أبي الجندل ١٠٦٢ ١٢٧ الحث على التثبت ١٠٦٧ ١٢٨ التحذير من الغدر ١٠٧٤ ١٢٩ الأصل في الأشياء الإباحة ٩٩١','2','1110',NULL,NULL,'0');
INSERT INTO [b31061] VALUES (1107,'وعلى ضوء ما تقدم من الفوائد الدعوية في الجدول السابق يتضح أن المنهج الدعوي المتعلق بموضوع الدعوة على النحو الآتي:
أولا: موضوع الدعوة: هو: الدعوة إلى كل خير، والحث عليه، والتحذير من كل شر، والتنفير منه.
ثانيا: تقدم موضوعات الدعوة على حسب أهميتها، فيقدم منها ما يتعلق بأركان الإيمان، ثم ما يتعلق بأركان الإسلام، ثم ما يتعلق بالتحذير من كبائر الذنوب، والحض على عمل الواجبات، ثم ما يتعلق بالترهيب من المعاصي، والترغيب في الطاعات، ومن هذه الأصول ما يأتي:
١ - الدعوة إلى كلمة التوحيد ((شهادة أن لا إله إلا الله وأن محمدا رسول الله صلى الله عليه وسلم))، وبيان معنى هذه الكلمة، ومقتضاها، وأركانها، وشروطها، وفضلها، ونواقضها، ونواقصها؛ فإن ذلك كله هو أساس الإسلام (١).
٢ - بيان أنواع التوحيد، وفضله، وحث الناس، وترغيبهم في العناية به عناية دقيقة فائقة (٢).
٣ - التحذير من الشرك: كبيره، وصغيره، وبيان أنواعه، ووسائله؛ لأن الشرك أعظم الذنوب، وأشنعها (٣).
٤ - أهمية بيان صفات الكمال لله، وإثبات ما أثبته الله عز وجل لنفسه، وما أثبته له رسوله صلى الله عليه وسلم من غير تحريف، ولا تعطيل، ولا تكييف، ولا تمثيل (٤).
٥ - بيان وتوضيح أصول الإيمان، وأصول الإسلام، والإحسان،
_________
(١) انظر من أمثلة ذلك: ص ٥٢١، ٥٣٨، ٥٤٢ من هذا البحث.
(٢) انظر مثال ذلك: ص ٣٥٢.
(٣) انظر من أمثلة ذلك: ص ٣٥٢، ٦٧٠.
(٤) انظر من أمثلة ذلك: ص ٢٦٥، ٦٤١، ٦٨٨، ٧٨٣.','2','1111',NULL,NULL,'0');
INSERT INTO [b31061] VALUES (1108,'بالتفصيل والإيضاح؛ لأهمية ذلك، وحاجة الناس إليه (١).
٦ - التحذير من كبائر الذنوب: كالسبع الموبقات، وغيرها من المهلكات، والحض على القيام بجميع الواجبات (٢).
٧ - الحث على جميع أنواع الطاعات، والتنفير عن جميع أنواع المعاصي والسيئات (٣).
ثالثا: العناية باختيار موضوعات الدعوة على حسب أحوال المدعوين، فيقدم لكل فئة ما يحتاجون إليه، ويراعى في عرض هذه الموضوعات ما يأتي:
١ - الأصل في الأشياء الإباحة؛ لأن الله خلق لعباده جميع ما على الأرض لينتفعوا به، إلا ما حرمه سبحانه وتعالى (٤).
٢ - الأصل في العبادات الحظر والتوقيف إلا ما ثبت في الشرع تشريعه عن الله عز وجل، أو عن رسوله صلى الله عليه وسلم (٥).
٣ - تقديم أعلى المصالح عند تعارض عدد المصالح (٦).
٤ - تقديم أدنى المفاسد عند تعارض عدد المفاسد (٧).
٥ - درء المفاسد مقدم على جلب المصالح (٨).
رابعا: الإسلام له خصائص ينبغي بيانها للناس؛ لما في ذلك من المصالح، ومن ذلك ما يأتي:
_________
(١) انظر من أمثلة ذلك: ص ١٨١، ١٨٢، ٥٧١ من هذا البحث.
(٢) انظر من أمثلة ذلك: ص ١٣١، ٦٧٩، ٧٨٧، ٨٣٢، ١٠٧٤.
(٣) انظر من أمثلة ذلك: ص ١١٧، ٣٤٩، ٦٦١، ٨٨٨، ١٠٥٣.
(٤) انظر مثال ذلك: ص ٩٩١.
(٥) انظر مثال ذلك: ص ٩٩١.
(٦) انظر مثال ذلك: ص ٢٧٥.
(٧) انظر من أمثلة ذلك: ص ٩٣١، ٩٧٥، ١٠٣٧.
(٨) انظر من أمثلة ذلك: ص ٩٣١، ٩٧٥، ١٠٣٧.','2','1112',NULL,NULL,'0');
INSERT INTO [b31061] VALUES (1109,'١ - العموم لجميع الإنس والجن (١).
٢ - البقاء إلى قيام الساعة (٢).
٣ - اليسر والسماحة (٣).
٤ - رفع الحرص عن الأئمة (٤).
٥ - العناية بحقوق الإنسان (٥).
٦ - حفظ العهود والمواثيق (٦).
٧ - الإسلام خير الأديان، وأمته خير الأمم (٧).
خامسا: الدلائل النبوية توضح الحق، وتزيد الإيمان، وتزيل الشبه، فينبغي توضيحها للناس عند الحاجة لذلك؛ لما فيها من الفوائد الكثيرة، والمنافع المتعددة (٨).
وإذا قدمت الموضوعات الدعوية على حسب القواعد، والضوابط، والأحوال، والفئات، والأهمية، فإنها ستثمر، وتُنتج، وتنجح بإذن الله عز وجل.
_________
(١) انظر من أمثلة ذلك: ص ٦٠٢، ٨٩٤ من هذا البحث.
(٢) انظر من أمثلة ذلك: ص ٣٣١، ٤٩٨.
(٣) انظر من أمثلة ذلك: ص ٣٠١، ٤٩٤، ٩٨٨.
(٤) انظر من أمثلة ذلك: ص ٥٣، ٢٢٩، ٢٨٤، ٣٠١، ٩٨٨.
(٥) انظر من أمثلة ذلك: ص ٧٥، ١٥٤.
(٦) انظر مثال ذلك: ص ١٠٢١.
(٧) انظر مثال ذلك: ص ٦٩١.
(٨) انظر أمثلة ذلك: ص ٤٨٧، ٥٣٢، ٥٣٥، ٥٧٣، ٧٦٨، ٨٢٢.','2','1113',NULL,NULL,'0');
INSERT INTO [b31061] VALUES (1110,'<span data-type="title" id=toc-348>الفصل الرابع: المنهج الدعوي المتعلق بالوسائل والأساليب</span>
المبحث الأول: المنهج الدعوي المتعلق بالوسائل
المبحث الثاني: المنهج الدعوي المتعلق بالأساليب','2','1115',NULL,NULL,'0');
INSERT INTO [b31061] VALUES (1111,'<span data-type="title" id=toc-349>المبحث الأول: المنهج الدعوي المتعلق بالوسائل</span>
الوسيلة في الأصل: ما يتوصل به إلى الشيء (١) ووسائل الدعوة: هي ما يتوصل به الداعية إلى تبليغ دعوته من أشياء وأمور (٢) وقيل: ((ما يتوصل به الداعية إلى تطبيق مناهج الدعوة من أمور معنوية، أو مادية)) (٣) قال الإمام ابن كثير - رحمه الله -: ((والوسيلة هي التي يتوصل بها إلى تحصيل المقصود)) (٤) فظهر من هذه التعريفات والحدود أن الوسيلة في الدعوة إلى الله عز وجل هي: ما يستعمله الداعية من أمور حسية، أو معنوية ينقل بها دعوته إلى المدعوين.
وعلى ضوء هذا التعريف استخلصت الفوائد الدعوية المتعلقة بالوسائل على النحو الآتي:
م الدرس الصفحة ١ القول اعظم وسائل الدعوة ٥٤ ٢ القدوة الحسنة ٦٣، ٩٣، ١١٦، ١٢٧، ١٤٠، ١٥٠، ٢٥٠، ٢٥٩، ٢٦٣، ٢٩٣، ٣١١، ٣١٨، ٣٣٤، ٣٧٥، ٣٨٢، ٤٠٧، ٤٥٥، ٤٧٣، ٤٨٠، ٥٢٢، ٥٣١، ٥٨٣، ٦٣٣، ٧٦٩، ٨١٠، ٨٥٩، ٩٦٦، ٩٧١ ٣ من وسائل الدعوة: الخطابة ٨٤، ٢٣٦، ٥٠٩، ٧٨١، ٨٦٤ ٤ البروز للناس على مكان مرتفع ٨٤، ٨٥٣ ٥ أهمية مراعاة أوقات نشاط المدعو ٨٥، ٢٣٣، ٥٠٩، ١٠١٢ ٦ التأليف بالمال ٨٥، ٩٢٦، ٩٣٩، ٩٥٤، ٩٦٣، ٩٧٢ ٧ التأليف بالجاه والنسب ٥٦، ٩٥٥ ٨ من وسائل الدعوة: اغتنام التذكير عند الحوادث الملمة ٢٣٦، ٥٠٩
_________
(١) انظر: النهاية في غريب الحديث والأثر لابن الأثير، باب الواو مع السين، مادة: وسل: ٥/ ١٨٥، ولسان العرب لابن منظور، باب اللام، فصل الواو: ١١/ ٢٧٥، والقاموس المحيط للفيروزآبادي: باب اللام فصل الواو: ١٣٧٩.
(٢) انظر: الحكمة في الدعوة إلى الله تعالى لسعيد بن على القحطانى: ١٢٦.
(٣) المدخل إلى علم الدعوة للدكتور / محمد أبو الفتح البيانونى: ٤٩.
(٤) تفسير القرآن العظيم: ٢/ ٥٤.','2','1116',NULL,NULL,'0');
INSERT INTO [b31061] VALUES (1112,'٩ - من وسائل الدعوة: الجهاد في سبيل الله عز وجل ٢١٦، ٦٩٠، ٦٩٥، ٨٠٩، ١٠١٣ ١٠ من وسائل الدعوة: ركوب البحر ١٧٨ ١١ من وسائل الدعوة: الكتب والرسائل ٢٣٢، ٥٠٩، ٥٢١، ٨٧٦ ١٢ من وسائل الدعوة: تسلية المدعوين وتنشيطهم ٢٩٢ ١٣ من وسائل الدعوة إثارة غيرة الرجال ٣٨٧ ١٤ من وسائل الدعوة: إرسال الرسل والدعاة والمجاهدين في سبيل الله ٢٧٣، ٣٩٩، ٥٢١، ٥٤٠، ٥٤٦، ٦٧٠، ٦٧٨، ٧١٣، ٧٢٤، ٧٦٤، ٩٢٢، ٩٩٤، ٩٩٧، ١٠٠٣، ١٠١١، ١٠٢٣ ١٥ من وسائل الدعوة: زيارة أهل المصائب وتسليتهم ٣١١ ١٦ من وسائل الدعوة: أن يتولى أمر كل قبيلة كبيرهم الصالح ٤٦٩ ١٧ من وسائل الدعوة: التطبيق العملي في التعليم ٤٨٨ ١٨ من وسائل الدعوة: التأليف بالعفو مكان الانتقام ٥١٦، ٩٦٣، ٩٧٢ ١٩ من وسائل الدعوة: إزالة الأماكن التي يفتتن بها الناس ٥٥٩، ٧١٤ ٢٠ من وسائل الدعوة: البيعة لإمام المسلمين ٥٦٢، ٥٦٥، ٥٦٨ ٢١ من وسائل الدعوة وأسباب النصر: إظهار القوة والنشاط أمام الأعداء ٥٨٩، ٥٩٨، ٧٦ ٢٢ أهمية اللواء والراية في الجهاد في سبيل الله عز وجل ٥٨٩، ٥٩١، ٥٩٦ ٢٣ من وسائل الدعوة: الخروج في سبيل الله عز وجل ٥٩٦ ٢٤ من وسائل الدعوة: الزيارة والعيادة ٦١٩ ٢٥ من وسائل الدعوة: استخدام القوة عند الحاجة ٦٣٢، ٦٩١ ٢٦ إخفاء المنافقين النفاق دليل على قوة المسلمين ٦٣٣ ٢٧ من وسائل الدعوة: استمالة قلب من له شان في قومه ٧١٨، ٧٣٥ ٢٨ من وسائل الدعوة: قتل الإمام لكل من آذى الله ورسوله صلي الله عليه وسلم ٧٢٥ ٢٩ من وسائل الدعوة: تعليم المجاهدين وتدريبهم استعدادا للجهاد ٧١٨، ٧٣٥، ٧٥١ ٣٠ من وسائل الدعوة: القيا للمقابلة بالسلام والمصافحة والتهنئة ٧٥٥ ٣١ من وسائل الدعوة: تأمير الأمراء على السريا والبعوث والرسل والمسافرين والأقطار ٧٦٤، ١٠٠٤، ١٠١٣ذ ٣٢ من وسائل الدعوة: إغاظة الأعداء بالامتداح بالشعر وإظهار القوة ٧٦٧ ٣٣ من وسائل الدعوة: أخذ الحذر والحيطة ٧٧٩ ٣٤ من وسائل الدعوة: إعطاء السلب للقاتل تشجيعا له ٧٧٨، ٩٤٤ ٣٥ من وسائل الدعوة: إظهار انتصار وشعار المسلمين ٨٠١ ٣٦ من وسائل الدعوة: الإهداء ٨٣٠ ٣٧ أهمية استخدام وسائل الإيضاح في الدعوة إلى الله عز وجل ٨٦٩ ٣٨ من وسائل الدعوة: إعطاء النفل للشجعان تشجيعا لهم ٩٢٢ ٣٩ من وسائل الدعوة: الأخذ بالقرائن عند عدم البينة ٩٤٥ ٤٠ من وسائل الدعوة: الستر على أهل الصلاح والتقوى ٩٥٤ ٤١ من وسائل الدعوة: نقل الكلام بقصد التحير والإصلاح وإزالة المنكر ٩٦٩','2','1117',NULL,NULL,'0');
INSERT INTO [b31061] VALUES (1113,'٤٢ - من القواعد الدعوية: درء المفاسد مقدم على جلب المصالح ٩٣١، ٩٧٥، ١٠٢٧ ٤٣ من وسائل الدعوة: أخذ الجزية من أهل الكتاب والمجوس ٩٩٩ ٤٤ من وسائل الدعوة: البدء بقتال الأهم فالأهم من أعداء الإسلام ١٠١٧ ٤٥ من وسائل الدعوة: التأليف بقبول هدية المشرك مع الحذر ١٠٣١ ٤٦ من وسائل الدعوة: عقد الصلح والهدنة مع الأعداء عند العجز عن الجهاد ١٠٦٧ ٤٧ من ميدان الدعوة: مراكب المواصلات ٣٤٣ ٤٨ تقديم أعلى المصلحتين عند التعارض ٢٧٥ ٤٩ من ميادين الدعوة: طرق السير والسفر ٣٠٠، ٦٧٠ ٥٠ من ميادين الدعوة: المسجد ٤٦٠، ١٠٢٣ ٥١ من ميادين الدعوة: المجالس العامة ٦٣٤ ٥٢ الاستعانة بنوم القائلة على قيام الليل والدعوة ١٧٨
وعلى ضوء ما تقدم من الفوائد الدعوية في هذا الجدول يظهر أن المنهج الدعوي المتعلق بالوسائل على النحو الآتي:
أولا: الوسائل ذات أهمية بالغة؛ لأن الداعية إلى الله عز وجل يتوصل بها إلى تبليغ دعوته، وهي أوعية الأساليب، تحملها وتوصلها إلى الناس، وهذا كله يدل على مكانة الوسائل، وأنه يتأكد العناية بها عناية فائقة.
ثانيا: وسائل الدعوة تنقسم إلى قسمين:
١ - الوسائل المحسوسة الملموسة، وهذا النوع هو أكثر وسائل الدعوة في الغالب، ومن هذا القسم:
أ- الجهاد في سبيل الله عز وجل بالنفس والمال، واللسان (١).
ب- الكتب والرسائل (٢).
_________
(١) انظر من أمثلة ذلك: ص ٢١٦، ٦٩٠، ٦٩٥، ٨٠٩، ١٠١٣ من هذا البحث.
(٢) انظر: ص ٢٣٢، ٥٠٩، ٥٢١، ٨٧٦.','2','1118',NULL,NULL,'0');
INSERT INTO [b31061] VALUES (1114,'جـ - إرسال الدعاة (١).
