-- Insert data for b31227
INSERT INTO [b31227] VALUES (1,'<span data-type="title" id=toc-1>[إسباغ الوضوء]</span>
كيفية صلاة النبي صلى الله عليه وسلم',NULL,NULL,NULL,NULL,'0');
INSERT INTO [b31227] VALUES (2,'بسم الله الرحمن الرحيم
كيفية صلاة النبي صلى الله عليه وسلم الحمد لله وحده، والصلاة والسلام على عبده ورسوله نبينا محمد وآله وصحبه، أما بعد: -
فهذه كلمات موجزة في بيان صفة صلاة النبي صلى الله عليه وسلم، أردت تقديمها إلى كل مسلم ومسلمة ليجتهد كل من يطلع عليها في التأسي به صلى الله عليه وسلم في ذلك',NULL,'3',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (3,'لقوله صلى الله عليه وسلم: «صلوا كما رأيتموني أصلي» رواه البخاري، وإلى القارئ بيان ذلك:
١ - يسبغ الوضوء، وهو أن يتوضأ كما أمره الله عملا بقوله سبحانه وتعالى: {يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا قُمْتُمْ إِلَى الصَّلَاةِ فَاغْسِلُوا وُجُوهَكُمْ وَأَيْدِيَكُمْ إِلَى الْمَرَافِقِ وَامْسَحُوا بِرُءُوسِكُمْ وَأَرْجُلَكُمْ إِلَى الْكَعْبَيْنِ} [المائدة: ٦] الآية.
وقول النبي صلى الله عليه وسلم: «لا تقبل صلاة بغير طهور» .',NULL,'4',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (4,'<span data-type="title" id=toc-2>[التوجه إلى القبلة]</span>
٢ - يتوجه المصلي إلى القبلة وهي الكعبة أينما كان بجميع بدنه قاصدا بقلبه فعل الصلاة التي يريدها من فريضة أو نافلة، ولا ينطق بلسانه بالنية؛ لأن النطق باللسان غير مشروع، بل بدعة لكون النبي صلى الله عليه وسلم لم ينطق بالنية ولا أصحابه رضي الله عنهم، ويجعل له سترة يصلي إليها إن كان إمامًا أو منفردًا، واستقبال القبلة شرط في الصلاة إلا في مسائل مستثناة معلومة موضحة في كتب أهل العلم.',NULL,'5',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (5,'<span data-type="title" id=toc-3>[تكبيرة الإحرام ورفع اليدين عند التكبير ووضع اليدين على الصدر]</span>
٣ - يكبر تكبيرة الإحرام قائلًا: الله أكبر، ناظرًا ببصره إلى محل سجوده.
٤ - يرفع يديه عند التكبير إلى حذو منكبيه أو إلى حيال أذنيه.
٥ - يضع يديه على صدره، اليمنى على كفه اليسرى والرسغ والساعد لثبوت ذلك عن النبي صلى الله عليه وسلم.

<span data-type="title" id=toc-4>[دعاء الاستفتاح]</span>
٦ - يسن أن يقرأ دعاء الاستفتاح وهو: «اللهم باعد بيني وبين خطاياي كما باعدت بين المشرق والمغرب، اللهم نقني من خطاياي كما ينقى الثوب',NULL,'6',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (6,'الأبيض من الدنس، اللهم اغسلني من خطاياي بالماء والثلج والبرد، وإن شاء قال بدلًا من ذلك: سبحانك اللهم وبحمدك وتبارك اسمك وتعالى جدك ولا إله غيرك» ، وإن أتى بغيرهما من الاستفتاحات الثابتة عن النبي صلى الله عليه وسلم فلا بأس، والأفضل أن يفعل هذا تارة وهذا تارة، لأن ذلك أكمل في الاتباع، ثم يقول: أعوذ بالله من الشيطان الرجيم، بسم الله الرحمن الرحيم، ويقرأ سورة الفاتحة، لقوله',NULL,'7',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (7,'صلى الله عليه وسلم: «لا صلاة لمن لم يقرأ بفاتحة الكتاب» ويقول بعدها- آمين- جهرًا في الصلاة الجهرية، وسرًا في السرية، ثم يقرأ ما تيسر له من القرآن، والأفضل أن يقرأ بعد الفاتحة في الظهر والعصر والعشاء من أوساط المفصل، وفي الفجر من طواله وفي المغرب تارة من طواله، وتارة من قصاره عملا بالأحاديث الواردة في ذلك.

<span data-type="title" id=toc-5>[الركوع والرفع منه وما يشتمل عليه]</span>
٧ - يركع مكبرًا رافعًا يديه إلى حذو منكبيه أو أذنيه جاعلا رأسه حيال ظهره',NULL,'8',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (8,'واضعًا يديه على ركبتيه مفرقًا أصابعه ويطمئن في ركوعه ويقول: سبحان ربي العظيم. والأفضل أن يكررها ثلاثًا أو أكثر، ويستحب أن يقول مع ذلك: «سبحانك اللهم وبحمدك، اللهم اغفر لي» .
٨ - يرفع رأسه من الركوع رافعًا يديه إلى حذو منكبيه أو أذنيه قائلا «سمع الله لمن حمده» - إن كان إماما أو منفردا- ويقول حال قيامه: «ربنا ولك الحمد حمدًا كثيرًا طيبا ًمباركًا فيه ملء السماوات وملء',NULL,'9',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (9,'الأرض وملء ما بينهما وملء ما شئت من شيء بعد» . . أما إن كان مأمومًا فإنه يقول عند الرفع: ربنا ولك الحمد إلى آخر ما تقدم، وإن زاد كل واحد منهم أعني الإمام والمأموم والمنفرد " «أهل الثناء والمجد أحق ما قال العبد وكلنا لك عبد، اللهم لا مانع لما أعطيت ولا معطي لما منعت ولا ينفع ذا الجد منك الجد» " فهو حسن لثبوت ذلك عنه صلى الله عليه وسلم، ويستحب أن يضع كل منهم يديه على صدره كما فعل في قيامه قبل الركوع، لثبوت',NULL,'10',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (10,'ما يدل على ذلك عن النبي صلى الله عليه وسلم من حديث وائل بن حجر وسهل بن سعد رضي الله عنهما.

<span data-type="title" id=toc-6>[السجود والرفع منه وما يشتمل عليه]</span>
٩ - يسجد مكبرًا واضعًا ركبتيه قبل يديه إذا تيسر له ذلك، فإن شق عليه قدم يديه قبل ركبتيه مستقبلا بأصابع رجليه ويديه القبلة ضامًا أصابع يديه مادًّا لها ويكون على أعضائه السبعة: الجبهة مع الأنف، واليدين، والركبتين، وبطون أصابع الرجلين، ويقول: سبحان ربي الأعلى، ويسن',NULL,'11',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (11,'أن يقول ذلك ثلاثًا أو أكثر، ويستحب أن يقول مع ذلك: «سبحانك اللهم ربنا وبحمدك، اللهم اغفر لي» ، ويكثر من الدعاء لقول النبي صلى الله عليه وسلم: «أما الركوع فعظموا فيه الرب، وأما السجود فاجتهدوا في الدعاء فقمن أن يستجاب لكم» ويسأل ربه من خير الدنيا والآخرة، سواء كانت الصلاة فرضًا أو نفلا، ويجافي عضديه عن جنبيه وبطنه عن فخذيه، وفخذيه عن ساقيه، ويرفع ذراعيه عن الأرض لقول',NULL,'12',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (12,'النبي صلى الله عليه وسلم: «اعتدلوا في السجود ولا يبسط أحدكم ذراعيه انبساط الكلب»

<span data-type="title" id=toc-7>[الجلوس بين السجدتين وكيفيته]</span>
١٠ - يرفع رأسه مكبرًا ويفرش قدمه اليسرى ويجلس عليها، وينصب رجله اليمنى ويضع يديه على فخذيه وركبتيه ويقول: «رب اغفر لي وارحمني واهدني وارزقني وعافني واجبرني» ويطمئن في هذا الجلوس.
١١ - يسجد السجدة الثانية مكبرًا ويفعل فيها كما فعل في السجدة الأولى.',NULL,'13',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (13,'١٢ - يرفع رأسه مكبرًا ويجلس جلسة خفيفة كالجلسة بين السجدتين وتسمى جلسة الاستراحة وهي مستحبة، وإن تركها فلا حرج عليه، وليس فيها ذكر ولا دعاء، ثم ينهض قائمًا إلى الركعة الثانية معتمدًا على ركبتيه إن تيسر له ذلك وإن شق عليه اعتمد على الأرض، ثم يقرأ الفاتحة وما تيسر له من القرآن بعد الفاتحة، ثم يفعل كما فعل في الركعة الأولى.

<span data-type="title" id=toc-8>[الجلوس للتشهد في الصلاة الثنائية وكيفيته]</span>
١٣ - إذا كانت الصلاة ثنائية- أي',NULL,'14',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (14,'ركعتين- كصلاة الفجر والجمعة والعيدين جلس بعد رفعه من السجدة الثانية ناصبًا رجله اليمنى، مفترشًا رجله اليسرى، واضعًا يده اليمنى على فخذه اليمنى، قابضًا أصابعه كلها إلا السبابة فيشير بها إلى التوحيد، وإن قبض الخنصر والبنصر من يده اليمنى وحلق إبهامها مع الوسطى وأشار بالسبابة فحسن لثبوت الصفتين عن النبي صلى الله عليه وسلم، والأفضل أن يفعل هذا تارة وهذا تارة ويضع يده اليسرى على فخذه',NULL,'15',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (15,'اليسرى وركبته، ثم يقرأ التشهد في هذا الجلوس وهو: «التحيات لله والصلوات والطيبات، السلام عليك أيها النبي ورحمة الله وبركاته، السلام علينا وعلى عباد الله الصالحين، أشهد أن لا إله إلا الله وأشهد أن محمدًا عبده ورسوله، ثم يقول اللهم صل على محمد وعلى آل محمد كما صليت على إبراهيم وآل إبراهيم إنك حميد مجيد، وبارك على محمد وعلى آل محمد كما باركت على إبراهيم وآل إبراهيم إنك',NULL,'16',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (16,'حميد مجيد» ويستعيذ بالله من أربع فيقول: «اللهم إني أعوذ بك من عذاب جهنم ومن عذاب القبر ومن فتنة المحيا والممات ومن فتنة المسيح الدجال» ثم يدعو بما يشاء من خير الدنيا والآخرة، وإذا دعا لوالديه أو غيرهما من المسلمين فلا بأس- سواء كانت الصلاة فريضة أو نافلة- لعموم قول النبي صلى الله عليه وسلم، في حديث ابن مسعود لما علمه التشهد ثم ليتخير من الدعاء أعجبه إليه فيدعو، وفي لفظ آخر ثم',NULL,'17',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (17,'ليختر من المسألة ما شاء وهذا يعم جميع ما ينفخ العبد في الدنيا والآخرة، ثم يسلم عن يمينه وشماله قائلا: السلام عليكم ورحمة الله، السلام عليكم ورحمة الله.

<span data-type="title" id=toc-9>[الجلوس للتشهد في الصلاة الثلاثية أو الرباعية وكيفيته]</span>
١٤ - إن كانت الصلاة ثلاثية كالمغرب، أو رباعية كالظهر والعصر والعشاء قرأ التشهد المذكور آنفًا مع الصلاة على النبي صلى الله عليه وسلم، ثم نهض قائمًا معتمدًا على ركبتيه، رافعًا يديه إلى حذو منكبيه أو أذنيه قائلا: الله أكبر،',NULL,'18',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (18,'ويضعهما- أي يديه- على صدره كما تقدم، ويقرأ الفاتحة فقط وإن قرأ في الثالثة والرابعة من الظهر زيادة عن الفاتحة في بعض الأحيان فلا بأس لثبوت ما يدل على ذلك عن النبي صلى الله عليه وسلم من حديث أبي سعيد رضي الله عنه، ثم يتشهد بعد الثالثة من المغرب وبعد الرابعة من الظهر والعصر والعشاء كما تقدم ذلك في الصلاة الثنائية، ثم يسلمِ عن يمينه وشماله ويستغفر الله ثلاثاَ، ثم يقول: «اللهم أنت السلام',NULL,'19',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (19,'ومنك السلام تباركت يا ذا الجلال والإكرام» قبل أن ينصرف إلى الناس إن كان إمامًا، ثم يقول: «لا إله إلا الله وحده لا شريك له، له الملك وله الحمد وهو على كل شيء قدير، اللهم لا مانع لما أعطيت ولا معطي لما منعت ولا ينفع ذا الجد منك الجد، لا حول ولا قوة إلا بالله، لا إله إلا الله ولا نعبد إلا إياه له النعمة وله الفضل وله الثناء الحسن لا إله إلا الله مخلصين له الدين ولو كره الكافرون» ، ويسبح الله ثلاثا وثلاثين',NULL,'20',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (20,'ويحمده مثل ذلك، ويكبره مثل ذلك ويقول تمام المائة لا إله إلا الله وحده لا شريك له، له الملك وله الحمد وهو على كل شيء قدير، ويقرأ آية الكرسي، وقل هو الله أحد، وقل أعوذ برب الفلق، وقل أعوذ برب الناس بعد كل صلاة، ويستحب تكرار هذه السور الثلاث ثلاث مرات بعد صلاة الفجر وصلاة المغرب، لورود الأحاديث بها عن النبي صلى الله عليه وسلم وكل هذه الأذكار سنة وليست بفريضة.',NULL,'21',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (21,'ويشرع لكل مسلم ومسلمة أن يصلي قبل صلاة الظهر أربع ركعات وبعدها ركعتين وبعد صلاة المغرب ركعتين وبعد صلاة العشاء ركعتين وقبل صلاة الفجر ركعتين، الجميع اثنتا عشرة ركعة، وهذه الركعات تسمى الرواتب لأن النبي كان يحافظ عليها في الحضر. . أما في السفر فكان يتركها إلا سنة الفجر والوتر فإنه كان عليه الصلاة والسلام يحافظ عليها حضرًا وسفرًا، والأفضل أن تصلى هذه الرواتب والوتر',NULL,'22',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (22,'في البيت فإن صلاها في المسجد فلا بأس، لقول النبي صلى الله عليه وسلم «أفضل صلاة المرء في بيته إلا المكتوبة» والمحافظة على هذه الركعات من أسباب دخول الجنة لقول النبي صلى الله عليه وسلم: «من صلى اثنتي عشرة ركعة في يومه وليلته تطوعًا بنى الله له بيتًا في الجنة» رواه مسلم في صحيحه، وإن صلى أربعًا قبل العصر واثنتين قبل صلاة المغرب واثنتين قبل صلاة العشاء فحسن؛ لأنه صح عن النبي صلى الله عليه وسلم ما يدل على ذلك، والله ولي التوفيق، وصلى',NULL,'23',NULL,NULL,'0');
INSERT INTO [b31227] VALUES (23,'الله وسلم على نبينا محمد بن عبد الله وعلى آله وأصحابه وأتباعه بإحسان إلى يوم الدين،،',NULL,'24',NULL,NULL,'0');
