INSERT INTO [b31378] VALUES (1,'<span data-type="title" id=toc-1>بسم الله الرحمن الرحيم
رب زدني علماً يا كريم</span>
الحمد لله الذي أنزل الفرقان، ونزل القرآن، وأنعم علينا بالإيمان، وأتم لنا بالإحسان.
والصلاة والسلام الأتمان الأكملان على سيد الخلق، وسند الحق محمد بن عبد الله من بني عدنان، وعلى آله الكرام، وأصحابه الفخام في كل زمان ومكان.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (2,'أما بعد: فيقول خادم كتاب الله القديم، وحديث نبيه الكريم، المحتاج إلى بر ربه الباري علي بن سلطان محمد القاري.
هذه أربعون حديثا في فضائل القرآن، ومن تلاه على وجه الإحسان بقدر الإمكان.',NULL,'12',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (3,'<span data-type="title" id=toc-2>الحديث الأول</span>
فعن عثمان بن عفان رضي الله تعالى عنه، عن النبي صلى الله عليه وسلم قال:) خيركم من تعلم القرآن وعلمه (.
- رواه أحمد، وأصحاب الكتب الستة.',NULL,'13',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (4,'وفي رواية لابن ماجه عن سعد، ولفظه:) خياركم (.
ورواه ابن أبي دواد عن ابن مسعود، ولفظه:) خياركم من قرأ القرآن وأقرأه (.',NULL,'14',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (5,'<span data-type="title" id=toc-3>الحديث الثاني</span>
وعن عبد الله بن مسعود رضي الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:) من قرأ حرفاً من كتاب الله، فله به حسنة، والحسنة بعشر أمثالها، لا أقو ل) الم (حرف، ولكن، ألف حرف، ولام حرف، وميم حرف (.
- رواه الترمذي، قال: حديث حسن صحيح.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (6,'<span data-type="title" id=toc-4>الحديث الثالث</span>
وعن عمر بن الخطاب رضي الله تعالى عنه، أن النبي صلى الله عليه وسلم قال:) إن الله تعالى يرفع بهذا الكتاب أقواماً، ويضع به آخرين (.
- رواه مسلم، وابن ماجه.',NULL,'16',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (7,'<span data-type="title" id=toc-5>الحديث الرابع</span>
وعن أبي سعيد الخدري قال: قال رسول الله صلى الله عليه وسلم:) يقول الرب تبارك وتعالى: من شغله القرآن عن ذكري، ومسألتي، أعطيته أفضل ما أعطي السائلين. وفضل كلام الله تعالى على سائر الكلام، كفضل الله تعالى على خلقه (.
- رواه الترمذي، وقال: حسن غريب.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (8,'<span data-type="title" id=toc-6>الحديث الخامس</span>
وعن أبي موسى الأشعري رضي الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:) مثل المؤمن الذي يقرأ القرآن كمثل الأترجة، ريحها طيب، وطعمها طيب، ومثل المؤمن الذي لا يقرأ القرآن كمثل التمرة لا ريح لها، وطعمها حلو، ومثل المنافق الذي يقرأ القرآن كمثل الريحانة، ريحها طيب، وطعمها مر، ومثل المنافق الذي لا يقرأ القرآن كمثل الحنظلة ليس لها ريح وطعمها مر (.
وفي رواية:) مثل الفاجر (بدل) المنافق (.
- رواه أحمد، والبخاري، ومسلم، وأبو داود، والترمذي، والنسائي، وابن ماجه.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (9,'<span data-type="title" id=toc-7>الحديث السادس</span>
وعن أنس رضي الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:) مثل المؤمن الذي يقرأ القرآن، كمثل الأترجة ريحها طيب، وطعمها طيب، ومثل المؤمن الذي لا يقرأ القرآن، كمثل التمرة لا ريح لها، وطعمها طيب، ومثل الفاجر الذي يقرأ القرآن كمثل الريحانة ريحها طيب، وطعمها مر، ومثل الفاجر الذي لا يقرأ القرآن، كمثل الحنظلة طعمها مر، ولا ريح لها، ومثل الجليس الصالح، كمثل صاحب المسك، إن لم يصبك منه شيء أصابك من ريحه، ومثل الجليس السوء، كمثل صاحب الكير، إن لم يصبك من سواده، أصابك من دخانه (.
- رواه أبو داود.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (10,'<span data-type="title" id=toc-8>الحديث السابع</span>
وعن عائشة رضي الله تعالى عنها، قالت: قال رسول الله صلى الله عليه وسلم:) الماهر بالقرآن مع السفرة الكرام البررة، والذي يقرأ القرآن، ويتتعتع فيه، وهو عليه شاق، فله أجران (.
وفي رواية:) الذي يقرأ القرآن، وهو يشتد عليه له أجران (.
- رواه البخاري، ومسلم، واللفظ له. وأبو داود، والترمذي، والنسائي، وابن ماجه.',NULL,'22',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (11,'<span data-type="title" id=toc-9>الحديث الثامن</span>
وعن أبي ذر رضي الله تعالى عنه قال:) قلت: يا رسول الله أوصني: قال صلى الله عليه وسلم: عليك بتقوى الله. فإنها رأس الأمر كله. قلت: يا رسول الله زدني. قال: عليك بتلاوة القرآن، فإنه نور لك في الأرض، ونور لك في السماء (.
- رواه ابن حبان، وصححه في حديث طويل، ورواه ابن الضريس، وأبو يعلى.
عن أبي سعيد:) عليك بتقوى الله، فإنها جماع كل خير، وعليك بذكر الله، وتلاوة القرآن، فإنه نور لك في الأرض، وذكر لك في السماء، واخزن لسانك إلا من خير، فإنك بذلك تغلب الشيطان (.',NULL,'24',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (12,'<span data-type="title" id=toc-10>الحديث التاسع</span>
وعن جابر رضي الله تعالى عنه، عن النبي صلى الله عليه وسلم قال:) القرآن شافع مشفع، وماحل مصدق، من جعله أمامه قاده إلى الجنة، ومن جعله خلف ظهره ساقه إلى النار (.
- رواه ابن حبان في صحيحه، والبيهقي في شعبه عنه، والبيهقي عن ابن مسعود.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (13,'<span data-type="title" id=toc-11>الحديث العاشر</span>
وعن أبي أمامة الباهلي رضي الله تعالى عنه قال: سمعت رسول الله صلى الله عليه وسلم يقول:) اقرأوا القرآن، فإنه يوم القيامة شفيعاً لأصحابه.. (. الحديث.
- رواه مسلم.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (14,'<span data-type="title" id=toc-12>الحديث الحادي عشر</span>
وعن سهل بن معاذ الجهني، عن أبيه رضي الله تعالى عنه قال: إن رسول الله صلى الله عليه وسلم قال:) من قرأ القرآن، وعمل بما فيه ألبس والداه تاجا يوم القيامة، ضوؤه أحسن من ضوء الشمس في بيوت الدنيا، لو كانت فيكم، فما ظنكم بالذي عمل هذا؟ (.
- رواه أبو داود، والحاكم وقال: صحيح الإسناد.',NULL,'28',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (15,'<span data-type="title" id=toc-13>الحديث الثاني عشر</span>
وعن أبي بريدة رضي الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:) من قرأ القرآن، وتعلمه، وعمل به، ألبس والداه يوم القيامة تاجا من نور ضوؤه مثل ضوء الشمس، ويكس والداه حلتان لا تقوم بهما الدنيا، فيقولان: لم كسينا هذا، فيقال: بأخذ ولدكما القرآن (.
- رواه الحاكم، وقال صحيح على شرط مسلم.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (16,'<span data-type="title" id=toc-14>الحديث الثالث عشر</span>
وعن أبي هريرة رضي الله تعالى عنه قال: عن رسول الله صلى الله عليه وسلم قال:) يجيء صاحب القرآن يوم القيامة، فيقول القرآن: يا رب حله، فيلبس تاج الكرامة، ويقول: يا رب زده، فيلبس حلة الكرامة، ويقول: يا رب ارض عنه، فيرضى عنه. فيقال: اقرأ، وارق، ويزداد بكل آية حسنة (.
- رواه الترمذي، وحسنه، وابن خزيمة، والحاكم، وقال: صحيح الإسناد.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (17,'<span data-type="title" id=toc-15>الحديث الرابع عشر</span>
وعن عبد الله بن عمرو بن العاص رضي الله تعالى عنهما قال: قال رسول الله صلى الله عليه وسلم:) يقال لصاحب القرآن: اقرأ، وارق، ورتل كما كنت ترتل في الدنيا، فإن منزلتك عند آخر آية تقرؤها (.
- رواه الترمذي، وأبو داود، وابن ماجه، وابن حبان في صحيحه، وقال الترمذي: حديث حسن صحيح.',NULL,'31',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (18,'<span data-type="title" id=toc-16>الحديث الخامس عشر</span>
وعن ابن عمر رضي الله تعالى عنهما قال: قال رسول الله صلى الله عليه وسلم:) لا حسد إلا على اثنتين: رجل آتاه الله هذا الكتاب، فقام به آناء الليل، وآناء النهار. ورجل أعطاه الله تعالى مالا، فتصدق به آناء الليل، وآناء النهار (.
- رواه البخاري ومسلم.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (19,'<span data-type="title" id=toc-17>الحديث السادس عشر</span>
وعن أبي هريرة رضي الله تعالى عنه، أن رسول الله صلى الله عليه وسلم، قال:) لا حسد إلا في اثنتين: رجل علمه القرآن، فهو يتلوه آناء الليل، وآناء النهار، فسمعه جار له، فقال: يا ليتني أوتيت مثل ما أوتي فلان، فعملت مثل ما يعمل.
ورجل آتاه الله مالا فهو يهلكه في الحل، فقال رجل: ليتني أوتيت مثل ما أوتي فلان، فعملت مثل ما يعمل (.
- رواه البخاري.',NULL,'33',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (20,'<span data-type="title" id=toc-18>الحديث السابع عشر</span>
وعن ابن عمر رضي الله تعالى عنهما قال: قال رسول الله صلى الله عليه وسلم:) ثلاثة لا يهولهم الفزع الأكبر، ولا ينالهم الحساب، هم على كثيب من مسك حتى يفرغ من حساب الخلائق: رجل قرأ القرآن ابتغاء وجه الله تعالى، وأم به قوماً، وهم راضون.
وداع يدعو إلى الصلاة ابتغاء وجه الله عز وجل. وعبد أحسن بينه، وبين ربه، وفيما بينه وبين مواليه (.
- رواه الطبراني في الأوسط، والصغير بإسناد لا بأس به، وفي الكبير نحوه، وزاد في أوله: قال ابن عمر: لو لم أسمعه من رسول الله إلا مرة، ومرة، حتى عد سبع مرات لا حدثت به. ولفظ الكبير على ما في الجامع الصغير:) ثلاثة على كثبان المسك يوم القيامة، لا يهولهم الفزع، ولا يفزعون حتى يفرغ الناس: رجل تعلم القرآن، فقام به يطلب وجه الله، ورجل نادى',NULL,'34',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (21,'في كل يوم وليلة خمس صلوات يطلب وجه الله وما عنده، ومملوك لم يمنعه رق الدنيا عن طاعة ربه (.',NULL,'35',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (22,'<span data-type="title" id=toc-19>الحديث الثامن عشر</span>
وعن أبي هريرة رضي الله تعالى عنه قال:
) بعث رسول الله صلى الله تعالى عليه وسلم بعثاً، وهم ذو عدد، فاستقرأهم، فاستقرأ كل رجل منهم - يعني ما معه من القرآن - فأتى على رجل من أحدثهم سناً، فقال: ما معك يا فلان؟ قال: معي كذا، وكذا، وسورة البقرة. قال: أمعك سورة البقرة؟ قال: نعم. فقال: اذهب، فأنت أميرهم. فقال رجل من أشرافهم: والله ما منعني أن أتعلم البقرة إلا خشية ألا أقوم بها. فقال رسول الله صلى الله عليه وسلم: تعلموا القرآن، واقرأوه، فإن مثل القرآن لمن تعلمه، فقرأه، وقام به كمثل جراب محشو مسكاً، يفوح ريحه في كل مكان، ومثل من تعلمه، فيرقد، وهو في جوفه كمثل جراب أوكي على مسك (.
- رواه الترمذي، واللفظ له، وقال: حديث حسن، وابن ماجه مختصراً وابن حبان في صحيحه.',NULL,'36',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (23,'<span data-type="title" id=toc-20>الحديث التاسع عشر</span>
وعن عبد الله بن عمرو رضي الله تعالى عنهما قال: قال رسول الله صلى الله تعالى عليه وسلم:) من قرأ القرآن، فقد استدرج النبوة بين جنبيه، غير أنه لا يوحى إليه، لا ينبغي لصاحب القرآن أن يجد مع جد، ولا يجهل مع جهل، وفي جوفه كلام الله تعالى (.
- رواه الحاكم، وقال: صحيح الإسناد.',NULL,'38',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (24,'<span data-type="title" id=toc-21>الحديث العشرون</span>
وعنه، قال: قال رسول الله صلى الله تعالى عليه وسلم:) الصيام، والقرآن يشفعان للعبد، يقول الصيام: رب إني منعته الطعام، والشراب بالنهار، فشفعني فيه - ويقول القرآن: منعته النوم بالليل، فشفعني فيه - فيشفعان (.
- رواه أحمد وابن أبي الدنيا في كتاب الجوع، والطبراني في الكبير، والحاكم، واللفظ له، وقال: صحيح على شرط مسلم.',NULL,'39',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (25,'<span data-type="title" id=toc-22>الحديث الحادي والعشرون</span>
وعن أبي ذر رضي الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:) إنكم لا ترجعون إلى الله تعالى بشيء أفضل مما خرج منه - يعني القرآن - ظهر منه (.
- رواه الحاكم، وصححه، ورواه أبو داود في مراسيله.',NULL,'40',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (26,'<span data-type="title" id=toc-23>الحديث الثاني والعشرون</span>
وعن أنس رضي الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:) إن لله أهلين من الناس. قالوا: من هم يا رسول الله؟ قال: أهل القرآن هم أهل الله، وخاصته (.
- رواه النسائي، وابن ماجه، والحاكم، وصححه المنذري.',NULL,'41',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (27,'<span data-type="title" id=toc-24>الحديث الثالث والعشرون</span>
وعن ابن عباس رضي الله تعالى عنهما قال:) من قرأ القرآن لم يرد إلى أرذل العمر، وذلك قوله تعالى: (ثُمَّ رَدَدناهُ أَسفَلَ سافِلينَ إِلّا الَّذينَ آمَنوا) قال: إلا الذين قرأوا القرآن (.
- رواه الحاكم، وقال: صحيح الإسناد.',NULL,'42',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (28,'<span data-type="title" id=toc-25>الحديث الرابع والعشرون</span>
وعن ابن عباس رضي الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:) أشراف أمتي حملة القرآن، وأصحاب الليل (.
- رواه البيهقي في شعب الإيمان، وابن أبي الدنيا.',NULL,'43',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (29,'<span data-type="title" id=toc-26>الحديث الخامس والعشرون</span>
وعن عبد الرحمن بن شبل الأنصاري رضي الله تعالى عنه، أن النبي صلى الله عليه وسلم، قال:) اقرأوا القرآن، واعملوا به، ولا تجفوا عنه، ولا تغلوا فيه، ولا تأكلوا به، ولا تستكثروا به (.
- رواه أحمد، وأبو يعلى، والطبراني، والبيهقي.',NULL,'44',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (30,'<span data-type="title" id=toc-27>الحديث السادس والعشرون</span>
وعن عمران بن حصين رضي الله تعالى عنه: أنه مر على قارىء يقرأ، ثم سأل، فاسترجع. ثم قال: سمعت رسول الله صلى الله عليه وسلم يقول:) من قرأ القرآن، فليسأل الله به، فإنه سيجيء أقوام يقرأون القرآن يسألون به الناس (.
- رواه الترمذي وقال: حديث حسن.',NULL,'45',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (31,'<span data-type="title" id=toc-28>الحديث السابع والعشرون</span>
وعن أبي هريرة رضي الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:) ليس منا من لم يتغن بالقرآن (.
- رواه البخاري، ورواه أحمد، وأبو داود، وابن حبان، والحاكم عن سعد.
قال جمهور العلماء: أي لم يحسن صوته. وقال بعضهم: لم يستغن به عن غيره.',NULL,'46',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (32,'<span data-type="title" id=toc-29>الحديث الثامن والعشرون</span>
وعن بريده رضي الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:) من قرأ القرآن يتأكل به الناس، جاء يوم القيامة ووجهه عظم ليس عليه لحم (.
- رواه البيهقي.',NULL,'48',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (33,'<span data-type="title" id=toc-30>الحديث التاسع والعشرون</span>
وعن عائشة رضي الله تعالى عنها، أنه عليه الصلاة والسلام قال:) قراءة القرآن في الصلاة أفضل من قراءة القرآن في غير الصلاة، وقراءة القرآن في غير الصلاة أفضل من التسبيح والتكبير، والتسبيح أفضل من الصدقة، والصدقة أفضل من الصوم، والصوم جنة من النار (.
- رواه الدارقطني في الإفراد، والبيهقي في شعب الإيمان.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (34,'<span data-type="title" id=toc-31>الحديث الثلاثون</span>
وعن أوس بن أبي أوس الثقفي مرفوعاً:) قراءة الرجل القرآن في غير المصحف ألف درجة، وقراءته في المصحف تضاعف على ذلك إلى ألفي درجة (.
- رواه الطبراني، والبيهقي.',NULL,'50',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (35,'<span data-type="title" id=toc-32>الحديث الحادي والثلاثون</span>
وعن ابن عمرو رضي الله عنهما مرفوعاً:) اقرإ القرآن في كل شهر، اقرأه في عشرين ليلة، اقرأه في عشر، اقرأه في سبع، ولا تزد على ذلك (.
- رواه الشيخان، وأبو داود.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (36,'<span data-type="title" id=toc-33>الحديث الثاني والثلاثون</span>
وعن ابن عمرو رضي الله عنهما قال: قال رسول الله صلى الله عليه وسلم:) اقرإ القرآن ما نهاك، فإذا لم ينهك فلست تقرأه (.
- رواه الديلمي في مسند الفردوس.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (37,'<span data-type="title" id=toc-34>الحديث الثالث والثلاثون</span>
وعن بريدة رضي الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:) اقرأوا القرآن بالحزن، فإنه نزل بالحزن (.
- رواه أبو يعلى، والطبراني في الأوسط، وأبو نعيم في الحلية.',NULL,'54',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (38,'<span data-type="title" id=toc-35>الحديث الرابع والثلاثون</span>
وعن جندب رضي الله تعالى عنه قال: قال رسول الله صلى الله عليه وسلم:) اقرأوا القرآن ما أتلفت عليه قلوبكم، فإذا اختلفتم فيه فقوموا (.
- رواه أحمد، والشيخان، والنسائي.',NULL,'55',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (39,'<span data-type="title" id=toc-36>الحديث الخامس والثلاثون</span>
وعن أبي أمامة رضي الله تعالى عنه، عن النبي صلى الله عليه وسلم:) اقرأوا القرآن، فإن الله تعالى لا يعذب قلباً وعى القرآن (.
- رواه تمام.',NULL,'56',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (40,'<span data-type="title" id=toc-37>الحديث السادس والثلاثون</span>
وعن أنس رضي الله تعالى عنه أن رسول الله صلى الله عليه وسلم قال:) القرآن غنى لا فقر بعده، ولا غنى دونه (.
- رواه أبو يعلى.',NULL,'57',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (41,'<span data-type="title" id=toc-38>الحديث السابع والعشرون</span>
وعن عمر رضي الله تعالى عنه، قال: قال رسول الله صلى الله عليه وسلم:) القرآن ألف ألف حرف، وسبعة وعشرون ألف حرف، فمن قرأه صابراً محتسباً كان له بكل حرف زوجة من الحور العين (.
- رواه الطبراني في الأوسط.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (42,'<span data-type="title" id=toc-39>الحديث الثامن والثلاثون</span>
وعن رجل، عن النبي صلى الله عليه وسلم قال:) القرآن هو النور المبين، والذكر الحكيم، والصراط المستقيم (.
- رواه البيهقي.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (43,'<span data-type="title" id=toc-40>الحديث التاسع والثلاثون</span>
وعن علي رضي الله تعالى عنه، قال: قال رسول الله صلى الله عليه وسلم:) القرآن هو الدواء (.
- رواه القضاعي.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b31378] VALUES (44,'<span data-type="title" id=toc-41>الحديث الأربعون</span>
وعن أنس رضي الله تعالى عنه أن رسول الله صلى الله عليه وسلم قال:) أهل القرآن عرفاء أهل الجنة (.
- رواه الضياء.
قال المصنف رحمه الله تعالى:) تم أحاديث الأربعين، والله حسبي، ونعم المعين (.',NULL,'61',NULL,NULL,'0');
