INSERT INTO [b321] VALUES (178,'٢٢ - "الاعتقاد والهداية إلى سبيل الرشاد": دار الآفاق الجديدة، بيروت، الطبعة الأولى، سنة ١٤٠١ هـ / ١٩٨١م، علق عليه: أحمد عصام كاتب.
