INSERT INTO [b338] VALUES (133,'<span data-type="title" id=toc-10>بَابٌ: مَنْ تَجِبُ عَلَيْهِ الْجِزْيَةُ وَمَنْ تَسْقُطُ عَنْهُ</span>','1','150',NULL,NULL,'0');
