INSERT INTO [b351] VALUES (58,'<table width="95%" dir=rtl><tr><td>أعنابها <br>بقصابها <br> × أودى بها </td><td> « </td><td> الأعشى </td><td> الخزانة ٣/ ٤١٥، ٤/ ٥٧٨، العيني ٢/ ٤٦٦، ٤/ ٣٢٧، ق (أثافت)، ابن الشجري ١/ ١٠٥، ٢٢٧، ٢/ ٣٤٥، سيبويه ١/ ٢٣٩</td></tr><tr><td>وانتيابها <br> ولم أُورابها </td><td> رجز </td><td> ــ </td><td> مع ١/ ٢٨، سيبويه ٢/ ١٦٥</td></tr></table>
