INSERT INTO [b351] VALUES (216,'<table width="95%" dir=rtl><tr><td>ساطع </td><td> « </td><td> جرير </td><td> الخزانة ١/ ٣٦١</td></tr><tr><td>من الفجر ساطع </td><td> « </td><td> مقّاس العائذي </td><td> ش (¬١) ١/ ٢١</td></tr><tr><td>جامع </td><td> « </td><td> ابن الدُّمينة </td><td> الخزانة ١/ ٣٧٣</td></tr><tr><td>وموتك فاجع </td><td> « </td><td> سلولي (¬٢) </td><td> الخزانة ٢/ ٨٩، ابن الشجري ٢/ ٢٣٠، سيبويه ١/ ٣٥٨، [مع ١/ ١٢٩]</td></tr><tr><td>المنون تتابع </td><td> « </td><td> ــ </td><td> العيني ٢/ ٣٣٣، مع ١/ ١٢٦</td></tr><tr><td>والجنوب مصارع </td><td> « </td><td> البعيث (¬٣) </td><td> العيني ٣/ ٣٥٢، [مع ٢/ ١٩٢]</td></tr><tr><td>أم هو الآن واقع </td><td> « </td><td> ــ </td><td> العيني ٤/ ١٣٦، شواهد المغني ٤٩، [مع ٢/ ١٧٥]</td></tr><tr><td>نوازع </td><td> « </td><td> أبو الطفيل عامر بن واثلة </td><td> الخزانة ٢/ ٩١</td></tr><tr><td>يقظان هاجع<br>الذي هو صانع </td><td> « </td><td> حميد بن ثور </td><td> الخزانة ٢/ ١٩٧، العيني ١/ ٥٦٢، ابن الشجري ٢/ ٣٥٢ </td></tr><tr><td>الودائع </td><td> « </td><td> لبيد </td><td> الخزانة ٢/ ٣٣٣</td></tr><tr><td>أنا وادع </td><td> « </td><td> ــ </td><td> الخزانة ٣/ ١٢١</td></tr><tr><td>لذي الجهل وازع </td><td> « </td><td> ــ </td><td> الخزانة ٣/ ١٥٢</td></tr><tr><td>الفوارع </td><td> « </td><td> معن بن أوس </td><td> الخزانة ٣/ ٢٥٥، ق (أحوس)، شواهد المغني ١٦</td></tr><tr><td>البارقات اللوامع<br>الأسير الروائع </td><td> « </td><td> قيس (بن العيزارة) </td><td> الخزانة ٣/ ٣٢٣، ق (أقتد)</td></tr></table>
