INSERT INTO [b351] VALUES (282,'<table width="95%" dir=rtl><tr><td>× من أيمُنٍ وأشمُلٍ <br>الأجلل <br>× عن فُلِ <br>من عَلِ (¬١) <br>بالصبا والشَمأل <br>× [المجزل] <br>× [أوحل] <br>× وأظلل</td><td> « </td><td> أبو النجم </td><td> الخزانة ١/ ٤٠١، العيني ٣/ ٤٤٨، ٤/ ٢٢٨، ٥٩٥، شواهد المغني ١٥٤، ٢٧٣، ابن الشجري ١/ ٣٠٦، ٢/ ٤٥، [١٠١]، سيبويه ١/ ١١٣، ٣٣٣، ٢/ ٤٦، ٤٧، [١٢٢، ١٦١، ١٩٥، ٣٠٢] </td></tr><tr><td>المرمِل<br>البطَّل <br>الأفضل <br>[الممرجل] </td><td> « </td><td> العجاج </td><td> الخزانة ٢/ ٣٢٢، ٣٢٧، سيبويه ١/ ٢١٧، ٣٣٤، ٢/ ٣٤٥ </td></tr><tr><td>حوب حل<br>لأبي الهجنجل <br>وأضحى من عل </td><td> « </td><td> أبو الهجنجل </td><td> شواهد المغني ١٥٣ </td></tr><tr><td>من التدلدل <br>ثنتا حنظل</td><td> « </td><td> خِطام المجاشعي (¬٢) </td><td> الخزانة ٣/ ٣١٤، ٣٦٠، ٣٦٧، العيني ٤/ ٤٨٥، ابن الشجري ١/ ٢٠، سيبويه ٢/ ١٧٧، ٢٠٢، [مع ١/ ٢٠٩] </td></tr><tr><td>كلّها من أرجُل </td><td> « </td><td> ــ </td><td> الخزانة ٤/ ٢٩١</td></tr></table>
_________
(¬١) في كتاب سيبويه (٢/ ٤٦): «من علُ» شاهدًا على أنه مبنيٌّ على الضم. وهو من أرجوزة مكسورة الروي.
(¬٢) ونسب لغيره. [المؤلف].','21','256',NULL,NULL,'0');
INSERT INTO [b351] VALUES (283,'<table width="95%" dir=rtl><tr><td>بأيادي غزّل </td><td> « </td><td> [جندل بن المثنى] </td><td> ابن الشجري ٢/ ٣٦</td></tr><tr><td>وردتُه عن منهل </td><td> « </td><td> ــ </td><td> شواهد المغني ١٤٨، ابن الشجري ٢/ ٢٦٩</td></tr><tr><td>من أهل الحلال </td><td> رمل </td><td> عبيد بن الأبرص </td><td> الخزانة ٣/ ٢٢٣، ٢٣٦، العيني ١/ ٥١١</td></tr><tr><td>من قيلٍ وقال </td><td> « </td><td> ابن مقبل </td><td> سيبويه ٢/ ٣٥</td></tr><tr><td>بالماء الزلال </td><td> « </td><td> عدي بن زيد </td><td> الخزانة ٤/ ٤٢</td></tr><tr><td>قلنا يا لَمالِ </td><td> « </td><td> مرة بن الرّواغ </td><td> العيني ٤/ ٣٠٠</td></tr><tr><td>لم تخمل </td><td> سريع </td><td> المتنخل الهذلي </td><td> ق (الأهيل، برقة أجول)</td></tr><tr><td>× ولا واغلِ <br>الباسل</td><td> « </td><td> امرؤ القيس </td><td> الخزانة ٢/ ١١٨، ٢٧٩، ٣/ ٥٣٠، ابن الشجري ١/ ٢٦٤، سيبويه ٢/ ٢٩٧، [مع ١/ ٣٢] </td></tr><tr><td>لدى السائل </td><td> « </td><td> سعية بن غريض (¬١) </td><td> الخزانة ٣/ ٥٦٥</td></tr><tr><td>عن الجاهل </td><td> « </td><td> كعب بن زهير </td><td> الخزانة ٤/ ١١</td></tr><tr><td>أو وائل </td><td> « </td><td> ــ </td><td> الخزانة ٤/ ٥٨٨</td></tr><tr><td>مشغول بمشغول </td><td> « </td><td> ــ </td><td> مع ١/ ٩٠</td></tr><tr><td>× أو عيهلِّ <br>على الكلكل <br>تعتلّي <br>المولّي </td><td> س مشطور </td><td> منظور بن مرثد الأسدي </td><td> الخزانة ٢/ ٢٨٣، ٥٥١، سيبويه ٢/ ٢٨٢، ابن الشجري ١/ ٥٠، ٢/ ٢٦ </td></tr></table>
_________
(¬١) في الأصل: «سعيد بن عريض» نقلًا عن الخزانة، وهو تحريف. انظر «الأغاني» (٣/ ١٢٩). وفي «الأغاني» (٢٢/ ١٢٢): « ... عريض». وانظر «الإكمال» لابن ماكولا (٧/ ٩).','21','257',NULL,NULL,'0');
INSERT INTO [b351] VALUES (284,'<table width="95%" dir=rtl><tr><td>لم يصلّ </td><td> « </td><td> ــ </td><td> الخزانة ٣/ ٦٢٤</td></tr><tr><td>لا تسالي  <br>من ذي آل </td><td> « </td><td> محمد بن الحتحات </td><td> ق (ألال)</td></tr><tr><td>يا مَرْبع الضلال </td><td> س مشطور </td><td> غضوب أخت بني ربيعة </td><td> شواهد المغني ٣٨</td></tr><tr><td>ذي الجلال </td><td> « </td><td> رقيم </td><td> الخزانة ٣/ ١٦٩</td></tr><tr><td>أيما تهليل </td><td> « </td><td> ــ </td><td> الخزانة ١/ ٤٨، ٥٣، ٥٤، ٥٦</td></tr><tr><td>يا خيرة الفسيل <br>أن تقيلي <br>ولا وبيل</td><td> « </td><td> أحيحة بن الجُلاح </td><td> الخزانة ٢/ ٣٠٨، العيني ٤/ ٣٦، ابن الشجري ١/ ٣٤٣ </td></tr><tr><td>كمعرس الدئل </td><td> منسرح </td><td> كعب بن مالك </td><td> العيني ٤/ ٥٦٣</td></tr><tr><td>عن حيال </td><td> خفيف </td><td> الحارث بن عُباد </td><td> الخزانة ١/ ٢٢٦، ابن الشجري ٢/ ٢٧٠</td></tr><tr><td>بطائف الأهوال <br>نصال <br>بالخال <br>خلود الجبال</td><td> « </td><td> الأعشى </td><td> الخزانة ٢/ ١٥٧، ٢٢٣، ٣/ ٢٣٥، ٤/ ١٥٥، ١٧٦، العيني ٢/ ١٠٦، ٣/ ٢٥١، ٤/ ١٩٨، شواهد المغني ٢٣٤، ٣١٧، م ٢/ ١٤٧، ق (با دَولي)، ابن الشجري ١/ ٣٠، ٢/ ٢٦٩، [مع ١/ ٥، ٨٠، ٩٩، ٢/ ٣] </td></tr><tr><td>البوالي </td><td> « </td><td> يزيد بن مفرّغ </td><td> الخزانة ٢/ ٢١٥، ٥١٦</td></tr></table>','21','258',NULL,NULL,'0');
INSERT INTO [b351] VALUES (285,'<table width="95%" dir=rtl><tr><td>كحلّ العقال </td><td> « </td><td> أمية بن أبي الصلت (¬١) </td><td> الخزانة ٢/ ٥٤١، ٤/ ١٩٤، العيني ١/ ٤٨٤، شواهد المغني ٢٤٠، ابن الشجري ٢/ ٢٣٨، سيبويه ١/ ٢٧٠، ٣٦٢، [مع ١/ ٤، ٦٩]</td></tr><tr><td>وكل هلال </td><td> « </td><td> (¬٢) </td><td> الخزانة ٢/ ٥٤٣</td></tr><tr><td>لفتنة الرحال </td><td> « </td><td> حنيف بن عمير اليشكري </td><td> الخزانة ٢/ ٥٤٣</td></tr><tr><td>والأوحال </td><td> « </td><td> حنيف أيضًا (¬٣) </td><td> الخزانة ٢/ ٥٤٤</td></tr><tr><td>وسط الرحال </td><td> « </td><td> السُّليك </td><td> الخزانة ٣/ ١٢٨</td></tr><tr><td>مولع بالمعالي </td><td> « </td><td> ــ </td><td> مع ٢/ ١١٧</td></tr><tr><td>كثيرة الأقتال </td><td> « </td><td> ابن قيس الرقيات </td><td> الخزانة ٤/ ١٧٨</td></tr><tr><td>بحكمة المتعالي </td><td> « </td><td> ــ </td><td> مع ١/ ٧٩، شواهد المغني ٢٨٦</td></tr><tr><td>فيها نزال </td><td> « </td><td> ــ </td><td> الخزانة ٤/ ٢٧٥، العيني ٣/ ٢٦٥، [مع ٢/ ٢٧]</td></tr><tr><td>والسنين الخوالي </td><td> « </td><td> عبيد بن الأبرص </td><td> العيني ٤/ ٤٦١، مع ١/ ١٥٧، [شواهد المغني ٣١٧، ابن الشجري ٢/ ٢٦٩]</td></tr><tr><td>حولي </td><td> « </td><td> نبيه بن الحجاج </td><td> الخزانة ٣/ ١٠١</td></tr><tr><td>بأعظم سُول </td><td> « </td><td> ــ </td><td> العيني ٢/ ٢٩٤، [مع ١/ ١٢٠]</td></tr><tr><td>من سبيل </td><td> « </td><td> ــ </td><td> العيني ٤/ ٢٣٠</td></tr></table>
_________
(¬١) وجاء في الأشعار الثلاثة الآتية. [المؤلف].
(¬٢) نسب لأبي قيس اليهودي، وقيل: لابن صرمة الأنصاري، وقيل: لأبي قيس صرمة بن أبي أنس الأنصاري. وانظر في «الإصابة» ترجمتي صرمة بن أنس وصرمة بن مالك. [المؤلف].
(¬٣) ونسبت لنهار ابن أخت مسيلمة. [المؤلف].','21','259',NULL,NULL,'0');
