INSERT INTO [b351] VALUES (320,'<table width="95%" dir=rtl><tr><td>غير ميمّم </td><td> « </td><td> المتنبي </td><td> الخزانة ١/ ٣٨٤</td></tr><tr><td>أظفارها لم تقلَّم <br>× المكرَّم </td><td> « </td><td> أوس بن حجر </td><td> الخزانة ١/ ٤٤٣، ٣/ ١٦١، [٤٩٤]، ابن الشجري ٢/ ٨١، سيبويه ١/ ٣٣٦ </td></tr><tr><td>السمين بدرهم </td><td> « </td><td> زياد الأعجم </td><td> الخزانة ٢/ ٩٠</td></tr><tr><td>عينيك والفم </td><td> « </td><td> ــ </td><td> الخزانة ٢/ ١٣٨</td></tr><tr><td>لا يَبُؤ الدمُ بالدم </td><td> « </td><td> تغلبي (¬١) </td><td> سيبويه ١/ ٤٥٠</td></tr><tr><td>بين عرب وأعجم </td><td> « </td><td> أبو محمد بن حزم </td><td> الخزانة ٢/ ٣٣٠</td></tr><tr><td>القناة من الدم <br>السماء بسُلَّم </td><td> « </td><td> الأعشى </td><td> الخزانة ٢/ ٣٣٠، العيني ٣/ ٣٧٨، شواهد المغني ٢٩٨، ق (أجياد)، ل (ب رأ)، سيبويه ١/ ٢٥، ٢٣١، [مع ٢/ ٥٩] </td></tr><tr><td>× من وراءٍ ومقدم <br>كفّ ومعصم <br>أيّ مأتم <br>× اللسان من الفم </td><td> « </td><td> أبو حية النميري </td><td> الخزانة ٢/ ٣٦٦، ٣/ ٣٧٩، ٤/ ٢٨٢، شواهد المغني ٢٤٥، ٢٥١، ابن الشجري ١/ ١٢٣، ٢/ ٢٤٤، سيبويه ١/ ٢٠٥، ٤٧٧، [مع ٢/ ٣٥، ٤١]  </td></tr><tr><td>لا تخلُّوا (¬٢) لهم دمي </td><td> « </td><td> كبشة بنت معد يكرب </td><td> الخزانة ٣/ ٧٧، ٣٣٢</td></tr><tr><td>دمع عينيك يسجم </td><td> « </td><td> سلولي </td><td> الخزانة ٣/ ١٦٣، سيبويه ١/ ٤٣٤</td></tr></table>
