INSERT INTO [b351] VALUES (346,'<table width="95%" dir=rtl><tr><td>ذو الحمق اللئيم <br>والرجل الحليم </td><td> « </td><td> زياد الأعجم </td><td> شواهد المغني ٧٤، ١٧١</td></tr><tr><td>بني كلاب أكرمُ </td><td> كامل </td><td> عامري </td><td> الخزانة ٢/ ١١٨</td></tr><tr><td>في الحوادث مُعْلَم <br>يتوسَّم </td><td> « </td><td> طريف بن تميم العنبري </td><td> سيبويه ٢/ ١٢٩، ٢١٥، ٣٧٨</td></tr><tr><td>أين المطعم </td><td> « </td><td> أبو وجزة </td><td> الخزانة ٢/ ١٤٧، [٤/ ١٠٤]</td></tr><tr><td>ولا يُهِمُّ المغنم </td><td> « </td><td> [الأخزم بن قارب] (¬١) </td><td> ابن الشجري ٢/ ١١٤، سيبويه ٢/ ٣٨</td></tr><tr><td>ما يكون يقسم </td><td> « </td><td> المتنبي </td><td> ابن الشجري ١/ ٣٥</td></tr><tr><td>الهدى إعلام </td><td> « </td><td> أشجع </td><td> الخزانة ١/ ١٤٥</td></tr><tr><td>ميسرٌ ونِدام </td><td> « </td><td> [لبيد] (¬٢) </td><td> مع ١/ ٧٨، سيبويه ١/ ٩٨</td></tr><tr><td>على الرجال حرام </td><td> « </td><td> أبو نواس </td><td> الخزانة ١/ ٤٥٤</td></tr><tr><td>قتلي عليك حرام </td><td> « </td><td> امرؤ القيس </td><td> شواهد المغني ٣٢٤، ابن الشجري ١/ ٢٧</td></tr><tr><td>لنا الأيام </td><td> « </td><td> محمود الإربلي </td><td> الخزانة ١/ ٤٥٦</td></tr><tr><td>بمضيعة أيتام </td><td> « </td><td> ــ </td><td> ابن الشجري ٢/ ١٥٦</td></tr><tr><td>عليك والإسلام </td><td> « </td><td> راشد بن عبد الله السلمي </td><td> الخزانة ٣/ ٢٤٥</td></tr><tr><td>يملكها عليَّ حرام <br>عليه من قدّام <br>عليهم للئام </td><td> « </td><td> تميمي </td><td> العيني ٣/ ٤٣٧، ابن الشجري ١/ ٣٢٩، ٢/ ٢٦٣</td></tr></table>
