INSERT INTO [b351] VALUES (347,'<table width="95%" dir=rtl><tr><td>حقّه المظلوم <br>كلّ المرام يروم <br>× ندبٌ لها وكلوم <br>× والمختوم</td><td> « </td><td> لبيد (¬١) </td><td> الخزانة ١/ ٣٣٤، ٢/ ٣٢٩، ٣/ ٤٤١، ٤٥٦، العيني ٣/ ٥١٢، شواهد المغني ٢٦٩، ابن الشجري ١/ ٢٢٨، ٢/ ٣٢، سيبويه ١/ ٥٧، ٢/ ٢٧٤، [مع ٢/ ٢٠٢] </td></tr><tr><td>القذى خرطوم <br>× ولا محروم</td><td> « </td><td> الأخطل </td><td> الخزانة ١/ ٥٥٢، ٢/ ٥٥٣، ابن الشجري ٢/ ٢٩٧، سيبويه ١/ ٢٥٩، ٣٩٨ </td></tr><tr><td>مبتغيه وخيم </td><td> « </td><td> [محمد بن عيسى] (¬٢) </td><td> الخزانة ٢/ ١٤٧، العيني ٢/ ١٤٦، [مع ١/ ٩٩]</td></tr><tr><td>إذا فعلتَ عظيم </td><td> « </td><td> المتوكل الليثي (¬٣)؟ </td><td> الخزانة ٣/ ٦١٧، العيني ٤/ ٣٩٣، شواهد المغني ١٩٤، ٢٦٤، سيبويه ١/ ٤٢٤</td></tr><tr><td>طلل يلوح قديم </td><td> « </td><td> ــ </td><td> الخزانة ٤/ ٤٠٣</td></tr><tr><td>حديثهن رخيم </td><td> « </td><td> كثيّر </td><td> ابن الشجري ٢/ ٢٦٨</td></tr><tr><td>شؤونها سَجْم </td><td> كامل أحذ </td><td> المخبل </td><td> الخزانة ١/ ٨٢، ق (الأغدرة)، ل (ظمأ)</td></tr></table>
