INSERT INTO [b351] VALUES (349,'<table width="95%" dir=rtl><tr><td>دُونَ ذاك حِمام </td><td> « </td><td> الكميت بن معروف؟ </td><td> العيني ٤/ ١٠٩، شواهد المغني ٢٦١، [مع ٢/ ١٦١]</td></tr><tr><td>أو ما عليك حرام </td><td> « </td><td> المتنبي </td><td> ابن الشجري ١/ ٢١٦</td></tr><tr><td>بظهر غيبٍ لئيم </td><td> « </td><td> حسّان (¬١) </td><td> الخزانة ٤/ ٤٦١، العيني ٤/ ١٣٥، ابن الشجري ٢/ ٣٣٤، سيبويه ١/ ٤٨٨</td></tr><tr><td>النجوم <br>النعيم</td><td> « </td><td> حسّان </td><td> الخزانة ٤/ ١٤٢، ٤٦٢، شواهد المغني ١١٦ </td></tr><tr><td>من الرجال الكريم<br>في الضلال يهيم </td><td> « </td><td> عبد الرحمن بن حسان </td><td> الخزانة ٤/ ٤٦٣ </td></tr><tr><td>عقعقان وبُوم </td><td> «(¬٢) </td><td> ــ </td><td> مع ١/ ١٤٤، شواهد المغني ٣٣٠، [مع ١/ ١٤٤]</td></tr><tr><td>لها حَمُ </td><td> خفيف مجزوء </td><td> ــ </td><td> ابن الشجري ٢/ ٣٧ (¬٣)</td></tr><tr><td>كلِفٌ مُغْرم <br>هو الدرهم</td><td> متقارب </td><td> ــ </td><td> الخزانة ٣/ ٤٧٧ </td></tr><tr><td>وكلُّهمُ ألْوم </td><td> « </td><td> [أمية بن أبي الصلت] </td><td> العيني ٢/ ٤٦٠، شواهد المغني ٢٦٥، ابن الشجري ١/ ١٣٣، [مع ١/ ١٤٢]</td></tr></table>
