-- Insert data for b36077
INSERT INTO [b36077] VALUES (1,'<span data-type="title" id=toc-1>الفنزج أي البالو</span>
<span data-type="title" id=toc-2>صفة ليلة من ليالي الشتاء</span>
ليلةٌ أضحيانةٌ قمراءُ، من ليالي الشّتاءِ، وأفقِ سجسج، كأنّه روضُ البنفسجِ، وهواءٌ رقَّ وطاب، فكأنّه عتابٌ بين أحباب، وكأنّما استدار الزّمان، وكأنّ أزار نيسان وقد أخذت (فينا) زخرفها، ولبست رفرفها، فحيثما كنت فأجنحة الطواويس، وأرواح الفراديس، وأصوات النواقيس.',NULL,'1',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (2,'<span data-type="title" id=toc-3>وصف القصر</span>
وثمّ قصر على النّهر كأنّه قصر غمدان، أو خورنق النعمان أو السّدير، أو القصر الكبير أو الزّاهر، أو دار ابن طاهر، أو الجعفريّ، أو الإيوان الكسرويّ.

<span data-type="title" id=toc-4>دور ومقاصير هذا القصر</span>
قد ارتفعت قبابَهُ في الأجواءِ، فكأنّ أبراجَهُ أبراج السّماءِ، وكأنّ كلّ ردهةٍ بطحاء، وكلّ روضةٍ صنعاء، بلاط وخندق، ودارات وديسق، وأبهاء وجوسق وكهرباء تضيء الأرجاء، كأنّها بدر، أو فجر.',NULL,'2',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (3,'وصلتُ إلى ذلك القصر ففُتِحَ الباب. وكُشِفَ الحجاب. فإذا جنةٌ وحرير. وملكٌ كبير. ودنيا في دار. وليل ونهار، ووجوه تشرق وحليٌّ يَبرق. وقبابٌ وشراعات. ومقاصيرٌ وسرادقات. وحنى كعطوف القسى. وصحون. في فسحة الظّنون. تقدَّر بالأفكار. لا بالأبصار وسقوفٍ من مرمر وأرضٍ من عرعر وكأنّ كلّ سقفٍ لوحٌ مصور. وكلّ أرضٍ روضٌ منوّر.
وإذا نظرت إلى غرائب سقفه ... أبصرت روضاً في السّماء نضيرا
وَضَعَتْ به صُنّاعُها أقلامَها ... فَأَرَتْكَ كلّ طريدةٍ تصويرا
وأبواب. كأنّها في حُسْنِها أبوابٌ من كتاب. في مصراعين كعاشقين. فتلاقٍ وافتراق.
فأبوابُها أثوابُها من نقوشِها ... فلا ظلمٌ إلاّ حين تُرْخى ستورها
وإذا الحجراتُ قد فُرِشَتْ بأراضٍ. كأنّه قطع الرّياض
بسطٌ أجادَ الرّسمُ صانِعُها ... وَزَها عليها النَّقشِ والشَّكل',NULL,'3',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (4,'فيكادُ يقطفُ من أزاهِرها ... ويكادُ يسقطُ فوقها النَّحل
ورُصفت في جوانبها أرائِك وحجل وطوارق وكلل وشوار وإنماط. وزرابي. ورياط ومطارح من ديباج ونضائد من عاج. عليها قطوع من سمّور وسنجاب. وعروش من إستبرق وزرياب في ألوان الحيقطان. وأجنحة الفواخت والورشان.
حتّى اتّكأْنَ على فرشٍ يُزَيِّنُها ... من جَيِّدِ الرقم أزواج تهاويل
فيها الطّيور وفيها الأُسْدُ مُخدرة ... من كل شيءٍ ترى فيها تماثيل
وقد رُكِّزتْ في الحيطان صفوف من مشاجب ورفوف عليها آنية عادية.
وعساس صينية وصحاف وسكرجات. وأجفان وطرجهارات. وبين ذلك مرايا تتقابل فتجمع الآحاد. وتعدّد الأفراد. إنْ وقفَتْ أمامها الحسناء. رأيت بدر السّماء في عين',NULL,'4',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (5,'ماء حسنٌ لا نظير له في البريّة. إلّا صورته على الماويّة فإن انصرَفَتْ عنها تركتها كربع خلاء أو صحيفة بيضاء. أو قلب ذي ملالة لا يثبت فيه إلّا ما كان حياله. وقام في الأركان تماثيلَ وتصاويرَ وأنصابَ وقواريرَ ممّا صنع (أوفرباخ) و (مليسونيا) و (ولمباخ) فكأنّما الدّار زون. أو معرض فنون وقد وُضِعَ في الأبهاء موقدٌ للاصطلاءِ كأنّ الجمر فيها نظر محنّق أو نار المحلّق وكأنّ الرّماد عليه عثير وأحاط بالدّار نوافذ وطاق. تطلُّ على الآفاق وتنظر الرّوضَ. والحوضَ. والمدينةَ والزّينةَ.
فمِنْ شُهبٍ تمتدُّ في الجوِّ مصعدا ... وتلوي على جنبيه مثل الأراقم',NULL,'5',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (6,'وتمطر فيه لؤلؤاً وزبرجدا ... شآبيبُ منها ساجمٌ بعد ساجمِ
فطوراً ترى أنّ السّماء حديقة ... تَفَتَّحَ فيها النّور بين الكمائم
وحيناً ترى أنّ الحديقةّ في الدُّجى ... سماء تهاوي بالنّجومِ الرَّواجم
أمّا الأضواء والأنوار. فالشّمس في ضحوة النّهار. قد عُلِّقَتْ بالسّقوف.
وتألّقت في الرُّفوف. وتلوَّنت كالأزهارِ وتشكَّلت كالأثمارِ وتدلَّت بينها الثُّريات كأنّها أشجارٌ مفتَّحة النوَّار وكأنّ أقياسها آذان جيادٍ أو عيون جرادٍ أو قطع أفلاذٍ أو صفائح فولاذٍ أو ذبالٍ على أسلِّ أو مرآةٍ في كفِّ الأشلِّ.
فيا لكَ من ليلٍ كأنَّ نجومه ... بكل مغار الفتل شدَّت بيذبل',NULL,'6',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (7,'<span data-type="title" id=toc-5>جمال النساء في باريس</span>
<span data-type="title" id=toc-6>حسان هذا القصر</span>
وثمّ الخرد الحسان. كاللؤلؤ والعقيان. من كل عطبولٍ رفلة أو أسحلانةٍ ربلة. أو خليفٍ بهتانة. أو رهرهةٍ فينانة. أو لاعةٍ سيفانة.
صدورٌ كالأغريض أو صدورُ البزاةِ البيض. وسواعدُ كأنّها شماريخَ من ماسٍ. أو مرمرٍ نحته فدياس وعيونُ كأنّ بين أهدابها رام من بني ثعل. أو أسدُ بين طرفاءٍ وأسلّ. أو أنّها',NULL,'7',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (8,'نرجسٌ عطشان. أو سيوفٌ تقتلُ وهي في الأجفان، وقد امتزجَ فيها الفتر بالحُور، فهي سكرى ولا مُدام. ووسنى ولا مَنام وفمٌ كأنّه أقحوانةٌ لم تتصوّح. ووردةٌ لم تتفتّح. يضحكُ عن جُمان. ويتنفّسُ عن ريحان. وينطقُ عن ألحان وخدودٌ كنارِ أخدود أو تفاح. أو ماءٍ وراح. أو الشّفق في الصّباح وردٌ يفتحه النظر. ويشعشعه الخفر. كأنّ حياءهُ الجُلَّنار. وبياضهُ ماءٌ واقفٌ جار.
إذا مَشَيْتِ على الحصباءِ صيَّرَها ... شعاعُ خدَّيْكِ ياقوتاً ومرجانا',NULL,'8',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (9,'<span data-type="title" id=toc-7>ما عليهن من الوشى والأكسية</span>
وقد اتشحْن بروداً من إبريسم وخز. وإستبرق وقز. كأنّها رقراق السّراب. أو برود الشّباب. كأنّ ألوانها أصيلٌ شفّ عنه غمام: أو أشعة الشّمس في أطواقِ الحَمام.

<span data-type="title" id=toc-8>حليهن</span>
وعليهنّ الحليّ من أربة وداح ويارج ووشاح. وقرمل وعضاد. ونقرس وزراد خاتم فارد. كأنّه عطارد. وسوار لمّاع. كأنّه الهلال في الذّراع.

<span data-type="title" id=toc-9>الموسيقى</span>
ثمّ صَدَحَتِ الموسيقات. وترنَّمت الكنارات. من دريج وصنج وزمخر وونج.',NULL,'9',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (10,'فكأنّما جاوب البلبل الهزار في الأسحار. وشدا مخارق وزنام، بالأنغام وكأنّما تلك الأصوات نسيمٌ عليل. والقوم أغصانٌ وكلُّ آلةٍ صور إسرافيل ينفخ الأرواح في الأبدانِ وإذا بالفتيانِ والغيدِ الحسانِ. والأسوارِ: وذات السّور. قد وثبوا للفنزج في المدرجِ.',NULL,'10',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (11,'<span data-type="title" id=toc-10>المرقص</span>
<span data-type="title" id=toc-11>أثناء الرقص</span>
وإذا فُلْكٌ يدور بالكواكب، من الكواعب، وإذا إعصارٌ أو حرفٌ جار، أو مهارى في خبب أو نجومٌ ذوات ذنب.
فناهيك بسير النضناض على الرضراض أو مشي القطا الكدري في الدمث الندى ونفرة السرب للشرب. حركاتٍ كأنّها لخفَّتِها سكون. وسيرٍ كسير الشّمس لا تستبينه العيون.',NULL,'11',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (12,'وأمشاطٍ لا تكاد تمسّ الأرض كأنّها آس يجسُّ النبض.
وكأنّما الخصور ماء. والصّدور هواء. والأعناق أطواق. والسّواعد مسائد. والألحان ميزان.
من كلِّ مائسةِ الأعطاف يجذبها ... موارٌ دعصٌ من الكثبان ممطور
ترعى الضرب بكفّيها وأرجلها ... وتحفظ الأصل من نقصٍ وتغيير
وتغرب الرّقص من لحنٍ فتلحقه ... ما يلحقُ النَّحوَ من حذفٍ وتقدير',NULL,'12',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (13,'وفي يديها غضيض الطرفِ ذو هَيَفٍ ... صاحي اللواحظِ يثني عطف مخمور
تظلّمت وجنتاه وهي ظالمةٌ ... وطرفه ساحرٌ في ذي مسحور

<span data-type="title" id=toc-12>البوفيه</span>
ولمّا انتصفَ اللّيل شطرين. وأمسى بين بين. رُفِعَتِ الرِّياط عن قاعة السّماط فإذا زخاري ورواء وزبرج وبهاء، وبنودٌ تخفق، وتهاويلُ تألق، وصحافٌ من جزع وجام من ينع، وغرب وأكواب، وصراحيّات وعلاب وقذمور وورسى، وخزفٌ صينيٌّ',NULL,'13',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (14,'وفي كلِّ ركنٍ روضةٌ معيشةٌ وبنانةٌ مخصبةٌ ونوردجةُ نوّار، ورعلة أرطاب وأزهار، فكأنّما القاعة جونة عطّار، أو أيكة غب قطار.
وبين ذلك سماطُ المُعِزِّ في قاعة الذهب، وجفنة ابن جدعان في العرب وقطعٍ من نون أو لحمِ طيرٍ ممّا يشتهون، وطباهجة وخواذب، وصلائق وصناب والسّلج والرشراش والقتن والهشاش والفانيد والمسير، واللوزينج والمزعفر وأثمارٍ',NULL,'14',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (15,'جنيّةٍ من مشلوز وملاحية، وجوح صنوان، ومن كلِّ فاكهةٍ زوجان ورحيقٍ من قرقف وقنديل، ودازي وسلسبيل، في ريح العنبر الورد، ومزاج العطري والبند موائد لا يفنى ما عليها ولا ينفذ كأنَّه نعيم أهل الجنَّةِ، كلّما فني يتجدد.',NULL,'15',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (16,'<span data-type="title" id=toc-13>الخمر</span>
<span data-type="title" id=toc-14>الشراب وقواريره</span>
خمرٌ كأنّها الزبخ، أو المريخ، عين الشّمس في كأسٍ وياقوتٍ مذابٍ في أكوابِ شعلةٍ شعلاء، يوقدها الماء برقٌ في غمامة، ووردٌ في كمامة مني ومنون، وريقُ ليلى في فمِ المجنونِ كأنّها سراجٌ يوقَدُ في زجاجٍ، أو إكسيرٍ، أو دمعٍ طليقٍ على أسيرٍ، أو دينارٍ منقوشٍ، أو ورقِ المردقوشِ أو عمودٍ من صباح، بين السّقاةِ والأقداحِ وكأنّ حُبَبَها عِقْدٌ، أو دمعٌ على خدٍ،',NULL,'16',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (17,'أو لام والماء حسام منظام يُكَبِّرُ المحسوس، في النفوسِ، إن فرح وإن ترح، تبعث على الصّدق في النّطق فتعقد اللّسان للكتمان تحكُمُ في العقلِ حُكْمَ مَنْ جار، أو حكم الزّمان في الأحرار شربٌ يلذّه غير الظمآن ولا يُروى المرء منه وهو صديان، وسقى بنبت الورد في الخدود والرنح في القدود كأنّها في النّفس روحُ الرّجاء وراحةُ اليأس منطادٌ يخرج بالنّفوس، من هذا العالمِ المنكوسِ جمرٌ ولا شرر، ونفعٌ أقلُّ من ضرر.',NULL,'17',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (18,'<span data-type="title" id=toc-15>انتهاء الليل وانصراف الناس</span>
ولمّا هم اللّيل، بطي الذّيل، وأشرف الظّلام، على الانصرام، هبَّ الأضيافُ للانصراف، فإذا كلّ إنسانٍ يتكلم بترجمان، وينظر إلى الأنام، بعينٍ إنسانها قد نام، نثبت في خلج، وتماسك في فلج وإذا زهرٌ منثور، ودخانٌ منشور، وقدحٌ مكسور، وجميلٌ مخمور، وليلٌ كالغداف، وندى ببلِّ الطراف، وقرٌّ لو رميتَ فيه كأس الرحيق، عاد عقداً من عقيق.
وكواكب كأنّها أعينٌ حول، أو زهرٌ مطلول، أو عقدٌ منتثر أو جلدُ نمر، فما زال الجمع ينصرف، واللّيل ينكشف، حتّى بدا الصّباح في التّخوم، بين النّجوم، كأنّه غديرٌ منبجس، في روضةِ نرجس أو سيلٌ طمى على نوار أو ملاءة، جمعت لؤلؤ النثار فغاب في ذلك الضّياء، كواكب الأرض والسّماء.',NULL,'18',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (19,'<span data-type="title" id=toc-16>الرحلة إلى القسطنطينية</span>
نهضتُ من القاهرة المُعَزِّيّة، قاصداً القسطنطينيّة، وهي بلدُ الإمامِ ومدينةُ السّلامِ، ودارُ خلافةِ الإسلامِ، فركبتُ سفينة عدوليّة إلى الثّغور الفرنجيّة فجرى بنا الفلك في خضمٍّ عجاج ملتطمِ الأمواج، أخضرِ الجلد، كأنّه إفرند بحرٍ عباب، لا يقطِّعْهُ الخليل بأوتادٍ وأسباب، تصطخبُ فيه النينان، وتضطرب الدعاميص والحيتان.

<span data-type="title" id=toc-17>سير السفينة في البحر</span>
وأخذتِ السّفينةُ تشقُّ اليمّ، شقّ الجلم، في ريحٍ رخاء، أو زعزعٍ',NULL,'19',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (20,'ونكباء، فهي تارة في طريقٍ معبّد، وميثٍ مطرّد، وطوراً فوق حزنٍ وقردد، وصرحٍ ممرَّد، فبينما هي تنساب، كالحباب، إذا هي تلحق بالرّباب، وتحلّق كالعُقاب، فتحسبها تارةً تحت القتام جبلاً تقشع عنه الغمام، وتخالها مرةً عائماً على شفا، قد غاب إلّا هامةً أو كتفاً.

<span data-type="title" id=toc-18>وصف البحر</span>
والبحر آونةٌ كالزّجاجِ الندي، أو السّيف الصدي، يلوح كالصّفيحةِ المدحوّة، أو المرآة المجلوّة. وحيناً يضرب زخاره، ويموج مواره، فكأنّما سيّرت الجبال، وكأنّما ترى قباباً فوق أفيال وكأنّ قبوراً في اليمِّ تُحْفَرْ وألويةً عليه تُنْشَرْ، وكأنّ العِدّ يمخض عن زبد. وكأنّ الدوي من جرجرة الآذي، زئير الأسد، وهزيم الرّعد.',NULL,'20',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (21,'<span data-type="title" id=toc-19>الأصيل في الماء</span>
فإذا كان الأصيل وسرى النسيم العليل، رأيت البحر كأنّه مبرد، أو درع مسرد، أو أنّه ماوية، تنظر السماء فيها وجهها بكرةً وعشية، وكأنّما كُسِرَ فيه الحلي، أو مُزِجَ بالرَّحيقِ القرطبلي وكأنّما هو قلائد العقيان، أو زجاجة المصوّر يؤلِّفُ عليها الأصباغ والألوان حتّى إذا أخضل اللّيل، وأرخى الذّيل.

<span data-type="title" id=toc-20>وصف الهلال</span>
بدأ الهلال كأنّه خنجرٌ من ضياء يشقُّ الظلماء، أو قلادةٌ، أو سوارُ غادةٍ، أو سنانٌ لواه الضِّراب، أو اللّيل فيلٌ وهو ناب أو عرجونٌ قديم، أو نونٌ من خطِّ ابن العديم أو برثنُ',NULL,'21',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (22,'ضيغم، أو مخلبُ قشعم أو ماءٌ خرج من أنبوبٍ في روض، أو ثمدٌ في أسفلِ حوض، أو وشيٌ مرقوم، أو دملجٌ من فضةٍ مقصوم أو قلّامة ظفر، أو صنّارٌ في شبكٍ في بحر.
أيا ضوَء الهلالِ لطفت جداً ... كأنّك في فمِ الدُّنيا ابتسام
يحبب لي سَناك العشق حتّى ... يصاحبني وأصحبه الغرام

<span data-type="title" id=toc-21>الليل والنجوم</span>
ثمّ إذا غاب الهلال وتوارى في الحجال. ألفيت الكونَ من السّوادِ في لبوس حديد أو لباس حدّاد وكأنّما الماء سماء، وكأنّ السماء ماء، وكأنّ النّجوم درٌّ، يموج في بحرٍ، أو ثقوبٌ في قبةِ الدّيجورِ، يلوح منها النّور، أو سكاك دلاص، أو فلقٌ وصاص أو عيونٌ جراد، أو جمرٌ في رماد، أو الماء، صفائحُ فضّةٍ بيضاء، سُمِّرَتْ بمساميرٍ صغار، من نضارٍ فلا تفتأ السّفينة تكابد الويل، من البحر واللّيل، حتّى يلوح من الأفق الضّياء، كابتسام',NULL,'22',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (23,'الشّفةِ اللّمياء فإذا السّفينة كأنّها سرٌّ كتمه الظّلام، وكشفه الضرام.

<span data-type="title" id=toc-22>الغذاء</span>
وكان غذاؤنا قطعاً من نون، ولحمَ طيرٍ ممّا يشتهون، وفاكهةً وأباً، وماءً عذباً، وفانيذاً مروقاً، وجلّاباً مصفقاً.

<span data-type="title" id=toc-23>الشراب</span>
أمّا الشّرب من الركب، فيطوف عليهم سقاة كجمّاع الثريا، بأقداح الحميّا وفي كلِّ مكان، أرائك وإيوان، وأضواء تبهر، وشموع تزهر، وناي ومزمر، وحديث وسُمَّر، فكأنّما نحن في المدينة لا في السّفينة، وفي أندرين أو جدر، لا في ذات ألواح ودسر وبعد ثلاثة أيام وكسر، قضيناها في البحر، وصلنا إلى أوروبا، فإذا أرضٍ أريضة، وبلادٍ عريضة، وجنةٍ وحرير وملكٍ كبير',NULL,'23',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (24,'كبرت حول ديارهم لمّا بدت منها الشّموس وليس فيها المشرق ثمّ بعد برهة من الزّمن نهضنا للظعن ورحلنا إلى القسطنطينيّة.

<span data-type="title" id=toc-24>وابور البر أثناء السير</span>
فركبنا إليها وابور البرّ في ليلة عريّةٍ فسرى بنا وكأنّه ثعبان، له عينان تقدّان، ينساب في
القيعان، ويلتوي على الرعان أو أنّه مُبْتَدَأ متعدّد الأخبار، أو كلم مجرورة بحرف جار، أو أنّه بيت ذو تقطيع، من البحر السّريع فتارةً وعلٌ على الجبال، وأخرى جدولٌ بين الأدغال، وآونةً ينطلق كالجراد، ومرّةً يثبُ كالجراد',NULL,'24',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (25,'وقد يدور في الصّعيد كالخذروف الوليد، إن ارتقى فدعوة المظلوم، أو انحط فروح الظّلوم. أسرى في الليالي من طيف الخيال، وأمضي في الذّهاب من العقاب،} وَتَرَى الْجِبَالَ تَحْسَبُهَا جَامِدَةً وَهِيَ تَمُرُّ مَرَّ السَّحَابِ {كأنّه غراب البين، إن نعب ففرقة بين اثنين، لمّا زال يطوي المنازل طي السجل، بين ارتحال وحلّ، إلى أن وصلنا دار السّعادة، وألفينا بها عصا الوفادة.',NULL,'25',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (26,'<span data-type="title" id=toc-25>جامع أيا صوفية</span>
في القسطنطينيّة اليوم محال، تُشَدُّ إليها الرّحال، وتُضْرَبُ بها الأمثال، فمن ذلك (أيا صوفية) وما أدراك ما هيه، مسجدٌ كأنّه هيكل، لجبلٍ قد طرح تربَهُ ورِضامَهُ، ورُكِّبَتْ أحجاره وعظامه قبّة جوفاء، كأنّها قبّة السّماء، فإنْ أوقدت رأيت بها الكواكب غير سائرةٍ، والأفلاك غير دائرةٍ، ودعائم كلّ دعامة كالحقِّ استقامة وأرض من مرمرٍ آلاق، وحجرٍ برّاق، يصف ما يحيط به من الأشياء، فكأنّه في وجه مرآة وضاء، وكأنّما تلتمع السّيوف في تلك السّقوف، ويكاد يرى القمر في ماء ذلك الحجر إلى محاريب وحنايا، وخبايا وزوايا، كأنّها ممّا صنع الجنُّ لسليمان بالصّفاح والصفوان.',NULL,'26',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (27,'فإنْ دخلته في العشاء الآخرة أبصرت الشّموع صنواناً وغير صنوان كأنّها رماح وفي كلِّ رمحٍ سِنان، وكأنّ أقباسها نضنضةُ الحيّات. أو إشارةُ السّبابةِ في التحيّات، ورأيت النّاس بين ركعٍ وسجدٍ، وأيقاظٍ وهجد، شِيَّبٌ ما زالوا يغسلون بالوضوء السّواد، حتّى مُحِيَ محو المِداد، وشبابٌ قيام للصّلاة كسطرٍ في كتاب، والكلُّ يجأرون بدعوة الإسلام، تحت أستار الظلام.',NULL,'27',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (28,'<span data-type="title" id=toc-26>خليج البوسفور</span>
خليجٌ كأنّه سيفٌ مسلول، أو سجنجلٌ مصقولٌ وعلى شاطئيه قرى ودساكر، ورساتيقٌ ومقاصر، وقصورٌ بيضٌ على الخضراء، كالنّجومِ في السّماء، أو أشرعةُ فلكٍ في ماء، وكأنّ كلِّ شاطئ منهما قد انتهت المحاسن إليه، فلا يفضّل أحدهما على الآخر إلّا لكونهِ يطلُّ عليه، فإذا رأيت ثم رأيت حين دلوك الشّمس وقد شعشع نورها كلّ بناءٍ وغرس، وقد عكس في الماء، صورَ ما يحيطُ به من الأشياء، أبصرت في الماء قباباً من ذهب، وأهلةً من لهب، وكثباناً من زمرّد، وودياناً من زبرجد، وجبالاً وأيفاعاً. وحصوناً وقلاعاً، وسدراً ودلاعاً وسقوفاً من جوهر',NULL,'28',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (29,'وعمداً من مرمر، وصرحاً من قوارير، وتماثيل وتصاوير، ودوراً وحوراً، وناراً ونوراً، وحللاً تُطوى وتُنْشَرْ، وسيوفاً تُغْمَدُ وتُشْهَرْ، وأقماراً تصاغ وتُكْسَر فكأنّما تَقرأُ في البرِّ، قصيدةً من الشِّعر، وتنظرُ في البحر، فانوساً من سحر.',NULL,'29',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (30,'<span data-type="title" id=toc-27>منتزه البندلر</span>
وكم على سيفِ الخليج، من روض وثيج ومرأى بهيج، ورساتيق ورعان، وخلج وغدران، فكأنّما هذا المكان، شِعْبُ بوّان، أو روضة من رياض الجنان ومن أبهر ما يجلي للنّظر من تلك المياه والخضر، منتزه (البندلر) وهو رياض في رياض وبساتين وحياض، ووهاد وأنجاد، ونجاف وأسناد وأطيار تصدح، وأمواه تنضح، وأعطار تنفح وكأنّما في كل ناحية لوح مصوّر، أو برد محبّر، أو طراز على خز، أو وشي على قز، أو فسيفساء مفروشة أو دنانير منقوشة.',NULL,'30',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (31,'وقد حفّ الشّجر الدوّاح بتلك البطاح، فمن شوعٍ ودرماء وخلافٍ وطحماء وريحانٍ نضر، وعيدانةٍ مرجحنة، من سدر وقد تلاحقت غصونها، وتعرّشت خيطانها وفنونها، وخضب بينها العرفج، وأزهر الياسمين والبنفسج فكأن تحت كلّ عرشٍ إيواناً، وفوق كلّ فرشٍ ديواناً، وفي كلّ تربٍ جونة عطار أو مسكٍ بين أفهار وقد علّقت الطّير بهذا الشّجر، كأنّها ثمر، فمن فواخت وقطامي وحباراً وقماري.
وكأنّ كلّ ورقاءٍ على عود، حسناء في يدها عود، ترجع من كتاب الأغاني ضروب الخفيف الأوّل والثّقيل الثّاني، وتفوق',NULL,'31',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (32,'في الغناء أصوات معبد والميلاء، وألحان عنان والذلفاء وقد شُهِرَ (البندلر) بمائه، في عذوبته وصفائه، فلا يفتأ به ينحدر كما تكسر المرمر، ويلتوي على الأشجار، كالسّوار، وينبثق من غدر، وأفواه أسود ونمر ويذهب في الهواء كلسان السراج، ويعود كقبةٍ من زجاج، كأنّه في الصفاء دمع جرى، أو برق سرى، أو بلّور مذاب، أو نصل قرضاب، أو سبيكة فضّة أو معصمٌ بضّة، وكأن الحصباء تحت الماء، عقد منثور أو جوهر منشور وكثيراً ما يهطل المطر، على هذا الماء والشّجر، فإذا معركة شعواء، بين الخضراء والزرقاء فالوبل نبل، والقنا أشلّ والبروق ظبي وأسنّة. وفي كلّ غديرٍ جنّة.',NULL,'32',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (33,'<span data-type="title" id=toc-28>غابة بولونيا</span>
<span data-type="title" id=toc-29>وصف باريس</span>
يقبل المرء على باريس فإذا حدائق وقصور. وليلٍ كسوادِ العين كلّه نور وإذا البرجُ في طخيةِ اللّيل. وكأنّ سيراجه سهيل. برجٌ ماثلٌ كأنّه بابل. غير أنّ ذلك فرّق البشر وهذا جمعَ البدو والحضر. وإذا المدينةُ كأنّها في يوم الزّينة. وقد جاشت الطرق بالسّيارة. وزخرت البرازيق بالنظارة. فكأنّما',NULL,'33',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (34,'انفضح سيل العرم وكأنّما في كلّ سبيلٍ جيشٌ منهزم وكأنّ كلّ بهوٍ إيوان. وكأنّ كلّ شاهقةٍ رأسُ غمدان وكأنّما كلّ بستانٍ شِعْبُ بوّان وكلّ حائطٍ سدُّ ذي القرنين، وكلّ طريقٍ وادٍ بين الصدفين وكلّ قنطرةٍ قنطرةُ خرزاذ أو قنطرةُ البردان ببغداد وكلّ قصرٍ قصرُ المشتهي، وكل كنيسةٍ كنيسةُ الرّها وقد أقيم',NULL,'34',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (35,'على كلّ حنيّة، صنمٌ ليعوق في الجاهليّة، وفجرٌ في كلّ رحبةٍ عينٌ تجري على صخر، كعينِ الخنساءِ على صخر واجتمع في كلّ مرجٍ ذورٌ وصنج، وبدت في كلّ ناحيةٍ غرائبُ هندمند، وعجائبُ كوكبان والسّغد وفي هذه المدينة حرجةٌ من نزه الدنيا يقال لها (غابة بولونيا) وهي بطاحٌ في بطاح وروضةٌ فساح، وشجرٌ دوّاح، وعدٌّ جلواح وطرقٌ بين الأدغال كهدى في ضلال، وشموسٌ بين الأشجار كأنّها نثار، وكأنّ الأزهار في حيالها فرشٌ، والأنهار في خلالها صوارم في كفّ مرتعش، والنّهار في ظلالها، فجرٌ بين الضياء والغبش',NULL,'35',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (36,'وكأنّ في كلّ غصنٍ صوتُ غنّاء، وفي كلّ عشٍّ بيتاً فيه ضوضاء وكأنّ الأغصان، مواصل غضبان، أو كأنّها وهي تميل وتعتدل، شاربٌ ثمل، أو أنّها تريد العناق ويمنعها الخجل وفي جوانب هذه الحرجة صخورٌ وشعاب، وأحجارٌ وهضاب يتفجّر منها ماءٌ عرانية ذو دفاع، في حفافية الآس والدلاع، وتجري بينهما خلجٌ كأنّها أراقمٌ جدّت في الهرب أو فرّت من طلب، وكأنّ كلّ خليجٍ حسام، والظّلّ صداه، أو أنّه جامٌ والأصيل طلاه، أو أنّ ذلك الظّلّ عذارٍ في خدّ أسيل أو طرّةٌ على جبين صقيل وكأنّ الحصباء، في الماء ثنايا عذاب، وفي رضاب.',NULL,'36',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (37,'<span data-type="title" id=toc-30>في ظلام الليل</span>
وأهيب ما تكون هذه الحرجة إذا غاب النّور، وأقبل الديجور، وأمسى الكون كأنّه لوحٌ ممسوح، أو راهبٌ في مسوح وتراءت هي كأنّها حسناءَ في ستر، أو صحيفةً بيضاءَ كسرت عليها زجاجةٌ من حبر وكأنّما صُبِغَ كلّ غصنٍ بسواد وكأنّ كلّ فرعٍ جناحُ غراب
منآد وكأنّ أشجارها لجٌّ متلاطم، وكأنّ في كلّ أيكةٍ قبةٌ تتهدّم وفي كلّ عودٍ حيّةٌ تترنّم وكأنّ تربها إثمد، وكأنّ حصباءها ينعٌ أو زبرجد وكأنّ المصابيح فيها اشتعلت لترى الظّلام، لا لتكشف الإعتام وكأنّ النّجوم فوق تلك الأغصان أسنّةٌ على مران، أو أنّ كلّ غصنٍ من ذاك السّمر والخطّ، حسناءُ والثّريّا في أذنها قرطٌ، وكأنّ المجرّة جدولٌ فيه الحوت والسّرطان،',NULL,'37',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (38,'يسقي من عليٍّ ذلك البستان.

<span data-type="title" id=toc-31>في ضوء القمر</span>
فإذا بزغ القمر، وألقى نوره بين الشّجر، ألفيتها كأنّها غادة كعاب، عليها نقاب، وكأنّ قطعاً من ماسٍ بين الأغراس وكأنّ البدر عينٌ، تسيل عليها بلجين وكأنّ في كل خوطٍ سراج وكأنّ كلّ زهرةٍ ثغرٌ باسمٌ وفي كلّ جدولٍ أسنّةٌ وصوارم.

<span data-type="title" id=toc-32>في إشراق الصباح</span>
فإذا ما انطفأ النّجمُ مع الصّباح، كأنّه مصباح، وبدا الفجرُ',NULL,'38',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (39,'تحت الغيهبِ، كأنّه ماءٌ تحت طحلب وتلاه الإشراق كالشجّة السمحاق، أو نارٌ في رماد، أو سيفٌ عليه دمٌ جساد ألفيت الحرجة كأنّ عليها خسروانية فوقها وشائعُ من ذهبٍ سائل، أو حلّةٍ موشيةٍ بها جادى جائل، وكأنّما على كلّ ورقةٍ دينار، وفي كلّ جدولٍ كأسٌ عقار، وكأنّ كلّ غرسٍ عبهر، وكلّ زهرةٍ شنفٌ أنضر.

<span data-type="title" id=toc-33>حديقة النبات وما فيها من حيوان</span>
وفي هذه الغابة (حديقة النبات) وهي رقمةٌ زهراء ووديفةٌ غلباء كأنّما نشر كتاب ديسقوريدس في بستانها، ونثرت',NULL,'39',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (40,'ربيعيات كشاجم بين أيكها وخيطانها. أو كأنّها رامة، أو خفان، أو أنّها سفينة نوح حملت كلّ حيوان ففيها (القسورة) أبو الأشبال يرسف في الأغلال كأنّه في الرّتاج يزيد بن المهلّب في سجن الحجّاج في هامة كهضبة من تهامة، وعينين، كنارين في غارين، ونابٌ كأنّه',NULL,'40',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (41,'سيف زهير بن جناب، وظفر كأنّه في أول شهر.
و (الغيلة) كأنّها بروجٌ مشيَّدة، أو قناطرَ مقرمدة، أو قطعٌ من اللّيلِ على الأرضِ، أو لججُ البحر يدفع بعضها بعض أو سحابٌ ثِقال، أو أنّ أخفافها رحى تُطْرَحُ وتُشال أو أنّها ليل والنّاب هلال، أو أنيابها رماحٌ طوال (والفهد) كأنّما عليه من حدقٍ نطاق، أو نثرٍ عليه
الشّجر الأوراق تريد الفتك ولا يريد (امكر وأنت في الحديد) و (الظباء) تمرح بين الآكام كظباء مكّة صيدها حرام كأنّ كلّ ظبيةٍ دمية، وكأنّ في محاجرها عيون ليلى وميّة و (حمار الوحش) أحقب مدمج،',NULL,'41',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (42,'كأنّه المحلج، ملمّع الأطراف، كأنّما بُسِطَ عليه طراف، به شام كأنّها خطوط الأقلام وإلى جانبه قود ثمان، كأمراسِ الكتّان يدور بها بين الأسوار، كأنّه أسوار وقد ذكر بطحاء عمان، والغوير والصّمّان، حيث كان يرعى الجزع والأرطاب، إلى أن تتصوّح الأعشاب فيسوقها في البيداء إلى عيون الماء، تنجد في الأوعاث وترمي أيديها بالعرار والجثجاث مستويات في',NULL,'42',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (43,'الصّفّ، كأصابع الكفّ تحيد عن أظلالها فرقاً، وتهوي في الصوّان زلفاً حتّى إذا بلغت المنهل وردته تمصع بالأذناب، من لوحٍ وذباب وقد اختبأ لها الصائد في غيل قصباء، وناموس في جوف شجراء، وفي يده سهام حجريّة، وكبداء نبعيّة فرمى فألقى أتاناً، وانصاع الباقون مثنى ووحداناً و (الكلاب) على أضراب فمنها الضاري. الّذي أعدّه الشّاعر للطاري ومنها الألوف، الدّاعي للمعروف، ومنها السلوقيّ الّذي كأنّه القوس إلّا أنّه السّهم، والعفريت إلّا أنّه الرّجم، إذا وقف فهو نون، أو ساب فهو منون و (الحيّات) كأنّها دروع مطويّات وكأنّ نفحها',NULL,'43',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (44,'غليان مِرْجَل. أو صريف نابي جمل وبينها الحارية، وآخر كأنّها جزوع نخلٍ خاوية و (النّاقة) ثمّة كأنّها عربيٌّ في سوق الأهواز أو كلامٍ استُعْمِلَ على المجاز قد أضناها الشوق إلى كلّ مروارة أقفر من أبرق العزاف، ومن بريّة خساف لا ماءٌ بها إلا مأجٌ زعاق، كأنّه خمرٌ براق يحدوها هناة، أَرْفَقَََ بالإبل من مالك',NULL,'44',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (45,'بن زيد مناة فتصل كلّ عشيّة بسحرة وتشكّل أخفافها كلّ مجهل بحمرة.
مجالُ وحوشٍ ومجلى أنيس ... فيا حُسْنُ لهوٍ ويا منظر',NULL,'45',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (46,'<span data-type="title" id=toc-34>صلاح الدين الأيوبي</span>
قد ظهر في الأمّة سميذع نقاب كأنّه قسور غاب، قلب حول، لو عاودته نجوم الأفق لعاد ذو الرمح منها وهو أعزل يعبس وهو راض كالسّحاب، ويضحك وهو غاضبٌ كالقرضاب عاجلُ العفوِ آجلُ الانتقام كأنّ الملوك صفٌ وهو الإمام طبيب بأدواء الأمم حذاق، يعالج تارةً بالسُّم وطوراً بالترياق واحدٌ لم يختلف في فضله اثنان، نطقت بمآثره ألسن الخرسان والخرصان فقرّت بظهوره القلوب، وإذا هو صلاح الدّين يوسف بن أيّوب.
أنتَ الأميرُ الّذي ولّته همته ... بغير عهدٍ من السّلطان معهود',NULL,'46',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (47,'أقبلت جموعٌ فرنجيّةٌ مهطعين، وأرسوا لحرب الصّليب على حطّين، فلقيهم بجحفلٍ جرار، وحمل عليهم حملة المهاجرين والأنصار حَمِسٌ يقابل منهم الأعداء، أمثال الجحّاف وأبي براء، كأنّهم في الصفوف حتوف، أو أسود أظافرها السّيوف، وكأنهم من حبّهم للقتال يَرَوْنَ النّقع ليلٌ وصال، تموج على صدورهم الفضفاضة السّلوقيّة، والزّعف الحطميّة، وكأنّ كلّ درع ردن هلهال، أو غدير تحرّك عليه شمال، وفي أيديهم السّيوف اليزنيّة',NULL,'47',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (48,'والسّهام الحجريّة وكأنّ كلّ سنان أرقم، وكل كنانةٍ جلدة شيهم.
وإذا تكافحٌ وجَلاد، وأبطالٌ في عصواد، وجسومٌ تحت الصّعيد ورؤوسٌ فوق الصّعاد وعثيرٌ في العنان، كادت تفرخ فيه العقبان، أصبحت الأرض به ستّاً والسّماء ثمان، وخيلٌ تنزع قبّاً، وتضبح وثباً، كأنّها في الجدد، طيرٌ تنجو من الشؤبوب ذي',NULL,'48',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (49,'البرد، وطعنٌ كلّ طعنةٍ نجلاء، لا ينفع فيها عصائب الخمر ولا ثمر الرّاء، وإذا العداة بين هاربٍ بدمائه، وباركٌ متجعجعٌ في دمائه، وإذا جموعهم كأنّها عرفجٌ علقت به نار، أو ليلٌ كشفه نهار، وإذا بالقدس قد فتح للمسلمين وكانت العاقبة للمتّقين.',NULL,'49',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (50,'<span data-type="title" id=toc-35>على قبر نابليون</span>
وقفتُ على قبر نابليون أمس. أحدِّثُ النّفس بما في ذلك الرّمس فإذا استكانةٌ بعد صولة، وقبرٌ في جوفه دولة وصولجان كرته الأرض. أمسى مخراق لاعب. وسرير كان فوقه البسط والقبض. أضحى ملتقى ناع وناعب. اللهم غفراً. هذا غلّاب القياصرة. وقهّار الجبابرة. دفع عنه سلطانه الأبطال والأقيال ولم يدفع عنه الآرض والنمال. وكانت الأرض تضيق عن نفسه. فأمسى تسعه حفرة من رمسه، فواهاً لهذا الموت الذي',NULL,'50',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (51,'يخبت الأسود. ويقتلع أنياب الحيّات السّود. ويفكّ النطاق عن الجوزاء ويساوي عمرو بن درماء بالدّرماء.',NULL,'51',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (52,'<span data-type="title" id=toc-36>نابليون</span>
نابليون وما أدراك ما هو. اسمٌ ملأ كلّ مكان. واستغنى عن التعريف بابن فلان. إذ لم يرث المجد عن أب وجدّ، ورجلٌ جاد به الدّهر وهو البخيل بالرّجال. كما تجود الصخرة بالماء الزلال، وسمح الزمان منه بما هو فوق قدره، كما يسمح الترب بتبره، ومَلِكٌ جاء أخيراً فتقدّم على الملوك الأُوَلْ. كالعنوان يُكْتَبُ أخيراً ويُقْرَأُ أولاً، طَلَبَ مُلْكُ الثّقَلَيْن. ورغب أن يكون الإسكندر',NULL,'52',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (53,'لا ديوجين. وآزره على ذلك عزمٌ بمحو الشّرّ بالشّر. كما يداوى شارب الخمر بالخمر.
وطبع فيه نفعٌ وضرر. كالغمامةِ فيها صاعقةٌ ومطر. أو البحر إن صدم أغرق. وإن طلب جوهره أغدق، وَجْدٌ لو صحب الأدبار لأربى على الإقبال. ولو حالف النّقص لشأى الكمال، فسار إلى غايته القصوى بسيرٍ لا يرى كسَيْرِ ذكاءٍ في السماء، لا يصادفه في طريقه دولةً إلّا قلبها. ولا رايةً إلّا نصبها ولا حصنَ ثغرٍ يحوم منه نسر السّماء. على وكرٍ. إلّا تدلّى عليه مع الظّلام.',NULL,'53',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (54,'كما تدلّت عقاب من شماريخ الأعلام ولا يمٌ طمْ. أو بحرٌ خضمْ إلّا خاضه بالقدم. وشرب ماءه بدم، ولا وقائع إلّا خاضها فترك بها أياماً كيوم رحرحان. أو يوم جبلة بين عبسٍ وذبيان.
حتّى أقام له مُلْكاً أين منه مُلْكُ قيصر وكسرى. هو كرة الأرض قامر بها الرّجل فكسبها في ساعة وخسرها في أخرى.

<span data-type="title" id=toc-37>أسترليز وانتصاره على الروس والنمساويين</span>
كأنّي أنظر إليه يوم أسترليز وقد خرج لقتاله القيصران، في يوم أرونان فصابت بِقَرٍّ وما يوم',NULL,'54',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (55,'حليمة بسِرّ فاصطفّ حياله الرّوس، كالسّطور في الطروس، وثبتوا في الأخاديد، كالجلاميد، وابذعروا في السّهول كالوعول، وأقبل النمساويّون في كتيبةٍ جأواء، وملمامةٍ شعلاء ينزل أُولاها وليس بنازل، ويرحل أُخراها وليس براحل، فقابلهم من جيش الفرنسيس، بالدهياء الدردبيس، دوسر بسط جناحيه على الشّعاب كما بسطت جناحها العقاب، فلا ترى ثمّة إلّا أعلاماً تخفق، وحديداً يبرق، وجنوداً في المآذي كأنّها صخور في ماء،',NULL,'55',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (56,'أو أفاعي عرماء، أو أسود والسّيوف أنياب، أو عقارب شائلات الأذناب، ثمّ صمّ
القتال، وزلزل الزلزال، واتّقد الوهج، وسطع الرهج، فكأنّما ترى جاناً من مارجٍ من نار، أو إعصاراً يدور فوق إعصار، كأنّما مدينةٌ في حريق، وسماءٌ تهطل برحيق، وكأنّما فكّت الشياطين، وانسابت الثّعابين وكأنّما في قلب الأرض وهل، وعلى خدّها من الدّماء خجل، وكأنّما في الجوّ من الدّخان والنّار، ليل وشرق، ومن الرّصاص والشفار، وبل وبروق، كأنّما كُسِرَتْ قبّة السّماء، فَهَوَتْ بما فيها من نورٍ وظلماء، وكأنّما',NULL,'56',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (57,'كلّ صفٍّ من الجنود يميل بحائطٍ من جهنّم. فيلقاه الآخر من الحديد بلجٍّ من يم. فما ينكفئ حتّى ينطفئ. وبين ذلك خيول تكدّس: وسلاح مضرّس. وجماجم تغلق، وأشلاء تفرّق. ومنا ومَنون. وطعنٌ كأنّه طاعون، وشهيق وزفير. وعير ونفير. وصرعى كأنّما غالتهم الكؤوس. ووادٍ يسيل على العلمين فقاقيعه الرؤوس، ومقلةٌ في مخلبِ طائر، وكبدٌ في رجل عاثر، وبنانٌ في نابِ وحشٍ كاسر.
كم رأسِ شخصٍ بكى من غير مقلته ... دماً ونحسبه بالقاعِ مبتسما
هذا ونابليون قد أشرف على المرقب، فوق نهد سهلب،',NULL,'57',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (58,'ثُبِّتَ في المعمعان كأنّه حنذيذة من كتفيّ ثهلان.
لا تهوله كثرة البهم، ولا جموع الأمم، كأنّ جنده قليلٌ من ضرم في كثير من فحم. يقلب عينه يمنةً وشامة، ويجبر أخبار زرقاء اليمامة، فتُطوى الجنود لأمره وتُنْشَر وتُقَدَّم وتُؤَخَََّر، كأنّه في هذا الهرج والمرج، أمام رقعة من الشطرنج إلى أن يبدو له النّصر من خلل القتام، كما تلوح الشّمس من تحت الغمام.',NULL,'58',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (59,'<span data-type="title" id=toc-38>نابليون بعد زوال ملكه وهو معتقل</span>
وكأنّي أنظر إليه بعد ذلك وقد جار عليه الزّمان الجّائر، ودارت عليه الدّوائر، وأمسى جيشه الّذي قهر الأرض وهو مقهور كآنية الزّجاج قابلت غيرها فالكلّ كاسرٌ مكسور، وانتهى به السّير، من خير إلى ضير، كما يسير الهلال بسيره بدراً ويمحق به تارةً أخرى وزال مُلْكَه الضّخم، فغاب مغيب الشّمس في أفق من دم، وأصبح ولا دولة، ولا بأسٌ ولا صولة، كصنم الجّاهلية في الملّة الإسلاميّة، كان بالأمس ربّاً فأصبح حجراً صلباً وإذا هو معتقل في جزيرةٍ قاصية، وصخرةٍ عارية، كأنّه قسور نُقِلَ من بيداء، أو غيلٍ قصباء إلى قيودٍ وأصفاد، وبيتٍ من صنعةِ الحدّاد، فهو فيه يدور ويحور تارةً يبسم ويعجب، من دهر يكسر
النّبع بالغرب، ويصيد الصّقر بالخرب، ومرةً يطرق ويتفكّر: ويفتح عينه فيرى كثيراً ويغلقها فيرى أكثر وحيناً يُحني الرأس من اليأس.',NULL,'59',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (60,'وآونة تبعثه الأوجال إلى الآمال. فيودّ لو قام شبلٌ من نسله أو رجلٌ من أهله. فاسترجع ملكه بعد الذّهاب. وحفظ من نور ذلك المجد بقدر ما يحفظ البدر نور الشّمس بعد الغياب، وهيهات أن يقوم الأفيل. بعبء الفيل. أو تتساوى الأشياء. إذا تساوت الأسماء. أين ذباب السّيف من ذباب الصّيف. وأين السنبلة الخضراء من سنبلة السّماء، وقد يقف بقامته القصيرة. على قنة من قنن تلك الجزيرة. بروح الفكر في أمواج البحر. وإذا بظلّه قد طال على لججه. وامتدّ بعيداً على ثبجه. فيرى قامته وهذا الخيال فرّق ما بين حالته وما كان فيه من الدولة والإجلال، فيبعد من نفسه الأمل. ويقرّب الأجل.',NULL,'60',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (61,'كان هذا جميعه يدور في فكري. ويتمثّل لنظري. وأنا واقفٌ إزاء قبره. أتأمّل في مبتداه وخبره. فيترك في قلبي عِبْرة. وفي جفني عَبْرة.',NULL,'61',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (62,'<span data-type="title" id=toc-39>حسان الأستانة</span>
وأبهى ما يكون هذا المكان وقت الأصيل، حتّى يفئ الظلّ الظّليل فترى فيه أسراب الغزلان، والرعابيب الحسان، يمشين مشي القطا الكدريّ في الدمث النديّ، فتارةً وقوفاً على شريعةِ ماء، وحيناً جلوساً تحت رفرف أيكةٍ خضراء، وآونةً يبدون للنظر وطوراً يختفين في الشجر، وكأنّ الثّوب طاوس وصليلُ الحليّ ناقوس، والوجوه أقمارٌ وشموس، وكأنّي بك وقد رأيت منهنّ ذات دلٍّ لعوبا، فينانةٍ خرعوبا، غرّاء فلجاء خدلجةً',NULL,'62',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (63,'لفاء، أملوداً خمصانة شموعاً خوطانة في وجهٍ كالوذيلة، وخدٍ كالجليلة، وقوس حاجب، كأنه قوس حاجب.
وشعرٌ كاللّيل، أو أذنابُ الخيل وثغرٌ أشنب، كأنّما ذُرَّ عليه الزرنب، وثنايا غر، ذات أشر، ومبتسم برد، وشفاه كأنّما ورق الورد - وعينين كسيفين في جفنين، أو سهمين في قوسين وقدٌّ كالرّمح، وفُرْقٌ كالصّبح، حسن للترك والجرج، لا يوجد عند الإفرنج، الّلهم إلّا صوراً في ألواح روفائيل، مَثّل بها',NULL,'63',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (64,'إسرافيل وميكائيل، أو صفاتٍ في أشعار دانتي ولامارتين صوراً بها الخلد والحور العين فلمّا لمحتها أشرتُ إليها بالكفِّ، فأومتْ لك بالطّرف، فحسبتها أقرب من مداركة، فإذا هي أمنع من عاتكة وتخيّلت أنّها منك على طرف الثمامة، وإذا بها طارت كالحمامة.',NULL,'64',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (65,'<span data-type="title" id=toc-40>الحسان في الطريق</span>
<span data-type="title" id=toc-41>حسان الأستانة أثناء مرورهن في الطريق</span>
حسانٌ غيدٌ كالأماليد، في وجوهٍ كالدّنانير، وأوساطٍ كأوساطِ الزّنابير، عليهن مطارف كألوان الحرباء، وأزهار الرّوض من حمراء وصفراء. خدٌّ تحت النّقاب، كالخمرِ في كأس الشّراب، ووجهٍ يخفيه ويبديه اللّثام، كالشّمس تحت الغمام.',NULL,'65',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (66,'<span data-type="title" id=toc-42>كنز مدفون أو وفاة رجل كبير</span>
أطلق الدمع وأطرق، فقد غربت الشمس في المشرق. فيا هزيمة العقل، وصولة الجهل، ويا وحشة الدور، وأنسة القبور، أسريرٌ ينقل ويسير، أم جبلٌ يتقلّع، ووسمي يتقشّع، وهذه أوصال، أم معال تنشر وتقبر.
أقبرٌ هذا أم جفنٌ فيه سيفٌ جرّار وتربٌ فيه تبرٌ ركاز، وقليب هريق فيه ذَنُوب من كرم، وجفر تهدم فيه بنيان من همم.',NULL,'66',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (67,'فإلى الله نشكو زمناً أطفأَ هذا السّراج، وكسرَ هذا التّاج، وأخبأَ هذا الشّهاب، وقفلَ هذا الباب، وغادرنا بعده في غيٍّ كرشد، ورشدٌ كغي، وحَيٌّ كميّت، وميّت كحَيّ.

<span data-type="title" id=toc-43>صفة الحزن عليه</span>
عينان، كأنّهما عينان نضاختان، طرفٌ خاشع، وشممٌ باخع ونَفَسٌ راجع، وإصبعٌ دامٍ، وعثيرٌ فوق هام، وحزنٌ ينقض الأضلاع، وهمٌّ يسلُّ النخاع، وفي كلّ قلبٍ صدع، وفي كلّ رأسٍ صداع.

<span data-type="title" id=toc-44>صفة الفقيد</span>
في سبيل الله منه واحد بألف، كالدّينار في الصّرف، كريم',NULL,'67',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (68,'المنبت والبيت، ما فيه لو ولا ليت، ماضٍ والسّيف ناب، كأنّه في الفضلاء سطر بسم الله في الكتاب.
جمّ الأصفاد والمنح، إذ استنجَدْتَهُ جاءك نصرُ الله والفتح إلى حكمة رسطاليس، أو الشّيخ الرئيس وخِطَبُ إياد، أو زياد، وفضلٌ كالمسك إن كتمتَه سطع، وكالقبس إن خفضته ارتفع، سجايا ومدح، إن عُدَّتْ نابت لأعدائه عن السبح.',NULL,'68',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (69,'<span data-type="title" id=toc-45>غرور الدنيا</span>
<span data-type="title" id=toc-46>دنيا غرورة</span>
دنيا تغرّ الجّاهل، ولا تسرّ العاقل، ودارٌ لا يدخلها الطفل إلّا وهو باكٍ، ولا يخرج منها الكهل إلّا وهو شاكٍ، قد عصفت بالشّرور سواقيها، ومَنْ أَذْنَبَ في جهنم وجب أن يُعَذَّبَ فيها، ليس بها لذةٌ إلّا ممزوجةً بألم، ولا دسمٌ إلّا مخلوطاً بسم، ولا ضاحكٌ إلّا وهو باكٍ كالغمامة، ولا شادٍ إلّا وهو نائحٌ كالحمامة.
لو يعلم النّاس علمي بالزّمان لما ... سُرّوا بشيءٍ ولا ربّوا ولا وَلَدوا',NULL,'69',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (70,'فلكٌ في هلك، سيّان بها من بالبقاع، ومن على الشّراع. وخطٌ في ماءٍ لا ينقسم، حتّى يلتئم، وأثرٌ في بيداء لا يرتسم، حتّى يرتطم.
وكيف أجيد في دار بناء ... وربّ الدّار يؤذنني بنقله',NULL,'70',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (71,'<span data-type="title" id=toc-47>وقفة بين المقابر</span>
انظر هذه المقابر بالحاجر، ففيها بلاغٌ ومعتبر لمن ادّكر، تريا كلّ جدثٍ كأنّه علمٌ بين السّاهرة والآخرة. خطٌ متضايقٌ فيه جميع الخلائق، كالقلب الصغير، وفيه العالم الكبير، وكأنّ سكّانها صرعى مدامة، أو نيامٌ في ليلةٍ صباحها يوم القيامة.

<span data-type="title" id=toc-48>رفات ملك</span>
وكم في تلك القبور من مَلِكٍ كان يصرف الأمر من مصر إلى عدن، أو يحتلّ غمدان ذي يزن، وكم بها من أميرٍ يملأ الدّست من جلالٍ ونور، وتحببي له دجلة والخابور.',NULL,'71',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (72,'<span data-type="title" id=toc-49>رفات حسناء</span>
وكم فيها من حسناء بضّة، كأنّها صليحة فضّة، أصابها الهزال كما يصيب الهلال، واعتلّ الجسم السّقيم كما يعتلّ النسيم، وإذا بها في القبر كأنّها مصباح راهب، في قبّةٍ مظلمةٍ أو كنزُ راغب، مهجورةٍ معتمة. وإذا بجسمٍ كان يخشى عليه الهزال، أصبح وهو بال. وخدٍ كان يصان عن قبلة، تعبث فيه الآرضة والنملة. وتغور كأنّها أقاح، أو حببٌ على راح، تُنْثَرُ في البوغاء، وتُخْلَطُ بالحصباء وعينين كأنّهما سنانان أزرقان في عامل،',NULL,'72',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (73,'أو سحر الملكين ببابل أضحيتا في الحجاج كما قال العجاج:
كأنّ عينيها من الغؤور ... لحدان في قَلْتَيْ صفا منقور
وإذا ثديان كأنّهما حِقّان من مرمر، أُثْبِتا بمسمارين من عنبر، باتا من الدّود كأنّهما أخدود، وإذا بمنزلها في الدّور أشعث مهجور، كأنّه محجورٌ بلا حدق، أو شجرٌ بلا ورق، وكأنّه مات بعد ساكنيه، وكأنّهم كانوا روحاً فيه، وكم ذابت في ذاك',NULL,'73',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (74,'الثّرى خدودٌ وجباه، وثغورٌ وشفاه، وسلبٌ من أنفٍ شمم ومن بنانٍ عنم، وكم خُرِّبَتْ فيه قصور، وهُتِكَتْ ستور وجُمِعَتْ أضداد، وفُرِّقَتْ أمهاتٌ وأولاد، سبحانك اللهم وسعدانك من حبسٍ إلى رمس، ومن عبثٍ إلى جدث، عملٌ ثمّ أمل.
عُذْتُ بما عاذَ به إبراهيم ... مستقبل القبلة وهو قائم
أني لك الهمّ عان راغم',NULL,'74',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (75,'<span data-type="title" id=toc-50>العزلة</span>
<span data-type="title" id=toc-51>صفة العزلة عن الناس</span>
كتابي إلى السّيد أيّده الله، وكلأه ورعاه، وأنا حِلٌّ بقرى السّواد، وريف البلاد، بعيدٌ عن المدينة، وما فيها من الشّينة والزّينة، في عزلةٍ عن النّاس، بين سقيٍ وغراس، سليمُ الجسم من السّقم، والنفسِ من الألم، والحميةِ من الأنام، كالحميةِ من الطعام، شفاء من كلّ داء، وخليق بمن ارتطم، في المزدحم، أن يصاب ببعض الأوصاب.

<span data-type="title" id=toc-52>وصف الريف</span>
يا ما أحيلى الوحدة والرّيف، وذلك المشتى والمصيف، والجوّ السجسج والظلّ الوريف.',NULL,'75',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (76,'<span data-type="title" id=toc-53>وصف الفجر</span>
فجرٌ يلوح في الأفق، كالنّور في الأعين الزرق، وضياءٌ ينبثق في الفضاء كما ينبثق الماء، وشمسٌ تبدو للإشراق في الآفاق، كبودقةٍ فيها ذهب، أو قنبلة ترمى باللهب، فيرتفع جرس كلّ حيوان كممنون في الأوثان. فللإنسان تسبيحٌ وتكبير، وللإبل حنين وهدير، وللحمام هديل، وللخيل صهيل، وللبقر خوار، وللمعز يعار، وللغراب نعيب، وللأرنب ضغيب، وللذئب ضغاء وللغنم ثغاء.',NULL,'76',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (77,'<span data-type="title" id=toc-54>وصف قرية وأهلها</span>
وبين ذلك بيوتٌ من قرميد، وسقوفٌ من جريد، وأقنٌ من حجر، وبجدٌ من وبر، وقطارٌ من آبال تسير بالغدو والآصال في أعناقها الأجراس، وفي رجالها الأمراس، يحدوها سواق حطم، كأنّه الزّلم.
وراعي غنم بين الغرقد والسلم، يدفعه مدخل اللّيل، إلى مجرى السّيل، يشرب بالعلب، وينفخ في القصب. وفي كلّ محلةٍ برٌّ يُقْنى، وحريملةٌ تُجْنى، وقصبٌ يُكْسَر، وسليطٌ يُعْصَر، وزبدٌ يُمْخَضْ، وصريحٌ يُمْحَضْ. وأناسي، من أربتي، وفروي، هريت ثوبه، نقي جيبه، كريمٌ في أطمار، كالخمر في خزفٍ وقار.',NULL,'77',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (78,'<span data-type="title" id=toc-55>وصف الصيف</span>
فإذا أقبل الحرور ألفيت كلّ أرضٍ كشِعر أبي نوّاس، وكلّ نهى كقطعةٍ من ماس، وعلى كلّ علمٍ، برد منمنم، وفي كلّ غيطٍ، وشيٍ وديط، إلى أزاهر كأنّها دنانيرُ جدد، أو ولّاهم بدد، أو فصوص من يواقيت، أو أوائل النّار في أطراف كبريت، وعندليبٌ وكركي، وحمامٌ وقمري، وبطٌ، على الشط، وإوزٌ في النز.
حتى إذا استحكمت من الصذيف الوقدات، واستحرّت الوغرات، إذا الحجران قد اصفرّت، والعيون قد نشت، واستُنّ السفا والذرق، على القبق، وغدت الحقول، وهي عصفٌ مأكول والبطاح صعيداً تذوره الرّياح ولاح السّراب على الشعاب،',NULL,'78',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (79,'كالرّياط البيض، والملاء الرّخيص، وجنّ الذباب، وصمّ الغراب، وسكن العصفور مع الضبّ في حجر، وسال لعاب الشّمس كمذاب الصّفر ودوّى النحل في المحل، ووثب الجراد، في الوهاد، وانساب النضناض، على الرضراض، وخرج الذرّ من الجفر وطاب المقيل، في الظّل الظّليل، ففي كلّ دوحةٍ أستارٌ وحجب، وتحت كلّ سدرةٍ قيّةٌ وطنب وسرى النّسيم في الظّهيرة بين الأشجار، كأنّه نسيم بين الأسحار.

<span data-type="title" id=toc-56>فصل الشتاء</span>
فإن أظلّ الشّتاء كنت في جوٍ كأدكن الخزِّ، وأرضٍ كأخضرِ القزِّ، ولقحةٍ تدرُّ، وكلبٍ يهرُّ، ونكباءُ صرصر وخبزٍ سميذ،',NULL,'79',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (80,'وحملٍ حنيذ، ولباءٍ وماذي، وكامخٍ طري، وحالومٍ وصير، وخيرٍ كثير وليلٍ مطلول، كأنّه ليل صول، وموقدٍ ودخان، وسمّارٍ وضيفان.
وفي الجوّ غيمٌ قد تعلّق بين الأفقين، وتدلّى قاب قوسين، كأنّه فروٌ مزرور، أو كافورٌ منثور تمجّ لواقحه الماء مجّ الدلاء، وترتعج فيه ألسنة اللهب، كسلاسل الذّهب والطّير سواكن بلا حراك، كأنّها من الغيث في شباك.

<span data-type="title" id=toc-57>النفس بين الرياض</span>
سرّاءٌ في جميع الأنحاء، وراحةٌ في كلّ ساحة، فكأنّما نفس الإنسان في كلّ مكان، عين ماء، تصف ما يقابلها من الأشياء، فإن كانت حذاء رياض، وفضاء وغياض، ألفيت فيها روضاً وزهراً، وسماءً وفجراً، وإن كانت بين الحيطان القتماء، وبيوت المدن الدكناء، ألفيتها معتمةً، كدراء مظلمةً.',NULL,'80',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (81,'<span data-type="title" id=toc-58>كتب العلماء والحكماء</span>
وصحبي في هذه العزلة نفرٌ من صياب الأقوام، ولباب الأنام، فمنهم أبو تمّام، والحارث بن همام، وعروة بن الورد، وطرفة بن العبد، وكثيراً ما ينشدنا أحمد بن سليمان، باقعة معرّة النعمان:
ذريني وكتبي والرياض ووحدتي ... أظلّ كوحشٍ بإحدى الأمالس
يسوف أزهار الرّبيع تعلّة ... ويأمن في البيداء شرّ المجالس
ويقول أيضاً:
غنّيت عن زائرٍ ملم ... فليشغل الخبر زائريّا',NULL,'81',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (82,'وربما أسمعنا ثعلب عن قطرب:
تمرّ علينا الأرض من أن ترى بها ... أنيساً ويحلو لي لنا البلد القفر
أو ارتجل ابن المعتز وارتجز:
قليل هموم النفس إلاّ للذّة ... ينعم نفساً آذنت بالتنقّل
ولست تراه سائلاً عن خليفةٍ ... ولا قائلاً من يعزلون ومن يلي
ولا صائحاً كالعير في يوم لذّة ... بناظرٍ في تفضيل عثمان أو علي
ولكنّه فيما عناه وسَرّه ... وعن غير ما يعنيه فهو بمعزل',NULL,'82',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (83,'وإن شئنا حدثنا أفلاطون، ونادمنا ابن زيدون، وعالجنا بقراط، ووعظنا سقراط:
ولي دونكم أهلون سيّدٌ عملّسٌ ... وأرقطَ ذهلولٍ وعرفاء وجبأل
هم الأهل لا مستودع السّر ذائعٌ ... لديهم ولا الجاني بما جر يخذل
أيامنا في ظلالهم أبداً ... فصل ربيعٍ ودهرنا عرس

<span data-type="title" id=toc-59>الوحشة من الاجتماع</span>
يدعوني السّيد دام علاه، وكبّت عداه، أن أهجر الدساكر وأسكن الحواضر وأترك تلك التلاع والأيفاع، وأقبل على',NULL,'83',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (84,'الاجتماع فقد كان ذلك قبل اليوم ألا من يشتري سهراً بنوم كيف بعد التجارب الرجوع، إن المعافى غير مخدوع، دع النفس وشأنها أعمرت أرضاً لم تلس حوذانها.
إذا تركت العزلة، فمن أقصد بالنفلة.
والقوم شر فلا يسررك إن بسطوا ... لك الوجوه ولا يحزنك إن عبسوا
أأفعل ذلك، وأقطع تلك المسالك، رغبة في حوار، حاكم ديوان أو جوار، صحبانٌ وخلّان، أم لمنافسة أبناء السّامة، أم ملابسة هذه العامّة.',NULL,'84',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (85,'<span data-type="title" id=toc-60>وصف الحكام</span>
أمّا الحاكم فأكثر ما لقيت امرؤ إن أونس تكبّر وإن أوحش تكدّر وإن قصد تخلّف، وإن ترك تكلّف، إمعٌّ، لا يضرّ ولا ينفع، قبةٌ جوفاء تردّد ما يُلقى فيها من النّغم، إن لا فلا أو نعم فنعم، ألقابٌ وأكاليل، على شخصٍ في مسرح التمثيل، فإن طرحت تلك الألقاب، ونزعت هاتيك الثّياب، ألفيت تحتها العجب العجاب.
لا عدّة ولا عدد. وملك أقامه الله بلا رجال كما رفع السّماء بغير عمد. مَنٌّ ولا منة. كالمهدر في العنّة وأعوانٌ وخدّام. وحجّاب كحجّاب أبي تمام.',NULL,'85',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (86,'إلى تيهٍ وخيلاء. وعنجهيةٍ وكبرياء. كأنّه جاء برأي خاقان. أو أدلّ دولة بني مروان، أو أنّ الإيوان داره. والهرمين آثاره. وعصام بن شهبر حاجبه وعمرو بن بحر كاتبه والحجّاج غلامه. والحماسة كلامه، رويدك ربّما علت الجيف.
وانحطّ الدّرّ في الصدف. وارتفع في الميزان جانب النقصان.
على أنّ الإنسان، إذا لم يكن فيه غير جثمان فكلّما علا يصغر لمن ينظر وربّما حسن الأفن. تعظيم الوثن.',NULL,'86',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (87,'عبوسٌ إذا حيّيته بتحيةٍ ... فيا لك من كبرٍ ومن منطق نزر

<span data-type="title" id=toc-61>الأصحاب والخلان</span>
وأمّا الأخلّاء، والصَحب والسجرّاء، فحسبك من رجلٍ عون في كلّ أمر تردّه، ونصيرٍ في كلّ مطلب لم تقصده، فإن عرض لك بعض الحاج، فالعلويّ يسترفد الحجّاج، ماءٌ يتلوّن بلون الإناء، - ونيلوفر - يدور مع الشّمس في الإصباح والإمساء.',NULL,'87',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (88,'إن جددت فإليك، أو شقيت فعليك، مدحٌ مع المادح، وقدحٌ مع القادح أجسامٌ متدانية، وقلوبٌ متنائية، وإن كان خبر سوءٍ، فحمّاد الرّاوية حدِّث عن البحر ولا حرج، مئذنةٌ في ظاهرٍ
مستقيمٍ وباطنٍ معوَّج.
له لطفُ قولٍ دونه كلّ رقية ... ولكنّه في فعله حيّة تسعى

<span data-type="title" id=toc-62>أبناء الأغنياء</span>
وأمّا أبناء السّامة فإن أحدهم غادةٌ ينقصها الحجاب ينظر ف',NULL,'88',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (89,'ي المرآة ولا ينظر في كتاب: إنّما هو لباس، على غيرِ ناس، كما تضع الباعةُ مبهرم الثّياب، على الأخشاب، رمادٌ تخلّف عن نار وحوض شُرِبَ أوّله ولم يبق منه غير أكدار، آباءٌ وأحساب، وحالٌ كشجر الشّلجم أحسن ما فيه ما كان تحت التراب ترى الفتيان كالنّخل وما يدريك ما الدّخل إلى رطانةٍ بالعجمة بين الأعراب أبرد من استعمال النّحو في الحساب لو كان ذا حيلة لتحوّل ميسرٌ يلعب. ومالٌ يُسْلَب. وخدنٌ',NULL,'89',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (90,'يُخدع. وكلبٌ يُتبع. وعطرٌ يَنفح، وفرسٌ يضبح، دنيا موجودة ونفسٌ مفقودة، وعقلٌ أسير، وهوى أمير اليوم خمر وغداً أمر فبيّناه غنيٌّ يتملّك. إذ هو فقير يتصعلك قوتٌ كيلا يموت. ومن إيوان كسرى إلى البيت العنكبوت.',NULL,'90',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (91,'<span data-type="title" id=toc-63>الحرص أو تثمير المال للذرية والآل</span>
أيّها الرّجل، وكلكم ذلك الرّجل، إنّ المال وسيلة لا غاية، فإن أصبت منه الكفاية، فقد بلغت النّهاية، ليس لك من عيشك إلا ما أكلت فأفنيت، ولبست فأبليت، ولو أفرغ ذَنُوبٌ في كوب ما أخذ إلّا ملأه، ولا وسع إلّا كفاه.
عجبت للمالك القنطار من ذهب ... يبغي الزّيادة والقيراط كافيه
وكثرة المال ساقت للفتى أشراً ... كالذّيل عثر عند المشي ضافيه
فلم هذا الطماح والطّمع، والاستكلاب والجشع.',NULL,'91',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (92,'أنت للمال إذا جمعته ... وإذا أنفقته فالمال لك
أتظن أن الدرهم حبيسٌ في مستقر، إن خرج فرّ، أم صديقٌ منك وإليك، إن لم تحرص عليه لا يحرص عليك، أو أنّ بيت المال بيت قريض، إن نقص منه حرفٌ أدركه التقويض، أو أنّ شيئاً عليه آيةٌ من القرآن، أو صورةٌ لسلطان، حريٌّ أن يكون تعويذةً من لجين، تُدّخر لدفع العين أم أردت أن تعيش كدودة القزّ، أو تكون كطلسم على كنز.',NULL,'92',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (93,'حتّى إذا قضيت، ومضيت ألقى بنوك ما ثمرت، في تلك الهاوية، وما أدرك ماهية نارُ حامية، وأطعم بناتك شحمة مالك لغير آلك.
وأكثر النّسل يشقى الولدان به ... فليته كان عن آبائه دفعا
وكم سليلٍ رجاه للجمال أبٌ ... فكان خزياً بأعلى هضبةٍ رفعا

<span data-type="title" id=toc-64>العامة من الناس</span>
وأمّا العامّة أيّدك الله فهم عظمٌ على وضم، وصيدٌ في غير حرم، سيّدٌ مأسور، والأخشيد في يد كافور، ويتيمٌ غنيّ، في يدِ وصيّ،',NULL,'93',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (94,'فبينما ترى قصوراً وثراء، وحبوراً وسرّاء، وعربات تتري، يعدو أمامها السّليك، والشنفرى ويقودها داحس والغبراء، على بساط الغبراء.
وخراج قرية أو قريتين، يذهب في لهوِ ليلةٍ أو ليلتين، تجد أرملةً صناعاً، وأيتاماً جياعاً، وشيخاً يعمل وهو في أرذل العمر، يقعده العجز وينهضه الفقر، أو عذراء كادت تبيع عرضها للاحتياج أو مريضاً عاجزاً عن العلاج.
وبينما ترى وذاحاً في جيدها عقدٌ كأنه فرود حضار. وفي أخمصها نعلٌ من نضار، تُرَى بائسةً في عنقها عقدٌ من دموع، وفي بيتها فقرٌ وجوع، حالٌ تُطْرِفُ العيون وتُثيرُ الشّجون.',NULL,'94',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (95,'رحماك إن عزلة بين كرمٍ وأعناب. ودواةٍ وكتاب. لهي الجماعة والأنس للنفس. وإن اجتماعاً بكبيرٍ يُبغض ويُزار. أو رئيسٍ لا يجد نفسه في اللّيل ولا تجده في النهار. أو عدوٍ ليس من صداقته بدٌّ. أو حقودٍ أظهر منه الود. أو حسودٍ ملق. كالذبالة يضحك ويحترق. أو جاهلٍ متعاقل. أو متفصحٍ وهو باقل. أو صغيرٍ به كبر. أو خدين فيه غدر، لهو وأيم الله الوحشة والوحدة والسلولية والغدة.
جزى الله عني مؤنسي بصدوده ... جميلاً ففي الإيحاش ما هو إيناس',NULL,'95',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (96,'<span data-type="title" id=toc-65>مصر</span>
أديار ميٍّ تنظر ... فدموع عينك تمطر
أو أبرق العلمين أم ... سفح اللوا تتذكّر
أم تام قلبك جؤذرٌ ... أحوى المدامع أحور
أم هبّ في مصرٍ صبا ... أم طار برقٌ أشقر
أم قد ذكرت بطاحها ... وهي البساط الأخضر
والنيل في لباتها ... عقد يلوح مجوهر
والجو صحوٌ مشرق ... وكأنّما هو ممطر
والظّلّ من خلل الشمو ... س مدرهم ومدثّر
فكأنّه جلدٌ من ال ... نمر المرقّش ينشر
وغصونها لدنٌ تميد ... بما تقلّ وتثمر
فكأنّهنّ ولائدٌ ... في حليها تتكسّر
هي نسج وشي نيلها ... فيه الطّراز الأحمر
هي مثل لوحٍ صوراً ال ... فردوس فيه مصوّر
يا جنةً يجني الجنى ... فيها ويجري الكوثر',NULL,'96',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (97,'أنا شاعرٌ في وصفها ... ولكنّها هي أشعر
أنّى بمصرَ ودونها ... بحرٌ يعجّ ويذخر
يا ساخر الفلك المسخّر ... في خضارةٍ يمخر
أقرّ التحيّة جيرةً ... حيث الكثيب الأعفر

<span data-type="title" id=toc-66>الهرمين والمقياس والروضة</span>
فالنيل فالهرمان من ... غريبه فالأزهر
فالروضة الغناء وال ... مقياس فيها يشبر

<span data-type="title" id=toc-67>قصر عابدين</span>
فالقصر قصر الملك وال ... أوهام عنه تقصر
فيه المقاصير التي ... ألواحهنّ المرمر
حيطانها الذّهب الصقي ... ل وأرضهنّ العرعر
قد صوّر التاريخ في ... أرجائهنّ مصوّر
فترى الوقائع منظرٌ ... فكأنّما هي مخبر
والجند تخطر في الحدي ... د فدارعون وحسّر',NULL,'97',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (98,'والخيل بين عجاجها ... تخفى وحيناً تظهر
وتظنّ إحياء بها ... فتمسّ كيما تخبر

<span data-type="title" id=toc-68>الجزيرة</span>
ثمّ الجزيرة تستبي ... ك بها أوانس نفّر
عجلاتها فلك بأش ... باه النّجوم يدوّر
من كلّ خركاةٍ بحس ... ناء تضيء وتقمر
فكأنّها المشكاة وال ... مصباح فيها يزهر

<span data-type="title" id=toc-69>الجيزة والمتحف</span>
فالجيزة الخضراء يع ... بق رندها والعبهر
فيها النّعامة والحبا ... ري والمها والقسور
كسفين نوحٍ أظهرت ... ما كان فيها يضمر
وترى الغصون على الأ ... رائك تلتوي فتشجر',NULL,'98',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (99,'وجداولٌ كسبائك ... بسنا الأصيل تعصفر
ماءٌ كبلّورٍ يذو ... ب وأدمعٍ تتقطّر
يروي القطا الكدري م ... نه وينتحيه الجؤذر
في حافّيته الورد والن ... سرين والنيلوفر
وعليه من نسج الصبا ... درعٌ هناك ومغفر
فالقصر وهو لمن مضى ... من أهل مصر مقبر
نشرت به أمواتهم ... فكأنّما هو محشر
رمسيس أين مطارف الدّ ... يباج أين الجوهر
أين السّرير وأين تا ... ج المُلك أين العسكر
نم في رقادٍ ليس في ... أحلامه ما يُذْعِر

<span data-type="title" id=toc-70>ملعب الحياة</span>
فالموت نومٌ أكبر ... والنوم موتٌ أصغر
دنيا تشابه ملعباً ... واللّيل ستر يستر
والفصل يضحك والثريّ ... االشّمس فيه تنور
جندٌ هناك وسوقةٌ ... ومتوّجٌ ومسخّر',NULL,'99',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (100,'فإذا طُرِحَتْ ثيابهم ... ساوى الأعزُّ الأحقر

<span data-type="title" id=toc-71>الأزهر</span>
فالأزهر الزاهي يدوّ ... ي بالعلوم ويجأر
كدويّ نحلٍ وهو يجم ... ع شهده أو يذخر

<span data-type="title" id=toc-72>حديقة الأزبكية</span>
فالأزبكيّة حيث تط ... وى بالعشي وتنشر
وتبيت نسجٌ في الدّجى ... ورقاؤها والمزهر
والبركة الفيحاء في ... فضفاضها تتمرمر

<span data-type="title" id=toc-73>وصف المياه</span>
ماءٌ كعين الدّيك ين ... ظم بالنّجوم وينثر
وترى ضياء البدر في ... هـ كمثل عينٍ تفجر
وإذا تلوح الشّمس في ... لألائه أو تسفر
ألفيته المرآةُ والحس ... ناءُ فيها تنظر',NULL,'100',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (101,'<span data-type="title" id=toc-74>قلعة الجبل</span>
فالقلعة العلياء تج ... لي للعيان وتبصر
بمآذنٍ كالحقّ لا ... جنفٌ ولا متأطّر

<span data-type="title" id=toc-75>مجد مصر القديم</span>
قطرٌ تمصر في الورى ... والأرض برٌّ أقفر
وطن الغريب وداره ... وقبيله والمعشر
ملكٌ محيط الأرض يص ... غر عن مداه ويكبر
في كلّ صرحٍ مخبر ... وبكلّ سفحٍ منظر
ولكلّ لبنةَ غرفةٍ ... فيها حديثاً يذكر
فرعون والأنهار تج ... ري واللوى والمنبر
ذهبوا فأمسوا مثل ر ... ؤيا في المنام وتعبر
هَرَمان فيه شاهدي ... ن شهادةً لا تُنْكَر
وهياكل دثرت وذك ... ر حديثها لا يدثر
والمجد مثل الخمر يكر ... م ما توالي الأعصر
كانت سلاطين الورى ... فيه تشيد وتعمر
والغَرب في أعماله ... والقبلتان وتدمر',NULL,'101',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (102,'والخيل خيل الله تر ... كب والصوائف تنصر
وفرنجةٌ ومليكها ... تُغزى بمصر وتُؤسَر
هذي مناقب مصر تر ... وى في الأنام وتسطر
ولسوف يرجع ما مضى ... ويعود ذاك المفخر
وكذا الزمان يدور وال ... قدر المغيب محور
والبدر إن وافى السرا ... ر فبعد ذلك يبدر
والعود ييبس برهةً ... فإذاه عودٌ أخضر',NULL,'102',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (103,'<span data-type="title" id=toc-76>ذات القوافي</span>
سقى دور ميّة بالأجرع ... مسفٌ من الدّجن لم يقلع
ولو ترك الشّوق دمعاً بجفني ... سقيت المنازل من أدمعي
شجيٌّ يحنّ لألافه ... ويصبو إلى دهره الغابر
فهل عائدٌ لي زمانٌ مضى ... بنعف الغوير إلى الحاجر
أرى بين أحناء صدري ناراً ... تؤجّجها الرّيح إذا ما هفت
وبين جفوني سحباً ثقالاً ... إذا ما تألّق برقٌ همت

<span data-type="title" id=toc-77>الهوى وأعماله</span>
وساورني الحبّ حتّى ثوى ... كأيمٍ على مهجتي ملتوي
وما الحبّ إلاّ كروضٍ غدا ... بغير المدامع لا يرتوي',NULL,'103',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (104,'وقد هجرت مقلتاي الكرى ... كأنّ بهدبي رؤوس الإبر
ولو كان ما بي بهذا الغمام ... لأمطر بالجمر أو بالشرر
فجسمي أصبح كالشّمع يفنيه ... سكبُ الدّموع ووقد الحرق
فلا ألبس الثّوب إلاّ وجسمي ... من تحت ثوبٍ كثوبٍ خُلِق
نحلت فلو زرتها ما خشيت ... رقيباً يراني فيمن يرى
ولو زرت ميّة في يقظة ... لظنّت بأنّي خيالٌ سرى
يمرّ ولم أدرِ شهرٌ فشهرٍ ... كأنّي في فلكٍ لم يدر
وأرتاح إمّا تمنّيتها ... ويا ربّ أمنية كالظّفر
أسيرٌ ولا أرتضي بالعتاق ... ومضنىً وأجزع أن أبرأ',NULL,'104',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (105,'وإن سلّمت خلتها ودّعت ... وأحسب مقتربي منتأى
إذا كنت وحدي أكون وإيّاك ... أو خالياً فاشتغالي بك
وأطلب المجد والمكرمات ... لتحسن لي شيمةٌ عندك
ليحنو قلبك رفقاً عليَّ ... فالصّخر بالماء قد ينبجس
وصوني الوداد وفيه الذماء ... فلن يورق العود إمّا يبس
لميّة خدٌ به وردةٌ ... تفتّحه نظرةٌ أو خجل
وقد قضيف إذا ما تثنّى ... يخال به رنحٌ أو ثمل
ووجهٌ إذا ما نظرت إليه ... نظرت لوجهك في مائه
وجفنٌ ترنقه فترةٌ ... كمستيقظٍ بعد إغفائه
كأنّي في مدحها ساجعٌ ... ودمعي في عنقي طوقه',NULL,'105',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (106,'تشوق فؤادي فأثني عليها ... كعودٍ يضوعه حرقه

<span data-type="title" id=toc-78>الشيب والغزل</span>
زمانٌ إذا ما تذكّرته ... تخيّلته حلماً في الكرى
وعهد الشّباب كرؤيا إذا ... أمضت أدركتها نفوس الورى',NULL,'106',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (107,'<span data-type="title" id=toc-79>أبي</span>
سقت رحمةُ الله الضّريح وما ضما ... وروت به هاماً وروت به عظما
يعز على العلياء أن يسكن النّدى ... تراباً وأن تلقي به الحسب الضخما
وأن تسكت الأجداث محراب ساجدٍ ... وكان به التّسبيح يفعمه فعما
كأنّك كنزٌ قد دفنّاه في الثّرى ... كأنّك غُنْم قد أحيل لنا غرما
كأنّك شمسٌ والجفون غمائمٌ ... حَجَبَتْ أضواؤك انسجمت سجما
ألا في جوار الله مولىً عهدته ... يجير على الأيام أن وهصت ظلما',NULL,'107',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (108,'له كنفٌ ينمو لآل محمدٍ ... تؤمّ الملوك الصيد أبوابه أمّا
وكفّان كانا كالفرات ودجلة ... يريشان من خصا بجودٍ ومن عما
وعلمٌ هو اليمّ الّذي قد تنوّرت ... أو أذية الورّاد فاستصغروا اليمّا
وبطشٌ لمن عاداه تحسب أنّه ... شهابٌ هوى في أثر عفريةٍ رجما
وصدرٌ هو الدهناء في الأزم فسحةً ... وليلة سرّ عند أسراره كتما
وقولٌ عريقٌ في الفصاحة لو غدت ... تساجله عربٌ إذا أصبحوا عجما
وعدلٌ هو العدل الّذي قد قضى به ... أبو حفص الفاروق في طيبةٍ حكما',NULL,'108',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (109,'فهذا أبي من بيت تيم بن مرّة ... إلى نضدٍ من هاشم يفرع النجما
وما ذاك في مديحه شعرٌ وإنّما ... خلائقه درّ أوجدت له نظما

<span data-type="title" id=toc-80>وصف فلك</span>
أخوض عباباً فوق فلكٍ تظنّها ... على سروات اليمّ قصراً مشيداً
تهادي به مثل العقارب وتارةً ... ترقى من صرحاً ممرّدا
وترزم حيناً فيه حتّى كأنّها ... تجوز على العلات حزناً وقرددا

<span data-type="title" id=toc-81>المضحك المبكي</span>
حمق الألى يحكمون النّاس يبكيني ... وسوء فعلهم في النّاس يبكيني',NULL,'109',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (110,'ما الذئب قد عاث بين الضأن أفتك من ... هذه الولاة بهاتيك المساكين

<span data-type="title" id=toc-82>الشيب</span>
أشعرةٌ بيضاء أم ... أوّل خيطِ الكفن
أم تلك سهمٌ مرسل ... لا يتقى بالجنن
والزرعُ إنْ هاج ... فقد حان الحصاد وأنّي
ففي سبيل الله ما عا ... نيته في زمني

<span data-type="title" id=toc-83>كيف نصبر</span>
أشفاهٌ تلوح أم ورد الورق ... وعينان أم هما سهمان
دربونا على التجافي وإلاّ ... فاحجبوا بيننا وبين الحسان

<span data-type="title" id=toc-84>صغائر الأمور</span>
وفي وسعة المرء نيل العلا ... وقد يمنع المرء ما يمنع
صغيرٌ من الأمر يلهيه عن ... بلوغ العظائم أو يقطع',NULL,'110',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (111,'كعينً تحيط بهذا الوجود ... جميعاً ويحجبها أصبع

<span data-type="title" id=toc-85>الحدة</span>
إنْ أحرجوا صدرك لا تنبعث ... للقذع بالفحشاء أو مثله
فغضبةُ الأحمق في قوله ... وغضبةُ العاقل في فعله

<span data-type="title" id=toc-86>الجزاء</span>
لا تعجبوا للظّلم يغشى أمّة ... فتنوء منه بفادح الأثقال
ظلم الرّعية كالعقاب لجهلها ... ألم المريض عقوبة الإهمال
وما أذن القوم لمّا أقاموا ... صلاة الجنازة يوم الوفاة
وأذّن للطفل يوم الولادة ... فهذا الآذان لتلك الصّلاة',NULL,'111',NULL,NULL,'0');
INSERT INTO [b36077] VALUES (112,'<span data-type="title" id=toc-87>الوجل</span>
النّاس يخشون من جاه المليك وما ... لديه لولاهم في ملكه جاه
كصانعٍ صنماً يوماً على يده ... وبعد ذلك يرجوه ويخشاه

<span data-type="title" id=toc-88>المرأة الخبيثة</span>
بثينةُ قد تراءت ... بحمرةٍ وبياض
خبيثةٌ في جمالٍ ... حيّةٌ في رياض',NULL,'112',NULL,NULL,'0');
