-- Insert data for b36386
INSERT INTO [b36386] VALUES (1,'بسم الله الرحمن الرحيم

<span data-type="title" id=toc-1>نسبها ومولدها:</span>
هي حفصة بنت عمر بن الخطاب بن فضيل العدوية القرشية وحفص على التذكير من أسماء الأسد وبالتأنيث أنثاه وكثيرًا ما كان ينادي رسول الله - صلى الله عليه وسلم - عمر - رضي الله عنه - يقول: (يا أبا حفص).
وأمها زينب بنت مظعون أخت عثمان بن مظعون أحد السابقين إلى الإسلام في مكة رضي الله عنها.
أصيلة الحسب والنسب وفي الذروة من قريش مكانةً ولدت كما تذكر روايات التاريخ قبل بعثة النبي - صلى الله عليه وسلم - بخمس سنوات ويؤرخون لمولدها ببناء قريش الكعبة بعد أن جرفها السيل.
وعلى هذا فيكون مولدها في نفس تاريخ مولد فاطمة الزهراء رضي الله عنها ابنة رسول الله - صلى الله عليه وسلم -.

<span data-type="title" id=toc-2>نشأتها:</span>
نشأت حفصة رضي الله عنها في بيت عمر بن الخطاب نشأة كريمة عزيزة تحترم الأب وتخشاه في آن معًا لما كان عليه من شدة وقسوة وغلظة في جاهليته.
وتعلمت حفصة القراءة والكتابة منذ نعومة أظافرها على نهج الأشراف والسادة وبرعت في ذلك وتفوقت على قريناتها، مرت أعوامها الأولى أثناء فتوتها وصباها وهي ترى بأم عينها وتسمع أنباء الصراع العنيف بين الفئة المؤمنة بقيادة محمد بن عبد الله - صلى الله عليه وسلم - وبين',NULL,'2',NULL,NULL,'0');
INSERT INTO [b36386] VALUES (2,'الكثرة الكافرة بقيادة الأفذاذ من قريش ومن بينهم والدها عمر. ولكنها لم تكن تدرك أو تتأثر إلا في حدود طاقة عقلها ووجدانها.

<span data-type="title" id=toc-3>إسلامها:</span>
عاد الأب عمر ذات يوم إلى داره بوجه غير الذي خرج به في الصباح وبلسان ولهجة غير ما كان ينطق من قبل إنه متهلل الوجه يشع نورًا وحبورًا طلق المحيا مبسوط اللسان بالكلام اللين الذي يقطر رقة وعذوبة لا شك أن تغييرًا كبيرًا قد حدث.
وعرف أهل الدار بإسلام عمر ففرحوا وانشرحت صدورهم وازدادوا فرحًا بالتغيير لقد زالت عن سماء الدار سحابة التجهم وانطلقت في أرجائه بسمة الأرض.

<span data-type="title" id=toc-4>زواجها الأول:</span>
وخطبها إلى أبيها خنيس بن حذافة السهمي الشاب المسلم المؤمن فرحب به عمر وأكرم وفادته.
وعلى بركة الله انتقلت حفصة من دار أبيها إلى دار زوجها خنيس وعاشا معًا في وفاق ومحبة ووئام لقد عاشت حفصة زوجة تعرف حقوق وواجب الزوجية تقدر المسئولية وتضطلع بأعباء بيت الزوجية وواجباته وترعى كل أموره بحكمة المرأة الناضجة العاقلة رغم سنها المبكرة.
وكانت حفصة رضي الله عنها تقدر في زوجها خنيس سابقته إلى الإسلام فهو من الأوائل الذين حملوا الدعوة في قلوبهم وتشبعت بها أرواحهم وجاهدوا من أجلها بأموالهم, وأنفسهم تقربًا إلى الله عز وجل لقد كان خنيس - رضي الله عنه - من مهاجري الحبشة الهجرة الأولى لذا كان حبها عظيمًا ومنزلته من قلبها في الصميم.',NULL,'3',NULL,NULL,'0');
INSERT INTO [b36386] VALUES (3,'<span data-type="title" id=toc-5>حفصة المهاجرة:</span>
ثم كان الحدث العظيم في حياة المسلمين وهو هجرتهم من مكة إلى المدينة حيث أخذت الدعوة مسارًا جديدًا ومفترقًا هامًا وفاصلاً فهاجرت حفصة مع زوجها خنيس رضي الله عنهما منتظمين في ركب المسلمين وموكب المؤمنين.

<span data-type="title" id=toc-6>الأرملة الحزينة:</span>
خاض المسلمون بقيادة رسول الله - صلى الله عليه وسلم - بدر في العام الثاني من الهجرة وانتصروا يومئذ بتأييد من الله تعالى على المشركين نصرًا مؤزرًا وعادوا إلى المدينة التي استقبلتهم استقبالاً حافلاً وعظيمًا. فقد كان النصر يومئذ أول صفحة في سفر الجهاد حيث تنكست أعلام الجاهلية وفرغ كبرياؤها وزلزلت أركان عنجهيتها وبدت قريش أمام العرب مهينة ضعيفة هزيلة لكن الثأر حركها من رقدتها وأيقظ ما عفا من عزتها الجوفاء فصممت على إعادة الكرة واستعادة الموقع والموقف كي تظل سيدة العرب بلا منازع وتقضي حسب وهمها على بدعة الدين الجديد فخرجت بجموعها وحشودها وكل عزمها وحزمها إلى المدينة تريد المواجهة مع النبي - صلى الله عليه وسلم - في عقر داره وحصنه الحصين.
وكان ما كان من نتائج أحد ...
ووقع خنيس - رضي الله عنه - جريحًا يعاني أشد المعاناة ينزف وتتردد أنفاسه بطيئة في صدره تنذر بدنو الأجل فكان قلب حفصة يئن من الحزن ويضج بالهم والغم ... وهي تلازم فراش خنيس وكانت تسعى كل السعي في محاولة الانقاذ اليائسة.',NULL,'4',NULL,NULL,'0');
INSERT INTO [b36386] VALUES (4,'ولم يكونا قد رزقا بمولود ذكر أو أنثى وأسرع ابن الخطاب - رضي الله عنه - إلى دار ابنته ليطمئن على خنيس، ولكن سبق السيف العذل ولم تفلح عقاقير الأطباء ولا معالجتهم في شفائه فقضي مأسوفًا على سبابه. وترملت حفصة رضي الله عنها وهي في سن مبكرة إذ كانت لا تعدو العشرين ربيعًا من عمرها وانطوت حفصة على نفسها حزينة بائسة صابرة محتسبة فقيدها الغالي عند الله تعالى وفي رحمة سبحانه وأسلمت أمرها للبارئ عز وجل يقدر من أمر أيامها ويفعل ما يشاء.

<span data-type="title" id=toc-7>أحزان عمر:</span>
لقد تألم عمر - رضي الله عنه - كثيرًا لفقدان خنيس وترملت حفصة، فكان يزورها ويواسيها ويحاول أن يخفف عنها ما تعانيه ثم يخرج من عندها وفي عينيه دمعة وفي قلبه حسرة وفي حلقه غصة وفي ذات يوم .. وقد بلغ الحزن مداه في نفسه، التقى في الطريق بعثمان بن عفان الذي كان قد فقد زوجته رقية بنت رسول الله - صلى الله عليه وسلم - فتشجع وعرض عليه الزواج من حفصة فقال عثمان: مالي في النساء حاجة ... !
لم يقلها عثمان بجفوة أو غلظة ولكن بإحساس مرارة الزوج الحزين الذي لا يزال يعيش جو حبه لزوجته الحبيبة ثم لقي عمر أبا بكر فعرض عليه نفس العرض، الزواج من حفصة فسكت ولم يجب .. !
فغضب عمر كثيرًا وأتى رسول الله - صلى الله عليه وسلم - والثورة بادية على محياه حمرة الأسى تتقد في عينيه وحين استمع إليه رسول الله - صلى الله عليه وسلم - وعرف',NULL,'5',NULL,NULL,'0');
INSERT INTO [b36386] VALUES (5,'ما به قال له: «يزوج الله تعالى عثمًان خيرًا من حفصة ويزوج حفصة خيرًا من عثمان فهدأ بعض غضبه - رضي الله عنه - وإن لم يدرك أبعاد هذه الكلمات وخرج من عند رسول الله - صلى الله عليه وسلم - يحمل في قلبه بعض الطمأنينة.

<span data-type="title" id=toc-8>خير من عثمان:</span>
الباب يقرع: إنه باب دار عمر بن الخطاب. والقارع: هو رسول الله - صلى الله عليه وسلم -. والطلب: هو زواجه عليه الصلاة والسلام من حفصة!
ولقد كانت فرحة عمر أعظم من أن توصف وأكبر من أن يخطها قلم .. ! لقد أصبح سيد المرسلين وخاتم النبيين زوجًا لحفصة الأرملة الحزينة تلك التي عرضها والدها على اثنين من أخلص أصحابه وأصفى مقربيه فرفض أحدهما وسكت الآخر.

<span data-type="title" id=toc-9>حديث عمر:</span>
ويحدثنا الفاروق عمر - رضي الله عنه - عن تلك الواقعة فيقول: أتيت عثمان بن عفان فعرضت عليه حفصة فقلت: إن شئت أنكحتك حفصة فقال: سأنظر في أمري، فمكثت ليالي ثم لقيني فقال: قد بدا لي لا أتزوج يومي هذا، فلقيت أبا بكر. فقلت: إن شئت زوجتك حفصة فصمت أبو بكر فلم يرجع إلى شيئًا فكنت عليه أوجد مني على عثمان.
فمكثت ليالي ثم خطبها رسول الله - صلى الله عليه وسلم - فأنكحتها إياه فلقيني أبو بكر فقال لعلك وجدت عليَّ حين عرضت علي حفصة فلم أرجع إليك شيئًا! ؟ فقلت: نعم فقال أبو بكر: إنه لم يمنعني أن أرجع إليك',NULL,'6',NULL,NULL,'0');
INSERT INTO [b36386] VALUES (6,'فيما عرضت إلا أنني قد كنت علمت أن رسول الله - صلى الله عليه وسلم - قد ذكرها فما كنت لأفشي سر رسول الله - صلى الله عليه وسلم - ولو تركها رسول الله قبلتها ولما تزوج رسول الله - صلى الله عليه وسلم - حفصة بنت عمر - رضي الله عنه - كان خيرًأ من أبي بكر ولما تزوج عثمان من أم كلثوم بنت رسول الله - صلى الله عليه وسلم - كانت خيرًا من حفصة.

<span data-type="title" id=toc-10>حفصة من نسائه - صلى الله عليه وسلم - في الجنة:</span>
أقامت حفصة رضي الله عنها في بيت النبوة فأدت قسطه وحقه من الإخلاص والوفاء والسمع والطاعة والتقوى والعبادة.
لكنها رضي الله عنها كانت بحكم تركيبها الأنثوي شأنها شأن النساء عامة تتأثر بعوامل الغيرة فلم يخل صدرها وقلبها من ضغط هذا العامل لقد تزعمت هي وعائشة رضي الله عنهما حزب المطالبة من رسول الله - صلى الله عليه وسلم - مما أدى إلى غضب رسول الله - صلى الله عليه وسلم - ومقاطعته أزواجه شهرًا كاملاً وانتشر خبر ذلك وظن عمر أن النبي - صلى الله عليه وسلم - قد طلق حفصة لأنها أغضبته فجاءها معاتبًا بقسوة وشدة وأغلظ لها القول حتى بكت وانتحبت.
ولكن ظهر فيما بعد أن هذا الأمر لم يكن إلا خاطرًا مر في ذهن النبي - صلى الله عليه وسلم - وأن جبريل - عليه السلام - قد جاءه قائلاً: لا تطلق حفصة فإنها صؤوم قؤوم وإنها من نسائك في الجنة.
وعلى ذكر الصؤوم القؤوم .. فإن حفصة رضي الله عنها اشتهرت شهرة ذائعة بأنها كانت قليلا ما تفطر تقوم أكثر الليل للصلاة والدعاء والذكر والاستغفار.',NULL,'7',NULL,NULL,'0');
INSERT INTO [b36386] VALUES (7,'<span data-type="title" id=toc-11>بعد رسول الله - صلى الله عليه وسلم </span>-:
مرت حياة حفصة في بيت النبي - صلى الله عليه وسلم - على أحب ما تشتهي وتريد وحفظت عن المصطفى عليه الصلاة والسلام بعض أقواله الحميمة وتوجيهاته السامية فسلكت مسلكها وعملت بمقتضاها ووعاها صدرها وقلبها لما دنت ساعة الفراق. ولحق رسول الله - صلى الله عليه وسلم - بالرفيق الأعلى بكته حفصة بدموع هتون وقلب محزون ولزمت دارها لا تفارقها أبدًا سوى الحج إلى بيت الله الحرام وكانت العبادة سلوكها والتصدق على الفقراء والمساكين عادتها.

<span data-type="title" id=toc-12>في عهد الخليفتين:</span>
وأضحت حفصة أم المؤمنين رضي الله عنها في عهد الخليفتين أبي بكر وعمر رضي الله عنهما موضع تقدير واحترام حرمتها من حرمة بيت النبوة .. حتى إن والدها الفاروق كان يعظم هذا المعنى ويقدس تلك المكانة إكرامًا ووفاءٌ للنبي الراحل إلى جوار ربه الكريم - صلى الله عليه وسلم -. إضافة إلى ذلك أن بيت حفصة حجرتها رضي الله عنها كانت مستودع ما كتب من وحي وما نزل من آيات بينات على الرقاع وألواح العظام .. تصون ذلك وتحفظه فلما شرح الله صدر أبي بكر - رضي الله عنه - لجمع المصحف بإلحاح من عمر بعد أن استشرى القتل في القراء والحفظة أثناء حروب الردة خاصة يوم اليمامة كان ما عند حفصة رضي الله عنها في جملة ما اعتمد عليه في المراجعة والضبط.
بهذا يحدثنا التاريخ وأصدق الروايات وأوثقها فكان لأم المؤمنين حفصة رضي الله عنها من الفضل ما يضاف إلى رصيدها العظيم في العلم والإيمان والخلق والعبادة والأمانة.',NULL,'8',NULL,NULL,'0');
INSERT INTO [b36386] VALUES (8,'<span data-type="title" id=toc-13>حفصة المتحدثة:</span>
روت حفصة رضي الله عنها عن رسول الله - صلى الله عليه وسلم - بأمانة الناقل وحفظ الواعي كثيرًا من الأحاديث النبوية الشريفة التي تتعلق بالأحكام والسلوك.
وروى عنها جماعة من الصحابة والتابعين رضي الله عنهم أجمعين كأخيها عبد الله بن عمر وابنه حمزة وحارثة بن وهب والمطلب بن أبي وداعة وأم مبشر الأنصارية وعبد الرحمن بن الحارث بن هشام وعبد الله بن صفوان والحسيب بن رافع وسوار الخزاعي وغيرهم.
فأسهمت رضي الله عنها بما حفظت ووعت وفقهت وعلمت في إثراء العلوم الدينية العظيمة من علوم الشريعة المحمدية.

<span data-type="title" id=toc-14>إكرام النبي - صلى الله عليه وسلم - لحفصة:</span>
سبق لنا القول بأن الرسول - صلى الله عليه وسلم - كان يسهم بين أزواجه حين يخرج لغزو أو قتال وكانت حفصة ذات مرة من أصحاب السهم فخرجت معه - صلى الله عليه وسلم - كانت تقوم في خيمتها وخبائها فإذا ما أسفرت المعركة عن وجهها وانجلى غبارها شمرت حفصة عن ساعديها وخاضت بين الجرحى تسقي العطاش وتداوي المكلومين وتخفف من ألم المصابين وتضمد جراح المعذبين .. وفي تلك الغزوة نفلها رسول الله - صلى الله عليه وسلم - كما تذكر لنا روايات التاريخ ثمانين وسقًا من القمح وهذا إكرام من النبي - صلى الله عليه وسلم - واعتراف منه بفضل وجهد السيدة المصون حفصة أم المؤمنين.',NULL,'9',NULL,NULL,'0');
INSERT INTO [b36386] VALUES (9,'<span data-type="title" id=toc-15>أخلاقها وفضائلها:</span>
وسبق لنا أيضًا أن قلنا إن السماء قد شهدت لحفصة بالمثل الأعلى في التدين والتقوى حين قال جبريل - عليه السلام - لرسول الله - صلى الله عليه وسلم - عنها: إنها صؤوم قؤوم .. ولقد حدث جويرية عن أسماء عن نافع قال: صامت حفصة حتى ما تفطر أما حجها فحدث عنه ولا حرج .. فلقد حجت حجة الوداع مع رسول الله - صلى الله عليه وسلم - ثم لما اختاره الله تعالى إلى جواره الكريم كانت رضي الله عنها كلما أذن مؤذن الحج من كل عام فهيأت لزيارة البيت العتيق وأداء المناسك من طواف وسعي .. وغير ذلك ثم التصدق على الفقراء والمساكين فتنفق بلا حساب لأن ما عند الله تعالى خير وأبقى فكان كل ما يقسم لها فيء وما يأتيها من أعطيات الخلفاء تجعله في ميزان حسناتها يوم القيامة بصرفه على المساكين والضعفاء والمحتاجين.

<span data-type="title" id=toc-16>وفاتها رضي الله عنها:</span>
وكانت دارها في المدينة حجرتها في بيت أزواج النبي - صلى الله عليه وسلم - محط أنظار كبار الصحابة يأتونها زائرين ومستفسرين ومتعلمين وسائلين واصلين أو موصلين.
لا تخرج من الدار إلا إلى المسجد لأداء الصلاة أو زيارة قبر زوجها الحبيب رسول الله - صلى الله عليه وسلم -.
ثم تمسح دموعها قد استذكرت الأيام الخوالي وتعود أدراجها إلى البيت يحتضنها بحنان وتدلف إليه بشوق. وفي العام الخامس والأربعين من الهجرة النبوية الشريفة وافاها الأجل المحتوم إثر إرهاق ومرض ولبت نداء ربها وأسلمت الروح وكانت جنازة مشهودة.',NULL,'10',NULL,NULL,'0');
INSERT INTO [b36386] VALUES (10,'حملت على سرير في نعش إلى المسجد كبار الصحابة يتبعونها بصمت وإجلال ووقار وصلى عليها مروان بن الحكم أمير المدينة في ذلك الحين. وكذلك كان يتقدم الصفوف كعادته الصحابي الجليل أبو هريرة عبد الرحمن بن صخر الدوسي - رضي الله عنه - وروي أن مروان حمل بين عمدي سريرها من عند دار بني حزم إلى دار المغيرة بن شعبة ثم حمله أبو هريرة من دار المغيرة إلى قبرها.
ودفنت في البقيع وجلس مروان بن الحكم ينتظر حتى فرغ من دفنها رضي الله عنها ونزل في قبرها أخواها عبد الله وعاصم ابنا عمر بن الخطاب وكانت وفاتها رضي الله عنها من تلك السنة، رضي الله عن أم المؤمنين حفصة بنت عمر بن الخطاب الصوامة القوامة وبارك مثواها وأكرم نزلها وألحقنا بها في الصالحين من عباده.',NULL,'11',NULL,NULL,'0');
