INSERT INTO [b37642] VALUES (485,'٨ ... سنن النسائي الكبرى ... أحمد بن شعيب أبو عبدالرحمن النسائي ... دار الكتب العلمية ... بيروت ... الأولى، ١٤١١ هـ، ١٩٩١ م
٩ ... صحيح ابن حبان ... محمد بن حبان بن أحمد التميمي البستي ... مؤسسة الرسالة ... بيروت ... الثانية ١٤١٤ هـ، ١٩٩٣ م
١٠ ... صحيح بن خزيمة ... محمد بن إسحاق بن خزيمة السلمي النيسابوري ... المكتب الإسلامي ... بيروت ... هـ، ١٩٧١ م
١١ ... صحيح مسلم ... مسلم بن الحجاج القشيري النيسابوري ... دار إحياء التراث العربي ... بيروت ... ـ
١٢ ... المستدرك على الصحيحين ... محمد بن عبدالله الحاكم النيسابوري ... دار الكتب العلمية ... بيروت ... الأولى ١٤٠٠ هـ، ١٩٩٠ م
١٣ ... مسند الإمام أحمد ... أحمد بن حنبل الشيباني ... مؤسسة قرطبة ... مصر ... ـ
١٤ ... مصنف ابن أبي شيبة ... عبدالله بن محمد بن أبي شيبة الكوفي ... مكتبة الرشد ... الرياض ... الأولى، ١٤٠٩ هـ
١٥ ... مصنف عبدالرزاق ... عبدالرزاق بن همام الصنعاني ... المكتب الإسلامي ... بيروت ... الثانية، ١٤٠٣ هـ',NULL,'482',NULL,NULL,'0');
INSERT INTO [b37642] VALUES (486,'١٦ ... المعجم الأوسط للطبراني ... سليمان بن أحمد الطبراني ... دار الحرمين ... القاهرة ... هـ
١٧ ... المعجم الصغير للطبراني ... سليمان بن أحمد الطبراني ... المكتب الإسلامي ... بيروت، عمان ... الأولى، ١٤٠٥ هـ، ١٩٨٥ م
