INSERT INTO [b37642] VALUES (487,'<span data-type="title" id=toc-106>ثالثاً: كتب المذهب الحنفي:</span>
م ... اسم الكتاب ... اسم المؤلف ... اسم الدار ... بلد الطبع ... تاريخ الطبع
١ ... الاختيار لتعليل المختار ... عبدالله بن محمود الموصلي ... دار الكتب العلمية ... بيروت ... الثانية، ١٤٢٦ هـ، ٢٠٠٥ م
٢ ... الأشباه والنظائر ... زين الدين بن نجيم الحنفي ... دار الكتب العلمية ... بيروت ... هـ، ١٩٨٠ م
٣ ... البحر الرائق شرح كنز الدقائق ... زين الدين بن نجيم الحنفي ... دار المعرفة ... بيروت ... الثانية
٤ ... بدائع الصنائع في ترتيب الشرائع ... علاء الدين الكاساني ... دار الكتاب العربي ... بيروت ... الثانية، ١٩٨٢ م
٥ ... تبيين الحقائق شرح كنز الدقائق ... فخر الدين عثمان بن علي الزيلعي ... دار الكتاب الإسلامي ... القاهرة ... هـ
٦ ... الجامع الصغير وشرحه النافع الكبير ... محمد بن الحسن الشيباني ... عالم الكتب ... بيروت ... الأولى، ١٤٠٦ هـ
٧ ... حاشية رد المحتار على الدر المختار شرح تنوير الأبصار ... علاء الدين ابن عابدين ... دار الفكر ... بيروت ... هـ، ٢٠٠٠ م',NULL,'484',NULL,NULL,'0');
INSERT INTO [b37642] VALUES (488,'٨ ... الدر المختار شرح تنوير الأبصار ... محمد علاء الدين الحصفكي ... دار الفكر ... بيروت ... الثانية، ١٣٨٦ هـ
٩ ... درر الحكام شرح مجلة الأحكام ... علي حيدر ... دار الكتب العلمية ... بيروت ... تعريب المحامي فهمي الحسني
١٠ ... شرح فتح القدير ... كمال الدين محمد بن عبدالواحد السيواسي ... دار الفكر ... بيروت ... الثانية
١١ ... الفتاوى الهندية ... جماعة من علماء الهند ... دار الفكر ... ــ ... هـ، ١٩٩١ م
١٢ ... المبسوط ... شمس الدين السرخسي ... دار المعرفة ... بيروت ... ــ
١٣ ... مجلة الأحكام العدلية ... لجنة من عدة علماء في الخلافة العثمانية ... نور محمد كارخانة كتب، آرامباغ، ... كراتشي ... ــ
١٤ ... الهداية شرح البداية ... علي بن أبي بكر بن عبدالجليل المرغياني ... المكتبة الإسلامية ... ــ ... ــ',NULL,'485',NULL,NULL,'0');
