INSERT INTO [b37834] VALUES (414,'العلم/ رقم الأثر ٢٨٤، ٣٤١، ٣٦٠، ٣٦٩، ٤٢٨، ٤٩٩، ٥٠١ إسماعيل بن جعفر بن أبي كثير ١٦، ٨٣، ٩٢، ٢٥٤، ٥٣٠ إسماعيل بن جعفر بن محمد (الصادق) / ٣٠٩ إسماعيل بن أبي خالد الأحمسي ٢٧، ٢١٨، ٢٣١، ٢٨١، ٢٩١، ٣٧٢، ٣٨٠، ٥٢٨ إسماعيل بن سالم الأسدي ٤٨، ٢٠١ إسماعيل بن عبد الرحمن السدّي ٢٩، ٢٤٥، ٣٩٣، ٣٩٨، ٤٤٢ إسماعيل بن عمر
الواسطي/ أبو المنذر ١٨، ٣٤٠ إسماعيل بن عياش العنسي ٤٣١ إسماعيل ٤٩٠ الأسود بن قيس العبدي ٤٦٢ الأسود بن يزيد النخعي ٣٥٢ أشعث بن سوّار الكندي ٢٢١، ٢٩٧ أشعث بن أبي الشعثاء ١٢٠ أشعث بن عبد الله الحداني ١١٣ أشعث بن عبد الله الحمراني ١٥٣، ٣٩٨ أشعث بن قيس الكندي ١١٥، ٣٩٨ أنس بن عياض الليثي ١٠٦ أنس بن مالك ١١، ٤٦، ٨٣، ٩٢، ١١٤، ٢٥٣، ٢٥٤، ٢٥٥، ٣٣٤، ٣٣٥، ٣٤٠، ٣٧٠ إياس بن عبد الله بن عبد ياليل (الفجاءة) ٣٩٨ أيوب بن أبي تميمة السختياني ٦٩، ١٠٩، ١١٤، ١١٩، ١٧٨، ٢٠٥، ٢١٠، ٢٢٨، ٣٦٩، ٤٢٨','1','336',NULL,NULL,'0');
