INSERT INTO [b38011] VALUES (496,'<span data-type="title" id=toc-591>فهرس أسماء المؤلفين ومؤلفاتهم في التجويد والقراءات</span>
إبراهيم بن إسماعيل بن محمود العدوي الصالحي الدمشقي الرسالة العدوية في الياءات الإضافية: ص ٣٧٨ القواعد السنية في قراءة حفص عن عاصم من طريق الشاطبية: أربع نسخ ص ٤٣٢، ٤٣٣، ٤٣٤، ٤٣٥ إبراهيم بن عمر الجعبري باب وقف حمزة وهشام: ص ١٢٥ شرح الواضحة في تجويد الفاتحة: ص ٢١٤ عقود الجمان في تجويد القرآن: ص ٢١٨، ٢١٩ كنز المعاني في حرز الأماني ووجه التهاني: ص ٤٥٠ إبراهيم بن عمر بن حسن بن علي البقاعي القول المفيد في أصول التجويد: ص ٢٤٤ الضوابط والإشارات الأجزاء علم القراءات: ص ٤٠٥ إبراهيم بن محمد بن عبد الرحمن السوهائي المالكي الأزهري الدرر المنثورة في قراءة أبي عمرو المشهورة: ص ٣٦٧ إبراهيم بن محمد بن عبد الوهاب رسالة في قراءة حفص: ص ٣٨٣ إبراهيم بن موسى بن بلال الكركي الشافعي الآلة في معرفة الإمالة: ص ١١٣','1','502',NULL,NULL,'0');
INSERT INTO [b38011] VALUES (497,'أحمد بن أحمد بن بدر الدين الطيبي الشافعي بلوغ الأماني في قراءة ورش من طريق الأصبهاني: ص ٣١٧ مذهب حمزة في تحقيق الهمزة: ص ٤٥٩ المفيد في التجويد: ص ٢٥٤ أحمد بن أحمد عمر النشوي النور برواية قالون وورش عن نافع: ص ٤٧٩ أحمد البقري إجازة بالقراءات لعلي بن أحمد الشهير بابن كزبر أحمد بن الحسن بن محمد بن مهران المقري الأصبهاني النيسابوري المبسوط في القراءات العشر: ص ٤٥٨ أحمد بن خالد بن مصطفى دهمان كفاية المريد في علم التجويد: ص ٢٤٦ أحمد بن سليمان بن عثمان الخالدي النقشبندي شرح الرسالة الميدانية:- نسختان- ص ٢٠٤، ٢٠٥ أحمد بن عبد الجواد الغرة البهية في شرح الدرة المضية في قراءات الأئمة الثلاث المرضية:
ص ٤١٤ أحمد بن عمر بن محمد الجملاني تعليق في ذكر ما رواه الزبير العمري عن أبي جعفر: ص ٣٢٩ نفيس الأثاث في القراءات الثلاث: ص ٤٧٨ أحمد بن محمد بن أحمد بن عبد الغني الدمياطي الشهير بالبناء إنحاف فضلاء البشر بالقراءات الأربعة عشر: ص ٢٩١','1','503',NULL,NULL,'0');
INSERT INTO [b38011] VALUES (498,'أحمد بن محمد الآقحصاري الحنفي المعروف بالرومي شرح الدرّ اليتيم في علم التجويد: ص ٢٠١ أحمد بن محمد بن عبد الملك القسطلاني لطائف الإشارات لفنون العبارات في القراءات الأربع عشرة: خمس نسخ- ص ٤٥٣، ٤٥٥، ٤٥٦، ٤٥٧، ٤٥٨ أحمد بن محمد بن عبد الكريم الأشموني الشافعي منار الهدى في بيان الوقف والابتداء: ص ٢٧٦ أحمد بن محمد بن علي الحلواني رسالة في التجويد: ص ١٧٧ أحمد بن محمد الجزري الحواشي المفهمة في شرح المقدمة: خمس نسخ: ص ١٥٤، ١٥٥، ١٥٦، ١٥٧ أحمد بن مصطفى بن خليل المعروف بطاش كوبري زاده شرح المقدمة الجزرية: ثلاث نسخ: ص ٢٠٨، ٢١٠، ٢١١ أحمد بن منصور الزاهد الحكيم المعروف بالحدادي زلة القاري: ص ٣٩٠ اسماعيل بن خلف بن سعيد المقرئ النحوي الأندلسي العنوان في القراءات السبع: نسختان- ص ٤٢١، ٤١٣ أبو بكر بن محمد بن بكر البرسوي السيف المسلول على من ينكر المنقول: ص ١٩٧ حافظ محمود الورداري ترتيب آيات القرآن الكريم على حروف المعجم: ص ٣٢٨','1','504',NULL,NULL,'0');
INSERT INTO [b38011] VALUES (499,'حامد بن عبد الفتاح البالوي زبدة العرفان في وجوه القرآن: ص ٣٨٩ الحسن بن أحمد بن سهل بن سلمة العطار الهمذاني الوقف والابتداء: ص ٢٨٣ الحسن بن شجاع بن محمد بن الحسن التوني التبيان في بيان القرآن: نسختان- ص ١٣٦، ١٣٧ حسن بن علي بن أحمد المنطاوي المدابغي حواش مفيدة على شرح الجزرية: ص ١٥٨ حسن بن عمار بن علي الشرنبلالي الوفائي الحنفي النفحة القدسية في أحكام قراءة القرآن وكتابته بالفارسية: ص ٤٧٧ الحسن بن القاسم بن عبد الله المرادي المصري المعروف بابن أم قاسم شرح باب وقف حمزة وهشام: ص ٨٩١ مسائل الهمزة الساكنة بعد الحركة والهمزة المتحركة بعد ساكن صحيح .... ص ٢٥٣ حسين بن إسكندر الحنفي لباب التجويد للقرآن المجيد: ص ٢٤٨ مختصر الأسباب في تجويد القرآن: ثلاث نسخ- ص ٢٥٠، ٢٥١ حسين بن رجب بن حسين الشطاري المولوي العقد الفريد فيما يجب تعلمه من أحكام التجويد: ص ٢١٦ حسين موسى الشافعي الأزهري رسالة في مخارج الضاد: ص ١٩١','1','505',NULL,NULL,'0');
INSERT INTO [b38011] VALUES (500,'خالد بن عبد الله بن محمد الجرجاوي الأزهري الوقاد الحواشي الأزهرية في حل ألفاظ المقدمة الجزرية: خمس نسخ- ص ١٥٠، ١٥١، ١٥٢، ١٥٣ خليل بن درويش بن حسن التاجي الأنوار البهية شرح المقدمة الميدانية: نسختان ص ١١٤، ١١٦ أبو عمرو زبان بن العلاء بن عمار التميمي البصري الإدغام الكبير: ثلاث نسخ- ص ١٠٤، ١٠٥، ١٠٦
زكريا بن محمد بن أحمد بن زكريا الأنصاري الشافعي تحفة نجباء العصر: ثلاث نسخ- ص ١٤١، ١٤٢ الدقائق المحكمة في شرح المقدمة: اثنتا عشرة نسخة- ص ١٦١، ١٦٣ ١٦٤، ١٦٥، ١٦٦، ١٦٧، ١٦٨ المقصد لتلخيص ما في المرشد: ثماني نسخ- ص ٢٦٧، ٢٦٨، ٢٦٩، ٢٧٠، ٢٧١، ٢٧٢ سلطان بن ناصر بن أحمد الجبوري الشافعي العقود المجوهرة واللآلي المبتكرة: نسختان- ص ٤١٠، ٤١٢ طاهر بن عمر بن إبراهيم بن أحمد بن عربشاه الأصبهاني الطاهرة في القراءات العشر: ص ٤٠٦ طيفور السجاوندي الغزنوي بيان الوقف اللازم: نسختان- ص ١٣٥، ١٣٦ عبد الباقي المغربي الحنبلي سؤال في القراءات: ص ٣٩٥ عبد الرحمن بن إسماعيل بن إبراهيم المقدسي المعروف بأبي شامة إبراز المعاني من حرز الأماني: ثلاث نسخ- ص ٢٨٨، ٢٨٩، ٢٩٠','1','506',NULL,NULL,'0');
INSERT INTO [b38011] VALUES (501,'عبد الرحمن بن علي التميمي البكري المعروف بابن الجوزي فوائد من المتشابه في الحروف المبدلات: ص ٢٣٢ عبد الرحمن بن كمال الدين السيوطي شرح الشاطبية: ص ٣٩٩ عبد الرحمن بن محمد النحراوي المصري الشهير بالمغربي النكات الحسان على شرح شيخ الاسلام لمقدمة تجويد القرآن: ص ٢٨٠ عبد الرزاق بن رزق الله بن خلف الرسعني الحنبلي درة القاري للفرق بين الضاد والظاء: ص ٣٦٦ عبد العزيز بن علي بن محمد بن سلمة السماني الأندلسي المعروف بابن الطحان رسالة في مخارج الحروف وصفاتها: ص ١٨٩ عبد العزيز بن منجى بن أحمد الحلبي الإتقان في تلاوة القرآن: ص ٢٩٢ عبد الغني بن إسماعيل بن عبد الغني النابلسي صرف العنان الى قراءة حفص بن سليمان: ص ٤٠٤ كفاية المستفيد في علم التجويد: ص ٢٤٧ عبد الله بن آي دوغدي الحنفي المعروف بابن الجندي شرح التسديد في علم التجويد: ص ٢٠٠ عبد الله بن عبد المؤمن الواسطي الكنز في القراءات العشر: ص ٤٤٦ عبد الله بن عمر الأنصاري المعروف بابن النكزاوي الاقتداء في معرفة الوقف والابتداء: نسختان- ص ١١١، ١١٣','1','507',NULL,NULL,'0');
INSERT INTO [b38011] VALUES (502,'عبد الله حلمي بن محمد بن يوسف الرومي المعروف بيوسف أفندي زادة مسائل في القراءات: ص ٤٦٢ عبد الواحد بن محمد بن علي بن أبي السداد الأموي المالقي عمدة التحرير في الادغام الكبير: ص ٢٢٠ عبد الوهاب بن أحمد بن وهبان الحارثي الدمشقي أحاسن الأخبار في محاسن السبعة الأخيار: ص ٢٩٧ عثمان بن سعيد الأموي الأندلسي الداني لتبصرة المبتدي وتذكرة المنتهي: ص ٣١٩ التيسير في القراءات وشرحها: تسع عشرة نسخة- ٣٣٤، ٣٣٦، ٣٣٧، ٣٣٨، ٣٣٩، ٣٤٠، ٣٤١، ٣٤٢، ٣٤٣، ٣٤٤، ٣٤٥، ٣٤٦، ٣٤٧ المكتفي في الوقف والابتداء: أربع نسخ- ص ٢٧٣، ٢٧٤، ٢٧٥ عثمان بن عمرو بن محمد بن علي الناشري الزبيدي در الناظم لرواية حفص من رواية عاصم: ص ٣٧١ إيضاح الدرة المضية في قراءة الثلاثة المرضيّة: ص ٣٠٦، ٣٠٨، ٣٠٩، ٣١٠ علي بن أبي محمد الديواني الواسطي جمع الأصول في مشهور المنقول في القراءات العشر: ص ٣٤٩ علي بن أحمد التدمري شرح المقدمة الميدانية في علم التجويد: ص ٢١١ علي بن جعفر بن محمد الرازي السعيدي التنبيه على اللحن الجلي والخفي في القرآن الكريم: ص ١٤٦','1','508',NULL,NULL,'0');
INSERT INTO [b38011] VALUES (503,'علي بن سعيد بن علي المقرئ المنصوري رسالة في مسائل من القراءات: نسختان- ص ٣٨٦، ٣٨٧ علي بن سلطان بن محمد الهروي المعروف بالقاري المنح الفكرية في شرح المقدمة الجزرية: نسختان ص ٢٧٧، ٢٧٨ علي بن سليمان بن عبد الله المقرئ المنصوري ردّ الإلحاد في النطق بالضاد: ص ١٦٩ رسالة في التجويد: ص ١٧٩ رسالة في الردّ على علي بن غانم المقدسي: ص ١٨٦ المقدمة في التجويد: ص ٢٥٧ تحرير الطرق والروايات في بعض الآيات: ٣٢٢ علي بن عثمان بن محمد المعروف بابن القاصح العدوي سراج القاري المبتدي وتذكرة المقري المنتهي في حل قصيدة الشاطبي:
