INSERT INTO [b38011] VALUES (893,'إرشاد الرحمن لأسباب النزول وللنسخ والمتشابه وتجويد القرآن ٥٨١٤/ ٣٠ أزهار التنزيل وأذكار التأويل ٦٠٨٧/ ٣١ أسباب النزول ٩١١٠/ ٣٢ أسد فكر وأسد مشية في إعراب أشدّ ذكرا وأشدّ خشية ١١٢/ ٣٣ أسرار متشابهات الفرقان ١٠٣٧٠/ ٣٤ أصل الحذف والثبت ٨٣٧١/ ٣٥ إعجاز القرآن ٧٠٩٠/ ٣٦ إعراب أم الكتاب ٣٢١٦/ ٣٧ إعراب التعوّذ والفاتحة ١٨٠٢/ ٣٩ إعراب سور من القرآن الكريم ٨٧٤٣/ ٤٠ إعراب القرآن ٦٨٦٣/ ٤١ الأغنى في شرح أسماء الله الحسنى ٨٩٢٦/ ٤٢ أقاويل الثقات في تأويل الأسماء والصفات والآيات المحكمات والمتشابهات ٦٣٢/ ٤٣ الأمور الناجحة في كشف أسرار الفاتحة ٥٠٦/ ٤٤ أنس الجليل في خواص «حسبنا الله ونعم الوكيل» ٥٩٥٠/ ٤٥ إنقاذ الهالكين ٦٠٤٤/ ٤٦ أنموذج جليل في بيان أسئلة وأجوبة من غرائب آي التنزيل ٥٤٣/ ٤٦ أنهار الجنان من منابيع آيات القرآن ١١٣/ ٤٨ أنواع البديع في البسملة ١١٠٧٧/ ٥٠ البديع في الهجاء ٣٠٧/ ٥٠ البرهان في توجيه متشابه القرآن ٣٧٧٦/ ٥١ البرهان الكاشف عن إعجاز القرآن ٥٦٢١/ ٥٢','2','362',NULL,NULL,'0');
INSERT INTO [b38011] VALUES (894,'بغية السائلين ٤٨٤٠، ٤٨٥٢/ ٥٣، ٥٤ بيان خواص القرآن المجيد ٧٩٢٧/ ٥٥ بيان ما ضلّت فيه الزنادقة من متشابه القرآن ٧٥٤٠/ ٥٥ بيان المكي والمدني من السور، وذكر عدد الآي في كل سورة ٧٦٥٩/ ٥٦ بيان نبذة من مناسبات سورة الفاتحة ٥٣٥١/ ٥٧ بيان النسب بين كل من الحمد اللغوي والعرفي ٥١٣٩/ ٥٨ بواطن القرآن ومواطن
الفرقان ٩٨٦٨/ ٥٩ التبيان في آداب حملة القرآن ٣٢٦، ٣٢٧، ٣٢٨، ٣٣٨، ٦٢٩٤، ٨٤٨٢، ١٠٥٦١، ٢٣١٩/ ٦١، ٦٢، ٦٣، ٦٤، ٦٥، ٦٦ التبيان في إعراب القرآن ٥٣١، ٥٣٢/ ٦٦، ٦٨ التبيان في معرفة تنزيل القرآن واختلاف عدد آيات القرآن ٤٢٣٣/ ٦٩ تتمة البيان لما أشكل من متشابه القرآن ٣٤٤/ ٧٠ التحبير في شرح أسماء اللطيف الخبير ٨٧٧٠، ٨٩٨٠، ٦٠٤١/ ٧٢، ٧٣، ٧٤ تحفة الأريب بما في القرآن من الغريب ١٥٧٠/ ٧٤ تذكرة المنتبه في عيون المشتبه ١٠٦٤/ ٧٦ ترتيب آيات القرآن العظيم ٥٣٨٧، ١٠٠١٨/ ٧٧، ٧٨ تسهيل إعراب القرآن العظيم ٤٣٨٤/ ٧٨ تشنيف السمع بتعديد السبع ٤٠٥١/ ٧٩ تعبير سور القرآن العظيم على ما يراه النائم ٦٠٩٨/ ٨١ التعريف والإعلام فيما أبهم في القرآن من الأسماء والأعلام ٥١٩، ٦١٠/ ٨٢، ٨٣ تفسير مشكل إعراب القرآن العظيم ٧٧٢٣/ ٨٣','2','363',NULL,NULL,'0');
INSERT INTO [b38011] VALUES (895,'تفسير المشكل من غريب القرآن العظيم على الإيجاز والاختصار ٨٩٩٣/ ٨٥ التقريب والبيان في معرفة شواذ القرآن ٦٨٦٦/ ٨٥ تقييد طرر على مورد الظمآن ٥٩٦٤/ ٨٧ التكملة والإتمام لكتاب التعريف والإعلام ٥١٨/ ٨٨ تلخيص الفوائد وتقريب المتباعد في شرح عقيلة أتراب القصائد في أسنى المقاصد ٨٤٧٧/ ٨٩ تمام الضبط والهجاء في الرسم ٣٨٨١/ ٩٠ تناسق الدرر في تناسب السور ٥٠٥٠/ ٩١ التنبيه على الألفاظ التي وقع في نقلها وضبطها تصحيف، وخطأ في تفسيرها ومعانيها ... للهروي ١٥٨٩/ ٩٢ التنزيل وترتيبه ٣٧٣٦/ ٩٤ التنزيل في هجاء المصاحف ٥٩٦٤/ ٩٥ تهذيب الترتيب ٦٢٦١/ ٩٦ تهذيب القرآن ٧١٤٨/ ٩٨ توضيح منظومة السخاوي في المتشابه ٣٥٧/ ٩٩ تهليل القرآن ٦٨٥٠/ ١٠٠ الجامع الأقصى في أسماء الله الحسنى ٩٦٤١/ ١٠١ جزء في علوم القرآن ١٠٣٠٠/ ١٠١ جمال القراء وكمال الإقراء ٣٣٣، ٩٠٣٥/ ١٠٢، ١٠٤ جواز قراءة القرآن بالألحان ٨٩٢٢/ ١٠٥ جواهر القرآن العزيز ودرره ٩١٦٣، ٧٧٢٧/ ١٠٦، ١٠٧ حميدية الزمان في أفضلية الرسول الأعظم بنص القرآن ٩١٦٧، ٨٦١٤/ ١٠٨، ١٠٩','2','364',NULL,NULL,'0');
INSERT INTO [b38011] VALUES (896,'خزائن الجواهر ومخازن الزواهر ٨١١١/ ١١٠ الخلاف والتشهير والاستحسان، وما أغفله مورد الظمآن وما سكت عنه التنزيل والبرهان ٨٦٤٩/ ١١١ خواص أسماء الله الحسنى ٩٢٧٦، ٩٨٢٨، ١١١٦٣/ ١١٢، ١١٣، ١١٤ خواص آية الكرسي ١٠٤٣٧/ ١١٥ خواص البسملة ٦٠٩٠/ ١١٦ خواص الفاتحة ٦٨٥٠/ ١١٧ خواص القرآن العظيم ٧٣٦٥، ٩٥٩٤/ ١١٨، ١١٩ الخواطر السوانح في كشف أسرار الفواتح ٩١٦٩/ ١١٩ خير الكلام على البسملة والحمدلة ٥١٣٩، ١٠٢٩٧، ١٠٢٨٠/ ١٢١، ١٢٢ خواص الأسماء الحسنى ١٠٢١٩/ ١٢٣ الدرر الحسان في اختصار كتاب التبيان في شرح مورد الظمآن ٨٦٤٩/ ١٢٤ الدرّ المصون في علوم الكتاب المكنون ٣٩١٧، ٥٦٥٢، ٧٠٩٤، ٧٠٩٥، ٧٠٩٧/ ١٢٥، ١٢٧، ١٢٨، ١٢٩، ١٣٠ الدرّة الجليّة ٨٣٧١/ ١٣١ درّة القاري ٦٣٩٣، ٣٨٤٧/ ١٣٢، ١٣٣ الدعوة المباركة لسورة يس ١٠٢٣/ ١٣٥ ديباجة الدرّ الأسنى لنظم أسماء الله الحسنى ١١٣٤٦/ ١٣٥ الذهب الإبريز في خواص كتاب الله العزيز ٨٠٦٣/ ١٣٦ الردّ على الزنادقة والجهمية فيما شكت فيه من متشابه القرآن ٣٨٥٢/ ١٣٧','2','365',NULL,NULL,'0');
